package ru.bible;

public class rubible30 {

public rubible30() {

putmapbible30();

}

private void putmapbible30() {

rusuperobject.biblemap.put("b30_1_1", new rusuperobject(30,1,1,"Слова Амоса, одного из пастухов Фекойских, которые он слышал в видении об Израиле во дни Озии, царя Иудейского, и во дни Иеровоама, сына Иоасова, царя Израильского, за два года перед землетрясением. "));
rusuperobject.biblemap.put("b30_1_2", new rusuperobject(30,1,2,"И сказал он: Господь возгремит с Сиона и даст глас Свой из Иерусалима, и восплачут хижины пастухов, и иссохнет вершина Кармила. "));
rusuperobject.biblemap.put("b30_1_3", new rusuperobject(30,1,3,"Так говорит Господь: за три преступления Дамаска и за четыре не пощажу его, потому что они молотили Галаад железными молотилами. "));
rusuperobject.biblemap.put("b30_1_4", new rusuperobject(30,1,4,"И пошлю огонь на дом Азаила, и пожрет он чертоги Венадада. "));
rusuperobject.biblemap.put("b30_1_5", new rusuperobject(30,1,5,"И сокрушу затворы Дамаска, и истреблю жителей долины Авен и держащего скипетр - из дома Еденова, и пойдет народ Арамейский в плен в Кир, говорит Господь. "));
rusuperobject.biblemap.put("b30_1_6", new rusuperobject(30,1,6,"Так говорит Господь: за три преступления Газы и за четыре не пощажу ее, потому что они вывели всех в плен, чтобы предать их Едому. "));
rusuperobject.biblemap.put("b30_1_7", new rusuperobject(30,1,7,"И пошлю огонь в стены Газы, - и пожрет чертоги ее. "));
rusuperobject.biblemap.put("b30_1_8", new rusuperobject(30,1,8,"И истреблю жителей Азота и держащего скипетр в Аскалоне, и обращу руку Мою на Екрон, и погибнет остаток Филистимлян, говорит Господь Бог. "));
rusuperobject.biblemap.put("b30_1_9", new rusuperobject(30,1,9,"Так говорит Господь: за три преступления Тира и за четыре не пощажу его, потому что они передали всех пленных Едому и не вспомнили братского союза. "));
rusuperobject.biblemap.put("b30_1_10", new rusuperobject(30,1,10,"Пошлю огонь в стены Тира, и пожрет чертоги его. "));
rusuperobject.biblemap.put("b30_1_11", new rusuperobject(30,1,11,"Так говорит Господь: за три преступления Едома и за четыре не пощажу его, потому что он преследовал брата своего мечом, подавил чувства родства, свирепствовал постоянно во гневе своем и всегда сохранял ярость свою. "));
rusuperobject.biblemap.put("b30_1_12", new rusuperobject(30,1,12,"И пошлю огонь на Феман, и пожрет чертоги Восора. "));
rusuperobject.biblemap.put("b30_1_13", new rusuperobject(30,1,13,"Так говорит Господь: за три преступления сынов Аммоновых и за четыре не пощажу их, потому что они рассекали беременных в Галааде, чтобы расширить пределы свои. "));
rusuperobject.biblemap.put("b30_1_14", new rusuperobject(30,1,14,"И запалю огонь в стенах Раввы, и пожрет чертоги ее, среди крика в день брани, с вихрем в день бури. "));
rusuperobject.biblemap.put("b30_1_15", new rusuperobject(30,1,15,"И пойдет царь их в плен, он и князья его вместе с ним, говорит Господь. "));
rusuperobject.biblemap.put("b30_2_1", new rusuperobject(30,2,1,"Так говорит Господь: за три преступления Моава и за четыре не пощажу его, потому что он пережег кости царя Едомского в известь. "));
rusuperobject.biblemap.put("b30_2_2", new rusuperobject(30,2,2,"И пошлю огонь на Моава, и пожрет чертоги Кериофа, и погибнет Моав среди разгрома с шумом, при звуке трубы. "));
rusuperobject.biblemap.put("b30_2_3", new rusuperobject(30,2,3,"Истреблю судью из среды его и умерщвлю всех князей его вместе с ним, говорит Господь. "));
rusuperobject.biblemap.put("b30_2_4", new rusuperobject(30,2,4,"Так говорит Господь: за три преступления Иуды и за четыре не пощажу его, потому что отвергли закон Господень и постановлений Его не сохранили, и идолы их, вслед которых ходили отцы их, совратили их с пути. "));
rusuperobject.biblemap.put("b30_2_5", new rusuperobject(30,2,5,"И пошлю огонь на Иуду, и пожрет чертоги Иерусалима. "));
rusuperobject.biblemap.put("b30_2_6", new rusuperobject(30,2,6,"Так говорит Господь: за три преступления Израиля и за четыре не пощажу его, потому что продают правого за серебро и бедного - за пару сандалий. "));
rusuperobject.biblemap.put("b30_2_7", new rusuperobject(30,2,7,"Жаждут, чтобы прах земной был на голове бедных, и путь кротких извращают; даже отец и сын ходят к одной женщине, чтобы бесславить святое имя Мое. "));
rusuperobject.biblemap.put("b30_2_8", new rusuperobject(30,2,8,"На одеждах, взятых в залог, возлежат при всяком жертвеннике, и вино, взыскиваемое с обвиненных, пьют в доме богов своих. "));
rusuperobject.biblemap.put("b30_2_9", new rusuperobject(30,2,9,"А Я истребил перед лицем их Аморрея, которого высота была как высота кедра и который был крепок как дуб; Я истребил плод его вверху и корни его внизу. "));
rusuperobject.biblemap.put("b30_2_10", new rusuperobject(30,2,10,"Вас же Я вывел из земли Египетской и водил вас в пустыне сорок лет, чтобы вам наследовать землю Аморрейскую. "));
rusuperobject.biblemap.put("b30_2_11", new rusuperobject(30,2,11,"Из сыновей ваших Я избирал в пророки и из юношей ваших - в назореи; не так ли это, сыны Израиля? говорит Господь. "));
rusuperobject.biblemap.put("b30_2_12", new rusuperobject(30,2,12,"А вы назореев поили вином и пророкам приказывали, говоря: «не пророчествуйте». "));
rusuperobject.biblemap.put("b30_2_13", new rusuperobject(30,2,13,"Вот, Я придавлю вас, как давит колесница, нагруженная снопами, - "));
rusuperobject.biblemap.put("b30_2_14", new rusuperobject(30,2,14,"и у проворного не станет силы бежать, и крепкий не удержит крепости своей, и храбрый не спасет своей жизни, "));
rusuperobject.biblemap.put("b30_2_15", new rusuperobject(30,2,15,"ни стреляющий из лука не устоит, ни скороход не убежит, ни сидящий на коне не спасет своей жизни. "));
rusuperobject.biblemap.put("b30_2_16", new rusuperobject(30,2,16,"И самый отважный из храбрых убежит нагой в тот день, говорит Господь. "));
rusuperobject.biblemap.put("b30_3_1", new rusuperobject(30,3,1,"Слушайте слово сие, которое Господь изрек на вас, сыны Израилевы, на все племя, которое вывел Я из земли Египетской, говоря: "));
rusuperobject.biblemap.put("b30_3_2", new rusuperobject(30,3,2,"только вас признал Я из всех племен земли, потому и взыщу с вас за все беззакония ваши. "));
rusuperobject.biblemap.put("b30_3_3", new rusuperobject(30,3,3,"Пойдут ли двое вместе, не сговорившись между собою? "));
rusuperobject.biblemap.put("b30_3_4", new rusuperobject(30,3,4,"Ревет ли лев в лесу, когда нет перед ним добычи? подает ли свой голос львенок из логовища своего, когда он ничего не поймал? "));
rusuperobject.biblemap.put("b30_3_5", new rusuperobject(30,3,5,"Попадет ли птица в петлю на земле, когда силка нет для нее? Поднимется ли с земли петля, когда ничего не попало в нее? "));
rusuperobject.biblemap.put("b30_3_6", new rusuperobject(30,3,6,"Трубит ли в городе труба, - и народ не испугался бы? Бывает ли в городе бедствие, которое не Господь попустил бы? "));
rusuperobject.biblemap.put("b30_3_7", new rusuperobject(30,3,7,"Ибо Господь Бог ничего не делает, не открыв Своей тайны рабам Своим, пророкам. "));
rusuperobject.biblemap.put("b30_3_8", new rusuperobject(30,3,8,"Лев начал рыкать, - кто не содрогнется? Господь Бог сказал, - кто не будет пророчествовать? "));
rusuperobject.biblemap.put("b30_3_9", new rusuperobject(30,3,9,"Провозгласите на кровлях в Азоте и на кровлях в земле Египетской и скажите: соберитесь на горы Самарии и посмотрите на великое бесчинство в ней и на притеснения среди нее. "));
rusuperobject.biblemap.put("b30_3_10", new rusuperobject(30,3,10,"Они не умеют поступать справедливо, говорит Господь: насилием и грабежом собирают сокровища в чертоги свои. "));
rusuperobject.biblemap.put("b30_3_11", new rusuperobject(30,3,11,"Посему так говорит Господь Бог: вот неприятель, и притом вокруг всей земли! он низложит могущество твое, и ограблены будут чертоги твои. "));
rusuperobject.biblemap.put("b30_3_12", new rusuperobject(30,3,12,"Так говорит Господь: как иногда пастух исторгает из пасти львиной две голени или часть уха, так спасены будут сыны Израилевы, сидящие в Самарии в углу постели и в Дамаске на ложе. "));
rusuperobject.biblemap.put("b30_3_13", new rusuperobject(30,3,13,"Слушайте и засвидетельствуйте дому Иакова, говорит Господь Бог, Бог Саваоф. "));
rusuperobject.biblemap.put("b30_3_14", new rusuperobject(30,3,14,"Ибо в тот день, когда Я взыщу с Израиля за преступления его, взыщу и за жертвенники в Вефиле, и отсечены будут роги алтаря, и падут на землю. "));
rusuperobject.biblemap.put("b30_3_15", new rusuperobject(30,3,15,"И поражу дом зимний вместе с домом летним, и исчезнут домы с украшениями из слоновой кости, и не станет многих домов, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_1", new rusuperobject(30,4,1,"Слушайте слово сие, телицы Васанские, которые на горе Самарийской, вы, притесняющие бедных, угнетающие нищих, говорящие господам своим: «подавай, и мы будем пить!» "));
rusuperobject.biblemap.put("b30_4_2", new rusuperobject(30,4,2,"Клялся Господь Бог святостью Своею, что вот, придут на вас дни, когда повлекут вас крюками и остальных ваших удами. "));
rusuperobject.biblemap.put("b30_4_3", new rusuperobject(30,4,3,"И сквозь проломы стен выйдете, каждая, как случится, и бросите все убранство чертогов, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_4", new rusuperobject(30,4,4,"Идите в Вефиль - и грешите, в Галгал - и умножайте преступления; приносите жертвы ваши каждое утро, десятины ваши хотя через каждые три дня. "));
rusuperobject.biblemap.put("b30_4_5", new rusuperobject(30,4,5,"Приносите в жертву благодарения квасное, провозглашайте о добровольных приношениях ваших и разглашайте о них, ибо это вы любите, сыны Израилевы, говорит Господь Бог. "));
rusuperobject.biblemap.put("b30_4_6", new rusuperobject(30,4,6,"За то и дал Я вам голые зубы во всех городах ваших и недостаток хлеба во всех селениях ваших; но вы не обратились ко Мне, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_7", new rusuperobject(30,4,7,"И удерживал от вас дождь за три месяца до жатвы; проливал дождь на один город, а на другой город не проливал дождя; один участок напояем был дождем, а другой, не окропленный дождем, засыхал. "));
rusuperobject.biblemap.put("b30_4_8", new rusuperobject(30,4,8,"И сходились два-три города в один город, чтобы напиться воды, и не могли досыта напиться; но и тогда вы не обратились ко Мне, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_9", new rusuperobject(30,4,9,"Я поражал вас ржою и блеклостью хлеба; множество садов ваших и виноградников ваших, и смоковниц ваших, и маслин ваших пожирала гусеница, - и при всем том вы не обратились ко Мне, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_10", new rusuperobject(30,4,10,"Посылал Я на вас моровую язву, подобную Египетской, убивал мечом юношей ваших, отводя коней в плен, так что смрад от станов ваших поднимался в ноздри ваши; и при всем том вы не обратились ко Мне, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_11", new rusuperobject(30,4,11,"Производил Я среди вас разрушения, как разрушил Бог Содом и Гоморру, и вы были выхвачены, как головня из огня, - и при всем том вы не обратились ко Мне, говорит Господь. "));
rusuperobject.biblemap.put("b30_4_12", new rusuperobject(30,4,12,"Посему так поступлю Я с тобою, Израиль; и как Я так поступлю с тобою, то приготовься к сретению Бога твоего, Израиль, "));
rusuperobject.biblemap.put("b30_4_13", new rusuperobject(30,4,13,"ибо вот Он, Который образует горы, и творит ветер, и объявляет человеку намерения его, утренний свет обращает в мрак, и шествует превыше земли; Господь Бог Саваоф - имя Ему. "));
rusuperobject.biblemap.put("b30_5_1", new rusuperobject(30,5,1,"Слушайте это слово, в котором я подниму плач о вас, дом Израилев. "));
rusuperobject.biblemap.put("b30_5_2", new rusuperobject(30,5,2,"Упала, не встает более дева Израилева! повержена на земле своей, и некому поднять ее. "));
rusuperobject.biblemap.put("b30_5_3", new rusuperobject(30,5,3,"Ибо так говорит Господь Бог: город, выступавший тысячею, останется только с сотнею, и выступавший сотнею, останется с десятком у дома Израилева. "));
rusuperobject.biblemap.put("b30_5_4", new rusuperobject(30,5,4,"Ибо так говорит Господь дому Израилеву: взыщите Меня, и будете живы. "));
rusuperobject.biblemap.put("b30_5_5", new rusuperobject(30,5,5,"Не ищите Вефиля и не ходите в Галгал, и в Вирсавию не странствуйте, ибо Галгал весь пойдет в плен и Вефиль обратится в ничто. "));
rusuperobject.biblemap.put("b30_5_6", new rusuperobject(30,5,6,"Взыщите Господа, и будете живы, чтобы Он не устремился на дом Иосифов как огонь, который пожрет его, и некому будет погасить его в Вефиле. "));
rusuperobject.biblemap.put("b30_5_7", new rusuperobject(30,5,7,"О, вы, которые суд превращаете в отраву и правду повергаете на землю! "));
rusuperobject.biblemap.put("b30_5_8", new rusuperobject(30,5,8,"Кто сотворил семизвездие и Орион, и претворяет смертную тень в ясное утро, а день делает темным как ночь, призывает воды морские и разливает их по лицу земли? - Господь имя Ему! "));
rusuperobject.biblemap.put("b30_5_9", new rusuperobject(30,5,9,"Он укрепляет опустошителя против сильного, и опустошитель входит в крепость. "));
rusuperobject.biblemap.put("b30_5_10", new rusuperobject(30,5,10,"А они ненавидят обличающего в воротах и гнушаются тем, кто говорит правду. "));
rusuperobject.biblemap.put("b30_5_11", new rusuperobject(30,5,11,"Итак за то, что вы попираете бедного и берете от него подарки хлебом, вы построите домы из тесаных камней, но жить не будете в них; разведете прекрасные виноградники, а вино из них не будете пить. "));
rusuperobject.biblemap.put("b30_5_12", new rusuperobject(30,5,12,"Ибо Я знаю, как многочисленны преступления ваши и как тяжки грехи ваши: вы враги правого, берете взятки и извращаете в суде дела бедных. "));
rusuperobject.biblemap.put("b30_5_13", new rusuperobject(30,5,13,"Поэтому разумный безмолвствует в это время, ибо злое это время. "));
rusuperobject.biblemap.put("b30_5_14", new rusuperobject(30,5,14,"Ищите добра, а не зла, чтобы вам остаться в живых, - и тогда Господь Бог Саваоф будет с вами, как вы говорите. "));
rusuperobject.biblemap.put("b30_5_15", new rusuperobject(30,5,15,"Возненавидьте зло и возлюбите добро, и восстановите у ворот правосудие; может быть, Господь Бог Саваоф помилует остаток Иосифов. "));
rusuperobject.biblemap.put("b30_5_16", new rusuperobject(30,5,16,"Посему так говорит Господь Бог Саваоф, Вседержитель: на всех улицах будет плач, и на всех дорогах будут восклицать: «увы, увы!», и призовут земледельца сетовать и искусных в плачевных песнях - плакать, "));
rusuperobject.biblemap.put("b30_5_17", new rusuperobject(30,5,17,"и во всех виноградниках будет плач, ибо Я пройду среди тебя, говорит Господь. "));
rusuperobject.biblemap.put("b30_5_18", new rusuperobject(30,5,18,"Горе желающим дня Господня! для чего вам этот день Господень? он тьма, а не свет, "));
rusuperobject.biblemap.put("b30_5_19", new rusuperobject(30,5,19,"то же, как если бы кто убежал от льва, и попался бы ему навстречу медведь, или если бы пришел домой и оперся рукою о стену, и змея ужалила бы его. "));
rusuperobject.biblemap.put("b30_5_20", new rusuperobject(30,5,20,"Разве день Господень не мрак, а свет? он тьма, и нет в нем сияния. "));
rusuperobject.biblemap.put("b30_5_21", new rusuperobject(30,5,21,"Ненавижу, отвергаю праздники ваши и не обоняю жертв во время торжественных собраний ваших. "));
rusuperobject.biblemap.put("b30_5_22", new rusuperobject(30,5,22,"Если вознесете Мне всесожжение и хлебное приношение, Я не приму их и не призрю на благодарственную жертву из тучных тельцов ваших. "));
rusuperobject.biblemap.put("b30_5_23", new rusuperobject(30,5,23,"Удали от Меня шум песней твоих, ибо звуков гуслей твоих Я не буду слушать. "));
rusuperobject.biblemap.put("b30_5_24", new rusuperobject(30,5,24,"Пусть, как вода, течет суд, и правда - как сильный поток! "));
rusuperobject.biblemap.put("b30_5_25", new rusuperobject(30,5,25,"Приносили ли вы Мне жертвы и хлебные дары в пустыне в течение сорока лет, дом Израилев? "));
rusuperobject.biblemap.put("b30_5_26", new rusuperobject(30,5,26,"Вы носили скинию Молохову и звезду бога вашего Ремфана, изображения, которые вы сделали для себя. "));
rusuperobject.biblemap.put("b30_5_27", new rusuperobject(30,5,27,"За то Я переселю вас за Дамаск, говорит Господь; Бог Саваоф - имя Ему! "));
rusuperobject.biblemap.put("b30_6_1", new rusuperobject(30,6,1,"Горе беспечным на Сионе и надеющимся на гору Самарийскую именитым первенствующего народа, к которым приходит дом Израиля! "));
rusuperobject.biblemap.put("b30_6_2", new rusuperobject(30,6,2,"Пройдите в Калне и посмотрите, оттуда перейдите в Емаф великий и спуститесь в Геф Филистимский: не лучше ли они сих царств? не обширнее ли пределы их пределов ваших? "));
rusuperobject.biblemap.put("b30_6_3", new rusuperobject(30,6,3,"Вы, которые день бедствия считаете далеким и приближаете торжество насилия, - "));
rusuperobject.biblemap.put("b30_6_4", new rusuperobject(30,6,4,"вы, которые лежите на ложах из слоновой кости и нежитесь на постелях ваших, едите лучших овнов из стада и тельцов с тучного пастбища, "));
rusuperobject.biblemap.put("b30_6_5", new rusuperobject(30,6,5,"поете под звуки гуслей, думая, что владеете музыкальным орудием, как Давид, "));
rusuperobject.biblemap.put("b30_6_6", new rusuperobject(30,6,6,"пьете из чаш вино, мажетесь наилучшими мастями, и не болезнуете о бедствии Иосифа! "));
rusuperobject.biblemap.put("b30_6_7", new rusuperobject(30,6,7,"За то ныне пойдут они в плен во главе пленных, и кончится ликование изнеженных. "));
rusuperobject.biblemap.put("b30_6_8", new rusuperobject(30,6,8,"Клянется Господь Бог Самим Собою, и так говорит Господь Бог Саваоф: гнушаюсь высокомерием Иакова и ненавижу чертоги его, и предам город и все, что наполняет его. "));
rusuperobject.biblemap.put("b30_6_9", new rusuperobject(30,6,9,"И будет: если в каком доме останется десять человек, то умрут и они, "));
rusuperobject.biblemap.put("b30_6_10", new rusuperobject(30,6,10,"и возьмет их родственник их или сожигатель, чтобы вынести кости их из дома, и скажет находящемуся при доме: есть ли еще у тебя кто? Тот ответит: нет никого. И скажет сей: молчи! ибо нельзя упоминать имени Господня. "));
rusuperobject.biblemap.put("b30_6_11", new rusuperobject(30,6,11,"Ибо вот, Господь даст повеление и поразит большие дома расселинами, а малые дома - трещинами. "));
rusuperobject.biblemap.put("b30_6_12", new rusuperobject(30,6,12,"Бегают ли кони по скале? можно ли распахивать ее волами? Вы между тем суд превращаете в яд и плод правды в горечь; "));
rusuperobject.biblemap.put("b30_6_13", new rusuperobject(30,6,13,"вы, которые восхищаетесь ничтожными вещами и говорите: «не своею ли силою мы приобрели себе могущество?» "));
rusuperobject.biblemap.put("b30_6_14", new rusuperobject(30,6,14,"Вот Я, говорит Господь Бог Саваоф, воздвигну народ против вас, дом Израилев, и будут теснить вас от входа в Емаф до потока в пустыне. "));
rusuperobject.biblemap.put("b30_7_1", new rusuperobject(30,7,1,"Такое видение открыл мне Господь Бог: вот, Он создал саранчу в начале произрастания поздней травы, и это была трава после царского покоса. "));
rusuperobject.biblemap.put("b30_7_2", new rusuperobject(30,7,2,"И было, когда она окончила есть траву на земле, я сказал: Господи Боже! пощади; как устоит Иаков? он очень мал. "));
rusuperobject.biblemap.put("b30_7_3", new rusuperobject(30,7,3,"И пожалел Господь о том; «не будет сего», сказал Господь. "));
rusuperobject.biblemap.put("b30_7_4", new rusuperobject(30,7,4,"Такое видение открыл мне Господь Бог: вот, Господь Бог произвел для суда огонь, - и он пожрал великую пучину, пожрал и часть земли. "));
rusuperobject.biblemap.put("b30_7_5", new rusuperobject(30,7,5,"И сказал я: Господи Боже! останови; как устоит Иаков? он очень мал. "));
rusuperobject.biblemap.put("b30_7_6", new rusuperobject(30,7,6,"И пожалел Господь о том; «и этого не будет», сказал Господь Бог. "));
rusuperobject.biblemap.put("b30_7_7", new rusuperobject(30,7,7,"Такое видение открыл Он мне: вот, Господь стоял на отвесной стене, и в руке у Него свинцовый отвес. "));
rusuperobject.biblemap.put("b30_7_8", new rusuperobject(30,7,8,"И сказал мне Господь: что ты видишь, Амос? Я ответил: отвес. И Господь сказал: вот, положу отвес среди народа Моего, Израиля; не буду более прощать ему. "));
rusuperobject.biblemap.put("b30_7_9", new rusuperobject(30,7,9,"И опустошены будут жертвенные высоты Исааковы, и разрушены будут святилища Израилевы, и восстану с мечом против дома Иеровоамова. "));
rusuperobject.biblemap.put("b30_7_10", new rusuperobject(30,7,10,"И послал Амасия, священник Вефильский, к Иеровоаму, царю Израильскому, сказать: Амос производит возмущение против тебя среди дома Израилева; земля не может терпеть всех слов его. "));
rusuperobject.biblemap.put("b30_7_11", new rusuperobject(30,7,11,"Ибо так говорит Амос: «от меча умрет Иеровоам, а Израиль непременно отведен будет пленным из земли своей». "));
rusuperobject.biblemap.put("b30_7_12", new rusuperobject(30,7,12,"И сказал Амасия Амосу: провидец! пойди и удались в землю Иудину; там ешь хлеб, и там пророчествуй, "));
rusuperobject.biblemap.put("b30_7_13", new rusuperobject(30,7,13,"а в Вефиле больше не пророчествуй, ибо он святыня царя и дом царский. "));
rusuperobject.biblemap.put("b30_7_14", new rusuperobject(30,7,14,"И отвечал Амос и сказал Амасии: я не пророк и не сын пророка; я был пастух и собирал сикоморы. "));
rusuperobject.biblemap.put("b30_7_15", new rusuperobject(30,7,15,"Но Господь взял меня от овец и сказал мне Господь: «иди, пророчествуй к народу Моему, Израилю». "));
rusuperobject.biblemap.put("b30_7_16", new rusuperobject(30,7,16,"Теперь выслушай слово Господне. Ты говоришь: «не пророчествуй на Израиля и не произноси слов на дом Исааков». "));
rusuperobject.biblemap.put("b30_7_17", new rusuperobject(30,7,17,"За это, вот что говорит Господь: жена твоя будет обесчещена в городе, сыновья и дочери твои падут от меча, земля твоя будет разделена межевою вервью, а ты умрешь в земле нечистой, и Израиль непременно выведен будет из земли своей. "));
rusuperobject.biblemap.put("b30_8_1", new rusuperobject(30,8,1,"Такое видение открыл мне Господь Бог: вот корзина со спелыми плодами. "));
rusuperobject.biblemap.put("b30_8_2", new rusuperobject(30,8,2,"И сказал Он: что ты видишь, Амос? Я ответил: корзину со спелыми плодами. Тогда Господь сказал мне: приспел конец народу Моему, Израилю: не буду более прощать ему. "));
rusuperobject.biblemap.put("b30_8_3", new rusuperobject(30,8,3,"Песни чертога в тот день обратятся в рыдание, говорит Господь Бог; много будет трупов, на всяком месте будут бросать их молча. "));
rusuperobject.biblemap.put("b30_8_4", new rusuperobject(30,8,4,"Выслушайте это, алчущие поглотить бедных и погубить нищих, - "));
rusuperobject.biblemap.put("b30_8_5", new rusuperobject(30,8,5,"вы, которые говорите: «когда-то пройдет новолуние, чтобы нам продавать хлеб, и суббота, чтобы открыть житницы, уменьшить меру, увеличить цену сикля и обманывать неверными весами, "));
rusuperobject.biblemap.put("b30_8_6", new rusuperobject(30,8,6,"чтобы покупать неимущих за серебро и бедных за пару обуви, а высевки из хлеба продавать». "));
rusuperobject.biblemap.put("b30_8_7", new rusuperobject(30,8,7,"Клялся Господь славою Иакова: поистине во веки не забуду ни одного из дел их! "));
rusuperobject.biblemap.put("b30_8_8", new rusuperobject(30,8,8,"Не поколеблется ли от этого земля, и не восплачет ли каждый, живущий на ней? Взволнуется вся она, как река, и будет подниматься и опускаться, как река Египетская. "));
rusuperobject.biblemap.put("b30_8_9", new rusuperobject(30,8,9,"И будет в тот день, говорит Господь Бог: произведу закат солнца в полдень и омрачу землю среди светлого дня. "));
rusuperobject.biblemap.put("b30_8_10", new rusuperobject(30,8,10,"И обращу праздники ваши в сетование и все песни ваши в плач, и возложу на все чресла вретище и плешь на всякую голову; и произведу в стране плач, как о единственном сыне, и конец ее будет - как горький день. "));
rusuperobject.biblemap.put("b30_8_11", new rusuperobject(30,8,11,"Вот наступают дни, говорит Господь Бог, когда Я пошлю на землю голод, - не голод хлеба, не жажду воды, но жажду слышания слов Господних. "));
rusuperobject.biblemap.put("b30_8_12", new rusuperobject(30,8,12,"И будут ходить от моря до моря и скитаться от севера к востоку, ища слова Господня, и не найдут его. "));
rusuperobject.biblemap.put("b30_8_13", new rusuperobject(30,8,13,"В тот день истаявать будут от жажды красивые девы и юноши, "));
rusuperobject.biblemap.put("b30_8_14", new rusuperobject(30,8,14,"которые клянутся грехом Самарийским и говорят: «жив бог твой, Дан! и жив путь в Вирсавию!» - Они падут и уже не встанут. "));
rusuperobject.biblemap.put("b30_9_1", new rusuperobject(30,9,1,"Видел я Господа стоящим над жертвенником, и Он сказал: ударь в притолоку над воротами, чтобы потряслись косяки, и обрушь их на головы всех их, остальных же из них Я поражу мечом: не убежит у них никто бегущий и не спасется из них никто, желающий спастись. "));
rusuperobject.biblemap.put("b30_9_2", new rusuperobject(30,9,2,"Хотя бы они зарылись в преисподнюю, и оттуда рука Моя возьмет их; хотя бы взошли на небо, и оттуда свергну их. "));
rusuperobject.biblemap.put("b30_9_3", new rusuperobject(30,9,3,"И хотя бы они скрылись на вершине Кармила, и там отыщу и возьму их; хотя бы сокрылись от очей Моих на дне моря, и там повелю морскому змею уязвить их. "));
rusuperobject.biblemap.put("b30_9_4", new rusuperobject(30,9,4,"И если пойдут в плен впереди врагов своих, то повелю мечу и там убить их. Обращу на них очи Мои на беду им, а не во благо. "));
rusuperobject.biblemap.put("b30_9_5", new rusuperobject(30,9,5,"Ибо Господь Бог Саваоф коснется земли, - и она растает, и восплачут все живущие на ней; и поднимется вся она как река, и опустится как река Египетская. "));
rusuperobject.biblemap.put("b30_9_6", new rusuperobject(30,9,6,"Он устроил горние чертоги Свои на небесах и свод Свой утвердил на земле, призывает воды морские, и изливает их по лицу земли; Господь имя Ему. "));
rusuperobject.biblemap.put("b30_9_7", new rusuperobject(30,9,7,"Не таковы ли, как сыны Ефиоплян, и вы для Меня, сыны Израилевы? говорит Господь. Не Я ли вывел Израиля из земли Египетской и Филистимлян - из Кафтора, и Арамлян - из Кира? "));
rusuperobject.biblemap.put("b30_9_8", new rusuperobject(30,9,8,"Вот, очи Господа Бога - на грешное царство, и Я истреблю его с лица земли; но дом Иакова не совсем истреблю, говорит Господь. "));
rusuperobject.biblemap.put("b30_9_9", new rusuperobject(30,9,9,"Ибо вот, Я повелю и рассыплю дом Израилев по всем народам, как рассыпают зерна в решете, и ни одно не падает на землю. "));
rusuperobject.biblemap.put("b30_9_10", new rusuperobject(30,9,10,"От меча умрут все грешники из народа Моего, которые говорят: «не постигнет нас и не придет к нам это бедствие!» "));
rusuperobject.biblemap.put("b30_9_11", new rusuperobject(30,9,11,"В тот день Я восстановлю скинию Давидову падшую, заделаю трещины в ней и разрушенное восстановлю, и устрою ее, как в дни древние, "));
rusuperobject.biblemap.put("b30_9_12", new rusuperobject(30,9,12,"чтобы они овладели остатком Едома и всеми народами, между которыми возвестится имя Мое, говорит Господь, творящий все сие. "));
rusuperobject.biblemap.put("b30_9_13", new rusuperobject(30,9,13,"Вот, наступят дни, говорит Господь, когда пахарь застанет еще жнеца, а топчущий виноград - сеятеля; и горы источать будут виноградный сок, и все холмы потекут. "));
rusuperobject.biblemap.put("b30_9_14", new rusuperobject(30,9,14,"И возвращу из плена народ Мой, Израиля, и застроят опустевшие города и поселятся в них, насадят виноградники и будут пить вино из них, разведут сады и станут есть плоды из них. "));
rusuperobject.biblemap.put("b30_9_15", new rusuperobject(30,9,15,"И водворю их на земле их, и они не будут более исторгаемы из земли своей, которую Я дал им, говорит Господь Бог твой. "));

}

}