rusuperobject.biblemap.put("b40_1_8", new rusuperobject(40,1,8,"Аса родил Иосафата; Иосафат родил Иорама; Иорам родил Озию; "));
rusuperobject.biblemap.put("b40_1_9", new rusuperobject(40,1,9,"Озия родил Иоафама; Иоафам родил Ахаза; Ахаз родил Езекию; "));
rusuperobject.biblemap.put("b40_1_10", new rusuperobject(40,1,10,"Езекия родил Манассию; Манассия родил Амона; Амон родил Иосию; "));
