package tam.bible;

public class tambible50 {

public tambible50() {

putmapbible50();

}

private void putmapbible50() {

tamsuperobject.biblemap.put("b50_1_1", new tamsuperobject(50,1,1,"இயேசுகிறிஸ்துவின் ஊழியக்காரராகிய பவுலும் தீமோத்தேயும், பிலிப்பி பட்டணத்தில் கிறிஸ்து இயேசுவுக்குள்ளான பரிசுத்தவான்கள் அனைவருக்கும், கண்காணிகளுக்கும், உதவிக்காரருக்கும் எழுதுகிறதாவது:"));
tamsuperobject.biblemap.put("b50_1_2", new tamsuperobject(50,1,2,"நம்முடைய பிதாவாகிய தேவனாலும் கர்த்தராகிய இயேசுகிறிஸ்துவினாலும் உங்களுக்குக் கிருபையும் சமாதானமும் உண்டாவதாக."));
tamsuperobject.biblemap.put("b50_1_3", new tamsuperobject(50,1,3,"சுவிசேஷம் உங்களுக்கு அறிவிக்கப்பட்ட நாள்முதல் இதுவரைக்கும் நீங்கள் அதற்கு உடன்பட்டவர்களானபடியால்,"));
tamsuperobject.biblemap.put("b50_1_4", new tamsuperobject(50,1,4,"நான் பண்ணுகிற ஒவ்வொரு விண்ணப்பத்திலும் உங்கள் அனைவருக்காகவும் எப்போதும் சந்தோஷத்தோடே விண்ணப்பம்பண்ணி,"));
tamsuperobject.biblemap.put("b50_1_5", new tamsuperobject(50,1,5,"உங்களில் நற்கிரியையைத் தொடங்கினவர் அதை இயேசுகிறிஸ்துவின் நாள் பரியந்தம் முடிய நடத்திவருவாரென்று நம்பி,"));
tamsuperobject.biblemap.put("b50_1_6", new tamsuperobject(50,1,6,"நான் உங்களை நினைக்கிறபொழுதெல்லாம் என் தேவனை ஸ்தோத்திரிக்கிறேன்."));
tamsuperobject.biblemap.put("b50_1_7", new tamsuperobject(50,1,7,"என் கட்டுகளிலும், நான் சவிசேஷத்திற்காக உத்தரவுசொல்லி அதைத் திடப்படுத்திவருகிறதிலும், நீங்கள் அனைவரும் எனக்கு அளிக்கப்பட்ட கிருபையில் பங்குள்ளவர்களானதால், உங்களை என் இருதயத்தில் தரித்துக்கொண்டிருக்கிறபடியினாலே, உங்களெல்லாரையுங்குறித்து நான் இப்படி நினைக்கிறது எனக்குத் தகுதியாயிருக்கிறது."));
tamsuperobject.biblemap.put("b50_1_8", new tamsuperobject(50,1,8,"இயேசுகிறிஸ்துவின் உருக்கமான அன்பிலே உங்களெல்லார்மேலும் எவ்வளவோ வாஞ்சையாயிருக்கிறேன் என்பதற்குத் தேவனே எனக்குச் சாட்சி."));
tamsuperobject.biblemap.put("b50_1_9", new tamsuperobject(50,1,9,"மேலும், உத்தமமானவைகளை நீங்கள் ஒப்புக்கொள்ளத்தக்கதாக உங்கள் அன்பானது அறிவிலும் எல்லா உணர்விலும் இன்னும் அதிகமதிகமாய்ப் பெருகவும்,"));
tamsuperobject.biblemap.put("b50_1_10", new tamsuperobject(50,1,10,"தேவனுக்கு மகிமையும் துதியும் உண்டாகும்படி இயேசுகிறிஸ்துவினால் வருகிற நீதியின் கனிகளால் நிறைந்தவர்களாகி,"));
tamsuperobject.biblemap.put("b50_1_11", new tamsuperobject(50,1,11,"நீங்கள் கிறிஸ்துவின் நாளுக்கென்று துப்பரவானவர்களும் இடறலற்றவர்களுமாயிருக்கவும் வேண்டுதல்செய்கிறேன்."));
tamsuperobject.biblemap.put("b50_1_12", new tamsuperobject(50,1,12,"சகோதரரே, எனக்குச் சம்பவித்தவைகள் சுவிசேஷம் பிரபலமாகும்படிக்கு ஏதுவாயிற்றென்று நீங்கள் அறிய மனதாயிருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_1_13", new tamsuperobject(50,1,13,"அரமனை யெங்குமுள்ளவர்களுக்கும் மற்ற யாவருக்கும் என் கட்டுகள் கிறிஸ்துவுக்குள்ளான கட்டுகளென்று வெளியரங்கமாகி,"));
tamsuperobject.biblemap.put("b50_1_14", new tamsuperobject(50,1,14,"சகோதரரில் அநேகர் என் கட்டுகளாலே கர்த்தருக்குள் திடன்கொண்டு பயமில்லாமல் திருவசனத்தைச் சொல்லும்படி அதிகமாய்த் துணிந்திருக்கிறார்கள்."));
tamsuperobject.biblemap.put("b50_1_15", new tamsuperobject(50,1,15,"சிலர் பொறாமையினாலும் விரோதத்தினாலும், சிலர் நல்மனதினாலும் கிறிஸ்துவைப் பிரசங்கிக்கிறார்கள்."));
tamsuperobject.biblemap.put("b50_1_16", new tamsuperobject(50,1,16,"சிலர் என் கட்டுகளோடே உபத்திரவத்தையுங்கூட்ட நினைத்து, சுத்தமனதோடே கிறிஸ்துவை அறிவியாமல், விரோதத்தினாலே அறிவிக்கிறார்கள்."));
tamsuperobject.biblemap.put("b50_1_17", new tamsuperobject(50,1,17,"சுவிசேஷத்திற்காக நான் உத்தரவு சொல்ல ஏற்படுத்தப்பட்டவனென்று அறிந்து, சிலர் அன்பினாலே அறிவிக்கிறார்கள்."));
tamsuperobject.biblemap.put("b50_1_18", new tamsuperobject(50,1,18,"இதனாலென்ன? வஞ்சகத்தினாலாவது, உண்மையினாலாவது, எப்படியாவது, கிறிஸ்து அறிவிக்கப்படுகிறார்; அதனால் சந்தோஷப்படுகிறேன், இன்னமும் சந்தோஷப்படுவேன்."));
tamsuperobject.biblemap.put("b50_1_19", new tamsuperobject(50,1,19,"அது உங்கள் வேண்டுதலினாலும் இயேசுகிறிஸ்துவினுடைய ஆவியின் உதவியினாலும் எனக்கு இரட்சிப்பாக முடியுமென்று அறிவேன்."));
tamsuperobject.biblemap.put("b50_1_20", new tamsuperobject(50,1,20,"நான் ஒன்றிலும் வெட்கப்பட்டுப்போகாமல், எப்பொழுதும்போல இப்பொழுதும், மிகுந்த தைரியத்தோடே ஜீவனாலாகிலும், சாவினாலாகிலும், கிறிஸ்து என் சரீரத்தினாலே மகிமைப்படுவாரென்று எனக்கு உண்டாயிருக்கிற வாஞ்சைக்கும் நம்பிக்கைக்கும் தக்கதாய், அப்படி முடியும்."));
tamsuperobject.biblemap.put("b50_1_21", new tamsuperobject(50,1,21,"கிறிஸ்து எனக்கு ஜீவன், சாவு எனக்கு ஆதாயம்."));
tamsuperobject.biblemap.put("b50_1_22", new tamsuperobject(50,1,22,"ஆகிலும் சரீரத்தில் பிழைத்திருக்கிறதினாலே என் கிரியைக்குப் பலனுண்டாயிருப்பதால், நான் தெரிந்துகொள்ளவேண்டியது இன்னதென்று அறியேன்."));
tamsuperobject.biblemap.put("b50_1_23", new tamsuperobject(50,1,23,"ஏனெனில் இவ்விரண்டினாலும் நான் நெருக்கப்படுகிறேன்; தேகத்தைவிட்டுப் பிரிந்து, கிறிஸ்துவுடனேகூட இருக்க எனக்கு ஆசையுண்டு, அது அதிக நன்மையாயிருக்கும்;"));
tamsuperobject.biblemap.put("b50_1_24", new tamsuperobject(50,1,24,"அப்படியிருந்தும், நான் சரீரத்தில் தரித்திருப்பது உங்களுக்கு அதிக அவசியம்."));
tamsuperobject.biblemap.put("b50_1_25", new tamsuperobject(50,1,25,"இந்த நிச்சயத்தைக்கொண்டிருந்து, நான் மறுபடியும் உங்களிடத்தில் வருகிறதினால் என்னைக்குறித்து உங்களுடைய மகிழ்ச்சி கிறிஸ்து இயேசுவுக்குள் பெருகும்படிக்கு,"));
tamsuperobject.biblemap.put("b50_1_26", new tamsuperobject(50,1,26,"உங்கள் விசுவாசத்தின் வர்த்தனைக்காகவும் சந்தோஷத்துக்காகவும் நான் பிழைத்து, உங்கள் அனைவரோடுங்கூட இருப்பேனென்று அறிந்திருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_1_27", new tamsuperobject(50,1,27,"நான் வந்து உங்களைக் கண்டாலும், நான் வராமலிருந்தாலும், நீங்கள் ஒரே ஆவியிலே உறுதியாய் நின்று, ஒரே ஆத்துமாவினாலே சுவிசேஷத்தின் விசுவாசத்திற்காகக் கூடப்போராடி, எதிர்க்கிறவர்களால் ஒன்றிலும் மருளாதிருக்கிறீர்களென்று உங்களைக்குறித்து நான் கேள்விப்படும்படி, எவ்விதத்திலும் நீங்கள் கிறிஸ்துவின் சுவிசேஷத்திற்குப் பாத்திரராக மாத்திரம் நடந்துகொள்ளுங்கள்."));
tamsuperobject.biblemap.put("b50_1_28", new tamsuperobject(50,1,28,"நீங்கள் மருளாதிருக்கிறது அவர்கள் கெட்டுப்போகிறதற்கும், நீங்கள் இரட்சிக்கப்படுகிறதற்கும் அத்தாட்சியாயிருக்கிறது; இதுவும் தேவனுடைய செயலே."));
tamsuperobject.biblemap.put("b50_1_29", new tamsuperobject(50,1,29,"ஏனெனில் கிறிஸ்துவினிடத்தில் விசுவாசிக்கிறதற்குமாத்திரமல்ல, அவர் நிமித்தமாகப் பாடுபடுகிறதற்கும் உங்களுக்கு அருளப்பட்டிருக்கிறது."));
tamsuperobject.biblemap.put("b50_1_30", new tamsuperobject(50,1,30,"நீங்கள் என்னிடத்திலே கண்டதும் எனக்கு உண்டென்று இப்பொழுது கேள்விப்படுகிறதுமான போராட்டமே உங்களுக்கும் உண்டு."));
tamsuperobject.biblemap.put("b50_2_1", new tamsuperobject(50,2,1,"ஆதலால் கிறிஸ்துவுக்குள் யாதொரு ஆறுதலும், அன்பினாலே யாதொரு தேறுதலும், ஆவியின் யாதொரு ஐக்கியமும், யாதொரு உருக்கமான பட்சமும் இரக்கங்களும் உண்டானால்,"));
tamsuperobject.biblemap.put("b50_2_2", new tamsuperobject(50,2,2,"நீங்கள் ஏக சிந்தையும் ஏக அன்புமுள்ளவர்களாயிருந்து, இசைந்த ஆத்துமாக்களாய் ஒன்றையே சிந்தித்து, என் சந்தோஷத்தை நிறைவாக்குங்கள்."));
tamsuperobject.biblemap.put("b50_2_3", new tamsuperobject(50,2,3,"ஒன்றையும் வாதினாலாவது, வீண்பெருமையினாலாவது செய்யாமல், மனத்தாழ்மையினாலே ஒருவரையொருவர் தங்களிலும் மேன்மையானவர்களாக எண்ணக்கடவீர்கள்."));
tamsuperobject.biblemap.put("b50_2_4", new tamsuperobject(50,2,4,"அவனவன் தனக்கானவைகளையல்ல, பிறருக்கானவைகளையும் நோக்குவானாக."));
tamsuperobject.biblemap.put("b50_2_5", new tamsuperobject(50,2,5,"கிறிஸ்து இயேசுவிலிருந்த சிந்தையே உங்களிலும் இருக்கக்கடவது;"));
tamsuperobject.biblemap.put("b50_2_6", new tamsuperobject(50,2,6,"அவர் தேவனுடைய ரூபமாயிருந்தும், தேவனுக்குச் சமமாயிருப்பதைக் கொள்ளையாடின பொருளாக எண்ணாமல்,"));
tamsuperobject.biblemap.put("b50_2_7", new tamsuperobject(50,2,7,"தம்மைத்தாமே வெறுமையாக்கி, அடிமையின் ரூபமெடுத்து, மனுஷர் சாயலானார்."));
tamsuperobject.biblemap.put("b50_2_8", new tamsuperobject(50,2,8,"அவர் மனுஷரூபமாய்க் காணப்பட்டு, மரணபரியந்தம், அதாவது சிலுவையின் மரணபரியந்தமும் கீழ்ப்படிந்தவராகி, தம்மைத்தாமே தாழ்த்தினார்."));
tamsuperobject.biblemap.put("b50_2_9", new tamsuperobject(50,2,9,"ஆதலால் தேவன் எல்லாவற்றிற்கும் மேலாக அவரை உயர்த்தி,"));
tamsuperobject.biblemap.put("b50_2_10", new tamsuperobject(50,2,10,"இயேசுவின் நாமத்தில் வானோர் பூதலத்தோர் பூமியின் கீழானோருடைய முழங்கால் யாவும் முடங்கும்படிக்கும்,"));
tamsuperobject.biblemap.put("b50_2_11", new tamsuperobject(50,2,11,"பிதாவாகிய தேவனுக்கு மகிமையாக இயேசுகிறிஸ்து கர்த்தரென்று நாவுகள் யாவும் அறிக்கைபண்ணும்படிக்கும், எல்லா நாமத்திற்கும் மேலான நாமத்தை அவருக்குத் தந்தருளினார்."));
tamsuperobject.biblemap.put("b50_2_12", new tamsuperobject(50,2,12,"ஆதலால், எனக்குப் பிரியமானவர்களே, நீங்கள் எப்பொழுதும் கீழ்ப்படிகிறபடியே, நான் உங்களுக்குச் சமீபமாயிருக்கும்பொழுது மாத்திரமல்ல, நான் தூரமாயிருக்கிற இப்பொழுதும், அதிக பயத்தோடும் நடுக்கத்தோடும் உங்கள் இரட்சிப்பு நிறைவேறப் பிரயாசப்படுங்கள்."));
tamsuperobject.biblemap.put("b50_2_13", new tamsuperobject(50,2,13,"ஏனெனில் தேவனே தம்முடைய தயவுள்ள சித்தத்தின்படி விருப்பத்தையும் செய்கையையும் உங்களில் உண்டாக்குகிறவராயிருக்கிறார்."));
tamsuperobject.biblemap.put("b50_2_14", new tamsuperobject(50,2,14,"நான் வீணாக ஓடினதும் வீணாகப் பிரயாசப்பட்டதுமில்லையென்கிற மகிழ்ச்சி கிறிஸ்துவின் நாளில் எனக்கு உண்டாயிருப்பதற்கு, ஜீவவசனத்தைப் பிடித்துக்கொண்டு, உலகத்திலே சுடர்களைப்போலப் பிரகாசிக்கிற நீங்கள்,"));
tamsuperobject.biblemap.put("b50_2_15", new tamsuperobject(50,2,15,"கோணலும் மாறுபாடுமான சந்ததியின் நடுவிலே குற்றமற்றவர்களும் கபடற்றவர்களும், தேவனுடைய மாசற்ற பிள்ளைகளுமாயிருக்கும்படிக்கு,"));
tamsuperobject.biblemap.put("b50_2_16", new tamsuperobject(50,2,16,"எல்லாவற்றையும் முறுமுறுப்பில்லாமலும் தர்க்கிப்பில்லாமலும் செய்யுங்கள்."));
tamsuperobject.biblemap.put("b50_2_17", new tamsuperobject(50,2,17,"மேலும், உங்கள் விசுவாசமாகிய பலியின்மேலும் ஊழியத்தின்மேலும் நான் வார்க்கப்பட்டுப்போனாலும், நான் மகிழ்ந்து, உங்களனைவரோடுங்கூடச் சந்தோஷப்படுவேன்."));
tamsuperobject.biblemap.put("b50_2_18", new tamsuperobject(50,2,18,"இதினிமித்தம் நீங்களும் மகிழ்ந்து, என்னோடேகூடச் சந்தோஷப்படுங்கள்."));
tamsuperobject.biblemap.put("b50_2_19", new tamsuperobject(50,2,19,"அன்றியும், நானும் உங்கள் செய்திகளை அறிந்து மனத்தேறுதல் அடையும்படிக்குச் சீக்கிரமாய்த் தீமோத்தேயுவை உங்களிடத்தில் அனுப்பலாமென்று கர்த்தராகிய இயேசுவுக்குள் நம்பியிருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_2_20", new tamsuperobject(50,2,20,"அதேனென்றால், உங்கள் காரியங்களை உண்மையாய் விசாரிக்கிறதற்கு என்னைப்போல மனதுள்ளவன் அவனையன்றி வேறொருவனும் என்னிடத்திலில்லை."));
tamsuperobject.biblemap.put("b50_2_21", new tamsuperobject(50,2,21,"மற்றவர்களெல்லாரும் கிறிஸ்து இயேசுவுக்குரியவைகளைத் தேடாமல், தங்களுக்குரியவைகளையே தேடுகிறார்கள்."));
tamsuperobject.biblemap.put("b50_2_22", new tamsuperobject(50,2,22,"தகப்பனுக்குப் பிள்ளை ஊழியஞ்செய்வதுபோல, அவன் என்னுடனேகூட சுவிசேஷத்தினிமித்தம் ஊழியஞ்செய்தானென்று அவனுடைய உத்தமகுணத்தை அறிந்திருக்கிறீர்கள்."));
tamsuperobject.biblemap.put("b50_2_23", new tamsuperobject(50,2,23,"ஆகையால் என் காரியங்கள் இன்னபடி நடக்கும் என்று நான் அறிந்தவுடனே அவனை அனுப்பலாமென்று நினைத்திருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_2_24", new tamsuperobject(50,2,24,"அன்றியும் நானே சீக்கிரத்தில் வருவேனென்று கர்த்தருக்குள் நம்பிக்கையாயிருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_2_25", new tamsuperobject(50,2,25,"மேலும், என் சகோதரனும், உடன்வேலையாளும், உடன்சேவகனும், உங்கள் ஸ்தானாபதியும், என் குறைச்சலுக்கு உதவிசெய்தவனுமான எப்பாப்பிரோதீத்துவை உங்களிடத்தில் அனுப்பவேண்டுமென்று எண்ணினேன்."));
tamsuperobject.biblemap.put("b50_2_26", new tamsuperobject(50,2,26,"அவன் உங்கள் எல்லார்மேலும் வாஞ்சையுள்ளவனும், தான் வியாதிப்பட்டதை நீங்கள் கேள்விப்பட்டதினாலே மிகவும் வியாகுலப்படுகிறவனுமாயிருந்தான்."));
tamsuperobject.biblemap.put("b50_2_27", new tamsuperobject(50,2,27,"அவன் வியாதிப்பட்டு மரணத்திற்குச் சமீபமாயிருந்தது மெய்தான். ஆகிலும், தேவன் அவனுக்கு இரங்கினார்; அவனுக்கு இரங்கினதுமல்லாமல், துக்கத்தின்மேல் துக்கம் எனக்கு உண்டாகாதபடிக்கு, எனக்கும் இரங்கினார்."));
tamsuperobject.biblemap.put("b50_2_28", new tamsuperobject(50,2,28,"ஆகையால் நீங்கள் அவனை மறுபடியும் கண்டு சந்தோஷப்படவும், என் துக்கங் குறையவும், அவனை அதிசீக்கிரமாய் அனுப்பினேன்."));
tamsuperobject.biblemap.put("b50_2_29", new tamsuperobject(50,2,29,"ஆனபடியினாலே நீங்கள் கர்த்தருக்குள் மிகுந்த சந்தோஷத்தோடே அவனை ஏற்றுக்கொண்டு, இப்படிப்பட்டவர்களைக் கனமாய் எண்ணுங்கள்."));
tamsuperobject.biblemap.put("b50_2_30", new tamsuperobject(50,2,30,"ஏனெனில் நீங்கள் எனக்குச் செய்ய வேண்டிய ஊழியத்திலே உங்கள் குறைவை நிறைவாக்கும்படிக்கு, அவன் தன் பிராணணையும் எண்ணாமல், கிறிஸ்துவின் ஊழியத்தினிமித்தம் மரணத்திற்குச் சமீபமாயிருந்தான்."));
tamsuperobject.biblemap.put("b50_3_1", new tamsuperobject(50,3,1,"மேலும், என் சகோதரரே, கர்த்தருக்குள் சந்தோஷப்படுங்கள். எழுதினவைகளையே எழுதுவது எனக்கு வருத்தமல்ல, அது உங்களுக்கு நலமாயிருக்கும்."));
tamsuperobject.biblemap.put("b50_3_2", new tamsuperobject(50,3,2,"நாய்களுக்கு எச்சரிக்கையாயிருங்கள், பொல்லாத வேலையாட்களுக்கு எச்சரிக்கையாயிருங்கள், சுன்னத்துக்காரருக்கு எச்சரிக்கையாயிருங்கள்."));
tamsuperobject.biblemap.put("b50_3_3", new tamsuperobject(50,3,3,"ஏனெனில், மாம்சத்தின்மேல் நம்பிக்கையாயிராமல், ஆவியினாலே தேவனுக்கு ஆராதனைசெய்து, கிறிஸ்து இயேசுவுக்குள் மேன்மைபாராட்டுகிற நாமே விருத்தசேதனமுள்ளவர்கள்."));
tamsuperobject.biblemap.put("b50_3_4", new tamsuperobject(50,3,4,"மாம்சத்தின்மேல் நம்பிக்கை வைக்கவேண்டுமானால் நானும் வைக்கலாம்; வேறொருவன் மாம்சத்தின்மேல் நம்பிக்கையாயிருக்க நினைத்தால் நான் அதிகமாய் அப்படிச் செய்யலாம்."));
tamsuperobject.biblemap.put("b50_3_5", new tamsuperobject(50,3,5,"நான் எட்டாம் நாளிலே விருத்தசேதனமடைந்தவன், இஸ்ரவேல் வம்சத்தான், பென்யமீன் கோத்திரத்தான், எபிரெயரில் பிறந்த எபிரெயன், நியாயப்பிரமாணத்தின்படி பரிசேயன்;"));
tamsuperobject.biblemap.put("b50_3_6", new tamsuperobject(50,3,6,"பக்திவைராக்கியத்தின்படி சபையைத் துன்பப்படுத்தினவன், நியாயப்பிரமாணத்திற்குரிய நீதியின்படி குற்றஞ்சாட்டப்படாதவன்."));
tamsuperobject.biblemap.put("b50_3_7", new tamsuperobject(50,3,7,"ஆகிலும், எனக்கு லாபமாயிருந்தவைகளெவைகளோ அவைகளைக் கிறிஸ்துவுக்காக நஷ்டமென்று எண்ணினேன்."));
tamsuperobject.biblemap.put("b50_3_8", new tamsuperobject(50,3,8,"அதுமாத்திரமல்ல, என் கர்த்தராகிய கிறிஸ்து இயேசுவை அறிகிற அறிவின் மேன்மைக்காக எல்லாவற்றையும் நஷ்டமென்று எண்ணிக்கொண்டிருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_3_9", new tamsuperobject(50,3,9,"நான் கிறிஸ்துவை ஆதாயப்படுத்திக்கொள்ளும்படிக்கும், நியாயப்பிரமாணத்தினால் வருகிற சுயநீதியை உடையவனாயிராமல், கிறிஸ்துவைப் பற்றும் விசுவாசத்தினால் வருகிறதும் விசுவாசமூலமாய்த் தேவனால் உண்டாயிருக்கிறதுமான நீதியை உடையவனாயிருந்து, கிறிஸ்துவுக்குள் இருக்கிறவனென்று காணப்படும்படிக்கும்,"));
tamsuperobject.biblemap.put("b50_3_10", new tamsuperobject(50,3,10,"இப்படி நான் அவரையும் அவருடைய உயிர்த்தெழுதலின் வல்லமையையும், அவருடைய பாடுகளின் ஐக்கியத்தையும் அறிகிறதற்கும், அவருடைய மரணத்திற்கொப்பான மரணத்திற்குள்ளாகி, எப்படியாயினும் நான் மரித்தோரிலிருந்து உயிரோடெழுந்திருப்பதற்குத் தகுதியாகும்படிக்கும்,"));
tamsuperobject.biblemap.put("b50_3_11", new tamsuperobject(50,3,11,"அவருக்காக எல்லாவற்றையும் நஷ்டமென்று விட்டேன்; குப்பையுமாக எண்ணுகிறேன்."));
tamsuperobject.biblemap.put("b50_3_12", new tamsuperobject(50,3,12,"நான் அடைந்தாயிற்று, அல்லது முற்றும் தேறினவனானேன் என்று எண்ணாமல், கிறிஸ்து இயேசுவினால் நான் எதற்காகப் பிடிக்கப்பட்டேனோ அதை நான் பிடித்துக்கொள்ளும்படி ஆசையாய்த் தொடர்கிறேன்."));
tamsuperobject.biblemap.put("b50_3_13", new tamsuperobject(50,3,13,"சகோதரரே, அதைப் பிடித்துக்கொண்டேனென்று நான் எண்ணுகிறதில்லை; ஒன்று செய்கிறேன், பின்னானவைகளை மறந்து, முன்னானவைகளை நாடி,"));
tamsuperobject.biblemap.put("b50_3_14", new tamsuperobject(50,3,14,"கிறிஸ்து இயேசுவுக்குள் தேவன் அழைத்த பரம அழைப்பின் பந்தயப்பொருளுக்காக இலக்கை நோக்கித் தொடருகிறேன்."));
tamsuperobject.biblemap.put("b50_3_15", new tamsuperobject(50,3,15,"ஆகையால், நம்மில் தேறினவர்கள் யாவரும் இந்தச் சிந்தையாயிருக்கக்கடவோம்; எந்தக் காரியத்திலாவது நீங்கள் வேறே சிந்தையாயிருந்தால், அதையும் தேவன் உங்களுக்கு வெளிப்படுத்துவார்."));
tamsuperobject.biblemap.put("b50_3_16", new tamsuperobject(50,3,16,"ஆகிலும் நாம் எதுவரையில் தேறியிருக்கிறோமோ, அதுமுதல் ஒரே ஒழுங்காய் நடந்துகொண்டு, ஒரே சிந்தையாயிருப்போமாக."));
tamsuperobject.biblemap.put("b50_3_17", new tamsuperobject(50,3,17,"சகோதரரே, நீங்கள் என்னோடேகூடப் பின்பற்றுகிறவர்களாகி, நாங்கள் உங்களுக்கு வழிகாட்டுகிறபடி நடக்கிறவர்களை மாதிரியாக நோக்குங்கள்."));
tamsuperobject.biblemap.put("b50_3_18", new tamsuperobject(50,3,18,"ஏனெனில், அநேகர் வேறுவிதமாய் நடக்கிறார்கள்; அவர்கள் கிறிஸ்துவின் சிலுவைக்குப் பகைஞரென்று உங்களுக்கு அநேகந்தரம் சொன்னேன், இப்பொழுது கண்ணீரோடும் சொல்லுகிறேன்."));
tamsuperobject.biblemap.put("b50_3_19", new tamsuperobject(50,3,19,"அவர்களுடைய முடிவு அழிவு, அவர்களுடைய தேவன் வயிறு, அவர்களுடைய மகிமை அவர்களுடைய இலச்சையே, அவர்கள் பூமிக்கடுத்தவைகளைச் சிந்திக்கிறார்கள்."));
tamsuperobject.biblemap.put("b50_3_20", new tamsuperobject(50,3,20,"நம்முடைய குடியிருப்போ பரலோகத்திலிருக்கிறது, அங்கேயிருந்து கர்த்தராயிருக்கிற இயேசுகிறிஸ்து என்னும் இரட்சகர் வர எதிர்பார்த்துக்கொண்டிருக்கிறோம்."));
tamsuperobject.biblemap.put("b50_3_21", new tamsuperobject(50,3,21,"அவர் எல்லாவற்றையும் தமக்குக் கீழ்ப்படுத்திக்கொள்ளத்தக்க தம்முடைய வல்லமையான செயலின்படியே, நம்முடைய அற்பமான சரீரத்தைத் தம்முடைய மகிமையான சரீரத்திற்கு ஒப்பாகமறுரூபப்படுத்துவார்."));
tamsuperobject.biblemap.put("b50_4_1", new tamsuperobject(50,4,1,"ஆதலால், எனக்குப் பிரியமும் வாஞ்சையுமான சகோதரரே, எனக்குச் சந்தோஷமும் கீரிடமுமானவர்களே, பிரியமானவர்களே, இந்தப்படியே கர்த்தருக்குள் நிலைத்திருங்கள்."));
tamsuperobject.biblemap.put("b50_4_2", new tamsuperobject(50,4,2,"கர்த்தருக்குள் ஒரே சிந்தையாயிருக்க எயோதியாளுக்கும் சிந்திகேயாளுக்கும் புத்திசொல்லுகிறேன்."));
tamsuperobject.biblemap.put("b50_4_3", new tamsuperobject(50,4,3,"அன்றியும், என் உத்தம கூட்டாளியே, அவர்களுக்கு உதவியாயிருக்கும்படி உன்னையும் வேண்டிக்கொள்ளுகிறேன்; அவர்கள் கிலேமெந்தோடும் மற்ற என் உடன்வேலையாட்களோடுங்கூடச் சுவிசேஷ விஷயத்தில் என்னோடேகூட மிகவும் பிரயாசப்பட்டார்கள், அவர்களுடைய நாமங்கள் ஜீவபுஸ்தகத்தில் இருக்கிறது."));
tamsuperobject.biblemap.put("b50_4_4", new tamsuperobject(50,4,4,"கர்த்தருக்குள் எப்பொழுதும் சந்தோஷமாயிருங்கள்; சந்தோஷமாயிருங்கள் என்று மறுபடியும் சொல்லுகிறேன்."));
tamsuperobject.biblemap.put("b50_4_5", new tamsuperobject(50,4,5,"உங்கள் சாந்தகுணம் எல்லா மனுஷருக்கும் தெரிந்திருப்பதாக. கர்த்தர் சமீபமாயிருக்கிறார்."));
tamsuperobject.biblemap.put("b50_4_6", new tamsuperobject(50,4,6,"நீங்கள் ஒன்றுக்குங் கவலைப்படாமல், எல்லாவற்றையுங்குறித்து உங்கள் விண்ணப்பங்களை ஸ்தோத்திரத்தோடே கூடிய ஜெபத்தினாலும் வேண்டுதலினாலும் தெரியப்படுத்துங்கள்."));
tamsuperobject.biblemap.put("b50_4_7", new tamsuperobject(50,4,7,"அப்பொழுது, எல்லாப் புத்திக்கும் மேலான தேவசமாதானம் உங்கள் இருதயங்களையும் உங்கள் சிந்தைகளையும் கிறிஸ்து இயேசுவுக்குள்ளாகக் காத்துக்கொள்ளும்."));
tamsuperobject.biblemap.put("b50_4_8", new tamsuperobject(50,4,8,"கடைசியாக, சகோதரரே, உண்மையுள்ளவைகளெவைகளோ, ஒழுக்கமுள்ளவைகளெவைகளோ, நீதியுள்ளவைகளெவைகளோ, கற்புள்ளவைகளெவைகளோ, அன்புள்ளவைகளெவைகளோ, நற்கீர்த்தியுள்ளவைகளெவைகளோ, புண்ணியம் எதுவோ, புகழ் எதுவோ அவைகளையே சிந்தித்துக்கொண்டிருங்கள்."));
tamsuperobject.biblemap.put("b50_4_9", new tamsuperobject(50,4,9,"நீங்கள் என்னிடத்தில் கற்றும் அடைந்தும் கேட்டும் கண்டும் இருக்கிறவைகளெவைகளோ, அவைகளையே செய்யுங்கள்; அப்பொழுது சமாதானத்தின் தேவன் உங்களோடிருப்பார்."));
tamsuperobject.biblemap.put("b50_4_10", new tamsuperobject(50,4,10,"என்னை விசாரிப்பதற்கு நீங்கள் இப்பொழுது மறுபடியும் மனமலர்ந்தபடியினாலே கர்த்தருக்குள் மிகவும் சந்தோஷப்பட்டேன்; இப்படிச் செய்ய எண்ணங்கொண்டிருந்தீர்கள், சமயம்மாத்திரம் உங்களுக்கு நேரிடவில்லை."));
tamsuperobject.biblemap.put("b50_4_11", new tamsuperobject(50,4,11,"என் குறைச்சலினால் நான் இப்படிச் சொல்லுகிறதில்லை; ஏனெனில் நான் எந்த நிலைமையிலிருந்தாலும் மனரம்மியமாயிருக்கக் கற்றுக்கொண்டேன்."));
tamsuperobject.biblemap.put("b50_4_12", new tamsuperobject(50,4,12,"தாழ்ந்திருக்கவும் எனக்குத் தெரியும், வாழ்ந்திருக்கவும் எனக்குத் தெரியும்; எவ்விடத்திலும் எல்லாவற்றிலும் திருப்பதியாயிருக்கவும் பட்டினியாயிருக்கவும், பரிபூரணமடையவும் குறைவுபடவும் போதிக்கப்பட்டேன்."));
tamsuperobject.biblemap.put("b50_4_13", new tamsuperobject(50,4,13,"என்னைப் பெலப்படுத்துகிற கிறிஸ்துவினாலே எல்லாவற்றையுஞ்செய்ய எனக்குப் பெலனுண்டு."));
tamsuperobject.biblemap.put("b50_4_14", new tamsuperobject(50,4,14,"ஆகிலும் நீங்கள் என் உபத்திரவத்தில் என்னோடே உடன்பட்டது நலமாயிருக்கிறது."));
tamsuperobject.biblemap.put("b50_4_15", new tamsuperobject(50,4,15,"மேலும், பிலிப்பியரே, சுவிசேஷத்தின் ஆரம்பத்திலே நான் மக்கெதோனியாவிலிருந்து புறப்பட்டபோது, கொடுக்கல் வாங்கல் காரியத்தில் நீங்கள்மாத்திரம் எனக்கு உடன்பட்டதேயல்லாமல், வேறொரு சபையும் உடன்படவில்லை என்று நீங்கள் அறிந்திருக்கிறீர்கள்."));
tamsuperobject.biblemap.put("b50_4_16", new tamsuperobject(50,4,16,"நான் தெசலோனிக்கேயிலிருந்தபோதும், என் குறைச்சலை நீக்கும்படி நீங்கள் இரண்டொருதரம் அனுப்பினீர்கள்."));
tamsuperobject.biblemap.put("b50_4_17", new tamsuperobject(50,4,17,"உபகாரத்தை நான் நாடாமல், உங்கள் கணக்குப் பலன் பெருகும்படியே நாடுகிறேன்."));
tamsuperobject.biblemap.put("b50_4_18", new tamsuperobject(50,4,18,"எல்லாம் எனக்குக் கிடைத்தது, பரிபூரணமும் உண்டாயிருக்கிறது; உங்களால் அனுப்பப்பட்டவைகளைச் சுகந்த வாசனையும் தேவனுக்குப் பிரியமான உகந்த பலியுமாக எப்பாப்பிரோதீத்துவின் கையில் வரப்பற்றிக்கொண்டபடியினால் நான் திருப்தியடைந்திருக்கிறேன்."));
tamsuperobject.biblemap.put("b50_4_19", new tamsuperobject(50,4,19,"என் தேவன் தம்முடைய ஐசுவரியத்தின்படி உங்கள் குறைவையெல்லாம் கிறிஸ்து இயேசுவுக்குள் மகிமையிலே நிறைவாக்குவார்."));
tamsuperobject.biblemap.put("b50_4_20", new tamsuperobject(50,4,20,"நம்முடைய பிதாவாகிய தேவனானவருக்கு என்றென்றைக்கும் மகிமை உண்டாவதாக. ஆமென்."));
tamsuperobject.biblemap.put("b50_4_21", new tamsuperobject(50,4,21,"கிறிஸ்து இயேசுவுக்குள்ளான பரிசுத்தவான்கள் யாவருக்கும் வாழ்த்துதல் சொல்லுங்கள். என்னோடிருக்கிற சகோதரர்கள் உங்களுக்கு வாழ்த்துதல் சொல்லுகிறார்கள்."));
tamsuperobject.biblemap.put("b50_4_22", new tamsuperobject(50,4,22,"பரிசுத்தவான்கள் அனைவரும், விசேஷமாக இராயனுடைய அரமனையிலுள்ளவர்களும் உங்களுக்கு வாழ்த்துதல் சொல்லுகிறார்கள்."));
tamsuperobject.biblemap.put("b50_4_23", new tamsuperobject(50,4,23,"நமது கர்த்தராகிய இயேசுகிறிஸ்துவினுடைய கிருபை உங்களனைவரோடுங்கூட இருப்பதாக. ஆமென்."));

}

}