trsuperobject.biblemap.put("b1_6_1", new trsuperobject(1,6,1,"Yeryüzünde insanlar çoğalmaya başladı, kızlar doğdu."));
trsuperobject.biblemap.put("b1_6_2", new trsuperobject(1,6,2,"İlahi varlıklarfı insan kızlarının güzelliğini görünce beğendikleriyle evlendiler."));
trsuperobject.biblemap.put("b1_6_4", new trsuperobject(1,6,4,"İlahi varlıklarınfı insan kızlarıyla evlenip çocuk sahibi oldukları günlerde ve daha sonra yeryüzünde Nefiller vardı. Bunlar eski çağ kahramanları, ünlü kişilerdi. Bunların melek ya da Şit soyundan gelen insanlar olduğu sanılıyor. gelir. Septuaginta bunu ‹‹Devler›› diye çevirir. Aynı sözcük Say.13:32-33 ayetlerinde de geçer."));
trsuperobject.biblemap.put("b1_6_6", new trsuperobject(1,6,6,"İnsanı yarattığına pişman oldu. Yüreği sızladı."));
trsuperobject.biblemap.put("b1_6_7", new trsuperobject(1,6,7,"‹‹Yarattığım insanları, hayvanları, sürüngenleri, kuşları yeryüzünden silip atacağım›› dedi, ‹‹Çünkü onları yarattığıma pişman oldum.››"));
trsuperobject.biblemap.put("b1_6_8", new trsuperobject(1,6,8,"Ama Nuh RABbin gözünde lütuf buldu."));
trsuperobject.biblemap.put("b1_6_9", new trsuperobject(1,6,9,"Nuhun öyküsü şöyledir: Nuh doğru bir insandı. Çağdaşları arasında kusursuz biriydi. Tanrı yolunda yürüdü."));
trsuperobject.biblemap.put("b1_6_10", new trsuperobject(1,6,10,"Üç oğlu vardı: Sam, Ham, Yafet."));
trsuperobject.biblemap.put("b1_6_11", new trsuperobject(1,6,11,"Tanrının gözünde yeryüzü bozulmuş, zorbalıkla dolmuştu."));
trsuperobject.biblemap.put("b1_6_12", new trsuperobject(1,6,12,"Tanrı yeryüzüne baktı ve her şeyin ne denli bozulduğunu gördü. Çünkü insanlar yoldan çıkmıştı."));
trsuperobject.biblemap.put("b1_6_13", new trsuperobject(1,6,13,"Tanrı Nuha, ‹‹İnsanlığa son vereceğim›› dedi, ‹‹Çünkü onlar yüzünden yeryüzü zorbalıkla doldu. Onlarla birlikte yeryüzünü de yok edeceğim."));
trsuperobject.biblemap.put("b1_6_14", new trsuperobject(1,6,14,"Kendine gofer ağacından bir gemi yap. İçini dışını ziftle, içeriye kamaralar yap."));
trsuperobject.biblemap.put("b1_6_15", new trsuperobject(1,6,15,"Gemiyi şöyle yapacaksın: Uzunluğu üç yüz, genişliği elli, yüksekliği otuz arşın olacak."));
trsuperobject.biblemap.put("b1_6_16", new trsuperobject(1,6,16,"Pencere de yap, boyu yukarıya doğru bir arşını bulsun. Kapıyı geminin yan tarafına koy. Alt, orta ve üst güverteler yap."));
trsuperobject.biblemap.put("b1_6_17", new trsuperobject(1,6,17,"Yeryüzüne tufan göndereceğim. Göklerin altında soluk alan bütün canlıları yok edeceğim. Yeryüzündeki her canlı ölecek."));
trsuperobject.biblemap.put("b1_6_18", new trsuperobject(1,6,18,"Ama seninle bir antlaşma yapacağım. Oğulların, karın, gelinlerinle birlikte gemiye bin."));
trsuperobject.biblemap.put("b1_6_19", new trsuperobject(1,6,19,"Sağ kalabilmeleri için her canlı türünden bir erkek, bir dişi olmak üzere birer çifti gemiye al."));
trsuperobject.biblemap.put("b1_6_20", new trsuperobject(1,6,20,"Çeşit çeşit kuşlar, hayvanlar, sürüngenler sağ kalmak için çifter çifter sana gelecekler."));
trsuperobject.biblemap.put("b1_6_21", new trsuperobject(1,6,21,"Yanına hem kendin, hem onlar için yenebilecek ne varsa al, ilerde yemek üzere depola.›› olduğu sanılıyor."));
trsuperobject.biblemap.put("b1_6_22", new trsuperobject(1,6,22,"Nuh Tanrı'nın bütün buyruklarını yerine getirdi."));
trsuperobject.biblemap.put("b1_7_2-3", new trsuperobject(1,7,2-3,"Yeryüzünde soyları tükenmesin diye, yanına temiz sayılan hayvanlardan erkek ve dişi olmak üzere yedişer çift, kirli sayılan hayvanlardan birer çift, kuşlardan yedişer çift al."));
trsuperobject.biblemap.put("b1_7_4", new trsuperobject(1,7,4,"Çünkü yedi gün sonra yeryüzüne kırk gün kırk gece yağmur yağdıracağım. Yarattığım her canlıyı yeryüzünden silip atacağım.››"));
trsuperobject.biblemap.put("b1_7_5", new trsuperobject(1,7,5,"Nuh RABbin bütün buyruklarını yerine getirdi."));
trsuperobject.biblemap.put("b1_7_6", new trsuperobject(1,7,6,"Yeryüzünde tufan koptuğunda Nuh altı yüz yaşındaydı."));
trsuperobject.biblemap.put("b1_7_7", new trsuperobject(1,7,7,"Nuh, oğulları, karısı, gelinleri tufandan kurtulmak için hep birlikte gemiye bindiler."));
trsuperobject.biblemap.put("b1_7_8-9", new trsuperobject(1,7,8-9,"Tanrının Nuha buyurduğu gibi temiz ve kirli sayılan her tür hayvan, kuş ve sürüngenden erkek ve dişi olmak üzere birer çift Nuha gelip gemiye bindiler."));
trsuperobject.biblemap.put("b1_7_10", new trsuperobject(1,7,10,"Yedi gün sonra tufan koptu."));
trsuperobject.biblemap.put("b1_7_11", new trsuperobject(1,7,11,"Nuh altı yüz yaşındayken, o yılın ikinci ayının on yedinci günü enginlerin bütün kaynakları fışkırdı, göklerin kapakları açıldı."));
trsuperobject.biblemap.put("b1_7_12", new trsuperobject(1,7,12,"Yeryüzüne kırk gün kırk gece yağmur yağdı."));
trsuperobject.biblemap.put("b1_7_13", new trsuperobject(1,7,13,"Nuh, oğulları Sam, Ham, Yafet, Nuhun karısıyla üç gelini tam o gün gemiye bindiler."));
trsuperobject.biblemap.put("b1_7_14", new trsuperobject(1,7,14,"Onlarla birlikte her tür hayvan -evcil hayvanların, sürüngenlerin, kuşların, uçan yaratıkların her türü- gemiye bindi."));
trsuperobject.biblemap.put("b1_7_15", new trsuperobject(1,7,15,"Soluk alan her tür canlı çifter çifter Nuhun yanına gelip gemiye bindi."));
trsuperobject.biblemap.put("b1_7_16", new trsuperobject(1,7,16,"Gemiye giren hayvanlar Tanrının Nuha buyurduğu gibi erkek ve dişiydi. RAB Nuhun ardından kapıyı kapadı."));
trsuperobject.biblemap.put("b1_7_17", new trsuperobject(1,7,17,"Tufan kırk gün sürdü. Çoğalan sular gemiyi yerden yukarı kaldırdı."));
trsuperobject.biblemap.put("b1_7_18", new trsuperobject(1,7,18,"Sular yükseldi, çoğaldıkça çoğaldı; gemi suyun üzerinde yüzmeye başladı."));
trsuperobject.biblemap.put("b1_7_19", new trsuperobject(1,7,19,"Sular öyle yükseldi ki, yeryüzündeki bütün yüksek dağlar su altında kaldı."));
trsuperobject.biblemap.put("b1_7_20", new trsuperobject(1,7,20,"Yükselen sular dağları on beş arşın aştı."));
trsuperobject.biblemap.put("b1_7_21-22", new trsuperobject(1,7,21-22,"Yeryüzünde yaşayan bütün canlılar yok oldu; kuşlar, evcil ve yabanıl hayvanlar, sürüngenler, insanlar, soluk alan bütün canlılar öldü."));
trsuperobject.biblemap.put("b1_7_23", new trsuperobject(1,7,23,"RAB insanlardan evcil hayvanlara, sürüngenlerden kuşlara dek bütün canlıları yok etti, yeryüzündeki her şey silinip gitti. Yalnız Nuhla gemidekiler kaldı."));
trsuperobject.biblemap.put("b1_7_24", new trsuperobject(1,7,24,"Sular yüz elli gün boyunca yeryüzünü kapladı."));
trsuperobject.biblemap.put("b1_8_1", new trsuperobject(1,8,1,"Sonra Tanrı Nuhu ve gemideki evcil ve yabanıl hayvanları anımsadı. Yeryüzünde bir rüzgar estirdi, sular alçalmaya başladı."));
trsuperobject.biblemap.put("b1_8_2", new trsuperobject(1,8,2,"Enginlerin kaynakları, göklerin kapakları kapandı. Yağmur dindi."));
trsuperobject.biblemap.put("b1_8_3", new trsuperobject(1,8,3,"Sular yeryüzünden çekilmeye başladı. Yüz elli gün geçtikten sonra sular azaldı."));
trsuperobject.biblemap.put("b1_8_4", new trsuperobject(1,8,4,"Gemi yedinci ayın on yedinci günü Ararat dağlarına oturdu."));
trsuperobject.biblemap.put("b1_8_5", new trsuperobject(1,8,5,"Sular onuncu aya kadar sürekli azaldı. Onuncu ayın birinde dağların doruğu göründü."));
trsuperobject.biblemap.put("b1_8_6", new trsuperobject(1,8,6,"Kırk gün sonra Nuh yapmış olduğu geminin penceresini açtı."));
trsuperobject.biblemap.put("b1_8_7", new trsuperobject(1,8,7,"Kuzgunu dışarı gönderdi. Kuzgun sular kuruyuncaya kadar dönmedi, uçup durdu."));
trsuperobject.biblemap.put("b1_8_8", new trsuperobject(1,8,8,"Bunun üzerine Nuh suların yeryüzünden çekilip çekilmediğini anlamak için güvercini gönderdi."));
trsuperobject.biblemap.put("b1_8_9", new trsuperobject(1,8,9,"Güvercin konacak bir yer bulamadı, çünkü her yer suyla kaplıydı. Gemiye, Nuhun yanına döndü. Nuh uzanıp güvercini tuttu ve gemiye, yanına aldı."));
trsuperobject.biblemap.put("b1_8_10", new trsuperobject(1,8,10,"Yedi gün daha bekledi, sonra güvercini yine dışarı saldı."));
trsuperobject.biblemap.put("b1_8_11", new trsuperobject(1,8,11,"Güvercin gagasında yeni kopmuş bir zeytin yaprağıyla akşamleyin geri döndü. O zaman Nuh suların yeryüzünden çekilmiş olduğunu anladı."));
trsuperobject.biblemap.put("b1_8_12", new trsuperobject(1,8,12,"Yedi gün daha bekledikten sonra güvercini yine gönderdi. Bu kez güvercin geri dönmedi."));
trsuperobject.biblemap.put("b1_8_13", new trsuperobject(1,8,13,"Nuh altı yüz bir yaşındayken, birinci ayın birinde yeryüzündeki sular kurudu. Nuh geminin üstündeki kapağı kaldırınca toprağın kurumuş olduğunu gördü."));
trsuperobject.biblemap.put("b1_8_14", new trsuperobject(1,8,14,"İkinci ayın yirmi yedinci günü toprak tümüyle kurumuştu."));
trsuperobject.biblemap.put("b1_8_15-16", new trsuperobject(1,8,15-16,"Tanrı Nuha, ‹‹Karın, oğulların ve gelinlerinle birlikte gemiden çık›› dedi,"));
trsuperobject.biblemap.put("b1_8_17", new trsuperobject(1,8,17,"‹‹Kendinle birlikte bütün canlıları, kuşları, hayvanları, sürüngenleri de çıkar. Üresinler, verimli olsunlar, yeryüzünde çoğalsınlar.››"));
trsuperobject.biblemap.put("b1_8_18", new trsuperobject(1,8,18,"Nuh karısı, oğulları ve gelinleriyle birlikte gemiden çıktı."));
trsuperobject.biblemap.put("b1_8_19", new trsuperobject(1,8,19,"Bütün hayvanlar, sürüngenler, kuşlar, yeryüzünde yaşayan her tür canlı da gemiyi terk etti."));
trsuperobject.biblemap.put("b1_8_20", new trsuperobject(1,8,20,"Nuh RABbe bir sunak yaptı. Orada bütün temiz sayılan hayvanlarla kuşlardan yakmalık sunular sundu."));
trsuperobject.biblemap.put("b1_8_21", new trsuperobject(1,8,21,"Güzel kokudan hoşnut olan RAB içinden şöyle dedi: ‹‹İnsanlar yüzünden yeryüzünü bir daha lanetlemeyeceğim. Çünkü insan yüreğindeki eğilimler çocukluğundan beri kötüdür. Şimdi yaptığım gibi bütün canlıları bir daha yok etmeyeceğim."));
trsuperobject.biblemap.put("b1_8_22", new trsuperobject(1,8,22,"‹‹Dünya durdukçaEkin ekmek, biçmek,Sıcak, soğuk,Yaz, kış,Gece, gündüz hep var olacaktır.››"));
trsuperobject.biblemap.put("b1_9_1", new trsuperobject(1,9,1,"Tanrı, Nuhu ve oğullarını kutsayarak, ‹‹Verimli olun, çoğalıp yeryüzünü doldurun›› dedi,"));
trsuperobject.biblemap.put("b1_9_2", new trsuperobject(1,9,2,"‹‹Yerdeki hayvanların, gökteki kuşların tümü sizden korkup ürkecek. Yeryüzündeki bütün canlılar, denizdeki bütün balıklar sizin yönetiminize verilmiştir."));
trsuperobject.biblemap.put("b1_9_3", new trsuperobject(1,9,3,"Bütün canlılar size yiyecek olacak. Yeşil bitkiler gibi, hepsini size veriyorum."));
trsuperobject.biblemap.put("b1_9_4", new trsuperobject(1,9,4,"‹‹Yalnız kanlı et yemeyeceksiniz, çünkü kan canı içerir."));
trsuperobject.biblemap.put("b1_9_5", new trsuperobject(1,9,5,"Sizin de kanınız dökülürse, hakkınızı kesinlikle arayacağım. Her hayvandan hesabını soracağım. Her insandan, kardeşinin canına kıyan herkesten hakkınızı arayacağım."));
trsuperobject.biblemap.put("b1_9_6", new trsuperobject(1,9,6,"‹‹Kim insan kanı dökerse,Kendi kanı da insan tarafından dökülecektir.Çünkü Tanrı insanı kendi suretinde yarattı."));
trsuperobject.biblemap.put("b1_9_7", new trsuperobject(1,9,7,"Verimli olun, çoğalın.Yeryüzünde üreyin, artın.››"));
trsuperobject.biblemap.put("b1_9_8", new trsuperobject(1,9,8,"Tanrı Nuha ve oğullarına şöyle dedi:"));
trsuperobject.biblemap.put("b1_9_9-10", new trsuperobject(1,9,9-10,"‹‹Sizinle ve gelecek kuşaklarınızla, sizinle birlikteki bütün canlılarla -kuşlar, evcil ve yabanıl hayvanlar, gemiden çıkan bütün hayvanlarla- antlaşmamı sürdürmek istiyorum."));
trsuperobject.biblemap.put("b1_9_11", new trsuperobject(1,9,11,"Sizinle antlaşmamı sürdüreceğim: Bir daha tufanla bütün canlılar yok olmayacak. Yeryüzünü yok eden tufan bir daha olmayacak.››"));
trsuperobject.biblemap.put("b1_9_12", new trsuperobject(1,9,12,"Tanrı şöyle sürdürdü konuşmasını: ‹‹Sizinle ve bütün canlılarla kuşaklar boyu sonsuza dek sürecek antlaşmamın belirtisi şu olacak:"));
trsuperobject.biblemap.put("b1_9_13", new trsuperobject(1,9,13,"Yayımı bulutlara yerleştireceğim ve bu, yeryüzüyle aramdaki antlaşmanın belirtisi olacak."));
trsuperobject.biblemap.put("b1_9_14", new trsuperobject(1,9,14,"Yeryüzüne ne zaman bulut göndersem, yayım bulutların arasında ne zaman görünse,"));
