trsuperobject.biblemap.put("b1_9_16", new trsuperobject(1,9,16,"Ne zaman bulutlarda yay görünse, ona bakıp yeryüzünde yaşayan bütün canlılarla yaptığım sonsuza dek geçerli antlaşmayı anımsayacağım.››"));
