trsuperobject.biblemap.put("b1_18_19", new trsuperobject(1,18,19,"Doğru ve adil olanı yaparak yolumda yürümeyi oğullarına ve soyuna buyursun diye İbrahimi seçtim. Öyle ki, ona verdiğim sözü yerine getireyim.››"));
trsuperobject.biblemap.put("b1_18_20", new trsuperobject(1,18,20,"Sonra İbrahime, ‹‹Sodom ve Gomora büyük suçlama altında›› dedi, ‹‹Günahları çok ağır."));
trsuperobject.biblemap.put("b1_18_21", new trsuperobject(1,18,21,"Onun için inip bakacağım. Duyduğum suçlamalar doğru mu, değil mi göreceğim. Bunları yapıp yapmadıklarını anlayacağım.››"));
trsuperobject.biblemap.put("b1_18_22", new trsuperobject(1,18,22,"Adamlar oradan ayrılıp Sodoma doğru gittiler. Ama İbrahim RABbin huzurunda kaldı."));
trsuperobject.biblemap.put("b1_18_23", new trsuperobject(1,18,23,"RABbe yaklaşarak, ‹‹Haksızla birlikte haklıyı da mı yok edeceksin?›› diye sordu,"));
trsuperobject.biblemap.put("b1_18_24", new trsuperobject(1,18,24,"‹‹Kentte elli doğru kişi var diyelim. Orayı gerçekten yok edecek misin? İçindeki elli doğru kişinin hatırı için kenti bağışlamayacak mısın?"));
trsuperobject.biblemap.put("b1_18_25", new trsuperobject(1,18,25,"Senden uzak olsun bu. Haklıyı, haksızı aynı kefeye koyarak haksızın yanında haklıyı da öldürmek senden uzak olsun. Bütün dünyayı yargılayan adil olmalı.›› din bilginlerine göre ‹‹RAB İbrahimin önünde kaldı.››"));
trsuperobject.biblemap.put("b1_18_26", new trsuperobject(1,18,26,"RAB, ‹‹Eğer Sodomda elli doğru kişi bulursam, onların hatırına bütün kenti bağışlayacağım›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_18_27", new trsuperobject(1,18,27,"İbrahim, ‹‹Ben toz ve külüm, bir hiçim›› dedi, ‹‹Ama seninle konuşma yürekliliğini göstereceğim."));
trsuperobject.biblemap.put("b1_18_28", new trsuperobject(1,18,28,"Kırk beş doğru kişi var diyelim, beş kişi için bütün kenti yok mu edeceksin?›› RAB, ‹‹Eğer kentte kırk beş doğru kişi bulursam, orayı yok etmeyeceğim›› dedi."));
trsuperobject.biblemap.put("b1_18_29", new trsuperobject(1,18,29,"İbrahim yine sordu: ‹‹Ya kırk kişi bulursan?›› RAB, ‹‹O kırk kişinin hatırı için hiçbir şey yapmayacağım›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_18_30", new trsuperobject(1,18,30,"İbrahim, ‹‹Ya Rab, öfkelenme ama, otuz kişi var diyelim?›› dedi. RAB, ‹‹Otuz kişi bulursam, kente dokunmayacağım›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_18_31", new trsuperobject(1,18,31,"İbrahim, ‹‹Ya Rab, lütfen konuşma yürekliliğimi bağışla›› dedi, ‹‹Eğer yirmi kişi bulursan?›› RAB, ‹‹Yirmi kişinin hatırı için kenti yok etmeyeceğim›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_18_32", new trsuperobject(1,18,32,"İbrahim, ‹‹Ya Rab, öfkelenme ama, bir kez daha konuşacağım›› dedi, ‹‹Eğer on kişi bulursan?›› RAB, ‹‹On kişinin hatırı için kenti yok etmeyeceğim›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_18_33", new trsuperobject(1,18,33,"RAB İbrahim'le konuşmasını bitirince oradan ayrıldı, İbrahim de çadırına döndü."));
trsuperobject.biblemap.put("b1_19_1", new trsuperobject(1,19,1,"İki melek akşamleyin Sodoma vardılar. Lut kentin kapısında oturuyordu. Onları görür görmez karşılamak için ayağa kalktı. Yere kapanarak,"));
trsuperobject.biblemap.put("b1_19_2", new trsuperobject(1,19,2,"‹‹Efendilerim›› dedi, ‹‹Kulunuzun evine buyurun. Ayaklarınızı yıkayın, geceyi bizde geçirin. Sonra erkenden kalkıp yolunuza devam edersiniz.›› Melekler, ‹‹Olmaz›› dediler, ‹‹Geceyi kent meydanında geçireceğiz.››"));
trsuperobject.biblemap.put("b1_19_3", new trsuperobject(1,19,3,"Ama Lut çok diretti. Sonunda onunla birlikte evine gittiler. Lut onlara yemek hazırladı, mayasız ekmek pişirdi. Yediler."));
trsuperobject.biblemap.put("b1_19_4", new trsuperobject(1,19,4,"Onlar yatmadan, kentin erkekleri -Sodomun her mahallesinden genç yaşlı bütün erkekler- evi sardı."));
trsuperobject.biblemap.put("b1_19_5", new trsuperobject(1,19,5,"Luta seslenerek, ‹‹Bu gece sana gelen adamlar nerede?›› diye sordular, ‹‹Getir onları da yatalım.››"));
trsuperobject.biblemap.put("b1_19_6", new trsuperobject(1,19,6,"Lut dışarı çıktı, arkasından kapıyı kapadı."));
trsuperobject.biblemap.put("b1_19_7", new trsuperobject(1,19,7,"‹‹Kardeşler, lütfen bu kötülüğü yapmayın›› dedi,"));
trsuperobject.biblemap.put("b1_19_8", new trsuperobject(1,19,8,"‹‹Erkek yüzü görmemiş iki kızım var. Size onları getireyim, ne isterseniz yapın. Yeter ki, bu adamlara dokunmayın. Çünkü onlar konuğumdur, çatımın altına geldiler.››"));
trsuperobject.biblemap.put("b1_19_9", new trsuperobject(1,19,9,"Adamlar, ‹‹Çekil önümüzden!›› diye karşılık verdiler, ‹‹Adam buraya dışardan geldi, şimdi yargıçlık taslıyor! Sana daha beterini yaparız.›› Lutu ite kaka kapıyı kırmaya davrandılar."));
trsuperobject.biblemap.put("b1_19_10", new trsuperobject(1,19,10,"Ama içerdeki adamlar uzanıp Lutu evin içine, yanlarına aldılar ve kapıyı kapadılar."));
trsuperobject.biblemap.put("b1_19_11", new trsuperobject(1,19,11,"Kapıya dayanan adamları, büyük küçük hepsini kör ettiler. Öyle ki, adamlar kapıyı bulamaz oldu."));
trsuperobject.biblemap.put("b1_19_12", new trsuperobject(1,19,12,"İçerdeki iki adam Luta, ‹‹Senin burada başka kimin var?›› diye sordular, ‹‹Oğullarını, kızlarını, damatlarını, kentte sana ait kim varsa hepsini dışarı çıkar."));
trsuperobject.biblemap.put("b1_19_13", new trsuperobject(1,19,13,"Çünkü burayı yok edeceğiz. RAB bu halk hakkında birçok kötü suçlama duydu, kenti yok etmek için bizi gönderdi.››"));
trsuperobject.biblemap.put("b1_19_14", new trsuperobject(1,19,14,"Lut dışarı çıktı ve kızlarıyla evlenecek olan adamlara, ‹‹Hemen buradan uzaklaşın!›› dedi, ‹‹Çünkü RAB bu kenti yok etmek üzere.›› Ne var ki damat adayları onun şaka yaptığını sandılar."));
trsuperobject.biblemap.put("b1_19_15", new trsuperobject(1,19,15,"Tan ağarırken melekler Luta, ‹‹Karınla iki kızını al, hemen buradan uzaklaş›› diye üstelediler, ‹‹Yoksa kent cezasını bulurken sen de canından olursun.››"));
trsuperobject.biblemap.put("b1_19_16", new trsuperobject(1,19,16,"Lut ağır davrandı, ama RAB ona acıdı. Adamlar Lutla karısının ve iki kızının elinden tutup onları kentin dışına çıkardılar."));
trsuperobject.biblemap.put("b1_19_17", new trsuperobject(1,19,17,"Kent dışına çıkınca, adamlardan biri Luta, ‹‹Kaç, canını kurtar, arkana bakma›› dedi, ‹‹Bu ovanın hiçbir yerinde durma. Dağa kaç, yoksa ölür gidersin.››"));
trsuperobject.biblemap.put("b1_19_18", new trsuperobject(1,19,18,"Lut, ‹‹Aman, efendim!›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b1_19_19", new trsuperobject(1,19,19,"‹‹Ben kulunuzdan hoşnut kaldınız, canımı kurtarmakla bana büyük iyilik yaptınız. Ama dağa kaçamam. Çünkü felaket bana yetişir, ölürüm."));
trsuperobject.biblemap.put("b1_19_20", new trsuperobject(1,19,20,"İşte, şurada kaçabileceğim yakın bir kent var, küçücük bir kent. İzin verin, oraya kaçıp canımı kurtarayım. Zaten küçücük bir kent.››"));
trsuperobject.biblemap.put("b1_19_21", new trsuperobject(1,19,21,"Adamlardan biri, ‹‹Peki, dileğini kabul ediyorum›› dedi, ‹‹O kenti yıkmayacağım."));
trsuperobject.biblemap.put("b1_19_22", new trsuperobject(1,19,22,"Çabuk ol, hemen kaç! Çünkü sen oraya varmadan bir şey yapamam.›› Bu yüzden o kente Soar adı verildi."));
trsuperobject.biblemap.put("b1_19_23", new trsuperobject(1,19,23,"Lut Soara vardığında güneş doğmuştu."));
trsuperobject.biblemap.put("b1_19_24", new trsuperobject(1,19,24,"RAB Sodom ve Gomoranın üzerine gökten ateşli kükürt yağdırdı."));
trsuperobject.biblemap.put("b1_19_25", new trsuperobject(1,19,25,"Bu kentleri, bütün ovayı, oradaki insanların hepsini ve bütün bitkileri yok etti."));
trsuperobject.biblemap.put("b1_19_26", new trsuperobject(1,19,26,"Ancak Lutun peşisıra gelen karısı dönüp geriye bakınca tuz kesildi."));
trsuperobject.biblemap.put("b1_19_27", new trsuperobject(1,19,27,"İbrahim sabah erkenden kalkıp önceki gün RABbin huzurunda durduğu yere gitti."));
trsuperobject.biblemap.put("b1_19_28", new trsuperobject(1,19,28,"Sodom ve Gomoraya ve bütün ovaya baktı. Yerden, tüten bir ocak gibi duman yükseliyordu."));
trsuperobject.biblemap.put("b1_19_29", new trsuperobject(1,19,29,"Tanrı ovadaki kentleri yok ederken İbrahimi anımsamış ve Lutun yaşadığı kentleri yok ederken Lutu bu felaketin dışına çıkarmıştı."));
trsuperobject.biblemap.put("b1_19_30", new trsuperobject(1,19,30,"Lut Soarda kalmaktan korkuyordu. Bu yüzden iki kızıyla kentten ayrılarak dağa yerleşti, onlarla birlikte bir mağarada yaşamaya başladı."));
trsuperobject.biblemap.put("b1_19_31", new trsuperobject(1,19,31,"Büyük kızı küçüğüne, ‹‹Babamız yaşlı›› dedi, ‹‹Dünya geleneklerine uygun biçimde burada bizimle yatabilecek bir erkek yok."));
trsuperobject.biblemap.put("b1_19_32", new trsuperobject(1,19,32,"Gel, babamıza şarap içirelim, soyumuzu yaşatmak için onunla yatalım.››"));
trsuperobject.biblemap.put("b1_19_33", new trsuperobject(1,19,33,"O gece babalarına şarap içirdiler. Büyük kız gidip babasıyla yattı. Ancak Lut yatıp kalktığının farkında değildi."));
trsuperobject.biblemap.put("b1_19_34", new trsuperobject(1,19,34,"Ertesi gün büyük kız küçüğüne, ‹‹Dün gece babamla yattım›› dedi, ‹‹Bu gece de ona şarap içirelim. Soyumuzu yaşatmak için sen de onunla yat.››"));
trsuperobject.biblemap.put("b1_19_35", new trsuperobject(1,19,35,"O gece de babalarına şarap içirdiler ve küçük kız babasıyla yattı. Ama Lut yatıp kalktığının farkında değildi."));
trsuperobject.biblemap.put("b1_19_36", new trsuperobject(1,19,36,"Böylece Lutun iki kızı da öz babalarından hamile kaldılar."));
trsuperobject.biblemap.put("b1_19_37", new trsuperobject(1,19,37,"Büyük kız bir erkek çocuk doğurdu, ona Moav adını verdi. Moav bugünkü Moavlıların atasıdır."));
trsuperobject.biblemap.put("b1_19_38", new trsuperobject(1,19,38,"Küçük kızın da bir oğlu oldu, adını Ben-Ammi koydu. O da bugünkü Ammonlular'ın atasıdır."));
trsuperobject.biblemap.put("b1_20_1", new trsuperobject(1,20,1,"İbrahim Mamreden Negeve göçerek Kadeş ve Sur kentlerinin arasına yerleşti. Sonra geçici bir süre Gerarda kaldı."));
trsuperobject.biblemap.put("b1_20_2", new trsuperobject(1,20,2,"Karısı Sara için, ‹‹Bu kadın kızkardeşimdir›› dedi. Bunun üzerine Gerar Kralı Avimelek adam gönderip Sarayı getirtti."));
trsuperobject.biblemap.put("b1_20_3", new trsuperobject(1,20,3,"Ama Tanrı gece düşünde Avimeleke görünerek, ‹‹Bu kadını aldığın için öleceksin›› dedi, ‹‹Çünkü o evli bir kadın.››"));
trsuperobject.biblemap.put("b1_20_4", new trsuperobject(1,20,4,"Avimelek henüz Saraya dokunmamıştı. ‹‹Ya RAB›› dedi, ‹‹Suçsuz bir ulusu mu yok edeceksin?"));
trsuperobject.biblemap.put("b1_20_5", new trsuperobject(1,20,5,"İbrahimin kendisi bana, ‹Bu kadın kızkardeşimdir› demedi mi? Kadın da İbrahim için, ‹O kardeşimdir› dedi. Ben temiz vicdanla, suçsuz ellerimle yaptım bunu.››"));
trsuperobject.biblemap.put("b1_20_6", new trsuperobject(1,20,6,"Tanrı, düşünde ona, ‹‹Bunu temiz vicdanla yaptığını biliyorum›› diye yanıtladı, ‹‹Ben de seni bu yüzden bana karşı günah işlemekten alıkoydum, kadına dokunmana izin vermedim."));
trsuperobject.biblemap.put("b1_20_7", new trsuperobject(1,20,7,"Şimdi kadını kocasına geri ver. Çünkü o bir peygamberdir. Senin için dua eder, ölmezsin. Ama kadını geri vermezsen, sen de sana ait olan herkes de ölecek, bilesin.››"));
trsuperobject.biblemap.put("b1_20_8", new trsuperobject(1,20,8,"Avimelek sabah erkenden kalktı, bütün adamlarını çağırarak olup biteni anlattı. Adamlar dehşete düştü."));
trsuperobject.biblemap.put("b1_20_9", new trsuperobject(1,20,9,"Avimelek İbrahimi çağırtarak, ‹‹Ne yaptın bize?›› dedi, ‹‹Sana ne haksızlık ettim ki, beni ve krallığımı bu büyük günaha sürükledin? Bana bu yaptığın yapılacak iş değil.››"));
trsuperobject.biblemap.put("b1_20_10", new trsuperobject(1,20,10,"Sonra, ‹‹Amacın neydi, niçin yaptın bunu?›› diye sordu."));
trsuperobject.biblemap.put("b1_20_11", new trsuperobject(1,20,11,"İbrahim, ‹‹Çünkü burada hiç Tanrı korkusu yok›› diye yanıtladı, ‹‹Karım yüzünden beni öldürebilirler diye düşündüm."));
trsuperobject.biblemap.put("b1_20_12", new trsuperobject(1,20,12,"Üstelik, Sara gerçekten kızkardeşimdir. Babamız bir, annemiz ayrıdır. Onunla evlendim."));
trsuperobject.biblemap.put("b1_20_13", new trsuperobject(1,20,13,"Tanrı beni babamın evinden gurbete gönderdiği zaman karıma, ‹Bana sevgini şöyle göstereceksin: Gideceğimiz her yerde kardeşin olduğumu söyle› dedim.››"));
trsuperobject.biblemap.put("b1_20_14", new trsuperobject(1,20,14,"Avimelek İbrahime karısı Sarayı geri verdi. Bunun yanısıra ona davar, sığır, köleler, cariyeler de verdi."));
trsuperobject.biblemap.put("b1_20_15", new trsuperobject(1,20,15,"İbrahime, ‹‹İşte ülkem önünde, nereye istersen oraya yerleş›› dedi."));
trsuperobject.biblemap.put("b1_20_16", new trsuperobject(1,20,16,"Saraya da, ‹‹Kardeşine bin parça gümüş veriyorum›› dedi, ‹‹Yanındakilere karşı senin suçsuz olduğunu gösteren bir kanıttır bu. Herkes suçsuz olduğunu bilsin.››"));
trsuperobject.biblemap.put("b1_20_17", new trsuperobject(1,20,17,"İbrahim Tanrıya dua etti ve Tanrı Avimelekle karısına, cariyelerine şifa verdi. Çocuk sahibi oldular."));
trsuperobject.biblemap.put("b1_20_18", new trsuperobject(1,20,18,"Çünkü İbrahim'in karısı Sara yüzünden RAB Avimelek'in evindeki kadınların hamile kalmasını engellemişti."));
trsuperobject.biblemap.put("b1_21_2", new trsuperobject(1,21,2,"Sara hamile kaldı; İbrahimin yaşlılık döneminde, tam Tanrının belirttiği zamanda ona bir erkek çocuk doğurdu."));
trsuperobject.biblemap.put("b1_21_3", new trsuperobject(1,21,3,"İbrahim Saranın doğurduğu çocuğa İshakfı adını verdi."));
trsuperobject.biblemap.put("b1_21_4", new trsuperobject(1,21,4,"Tanrının kendisine buyurduğu gibi oğlu İshakı sekiz günlükken sünnet etti."));
trsuperobject.biblemap.put("b1_21_5", new trsuperobject(1,21,5,"İshak doğduğunda İbrahim yüz yaşındaydı."));
trsuperobject.biblemap.put("b1_21_6", new trsuperobject(1,21,6,"Sara, ‹‹Tanrı yüzümü güldürdü›› dedi, ‹‹Bunu duyan herkes benimle birlikte gülecek."));
trsuperobject.biblemap.put("b1_21_7", new trsuperobject(1,21,7,"Kim İbrahime Sara çocuk emzirecek derdi? Bu yaşında ona bir oğul doğurdum.››"));
trsuperobject.biblemap.put("b1_21_8", new trsuperobject(1,21,8,"Çocuk büyüdü. Sütten kesildiği gün İbrahim büyük bir şölen verdi."));
trsuperobject.biblemap.put("b1_21_9", new trsuperobject(1,21,9,"Ne var ki Sara, Mısırlı Hacerin İbrahimden olma oğlu İsmailin alay ettiğini görünce,"));
trsuperobject.biblemap.put("b1_21_10", new trsuperobject(1,21,10,"İbrahime, ‹‹Bu cariyeyle oğlunu kov›› dedi, ‹‹Bu cariyenin oğlu, oğlum İshakın mirasına ortak olmasın.››"));
trsuperobject.biblemap.put("b1_21_11", new trsuperobject(1,21,11,"Bu İbrahimi çok üzdü, çünkü İsmail de öz oğluydu."));
trsuperobject.biblemap.put("b1_21_12", new trsuperobject(1,21,12,"Ancak Tanrı İbrahime, ‹‹Oğlunla cariyen için üzülme›› dedi, ‹‹Sara ne derse, onu yap. Çünkü senin soyun İshakla sürecektir."));
trsuperobject.biblemap.put("b1_21_13", new trsuperobject(1,21,13,"Cariyenin oğlundan da bir ulus yaratacağım, çünkü o da senin soyun.››"));
trsuperobject.biblemap.put("b1_21_14", new trsuperobject(1,21,14,"İbrahim sabah erkenden kalktı, biraz yiyecek, bir tulum da su hazırlayıp Hacerin omuzuna attı, çocuğunu da verip onu gönderdi. Hacer Beer-Şeva Çölüne gitti, orada bir süre dolaştı."));
trsuperobject.biblemap.put("b1_21_15", new trsuperobject(1,21,15,"Tulumdaki su tükenince, oğlunu bir çalının altına bıraktı."));
trsuperobject.biblemap.put("b1_21_16", new trsuperobject(1,21,16,"Yaklaşık bir ok atımı uzaklaşıp, ‹‹Oğlumun ölümünü görmeyeyim›› diyerek onun karşısına oturup hıçkıra hıçkıra ağladı."));
trsuperobject.biblemap.put("b1_21_17", new trsuperobject(1,21,17,"Tanrı çocuğun sesini duydu. Tanrının meleği göklerden Hacere, ‹‹Nen var, Hacer?›› diye seslendi, ‹‹Korkma! Çünkü Tanrı çocuğun sesini duydu."));
trsuperobject.biblemap.put("b1_21_18", new trsuperobject(1,21,18,"Kalk, oğlunu kaldır, elini tut. Onu büyük bir ulus yapacağım.››"));
trsuperobject.biblemap.put("b1_21_19", new trsuperobject(1,21,19,"Sonra Tanrı Hacerin gözlerini açtı, Hacer bir kuyu gördü. Gidip tulumunu doldurdu, oğluna içirdi."));
trsuperobject.biblemap.put("b1_21_20", new trsuperobject(1,21,20,"Çocuk büyürken Tanrı onunlaydı. Çocuk çölde yaşadı ve okçu oldu."));
trsuperobject.biblemap.put("b1_21_21", new trsuperobject(1,21,21,"Paran Çölünde yaşarken annesi ona Mısırlı bir kadın aldı."));
trsuperobject.biblemap.put("b1_21_22", new trsuperobject(1,21,22,"O sırada Avimelekle ordusunun komutanı Fikol İbrahime, ‹‹Yaptığın her şeyde Tanrı seninle›› dediler,"));
trsuperobject.biblemap.put("b1_21_23", new trsuperobject(1,21,23,"‹‹Onun için, Tanrının önünde bana, oğluma ve soyuma haksız davranmayacağına ant iç. Bana ve konuk olarak yaşadığın bu ülkeye, benim sana yaptığım gibi iyi davran.››"));
trsuperobject.biblemap.put("b1_21_24", new trsuperobject(1,21,24,"İbrahim, ‹‹Ant içerim›› dedi."));
trsuperobject.biblemap.put("b1_21_25", new trsuperobject(1,21,25,"İbrahim Avimeleke bir kuyuyu zorla ele geçiren adamlarından yakındı."));
trsuperobject.biblemap.put("b1_21_26", new trsuperobject(1,21,26,"Avimelek, ‹‹Bunu kimin yaptığını bilmiyorum›› diye yanıtladı, ‹‹Sen de bana söylemedin, ilk kez duyuyorum.››"));
trsuperobject.biblemap.put("b1_21_27", new trsuperobject(1,21,27,"Daha sonra İbrahim Avimeleke davar ve sığır verdi. Böylece ikisi bir antlaşma yaptılar."));
trsuperobject.biblemap.put("b1_21_28", new trsuperobject(1,21,28,"İbrahim sürüsünden yedi dişi kuzu ayırdı."));
trsuperobject.biblemap.put("b1_21_29", new trsuperobject(1,21,29,"Avimelek, ‹‹Bunun anlamı ne, niçin bu yedi dişi kuzuyu ayırdın?›› diye sordu."));
trsuperobject.biblemap.put("b1_21_30", new trsuperobject(1,21,30,"İbrahim, ‹‹Bu yedi dişi kuzuyu benim elimden almalısın›› diye yanıtladı, ‹‹Kuyuyu benim açtığımın kanıtı olsun.››"));
trsuperobject.biblemap.put("b1_21_31", new trsuperobject(1,21,31,"Bu yüzden oraya Beer-Şeva adı verildi. Çünkü ikisi orada ant içmişlerdi."));
trsuperobject.biblemap.put("b1_21_32", new trsuperobject(1,21,32,"Beer-Şevada yapılan bu antlaşmadan sonra Avimelek, ordusunun komutanı Fikolla birlikte Filist yöresine geri döndü."));
trsuperobject.biblemap.put("b1_21_33", new trsuperobject(1,21,33,"İbrahim Beer-Şevada bir ılgın ağacı dikti; orada RABbi, ölümsüz Tanrıyı adıyla çağırdı."));
trsuperobject.biblemap.put("b1_21_34", new trsuperobject(1,21,34,"Filist yöresinde konuk olarak uzun süre yaşadı."));
trsuperobject.biblemap.put("b1_22_1", new trsuperobject(1,22,1,"Daha sonra Tanrı İbrahimi denedi. ‹‹İbrahim!›› diye seslendi. İbrahim, ‹‹Buradayım!›› dedi."));
trsuperobject.biblemap.put("b1_22_2", new trsuperobject(1,22,2,"Tanrı, ‹‹İshakı, sevdiğin biricik oğlunu al, Moriya bölgesine git›› dedi, ‹‹Orada sana göstereceğim bir dağda oğlunu yakmalık sunu olarak sun.››"));
trsuperobject.biblemap.put("b1_22_3", new trsuperobject(1,22,3,"İbrahim sabah erkenden kalktı, eşeğine palan vurdu. Yanına uşaklarından ikisini ve oğlu İshakı aldı. Yakmalık sunu için odun yardıktan sonra, Tanrının kendisine belirttiği yere doğru yola çıktı."));
trsuperobject.biblemap.put("b1_22_4", new trsuperobject(1,22,4,"Üçüncü gün gideceği yeri uzaktan gördü."));
trsuperobject.biblemap.put("b1_22_5", new trsuperobject(1,22,5,"Uşaklarına, ‹‹Siz burada, eşeğin yanında kalın›› dedi, ‹‹Tapınmak için oğlumla birlikte oraya gidip döneceğiz.››"));
trsuperobject.biblemap.put("b1_22_6-7", new trsuperobject(1,22,6-7,"Yakmalık sunu için yardığı odunları oğlu İshaka yükledi. Ateşi ve bıçağı kendisi aldı. Birlikte giderlerken İshak İbrahime, ‹‹Baba!›› dedi. İbrahim, ‹‹Evet, oğlum!›› diye yanıtladı. İshak, ‹‹Ateşle odun burada, ama yakmalık sunu kuzusu nerede?›› diye sordu."));
trsuperobject.biblemap.put("b1_22_8", new trsuperobject(1,22,8,"İbrahim, ‹‹Oğlum, yakmalık sunu için kuzuyu Tanrı kendisi sağlayacak›› dedi. İkisi birlikte yürümeye devam ettiler."));
trsuperobject.biblemap.put("b1_22_9", new trsuperobject(1,22,9,"Tanrının kendisine belirttiği yere varınca İbrahim bir sunak yaptı, üzerine odun dizdi. Oğlu İshakı bağlayıp sunaktaki odunların üzerine yatırdı."));
trsuperobject.biblemap.put("b1_22_10", new trsuperobject(1,22,10,"Onu boğazlamak için uzanıp bıçağı aldı."));
trsuperobject.biblemap.put("b1_22_11", new trsuperobject(1,22,11,"Ama RABbin meleği göklerden, ‹‹İbrahim, İbrahim!›› diye seslendi. İbrahim, ‹‹İşte buradayım!›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_22_12", new trsuperobject(1,22,12,"Melek, ‹‹Çocuğa dokunma›› dedi, ‹‹Ona hiçbir şey yapma. Şimdi Tanrıdan korktuğunu anladım, biricik oğlunu benden esirgemedin.››"));
trsuperobject.biblemap.put("b1_22_13", new trsuperobject(1,22,13,"İbrahim çevresine bakınca, boynuzları sık çalılara takılmış bir koç gördü. Gidip koçu getirdi. Oğlunun yerine onu yakmalık sunu olarak sundu."));
trsuperobject.biblemap.put("b1_22_14", new trsuperobject(1,22,14,"Oraya ‹‹Yahve yire›› adını verdi. ‹‹RABbin dağında sağlanacaktır›› sözü bu yüzden bugün de söyleniyor."));
trsuperobject.biblemap.put("b1_22_15", new trsuperobject(1,22,15,"RABbin meleği göklerden İbrahime ikinci kez seslendi:"));
trsuperobject.biblemap.put("b1_22_16", new trsuperobject(1,22,16,"‹‹RAB diyor ki, kendi üzerime ant içiyorum. Bunu yaptığın için, biricik oğlunu esirgemediğin için"));
trsuperobject.biblemap.put("b1_22_17", new trsuperobject(1,22,17,"seni fazlasıyla kutsayacağım; soyunu göklerin yıldızları, kıyıların kumu kadar çoğaltacağım. Soyun düşmanlarının kentlerini mülk edinecek."));
trsuperobject.biblemap.put("b1_22_18", new trsuperobject(1,22,18,"Soyunun aracılığıyla yeryüzündeki bütün uluslar kutsanacak. Çünkü sözümü dinledin.››"));
trsuperobject.biblemap.put("b1_22_19", new trsuperobject(1,22,19,"Sonra İbrahim uşaklarının yanına döndü. Birlikte yola çıkıp Beer-Şevaya gittiler. İbrahim Beer-Şevada kaldı."));
trsuperobject.biblemap.put("b1_22_20", new trsuperobject(1,22,20,"Bir süre sonra İbrahime, ‹‹Milka, kardeşin Nahora çocuklar doğurdu›› diye haber verdiler,"));
trsuperobject.biblemap.put("b1_22_21", new trsuperobject(1,22,21,"‹‹İlk oğlu Ûs, kardeşi Bûz, Kemuel -Aramın babası-"));
trsuperobject.biblemap.put("b1_22_22", new trsuperobject(1,22,22,"Keset, Hazo, Pildaş, Yidlaf, Betuel.››"));
trsuperobject.biblemap.put("b1_22_23", new trsuperobject(1,22,23,"Betuel Rebekanın babası oldu. Bu sekiz çocuğu İbrahimin kardeşi Nahora Milka doğurdu."));
trsuperobject.biblemap.put("b1_22_24", new trsuperobject(1,22,24,"Reuma adındaki cariyesi de Nahor'a Tevah, Gaham, Tahaş ve Maaka'yı doğurdu."));
trsuperobject.biblemap.put("b1_23_1", new trsuperobject(1,23,1,"Sara yüz yirmi yedi yıl yaşadı. Ömrü bu kadardı."));
trsuperobject.biblemap.put("b1_23_2", new trsuperobject(1,23,2,"Kenan ülkesinde, bugün Hevron denilen Kiryat-Arbada öldü. İbrahim yas tutmak, ağlamak için Saranın ölüsünün başına gitti."));
trsuperobject.biblemap.put("b1_23_3", new trsuperobject(1,23,3,"Sonra karısının ölüsünün başından kalkıp Hititlere,"));
trsuperobject.biblemap.put("b1_23_4", new trsuperobject(1,23,4,"‹‹Ben aranızda konuk ve yabancıyım›› dedi, ‹‹Bana mezar yapabileceğim bir toprak satın. Ölümü kaldırıp gömeyim.››"));
trsuperobject.biblemap.put("b1_23_5-6", new trsuperobject(1,23,5-6,"Hititler, ‹‹Efendim, bizi dinle›› diye yanıtladılar, ‹‹Sen aramızda güçlü bir beysin. Ölünü mezarlarımızın en iyisine göm. Ölünü gömmen için kimse senden mezarını esirgemez.››"));
trsuperobject.biblemap.put("b1_23_7", new trsuperobject(1,23,7,"İbrahim, ülke halkı olan Hititlerin önünde eğilerek,"));
trsuperobject.biblemap.put("b1_23_8", new trsuperobject(1,23,8,"‹‹Eğer ölümü gömmemi istiyorsanız, benim için Sohar oğlu Efrona ricada bulunun›› dedi,"));
trsuperobject.biblemap.put("b1_23_9", new trsuperobject(1,23,9,"‹‹Tarlasının dibindeki Makpela Mağarasını bana satsın. Fiyatı neyse huzurunuzda eksiksiz ödeyip orayı mezarlık yapacağım.››"));
trsuperobject.biblemap.put("b1_23_10", new trsuperobject(1,23,10,"Hititli Efron halkının arasında oturuyordu. Kent kapısında toplanan herkesin duyacağı biçimde,"));
trsuperobject.biblemap.put("b1_23_11", new trsuperobject(1,23,11,"‹‹Hayır, efendim!›› diye karşılık verdi, ‹‹Beni dinle, mağarayla birlikte tarlayı da sana veriyorum. Halkımın huzurunda onu sana veriyorum. Ölünü göm.››"));
trsuperobject.biblemap.put("b1_23_12", new trsuperobject(1,23,12,"İbrahim ülke halkının önünde eğildi."));
trsuperobject.biblemap.put("b1_23_13", new trsuperobject(1,23,13,"Herkesin duyacağı biçimde Efrona, ‹‹Lütfen beni dinle›› dedi, ‹‹Tarlanın parasını ödeyeyim. Parayı kabul et ki, ölümü oraya gömeyim.››"));
trsuperobject.biblemap.put("b1_23_14-15", new trsuperobject(1,23,14-15,"Efron, ‹‹Efendim, beni dinle›› diye karşılık verdi, ‹‹Aramızda dört yüz şekel gümüşün sözü mü olur? Ölünü göm.››"));
trsuperobject.biblemap.put("b1_23_16", new trsuperobject(1,23,16,"İbrahim Efronun önerisini kabul etti. Efronun Hititlerin önünde sözünü ettiği dört yüz şekel gümüşü tüccarların ağırlık ölçülerine göre tarttı."));
trsuperobject.biblemap.put("b1_23_17-18", new trsuperobject(1,23,17-18,"Böylece Efronun Mamre yakınında Makpeladaki tarlası, çevresindeki bütün ağaçlarla ve içindeki mağarayla birlikte, kent kapısında toplanan Hititlerin huzurunda İbrahimin mülkü kabul edildi."));
trsuperobject.biblemap.put("b1_23_19", new trsuperobject(1,23,19,"İbrahim karısı Sarayı Kenan ülkesinde Mamreye -Hevrona- yakın Makpela Tarlasındaki mağaraya gömdü."));
trsuperobject.biblemap.put("b1_23_20", new trsuperobject(1,23,20,"Hititler tarlayı içindeki mağarayla birlikte İbrahim'in mezarlık yeri olarak onayladılar."));
trsuperobject.biblemap.put("b1_24_1", new trsuperobject(1,24,1,"İbrahim kocamış, iyice yaşlanmıştı. RAB onu her yönden kutsamıştı."));
trsuperobject.biblemap.put("b1_24_2", new trsuperobject(1,24,2,"İbrahim, evindeki en yaşlı ve her şeyden sorumlu uşağına, ‹‹Elini uyluğumun altına koy›› dedi,"));
trsuperobject.biblemap.put("b1_24_3", new trsuperobject(1,24,3,"‹‹Yerin göğün Tanrısı RABbin adıyla ant içmeni istiyorum. Aralarında yaşadığım Kenanlılardan oğluma kız almayacaksın."));
trsuperobject.biblemap.put("b1_24_4", new trsuperobject(1,24,4,"Oğlum İshaka kız almak için benim ülkeme, akrabalarımın yanına gideceksin.›› gösterirdi."));
trsuperobject.biblemap.put("b1_24_5", new trsuperobject(1,24,5,"Uşak, ‹‹Ya kız benimle bu ülkeye gelmek istemezse?›› diye sordu, ‹‹O zaman oğlunu geldiğin ülkeye götüreyim mi?››"));
trsuperobject.biblemap.put("b1_24_6", new trsuperobject(1,24,6,"İbrahim, ‹‹Sakın oğlumu oraya götürme!›› dedi,"));
trsuperobject.biblemap.put("b1_24_7", new trsuperobject(1,24,7,"‹‹Beni baba ocağından, doğduğum ülkeden getiren, ‹Bu toprakları senin soyuna vereceğim› diyerek ant içen Göklerin Tanrısı RAB senin önünden meleğini gönderecek. Böylece oradan oğluma bir kız alabileceksin."));
trsuperobject.biblemap.put("b1_24_8", new trsuperobject(1,24,8,"Eğer kız seninle gelmek istemezse, içtiğin ant seni bağlamaz. Yalnız, oğlumu oraya götürme.››"));
trsuperobject.biblemap.put("b1_24_9", new trsuperobject(1,24,9,"Bunun üzerine uşak elini efendisi İbrahimin uyluğunun altına koyarak bu konuda ant içti."));
trsuperobject.biblemap.put("b1_24_10", new trsuperobject(1,24,10,"Sonra efendisinden on deve alarak en iyi eşyalarla birlikte yola çıktı; Aram-Naharayime, Nahorun yaşadığı kente gitti."));
trsuperobject.biblemap.put("b1_24_11", new trsuperobject(1,24,11,"Develerini kentin dışındaki kuyunun yanına çöktürdü. Akşamüzeriydi, kadınların su almak için dışarı çıkacakları zamandı."));
trsuperobject.biblemap.put("b1_24_12", new trsuperobject(1,24,12,"Uşak, ‹‹Ya RAB, efendim İbrahimin Tanrısı, yalvarırım bugün beni başarılı kıl›› diye dua etti, ‹‹Efendim İbrahime iyilik et."));
trsuperobject.biblemap.put("b1_24_13", new trsuperobject(1,24,13,"İşte, pınarın başında bekliyorum. Kentin kızları su almaya geliyorlar."));
trsuperobject.biblemap.put("b1_24_14", new trsuperobject(1,24,14,"Birine, ‹Lütfen testini indir, biraz su içeyim› diyeceğim. O da, ‹Sen iç, ben de develerine içireyim› derse, bileceğim ki o kız kulun İshak için seçtiğin kızdır. Böylece efendime iyilik ettiğini anlayacağım.››"));
trsuperobject.biblemap.put("b1_24_15", new trsuperobject(1,24,15,"O duasını bitirmeden, İbrahimin kardeşi Nahorla karısı Milkanın oğlu Betuelin kızı Rebeka, omuzunda su testisiyle dışarı çıktı."));
trsuperobject.biblemap.put("b1_24_16", new trsuperobject(1,24,16,"Çok güzel bir genç kızdı. Ona erkek eli değmemişti. Pınara gitti, testisini doldurup geri döndü."));
trsuperobject.biblemap.put("b1_24_17", new trsuperobject(1,24,17,"Uşak onu karşılamaya koştu, ‹‹Lütfen testinden biraz su ver, içeyim›› dedi."));
trsuperobject.biblemap.put("b1_24_18", new trsuperobject(1,24,18,"Rebeka, ‹‹İç, efendim›› diyerek hemen testisini indirdi, içmesi için ona uzattı."));
trsuperobject.biblemap.put("b1_24_19", new trsuperobject(1,24,19,"Ona su verdikten sonra, ‹‹Develerin için de su çekeyim›› dedi, ‹‹Kanıncaya kadar içsinler.››"));
trsuperobject.biblemap.put("b1_24_20", new trsuperobject(1,24,20,"Çabucak suyu hayvanların teknesine boşalttı, yine su çekmek için kuyuya koştu. Adamın bütün develeri için su çekti."));
trsuperobject.biblemap.put("b1_24_21", new trsuperobject(1,24,21,"Adam RABbin yolunu açıp açmadığını anlamak için sessizce genç kızı süzüyordu."));
trsuperobject.biblemap.put("b1_24_22", new trsuperobject(1,24,22,"Develer su içtikten sonra, adam bir beka ağırlığında altın bir burun halkasıyla on şekel ağırlığında iki altın bilezik çıkardı."));
trsuperobject.biblemap.put("b1_24_23", new trsuperobject(1,24,23,"‹‹Lütfen söyle, kimin kızısın sen?›› diye sordu, ‹‹Babanın evinde geceyi geçirebileceğimiz bir yer var mı?››"));
trsuperobject.biblemap.put("b1_24_24", new trsuperobject(1,24,24,"Kız, ‹‹Milkayla Nahorun oğlu Betuelin kızıyım›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b1_24_25", new trsuperobject(1,24,25,"‹‹Bizde saman ve yem bol, geceyi geçirebileceğiniz yer de var.››"));
trsuperobject.biblemap.put("b1_24_26", new trsuperobject(1,24,26,"Adam eğilip RABbe tapındı."));
trsuperobject.biblemap.put("b1_24_27", new trsuperobject(1,24,27,"‹‹Efendim İbrahimin Tanrısı RABbe övgüler olsun›› dedi, ‹‹Sevgisini, sadakatini efendimden esirgemedi. Efendimin akrabalarının evine giden yolu bana gösterdi.››"));
trsuperobject.biblemap.put("b1_24_28", new trsuperobject(1,24,28,"Kız annesinin evine koşup olanları anlattı."));
trsuperobject.biblemap.put("b1_24_29", new trsuperobject(1,24,29,"Rebekanın Lavan adında bir kardeşi vardı. Lavan pınarın başındaki adama doğru koştu."));
trsuperobject.biblemap.put("b1_24_30", new trsuperobject(1,24,30,"Kızkardeşinin burnundaki halkayı, kollarındaki bilezikleri görmüştü. Rebeka adamın kendisine söylediklerini de anlatınca, Lavan adamın yanına gitti. Adam pınarın başında, develerinin yanında duruyordu."));
trsuperobject.biblemap.put("b1_24_31", new trsuperobject(1,24,31,"Lavan, ‹‹Eve buyur, ey RABbin kutsadığı adam›› dedi, ‹‹Niçin dışarıda bekliyorsun? Senin için oda, develerin için yer hazırladım.››"));
trsuperobject.biblemap.put("b1_24_32", new trsuperobject(1,24,32,"Böylece adam eve girdi. Lavan develerin kolanlarını çözdü, onlara saman ve yem verdi. Adamla yanındakilere ayaklarını yıkamaları için su getirdi."));
trsuperobject.biblemap.put("b1_24_33", new trsuperobject(1,24,33,"Önüne yemek konulunca, adam, ‹‹Niçin geldiğimi anlatmadan yemek yemeyeceğim›› dedi. Lavan, ‹‹Öyleyse anlat›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_24_34", new trsuperobject(1,24,34,"Adam, ‹‹Ben İbrahimin uşağıyım›› dedi,"));
trsuperobject.biblemap.put("b1_24_35", new trsuperobject(1,24,35,"‹‹RAB efendimi alabildiğine kutsadı. Onu zengin etti. Ona davar, sığır, altın, gümüş, erkek ve kadın köleler, develer, eşekler verdi."));
trsuperobject.biblemap.put("b1_24_36", new trsuperobject(1,24,36,"Karısı Sara ileri yaşta efendime bir oğul doğurdu. Efendim sahip olduğu her şeyi oğluna verdi."));
trsuperobject.biblemap.put("b1_24_37-38", new trsuperobject(1,24,37-38,"‹Ülkelerinde yaşadığım Kenanlılardan oğluma kız almayacaksın. Oğluma kız almak için babamın ailesine, akrabalarımın yanına gideceksin› diyerek bana ant içirdi."));
trsuperobject.biblemap.put("b1_24_39", new trsuperobject(1,24,39,"‹‹Efendime, ‹Ya kız benimle gelmezse?› diye sordum."));
trsuperobject.biblemap.put("b1_24_40", new trsuperobject(1,24,40,"‹‹Efendim, ‹Yolunda yürüdüğüm RAB meleğini seninle gönderecek, yolunu açacak› dedi, ‹Akrabalarımdan, babamın ailesinden oğluma bir kız getireceksin."));
trsuperobject.biblemap.put("b1_24_41", new trsuperobject(1,24,41,"İçtiğin anttan ancak akrabalarımın yanına vardığında sana kızı vermezlerse, evet, ancak o zaman özgür olabilirsin.›"));
trsuperobject.biblemap.put("b1_24_42", new trsuperobject(1,24,42,"‹‹Bugün pınarın başına geldiğimde şöyle dua ettim: ‹Ya RAB, efendim İbrahimin Tanrısı, yalvarırım yolumu aç."));
trsuperobject.biblemap.put("b1_24_43", new trsuperobject(1,24,43,"İşte pınarın başında bekliyorum. Su almaya gelen kızlardan birine, lütfen testinden bana biraz su ver, içeyim, diyeceğim."));
trsuperobject.biblemap.put("b1_24_44", new trsuperobject(1,24,44,"O da, sen iç, develerin için de su çekeyim derse, anlayacağım ki efendimin oğlu için RABbin seçtiği kız odur.›"));
trsuperobject.biblemap.put("b1_24_45", new trsuperobject(1,24,45,"‹‹Ben içimden dua ederken, Rebeka omuzunda su testisiyle dışarı çıktı. Pınar başına gidip su aldı. Ona, ‹Lütfen, biraz su ver, içeyim› dedim."));
trsuperobject.biblemap.put("b1_24_46", new trsuperobject(1,24,46,"‹‹Rebeka hemen testisini omuzundan indirdi, ‹İç efendim› dedi, ‹Ben de develerine içireyim.› Ben içtim. Develere de su verdi."));
trsuperobject.biblemap.put("b1_24_47", new trsuperobject(1,24,47,"‹‹Ona, ‹Kimin kızısın sen?› diye sordum. ‹‹Milkayla Nahorun oğlu Betuelin kızıyım› dedi. ‹‹Bunun üzerine burnuna halka, kollarına bilezik taktım."));
trsuperobject.biblemap.put("b1_24_48", new trsuperobject(1,24,48,"Eğilip RABbe tapındım. Efendimin oğluna kardeşinin torununu almak için bana doğru yolu gösteren efendim İbrahimin Tanrısı RABbe övgüler sundum."));
trsuperobject.biblemap.put("b1_24_49", new trsuperobject(1,24,49,"Şimdi efendime sevgi ve sadakat mı göstereceksiniz, yoksa olmaz mı diyeceksiniz, bana bildirin. Öyle ki, ben de ne yapacağıma karar vereyim.››"));
trsuperobject.biblemap.put("b1_24_50", new trsuperobject(1,24,50,"Lavanla Betuel, ‹‹Bu RABbin işi›› diye karşılık verdiler, ‹‹Biz sana ne iyi, ne kötü diyebiliriz."));
trsuperobject.biblemap.put("b1_24_51", new trsuperobject(1,24,51,"İşte Rebeka burada. Al götür. RABbin buyurduğu gibi efendinin oğluna karı olsun.››"));
trsuperobject.biblemap.put("b1_24_52", new trsuperobject(1,24,52,"İbrahimin uşağı bu sözleri duyunca, yere kapanarak RABbe tapındı."));
trsuperobject.biblemap.put("b1_24_53", new trsuperobject(1,24,53,"Rebekaya altın, gümüş takımlar, giysiler, kardeşiyle annesine de değerli eşyalar çıkarıp verdi."));
trsuperobject.biblemap.put("b1_24_54", new trsuperobject(1,24,54,"Sonra yanındakilerle birlikte yedi, içti. Geceyi orada geçirdiler. Sabah kalkınca İbrahimin uşağı, ‹‹Beni yolcu edin, efendime döneyim›› dedi."));
trsuperobject.biblemap.put("b1_24_55", new trsuperobject(1,24,55,"Rebekanın kardeşiyle annesi, ‹‹Bırak kız on gün kadar bizimle kalsın, sonra gidersin›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b1_24_56", new trsuperobject(1,24,56,"Adam, ‹‹Madem RAB yolumu açtı, beni geciktirmeyin›› dedi, ‹‹İzin verin, efendime döneyim.››"));
trsuperobject.biblemap.put("b1_24_57", new trsuperobject(1,24,57,"‹‹Kızı çağırıp ona soralım›› dediler."));
trsuperobject.biblemap.put("b1_24_58", new trsuperobject(1,24,58,"Rebekayı çağırıp, ‹‹Bu adamla gitmek istiyor musun?›› diye sordular. Rebeka, ‹‹İstiyorum›› dedi."));
trsuperobject.biblemap.put("b1_24_59", new trsuperobject(1,24,59,"Böylece Rebekayla dadısını, İbrahimin uşağıyla adamlarını uğurlamaya çıktılar."));
trsuperobject.biblemap.put("b1_24_60", new trsuperobject(1,24,60,"Rebekayı şöyle kutsadılar:  ‹‹Ey kızkardeşimiz,Binlerce, on binlerce kişiye analık et,Soyun düşmanlarının kentlerini mülk edinsin.››"));
trsuperobject.biblemap.put("b1_24_61", new trsuperobject(1,24,61,"Rebekayla genç hizmetçileri hazırlanıp develere binerek İbrahimin uşağını izlediler. Uşak Rebekayı alıp oradan ayrıldı."));
trsuperobject.biblemap.put("b1_24_62", new trsuperobject(1,24,62,"İshak Beer-Lahay-Roiden gelmişti. Çünkü Negev bölgesinde yaşıyordu."));
trsuperobject.biblemap.put("b1_24_63", new trsuperobject(1,24,63,"Akşamüzeri düşünmek için tarlaya gitti. Başını kaldırdığında develerin yaklaştığını gördü."));
trsuperobject.biblemap.put("b1_24_64", new trsuperobject(1,24,64,"Rebeka İshakı görünce deveden indi,"));
trsuperobject.biblemap.put("b1_24_65", new trsuperobject(1,24,65,"İbrahimin uşağına, ‹‹Tarladan bizi karşılamaya gelen şu adam kim?›› diye sordu. Uşak, ‹‹Efendim›› diye karşılık verdi. Rebeka peçesini alıp yüzünü örttü."));
trsuperobject.biblemap.put("b1_24_66", new trsuperobject(1,24,66,"Uşak bütün yaptıklarını İshaka anlattı."));
trsuperobject.biblemap.put("b1_24_67", new trsuperobject(1,24,67,"İshak Rebeka'yı annesi Sara'nın yaşamış olduğu çadıra götürüp onunla evlendi. Böylece Rebeka İshak'ın karısı oldu. İshak onu sevdi. Annesinin ölümünden sonra onunla avunç buldu."));
trsuperobject.biblemap.put("b1_25_1", new trsuperobject(1,25,1,"İbrahim bir kadınla daha evlendi. Kadının adı Keturaydı."));
trsuperobject.biblemap.put("b1_25_2", new trsuperobject(1,25,2,"Ondan Zimran, Yokşan, Medan, Midyan, Yişbak, Şuah adlı çocukları oldu."));
trsuperobject.biblemap.put("b1_25_3", new trsuperobject(1,25,3,"Yokşandan da Şeva, Dedan oldu. Dedan soyundan Aşurlular, Letuşlular, Leumlular doğdu."));
trsuperobject.biblemap.put("b1_25_4", new trsuperobject(1,25,4,"Midyanın Efa, Efer, Hanok, Avida, Eldaa adlı oğulları oldu. Bunların hepsi Keturanın soyundandı."));
trsuperobject.biblemap.put("b1_25_5", new trsuperobject(1,25,5,"İbrahim sahip olduğu her şeyi İshaka bıraktı."));
trsuperobject.biblemap.put("b1_25_6", new trsuperobject(1,25,6,"Cariyelerinin oğullarına da armağanlar verdi. Kendisi sağken bu çocukları oğlu İshaktan uzaklaştırıp doğuya gönderdi."));
trsuperobject.biblemap.put("b1_25_7", new trsuperobject(1,25,7,"İbrahim yüz yetmiş beş yıl yaşadı. Ömrü bu kadardı."));
trsuperobject.biblemap.put("b1_25_8", new trsuperobject(1,25,8,"Kocamış, yaşama doymuş, iyice yaşlanmış olarak son soluğunu verdi. Ölüp atalarına kavuştu."));
trsuperobject.biblemap.put("b1_25_9", new trsuperobject(1,25,9,"Oğulları İshakla İsmail onu Hititli Sohar oğlu Efronun tarlasında Mamreye yakın Makpela Mağarasına gömdüler."));
trsuperobject.biblemap.put("b1_25_10", new trsuperobject(1,25,10,"İbrahim o tarlayı Hititlerden satın almıştı. Böylece İbrahimle karısı Sara oraya gömüldüler."));
trsuperobject.biblemap.put("b1_25_11", new trsuperobject(1,25,11,"Tanrı İbrahimin ölümünden sonra oğlu İshakı kutsadı. İshak Beer-Lahay-Roide yaşıyordu."));
trsuperobject.biblemap.put("b1_25_12", new trsuperobject(1,25,12,"Saranın cariyesi Mısırlı Hacerin İbrahime doğurduğu İsmailin öyküsü:"));
trsuperobject.biblemap.put("b1_25_13", new trsuperobject(1,25,13,"Doğum sırasına göre İsmailin oğullarının adları şunlardır: İlk oğlu Nevayot. Sonra Kedar, Adbeel, Mivsam,"));
trsuperobject.biblemap.put("b1_25_14", new trsuperobject(1,25,14,"Mişma, Duma, Massa,"));
trsuperobject.biblemap.put("b1_25_15", new trsuperobject(1,25,15,"Hadat, Tema, Yetur, Nafiş, Kedema gelir."));
trsuperobject.biblemap.put("b1_25_16", new trsuperobject(1,25,16,"İsmailin oğulları olan bu on iki bey oymakların atalarıydı. Köylerine, obalarına da bu adları verdiler."));
trsuperobject.biblemap.put("b1_25_17", new trsuperobject(1,25,17,"İsmail yüz otuz yedi yıl yaşadıktan sonra son soluğunu verdi. Ölüp halkına kavuştu."));
trsuperobject.biblemap.put("b1_25_18", new trsuperobject(1,25,18,"İsmailoğulları Aşura doğru giderken Mısır sınırı yakınında, Havila ile Şur arasındaki bölgeye yerleştiler. Kardeşlerinin yaşadığı yerin doğusuna yerleşmişlerdi."));
trsuperobject.biblemap.put("b1_25_19", new trsuperobject(1,25,19,"İbrahimin oğlu İshakın öyküsü:"));
trsuperobject.biblemap.put("b1_25_20", new trsuperobject(1,25,20,"İshak Aramlı Lavanın kızkardeşi, Paddan-Aramlı Betuelin kızı Rebekayla evlendiğinde kırk yaşındaydı."));
trsuperobject.biblemap.put("b1_25_21", new trsuperobject(1,25,21,"İshak karısı için RABbe yakardı, çünkü karısı kısırdı. RAB İshakın yakarışını yanıtladı, Rebeka hamile kaldı."));
trsuperobject.biblemap.put("b1_25_22", new trsuperobject(1,25,22,"Çocuklar karnında itişiyordu. Rebeka, ‹‹Nedir bu başıma gelen?›› diyerek RABbe danışmaya gitti."));
trsuperobject.biblemap.put("b1_25_23", new trsuperobject(1,25,23,"RAB onu şöyle yanıtladı:  ‹‹Rahminde iki ulus var,Senden iki ayrı halk doğacak,Biri öbüründen güçlü olacak,Büyüğü küçüğüne hizmet edecek.››"));
trsuperobject.biblemap.put("b1_25_24", new trsuperobject(1,25,24,"Doğum vakti gelince, Rebekanın ikiz oğulları oldu."));
trsuperobject.biblemap.put("b1_25_25", new trsuperobject(1,25,25,"İlk doğan oğlu kıpkırmızı ve tüylüydü; kırmızı bir cüppeyi andırıyordu. Adını Esav koydular."));
trsuperobject.biblemap.put("b1_25_26", new trsuperobject(1,25,26,"Sonra kardeşi doğdu. Eliyle Esavın topuğunu tutuyordu. Bu yüzden İshak ona Yakupfö adını verdi. Rebeka doğum yaptığında İshak altmış yaşındaydı."));
trsuperobject.biblemap.put("b1_25_27", new trsuperobject(1,25,27,"Çocuklar büyüdü. Esav kırları seven usta bir avcı oldu. Yakupsa hep çadırda oturan sakin bir adamdı."));
trsuperobject.biblemap.put("b1_25_28", new trsuperobject(1,25,28,"İshak Esavı daha çok severdi, çünkü onun getirdiği av etlerini yerdi. Rebeka ise Yakupu severdi."));
trsuperobject.biblemap.put("b1_25_29", new trsuperobject(1,25,29,"Bir gün Yakup çorba pişirirken Esav avdan geldi. Aç ve bitkindi."));
trsuperobject.biblemap.put("b1_25_30", new trsuperobject(1,25,30,"Yakupa, ‹‹Lütfen şu kızıl çorbadan biraz ver de içeyim. Aç ve bitkinim›› dedi. Bu nedenle ona Edom adı da verildi."));
trsuperobject.biblemap.put("b1_25_31", new trsuperobject(1,25,31,"Yakup, ‹‹Önce sen ilk oğulluk hakkını bana ver›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_25_32", new trsuperobject(1,25,32,"Esav, ‹‹Baksana, açlıktan ölmek üzereyim›› dedi, ‹‹İlk oğulluk hakkının bana ne yararı var?››"));
trsuperobject.biblemap.put("b1_25_33", new trsuperobject(1,25,33,"Yakup, ‹‹Önce ant iç›› dedi. Esav ant içerek ilk oğulluk hakkını Yakupa sattı."));
trsuperobject.biblemap.put("b1_25_34", new trsuperobject(1,25,34,"Yakup Esav'a ekmekle mercimek çorbası verdi. Esav yiyip içtikten sonra kalkıp gitti. Böylece Esav ilk oğulluk hakkını küçümsemiş oldu."));
trsuperobject.biblemap.put("b1_26_1", new trsuperobject(1,26,1,"İbrahimin yaşadığı dönemdeki kıtlıktan başka ülkede bir kıtlık daha oldu. İshak Gerara, Filist Kralı Avimelekin yanına gitti."));
trsuperobject.biblemap.put("b1_26_3", new trsuperobject(1,26,3,"Orada bir süre kal. Ben seninle olacak, seni kutsayacağım: Bütün bu toprakları sana ve soyuna vereceğim. Baban İbrahime ant içerek verdiğim sözü yerine getireceğim."));
trsuperobject.biblemap.put("b1_26_4", new trsuperobject(1,26,4,"Soyunu gökteki yıldızlar kadar çoğaltacağım. Bu ülkelerin tümünü onlara vereceğim. Yeryüzündeki bütün uluslar senin soyun aracılığıyla kutsanacak."));
trsuperobject.biblemap.put("b1_26_5", new trsuperobject(1,26,5,"Çünkü İbrahim sözümü dinledi. Uyarılarıma, buyruklarıma, kurallarıma, yasalarıma bağlı kaldı.››"));
trsuperobject.biblemap.put("b1_26_6", new trsuperobject(1,26,6,"Böylece İshak Gerarda kaldı."));
trsuperobject.biblemap.put("b1_26_7", new trsuperobject(1,26,7,"Yöre halkı karısıyla ilgili soru sorunca, ‹‹Kızkardeşimdir›› diyordu. Çünkü ‹‹Karımdır›› demekten korkuyordu. Rebeka yüzünden yöre halkı beni öldürebilir diye düşünüyordu. Çünkü Rebeka güzeldi."));
trsuperobject.biblemap.put("b1_26_8", new trsuperobject(1,26,8,"İshak orada uzun zaman kaldı. Bir gün Filist Kralı Avimelek, pencereden dışarı bakarken, İshakın karısı Rebekayı okşadığını gördü."));
trsuperobject.biblemap.put("b1_26_9", new trsuperobject(1,26,9,"İshakı çağırtarak, ‹‹Bu kadın gerçekte senin karın!›› dedi, ‹‹Neden kızkardeşin olduğunu söyledin?›› İshak, ‹‹Çünkü onun yüzünden canımdan olurum diye düşündüm›› dedi."));
trsuperobject.biblemap.put("b1_26_10", new trsuperobject(1,26,10,"Avimelek, ‹‹Nedir bize bu yaptığın?›› dedi, ‹‹Az kaldı halkımdan biri karınla yatacaktı. Bize suç işletecektin.››"));
trsuperobject.biblemap.put("b1_26_11", new trsuperobject(1,26,11,"Sonra bütün halka, ‹‹Kim bu adama ya da karısına dokunursa, kesinlikle öldürülecek›› diye buyruk verdi."));
trsuperobject.biblemap.put("b1_26_12", new trsuperobject(1,26,12,"İshak o ülkede ekin ekti ve o yıl ektiğinin yüz katını biçti. RAB onu kutsamıştı."));
trsuperobject.biblemap.put("b1_26_13", new trsuperobject(1,26,13,"İshak bolluğa kavuştu. Varlığı gittikçe büyüyordu. Çok zengin oldu."));
trsuperobject.biblemap.put("b1_26_14", new trsuperobject(1,26,14,"Sürülerle davar, sığır ve birçok uşak sahibi oldu. Filistliler onu kıskanmaya başladılar."));
trsuperobject.biblemap.put("b1_26_15", new trsuperobject(1,26,15,"Babası İbrahim yaşarken kölelerinin kazmış olduğu bütün kuyuları toprakla doldurup kapadılar."));
trsuperobject.biblemap.put("b1_26_16", new trsuperobject(1,26,16,"Avimelek İshaka, ‹‹Ülkemizden git›› dedi, ‹‹Çünkü gücün bizim gücümüzü aştı.››"));
trsuperobject.biblemap.put("b1_26_17", new trsuperobject(1,26,17,"İshak oradan ayrıldı. Gerar Vadisinde çadır kurup oraya yerleşti."));
trsuperobject.biblemap.put("b1_26_18", new trsuperobject(1,26,18,"Babası İbrahim yaşarken kazılmış olan kuyuları yeniden açtırdı. Çünkü Filistliler İbrahimin ölümünden sonra o kuyuları kapamışlardı. Kuyulara aynı adları, babasının vermiş olduğu adları verdi."));
trsuperobject.biblemap.put("b1_26_19", new trsuperobject(1,26,19,"İshakın köleleri vadide kuyu kazarken bir kaynak buldular."));
trsuperobject.biblemap.put("b1_26_20", new trsuperobject(1,26,20,"Gerarın çobanları, ‹‹Su bizim›› diyerek İshakın çobanlarıyla kavgaya tutuştular. İshak kendisiyle çekiştikleri için kuyuya Esek adını verdi."));
trsuperobject.biblemap.put("b1_26_21", new trsuperobject(1,26,21,"İshakın köleleri başka bir kuyu kazdılar. Bu kuyu yüzünden de kavga çıkınca İshak kuyuya Sitna adını verdi."));
trsuperobject.biblemap.put("b1_26_22", new trsuperobject(1,26,22,"Oradan ayrılıp başka bir yerde kuyu kazdırdı. Bu kuyu yüzünden kavga çıkmadı. Bu nedenle İshak ona Rehovotfş adını verdi. ‹‹RAB en sonunda bize rahatlık verdi›› dedi, ‹‹Bu ülkede verimli olacağız.››"));
trsuperobject.biblemap.put("b1_26_23", new trsuperobject(1,26,23,"İshak oradan Beer-Şevaya gitti."));
trsuperobject.biblemap.put("b1_26_24", new trsuperobject(1,26,24,"O gece RAB kendisine görünerek, ‹‹Ben baban İbrahimin Tanrısıyım, korkma›› dedi, ‹‹Seninle birlikteyim. Seni kutsayacak, kulum İbrahimin hatırı için soyunu çoğaltacağım.››"));
trsuperobject.biblemap.put("b1_26_25", new trsuperobject(1,26,25,"İshak orada bir sunak yaparak RABbi adıyla çağırdı. Çadırını oraya kurdu. Köleleri de orada bir kuyu kazdı."));
trsuperobject.biblemap.put("b1_26_26", new trsuperobject(1,26,26,"Avimelek, danışmanı Ahuzzat ve ordusunun komutanı Fikol ile birlikte, Gerardan İshakın yanına gitti."));
trsuperobject.biblemap.put("b1_26_27", new trsuperobject(1,26,27,"İshak onlara, ‹‹Niçin yanıma geldiniz?›› dedi, ‹‹Benden nefret ediyorsunuz. Üstelik beni ülkenizden kovdunuz.››"));
trsuperobject.biblemap.put("b1_26_28-29", new trsuperobject(1,26,28-29,"‹‹Açıkça gördük ki, RAB seninle›› diye yanıtladılar, ‹‹Onun için, aramızda ant olsun: Biz nasıl sana dokunmadıksa, hep iyi davranarak seni esenlik içinde gönderdikse, sen de bize kötülük etme. Bu konuda seninle anlaşalım. Sen şimdi RABbin kutsadığı bir adamsın.››"));
trsuperobject.biblemap.put("b1_26_30", new trsuperobject(1,26,30,"İshak onlara bir şölen verdi, yiyip içtiler."));
trsuperobject.biblemap.put("b1_26_31", new trsuperobject(1,26,31,"Sabah erkenden kalkıp karşılıklı ant içtiler. Sonra İshak onları yolcu etti. Esenlik içinde oradan ayrıldılar."));
trsuperobject.biblemap.put("b1_26_32", new trsuperobject(1,26,32,"Aynı gün İshakın köleleri gelip kazdıkları kuyu hakkında kendisine bilgi verdiler, ‹‹Su bulduk›› dediler."));
trsuperobject.biblemap.put("b1_26_33", new trsuperobject(1,26,33,"İshak kuyuya Şiva adını verdi. Bu yüzden kent bugüne kadar Beer-Şeva diye anılır."));
trsuperobject.biblemap.put("b1_26_34", new trsuperobject(1,26,34,"Esav kırk yaşında Hititli Beerinin kızı Yudit ve Hititli Elonun kızı Basematla evlendi."));
trsuperobject.biblemap.put("b1_26_35", new trsuperobject(1,26,35,"Bu kadınlar İshak'la Rebeka'nın başına dert oldular."));
trsuperobject.biblemap.put("b1_27_1", new trsuperobject(1,27,1,"İshak yaşlanmış, gözleri görmez olmuştu. Büyük oğlu Esavı çağırıp, ‹‹Oğlum!›› dedi. Esav, ‹‹Efendim!›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_27_2", new trsuperobject(1,27,2,"İshak, ‹‹Artık yaşlandım›› dedi, ‹‹Ne zaman öleceğimi bilmiyorum."));
trsuperobject.biblemap.put("b1_27_3", new trsuperobject(1,27,3,"Silahlarını -ok kılıfını, yayını- al, kırlara çıkıp benim için bir hayvan avla."));
trsuperobject.biblemap.put("b1_27_4", new trsuperobject(1,27,4,"Sevdiğim lezzetli bir yemek yap, bana getir yiyeyim. Ölmeden önce seni kutsayayım.››"));
trsuperobject.biblemap.put("b1_27_5", new trsuperobject(1,27,5,"İshak, oğlu Esavla konuşurken Rebeka onları dinliyordu. Esav avlanmak için kıra çıkınca,"));
trsuperobject.biblemap.put("b1_27_6", new trsuperobject(1,27,6,"Rebeka oğlu Yakupa şöyle dedi: ‹‹Dinle, babanın ağabeyin Esava söylediklerini duydum."));
