trsuperobject.biblemap.put("b1_29_32", new trsuperobject(1,29,32,"Lea hamile kalıp bir erkek çocuk doğurdu. Adını Ruben koydu. ‹‹Çünkü RAB mutsuzluğumu gördü›› dedi, ‹‹Kuşkusuz artık kocam beni sever.››"));
trsuperobject.biblemap.put("b1_29_33", new trsuperobject(1,29,33,"Yine hamile kaldı ve bir erkek çocuk daha doğurdu. ‹‹RAB sevilmediğimi duyduğu için bana bu çocuğu verdi›› diyerek adını Şimon koydu."));
trsuperobject.biblemap.put("b1_29_34", new trsuperobject(1,29,34,"Üçüncü kez hamile kalıp bir daha erkek çocuk doğurdu. ‹‹Artık kocam bana bağlanacak›› dedi, ‹‹Çünkü ona üç erkek çocuk doğurdum.›› Onun için çocuğa Levi adı verildi."));
trsuperobject.biblemap.put("b1_29_35", new trsuperobject(1,29,35,"Dördüncü kez hamile kaldı ve bir erkek çocuk daha doğurdu. ‹‹Bu kez RAB'be övgüler sunacağım›› dedi. Onun için çocuğa Yahuda adını verdi. Bir süre doğum yapmadı."));
trsuperobject.biblemap.put("b1_30_1", new trsuperobject(1,30,1,"Rahel Yakupa çocuk doğuramayınca, ablasını kıskanmaya başladı. Yakupa, ‹‹Bana çocuk ver, yoksa öleceğim›› dedi."));
trsuperobject.biblemap.put("b1_30_2", new trsuperobject(1,30,2,"Yakup Rahele öfkelendi. ‹‹Çocuk sahibi olmanı Tanrı engelliyor. Ben Tanrı değilim ki!›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_30_3", new trsuperobject(1,30,3,"Rahel, ‹‹İşte cariyem Bilha›› dedi, ‹‹Onunla yat, benim için çocuk doğursun, ben de aile kurayım.››"));
trsuperobject.biblemap.put("b1_30_4", new trsuperobject(1,30,4,"Rahel cariyesi Bilhayı eş olarak kocasına verdi. Yakup onunla yattı."));
trsuperobject.biblemap.put("b1_30_5", new trsuperobject(1,30,5,"Bilha hamile kalıp Yakupa bir erkek çocuk doğurdu."));
trsuperobject.biblemap.put("b1_30_6", new trsuperobject(1,30,6,"Rahel, ‹‹Tanrı beni haklı çıkardı›› dedi, ‹‹Yakarışımı duyup bana bir oğul verdi.›› Bu yüzden çocuğa Dan adını verdi."));
trsuperobject.biblemap.put("b1_30_7", new trsuperobject(1,30,7,"Rahelin cariyesi Bilha yine hamile kaldı ve Yakupa ikinci bir oğul doğurdu."));
trsuperobject.biblemap.put("b1_30_8", new trsuperobject(1,30,8,"Rahel, ‹‹Ablama karşı büyük savaşım verdim ve onu yendim›› diyerek çocuğa Naftalifç adını verdi."));
trsuperobject.biblemap.put("b1_30_9", new trsuperobject(1,30,9,"Lea artık doğum yapamadığını görünce, cariyesi Zilpayı Yakupa eş olarak verdi."));
trsuperobject.biblemap.put("b1_30_10", new trsuperobject(1,30,10,"Zilpa Yakupa bir erkek çocuk doğurdu."));
trsuperobject.biblemap.put("b1_30_11", new trsuperobject(1,30,11,"Lea, ‹‹Uğurum!›› diyerek çocuğa Gad adını verdi."));
trsuperobject.biblemap.put("b1_30_12", new trsuperobject(1,30,12,"Leanın cariyesi Zilpa Yakupa ikinci bir oğul doğurdu."));
trsuperobject.biblemap.put("b1_30_13", new trsuperobject(1,30,13,"Lea, ‹‹Mutluyum!›› dedi, ‹‹Kadınlar bana ‹Mutlu› diyecek.›› Ve çocuğa Aşer adını verdi."));
trsuperobject.biblemap.put("b1_30_14", new trsuperobject(1,30,14,"Ruben hasat mevsimi tarlaya gitti. Orada adamotu bulup annesi Leaya getirdi. Rahel Leaya, ‹‹Lütfen oğlunun getirdiği adamotundan bana da ver›› dedi. biçiminde köke sahip, yenildiğinde cinsel gücü artırdığına inanılan bir bitki."));
trsuperobject.biblemap.put("b1_30_15", new trsuperobject(1,30,15,"Lea, ‹‹Kocamı aldığın yetmez mi? Bir de oğlumun adamotunu mu istiyorsun?›› diye karşılık verdi. Rahel, ‹‹Öyle olsun›› dedi, ‹‹Oğlunun adamotuna karşılık kocam bu gece seninle yatsın.››"));
trsuperobject.biblemap.put("b1_30_16", new trsuperobject(1,30,16,"Akşamleyin Yakup tarladan dönerken Lea onu karşılamaya gitti. Yakupa, ‹‹Benimle yatacaksın›› dedi, ‹‹Oğlumun adamotuna karşılık bu gece benimsin.›› Yakup o gece onunla yattı."));
trsuperobject.biblemap.put("b1_30_17", new trsuperobject(1,30,17,"Tanrı Leanın duasını işitti. Lea hamile kalıp Yakupa beşinci oğlunu doğurdu."));
trsuperobject.biblemap.put("b1_30_18", new trsuperobject(1,30,18,"‹‹Cariyemi kocama verdiğim için Tanrı beni ödüllendirdi›› diyerek çocuğa İssakar adını verdi."));
trsuperobject.biblemap.put("b1_30_19", new trsuperobject(1,30,19,"Lea yine hamile kaldı ve Yakupa altıncı oğlunu doğurdu."));
trsuperobject.biblemap.put("b1_30_20", new trsuperobject(1,30,20,"‹‹Tanrı bana iyi bir armağan verdi›› dedi, ‹‹Artık kocam bana değer verir. Çünkü ona altı erkek çocuk doğurdum.›› Ve çocuğa Zevulun adını verdi."));
trsuperobject.biblemap.put("b1_30_21", new trsuperobject(1,30,21,"Bir süre sonra Lea bir kız doğurdu ve adını Dina koydu."));
trsuperobject.biblemap.put("b1_30_22", new trsuperobject(1,30,22,"Tanrı Raheli anımsadı, onun duasını işiterek çocuk sahibi olmasını sağladı."));
trsuperobject.biblemap.put("b1_30_23-24", new trsuperobject(1,30,23-24,"Rahel hamile kaldı ve bir erkek çocuk doğurdu. ‹‹Tanrı utancımı kaldırdı. RAB bana bir oğul daha versin!›› diyerek çocuğa Yusuffı adını verdi."));
trsuperobject.biblemap.put("b1_30_25", new trsuperobject(1,30,25,"Rahel Yusufu doğurduktan sonra Yakup Lavana, ‹‹Beni gönder, evime, topraklarıma gideyim›› dedi,"));
trsuperobject.biblemap.put("b1_30_26", new trsuperobject(1,30,26,"‹‹Hizmetime karşılık karılarımı, çocuklarımı ver de gideyim. Sana nasıl hizmet ettiğimi biliyorsun.››"));
trsuperobject.biblemap.put("b1_30_27", new trsuperobject(1,30,27,"Lavan, ‹‹Eğer benden hoşnutsan, burada kal›› dedi, ‹‹Çünkü fala bakarak anladım ki, RAB senin sayende beni kutsadı."));
trsuperobject.biblemap.put("b1_30_28", new trsuperobject(1,30,28,"Alacağın neyse söyle, ödeyeyim.››"));
trsuperobject.biblemap.put("b1_30_29", new trsuperobject(1,30,29,"Yakup, ‹‹Sana nasıl hizmet ettiğimi, sürülerine nasıl baktığımı biliyorsun›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b1_30_30", new trsuperobject(1,30,30,"‹‹Ben gelmeden önce malın azdı. Sayemde RAB seni kutsadı, malın gitgide arttı. Ya kendi evim için ne zaman çalışacağım?››"));
trsuperobject.biblemap.put("b1_30_31", new trsuperobject(1,30,31,"Lavan, ‹‹Sana ne vereyim?›› diye sordu. Yakup, ‹‹Bana bir şey verme›› diye yanıtladı, ‹‹Eğer şu önerimi kabul edersen, yine sürünü güder, hayvanlarına bakarım:"));
trsuperobject.biblemap.put("b1_30_32", new trsuperobject(1,30,32,"Bugün bütün sürülerini yoklayıp noktalı veya benekli koyunları, kara kuzuları, benekli veya noktalı keçileri ayırayım. Ücretim bu olsun."));
trsuperobject.biblemap.put("b1_30_33", new trsuperobject(1,30,33,"İleride bana verdiklerini denetlemeye geldiğinde, dürüst olup olmadığımı kolayca anlayabilirsin. Noktalı ve benekli olmayan keçilerim, kara olmayan kuzularım varsa, onları çalmışım demektir.››"));
trsuperobject.biblemap.put("b1_30_34", new trsuperobject(1,30,34,"Lavan, ‹‹Kabul, söylediğin gibi olsun›› dedi."));
trsuperobject.biblemap.put("b1_30_35", new trsuperobject(1,30,35,"Ama o gün çizgili ve benekli tekeleri, noktalı ve benekli keçileri, beyaz keçilerin hepsini, bütün kara kuzuları ayırıp oğullarına teslim etti."));
trsuperobject.biblemap.put("b1_30_36", new trsuperobject(1,30,36,"Sonra Yakuptan üç günlük yol kadar uzaklaştı. Yakup Lavanın kalan sürüsünü gütmeye devam etti."));
trsuperobject.biblemap.put("b1_30_37", new trsuperobject(1,30,37,"Yakup aselbent, badem, çınar ağaçlarından taze dallar kesti. Dalları soyarak beyaz çentikler açtı."));
trsuperobject.biblemap.put("b1_30_38", new trsuperobject(1,30,38,"Soyduğu çubukları koyunların önüne, su içtikleri yalaklara koydu. Koyunlar su içmeye gelince çiftleşiyorlardı."));
trsuperobject.biblemap.put("b1_30_39", new trsuperobject(1,30,39,"Çubukların önünde çiftleşince çizgili, noktalı, benekli yavrular doğuruyorlardı."));
trsuperobject.biblemap.put("b1_30_40", new trsuperobject(1,30,40,"Yakup kuzuları ayırıp sürülerin yüzünü Lavanın çizgili, kara hayvanlarına döndürüyordu. Kendi sürülerini ayrı tutuyor, Lavanınkilerle karıştırmıyordu."));
trsuperobject.biblemap.put("b1_30_41", new trsuperobject(1,30,41,"Sürüdeki güçlü hayvanlar kızışınca, Yakup çubukları onların gözü önüne, yalaklara koyuyordu ki, çubukların yanında çiftleşsinler."));
trsuperobject.biblemap.put("b1_30_42", new trsuperobject(1,30,42,"Sürünün zayıf hayvanlarının önüneyse çubuk koymuyordu. Böylece zayıf hayvanları Lavan, güçlüleri Yakup aldı."));
trsuperobject.biblemap.put("b1_30_43", new trsuperobject(1,30,43,"Yakup alabildiğine zenginleşti. Çok sayıda sürü, erkek ve kadın köle, deve, eşek sahibi oldu."));
trsuperobject.biblemap.put("b1_31_1", new trsuperobject(1,31,1,"Lavanın oğulları, ‹‹Yakup babamızın sahip olduğu her şeyi aldı›› dediler, ‹‹Bütün varlığını babamıza ait şeylerden kazandı.›› Yakup bu sözleri duyunca,"));
trsuperobject.biblemap.put("b1_31_2", new trsuperobject(1,31,2,"Lavanın kendisine karşı tutumunun eskisi gibi olmadığını anladı."));
trsuperobject.biblemap.put("b1_31_4", new trsuperobject(1,31,4,"Bunun üzerine Yakup Rahelle Leayı sürüsünün bulunduğu kırlara çağırttı."));
trsuperobject.biblemap.put("b1_31_5", new trsuperobject(1,31,5,"Onlara, ‹‹Bakıyorum, babanız bana eskisi gibi davranmıyor›› dedi, ‹‹Ama babamın Tanrısı benimle birlikte."));
trsuperobject.biblemap.put("b1_31_6", new trsuperobject(1,31,6,"Var gücümle babanıza hizmet ettiğimi bilirsiniz."));
trsuperobject.biblemap.put("b1_31_7", new trsuperobject(1,31,7,"Ne yazık ki, babanız beni aldattı, ondan alacağımı on kez değiştirdi. Ama Tanrı bana kötülük etmesine izin vermedi."));
trsuperobject.biblemap.put("b1_31_8", new trsuperobject(1,31,8,"Lavan, ‹Ücret olarak noktalı hayvanları al› deyince, bütün sürü noktalı doğurdu. ‹Ücret olarak çizgili olanları al› deyince de bütün sürü çizgili doğurdu."));
trsuperobject.biblemap.put("b1_31_9", new trsuperobject(1,31,9,"Tanrı babanızın hayvanlarını aldı, bana verdi."));
trsuperobject.biblemap.put("b1_31_10", new trsuperobject(1,31,10,"‹‹Sürülerin çiftleştiği mevsimde bir düş gördüm. Çiftleşen tekeler çizgili, noktalı, kırçıldı."));
trsuperobject.biblemap.put("b1_31_11", new trsuperobject(1,31,11,"Düşümde Tanrının meleği bana, ‹Yakup!› diye seslendi. ‹Buyur› dedim."));
trsuperobject.biblemap.put("b1_31_12", new trsuperobject(1,31,12,"Bana, ‹Bak, bütün çiftleşen tekeler çizgili, noktalı ve kırçıl› dedi, ‹Çünkü Lavanın sana yaptıklarının hepsini gördüm."));
trsuperobject.biblemap.put("b1_31_13", new trsuperobject(1,31,13,"Ben Beytelin Tanrısıyım. Hani orada bana anıt dikip meshetmiş, adak adamıştın. Kalk, bu ülkeden git, doğduğun ülkeye dön.››"));
trsuperobject.biblemap.put("b1_31_14", new trsuperobject(1,31,14,"Rahelle Lea, ‹‹Babamızın evinde hâlâ payımız, mirasımız var mı?›› dediler,"));
trsuperobject.biblemap.put("b1_31_15", new trsuperobject(1,31,15,"‹‹Onun gözünde artık yabancı değil miyiz? Çünkü bizi sattı. Bizim için ödenen bedelin hepsini yedi."));
trsuperobject.biblemap.put("b1_31_16", new trsuperobject(1,31,16,"Tanrının babamızdan aldığı varlığın tümü bize ve çocuklarımıza aittir. Tanrı sana ne dediyse öyle yap.››"));
trsuperobject.biblemap.put("b1_31_17", new trsuperobject(1,31,17,"Böylece Yakup çocuklarını, karılarını develere bindirdi."));
trsuperobject.biblemap.put("b1_31_18", new trsuperobject(1,31,18,"Bütün hayvanları önüne kattı; topladığı mallarla, Paddan-Aramda kazandığı hayvanlarla birlikte Kenan ülkesine, babası İshakın yanına gitmek üzere yola çıktı."));
trsuperobject.biblemap.put("b1_31_19", new trsuperobject(1,31,19,"Lavan koyunlarını kırkmaya gidince, Rahel babasının putlarını çaldı."));
trsuperobject.biblemap.put("b1_31_20", new trsuperobject(1,31,20,"Yakup da kaçacağını söylemeyerek Aramlı Lavanı kandırdı."));
trsuperobject.biblemap.put("b1_31_21", new trsuperobject(1,31,21,"Böylece kendisine ait her şeyi alıp kaçtı. Fırat Irmağını geçip Gilat dağlık bölgesine doğru gitti."));
trsuperobject.biblemap.put("b1_31_22", new trsuperobject(1,31,22,"Üçüncü gün Yakupun kaçtığını Lavana bildirdiler."));
trsuperobject.biblemap.put("b1_31_23", new trsuperobject(1,31,23,"Lavan yakınlarını yanına alıp Yakupun peşine düştü. Yedi gün sonra Gilat dağlık bölgesinde ona yetişti."));
trsuperobject.biblemap.put("b1_31_24", new trsuperobject(1,31,24,"O gece Tanrı Aramlı Lavanın düşüne girerek ona, ‹‹Dikkatli ol!›› dedi, ‹‹Yakupa ne iyi, ne kötü bir şey söyle.››"));
trsuperobject.biblemap.put("b1_31_25", new trsuperobject(1,31,25,"Lavan Yakupa yetişti. Yakup çadırını Gilat dağlık bölgesine kurmuştu. Lavan da yakınlarıyla birlikte çadırını aynı yere kurdu."));
trsuperobject.biblemap.put("b1_31_26", new trsuperobject(1,31,26,"Yakupa, ‹‹Nedir bu yaptığın?›› dedi, ‹‹Beni aldattın. Kızlarımı alıp savaş tutsağı gibi götürdün."));
trsuperobject.biblemap.put("b1_31_27", new trsuperobject(1,31,27,"Neden gizlice kaçtın? Neden beni aldattın? Niçin bana söylemedin? Seni sevinçle, ezgilerle, tefle, lirle yolcu ederdim."));
trsuperobject.biblemap.put("b1_31_28", new trsuperobject(1,31,28,"Torunlarımla, kızlarımla öpüşüp vedalaşmama izin vermedin. Aptallık ettin."));
trsuperobject.biblemap.put("b1_31_29", new trsuperobject(1,31,29,"Size kötülük yapacak güçteyim, ama babanın Tanrısı dün gece bana, ‹Dikkatli ol!› dedi, ‹Yakupa ne iyi, ne kötü hiçbir şey söyleme.›"));
trsuperobject.biblemap.put("b1_31_30", new trsuperobject(1,31,30,"Babanın evini çok özlediğin için bizden ayrıldın. Ama ilahlarımı niçin çaldın?››"));
trsuperobject.biblemap.put("b1_31_31", new trsuperobject(1,31,31,"Yakup, ‹‹Korktum›› diye karşılık verdi, ‹‹Kızlarını zorla elimden alırsın diye düşündüm."));
trsuperobject.biblemap.put("b1_31_32", new trsuperobject(1,31,32,"İlahlarını kimde bulursan, o öldürülecektir. Yakınlarımızın önünde kendin ara, eşyalarımın arasında sana ait ne bulursan al.›› Yakup ilahları Rahelin çaldığını bilmiyordu."));
trsuperobject.biblemap.put("b1_31_33", new trsuperobject(1,31,33,"Lavan Yakupun, Leanın ve iki cariyenin çadırına baktıysa da ilahları bulamadı. Leanın çadırından çıkıp Rahelin çadırına girdi."));
trsuperobject.biblemap.put("b1_31_34", new trsuperobject(1,31,34,"Rahel çaldığı putları devesinin semerine koymuş, üzerine oturmuştu. Lavan çadırını didik didik aradıysa da putları bulamadı."));
trsuperobject.biblemap.put("b1_31_35", new trsuperobject(1,31,35,"Rahel babasına, ‹‹Efendim, huzurunda kalkamadığım için kızma, âdet görüyorum da›› dedi. Lavan her yeri aradıysa da, putları bulamadı."));
trsuperobject.biblemap.put("b1_31_36", new trsuperobject(1,31,36,"Yakup kendini tutamadı. Lavana çıkışarak, ‹‹Suçum ne?›› diye sordu, ‹‹Ne günah işledim ki böyle öfkeyle peşime takıldın?"));
trsuperobject.biblemap.put("b1_31_37", new trsuperobject(1,31,37,"Bütün eşyalarımı aradın, kendine ait bir şey buldun mu? Varsa onu buraya, yakınlarımızın önüne koy. Onlar ikimiz hakkında karar versinler."));
trsuperobject.biblemap.put("b1_31_38", new trsuperobject(1,31,38,"Yirmi yıl yanında kaldım. Koyunların, keçilerin hiç düşük yapmadı. Sürülerinin içinden bir tek koç yemedim."));
trsuperobject.biblemap.put("b1_31_39", new trsuperobject(1,31,39,"Yabanıl hayvanların parçaladığını sana göstermedim, zararını ben çektim. Gece ya da gündüz çalınan her hayvanın karşılığını benden istedin."));
trsuperobject.biblemap.put("b1_31_40", new trsuperobject(1,31,40,"Öyle bir durumdaydım ki, gündüz sıcak, gece kırağı yedi bitirdi beni. Gözüme uyku girmedi."));
trsuperobject.biblemap.put("b1_31_41", new trsuperobject(1,31,41,"Yirmi yıl evinde böyle yaşadım. İki kızın için on dört yıl, sürün için altı yıl sana hizmet ettim. On kez alacağımı değiştirdin."));
trsuperobject.biblemap.put("b1_31_42", new trsuperobject(1,31,42,"Babamın ve İbrahimin Tanrısı, İshakın taptığı Tanrı benden yana olmasaydı, beni eli boş gönderecektin. Tanrı çektiğim zorluğu, verdiğim emeği gördü ve dün gece seni uyardı.›› de geçer."));
trsuperobject.biblemap.put("b1_31_43", new trsuperobject(1,31,43,"Lavan, ‹‹Kadınlar benim kızlarım, çocuklar benim çocuklarım, sürüler benim sürülerim›› diye karşılık verdi, ‹‹Burada gördüğün her şey bana ait. Kızlarıma ya da doğurdukları çocuklara bugün ne yapabilirim ki?"));
trsuperobject.biblemap.put("b1_31_44", new trsuperobject(1,31,44,"Gel anlaşalım. Aramıza tanık koyalım.››"));
trsuperobject.biblemap.put("b1_31_45", new trsuperobject(1,31,45,"Yakup bir taş alıp onu anıt olarak dikti."));
trsuperobject.biblemap.put("b1_31_46", new trsuperobject(1,31,46,"Yakınlarına, ‹‹Taş toplayın›› dedi. Adamlar topladıkları taşları bir yere yığdılar. Orada, yığının yanında yemek yediler."));
trsuperobject.biblemap.put("b1_31_47", new trsuperobject(1,31,47,"Lavan taş yığınına Yegar-Sahaduta, Yakup ise Galet adını verdi. ‹‹Tanıklık yığını›› anlamına gelir."));
trsuperobject.biblemap.put("b1_31_48", new trsuperobject(1,31,48,"Lavan, ‹‹Bu yığın bugün aramızda tanık olsun›› dedi. Bu yüzden yığına Galet adı verildi."));
trsuperobject.biblemap.put("b1_31_49", new trsuperobject(1,31,49,"Mispa diye de anılır. Çünkü Lavan, ‹‹Birbirimizden uzak olduğumuz zaman RAB aramızda gözcülük etsin›› dedi,"));
trsuperobject.biblemap.put("b1_31_50", new trsuperobject(1,31,50,"‹‹Eğer kızlarıma kötü davranır, başka kadınlarla evlenirsen, yanımızda kimse olmasa bile Tanrı tanık olacaktır.››"));
trsuperobject.biblemap.put("b1_31_51", new trsuperobject(1,31,51,"Sonra, ‹‹İşte taş yığını, işte aramıza diktiğim anıt›› dedi,"));
trsuperobject.biblemap.put("b1_31_52", new trsuperobject(1,31,52,"‹‹Bu yığın ve anıt birer tanık olsun. Bu yığının ötesine geçip sana kötülük etmeyeceğim. Sen de bu yığını ve anıtı geçip bana kötülük etmeyeceksin."));
trsuperobject.biblemap.put("b1_31_53", new trsuperobject(1,31,53,"İbrahimin, Nahorun ve babalarının Tanrısı aramızda yargıç olsun.›› Yakup babası İshakın taptığı Tanrının adıyla ant içti."));
trsuperobject.biblemap.put("b1_31_54", new trsuperobject(1,31,54,"Sonra dağda kurban kesip yakınlarını yemeğe çağırdı. Yemeği yiyip geceyi dağda geçirdiler."));
trsuperobject.biblemap.put("b1_31_55", new trsuperobject(1,31,55,"Lavan sabah erkenden kalktı; torunlarını, kızlarını öpüp kutsadıktan sonra evine gitti."));
trsuperobject.biblemap.put("b1_32_1", new trsuperobject(1,32,1,"Yakup yoluna devam ederken, Tanrının melekleriyle karşılaştı."));
trsuperobject.biblemap.put("b1_32_2", new trsuperobject(1,32,2,"Onları görünce, ‹‹Tanrının ordugahı bu›› diyerek oraya Mahanayim adını verdi."));
trsuperobject.biblemap.put("b1_32_3", new trsuperobject(1,32,3,"Yakup Edom topraklarında, Seir ülkesinde yaşayan ağabeyi Esava önceden haberciler gönderdi."));
trsuperobject.biblemap.put("b1_32_4", new trsuperobject(1,32,4,"Onlara şu buyruğu verdi: ‹‹Efendim Esava şöyle deyin: Kulun Yakup diyor ki, ‹Şimdiye kadar Lavanın yanında konuk olarak kaldım."));
trsuperobject.biblemap.put("b1_32_5", new trsuperobject(1,32,5,"Öküzlere, eşeklere, davarlara, erkek ve kadın kölelere sahip oldum. Efendimi hoşnut etmek için önceden haber gönderiyorum.››"));
trsuperobject.biblemap.put("b1_32_6", new trsuperobject(1,32,6,"Haberciler geri dönüp Yakupa, ‹‹Ağabeyin Esavın yanına gittik›› dediler, ‹‹Dört yüz adamla seni karşılamaya geliyor.››"));
trsuperobject.biblemap.put("b1_32_7", new trsuperobject(1,32,7,"Yakup çok korktu, sıkıldı. Yanındaki adamları, davarları, sığırları, develeri iki gruba ayırdı."));
trsuperobject.biblemap.put("b1_32_8", new trsuperobject(1,32,8,"‹‹Esav gelir, bir gruba saldırırsa, hiç değilse öteki grup kurtulur›› diye düşündü."));
trsuperobject.biblemap.put("b1_32_9", new trsuperobject(1,32,9,"Sonra şöyle dua etti: ‹‹Ey atam İbrahimin, babam İshakın Tanrısı RAB! Bana, ‹Ülkene, akrabalarının yanına dön, seni başarılı kılacağım› diye söz verdin."));
trsuperobject.biblemap.put("b1_32_10", new trsuperobject(1,32,10,"Bana gösterdiğin bunca iyiliğe, güvene layık değilim. Şeria Irmağını geçtiğimde değneğimden başka bir şeyim yoktu. Şimdi iki orduyla döndüm."));
trsuperobject.biblemap.put("b1_32_11", new trsuperobject(1,32,11,"Yalvarırım, beni ağabeyim Esavdan koru. Gelip bana, çocuklarla annelerine saldırmasından korkuyorum."));
trsuperobject.biblemap.put("b1_32_12", new trsuperobject(1,32,12,"‹Seni kesinlikle başarılı kılacağım, soyunu denizin kumu gibi sayılamayacak kadar çoğaltacağım› diye söz vermiştin bana.››"));
trsuperobject.biblemap.put("b1_32_13-15", new trsuperobject(1,32,13-15,"Yakup geceyi orada geçirdi. Birlikte getirdiği hayvanlardan ağabeyi Esava armağan olarak iki yüz keçi, yirmi teke, iki yüz koyun, yirmi koç, yavrularıyla birlikte otuz dişi deve, kırk inek, on boğa, yirmi dişi, on erkek eşek ayırdı."));
trsuperobject.biblemap.put("b1_32_16", new trsuperobject(1,32,16,"Bunları ayrı sürüler halinde kölelerine teslim ederek, ‹‹Önümden gidin, sürüler arasında boşluk bırakın›› dedi."));
trsuperobject.biblemap.put("b1_32_17", new trsuperobject(1,32,17,"Birinci köleye buyruk verdi: ‹‹Ağabeyim Esavla karşılaştığında, ‹Sahibin kim, nereye gidiyorsun? Önündeki bu hayvanlar kimin?› diye sorarsa,"));
trsuperobject.biblemap.put("b1_32_18", new trsuperobject(1,32,18,"‹Kulun Yakupun› diyeceksin, ‹Efendisi Esava armağan olarak gönderiyor. Kendisi de arkamızdan geliyor.››"));
trsuperobject.biblemap.put("b1_32_19", new trsuperobject(1,32,19,"İkinci ve üçüncü köleye, sürülerin peşinden giden herkese aynı buyruğu verdi: ‹‹Esavla karşılaştığınızda aynı şeyleri söyleyeceksiniz."));
trsuperobject.biblemap.put("b1_32_20", new trsuperobject(1,32,20,"‹Kulun Yakup arkamızdan geliyor› diyeceksiniz.›› ‹‹Önden göndereceğim armağanla onu yatıştırır, sonra kendisini görürüm. Belki beni bağışlar›› diye düşünüyordu."));
trsuperobject.biblemap.put("b1_32_21", new trsuperobject(1,32,21,"Böylece armağanı önden gönderip geceyi konakladığı yerde geçirdi."));
trsuperobject.biblemap.put("b1_32_22", new trsuperobject(1,32,22,"Yakup o gece kalktı; iki karısını, iki cariyesini, on bir oğlunu yanına alıp Yabbuk Irmağının sığ yerinden karşıya geçti."));
trsuperobject.biblemap.put("b1_32_23", new trsuperobject(1,32,23,"Onları geçirdikten sonra sahip olduğu her şeyi de karşıya geçirdi."));
trsuperobject.biblemap.put("b1_32_24", new trsuperobject(1,32,24,"Böylece Yakup arkada yalnız kaldı. Bir adam gün ağarıncaya kadar onunla güreşti."));
trsuperobject.biblemap.put("b1_32_25", new trsuperobject(1,32,25,"Yakupu yenemeyeceğini anlayınca, onun uyluk kemiğinin başına çarptı. Öyle ki, güreşirken Yakupun uyluk kemiği çıktı."));
trsuperobject.biblemap.put("b1_32_26", new trsuperobject(1,32,26,"Adam, ‹‹Bırak beni, gün ağarıyor›› dedi. Yakup, ‹‹Beni kutsamadıkça seni bırakmam›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_32_27", new trsuperobject(1,32,27,"Adam, ‹‹Adın ne?›› diye sordu. ‹‹Yakup.››"));
trsuperobject.biblemap.put("b1_32_28", new trsuperobject(1,32,28,"Adam, ‹‹Artık sana Yakup değil, İsrail denecek›› dedi, ‹‹Çünkü Tanrıyla, insanlarla güreşip yendin.››"));
trsuperobject.biblemap.put("b1_32_29", new trsuperobject(1,32,29,"Yakup, ‹‹Lütfen adını söyler misin?›› diye sordu. Ama adam, ‹‹Neden adımı soruyorsun?›› dedi. Sonra Yakupu kutsadı."));
trsuperobject.biblemap.put("b1_32_30", new trsuperobject(1,32,30,"Yakup, ‹‹Tanrıyla yüzyüze görüştüm, ama canım bağışlandı›› diyerek oraya Peniel adını verdi."));
trsuperobject.biblemap.put("b1_32_31", new trsuperobject(1,32,31,"Yakup Penielden ayrılırken güneş doğdu. Uyluğundan ötürü aksıyordu."));
trsuperobject.biblemap.put("b1_32_32", new trsuperobject(1,32,32,"Bu nedenle İsrailliler bugün bile uyluk kemiğinin üzerindeki siniri yemezler. Çünkü Yakup'un uyluk kemiğinin başındaki sinire çarpılmıştı."));
trsuperobject.biblemap.put("b1_33_1", new trsuperobject(1,33,1,"Yakup baktı, Esav dört yüz adamıyla birlikte geliyor. Çocukları Leayla Rahele ve iki cariyeye teslim etti."));
trsuperobject.biblemap.put("b1_33_2", new trsuperobject(1,33,2,"Cariyelerle çocuklarını öne, Leayla çocuklarını arkaya, Rahelle Yusufu da en arkaya dizdi."));
trsuperobject.biblemap.put("b1_33_3", new trsuperobject(1,33,3,"Kendisi hepsinin önüne geçti. Ağabeyine yaklaşırken yedi kez yere kapandı."));
trsuperobject.biblemap.put("b1_33_4", new trsuperobject(1,33,4,"Ne var ki Esav koşarak onu karşıladı, kucaklayıp boynuna sarıldı, öptü. İkisi de ağlamaya başladı."));
trsuperobject.biblemap.put("b1_33_5", new trsuperobject(1,33,5,"Esav kadınlarla çocuklara baktı. ‹‹Kim bu yanındakiler?›› diye sordu. Yakup, ‹‹Tanrının kuluna lütfettiği çocuklar›› dedi."));
trsuperobject.biblemap.put("b1_33_6", new trsuperobject(1,33,6,"Cariyelerle yanlarındaki çocuklar yaklaşıp eğildiler."));
trsuperobject.biblemap.put("b1_33_7", new trsuperobject(1,33,7,"Ardından Lea çocuklarıyla birlikte yaklaşıp eğildi. En son da Yusufla Rahel yaklaşıp eğildi."));
trsuperobject.biblemap.put("b1_33_8", new trsuperobject(1,33,8,"Esav, ‹‹Karşılaştığım öbür topluluğun anlamı neydi?›› diye sordu. Yakup, ‹‹Efendimi hoşnut etmek için›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_33_9", new trsuperobject(1,33,9,"Esav, ‹‹Benim yeterince malım var, kardeşim›› dedi, ‹‹Senin malın sana kalsın.››"));
trsuperobject.biblemap.put("b1_33_10", new trsuperobject(1,33,10,"Yakup, ‹‹Olmaz, eğer sevgini kazandımsa, lütfen armağanımı kabul et›› diye karşılık verdi, ‹‹Senin yüzünü görmek Tanrının yüzünü görmek gibi. Çünkü beni kabul ettin."));
trsuperobject.biblemap.put("b1_33_11", new trsuperobject(1,33,11,"Lütfen sana gönderdiğim armağanı al. Tanrı bana öyle iyilik yaptı ki, her şeyim var.›› Armağanı kabul ettirinceye kadar diretti."));
trsuperobject.biblemap.put("b1_33_12", new trsuperobject(1,33,12,"Esav, ‹‹Haydi yolumuza devam edelim›› dedi, ‹‹Ben önünsıra gideceğim.››"));
trsuperobject.biblemap.put("b1_33_13", new trsuperobject(1,33,13,"Yakup, ‹‹Efendim, bilirsin, çocuklar narindir›› dedi, ‹‹Yanımdaki koyunların, sığırların yavruları var. Hayvanları bir gün daha yürümeye zorlarsak hepsi ölür."));
trsuperobject.biblemap.put("b1_33_14", new trsuperobject(1,33,14,"Efendim, lütfen sen kulunun önünden git. Ben hayvanlarla çocuklara ayak uydurarak yavaş yavaş geleceğim. Seirde efendime yetişirim.››"));
trsuperobject.biblemap.put("b1_33_15", new trsuperobject(1,33,15,"Esav, ‹‹Yanımdaki adamlardan birkaçını yanına vereyim›› dedi. Yakup, ‹‹Niçin?›› diye sordu, ‹‹Ben yalnızca seni hoşnut etmek istiyorum.››"));
trsuperobject.biblemap.put("b1_33_16", new trsuperobject(1,33,16,"Esav o gün Seire dönmek üzere yola koyuldu."));
trsuperobject.biblemap.put("b1_33_17", new trsuperobject(1,33,17,"Yakupsa Sukkota gitti. Orada kendine ev, hayvanlarına barınaklar yaptı. Bu yüzden oraya Sukkot adını verdi."));
trsuperobject.biblemap.put("b1_33_18", new trsuperobject(1,33,18,"Yakup güvenlik içinde Paddan-Aramdan Kenan ülkesine, Şekem Kentine vardı. Kentin yakınında konakladı."));
trsuperobject.biblemap.put("b1_33_19", new trsuperobject(1,33,19,"Çadırını kurduğu arsayı Şekemin babası Hamorun oğullarından yüz parça gümüşefö aldı."));
trsuperobject.biblemap.put("b1_33_20", new trsuperobject(1,33,20,"Orada bir sunak kurarak El-Elohe-İsrail adını verdi. ağırlığı ve değeri bilinmeyen bir para birimiydi. gelir."));
trsuperobject.biblemap.put("b1_34_1", new trsuperobject(1,34,1,"Leayla Yakupun kızı Dina bir gün yöre kadınlarını ziyarete gitti."));
trsuperobject.biblemap.put("b1_34_2", new trsuperobject(1,34,2,"O bölgenin beyi Hivli Hamorun oğlu Şekem Dinayı görünce tutup ırzına geçti."));
