trsuperobject.biblemap.put("b1_34_5", new trsuperobject(1,34,5,"Yakup kızı Dinanın kirletildiğini duyduğunda, oğulları kırda hayvanların başındaydı. Yakup onlar gelinceye kadar konuşmadı."));
trsuperobject.biblemap.put("b1_34_6", new trsuperobject(1,34,6,"Bu arada Şekemin babası Hamor konuşmak için Yakupun yanına gitti."));
trsuperobject.biblemap.put("b1_34_7", new trsuperobject(1,34,7,"Yakupun oğulları olayı duyar duymaz kırdan döndüler. Üzüntülü ve çok öfkeliydiler. Çünkü Şekem Yakupun kızıyla yatarak İsrailin onurunu kırmıştı. Böyle bir şey olmamalıydı."));
trsuperobject.biblemap.put("b1_34_8", new trsuperobject(1,34,8,"Hamor onlara, ‹‹Oğlum Şekemin gönlü kızınızda›› dedi, ‹‹Lütfen onu oğluma eş olarak verin."));
trsuperobject.biblemap.put("b1_34_9", new trsuperobject(1,34,9,"Bizimle akraba olun. Birbirimize kız verip kız alalım."));
trsuperobject.biblemap.put("b1_34_10", new trsuperobject(1,34,10,"Bizimle birlikte yaşayın. Ülke önünüzde, nereye isterseniz yerleşin, ticaret yapın, mülk edinin.››"));
trsuperobject.biblemap.put("b1_34_11", new trsuperobject(1,34,11,"Şekem de Dinanın babasıyla kardeşlerine, ‹‹Bana bu iyiliği yapın, ne isterseniz veririm›› dedi,"));
trsuperobject.biblemap.put("b1_34_12", new trsuperobject(1,34,12,"‹‹Ne kadar başlık ve armağan isterseniz isteyin, dilediğiniz her şeyi vereceğim. Yeter ki, kızı bana eş olarak verin.››"));
trsuperobject.biblemap.put("b1_34_13", new trsuperobject(1,34,13,"Kızkardeşleri Dinanın ırzına geçildiği için, Yakupun oğulları Şekemle babası Hamora aldatıcı bir yanıt verdiler."));
trsuperobject.biblemap.put("b1_34_14", new trsuperobject(1,34,14,"‹‹Olmaz, kızkardeşimizi sünnetsiz bir adama veremeyiz›› dediler, ‹‹Bizim için utanç olur."));
trsuperobject.biblemap.put("b1_34_15", new trsuperobject(1,34,15,"Ancak şu koşulla kabul ederiz: Bütün erkekleriniz bizim gibi sünnet olursa,"));
trsuperobject.biblemap.put("b1_34_16", new trsuperobject(1,34,16,"birbirimize kız verip kız alabiliriz. Sizinle birlikte yaşar, bir halk oluruz."));
trsuperobject.biblemap.put("b1_34_17", new trsuperobject(1,34,17,"Eğer kabul etmez, sünnet olmazsanız, kızımızı alır gideriz.››"));
trsuperobject.biblemap.put("b1_34_18", new trsuperobject(1,34,18,"Bu öneri Hamorla oğlu Şekeme iyi göründü."));
trsuperobject.biblemap.put("b1_34_19", new trsuperobject(1,34,19,"Ailesinde en saygın kişi olan genç Şekem öneriyi yerine getirmekte gecikmedi. Çünkü Yakupun kızına aşıktı."));
trsuperobject.biblemap.put("b1_34_20", new trsuperobject(1,34,20,"Hamorla oğlu Şekem durumu kent halkına bildirmek için kentin kapısına gittiler."));
trsuperobject.biblemap.put("b1_34_21", new trsuperobject(1,34,21,"‹‹Bu adamlar bize dostluk gösteriyor›› dediler, ‹‹Ülkemizde yaşasınlar, ticaret yapsınlar. Topraklarımız geniş, onlara da yeter, bize de. Birbirimize kız verip kız alabiliriz."));
trsuperobject.biblemap.put("b1_34_22", new trsuperobject(1,34,22,"Yalnız, şu koşulla bizimle birleşmeyi, birlikte yaşamayı kabul ediyorlar: Bizim erkeklerin de kendileri gibi sünnet olmasını istiyorlar."));
trsuperobject.biblemap.put("b1_34_23", new trsuperobject(1,34,23,"Böylece bütün sürüleri, malları, öbür hayvanları da bizim olur, değil mi? Gelin onlarla anlaşalım, bizimle birlikte yaşasınlar.››"));
trsuperobject.biblemap.put("b1_34_24", new trsuperobject(1,34,24,"Kent kapısından geçen herkes Hamorla oğlu Şekemin söylediklerini kabul etti ve kentteki bütün erkekler sünnet oldu."));
trsuperobject.biblemap.put("b1_34_25", new trsuperobject(1,34,25,"Üçüncü gün erkekler daha sünnetin acısını çekerken, Yakupun oğullarından ikisi -Dinanın kardeşleri Şimonla Levi- kılıçlarını kuşanıp kuşku uyandırmadan kente girip bütün erkekleri kılıçtan geçirdiler."));
trsuperobject.biblemap.put("b1_34_26", new trsuperobject(1,34,26,"Hamorla oğlu Şekemi de öldürdüler. Dinayı Şekemin evinden alıp gittiler."));
trsuperobject.biblemap.put("b1_34_27", new trsuperobject(1,34,27,"Sonra Yakupun bütün oğulları cesetleri soyup kenti yağmaladılar. Çünkü kızkardeşlerini kirletmişlerdi."));
trsuperobject.biblemap.put("b1_34_28", new trsuperobject(1,34,28,"Kentteki ve kırdaki davarları, sığırları, eşekleri ele geçirdiler."));
trsuperobject.biblemap.put("b1_34_29", new trsuperobject(1,34,29,"Bütün mallarını, çocuklarını, kadınlarını aldılar, evlerindeki her şeyi yağmaladılar."));
trsuperobject.biblemap.put("b1_34_30", new trsuperobject(1,34,30,"Yakup, Şimonla Leviye, ‹‹Bu ülkede yaşayan Kenanlılarla Perizlileri bana düşman ettiniz, başımı belaya soktunuz›› dedi, ‹‹Sayıca azız. Eğer birleşir, bana saldırırlarsa, ailemle birlikte yok olurum.››"));
trsuperobject.biblemap.put("b1_34_31", new trsuperobject(1,34,31,"Şimon'la Levi, ‹‹Kızkardeşimize bir fahişe gibi mi davranmalıydı?›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b1_35_1", new trsuperobject(1,35,1,"Tanrı Yakupa, ‹‹Git, Beytele yerleş›› dedi, ‹‹Ağabeyin Esavdan kaçarken sana görünen Tanrıya orada bir sunak yap.››"));
trsuperobject.biblemap.put("b1_35_2", new trsuperobject(1,35,2,"Yakup ailesine ve yanındakilere, ‹‹Yabancı ilahlarınızı atın›› dedi, ‹‹Kendinizi arındırıp giysilerinizi değiştirin."));
trsuperobject.biblemap.put("b1_35_3", new trsuperobject(1,35,3,"Beytele gidelim. Sıkıntı çektiğim günlerde yakarışımı duyan, gittiğim her yerde benimle birlikte olan Tanrıya orada bir sunak yapacağım.››"));
trsuperobject.biblemap.put("b1_35_4", new trsuperobject(1,35,4,"Böylece herkes yabancı ilahlarını, kulaklarındaki küpeleri Yakupa verdi. Yakup bunları Şekem yakınlarında bir yabanıl fıstık ağacının altına gömdü."));
trsuperobject.biblemap.put("b1_35_5", new trsuperobject(1,35,5,"Sonra göçtüler. Çevre kentlerde yaşayan halk peşlerine düşmedi, çünkü hepsini Tanrı korkusu sarmıştı."));
trsuperobject.biblemap.put("b1_35_6", new trsuperobject(1,35,6,"Yakup adamlarıyla birlikte Kenan ülkesindeki Luz -Beytel- Kentine geldi."));
trsuperobject.biblemap.put("b1_35_7", new trsuperobject(1,35,7,"Bir sunak yaparak oraya El-Beytel adını verdi. Çünkü ağabeyinden kaçarken Tanrı orada kendisine görünmüştü."));
trsuperobject.biblemap.put("b1_35_8", new trsuperobject(1,35,8,"Rebekanın dadısı Debora ölünce Beytelin güneyindeki meşe ağacının altına gömüldü. Bu yüzden ağaca Allon-Bakut adı verildi."));
trsuperobject.biblemap.put("b1_35_9", new trsuperobject(1,35,9,"Yakup Paddan-Aramdan dönünce, Tanrı ona yine görünerek onu kutsadı."));
trsuperobject.biblemap.put("b1_35_10", new trsuperobject(1,35,10,"‹‹Sana Yakup diyorlar, ama bundan böyle adın Yakup değil, İsrail olacak›› diyerek onun adını İsrailfş koydu."));
trsuperobject.biblemap.put("b1_35_11", new trsuperobject(1,35,11,"‹‹Ben Her Şeye Gücü Yeten Tanrıyım›› dedi, ‹‹Verimli ol, çoğal. Senden bir ulus ve uluslar topluluğu doğacak. Kralların atası olacaksın."));
trsuperobject.biblemap.put("b1_35_12", new trsuperobject(1,35,12,"İbrahime, İshaka verdiğim toprakları sana verecek, senden sonra da soyuna bağışlayacağım.››"));
trsuperobject.biblemap.put("b1_35_13", new trsuperobject(1,35,13,"Sonra Tanrı Yakuptan ayrılarak onunla konuştuğu yerden yukarı çekildi."));
trsuperobject.biblemap.put("b1_35_14", new trsuperobject(1,35,14,"Yakup Tanrının kendisiyle konuştuğu yere taş bir anıt dikti. Üzerine dökmelik sunu ve zeytinyağı döktü."));
trsuperobject.biblemap.put("b1_35_15", new trsuperobject(1,35,15,"Oraya, Tanrının kendisiyle konuştuğu yere Beytel adını verdi."));
trsuperobject.biblemap.put("b1_35_16", new trsuperobject(1,35,16,"Sonra Beytelden göçtüler. Efrata varmadan Rahel doğum yaptı. Doğum yaparken çok sancı çekti."));
trsuperobject.biblemap.put("b1_35_17", new trsuperobject(1,35,17,"O sancı çekerken, ebesi, ‹‹Korkma!›› dedi, ‹‹Bir oğlun daha oluyor.››"));
trsuperobject.biblemap.put("b1_35_18", new trsuperobject(1,35,18,"Ama Rahel ölmek üzereydi. Can verirken oğlunun adını Ben-Oni koydu. Babası ise çocuğa Benyaminfü adını verdi. anlamına gelir."));
trsuperobject.biblemap.put("b1_35_19", new trsuperobject(1,35,19,"Rahel öldü ve Efrat -Beytlehem- yolunda gömüldü."));
trsuperobject.biblemap.put("b1_35_20", new trsuperobject(1,35,20,"Yakup Rahelin mezarına bir taş dikti. Bu mezar taşı bugüne kadar kaldı."));
trsuperobject.biblemap.put("b1_35_21", new trsuperobject(1,35,21,"İsrail yine göçtü ve Eder Kulesinin ötesinde konakladı."));
trsuperobject.biblemap.put("b1_35_22", new trsuperobject(1,35,22,"İsrail o bölgede yaşarken Ruben babasının cariyesi Bilhayla yattı. İsrail bunu duyunca çok kızdı. ‹‹Bunu duydu››."));
trsuperobject.biblemap.put("b1_35_23", new trsuperobject(1,35,23,"Yakupun on iki oğlu vardı. Leanın oğulları: Ruben -Yakupun ilk oğlu- Şimon, Levi, Yahuda, İssakar, Zevulun."));
trsuperobject.biblemap.put("b1_35_24", new trsuperobject(1,35,24,"Rahelin oğulları: Yusuf, Benyamin."));
trsuperobject.biblemap.put("b1_35_25", new trsuperobject(1,35,25,"Rahelin cariyesi Bilhanın oğulları: Dan, Naftali."));
trsuperobject.biblemap.put("b1_35_26", new trsuperobject(1,35,26,"Leanın cariyesi Zilpanın oğulları: Gad, Aşer. Yakupun Paddan-Aramda doğan oğulları bunlardır."));
trsuperobject.biblemap.put("b1_35_27", new trsuperobject(1,35,27,"Yakup, İshakla İbrahimin de yabancı olarak kalmış olduğu, bugün Hevron denen Kiryat-Arba yakınlarındaki Mamreye, babası İshakın yanına gitti."));
trsuperobject.biblemap.put("b1_35_28", new trsuperobject(1,35,28,"İshak yüz seksen yıl yaşadı."));
trsuperobject.biblemap.put("b1_35_29", new trsuperobject(1,35,29,"Kocamış, yaşama doymuş olarak son soluğunu verdi. Ölüp halkına kavuştu. Oğulları Esav'la Yakup onu gömdüler."));
trsuperobject.biblemap.put("b1_36_1", new trsuperobject(1,36,1,"Esavın, yani Edomun öyküsü:"));
trsuperobject.biblemap.put("b1_36_2", new trsuperobject(1,36,2,"Esav şu Kenanlı kızlarla evlendi: Hititli Elonun kızı Âda; Hivli Sivonun torunu, Ânanın kızı Oholivama;"));
trsuperobject.biblemap.put("b1_36_3", new trsuperobject(1,36,3,"Nevayotun kızkardeşi, İsmailin kızı Basemat."));
trsuperobject.biblemap.put("b1_36_4", new trsuperobject(1,36,4,"Âda Esava Elifazı, Basemat Reueli,"));
trsuperobject.biblemap.put("b1_36_5", new trsuperobject(1,36,5,"Oholivama Yeuş, Yalam ve Korahı doğurdu. Esavın Kenan ülkesinde doğan oğulları bunlardı."));
trsuperobject.biblemap.put("b1_36_6", new trsuperobject(1,36,6,"Esav karılarını, oğullarını, kızlarını, evindeki bütün adamlarını, hayvanlarının hepsini, Kenan ülkesinde kazandığı malların tümünü alıp kardeşi Yakuptan ayrıldı, başka bir ülkeye gitti."));
trsuperobject.biblemap.put("b1_36_7", new trsuperobject(1,36,7,"Birlikte yaşayamayacak kadar çok malları vardı. Yabancı olarak yaşadıkları bu topraklar davarlarına yetmiyordu."));
trsuperobject.biblemap.put("b1_36_8", new trsuperobject(1,36,8,"Esav -Edom- Seir dağlık bölgesine yerleşti."));
trsuperobject.biblemap.put("b1_36_9", new trsuperobject(1,36,9,"Seir dağlık bölgesine yerleşen Edomluların atası Esavın soyu:"));
trsuperobject.biblemap.put("b1_36_10", new trsuperobject(1,36,10,"Esavın oğullarının adları şunlardır: Esavın karılarından Âdanın oğlu Elifaz, Basematın oğlu Reuel."));
trsuperobject.biblemap.put("b1_36_11", new trsuperobject(1,36,11,"Elifazın oğulları: Teman, Omar, Sefo, Gatam, Kenaz."));
trsuperobject.biblemap.put("b1_36_12", new trsuperobject(1,36,12,"Timna Esavın oğlu Elifazın cariyesiydi. Elifaza Amaleki doğurdu. Bunlar Esavın karısı Âdanın torunlarıdır."));
trsuperobject.biblemap.put("b1_36_13", new trsuperobject(1,36,13,"Reuelin oğulları: Nahat, Zerah, Şamma, Mizza. Bunlar Esavın karısı Basematın torunlarıdır."));
trsuperobject.biblemap.put("b1_36_14", new trsuperobject(1,36,14,"Sivonun torunu ve Ânanın kızı olan Esavın karısı Oholivamanın Esava doğurduğu oğullar şunlardır: Yeuş, Yalam, Korah."));
trsuperobject.biblemap.put("b1_36_15", new trsuperobject(1,36,15,"Esavoğullarının boy beyleri şunlardır: Esavın ilk oğlu Elifazın oğulları: Teman, Omar, Sefo, Kenaz,"));
trsuperobject.biblemap.put("b1_36_16", new trsuperobject(1,36,16,"Korah, Gatam, Amalek. Bunlar Edom ülkesinde Elifazın soyundan beylerdi ve Âdanın torunlarıydı."));
trsuperobject.biblemap.put("b1_36_17", new trsuperobject(1,36,17,"Esav oğlu Reuelin oğulları şunlardır: Nahat, Zerah, Şamma, Mizza. Bunlar Edom ülkesinde Reuelin soyundan gelen beylerdi ve Esavın karısı Basematın torunlarıydı."));
trsuperobject.biblemap.put("b1_36_18", new trsuperobject(1,36,18,"Esavın karısı Oholivamanın oğulları şunlardır: Yeuş, Yalam, Korah. Bunlar Ânanın kızı olan Esavın karısı Oholivamanın soyundan gelen beylerdi."));
trsuperobject.biblemap.put("b1_36_19", new trsuperobject(1,36,19,"Bunların hepsi Esavın -Edomun- oğullarıdır. Yukardakiler de onların beyleridir."));
trsuperobject.biblemap.put("b1_36_20", new trsuperobject(1,36,20,"Ülkede yaşayan Horlu Seirin oğulları şunlardı: Lotan, Şoval, Sivon, Âna,"));
trsuperobject.biblemap.put("b1_36_21", new trsuperobject(1,36,21,"Dişon, Eser, Dişan. Seirin Edomda beylik eden Horlu oğulları bunlardı."));
trsuperobject.biblemap.put("b1_36_22", new trsuperobject(1,36,22,"Lotanın oğulları: Hori, Hemam. Timna Lotanın kızkardeşiydi."));
trsuperobject.biblemap.put("b1_36_23", new trsuperobject(1,36,23,"Şovalın oğulları: Alvan, Manahat, Eval, Şefo, Onam."));
trsuperobject.biblemap.put("b1_36_24", new trsuperobject(1,36,24,"Sivonun oğulları: Aya ve Âna. Babası Sivonun eşeklerini güderken çölde sıcak su kaynakları bulan Ânadır bu."));
trsuperobject.biblemap.put("b1_36_25", new trsuperobject(1,36,25,"Ânanın çocukları şunlardı: Dişon ve Ânanın kızı Oholivama."));
trsuperobject.biblemap.put("b1_36_26", new trsuperobject(1,36,26,"Dişonun oğulları şunlardı: Hemdan, Eşban, Yitran, Keran."));
trsuperobject.biblemap.put("b1_36_27", new trsuperobject(1,36,27,"Eserin oğulları şunlardı: Bilhan, Zaavan, Akan."));
trsuperobject.biblemap.put("b1_36_28", new trsuperobject(1,36,28,"Dişanın oğulları şunlardı: Ûs, Aran."));
trsuperobject.biblemap.put("b1_36_29", new trsuperobject(1,36,29,"Horlu boy beyleri şunlardı: Lotan, Şoval, Sivon, Âna,"));
trsuperobject.biblemap.put("b1_36_30", new trsuperobject(1,36,30,"Dişon, Eser, Dişan. Seir ülkesindeki Horlu boy beyleri bunlardı."));
trsuperobject.biblemap.put("b1_36_31", new trsuperobject(1,36,31,"İsraillileri yöneten bir kralın olmadığı dönemde, Edomu şu krallar yönetti:"));
trsuperobject.biblemap.put("b1_36_32", new trsuperobject(1,36,32,"Beor oğlu Bala Edom Kralı oldu. Kentinin adı Dinhavaydı."));
trsuperobject.biblemap.put("b1_36_33", new trsuperobject(1,36,33,"Bala ölünce, yerine Bosralı Zerah oğlu Yovav geçti."));
trsuperobject.biblemap.put("b1_36_34", new trsuperobject(1,36,34,"Yovav ölünce, Temanlılar ülkesinden Huşam kral oldu."));
trsuperobject.biblemap.put("b1_36_35", new trsuperobject(1,36,35,"Huşam ölünce, Midyanı Moav kırlarında bozguna uğratan Bedat oğlu Hadat kral oldu. Kentinin adı Avitti."));
trsuperobject.biblemap.put("b1_36_36", new trsuperobject(1,36,36,"Hadat ölünce, yerine Masrekalı Samla geçti."));
trsuperobject.biblemap.put("b1_36_37", new trsuperobject(1,36,37,"Samla ölünce, yerine Rehovot-Hannaharlı Şaul geçti."));
trsuperobject.biblemap.put("b1_36_38", new trsuperobject(1,36,38,"Şaul ölünce, yerine Akbor oğlu Baal-Hanan geçti."));
trsuperobject.biblemap.put("b1_36_39", new trsuperobject(1,36,39,"Akbor oğlu Baal-Hanan ölünce, yerine Hadat geçti. Kentinin adı Pauydu. Karısı, Me-Zahav kızı Matretin kızı Mehetaveldi."));
trsuperobject.biblemap.put("b1_36_40", new trsuperobject(1,36,40,"Boylarına ve bölgelerine göre Esavın soyundan gelen beylerin adları şunlardı: Timna, Alva, Yetet,"));
trsuperobject.biblemap.put("b1_36_41", new trsuperobject(1,36,41,"Oholivama, Ela, Pinon,"));
trsuperobject.biblemap.put("b1_36_42", new trsuperobject(1,36,42,"Kenaz, Teman, Mivsar,"));
trsuperobject.biblemap.put("b1_36_43", new trsuperobject(1,36,43,"Magdiel, İram. Sahip oldukları ülkede yaşadıkları yerlere adlarını veren Edom beyleri bunlardı. Edomlular'ın atası Esav'dı."));
trsuperobject.biblemap.put("b1_37_1", new trsuperobject(1,37,1,"Yakup babasının yabancı olarak kalmış olduğu Kenan ülkesinde yaşadı."));
trsuperobject.biblemap.put("b1_37_2", new trsuperobject(1,37,2,"Yakup soyunun öyküsü: Yusuf on yedi yaşında bir gençti. Babasının karıları Bilha ve Zilpadan olan üvey kardeşleriyle birlikte sürü güdüyordu. Kardeşlerinin yaptığı kötülükleri babasına ulaştırırdı."));
trsuperobject.biblemap.put("b1_37_3", new trsuperobject(1,37,3,"İsrail Yusufu öbür oğullarının hepsinden çok severdi. Çünkü Yusuf onun yaşlılığında doğmuştu. Yusufa uzun, renkli bir giysi yaptırmıştı."));
trsuperobject.biblemap.put("b1_37_4", new trsuperobject(1,37,4,"Yusufun kardeşleri babalarının onu kendilerinden çok sevdiğini görünce, ondan nefret ettiler. Yusufa tatlı söz söylemez oldular."));
trsuperobject.biblemap.put("b1_37_5", new trsuperobject(1,37,5,"Yusuf bir düş gördü. Bunu kardeşlerine anlatınca, ondan daha çok nefret ettiler."));
trsuperobject.biblemap.put("b1_37_6", new trsuperobject(1,37,6,"Yusuf, ‹‹Lütfen gördüğüm düşü dinleyin!›› dedi,"));
trsuperobject.biblemap.put("b1_37_7", new trsuperobject(1,37,7,"‹‹Tarlada demet bağlıyorduk. Ansızın benim demetim kalkıp dikildi. Sizinkilerse, çevresine toplanıp önünde eğildiler.››"));
trsuperobject.biblemap.put("b1_37_8", new trsuperobject(1,37,8,"Kardeşleri, ‹‹Başımıza kral mı olacaksın? Bizi sen mi yöneteceksin?›› dediler. Düşlerinden, söylediklerinden ötürü ondan büsbütün nefret ettiler."));
trsuperobject.biblemap.put("b1_37_9", new trsuperobject(1,37,9,"Yusuf bir düş daha görüp kardeşlerine anlattı. ‹‹Dinleyin, bir düş daha gördüm›› dedi, ‹‹Güneş, ay ve on bir yıldız önümde eğildiler.››"));
trsuperobject.biblemap.put("b1_37_10", new trsuperobject(1,37,10,"Yusuf babasıyla kardeşlerine bu düşü anlatınca, babası onu azarladı: ‹‹Ne biçim düş bu?›› dedi, ‹‹Ben, annen, kardeşlerin gelip önünde yere mi eğileceğiz yani?››"));
trsuperobject.biblemap.put("b1_37_11", new trsuperobject(1,37,11,"Kardeşleri Yusufu kıskanıyordu, ama bu olay babasının aklına takıldı."));
trsuperobject.biblemap.put("b1_37_12", new trsuperobject(1,37,12,"Bir gün Yusufun kardeşleri babalarının sürüsünü gütmek için Şekeme gittiler."));
trsuperobject.biblemap.put("b1_37_13", new trsuperobject(1,37,13,"İsrail Yusufa, ‹‹Kardeşlerin Şekemde sürü güdüyorlar›› dedi, ‹‹Gel seni de onların yanına göndereyim.›› Yusuf, ‹‹Hazırım›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_37_14", new trsuperobject(1,37,14,"Babası, ‹‹Git kardeşlerine ve sürüye bak›› dedi, ‹‹Her şey yolunda mı, değil mi, bana haber getir.›› Böylece onu Hevron Vadisinden gönderdi. Yusuf Şekeme vardı."));
trsuperobject.biblemap.put("b1_37_15", new trsuperobject(1,37,15,"Kırda dolaşırken bir adam onu görüp, ‹‹Ne arıyorsun?›› diye sordu."));
trsuperobject.biblemap.put("b1_37_16", new trsuperobject(1,37,16,"Yusuf, ‹‹Kardeşlerimi arıyorum›› diye yanıtladı, ‹‹Buralarda sürü güdüyorlar. Nerede olduklarını biliyor musun?››"));
trsuperobject.biblemap.put("b1_37_17", new trsuperobject(1,37,17,"Adam, ‹‹Buradan ayrıldılar›› dedi, ‹‹Dotana gidelim› dediklerini duydum.›› Böylece Yusuf kardeşlerinin peşinden gitti ve Dotanda onları buldu."));
trsuperobject.biblemap.put("b1_37_18", new trsuperobject(1,37,18,"Kardeşleri onu uzaktan gördüler. Yusuf yanlarına varmadan, onu öldürmek için düzen kurdular."));
trsuperobject.biblemap.put("b1_37_19", new trsuperobject(1,37,19,"Birbirlerine, ‹‹İşte düş hastası geliyor›› dediler,"));
trsuperobject.biblemap.put("b1_37_20", new trsuperobject(1,37,20,"‹‹Hadi onu öldürüp kuyulardan birine atalım. Yabanıl bir hayvan yedi deriz. Bakalım o zaman düşleri ne olacak!››"));
trsuperobject.biblemap.put("b1_37_21", new trsuperobject(1,37,21,"Ruben bunu duyunca Yusufu kurtarmaya çalıştı: ‹‹Canına kıymayın›› dedi,"));
trsuperobject.biblemap.put("b1_37_22", new trsuperobject(1,37,22,"‹‹Kan dökmeyin. Onu şu ıssız yerdeki kuyuya atın, ama kendisine dokunmayın.›› Amacı Yusufu kurtarıp babasına geri götürmekti."));
trsuperobject.biblemap.put("b1_37_23", new trsuperobject(1,37,23,"Yusuf yanlarına varınca, kardeşleri sırtındaki renkli uzun giysiyi çekip çıkardılar"));
trsuperobject.biblemap.put("b1_37_24", new trsuperobject(1,37,24,"ve onu susuz, boş bir kuyuya attılar."));
trsuperobject.biblemap.put("b1_37_25", new trsuperobject(1,37,25,"Yemek yemek için oturduklarında, Gilat yönünden bir İsmaili kervanının geldiğini gördüler. Develeri kitre, pelesenk, laden yüklüydü. Mısıra gidiyorlardı."));
trsuperobject.biblemap.put("b1_37_26", new trsuperobject(1,37,26,"Yahuda, kardeşlerine, ‹‹Kardeşimizi öldürür, suçumuzu gizlersek ne kazanırız?›› dedi,"));
trsuperobject.biblemap.put("b1_37_27", new trsuperobject(1,37,27,"‹‹Gelin onu İsmaililere satalım. Böylece canına dokunmamış oluruz. Çünkü o kardeşimizdir, aynı kanı taşıyoruz.›› Kardeşleri kabul etti."));
trsuperobject.biblemap.put("b1_37_28", new trsuperobject(1,37,28,"Midyanlı tüccarlar oradan geçerken, kardeşleri Yusufu kuyudan çekip çıkardılar, yirmi gümüşe İsmaililere sattılar. İsmaililer Yusufu Mısıra götürdüler."));
trsuperobject.biblemap.put("b1_37_29", new trsuperobject(1,37,29,"Kuyuya geri dönen Ruben Yusufu orada göremeyince üzüntüden giysilerini yırttı."));
trsuperobject.biblemap.put("b1_37_30", new trsuperobject(1,37,30,"Kardeşlerinin yanına gidip, ‹‹Çocuk orada yok›› dedi, ‹‹Ne yapacağım şimdi ben?››"));
trsuperobject.biblemap.put("b1_37_31", new trsuperobject(1,37,31,"Bunun üzerine bir teke keserek Yusufun renkli uzun giysisini kanına buladılar."));
trsuperobject.biblemap.put("b1_37_32", new trsuperobject(1,37,32,"Giysiyi babalarına götürerek, ‹‹Bunu bulduk›› dediler, ‹‹Bak, bakalım, oğlunun mu, değil mi?››"));
trsuperobject.biblemap.put("b1_37_33", new trsuperobject(1,37,33,"Yakup giysiyi tanıdı, ‹‹Evet, bu oğlumun giysisi›› dedi, ‹‹Onu yabanıl bir hayvan yemiş olmalı. Yusufu parçalamış olsa gerek.››"));
trsuperobject.biblemap.put("b1_37_34", new trsuperobject(1,37,34,"Yakup üzüntüden giysilerini yırttı, beline çul sardı, oğlu için uzun süre yas tuttu."));
trsuperobject.biblemap.put("b1_37_35", new trsuperobject(1,37,35,"Bütün oğulları, kızları onu avutmaya çalıştılarsa da o avunmak istemedi. ‹‹Oğlumun yanına, ölüler diyarına yas tutarak gideceğim›› diyerek oğlu için ağlamaya devam etti."));
trsuperobject.biblemap.put("b1_37_36", new trsuperobject(1,37,36,"Bu arada Midyanlılar da Yusuf'u Mısır'da firavunun bir görevlisine, muhafız birliği komutanı Potifar'a sattılar."));
trsuperobject.biblemap.put("b1_38_1", new trsuperobject(1,38,1,"O sıralarda Yahuda kardeşlerinden ayrılarak Adullamlı Hira adında bir adamın yanına gitti."));
trsuperobject.biblemap.put("b1_38_2", new trsuperobject(1,38,2,"Orada Kenanlı bir kızla karşılaştı. Kızın babasının adı Şuaydı. Yahuda kızla evlendi."));
trsuperobject.biblemap.put("b1_38_3", new trsuperobject(1,38,3,"Kadın hamile kaldı ve bir erkek çocuk doğurdu. Yahuda ona Er adını verdi."));
trsuperobject.biblemap.put("b1_38_4", new trsuperobject(1,38,4,"Kadın yine hamile kaldı, bir erkek çocuk daha doğurdu, adını Onan koydu."));
trsuperobject.biblemap.put("b1_38_5", new trsuperobject(1,38,5,"Yine bir erkek çocuk doğurdu, adını Şela koydu. Şela doğduğu zaman Yahuda Kezivdeydi."));
trsuperobject.biblemap.put("b1_38_6", new trsuperobject(1,38,6,"Yahuda ilk oğlu Er için bir kadın aldı. Kadının adı Tamardı."));
trsuperobject.biblemap.put("b1_38_7", new trsuperobject(1,38,7,"Yahudanın ilk oğlu Er, RABbin gözünde kötüydü. Bu yüzden RAB onu öldürdü."));
trsuperobject.biblemap.put("b1_38_8", new trsuperobject(1,38,8,"Yahuda Onana, ‹‹Kardeşinin karısıyla evlen›› dedi, ‹‹Kayınbiraderlik görevini yap. Kardeşinin soyunu sürdür.››"));
trsuperobject.biblemap.put("b1_38_9", new trsuperobject(1,38,9,"Ama Onan doğacak çocukların kendisine ait olmayacağını biliyordu. Bu yüzden ne zaman kardeşinin karısıyla yatsa, kardeşine soy yetiştirmemek için menisini yere boşaltıyordu."));
trsuperobject.biblemap.put("b1_38_10", new trsuperobject(1,38,10,"Bu yaptığı RABbin gözünde kötüydü. Bu yüzden RAB onu da öldürdü."));
trsuperobject.biblemap.put("b1_38_11", new trsuperobject(1,38,11,"Bunun üzerine Yahuda, gelini Tamara, ‹‹Babanın evine dön›› dedi, ‹‹Oğlum Şela büyüyünceye kadar orada dul olarak yaşa.›› Yahuda, ‹‹Şela da kardeşleri gibi ölebilir›› diye düşünüyordu. Böylece Tamar babasının evine döndü."));
trsuperobject.biblemap.put("b1_38_12", new trsuperobject(1,38,12,"Uzun süre sonra Şuanın kızı olan Yahudanın karısı öldü. Yahuda yası bittikten sonra arkadaşı Adullamlı Hirayla birlikte Timnaya, sürüsünü kırkanların yanına gitti."));
trsuperobject.biblemap.put("b1_38_13", new trsuperobject(1,38,13,"Tamara, ‹‹Kayınbaban sürüsünü kırkmak için Timnaya gidiyor›› diye haber verdiler."));
trsuperobject.biblemap.put("b1_38_14", new trsuperobject(1,38,14,"Tamar üzerindeki dul giysilerini çıkardı. Peçesini örttü, sarınıp Timna yolu üzerindeki Enayim Kapısında oturdu. Çünkü Şela büyüdüğü halde onunla evlenmesine izin verilmediğini görmüştü."));
trsuperobject.biblemap.put("b1_38_15", new trsuperobject(1,38,15,"Yahuda onu görünce fahişe sandı. Çünkü yüzü örtülüydü."));
trsuperobject.biblemap.put("b1_38_16", new trsuperobject(1,38,16,"Yolun kenarına, ona doğru seğirterek, kendi gelini olduğunu bilmeden, ‹‹Hadi gel, seninle yatmak istiyorum›› dedi. Tamar, ‹‹Seninle yatarsam, bana ne vereceksin?›› diye sordu."));
trsuperobject.biblemap.put("b1_38_17", new trsuperobject(1,38,17,"Yahuda, ‹‹Sürümden sana bir oğlak göndereyim›› dedi. Tamar, ‹‹Oğlağı gönderinceye kadar rehin olarak bana bir şey verebilir misin?›› dedi."));
trsuperobject.biblemap.put("b1_38_18", new trsuperobject(1,38,18,"Yahuda, ‹‹Ne vereyim?›› diye sordu. Tamar, ‹‹Mührünü, kaytanını ve elindeki değneği›› diye yanıtladı. Yahuda bunları verip onunla yattı. Tamar hamile kaldı."));
trsuperobject.biblemap.put("b1_38_19", new trsuperobject(1,38,19,"Gidip peçesini çıkardı, yine dul giysilerini giydi."));
trsuperobject.biblemap.put("b1_38_20", new trsuperobject(1,38,20,"Bu arada Yahuda rehin bıraktığı eşyaları geri almak için Adullamlı arkadaşıyla kadına bir oğlak gönderdi. Ne var ki arkadaşı kadını bulamadı."));
trsuperobject.biblemap.put("b1_38_21", new trsuperobject(1,38,21,"O çevrede yaşayanlara, ‹‹Enayimde, yol kenarında bir fahişe vardı, nerede o?›› diye sordu. ‹‹Burada öyle bir kadın yok›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b1_38_22", new trsuperobject(1,38,22,"Bunun üzerine Yahudanın yanına dönerek, ‹‹Kadını bulamadım›› dedi, ‹‹O çevrede yaşayanlar da ‹Burada fahişe yok› dediler.››"));
trsuperobject.biblemap.put("b1_38_23", new trsuperobject(1,38,23,"Yahuda, ‹‹Varsın eşyalar onun olsun›› dedi, ‹‹Kimseyi kendimize güldürmeyelim. Ben oğlağı gönderdim, ama sen kadını bulamadın.››"));
trsuperobject.biblemap.put("b1_38_24", new trsuperobject(1,38,24,"Yaklaşık üç ay sonra Yahudaya, ‹‹Gelinin Tamar zina etmiş, şu anda hamile›› diye haber verdiler. Yahuda, ‹‹Onu dışarıya çıkarıp yakın›› dedi."));
trsuperobject.biblemap.put("b1_38_25", new trsuperobject(1,38,25,"Tamar dışarı çıkarılınca, kayınbabasına, ‹‹Ben bu eşyaların sahibinden hamile kaldım›› diye haber gönderdi, ‹‹Lütfen şunlara bak. Bu mühür, kaytan, değnek kime ait?››"));
trsuperobject.biblemap.put("b1_38_26", new trsuperobject(1,38,26,"Yahuda eşyaları tanıdı. ‹‹O benden daha doğru bir kişi›› dedi, ‹‹Çünkü onu oğlum Şelaya almadım.›› Bir daha onunla yatmadı."));
trsuperobject.biblemap.put("b1_38_27", new trsuperobject(1,38,27,"Doğum vakti gelince Tamarın rahminde ikiz olduğu anlaşıldı."));
trsuperobject.biblemap.put("b1_38_28", new trsuperobject(1,38,28,"Doğum yaparken ikizlerden biri elini dışarı çıkardı. Ebe çocuğun elini yakalayıp bileğine kırmızı bir iplik bağladı, ‹‹Bu önce doğdu›› dedi."));
trsuperobject.biblemap.put("b1_38_29", new trsuperobject(1,38,29,"Ne var ki, çocuk elini içeri çekti, o sırada da kardeşi doğdu. Ebe, ‹‹Kendine böyle mi gedik açtın?›› dedi. Bu yüzden çocuğa Peres adı kondu."));
trsuperobject.biblemap.put("b1_38_30", new trsuperobject(1,38,30,"Sonra bileğine kırmızı iplik bağlı kardeşi doğdu. Ona da Zerah adı verildi."));
trsuperobject.biblemap.put("b1_39_1", new trsuperobject(1,39,1,"İsmaililer Yusufu Mısıra götürmüştü. Firavunun görevlisi, muhafız birliği komutanı Mısırlı Potifar onu İsmaililerden satın almıştı."));
trsuperobject.biblemap.put("b1_39_3", new trsuperobject(1,39,3,"Efendisi RABbin Yusufla birlikte olduğunu, yaptığı her işte onu başarılı kıldığını gördü."));
trsuperobject.biblemap.put("b1_39_4", new trsuperobject(1,39,4,"Yusuftan hoşnut kalarak onu özel hizmetine aldı. Evinin ve sahip olduğu her şeyin sorumluluğunu ona verdi."));
trsuperobject.biblemap.put("b1_39_5", new trsuperobject(1,39,5,"Yusufu evinin ve sahip olduğu her şeyin sorumlusu atadığı andan itibaren RAB Yusuf sayesinde Potifarın evini kutsadı. Evini, tarlasını, kendisine ait her şeyi bereketli kıldı."));
trsuperobject.biblemap.put("b1_39_6", new trsuperobject(1,39,6,"Potifar sahip olduğu her şeyin sorumluluğunu Yusufa verdi; yediği yemek dışında hiçbir şeyle ilgilenmedi. Yusuf güzel yapılı, yakışıklıydı."));
trsuperobject.biblemap.put("b1_39_7", new trsuperobject(1,39,7,"Bir süre sonra efendisinin karısı ona göz koyarak, ‹‹Benimle yat›› dedi."));
trsuperobject.biblemap.put("b1_39_8", new trsuperobject(1,39,8,"Ama Yusuf reddetti. ‹‹Ben burada olduğum için efendim evdeki hiçbir şeyle ilgilenme gereğini duymuyor›› dedi, ‹‹Sahip olduğu her şeyin yönetimini bana verdi."));
trsuperobject.biblemap.put("b1_39_9", new trsuperobject(1,39,9,"Bu evde ben de onun kadar yetkiliyim. Senin dışında hiçbir şeyi benden esirgemedi. Sen onun karısısın. Nasıl böyle bir kötülük yapar, Tanrıya karşı günah işlerim?››"));
trsuperobject.biblemap.put("b1_39_10", new trsuperobject(1,39,10,"Potifarın karısı her gün kendisiyle yatması ya da birlikte olması için direttiyse de, Yusuf onun isteğini kabul etmedi."));
trsuperobject.biblemap.put("b1_39_11", new trsuperobject(1,39,11,"Bir gün Yusuf olağan işlerini yapmak üzere eve gitti. İçerde ev halkından hiç kimse yoktu."));
trsuperobject.biblemap.put("b1_39_12", new trsuperobject(1,39,12,"Potifarın karısı Yusufun giysisini tutarak, ‹‹Benimle yat›› dedi. Ama Yusuf giysisini onun elinde bırakıp evden dışarı kaçtı."));
trsuperobject.biblemap.put("b1_39_13", new trsuperobject(1,39,13,"Kadın Yusufun giysisini bırakıp kaçtığını görünce,"));
trsuperobject.biblemap.put("b1_39_14", new trsuperobject(1,39,14,"uşaklarını çağırdı. ‹‹Bakın şuna!›› dedi, ‹‹Kocamın getirdiği bu İbrani bizi rezil etti. Yanıma geldi, benimle yatmak istedi. Ben de bağırdım."));
trsuperobject.biblemap.put("b1_39_15", new trsuperobject(1,39,15,"Bağırdığımı duyunca giysisini yanımda bırakıp dışarı kaçtı.››"));
trsuperobject.biblemap.put("b1_39_16", new trsuperobject(1,39,16,"Efendisi eve gelinceye kadar Yusufun giysisini yanında alıkoydu."));
trsuperobject.biblemap.put("b1_39_17", new trsuperobject(1,39,17,"Ona da aynı şeyleri anlattı: ‹‹Buraya getirdiğin İbrani köle yanıma gelip beni aşağılamak istedi."));
trsuperobject.biblemap.put("b1_39_18", new trsuperobject(1,39,18,"Ama ben bağırınca giysisini yanımda bırakıp kaçtı.››"));
trsuperobject.biblemap.put("b1_39_19", new trsuperobject(1,39,19,"Karısının, ‹‹Kölen bana böyle yaptı›› diyerek anlattıklarını duyunca, Yusufun efendisinin öfkesi tepesine çıktı."));
trsuperobject.biblemap.put("b1_39_20", new trsuperobject(1,39,20,"Yusufu yakalayıp zindana, kralın tutsaklarının bağlı olduğu yere attı. Ama Yusuf zindandayken"));
trsuperobject.biblemap.put("b1_39_21", new trsuperobject(1,39,21,"RAB onunla birlikteydi. Ona iyilik etti. Zindancıbaşı Yusuftan hoşnut kaldı."));
trsuperobject.biblemap.put("b1_39_22", new trsuperobject(1,39,22,"Bütün tutsakların yönetimini ona verdi. Zindanda olup biten her şeyden Yusuf sorumluydu."));
trsuperobject.biblemap.put("b1_39_23", new trsuperobject(1,39,23,"Zindancıbaşı Yusuf'un sorumlu olduğu işlerle hiç ilgilenmezdi. Çünkü RAB Yusuf'la birlikteydi ve yaptığı her işte onu başarılı kılıyordu."));
trsuperobject.biblemap.put("b1_40_1", new trsuperobject(1,40,1,"Bir süre sonra Mısır Kralının sakisiyle fırıncısı efendilerini gücendirdiler."));
trsuperobject.biblemap.put("b1_40_2", new trsuperobject(1,40,2,"Firavun bu iki görevlisine, baş sakiyle fırıncıbaşına öfkelendi."));
trsuperobject.biblemap.put("b1_40_3", new trsuperobject(1,40,3,"Onları muhafız birliği komutanının evinde, Yusufun tutsak olduğu zindanda göz altına aldı."));
trsuperobject.biblemap.put("b1_40_4", new trsuperobject(1,40,4,"Muhafız birliği komutanı Yusufu onların hizmetine atadı. Bir süre zindanda kaldılar."));
trsuperobject.biblemap.put("b1_40_5", new trsuperobject(1,40,5,"Firavunun sakisiyle fırıncısı tutsak oldukları zindanda aynı gece birer düş gördüler. Düşleri farklı anlamlar taşıyordu."));
trsuperobject.biblemap.put("b1_40_6", new trsuperobject(1,40,6,"Sabah Yusuf yanlarına gittiğinde, onları tedirgin gördü."));
trsuperobject.biblemap.put("b1_40_7", new trsuperobject(1,40,7,"Efendisinin evinde, kendisiyle birlikte zindanda kalan firavunun görevlilerine, ‹‹Niçin suratınız asık bugün?›› diye sordu."));
trsuperobject.biblemap.put("b1_40_8", new trsuperobject(1,40,8,"‹‹Düş gördük ama yorumlayacak kimse yok›› dediler. Yusuf, ‹‹Yorum Tanrıya özgü değil mi?›› dedi, ‹‹Lütfen düşünüzü bana anlatın.››"));
trsuperobject.biblemap.put("b1_40_9", new trsuperobject(1,40,9,"Baş saki düşünü Yusufa anlattı: ‹‹Düşümde önümde bir asma gördüm."));
trsuperobject.biblemap.put("b1_40_10", new trsuperobject(1,40,10,"Üç çubuğu vardı. Tomurcuklar açar açmaz çiçeklendi, salkım salkım üzüm verdi."));
trsuperobject.biblemap.put("b1_40_11", new trsuperobject(1,40,11,"Firavunun kâsesi elimdeydi. Üzümleri alıp firavunun kâsesine sıktım. Sonra kâseyi ona verdim.››"));
trsuperobject.biblemap.put("b1_40_12", new trsuperobject(1,40,12,"Yusuf, ‹‹Bu şu anlama gelir›› dedi, ‹‹Üç çubuk üç gün demektir."));
trsuperobject.biblemap.put("b1_40_13", new trsuperobject(1,40,13,"Üç gün içinde firavun seni zindandan çıkaracak, yine eski görevine döneceksin. Geçmişte olduğu gibi yine ona sakilik yapacaksın."));
trsuperobject.biblemap.put("b1_40_14", new trsuperobject(1,40,14,"Ama her şey yolunda giderse, lütfen beni anımsa. Bir iyilik yap, firavuna benden söz et. Çıkar beni bu zindandan."));
trsuperobject.biblemap.put("b1_40_15", new trsuperobject(1,40,15,"Çünkü ben İbrani ülkesinden zorla kaçırıldım. Burada da zindana atılacak bir şey yapmadım.››"));
trsuperobject.biblemap.put("b1_40_16", new trsuperobject(1,40,16,"Fırıncıbaşı bu iyi yorumu duyunca, Yusufa, ‹‹Ben de bir düş gördüm›› dedi, ‹‹Başımın üstünde üç sepet beyaz ekmek vardı."));
trsuperobject.biblemap.put("b1_40_17", new trsuperobject(1,40,17,"En üstteki sepette firavun için pişirilmiş çeşitli pastalar vardı. Kuşlar başımın üstündeki sepetten pastaları yiyorlardı.››"));
trsuperobject.biblemap.put("b1_40_18", new trsuperobject(1,40,18,"Yusuf, ‹‹Bu şu anlama gelir›› dedi, ‹‹Üç sepet üç gün demektir."));
trsuperobject.biblemap.put("b1_40_19", new trsuperobject(1,40,19,"Üç gün içinde firavun seni zindandan çıkarıp ağaca asacak. Kuşlar etini yiyecekler.››"));
trsuperobject.biblemap.put("b1_40_20", new trsuperobject(1,40,20,"Üç gün sonra, firavun doğum gününde bütün görevlilerine bir şölen verdi. Görevlilerinin önünde baş sakisiyle fırıncıbaşını zindandan çıkardı."));
trsuperobject.biblemap.put("b1_40_21-22", new trsuperobject(1,40,21-22,"Yusufun yaptığı yoruma uygun olarak baş sakisini eski görevine atadı. Baş saki firavuna şarap sunmaya başladı. Ama firavun fırıncıbaşını astırdı."));
trsuperobject.biblemap.put("b1_40_23", new trsuperobject(1,40,23,"Gelgelelim, baş saki Yusuf'u anımsamadı, unuttu gitti."));
trsuperobject.biblemap.put("b1_41_1", new trsuperobject(1,41,1,"Tam iki yıl sonra firavun bir düş gördü: Nil Irmağının kıyısında duruyordu."));
trsuperobject.biblemap.put("b1_41_2", new trsuperobject(1,41,2,"Irmaktan güzel ve semiz yedi inek çıktı. Sazlar arasında otlamaya başladılar."));
trsuperobject.biblemap.put("b1_41_3", new trsuperobject(1,41,3,"Sonra yedi çirkin ve cılız inek çıktı. Irmağın kıyısında öbür ineklerin yanında durdular."));
trsuperobject.biblemap.put("b1_41_4", new trsuperobject(1,41,4,"Çirkin ve cılız inekler güzel ve semiz yedi ineği yiyince, firavun uyandı."));
trsuperobject.biblemap.put("b1_41_5", new trsuperobject(1,41,5,"Yine uykuya daldı, bu kez başka bir düş gördü: Bir sapta yedi güzel ve dolgun başak bitti."));
trsuperobject.biblemap.put("b1_41_6", new trsuperobject(1,41,6,"Sonra, cılız ve doğu rüzgarıyla kavrulmuş yedi başak daha bitti."));
trsuperobject.biblemap.put("b1_41_7", new trsuperobject(1,41,7,"Cılız başaklar, yedi güzel ve dolgun başağı yuttular. Firavun uyandı, düş gördüğünü anladı."));
trsuperobject.biblemap.put("b1_41_8", new trsuperobject(1,41,8,"Sabah uyandığında kaygılıydı. Bütün Mısırlı büyücüleri, bilgeleri çağırttı. Onlara gördüğü düşleri anlattı. Ama hiçbiri firavunun düşlerini yorumlayamadı."));
trsuperobject.biblemap.put("b1_41_9", new trsuperobject(1,41,9,"Bu arada baş saki firavuna, ‹‹Bugün suçumu itiraf etmeliyim›› dedi,"));
trsuperobject.biblemap.put("b1_41_10", new trsuperobject(1,41,10,"‹‹Kullarına -bana ve fırıncıbaşına- öfkelenince bizi zindana, muhafız birliği komutanının evine kapattın."));
trsuperobject.biblemap.put("b1_41_11", new trsuperobject(1,41,11,"Bir gece ikimiz de düş gördük. Düşlerimiz farklı anlamlar taşıyordu."));
trsuperobject.biblemap.put("b1_41_12", new trsuperobject(1,41,12,"Orada bizimle birlikte muhafız birliği komutanının kölesi İbrani bir genç vardı. Gördüğümüz düşleri ona anlattık. Bize bir bir yorumladı."));
trsuperobject.biblemap.put("b1_41_13", new trsuperobject(1,41,13,"Her şey onun yorumladığı gibi çıktı: Ben görevime döndüm, fırıncıbaşıysa asıldı.››"));
trsuperobject.biblemap.put("b1_41_14", new trsuperobject(1,41,14,"Firavun Yusufu çağırttı. Hemen onu zindandan çıkardılar. Yusuf tıraş olup giysilerini değiştirdikten sonra firavunun huzuruna çıktı."));
trsuperobject.biblemap.put("b1_41_15", new trsuperobject(1,41,15,"Firavun Yusufa, ‹‹Bir düş gördüm›› dedi, ‹‹Ama kimse yorumlayamadı. Duyduğun her düşü yorumlayabildiğini işittim.››"));
trsuperobject.biblemap.put("b1_41_16", new trsuperobject(1,41,16,"Yusuf, ‹‹Ben yorumlayamam›› dedi, ‹‹Firavuna en uygun yorumu Tanrı yapacaktır.››"));
trsuperobject.biblemap.put("b1_41_17", new trsuperobject(1,41,17,"Firavun Yusufa anlatmaya başladı: ‹‹Düşümde bir ırmak kıyısında duruyordum."));
trsuperobject.biblemap.put("b1_41_18", new trsuperobject(1,41,18,"Irmaktan semiz ve güzel yedi inek çıktı. Sazlar arasında otlamaya başladılar."));
trsuperobject.biblemap.put("b1_41_19", new trsuperobject(1,41,19,"Sonra arık, çirkin, cılız yedi inek daha çıktı. Mısırda onlar kadar çirkin inek görmedim."));
trsuperobject.biblemap.put("b1_41_20", new trsuperobject(1,41,20,"Cılız ve çirkin inekler ilk çıkan yedi semiz ineği yedi."));
trsuperobject.biblemap.put("b1_41_21", new trsuperobject(1,41,21,"Ancak kötü görünüşleri değişmedi. Sanki bir şey yememiş gibi görünüyorlardı. Sonra uyandım."));
trsuperobject.biblemap.put("b1_41_22", new trsuperobject(1,41,22,"‹‹Bir de düşümde bir sapta dolgun ve güzel yedi başak bittiğini gördüm."));
trsuperobject.biblemap.put("b1_41_23", new trsuperobject(1,41,23,"Sonra solgun, cılız, doğu rüzgarının kavurduğu yedi başak daha bitti."));
trsuperobject.biblemap.put("b1_41_24", new trsuperobject(1,41,24,"Cılız başaklar yedi güzel başağı yuttular. Büyücülere bunu anlattım. Ama hiçbiri yorumlayamadı.››"));
trsuperobject.biblemap.put("b1_41_25", new trsuperobject(1,41,25,"Yusuf, ‹‹Efendim, iki düş de aynı anlamı taşıyor›› dedi, ‹‹Tanrı ne yapacağını sana bildirmiş."));
trsuperobject.biblemap.put("b1_41_26", new trsuperobject(1,41,26,"Yedi güzel inek yedi yıl demektir. Yedi güzel başak da yedi yıldır. Aynı anlama geliyor."));
trsuperobject.biblemap.put("b1_41_27", new trsuperobject(1,41,27,"Daha sonra çıkan yedi cılız, çirkin inek ve doğu rüzgarının kavurduğu yedi solgun başaksa yedi yıl kıtlık olacağı anlamına gelir."));
trsuperobject.biblemap.put("b1_41_28", new trsuperobject(1,41,28,"‹‹Söylediğim gibi, Tanrı ne yapacağını sana göstermiş."));
trsuperobject.biblemap.put("b1_41_29", new trsuperobject(1,41,29,"Mısırda yedi yıl bolluk olacak."));
trsuperobject.biblemap.put("b1_41_30", new trsuperobject(1,41,30,"Sonra yedi yıl öyle bir kıtlık olacak ki, bolluk yılları hiç anımsanmayacak. Çünkü kıtlık ülkeyi kasıp kavuracak."));
trsuperobject.biblemap.put("b1_41_31", new trsuperobject(1,41,31,"Ardından gelen kıtlık bolluğu unutturacak, çünkü çok şiddetli olacak."));
trsuperobject.biblemap.put("b1_41_32", new trsuperobject(1,41,32,"Bu konuda iki kez düş görmenin anlamı, Tanrının kesin kararını verdiğini ve en kısa zamanda uygulayacağını gösteriyor."));
trsuperobject.biblemap.put("b1_41_33", new trsuperobject(1,41,33,"‹‹Şimdi firavunun akıllı, bilgili bir adam bulup onu Mısırın başına getirmesi gerekir."));
trsuperobject.biblemap.put("b1_41_34", new trsuperobject(1,41,34,"Ülke çapında adamlar görevlendirmeli, bunlar yedi bolluk yılı boyunca ürünlerin beşte birini toplamalı."));
trsuperobject.biblemap.put("b1_41_35", new trsuperobject(1,41,35,"Gelecek verimli yılların bütün yiyeceğini toplasınlar, firavunun yönetimi altında kentlerde depolayıp korusunlar."));
trsuperobject.biblemap.put("b1_41_36", new trsuperobject(1,41,36,"Bu yiyecek, gelecek yedi kıtlık yılı boyunca Mısırda ihtiyat olarak kullanılacak, ülke kıtlıktan kırılmayacak.››"));
trsuperobject.biblemap.put("b1_41_37", new trsuperobject(1,41,37,"Bu öneri firavunla görevlilerine iyi göründü."));
trsuperobject.biblemap.put("b1_41_38", new trsuperobject(1,41,38,"Firavun görevlilerine, ‹‹Bu adam gibi Tanrı Ruhuna sahip birini bulabilir miyiz?›› diye sordu."));
trsuperobject.biblemap.put("b1_41_39", new trsuperobject(1,41,39,"Sonra Yusufa, ‹‹Madem Tanrı bütün bunları sana açıkladı, senden daha akıllısı, bilgilisi yoktur›› dedi,"));
trsuperobject.biblemap.put("b1_41_40", new trsuperobject(1,41,40,"‹‹Sarayımın yönetimini sana vereceğim. Bütün halkım buyruklarına uyacak. Tahttan başka senden üstünlüğüm olmayacak."));
trsuperobject.biblemap.put("b1_41_41", new trsuperobject(1,41,41,"Seni bütün Mısıra yönetici atıyorum.››"));
trsuperobject.biblemap.put("b1_41_42", new trsuperobject(1,41,42,"Sonra mührünü parmağından çıkarıp Yusufun parmağına taktı. Ona ince ketenden giysi giydirdi. Boynuna altın zincir taktı."));
trsuperobject.biblemap.put("b1_41_43", new trsuperobject(1,41,43,"Onu kendi yardımcısının arabasına bindirdi. Yusufun önünde, ‹‹Yol açın!›› diye bağırdılar. Böylece firavun ona bütün Mısırın yönetimini verdi."));
trsuperobject.biblemap.put("b1_41_44", new trsuperobject(1,41,44,"Firavun Yusufa, ‹‹Firavun benim›› dedi, ‹‹Ama Mısırda senden izinsiz kimse elini ayağını oynatmayacak.››"));
trsuperobject.biblemap.put("b1_41_45", new trsuperobject(1,41,45,"Yusufun adını Safenat-Paneah koydu. On Kentinin kâhini Potiferanın kızı Asenatı da ona karı olarak verdi. Yusuf ülkeyi boydan boya dolaştı."));
trsuperobject.biblemap.put("b1_41_46", new trsuperobject(1,41,46,"Yusuf firavunun hizmetine girdiğinde otuz yaşındaydı. Firavunun huzurundan ayrıldıktan sonra bütün Mısırı dolaştı."));
trsuperobject.biblemap.put("b1_41_47", new trsuperobject(1,41,47,"Yedi bolluk yılı boyunca toprak çok ürün verdi."));
trsuperobject.biblemap.put("b1_41_48", new trsuperobject(1,41,48,"Yusuf Mısırda yedi yıl içinde yetişen bütün ürünleri toplayıp kentlerde depoladı. Her kente o kentin çevresindeki tarlalarda yetişen ürünleri koydu."));
trsuperobject.biblemap.put("b1_41_49", new trsuperobject(1,41,49,"Denizin kumu kadar çok buğday depoladı; öyle ki, ölçmekten vazgeçti. Çünkü buğday ölçülemeyecek kadar çoktu."));
trsuperobject.biblemap.put("b1_41_50", new trsuperobject(1,41,50,"Kıtlık yılları başlamadan, On Kentinin kâhini Potiferanın kızı Asenat Yusufa iki erkek çocuk doğurdu."));
trsuperobject.biblemap.put("b1_41_51", new trsuperobject(1,41,51,"Yusuf ilk oğlunun adını Manaşşe koydu. ‹‹Tanrı bana bütün acılarımı ve babamın ailesini unutturdu›› dedi."));
trsuperobject.biblemap.put("b1_41_52", new trsuperobject(1,41,52,"‹‹Tanrı sıkıntı çektiğim ülkede beni verimli kıldı›› diyerek ikinci oğlunun adını Efrayim koydu."));
trsuperobject.biblemap.put("b1_41_53", new trsuperobject(1,41,53,"Mısırda yedi bolluk yılı sona erdi."));
trsuperobject.biblemap.put("b1_41_54", new trsuperobject(1,41,54,"Yusufun söylemiş olduğu gibi yedi kıtlık yılı başgösterdi. Bütün ülkelerde kıtlık vardı, ama Mısırın her yanında yiyecek bulunuyordu."));
trsuperobject.biblemap.put("b1_41_55", new trsuperobject(1,41,55,"Mısırlılar aç kalınca, yiyecek için firavuna yakardılar. Firavun, ‹‹Yusufa gidin›› dedi, ‹‹O size ne derse öyle yapın.››"));
trsuperobject.biblemap.put("b1_41_56", new trsuperobject(1,41,56,"Kıtlık bütün ülkeyi sarınca, Yusuf depoları açıp Mısırlılara buğday satmaya başladı. Çünkü kıtlık Mısırı boydan boya kavuruyordu."));
trsuperobject.biblemap.put("b1_41_57", new trsuperobject(1,41,57,"Bütün ülkelerden insanlar da buğday satın almak için Mısır'a, Yusuf'a geliyordu. Çünkü kıtlık bütün dünyayı sarmıştı ve şiddetliydi."));
trsuperobject.biblemap.put("b1_42_1", new trsuperobject(1,42,1,"Yakup Mısırda buğday olduğunu öğrenince, oğullarına, ‹‹Neden birbirinize bakıp duruyorsunuz?›› dedi,"));
trsuperobject.biblemap.put("b1_42_2", new trsuperobject(1,42,2,"‹‹Mısırda buğday olduğunu duydum. Gidin, satın alın ki, yaşayalım, yoksa öleceğiz.››"));
trsuperobject.biblemap.put("b1_42_3", new trsuperobject(1,42,3,"Böylece Yusufun on kardeşi buğday almak için Mısıra gittiler."));
trsuperobject.biblemap.put("b1_42_4", new trsuperobject(1,42,4,"Ancak Yakup Yusufun kardeşi Benyamini onlarla birlikte göndermedi, çünkü oğlunun başına bir şey gelmesinden korkuyordu."));
trsuperobject.biblemap.put("b1_42_5", new trsuperobject(1,42,5,"Buğday satın almaya gelenler arasında İsrailin oğulları da vardı. Çünkü Kenan ülkesinde de kıtlık hüküm sürüyordu."));
trsuperobject.biblemap.put("b1_42_6", new trsuperobject(1,42,6,"Yusuf ülkenin yöneticisiydi, herkese o buğday satıyordu. Kardeşleri gelip onun önünde yere kapandılar."));
trsuperobject.biblemap.put("b1_42_7", new trsuperobject(1,42,7,"Yusuf kardeşlerini görünce tanıdı. Ama onlara yabancı gibi davranarak sert konuştu: ‹‹Nereden geliyorsunuz?›› ‹‹Kenan ülkesinden›› diye yanıtladılar, ‹‹Yiyecek satın almaya geldik.››"));
trsuperobject.biblemap.put("b1_42_8", new trsuperobject(1,42,8,"Yusuf kardeşlerini tanıdıysa da kardeşleri onu tanımadılar."));
trsuperobject.biblemap.put("b1_42_9", new trsuperobject(1,42,9,"Yusuf onlarla ilgili düşlerini anımsayarak, ‹‹Siz casussunuz›› dedi, ‹‹Ülkenin zayıf noktalarını öğrenmeye geldiniz.››"));
trsuperobject.biblemap.put("b1_42_10", new trsuperobject(1,42,10,"‹‹Aman, efendim›› diye karşılık verdiler, ‹‹Biz kulların yalnızca yiyecek satın almaya geldik."));
trsuperobject.biblemap.put("b1_42_11", new trsuperobject(1,42,11,"Hepimiz aynı babanın çocuklarıyız. Biz kulların dürüst insanlarız, casus değiliz.››"));
trsuperobject.biblemap.put("b1_42_12", new trsuperobject(1,42,12,"Yusuf, ‹‹Hayır!›› dedi, ‹‹Siz ülkenin zayıf noktalarını öğrenmeye geldiniz.››"));
trsuperobject.biblemap.put("b1_42_13", new trsuperobject(1,42,13,"Kardeşleri, ‹‹Biz kulların on iki kardeşiz›› dediler, ‹‹Hepimiz Kenan ülkesinde yaşayan aynı babanın çocuklarıyız. En küçüğümüz babamızın yanında kaldı, biri de kayboldu.››"));
trsuperobject.biblemap.put("b1_42_14", new trsuperobject(1,42,14,"Yusuf, ‹‹Söylediğim gibi›› dedi, ‹‹Casussunuz siz."));
trsuperobject.biblemap.put("b1_42_15", new trsuperobject(1,42,15,"Sizi sınayacağım. Firavunun başına ant içerim. Küçük kardeşiniz de gelmedikçe, buradan ayrılamazsınız."));
trsuperobject.biblemap.put("b1_42_16", new trsuperobject(1,42,16,"Aranızdan birini gönderin, kardeşinizi getirsin. Geri kalanlarınız göz altına alınacak. Anlattıklarınız doğru mu, değil mi, sizi sınayacağız. Değilse, firavunun başına ant içerim ki casussunuz.››"));
trsuperobject.biblemap.put("b1_42_17", new trsuperobject(1,42,17,"Üç gün onları göz altında tuttu."));
trsuperobject.biblemap.put("b1_42_18", new trsuperobject(1,42,18,"Üçüncü gün, ‹‹Bir koşulla canınızı bağışlarım›› dedi, ‹‹Ben Tanrıdan korkarım."));
trsuperobject.biblemap.put("b1_42_19", new trsuperobject(1,42,19,"Dürüst olduğunuzu kanıtlamak için, içinizden biri göz altında tutulduğunuz evde kalsın, ötekiler gidip aç kalan ailenize buğday götürsün."));
trsuperobject.biblemap.put("b1_42_20", new trsuperobject(1,42,20,"Sonra küçük kardeşinizi bana getirin. Böylece anlattıklarınızın doğru olup olmadığı ortaya çıkar, ölümden kurtulursunuz.›› Kabul ettiler."));
trsuperobject.biblemap.put("b1_42_21", new trsuperobject(1,42,21,"Birbirlerine, ‹‹Besbelli kardeşimize yaptığımızın cezasını çekiyoruz›› dediler, ‹‹Bize yalvardığında nasıl sıkıntı çektiğini gördük, ama dinlemedik. Bu sıkıntı onun için başımıza geldi.››"));
trsuperobject.biblemap.put("b1_42_22", new trsuperobject(1,42,22,"Ruben, ‹‹Çocuğa zarar vermeyin diye sizi uyarmadım mı?›› dedi, ‹‹Ama dinlemediniz. İşte şimdi kanının hesabı soruluyor.››"));
trsuperobject.biblemap.put("b1_42_23", new trsuperobject(1,42,23,"Yusufun konuştuklarını anladığını farketmediler, çünkü onunla çevirmen aracılığıyla konuşuyorlardı."));
trsuperobject.biblemap.put("b1_42_24", new trsuperobject(1,42,24,"Yusuf kardeşlerinden ayrılıp ağlamaya başladı. Sonra dönüp onlarla konuştu. Aralarından Şimonu alarak ötekilerin gözleri önünde bağladı."));
trsuperobject.biblemap.put("b1_42_25", new trsuperobject(1,42,25,"Sonra torbalarına buğday doldurulmasını, paralarının torbalarına geri konulmasını, yol için kendilerine azık verilmesini buyurdu. Bunlar yapıldıktan sonra"));
trsuperobject.biblemap.put("b1_42_26", new trsuperobject(1,42,26,"buğdayları eşeklerine yükleyip oradan ayrıldılar."));
trsuperobject.biblemap.put("b1_42_27", new trsuperobject(1,42,27,"Konakladıkları yerde içlerinden biri eşeğine yem vermek için torbasını açınca parasını gördü. Para torbanın ağzına konmuştu."));
trsuperobject.biblemap.put("b1_42_28", new trsuperobject(1,42,28,"Kardeşlerine, ‹‹Paramı geri vermişler›› diye seslendi, ‹‹İşte torbamda!›› Yürekleri yerinden oynadı. Titreyerek birbirlerine, ‹‹Tanrının bize bu yaptığı nedir?›› dediler."));
trsuperobject.biblemap.put("b1_42_29", new trsuperobject(1,42,29,"Kenan ülkesine, babaları Yakupun yanına varınca, başlarına gelenleri ona anlattılar:"));
trsuperobject.biblemap.put("b1_42_30", new trsuperobject(1,42,30,"‹‹Mısırın yöneticisi bizimle sert konuştu. Bize casusmuşuz gibi davrandı."));
trsuperobject.biblemap.put("b1_42_31", new trsuperobject(1,42,31,"Ona, ‹Biz dürüst insanlarız› dedik, ‹Casus değiliz."));
trsuperobject.biblemap.put("b1_42_32", new trsuperobject(1,42,32,"Hepimiz aynı babanın çocuklarıyız. On iki kardeşiz; biri kayboldu, en küçüğü de Kenan ülkesinde, babamızın yanında.›"));
trsuperobject.biblemap.put("b1_42_33", new trsuperobject(1,42,33,"‹‹Ülkenin yöneticisi, ‹Dürüst olduğunuzu şöyle anlayabilirim› dedi, ‹Kardeşlerinizden birini yanımda bırakın, buğdayı alıp aç kalan ailelerinize götürün."));
trsuperobject.biblemap.put("b1_42_34", new trsuperobject(1,42,34,"Küçük kardeşinizi de bana getirin. O zaman casus olmadığınızı, dürüst insanlar olduğunuzu anlar, kardeşinizi size geri veririm. Ülkede ticaret yapabilirsiniz.››"));
trsuperobject.biblemap.put("b1_42_35", new trsuperobject(1,42,35,"Torbalarını boşaltınca, hepsi para kesesini torbasında buldu. Para keselerini görünce hem kendileri hem babaları korkuya kapıldı."));
trsuperobject.biblemap.put("b1_42_36", new trsuperobject(1,42,36,"Yakup, ‹‹Beni çocuklarımdan yoksun bırakıyorsunuz›› dedi, ‹‹Yusuf yok, Şimon yok. Şimdi de Benyamini götürmek istiyorsunuz. Sıkıntıyı çeken hep benim.››"));
trsuperobject.biblemap.put("b1_42_37", new trsuperobject(1,42,37,"Ruben babasına, ‹‹Benyamini geri getirmezsem, iki oğlumu öldür›› dedi, ‹‹Onu bana teslim et, ben sana geri getireceğim.››"));
trsuperobject.biblemap.put("b1_42_38", new trsuperobject(1,42,38,"Ama Yakup, ‹‹Oğlumu sizinle göndermeyeceğim›› dedi, ‹‹Çünkü kardeşi öldü, yalnız o kaldı. Yolda ona bir zarar gelirse, bu acıyla ak saçlı başımı ölüler diyarına götürürsünüz.››"));
trsuperobject.biblemap.put("b1_43_1", new trsuperobject(1,43,1,"Kenan ülkesinde kıtlık şiddetlenmişti."));
trsuperobject.biblemap.put("b1_43_2", new trsuperobject(1,43,2,"Mısırdan getirilen buğday tükenince Yakup, oğullarına, ‹‹Yine gidin, bize biraz yiyecek alın›› dedi."));
trsuperobject.biblemap.put("b1_43_3", new trsuperobject(1,43,3,"Yahuda, ‹‹Adam bizi sıkı sıkı uyardı›› diye karşılık verdi, ‹‹Kardeşiniz sizinle birlikte gelmezse, yüzümü göremezsiniz› dedi."));
trsuperobject.biblemap.put("b1_43_4", new trsuperobject(1,43,4,"Kardeşimizi bizimle gönderirsen, gider sana yiyecek alırız."));
trsuperobject.biblemap.put("b1_43_5", new trsuperobject(1,43,5,"Göndermezsen gitmeyiz. Çünkü o adam, ‹Kardeşinizi birlikte getirmezseniz, yüzümü göremezsiniz› dedi.››"));
trsuperobject.biblemap.put("b1_43_6", new trsuperobject(1,43,6,"İsrail, ‹‹Niçin adama bir kardeşiniz daha olduğunu söyleyerek bana bu kötülüğü yaptınız?›› dedi."));
trsuperobject.biblemap.put("b1_43_7", new trsuperobject(1,43,7,"Şöyle yanıtladılar: ‹‹Adam, ‹Babanız hâlâ yaşıyor mu? Başka kardeşiniz var mı?› diye sordu. Bizimle ve akrabalarımızla ilgili öyle sorular sordu ki, yanıt vermek zorunda kaldık. Kardeşinizi getirin diyeceğini nereden bilebilirdik?››"));
trsuperobject.biblemap.put("b1_43_8", new trsuperobject(1,43,8,"Yahuda, babası İsraile, ‹‹Çocuğu benimle gönder, gidelim›› dedi, ‹‹Sen de biz de yavrularımız da ölmez, yaşarız."));
trsuperobject.biblemap.put("b1_43_9", new trsuperobject(1,43,9,"Ona ben kefil oluyorum. Beni sorumlu say. Eğer onu geri getirmez, önüne çıkarmazsam, ömrümce sana karşı suçlu sayılayım."));
trsuperobject.biblemap.put("b1_43_10", new trsuperobject(1,43,10,"Çünkü gecikmeseydik, şimdiye dek iki kez gidip gelmiş olurduk.››"));
trsuperobject.biblemap.put("b1_43_11", new trsuperobject(1,43,11,"Bunun üzerine İsrail, ‹‹Öyleyse gidin›› dedi, ‹‹Yalnız, torbalarınıza bu ülkenin en iyi ürünlerinden biraz pelesenk, biraz bal, kitre, laden, fıstık, badem koyun, Mısırın yöneticisine armağan olarak götürün."));
trsuperobject.biblemap.put("b1_43_12", new trsuperobject(1,43,12,"Yanınıza iki kat para alın. Torbalarınızın ağzına konan parayı geri götürün. Belki bir yanlışlık olmuştur."));
trsuperobject.biblemap.put("b1_43_13", new trsuperobject(1,43,13,"Kardeşinizi alıp gidin, o adamın yanına dönün."));
trsuperobject.biblemap.put("b1_43_14", new trsuperobject(1,43,14,"Her Şeye Gücü Yeten Tanrı, adamın yüreğine size karşı merhamet koysun da, adam öbür kardeşinizle Benyamini size geri versin. Bana gelince, çocuklarımdan yoksun kalacaksam kalayım.››"));
trsuperobject.biblemap.put("b1_43_15", new trsuperobject(1,43,15,"Böylece kardeşler yanlarına armağanlar, iki kat para ve Benyamini alarak hemen Mısıra gidip Yusufun huzuruna çıktılar."));
trsuperobject.biblemap.put("b1_43_16", new trsuperobject(1,43,16,"Yusuf Benyamini yanlarında görünce, kâhyasına, ‹‹Bu adamları eve götür›› dedi, ‹‹Bir hayvan kesip hazırla. Çünkü öğlen benimle birlikte yemek yiyecekler.››"));
trsuperobject.biblemap.put("b1_43_17", new trsuperobject(1,43,17,"Kâhya Yusufun buyurduğu gibi onları Yusufun evine götürdü."));
trsuperobject.biblemap.put("b1_43_18", new trsuperobject(1,43,18,"Ne var ki kardeşleri Yusufun evine götürüldükleri için korktular. ‹‹İlk gelişimizde torbalarımıza konan para yüzünden götürülüyoruz galiba!›› dediler, ‹‹Bize saldırıp egemen olmak, bizi köle edip eşeklerimizi almak istiyor.››"));
trsuperobject.biblemap.put("b1_43_19", new trsuperobject(1,43,19,"Yusufun kâhyasına yaklaşıp evin kapısında onunla konuştular:"));
trsuperobject.biblemap.put("b1_43_20", new trsuperobject(1,43,20,"‹‹Aman, efendim!›› dediler, ‹‹Buraya ilk kez yiyecek satın almaya gelmiştik."));
trsuperobject.biblemap.put("b1_43_21", new trsuperobject(1,43,21,"Konakladığımız yerde torbalarımızı açınca, bir de baktık ki, paramız eksiksiz olarak torbalarımızın ağzına konmuş. Onu size geri getirdik."));
trsuperobject.biblemap.put("b1_43_22", new trsuperobject(1,43,22,"Ayrıca yeniden yiyecek almak için yanımıza başka para da aldık. Paraları torbalarımıza kimin koyduğunu bilmiyoruz.››"));
trsuperobject.biblemap.put("b1_43_23", new trsuperobject(1,43,23,"Kâhya, ‹‹Merak etmeyin›› dedi, ‹‹Korkmanıza gerek yok. Parayı Tanrınız, babanızın Tanrısı torbalarınıza koydurmuş. Ben paranızı aldım.›› Sonra Şimonu onlara getirdi."));
trsuperobject.biblemap.put("b1_43_24", new trsuperobject(1,43,24,"Kâhya onları Yusufun evine götürüp ayaklarını yıkamaları için su getirdi, eşeklerine yem verdi."));
trsuperobject.biblemap.put("b1_43_25", new trsuperobject(1,43,25,"Kardeşler öğlene, Yusufun geleceği saate kadar armağanlarını hazırladılar. Çünkü orada yemek yiyeceklerini duymuşlardı."));
trsuperobject.biblemap.put("b1_43_26", new trsuperobject(1,43,26,"Yusuf eve gelince, getirdikleri armağanları kendisine sunup önünde yere kapandılar."));
trsuperobject.biblemap.put("b1_43_27", new trsuperobject(1,43,27,"Yusuf hatırlarını sorduktan sonra, ‹‹Bana sözünü ettiğiniz yaşlı babanız iyi mi?›› dedi, ‹‹Hâlâ yaşıyor mu?››"));
trsuperobject.biblemap.put("b1_43_28", new trsuperobject(1,43,28,"Kardeşleri, ‹‹Babamız kulun iyi›› diye yanıtladılar, ‹‹Hâlâ yaşıyor.›› Sonra saygıyla eğilip yere kapandılar."));
trsuperobject.biblemap.put("b1_43_29", new trsuperobject(1,43,29,"Yusuf göz gezdirirken kendisiyle aynı anneden olan kardeşi Benyamini gördü. ‹‹Bana sözünü ettiğiniz küçük kardeşiniz bu mu?›› dedi, ‹‹Tanrı sana lütfetsin, oğlum.››"));
trsuperobject.biblemap.put("b1_43_30", new trsuperobject(1,43,30,"Sonra hemen oradan ayrıldı, çünkü kardeşini görünce yüreği sızlamıştı. Ağlayacak bir yer aradı. Odasına girip orada ağladı."));
trsuperobject.biblemap.put("b1_43_31", new trsuperobject(1,43,31,"Yüzünü yıkadıktan sonra dışarı çıktı. Kendisini toparlayarak, ‹‹Yemeği getirin›› dedi."));
trsuperobject.biblemap.put("b1_43_32", new trsuperobject(1,43,32,"Yusufa ayrı, kardeşlerine ayrı, Yusufla yemek yiyen Mısırlılara ayrı hizmet edildi. Çünkü Mısırlılar İbranilerle birlikte yemek yemez, bunu iğrenç sayarlardı."));
trsuperobject.biblemap.put("b1_43_33", new trsuperobject(1,43,33,"Kardeşleri Yusufun önünde büyükten küçüğe doğru yaş sırasına göre oturdular. Şaşkın şaşkın birbirlerine baktılar."));
trsuperobject.biblemap.put("b1_43_34", new trsuperobject(1,43,34,"Yusuf'un masasından onlara yemek dağıtıldı. Benyamin'in payı ötekilerden beş kat fazlaydı. İçtiler, birlikte hoş vakit geçirdiler."));
trsuperobject.biblemap.put("b1_44_1", new trsuperobject(1,44,1,"Yusuf kâhyasına, ‹‹Bu adamların torbalarına taşıyabilecekleri kadar yiyecek doldur›› diye buyurdu, ‹‹Her birinin parasını torbasının ağzına koy."));
trsuperobject.biblemap.put("b1_44_2", new trsuperobject(1,44,2,"En küçüğünün torbasına benim gümüş kâsemi ve buğdayının parasını koy.›› Kâhya Yusufun buyruğunu yerine getirdi."));
trsuperobject.biblemap.put("b1_44_3", new trsuperobject(1,44,3,"Sabah erkenden adamlar eşekleriyle yolcu edildi."));
trsuperobject.biblemap.put("b1_44_4", new trsuperobject(1,44,4,"Onlar kentten pek uzaklaşmamıştı ki Yusuf kâhyasına, ‹‹Hemen o adamların peşine düş›› dedi, ‹‹Onlara yetişince, ‹Niçin iyiliğe karşı kötülük yaptınız?› de,"));
trsuperobject.biblemap.put("b1_44_5", new trsuperobject(1,44,5,"‹Efendimin şarap içmek, fala bakmak için kullandığı kâse değil mi bu? Bunu yapmakla kötülük ettiniz.››"));
trsuperobject.biblemap.put("b1_44_6", new trsuperobject(1,44,6,"Kâhya onlara yetişip bu sözleri yineledi."));
trsuperobject.biblemap.put("b1_44_7", new trsuperobject(1,44,7,"Adamlar, ‹‹Efendim, neden böyle konuşuyorsun?›› dediler, ‹‹Bizden uzak olsun, biz kulların böyle şey yapmayız."));
trsuperobject.biblemap.put("b1_44_8", new trsuperobject(1,44,8,"Torbalarımızın ağzında bulduğumuz paraları Kenan ülkesinden sana geri getirdik. Nasıl efendinin evinden altın ya da gümüş çalarız?"));
trsuperobject.biblemap.put("b1_44_9", new trsuperobject(1,44,9,"Kullarından birinde çıkarsa öldürülsün, geri kalanlar efendimin kölesi olsun.››"));
trsuperobject.biblemap.put("b1_44_10", new trsuperobject(1,44,10,"Kâhya, ‹‹Peki, dediğiniz gibi olsun›› dedi, ‹‹Kimde çıkarsa kölem olacak, geri kalanlar suçsuz sayılacak.››"));
trsuperobject.biblemap.put("b1_44_11", new trsuperobject(1,44,11,"Hemen torbalarını indirip açtılar."));
trsuperobject.biblemap.put("b1_44_12", new trsuperobject(1,44,12,"Kâhya büyükten küçüğe doğru hepsinin torbasını aradı. Kâse Benyaminin torbasında çıktı."));
trsuperobject.biblemap.put("b1_44_13", new trsuperobject(1,44,13,"Kardeşleri üzüntüden giysilerini yırttılar. Sonra torbalarını eşeklerine yükleyip kente geri döndüler."));
trsuperobject.biblemap.put("b1_44_14", new trsuperobject(1,44,14,"Yahudayla kardeşleri Yusufun evine geldiğinde, Yusuf daha evdeydi. Önünde yere kapandılar."));
trsuperobject.biblemap.put("b1_44_15", new trsuperobject(1,44,15,"Yusuf, ‹‹Nedir bu yaptığınız?›› dedi, ‹‹Benim gibi birinin fala bakabileceği aklınıza gelmedi mi?››"));
trsuperobject.biblemap.put("b1_44_16", new trsuperobject(1,44,16,"Yahuda, ‹‹Ne diyelim, efendim?›› diye karşılık verdi, ‹‹Nasıl anlatalım? Kendimizi nasıl temize çıkaralım? Tanrı suçumuzu ortaya çıkardı. Hepimiz köleniz artık, efendim; hem biz hem de kendisinde kâse bulunan kardeşimiz.››"));
trsuperobject.biblemap.put("b1_44_17", new trsuperobject(1,44,17,"Yusuf, ‹‹Benden uzak olsun!›› dedi, ‹‹Yalnız kendisinde kâse bulunan kölem olacak. Siz esenlikle babanızın yanına dönün.››"));
trsuperobject.biblemap.put("b1_44_18", new trsuperobject(1,44,18,"Yahuda yaklaşıp, ‹‹Efendim, lütfen izin ver konuşayım›› dedi, ‹‹Kuluna öfkelenme. Sen firavunla aynı yetkiye sahipsin."));
trsuperobject.biblemap.put("b1_44_19", new trsuperobject(1,44,19,"Efendim, biz kullarına sormuştun: ‹Babanız ya da başka kardeşiniz var mı?› diye."));
trsuperobject.biblemap.put("b1_44_20", new trsuperobject(1,44,20,"Biz de, ‹Yaşlı bir babamız ve onun yaşlılığında doğan küçük bir kardeşimiz var› demiştik, ‹O çocuğun kardeşi öldü, kendisi annesinin tek oğlu. Babamız onu çok sever.›"));
trsuperobject.biblemap.put("b1_44_21", new trsuperobject(1,44,21,"‹‹Sen de biz kullarına, ‹O çocuğu bana getirin, gözümle göreyim› demiştin."));
trsuperobject.biblemap.put("b1_44_22", new trsuperobject(1,44,22,"Biz de, ‹Çocuk babasından ayrılamaz, ayrılırsa babası ölür› diye karşılık vermiştik."));
trsuperobject.biblemap.put("b1_44_23", new trsuperobject(1,44,23,"Sen de biz kullarına, ‹Eğer küçük kardeşiniz sizinle gelmezse, yüzümü bir daha göremezsiniz› demiştin."));
trsuperobject.biblemap.put("b1_44_24", new trsuperobject(1,44,24,"‹‹Kulun babamızın yanına döndüğümüzde, söylediklerini ona anlattık."));
trsuperobject.biblemap.put("b1_44_25", new trsuperobject(1,44,25,"Babamız, ‹Yine gidin, bize biraz yiyecek alın› dedi."));
trsuperobject.biblemap.put("b1_44_26", new trsuperobject(1,44,26,"Ama biz, ‹Gidemeyiz› dedik, ‹Ancak küçük kardeşimiz bizimle gelirse gideriz. Küçük kardeşimiz bizimle olmazsa o adamın yüzünü göremeyiz.›"));
trsuperobject.biblemap.put("b1_44_27", new trsuperobject(1,44,27,"‹‹Babam, biz kullarına, ‹Biliyorsunuz, karım bana iki erkek çocuk doğurdu› dedi,"));
trsuperobject.biblemap.put("b1_44_28", new trsuperobject(1,44,28,"‹Biri yanımdan ayrıldı. Besbelli bir hayvan parçaladı, bir daha göremedim onu."));
trsuperobject.biblemap.put("b1_44_29", new trsuperobject(1,44,29,"Bunu da götürürseniz ve ona bir zarar gelirse, bu acıyla ak saçlı başımı ölüler diyarına götürürsünüz.›"));
trsuperobject.biblemap.put("b1_44_3-031", new trsuperobject(1,44,3-031,"‹‹Efendim, şimdi babam kulunun yanına döndüğümde çocuk yanımızda olmazsa, babam onu görmeyince ölür. Çünkü onu yaşama bağlayan bu çocuktur. Biz kulların da acı içinde babamızın ak saçlı başını ölüler diyarına indiririz."));
trsuperobject.biblemap.put("b1_44_32", new trsuperobject(1,44,32,"Ben kulun bu çocuğa kefil oldum. Babama, ‹Onu sana geri getirmezsem, ömrümce kendimi sana karşı suçlu sayarım› dedim."));
trsuperobject.biblemap.put("b1_44_33", new trsuperobject(1,44,33,"‹‹Lütfen şimdi çocuğun yerine beni kölen kabul et. Çocuk kardeşleriyle birlikte geri dönsün."));
trsuperobject.biblemap.put("b1_44_34", new trsuperobject(1,44,34,"O yanımda olmadan babamın yanına nasıl dönerim? Babamın başına gelecek kötülüğe dayanamam.››"));
trsuperobject.biblemap.put("b1_45_1", new trsuperobject(1,45,1,"Yusuf adamlarının önünde kendini tutamayıp, ‹‹Herkesi çıkarın buradan!›› diye bağırdı. Kendini kardeşlerine tanıttığında yanında kimse olmasın istiyordu."));
trsuperobject.biblemap.put("b1_45_2", new trsuperobject(1,45,2,"O kadar yüksek sesle ağladı ki, Mısırlılar ağlayışını işitti. Bu haber firavunun ev halkına da ulaştı."));
trsuperobject.biblemap.put("b1_45_3", new trsuperobject(1,45,3,"Yusuf kardeşlerine, ‹‹Ben Yusufum!›› dedi, ‹‹Babam yaşıyor mu?›› Kardeşleri donup kaldı, yanıt veremediler."));
trsuperobject.biblemap.put("b1_45_4", new trsuperobject(1,45,4,"Yusuf, ‹‹Lütfen bana yaklaşın›› dedi. Onlar yaklaşınca Yusuf şöyle devam etti: ‹‹Mısıra sattığınız kardeşiniz Yusuf benim."));
trsuperobject.biblemap.put("b1_45_5", new trsuperobject(1,45,5,"Beni buraya sattığınız için üzülmeyin. Kendinizi suçlamayın. Tanrı insanlığı korumak için beni önden gönderdi."));
trsuperobject.biblemap.put("b1_45_6", new trsuperobject(1,45,6,"Çünkü iki yıldır ülkede kıtlık var, beş yıl daha sürecek. Kimse çift süremeyecek, ekin biçemeyecek."));
trsuperobject.biblemap.put("b1_45_7", new trsuperobject(1,45,7,"Tanrı yeryüzünde soyunuzu korumak ve harika biçimde canınızı kurtarmak için beni önünüzden gönderdi."));
trsuperobject.biblemap.put("b1_45_8", new trsuperobject(1,45,8,"Beni buraya gönderen siz değilsiniz, Tanrıdır. Beni firavunun başdanışmanı, sarayının efendisi, bütün Mısır ülkesinin yöneticisi yaptı."));
trsuperobject.biblemap.put("b1_45_9", new trsuperobject(1,45,9,"Hemen babamın yanına gidin, ona oğlun Yusuf şöyle diyor deyin: ‹Tanrı beni Mısır ülkesine yönetici yaptı. Durma, yanıma gel."));
trsuperobject.biblemap.put("b1_45_10", new trsuperobject(1,45,10,"Goşen bölgesine yerleşirsin; çocukların, torunların, davarların, sığırların ve sahip olduğun her şeyle birlikte yakınımda olursun."));
trsuperobject.biblemap.put("b1_45_11", new trsuperobject(1,45,11,"Orada sana bakarım, çünkü kıtlık beş yıl daha sürecek. Yoksa sen de ailen ve sana bağlı olan herkes de perişan olursunuz.›"));
trsuperobject.biblemap.put("b1_45_12", new trsuperobject(1,45,12,"‹‹Hepiniz gözlerinizle görüyorsunuz, kardeşim Benyamin, sen de görüyorsun konuşanın gerçekten ben olduğumu."));
trsuperobject.biblemap.put("b1_45_13", new trsuperobject(1,45,13,"Mısırda ne denli güçlü olduğumu ve bütün gördüklerinizi babama anlatın. Babamı hemen buraya getirin.››"));
trsuperobject.biblemap.put("b1_45_14", new trsuperobject(1,45,14,"Sonra kardeşi Benyaminin boynuna sarılıp ağladı. Benyamin de ağlayarak ona sarıldı."));
trsuperobject.biblemap.put("b1_45_15", new trsuperobject(1,45,15,"Yusuf ağlayarak bütün kardeşlerini öptü. Sonra kardeşleri onunla konuşmaya başladı."));
trsuperobject.biblemap.put("b1_45_16", new trsuperobject(1,45,16,"Yusufun kardeşlerinin geldiği haberi firavunun sarayına ulaşınca, firavunla görevlileri hoşnut oldu."));
trsuperobject.biblemap.put("b1_45_17", new trsuperobject(1,45,17,"Firavun Yusufa şöyle dedi: ‹‹Kardeşlerine de ki, ‹Hayvanlarınızı yükleyip Kenan ülkesine gidin."));
trsuperobject.biblemap.put("b1_45_18", new trsuperobject(1,45,18,"Babanızı ve ailelerinizi buraya getirin. Size Mısırın en iyi topraklarını vereceğim. Ülkenin kaymağını yiyeceksiniz.›"));
trsuperobject.biblemap.put("b1_45_19", new trsuperobject(1,45,19,"Onlara ayrıca şöyle demeni de buyuruyorum: ‹Çocuklarınızla karılarınız için Mısırdan arabalar alın, babanızla birlikte buraya gelin."));
trsuperobject.biblemap.put("b1_45_20", new trsuperobject(1,45,20,"Gözünüz arkada kalmasın, çünkü Mısırda en iyi ne varsa sizin olacak.››"));
trsuperobject.biblemap.put("b1_45_21", new trsuperobject(1,45,21,"İsrailin oğulları söyleneni yaptı. Firavunun buyruğu üzerine Yusuf onlara araba ve yol için azık verdi."));
trsuperobject.biblemap.put("b1_45_22", new trsuperobject(1,45,22,"Hepsine birer kat yedek giysi, Benyamine ise üç yüz parça gümüşle beş kat yedek giysi verdi."));
trsuperobject.biblemap.put("b1_45_23", new trsuperobject(1,45,23,"Böylece babasına Mısırda en iyi ne varsa hepsiyle yüklü on eşek, yolculuk için buğday, ekmek ve azık yüklü on dişi eşek gönderdi."));
trsuperobject.biblemap.put("b1_45_24", new trsuperobject(1,45,24,"Kardeşlerini yolcu ederken onlara, ‹‹Yolda kavga etmeyin›› dedi."));
trsuperobject.biblemap.put("b1_45_25", new trsuperobject(1,45,25,"Yusufun kardeşleri Mısırdan ayrılıp Kenan ülkesine, babaları Yakupun yanına döndüler."));
trsuperobject.biblemap.put("b1_45_26", new trsuperobject(1,45,26,"Ona, ‹‹Yusuf yaşıyor!›› dediler, ‹‹Üstelik Mısırın yöneticisi olmuş.›› Babaları donup kaldı, onlara inanmadı."));
trsuperobject.biblemap.put("b1_45_27", new trsuperobject(1,45,27,"Yusufun kendilerine bütün söylediklerini anlattılar. Kendisini Mısıra götürmek için Yusufun gönderdiği arabaları görünce, Yakupun keyfi yerine geldi."));
trsuperobject.biblemap.put("b1_45_28", new trsuperobject(1,45,28,"‹‹Tamam!›› dedi, ‹‹Oğlum Yusuf yaşıyor. Ölmeden önce gidip onu göreceğim.››"));
trsuperobject.biblemap.put("b1_46_1", new trsuperobject(1,46,1,"İsrail sahip olduğu her şeyle birlikte yola çıktı. Beer-Şevaya varınca, orada babası İshakın Tanrısına kurbanlar kesti."));
trsuperobject.biblemap.put("b1_46_2", new trsuperobject(1,46,2,"O gece Tanrı bir görümde İsraile, ‹‹Yakup, Yakup!›› diye seslendi. Yakup, ‹‹Buradayım›› diye yanıtladı."));
trsuperobject.biblemap.put("b1_46_3", new trsuperobject(1,46,3,"Tanrı, ‹‹Ben Tanrıyım, babanın Tanrısı›› dedi, ‹‹Mısıra gitmekten çekinme. Soyunu orada büyük bir ulus yapacağım."));
trsuperobject.biblemap.put("b1_46_4", new trsuperobject(1,46,4,"Seninle birlikte Mısıra gelecek, soyunu bu ülkeye geri getireceğim. Senin gözlerini Yusufun elleri kapayacak.››"));
trsuperobject.biblemap.put("b1_46_5", new trsuperobject(1,46,5,"Yakup Beer-Şevadan ayrıldı. Oğulları Yakupu -İsraili- götürmek üzere firavunun gönderdiği arabalara onu, kendi çocuklarıyla karılarını bindirdiler."));
trsuperobject.biblemap.put("b1_46_6-7", new trsuperobject(1,46,6-7,"Yakup, bütün ailesini -oğullarını, kızlarını, torunlarını- hayvanlarını ve Kenan ülkesinde kazandığı malları yanına alarak Mısıra gitti."));
trsuperobject.biblemap.put("b1_46_8", new trsuperobject(1,46,8,"İsrailin Mısıra giden oğullarının -Yakupla oğullarının- adları şunlardır:  Yakupun ilk oğlu Ruben."));
trsuperobject.biblemap.put("b1_46_9", new trsuperobject(1,46,9,"Rubenin oğulları:Hanok, Pallu, Hesron, Karmi."));
trsuperobject.biblemap.put("b1_46_10", new trsuperobject(1,46,10,"Şimonun oğulları:Yemuel, Yamin, Ohat, Yakin, Sohar ve Kenanlı bir kadının oğlu Şaul."));
trsuperobject.biblemap.put("b1_46_11", new trsuperobject(1,46,11,"Levinin oğulları:Gerşon, Kehat, Merari."));
trsuperobject.biblemap.put("b1_46_12", new trsuperobject(1,46,12,"Yahudanın oğulları:Er, Onan, Şela, Peres, Zerah.Ancak Erle Onan Kenan ülkesinde ölmüştü.Peresin oğulları:Hesron, Hamul."));
trsuperobject.biblemap.put("b1_46_13", new trsuperobject(1,46,13,"İssakarın oğulları:Tola, Puvva, Yov, Şimron."));
trsuperobject.biblemap.put("b1_46_14", new trsuperobject(1,46,14,"Zevulunun oğulları:Seret, Elon, Yahleel."));
trsuperobject.biblemap.put("b1_46_15", new trsuperobject(1,46,15,"Bunlar Leanın Yakupa doğurduğu oğullardır. Lea onları ve kızı Dinayı Paddan-Aramda doğurmuştu. Yakupun bu oğullarıyla kızları toplam otuz üç kişiydi."));
trsuperobject.biblemap.put("b1_46_16", new trsuperobject(1,46,16,"Gadın oğulları:Sifyon, Hagi, Şuni, Esbon, Eri, Arodi, Areli."));
trsuperobject.biblemap.put("b1_46_17", new trsuperobject(1,46,17,"Aşerin çocukları:Yimna, Yişva, Yişvi, Beria; kızkardeşleri Serah.Berianın oğulları:Hever, Malkiel."));
trsuperobject.biblemap.put("b1_46_18", new trsuperobject(1,46,18,"Bunlar Lavanın kızı Leaya verdiği Zilpanın Yakupa doğurduğu çocuklardır. Toplam on altı kişiydiler."));
trsuperobject.biblemap.put("b1_46_19", new trsuperobject(1,46,19,"Yakupun karısı Rahelin oğulları:Yusuf, Benyamin."));
trsuperobject.biblemap.put("b1_46_20", new trsuperobject(1,46,20,"Yusufun Mısırda On Kentifç kâhini Potiferanın kızı Asenattan Manaşşe ve Efrayim adında iki oğlu oldu."));
trsuperobject.biblemap.put("b1_46_21", new trsuperobject(1,46,21,"Benyaminin oğulları:Bala, Beker, Aşbel, Gera, Naaman, Ehi, Roş, Muppim, Huppim, Ard."));
trsuperobject.biblemap.put("b1_46_22", new trsuperobject(1,46,22,"Bunlar Rahelin Yakupa doğurduğu çocuklardır. Toplam on dört kişiydiler."));
trsuperobject.biblemap.put("b1_46_23", new trsuperobject(1,46,23,"Danın oğlu:Huşim."));
trsuperobject.biblemap.put("b1_46_24", new trsuperobject(1,46,24,"Naftalinin oğulları:Yahseel, Guni, Yeser, Şillem."));
trsuperobject.biblemap.put("b1_46_25", new trsuperobject(1,46,25,"Bunlar Lavanın, kızı Rahele verdiği Bilhanın Yakupa doğurduğu çocuklardır. Toplam yedi kişiydiler."));
trsuperobject.biblemap.put("b1_46_26", new trsuperobject(1,46,26,"Oğullarının karıları dışında Yakupun soyundan gelen ve onunla birlikte Mısıra gidenler toplam altmış altı kişiydi. Bunların hepsi Yakuptan olmuştu."));
trsuperobject.biblemap.put("b1_46_27", new trsuperobject(1,46,27,"Yusufun Mısırda doğan iki oğluyla birlikte Mısıra göçen Yakup ailesi toplam yetmiş kişiydi."));
trsuperobject.biblemap.put("b1_46_28", new trsuperobject(1,46,28,"Yakup Goşen yolunu göstermesi için Yahudayı önden Yusufa gönderdi. Onlar Goşene varınca,"));
trsuperobject.biblemap.put("b1_46_29", new trsuperobject(1,46,29,"Yusuf arabasını hazırlayıp babası İsraili karşılamak üzere Goşene gitti. Babasını görür görmez boynuna sarılıp uzun uzun ağladı."));
trsuperobject.biblemap.put("b1_46_30", new trsuperobject(1,46,30,"İsrail Yusufa, ‹‹Yüzünü gördüm ya, artık ölsem de gam yemem›› dedi, ‹‹Yaşıyorsun!››"));
trsuperobject.biblemap.put("b1_46_31", new trsuperobject(1,46,31,"Yusuf kardeşleriyle babasının ev halkına şöyle dedi: ‹‹Gidip firavuna haber vereyim, ‹Kenan ülkesinde yaşayan kardeşlerimle babamın ev halkı yanıma geldi› diyeyim."));
trsuperobject.biblemap.put("b1_46_32", new trsuperobject(1,46,32,"Çoban olduğunuzu, hayvancılık yaptığınızı, bu yüzden davarlarınızla sığırlarınızı ve her şeyinizi birlikte getirdiğinizi anlatayım."));
trsuperobject.biblemap.put("b1_46_33", new trsuperobject(1,46,33,"Firavun sizi çağırıp da, ‹Ne iş yaparsınız?› diye sorarsa,"));
trsuperobject.biblemap.put("b1_46_34", new trsuperobject(1,46,34,"‹Atalarımız gibi biz de çocukluktan beri hayvancılık yapıyoruz› dersiniz. Öyle deyin ki, sizi Goşen bölgesine yerleştirsin. Çünkü Mısırlılar çobanlardan iğrenir.››"));
trsuperobject.biblemap.put("b1_47_1", new trsuperobject(1,47,1,"Yusuf gidip firavuna, ‹‹Babamla kardeşlerim davarları, sığırları ve bütün eşyalarıyla Kenan ülkesinden geldiler›› diye haber verdi, ‹‹Şu anda Goşen bölgesindeler.››"));
trsuperobject.biblemap.put("b1_47_2", new trsuperobject(1,47,2,"Sonra kardeşlerinden beşini seçerek firavunun huzuruna çıkardı."));
trsuperobject.biblemap.put("b1_47_3", new trsuperobject(1,47,3,"Firavun Yusufun kardeşlerine, ‹‹Ne iş yapıyorsunuz?›› diye sordu. ‹‹Biz kulların atalarımız gibi çobanız›› diye yanıtladılar,"));
trsuperobject.biblemap.put("b1_47_4", new trsuperobject(1,47,4,"‹‹Bu ülkeye geçici bir süre için geldik. Çünkü Kenan ülkesinde şiddetli kıtlık var. Davarlarımız için otlak bulamıyoruz. İzin ver, Goşen bölgesine yerleşelim.››"));
trsuperobject.biblemap.put("b1_47_5", new trsuperobject(1,47,5,"Firavun Yusufa, ‹‹Babanla kardeşlerin yanına geldiler›› dedi,"));
trsuperobject.biblemap.put("b1_47_6", new trsuperobject(1,47,6,"‹‹Mısır ülkesi senin sayılır. Onları ülkenin en iyi yerine yerleştir. Goşen bölgesine yerleşsinler. Sence aralarında becerikli olanlar varsa, davarlarıma bakmakla görevlendir.››"));
trsuperobject.biblemap.put("b1_47_7", new trsuperobject(1,47,7,"Yusuf babası Yakupu getirip firavunun huzuruna çıkardı. Yakup firavunu kutsadı."));
trsuperobject.biblemap.put("b1_47_8", new trsuperobject(1,47,8,"Firavun, Yakupa, ‹‹Kaç yaşındasın?›› diye sordu."));
trsuperobject.biblemap.put("b1_47_9", new trsuperobject(1,47,9,"Yakup, ‹‹Gurbet yıllarım yüz otuz yılı buldu›› diye yanıtladı, ‹‹Ama yıllar çabuk ve zorlu geçti. Atalarımın gurbet yılları kadar uzun sürmedi.››"));
trsuperobject.biblemap.put("b1_47_10", new trsuperobject(1,47,10,"Sonra firavunu kutsayıp huzurundan ayrıldı."));
trsuperobject.biblemap.put("b1_47_11", new trsuperobject(1,47,11,"Yusuf babasıyla kardeşlerini Mısıra yerleştirdi; firavunun buyruğu uyarınca onlara ülkenin en iyi yerinde, Ramses bölgesinde mülk verdi."));
trsuperobject.biblemap.put("b1_47_12", new trsuperobject(1,47,12,"Ayrıca babasıyla kardeşlerine ve babasının ev halkına, sahip oldukları çocukların sayısına göre yiyecek sağladı."));
trsuperobject.biblemap.put("b1_47_13", new trsuperobject(1,47,13,"Kıtlık öyle şiddetlendi ki, hiçbir ülkede yiyecek bulunmaz oldu. Mısır ve Kenan ülkeleri kıtlıktan kırılıyordu."));
trsuperobject.biblemap.put("b1_47_14", new trsuperobject(1,47,14,"Yusuf sattığı buğdaya karşılık Mısır ve Kenandaki bütün paraları toplayıp firavunun sarayına götürdü."));
trsuperobject.biblemap.put("b1_47_15", new trsuperobject(1,47,15,"Mısır ve Kenanda para tükenince Mısırlılar Yusufa giderek, ‹‹Bize yiyecek ver›› dediler, ‹‹Gözünün önünde ölelim mi? Paramız bitti.››"));
trsuperobject.biblemap.put("b1_47_16", new trsuperobject(1,47,16,"Yusuf, ‹‹Paranız bittiyse, davarlarınızı getirin›› dedi, ‹‹Onlara karşılık size yiyecek vereyim.››"));
trsuperobject.biblemap.put("b1_47_17", new trsuperobject(1,47,17,"Böylece davarlarını Yusufa getirdiler. Yusuf atlara, davar ve sığır sürülerine, eşeklere karşılık onlara yiyecek verdi. Bir yıl boyunca hayvanlarına karşılık onlara yiyecek sağladı."));
trsuperobject.biblemap.put("b1_47_18", new trsuperobject(1,47,18,"O yıl geçince, ikinci yıl yine geldiler. Yusufa, ‹‹Efendim, gerçeği senden saklayacak değiliz›› dediler, ‹‹Paramız tükendi, davarlarımızı da sana verdik. Canımızdan ve toprağımızdan başka verecek bir şeyimiz kalmadı."));
trsuperobject.biblemap.put("b1_47_19", new trsuperobject(1,47,19,"Gözünün önünde ölelim mi? Toprağımız çöle mi dönsün? Canımıza ve toprağımıza karşılık bize yiyecek sat. Toprağımızla birlikte firavunun kölesi olalım. Bize tohum ver ki ölmeyelim, yaşayalım; toprak da çöle dönmesin.››"));
trsuperobject.biblemap.put("b1_47_20", new trsuperobject(1,47,20,"Böylece Yusuf Mısırdaki bütün toprakları firavun için satın aldı. Mısırlıların hepsi tarlalarını sattılar, çünkü kıtlık onları buna zorluyordu. Toprakların tümü firavunun oldu."));
trsuperobject.biblemap.put("b1_47_21", new trsuperobject(1,47,21,"Yusuf Mısırın bir ucundan öbür ucuna kadar bütün halkı köleleştirdi."));
trsuperobject.biblemap.put("b1_47_22", new trsuperobject(1,47,22,"Yalnız kâhinlerin toprağını satın almadı. Çünkü onlar firavundan aylık alıyor, firavunun bağladığı aylıkla geçiniyorlardı. Bu yüzden topraklarını satmadılar. Masoretik metin ‹‹Kentlere göçtürdü››."));
trsuperobject.biblemap.put("b1_47_23", new trsuperobject(1,47,23,"Yusuf halka, ‹‹Sizi de toprağınızı da firavun için satın aldım›› dedi, ‹‹İşte size tohum, toprağı ekin."));
trsuperobject.biblemap.put("b1_47_24", new trsuperobject(1,47,24,"Ürün devşirdiğinizde, beşte birini firavuna vereceksiniz. Beşte dördünü ise tohumluk olarak kullanacak ve ailelerinizle, çocuklarınızla yiyeceksiniz.››"));
trsuperobject.biblemap.put("b1_47_25", new trsuperobject(1,47,25,"‹‹Canımızı kurtardın›› diye karşılık verdiler, ‹‹Efendimizin gözünde lütuf bulalım. Firavunun kölesi oluruz.››"));
trsuperobject.biblemap.put("b1_47_26", new trsuperobject(1,47,26,"Yusuf ürünün beşte birinin firavuna verilmesini Mısırda toprak yasası yaptı. Bu yasa bugün de yürürlüktedir. Yalnız kâhinlerin toprağı firavuna verilmedi."));
trsuperobject.biblemap.put("b1_47_27", new trsuperobject(1,47,27,"İsrail Mısırda Goşen bölgesine yerleşti. Orada mülk sahibi oldular, çoğalıp arttılar."));
trsuperobject.biblemap.put("b1_47_28", new trsuperobject(1,47,28,"Yakup Mısırda on yedi yıl yaşadı. Ömrü toplam yüz kırk yedi yıl sürdü."));
trsuperobject.biblemap.put("b1_47_29", new trsuperobject(1,47,29,"Ölümü yaklaşınca, oğlu Yusufu çağırıp, ‹‹Eğer benden hoşnut kaldınsa, lütfen elini uyluğumun altına koy›› dedi, ‹‹Bana sevgi ve sadakat göstereceğine söz ver. Lütfen beni Mısırda gömme."));
trsuperobject.biblemap.put("b1_47_30", new trsuperobject(1,47,30,"Atalarıma kavuştuğum zaman beni Mısırdan çıkarıp onların yanına göm.›› Yusuf, ‹‹Dediğin gibi yapacağım›› diye karşılık verdi."));
trsuperobject.biblemap.put("b1_47_31", new trsuperobject(1,47,31,"İsrail, ‹‹Ant iç›› dedi. Yusuf ant içti. İsrail yatağının başı ucunda eğilip RAB'be tapındı. ucuna yaslanıp››."));
trsuperobject.biblemap.put("b1_48_1", new trsuperobject(1,48,1,"Bir süre sonra, ‹‹Baban hasta›› diye Yusufa haber geldi. Yusuf iki oğlu Manaşşeyle Efrayimi yanına alıp yola çıktı."));
trsuperobject.biblemap.put("b1_48_2", new trsuperobject(1,48,2,"Yakupa, ‹‹Oğlun Yusuf geliyor›› diye haber verdiler. İsrail kendini toparlayıp yatağında oturdu."));
trsuperobject.biblemap.put("b1_48_3", new trsuperobject(1,48,3,"Yusufa, ‹‹Her Şeye Gücü Yeten Tanrı Kenan ülkesinde, Luzda bana görünerek beni kutsadı›› dedi,"));
trsuperobject.biblemap.put("b1_48_4", new trsuperobject(1,48,4,"‹‹Bana, ‹Seni verimli kılacak, çoğaltacağım› dedi, ‹Soyundan birçok ulus doğuracağım. Senden sonra bu ülkeyi sonsuza dek mülk olarak senin soyuna vereceğim.›"));
trsuperobject.biblemap.put("b1_48_5", new trsuperobject(1,48,5,"‹‹Ben Mısıra gelmeden önce burada doğan iki oğlun benim sayılır. Efrayimle Manaşşe benim için Rubenle Şimon gibidir."));
trsuperobject.biblemap.put("b1_48_6", new trsuperobject(1,48,6,"Onlardan sonra doğacak çocuklar senin olsun. Efrayimle Manaşşeden onlara miras geçecek."));
trsuperobject.biblemap.put("b1_48_7", new trsuperobject(1,48,7,"Ben Paddandan dönerken Rahel Kenan ülkesinde, Efrata varmadan yolda yanımda öldü. Çok üzüldüm, onu orada Efrata -Beytleheme- giden yolun kenarına gömdüm.››"));
trsuperobject.biblemap.put("b1_48_8", new trsuperobject(1,48,8,"İsrail, Yusufun oğullarını görünce, ‹‹Bunlar kim?›› diye sordu."));
trsuperobject.biblemap.put("b1_48_9", new trsuperobject(1,48,9,"Yusuf, ‹‹Oğullarım›› diye yanıtladı, ‹‹Tanrı onları bana Mısırda verdi.›› İsrail, ‹‹Lütfen onları yanıma getir, kutsayayım›› dedi."));
trsuperobject.biblemap.put("b1_48_10", new trsuperobject(1,48,10,"İsrailin gözleri yaşlılıktan zayıflamıştı, göremiyordu. Yusuf oğullarını onun yanına götürdü. Babası onları öpüp kucakladı."));
trsuperobject.biblemap.put("b1_48_11", new trsuperobject(1,48,11,"Sonra Yusufa, ‹‹Senin yüzünü göreceğimi hiç sanmıyordum›› dedi, ‹‹Ama işte Tanrı bana soyunu bile gösterdi.››"));
trsuperobject.biblemap.put("b1_48_12", new trsuperobject(1,48,12,"Yusuf oğullarını babasının kucağından alıp onun önünde yere kapandı."));
trsuperobject.biblemap.put("b1_48_13", new trsuperobject(1,48,13,"Sonra Efrayimi sağına alarak İsrailin sol eline, Manaşşeyi soluna alarak İsrailin sağ eline yaklaştırdı."));
trsuperobject.biblemap.put("b1_48_14", new trsuperobject(1,48,14,"İsrail ellerini çapraz olarak uzattı, sağ elini küçük olan Efrayimin, sol elini Manaşşenin başına koydu. Oysa ilkin Manaşşe doğmuştu."));
trsuperobject.biblemap.put("b1_48_15", new trsuperobject(1,48,15,"Sonra Yusufu kutsayarak şöyle dedi:  ‹‹Atalarım İbrahimin, İshakın hizmet ettiği,Bugüne dek yaşamım boyunca bana çobanlık eden Tanrı,"));
trsuperobject.biblemap.put("b1_48_16", new trsuperobject(1,48,16,"Beni bütün kötülüklerden kurtaran melek bu gençleri kutsasın!Adım ve atalarım İbrahimle İshakın adları bu gençlerle yaşasın!Yeryüzünde çoğaldıkça çoğalsınlar.››"));
trsuperobject.biblemap.put("b1_48_17", new trsuperobject(1,48,17,"Yusuf, babasının sağ elini Efrayimin başına koyduğunu görünce, bundan hoşlanmadı. Babasının elini Efrayimin başından kaldırıp Manaşşenin başına koymak istedi."));
trsuperobject.biblemap.put("b1_48_18", new trsuperobject(1,48,18,"‹‹Baba, öyle değil›› dedi, ‹‹İlkin Manaşşe doğdu. Sağ elini onun başına koy.››"));
trsuperobject.biblemap.put("b1_48_19", new trsuperobject(1,48,19,"Ancak babası bunu istemedi. ‹‹Biliyorum oğlum, biliyorum›› dedi, ‹‹Manaşşe de büyük bir halk olacak. Ama küçük kardeşi daha büyük bir halk olacak, soyundan birçok ulus doğacak.››"));
trsuperobject.biblemap.put("b1_48_20", new trsuperobject(1,48,20,"O gün onları kutsayarak şöyle dedi:  ‹‹İsrailliler, ‹Tanrı seni Efrayim ve Manaşşe gibi yapsın›Diyerek sizin adınızla kutsayacaklar.›› Böylece Yakup Efrayimi Manaşşenin önüne geçirdi."));
trsuperobject.biblemap.put("b1_48_21", new trsuperobject(1,48,21,"İsrail Yusufa, ‹‹Ben ölmek üzereyim›› dedi, ‹‹Tanrı sizinle olacak. Sizi atalarınızın toprağına geri götürecek."));
trsuperobject.biblemap.put("b1_48_22", new trsuperobject(1,48,22,"Sana kardeşlerinden bir pay fazla veriyorum; onu Amorlular'dan kılıcımla, yayımla aldım.››"));
trsuperobject.biblemap.put("b1_49_1", new trsuperobject(1,49,1,"Yakup oğullarını çağırarak, ‹‹Yanıma toplanın›› dedi, ‹‹Gelecekte size neler olacağını anlatayım."));
trsuperobject.biblemap.put("b1_49_2", new trsuperobject(1,49,2,"‹‹Yakupoğulları, toplanın ve dinleyin,Babanız İsraile kulak verin."));
trsuperobject.biblemap.put("b1_49_3", new trsuperobject(1,49,3,"‹‹Ruben, sen benim ilk oğlum, gücümsün,Kudretimin ilk ürünüsün,Saygı ve güç bakımından en üstünsün."));
trsuperobject.biblemap.put("b1_49_4", new trsuperobject(1,49,4,"Ama su gibi oynaksın,Üstün olmayacaksın artık.Çünkü babanın yatağına giripOnu kirlettin.Döşeğimi rezil ettin."));
trsuperobject.biblemap.put("b1_49_5", new trsuperobject(1,49,5,"‹‹Şimonla Levi kardeştir,Kılıçları şiddet kusar."));
trsuperobject.biblemap.put("b1_49_6", new trsuperobject(1,49,6,"Gizli tasarılarına ortak olmam,Toplantılarına katılmam.Çünkü öfkelenince adam öldürdüler,Canları istedikçe sığırları sakatladılar."));
trsuperobject.biblemap.put("b1_49_7", new trsuperobject(1,49,7,"Lanet olsun öfkelerine,Çünkü şiddetlidir.Lanet olsun gazaplarına,Çünkü zalimcedir.Onları Yakupta bölecekVe İsrailde dağıtacağım."));
trsuperobject.biblemap.put("b1_49_8", new trsuperobject(1,49,8,"‹‹Yahuda, kardeşlerin seni övecek,Düşmanlarının ensesinde olacak elin.Kardeşlerin önünde eğilecek."));
trsuperobject.biblemap.put("b1_49_9", new trsuperobject(1,49,9,"Yahuda bir aslan yavrusudur.Oğlum benim! Avından dönüp yere çömelir,Aslan gibi, dişi bir aslan gibi yatarsın.Kim onu uyandırmaya cesaret edebilir?"));
trsuperobject.biblemap.put("b1_49_10", new trsuperobject(1,49,10,"Sahibi gelene kadarKrallık asası Yahudanın elinden çıkmayacak,Yönetim hep onun soyunda kalacak,Uluslar onun sözünü dinleyecek."));
trsuperobject.biblemap.put("b1_49_11", new trsuperobject(1,49,11,"Eşeğini bir asmaya,Sıpasını seçme bir dala bağlayacak;Giysilerini şarapta,Kaftanını üzümün kızıl kanında yıkayacak."));
trsuperobject.biblemap.put("b1_49_12", new trsuperobject(1,49,12,"Gözleri şaraptan kızıl,Dişleri sütten beyaz olacak."));
trsuperobject.biblemap.put("b1_49_13", new trsuperobject(1,49,13,"‹‹Zevulun deniz kıyısında yaşayacak,Liman olacak gemilere,Sınırı Saydaya dek uzanacak."));
trsuperobject.biblemap.put("b1_49_14", new trsuperobject(1,49,14,"‹‹İssakar semerler arasında yatan güçlü eşek gibidir;"));
trsuperobject.biblemap.put("b1_49_15", new trsuperobject(1,49,15,"Ne zaman dinlenecek iyi bir yer,Hoşuna giden bir ülke görse,Yüklenmek için sırtını eğer,Angaryaya katlanır."));
trsuperobject.biblemap.put("b1_49_16", new trsuperobject(1,49,16,"‹‹Dan kendi halkını yönetecek,Bir İsrail oymağı gibi."));
trsuperobject.biblemap.put("b1_49_17", new trsuperobject(1,49,17,"Yol kenarında bir yılan,Toprak yolda bir engerek olacak;Atın topuklarını ısırıpAtlıyı sırtüstü düşüren bir engerek."));
trsuperobject.biblemap.put("b1_49_18", new trsuperobject(1,49,18,"‹‹Ben senin kurtarışını bekliyorum, ya RAB."));
trsuperobject.biblemap.put("b1_49_19", new trsuperobject(1,49,19,"‹‹Gad akıncıların saldırısına uğrayacak,Ama onların topuklarına saldıracak."));
trsuperobject.biblemap.put("b1_49_20", new trsuperobject(1,49,20,"‹‹Zengin yemekler olacak Aşerde,Krallara yaraşır lezzetli yiyecekler yetiştirecek Aşer."));
trsuperobject.biblemap.put("b1_49_21", new trsuperobject(1,49,21,"‹‹Naftali salıverilmiş geyiğe benzer,Sevimli yavrular doğurur."));
trsuperobject.biblemap.put("b1_49_22", new trsuperobject(1,49,22,"‹‹Yusuf meyveli bir dal gibidir,Kaynak kıyısında verimli bir dal gibi,Filizleri duvarların üzerinden aşar."));
trsuperobject.biblemap.put("b1_49_23", new trsuperobject(1,49,23,"Okçular acımadan saldırdı ona.Düşmanca savurdular oklarını üzerine."));
trsuperobject.biblemap.put("b1_49_24", new trsuperobject(1,49,24,"Ama onun yayı sağlam,Kolları esnek çıktı;Yakupun güçlü Tanrısı,İsrailin Kayası, Çobanı olan Tanrı sayesinde."));
trsuperobject.biblemap.put("b1_49_25", new trsuperobject(1,49,25,"Sana yardım eden babanın Tanrısıdır,Her Şeye Gücü Yeten Tanrıdır seni kutsayan.Yukarıdaki göklerinVe aşağıdaki denizlerin bereketiyle,Memelerin, rahimlerin bereketiyle Odur seni kutsayan."));
trsuperobject.biblemap.put("b1_49_26", new trsuperobject(1,49,26,"Babanın kutsamaları ebedi dağların nimetlerinden,Ebedi tepelerin bolluğundan daha yücedir;Yusufun başı üzerinde,Kardeşleri arasında önder olanın üstünde olacak."));
trsuperobject.biblemap.put("b1_49_27", new trsuperobject(1,49,27,"‹‹Benyamin aç kurda benzer;Sabah avını yer,Akşam ganimeti paylaşır.››"));
trsuperobject.biblemap.put("b1_49_28", new trsuperobject(1,49,28,"İsrailin on iki oymağı bunlardır. Babaları onları kutsarken bunları söyledi. Her birini uygun biçimde kutsadı."));
trsuperobject.biblemap.put("b1_49_29-30", new trsuperobject(1,49,29-30,"Sonra Yakup oğullarına şu buyrukları verdi: ‹‹Ben ölmek, halkıma kavuşmak üzereyim. Beni Kenan ülkesinde atalarımın yanına, Mamre yakınlarında Hititli Efronun tarlasındaki mağaraya, Makpela Tarlasındaki mağaraya gömün. İbrahim o mağarayı mezar yapmak üzere Hititli Efrondan tarlasıyla birlikte satın almıştı."));
trsuperobject.biblemap.put("b1_49_31", new trsuperobject(1,49,31,"İbrahimle karısı Sara, İshakla karısı Rebeka oraya gömüldüler. Leayı da ben oraya gömdüm."));
trsuperobject.biblemap.put("b1_49_32", new trsuperobject(1,49,32,"Tarla ile içindeki mağara Hititlerden satın alındı.››"));
trsuperobject.biblemap.put("b1_49_33", new trsuperobject(1,49,33,"Yakup oğullarına verdiği buyrukları bitirince, ayaklarını yatağın içine çekti, son soluğunu vererek halkına kavuştu."));
trsuperobject.biblemap.put("b1_50_1", new trsuperobject(1,50,1,"Yusuf kendini babasının üzerine attı, ağlayarak onu öptü."));
trsuperobject.biblemap.put("b1_50_2", new trsuperobject(1,50,2,"Babasının cesedini mumyalamaları için özel hekimlerine buyruk verdi. Hekimler İsraili mumyaladılar."));
trsuperobject.biblemap.put("b1_50_3", new trsuperobject(1,50,3,"Bu iş kırk gün sürdü. Mumyalama için bu süre gerekliydi. Mısırlılar İsrail için yetmiş gün yas tuttu."));
trsuperobject.biblemap.put("b1_50_4", new trsuperobject(1,50,4,"Yas günleri geçince, Yusuf firavunun ev halkına, ‹‹Eğer benden hoşnut kaldınızsa, lütfen firavunla konuşun›› dedi,"));
trsuperobject.biblemap.put("b1_50_5", new trsuperobject(1,50,5,"‹‹Babam bana ant içirdi: ‹Ölmek üzereyim. Beni Kenan ülkesinde kendim için kazdırdığım mezara gömeceksin› dedi. Şimdi lütfen firavuna bildirin, izin versin gideyim, babamı gömüp geleyim.››"));
trsuperobject.biblemap.put("b1_50_6", new trsuperobject(1,50,6,"Firavun, ‹‹Git, babanı göm, andını yerine getir›› dedi."));
trsuperobject.biblemap.put("b1_50_7", new trsuperobject(1,50,7,"Böylece Yusuf babasını gömmeye gitti. Firavunun bütün görevlileri, sarayın ve Mısırın ileri gelenleri ona eşlik etti."));
trsuperobject.biblemap.put("b1_50_8", new trsuperobject(1,50,8,"Yusufun bütün ailesi, kardeşleri, babasının ev halkı da onunla birlikteydi. Yalnız çocukları, davarlarla sığırları Goşende bıraktılar."));
trsuperobject.biblemap.put("b1_50_9", new trsuperobject(1,50,9,"Arabalarla atlılar da onları izledi. Büyük bir alay oluşturdular."));
trsuperobject.biblemap.put("b1_50_10", new trsuperobject(1,50,10,"Şeria Irmağının doğusunda Atat Harmanına varınca, yüksek sesle, acı acı ağıt yaktılar. Yusuf babası için yedi gün yas tuttu."));
trsuperobject.biblemap.put("b1_50_11", new trsuperobject(1,50,11,"O bölgede yaşayan Kenanlılar, Atat Harmanındaki yası görünce, ‹‹Mısırlılar ne kadar hüzünlü yas tutuyor!›› dediler. Bu yüzden, Şeria Irmağının doğusundaki bu yere Avel-Misrayim adı verildi. ‹‹Mısırlıların çayırı›› anlamına gelir."));
trsuperobject.biblemap.put("b1_50_12", new trsuperobject(1,50,12,"Yakupun oğulları, babalarının vermiş olduğu buyruğu tam tamına yerine getirdiler."));
trsuperobject.biblemap.put("b1_50_13", new trsuperobject(1,50,13,"Onu Kenan ülkesine götürüp Mamre yakınlarında Makpela Tarlasındaki mağaraya gömdüler. O mağarayı mezar yapmak üzere tarlayla birlikte Hititli Efrondan İbrahim satın almıştı."));
trsuperobject.biblemap.put("b1_50_14", new trsuperobject(1,50,14,"Yusuf babasını gömdükten sonra, kendisi, kardeşleri ve onunla birlikte babasını gömmeye gelenlerin hepsi Mısıra döndüler."));
trsuperobject.biblemap.put("b1_50_15", new trsuperobject(1,50,15,"Babalarının ölümünden sonra Yusufun kardeşleri, ‹‹Belki Yusuf bize kin besliyordur›› dediler, ‹‹Ya ona yaptığımız kötülüğe karşılık bizden öç almaya kalkarsa?››"));
trsuperobject.biblemap.put("b1_50_16-17", new trsuperobject(1,50,16-17,"Böylece Yusufa haber gönderdiler: ‹‹Babamız ölmeden önce Yusufa şöyle deyin diye buyurmuştu: ‹Kardeşlerin sana kötülük yaptılar, lütfen onların suçunu, günahını bağışla.› Ne olur şimdi günahımızı bağışla. Biz babanın Tanrısının kullarıyız.›› Yusuf bu haberi alınca ağladı."));
trsuperobject.biblemap.put("b1_50_18", new trsuperobject(1,50,18,"Bunun üzerine kardeşleri gidip onun önünde yere kapanarak, ‹‹Senin köleniz›› dediler."));
trsuperobject.biblemap.put("b1_50_19", new trsuperobject(1,50,19,"Yusuf, ‹‹Korkmayın›› dedi, ‹‹Ben Tanrı mıyım?"));
trsuperobject.biblemap.put("b1_50_20", new trsuperobject(1,50,20,"Siz bana kötülük düşündünüz, ama Tanrı bugün olduğu gibi birçok halkın yaşamını korumak için o kötülüğü iyiliğe çevirdi."));
trsuperobject.biblemap.put("b1_50_21", new trsuperobject(1,50,21,"Korkmanıza gerek yok, size de çocuklarınıza da bakacağım.›› Yüreklerine dokunacak güzel sözlerle onlara güven verdi."));
trsuperobject.biblemap.put("b1_50_22", new trsuperobject(1,50,22,"Yusufla babasının ev halkı Mısıra yerleştiler. Yusuf yüz on yıl yaşadı."));
trsuperobject.biblemap.put("b1_50_23", new trsuperobject(1,50,23,"Efrayimin üç göbek çocuklarını gördü. Manaşşenin oğlu Makirin çocukları onun elinde doğdu."));
trsuperobject.biblemap.put("b1_50_24", new trsuperobject(1,50,24,"Yusuf yakınlarına, ‹‹Ben ölmek üzereyim›› dedi, ‹‹Ama Tanrı kesinlikle size yardım edecek; sizi İbrahime, İshaka, Yakupa ant içerek söz verdiği topraklara götürecek.››"));
trsuperobject.biblemap.put("b1_50_25", new trsuperobject(1,50,25,"Sonra onlara ant içirerek, ‹‹Tanrı kesinlikle size yardım edecek›› dedi, ‹‹O zaman kemiklerimi buradan götürürsünüz.››"));
trsuperobject.biblemap.put("b1_50_26", new trsuperobject(1,50,26,"Yusuf yüz on yaşında öldü. Onu mumyalayıp Mısır'da bir tabuta koydular."));

