trsuperobject.biblemap.put("b11_2_30", new trsuperobject(11,2,30,"Benaya RABbin Çadırına gitti ve Yoava, ‹‹Kral dışarı çıkmanı buyuruyor!›› dedi. Yoav, ‹‹Hayır, burada ölmek istiyorum›› karşılığını verdi. Benaya gidip Yoavın kendisini nasıl yanıtladığını krala bildirdi."));
trsuperobject.biblemap.put("b11_2_31", new trsuperobject(11,2,31,"Kral, ‹‹Onun istediği gibi yap›› dedi, ‹‹Onu orada öldür ve göm. Yoavın boş yere döktüğü kanın sorumluluğunu benim ve babamın soyu üstünden kaldırmış olursun."));
trsuperobject.biblemap.put("b11_2_32", new trsuperobject(11,2,32,"RAB döktüğü kandan ötürü onu cezalandıracaktır. Çünkü Yoav babam Davutun bilgisi dışında, kendisinden daha iyi ve doğru olan iki kişiyi -İsrail ordusunun komutanı Ner oğlu Avnerle Yahuda ordusunun komutanı Yeter oğlu Amasayı- kılıçla öldürdü."));
trsuperobject.biblemap.put("b11_2_33", new trsuperobject(11,2,33,"Böylece dökülen kanlarının sorumluluğu sonsuza dek Yoavın ve soyunun üstünde kalacaktır. Ama RAB, Davuta, soyuna, ailesine ve tahtına sonsuza dek esenlik verecektir.››"));
trsuperobject.biblemap.put("b11_2_34", new trsuperobject(11,2,34,"Yehoyada oğlu Benaya gidip Yoavı öldürdü. Onu ıssız bir bölgede bulunan kendi evine gömdüler."));
trsuperobject.biblemap.put("b11_2_35", new trsuperobject(11,2,35,"Kral, Yoavın yerine Yehoyada oğlu Benayayı ordu komutanı yaptı. Aviyatarın yerine de Kâhin Sadoku atadı."));
trsuperobject.biblemap.put("b11_2_36", new trsuperobject(11,2,36,"Sonra kral haber gönderip Şimiyi çağırttı. Ona, ‹‹Yeruşalimde kendine bir ev yap ve orada otur›› dedi, ‹‹Hiçbir yere gitme."));
trsuperobject.biblemap.put("b11_2_37", new trsuperobject(11,2,37,"Oradan ayrılıp Kidron Vadisinden öteye geçtiğin gün bil ki öleceksin. Sorumluluk sana ait.››"));
trsuperobject.biblemap.put("b11_2_38", new trsuperobject(11,2,38,"Şimi krala, ‹‹Efendim kral, peki›› diye karşılık verdi, ‹‹Kulun olarak söylediklerini aynen yapacağım.›› Şimi Yeruşalimde uzun süre yaşadı."));
trsuperobject.biblemap.put("b11_2_39", new trsuperobject(11,2,39,"Aradan üç yıl geçmişti, Şiminin iki kölesi Gat Kralı Maaka oğlu Akişin yanına kaçtı. Kölelerin Gata kaçtığını Şimiye haber verdiler."));
trsuperobject.biblemap.put("b11_2_40", new trsuperobject(11,2,40,"Bunun üzerine Şimi kalkıp eşeğine palan vurdu ve kölelerini aramak üzere Gata Akişin yanına gitti. Kölelerini bulup Gattan geri getirdi."));
trsuperobject.biblemap.put("b11_2_41", new trsuperobject(11,2,41,"Şiminin Yeruşalimden Gata gidip döndüğü Süleymana anlatılınca,"));
trsuperobject.biblemap.put("b11_2_42", new trsuperobject(11,2,42,"Süleyman Şimiyi çağırttı. ‹‹Sana RABbin adıyla ant içirmedim mi?›› dedi, ‹‹Kalkıp herhangi bir yere gittiğin gün öleceğini bil!› diye seni uyarmadım mı? Sen de bana: ‹Peki, sözünü dinleyeceğim› demedin mi?"));
trsuperobject.biblemap.put("b11_2_43", new trsuperobject(11,2,43,"Öyleyse neden RABbin adına içtiğin anda ve buyruğuma uymadın?››"));
trsuperobject.biblemap.put("b11_2_44", new trsuperobject(11,2,44,"Kral, Şimiye karşı sözlerini şöyle sürdürdü: ‹‹Babam Davuta yaptığın bütün kötülükleri çok iyi biliyorsun. Bu yaptıklarından dolayı RAB seni cezalandıracak."));
trsuperobject.biblemap.put("b11_2_45", new trsuperobject(11,2,45,"Ama Kral Süleyman kutsanacak ve Davutun tahtı RABbin önünde sonsuza dek kurulu kalacaktır.››"));
trsuperobject.biblemap.put("b11_2_46", new trsuperobject(11,2,46,"Kral, Yehoyada oğlu Benaya'ya buyruk verdi. O da gidip Şimi'yi öldürdü."));
trsuperobject.biblemap.put("b11_3_1", new trsuperobject(11,3,1,"Süleyman, Mısır Firavununun kızıyla evlendi. Böylece firavunla müttefik oldu. Eşini Davut Kentine götürdü. Kendi sarayı, RABbin Tapınağı ve Yeruşalimin çevre surları tamamlanıncaya kadar orada yaşadılar."));
trsuperobject.biblemap.put("b11_3_2", new trsuperobject(11,3,2,"Halk, hâlâ çeşitli tapınma yerlerinde RABbe kurban sunuyordu. Çünkü o güne dek RABbin adına yapılmış bir tapınak yoktu."));
trsuperobject.biblemap.put("b11_3_3", new trsuperobject(11,3,3,"Süleyman babası Davutun kurallarına uyarak RABbe olan sevgisini gösterdi. Ancak hâlâ çeşitli tapınma yerlerinde kurban sunuyor, buhur yakıyordu."));
trsuperobject.biblemap.put("b11_3_4", new trsuperobject(11,3,4,"Tapınma yerlerinin en ünlüsü Givondaydı. Kral Süleyman oraya giderek sunakta bin yakmalık sunu sundu."));
trsuperobject.biblemap.put("b11_3_6", new trsuperobject(11,3,6,"Süleyman, ‹‹Kulun babam Davuta büyük iyilikler yaptın›› diye karşılık verdi, ‹‹O sana bağlı, doğru, bütün yüreğiyle dürüst biri olarak yolunda yürüdü. Bugün tahtına oturacak bir oğul vermekle ona büyük bir iyilik daha yapmış oldun."));
trsuperobject.biblemap.put("b11_3_7", new trsuperobject(11,3,7,"‹‹Ya RAB Tanrım! Ben henüz çocuk denecek bir yaşta, yöneticilik nedir bilmezken bu kulunu babam Davutun yerine kral atadın."));
trsuperobject.biblemap.put("b11_3_8", new trsuperobject(11,3,8,"İşte kulun kendi seçtiğin kalabalık halkın, sayılamayacak kadar büyük bir kalabalığın ortasındadır."));
trsuperobject.biblemap.put("b11_3_9", new trsuperobject(11,3,9,"Bu yüzden bana öyle sezgi dolu bir yürek ver ki, iyi ile kötüyü ayırt edip halkını yönetebileyim. Başka türlü senin bu büyük halkını kim yönetebilir!››"));
trsuperobject.biblemap.put("b11_3_10", new trsuperobject(11,3,10,"Süleymanın bu isteği Rabbi hoşnut etti."));
trsuperobject.biblemap.put("b11_3_11-12", new trsuperobject(11,3,11-12,"Tanrı ona şöyle dedi: ‹‹Madem kendin için uzun ömür, zenginlik ve düşmanlarının ölümünü istemedin, bunların yerine adil bir yönetim için bilgelik istedin; isteğini yerine getireceğim. Sana öyle bir bilgelik ve sezgi dolu bir yürek vereceğim ki, benzeri ne senden öncekilerde görülmüştür, ne de senden sonrakilerde görülecektir."));
trsuperobject.biblemap.put("b11_3_13", new trsuperobject(11,3,13,"Sana istemediklerini de vereceğim: Yaşadığın sürece öbür kralların erişemeyeceği bir zenginlik ve onura ulaşacaksın."));
trsuperobject.biblemap.put("b11_3_14", new trsuperobject(11,3,14,"Eğer sen de baban Davut gibi kurallarıma ve buyruklarıma uyup yollarımda yürürsen, sana uzun ömür de vereceğim.››"));
trsuperobject.biblemap.put("b11_3_15", new trsuperobject(11,3,15,"Süleyman uyanınca bunun bir rüya olduğunu anladı. Sonra Yeruşalime gitti, Rabbin Antlaşma Sandığının önünde durup yakmalık sunular ve esenlik sunuları sundu. Ayrıca bütün görevlilerine de bir şölen verdi."));
trsuperobject.biblemap.put("b11_3_16", new trsuperobject(11,3,16,"Bir gün iki fahişe gelip kralın önünde durdu."));
trsuperobject.biblemap.put("b11_3_17", new trsuperobject(11,3,17,"Kadınlardan biri krala şöyle dedi: ‹‹Efendim, bu kadınla ben aynı evde kalıyoruz. Birlikte kaldığımız sırada ben bir çocuk doğurdum."));
trsuperobject.biblemap.put("b11_3_18", new trsuperobject(11,3,18,"İki gün sonra da o doğurdu. Evde yalnızdık, ikimizden başka kimse yoktu."));
trsuperobject.biblemap.put("b11_3_19", new trsuperobject(11,3,19,"Bu kadın geceleyin çocuğunun üzerine yattığı için çocuk ölmüş."));
trsuperobject.biblemap.put("b11_3_20", new trsuperobject(11,3,20,"Gece yarısı, ben kulun uyurken, kalkıp çocuğumu almış, koynuna yatırmış, kendi ölü çocuğunu da benim koynuma koymuş."));
trsuperobject.biblemap.put("b11_3_21", new trsuperobject(11,3,21,"Sabahleyin oğlumu emzirmek için kalktığımda, onu ölmüş buldum. Ama sabah aydınlığında dikkatle bakınca, onun benim doğurduğum çocuk olmadığını anladım.››"));
trsuperobject.biblemap.put("b11_3_22", new trsuperobject(11,3,22,"Öbür kadın, ‹‹Hayır! Yaşayan çocuk benim, ölü olan senin!›› diye çıkıştı. Birinci kadın, ‹‹Hayır! Ölen çocuk senin, yaşayan çocuk benim!›› diye diretti. Kralın önünde böyle tartışıp durdular."));
trsuperobject.biblemap.put("b11_3_23", new trsuperobject(11,3,23,"Kral, ‹‹Biri, ‹Yaşayan çocuk benim, ölü olan senin› diyor, öbürü, ‹Hayır! Ölen çocuk senin, yaşayan benim› diyor."));
trsuperobject.biblemap.put("b11_3_24", new trsuperobject(11,3,24,"O halde bana bir kılıç getirin!›› dedi. Kılıç getirilince,"));
trsuperobject.biblemap.put("b11_3_25", new trsuperobject(11,3,25,"kral, ‹‹Yaşayan çocuğu ikiye bölüp yarısını birine, yarısını öbürüne verin!›› diye buyurdu."));
trsuperobject.biblemap.put("b11_3_26", new trsuperobject(11,3,26,"Yüreği oğlunun acısıyla sızlayan, çocuğun gerçek annesi krala, ‹‹Aman efendim, sakın çocuğu öldürmeyin! Ona verin!›› dedi. Öbür kadınsa, ‹‹Çocuk ne benim, ne de senin olsun, onu ikiye bölsünler!›› dedi."));
trsuperobject.biblemap.put("b11_3_27", new trsuperobject(11,3,27,"O zaman kral kararını verdi: ‹‹Sakın çocuğu öldürmeyin! Birinci kadına verin, çünkü gerçek annesi odur.››"));
trsuperobject.biblemap.put("b11_3_28", new trsuperobject(11,3,28,"Kralın verdiği bu kararı duyan bütün İsrailliler hayranlık içinde kaldı. Herkes adil bir yönetim için Süleyman'ın Tanrı'dan gelen bilgeliğe sahip olduğunu anladı."));
trsuperobject.biblemap.put("b11_4_1", new trsuperobject(11,4,1,"Süleyman bütün İsrailin kralıydı."));
trsuperobject.biblemap.put("b11_4_2", new trsuperobject(11,4,2,"Görevlileri ise şunlardı: Kâhin: Sadok oğlu Azarya."));
trsuperobject.biblemap.put("b11_4_3", new trsuperobject(11,4,3,"Yazmanlar: Şişanın oğulları Elihoref ve Ahiya. Devlet tarihçisi: Ahilut oğlu Yehoşafat."));
trsuperobject.biblemap.put("b11_4_4", new trsuperobject(11,4,4,"Ordu komutanı: Yehoyada oğlu Benaya. Kâhinler: Sadok ve Aviyatar."));
trsuperobject.biblemap.put("b11_4_5", new trsuperobject(11,4,5,"Baş vali: Natan oğlu Azarya. Kralın özel danışmanı: Natan oğlu Kâhin Zavut."));
trsuperobject.biblemap.put("b11_4_6", new trsuperobject(11,4,6,"Saray sorumlusu: Ahişar. Angaryacıların başı: Avda oğlu Adoniram."));
trsuperobject.biblemap.put("b11_4_7", new trsuperobject(11,4,7,"Süleymanın İsrailde on iki bölge valisi vardı. Bunlar kralın ve sarayın yiyecek içecek gereksinimini karşılardı. Her vali yılda bir ay bu gereksinimleri karşılamakla yükümlüydü."));
trsuperobject.biblemap.put("b11_4_8", new trsuperobject(11,4,8,"Bu valiler şunlardı: Efrayimin dağlık bölgesinde Ben-Hur;"));
trsuperobject.biblemap.put("b11_4_9", new trsuperobject(11,4,9,"Makaz, Şaalvim, Beytşemeş ve Elon-Beythanan bölgelerinde Ben-Deker;"));
trsuperobject.biblemap.put("b11_4_10", new trsuperobject(11,4,10,"Arubbot, Soko ve bütün Hefer bölgesinde Ben-Heset;"));
trsuperobject.biblemap.put("b11_4_11", new trsuperobject(11,4,11,"Nafat-Dor bölgesinde Süleymanın kızı Tafatla evli olan Ben-Avinadav;"));
trsuperobject.biblemap.put("b11_4_12", new trsuperobject(11,4,12,"Taanak, Megiddo, Yizreelin altında Saretanın yanındaki bütün Beytşean ve Beytşeandan Avel-Mehola ve Yokmoamın ötelerine kadar uzanan bölgede Ahilut oğlu Baana;"));
trsuperobject.biblemap.put("b11_4_13", new trsuperobject(11,4,13,"Ramot-Gilat, Gilatta Manaşşe oğlu Yairin yerleşim birimleri ve Başandaki Argov yöresinde surlar ve tunç sürgülerle güçlendirilmiş altmış büyük kentin başında Ben-Gever;"));
trsuperobject.biblemap.put("b11_4_14", new trsuperobject(11,4,14,"Mahanayim bölgesinde İddo oğlu Ahinadav;"));
trsuperobject.biblemap.put("b11_4_15", new trsuperobject(11,4,15,"Naftali bölgesinde Süleymanın kızı Basematla evlenen Ahimaas;"));
trsuperobject.biblemap.put("b11_4_16", new trsuperobject(11,4,16,"Aşer ve Bealot bölgelerinde Huşay oğlu Baana;"));
trsuperobject.biblemap.put("b11_4_17", new trsuperobject(11,4,17,"İssakar bölgesinde Paruah oğlu Yehoşafat;"));
trsuperobject.biblemap.put("b11_4_18", new trsuperobject(11,4,18,"Benyamin bölgesinde Ela oğlu Şimi;"));
trsuperobject.biblemap.put("b11_4_19", new trsuperobject(11,4,19,"Gilat bölgesinde, yani Amorluların Kralı Sihonla Başan Kralı Ogun eski topraklarında Uri oğlu Gever. Ayrıca Yahuda bölgesinin tek valisi vardı."));
trsuperobject.biblemap.put("b11_4_20", new trsuperobject(11,4,20,"Yahuda ve İsrail halkı kıyıların kumu kadar kalabalıktı. Herkes yiyip içip sevinç içinde yaşıyordu."));
trsuperobject.biblemap.put("b11_4_21", new trsuperobject(11,4,21,"Süleyman, Fırat Irmağından Filiste, oradan Mısır sınırına kadar bütün ülkelere egemendi. Bu ülkeler Süleymanın yaşamı boyunca ona haraç ödeyip hizmet ettiler."));
trsuperobject.biblemap.put("b11_4_22", new trsuperobject(11,4,22,"Süleymanın sarayının bir günlük yiyecek gereksinimi şunlardı: Otuz kor ince, altmış korfç kepekli un;"));
trsuperobject.biblemap.put("b11_4_23", new trsuperobject(11,4,23,"onu ahırda, yirmisi çayırda yetiştirilmiş sığır ve yüz koyun; ayrıca geyikler, ceylanlar, karacalar ve semiz kuşlar."));
trsuperobject.biblemap.put("b11_4_24", new trsuperobject(11,4,24,"Tifsahtan Gazzeye kadar, Fırat Irmağının batısındaki bütün krallıkları Süleyman yönetiyordu. Her tarafta barış vardı."));
trsuperobject.biblemap.put("b11_4_25", new trsuperobject(11,4,25,"Dandan Beer-Şevaya kadar Yahuda ve İsrail halkının her bireyi Süleymanın yaşamı boyunca kendi asması ve incir ağacı altında güvenlik içinde yaşadı."));
trsuperobject.biblemap.put("b11_4_26", new trsuperobject(11,4,26,"Süleymanın savaş arabalarının atları için kırk bin ahırı ve on iki bin atlısı vardı."));
trsuperobject.biblemap.put("b11_4_27", new trsuperobject(11,4,27,"Bölge valilerinin her biri kendine düşen bir ay boyunca, Kral Süleymana ve sofrasına oturan herkese yiyecek sağlar, hiçbir şeyi eksik etmezdi."));
trsuperobject.biblemap.put("b11_4_28", new trsuperobject(11,4,28,"Her vali kendisine verilen buyruk uyarınca, savaş arabalarının atlarıyla öbür atlar için belirli bir yere arpa ve saman getirirdi."));
trsuperobject.biblemap.put("b11_4_29", new trsuperobject(11,4,29,"Tanrı, Süleymana bilgelik, derin bir sezgi, kıyılardaki kum kadar anlayış verdi."));
trsuperobject.biblemap.put("b11_4_30", new trsuperobject(11,4,30,"Süleymanın bilgeliği, bütün doğuluların ve Mısırlıların bilgeliğinden daha üstündü."));
trsuperobject.biblemap.put("b11_4_31", new trsuperobject(11,4,31,"O, Ezrahlı Etan, Maholun oğulları Heman, Kalkol ve Darda dahil herkesten daha bilgeydi. Ünü çevredeki bütün uluslara yayılmıştı."));
trsuperobject.biblemap.put("b11_4_32", new trsuperobject(11,4,32,"Üç bin özdeyişi ve bin beş ezgisi vardı."));
trsuperobject.biblemap.put("b11_4_33", new trsuperobject(11,4,33,"Lübnan sedir ağacından duvarlarda biten mercanköşkotuna kadar bütün ağaçlardan söz ettiği gibi, hayvanlar, kuşlar, sürüngenler ve balıklardan da söz edebiliyordu."));
trsuperobject.biblemap.put("b11_4_34", new trsuperobject(11,4,34,"Süleyman'ın bilgeliğini duyan dünyanın bütün kralları ona adamlarını gönderirdi. Bütün uluslardan insanlar gelir, Süleyman'ın bilgece sözlerini dinlerdi."));
trsuperobject.biblemap.put("b11_5_1", new trsuperobject(11,5,1,"Sur Kralı Hiram, Süleymanın babası Davutun yerine kral olarak meshedildiğini duyunca, elçilerini Süleymana gönderdi. Çünkü Davutla hep dostça geçinmişti."));
trsuperobject.biblemap.put("b11_5_2", new trsuperobject(11,5,2,"Süleyman Hirama şu haberi gönderdi:"));
trsuperobject.biblemap.put("b11_5_3", new trsuperobject(11,5,3,"‹‹Bildiğin gibi, babam Davut çevresindeki savaşlar yüzünden Tanrısı RABbin adına bir tapınak yapamadı. Bu savaşlarda RAB, Davutun düşmanlarını onun ayakları altına serdi."));
trsuperobject.biblemap.put("b11_5_4", new trsuperobject(11,5,4,"Oysa şimdi Tanrım RAB her yönden bana rahatlık verdi. Ne bir düşmanım var, ne de kötü bir olay."));
trsuperobject.biblemap.put("b11_5_6", new trsuperobject(11,5,6,"‹‹Şimdi bana Lübnandan sedir ağaçları kesmeleri için adamlarına buyruk ver. Benim adamlarım da seninkilerle birlikte çalışsın. Adamların için istediğin ücreti vereceğim. Aramızda Saydalılar kadar ağaç kesmede usta adamlar olmadığını biliyorsun.››"));
trsuperobject.biblemap.put("b11_5_7", new trsuperobject(11,5,7,"Hiram, Süleymandan bu haberi alınca çok sevindi ve, ‹‹Bugün, o büyük ulusu yönetmek üzere Davuta bilge bir oğul veren RABbe övgüler olsun!›› dedi."));
trsuperobject.biblemap.put("b11_5_8", new trsuperobject(11,5,8,"Sonra Hiram Süleymana şu haberi gönderdi: ‹‹Gönderdiğin haberi aldım. Sedir ve çam ağaçlarıyla ilgili bütün dileklerini yerine getireceğim."));
trsuperobject.biblemap.put("b11_5_9", new trsuperobject(11,5,9,"Adamlarım tomrukları Lübnandan denize indirecekler, ben de onları sallar halinde bağlatıp belirteceğin yere kadar yüzdüreceğim. Orada adamlarım onları çözer, sen de alıp götürürsün. Sarayımın yiyecek gereksinimini karşılamakla, sen de benim dileğimi yerine getirmiş olursun.››"));
trsuperobject.biblemap.put("b11_5_10", new trsuperobject(11,5,10,"Hiram Süleymana istediği kadar sedir ve çam tomruğu sağladı."));
trsuperobject.biblemap.put("b11_5_11", new trsuperobject(11,5,11,"Süleyman her yıl Hirama sarayının yiyecek gereksinimi olarak yirmi bin kor buğday, yirmi kor saf zeytinyağı verirdi."));
trsuperobject.biblemap.put("b11_5_12", new trsuperobject(11,5,12,"RAB, verdiği söz uyarınca, Süleymana bilgelik verdi. Süleymanla Hiram arasında barış vardı. Aralarında bir antlaşma yaptılar."));
trsuperobject.biblemap.put("b11_5_13", new trsuperobject(11,5,13,"Kral Süleyman angaryasına çalıştırmak üzere bütün İsrailden otuz bin adam topladı."));
trsuperobject.biblemap.put("b11_5_14", new trsuperobject(11,5,14,"Sırayla her ay on binini Lübnana gönderiyordu. Bir ay Lübnanda, iki ay evlerinde kalıyorlardı. Angaryasına çalışan adamların başında Adoniram vardı."));
trsuperobject.biblemap.put("b11_5_15", new trsuperobject(11,5,15,"Süleymanın yük taşıyan 70 000, dağlarda taş kesen 80 000 adamı vardı."));
trsuperobject.biblemap.put("b11_5_16", new trsuperobject(11,5,16,"Ayrıca, işin yürümesini sağlayan ve işçileri yöneten 3 300 görevlisi vardı."));
trsuperobject.biblemap.put("b11_5_17", new trsuperobject(11,5,17,"İşçiler, kralın buyruğu uyarınca, tapınağın temelini yontma taşlarla atmak üzere ocaktan büyük ve kaliteli taşlar kesip çıkardılar."));
trsuperobject.biblemap.put("b11_5_18", new trsuperobject(11,5,18,"Süleyman'ın ve Hiram'ın yapıcılarıyla Gevallılar, tapınağın yapımı için taşlarla keresteleri kesip hazırladılar."));
trsuperobject.biblemap.put("b11_6_1", new trsuperobject(11,6,1,"İsrail halkı Mısırdan çıktıktan dört yüz seksen yıl sonra, Süleyman, krallığının dördüncü yılının ikinci ayı olan Ziv ayında RABbin Tapınağının yapımına başladı."));
trsuperobject.biblemap.put("b11_6_2", new trsuperobject(11,6,2,"Kral Süleymanın RAB için yaptığı tapınağın uzunluğu altmış, genişliği yirmi, yüksekliği otuz arşındı."));
trsuperobject.biblemap.put("b11_6_3", new trsuperobject(11,6,3,"Tapınağın ana bölümünün önündeki eyvan tapınağın genişliğinde olup yirmi arşındı. Eyvan tapınağın önünden ileriye doğru on arşındıfı."));
trsuperobject.biblemap.put("b11_6_4", new trsuperobject(11,6,4,"Süleyman tapınakta dışa doğru daralan kafesli pencereler yaptırdı."));
trsuperobject.biblemap.put("b11_6_5", new trsuperobject(11,6,5,"Tapınağın dış cephesine bitişik, ana bölümün ve iç odanın çevresindeki duvarlara bitişik, odalardan oluşan katlar yaptırdı."));
trsuperobject.biblemap.put("b11_6_6", new trsuperobject(11,6,6,"Alt kat beş arşın, orta kat altı arşın, üst kat yedi arşın genişliğindeydi. Kirişler tapınağın duvarlarına girmesin diye duvarların çevresinde dışarıya doğru çıkıntılar bıraktı."));
trsuperobject.biblemap.put("b11_6_7", new trsuperobject(11,6,7,"Tapınağın yapımında kullanılan taşlar taş ocaklarında yontulmuştu. Onun için yapım halindeki tapınakta çekiç ve balta dahil hiçbir demir aletin sesi duyulmadı."));
trsuperobject.biblemap.put("b11_6_8", new trsuperobject(11,6,8,"Aşağı yan katın girişi tapınağın güneyindeydi. Döner bir merdivenle orta kata, oradan da üçüncü kata çıkılırdı."));
trsuperobject.biblemap.put("b11_6_9", new trsuperobject(11,6,9,"Süleyman tapınağı yapıp tamamladı. Üstünü sedir ağacından direklerle, kalın tahtalarla kapattı."));
trsuperobject.biblemap.put("b11_6_10", new trsuperobject(11,6,10,"Dış duvarlara bitişik katlar tapınağın çevresini kapsıyordu. Her birinin yüksekliği beş arşındı. Bunlar sedir ağacından kirişlerle tapınağa eklendi."));
trsuperobject.biblemap.put("b11_6_11", new trsuperobject(11,6,11,"RAB, Süleymana şöyle seslendi:"));
trsuperobject.biblemap.put("b11_6_12", new trsuperobject(11,6,12,"‹‹Bu tapınağı yapmaktasın. Kurallarıma, ilkelerime ve bütün buyruklarıma uyup onlara bağlı kalırsan, baban Davuta verdiğim sözü senin aracılığınla yerine getireceğim."));
trsuperobject.biblemap.put("b11_6_13", new trsuperobject(11,6,13,"Halkım İsrailin arasında yaşayıp onları hiç terk etmeyeceğim.››"));
trsuperobject.biblemap.put("b11_6_14", new trsuperobject(11,6,14,"Süleyman tapınağı yapıp bitirdi."));
trsuperobject.biblemap.put("b11_6_15", new trsuperobject(11,6,15,"Tapınağın iç duvarlarının yüzeyini sedir ağaçlarıyla döşeyip üstlerini tabandan tavana kadar tahtalarla kapladı. Tapınağın zeminini ise çam tahtalarla döşetti."));
trsuperobject.biblemap.put("b11_6_16", new trsuperobject(11,6,16,"En Kutsal Yer olarak adlandırılan iç oda, tapınağın arka bölümünde yapıldı. Tabandan tavana kadar sedir tahtasından oluşan bir duvarla öbür bölümlerden ayrıldı. Bu bölümün uzunluğu yirmi arşındı."));
trsuperobject.biblemap.put("b11_6_17", new trsuperobject(11,6,17,"Bu iç odanın önündeki ana bölümün uzunluğu ise kırk arşındı."));
trsuperobject.biblemap.put("b11_6_18", new trsuperobject(11,6,18,"Taşlar görünmesin diye tapınağın içi, üzerine sukabağı ve çiçek motifleri oyulmuş sedir tahtalarıyla kaplandı."));
trsuperobject.biblemap.put("b11_6_19", new trsuperobject(11,6,19,"Tapınağın içinde RABbin Antlaşma Sandığının konacağı iç oda hazırlandı."));
trsuperobject.biblemap.put("b11_6_20", new trsuperobject(11,6,20,"Bu odanın içten içe uzunluğu, genişliği ve yüksekliği yirmişer arşındı. Süleyman odayı saf altınla kaplattı. Sunağı da sedir tahtalarla döşetti."));
trsuperobject.biblemap.put("b11_6_21", new trsuperobject(11,6,21,"Tapınağın içini saf altınla kaplattı; iç odanın önüne altın zincirler asıp orayı da altınla kaplattı."));
trsuperobject.biblemap.put("b11_6_22", new trsuperobject(11,6,22,"Böylece iç odadaki sunak dahil, tapınağın içini tamamen altınla kaplatmış oldu."));
trsuperobject.biblemap.put("b11_6_23", new trsuperobject(11,6,23,"İç odada her biri on arşın yüksekliğinde, iğde ağacından iki Keruv yaptı."));
trsuperobject.biblemap.put("b11_6_24", new trsuperobject(11,6,24,"Her kanadı beşer arşın olan Keruvun açık kanatları bir uçtan öbür uca toplam on arşındı."));
trsuperobject.biblemap.put("b11_6_25", new trsuperobject(11,6,25,"Öbür Keruvun kanat açıklığı da on arşındı. Her iki Keruvun da ölçüsü ve görünüşü aynıydı."));
trsuperobject.biblemap.put("b11_6_26", new trsuperobject(11,6,26,"İkisinin de yüksekliği on arşındı."));
trsuperobject.biblemap.put("b11_6_27", new trsuperobject(11,6,27,"Süleyman Keruvları tapınağın iç odasına yerleştirdi. Keruvlardan birinin açık kanadı bir duvara, ötekinin kanadı karşı duvara erişirken, öbür kanatları da odanın ortasında birbirine değiyordu."));
trsuperobject.biblemap.put("b11_6_28", new trsuperobject(11,6,28,"Süleyman Keruvları da altınla kaplattı."));
trsuperobject.biblemap.put("b11_6_29", new trsuperobject(11,6,29,"Tapınağın iç ve dış odalarının bütün duvarlarını kabartma Keruvlar, hurma ağaçları ve çiçek motifleriyle süsletti."));
trsuperobject.biblemap.put("b11_6_30", new trsuperobject(11,6,30,"Tapınağın hem iç, hem de dış odasının döşemelerini altınla kaplattı."));
trsuperobject.biblemap.put("b11_6_31", new trsuperobject(11,6,31,"İç odanın girişine iğde ağacından iki kanatlı söveli bir kapı yaptırdı. Söveli kapının genişliği tapınağın genişliğinin beşte biriydi."));
trsuperobject.biblemap.put("b11_6_32", new trsuperobject(11,6,32,"İğde ağacından yapılan iki kapı kanadının üstüne kabartma Keruvlar, hurma ağaçları ve çiçek motifleri oydurdu. Keruvlar ve hurma ağaçlarını altınla kaplattı."));
trsuperobject.biblemap.put("b11_6_33", new trsuperobject(11,6,33,"Aynı biçimde ana bölümün girişine de iğde ağacından kapı söveleri yaptırdı. Bu söveli kapı tapınağın genişliğinin dörtte biriydi."));
trsuperobject.biblemap.put("b11_6_34", new trsuperobject(11,6,34,"Ayrıca, çam ağacından, her biri iki kanatlı, katlanabilen iki kapı yaptırdı."));
trsuperobject.biblemap.put("b11_6_35", new trsuperobject(11,6,35,"Bunların da üstüne Keruvlar, hurma ağaçları ve çiçek motifleri oydurdu. Oymaların üzerini düzgün biçimde altınla kaplattı. biriydi›› ya da ‹‹Kapı söveleri beş köşeliydi.›› Benzer ifade 33. ayette de geçmektedir."));
trsuperobject.biblemap.put("b11_6_36", new trsuperobject(11,6,36,"İç avlunun duvarlarını üç sıra yontma taş ve bir sıra sedir ağacı kirişleriyle yaptırdı."));
trsuperobject.biblemap.put("b11_6_37", new trsuperobject(11,6,37,"RABbin Tapınağının temeli dördüncü yılın Ziv ayında atıldı."));
trsuperobject.biblemap.put("b11_6_38", new trsuperobject(11,6,38,"On birinci yılın sekizinci ayı olan Bul ayında tapınak tasarlandığı biçimde bütün ayrıntılarıyla tamamlandı. Tapınağın yapımı Süleyman'ın yedi yılını almıştı."));
trsuperobject.biblemap.put("b11_7_1", new trsuperobject(11,7,1,"Süleyman kendine, yapımı on üç yıl süren bir saray yaptırdı."));
trsuperobject.biblemap.put("b11_7_2", new trsuperobject(11,7,2,"Uzunluğu yüzfö, genişliği elli, yüksekliği otuz arşın olan Lübnan Ormanı adında bir saray daha yaptırdı. Saray sedir kirişler yerleştirilmiş dört sıra halindeki sedir sütunların üzerine yapılmıştı."));
trsuperobject.biblemap.put("b11_7_3", new trsuperobject(11,7,3,"Sütunların üstündeki kırk beş kirişin üstü sedir tahtalarıyla kaplanmıştı. Bir sıra on beş kirişten oluşuyordu."));
trsuperobject.biblemap.put("b11_7_4", new trsuperobject(11,7,4,"Kafesli pencereler üç sıra halinde birbirine bakacak biçimde yapılmıştı."));
trsuperobject.biblemap.put("b11_7_5", new trsuperobject(11,7,5,"Kapılar ve kapı söveleri dört köşeliydi. Pencereler ise üç sıra halinde birbirine bakacak biçimde yapılmıştı."));
trsuperobject.biblemap.put("b11_7_6", new trsuperobject(11,7,6,"Süleyman elli arşın uzunluğunda otuz arşın genişliğinde sütunlu bir eyvan yaptırdı. Eyvanın önünde sütunlarla desteklenmiş asma tavan vardı."));
trsuperobject.biblemap.put("b11_7_7", new trsuperobject(11,7,7,"Taht Eyvanını, yani kararların verileceği Yargı Eyvanını da yaptırdı. Bu eyvan da baştan aşağı sedir tahtalarıyla kaplıydı."));
trsuperobject.biblemap.put("b11_7_8", new trsuperobject(11,7,8,"Eyvanın arkasında öbür avludaki kendi oturacağı saray da aynı biçimde yapılmıştı. Süleyman, karısı olan firavunun kızı için de bu eyvanın benzeri bir saray yaptırdı."));
trsuperobject.biblemap.put("b11_7_9", new trsuperobject(11,7,9,"Dışarıdan büyük avluya, temelden çatıya kadar bütün bu yapılar kaliteli taşlarla yapılmıştı. Taşlar testereyle kesilmiş, ön ve arka yüzleri yontulmuş, belirli ölçülere göre hazırlanmıştı."));
trsuperobject.biblemap.put("b11_7_10", new trsuperobject(11,7,10,"Temeller sekiz ve on arşınfş uzunluğunda büyük, seçme taşlardan atılmıştı."));
trsuperobject.biblemap.put("b11_7_11", new trsuperobject(11,7,11,"Üstlerinde belirli ölçülere göre kesilmiş kaliteli taşlar ve sedir kirişler vardı."));
trsuperobject.biblemap.put("b11_7_12", new trsuperobject(11,7,12,"Büyük avlu üç sıra yontma taş ve bir sıra sedir kirişlerinden oluşan bir duvarla çevrilmişti. RABbin Tapınağının iç avlusuyla eyvanın duvarları da aynı yapıdaydı."));
trsuperobject.biblemap.put("b11_7_13", new trsuperobject(11,7,13,"Kral Süleyman haber gönderip Surdan Hiramı getirtti."));
trsuperobject.biblemap.put("b11_7_14", new trsuperobject(11,7,14,"Hiramın annesi Naftali oymağından dul bir kadın, babası ise Surlu bir tunç işçisiydi. Hiram tunç işlemede bilgili, deneyimli, usta biriydi. Gelip Kral Süleymanın bütün işlerini yaptı."));
trsuperobject.biblemap.put("b11_7_15", new trsuperobject(11,7,15,"Hiram her birinin yüksekliği on sekiz arşın ve çevresi on iki arşın olan iki tunç sütun döktü."));
trsuperobject.biblemap.put("b11_7_16", new trsuperobject(11,7,16,"Sütunların üzerine koymak için beşer arşınfü yüksekliğinde dökme tunçtan iki sütun başlığı yaptı."));
trsuperobject.biblemap.put("b11_7_17-18", new trsuperobject(11,7,17-18,"Sütun başlıklarının her biri ağla kaplanmıştı. Ağın üzeri yedi sıra örgülü zincirle ve iki sıra nar motifiyle bezenmişti."));
trsuperobject.biblemap.put("b11_7_19", new trsuperobject(11,7,19,"Eyvanda bulunan dört arşın yüksekliğindeki sütun başlıkları da nilüfer biçimindeydi."));
trsuperobject.biblemap.put("b11_7_20", new trsuperobject(11,7,20,"Her iki sütun başlığında, örgülü ağa yakın çıkıntının yukarısında çepeçevre diziler halinde iki yüz nar motifi vardı."));
trsuperobject.biblemap.put("b11_7_21", new trsuperobject(11,7,21,"Hiram sütunları tapınağın eyvanına dikip sağdakine Yakin, soldakine Boaz adını verdi."));
trsuperobject.biblemap.put("b11_7_22", new trsuperobject(11,7,22,"Sütun başlıkları nilüfer biçimindeydi. Böylece sütunların işi tamamlanmış oldu. gelebilir."));
trsuperobject.biblemap.put("b11_7_23", new trsuperobject(11,7,23,"Hiram dökme tunçtan on arşın çapında, beş arşın derinliğinde, çevresi otuz arşın yuvarlak bir havuz yaptı."));
trsuperobject.biblemap.put("b11_7_24", new trsuperobject(11,7,24,"Havuz, kenarlarının altındaki iki sıra sukabağı motifiyle birlikte dökülmüştü. Her arşında onar tane olan bu motifler havuzu çepeçevre kuşatıyordu."));
trsuperobject.biblemap.put("b11_7_25", new trsuperobject(11,7,25,"Havuz üçü kuzeye, üçü batıya, üçü güneye, üçü de doğuya bakan on iki boğa heykeli üzerine oturtulmuştu. Boğaların sağrıları içe dönüktü."));
trsuperobject.biblemap.put("b11_7_26", new trsuperobject(11,7,26,"Havuzun çeperi dört parmak kalınlığındaydı; kenarları kâse kenarlarını, nilüferleri andırıyordu. İki bin bat su alıyordu."));
trsuperobject.biblemap.put("b11_7_27", new trsuperobject(11,7,27,"Hiram her biri dört arşın uzunluğunda, dört arşın genişliğinde ve üç arşınfç yüksekliğinde on adet tunç ayaklık yaptı."));
trsuperobject.biblemap.put("b11_7_28", new trsuperobject(11,7,28,"Ayaklıklar aynalıklarla döşenmiş, aynalıklar da çerçeve içine alınmıştı."));
trsuperobject.biblemap.put("b11_7_29", new trsuperobject(11,7,29,"Aynalıklar aslan, boğa, Keruv motifleriyle süslenmişti. Çerçeveler de böyleydi, yalnız aslanlarla boğaların üstünde ve altında sarkık çelenk işlemeleri vardı."));
trsuperobject.biblemap.put("b11_7_30", new trsuperobject(11,7,30,"Her bir ayaklığın dört tunç tekerleği ve dingilleri vardı. Dört köşeye de kazan için destekler yapılmıştı. Her dökme destek çelenklerle süslenmişti."));
trsuperobject.biblemap.put("b11_7_31", new trsuperobject(11,7,31,"Ayaklığın üst yüzeyinde kazan için bir arşın yüksekliğinde yuvarlak çerçeveli bir boşluk vardı. Boşluğun tabanı bir buçuk arşın genişliğindeydi. Çevresinde oymalar vardı. Ayaklıkların aynalıkları yuvarlak değil, kareydi."));
trsuperobject.biblemap.put("b11_7_32", new trsuperobject(11,7,32,"Aynalıkların altındaki dört tekerleğin dingilleri ayaklıklara bağlıydı. Her tekerleğin çapı bir buçuk arşındı."));
trsuperobject.biblemap.put("b11_7_33", new trsuperobject(11,7,33,"Tekerlekler savaş arabalarının tekerlekleri gibiydi. Dingilleri, jantları, parmakları ve göbeklerinin hepsi dökümdü."));
trsuperobject.biblemap.put("b11_7_34", new trsuperobject(11,7,34,"Her ayaklığın dört köşesinde de kendinden dört destek vardı."));
trsuperobject.biblemap.put("b11_7_35", new trsuperobject(11,7,35,"Ayaklıkların üstünde yarım arşın yüksekliğinde yuvarlak birer halka vardı. Ayaklıkların başındaki dayanaklar ve yan aynalıklar da ayaklıklara bitişikti."));
trsuperobject.biblemap.put("b11_7_36", new trsuperobject(11,7,36,"Hiram dayanakların ve aynalıklarının genişliği oranında her birinin yüzeyine Keruvlar, aslanlar, hurma ağaçları, çevrelerine de çelenkler oydu."));
trsuperobject.biblemap.put("b11_7_37", new trsuperobject(11,7,37,"Böylece on ayaklığı yaptı; hepsinin dökümü, ölçüsü ve biçimi aynıydı."));
trsuperobject.biblemap.put("b11_7_38", new trsuperobject(11,7,38,"Hiram ayrıca on ayaklığın üzerine oturan dörder arşın genişliğinde on tunç kazan yaptı. Her kazan kırk bat su alıyordu."));
trsuperobject.biblemap.put("b11_7_39", new trsuperobject(11,7,39,"Ayaklıkların beşini tapınağın güneyine, beşini kuzeyine yerleştirdi. Havuzu ise tapınağın güneydoğu köşesine yerleştirdi."));
trsuperobject.biblemap.put("b11_7_40", new trsuperobject(11,7,40,"Hiram kazanlar, kürekler, çanaklar yaptı. Böylece Kral Süleyman için üstlenmiş olduğu RABbin Tapınağıyla ilgili bütün işleri tamamlamış oldu:"));
trsuperobject.biblemap.put("b11_7_41", new trsuperobject(11,7,41,"İki sütun ve iki yuvarlak sütun başlığı, bu başlıkları süsleyen iki örgülü ağ,"));
trsuperobject.biblemap.put("b11_7_42", new trsuperobject(11,7,42,"Sütunların yuvarlak başlıklarını süsleyen iki örgülü ağın üzerini ikişer sıra halinde süsleyen dört yüz nar motifi,"));
trsuperobject.biblemap.put("b11_7_43", new trsuperobject(11,7,43,"On kazan ve ayaklıkları,"));
trsuperobject.biblemap.put("b11_7_44", new trsuperobject(11,7,44,"Havuz ve havuzu taşıyan on iki boğa heykeli,"));
trsuperobject.biblemap.put("b11_7_45", new trsuperobject(11,7,45,"Kovalar, kürekler, çanaklar. Hiramın Kral Süleyman için RABbin Tapınağına yaptığı bütün bu eşyalar parlak tunçtandı."));
trsuperobject.biblemap.put("b11_7_46", new trsuperobject(11,7,46,"Kral bunları Şeria Ovasında, Sukkot ile Saretan arasındaki killi topraklarda döktürmüştü."));
trsuperobject.biblemap.put("b11_7_47", new trsuperobject(11,7,47,"Eşyalar o kadar çoktu ki, Süleyman hepsini tartmadı. Kullanılan tuncun hesabı tutulmadı."));
trsuperobject.biblemap.put("b11_7_48", new trsuperobject(11,7,48,"Süleymanın RABbin Tapınağı için yaptırdığı altın eşyalar şunlardı: Sunak, ekmeklerin Tanrının huzuruna konduğu masa,"));
trsuperobject.biblemap.put("b11_7_49", new trsuperobject(11,7,49,"İç odanın girişine, beşi sağa, beşi sola yerleştirilen saf altın kandillikler, çiçek süslemeleri, kandiller, maşalar,"));
trsuperobject.biblemap.put("b11_7_50", new trsuperobject(11,7,50,"Saf altın taslar, fitil maşaları, çanaklar, tabaklar, buhurdanlar. Tapınaktaki iç odanın, yani En Kutsal Yerin ve ana bölümün kapı menteşeleri de altındandı."));
trsuperobject.biblemap.put("b11_7_51", new trsuperobject(11,7,51,"RAB'bin Tapınağı'nın yapımı tamamlanınca Kral Süleyman, babası Davut'un adadığı altın, gümüş ve öbür eşyaları getirip tapınağın hazine odalarına yerleştirdi."));
trsuperobject.biblemap.put("b11_8_1", new trsuperobject(11,8,1,"Kral Süleyman RABbin Antlaşma Sandığını Davut Kenti olan Siyondan getirmek üzere İsrail halkının ileri gelenleriyle bütün oymak ve boy başlarını Yeruşalime çağırdı."));
trsuperobject.biblemap.put("b11_8_2", new trsuperobject(11,8,2,"Hepsi yedinci ay olan Etanim ayındaki bayramda Kral Süleymanın önünde toplandı."));
trsuperobject.biblemap.put("b11_8_3", new trsuperobject(11,8,3,"İsrailin bütün ileri gelenleri toplanınca, bazı kâhinler Antlaşma Sandığını yerden kaldırdılar."));
trsuperobject.biblemap.put("b11_8_4", new trsuperobject(11,8,4,"Sandığı, Buluşma Çadırını ve çadırdaki bütün kutsal eşyaları kâhinlerle Levililer tapınağa taşıdılar."));
trsuperobject.biblemap.put("b11_8_5", new trsuperobject(11,8,5,"Kral Süleyman ve bütün İsrail topluluğu Antlaşma Sandığının önünde sayısız davar ve sığır kurban etti."));
trsuperobject.biblemap.put("b11_8_6", new trsuperobject(11,8,6,"Kâhinler RABbin Antlaşma Sandığını tapınağın iç odasına, En Kutsal Yere taşıyıp Keruvların kanatlarının altına yerleştirdiler."));
trsuperobject.biblemap.put("b11_8_7", new trsuperobject(11,8,7,"Keruvların kanatları sandığın konduğu yerin üstüne kadar uzanıyor ve sandığı da, sırıklarını da örtüyordu."));
trsuperobject.biblemap.put("b11_8_8", new trsuperobject(11,8,8,"Sırıklar öyle uzundu ki, uçları iç odanın önündeki Kutsal Yerden görünüyordu. Ancak dışarıdan görünmüyordu. Bunlar hâlâ oradadır."));
trsuperobject.biblemap.put("b11_8_9", new trsuperobject(11,8,9,"Sandığın içinde Musanın Horev Dağında koyduğu iki taş levhadan başka bir şey yoktu. Bunlar Mısırdan çıkışlarında RABbin İsraillilerle yaptığı antlaşmanın taş levhalarıydı."));
trsuperobject.biblemap.put("b11_8_10", new trsuperobject(11,8,10,"Kâhinler Kutsal Yerden çıkınca, RABbin Tapınağını bir bulut doldurdu."));
trsuperobject.biblemap.put("b11_8_11", new trsuperobject(11,8,11,"Bu bulut yüzünden kâhinler görevlerini sürdüremediler. Çünkü RABbin görkemi tapınağı doldurmuştu."));
trsuperobject.biblemap.put("b11_8_12", new trsuperobject(11,8,12,"O zaman Süleyman şöyle dedi: ‹‹Ya RAB, karanlık bulutlarda otururum demiştin."));
trsuperobject.biblemap.put("b11_8_13", new trsuperobject(11,8,13,"Senin için görkemli bir tapınak, sonsuza dek yaşayacağın bir konut yaptım.››"));
trsuperobject.biblemap.put("b11_8_14", new trsuperobject(11,8,14,"Kral ayakta duran bütün İsrail topluluğuna dönerek onları kutsadıktan sonra şöyle dedi:"));
trsuperobject.biblemap.put("b11_8_15", new trsuperobject(11,8,15,"‹‹Babam Davuta verdiği sözü tutan İsrailin Tanrısı RABbe övgüler olsun! RAB demişti ki,"));
trsuperobject.biblemap.put("b11_8_16", new trsuperobject(11,8,16,"‹Halkım İsraili Mısırdan çıkardığım günden bu yana, içinde bulunacağım bir tapınak yaptırmak için İsrail oymaklarına ait kentlerden hiçbirini seçmedim. Ancak halkım İsraili yönetmesi için Davutu seçtim.› Benzer ifadeler 8:29,44; 9:3; 11:36da da geçer."));
trsuperobject.biblemap.put("b11_8_17", new trsuperobject(11,8,17,"‹‹Babam Davut İsrailin Tanrısı RABbin adına bir tapınak yapmayı yürekten istiyordu."));
trsuperobject.biblemap.put("b11_8_18", new trsuperobject(11,8,18,"Ama RAB, babam Davuta, ‹Adıma bir tapınak yapmayı yürekten istemen iyi bir şey› dedi,"));
trsuperobject.biblemap.put("b11_8_19", new trsuperobject(11,8,19,"‹Ne var ki, adıma yapılacak bu tapınağı sen değil, öz oğlun yapacak.›"));
trsuperobject.biblemap.put("b11_8_20", new trsuperobject(11,8,20,"‹‹RAB verdiği sözü yerine getirdi. RABbin sözü uyarınca, babam Davuttan sonra İsrail tahtına ben geçtim ve İsrailin Tanrısı RABbin adına tapınağı ben yaptırdım."));
trsuperobject.biblemap.put("b11_8_21", new trsuperobject(11,8,21,"Ayrıca, RABbin atalarımızı Mısırdan çıkardığında onlarla yaptığı antlaşmanın içinde korunduğu sandık için tapınakta bir yer hazırladım.››"));
trsuperobject.biblemap.put("b11_8_22", new trsuperobject(11,8,22,"Süleyman RABbin sunağının önünde, İsrail topluluğunun karşısında durup ellerini göklere açtı."));
trsuperobject.biblemap.put("b11_8_23", new trsuperobject(11,8,23,"‹‹Ya RAB, İsrailin Tanrısı, yerde ve gökte sana benzer başka tanrı yoktur›› dedi, ‹‹Bütün yürekleriyle yolunu izleyen kullarınla yaptığın antlaşmaya bağlı kalırsın."));
trsuperobject.biblemap.put("b11_8_24", new trsuperobject(11,8,24,"Ağzınla kulun babam Davuta verdiğin sözü bugün ellerinle yerine getirdin."));
trsuperobject.biblemap.put("b11_8_25", new trsuperobject(11,8,25,"‹‹Şimdi, ya RAB, İsrailin Tanrısı, kulun babam Davuta verdiğin öbür sözü de tutmanı istiyorum. Ona, ‹Senin soyundan İsrail tahtına oturacakların ardı arkası kesilmeyecektir; yeter ki, çocukların önümde senin gibi dikkatle yürüsünler› demiştin."));
trsuperobject.biblemap.put("b11_8_26", new trsuperobject(11,8,26,"Ey İsrailin Tanrısı, şimdi kulun babam Davuta verdiğin sözleri yerine getirmeni istiyorum."));
trsuperobject.biblemap.put("b11_8_27", new trsuperobject(11,8,27,"‹‹Tanrı gerçekten yeryüzünde yaşar mı? Sen göklere, göklerin göklerine bile sığmazsın. Benim yaptığım bu tapınak ne ki!"));
trsuperobject.biblemap.put("b11_8_28", new trsuperobject(11,8,28,"Ya RAB Tanrım, kulunun bugün ettiği duayı, yalvarışı işit; duasına ve yakarışına kulak ver."));
trsuperobject.biblemap.put("b11_8_29", new trsuperobject(11,8,29,"Gözlerin gece gündüz, ‹Orada bulunacağım!› dediğin bu tapınağın üzerinde olsun. Kulunun buraya yönelerek ettiği duayı işit."));
trsuperobject.biblemap.put("b11_8_30", new trsuperobject(11,8,30,"Buraya yönelerek dua eden kulunun ve halkın İsrailin yalvarışını işit. Göklerden, oturduğun yerden kulak ver; duyunca bağışla."));
trsuperobject.biblemap.put("b11_8_31", new trsuperobject(11,8,31,"‹‹Biri komşusuna karşı günah işleyip ant içmek zorunda kaldığında, gelip bu tapınakta, senin sunağının önünde ant içerse,"));
trsuperobject.biblemap.put("b11_8_32", new trsuperobject(11,8,32,"göklerden kulak ver ve gereğini yap. Suçlunun cezasını vererek, suçsuzu haklı çıkararak kullarını yargıla."));
trsuperobject.biblemap.put("b11_8_33", new trsuperobject(11,8,33,"‹‹Sana karşı günah işlediği için düşmanlarına yenik düşen halkın İsrail yine sana döner, adını anar, bu tapınakta dua edip yakararak önüne çıkarsa,"));
trsuperobject.biblemap.put("b11_8_34", new trsuperobject(11,8,34,"göklerden kulak ver, halkın İsrailin günahını bağışla. Onları atalarına verdiğin ülkeye yine kavuştur."));
trsuperobject.biblemap.put("b11_8_35", new trsuperobject(11,8,35,"‹‹Halkın sana karşı günah işlediği için gökler kapanıp yağmur yağmazsa, sıkıntıya düşen halkın buraya yönelip dua eder, adını anar ve günahlarından dönerse,"));
trsuperobject.biblemap.put("b11_8_36", new trsuperobject(11,8,36,"göklerden kulak ver; kullarının, halkın İsrailin günahlarını bağışla. Onlara doğru yolda yürümeyi öğret, halkına mülk olarak verdiğin ülkene yağmurlarını gönder."));
trsuperobject.biblemap.put("b11_8_37", new trsuperobject(11,8,37,"‹‹Ülkeyi kıtlık, salgın hastalık, samyeli, küf, tırtıl ya da çekirgeler kavurduğunda, düşmanlar kentlerden birinde halkını kuşattığında, herhangi bir felaket ya da hastalık ortalığı sardığında,"));
trsuperobject.biblemap.put("b11_8_38", new trsuperobject(11,8,38,"halkından bir kişi ya da bütün halkın İsrail başına gelen felaketi ayrımsar, dua edip yakararak ellerini bu tapınağa doğru açarsa,"));
trsuperobject.biblemap.put("b11_8_39", new trsuperobject(11,8,39,"göklerden, oturduğun yerden kulak ver ve bağışla. İnsanların yüreklerini yalnızca sen bilirsin. Onlara yaptıklarına göre davran ki,"));
trsuperobject.biblemap.put("b11_8_40", new trsuperobject(11,8,40,"atalarımıza verdiğin bu ülkede yaşadıkları sürece senden korksunlar."));
trsuperobject.biblemap.put("b11_8_41", new trsuperobject(11,8,41,"‹‹Halkın İsrailden olmayan, ama senin yüce adını,"));
trsuperobject.biblemap.put("b11_8_42", new trsuperobject(11,8,42,"gücünü, kudretini duyup uzak ülkelerden gelen yabancılar bu tapınağa gelip dua ederlerse,"));
trsuperobject.biblemap.put("b11_8_43", new trsuperobject(11,8,43,"göklerden, oturduğun yerden kulak ver, yalvarışlarını yanıtla. Öyle ki, dünyanın bütün ulusları, halkın İsrail gibi, senin adını bilsin, senden korksun ve yaptırdığım bu tapınağın sana ait olduğunufı öğrensin. çağrıldığını.››"));
trsuperobject.biblemap.put("b11_8_44", new trsuperobject(11,8,44,"‹‹Halkın düşmanlarına karşı gösterdiğin yoldan savaşa giderken sana, seçtiğin bu kente ve adına yaptırdığım bu tapınağa yönelip dua ederse,"));
trsuperobject.biblemap.put("b11_8_45", new trsuperobject(11,8,45,"dualarına, yakarışlarına göklerden kulak ver ve onları kurtar."));
trsuperobject.biblemap.put("b11_8_46", new trsuperobject(11,8,46,"‹‹Sana karşı günah işlediklerinde -günah işlemeyen tek kişi yoktur- sen öfkelenip onları yakın ya da uzak bir ülkeye tutsak olarak götürecek düşmanlarının eline teslim edersen,"));
trsuperobject.biblemap.put("b11_8_47", new trsuperobject(11,8,47,"onlar da tutsak oldukları ülkede pişmanlık duyup günahlarından döner, ‹Günah işledik, yoldan sapıp kötülük yaptık› diyerek sana yakarırlarsa,"));
trsuperobject.biblemap.put("b11_8_48", new trsuperobject(11,8,48,"tutsak oldukları ülkede candan ve yürekten sana dönerlerse, atalarına verdiğin ülkelerine, seçtiğin kente ve adına yaptırdığım tapınağına yönelip dua ederlerse,"));
trsuperobject.biblemap.put("b11_8_49", new trsuperobject(11,8,49,"göklerden, oturduğun yerden dualarına, yakarışlarına kulak ver, onları kurtar."));
trsuperobject.biblemap.put("b11_8_50", new trsuperobject(11,8,50,"Sana karşı günah işlemiş olan halkını ve işledikleri bütün suçları bağışla. Düşmanlarının onlara acımasını sağla."));
trsuperobject.biblemap.put("b11_8_51", new trsuperobject(11,8,51,"Çünkü onlar demir eritme ocağından, Mısırdan çıkardığın kendi halkın, kendi mirasındır."));
trsuperobject.biblemap.put("b11_8_52", new trsuperobject(11,8,52,"‹‹Sana her yalvarışlarında onlara kulak ver, bu kulunun ve halkın İsrailin yalvarışlarını dinle."));
trsuperobject.biblemap.put("b11_8_53", new trsuperobject(11,8,53,"Ey Egemen RAB, atalarımızı Mısırdan çıkardığında kulun Musa aracılığıyla dediğin gibi, onları dünyanın bütün halkları arasından kendine miras olarak seçtin.››"));
trsuperobject.biblemap.put("b11_8_54", new trsuperobject(11,8,54,"Süleyman, RABbe duasını ve yalvarışını bitirince, elleri göklere açık, dizleri üzerine çökmüş olduğu RABbin sunağının önünden kalktı."));
trsuperobject.biblemap.put("b11_8_55", new trsuperobject(11,8,55,"Ayakta durup bütün İsrail topluluğunu yüksek sesle şöyle kutsadı:"));
trsuperobject.biblemap.put("b11_8_56", new trsuperobject(11,8,56,"‹‹Sözünü tutup halkı İsraile esenlik veren RABbe övgüler olsun. Kulu Musa aracılığıyla verdiği iyi sözlerin hiçbiri boşa çıkmadı."));
trsuperobject.biblemap.put("b11_8_57", new trsuperobject(11,8,57,"Tanrımız RAB atalarımızla olduğu gibi bizimle de olsun ve bizi hiç bırakmasın, bizden ayrılmasın."));
trsuperobject.biblemap.put("b11_8_58", new trsuperobject(11,8,58,"Bütün yollarını izlememiz, atalarımıza verdiği buyruklara, kurallara, ilkelere uymamız için RAB yüreklerimizi kendine yöneltsin."));
trsuperobject.biblemap.put("b11_8_59", new trsuperobject(11,8,59,"Ya RAB Tanrımız, önünde yalvarırken söylediğim bu sözleri gece gündüz anımsa. Kulunu ve halkın İsraili her durumda koru."));
trsuperobject.biblemap.put("b11_8_60", new trsuperobject(11,8,60,"Sonunda dünyanın bütün ulusları bilsinler ki, tek Tanrı RABdir ve Ondan başka Tanrı yoktur."));
trsuperobject.biblemap.put("b11_8_61", new trsuperobject(11,8,61,"Bugünkü gibi Onun kurallarına göre yaşamak ve buyruklarına uymak için bütün yüreğinizi Tanrımız RABbe adayın.››"));
trsuperobject.biblemap.put("b11_8_62", new trsuperobject(11,8,62,"Kral ve bütün İsrail halkı RABbin önünde kurban kestiler."));
trsuperobject.biblemap.put("b11_8_63", new trsuperobject(11,8,63,"Süleyman, esenlik kurbanı olarak RABbe yirmi iki bin sığır, yüz yirmi bin davar kurban etti. Böylece kral ve bütün İsrail halkı, RABbin Tapınağını adama işini tamamlamış oldu."));
trsuperobject.biblemap.put("b11_8_64", new trsuperobject(11,8,64,"Aynı gün kral, tapınağın önündeki avlunun orta kısmını da kutsadı. Yakmalık sunuları, tahıl sunularını ve esenlik sunularının yağlarını orada sundu. Çünkü RABbin önündeki tunç sunak yakmalık sunuları, tahıl sunularını ve esenlik sunularının yağlarını alamayacak kadar küçüktü."));
trsuperobject.biblemap.put("b11_8_65", new trsuperobject(11,8,65,"Süleyman, Levo-Hamattan Mısır Vadisine kadar her yerden gelen İsraillilerin oluşturduğu büyük toplulukla birlikte Tanrımız RABbin önünde art arda yedişer gün, toplam on dört gün bayram yaptı."));
trsuperobject.biblemap.put("b11_8_66", new trsuperobject(11,8,66,"Kral sekizinci gün halkı evlerine gönderdi. Onlar da kralı kutsayıp RAB'bin, kulu Davut ve halkı İsrail için yapmış olduğu bütün iyiliklerden dolayı sevinç duyarak mutluluk içinde evlerine döndüler."));
trsuperobject.biblemap.put("b11_9_1", new trsuperobject(11,9,1,"Süleyman RABbin Tapınağını, sarayı ve yapmayı istediği bütün işleri bitirince,"));
trsuperobject.biblemap.put("b11_9_3", new trsuperobject(11,9,3,"şöyle dedi: ‹‹Duanı ve yakarışını duydum. Adım sürekli orada bulunsun diye yaptığın bu tapınağı kutsal kıldım. Gözlerim onun üstünde, yüreğim her zaman orada olacaktır."));
trsuperobject.biblemap.put("b11_9_4", new trsuperobject(11,9,4,"Sana gelince, baban Davutun yaptığı gibi, bütün yüreğinle ve doğrulukla yollarımı izler, buyurduğum her şeyi yapar, kurallarıma ve ilkelerime uyarsan,"));
trsuperobject.biblemap.put("b11_9_5", new trsuperobject(11,9,5,"baban Davuta, ‹İsrail tahtından senin soyunun ardı arkası kesilmeyecektir› diye verdiğim sözü tutup krallığını sonsuza dek pekiştireceğim."));
trsuperobject.biblemap.put("b11_9_6", new trsuperobject(11,9,6,"‹‹Ama siz ya da çocuklarınız yollarımdan sapar, buyruklarıma ve kurallarıma uymaz, gidip başka ilahlara kulluk eder, taparsanız,"));
trsuperobject.biblemap.put("b11_9_7", new trsuperobject(11,9,7,"size verdiğim bu ülkeden sizi söküp atacağım, adıma kutsal kıldığım bu tapınağı terk edeceğim; İsrail bütün uluslar arasında aşağılanıp alay konusu olacak."));
trsuperobject.biblemap.put("b11_9_8", new trsuperobject(11,9,8,"Bu gösterişli tapınağın önünden geçenler, hayret ve dehşet içinde, ‹RAB bu ülkeyi ve tapınağı neden bu duruma getirdi?› diye soracaklar."));
trsuperobject.biblemap.put("b11_9_9", new trsuperobject(11,9,9,"Ve, diyecekler ki, ‹İsrail halkı, atalarını Mısırdan çıkaran Tanrıları RABbi terk etti; başka ilahların ardından gitti, onlara tapıp kulluk etti. RAB bu yüzden bütün bu kötülükleri başlarına getirdi.››"));
trsuperobject.biblemap.put("b11_9_10", new trsuperobject(11,9,10,"Süleyman iki yapıyı -RABbin Tapınağıyla kendi sarayını- yirmi yılda bitirdi."));
trsuperobject.biblemap.put("b11_9_11", new trsuperobject(11,9,11,"Bu yapılar için istediği sedir ve çam ağaçlarıyla altını sağlayan Sur Kralı Hirama Celile bölgesinde yirmi kent verdi."));
trsuperobject.biblemap.put("b11_9_12", new trsuperobject(11,9,12,"Hiram gidip Süleymanın kendisine verdiği kentleri görünce onları beğenmedi."));
trsuperobject.biblemap.put("b11_9_13", new trsuperobject(11,9,13,"Süleymana, ‹‹Bunlar mı bana verdiğin kentler, kardeşim!›› dedi. Bu yüzden o bölge bugün de ‹‹Kavul›› diye bilinir."));
trsuperobject.biblemap.put("b11_9_14", new trsuperobject(11,9,14,"Oysa Hiram, Kral Süleymana yüz yirmi talant altın göndermişti."));
trsuperobject.biblemap.put("b11_9_15", new trsuperobject(11,9,15,"Kral Süleyman RABbin Tapınağını, kendi sarayını, Milloyu ve Yeruşalimin surlarını yaptırmak; ayrıca Hasor, Megiddo ve Gezer kentlerini onarıp güçlendirmek amacıyla angaryacıları toplamıştı."));
trsuperobject.biblemap.put("b11_9_16", new trsuperobject(11,9,16,"Mısır Firavunu gidip Gezeri ele geçirmiş ve ateşe vermişti. Orada yaşayan Kenanlıları öldürerek kenti Süleymanla evlenen kızına armağan etmişti."));
trsuperobject.biblemap.put("b11_9_17", new trsuperobject(11,9,17,"Süleyman Gezeri, Aşağı Beythoronu,"));
trsuperobject.biblemap.put("b11_9_18", new trsuperobject(11,9,18,"Baalatı ve kırsal bir bölgede bulunan Tamarla birlikte"));
trsuperobject.biblemap.put("b11_9_19", new trsuperobject(11,9,19,"bütün ambarlı kentleri, ayrıca savaş arabalarıyla atların bulunduğu kentleri de onarıp güçlendirdi. Böylece Yeruşalimde, Lübnanda, yönetimi altındaki bütün topraklarda her istediğini yaptırmış oldu."));
trsuperobject.biblemap.put("b11_9_20", new trsuperobject(11,9,20,"İsrail halkından olmayan Amorlular, Hititler, Perizliler, Hivliler ve Yevuslulardan artakalanlara gelince,"));
trsuperobject.biblemap.put("b11_9_21", new trsuperobject(11,9,21,"Süleyman İsrail halkının tamamen yok edemediği bu insanların soyundan gelip ülkede kalanları angaryaya koştu. Bu durum bugün de sürüyor."));
trsuperobject.biblemap.put("b11_9_22", new trsuperobject(11,9,22,"Ancak Süleyman İsrail halkından hiç kimseye kölelik yaptırmadı. Onlar savaşçı, görevli, komutan, subay, savaş arabalarıyla atlıların komutanı olarak görev yaptılar."));
trsuperobject.biblemap.put("b11_9_23", new trsuperobject(11,9,23,"Süleymanın yapılan işlerin başında duran ve çalışanları denetleyen beş yüz elli görevlisi vardı."));
trsuperobject.biblemap.put("b11_9_24", new trsuperobject(11,9,24,"Firavunun kızı, Davut Kentinden Süleymanın kendisi için yaptırdığı saraya taşındıktan sonra, Süleyman Milloyu yaptırdı."));
trsuperobject.biblemap.put("b11_9_25", new trsuperobject(11,9,25,"Süleyman RAB için yaptırdığı sunakta yılda üç kez yakmalık sunular ve esenlik sunuları sunardı. Ayrıca RABbin önündeki sunağın üstünde buhur da yakardı. Böylece Süleyman tapınağın yapımını tamamlamış oldu."));
trsuperobject.biblemap.put("b11_9_26", new trsuperobject(11,9,26,"Kral Süleyman Edomluların ülkesinde, Kızıldeniz kıyısında Eylat yakınlarındaki Esyon-Geverde gemiler yaptırdı."));
trsuperobject.biblemap.put("b11_9_27", new trsuperobject(11,9,27,"Hiram denizi bilen gemicilerini Süleymanın adamlarıyla birlikte Ofire gönderdi."));
trsuperobject.biblemap.put("b11_9_28", new trsuperobject(11,9,28,"Ofir'e giden bu gemiciler, Kral Süleyman'a 420 talant altın getirdiler."));
trsuperobject.biblemap.put("b11_10_1", new trsuperobject(11,10,1,"Saba Kraliçesi, RABbin adından ötürü Süleymanın artan ününü duyunca, onu çetin sorularla sınamaya geldi."));
trsuperobject.biblemap.put("b11_10_2", new trsuperobject(11,10,2,"Çeşitli baharat, çok miktarda altın ve değerli taşlarla yüklü büyük bir kervan eşliğinde Yeruşalime gelen kraliçe, aklından geçen her şeyi Süleymanla konuştu."));
trsuperobject.biblemap.put("b11_10_3", new trsuperobject(11,10,3,"Süleyman onun bütün sorularına karşılık verdi. Kralın ona yanıt bulmakta güçlük çektiği hiçbir konu olmadı."));
trsuperobject.biblemap.put("b11_10_4-5", new trsuperobject(11,10,4-5,"Süleymanın bilgeliğini, yaptırdığı sarayı, sofrasının zenginliğini, görevlilerinin oturup kalkışını, hizmetkârlarının özel giysileriyle yaptığı hizmeti, sakilerini ve RABbin Tapınağında sunduğu yakmalık sunuları gören Saba Kraliçesi hayranlık içinde kaldı."));
trsuperobject.biblemap.put("b11_10_6", new trsuperobject(11,10,6,"Krala, ‹‹Ülkemdeyken yaptıklarınla ve bilgeliğinle ilgili duyduklarım doğruymuş›› dedi,"));
trsuperobject.biblemap.put("b11_10_7", new trsuperobject(11,10,7,"‹‹Ama gelip kendi gözlerimle görünceye dek inanmamıştım. Bunların yarısı bile bana anlatılmadı. Bilgeliğin de, zenginliğin de duyduklarımdan kat kat fazla."));
trsuperobject.biblemap.put("b11_10_8", new trsuperobject(11,10,8,"Ne mutlu adamlarına! Ne mutlu sana hizmet eden görevlilere! Çünkü sürekli bilgeliğine tanık oluyorlar."));
trsuperobject.biblemap.put("b11_10_9", new trsuperobject(11,10,9,"Senden hoşnut kalan, seni İsrail tahtına oturtan Tanrın RABbe övgüler olsun! RAB İsraile sonsuz sevgi duyduğundan, adaleti ve doğruluğu sağlaman için seni kral yaptı.››"));
trsuperobject.biblemap.put("b11_10_10", new trsuperobject(11,10,10,"Saba Kraliçesi krala 120 talant altın, çok büyük miktarda baharat ve değerli taşlar armağan etti. Krala o kadar baharat armağan etti ki, bir daha bu kadar çok baharat görülmedi."));
trsuperobject.biblemap.put("b11_10_11", new trsuperobject(11,10,11,"Bu arada Hiramın gemileri Ofirden altın ve büyük miktarda almug kerestesiyle değerli taşlar getirdiler."));
trsuperobject.biblemap.put("b11_10_12", new trsuperobject(11,10,12,"Kral, RABbin Tapınağıyla sarayın tırabzanlarını, çalgıcıların lirleriyle çenklerini bu almug kerestesinden yaptırdı. Bugüne dek o kadar almug ağacı ne gelmiş, ne de görülmüştür. ardıç ağacı olduğu sanılıyor."));
trsuperobject.biblemap.put("b11_10_13", new trsuperobject(11,10,13,"Kral Süleyman Saba Kraliçesinin her isteğini, her dileğini yerine getirdi. Ayrıca ona gönülden kopan birçok armağan verdi. Bundan sonra kraliçe adamlarıyla birlikte oradan ayrılıp kendi ülkesine döndü."));
trsuperobject.biblemap.put("b11_10_14", new trsuperobject(11,10,14,"Süleymana bir yılda gelen altının miktarı 666 talantı buluyordu."));
trsuperobject.biblemap.put("b11_10_15", new trsuperobject(11,10,15,"Alım satımla uğraşanlarla tüccarların kazançlarından ve Arabistanın bütün krallarıyla İsrail valilerinden gelenler bunun dışındaydı."));
trsuperobject.biblemap.put("b11_10_16", new trsuperobject(11,10,16,"Kral Süleyman her biri altı yüz şekel ağırlığında dövme altından iki yüz büyük kalkan yaptırdı."));
trsuperobject.biblemap.put("b11_10_17", new trsuperobject(11,10,17,"Ayrıca her biri üç minafö ağırlığında dövme altından üç yüz küçük kalkan yaptırdı. Kral bu kalkanları Lübnan Ormanı adındaki saraya koydu."));
trsuperobject.biblemap.put("b11_10_18", new trsuperobject(11,10,18,"Kral fildişinden büyük bir taht yaptırıp saf altınla kaplattı."));
trsuperobject.biblemap.put("b11_10_19", new trsuperobject(11,10,19,"Tahtın altı basamağı, arka kısmında yuvarlak bir başlığı vardı. Oturulan yerin iki yanında kollar, her kolun yanında birer aslan heykeli bulunuyordu."));
trsuperobject.biblemap.put("b11_10_20", new trsuperobject(11,10,20,"Altı basamağın iki yanında on iki aslan heykeli vardı. Hiçbir krallıkta böylesi yapılmamıştı."));
trsuperobject.biblemap.put("b11_10_21", new trsuperobject(11,10,21,"Kral Süleymanın kadehleriyle Lübnan Ormanı adındaki sarayın bütün eşyaları saf altından yapılmış, hiç gümüş kullanılmamıştı. Çünkü Süleymanın döneminde gümüşün değeri yoktu."));
trsuperobject.biblemap.put("b11_10_22", new trsuperobject(11,10,22,"Hiramın gemilerinin yanısıra, kralın da denizde ticaret gemileri vardı. Bu gemiler üç yılda bir altın, gümüş, fildişi ve türlü maymunlarla yüklü olarak dönerlerdi. tavuskuşlarıyla››."));
trsuperobject.biblemap.put("b11_10_23", new trsuperobject(11,10,23,"Kral Süleyman dünyanın bütün krallarından daha zengin, daha bilgeydi."));
trsuperobject.biblemap.put("b11_10_24", new trsuperobject(11,10,24,"Tanrının Süleymana verdiği bilgeliği dinlemek için bütün dünya onu görmek isterdi."));
trsuperobject.biblemap.put("b11_10_25", new trsuperobject(11,10,25,"Onu görmeye gelenler her yıl armağan olarak altın ve gümüş eşya, giysi, silah, baharat, at, katır getirirlerdi."));
trsuperobject.biblemap.put("b11_10_26", new trsuperobject(11,10,26,"Süleyman savaş arabalarıyla atlarını topladı. Bin dört yüz savaş arabası, on iki bin atı vardı. Bunların bir kısmını savaş arabaları için ayrılan kentlere, bir kısmını da kendi yanına, Yeruşalime yerleştirdi."));
trsuperobject.biblemap.put("b11_10_27", new trsuperobject(11,10,27,"Krallığı döneminde Yeruşalimde gümüş taş değerine düştü. Sedir ağaçları Şefeladaki yabanıl incir ağaçları kadar bollaştı."));
trsuperobject.biblemap.put("b11_10_28", new trsuperobject(11,10,28,"Süleymanın atları Mısır ve Keveden getirilirdi. Kralın tüccarları atları Keveden satın alırdı."));
trsuperobject.biblemap.put("b11_10_29", new trsuperobject(11,10,29,"Mısır'dan bir savaş arabası altı yüz, bir at yüz elli şekel gümüşe getirilirdi. Bunları bütün Hitit ve Aram krallarına satarlardı. bölgesi››."));
trsuperobject.biblemap.put("b11_11_1", new trsuperobject(11,11,1,"Kral Süleyman firavunun kızının yanısıra Moavlı, Ammonlu, Edomlu, Saydalı ve Hititli birçok yabancı kadın sevdi."));
trsuperobject.biblemap.put("b11_11_2", new trsuperobject(11,11,2,"Bu kadınlar RABbin İsrail halkına, ‹‹Ne siz onların arasına girin, ne de onlar sizin aranıza girsinler; çünkü onlar kesinlikle sizi kendi ilahlarının ardınca yürümek üzere saptıracaklardır›› dediği uluslardandı. Buna karşın, Süleyman onlara sevgiyle bağlandı."));
trsuperobject.biblemap.put("b11_11_3", new trsuperobject(11,11,3,"Süleymanın kral kızlarından yedi yüz karısı ve üç yüz cariyesi vardı. Karıları onu yolundan saptırdılar."));
trsuperobject.biblemap.put("b11_11_4", new trsuperobject(11,11,4,"Süleyman yaşlandıkça, karıları onu başka ilahların ardınca yürümek üzere saptırdılar. Böylece Süleyman bütün yüreğini Tanrısı RABbe adayan babası Davut gibi yaşamadı."));
trsuperobject.biblemap.put("b11_11_5", new trsuperobject(11,11,5,"Saydalıların tanrıçası Aştorete ve Ammonluların iğrenç ilahı Moleke taptı."));
trsuperobject.biblemap.put("b11_11_6", new trsuperobject(11,11,6,"Böylece RABbin gözünde kötü olanı yaptı, RABbin yolunda yürüyen babası Davut gibi tam anlamıyla RABbi izlemedi."));
trsuperobject.biblemap.put("b11_11_7", new trsuperobject(11,11,7,"Yeruşalimin doğusundaki tepede Moavlıların iğrenç ilahı Kemoşa ve Ammonluların iğrenç ilahı Moleke tapmak için bir yer yaptırdı."));
trsuperobject.biblemap.put("b11_11_8", new trsuperobject(11,11,8,"İlahlarına buhur yakıp kurban kesen bütün yabancı karıları için de aynı şeyleri yaptı."));
trsuperobject.biblemap.put("b11_11_9-10", new trsuperobject(11,11,9-10,"İsrailin Tanrısı RAB, kendisine iki kez görünüp, ‹‹Başka ilahlara tapma!›› demesine karşın, Süleyman RABbin yolundan saptı ve Onun buyruğuna uymadı. Bu yüzden RAB Süleymana öfkelenerek,"));
trsuperobject.biblemap.put("b11_11_11", new trsuperobject(11,11,11,"‹‹Seninle yaptığım antlaşmaya ve kurallarıma bilerek uymadığın için krallığı elinden alacağım ve görevlilerinden birine vereceğim›› dedi,"));
trsuperobject.biblemap.put("b11_11_12", new trsuperobject(11,11,12,"‹‹Ancak baban Davutun hatırı için, bunu senin yaşadığın sürede değil, oğlun kral olduktan sonra yapacağım."));
trsuperobject.biblemap.put("b11_11_13", new trsuperobject(11,11,13,"Ama oğlunun elinden bütün krallığı almayacağım. Kulum Davutun ve kendi seçtiğim Yeruşalimin hatırı için oğluna bir oymak bırakacağım.››"));
trsuperobject.biblemap.put("b11_11_14", new trsuperobject(11,11,14,"RAB kral soyundan gelen bir düşmanı, Edomlu Hadatı Süleymana karşı ayaklandırdı."));
trsuperobject.biblemap.put("b11_11_15", new trsuperobject(11,11,15,"Daha önce, Davut Edomlularla savaşırken, ölüleri gömmeye giden ordu komutanı Yoav Edomdaki bütün erkekleri öldürmüştü."));
trsuperobject.biblemap.put("b11_11_16", new trsuperobject(11,11,16,"Yoav ile İsrailliler Edomdaki erkeklerin hepsini yok edinceye dek, altı ay orada kalmışlardı."));
trsuperobject.biblemap.put("b11_11_17", new trsuperobject(11,11,17,"Ancak genç Hadat, babasının görevlilerinden bazı Edomlularla birlikte Mısıra kaçmıştı."));
trsuperobject.biblemap.put("b11_11_18", new trsuperobject(11,11,18,"Sonra Midyandan ayrılıp Parana gitmişler, oradan bazı Paranlıları da yanlarına alıp Mısıra, firavunun yanına gelmişlerdi. Firavun Hadata barınak, yiyecek ve toprak sağlamıştı."));
trsuperobject.biblemap.put("b11_11_19", new trsuperobject(11,11,19,"Hadat firavunun dostluğunu kazandı. Bunun üzerine firavun, kendi karısı Kraliçe Tahpenesin kızkardeşini Hadatla evlendirdi."));
trsuperobject.biblemap.put("b11_11_20", new trsuperobject(11,11,20,"Tahpenesin kızkardeşi Hadata Genuvat adlı bir oğul doğurdu. Tahpenes çocuğu sarayda firavunun çocuklarıyla birlikte büyüttüfş."));
trsuperobject.biblemap.put("b11_11_21", new trsuperobject(11,11,21,"Hadat Davutla ordu komutanı Yoavın ölüm haberini Mısırda duydu. Firavuna, ‹‹İzin ver, kendi ülkeme döneyim›› dedi."));
trsuperobject.biblemap.put("b11_11_22", new trsuperobject(11,11,22,"Firavun, ‹‹Bir eksiğin mi var, neden ülkene dönmek istiyorsun?›› diye sordu. Hadat, ‹‹Hayır, ama lütfen gitmeme izin ver›› diye yanıtladı."));
trsuperobject.biblemap.put("b11_11_23", new trsuperobject(11,11,23,"Tanrı, efendisi Sova Kralı Hadadezerden kaçan bir düşmanı, Elyada oğlu Rezonu Süleymana karşı ayaklandırdı."));
trsuperobject.biblemap.put("b11_11_24", new trsuperobject(11,11,24,"Davut Sovalılara saldırdığında, Rezon çevresine haydutları toplayıp onlara önderlik etmişti. Birlikte Şama gitmişler, orada kalıp yönetimi ele geçirmişlerdi."));
trsuperobject.biblemap.put("b11_11_25", new trsuperobject(11,11,25,"Hadatın yaptığı kötülüğün yanısıra, Rezon Süleyman yaşadığı sürece İsrailin düşmanı oldu; Aramda krallık yaparak İsrailden nefret etti."));
trsuperobject.biblemap.put("b11_11_26", new trsuperobject(11,11,26,"Efrayim oymağından Nevat oğlu Seredalı Yarovam Kral Süleymana karşı ayaklandı. Yarovam Süleymanın görevlilerindendi. Annesi Serua adlı dul bir kadındı."));
trsuperobject.biblemap.put("b11_11_27", new trsuperobject(11,11,27,"Yarovamın krala karşı ayaklanmasının öyküsü şöyleydi: Süleyman Milloyu yaptırıp babası Davutun Kentindeki surların gediğini kapatmıştı."));
trsuperobject.biblemap.put("b11_11_28", new trsuperobject(11,11,28,"Yarovam çok yetenekli biriydi. Süleyman bu genç adamın ne denli çalışkan olduğunu görünce, Yusuf soyunun bütün ağır işlerinin sorumluluğunu ona verdi."));
trsuperobject.biblemap.put("b11_11_29", new trsuperobject(11,11,29,"Bir gün Yarovam Yeruşalimin dışına çıktı. Yolda Şilolu Peygamber Ahiya ile karşılaştı. Ahiya yeni giysisini giymişti. İkisi kent dışında yalnızdılar."));
trsuperobject.biblemap.put("b11_11_30", new trsuperobject(11,11,30,"Ahiya üzerindeki giysiyi yırtıp on iki parçaya ayırdı"));
trsuperobject.biblemap.put("b11_11_31", new trsuperobject(11,11,31,"ve Yarovama, ‹‹On parçayı kendine al›› dedi, ‹‹Çünkü İsrailin Tanrısı RAB diyor ki, ‹Ben, Süleymanın elinden krallığı alıp on oymağı sana vereceğim."));
trsuperobject.biblemap.put("b11_11_32", new trsuperobject(11,11,32,"Ama kulum Davutun ve İsrail oymaklarının yaşadığı kentler arasından seçtiğim Yeruşalim Kentinin hatırı için bir oymağı onda bırakacağım."));
trsuperobject.biblemap.put("b11_11_33", new trsuperobject(11,11,33,"Çünkü Süleyman bana sırt çevirip Saydalıların tanrıçası Aştorete, Moavlıların ilahı Kemoşa ve Ammonluların ilahı Moleke taptı. Kurallarıma, ilkelerime uyup gözümde doğru olanı yapan babası Davut gibi yollarımı izlemedi."));
trsuperobject.biblemap.put("b11_11_34", new trsuperobject(11,11,34,"Ama buyruklarıma, kurallarıma bağlı kalan, seçtiğim kulum Davutun hatırı için Süleymanın elinden bütün krallığı almayacağım. Yaşamı boyunca onu önder yapacağım."));
trsuperobject.biblemap.put("b11_11_35", new trsuperobject(11,11,35,"Ancak krallığı oğlunun elinden alıp on oymağı sana vereceğim."));
trsuperobject.biblemap.put("b11_11_36", new trsuperobject(11,11,36,"Adımı yerleştirmek için kendime seçtiğim Yeruşalim Kentinde kulum Davut için önümde sönmeyen bir ışık olmak üzere Süleymanın oğluna bir oymak vereceğim."));
trsuperobject.biblemap.put("b11_11_37", new trsuperobject(11,11,37,"Sana gelince, seni İsrail Kralı yapacağım. İsraili dilediğin gibi yöneteceksin."));
trsuperobject.biblemap.put("b11_11_38", new trsuperobject(11,11,38,"Kulum Davut gibi isteklerimi yerine getirir, kurallarıma ve buyruklarıma uyar, gözümde doğru olanı yapar, yollarımı izlersen, seninle birlikte olacağım. Davuta yaptığım gibi senin için de güçlü bir hanedan kurup İsraili sana vereceğim."));
trsuperobject.biblemap.put("b11_11_39", new trsuperobject(11,11,39,"Süleymanın günahından ötürü Davut soyunun gururunu kıracağım, ancak sonsuza dek değil.››"));
trsuperobject.biblemap.put("b11_11_40", new trsuperobject(11,11,40,"Süleyman Yarovamı öldürmeye çalıştı. Ama Yarovam Mısıra kaçıp Mısır Kralı Şişaka sığındı. Süleymanın ölümüne kadar orada kaldı."));
trsuperobject.biblemap.put("b11_11_41", new trsuperobject(11,11,41,"Süleymanın krallığı dönemindeki öteki olaylar, bütün yaptıkları ve bilgeliği Süleymanın tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_11_42", new trsuperobject(11,11,42,"Süleyman kırk yıl süreyle bütün İsraili Yeruşalimden yönetti."));
trsuperobject.biblemap.put("b11_11_43", new trsuperobject(11,11,43,"Süleyman ölüp atalarına kavuşunca babası Davut'un Kenti'nde gömüldü. Yerine oğlu Rehavam kral oldu."));
trsuperobject.biblemap.put("b11_12_1", new trsuperobject(11,12,1,"Rehavam Şekeme gitti. Çünkü bütün İsrailliler kendisini kral ilan etmek için orada toplanmışlardı."));
trsuperobject.biblemap.put("b11_12_2", new trsuperobject(11,12,2,"Kral Süleymandan kaçıp Mısıra yerleşen Nevat oğlu Yarovam bunu duyunca Mısırda kalmaya karar verdi."));
trsuperobject.biblemap.put("b11_12_3", new trsuperobject(11,12,3,"İsrail topluluğu Yarovamı çağırttı. Birlikte gidip Rehavama şöyle dediler:"));
trsuperobject.biblemap.put("b11_12_4", new trsuperobject(11,12,4,"‹‹Baban üzerimize ağır bir boyunduruk koydu. Ama babanın üzerimize yüklediği ağır yükü ve boyunduruğu hafifletirsen sana kul köle oluruz.››"));
trsuperobject.biblemap.put("b11_12_5", new trsuperobject(11,12,5,"Rehavam, ‹‹Şimdi gidin, üç gün sonra yine gelin›› yanıtını verince halk yanından ayrıldı."));
trsuperobject.biblemap.put("b11_12_6", new trsuperobject(11,12,6,"Kral Rehavam, babası Süleymana sağlığında danışmanlık yapan ileri gelenlere, ‹‹Bu halka nasıl yanıt vermemi öğütlersiniz?›› diye sordu."));
trsuperobject.biblemap.put("b11_12_7", new trsuperobject(11,12,7,"İleri gelenler, ‹‹Bugün bu halka hizmet eder, olumlu yanıt verirsen, sana her zaman kul köle olurlar›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b11_12_8", new trsuperobject(11,12,8,"Ne var ki, Rehavam ileri gelenlerin öğüdünü reddederek birlikte büyüdüğü genç görevlilerine danıştı:"));
trsuperobject.biblemap.put("b11_12_9", new trsuperobject(11,12,9,"‹‹Siz ne yapmamı öğütlersiniz? ‹Babanın üzerimize koyduğu boyunduruğu hafiflet› diyen bu halka nasıl bir yanıt verelim?››"));
trsuperobject.biblemap.put("b11_12_10", new trsuperobject(11,12,10,"Birlikte büyüdüğü gençler ona şu karşılığı verdiler: ‹‹Sana ‹Babanın üzerimize koyduğu boyunduruğu hafiflet› diyen halka de ki, ‹Benim küçük parmağım babamın belinden daha kalındır."));
trsuperobject.biblemap.put("b11_12_11", new trsuperobject(11,12,11,"Babam size ağır bir boyunduruk yüklediyse, ben boyunduruğunuzu daha da ağırlaştıracağım. Babam sizi kırbaçla yola getirdiyse, ben sizi akreplerle yola getireceğim.››"));
trsuperobject.biblemap.put("b11_12_12", new trsuperobject(11,12,12,"Yarovamla bütün halk, kralın, ‹‹Üç gün sonra yine gelin›› sözü üzerine, üçüncü gün Rehavamın yanına geldiler."));
trsuperobject.biblemap.put("b11_12_13-14", new trsuperobject(11,12,13-14,"İleri gelenlerin öğüdünü reddeden Kral Rehavam, gençlerin öğüdüne uyarak halka sert bir yanıt verdi: ‹‹Babamın size yüklediği boyunduruğu ben daha da ağırlaştıracağım. Babam sizi kırbaçla yola getirdiyse, ben sizi akreplerle yola getireceğim.››"));
trsuperobject.biblemap.put("b11_12_15", new trsuperobject(11,12,15,"Kral halkı dinlemedi. Çünkü Şilolu Ahiya aracılığıyla Nevat oğlu Yarovama verdiği sözü yerine getirmek için RAB bu olayı düzenlemişti."));
trsuperobject.biblemap.put("b11_12_16", new trsuperobject(11,12,16,"Kralın kendilerini dinlemediğini görünce, bütün İsrailliler,  ‹‹İşay oğlu, Davutla ne ilgimiz,Ne de payımız var!›› diye bağırdılar,‹‹Ey İsrail halkı, haydi evimize dönelim!Davutun soyu başının çaresine baksın.››"));
trsuperobject.biblemap.put("b11_12_17", new trsuperobject(11,12,17,"Rehavam da yalnızca Yahuda kentlerinde yaşayan İsraillilere krallık yapmaya başladı."));
trsuperobject.biblemap.put("b11_12_18", new trsuperobject(11,12,18,"İsrailliler Kral Rehavamın gönderdiği angaryacıbaşı Adoramı taşa tutup öldürdüler. Bunun üzerine Kral Rehavam savaş arabasına atlayıp Yeruşalime kaçtı."));
trsuperobject.biblemap.put("b11_12_19", new trsuperobject(11,12,19,"İsrail halkı, Davut soyundan gelenlere hep başkaldırdı."));
trsuperobject.biblemap.put("b11_12_20", new trsuperobject(11,12,20,"Yarovamın Mısırdan döndüğünü duyunca, bütün İsrailliler haber gönderip kendisini toplantıya çağırdılar ve onu İsrail Kralı ilan ettiler. Yahuda oymağından başka hiç kimse Davut soyunu izlemedi."));
trsuperobject.biblemap.put("b11_12_21", new trsuperobject(11,12,21,"Süleyman oğlu Rehavam Yeruşalime varınca, İsrail oymaklarıyla savaşıp onları yeniden egemenliği altına almak amacıyla bütün Yahuda ve Benyamin oymaklarından yüz seksen bin seçkin savaşçı topladı."));
trsuperobject.biblemap.put("b11_12_22", new trsuperobject(11,12,22,"Bu arada Tanrı adamı Şemayaya Tanrı şöyle seslendi:"));
trsuperobject.biblemap.put("b11_12_23", new trsuperobject(11,12,23,"‹‹Süleyman oğlu Yahuda Kralı Rehavama, bütün Yahudalılara, Benyaminlilere ve orada yaşayan öteki insanlara şunu söyle:"));
trsuperobject.biblemap.put("b11_12_24", new trsuperobject(11,12,24,"‹RAB diyor ki, İsrailli kardeşlerinize saldırmayın, onlarla savaşmayın. Herkes evine dönsün! Çünkü bu olayı ben düzenledim.›› RABbin bu sözlerini duyan halk Onun buyruğuna uyup evine döndü."));
trsuperobject.biblemap.put("b11_12_25", new trsuperobject(11,12,25,"Yarovam Efrayimin dağlık bölgesindeki Şekem Kentini onarıp orada yaşamaya başladı. Daha sonra oradan ayrılıp Penuel Kentini onardı."));
trsuperobject.biblemap.put("b11_12_26", new trsuperobject(11,12,26,"Yarovam, ‹‹Şimdi krallık yine Davut soyunun eline geçebilir›› diye düşündü,"));
trsuperobject.biblemap.put("b11_12_27", new trsuperobject(11,12,27,"‹‹Eğer bu halk Yeruşalime gidip RABbin Tapınağında kurbanlar sunarsa, yürekleri efendileri, Yahuda Kralı Rehavama döner. Beni öldürüp yeniden Rehavama bağlanırlar.››"));
trsuperobject.biblemap.put("b11_12_28", new trsuperobject(11,12,28,"Kral, danışmanlarına danıştıktan sonra, iki altın buzağı yaptırıp halkına, ‹‹Tapınmak için artık Yeruşalime gitmenize gerek yok›› dedi, ‹‹Ey İsrail halkı, işte sizi Mısırdan çıkaran ilahlarınız!››"));
trsuperobject.biblemap.put("b11_12_29", new trsuperobject(11,12,29,"Altın buzağılardan birini Beytel, ötekini Dan Kentine yerleştirdi."));
trsuperobject.biblemap.put("b11_12_30", new trsuperobject(11,12,30,"Bu günahtı. Böylece halk buzağıya tapmak için Dana kadar gitmeye başladı."));
trsuperobject.biblemap.put("b11_12_31", new trsuperobject(11,12,31,"Yarovam ayrıca tapınma yerlerinde tapınaklar yaptırdı. Levililerin dışında her türlü insanlardan kâhinler atadı."));
trsuperobject.biblemap.put("b11_12_32", new trsuperobject(11,12,32,"Yarovam sekizinci ayın on beşinci günü Yahudadaki bayrama benzer bir bayram başlattı. Dandaki sunakta ve Beytelde yaptırdığı altın buzağılara kurbanlar sundu; orada kurmuş olduğu tapınma yerlerine kâhinler yerleştirdi."));
trsuperobject.biblemap.put("b11_12_33", new trsuperobject(11,12,33,"Kendi kendine uydurduğu sekizinci ayın on beşinci günü, Beytel'de yaptırdığı sunağa gitti, kurban sunup buhur yaktı. Ve o günü İsrail halkı için bayram ilan etti."));
trsuperobject.biblemap.put("b11_13_1", new trsuperobject(11,13,1,"Yarovam buhur yakmak için sunağın yanında dururken, bir Tanrı adamı RABbin buyruğu üzerine Yahudadan Beytele geldi."));
trsuperobject.biblemap.put("b11_13_3", new trsuperobject(11,13,3,"Aynı gün Tanrı adamı bir belirti göstererek konuşmasını şöyle sürdürdü: ‹‹RABbin bana açıkladığı belirti şudur: Bu sunak parçalanacak, üstündeki küller çevreye savrulacak.››"));
trsuperobject.biblemap.put("b11_13_4", new trsuperobject(11,13,4,"Kral Yarovam, Tanrı adamının Beytelde sunağa karşı söylediklerini duyunca, elini ona doğru uzatarak, ‹‹Yakalayın onu!›› diye buyruk verdi. Ancak Tanrı adamına uzattığı eli felç oldu ve düzelmedi."));
trsuperobject.biblemap.put("b11_13_5", new trsuperobject(11,13,5,"Tanrı adamının RABbin buyruğuyla gösterdiği belirti uyarınca, sunak parçalandı, üstündeki küller çevreye savruldu."));
trsuperobject.biblemap.put("b11_13_6", new trsuperobject(11,13,6,"O zaman Kral Yarovam, Tanrı adamına, ‹‹Lütfen benim için dua et, Tanrın RABbe yalvar ki, elim eski halini alsın›› dedi. Tanrı adamı RABbe yalvarınca kralın eli iyileşip eski halini aldı."));
trsuperobject.biblemap.put("b11_13_7", new trsuperobject(11,13,7,"Kral, Tanrı adamına, ‹‹Benimle eve kadar gel de bir şeyler ye›› dedi, ‹‹Sana bir armağan vereceğim.››"));
trsuperobject.biblemap.put("b11_13_8", new trsuperobject(11,13,8,"Tanrı adamı, ‹‹Varlığının yarısını bile versen, seninle gelmem›› diye karşılık verdi, ‹‹Burada ne yer, ne de içerim."));
trsuperobject.biblemap.put("b11_13_9", new trsuperobject(11,13,9,"Çünkü RAB bana, ‹Orada hiçbir şey yiyip içme ve gittiğin yoldan dönme› diye buyruk verdi.››"));
trsuperobject.biblemap.put("b11_13_10", new trsuperobject(11,13,10,"Böylece Tanrı adamı, Beytele gelmiş olduğu yoldan değil, başka bir yoldan gitti."));
trsuperobject.biblemap.put("b11_13_11", new trsuperobject(11,13,11,"O sıralarda Beytelde yaşayan yaşlı bir peygamber vardı. Çocukları gelip o gün Tanrı adamının Beytelde yaptıklarını ve krala söylediklerini babalarına anlattılar."));
trsuperobject.biblemap.put("b11_13_12", new trsuperobject(11,13,12,"Yaşlı baba, ‹‹Tanrı adamı hangi yoldan gitti?›› diye sordu. Çocuklar Yahudadan gelen Tanrı adamının hangi yoldan gittiğini ona gösterdiler."));
trsuperobject.biblemap.put("b11_13_13", new trsuperobject(11,13,13,"Bunun üzerine yaşlı baba, ‹‹Eşeğimi hazırlayın›› dedi. Çocuklar eşeğe palan vurunca, binip"));
trsuperobject.biblemap.put("b11_13_14", new trsuperobject(11,13,14,"Tanrı adamının ardına düştü. Onun bir yabanıl fıstık ağacının altında oturduğunu görünce, ‹‹Yahudadan gelen Tanrı adamı sen misin?›› diye sordu. Adam, ‹‹Evet, benim›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_13_15", new trsuperobject(11,13,15,"Yaşlı peygamber, ‹‹Gel benimle eve gidelim, bir şeyler ye›› dedi."));
trsuperobject.biblemap.put("b11_13_16", new trsuperobject(11,13,16,"Tanrı adamı şöyle karşılık verdi: ‹‹Yolumdan dönüp seninle gelemem. Burada ne yer, ne de içerim."));
trsuperobject.biblemap.put("b11_13_17", new trsuperobject(11,13,17,"Çünkü RAB bana, ‹Orada hiçbir şey yiyip içme ve gittiğin yoldan dönme› diye buyruk verdi.››"));
trsuperobject.biblemap.put("b11_13_18", new trsuperobject(11,13,18,"Bunun üzerine yaşlı peygamber, ‹‹Ben de senin gibi peygamberim›› dedi, ‹‹RABbin buyruğu üzerine bir melek bana, ‹Onu evine götür ve yiyip içmesini sağla› dedi.›› Ne var ki yalan söyleyerek Tanrı adamını kandırdı."));
trsuperobject.biblemap.put("b11_13_19", new trsuperobject(11,13,19,"Böylece Tanrı adamı onunla birlikte geri döndü ve evinde yiyip içmeye başladı."));
trsuperobject.biblemap.put("b11_13_20", new trsuperobject(11,13,20,"Sofrada otururlarken, RAB, Tanrı adamını yolundan döndüren peygambere seslendi."));
trsuperobject.biblemap.put("b11_13_21", new trsuperobject(11,13,21,"O da Yahudadan gelen Tanrı adamına şöyle dedi: ‹‹RAB diyor ki, ‹Madem RABbin sözünü dinlemedin, Tanrın RABbin sana verdiği buyruğa uymayıp"));
trsuperobject.biblemap.put("b11_13_22", new trsuperobject(11,13,22,"yolundan döndün; sana yiyip içme dediği yerde yiyip içtin, cesedin atalarının mezarlığına gömülmeyecek.››"));
trsuperobject.biblemap.put("b11_13_23", new trsuperobject(11,13,23,"Tanrı adamı yiyip içtikten sonra yaşlı peygamber onun için eşeği hazırladı."));
trsuperobject.biblemap.put("b11_13_24", new trsuperobject(11,13,24,"Tanrı adamı giderken yolda bir aslanla karşılaştı. Aslan onu oracıkta öldürdü. Eşekle aslan yere serilen cesedin yanında duruyordu."));
trsuperobject.biblemap.put("b11_13_25", new trsuperobject(11,13,25,"Yoldan geçenler yerde yatan cesetle yanında duran aslanı gördüler. Gidip yaşlı peygamberin yaşadığı kentte gördüklerini anlattılar."));
trsuperobject.biblemap.put("b11_13_26", new trsuperobject(11,13,26,"Tanrı adamını yolundan döndüren yaşlı peygamber olanları duyunca, şöyle dedi: ‹‹RABbin sözünü dinlemeyen Tanrı adamı budur. Bu yüzden RAB, sözü uyarınca, onun üzerine bir aslan gönderdi. Aslan onu parçalayıp öldürdü.››"));
trsuperobject.biblemap.put("b11_13_27-28", new trsuperobject(11,13,27-28,"Peygamber, çocuklarına, ‹‹Eşeği hazırlayın!›› dedi. Çocukların hazırladığı eşeğe binip gitti. Eşekle aslanı yerde yatan Tanrı adamının cesedi başında buldu. Ancak aslan cesedi yemediği gibi eşeğe de dokunmamıştı."));
trsuperobject.biblemap.put("b11_13_29", new trsuperobject(11,13,29,"Yaşlı peygamber Tanrı adamının cesedini eşeğin sırtına attı ve ona ağıt yakıp gömmek için kendi kentine götürdü."));
trsuperobject.biblemap.put("b11_13_30", new trsuperobject(11,13,30,"Cesedi kendi mezarına gömdü. ‹‹Ah kardeşim!›› diyerek ardından ağıt yaktılar."));
trsuperobject.biblemap.put("b11_13_31", new trsuperobject(11,13,31,"Tanrı adamını gömdükten sonra, yaşlı peygamber çocuklarına şöyle dedi: ‹‹Ben ölünce beni bu Tanrı adamının yanına gömün, kemiklerimi de onun kemiklerinin yanına koyun."));
trsuperobject.biblemap.put("b11_13_32", new trsuperobject(11,13,32,"Çünkü onun Beyteldeki sunağa ve Samiriye kentlerindeki tapınma yerlerinde bulunan bütün tapınaklara karşı RABbin buyruğuyla yaptığı uyarılar kesinlikle yerine gelecektir.››"));
trsuperobject.biblemap.put("b11_13_33", new trsuperobject(11,13,33,"Bu olaya karşın Yarovam gittiği kötü yoldan ayrılmadı. Yine her türlü insanı tapınma yerlerine kâhin olarak atadı ve buralara kâhin olmak isteyen herkese görev verdi."));
trsuperobject.biblemap.put("b11_13_34", new trsuperobject(11,13,34,"Yarovam'ın soyu günah işledi. Bu günahlar onların yeryüzünden silinip yok edilmesine neden oldu."));
trsuperobject.biblemap.put("b11_14_1", new trsuperobject(11,14,1,"O sıralarda İsrail Kralı Yarovamın oğlu Aviya hastalandı."));
trsuperobject.biblemap.put("b11_14_2", new trsuperobject(11,14,2,"Yarovam, karısına, ‹‹Kalk, Yarovamın karısı olduğunu anlamamaları için kılığını değiştirip Şiloya git›› dedi, ‹‹Bu halkın kralı olacağımı bana bildiren Peygamber Ahiya orada oturuyor."));
trsuperobject.biblemap.put("b11_14_3", new trsuperobject(11,14,3,"Ona on ekmek, birkaç çörek, bir tulum bal götür. Çocuğa ne olacağını o sana bildirecektir.››"));
trsuperobject.biblemap.put("b11_14_4", new trsuperobject(11,14,4,"Yarovamın karısı denileni yaptı; kalkıp Şiloya, Ahiyanın evine gitti. Ahiyanın gözleri yaşlılıktan görmez olmuştu."));
trsuperobject.biblemap.put("b11_14_6", new trsuperobject(11,14,6,"Ahiya, kapıdan içeri giren kadının ayak seslerini duyunca, ‹‹Gel, Yarovamın karısı!›› dedi, ‹‹Neden başka kılığa giriyorsun? Sana kötü haberlerim var."));
trsuperobject.biblemap.put("b11_14_7", new trsuperobject(11,14,7,"Git Yarovama de ki, ‹İsrailin Tanrısı RAB, Ben seni halkın arasından seçip kendi halkıma, İsraillilere önder yaptım, diyor,"));
trsuperobject.biblemap.put("b11_14_8", new trsuperobject(11,14,8,"Krallığı Davutun soyundan alıp sana verdim. Ama sen buyruklarıma uyan, gözümde yalnız doğru olanı yapan ve bütün yüreğiyle yollarımı izleyen kulum Davuta benzemedin."));
trsuperobject.biblemap.put("b11_14_9", new trsuperobject(11,14,9,"Senden önce yaşayanların hepsinden çok kötülük yaptın. Beni reddettin; kendine başka ilahlar buldun, dökme putlar yaparak beni öfkelendirdin."));
trsuperobject.biblemap.put("b11_14_10", new trsuperobject(11,14,10,"‹‹Bundan dolayı Yarovamın ailesini sıkıntılara sokup İsrailde onun soyundan gelen genç yaşlı bütün erkekleri öldüreceğim. Yarovamın ailesini gübre yakarcasına kökünden kurutacağım."));
trsuperobject.biblemap.put("b11_14_11", new trsuperobject(11,14,11,"Yarovamın ailesinden kentte ölenleri köpekler, kırda ölenleri yırtıcı kuşlar yiyecek.› RAB böyle konuştu."));
trsuperobject.biblemap.put("b11_14_12", new trsuperobject(11,14,12,"‹‹Sana gelince, kalk, evine dön. Kente ayak basar basmaz çocuk ölecek."));
trsuperobject.biblemap.put("b11_14_13", new trsuperobject(11,14,13,"Bütün İsrail halkı ağıt yakıp onu gömecek. Yarovamın ailesinden yalnız o gömülecek. Çünkü Yarovam ailesi içinde İsrailin Tanrısı RABbi hoşnut eden nitelikler yalnız onda bulundu."));
trsuperobject.biblemap.put("b11_14_14", new trsuperobject(11,14,14,"‹‹RAB İsraile bir kral atayacak. Bu kral aynı gün Yarovamın ailesine son verecek. Ne zaman mı? Hemen şimdi."));
trsuperobject.biblemap.put("b11_14_15", new trsuperobject(11,14,15,"RAB İsrail halkını cezalandıracak. İsrail halkı suda sallanan bir kamışa dönecek. RAB onları atalarına vermiş olduğu bu iyi topraklardan söküp Fırat Irmağının ötelerine dağıtacak. Çünkü Aşera putlarını dikerek RABbi öfkelendirdiler."));
trsuperobject.biblemap.put("b11_14_16", new trsuperobject(11,14,16,"Yarovamın işlediği ve İsrail halkını sürüklediği günahlar yüzünden RAB İsraili terk edecek.››"));
trsuperobject.biblemap.put("b11_14_17", new trsuperobject(11,14,17,"Yarovamın karısı oradan ayrılıp Tirsaya döndü. Evinin eşiğine varınca çocuk öldü."));
trsuperobject.biblemap.put("b11_14_18", new trsuperobject(11,14,18,"Bütün İsrail halkı, RABbin kulu Peygamber Ahiya aracılığıyla söylediği söz uyarınca, çocuğu gömüp onun için ağıt yaktı."));
trsuperobject.biblemap.put("b11_14_19", new trsuperobject(11,14,19,"Yarovamın krallığı dönemindeki öteki olaylar, nasıl savaştığı, ülkesini nasıl yönettiği İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_14_20", new trsuperobject(11,14,20,"Yarovam yirmi iki yıl krallık yaptı. Ölüp atalarına kavuşunca, yerine oğlu Nadav kral oldu."));
trsuperobject.biblemap.put("b11_14_21", new trsuperobject(11,14,21,"Süleyman oğlu Rehavam Yahuda Kralı olduğunda kırk bir yaşındaydı. RABbin adını yerleştirmek için bütün İsrail oymaklarının yaşadığı kentler arasından seçtiği Yeruşalim Kentinde on yedi yıl krallık yaptı. Annesi Ammonlu Naamaydı."));
trsuperobject.biblemap.put("b11_14_22", new trsuperobject(11,14,22,"Yahudalılar RABbin gözünde kötü olanı yaparak, işledikleri günahlarla Tanrıyı atalarından daha çok öfkelendirdiler."));
trsuperobject.biblemap.put("b11_14_23", new trsuperobject(11,14,23,"Ayrıca kendilerine her yüksek tepenin üstüne ve bol yapraklı her ağacın altına tapınma yerleri, dikili taşlar ve Aşera putları yaptılar."));
trsuperobject.biblemap.put("b11_14_24", new trsuperobject(11,14,24,"Ülkedeki putperest törenlerinde fuhuş yapan kadın ve erkekler bile vardı. Yahudalılar RABbin İsrail halkının önünden kovduğu ulusların yaptığı bütün iğrençlikleri yaptılar."));
trsuperobject.biblemap.put("b11_14_25", new trsuperobject(11,14,25,"Rehavamın krallığının beşinci yılında Mısır Kralı Şişak Yeruşalime saldırdı."));
trsuperobject.biblemap.put("b11_14_26", new trsuperobject(11,14,26,"Süleymanın yaptırmış olduğu altın kalkanlar dahil RABbin Tapınağının ve sarayın bütün hazinelerini boşaltıp götürdü."));
trsuperobject.biblemap.put("b11_14_27", new trsuperobject(11,14,27,"Kral Rehavam bunların yerine tunç kalkanlar yaptırarak sarayın kapı muhafızlarının komutanlarına emanet etti."));
trsuperobject.biblemap.put("b11_14_28", new trsuperobject(11,14,28,"Kral RABbin Tapınağına her gittiğinde, muhafızlar bu kalkanları taşır, sonra muhafız odasına götürürlerdi."));
trsuperobject.biblemap.put("b11_14_29", new trsuperobject(11,14,29,"Rehavamın krallığı dönemindeki öteki olaylar ve bütün yaptıkları Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_14_30", new trsuperobject(11,14,30,"Rehavamla Yarovam arasında sürekli savaş vardı."));
trsuperobject.biblemap.put("b11_14_31", new trsuperobject(11,14,31,"Rehavam ölüp atalarına kavuşunca, Davut Kenti'nde atalarının yanına gömüldü. Annesi Ammonlu Naama'ydı. Rehavam'ın yerine oğlu Aviyam kral oldu."));
trsuperobject.biblemap.put("b11_15_1", new trsuperobject(11,15,1,"Nevat oğlu İsrail Kralı Yarovamın krallığının on sekizinci yılında Aviyam Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b11_15_2", new trsuperobject(11,15,2,"Yeruşalimde üç yıl krallık yaptı. Annesi Avşalomun kızı Maakaydı."));
trsuperobject.biblemap.put("b11_15_3", new trsuperobject(11,15,3,"Babasının kendisinden önce işlemiş olduğu bütün günahlara Aviyam da katıldı. Bütün yüreğini Tanrısı RABbe adayan atası Davut gibi değildi."));
trsuperobject.biblemap.put("b11_15_4", new trsuperobject(11,15,4,"Buna karşın Tanrısı RAB, Davutun hatırına Yeruşalimi güçlendirmek için kendisinden sonra oğlunu kral atayarak ona Yeruşalimde bir ışık verdi."));
trsuperobject.biblemap.put("b11_15_5", new trsuperobject(11,15,5,"Çünkü RABbin gözünde doğru olanı yapan Davut, Hititli Uriya olayı dışında, yaşamı boyunca RABbin buyruklarının hiçbirinden sapmamıştı."));
trsuperobject.biblemap.put("b11_15_6", new trsuperobject(11,15,6,"Rehavamla Yarovam arasındaki savaş Aviyamın yaşamı boyunca sürüp gitti."));
trsuperobject.biblemap.put("b11_15_7", new trsuperobject(11,15,7,"Aviyamın krallığı dönemindeki öteki olaylar ve bütün yaptıkları Yahuda krallarının tarihinde yazılıdır. Aviyamla Yarovam arasındaki savaş sürüp gitti."));
trsuperobject.biblemap.put("b11_15_8", new trsuperobject(11,15,8,"Aviyam ölüp atalarına kavuşunca, Davut Kentinde gömüldü, yerine oğlu Asa kral oldu."));
trsuperobject.biblemap.put("b11_15_9", new trsuperobject(11,15,9,"İsrail Kralı Yarovamın krallığının yirminci yılında Asa Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b11_15_10", new trsuperobject(11,15,10,"Yeruşalimde kırk bir yıl krallık yaptı. Büyükannesi Avşalomun kızı Maakaydı."));
trsuperobject.biblemap.put("b11_15_11", new trsuperobject(11,15,11,"Atası Davut gibi RABbin gözünde doğru olanı yapan Asa,"));
trsuperobject.biblemap.put("b11_15_12", new trsuperobject(11,15,12,"putperest törenlerinde fuhuş yapan kadın ve erkekleri ülkeden kovdu. Atalarının yapmış olduğu bütün putları yok etti."));
trsuperobject.biblemap.put("b11_15_13", new trsuperobject(11,15,13,"Kral Asa annesi Maakanın kraliçeliğini elinden aldı. Çünkü o Aşera için iğrenç bir put yaptırmıştı. Asa bu iğrenç putu kesip Kidron Vadisinde yaktı."));
trsuperobject.biblemap.put("b11_15_14", new trsuperobject(11,15,14,"Ancak puta tapılan yerleri kaldırmadı. Ama yaşamı boyunca yüreğini RABbe adadı."));
trsuperobject.biblemap.put("b11_15_15", new trsuperobject(11,15,15,"Babasının ve kendisinin adadığı altını, gümüşü ve eşyaları RABbin Tapınağına getirdi."));
trsuperobject.biblemap.put("b11_15_16", new trsuperobject(11,15,16,"Asayla İsrail Kralı Baaşa arasındaki savaş yaşamları boyunca sürüp gitti."));
trsuperobject.biblemap.put("b11_15_17", new trsuperobject(11,15,17,"İsrail Kralı Baaşa Yahudaya saldırmaya hazırlanıyordu. Yahuda Kralı Asanın topraklarına giriş çıkışı engellemek amacıyla, Rama Kentini güçlendirmeye başladı."));
trsuperobject.biblemap.put("b11_15_18", new trsuperobject(11,15,18,"Bunun üzerine Asa, Şamda oturan Hezyon oğlu Tavrimmon oğlu Aram Kralı Ben-Hadata, RABbin Tapınağının ve sarayın hazinelerindeki bütün altın ve gümüşü görevlileri aracılığıyla şu haberle birlikte gönderdi:"));
trsuperobject.biblemap.put("b11_15_19", new trsuperobject(11,15,19,"‹‹Babamla baban arasında olduğu gibi seninle benim aramızda da bir antlaşma olsun. Sana armağan olarak gönderdiğim bu altınlara, gümüşlere karşılık, sen de İsrail Kralı Baaşa ile yaptığın antlaşmayı boz, topraklarımdan askerlerini çeksin.››"));
trsuperobject.biblemap.put("b11_15_20", new trsuperobject(11,15,20,"Kral Asanın önerisini kabul eden Ben-Hadat, komutanlarını İsrail kentlerinin üzerine gönderdi. İyonu, Danı, Avel-Beytmaakayı ve bütün Naftali bölgesiyle birlikte Kinrotu ele geçirdi."));
trsuperobject.biblemap.put("b11_15_21", new trsuperobject(11,15,21,"Baaşa bunu duyunca Ramanın yapımını durdurup Tirsaya çekildi."));
trsuperobject.biblemap.put("b11_15_22", new trsuperobject(11,15,22,"Kral Asa istisnasız bütün Yahudalıları kapsayan bir çağrı yaptı. Baaşanın Ramanın yapımında kullandığı taşlarla keresteleri alıp götürdüler. Kral Asa bunlarla Benyamin bölgesindeki Geva ve Mispa kentlerini onardı."));
trsuperobject.biblemap.put("b11_15_23-24", new trsuperobject(11,15,23-24,"Asanın krallığı dönemindeki öteki olaylar, başarıları, bütün yaptıkları ve kurduğu kentler Yahuda krallarının tarihinde yazılıdır. Yaşlılığında ayaklarından hastalanan Asa, ölüp atalarına kavuşunca, atası Davutun Kentinde atalarının yanına gömüldü; yerine oğlu Yehoşafat kral oldu."));
trsuperobject.biblemap.put("b11_15_25", new trsuperobject(11,15,25,"Yahuda Kralı Asanın krallığının ikinci yılında Yarovam oğlu Nadav İsrail Kralı oldu ve İsrailde iki yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_15_26", new trsuperobject(11,15,26,"O da RABbin gözünde kötü olanı yaptı. Babasının yolunu izledi ve babasının İsraili sürüklediği günahlara katıldı."));
trsuperobject.biblemap.put("b11_15_27", new trsuperobject(11,15,27,"Nadav ve İsrail ordusu Filistlilerin Gibbeton Kentini kuşatırken, İssakar oymağından Ahiya oğlu Baaşa, Nadava düzen kurup onu Gibbetonda öldürdü."));
trsuperobject.biblemap.put("b11_15_28", new trsuperobject(11,15,28,"Yahuda Kralı Asanın krallığının üçüncü yılında Nadavı öldüren Baaşa, onun yerine kral oldu."));
trsuperobject.biblemap.put("b11_15_29", new trsuperobject(11,15,29,"Baaşa kral olur olmaz, Yarovamın bütün ailesini ortadan kaldırdı. RABbin, kulu Şilolu Ahiya aracılığıyla söylediği söz uyarınca, Yarovamın bütün ailesi yok edildi; sağ kalan olmadı."));
trsuperobject.biblemap.put("b11_15_30", new trsuperobject(11,15,30,"Bütün bunlar İsrailin Tanrısı RABbi öfkelendiren Yarovamın işlediği ve İsraili sürüklediği günahlar yüzünden oldu."));
trsuperobject.biblemap.put("b11_15_31", new trsuperobject(11,15,31,"Nadavın krallığı dönemindeki öteki olaylar ve bütün yaptıkları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_15_32", new trsuperobject(11,15,32,"Yahuda Kralı Asa ile İsrail Kralı Baaşa arasındaki savaş yaşamları boyunca sürüp gitti."));
trsuperobject.biblemap.put("b11_15_33", new trsuperobject(11,15,33,"Yahuda Kralı Asanın krallığının üçüncü yılında Ahiya oğlu Baaşa Tirsada bütün İsrailin Kralı oldu ve yirmi dört yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_15_34", new trsuperobject(11,15,34,"Baaşa, RAB'bin gözünde kötü olanı yaptı. Yarovam'ın yolunu izledi ve onun İsrail'i sürüklediği günahlara katıldı."));
trsuperobject.biblemap.put("b11_16_2", new trsuperobject(11,16,2,"‹‹Sen önemsiz biriyken, ben seni halkım İsraile önder yaptım. Ama sen Yarovamın yolunu izleyip halkım İsraili günaha sürükledin. Günahlarınız beni öfkelendirdi."));
trsuperobject.biblemap.put("b11_16_3", new trsuperobject(11,16,3,"Onun için Nevat oğlu Yarovama yaptığım gibi, senin ve ailenin kökünü kurutacağım."));
trsuperobject.biblemap.put("b11_16_4", new trsuperobject(11,16,4,"Baaşanın ailesinden kentte ölenleri köpekler, kırda ölenleri yırtıcı kuşlar yiyecek.››"));
trsuperobject.biblemap.put("b11_16_5", new trsuperobject(11,16,5,"Baaşanın krallığı dönemindeki öteki olaylar, yaptıkları ve başarıları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_16_6", new trsuperobject(11,16,6,"Baaşa ölüp atalarına kavuşunca, Tirsada gömüldü ve yerine oğlu Ela kral oldu."));
trsuperobject.biblemap.put("b11_16_7", new trsuperobject(11,16,7,"Baaşanın RABbin gözünde yaptığı her kötülükten ötürü RAB, Hanani oğlu Peygamber Yehu aracılığıyla ona ve ailesine karşı yargısını bildirdi. Baaşa yaptığı kötülüklerle RABbi öfkelendirmiş, Yarovamın ailesine benzemiş ve bu aileyi ortadan kaldırmıştı."));
trsuperobject.biblemap.put("b11_16_8", new trsuperobject(11,16,8,"Yahuda Kralı Asanın krallığının yirmi altıncı yılında Baaşa oğlu Ela Tirsada İsrail Kralı oldu ve iki yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_16_9", new trsuperobject(11,16,9,"Savaş arabalarının yarısına komuta eden Zimri adındaki bir görevlisi ona düzen kurdu. Ela o sırada Tirsada sarayının sorumlusu Arsanın evinde içip sarhoş olmuştu."));
trsuperobject.biblemap.put("b11_16_10", new trsuperobject(11,16,10,"Zimri içeri girip Elayı öldürdü ve onun yerine kral oldu. Yahuda Kralı Asanın krallığının yirmi yedinci yılıydı."));
trsuperobject.biblemap.put("b11_16_11", new trsuperobject(11,16,11,"Zimri İsrail Kralı olup tahta geçince, Baaşanın bütün ailesini yok etti. Dost ve akrabalarından hiçbir erkeği sağ bırakmadı."));
trsuperobject.biblemap.put("b11_16_12-13", new trsuperobject(11,16,12-13,"Baaşayla oğlu Ela, değersiz putlara taptıkları için İsrailin Tanrısı RABbi öfkelendirmişlerdi. Onların işlediği ve İsraili sürükledikleri günahlardan dolayı RABbin Peygamber Yehu aracılığıyla Baaşaya karşı söylediği söz uyarınca, Zimri Baaşanın bütün ailesini ortadan kaldırdı."));
trsuperobject.biblemap.put("b11_16_14", new trsuperobject(11,16,14,"Elanın krallığı dönemindeki öteki olaylar ve bütün yaptıkları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_16_15", new trsuperobject(11,16,15,"Yahuda Kralı Asanın krallığının yirmi yedinci yılında Zimri Tirsada yedi gün krallık yaptı. İsrail ordusu Filistlilerin Gibbeton Kenti yakınlarında ordugah kurmuştu."));
trsuperobject.biblemap.put("b11_16_16", new trsuperobject(11,16,16,"Ordugahta bulunan İsrailliler, Zimrinin düzen kurup kralı öldürdüğünü duyunca, ordu komutanı Omriyi o gün orada İsrail Kralı yaptılar."));
trsuperobject.biblemap.put("b11_16_17", new trsuperobject(11,16,17,"Omri ve yanındaki bütün İsrailliler Gibbetondan çıkıp Tirsayı kuşattılar."));
trsuperobject.biblemap.put("b11_16_18", new trsuperobject(11,16,18,"Zimri kentin alındığını görünce, sarayın kalesine girip sarayı ateşe verdi ve orada öldü."));
trsuperobject.biblemap.put("b11_16_19", new trsuperobject(11,16,19,"Çünkü o RABbin gözünde kötü olanı yapmış, Yarovamın yolunu izlemiş, onun işlediği ve İsraili sürüklediği günahlara katılmıştı."));
trsuperobject.biblemap.put("b11_16_20", new trsuperobject(11,16,20,"Zimrinin krallığı dönemindeki öteki olaylar ve krala kurduğu düzen İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_16_21", new trsuperobject(11,16,21,"İsrail halkı ikiye bölündü. Halkın yarısı Ginatın oğlu Tivniyi kral yapmak isterken, öbür yarısı Omriyi destekliyordu."));
trsuperobject.biblemap.put("b11_16_22", new trsuperobject(11,16,22,"Sonunda Omriyi destekleyenler Ginat oğlu Tivniyi destekleyenlerden daha güçlü çıktı. Tivni öldü, Omri kral oldu."));
trsuperobject.biblemap.put("b11_16_23", new trsuperobject(11,16,23,"Yahuda Kralı Asanın krallığının otuz birinci yılında Omri İsrail Kralı oldu ve altı yılı Tirsada olmak üzere toplam on iki yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_16_24", new trsuperobject(11,16,24,"Omri, Şemer adlı birinden Samiriye Tepesini iki talant gümüşe satın alıp üstüne bir kent yaptırdı. Tepenin eski sahibi Şemerin adından dolayı kente Samiriye adını verdi."));
trsuperobject.biblemap.put("b11_16_25", new trsuperobject(11,16,25,"RABbin gözünde kötü olanı yapan Omri, kendisinden önceki bütün krallardan daha çok kötülük yaptı."));
trsuperobject.biblemap.put("b11_16_26", new trsuperobject(11,16,26,"Nevat oğlu Yarovamın bütün yollarını izledi ve onun İsraili sürüklediği günahlara katılıp değersiz putlara taparak İsrailin Tanrısı RABbi öfkelendirdi."));
trsuperobject.biblemap.put("b11_16_27", new trsuperobject(11,16,27,"Omrinin krallığı dönemindeki öteki olaylar, yaptıkları ve başarıları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_16_28", new trsuperobject(11,16,28,"Omri ölüp atalarına kavuşunca, Samiriyede gömüldü ve yerine oğlu Ahav kral oldu."));
trsuperobject.biblemap.put("b11_16_29", new trsuperobject(11,16,29,"Yahuda Kralı Asanın krallığının otuz sekizinci yılında Omri oğlu Ahav İsrail Kralı oldu ve Samiriyede yirmi iki yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_16_30", new trsuperobject(11,16,30,"RABbin gözünde kötü olanı yapan Omri oğlu Ahav, kendisinden önceki bütün krallardan daha çok kötülük yaptı."));
trsuperobject.biblemap.put("b11_16_31", new trsuperobject(11,16,31,"Nevat oğlu Yarovamın günahlarını izlemek yetmezmiş gibi, bir de Sayda Kralı Etbaalın kızı İzebelle evlendi. Gidip Baala hizmet ederek ona taptı."));
trsuperobject.biblemap.put("b11_16_32", new trsuperobject(11,16,32,"Baal için Samiriyede yaptırdığı tapınağın içine bir sunak kurdu."));
trsuperobject.biblemap.put("b11_16_33", new trsuperobject(11,16,33,"Ayrıca bir Aşera putu yaptırdı. Ahav İsrailin Tanrısı RABbi kendisinden önceki bütün İsrail krallarından daha çok öfkelendirdi."));
trsuperobject.biblemap.put("b11_16_34", new trsuperobject(11,16,34,"Ahav'ın krallığı döneminde, Beytelli Hiel Eriha Kenti'ni yeniden inşa etti. RAB'bin Nun oğlu Yeşu aracılığıyla söylediği söz uyarınca, Hiel ilk oğlu Aviram'ı kaybetme pahasına kentin temelini attı; en küçük oğlu Seguv'u kaybetme pahasına da kentin kapılarını taktı."));
trsuperobject.biblemap.put("b11_17_1", new trsuperobject(11,17,1,"Gilatın Tişbe Kentinden olan İlyas, Ahava şöyle dedi: ‹‹Hizmet ettiğim İsrailin Tanrısı yaşayan RABbin adıyla derim ki, ben söylemedikçe önümüzdeki yıllarda ne yağmur yağacak, ne de çiy düşecek.››"));
trsuperobject.biblemap.put("b11_17_2", new trsuperobject(11,17,2,"O zaman RAB, İlyasa şöyle seslendi:"));
trsuperobject.biblemap.put("b11_17_3", new trsuperobject(11,17,3,"‹‹Buradan ayrıl, doğuya git. Şeria Irmağının doğusundaki Kerit Vadisinde gizlen."));
trsuperobject.biblemap.put("b11_17_4", new trsuperobject(11,17,4,"Dereden su içeceksin ve buyruk verdiğim kargaların getirdiklerini yiyeceksin.››"));
trsuperobject.biblemap.put("b11_17_6", new trsuperobject(11,17,6,"Dereden su içiyor, kargaların sabah akşam getirdiği et ve ekmekle besleniyordu."));
trsuperobject.biblemap.put("b11_17_7", new trsuperobject(11,17,7,"Ancak ülkede yağmur yağmadığı için bir süre sonra dere kurudu."));
trsuperobject.biblemap.put("b11_17_8", new trsuperobject(11,17,8,"O zaman RAB, İlyasa,"));
trsuperobject.biblemap.put("b11_17_9", new trsuperobject(11,17,9,"‹‹Şimdi kalk git, Sayda yakınlarındaki Sarefat Kentine yerleş›› dedi, ‹‹Orada sana yiyecek sağlaması için dul bir kadına buyruk verdim.››"));
trsuperobject.biblemap.put("b11_17_10", new trsuperobject(11,17,10,"Sarefata giden İlyas kentin kapısına varınca, orada dul bir kadının odun topladığını gördü. Kadına: ‹‹Bana içmek için biraz su verebilir misin?›› dedi."));
trsuperobject.biblemap.put("b11_17_11", new trsuperobject(11,17,11,"Kadın su getirmeye giderken İlyas yine seslendi: ‹‹Lütfen bir parça da ekmek getir.››"));
trsuperobject.biblemap.put("b11_17_12", new trsuperobject(11,17,12,"Kadın, ‹‹Senin Tanrın yaşayan RABbin adıyla ant içerim, hiç ekmeğim yok›› diye karşılık verdi, ‹‹Yalnız küpte bir avuç un, çömleğin dibinde de azıcık yağ var. Görüyorsun, bir iki parça odun topluyorum. Götürüp oğlumla kendim için bir şeyler hazırlayacağım. Belki de son yemeğimiz olacak, ölüp gideceğiz.››"));
trsuperobject.biblemap.put("b11_17_13", new trsuperobject(11,17,13,"İlyas kadına, ‹‹Korkma, git yiyeceğini hazırla›› dedi, ‹‹Yalnız önce bana küçük bir pide yapıp getir. Sonra oğlunla kendin için yaparsın."));
trsuperobject.biblemap.put("b11_17_14", new trsuperobject(11,17,14,"İsrailin Tanrısı RAB diyor ki, ‹Toprağa yağmur düşünceye dek küpten un, çömlekten yağ eksilmeyecek.››"));
trsuperobject.biblemap.put("b11_17_15", new trsuperobject(11,17,15,"Kadın gidip İlyasın söylediklerini yaptı. Hep birlikte günlerce yiyip içtiler."));
trsuperobject.biblemap.put("b11_17_16", new trsuperobject(11,17,16,"RABbin İlyas aracılığıyla söylediği söz uyarınca, küpten un, çömlekten yağ eksilmedi."));
trsuperobject.biblemap.put("b11_17_17", new trsuperobject(11,17,17,"Bir süre sonra ev sahibi dul kadının oğlu gittikçe ağırlaşan kötü bir hastalığa yakalandı, sonunda öldü."));
trsuperobject.biblemap.put("b11_17_18", new trsuperobject(11,17,18,"Kadın İlyasa, ‹‹Ey Tanrı adamı, alıp veremediğimiz nedir?›› dedi, ‹‹Günahlarımı Tanrıya anımsatıp oğlumun ölümüne neden olmak için mi buraya geldin?››"));
trsuperobject.biblemap.put("b11_17_19", new trsuperobject(11,17,19,"İlyas, ‹‹Oğlunu bana ver›› diyerek çocuğu kadının kucağından aldı, kaldığı yukarı odaya çıkardı ve yatağına yatırdı."));
trsuperobject.biblemap.put("b11_17_20", new trsuperobject(11,17,20,"Sonra RABbe şöyle yalvardı: ‹‹Ya RAB Tanrım, neden yanında kaldığım dul kadının oğlunu öldürerek ona bu kötülüğü yaptın?››"));
trsuperobject.biblemap.put("b11_17_21", new trsuperobject(11,17,21,"İlyas üç kez çocuğun üzerine kapanıp RABbe şöyle dua etti: ‹‹Ya RAB Tanrım, bu çocuğa yeniden can ver.››"));
trsuperobject.biblemap.put("b11_17_22", new trsuperobject(11,17,22,"RAB İlyasın yalvarışını duydu. Çocuk dirilip yeniden yaşama döndü."));
trsuperobject.biblemap.put("b11_17_23", new trsuperobject(11,17,23,"İlyas çocuğu yukarı odadan indirip annesine verirken, ‹‹İşte oğlun yaşıyor!›› dedi."));
trsuperobject.biblemap.put("b11_17_24", new trsuperobject(11,17,24,"Bunun üzerine kadın, ‹‹Şimdi anladım ki, sen Tanrı adamısın ve söylediğin söz gerçekten RAB'bin sözüdür›› dedi."));
trsuperobject.biblemap.put("b11_18_1", new trsuperobject(11,18,1,"Uzun bir süre sonra kuraklığın üçüncü yılında RAB İlyasa, ‹‹Git, Ahavın huzuruna çık›› dedi, ‹‹Toprağı yağmursuz bırakmayacağım.››"));
trsuperobject.biblemap.put("b11_18_2", new trsuperobject(11,18,2,"İlyas Ahavın huzuruna çıkmaya gitti. Samiriyede kıtlık şiddetlenmişti."));
trsuperobject.biblemap.put("b11_18_3", new trsuperobject(11,18,3,"Ahav sarayının sorumlusu Ovadyayı çağırdı. -Ovadya RABden çok korkardı."));
trsuperobject.biblemap.put("b11_18_4", new trsuperobject(11,18,4,"İzebel RABbin peygamberlerini öldürdüğünde, Ovadya yüz peygamberi yanına alıp ellişer ellişer mağaralara gizlemiş ve yiyecek, içecek gereksinimlerini karşılamıştı.-"));
trsuperobject.biblemap.put("b11_18_5", new trsuperobject(11,18,5,"Ahav, Ovadyaya, ‹‹Haydi gidip ülkedeki bütün su kaynaklarıyla vadilere bakalım›› dedi, ‹‹Belki atlarla katırların yaşamasını sağlayacak kadar ot buluruz da onları ölüme terk etmemiş oluruz.››"));
trsuperobject.biblemap.put("b11_18_6", new trsuperobject(11,18,6,"Ahavla Ovadya, araştırma yapmak üzere ülkeyi aralarında bölüştükten sonra, her biri yalnız başına bir yöne gitti."));
trsuperobject.biblemap.put("b11_18_7", new trsuperobject(11,18,7,"Ovadya giderken yolda İlyasla karşılaştı. İlyası tanıyınca yüzüstü yere kapanarak, ‹‹Efendim İlyas sen misin?›› diye sordu."));
trsuperobject.biblemap.put("b11_18_8", new trsuperobject(11,18,8,"İlyas, ‹‹Evet, benim. Git efendine, ‹İlyas burada› de›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_18_9", new trsuperobject(11,18,9,"Ovadya, ‹‹Ne günah işledim ki, beni öldürsün diye Ahava gönderiyorsun?›› dedi ve ekledi:"));
trsuperobject.biblemap.put("b11_18_10", new trsuperobject(11,18,10,"‹‹Tanrın yaşayan RABbin adıyla derim ki, efendimin seni aramak için adam göndermediği ulus ve krallık kalmadı. Ahav ülkelerinde olmadığını söyleyen herkese, seni bulamadıklarına dair ant içirdi."));
trsuperobject.biblemap.put("b11_18_11", new trsuperobject(11,18,11,"Oysa sen şimdi, ‹Git, efendine İlyas burada de› diyorsun."));
trsuperobject.biblemap.put("b11_18_12", new trsuperobject(11,18,12,"Ben senin yanından ayrıldığımda, RABbin Ruhu seni bilmediğim bir yere götürebilir. Durumu Ahava bildirince, gelip seni bulamazsa beni öldürür. Ben kulun gençliğimden beri RABden korkan biriyim."));
trsuperobject.biblemap.put("b11_18_13", new trsuperobject(11,18,13,"Efendim, İzebel RABbin peygamberlerini öldürdüğünde yaptıklarımı duymadın mı? RABbin peygamberlerinden yüzünü ellişer ellişer iki mağaraya saklayıp onların yiyecek, içecek gereksinimlerini karşıladım."));
trsuperobject.biblemap.put("b11_18_14", new trsuperobject(11,18,14,"Ama sen şimdi, ‹Git, efendine İlyas burada de› diyorsun. O zaman beni öldürür!››"));
trsuperobject.biblemap.put("b11_18_15", new trsuperobject(11,18,15,"İlyas şöyle karşılık verdi: ‹‹Hizmetinde bulunduğum yaşayan ve Her Şeye Egemen RABbin adıyla diyorum, bugün Ahavın huzuruna çıkacağım.››"));
trsuperobject.biblemap.put("b11_18_16", new trsuperobject(11,18,16,"Ovadya gidip Ahavı gördü, ona durumu anlattı. Bunun üzerine Ahav İlyası karşılamaya gitti."));
trsuperobject.biblemap.put("b11_18_17", new trsuperobject(11,18,17,"İlyası görünce, ‹‹Ey İsraili sıkıntıya sokan adam, sen misin?›› diye sordu."));
trsuperobject.biblemap.put("b11_18_18", new trsuperobject(11,18,18,"İlyas, ‹‹İsraili sıkıntıya sokan ben değilim, seninle babanın ailesi İsraili sıkıntıya soktunuz›› diye karşılık verdi, ‹‹RABbin buyruklarını terk edip Baalların ardınca gittiniz."));
trsuperobject.biblemap.put("b11_18_19", new trsuperobject(11,18,19,"Şimdi haber sal: Bütün İsrail halkı, İzebelin sofrasında yiyip içen Baalın dört yüz elli peygamberi ve Aşeranın dört yüz peygamberi Karmel Dağına gelip önümde toplansın.››"));
trsuperobject.biblemap.put("b11_18_20", new trsuperobject(11,18,20,"Ahav bütün İsraile haber salarak peygamberlerin Karmel Dağında toplanmalarını sağladı."));
trsuperobject.biblemap.put("b11_18_21", new trsuperobject(11,18,21,"İlyas halka doğru ilerleyip, ‹‹Daha ne zamana kadar böyle iki taraf arasında dalgalanacaksınız?›› dedi, ‹‹Eğer RAB Tanrıysa, onu izleyin; yok eğer Baal Tanrıysa, onun ardınca gidin.›› Halk İlyasa hiç karşılık vermedi."));
trsuperobject.biblemap.put("b11_18_22", new trsuperobject(11,18,22,"İlyas konuşmasını şöyle sürdürdü: ‹‹RABbin peygamberi olarak sadece ben kaldım. Ama Baalın dört yüz elli peygamberi var."));
trsuperobject.biblemap.put("b11_18_23", new trsuperobject(11,18,23,"Bize iki boğa getirin. Birini Baalın peygamberleri alıp kessinler, parçalayıp odunların üzerine koysunlar; ama odunları yakmasınlar. Öbür boğayı da ben kesip hazırlayacağım ve odunların üzerine koyacağım; ama odunları yakmayacağım."));
trsuperobject.biblemap.put("b11_18_24", new trsuperobject(11,18,24,"Sonra siz kendi ilahınızı adıyla çağırın, ben de RABbi adıyla çağırayım. Hangisi ateşle karşılık verirse, Tanrı odur.›› Bütün halk, ‹‹Peki, öyle olsun›› dedi."));
trsuperobject.biblemap.put("b11_18_25", new trsuperobject(11,18,25,"İlyas, Baalın peygamberlerine, ‹‹Kalabalık olduğunuz için önce siz boğalardan birini seçip hazırlayın ve ilahınızı adıyla çağırın›› dedi, ‹‹Ama ateş yakmayın.››"));
trsuperobject.biblemap.put("b11_18_26", new trsuperobject(11,18,26,"Kendilerine verilen boğayı alıp hazırlayan Baalın peygamberleri sabahtan öğlene kadar, ‹‹Ey Baal, bize karşılık ver!›› diye yalvardılar. Ama ne bir ses vardı, ne de bir karşılık. Yaptıkları sunağın çevresinde zıplayıp oynadılar."));
trsuperobject.biblemap.put("b11_18_27", new trsuperobject(11,18,27,"Öğleyin İlyas onlarla alay etmeye başladı: ‹‹Bağırın, yüksek sesle bağırın! O tanrıymış. Belki dalgındır, ya da heladadır, belki de yolculuk yapıyor! Yahut uyuyordur da uyandırmak gerekir!››"));
trsuperobject.biblemap.put("b11_18_28", new trsuperobject(11,18,28,"Böylece yüksek sesle bağırdılar. Adetleri uyarınca, kılıç ve mızraklarla kanlarını akıtıncaya dek bedenlerini yaraladılar."));
trsuperobject.biblemap.put("b11_18_29", new trsuperobject(11,18,29,"Öğlenden akşam sunusu saatine kadar kıvrandılar. Ama hâlâ ne bir ses, ne ilgi, ne de bir karşılık vardı."));
trsuperobject.biblemap.put("b11_18_30", new trsuperobject(11,18,30,"O zaman İlyas bütün halka, ‹‹Bana yaklaşın›› dedi. Herkes onun çevresinde toplandı. İlyas RABbin yıkılan sunağını onarmaya başladı."));
trsuperobject.biblemap.put("b11_18_31", new trsuperobject(11,18,31,"On iki taş aldı. Bu sayı RABbin Yakupa, ‹‹Senin adın İsrail olacak›› diye bildirdiği Yakupoğulları oymaklarının sayısı kadardı."));
trsuperobject.biblemap.put("b11_18_32", new trsuperobject(11,18,32,"İlyas bu taşlarla RABbin adına bir sunak yaptırdı. Çevresine de iki seafü tohum alacak kadar bir hendek kazdı."));
trsuperobject.biblemap.put("b11_18_33", new trsuperobject(11,18,33,"Sunağın üzerine odunları dizdi, boğayı parça parça kesip odunların üzerine yerleştirdi. ‹‹Dört küp su doldurup yakmalık sunuyla odunların üzerine dökün›› dedi."));
trsuperobject.biblemap.put("b11_18_34", new trsuperobject(11,18,34,"Sonra, ‹‹Bir daha yapın›› dedi. Bir daha yaptılar. ‹‹Bir kez daha yapın›› dedi. Üçüncü kez aynı şeyi yaptılar."));
trsuperobject.biblemap.put("b11_18_35", new trsuperobject(11,18,35,"O zaman sunağın çevresine akan su hendeği doldurdu."));
trsuperobject.biblemap.put("b11_18_36", new trsuperobject(11,18,36,"Akşam sunusu saatinde, Peygamber İlyas sunağa yaklaşıp şöyle dua etti: ‹‹Ey İbrahimin, İshakın ve İsrailin Tanrısı olan RAB! Bugün bilinsin ki, sen İsrailin Tanrısısın, ben de senin kulunum ve bütün bunları senin buyruklarınla yaptım."));
trsuperobject.biblemap.put("b11_18_37", new trsuperobject(11,18,37,"Ya RAB, bana yanıt ver! Yanıt ver ki, bu halk senin Tanrı olduğunu anlasın. Onların yine sana dönmelerini sağla.››"));
trsuperobject.biblemap.put("b11_18_38", new trsuperobject(11,18,38,"O anda gökten RABbin ateşi düştü. Düşen ateş yakmalık sunuyu, odunları, taşları ve toprağı yakıp hendekteki suyu kuruttu."));
trsuperobject.biblemap.put("b11_18_39", new trsuperobject(11,18,39,"Halk olanları görünce yüzüstü yere kapandı. ‹‹RAB Tanrıdır, RAB Tanrıdır!›› dediler."));
trsuperobject.biblemap.put("b11_18_40", new trsuperobject(11,18,40,"İlyas, ‹‹Baalın peygamberlerini yakalayın, hiçbirini kaçırmayın›› diye onlara buyruk verdi. Peygamberler yakalandı, İlyas onları Kişon Vadisine götürüp orada öldürdü."));
trsuperobject.biblemap.put("b11_18_41", new trsuperobject(11,18,41,"Sonra İlyas, Ahava, ‹‹Git, yemene içmene bak; çünkü güçlü bir yağmur sesi var›› dedi."));
trsuperobject.biblemap.put("b11_18_42", new trsuperobject(11,18,42,"Ahav yiyip içmek üzere oradan ayrılınca, İlyas Karmel Dağının tepesine çıktı. Yere kapanarak başını dizlerinin arasına koydu."));
trsuperobject.biblemap.put("b11_18_43", new trsuperobject(11,18,43,"Sonra uşağına, ‹‹Haydi git, denize doğru bak!›› dedi. Uşağı gidip denize baktı ve, ‹‹Hiçbir şey görmedim›› diye karşılık verdi. İlyas, uşağına yedi kez, ‹‹Git, bak›› dedi."));
trsuperobject.biblemap.put("b11_18_44", new trsuperobject(11,18,44,"Yedinci kez gidip bakan uşak, ‹‹Denizden avuç kadar küçük bir bulut çıkıyor›› dedi. İlyas şöyle dedi: ‹‹Git, Ahava, ‹Yağmura yakalanmadan arabanı al ve geri dön› de.››"));
trsuperobject.biblemap.put("b11_18_45", new trsuperobject(11,18,45,"Tam o sırada gökyüzü bulutlarla karardı, rüzgar çıktı, şiddetli bir yağmur başladı. Ahav hemen arabasına binip Yizreele gitti."));
trsuperobject.biblemap.put("b11_18_46", new trsuperobject(11,18,46,"Üzerine RAB'bin gücü inen İlyas kemerini kuşanıp Yizreel'e kadar Ahav'ın önünde koştu."));
trsuperobject.biblemap.put("b11_19_1", new trsuperobject(11,19,1,"Ahav, İlyasın bütün yaptıklarını, peygamberleri nasıl kılıçtan geçirdiğini İzebele anlattı."));
trsuperobject.biblemap.put("b11_19_2", new trsuperobject(11,19,2,"İzebel, İlyasa, ‹‹Yarın bu saate kadar senin peygamberlere yaptığını ben de sana yapmazsam, ilahlar bana aynısını, hatta daha kötüsünü yapsın›› diye haber gönderdi."));
trsuperobject.biblemap.put("b11_19_3", new trsuperobject(11,19,3,"İlyas can korkusuyla Yahudanın Beer-Şeva Kentine kaçıp uşağını orada bıraktı."));
trsuperobject.biblemap.put("b11_19_4", new trsuperobject(11,19,4,"Bir gün boyunca çölde yürüdü, sonunda bir retem çalısının altına oturdu ve ölmek için dua etti: ‹‹Ya RAB, yeter artık, canımı al, ben atalarımdan daha iyi değilim.››"));
trsuperobject.biblemap.put("b11_19_5", new trsuperobject(11,19,5,"Sonra retem çalısının altına yatıp uykuya daldı. Ansızın bir melek ona dokunarak, ‹‹Kalk yemek ye›› dedi."));
trsuperobject.biblemap.put("b11_19_6", new trsuperobject(11,19,6,"İlyas çevresine bakınca yanıbaşında, kızgın taşların üstünde bir pideyle bir testi su gördü. Yiyip içtikten sonra yine uzandı."));
trsuperobject.biblemap.put("b11_19_8", new trsuperobject(11,19,8,"İlyas kalktı, yiyip içti. Yediklerinden aldığı güçle kırk gün kırk gece Tanrı Dağı Horeve kadar yürüdü."));
trsuperobject.biblemap.put("b11_19_9", new trsuperobject(11,19,9,"Geceyi orada bulunan bir mağarada geçirdi. RAB, ‹‹Burada ne yapıyorsun, İlyas?›› diye sordu."));
trsuperobject.biblemap.put("b11_19_10", new trsuperobject(11,19,10,"İlyas, ‹‹RABbe, Her Şeye Egemen Tanrıya büyük bir istekle kulluk ettim›› diye karşılık verdi, ‹‹Ama İsrail halkı senin antlaşmanı reddetti, sunaklarını yıktı ve peygamberlerini kılıçtan geçirdi. Yalnız ben kaldım. Beni de öldürmeye çalışıyorlar.››"));
trsuperobject.biblemap.put("b11_19_11", new trsuperobject(11,19,11,"RAB, ‹‹Dağa çık ve önümde dur, yanından geçeceğim›› dedi. RABbin önünde çok güçlü bir rüzgar dağları yarıp kayaları parçaladı. Ancak RAB rüzgarın içinde değildi. Rüzgarın ardından bir deprem oldu, RAB depremin içinde de değildi."));
trsuperobject.biblemap.put("b11_19_12", new trsuperobject(11,19,12,"Depremden sonra bir ateş çıktı, ancak RAB ateşin içinde de değildi. Ateşten sonra ince, yumuşak bir ses duyuldu."));
trsuperobject.biblemap.put("b11_19_13", new trsuperobject(11,19,13,"İlyas bu sesi duyunca, cüppesiyle yüzünü örttü, çıkıp mağaranın girişinde durdu. O sırada bir ses, ‹‹Burada ne yapıyorsun, İlyas?›› dedi."));
trsuperobject.biblemap.put("b11_19_14", new trsuperobject(11,19,14,"İlyas, ‹‹RABbe, Her Şeye Egemen Tanrıya büyük bir istekle kulluk ettim›› diye karşılık verdi, ‹‹Ama İsrail halkı senin antlaşmanı reddetti, sunaklarını yıktı ve peygamberlerini kılıçtan geçirdi. Yalnız ben kaldım. Beni de öldürmeye çalışıyorlar.››"));
trsuperobject.biblemap.put("b11_19_15-16", new trsuperobject(11,19,15-16,"RAB, ‹‹Geldiğin yoldan geri dön, Şam yakınındaki kırlara git›› dedi, ‹‹Oraya vardığında, Hazaeli Aram Kralı olarak, Nimşi oğlu Yehuyu İsrail Kralı olarak, Avel-Meholalı Şafatın oğlu Elişayı da kendi yerine peygamber olarak meshedeceksin."));
trsuperobject.biblemap.put("b11_19_17", new trsuperobject(11,19,17,"Hazaelin kılıcından kurtulanı Yehu, Yehunun kılıcından kurtulanı Elişa öldürecek."));
trsuperobject.biblemap.put("b11_19_18", new trsuperobject(11,19,18,"Ancak İsrailde Baalın önünde diz çöküp onu öpmemiş yedi bin kişiyi sağ bırakacağım.››"));
trsuperobject.biblemap.put("b11_19_19", new trsuperobject(11,19,19,"İlyas oradan ayrılıp gitti, Şafat oğlu Elişayı buldu. Elişa, on iki çift öküzle saban sürenlerin ardından on ikinci çifti sürüyordu. İlyas Elişanın yanından geçerek kendi cüppesini onun üzerine attı."));
trsuperobject.biblemap.put("b11_19_20", new trsuperobject(11,19,20,"Elişa öküzleri bırakıp İlyasın ardından koştu ve, ‹‹İzin ver, annemle babamı öpeyim, sonra seninle geleyim›› dedi. İlyas, ‹‹Geri dön, ben sana ne yaptım ki?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_19_21", new trsuperobject(11,19,21,"Böylece Elişa gidip sürdüğü çiftin öküzlerini kesti. Boyunduruklarıyla ateş yakıp etleri pişirdikten sonra, yesinler diye halka dağıttı. Sonra, İlyas'ın ardından gidip ona hizmet etti."));
trsuperobject.biblemap.put("b11_20_1", new trsuperobject(11,20,1,"Aram Kralı Ben-Hadat bütün ordusunu topladı. Atları, savaş arabaları ve kendisini destekleyen otuz iki kralla birlikte Samiriyenin üzerine yürüyerek kenti kuşattı."));
trsuperobject.biblemap.put("b11_20_2", new trsuperobject(11,20,2,"Ben-Hadat, kentte bulunan İsrail Kralı Ahava haberciler göndererek şöyle buyruk verdi:"));
trsuperobject.biblemap.put("b11_20_3", new trsuperobject(11,20,3,"‹‹Ben-Hadat diyor ki, ‹Altınını, gümüşünü, karılarını ve en gürbüz çocuklarını bana teslim et.››"));
trsuperobject.biblemap.put("b11_20_4", new trsuperobject(11,20,4,"İsrail Kralı, ‹‹Efendim kralın dediklerini kabul ediyorum›› diye karşılık verdi, ‹‹Beni ve sahip olduğum her şeyi alabilirsin.››"));
trsuperobject.biblemap.put("b11_20_5", new trsuperobject(11,20,5,"Haberciler yine gelip Ahava şöyle dediler: ‹‹Ben-Hadat diyor ki, ‹Sana altınını, gümüşünü, karılarını ve çocuklarını bana vereceksin diye haber göndermiştim."));
trsuperobject.biblemap.put("b11_20_6", new trsuperobject(11,20,6,"Ayrıca yarın bu saatlerde sarayında ve görevlilerinin evlerinde arama yapmak üzere kendi görevlilerimi göndereceğim. Değerli olan her şeyini alıp getirecekler.››"));
trsuperobject.biblemap.put("b11_20_7", new trsuperobject(11,20,7,"İsrail Kralı ülkenin bütün ileri gelenlerini toplayarak, ‹‹Bakın, bu adam nasıl bela arıyor!›› dedi, ‹‹Bana haber gönderip altınımı, gümüşümü, karılarımı, çocuklarımı istedi, reddetmedim.››"));
trsuperobject.biblemap.put("b11_20_8", new trsuperobject(11,20,8,"Bütün ileri gelenler ve halk, ‹‹Onu dinleme, isteklerini de kabul etme›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b11_20_9", new trsuperobject(11,20,9,"Böylece Ahav, Ben-Hadatın habercilerine, ‹‹Efendimiz krala ilk isteklerinin hepsini kabul edeceğimi, ama ikincisini kabul edemeyeceğimi söyleyin›› dedi. Haberciler gidip Ben-Hadata durumu bildirdiler."));
trsuperobject.biblemap.put("b11_20_10", new trsuperobject(11,20,10,"O zaman Ben-Hadat Ahava başka bir haber gönderdi: ‹‹O kadar çok adamla senin üstüne yürüyeceğim ki, Samiriyeyi yerle bir edeceğim. Kentin tozları askerlerimin avuçlarını bile dolduramayacak. Eğer bunu yapmazsam, ilahlar bana aynısını, hatta daha kötüsünü yapsın!››"));
trsuperobject.biblemap.put("b11_20_11", new trsuperobject(11,20,11,"İsrail Kralı şöyle karşılık verdi: ‹‹Kralınıza deyin ki, ‹Zırhını kuşanmadan önce değil, kuşandıktan sonra övünsün.››"));
trsuperobject.biblemap.put("b11_20_12", new trsuperobject(11,20,12,"Ben-Hadat bunu duyduğunda, kendisini destekleyen krallarla birlikte çadırda içki içiyordu. Hemen adamlarına buyruk verdi: ‹‹Saldırıya hazırlanın.›› Böylece Samiriyeye karşı saldırı hazırlıklarına giriştiler."));
trsuperobject.biblemap.put("b11_20_13", new trsuperobject(11,20,13,"O sırada bir peygamber gelip İsrail Kralı Ahava şöyle dedi: ‹‹RAB diyor ki, ‹Bu büyük orduyu görüyor musun? Onları bugün senin eline teslim edeceğim. O zaman benim RAB olduğumu anlayacaksın.››"));
trsuperobject.biblemap.put("b11_20_14", new trsuperobject(11,20,14,"Ahav, ‹‹Kimin aracılığıyla olacak bu?›› diye sordu. Peygamber şu karşılığı verdi: ‹‹RAB diyor ki, ‹İlçe komutanlarının genç askerleri bunu başaracak.›› Ahav, ‹‹Savaşa kim başlayacak?›› diye sordu. Peygamber, ‹‹Sen başlayacaksın›› dedi."));
trsuperobject.biblemap.put("b11_20_15", new trsuperobject(11,20,15,"Ahav ilçe komutanlarının genç askerlerini çağırıp saydı. İki yüz otuz iki kişiydiler. Sonra bütün İsrail ordusunu toplayıp saydı, onlar da yedi bin kişiydiler."));
trsuperobject.biblemap.put("b11_20_16", new trsuperobject(11,20,16,"Öğleyin Ben-Hadat ile kendisini destekleyen otuz iki kral çadırlarda içip sarhoş olmuşken İsrail saldırısı başladı."));
trsuperobject.biblemap.put("b11_20_17", new trsuperobject(11,20,17,"Önce genç askerler saldırıya geçti. Ben-Hadatın gönderdiği gözcüler, ‹‹Samiriyeliler geliyor›› diye ona haber getirdiler."));
trsuperobject.biblemap.put("b11_20_18", new trsuperobject(11,20,18,"Ben-Hadat, ‹‹İster barış, ister savaş için gelsinler, onları canlı yakalayın›› dedi."));
trsuperobject.biblemap.put("b11_20_19", new trsuperobject(11,20,19,"Genç askerler arkalarındaki İsrail ordusuyla birlikte kentten çıkıp saldırıya geçtiler."));
trsuperobject.biblemap.put("b11_20_20", new trsuperobject(11,20,20,"Herkes önüne geleni öldürdü. Aramlılar kaçmaya başlayınca, İsrailliler peşlerine düştü. Ama Aram Kralı Ben-Hadat, atına binerek atlılarla birlikte kaçıp kurtuldu."));
trsuperobject.biblemap.put("b11_20_21", new trsuperobject(11,20,21,"İsrail Kralı atlarla savaş arabalarına büyük zararlar vererek Aramlıları ağır bir yenilgiye uğrattı."));
trsuperobject.biblemap.put("b11_20_22", new trsuperobject(11,20,22,"Daha sonra peygamber gelip İsrail Kralına, ‹‹Git, gücünü pekiştir ve neler yapman gerektiğini iyi düşün›› dedi, ‹‹Çünkü önümüzdeki ilkbaharda Aram Kralı sana yine saldıracak.››"));
trsuperobject.biblemap.put("b11_20_23", new trsuperobject(11,20,23,"Bu arada görevlileri Aram Kralının kendisine, ‹‹İsrailin ilahı dağ ilahıdır›› dediler, ‹‹Bu nedenle bizden güçlü çıktılar. Ama ovada savaşırsak, onları kesinlikle yeneriz."));
trsuperobject.biblemap.put("b11_20_24", new trsuperobject(11,20,24,"Şimdi bütün kralları görevlerinden al, onların yerine yeni komutanlar ata."));
trsuperobject.biblemap.put("b11_20_25", new trsuperobject(11,20,25,"Kaybettiğin kadar at ve savaş arabası toplayarak kendine yeni bir ordu kur. İsraillilerle ovada savaşalım. O zaman onları kesinlikle yeneriz.›› Aram Kralı Ben-Hadat bütün söylenenleri kabul edip yerine getirdi."));
trsuperobject.biblemap.put("b11_20_26", new trsuperobject(11,20,26,"İlkbaharda Aramlıları toplayıp İsraillilerle savaşmak üzere Afek Kentine gitti."));
trsuperobject.biblemap.put("b11_20_27", new trsuperobject(11,20,27,"İsrail halkı da toplanıp yiyeceğini hazırladı. Aramlılarla savaşmak üzere yola çıkıp onların karşısına ordugah kurdu. Ülkeyi dolduran Aramlıların karşısında İsrailliler iki küçük oğlak sürüsü gibi kalıyordu."));
trsuperobject.biblemap.put("b11_20_28", new trsuperobject(11,20,28,"Bir Tanrı adamı gidip İsrail Kralı Ahava şöyle dedi: ‹‹RAB diyor ki, ‹Aramlılar, RAB dağların Tanrısıdır, ovaların değil, dedikleri için bu güçlü ordunun tümünü senin eline teslim edeceğim. O zaman benim RAB olduğumu anlayacaksın.››"));
trsuperobject.biblemap.put("b11_20_29", new trsuperobject(11,20,29,"Birbirlerine karşı ordugah kuran Aramlılarla İsrailliler yedi gün beklediler. Yedinci gün savaş başladı. İsrailliler bir gün içinde yüz bin Aramlı yaya asker öldürdü."));
trsuperobject.biblemap.put("b11_20_30", new trsuperobject(11,20,30,"Sağ kalanlar Afek Kentine kaçtılar. Orada da yirmi yedi bin kişinin üstüne surlar yıkıldı. Ben-Hadat kentin içine kaçıp bir iç odaya saklandı."));
trsuperobject.biblemap.put("b11_20_31", new trsuperobject(11,20,31,"Görevlileri Ben-Hadata şöyle dediler: ‹‹Duyduğumuza göre, İsrail kralları iyi yürekli krallarmış. Haydi bellerimize çul kuşanıp başlarımıza ip saralım ve İsrail Kralının huzuruna çıkalım. Belki senin canını bağışlar.››"));
trsuperobject.biblemap.put("b11_20_32", new trsuperobject(11,20,32,"Bellerine çul kuşanıp başlarına da ip bağladılar ve İsrail Kralının huzuruna çıkarak, ‹‹Kulun Ben-Hadat ‹Canımı bağışla› diye yalvarıyor›› dediler. Ahav, ‹‹Ben-Hadat hâlâ yaşıyor mu? O benim kardeşim sayılır›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_20_33", new trsuperobject(11,20,33,"Adamlar bunu olumlu bir belirti sayarak hemen sözü ağzından aldılar ve, ‹‹Evet, Ben-Hadat kardeşin sayılır!›› dediler. Kral, ‹‹Gidin, onu getirin›› diye buyruk verdi. Ben-Hadat gelince, Ahav onu kendi savaş arabasına aldı."));
trsuperobject.biblemap.put("b11_20_34", new trsuperobject(11,20,34,"Ben-Hadat, ‹‹Babamın babandan almış olduğu kentleri geri vereceğim›› dedi, ‹‹Babam nasıl Samiriyede çarşılar kurduysa, sen de Şamda çarşılar kurabilirsin.›› Bunun üzerine Ahav, ‹‹Ben de bu şartlara dayanarak sana özgürlüğünü veriyorum›› dedi. Böylece onunla bir antlaşma yaparak gitmesine izin verdi."));
trsuperobject.biblemap.put("b11_20_35", new trsuperobject(11,20,35,"Peygamberlerden biri, RABbin sözüne uyarak arkadaşına, ‹‹Lütfen, beni vur!›› dedi. Ama arkadaşı onu vurmak istemedi."));
trsuperobject.biblemap.put("b11_20_36", new trsuperobject(11,20,36,"O zaman peygamber arkadaşına şöyle dedi: ‹‹Sen RABbin buyruğunu dinlemediğin için, yanımdan ayrılır ayrılmaz bir aslan seni öldürecek.›› Adam oradan ayrıldıktan sonra aslan onu yakalayıp öldürdü."));
trsuperobject.biblemap.put("b11_20_37", new trsuperobject(11,20,37,"Bunun üzerine aynı peygamber, başka bir adama giderek, ‹‹Lütfen beni vur!›› dedi. Adam da onu vurup yaraladı."));
trsuperobject.biblemap.put("b11_20_38", new trsuperobject(11,20,38,"Peygamber gitti, kılığını değiştirmek için gözlerini bağladı. Yol kenarında kralın geçmesini beklemeye başladı."));
trsuperobject.biblemap.put("b11_20_39", new trsuperobject(11,20,39,"Kral oradan geçerken, peygamber ona şöyle seslendi: ‹‹Ben kulun, tam savaşın içindeyken, askerin biri bana bir tutsak getirip, ‹Bu adamı iyi koru› dedi, ‹Kaçacak olursa, karşılığını ya canınla, ya da bir talant gümüşle ödersin.›"));
trsuperobject.biblemap.put("b11_20_40", new trsuperobject(11,20,40,"Ama ben oraya buraya bakarken, adam kayboldu.›› verdi, ‹‹Cezanı çekeceksin.››"));
trsuperobject.biblemap.put("b11_20_41", new trsuperobject(11,20,41,"Peygamber, hemen gözlerindeki sargıyı çıkardı. O zaman İsrail Kralı onun bir peygamber olduğunu anladı."));
trsuperobject.biblemap.put("b11_20_42", new trsuperobject(11,20,42,"Bunun üzerine peygamber krala şöyle dedi: ‹‹RAB diyor ki, ‹Ölüme mahkûm ettiğim adamı salıverdiğin için onun yerine sen öleceksin. Onun halkının başına gelecekler senin halkının başına gelecek.››"));
trsuperobject.biblemap.put("b11_20_43", new trsuperobject(11,20,43,"Keyfi kaçan İsrail Kralı öfkeyle Samiriye'deki sarayına döndü."));
trsuperobject.biblemap.put("b11_21_1-2", new trsuperobject(11,21,1-2,"Yizreelde Samiriye Kralı Ahavın sarayının yanında Yizreelli Navotun bir bağı vardı. Bir gün Ahav, Navota şunu önerdi: ‹‹Bağını bana ver. Sarayıma yakın olduğu için orayı sebze bahçesi olarak kullanmak istiyorum. Karşılığında ben de sana daha iyi bir bağ vereyim, ya da istersen değerini gümüş olarak ödeyeyim.››"));
trsuperobject.biblemap.put("b11_21_3", new trsuperobject(11,21,3,"Ama Navot, ‹‹Atalarımın bana bıraktığı mirası sana vermekten RAB beni esirgesin›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_21_4", new trsuperobject(11,21,4,"‹‹Atalarımın bana bıraktığı mirası sana vermem›› diyen Yizreelli Navotun bu sözlerine sıkılıp öfkelenen Ahav sarayına döndü. Asık bir yüzle yatağına uzanıp hiçbir şey yemedi."));
trsuperobject.biblemap.put("b11_21_5", new trsuperobject(11,21,5,"Karısı İzebel yanına gelip, ‹‹Neden bu kadar sıkılıyorsun? Neden yemek yemiyorsun?›› diye sordu."));
trsuperobject.biblemap.put("b11_21_6", new trsuperobject(11,21,6,"Ahav karısına şöyle karşılık verdi: ‹‹Yizreelli Navota, ‹Sen bağını gümüş karşılığında bana sat, istersen ben de onun yerine sana başka bir bağ vereyim› dedim. Ama o, ‹Hayır, bağımı sana vermem› dedi.››"));
trsuperobject.biblemap.put("b11_21_7", new trsuperobject(11,21,7,"İzebel, ‹‹Sen İsraile böyle mi krallık yapıyorsun?›› dedi, ‹‹Kalk, yemeğini ye, keyfini bozma. Yizreelli Navotun bağını sana ben vereceğim.››"));
trsuperobject.biblemap.put("b11_21_8", new trsuperobject(11,21,8,"İzebel Ahavın mührünü kullanarak onun adına mektuplar yazdı, Navotun yaşadığı kentin ileri gelenleriyle soylularına gönderdi."));
trsuperobject.biblemap.put("b11_21_9", new trsuperobject(11,21,9,"Mektuplarda şunları yazdı:"));
trsuperobject.biblemap.put("b11_21_10", new trsuperobject(11,21,10,"Karşısına da, ‹Navot Tanrıya ve krala sövdü› diyen iki yalancı tanık koyun. Sonra onu dışarı çıkarıp taşlayarak öldürün.››"));
trsuperobject.biblemap.put("b11_21_11", new trsuperobject(11,21,11,"Navotun yaşadığı kentin ileri gelenleriyle soyluları İzebelin gönderdiği mektuplarda yazdıklarını uyguladılar."));
trsuperobject.biblemap.put("b11_21_12", new trsuperobject(11,21,12,"Oruç ilan edip Navotu halkın önüne oturttular."));
trsuperobject.biblemap.put("b11_21_13", new trsuperobject(11,21,13,"Sonra iki kötü adam gelip Navotun karşısına oturdu ve halkın önünde: ‹‹Navot, Tanrıya ve krala sövdü›› diyerek yalan yere tanıklık etti. Bunun üzerine onu kentin dışına çıkardılar ve taşlayarak öldürdüler."));
trsuperobject.biblemap.put("b11_21_14", new trsuperobject(11,21,14,"Sonra İzebele, ‹‹Navot taşlanarak öldürüldü›› diye haber gönderdiler."));
trsuperobject.biblemap.put("b11_21_15", new trsuperobject(11,21,15,"İzebel, Navotun taşlanıp öldürüldüğünü duyar duymaz, Ahava, ‹‹Kalk, Yizreelli Navotun sana gümüş karşılığında satmak istemediği bağını sahiplen›› dedi, ‹‹Çünkü o artık yaşamıyor, öldü.››"));
trsuperobject.biblemap.put("b11_21_16", new trsuperobject(11,21,16,"Ahav, Yizreelli Navotun öldüğünü duyunca, onun bağını almaya gitti."));
trsuperobject.biblemap.put("b11_21_17", new trsuperobject(11,21,17,"O zaman RAB, Tişbeli İlyasa şöyle dedi:"));
trsuperobject.biblemap.put("b11_21_18", new trsuperobject(11,21,18,"‹‹Kalk, Samiriyeli İsrail Kralı Ahavı karşılamaya git. Şu anda Navotun bağındadır. Orayı almaya gitti."));
trsuperobject.biblemap.put("b11_21_19", new trsuperobject(11,21,19,"Ona de ki, RAB şöyle diyor: ‹Hem adamı öldürdün, hem de bağını aldın, değil mi? Navotun kanını köpekler nerede yaladıysa, senin kanını da orada yalayacak.››"));
trsuperobject.biblemap.put("b11_21_20", new trsuperobject(11,21,20,"Ahav, İlyasa, ‹‹Ey düşmanım, beni buldun, değil mi?›› dedi. İlyas şöyle karşılık verdi: ‹‹Evet, buldum. Çünkü sen RABbin gözünde kötü olanı yaparak kendini sattın."));
trsuperobject.biblemap.put("b11_21_21", new trsuperobject(11,21,21,"RAB diyor ki, ‹Seni sıkıntılara sokacak ve yok edeceğim. İsrailde senin soyundan gelen genç yaşlı bütün erkeklerin kökünü kurutacağım."));
trsuperobject.biblemap.put("b11_21_22", new trsuperobject(11,21,22,"Beni öfkelendirip İsraili günaha sürüklediğin için senin ailen de Nevat oğlu Yarovamın ve Ahiya oğlu Baaşanın ailelerinin akıbetine uğrayacak.›"));
trsuperobject.biblemap.put("b11_21_23", new trsuperobject(11,21,23,"‹‹RAB İzebel için de, ‹İzebeli Yizreel Kentinin surları dibinde köpekler yiyecek› diyor."));
trsuperobject.biblemap.put("b11_21_24", new trsuperobject(11,21,24,"‹Ahavın ailesinden kentte ölenleri köpekler, kırda ölenleri yırtıcı kuşlar yiyecek.››"));
trsuperobject.biblemap.put("b11_21_25", new trsuperobject(11,21,25,"Ahav kadar, RABbin gözünde kötü olanı yaparak kendini satan hiç kimse olmadı. Karısı İzebel onu her konuda kışkırtıyordu."));
trsuperobject.biblemap.put("b11_21_26", new trsuperobject(11,21,26,"Ahav RABbin İsrail halkının önünden kovduğu Amorluların her yaptığına uyarak putların ardınca yürüdü ve iğrenç işler yaptı.-"));
trsuperobject.biblemap.put("b11_21_27", new trsuperobject(11,21,27,"Ahav bu sözleri dinledikten sonra, giysilerini yırttı, çula sarınıp oruç tutmaya başladı. Çul içinde yatıp kalkarak, alçakgönüllü bir yol tuttu."));
trsuperobject.biblemap.put("b11_21_28", new trsuperobject(11,21,28,"RAB, Tişbeli İlyasa şöyle dedi:"));
trsuperobject.biblemap.put("b11_21_29", new trsuperobject(11,21,29,"‹‹Ahav'ın önümde ne denli alçakgönüllü davrandığını gördün mü? Bu alçakgönüllülüğünden ötürü yaşamı boyunca ben de onu sıkıntıya sokmayacağım. Ama oğlunun zamanında ailesine sıkıntı vereceğim.››"));
trsuperobject.biblemap.put("b11_22_1", new trsuperobject(11,22,1,"Üç yıl boyunca Aram ile İsrail arasında savaş çıkmadı."));
trsuperobject.biblemap.put("b11_22_2", new trsuperobject(11,22,2,"Üçüncü yıl Yahuda Kralı Yehoşafat, İsrail Kralını görmeye gitti."));
trsuperobject.biblemap.put("b11_22_3", new trsuperobject(11,22,3,"İsrail Kralı Ahav, görevlilerine, ‹‹Ramot-Gilatın bize ait olduğunu bilmiyor musunuz?›› dedi, ‹‹Biz onu Aram Kralından geri almak için bir şey yapmadık.››"));
trsuperobject.biblemap.put("b11_22_4", new trsuperobject(11,22,4,"Sonra Yehoşafata, ‹‹Ramot-Gilata karşı benimle birlikte savaşır mısın?›› diye sordu. Yehoşafat, ‹‹Beni kendin, halkımı halkın, atlarımı atların say›› diye yanıtladı,"));
trsuperobject.biblemap.put("b11_22_5", new trsuperobject(11,22,5,"‹‹Ama önce RABbe danışalım›› diye ekledi."));
trsuperobject.biblemap.put("b11_22_6", new trsuperobject(11,22,6,"İsrail Kralı dört yüz kadar peygamberi toplayıp, ‹‹Ramot-Gilata karşı savaşayım mı, yoksa vaz mı geçeyim?›› diye sordu. Peygamberler, ‹‹Savaş, çünkü Rab kenti senin eline teslim edecek›› diye yanıtladılar."));
trsuperobject.biblemap.put("b11_22_7", new trsuperobject(11,22,7,"Ama Yehoşafat, ‹‹Burada danışabileceğimiz RABbin başka peygamberi yok mu?›› diye sordu."));
trsuperobject.biblemap.put("b11_22_8", new trsuperobject(11,22,8,"İsrail Kralı, ‹‹Yimla oğlu Mikaya adında biri daha var›› diye yanıtladı, ‹‹Onun aracılığıyla RABbe danışabiliriz. Ama ben ondan nefret ederim. Çünkü benimle ilgili hiç iyi peygamberlik etmez, yalnız kötü şeyler söyler.›› Yehoşafat, ‹‹Böyle konuşmaman gerekir, ey kral!›› dedi."));
trsuperobject.biblemap.put("b11_22_9", new trsuperobject(11,22,9,"İsrail Kralı bir görevli çağırıp, ‹‹Hemen Yimla oğlu Mikayayı getir!›› diye buyurdu."));
trsuperobject.biblemap.put("b11_22_10", new trsuperobject(11,22,10,"İsrail Kralı Ahav ile Yahuda Kralı Yehoşafat kral giysileriyle Samiriye Kapısının girişinde, harman yerine konan tahtlarında oturuyorlardı. Bütün peygamberler de onların önünde peygamberlik ediyordu."));
trsuperobject.biblemap.put("b11_22_11", new trsuperobject(11,22,11,"Kenaana oğlu Sidkiya, yaptığı demir boynuzları göstererek şöyle dedi: ‹‹RAB diyor ki, ‹Aramlıları yok edinceye dek onları bu boynuzlarla vuracaksın.››"));
trsuperobject.biblemap.put("b11_22_12", new trsuperobject(11,22,12,"Öteki peygamberlerin hepsi de aynı şeyi söylediler: ‹‹Ramot-Gilata saldır, kazanacaksın! Çünkü RAB onları senin eline teslim edecek.››"));
trsuperobject.biblemap.put("b11_22_13", new trsuperobject(11,22,13,"Mikayayı çağırmaya giden görevli ona, ‹‹Bak! Peygamberler bir ağızdan kral için olumlu şeyler söylüyorlar›› dedi, ‹‹Rica ederim, senin sözün de onlarınkine uygun olsun; olumlu bir şey söyle.››"));
trsuperobject.biblemap.put("b11_22_14", new trsuperobject(11,22,14,"Mikaya, ‹‹Yaşayan RABbin hakkı için, RAB bana ne derse onu söyleyeceğim›› diye karşılık verdi."));
trsuperobject.biblemap.put("b11_22_15", new trsuperobject(11,22,15,"Mikaya gelince kral, ‹‹Mikaya, Ramot-Gilata karşı savaşa gidelim mi, yoksa vaz mı geçelim?›› diye sordu. Mikaya, ‹‹Saldır, kazanacaksın! Çünkü RAB onları senin eline teslim edecek›› diye yanıtladı."));
trsuperobject.biblemap.put("b11_22_16", new trsuperobject(11,22,16,"Bunun üzerine kral, ‹‹RABbin adına bana gerçeğin dışında bir şey söylemeyeceğine ilişkin sana kaç kez ant içireyim?›› diye sordu."));
trsuperobject.biblemap.put("b11_22_17", new trsuperobject(11,22,17,"Mikaya şöyle karşılık verdi: ‹‹İsraillileri dağlara dağılmış çobansız koyunlar gibi gördüm. RAB, ‹Bunların sahibi yok. Herkes güvenlik içinde evine dönsün› dedi.››"));
trsuperobject.biblemap.put("b11_22_18", new trsuperobject(11,22,18,"İsrail Kralı Yehoşafata, ‹‹Benimle ilgili iyi peygamberlik etmez, hep kötü şeyler söyler dememiş miydim?›› dedi."));
trsuperobject.biblemap.put("b11_22_19", new trsuperobject(11,22,19,"Mikaya konuşmasını sürdürdü: ‹‹Öyleyse RABbin sözünü dinle! Gördüm ki, RAB tahtında oturuyor, bütün göksel varlıklar da sağında, solunda duruyordu."));
trsuperobject.biblemap.put("b11_22_20", new trsuperobject(11,22,20,"RAB sordu: ‹Ramot-Gilata saldırıp ölsün diye Ahavı kim kandıracak?› ‹‹Kimi şöyle, kimi böyle derken,"));
trsuperobject.biblemap.put("b11_22_21", new trsuperobject(11,22,21,"bir ruh çıkıp RABbin önünde durdu ve, ‹Ben onu kandıracağım› dedi. ‹‹RAB, ‹Nasıl?› diye sordu."));
trsuperobject.biblemap.put("b11_22_22", new trsuperobject(11,22,22,"‹‹Ruh, ‹Aldatıcı ruh olarak gidip Ahavın bütün peygamberlerine yalan söyleteceğim› diye karşılık verdi. ‹‹RAB, ‹Onu kandırmayı başaracaksın!› dedi, ‹Git, dediğini yap.›"));
trsuperobject.biblemap.put("b11_22_23", new trsuperobject(11,22,23,"‹‹İşte RAB bütün bu peygamberlerin ağzına aldatıcı bir ruh koydu. Çünkü sana kötülük etmeye karar verdi.››"));
trsuperobject.biblemap.put("b11_22_24", new trsuperobject(11,22,24,"Kenaana oğlu Sidkiya yaklaşıp Mikayanın yüzüne bir tokat attı. ‹‹RABbin Ruhu nasıl benden çıkıp da seninle konuştu?›› dedi."));
trsuperobject.biblemap.put("b11_22_25", new trsuperobject(11,22,25,"Mikaya, ‹‹Gizlenmek için bir iç odaya girdiğin gün göreceksin›› diye yanıtladı."));
trsuperobject.biblemap.put("b11_22_26", new trsuperobject(11,22,26,"Bunun üzerine İsrail Kralı, ‹‹Mikayayı kentin yöneticisi Amona ve kralın oğlu Yoaşa götürün›› dedi,"));
trsuperobject.biblemap.put("b11_22_27", new trsuperobject(11,22,27,"‹‹Ben güvenlik içinde dönünceye dek bu adamı cezaevinde tutmalarını, ona su ve ekmekten başka bir şey vermemelerini söyleyin!››"));
trsuperobject.biblemap.put("b11_22_28", new trsuperobject(11,22,28,"Mikaya, ‹‹Eğer sen güvenlik içinde dönersen, RAB benim aracılığımla konuşmamış demektir›› dedi ve, ‹‹Herkes bunu duysun!›› diye ekledi."));
trsuperobject.biblemap.put("b11_22_29", new trsuperobject(11,22,29,"İsrail Kralı Ahavla Yahuda Kralı Yehoşafat Ramot-Gilata saldırmak için yola çıktılar."));
trsuperobject.biblemap.put("b11_22_30", new trsuperobject(11,22,30,"İsrail Kralı, Yehoşafata, ‹‹Ben kılık değiştirip savaşa öyle gireceğim, ama sen kral giysilerini giy›› dedi. Böylece İsrail Kralı kılığını değiştirip savaşa girdi."));
trsuperobject.biblemap.put("b11_22_31", new trsuperobject(11,22,31,"Aram Kralı, savaş arabalarının otuz iki komutanına, ‹‹İsrail Kralı dışında, büyük küçük hiç kimseye saldırmayın!›› diye buyruk vermişti."));
trsuperobject.biblemap.put("b11_22_32", new trsuperobject(11,22,32,"Savaş arabalarının komutanları Yehoşafatı görünce, İsrail Kralı sanıp saldırmak için ona döndüler. Yehoşafat yakarmaya başladı."));
trsuperobject.biblemap.put("b11_22_33", new trsuperobject(11,22,33,"Komutanlar onun İsrail Kralı olmadığını anlayınca peşini bıraktılar."));
trsuperobject.biblemap.put("b11_22_34", new trsuperobject(11,22,34,"O sırada bir asker rasgele attığı bir okla İsrail Kralını zırhının parçalarının birleştiği yerden vurdu. Kral arabacısına, ‹‹Dönüp beni savaş alanından çıkar, yaralandım›› dedi."));
trsuperobject.biblemap.put("b11_22_35", new trsuperobject(11,22,35,"Savaş o gün şiddetlendi. İsrail Kralı, arabasında Aramlılara karşı akşama kadar dayandı ve akşamleyin öldü. Yarasından akan kanlar arabasının içinde kaldı."));
trsuperobject.biblemap.put("b11_22_36", new trsuperobject(11,22,36,"Güneş batarken ordugahta, ‹‹Herkes kendi kentine, ülkesine dönsün!›› diye bağırdılar."));
trsuperobject.biblemap.put("b11_22_37", new trsuperobject(11,22,37,"Kral ölmüştü. Onu Samiriyeye getirip orada gömdüler."));
trsuperobject.biblemap.put("b11_22_38", new trsuperobject(11,22,38,"Arabası fahişelerin yıkandığı Samiriye Havuzunun kenarında temizlenirken RABbin sözü uyarınca köpekler kanını yaladı."));
trsuperobject.biblemap.put("b11_22_39", new trsuperobject(11,22,39,"Ahavın krallığı dönemindeki öteki olaylar, bütün yaptıkları, yaptırdığı fildişi süslemeli saray ve bütün kentler İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_22_40", new trsuperobject(11,22,40,"Ahav ölüp atalarına kavuşunca yerine oğlu Ahazya kral oldu."));
trsuperobject.biblemap.put("b11_22_41", new trsuperobject(11,22,41,"İsrail Kralı Ahavın krallığının dördüncü yılında Asa oğlu Yehoşafat Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b11_22_42", new trsuperobject(11,22,42,"Yehoşafat otuz beş yaşında kral oldu ve Yeruşalimde yirmi beş yıl krallık yaptı. Annesi Şilhinin kızı Azuvaydı."));
trsuperobject.biblemap.put("b11_22_43", new trsuperobject(11,22,43,"Babası Asanın bütün yollarını izleyen ve bunlardan sapmayan Yehoşafat RABbin gözünde doğru olanı yaptı. Ancak alışılagelen tapınma yerleri kaldırılmadı. Halk hâlâ oralarda kurban kesip buhur yakıyordu."));
trsuperobject.biblemap.put("b11_22_44", new trsuperobject(11,22,44,"Yehoşafat İsrail Kralı ile barış yaptı."));
trsuperobject.biblemap.put("b11_22_45", new trsuperobject(11,22,45,"Yehoşafatın krallığı dönemindeki öteki olaylar, başarıları ve savaşları Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b11_22_46", new trsuperobject(11,22,46,"Yehoşafat babası Asanın döneminden kalan, putperest törenlerinde fuhuş yapan kadın ve erkeklerin hepsini ülkeden süpürüp attı."));
trsuperobject.biblemap.put("b11_22_47", new trsuperobject(11,22,47,"Edomda kral yoktu, yerine bir vekil bakıyordu."));
trsuperobject.biblemap.put("b11_22_48", new trsuperobject(11,22,48,"Yehoşafat altın almak için Ofire gitmek üzere ticaret gemileri yaptırdı. Ancak gemiler oraya gidemeden Esyon-Geverde parçalandı."));
trsuperobject.biblemap.put("b11_22_49", new trsuperobject(11,22,49,"O zaman Ahav oğlu Ahazya, Yehoşafata, ‹‹Benim adamlarım gemilerde seninkilerle birlikte gitsinler›› dedi. Ama Yehoşafat kabul etmedi."));
trsuperobject.biblemap.put("b11_22_50", new trsuperobject(11,22,50,"Yehoşafat ölüp atalarına kavuştu ve atası Davutun Kentinde atalarının yanına gömüldü. Yerine oğlu Yehoram kral oldu."));
trsuperobject.biblemap.put("b11_22_51", new trsuperobject(11,22,51,"Yahuda Kralı Yehoşafatın krallığının on yedinci yılında Ahav oğlu Ahazya Samiriyede İsrail Kralı oldu. İki yıl krallık yaptı."));
trsuperobject.biblemap.put("b11_22_52", new trsuperobject(11,22,52,"RABbin gözünde kötü olanı yaptı. Babasının, annesinin ve İsraili günaha sürükleyen Nevat oğlu Yarovamın yolunda yürüdü."));
trsuperobject.biblemap.put("b11_22_53", new trsuperobject(11,22,53,"Baal'a hizmet edip taptı. Babasının her yaptığına uyarak İsrail'in Tanrısı RAB'bi öfkelendirdi."));

