trsuperobject.biblemap.put("b12_4_8", new trsuperobject(12,4,8,"Elişa bir gün Şuneme gitti. Orada zengin bir kadın vardı. Elişayı yemeğe alıkoydu. O günden sonra Elişa ne zaman Şuneme gitse, yemek için oraya uğradı."));
trsuperobject.biblemap.put("b12_4_9", new trsuperobject(12,4,9,"Kadın kocasına, ‹‹Bize sık sık gelen bu adamın kutsal bir Tanrı adamı olduğunu anladım›› dedi,"));
trsuperobject.biblemap.put("b12_4_10", new trsuperobject(12,4,10,"‹‹Gel, damda onun için küçük bir oda yapalım; içine yatak, masa, sandalye, bir de kandil koyalım. Bize geldiğinde orada kalsın.››"));
trsuperobject.biblemap.put("b12_4_11", new trsuperobject(12,4,11,"Bir gün Elişa geldi, yukarı odaya çıkıp uzandı."));
trsuperobject.biblemap.put("b12_4_12", new trsuperobject(12,4,12,"Uşağı Gehaziye, ‹‹Şunemli kadını çağır›› dedi. Gehazi kadını çağırdı. Kadın gelince,"));
trsuperobject.biblemap.put("b12_4_13", new trsuperobject(12,4,13,"Elişa Gehaziye şöyle dedi: ‹‹Ona de ki, ‹Bizim için katlandığın bunca zahmetlere karşılık ne yapabilirim? Senin için kralla ya da ordu komutanıyla konuşayım mı?›› Kadın, ‹‹Ben halkımın arasında mutlu yaşıyorum›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_4_14", new trsuperobject(12,4,14,"Elişa, ‹‹Öyleyse ne yapabilirim?›› diye sordu. Gehazi, ‹‹Kadının oğlu yok, kocası da yaşlı›› diye yanıtladı."));
trsuperobject.biblemap.put("b12_4_15", new trsuperobject(12,4,15,"Bunun üzerine Elişa, ‹‹Kadını çağır›› dedi. Gehazi kadını çağırdı. Kadın gelip kapının eşiğinde durdu."));
trsuperobject.biblemap.put("b12_4_16", new trsuperobject(12,4,16,"Elişa, kadına, ‹‹Gelecek yıl bu zaman kucağında bir oğlun olacak›› dedi. Kadın, ‹‹Olamaz, efendim!›› diye karşılık verdi, ‹‹Sen ki bir Tanrı adamısın, lütfen kuluna yalan söyleme!››"));
trsuperobject.biblemap.put("b12_4_17", new trsuperobject(12,4,17,"Ama kadın gebe kaldı ve bir yıl sonra, Elişanın söylediği günlerde bir oğul doğurdu."));
trsuperobject.biblemap.put("b12_4_18", new trsuperobject(12,4,18,"Çocuk büyüdü. Bir gün orakçıların başında bulunan babasının yanına gitti."));
trsuperobject.biblemap.put("b12_4_19", new trsuperobject(12,4,19,"‹‹Başım ağrıyor, başım!›› diye bağırmaya başladı. Babası uşağına, ‹‹Onu annesine götür›› dedi."));
trsuperobject.biblemap.put("b12_4_20", new trsuperobject(12,4,20,"Uşak çocuğu alıp annesine götürdü. Çocuk öğlene kadar annesinin dizlerinde yattıktan sonra öldü."));
trsuperobject.biblemap.put("b12_4_21", new trsuperobject(12,4,21,"Annesi onu yukarı çıkardı, Tanrı adamının yatağına yatırdı, sonra kapıyı kapayıp dışarıya çıktı."));
trsuperobject.biblemap.put("b12_4_22", new trsuperobject(12,4,22,"Kocasını çağırıp şöyle dedi: ‹‹Lütfen bir eşekle birlikte uşaklarından birini bana gönder. Tanrı adamının yanına gitmeliyim. Hemen dönerim.››"));
trsuperobject.biblemap.put("b12_4_23", new trsuperobject(12,4,23,"Kocası, ‹‹Neden bugün gidiyorsun?›› dedi, ‹‹Ne Yeni Ay, ne de Şabat bugün.›› Kadın, ‹‹Zarar yok›› karşılığını verdi."));
trsuperobject.biblemap.put("b12_4_24", new trsuperobject(12,4,24,"Eşeğe palan vurup uşağına, ‹‹Haydi yürü, ben sana söylemedikçe yavaşlama›› dedi."));
trsuperobject.biblemap.put("b12_4_25", new trsuperobject(12,4,25,"Karmel Dağına varıp Tanrı adamının yanına çıktı. Tanrı adamı, kadını uzaktan görünce, uşağı Gehaziye, ‹‹Bak, Şunemli kadın geliyor!›› dedi,"));
trsuperobject.biblemap.put("b12_4_26", new trsuperobject(12,4,26,"‹‹Haydi koş, onu karşıla, ‹Nasılsın, kocanla oğlun nasıllar?› diye sor.›› Kadın Gehaziye, ‹‹Herkes iyi›› dedi."));
trsuperobject.biblemap.put("b12_4_27", new trsuperobject(12,4,27,"Kadın dağa çıkıp Tanrı adamının yanına varınca, onun ayaklarına sarıldı. Gehazi kadını uzaklaştırmak istediyse de Tanrı adamı, ‹‹Kadını rahat bırak!›› dedi, ‹‹Çünkü acı çekiyor. RAB bunun nedenini benden gizledi, açıklamadı.››"));
trsuperobject.biblemap.put("b12_4_28", new trsuperobject(12,4,28,"Kadın ona, ‹‹Efendim, ben senden çocuk istedim mi?›› dedi, ‹‹Beni umutlandırma demedim mi?››"));
trsuperobject.biblemap.put("b12_4_29", new trsuperobject(12,4,29,"Elişa Gehaziye, ‹‹Hemen kemerini kuşan, değneğimi al, koş›› dedi, ‹‹Biriyle karşılaşırsan selam verme, biri seni selamlarsa karşılık verme. Git, değneğimi çocuğun yüzüne tut.››"));
trsuperobject.biblemap.put("b12_4_30", new trsuperobject(12,4,30,"Çocuğun annesi, ‹‹Yaşayan RABbin adıyla başın üzerine ant içerim ki, senden ayrılmayacağım›› dedi. Sonra Gehaziyle birlikte yola çıktı."));
trsuperobject.biblemap.put("b12_4_31", new trsuperobject(12,4,31,"Gehazi önden gidip değneği çocuğun yüzüne tuttu, ama ne bir ses vardı, ne de bir yanıt. Bunun üzerine Gehazi geri dönüp Elişayı karşıladı ve ona, ‹‹Çocuk dirilmedi›› diye haber verdi."));
trsuperobject.biblemap.put("b12_4_32", new trsuperobject(12,4,32,"Elişa eve vardığında, çocuğu yatağında ölü buldu."));
trsuperobject.biblemap.put("b12_4_33", new trsuperobject(12,4,33,"İçeri girdi, kapıyı kapayıp RABbe yalvarmaya başladı."));
trsuperobject.biblemap.put("b12_4_34", new trsuperobject(12,4,34,"Sonra ağzı çocuğun ağzının, gözleriyle elleri de çocuğun gözleriyle ellerinin üzerine gelecek biçimde yatağa, çocuğun üzerine kapandı. Çocuğun bedeni ısınmaya başladı."));
trsuperobject.biblemap.put("b12_4_35", new trsuperobject(12,4,35,"Elişa kalkıp odanın içinde sağa sola gezindi, sonra yine dönüp çocuğun üzerine kapandı. Çocuk yedi kez aksırdı ve gözlerini açtı."));
trsuperobject.biblemap.put("b12_4_36", new trsuperobject(12,4,36,"Elişa Gehaziye, ‹‹Şunemli kadını çağır›› diye seslendi. Gehazi kadını çağırdı. Kadın gelince, Elişa, ‹‹Al oğlunu›› dedi."));
trsuperobject.biblemap.put("b12_4_37", new trsuperobject(12,4,37,"Kadın Elişanın ayaklarına kapandı, yerlere kadar eğildi, sonra çocuğunu alıp gitti."));
trsuperobject.biblemap.put("b12_4_38", new trsuperobject(12,4,38,"Elişa Gilgala döndü. Ülkede kıtlık vardı. Elişa bir peygamber topluluğuyla otururken uşağına, ‹‹Büyük tencereyi ateşe koy, peygamberlere çorba pişir›› dedi."));
trsuperobject.biblemap.put("b12_4_39", new trsuperobject(12,4,39,"Biri ot toplamak için tarlaya gitti ve yabanıl bir bitki buldu. Bitkiden bir etek dolusu yaban kabağı topladı, getirip tencereye doğradı. Bunların ne olduğunu kimse bilmiyordu."));
trsuperobject.biblemap.put("b12_4_40", new trsuperobject(12,4,40,"Çorba yenmek üzere boşaltıldı. Ama adamlar çorbayı tadar tatmaz, ‹‹Ey Tanrı adamı, zehirli bu!›› diye bağırdılar ve yiyemediler."));
trsuperobject.biblemap.put("b12_4_41", new trsuperobject(12,4,41,"Elişa, ‹‹Biraz un getirin›› dedi. Unu tencereye atıp, ‹‹Koy önlerine, yesinler›› dedi. Tencerede zararlı bir şey kalmadı."));
trsuperobject.biblemap.put("b12_4_42", new trsuperobject(12,4,42,"Baal-Şalişadan bir adam geldi. Tanrı adamına o yıl ilk biçilen arpadan yapılmış yirmi ekmekle taze buğday başağı getirdi. Elişa uşağına, ‹‹Bunları halka dağıt, yesinler›› dedi."));
trsuperobject.biblemap.put("b12_4_43", new trsuperobject(12,4,43,"Uşak, ‹‹Nasıl olur, bu yüz kişinin önüne konur mu?›› diye sordu. Elişa, ‹‹Halka dağıt, yesinler›› diye karşılık verdi, ‹‹Çünkü RAB diyor ki, ‹Yiyecekler, birazı da artacak.››"));
trsuperobject.biblemap.put("b12_4_44", new trsuperobject(12,4,44,"Bunun üzerine uşak yiyecekleri halkın önüne koydu. RAB'bin sözü uyarınca halk yedi, birazı da arttı."));
trsuperobject.biblemap.put("b12_5_1", new trsuperobject(12,5,1,"Aram Kralının ordu komutanı Naaman efendisinin gözünde saygın, değerli bir adamdı. Çünkü RAB onun aracılığıyla Aramlıları zafere ulaştırmıştı. Naaman yiğit bir askerdi, ama bir deri hastalığına yakalanmıştı."));
trsuperobject.biblemap.put("b12_5_2", new trsuperobject(12,5,2,"Aramlılar düzenledikleri akınlar sırasında İsrailden küçük bir kızı tutsak almışlardı. Bu kız Naamanın karısının hizmetine verilmişti."));
trsuperobject.biblemap.put("b12_5_3", new trsuperobject(12,5,3,"Bir gün hanımına, ‹‹Keşke efendim Samiriyedeki peygamberin yanına gitse! Peygamber onu deri hastalığından kurtarırdı›› dedi."));
trsuperobject.biblemap.put("b12_5_4", new trsuperobject(12,5,4,"Naaman gidip İsrailli kızın söylediklerini efendisi krala anlattı."));
trsuperobject.biblemap.put("b12_5_5", new trsuperobject(12,5,5,"Aram Kralı şöyle karşılık verdi: ‹‹Kalk git, seninle İsrail Kralına bir mektup göndereceğim.›› Naaman yanına on talant gümüş, altı bin şekelfç altın ve on takım giysi alıp gitti."));
trsuperobject.biblemap.put("b12_5_6", new trsuperobject(12,5,6,"Mektubu İsrail Kralına verdi. Mektupta şunlar yazılıydı: ‹‹Bu mektupla birlikte sana kulum Naamanı gönderiyorum. Onu deri hastalığından kurtarmanı dilerim.››"));
trsuperobject.biblemap.put("b12_5_7", new trsuperobject(12,5,7,"İsrail Kralı mektubu okuyunca giysilerini yırtıp şöyle haykırdı: ‹‹Ben Tanrı mıyım, can alıp can vereyim? Nasıl bana bir adam gönderip onu deri hastalığından kurtar der? Görüyor musunuz, açıkça benimle kavga çıkarmaya çalışıyor!››"));
trsuperobject.biblemap.put("b12_5_8", new trsuperobject(12,5,8,"İsrail Kralının giysilerini yırttığını duyan Tanrı adamı Elişa ona şu haberi gönderdi: ‹‹Neden giysilerini yırttın? Adam bana gelsin, İsrailde bir peygamber olduğunu anlasın!››"));
trsuperobject.biblemap.put("b12_5_9", new trsuperobject(12,5,9,"Böylece Naaman atları ve savaş arabalarıyla birlikte gidip Elişanın evinin kapısı önünde durdu."));
trsuperobject.biblemap.put("b12_5_10", new trsuperobject(12,5,10,"Elişa ona şu haberi gönderdi: ‹‹Git, Şeria Irmağında yedi kez yıkan. Tenin eski halini alacak, tertemiz olacaksın.››"));
trsuperobject.biblemap.put("b12_5_11", new trsuperobject(12,5,11,"Gelgelelim Naaman oradan öfkeyle ayrıldı. ‹‹Sandım ki dışarı çıkıp yanıma gelecek, Tanrısı RABbi adıyla çağırarak eliyle hastalıklı derime dokunup beni iyileştirecek›› dedi,"));
trsuperobject.biblemap.put("b12_5_12", new trsuperobject(12,5,12,"‹‹Şamın Avana ve Farpar ırmakları İsrailin bütün ırmaklarından daha iyi değil mi? Oralarda yıkanıp paklanamaz mıydım sanki?›› Sonra öfkeyle dönüp gitti."));
trsuperobject.biblemap.put("b12_5_13", new trsuperobject(12,5,13,"Naamanın görevlileri yanına varıp, ‹‹Efendim, peygamber senden daha zor bir şey istemiş olsaydı, yapmaz mıydın?›› dediler, ‹‹Oysa o sana sadece, ‹Yıkan, temizlen› diyor.››"));
trsuperobject.biblemap.put("b12_5_14", new trsuperobject(12,5,14,"Bunun üzerine Naaman Tanrı adamının sözü uyarınca gidip Şeria Irmağında yedi kez suya daldı. Teni eski haline döndü, bebek teni gibi tertemiz oldu."));
trsuperobject.biblemap.put("b12_5_15", new trsuperobject(12,5,15,"Naaman adamlarıyla birlikte Tanrı adamının yanına döndü. Onun önünde durup şöyle dedi: ‹‹Şimdi anladım ki, İsrail dışında dünyanın hiçbir yerinde Tanrı yoktur. Lütfen, bu kulunun armağanını kabul et.››"));
trsuperobject.biblemap.put("b12_5_16", new trsuperobject(12,5,16,"Elişa, ‹‹Hizmetinde olduğum yaşayan RABbin adıyla ant içerim ki, hiçbir şey alamam›› diye karşılık verdi. Naaman direttiyse de, Elişa almak istemedi."));
trsuperobject.biblemap.put("b12_5_17", new trsuperobject(12,5,17,"Bunun üzerine Naaman, ‹‹Madem armağan istemiyorsun, öyleyse buradan iki katır yükü toprak almama izin ver›› dedi, ‹‹Çünkü bu kulun artık RABbin dışında başka ilahlara yakmalık sunu ve kurban sunmayacaktır."));
trsuperobject.biblemap.put("b12_5_18", new trsuperobject(12,5,18,"Ama RAB kulunu bir konuda bağışlasın. Efendim tapınmak için Rimmon Tapınağına girip kendisine eşlik etmemi isteyince, tapınakta onunla birlikte yere kapandığımda RAB bu kulunu bağışlasın.››"));
trsuperobject.biblemap.put("b12_5_19", new trsuperobject(12,5,19,"Elişa ona, ‹‹Esenlikle git›› dedi. Naaman oradan ayrılıp biraz uzaklaşınca,"));
trsuperobject.biblemap.put("b12_5_20", new trsuperobject(12,5,20,"Tanrı adamı Elişanın uşağı Gehazi, ‹‹Efendim, Aramlı Naamana çok yumuşak davrandı; getirdiği armağanları kabul etmedi›› dedi, ‹‹Yaşayan RABbin hakkı için, peşinden koşup ondan bir şey alacağım.››"));
trsuperobject.biblemap.put("b12_5_21", new trsuperobject(12,5,21,"Böylece Gehazi Naamanın peşine düştü. Naaman ardından birinin koştuğunu görünce, arabasından inip onu karşıladı ve, ‹‹Ne oldu?›› diye sordu."));
trsuperobject.biblemap.put("b12_5_22", new trsuperobject(12,5,22,"Gehazi, ‹‹Bir şey yok›› dedi, ‹‹Yalnız efendimin bir ricası var. Biraz önce Efrayimin dağlık bölgesinden iki genç peygamber geldi. Efendim onlara bir talant gümüşle iki takım giysi vermen için beni gönderdi.››"));
trsuperobject.biblemap.put("b12_5_23", new trsuperobject(12,5,23,"Naaman, ‹‹Lütfen iki talant al!›› dedi ve ısrarla iki talant gümüşü iki torbaya koyup bağladı. Ayrıca iki uşağına da birer takım giysi verdi. Uşaklar Gehazinin önüsıra bunları taşıdılar."));
trsuperobject.biblemap.put("b12_5_24", new trsuperobject(12,5,24,"Tepeye varınca Gehazi eşyaları ellerinden alıp eve koydu, adamları da geri gönderdi."));
trsuperobject.biblemap.put("b12_5_25", new trsuperobject(12,5,25,"Sonra gidip efendisi Elişanın huzuruna çıktı. Elişa, ‹‹Neredeydin, Gehazi?›› diye sordu. Gehazi, ‹‹Kulun hiçbir yere gitmedi›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_5_26", new trsuperobject(12,5,26,"Bunun üzerine Elişa, ‹‹O adam arabasından inip seni karşılarken ruhum seninle değil miydi?›› diye sordu, ‹‹Şimdi gümüş ya da giysi, zeytinlik, bağ, koyun, sığır, erkek ve kadın köle almanın zamanı mı?"));
trsuperobject.biblemap.put("b12_5_27", new trsuperobject(12,5,27,"Bu yüzden Naaman'ın deri hastalığı sonsuza dek senin ve soyunun üzerinde kalacak.›› Böylece Gehazi Elişa'nın huzurundan kar gibi beyaz bir deri hastalığıyla ayrıldı."));
trsuperobject.biblemap.put("b12_6_1", new trsuperobject(12,6,1,"Bir gün peygamber topluluğu Elişaya, ‹‹Bak, yaşadığımız yer bize küçük geliyor›› dedi,"));
trsuperobject.biblemap.put("b12_6_2", new trsuperobject(12,6,2,"‹‹Lütfen izin ver, Şeria Irmağı kıyısına gidelim, ağaç kesip kendimize ev yapalım.›› Elişa, ‹‹Gidin›› dedi."));
trsuperobject.biblemap.put("b12_6_3", new trsuperobject(12,6,3,"Peygamberlerden biri, ‹‹Lütfen kullarınla birlikte sen de gel›› dedi. Elişa, ‹‹Olur, gelirim›› diye karşılık verdi"));
trsuperobject.biblemap.put("b12_6_4", new trsuperobject(12,6,4,"ve onlarla birlikte gitti. Şeria Irmağı kıyısına varınca ağaç kesmeye başladılar."));
trsuperobject.biblemap.put("b12_6_5", new trsuperobject(12,6,5,"Biri ağaç keserken balta demirini suya düşürdü. ‹‹Eyvah, efendim! Onu ödünç almıştım›› diye bağırdı."));
trsuperobject.biblemap.put("b12_6_6", new trsuperobject(12,6,6,"Tanrı adamı, ‹‹Nereye düştü?›› diye sordu. Adam ona demirin düştüğü yeri gösterdi. Elişa bir dal kesip oraya atınca, balta demiri su yüzüne çıktı."));
trsuperobject.biblemap.put("b12_6_7", new trsuperobject(12,6,7,"Elişa, ‹‹Al onu!›› dedi. Adam elini uzatıp balta demirini aldı."));
trsuperobject.biblemap.put("b12_6_8", new trsuperobject(12,6,8,"Aram Kralı İsraille savaş halindeydi. Görevlilerine danıştıktan sonra, ‹‹Ordugahımı kuracak bir yer seçtim›› dedi."));
trsuperobject.biblemap.put("b12_6_9", new trsuperobject(12,6,9,"Tanrı adamı Elişa, İsrail Kralına şu haberi gönderdi: ‹‹Sakın oradan geçmeyin, çünkü Aramlılar oraya doğru iniyorlar.››"));
trsuperobject.biblemap.put("b12_6_10", new trsuperobject(12,6,10,"İsrail Kralı adam gönderip oradaki durumu denetledi. Böylece Tanrı adamı İsrail Kralını birkaç kez uyardı. Kral da önlem aldı."));
trsuperobject.biblemap.put("b12_6_11", new trsuperobject(12,6,11,"Bu durum Aram Kralını çok öfkelendirdi. Görevlilerini çağırıp, ‹‹İçinizden hanginizin İsrail Kralından yana olduğunu söylemeyecek misiniz?›› dedi."));
trsuperobject.biblemap.put("b12_6_12", new trsuperobject(12,6,12,"Görevlilerden biri, ‹‹Hiçbirimiz, efendimiz kral›› diye karşılık verdi, ‹‹Yalnız İsrailde yaşayan Peygamber Elişa senin yatak odanda söylediklerini bile İsrail Kralına bildiriyor.››"));
trsuperobject.biblemap.put("b12_6_13", new trsuperobject(12,6,13,"Aram Kralı şöyle buyurdu: ‹‹Gidip onun nerede olduğunu öğrenin. Adam gönderip onu yakalayacağım.›› Elişanın Dotanda olduğu bildirilince,"));
trsuperobject.biblemap.put("b12_6_14", new trsuperobject(12,6,14,"kral oraya atlılar, savaş arabaları ve büyük bir kuvvet gönderdi. Geceleyin varıp kenti kuşattılar."));
trsuperobject.biblemap.put("b12_6_15", new trsuperobject(12,6,15,"Tanrı adamının uşağı erkenden kalktı. Dışarıya çıkınca kentin askerler, atlılar ve savaş arabalarınca kuşatıldığını gördü. Dönüp Elişaya, ‹‹Eyvah, efendim, ne yapacağız?›› diye sordu."));
trsuperobject.biblemap.put("b12_6_16", new trsuperobject(12,6,16,"Elişa, ‹‹Korkma, çünkü bizim yandaşlarımız onlarınkinden daha çok›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_6_17", new trsuperobject(12,6,17,"Sonra şöyle dua etti: ‹‹Ya RAB, lütfen onun gözlerini aç, görsün!›› RAB uşağın gözlerini açtı. Uşak Elişanın çevresindeki dağların atlılarla, ateşten savaş arabalarıyla dolu olduğunu gördü."));
trsuperobject.biblemap.put("b12_6_18", new trsuperobject(12,6,18,"Aramlılar kendisine doğru ilerleyince Elişa RABbe şöyle yalvardı: ‹‹Ya RAB, lütfen bu halkı kör et.›› RAB Elişanın yalvarışını duydu ve onları kör etti."));
trsuperobject.biblemap.put("b12_6_19", new trsuperobject(12,6,19,"Bunun üzerine Elişa onlara, ‹‹Yanlış yoldasınız›› dedi, ‹‹Aradığınız kent bu değil. Beni izleyin, sizi aradığınız adama götüreyim.›› Sonra onları Samiriyeye götürdü."));
trsuperobject.biblemap.put("b12_6_20", new trsuperobject(12,6,20,"Samiriyeye girdiklerinde Elişa şöyle dua etti: ‹‹Ya RAB, bu adamların gözlerini aç, görsünler.›› RAB gözlerini açınca adamlar Samiriyenin ortasında olduklarını anladılar."));
trsuperobject.biblemap.put("b12_6_21", new trsuperobject(12,6,21,"İsrail Kralı adamları görünce Elişaya, ‹‹Onları öldüreyim mi? Öldüreyim mi, baba?›› dedi."));
trsuperobject.biblemap.put("b12_6_22", new trsuperobject(12,6,22,"Elişa, ‹‹Hayır, öldürme›› diye karşılık verdi, ‹‹Kendi kılıç ve yayınla tutsak aldığın insanları nasıl öldürürsün. Önlerine yiyecek içecek bir şeyler koy, yiyip içtikten sonra izin ver, krallarına dönsünler.››"));
trsuperobject.biblemap.put("b12_6_23", new trsuperobject(12,6,23,"Bunun üzerine İsrail Kralı adamlara büyük bir şölen verdi, yedirip içirdikten sonra da onları krallarına gönderdi. Aramlı akıncılar bir daha İsrail topraklarına ayak basmadılar."));
trsuperobject.biblemap.put("b12_6_24", new trsuperobject(12,6,24,"Bir süre sonra, Aram Kralı Ben-Hadat bütün ordusunu toplayıp İsraile girdi ve Samiriyeyi kuşattı."));
trsuperobject.biblemap.put("b12_6_25", new trsuperobject(12,6,25,"Samiriyede büyük bir kıtlık oldu. Kuşatma sonunda bir eşek kellesinin fiyatı seksen şekel gümüşe, dörtte bir kav güvercin gübresinin fiyatı ise beş şekelfı gümüşe çıktı."));
trsuperobject.biblemap.put("b12_6_26", new trsuperobject(12,6,26,"İsrail Kralı surların üzerinde yürürken, bir kadın, ‹‹Efendim kral, bana yardım et!›› diye seslendi."));
trsuperobject.biblemap.put("b12_6_27", new trsuperobject(12,6,27,"Kral, ‹‹RAB sana yardım etmiyorsa, ben nasıl yardım edebilirim ki?›› diye karşılık verdi, ‹‹Buğday mı, yoksa şarap mı istersin?"));
trsuperobject.biblemap.put("b12_6_28", new trsuperobject(12,6,28,"Derdin ne?›› Kadın şöyle yanıtladı: ‹‹Geçen gün şu kadın bana dedi ki, ‹Oğlunu ver, bugün yiyelim, yarın da benim oğlumu yeriz.›"));
trsuperobject.biblemap.put("b12_6_29", new trsuperobject(12,6,29,"Böylece oğlumu pişirip yedik. Ertesi gün ona, ‹Oğlunu ver de yiyelim› dedim. Ama o, oğlunu gizledi.››"));
trsuperobject.biblemap.put("b12_6_30", new trsuperobject(12,6,30,"Kadının bu sözlerini duyan kral giysilerini yırttı. Surların üzerinde yürürken, halk onun giysilerinin altına çul giydiğini gördü."));
trsuperobject.biblemap.put("b12_6_31", new trsuperobject(12,6,31,"Kral, ‹‹Eğer bugün Şafat oğlu Elişanın başı yerinde kalırsa, Tanrı bana aynısını, hatta daha kötüsünü yapsın!›› dedi."));
trsuperobject.biblemap.put("b12_6_32", new trsuperobject(12,6,32,"Elişa o sırada halkın ileri gelenleriyle birlikte evinde oturuyordu. Kral önden bir haberci gönderdi. Ama daha haberci gelmeden, Elişa ileri gelenlere, ‹‹Görüyor musunuz caniyi?›› dedi, ‹‹Kalkmış, başımı kestirmek için adam gönderiyor! Haberci geldiğinde kapıyı kapayın, onu içeri almayın. Çünkü ardından efendisi kral da gelecek.››"));
trsuperobject.biblemap.put("b12_6_33", new trsuperobject(12,6,33,"Elişa konuşmasını bitirmeden, haberci yanına geldi ve, ‹‹Bu felaket RAB'dendir›› dedi, ‹‹Neden hâlâ RAB'bi bekleyeyim?››"));
trsuperobject.biblemap.put("b12_7_1", new trsuperobject(12,7,1,"Elişa, ‹‹RABbin sözüne kulak verin!›› dedi, ‹‹RAB diyor ki, ‹Yarın bu saatlerde Samiriye Kapısında bir sea ince un da, iki sea arpa da birer şekele satılacak.››"));
trsuperobject.biblemap.put("b12_7_2", new trsuperobject(12,7,2,"Kralın özel yardımcısı olan komutan, Tanrı adamına, ‹‹RAB göklerin kapaklarını açsa bile olacak şey değil bu!›› dedi. Elişa, ‹‹Sen herşeyi gözlerinle göreceksin, ama onlardan hiçbir şey yiyemeyeceksin!›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_7_3", new trsuperobject(12,7,3,"Kent kapısının girişinde deri hastalığına yakalanmış dört adam vardı. Birbirlerine, ‹‹Ne diye ölene dek burada kalalım?›› diyorlardı,"));
trsuperobject.biblemap.put("b12_7_4", new trsuperobject(12,7,4,"‹‹Kente girelim desek, orada kıtlık var, ölürüz; burada kalsak da öleceğiz. Bari gidip Aram ordugahına teslim olalım. Canımızı bağışlarlarsa yaşarız, öldürürlerse de öldürsünler.››"));
trsuperobject.biblemap.put("b12_7_5", new trsuperobject(12,7,5,"Akşam karanlığında kalkıp Aram ordugahına doğru gittiler. Ordugaha yaklaştıklarında, orada kimseyi göremediler."));
trsuperobject.biblemap.put("b12_7_6", new trsuperobject(12,7,6,"Çünkü Rab Aram ordugahında savaş arabalarıyla, atlarıyla yaklaşan büyük bir ordunun çıkardığı seslerin duyulmasını sağlamıştı. Aramlılar da birbirlerine, ‹‹Bakın, İsrail Kralı bize saldırmak için Hitit ve Mısır krallarını kiralamış!›› demişlerdi."));
trsuperobject.biblemap.put("b12_7_7", new trsuperobject(12,7,7,"Böylece, gün batarken çadırlarını, atlarını, eşeklerini bırakıp kaçmışlar, canlarını kurtarmak için ordugahı olduğu gibi bırakmışlardı."));
trsuperobject.biblemap.put("b12_7_8", new trsuperobject(12,7,8,"Deri hastalığına yakalanmış adamlar ordugaha varıp çadırların birine girdiler. Yiyip içtikten sonra oradaki altın, gümüş ve giysileri götürüp gizlediler. Sonra dönüp başka bir çadıra girdiler, orada bulduklarını da götürüp gizlediler."));
trsuperobject.biblemap.put("b12_7_9", new trsuperobject(12,7,9,"Ardından birbirlerine, ‹‹Yaptığımız doğru değil›› dediler, ‹‹Bugün müjde günü. Oysa biz susuyoruz. Gün doğuncaya kadar beklersek, cezaya çarptırılacağımız kesin. Haydi saraya gidip durumu bildirelim.››"));
trsuperobject.biblemap.put("b12_7_10", new trsuperobject(12,7,10,"Böylece gidip kent kapısındaki nöbetçilere seslendiler. ‹‹Aram ordugahına gittik›› dediler, ‹‹Hiç kimseyi göremedik; ne de bir insan sesi duyduk. Yalnızca bağlı atlar, eşekler vardı. Çadırları da olduğu gibi bırakıp gitmişler.››"));
trsuperobject.biblemap.put("b12_7_11", new trsuperobject(12,7,11,"Kapı nöbetçileri haberi duyurdu. Haber kralın sarayına ulaştırıldı."));
trsuperobject.biblemap.put("b12_7_12", new trsuperobject(12,7,12,"Kral gece kalkıp görevlilerine, ‹‹Aramlıların ne tasarladığını size söyleyeyim›› dedi, ‹‹Aç kaldığımızı biliyorlar. Onun için ordugahlarını bırakıp kırda gizlenmişler. Kentin dışına çıktığımızda, bizi canlı yakalayıp kenti ele geçirmeyi düşünüyorlar.››"));
trsuperobject.biblemap.put("b12_7_13", new trsuperobject(12,7,13,"Görevlilerden biri, ‹‹Kentte kalan beş atla birkaç adam gönderelim, o zaman durumu anlarız›› dedi, ‹‹Nasıl olsa gidecek olanlar da burada, kentte kalan nice İsrailli gibi ölüme mahkûm!››"));
trsuperobject.biblemap.put("b12_7_14", new trsuperobject(12,7,14,"Adamlar yanlarına iki atlı araba aldılar. Kral, ‹‹Gidin, ne olduğunu öğrenin›› diyerek onları Aram ordusunun ardından gönderdi."));
trsuperobject.biblemap.put("b12_7_15", new trsuperobject(12,7,15,"Adamlar Şeria Irmağına kadar Aram ordusunu izlediler. Yol baştan sona kadar Aramlıların kaçarken attıkları giysi ve eşyalarla doluydu. Haberciler dönüp krala durumu bildirdiler."));
trsuperobject.biblemap.put("b12_7_16", new trsuperobject(12,7,16,"Bunun üzerine halk kentten çıkıp Aram ordugahını yağmaladı. RABbin dediği gibi, bir sea ince unun da, iki sea arpanın da fiyatı bir şekele düştü."));
trsuperobject.biblemap.put("b12_7_17", new trsuperobject(12,7,17,"Kral özel yardımcısı olan komutanı kentin kapısında bırakmıştı. Halk onu kapının ağzında çiğneyerek öldürdü. Kral Elişanın evine gittiğinde, Tanrı adamı ona olacakları önceden bildirmişti."));
trsuperobject.biblemap.put("b12_7_18", new trsuperobject(12,7,18,"Her şey Tanrı adamının krala dediği gibi oldu. ‹‹Yarın bu saatlerde Samiriye Kapısında bir sea ince un da, iki sea arpa da birer şekele satılacak›› demişti."));
trsuperobject.biblemap.put("b12_7_19", new trsuperobject(12,7,19,"Komutan da Tanrı adamına şöyle karşılık vermişti: ‹‹RAB göklerin kapaklarını açsa bile, olacak şey değil bu!›› Elişa, ‹‹Sen her şeyi gözlerinle görecek, ama onlardan hiçbir şey yiyemeyeceksin!›› demişti."));
trsuperobject.biblemap.put("b12_7_20", new trsuperobject(12,7,20,"Tam dediği gibi oldu. Komutan kentin kapısında halk tarafından çiğnenerek öldü."));
trsuperobject.biblemap.put("b12_8_1", new trsuperobject(12,8,1,"Elişa, oğlunu diriltmiş olduğu Şunemli kadına şöyle demişti: ‹‹Kalk, ailenle birlikte buradan git, geçici olarak kalabileceğin bir yer bul. Çünkü RAB ülkeye yedi yıl sürecek bir kıtlık göndermeye karar verdi.››"));
trsuperobject.biblemap.put("b12_8_2", new trsuperobject(12,8,2,"Kadın Tanrı adamının öğüdüne uyarak ailesiyle birlikte kalkıp Filist ülkesine gitti ve orada yedi yıl kaldı."));
trsuperobject.biblemap.put("b12_8_3", new trsuperobject(12,8,3,"Yedi yıl sonra Filistten döndü. Evini, tarlasını geri almak için kraldan yardım istemeye gitti."));
trsuperobject.biblemap.put("b12_8_4", new trsuperobject(12,8,4,"O sırada kral Tanrı adamının uşağı Gehaziyle konuşuyor, ‹‹Bana Elişanın yaptığı bütün mucizeleri anlat›› diyordu."));
trsuperobject.biblemap.put("b12_8_5", new trsuperobject(12,8,5,"İşte Gehazi tam Elişanın ölüyü nasıl dirilttiğini krala anlatırken, oğlu diriltilen kadın eviyle tarlasını geri almak için kraldan yardım istemeye geldi. Gehazi krala, ‹‹Efendim kral, sözünü ettiğim kadın budur. Yanındaki oğlu da Elişanın dirilttiği çocuktur›› dedi."));
trsuperobject.biblemap.put("b12_8_6", new trsuperobject(12,8,6,"Kral kadına sorunca kadın her şeyi anlattı. Bunun üzerine kral bir görevli çağırtıp şu buyruğu verdi: ‹‹Bu kadına her şeyini, ülkeden ayrıldığı günden bugüne kadar biriken bütün geliriyle birlikte tarlasını geri verin.››"));
trsuperobject.biblemap.put("b12_8_7", new trsuperobject(12,8,7,"Aram Kralı Ben-Hadat hastalandığı sırada Elişa Şama gitti. Tanrı adamının Şama geldiği krala bildirildi."));
trsuperobject.biblemap.put("b12_8_8", new trsuperobject(12,8,8,"Kral, Hazaele, ‹‹Bir armağan al, Tanrı adamını karşılamaya git›› dedi, ‹‹Onun aracılığıyla RABbe danış, bu hastalıktan kurtulup kurtulamayacağımı sor.››"));
trsuperobject.biblemap.put("b12_8_9", new trsuperobject(12,8,9,"Hazael, Şamın en iyi mallarından oluşan kırk deve yükü armağanı yanına alarak, Tanrı adamını karşılamaya gitti. Elişanın önünde durup şöyle dedi: ‹‹Kulun Aram Kralı Ben-Hadat, hastalığından kurtulup kurtulamayacağını sormam için beni gönderdi.››"));
trsuperobject.biblemap.put("b12_8_10", new trsuperobject(12,8,10,"Elişa, ‹‹Git ona, ‹Kesinlikle iyileşeceksin› de; ama RAB bana onun kesinlikle öleceğini açıkladı›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_8_11", new trsuperobject(12,8,11,"Tanrı adamı, Hazaeli utandırıncaya kadar dik dik yüzüne baktı. Ardından ağlamaya başladı."));
trsuperobject.biblemap.put("b12_8_12", new trsuperobject(12,8,12,"Hazael, ‹‹Efendim, niçin ağlıyorsun?›› diye sordu. Elişa, ‹‹Senin İsrail halkına yapacağın kötülükleri biliyorum›› diye yanıtladı, ‹‹Kalelerini ateşe verecek, gençlerini kılıçtan geçirecek, çocuklarını yere çalıp öldürecek, gebe kadınlarının karınlarını deşeceksin.››"));
trsuperobject.biblemap.put("b12_8_13", new trsuperobject(12,8,13,"Hazael, ‹‹Bir köpekten farksız olan bu kulun, bütün bu işleri nasıl yapabilir?›› dedi. Elişa, ‹‹RAB bana senin Aram Kralı olacağını gösterdi›› diye yanıtladı."));
trsuperobject.biblemap.put("b12_8_14", new trsuperobject(12,8,14,"Bunun üzerine Hazael Elişadan ayrılıp efendisi Ben-Hadatın yanına döndü. Ben-Hadat ona, ‹‹Elişa sana ne söyledi?›› diye sordu. Hazael, ‹‹Kesinlikle iyileşeceğini söyledi›› diye yanıtladı."));
trsuperobject.biblemap.put("b12_8_15", new trsuperobject(12,8,15,"Gelgelelim ertesi gün Hazael ıslattığı bir örtüyü kralın yüzüne kapatıp onu boğdu. Böylece kral öldü, yerine Hazael geçti."));
trsuperobject.biblemap.put("b12_8_16", new trsuperobject(12,8,16,"İsrail Kralı Ahav oğlu Yoramın krallığının beşinci yılında, Yehoşafatın Yahuda Kralı olduğu sırada, Yehoşafatın oğlu Yehoram Yahudayı yönetmeye başladı."));
trsuperobject.biblemap.put("b12_8_17", new trsuperobject(12,8,17,"Yehoram otuz iki yaşında kral oldu ve Yeruşalimde sekiz yıl krallık yaptı."));
trsuperobject.biblemap.put("b12_8_18", new trsuperobject(12,8,18,"Karısı Ahavın kızı olduğu için, o da Ahavın ailesi gibi İsrail krallarının yolunu izledi ve RABbin gözünde kötü olanı yaptı."));
trsuperobject.biblemap.put("b12_8_19", new trsuperobject(12,8,19,"Ama RAB kulu Davutun hatırı için Yahudayı yok etmek istemedi. Çünkü Davuta ve soyuna sönmeyen bir ışık vereceğine söz vermişti."));
trsuperobject.biblemap.put("b12_8_20", new trsuperobject(12,8,20,"Yehoramın krallığı döneminde Edomlular Yahudalılara karşı ayaklanarak kendi krallıklarını kurdular."));
trsuperobject.biblemap.put("b12_8_21", new trsuperobject(12,8,21,"Yehoram bütün savaş arabalarıyla Saire gitti. Edomlular onu ve savaş arabalarının komutanlarını kuşattılar. Ama Yehoram gece kalkıp kuşatmayı yararak kaçtı. Askerleri de kaçarak evlerine döndü."));
trsuperobject.biblemap.put("b12_8_22", new trsuperobject(12,8,22,"O sırada Livna Kenti ayaklandı. Edomluların Yahudaya karşı başkaldırması bugün de sürüyor."));
trsuperobject.biblemap.put("b12_8_23", new trsuperobject(12,8,23,"Yehoramın krallığı dönemindeki öteki olaylar ve bütün yaptıkları Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_8_24", new trsuperobject(12,8,24,"Yehoram ölüp atalarına kavuştu ve Davut Kentinde atalarının yanına gömüldü. Yerine oğlu Ahazya kral oldu."));
trsuperobject.biblemap.put("b12_8_25", new trsuperobject(12,8,25,"İsrail Kralı Ahav oğlu Yoramın krallığının on ikinci yılında Yehoram oğlu Ahazya Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b12_8_26", new trsuperobject(12,8,26,"Ahazya yirmi iki yaşında kral oldu ve Yeruşalimde bir yıl krallık yaptı. Annesi İsrail Kralı Omrinin torunu Atalyaydı."));
trsuperobject.biblemap.put("b12_8_27", new trsuperobject(12,8,27,"Ahazya evlilik yoluyla Ahava akraba olduğu için Ahav ailesinin yolunu izledi ve onlar gibi RABbin gözünde kötü olanı yaptı."));
trsuperobject.biblemap.put("b12_8_28", new trsuperobject(12,8,28,"Ahazya, Aram Kralı Hazaelle savaşmak üzere Ahav oğlu Yoramla birlikte Ramot-Gilata gitti. Aramlılar Yoramı yaraladılar."));
trsuperobject.biblemap.put("b12_8_29", new trsuperobject(12,8,29,"Kral Yoram Ramot-Gilat'ta Aram Kralı Hazael'le savaşırken aldığı yaraların iyileşmesi için Yizreel'e döndü. Yahuda Kralı Yehoram oğlu Ahazya da yaralanan Ahav oğlu Yoram'ı görmek için Yizreel'e gitti."));
trsuperobject.biblemap.put("b12_9_1", new trsuperobject(12,9,1,"Peygamber Elişa, peygamberler topluluğundan bir adam çağırıp, ‹‹Kemerini kuşan, bu yağ kabını alıp Ramot-Gilata git›› dedi,"));
trsuperobject.biblemap.put("b12_9_2", new trsuperobject(12,9,2,"‹‹Oraya varınca Nimşi oğlu, Yehoşafat oğlu Yehuyu ara. Onu kardeşlerinin arasından alıp başka bir odaya götür."));
trsuperobject.biblemap.put("b12_9_3", new trsuperobject(12,9,3,"Zeytinyağını başına dök ve ona RAB şöyle diyor de: ‹Seni İsrail Kralı olarak meshettim.› Sonra kapıyı aç ve koş, oyalanma!››"));
trsuperobject.biblemap.put("b12_9_4", new trsuperobject(12,9,4,"Böylece peygamberin uşağı Ramot-Gilata gitti."));
trsuperobject.biblemap.put("b12_9_5", new trsuperobject(12,9,5,"Oraya vardığında ordu komutanlarının bir arada oturduklarını gördü. ‹‹Komutanım, sana bir haberim var›› dedi. Yehu, ‹‹Hangimize söylüyorsun?›› diye sordu. Uşak, ‹‹Sana, efendim›› diye yanıtladı."));
trsuperobject.biblemap.put("b12_9_6", new trsuperobject(12,9,6,"Yehu kalkıp eve girdi. Uşak yağı Yehunun başına döküp ona şöyle dedi: ‹‹İsrailin Tanrısı RAB diyor ki, ‹Seni halkım İsrailin kralı olarak meshettim."));
trsuperobject.biblemap.put("b12_9_7", new trsuperobject(12,9,7,"Efendin Ahavın ailesini öldüreceksin. Bana hizmet eden peygamberlerin ve bütün kullarımın dökülen kanının öcünü İzebelden alacağım."));
trsuperobject.biblemap.put("b12_9_8", new trsuperobject(12,9,8,"Ahavın bütün soyu ortadan kalkacak. İsrailde genç yaşlı Ahavın soyundan gelen bütün erkeklerin kökünü kurutacağım."));
trsuperobject.biblemap.put("b12_9_9", new trsuperobject(12,9,9,"Nevat oğlu Yarovamla Ahiya oğlu Baaşanın ailelerine ne yaptımsa, Ahavın ailesine de aynısını yapacağım."));
trsuperobject.biblemap.put("b12_9_10", new trsuperobject(12,9,10,"Yizreel topraklarında İzebelin ölüsünü köpekler yiyecek ve onu gömen olmayacak.›› Uşak bunları söyledikten sonra kapıyı açıp kaçtı."));
trsuperobject.biblemap.put("b12_9_11", new trsuperobject(12,9,11,"Yehu komutan arkadaşlarının yanına döndü. İçlerinden biri, ‹‹Her şey yolunda mı? O delinin seninle ne işi vardı?›› diye sordu. Yehu, ‹‹Onu tanıyorsunuz, neler saçmaladığını bilirsiniz›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_9_12", new trsuperobject(12,9,12,"‹‹Hayır, bilmiyoruz, ne söyledi? Anlat bize!›› dediler. Yehu şöyle yanıtladı: ‹‹Bana RAB şöyle diyor dedi: ‹Seni İsrail Kralı olarak meshettim.››"));
trsuperobject.biblemap.put("b12_9_13", new trsuperobject(12,9,13,"Bunun üzerine hepsi hemen cüppelerini çıkarıp merdivenin başında duran Yehunun ayaklarına serdi. Boru çalarak, ‹‹Yehu kraldır!›› diye bağırdılar."));
trsuperobject.biblemap.put("b12_9_14", new trsuperobject(12,9,14,"Nimşi oğlu Yehoşafat oğlu Yehu Yorama karşı bir düzen kurdu. O sıralarda Yoram ile İsrail halkı Aram Kralı Hazaele karşı Ramot-Gilatı savunuyordu."));
trsuperobject.biblemap.put("b12_9_15", new trsuperobject(12,9,15,"Ancak Kral Yoram, Aram Kralı Hazaelle savaşırken Aramlılar onu yaralamıştı. Yoram da yaraların iyileşmesi için Yizreele dönmüştü. Yehu arkadaşlarına, ‹‹Eğer siz de benimle aynı görüşteyseniz, hiç kimsenin kentten kaçmasına ve gidip durumu Yizreele bildirmesine izin vermeyin›› dedi."));
trsuperobject.biblemap.put("b12_9_16", new trsuperobject(12,9,16,"Yehu savaş arabasına binip Yizreele gitti. Çünkü Yoram orada hasta yatıyordu. Yahuda Kralı Ahazya da Yoramı görmek için oraya gitmişti."));
trsuperobject.biblemap.put("b12_9_17", new trsuperobject(12,9,17,"Yizreelde kulede nöbet tutan gözcü, Yehunun ordusuyla yaklaştığını görünce, ‹‹Bir kalabalık görüyorum!›› diye bağırdı. Yoram, ‹‹Bir atlı gönder, onu karşılasın, barış için gelip gelmediğini sorsun›› dedi."));
trsuperobject.biblemap.put("b12_9_18", new trsuperobject(12,9,18,"Atlı Yehuyu karşılamaya gitti ve ona, ‹‹Kralımız, ‹Barış için mi geldin?› diye soruyor›› dedi. Yehu, ‹‹Barıştan sana ne! Sen beni izle›› diye karşılık verdi. Gözcü durumu krala bildirdi: ‹‹Ulak onlara vardı, ama geri dönmedi.››"));
trsuperobject.biblemap.put("b12_9_19", new trsuperobject(12,9,19,"Bu kez ikinci bir atlı gönderildi. Atlı onlara varıp, ‹‹Kralımız, ‹Barış için mi geldin?› diye soruyor›› dedi. Yehu, ‹‹Barıştan sana ne! Sen beni izle›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_9_20", new trsuperobject(12,9,20,"Gözcü durumu krala bildirdi: ‹‹Ulak onlara vardı, ama geri dönmedi. Komutanları savaş arabasını Nimşi oğlu Yehu gibi delicesine sürüyor.››"));
trsuperobject.biblemap.put("b12_9_21", new trsuperobject(12,9,21,"Kral Yoram, ‹‹Arabamı hazırlayın!›› diye buyruk verdi. Arabası hazırlandı. İsrail Kralı Yoram ile Yahuda Kralı Ahazya arabalarına binip Yehuyu karşılamaya gittiler. Yizreelli Navotun topraklarında onunla karşılaştılar."));
trsuperobject.biblemap.put("b12_9_22", new trsuperobject(12,9,22,"Yoram Yehuyu görünce, ‹‹Barış için mi geldin?›› diye sordu. Yehu, ‹‹Annen İzebelin yaptığı bunca putperestlik ve büyücülük sürüp giderken barıştan söz edilir mi?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b12_9_23", new trsuperobject(12,9,23,"Yoram, ‹‹Hainlik bu, Ahazya!›› diye bağırdı ve arabasının dizginlerini çevirip kaçtı."));
trsuperobject.biblemap.put("b12_9_24", new trsuperobject(12,9,24,"Yehu var gücüyle yayını çekip Yoramı sırtından vurdu. Ok Yoramın kalbini delip geçti. Yoram arabasının içine yığılıp kaldı."));
trsuperobject.biblemap.put("b12_9_25", new trsuperobject(12,9,25,"Yehu yardımcısı Bidkara, ‹‹Onun cesedini al, Yizreelli Navotun toprağına at›› dedi, ‹‹Anımsa, senle ben birlikte Yoramın babası Ahavın ardından savaş arabasıyla giderken, RAB Ahava,"));
trsuperobject.biblemap.put("b12_9_26", new trsuperobject(12,9,26,"‹Dün Navotla oğullarının kanını gördüm. Seni de bu topraklarda cezalandıracağım› demişti. Şimdi RABbin sözü uyarınca, Yoramın cesedini al, Navotun toprağına at!››"));
trsuperobject.biblemap.put("b12_9_27", new trsuperobject(12,9,27,"Yahuda Kralı Ahazya olanları görünce Beythaggana doğru kaçmaya başladı. Yehu ardına takılıp, ‹‹Onu da öldürün!›› diye bağırdı. Ahazyayı Yivleam yakınlarında, Gur yolunda, arabasının içinde vurdular. Yaralı olarak Megiddoya kadar kaçıp orada öldü."));
trsuperobject.biblemap.put("b12_9_28", new trsuperobject(12,9,28,"Adamları Ahazyanın cesedini bir savaş arabasına koyup Yeruşalime götürdüler. Onu Davut Kentinde atalarının yanına, kendi mezarına gömdüler."));
trsuperobject.biblemap.put("b12_9_29", new trsuperobject(12,9,29,"Ahazya Ahav oğlu Yoramın krallığının on birinci yılında Yahuda Kralı olmuştu."));
trsuperobject.biblemap.put("b12_9_30", new trsuperobject(12,9,30,"Sonra Yehu Yizreele gitti. İzebel bunu duyunca, gözlerine sürme çekti, saçlarını tarayıp pencereden dışarıyı gözlemeye başladı."));
trsuperobject.biblemap.put("b12_9_31", new trsuperobject(12,9,31,"Yehu kentin kapısından içeri girince, İzebel, ‹‹Ey efendisini öldüren Zimri, barış için mi geldin?›› diye seslendi."));
trsuperobject.biblemap.put("b12_9_32", new trsuperobject(12,9,32,"Yehu pencereye doğru bakıp, ‹‹Kim benden yana?›› diye bağırdı. İki üç görevli yukarıdan ona baktı."));
trsuperobject.biblemap.put("b12_9_33", new trsuperobject(12,9,33,"Yehu, ‹‹Atın onu aşağı!›› dedi. Görevliler İzebeli aşağıya attılar. Kanı surların ve bedenini çiğneyen atların üzerine sıçradı."));
trsuperobject.biblemap.put("b12_9_34", new trsuperobject(12,9,34,"Yehu içeri girip yedi, içti. Sonra, ‹‹O lanet olası kadını alıp gömün, ne de olsa bir kral kızıdır›› dedi."));
trsuperobject.biblemap.put("b12_9_35", new trsuperobject(12,9,35,"Ama İzebeli gömmeye giden adamlar başından, ayaklarından, ellerinden başka bir şey bulamadılar."));
trsuperobject.biblemap.put("b12_9_36", new trsuperobject(12,9,36,"Geri dönüp durumu Yehuya bildirdiler. Yehu onlara şöyle dedi: ‹‹Kulu Tişbeli İlyas aracılığıyla konuşan RABbin sözü yerine geldi. RAB, ‹Yizreel topraklarında İzebelin ölüsünü köpekler yiyecek› demişti."));
trsuperobject.biblemap.put("b12_9_37", new trsuperobject(12,9,37,"‹İzebel'in leşi Yizreel topraklarına gübre olacak ve kimse, bu İzebel'dir, diyemeyecek.››"));
trsuperobject.biblemap.put("b12_10_1", new trsuperobject(12,10,1,"Ahavın Samiriyede yetmiş oğlu vardı. Yehu mektuplar yazıp Samiriyeye gönderdi. Yizreelin yöneticilerine, ileri gelenlere ve Ahavın çocuklarını koruyanlara yazdığı mektuplarda Yehu şöyle diyordu:"));
trsuperobject.biblemap.put("b12_10_2", new trsuperobject(12,10,2,"‹‹Efendinizin oğulları sizinle birliktedir. Savaş arabalarınız, atlarınız, silahlarınız var. Surlu bir kentte yaşıyorsunuz. Bu mektup size ulaşır ulaşmaz,"));
trsuperobject.biblemap.put("b12_10_3", new trsuperobject(12,10,3,"efendinizin oğullarından en iyi ve en uygun olanı seçip babasının tahtına oturtun. Ve efendinizin ailesini korumak için savaşın.››"));
trsuperobject.biblemap.put("b12_10_4", new trsuperobject(12,10,4,"Ama onlar dehşete düştüler. ‹‹İki kral Yehuyla başa çıkamadı, biz nasıl çıkarız?›› dediler."));
trsuperobject.biblemap.put("b12_10_5", new trsuperobject(12,10,5,"Saray sorumlusu, kent valisi, ileri gelenler ve Ahavın çocuklarını koruyanlar Yehuya şu haberi gönderdi: ‹‹Biz senin kullarınız, söyleyeceğin her şeyi yapmaya hazırız. Kimseyi kral yapmaya niyetimiz yok. Kendin için en iyi olan neyse onu yap.››"));
trsuperobject.biblemap.put("b12_10_6", new trsuperobject(12,10,6,"Yehu onlara ikinci bir mektup yazdı: ‹‹Eğer siz benden yana ve bana bağlıysanız, efendinizin oğullarının başını kesip yarın bu saatlerde Yizreele, bana getirin.›› Kral Ahavın yetmiş oğlu, onları yetiştirmekle görevli kent ileri gelenlerinin koruması altındaydı."));
trsuperobject.biblemap.put("b12_10_7", new trsuperobject(12,10,7,"Yehunun mektubu kent ileri gelenlerine ulaşınca, Ahavın yetmiş oğlunu öldürüp başlarını küfelere koydular ve Yizreele, Yehuya gönderdiler."));
trsuperobject.biblemap.put("b12_10_8", new trsuperobject(12,10,8,"Ulak gelip Yehuya, ‹‹Kral oğullarının başlarını getirdiler›› diye haber verdi. Yehu, ‹‹Onları iki yığın halinde kent kapısının girişine bırakın, sabaha kadar orada kalsınlar›› dedi."));
trsuperobject.biblemap.put("b12_10_9", new trsuperobject(12,10,9,"Ertesi sabah Yehu halkın önüne çıkıp şöyle dedi: ‹‹Efendime düzen kurup onu öldüren benim, sizin suçunuz yok. Ama bunları kim öldürdü?"));
trsuperobject.biblemap.put("b12_10_10", new trsuperobject(12,10,10,"Bu olay gösteriyor ki, RABbin Ahavın ailesine ilişkin söylediği hiçbir söz boşa çıkmayacaktır. RAB, kulu İlyas aracılığıyla verdiği sözü yerine getirdi.››"));
trsuperobject.biblemap.put("b12_10_11", new trsuperobject(12,10,11,"Sonra Yizreelde Ahavın öteki akrabalarının hepsini, bütün yüksek görevlilerini, yakın arkadaşlarını ve kâhinlerini öldürdü. Sağ kalan olmadı."));
trsuperobject.biblemap.put("b12_10_12", new trsuperobject(12,10,12,"Yehu Yizreelden ayrılıp Samiriyeye doğru yola çıktı. Yolda çobanların Beyteket adını verdiği yerde,"));
trsuperobject.biblemap.put("b12_10_13", new trsuperobject(12,10,13,"Yahuda Kralı Ahazyanın akrabalarıyla karşılaştı. Onlara, ‹‹Siz kimsiniz?›› diye sordu. ‹‹Biz Ahazyanın akrabalarıyız›› diye karşılık verdiler, ‹‹Kralın ve ana kraliçe İzebelin çocuklarına saygılarımızı sunmaya gidiyoruz.››"));
trsuperobject.biblemap.put("b12_10_14", new trsuperobject(12,10,14,"Yehu adamlarına, ‹‹Bunları diri yakalayın!›› diye buyruk verdi. Onları diri yakalayıp Beyteket Kuyusu yakınında kılıçtan geçirdiler. Öldürülenler kırk iki kişiydi. Sağ kalan olmadı."));
trsuperobject.biblemap.put("b12_10_15", new trsuperobject(12,10,15,"Yehu oradan ayrıldı. Yolda kendisine doğru gelen Rekav oğlu Yehonadavla karşılaştı. Ona selam vererek, ‹‹Ben sana karşı iyi duygular besliyorum, sen de aynı duygulara sahip misin?›› diye sordu. Yehonadav, ‹‹Evet›› diye yanıtladı. Yehu, ‹‹Öyleyse elini ver›› dedi. Yehonadav elini uzattı. Yehu onu arabasına alarak,"));
trsuperobject.biblemap.put("b12_10_16", new trsuperobject(12,10,16,"‹‹Benimle gel ve RAB için nasıl çaba harcadığımı gör›› dedi. Sonra onu arabasıyla Samiriyeye götürdü."));
trsuperobject.biblemap.put("b12_10_17", new trsuperobject(12,10,17,"Samiriyeye varınca Yehu RABbin İlyas aracılığıyla söylediği söz uyarınca, Ahavın orada kalan akrabalarının hepsini öldürdü."));
trsuperobject.biblemap.put("b12_10_18", new trsuperobject(12,10,18,"Yehu, bütün halkı toplayarak, ‹‹Ahav Baala az kulluk etti, ben daha çok edeceğim›› dedi,"));
trsuperobject.biblemap.put("b12_10_19", new trsuperobject(12,10,19,"‹‹Baalın bütün peygamberlerini, kâhinlerini, ona tapan herkesi çağırın. Hiçbiri gelmemezlik etmesin. Çünkü Baala büyük bir kurban sunacağım. Kim gelmezse öldürülecek.›› Gerçekte Yehu Baala tapanları yok etmek için bir düzen kurmaktaydı."));
trsuperobject.biblemap.put("b12_10_20", new trsuperobject(12,10,20,"Yehu, ‹‹Baalın onuruna bir toplantı yapılacağını duyurun›› dedi. Duyuru yapıldı."));
trsuperobject.biblemap.put("b12_10_21", new trsuperobject(12,10,21,"Yehu bütün İsraile haber saldı. Baala tapanların hepsi geldi, gelmeyen kalmadı. Baalın tapınağı hıncahınç doldu."));
trsuperobject.biblemap.put("b12_10_22", new trsuperobject(12,10,22,"Yehu, kutsal giysiler görevlisine, ‹‹Baala tapanların hepsine giysi çıkar›› diye buyruk verdi. Görevli herkese giysi getirdi."));
trsuperobject.biblemap.put("b12_10_23", new trsuperobject(12,10,23,"O zaman Yehu Rekav oğlu Yehonadavla birlikte Baalın tapınağına girdi. İçerdekilere, ‹‹Çevrenize iyi bakın›› dedi, ‹‹Aranızda RABbe tapanlardan kimse olmasın, sadece Baala tapanlar olsun.››"));
trsuperobject.biblemap.put("b12_10_24", new trsuperobject(12,10,24,"Ardından Yehuyla Yehonadav kurban ve yakmalık sunu sunmak üzere içeri girdiler. Yehu tapınağın çevresine seksen kişi yerleştirmiş ve onlara şu buyruğu vermişti: ‹‹Elinize teslim ettiğim bu adamlardan biri kaçarsa, bunu canınızla ödersiniz!››"));
trsuperobject.biblemap.put("b12_10_25", new trsuperobject(12,10,25,"Yakmalık sununun sunulması biter bitmez, Yehu muhafızlarla komutanlara, ‹‹İçeriye girin, hepsini öldürün, hiçbiri kaçmasın!›› diye buyruk verdi. Muhafızlarla komutanlar hepsini kılıçtan geçirip ölülerini dışarı attılar. Sonra Baalın tapınağının iç bölümüne girdiler."));
trsuperobject.biblemap.put("b12_10_26", new trsuperobject(12,10,26,"Baalın tapınağındaki dikili taşları çıkarıp yaktılar."));
trsuperobject.biblemap.put("b12_10_27", new trsuperobject(12,10,27,"Baalın dikili taşını ve tapınağını ortadan kaldırdılar. Halk orayı helaya çevirdi. Orası bugüne kadar da öyle kaldı."));
trsuperobject.biblemap.put("b12_10_28", new trsuperobject(12,10,28,"Böylece Yehu İsrailde Baala tapmaya son verdi."));
trsuperobject.biblemap.put("b12_10_29", new trsuperobject(12,10,29,"Ne var ki, Nevat oğlu Yarovamın İsraili sürüklediği günahlardan -Beytel ve Dandaki altın buzağılara tapmaktan- vazgeçmedi."));
trsuperobject.biblemap.put("b12_10_30", new trsuperobject(12,10,30,"RAB Yehuya, ‹‹Gözümde doğru olanı yaparak başarılı oldun›› dedi, ‹‹Ahavın ailesine istediğim her şeyi yaptın. Bunun için senin soyun dört kuşak İsrail tahtında oturacak.››"));
trsuperobject.biblemap.put("b12_10_31", new trsuperobject(12,10,31,"Gelgelelim Yehu İsrailin Tanrısı RABbin yasasını yürekten izlemedi, önemsemedi. Yarovamın İsraili sürüklediği günahlardan ayrılmadı."));
trsuperobject.biblemap.put("b12_10_32-33", new trsuperobject(12,10,32-33,"RAB o günlerde İsrail topraklarını küçültmeye başladı. Aram Kralı Hazael Şeria Irmağının doğusunda Gadlılar, Rubenliler ve Manaşşelilerin yaşadığı bütün Gilat bölgesini, Arnon Vadisindeki Aroerden Gilat ve Başana kadar bütün İsrail topraklarını ele geçirdi."));
trsuperobject.biblemap.put("b12_10_34", new trsuperobject(12,10,34,"Yehunun krallığı dönemindeki öteki olaylar, bütün yaptıkları ve başarıları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_10_35", new trsuperobject(12,10,35,"Yehu ölüp atalarına kavuşunca, Samiriyede gömüldü. Yerine oğlu Yehoahaz kral oldu."));
trsuperobject.biblemap.put("b12_10_36", new trsuperobject(12,10,36,"Yehu Samiriye'de yirmi sekiz yıl İsrail krallığı yaptı."));
trsuperobject.biblemap.put("b12_11_1", new trsuperobject(12,11,1,"Ahazyanın annesi Atalya, oğlunun öldürüldüğünü duyunca, kral soyunun bütün bireylerini yok etmeye çalıştı."));
trsuperobject.biblemap.put("b12_11_2", new trsuperobject(12,11,2,"Ne var ki, Kral Yehoramın kızı, Ahazyanın üvey kızkardeşi Yehoşeva, Ahazya oğlu Yoaşı kralın öldürülmek istenen öteki oğullarının arasından alıp kaçırdı ve dadısıyla birlikte yatak odasına gizledi. Çocuğu Atalyadan gizleyerek kurtarmış oldu."));
trsuperobject.biblemap.put("b12_11_3", new trsuperobject(12,11,3,"Atalya ülkeyi yönetirken, çocuk altı yıl boyunca RABbin Tapınağında dadısıyla birlikte gizlendi."));
trsuperobject.biblemap.put("b12_11_4", new trsuperobject(12,11,4,"Yedinci yıl Yehoyada haber gönderip Karyalılarınfö ve muhafızların yüzbaşılarını çağırttı. Onları RABbin Tapınağında toplayarak onlarla bir antlaşma yaptı. Hepsine RABbin Tapınağında ant içirdikten sonra kralın oğlu Yoaşı kendilerine gösterdi."));
trsuperobject.biblemap.put("b12_11_5", new trsuperobject(12,11,5,"Onlara şu buyrukları verdi: ‹‹Şabat Günü göreve gidenlerin üçte biri kral sarayını koruyacak,"));
trsuperobject.biblemap.put("b12_11_6", new trsuperobject(12,11,6,"üçte biri Sur Kapısında, üçte biri de muhafızların arkasındaki kapıda bulunacak. Sırayla tapınak nöbeti tutacaksınız."));
trsuperobject.biblemap.put("b12_11_7", new trsuperobject(12,11,7,"Şabat Günü görevleri biten öbür iki bölükteki askerlerin tümü RABbin Tapınağının çevresinde durup kralı koruyacak."));
trsuperobject.biblemap.put("b12_11_8", new trsuperobject(12,11,8,"Herkes yalın kılıç kralın çevresini sarsın, yaklaşan olursa öldürün. Kral nereye giderse, ona eşlik edin.›› paralı askerler."));
trsuperobject.biblemap.put("b12_11_9", new trsuperobject(12,11,9,"Yüzbaşılar Kâhin Yehoyadanın buyruklarını tam tamına uyguladılar. Şabat Günü göreve gidenlerle görevi biten adamlarını alıp Yehoyadanın yanına gittiler."));
trsuperobject.biblemap.put("b12_11_10", new trsuperobject(12,11,10,"Kâhin RABbin Tapınağındaki Kral Davuttan kalan mızraklarla kalkanları yüzbaşılara dağıttı."));
trsuperobject.biblemap.put("b12_11_11", new trsuperobject(12,11,11,"Kralı korumak için sunağın ve tapınağın çevresine tapınağın güneyinden kuzeyine kadar silahlı muhafızlar yerleştirildi."));
trsuperobject.biblemap.put("b12_11_12", new trsuperobject(12,11,12,"Yehoyada kralın oğlu Yoaşı dışarı çıkarıp başına taç koydu. Tanrının Yasasını da ona verip krallığını ilan ettiler. Onu meshedip alkışlayarak, ‹‹Yaşasın kral!›› diye bağırdılar."));
trsuperobject.biblemap.put("b12_11_13", new trsuperobject(12,11,13,"Atalya muhafızlarla halkın çıkardığı gürültüyü duyunca, RABbin Tapınağında toplananların yanına gitti."));
trsuperobject.biblemap.put("b12_11_14", new trsuperobject(12,11,14,"Baktı, kral geleneğe uygun olarak sütunun yanında duruyor; yüzbaşılar, borazan çalanlar çevresine toplanmış. Ülke halkı sevinç içindeydi, borazanlar çalınıyordu. Atalya giysilerini yırtarak, ‹‹Hainlik! Hainlik!›› diye bağırdı."));
trsuperobject.biblemap.put("b12_11_15", new trsuperobject(12,11,15,"Kâhin Yehoyada yüzbaşılara, ‹‹O kadını aradan çıkarın. Ardından kim giderse kılıçtan geçirin›› diye buyruk verdi. Çünkü kadının RABbin Tapınağında öldürülmesini istemiyordu."));
trsuperobject.biblemap.put("b12_11_16", new trsuperobject(12,11,16,"Atalya yakalandı ve sarayın At Kapısına götürülüp öldürüldü."));
trsuperobject.biblemap.put("b12_11_17", new trsuperobject(12,11,17,"Yehoyada RABbin halkı olmaları için RAB ile kral ve halk arasında bir antlaşma yaptı. Ayrıca halkla kral arasında da bir antlaşma yaptı."));
trsuperobject.biblemap.put("b12_11_18", new trsuperobject(12,11,18,"Ülke halkı gidip Baalın tapınağını yıktı. Sunaklarını, putlarını parçaladılar; Baalın Kâhini Mattanı da sunakların önünde öldürdüler. Kâhin Yehoyada RABbin Tapınağına nöbetçiler yerleştirdi."));
trsuperobject.biblemap.put("b12_11_19", new trsuperobject(12,11,19,"Sonra yüzbaşıları, Karyalıları, muhafızları ve halkı yanına aldı. Kralı RABbin Tapınağından getirdiler. Muhafızlar Kapısından geçerek sarayına götürdüler, kral tahtına oturttular."));
trsuperobject.biblemap.put("b12_11_20", new trsuperobject(12,11,20,"Ülke halkı sevinç içindeydi, ancak kent suskundu. Çünkü Atalya sarayda kılıçla öldürülmüştü."));
trsuperobject.biblemap.put("b12_12_1", new trsuperobject(12,12,1,"İsrail Kralı Yehunun krallığının yedinci yılında Yoaş Yahuda Kralı oldu. Yedi yaşında kral oldu ve Yeruşalimde kırk yıl krallık yaptı. Annesi Beer-Şevalı Sivyaydı."));
trsuperobject.biblemap.put("b12_12_2", new trsuperobject(12,12,2,"Yoaş Kâhin Yehoyada yaşadığı sürece RABbin gözünde doğru olanı yaptı. Çünkü Kâhin Yehoyada ona yol gösteriyordu."));
trsuperobject.biblemap.put("b12_12_3", new trsuperobject(12,12,3,"Ancak alışılagelen tapınma yerleri henüz kaldırılmamıştı ve halk oralarda hâlâ kurban kesip buhur yakıyordu. eklenmiştir."));
trsuperobject.biblemap.put("b12_12_4", new trsuperobject(12,12,4,"Yoaş kâhinlere şöyle dedi: ‹‹RABbin Tapınağı için yapılan bağışları: Nüfus sayımından elde edilen geliri, kişi başına düşen vergiyi ve halkın gönüllü olarak RABbin Tapınağına sunduğu paraları toplayın."));
trsuperobject.biblemap.put("b12_12_5", new trsuperobject(12,12,5,"Her kâhin bunları hazine görevlilerinden alsın. Tapınağın neresinde yıkık bir yer varsa, onarılsın.››"));
trsuperobject.biblemap.put("b12_12_6", new trsuperobject(12,12,6,"Yoaşın krallığının yirmi üçüncü yılında kâhinler tapınağı hâlâ onarmamışlardı."));
trsuperobject.biblemap.put("b12_12_7", new trsuperobject(12,12,7,"Bunun üzerine Kral Yoaş, Kâhin Yehoyada ile öbür kâhinleri çağırıp, ‹‹Neden RABbin Tapınağını onarmıyorsunuz?›› diye sordu, ‹‹Hazine görevlilerinden artık para almayın. Aldığınız paraları da RABbin Tapınağının onarımına devredin.››"));
trsuperobject.biblemap.put("b12_12_8", new trsuperobject(12,12,8,"Böylece kâhinler halktan para toplamamayı ve tapınağın onarım işlerine karışmamayı kabul ettiler."));
trsuperobject.biblemap.put("b12_12_9", new trsuperobject(12,12,9,"Kâhin Yehoyada bir sandık aldı. Kapağına bir delik açıp sunağın yanına, RABbin Tapınağına girenlerin sağına yerleştirdi. Kapıda görevli kâhinler RABbin Tapınağına getirilen bütün paraları sandığa atıyorlardı."));
trsuperobject.biblemap.put("b12_12_10", new trsuperobject(12,12,10,"Sandıkta çok para biriktiğini görünce kralın yazmanıyla başkâhin RABbin Tapınağına getirilen paraları sayıp torbalara koyarlardı."));
trsuperobject.biblemap.put("b12_12_11", new trsuperobject(12,12,11,"Sayılan paralar RABbin Tapınağındaki işlerin başında bulunan adamlara verilirdi. Onlar da paraları RABbin Tapınağında çalışan marangozlara, yapıcılara,"));
trsuperobject.biblemap.put("b12_12_12", new trsuperobject(12,12,12,"duvarcılara, taşçılara öder, tapınağı onarmak için kereste ve yontma taş alımında kullanır ve onarım için gereken öbür malzemelere harcarlardı."));
trsuperobject.biblemap.put("b12_12_13", new trsuperobject(12,12,13,"RABbin Tapınağında toplanan paralar, tapınak için gümüş tas, fitil maşaları, çanak, borazan, altın ya da gümüş eşya yapımında kullanılmadı."));
trsuperobject.biblemap.put("b12_12_14", new trsuperobject(12,12,14,"Bu para yalnız işçilere ödendi ve tapınağın onarımına harcandı."));
trsuperobject.biblemap.put("b12_12_15", new trsuperobject(12,12,15,"Tapınakta çalışanlara para ödemekle görevli kişiler öyle dürüst insanlardı ki, onlara hesap bile sorulmazdı."));
trsuperobject.biblemap.put("b12_12_16", new trsuperobject(12,12,16,"Suç ve günah sunusu olarak verilen paralarsa RABbin Tapınağına getirilmezdi; bunlar kâhinlere aitti."));
trsuperobject.biblemap.put("b12_12_17", new trsuperobject(12,12,17,"O sırada Aram Kralı Hazael, Gat Kentine saldırıp kenti ele geçirdi. Sonra Yeruşalime saldırmaya karar verdi."));
trsuperobject.biblemap.put("b12_12_18", new trsuperobject(12,12,18,"Yahuda Kralı Yoaş, ataları olan öbür Yahuda krallarından Yehoşafatın, Yehoramın, Ahazyanın ve kendisinin RABbe adamış olduğu bütün kutsal armağanları, RABbin Tapınağında ve sarayın hazinelerinde bulunan bütün altınları Aram Kralı Hazaele gönderdi. Bunun üzerine Hazael Yeruşalime saldırmaktan vazgeçti."));
trsuperobject.biblemap.put("b12_12_19", new trsuperobject(12,12,19,"Yoaşın krallığı dönemindeki öteki olaylar ve bütün yaptıkları Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_12_20", new trsuperobject(12,12,20,"Kral Yoaşın görevlileri düzen kurup onu Sillaya inen yolda, Beytmilloda öldürdüler."));
trsuperobject.biblemap.put("b12_12_21", new trsuperobject(12,12,21,"Yoaş'ı öldürenler, görevlilerinden Şimat oğlu Yozakar'la Şomer oğlu Yehozavat'tı. Yoaş Davut Kenti'nde atalarının yanına gömüldü. Yerine oğlu Amatsya kral oldu."));
trsuperobject.biblemap.put("b12_13_1", new trsuperobject(12,13,1,"Yahuda Kralı Ahazya oğlu Yoaşın krallığının yirmi üçüncü yılında Yehu oğlu Yehoahaz Samiriyede İsrail Kralı oldu ve on yedi yıl krallık yaptı."));
trsuperobject.biblemap.put("b12_13_2", new trsuperobject(12,13,2,"Yehoahaz RABbin gözünde kötü olanı yaptı ve Nevat oğlu Yarovamın İsraili sürüklediği günahlara katıldı. Bu günahlardan ayrılmadı."));
trsuperobject.biblemap.put("b12_13_3", new trsuperobject(12,13,3,"İşte bu yüzden RABbin İsraile karşı öfkesi alevlendi ve RAB onları uzun süre Aram Kralı Hazaelle oğlu Ben-Hadatın egemenliği altına soktu."));
trsuperobject.biblemap.put("b12_13_4", new trsuperobject(12,13,4,"Bunun üzerine Yehoahaz RABbe yakardı. RAB onun yakarışını kabul etti. Çünkü İsrailin çektiği sıkıntıyı, Aram Kralının onlara neler yaptığını görüyordu."));
trsuperobject.biblemap.put("b12_13_6", new trsuperobject(12,13,6,"Ne var ki Yarovam ailesinin İsraili sürüklediği günahları izlediler, bu günahlardan ayrılmadılar. Aşera putu da Samiriyede dikili kaldı."));
trsuperobject.biblemap.put("b12_13_7", new trsuperobject(12,13,7,"Yehoahazın elli atlı, on savaş arabası, on bin de yaya asker dışında gücü kalmamıştı. Çünkü Aram Kralı ötekileri harman tozu gibi ezip yok etmişti."));
trsuperobject.biblemap.put("b12_13_8", new trsuperobject(12,13,8,"Yehoahazın krallığı dönemindeki öteki olaylar, bütün yaptıkları ve başarıları İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_13_9", new trsuperobject(12,13,9,"Yehoahaz ölüp atalarına kavuşunca, Samiriyede gömüldü ve yerine oğlu Yehoaş kral oldu."));
trsuperobject.biblemap.put("b12_13_10", new trsuperobject(12,13,10,"Yahuda Kralı Yoaşın krallığının otuz yedinci yılında Yehoahaz oğlu Yehoaş Samiriyede İsrail Kralı oldu ve on altı yıl krallık yaptı."));
trsuperobject.biblemap.put("b12_13_11", new trsuperobject(12,13,11,"RABbin gözünde kötü olanı yaptı ve Nevat oğlu Yarovamın İsraili sürüklediği günahlardan ayrılmadı; onun yolunu izledi."));
trsuperobject.biblemap.put("b12_13_12", new trsuperobject(12,13,12,"Yehoaşın krallığı dönemindeki öteki olaylar, bütün yaptıkları, Yahuda Kralı Amatsya ile savaşırken gösterdiği başarılar İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_13_13", new trsuperobject(12,13,13,"Yehoaş ölüp atalarına kavuşunca, Samiriyede İsrail krallarının yanına gömüldü ve tahtına Yarovam geçti."));
trsuperobject.biblemap.put("b12_13_14", new trsuperobject(12,13,14,"Elişa ölümcül bir hastalığa yakalandı. İsrail Kralı Yehoaş gidip onu ziyaret etti, ‹‹Baba, baba, İsrailin arabası ve atlıları!›› diyerek ağladı."));
trsuperobject.biblemap.put("b12_13_15", new trsuperobject(12,13,15,"Elişa ona, ‹‹Bir yayla birkaç ok al›› dedi. Kral yayla okları aldı."));
trsuperobject.biblemap.put("b12_13_16", new trsuperobject(12,13,16,"Elişa, ‹‹Yayı hazırla›› dedi. Kral yayı hazırlayınca, Elişa ellerini kralın elleri üzerine koydu."));
trsuperobject.biblemap.put("b12_13_17", new trsuperobject(12,13,17,"‹‹Doğuya bakan pencereyi aç›› dedi. Kral pencereyi açtı. Elişa, ‹‹Oku at!›› dedi. Kral oku atınca, Elişa, ‹‹Bu ok Aramlılara karşı sizi zafere ulaştıracak RABbin kurtarış okudur›› dedi, ‹‹Afekte onları kesin bozguna uğratacaksınız.››"));
trsuperobject.biblemap.put("b12_13_18", new trsuperobject(12,13,18,"Sonra, ‹‹Öbür okları al›› dedi. İsrail Kralı okları alınca, Elişa, ‹‹Onları yere vur›› dedi. Kral okları üç kez yere vurdu ve durdu."));
trsuperobject.biblemap.put("b12_13_19", new trsuperobject(12,13,19,"Buna öfkelenen Tanrı adamı Elişa, ‹‹Beş altı kez vurmalıydın, o zaman Aramlılara karşı kesin bir zafer kazanırdın›› dedi, ‹‹Ama şimdi Aramlıları ancak üç kez bozguna uğratacaksın.››"));
trsuperobject.biblemap.put("b12_13_20", new trsuperobject(12,13,20,"Elişa öldü ve gömüldü. Her ilkbaharda Moav akıncıları İsrail topraklarına girerlerdi."));
trsuperobject.biblemap.put("b12_13_21", new trsuperobject(12,13,21,"Bir keresinde İsrailliler, ölü gömerken akıncıların geldiğini görünce, ölüyü Elişanın mezarına atıp kaçtılar. Ölü Elişanın kemiklerine dokununca dirilip ayağa kalktı."));
trsuperobject.biblemap.put("b12_13_22", new trsuperobject(12,13,22,"Aram Kralı Hazael Yehoahazın krallığı boyunca İsraillilere baskı yaptı."));
trsuperobject.biblemap.put("b12_13_23", new trsuperobject(12,13,23,"Ama RAB, İbrahim, İshak ve Yakupla yaptığı antlaşmadan ötürü, İsraillilere iyilik etti. Onlara acıyıp yardım etti. Yok olmalarına izin vermedi. Onları şimdiye kadar huzurundan atmadı."));
trsuperobject.biblemap.put("b12_13_24", new trsuperobject(12,13,24,"Aram Kralı Hazael ölünce yerine oğlu Ben-Hadat kral oldu."));
trsuperobject.biblemap.put("b12_13_25", new trsuperobject(12,13,25,"Bunun üzerine İsrail Kralı Yehoaş, babası Yehoahaz'ın krallığı döneminde Hazael oğlu Ben-Hadat'ın savaşta ele geçirmiş olduğu kentleri geri aldı. Onu üç kez bozguna uğratıp İsrail kentlerine yeniden sahip oldu."));
trsuperobject.biblemap.put("b12_14_1", new trsuperobject(12,14,1,"İsrail Kralı Yehoahaz oğlu Yehoaşın krallığının ikinci yılında Yoaş oğlu Amatsya Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b12_14_2", new trsuperobject(12,14,2,"Amatsya yirmi beş yaşında kral oldu ve Yeruşalimde yirmi dokuz yıl krallık yaptı. Annesi Yeruşalimli Yehoaddandı."));
trsuperobject.biblemap.put("b12_14_3", new trsuperobject(12,14,3,"Amatsya RABbin gözünde doğru olanı yaptıysa da atası Davut gibi değildi. Her konuda babası Yoaşı örnek aldı."));
trsuperobject.biblemap.put("b12_14_4", new trsuperobject(12,14,4,"Ancak alışılagelen tapınma yerleri henüz kaldırılmamıştı ve halk oralarda hâlâ kurban kesip buhur yakıyordu."));
trsuperobject.biblemap.put("b12_14_5", new trsuperobject(12,14,5,"Amatsya krallığını güçlendirdikten sonra, babasını öldüren görevlileri ortadan kaldırdı."));
trsuperobject.biblemap.put("b12_14_6", new trsuperobject(12,14,6,"Ancak Musanın Kitabındaki yasaya uyarak katillerin çocuklarını öldürtmedi. Çünkü RAB, ‹‹Ne babalar çocuklarının yerine öldürülecek, ne de çocuklar babalarının yerine. Herkes kendi günahı için öldürülecek›› diye buyurmuştu."));
trsuperobject.biblemap.put("b12_14_7", new trsuperobject(12,14,7,"Amatsya Tuz Vadisinde on bin Edomlu asker öldürdü. Savaşarak Selayı ele geçirdi ve oraya Yokteel adını verdi. Orası hâlâ aynı adla anılmaktadır."));
trsuperobject.biblemap.put("b12_14_8", new trsuperobject(12,14,8,"Bundan sonra Amatsya, Yehu oğlu Yehoahaz oğlu İsrail Kralı Yehoaşa, ‹‹Gel, yüz yüze görüşelim›› diye haber gönderdi."));
trsuperobject.biblemap.put("b12_14_9", new trsuperobject(12,14,9,"İsrail Kralı Yehoaş da karşılık olarak Yahuda Kralı Amatsyaya şu haberi gönderdi: ‹‹Lübnanda dikenli bir çalı, sedir ağacına, ‹Kızını oğluma eş olarak ver› diye haber yollar. O sırada oradan geçen yabanıl bir hayvan basıp çalıyı çiğner."));
trsuperobject.biblemap.put("b12_14_10", new trsuperobject(12,14,10,"Edomluları bozguna uğrattın diye böbürleniyorsun. Bu zafer sana yeter. Otur evinde! Niçin bela arıyorsun? Kendi başını da, Yahuda halkının başını da derde sokacaksın.››"));
trsuperobject.biblemap.put("b12_14_11", new trsuperobject(12,14,11,"Ne var ki, Amatsya dinlemek istemedi. Derken İsrail Kralı Yehoaş Yahuda Kralı Amatsyanın üzerine yürüdü. İki ordu Yahudanın Beytşemeş Kentinde karşılaştı."));
trsuperobject.biblemap.put("b12_14_12", new trsuperobject(12,14,12,"İsraillilerin önünde bozguna uğrayan Yahudalılar evlerine kaçtı."));
trsuperobject.biblemap.put("b12_14_13", new trsuperobject(12,14,13,"İsrail Kralı Yehoaş, Ahazya oğlu Yoaş oğlu Yahuda Kralı Amatsyayı Beytşemeşte yakaladı. Sonra Yeruşalime girip Efrayim Kapısından Köşe Kapısına kadar Yeruşalim surlarının dört yüz arşınlıkfş bölümünü yıktırdı."));
trsuperobject.biblemap.put("b12_14_14", new trsuperobject(12,14,14,"RABbin Tapınağında ve sarayın hazinelerinde bulduğu altını, gümüşü ve bütün eşyaları aldı. Ayrıca bazı adamları da rehine olarak yanına alıp Samiriyeye döndü."));
trsuperobject.biblemap.put("b12_14_15", new trsuperobject(12,14,15,"Yehoaşın krallığı dönemindeki öteki olaylar, yaptıkları ve Yahuda Kralı Amatsya ile savaşırken gösterdiği başarılar İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_14_16", new trsuperobject(12,14,16,"Yehoaş ölüp atalarına kavuşunca, Samiriyede İsrail krallarının yanına gömüldü. Yerine oğlu Yarovam kral oldu."));
trsuperobject.biblemap.put("b12_14_17", new trsuperobject(12,14,17,"Yahuda Kralı Yoaş oğlu Amatsya, İsrail Kralı Yehoahaz oğlu Yehoaşın ölümünden sonra on beş yıl daha yaşadı."));
trsuperobject.biblemap.put("b12_14_18", new trsuperobject(12,14,18,"Amatsyanın krallığı dönemindeki öteki olaylar Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_14_19", new trsuperobject(12,14,19,"Yeruşalimde Amatsyaya bir düzen kurulmuştu. Amatsya Lakişe kaçtı. Ardından adam göndererek onu öldürttüler."));
trsuperobject.biblemap.put("b12_14_20", new trsuperobject(12,14,20,"Ölüsü at sırtında Yeruşalime getirildi, Davut Kentinde atalarının yanına gömüldü."));
trsuperobject.biblemap.put("b12_14_21", new trsuperobject(12,14,21,"Yahuda halkı Amatsyanın yerine on altı yaşındaki oğlu Azaryayı kral yaptı."));
trsuperobject.biblemap.put("b12_14_22", new trsuperobject(12,14,22,"Babası Amatsya ölüp atalarına kavuştuktan sonra Azarya Eylat Kentini onarıp Yahuda topraklarına kattı."));
trsuperobject.biblemap.put("b12_14_23", new trsuperobject(12,14,23,"Yahuda Kralı Yoaş oğlu Amatsyanın krallığının on beşinci yılında Yehoaş oğlu Yarovam Samiriyede İsrail Kralı oldu ve kırk bir yıl krallık yaptı."));
trsuperobject.biblemap.put("b12_14_24", new trsuperobject(12,14,24,"Yarovam RABbin gözünde kötü olanı yaptı ve Nevat oğlu Yarovamın İsraili sürüklediği günahlardan ayrılmadı."));
trsuperobject.biblemap.put("b12_14_25", new trsuperobject(12,14,25,"İsrailin Tanrısı RABbin, kulu Gat-Heferli Amittay oğlu Yunus Peygamber aracılığıyla söylediği söz uyarınca, Yarovam Levo-Hamattan Arava Gölünefü kadar İsrail topraklarını yeniden ele geçirdi."));
trsuperobject.biblemap.put("b12_14_26", new trsuperobject(12,14,26,"RAB İsrailin çektiği sıkıntıyı görmüştü. Genç yaşlı herkes acı içinde kıvranıyordu. İsraile yardım edecek kimse yoktu."));
trsuperobject.biblemap.put("b12_14_27", new trsuperobject(12,14,27,"RAB İsrailin adını yeryüzünden silmek istemiyordu. Onun için, Yehoaş oğlu Yarovam aracılığıyla onları kurtardı."));
trsuperobject.biblemap.put("b12_14_28", new trsuperobject(12,14,28,"Yarovamın krallığı dönemindeki öteki olaylar, bütün yaptıkları, askeri başarıları, eskiden Yaudiye ait olan Şam ve Hama kentlerini yeniden İsrail topraklarına katışı İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_14_29", new trsuperobject(12,14,29,"Yarovam ölüp ataları olan İsrail krallarına kavuştu. Yerine oğlu Zekeriya kral oldu. o dönemde topraklarının çoğu bugünkü Suriye'de bulunan küçük bir krallık. Başkenti Gaziantep İli İslahiye İlçesi'ne bağlı Zincirli Köyü'ydü."));
