trsuperobject.biblemap.put("b12_15_20", new trsuperobject(12,15,20,"İsraildeki bütün zenginleri adam başı elli şekel gümüş ödemekle yükümlü kılarak Asur Kralına verilen gümüşü karşıladı. Böylece Asur Kralı İsrail topraklarından çekilip ülkesine döndü. (Tiglat-Pileserin Babilce adı.)"));
trsuperobject.biblemap.put("b12_15_21", new trsuperobject(12,15,21,"Menahemin krallığı dönemindeki öteki olaylar ve bütün yaptıkları İsrail krallarının tarihinde yazılıdır."));
