trsuperobject.biblemap.put("b12_15_28", new trsuperobject(12,15,28,"RABbin gözünde kötü olanı yaptı ve Nevat oğlu Yarovamın İsraili sürüklediği günahlardan ayrılmadı."));
trsuperobject.biblemap.put("b12_15_29", new trsuperobject(12,15,29,"İsrail Kralı Pekahın krallığı sırasında, Asur Kralı Tiglat-Pileser İsrailin İyon, Avel-Beytmaaka, Yanoah, Kedeş, Hasor kentleriyle Gilat, Celile ve Naftali bölgelerini ele geçirerek halkı Asura sürdü."));
