trsuperobject.biblemap.put("b12_16_16", new trsuperobject(12,16,16,"Kâhin Uriya Kral Ahazın bütün buyruklarını yerine getirdi."));
trsuperobject.biblemap.put("b12_16_17", new trsuperobject(12,16,17,"Kral Ahaz tapınaktaki kazanların üzerine oturduğu ayaklıkların yan aynalıklarını söküp kazanları kaldırdı. Havuzu tunç boğaların üzerinden indirip taş bir döşeme üzerine yerleştirdi."));
trsuperobject.biblemap.put("b12_16_18", new trsuperobject(12,16,18,"Asur Kralını hoşnut etmek için RABbin Tapınağına konan kral kürsüsünün setini kaldırıp kralın tapınağa girmek için kullandığı özel kapıyı kapattı. seti››, Masoretik metin ‹‹Şabat günleri RABbin Tapınağına gitmek için kullanılan üstü kapalı yol.››"));
trsuperobject.biblemap.put("b12_16_19", new trsuperobject(12,16,19,"Ahazın krallığı dönemindeki öteki olaylar ve yaptıkları Yahuda krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b12_16_20", new trsuperobject(12,16,20,"Ahaz ölüp atalarına kavuşunca, Davut Kenti'nde atalarının yanına gömüldü. Yerine oğlu Hizkiya kral oldu."));
trsuperobject.biblemap.put("b12_17_1", new trsuperobject(12,17,1,"Yahuda Kralı Ahazın krallığının on ikinci yılında Ela oğlu Hoşea Samiriyede İsrail Kralı oldu ve dokuz yıl krallık yaptı."));
trsuperobject.biblemap.put("b12_17_3", new trsuperobject(12,17,3,"Asur Kralı Şalmaneser Hoşeaya savaş açtı. Hoşea teslim olup haraç ödemeye başladı."));
