trsuperobject.biblemap.put("b13_3_21", new trsuperobject(13,3,21,"Hananyanın oğulları: Pelatya, Yeşaya.Yeşaya Refayanın,Refaya Arnanın,Arnan Ovadyanın,Ovadya Şekanyanın babasıydı."));
trsuperobject.biblemap.put("b13_3_22", new trsuperobject(13,3,22,"Şekanyanın oğlu: Şemaya. Şemayanın oğulları: Hattuş, Yigal, Bariah, Nearya, Şafat. Toplam altı kişiydi."));
trsuperobject.biblemap.put("b13_3_23", new trsuperobject(13,3,23,"Nearyanın oğulları: Elyoenay, Hizkiya, Azrikam. Toplam üç kişiydi."));
trsuperobject.biblemap.put("b13_3_24", new trsuperobject(13,3,24,"Elyoenay'ın oğulları: Hodavya, Elyaşiv, Pelaya, Akkuv, Yohanan, Delaya, Anani. Toplam yedi kişiydi."));
trsuperobject.biblemap.put("b13_4_1", new trsuperobject(13,4,1,"Yahuda oğulları: Peres, Hesron, Karmi, Hur, Şoval."));
trsuperobject.biblemap.put("b13_4_2", new trsuperobject(13,4,2,"Şoval oğlu Reaya Yahatın babasıydı. Yahat Ahumayın ve Lahatın babasıydı. Soralı boylar bunlardı."));
trsuperobject.biblemap.put("b13_4_3", new trsuperobject(13,4,3,"Etamın oğulları: Yizreel, Yişma, Yidbaş. Kızkardeşlerinin adı Haselelponidir."));
trsuperobject.biblemap.put("b13_4_4", new trsuperobject(13,4,4,"Penuel Gedorun babasıydı. Ezer Huşanın babasıydı. Bunlar Beytlehemin kurucusu ve Efratın ilk oğlu Hurun torunlarıydı. ya da ‹‹Kurucusu››."));
trsuperobject.biblemap.put("b13_4_5", new trsuperobject(13,4,5,"Tekoanın kurucusu Aşhurun Helah ve Naara adında iki karısı vardı."));
trsuperobject.biblemap.put("b13_4_6", new trsuperobject(13,4,6,"Naara ona Ahuzzam, Hefer, Temeni ve Haahaştariyi doğurdu. Bunlar Naaranın oğullarıydı."));
trsuperobject.biblemap.put("b13_4_7", new trsuperobject(13,4,7,"Helahın oğulları: Seret, Yishar, Etnan"));
trsuperobject.biblemap.put("b13_4_8", new trsuperobject(13,4,8,"ve Anuvla Hassovevanın babası Kos. Kos Harum oğlu Aharhel boylarının atasıydı."));
trsuperobject.biblemap.put("b13_4_9", new trsuperobject(13,4,9,"Yabesfı kardeşlerinden daha saygın biriydi. Annesi, ‹‹Onu acı çekerek doğurdum›› diyerek adını Yabes koymuştu."));
trsuperobject.biblemap.put("b13_4_10", new trsuperobject(13,4,10,"Yabes, İsrailin Tanrısına, ‹‹Ne olur, beni kutsa, sınırlarımı genişlet!›› diye yakardı, ‹‹Elin üzerimde olsun, beni kötülükten koru. Öyle ki, acı çekmeyeyim.›› Tanrı onun yakarışını duydu."));
trsuperobject.biblemap.put("b13_4_11", new trsuperobject(13,4,11,"Şuhanın kardeşi Keluv, Eştonun babası Mehirin babasıydı."));
trsuperobject.biblemap.put("b13_4_12", new trsuperobject(13,4,12,"Eşton Beytrafanın, Paseahın ve İr-Nahaşın kurucusu Tehinnanın babasıydı. Bunlar Rekalılardır."));
trsuperobject.biblemap.put("b13_4_13-14", new trsuperobject(13,4,13-14,"Kenazın oğulları: Otniel, Seraya. Otnielin oğulları: Hatat ve Meonotay. Meonotay Ofranın babasıydı. Seraya Ge-Haraşimin kurucusu Yoavın babasıydı. Bunlar el işlerinde becerikli kişilerdi."));
trsuperobject.biblemap.put("b13_4_15", new trsuperobject(13,4,15,"Yefunne oğlu Kalevin oğulları: İru, Ela, Naam. Elanın oğlu: Kenaz."));
trsuperobject.biblemap.put("b13_4_16", new trsuperobject(13,4,16,"Yehallelelin oğulları: Zif, Zifa, Tirya, Asarel."));
trsuperobject.biblemap.put("b13_4_17", new trsuperobject(13,4,17,"Ezranın oğulları: Yeter, Meret, Efer, Yalon. Meretin karılarından biri Miryamı, Şammayı ve Eştemoanın kurucusu Yişbahı doğurdu."));
trsuperobject.biblemap.put("b13_4_18", new trsuperobject(13,4,18,"Bunlar Meretin evlendiği firavunun kızı Bityanın doğurduğu çocuklardır. Meretin Yahudalı karısı, Gedorun kurucusu Yereti, Sokonun kurucusu Heveri, Zanoahın kurucusu Yekutieli doğurdu."));
trsuperobject.biblemap.put("b13_4_19", new trsuperobject(13,4,19,"Hodiya Nahamın kızkardeşiyle evlendi. Ondan doğan oğulları Keilada yaşayan Garm ve Eştemoada yaşayan Maaka boylarının atasıydı."));
trsuperobject.biblemap.put("b13_4_20", new trsuperobject(13,4,20,"Şimonun oğulları: Amnon, Rinna, Ben-Hanan, Tilon. Yişinin torunları: Zohet ve Ben-Zohet."));
trsuperobject.biblemap.put("b13_4_21", new trsuperobject(13,4,21,"Yahuda oğlu Selanın oğulları: Lekanın kurucusu Er, Mareşanın kurucusu Lada, Beytaşbeada ince keten işinde çalışanların boyları,"));
trsuperobject.biblemap.put("b13_4_22", new trsuperobject(13,4,22,"Yokim, Kozevalılar, Moavlı kadınlarla evlenen Yoaşla Saraf ve Yaşuvi-Lehem. Bu kayıtlar eskidir."));
trsuperobject.biblemap.put("b13_4_23", new trsuperobject(13,4,23,"Netayim ve Gederada oturur, çömlekçilik yapar ve kral için çalışırlardı."));
trsuperobject.biblemap.put("b13_4_24", new trsuperobject(13,4,24,"Şimonoğulları: Nemuel, Yamin, Yariv, Zerah, Şaul."));
trsuperobject.biblemap.put("b13_4_25", new trsuperobject(13,4,25,"Şallum Şaulun oğluydu. Mivsam Şallumun, Mişma da Mivsamın oğluydu."));
trsuperobject.biblemap.put("b13_4_26", new trsuperobject(13,4,26,"Mişmanın torunları: Hammuel Mişmanın oğluydu. Zakkur Hammuelin, Şimi de Zakkurun oğluydu."));
trsuperobject.biblemap.put("b13_4_27", new trsuperobject(13,4,27,"Şiminin on altı oğlu, altı kızı vardı. Ancak kardeşlerinin çok sayıda çocukları yoktu. Bu yüzden Şimon oymağı, sayıca Yahuda oymağı kadar artmadı."));
trsuperobject.biblemap.put("b13_4_28-31", new trsuperobject(13,4,28-31,"Şimonoğulları Beer-Şeva, Molada, Hasar-Şual, Bilha, Esem, Tolat, Betuel, Horma, Ziklak, Beytmarkavot, Hasar-Susim, Beytbiri ve Şaarayim kentlerinde yaşadılar. Davut dönemine dek kentleri bunlardı."));
trsuperobject.biblemap.put("b13_4_32", new trsuperobject(13,4,32,"Ayrıca şu beş ilçede de yaşadılar: Etam, Ayin, Rimmon, Token, Aşan."));
trsuperobject.biblemap.put("b13_4_33", new trsuperobject(13,4,33,"Baalata kadar uzanan bu kentlerin çevresindeki bütün köyler Şimonoğullarına aitti. Ailelerinin soy kütüğünü de tuttular. ‹‹Baal›› (bkz. Yşu.19:8)."));
trsuperobject.biblemap.put("b13_4_34", new trsuperobject(13,4,34,"Meşovav, Yamlek, Amatsya oğlu Yoşa,"));
trsuperobject.biblemap.put("b13_4_35", new trsuperobject(13,4,35,"Yoel, Asiel oğlu Seraya oğlu Yoşivya oğlu Yehu,"));
trsuperobject.biblemap.put("b13_4_36", new trsuperobject(13,4,36,"Elyoenay, Yaakova, Yeşohaya, Asaya, Adiel, Yesimiel, Benaya"));
trsuperobject.biblemap.put("b13_4_37", new trsuperobject(13,4,37,"ve Şemaya oğlu Şimri oğlu Yedaya oğlu Allon oğlu Şifi oğlu Ziza."));
trsuperobject.biblemap.put("b13_4_38", new trsuperobject(13,4,38,"Yukarıda adı geçenlerin her biri, ait olduğu boyun başıydı. Aileleri sayıca çoğaldılar."));
trsuperobject.biblemap.put("b13_4_39", new trsuperobject(13,4,39,"Sürülerine otlak aramak için Gedor yakınlarına, vadinin doğusuna kadar gittiler."));
trsuperobject.biblemap.put("b13_4_40", new trsuperobject(13,4,40,"Orada çok zengin otlaklar buldular. Ülke geniş, rahat ve huzur doluydu. Eskiden orada Hamın soyundan gelenler yaşardı."));
trsuperobject.biblemap.put("b13_4_41", new trsuperobject(13,4,41,"Yukarıda adı yazılı olanlar, Yahuda Kralı Hizkiya döneminde geldiler. Hamlıların çadırlarına ve orada yaşayan Meunlulara saldırıp tümünü öldürdüler. Sonra ülkelerine yerleştiler. Bugün de oradalar. Çünkü orada sürüleri için otlak vardı."));
trsuperobject.biblemap.put("b13_4_42", new trsuperobject(13,4,42,"Yişinin oğulları Pelatya, Nearya, Refaya, Uzziel önderliğinde Şimon oymağından beş yüz kişi Seir dağlık bölgesine gidip"));
trsuperobject.biblemap.put("b13_4_43", new trsuperobject(13,4,43,"Amalekliler'den sağ kalanları öldürdüler. Bugün de orada yaşıyorlar."));
trsuperobject.biblemap.put("b13_5_1", new trsuperobject(13,5,1,"İsrailin ilk oğlu Rubenin oğulları. -Ruben ilk doğandır. Babasının yatağına yatıp onu kirlettiği için ilk oğulluk hakkı İsrail oğlu Yusufun oğullarına verildi. Bu yüzden Ruben soy kütüğüne ilk oğulluk hakkına göre yazılmadı."));
trsuperobject.biblemap.put("b13_5_2", new trsuperobject(13,5,2,"Yahuda kardeşleri arasında en güçlü olandı, önderlik hep onun soyundan çıktı. Ama ilk oğulluk hakkı Yusufa aitti.-"));
trsuperobject.biblemap.put("b13_5_3", new trsuperobject(13,5,3,"İsrailin ilk oğlu Rubenin oğulları: Hanok, Pallu, Hesron, Karmi."));
trsuperobject.biblemap.put("b13_5_4-6", new trsuperobject(13,5,4-6,"Yoelin soyu:Şemaya Yoelin,Gog Şemayanın,Şimi Gogun,Mika Şiminin,Reaya Mikanın,Baal Reayanın,Beera Baalın oğluydu. Rubenlilerin önderiydi."));
trsuperobject.biblemap.put("b13_5_7", new trsuperobject(13,5,7,"Boylarına göre aile soy kütüğüne yazılan akrabaları şunlardır: Önder Yeiel, Zekeriya,"));
trsuperobject.biblemap.put("b13_5_8", new trsuperobject(13,5,8,"Yoel oğlu Şema oğlu Azaz oğlu Bala. Bunlar Aroerde, Nevo ve Baal-Meona kadar uzanan topraklarda yaşadılar."));
trsuperobject.biblemap.put("b13_5_9", new trsuperobject(13,5,9,"Doğuda Fırattan çöle kadar uzanan topraklara yayıldılar. Çünkü Gilat bölgesinde sığırları çoğalmıştı."));
trsuperobject.biblemap.put("b13_5_10", new trsuperobject(13,5,10,"Rubenliler Saul döneminde Hacerlilere karşı savaş açtı. Onları yenilgiye uğratıp Gilatın doğusunda kalan topraklardaki çadırlarını ele geçirdiler."));
trsuperobject.biblemap.put("b13_5_11", new trsuperobject(13,5,11,"Gadlılar, Başanda, Selkaya kadar uzanan topraklarda Rubenlilerin karşısında yaşadılar."));
trsuperobject.biblemap.put("b13_5_12", new trsuperobject(13,5,12,"Önderleri Yoeldi; ikinci derecede önemli Şafam, Başanda ise Yanay ve Şafattı."));
trsuperobject.biblemap.put("b13_5_13", new trsuperobject(13,5,13,"Boylarına göre akrabaları şunlardır: Mikael, Meşullam, Şeva, Yoray, Yakan, Zia, Ever. Toplam yedi kişiydi."));
trsuperobject.biblemap.put("b13_5_14", new trsuperobject(13,5,14,"Bunlar Bûz oğlu Yahdo oğlu Yeşişay oğlu Mikael oğlu Gilat oğlu Yaroah oğlu Huri oğlu Avihayilin oğullarıydı."));
trsuperobject.biblemap.put("b13_5_15", new trsuperobject(13,5,15,"Guni oğlu Avdiel oğlu Ahi bu boyların başıydı."));
trsuperobject.biblemap.put("b13_5_16", new trsuperobject(13,5,16,"Gadlılar Gilatta, Başanda, çevredeki köylerde ve Şaronun bütün otlaklarında yaşadılar."));
trsuperobject.biblemap.put("b13_5_17", new trsuperobject(13,5,17,"Bunların hepsi Yahuda Kralı Yotamın ve İsrail Kralı Yarovamın döneminde soy kütüğüne yazıldılar."));
trsuperobject.biblemap.put("b13_5_18", new trsuperobject(13,5,18,"Rubenlilerin, Gadlıların ve Manaşşe oymağının yarısının kalkan ve kılıç kullanabilen, ok atabilen, savaş için eğitilmiş 44 760 yiğit askeri vardı."));
trsuperobject.biblemap.put("b13_5_19", new trsuperobject(13,5,19,"Hacerlilere, Yetura, Nafişe, Nodava karşı savaş açtılar."));
trsuperobject.biblemap.put("b13_5_20", new trsuperobject(13,5,20,"Onlarla savaşırken yardım gördüler. Tanrı Hacerlileri ve onlarla birlikte olanları ellerine teslim etti. Çünkü savaş sırasında Tanrıya yalvarmışlar, Ona güvenmişlerdi. Bu yüzden Tanrı yalvarışlarını yanıtladı."));
trsuperobject.biblemap.put("b13_5_21", new trsuperobject(13,5,21,"Hacerlilerin hayvanlarını ele geçirdiler: Elli bin deve, iki yüz elli bin davar, iki bin eşek. Tutsak olarak da yüz bin kişi aldılar."));
trsuperobject.biblemap.put("b13_5_22", new trsuperobject(13,5,22,"Savaş Tanrının isteğiyle olduğu için düşmandan birçok kişiyi öldürdüler. Sürgün dönemine dek Hacerlilerin topraklarında yaşadılar."));
trsuperobject.biblemap.put("b13_5_23", new trsuperobject(13,5,23,"Manaşşe oymağının yarısı Başandan Baal-Hermona, Senir, yani Hermon Dağına kadar uzanan topraklarda yaşadı ve sayıca çoğaldı."));
trsuperobject.biblemap.put("b13_5_24", new trsuperobject(13,5,24,"Boy başları şunlardı: Efer, Yişi, Eliel, Azriel, Yeremya, Hodavya, Yahdiel. Bunlar yiğit savaşçılar, ünlü kişiler ve boy başlarıydı."));
trsuperobject.biblemap.put("b13_5_25", new trsuperobject(13,5,25,"Ne var ki atalarının Tanrısına bağlı kalmadılar. Tanrıya ihanet ederek önlerinden yok ettiği ulusların ilahlarına yöneldiler."));
trsuperobject.biblemap.put("b13_5_26", new trsuperobject(13,5,26,"Bu yüzden İsrail'in Tanrısı, Tiglat-Pileser diye bilinen Asur Kralı Pûl'u harekete geçirdi. Asur Kralı Rubenliler'i, Gadlılar'ı, Manaşşe oymağının yarısını tutsak edip Halah'a, Habur'a, Hara'ya, Gozan Irmağı'na sürdü. Onlar bugün de oralarda yaşıyorlar."));
trsuperobject.biblemap.put("b13_6_1", new trsuperobject(13,6,1,"Levinin oğulları: Gerşon, Kehat, Merari."));
trsuperobject.biblemap.put("b13_6_2", new trsuperobject(13,6,2,"Kehatın oğulları: Amram, Yishar, Hevron, Uzziel."));
trsuperobject.biblemap.put("b13_6_3", new trsuperobject(13,6,3,"Amramın çocukları: Harun, Musa, Miryam. Harunun oğulları: Nadav, Avihu, Elazar, İtamar."));
trsuperobject.biblemap.put("b13_6_4", new trsuperobject(13,6,4,"Pinehas Elazarın oğluydu.Avişua Pinehasın,"));
