trsuperobject.biblemap.put("b13_6_9", new trsuperobject(13,6,9,"Azarya Ahimaasın,Yohanan Azaryanın,"));
trsuperobject.biblemap.put("b13_6_10", new trsuperobject(13,6,10,"Azarya Yohananın oğluydu. oydu.-"));
trsuperobject.biblemap.put("b13_6_11", new trsuperobject(13,6,11,"Amarya Azaryanın,Ahituv Amaryanın,"));
trsuperobject.biblemap.put("b13_6_12", new trsuperobject(13,6,12,"Sadok Ahituvun,Şallum Sadokun,"));
