trsuperobject.biblemap.put("b13_6_14", new trsuperobject(13,6,14,"Seraya Azaryanın,Yehosadak Serayanın oğluydu."));
trsuperobject.biblemap.put("b13_6_15", new trsuperobject(13,6,15,"RAB Nebukadnessar aracılığıyla Yahuda ve Yeruşalim halkını sürdüğünde Yehosadak da sürgüne gitmişti."));
trsuperobject.biblemap.put("b13_6_16", new trsuperobject(13,6,16,"Levinin oğulları: Gerşon, Kehat, Merari."));
trsuperobject.biblemap.put("b13_6_17", new trsuperobject(13,6,17,"Gerşonun oğulları: Livni, Şimi."));
trsuperobject.biblemap.put("b13_6_18", new trsuperobject(13,6,18,"Kehatın oğulları: Amram, Yishar, Hevron, Uzziel."));
trsuperobject.biblemap.put("b13_6_19", new trsuperobject(13,6,19,"Merarinin oğulları: Mahli, Muşi. Soylarına göre yazılan Levi oymağının boyları şunlardır:"));
trsuperobject.biblemap.put("b13_6_20", new trsuperobject(13,6,20,"Gerşonun soyu:Livni Gerşonun,Yahat Livninin,Zimma Yahatın,"));
trsuperobject.biblemap.put("b13_6_21", new trsuperobject(13,6,21,"Yoah Zimmanın,İddo Yoahın,Zerah İddonun,Yeateray Zerahın oğluydu."));
trsuperobject.biblemap.put("b13_6_22", new trsuperobject(13,6,22,"Kehatın soyu:Amminadav Kehatın,Korah Amminadavın,Assir Korahın,"));
trsuperobject.biblemap.put("b13_6_23", new trsuperobject(13,6,23,"Elkana Assirin,Evyasaf Elkananın,Assir Evyasafın,"));
trsuperobject.biblemap.put("b13_6_24", new trsuperobject(13,6,24,"Tahat Assirin,Uriel Tahatın,Uzziya Urielin,Şaul Uzziyanın oğluydu."));
trsuperobject.biblemap.put("b13_6_25", new trsuperobject(13,6,25,"Elkananın öbür oğulları: Amasay, Ahimot."));
trsuperobject.biblemap.put("b13_6_26", new trsuperobject(13,6,26,"Elkana Ahimotun,Sofay Elkananın,Nahat Sofayın,"));
trsuperobject.biblemap.put("b13_6_27", new trsuperobject(13,6,27,"Eliav Nahatın,Yeroham Eliavın,Elkana Yerohamın,Samuel Elkananın oğluydu. metinde geçmemektedir."));
trsuperobject.biblemap.put("b13_6_28", new trsuperobject(13,6,28,"Samuelin oğulları: İlk oğlu Yoel, ikincisi Aviya. ad Masoretik metinde geçmemektedir."));
