trsuperobject.biblemap.put("b13_6_31", new trsuperobject(13,6,31,"Antlaşma Sandığı RABbin Tapınağına taşındıktan sonra Davutun orada görevlendirdiği ezgiciler şunlardır."));
trsuperobject.biblemap.put("b13_6_32", new trsuperobject(13,6,32,"Bunlar Süleyman Yeruşalimde RABbin Tapınağını kurana dek Buluşma Çadırında ezgi okuyarak hizmet eder, belirlenmiş kurallar uyarınca görevlerini yerine getirirlerdi."));
trsuperobject.biblemap.put("b13_6_33-38", new trsuperobject(13,6,33-38,"Oğullarıyla birlikte görev yapan kişiler şunlardır: Kehatoğullarından: Ezgici Heman. Heman, İsrail oğlu Levi oğlu Kehat oğlu Yishar oğlu Korah oğlu Evyasaf oğlu Assir oğlu Tahat oğlu Sefanya oğlu Azarya oğlu Yoel oğlu Elkana oğlu Amasay oğlu Mahat oğlu Elkana oğlu Suf oğlu Toah oğlu Eliel oğlu Yeroham oğlu Elkana oğlu Samuel oğlu Yoelin oğluydu."));
trsuperobject.biblemap.put("b13_6_39-43", new trsuperobject(13,6,39-43,"Hemanın sağ yanında görev yapan akrabası Asaf. Asaf, Levi oğlu Gerşon oğlu Yahat oğlu Şimi oğlu Zimma oğlu Etan oğlu Adaya oğlu Zerah oğlu Etni oğlu Malkiya oğlu Baaseya oğlu Mikael oğlu Şima oğlu Berekyanın oğluydu."));
trsuperobject.biblemap.put("b13_6_44-47", new trsuperobject(13,6,44-47,"Hemanın solunda görev yapan kardeşleri Merarioğullarından: Etan. Etan, Levi oğlu Merari oğlu Muşi oğlu Mahli oğlu Şemer oğlu Bani oğlu Amsi oğlu Hilkiya oğlu Amatsya oğlu Haşavya oğlu Malluk oğlu Avdi oğlu Kiyşinin oğluydu."));
trsuperobject.biblemap.put("b13_6_48", new trsuperobject(13,6,48,"Bunların Levili akrabaları, çadırın, Tanrının Tapınağının bütün görevlerini yerine getirmek üzere atandılar."));
trsuperobject.biblemap.put("b13_6_49", new trsuperobject(13,6,49,"Ancak, Tanrı kulu Musanın buyruğu uyarınca, yakmalık sunu sunağında ve buhur sunağında sunu sunanlar Harunla oğullarıydı. En Kutsal Yerde yapılan hizmetlerden ve İsraillilerin bağışlanması için sunulan kurbanlardan onlar sorumluydu."));
