trsuperobject.biblemap.put("b13_6_60", new trsuperobject(13,6,60,"Benyamin oymağından da Givon, Geva, Alemet, Anatot ve bunların otlakları verildi. Kehat boylarına verilen bu kentlerin toplam sayısı on üçtü. geçmemektedir (bkz. Yşu.21:16). Yşu.21:17)."));
trsuperobject.biblemap.put("b13_6_61", new trsuperobject(13,6,61,"Geri kalan Kehatoğullarına Manaşşe oymağının yarısına ait boylardan alınan on kent kurayla verildi."));
trsuperobject.biblemap.put("b13_6_62", new trsuperobject(13,6,62,"Gerşonoğullarına boy sayısına göre İssakar, Aşer, Naftali ve Başandaki Manaşşe oymağından alınan on üç kent verildi."));
trsuperobject.biblemap.put("b13_6_63", new trsuperobject(13,6,63,"Merarioğullarına boy sayısına göre Ruben, Gad ve Zevulun oymaklarından alınan on iki kent kurayla verildi."));
