trsuperobject.biblemap.put("b13_6_67-69", new trsuperobject(13,6,67-69,"Efrayim dağlık bölgesinde sığınak kent seçilen Şekem, Gezer, Yokmoam, Beythoron, Ayalon, Gat-Rimmon ve bunların otlakları verildi."));
trsuperobject.biblemap.put("b13_6_70", new trsuperobject(13,6,70,"İsrailliler Manaşşe oymağının yarısından alınan Aner, Bilam ve bunların otlaklarını Kehat boyunun öbür ailelerine verdiler."));
