trsuperobject.biblemap.put("b13_7_10", new trsuperobject(13,7,10,"Yediaelin oğlu: Bilhan. Bilhanın oğulları: Yeuş, Benyamin, Ehut, Kenaana, Zetan, Tarşiş, Ahişahar."));
