trsuperobject.biblemap.put("b13_9_17", new trsuperobject(13,9,17,"Tapınak kapı nöbetçileri: Şallum, Akkuv, Talmon, Ahiman ve kardeşleri. Şallum başlarıydı."));
trsuperobject.biblemap.put("b13_9_18", new trsuperobject(13,9,18,"Bugüne kadar doğuya bakan Kral Kapısında görevlidirler. Levili bölüklere bağlı kapı nöbetçileri bunlardı."));
trsuperobject.biblemap.put("b13_9_19", new trsuperobject(13,9,19,"Korah oğlu Evyasaf oğlu Kore oğlu Şallum ve ailesinden -Korahoğullarından- olan çalışma arkadaşları Çadırınfş giriş kapısının nöbetçileriydi. Bunların ataları da RABbin ordugahının giriş kapısının nöbetçileriydi."));
trsuperobject.biblemap.put("b13_9_20", new trsuperobject(13,9,20,"Önceleri Elazar oğlu Pinehas onların başıydı. RAB onunlaydı."));
trsuperobject.biblemap.put("b13_9_21", new trsuperobject(13,9,21,"Buluşma Çadırının kapısında Meşelemya oğlu Zekeriya nöbet tutardı. de geçmektedir."));
trsuperobject.biblemap.put("b13_9_22", new trsuperobject(13,9,22,"Giriş kapısına nöbetçi seçilenlerin toplamı 212ydi. Bunlar, köylerinde bağlı oldukları soy kütüğüne yazılıdır. Davutla Bilici Samuel tarafından bu göreve atanmışlardı."));
trsuperobject.biblemap.put("b13_9_23", new trsuperobject(13,9,23,"Oğullarıyla birlikte RABbin Tapınağının, yani Çadırın kapılarında nöbet tutarlardı."));
trsuperobject.biblemap.put("b13_9_24", new trsuperobject(13,9,24,"Nöbetçiler dört yanda -doğuda, batıda, kuzeyde ve güneyde- nöbet tutardı."));
trsuperobject.biblemap.put("b13_9_25", new trsuperobject(13,9,25,"Köylerdeki kardeşleri zaman zaman gelir, yedi günlük bir süre için görevi onlarla paylaşırdı."));
trsuperobject.biblemap.put("b13_9_26", new trsuperobject(13,9,26,"Dört kapının baş nöbetçileri Leviliydi. Bunlar Tanrının Tapınağındaki odalardan ve hazinelerden sorumluydu."));
trsuperobject.biblemap.put("b13_9_27", new trsuperobject(13,9,27,"Geceyi Tanrının Tapınağının çevresinde geçirirlerdi. Çünkü tapınağı koruma ve her sabah kapılarını açma görevi onlarındı."));
trsuperobject.biblemap.put("b13_9_28", new trsuperobject(13,9,28,"Bazıları da tapınak hizmetinde kullanılan eşyalardan sorumluydu. Eşyaları sayarak içeri alır, sayarak dışarıya çıkarırlardı."));
trsuperobject.biblemap.put("b13_9_29", new trsuperobject(13,9,29,"Öbürleri eşyalardan, kutsal yere ait nesnelerden, ince undan, şaraptan, zeytinyağından, günnükten, baharattan sorumluydu."));
trsuperobject.biblemap.put("b13_9_30", new trsuperobject(13,9,30,"Ancak baharatı karıştırıp hazırlama görevi kâhinlerindi."));
trsuperobject.biblemap.put("b13_9_31", new trsuperobject(13,9,31,"Korahoğullarından Şallumun ilk oğlu Levili Mattitya sacda pide pişirme görevine atanmıştı."));
trsuperobject.biblemap.put("b13_9_32", new trsuperobject(13,9,32,"Kardeşleri Kehatoğullarından bazıları da her Şabat Günü adak ekmeklerini hazırlamakla görevliydi."));
trsuperobject.biblemap.put("b13_9_33", new trsuperobject(13,9,33,"Levililerin boy başları olan ezgiciler tapınağın odalarında yaşardı. Başka iş yapmazlardı. Çünkü yaptıkları işten gece gündüz sorumluydular."));
trsuperobject.biblemap.put("b13_9_34", new trsuperobject(13,9,34,"Bunların hepsi soy kütüğüne göre Levililerin boy başları, önderleriydi ve Yeruşalimde yaşarlardı."));
trsuperobject.biblemap.put("b13_9_35", new trsuperobject(13,9,35,"Givonun kurucusu Yeiel, Givonda yaşadı. Karısının adı Maakaydı."));
trsuperobject.biblemap.put("b13_9_36", new trsuperobject(13,9,36,"Yeielin ilk oğlu Avdondu. Öbürleri şunlardı: Sur, Kiş, Baal, Ner, Nadav,"));
