trsuperobject.biblemap.put("b13_9_44", new trsuperobject(13,9,44,"Asel'in altı oğlu vardı. Adları şöyledir: Azrikam, Bokeru, İsmail, Şearya, Ovadya, Hanan. Bütün bunlar Asel'in oğullarıydı."));
trsuperobject.biblemap.put("b13_10_1", new trsuperobject(13,10,1,"Filistliler İsraillilerle savaşa tutuştu. İsrailliler Filistlilerin önünden kaçtı. Birçoğu Gilboa Dağında ölüp yere serildi."));
trsuperobject.biblemap.put("b13_10_2", new trsuperobject(13,10,2,"Filistliler Saulla oğullarının ardına düştüler. Saulun oğulları Yonatanı, Avinadavı ve Malkişuayı yakalayıp öldürdüler."));
trsuperobject.biblemap.put("b13_10_3", new trsuperobject(13,10,3,"Saulun çevresinde savaş kızıştı. Derken Saul Filistli okçular tarafından vuruldu ve yaralandı."));
trsuperobject.biblemap.put("b13_10_4", new trsuperobject(13,10,4,"Saul silahını taşıyan adama, ‹‹Kılıcını çek de bana sapla›› dedi, ‹‹Yoksa bu sünnetsizler gelip benimle alay edecekler.›› Ama silah taşıyıcısı büyük bir korkuya kapılarak bunu yapmak istemedi. Bunun üzerine Saul kılıcını çekip kendini üzerine attı."));
trsuperobject.biblemap.put("b13_10_5", new trsuperobject(13,10,5,"Saulun öldüğünü görünce, silah taşıyıcısı da kendini kılıcının üzerine atıp öldü."));
trsuperobject.biblemap.put("b13_10_6", new trsuperobject(13,10,6,"Böylece Saul, üç oğlu ve bütün ev halkı birlikte öldüler."));
trsuperobject.biblemap.put("b13_10_7", new trsuperobject(13,10,7,"Vadide oturan İsrailliler, İsrail ordusunun kaçtığını, Saulla oğullarının öldüğünü anlayınca, kentlerini terk edip kaçmaya başladılar. Filistliler gelip bu kentlere yerleştiler."));
trsuperobject.biblemap.put("b13_10_8", new trsuperobject(13,10,8,"Ertesi gün Filistliler, öldürülenleri soymak için geldiklerinde, Saulla oğullarının Gilboa Dağında öldüğünü gördüler."));
trsuperobject.biblemap.put("b13_10_9", new trsuperobject(13,10,9,"Saulu soyduktan sonra başını kesip silahlarını aldılar. Sonra bu iyi haberi putlarına ve halka duyurmaları için Filist ülkesinin her yanına ulaklar gönderdiler."));
trsuperobject.biblemap.put("b13_10_10", new trsuperobject(13,10,10,"Saulun silahlarını ilahlarının tapınağına koyup başını Dagon Tapınağına çaktılar."));
trsuperobject.biblemap.put("b13_10_11", new trsuperobject(13,10,11,"Yaveş-Gilat halkı Filistlilerin Saula yaptıklarını duydu."));
trsuperobject.biblemap.put("b13_10_12", new trsuperobject(13,10,12,"Bütün yiğitler gidip Saulla oğullarının cesetlerini Yaveşe getirdiler. Sonra kemiklerini Yaveşteki yabanıl fıstık ağacının altına gömdüler ve yedi gün oruç tuttular."));
trsuperobject.biblemap.put("b13_10_13", new trsuperobject(13,10,13,"Saul RAB'be ihanet ettiği için öldü. RAB'bin sözünü yerine getirmedi. Yol göstermesi için RAB'be danışacağına bir cinciye danıştı. Bu yüzden RAB onu öldürdü. Krallığını da İşay oğlu Davut'a devretti."));
trsuperobject.biblemap.put("b13_11_1", new trsuperobject(13,11,1,"İsraillilerin tümü Hevronda bulunan Davuta gelip şöyle dediler: ‹‹Biz senin etin, kemiğiniz."));
trsuperobject.biblemap.put("b13_11_2", new trsuperobject(13,11,2,"Geçmişte Saul kralımızken, savaşta İsraile komuta eden sendin. Tanrın RAB sana, ‹Halkım İsraili sen güdecek, onlara sen önder olacaksın› diye söz verdi.››"));
trsuperobject.biblemap.put("b13_11_3", new trsuperobject(13,11,3,"İsrailin bütün ileri gelenleri Hevrona, Kral Davutun yanına gelince, Davut RABbin önünde orada onlarla bir antlaşma yaptı. Onlar da RABbin Samuel aracılığıyla söylediği söz uyarınca, Davutu İsrail Kralı olarak meshettiler."));
trsuperobject.biblemap.put("b13_11_4", new trsuperobject(13,11,4,"Kral Davutla İsrailliler Yevus diye bilinen Yeruşalime saldırmak için yola çıktılar. Orada yaşayan Yevuslular"));
trsuperobject.biblemap.put("b13_11_5", new trsuperobject(13,11,5,"Davuta, ‹‹Sen buraya giremezsin›› dediler. Ne var ki, Davut Siyon Kalesini, Davut Kentini ele geçirdi."));
trsuperobject.biblemap.put("b13_11_6", new trsuperobject(13,11,6,"Davut, ‹‹Yevuslulara ilk saldıran kişi komutan ve önder olacak›› demişti. İlk saldırıyı Seruya oğlu Yoav yaptı, böylece ordu komutanı oldu."));
trsuperobject.biblemap.put("b13_11_7", new trsuperobject(13,11,7,"Bundan sonra Davut kalede oturmaya başladı. Bunun için oraya ‹‹Davut Kenti›› adı verildi."));
trsuperobject.biblemap.put("b13_11_8", new trsuperobject(13,11,8,"Çevredeki bölgeyi, Millodan çevre surlara kadar uzanan kesimi inşa etti. Yoav da kentin geri kalan bölümünü onardı."));
trsuperobject.biblemap.put("b13_11_9", new trsuperobject(13,11,9,"Davut giderek güçleniyordu. Çünkü Her Şeye Egemen RAB onunlaydı."));
trsuperobject.biblemap.put("b13_11_10", new trsuperobject(13,11,10,"RABbin İsraile verdiği söz uyarınca Davutun yiğit askerlerinin komutanları İsrail halkıyla birlikte Davutu kral yaptılar ve krallığının güçlenmesi için onu desteklediler."));
trsuperobject.biblemap.put("b13_11_11", new trsuperobject(13,11,11,"Bunların adları şöyledir: Üçlerin önderi Hakmonlu Yaşovam, mızrağını üç yüz kişiye karşı kaldırıp bir saldırıda hepsini öldürdü. ‹‹Otuzlar››."));
trsuperobject.biblemap.put("b13_11_12", new trsuperobject(13,11,12,"İkincisi, üç yiğitlerden biri olan Ahohlu Dodo oğlu Elazar."));
trsuperobject.biblemap.put("b13_11_13", new trsuperobject(13,11,13,"Filistliler savaş için Pas-Dammimde toplandıklarında Elazar Davutun yanındaydı. Orada bir arpa tarlası vardı. İsrailliler Filistlilerin önünden kaçmıştı."));
trsuperobject.biblemap.put("b13_11_14", new trsuperobject(13,11,14,"Ama Elazarla Davut tarlanın ortasında durup orayı savunmuş, Filistlileri öldürmüşlerdi. RAB onlara büyük bir zafer sağlamıştı."));
trsuperobject.biblemap.put("b13_11_15", new trsuperobject(13,11,15,"Otuzlardan üçü Davutun yanına, Adullam Mağarasındaki kayaya gittiler. Bir Filist birliği Refaim Vadisinde ordugah kurmuştu."));
trsuperobject.biblemap.put("b13_11_16", new trsuperobject(13,11,16,"Bu sırada Davut hisarda, başka bir Filist birliğiyse Beytlehemdeydi."));
trsuperobject.biblemap.put("b13_11_17", new trsuperobject(13,11,17,"Davut özlemle, ‹‹Keşke biri Beytlehemde kapının yanındaki kuyudan bana su getirse!›› dedi."));
trsuperobject.biblemap.put("b13_11_18", new trsuperobject(13,11,18,"Bu Üçler Filist ordugahının ortasından geçerek Beytlehemde kapının yanındaki kuyudan su çekip Davuta getirdiler. Ama Davut içmek istemedi; suyu yere dökerek RABbe sundu."));
trsuperobject.biblemap.put("b13_11_19", new trsuperobject(13,11,19,"‹‹Ey Tanrım, bunu yapmak benden uzak olsun!›› dedi, ‹‹Canlarını tehlikeye atıp giden bu üç kişinin kanını mı içeyim?›› Canlarını tehlikeye atarak suyu getirdikleri için Davut içmek istemedi. Bu üç kişinin yiğitliği işte böyleydi."));
trsuperobject.biblemap.put("b13_11_20", new trsuperobject(13,11,20,"Yoavın kardeşi Avişay Üçlerin önderiydi. Mızrağını kaldırıp üç yüz kişiyi öldürdü. Bu yüzden Üçler kadar ünlendi."));
trsuperobject.biblemap.put("b13_11_21", new trsuperobject(13,11,21,"Üçlerin en saygın kişisiydi ve onların önderi oldu. Ama Üçlerden sayılmadı."));
trsuperobject.biblemap.put("b13_11_22", new trsuperobject(13,11,22,"Yehoyada oğlu Kavseelli Benaya yürekli bir savaşçıydı. Büyük işler başardı. Aslan yürekli iki Moavlıyı öldürdü. Ayrıca karlı bir gün çukura inip bir aslan öldürdü."));
trsuperobject.biblemap.put("b13_11_23", new trsuperobject(13,11,23,"Beş arşınfü boyunda iri yarı bir Mısırlıyı da öldürdü. Mısırlının elinde dokumacı sırığı gibi bir mızrak vardı. Benaya sopayla onun üzerine yürüdü. Mızrağı elinden kaptığı gibi onu kendi mızrağıyla öldürdü."));
trsuperobject.biblemap.put("b13_11_24", new trsuperobject(13,11,24,"Yehoyada oğlu Benayanın yaptıkları bunlardır. Bu sayede o da üç yiğitler kadar ünlendi."));
trsuperobject.biblemap.put("b13_11_25", new trsuperobject(13,11,25,"Benaya Otuzlar arasında saygın bir yer edindiyse de, Üçlerden sayılmadı. Davut onu muhafız birliği komutanlığına atadı."));
trsuperobject.biblemap.put("b13_11_26", new trsuperobject(13,11,26,"Öteki yiğitler şunlardır:  Yoavın kardeşi Asahel,  Beytlehemli Dodo oğlu Elhanan,"));
