trsuperobject.biblemap.put("b13_12_2", new trsuperobject(13,12,2,"Benyamin oymağından, Saulun ailesindendiler. Yay taşır ve yayla ok, sapanla taş atmak için hem sağ, hem sol ellerini kullanabilirlerdi."));
trsuperobject.biblemap.put("b13_12_3", new trsuperobject(13,12,3,"Givalı Şemaanın oğulları Ahiezerle Yoaşın komutası altındaydılar. Adları şunlardı: Azmavetin oğulları Yezielle Pelet, Beraka, Anatotlu Yehu,"));
