trsuperobject.biblemap.put("b13_12_10", new trsuperobject(13,12,10,"dördüncüsü Mişmanna, beşincisi Yeremya,"));
trsuperobject.biblemap.put("b13_12_11", new trsuperobject(13,12,11,"altıncısı Attay, yedincisi Eliel,"));
trsuperobject.biblemap.put("b13_12_12", new trsuperobject(13,12,12,"sekizincisi Yohanan, dokuzuncusu Elzavat,"));
trsuperobject.biblemap.put("b13_12_13", new trsuperobject(13,12,13,"onuncusu Yeremya, on birincisi de Makbannaydı."));
trsuperobject.biblemap.put("b13_12_14", new trsuperobject(13,12,14,"Bu Gadlılar ordu komutanlarıydı. En güçsüzleri yüz, güçlüleri bin kişinin yerini tutardı."));
trsuperobject.biblemap.put("b13_12_15", new trsuperobject(13,12,15,"Birinci ay, Şeria Irmağı her yana taşmışken, karşı yakaya geçtiler. Oradaki vadilerde oturanların tümünü doğuya, batıya kaçırdılar."));
trsuperobject.biblemap.put("b13_12_16", new trsuperobject(13,12,16,"Benyamin ve Yahudaoğullarından bazı kişiler de Davutun yanına, saklandığı yere gittiler."));
trsuperobject.biblemap.put("b13_12_17", new trsuperobject(13,12,17,"Onları karşılamaya çıkan Davut şöyle dedi: ‹‹Eğer bana yardım etmek için esenlikle geldiyseniz, buyrun bize katılın. Ama ben haksızlık yapmamışken beni düşmanlarımın eline teslim etmeye geldiyseniz, atalarımızın Tanrısı bunu görsün ve sizi yargılasın.››"));
trsuperobject.biblemap.put("b13_12_18", new trsuperobject(13,12,18,"Tanrının Ruhu Otuzların önderi Amasayın üzerine indi. Amasay şöyle dedi:  ‹‹Ey Davut, seniniz biz!Ey İşay oğlu, seninleyiz!Esenlik olsun sana, esenlik!Seni destekleyenlere de esenlik olsun!Tanrın sana yardım edecektir.›› Davut onları iyi karşıladı ve akıncıların başı yaptı."));
trsuperobject.biblemap.put("b13_12_19", new trsuperobject(13,12,19,"Davut Filistlilerle birlikte Saula karşı savaşmaya gidince, Manaşşe oymağından da bazı kişiler onun yanına geçtiler. Ne var ki Davutla adamları Filistlilere yardım etmediler. Çünkü Filist beyleri birbirlerine danışıp, ‹‹Davut efendisi Saula dönerse başımızdan oluruz›› diyerek onu geri göndermişlerdi."));
trsuperobject.biblemap.put("b13_12_20", new trsuperobject(13,12,20,"Davut Ziklaka gittiğinde yanına geçen Manaşşeliler şunlardır: Adna, Yozavat, Yediael, Mikael, Yozavat, Elihu, Silletay. Bunlar Manaşşede bin kişilik birliklerin komutanlarıydı."));
trsuperobject.biblemap.put("b13_12_21", new trsuperobject(13,12,21,"Düşman akıncılarına karşı Davuta yardım ettiler. Hepsi de yiğit savaşçılar, ordu komutanlarıydı."));
trsuperobject.biblemap.put("b13_12_22", new trsuperobject(13,12,22,"Her gün insanlar Davuta yardım etmeye geliyorlardı. Davut büyük, güçlü bir orduya sahip oluncaya dek bu böyle sürdü."));
trsuperobject.biblemap.put("b13_12_23", new trsuperobject(13,12,23,"RABbin sözü uyarınca Saulun krallığını Davuta vermek için Hevrona, Davutun yanına gelen savaşçıların sayısı şudur:"));
trsuperobject.biblemap.put("b13_12_24", new trsuperobject(13,12,24,"Savaşa hazır, kalkan ve mızrak taşıyan Yahudaoğullarından 6 800 kişi."));
trsuperobject.biblemap.put("b13_12_25", new trsuperobject(13,12,25,"Savaşa hazır, yiğit Şimonoğullarından 7 100 kişi."));
trsuperobject.biblemap.put("b13_12_26", new trsuperobject(13,12,26,"Levioğullarından 4 600 kişi."));
trsuperobject.biblemap.put("b13_12_27", new trsuperobject(13,12,27,"Harun ailesinin başı Yehoyada ve onunla birlikte olanlar 3 700 kişi."));
trsuperobject.biblemap.put("b13_12_28", new trsuperobject(13,12,28,"Genç yiğit Sadokla ailesinden 22 subay."));
trsuperobject.biblemap.put("b13_12_29", new trsuperobject(13,12,29,"Saulun soyu Benyaminlilerden 3 000 kişi. Benyaminlilerin çoğu o zamana dek Saulun ailesine bağlı kalmışlardı."));
trsuperobject.biblemap.put("b13_12_30", new trsuperobject(13,12,30,"Efrayimoğullarından yiğit savaşçı ve boylarında ün salmış 20 800 kişi."));
trsuperobject.biblemap.put("b13_12_31", new trsuperobject(13,12,31,"Davutu kral yapmak için Manaşşe oymağının yarısından özel olarak seçilip gelenler 18 000 kişi."));
trsuperobject.biblemap.put("b13_12_32", new trsuperobject(13,12,32,"İssakaroğullarından 200 kişi. Bunlar İsraillilerin ne zaman ne yapması gerektiğini bilen kişilerdi. Boy başlarıydı ve bütün akrabalarını yönetirlerdi."));
trsuperobject.biblemap.put("b13_12_33", new trsuperobject(13,12,33,"Zevulundan 50 000 kişi. Bunlar savaşa hazır, deneyimli askerlerdi. Her tür silahı kullanmada ustaydılar. Davuta candan ve yürekten yardım ettiler."));
trsuperobject.biblemap.put("b13_12_34", new trsuperobject(13,12,34,"Naftaliden 1 000 subay ile kalkan ve mızrak taşıyan 37 000 kişi."));
trsuperobject.biblemap.put("b13_12_35", new trsuperobject(13,12,35,"Danlılardan savaşa hazır 28 600 kişi."));
trsuperobject.biblemap.put("b13_12_36", new trsuperobject(13,12,36,"Aşerden savaşa hazır, deneyimli 40 000 asker."));
trsuperobject.biblemap.put("b13_12_37", new trsuperobject(13,12,37,"Şeria Irmağının doğusunda yaşayan, savaş için her tür silahla donatılmış Rubenlilerden, Gadlılardan ve Manaşşe oymağının yarısından 120 000 kişi."));
trsuperobject.biblemap.put("b13_12_38", new trsuperobject(13,12,38,"Bunların hepsi savaşa hazır yiğit askerlerdi. Büyük bir kararlılıkla Davutu bütün İsrailin kralı yapmak için Hevrona geldiler. Geri kalan İsrailliler de Davutu kral yapma konusunda aynı düşüncedeydiler."));
trsuperobject.biblemap.put("b13_12_39", new trsuperobject(13,12,39,"Adamlar Davutun yanında üç gün kaldılar. Orada yiyip içtiler. Gereksinimlerini yakınları sağlamıştı."));
trsuperobject.biblemap.put("b13_12_40", new trsuperobject(13,12,40,"İssakar, Zevulun ve Naftali'ye kadar yayılmış olan yakınları da yiyecek yüklü eşeklerle, develerle, katırlarla, öküzlerle geldiler. Bol miktarda un, incir pestili, kuru üzüm salkımları, şarap, zeytinyağı, çok sayıda sığır ve davar getirdiler. Çünkü İsrail'de sevinç vardı."));
trsuperobject.biblemap.put("b13_13_1", new trsuperobject(13,13,1,"Davut binbaşılara, yüzbaşılara ve subaylarına danıştı."));
trsuperobject.biblemap.put("b13_13_2", new trsuperobject(13,13,2,"Sonra bütün İsrail topluluğuna şöyle seslendi: ‹‹Eğer onaylarsanız ve Tanrımız RABbin isteğiyse, İsrail ülkesinin her yanına yayılmış öbür soydaşlarımıza ve onlarla birlikte kendi kentlerinde ve otlaklarında yaşayan kâhinlerle Levililere haberciler gönderelim. Onlar da gelip bize katılsınlar."));
trsuperobject.biblemap.put("b13_13_3", new trsuperobject(13,13,3,"Tanrımızın Sandığını geri getirelim. Çünkü Saulun krallığı döneminde ona gereken önemi vermedik.››"));
trsuperobject.biblemap.put("b13_13_4", new trsuperobject(13,13,4,"Topluluk bu öneriyi benimseyerek sandığı geri getirmeye karar verdi."));
trsuperobject.biblemap.put("b13_13_5", new trsuperobject(13,13,5,"Davut Tanrının Antlaşma Sandığını Kiryat-Yearimden geri getirmek için Mısırdaki Şihor Irmağından Levo-Hamata kadar bütün İsraillileri topladı."));
trsuperobject.biblemap.put("b13_13_6", new trsuperobject(13,13,6,"Böylece Davutla İsrailliler Keruvlar arasında taht kuran RAB Tanrının adıyla anılan Tanrının Antlaşma Sandığını getirmek için Yahudadaki Baala Kentine -Kiryat-Yearime- gittiler."));
trsuperobject.biblemap.put("b13_13_7", new trsuperobject(13,13,7,"Tanrının Sandığını Avinadavın evinden alıp yeni bir arabaya koydular. Arabayı Uzzayla Ahyo sürüyordu."));
trsuperobject.biblemap.put("b13_13_8", new trsuperobject(13,13,8,"Bu arada Davutla bütün İsrail halkı da Tanrının önünde lir, çenk, tef, zil ve borazanlar eşliğinde ezgiler okuyarak, var güçleriyle bu olayı kutluyorlardı."));
trsuperobject.biblemap.put("b13_13_9", new trsuperobject(13,13,9,"Kidonun harman yerine vardıklarında öküzler tökezledi. Bu nedenle Uzza elini uzatıp sandığı tuttu."));
trsuperobject.biblemap.put("b13_13_10", new trsuperobject(13,13,10,"RAB sandığa elini uzatan Uzzaya öfkelenerek onu yere çaldı. Uzza orada, Tanrının önünde öldü."));
trsuperobject.biblemap.put("b13_13_11", new trsuperobject(13,13,11,"Davut, RABbin Uzzayı cezalandırmasına öfkelendi. O günden bu yana oraya Peres-Uzza denilir."));
trsuperobject.biblemap.put("b13_13_12", new trsuperobject(13,13,12,"Davut o gün Tanrıdan korkarak, ‹‹Tanrının Sandığını nasıl yanıma getirsem?›› diye düşündü."));
trsuperobject.biblemap.put("b13_13_13", new trsuperobject(13,13,13,"Sandığı yanına, Davut Kentine götüreceğine Gatlı Ovet-Edomun evine götürdü."));
trsuperobject.biblemap.put("b13_13_14", new trsuperobject(13,13,14,"Tanrı'nın Sandığı Gatlı Ovet-Edom'un evinde üç ay kaldı. RAB Ovet-Edom'un ailesini ve ona ait her şeyi kutsadı."));
trsuperobject.biblemap.put("b13_14_1", new trsuperobject(13,14,1,"Sur Kralı Hiram Davuta ulaklar ve bir saray yapmak için sedir tomrukları, taşçılar, marangozlar gönderdi."));
trsuperobject.biblemap.put("b13_14_2", new trsuperobject(13,14,2,"Böylece Davut RABbin kendisini İsrail Kralı atadığını ve halkı İsrailin hatırı için krallığını çok yücelttiğini anladı."));
trsuperobject.biblemap.put("b13_14_3", new trsuperobject(13,14,3,"Davut Yeruşalimde kendine daha birçok karı aldı; bunlardan erkek ve kız çocukları oldu."));
trsuperobject.biblemap.put("b13_14_4", new trsuperobject(13,14,4,"Davutun Yeruşalimde doğan çocuklarının adları şunlardı: Şammua, Şovav, Natan, Süleyman,"));
trsuperobject.biblemap.put("b13_14_5", new trsuperobject(13,14,5,"Yivhar, Elişua, Elpelet,"));
trsuperobject.biblemap.put("b13_14_6", new trsuperobject(13,14,6,"Nogah, Nefek, Yafia,"));
trsuperobject.biblemap.put("b13_14_7", new trsuperobject(13,14,7,"Elişama, Beelyada, Elifelet."));
trsuperobject.biblemap.put("b13_14_8", new trsuperobject(13,14,8,"Filistliler Davutun İsrail Kralı olarak meshedildiğini duyunca, bütün Filist ordusu onu aramak için yola çıktı. Bunu duyan Davut onları karşılamaya gitti."));
trsuperobject.biblemap.put("b13_14_9", new trsuperobject(13,14,9,"Filistliler gelip Refaim Vadisinde baskın yapmışlardı."));
trsuperobject.biblemap.put("b13_14_10", new trsuperobject(13,14,10,"Davut Tanrıya danıştı: ‹‹Filistlilere saldırayım mı? Onları elime teslim edecek misin?›› RAB, ‹‹Saldır›› dedi, ‹‹Onları eline teslim edeceğim.››"));
trsuperobject.biblemap.put("b13_14_11", new trsuperobject(13,14,11,"Bunun üzerine Davutla adamları Baal-Perasime gittiler. Davut orada Filistlileri bozguna uğrattı. Sonra, ‹‹Her şeyi yarıp geçen sular gibi, Tanrı düşmanlarımı benim elimle yarıp geçti›› dedi. Bundan ötürü oraya Baal-Perasim adı verildi."));
trsuperobject.biblemap.put("b13_14_12", new trsuperobject(13,14,12,"Filistliler putlarını orada bıraktılar. Davutun buyruğu uyarınca putlar yakıldı."));
trsuperobject.biblemap.put("b13_14_13", new trsuperobject(13,14,13,"Filistliler bir kez daha gelip vadiye baskın yaptılar."));
trsuperobject.biblemap.put("b13_14_14", new trsuperobject(13,14,14,"Davut yine Tanrıya danıştı. Tanrı şöyle karşılık verdi: ‹‹Buradan saldırma! Onları arkadan çevirip pelesenk ağaçlarının önünden saldır."));
trsuperobject.biblemap.put("b13_14_15", new trsuperobject(13,14,15,"Pelesenk ağaçlarının tepesinden yürüyüş sesi duyar duymaz, saldırıya geç. Çünkü ben Filist ordusunu bozguna uğratmak için önünsıra gitmişim demektir.››"));
trsuperobject.biblemap.put("b13_14_16", new trsuperobject(13,14,16,"Davut Tanrının kendisine buyurduğu gibi yaptı ve Filist ordusunu Givondan Gezere kadar bozguna uğrattı."));
trsuperobject.biblemap.put("b13_14_17", new trsuperobject(13,14,17,"Böylece Davut'un ünü her yana yayıldı. RAB bütün ulusların ondan korkmasını sağladı."));
trsuperobject.biblemap.put("b13_15_1", new trsuperobject(13,15,1,"Davut kendisine Davut Kentinde evler yaptı. Ardından Tanrının Antlaşma Sandığı için bir yer hazırlayıp çadır kurdu."));
trsuperobject.biblemap.put("b13_15_2", new trsuperobject(13,15,2,"Sonra, ‹‹Tanrının Antlaşma Sandığını yalnız Levililer taşıyacak›› dedi, ‹‹Çünkü sandığı taşımak ve sonsuza dek kendisine hizmet etmek için RAB Levilileri seçti.››"));
trsuperobject.biblemap.put("b13_15_3", new trsuperobject(13,15,3,"Davut RABbin Antlaşma Sandığını hazırlamış olduğu yere getirmek için bütün İsraillileri Yeruşalimde topladı."));
trsuperobject.biblemap.put("b13_15_4", new trsuperobject(13,15,4,"Sonra Harunoğullarıyla Levilileri bir araya getirdi:"));
trsuperobject.biblemap.put("b13_15_5", new trsuperobject(13,15,5,"Kehatoğullarından: Önder Urielle 120 yakını,"));
trsuperobject.biblemap.put("b13_15_6", new trsuperobject(13,15,6,"Merarioğullarından: Önder Asayayla 220 yakını,"));
trsuperobject.biblemap.put("b13_15_7", new trsuperobject(13,15,7,"Gerşonoğullarından: Önder Yoelle 130 yakını,"));
trsuperobject.biblemap.put("b13_15_8", new trsuperobject(13,15,8,"Elisafanoğullarından: Önder Şemayayla 200 yakını,"));
trsuperobject.biblemap.put("b13_15_9", new trsuperobject(13,15,9,"Hevronoğullarından: Önder Elielle 80 yakını,"));
trsuperobject.biblemap.put("b13_15_10", new trsuperobject(13,15,10,"Uzzieloğullarından: Önder Amminadavla 112 yakını."));
trsuperobject.biblemap.put("b13_15_11", new trsuperobject(13,15,11,"Bundan sonra Davut Kâhin Sadokla Kâhin Aviyatarı, Levili Urieli, Asayayı, Yoeli, Şemayayı, Elieli, Amminadavı yanına çağırdı."));
trsuperobject.biblemap.put("b13_15_12", new trsuperobject(13,15,12,"Onlara şöyle dedi: ‹‹Siz Levili boyların başlarısınız. Levili kardeşlerinizle birlikte kendinizi kutsayın, sonra İsrailin Tanrısı RABbin Antlaşma Sandığını hazırlamış olduğum yere getirin."));
trsuperobject.biblemap.put("b13_15_13", new trsuperobject(13,15,13,"Çünkü geçen sefer sandığı siz taşımadığınız ve biz de kurala uygun davranmadığımız için Tanrımız RAB bize öfkelendi.››"));
trsuperobject.biblemap.put("b13_15_14", new trsuperobject(13,15,14,"Böylece kâhinlerle Levililer İsrailin Tanrısı RABbin Antlaşma Sandığını getirmek için kendilerini kutsadılar."));
trsuperobject.biblemap.put("b13_15_15", new trsuperobject(13,15,15,"RABbin sözü uyarınca ve Musanın onlara buyurduğu gibi, Tanrının Sandığının sırıklarını omuzları üzerinde taşıdılar."));
trsuperobject.biblemap.put("b13_15_16", new trsuperobject(13,15,16,"Davut Levili önderlere, kardeşlerinden çenk, lir ve zil gibi çalgılar eşliğinde yüksek sesle sevinçli ezgiler okuyacak ezgiciler atamalarını söyledi."));
trsuperobject.biblemap.put("b13_15_17", new trsuperobject(13,15,17,"Levililer de Yoel oğlu Hemanı, akrabalarından Berekya oğlu Asafı, akrabaları Merarioğullarından Kuşaya oğlu Etanı atadılar."));
trsuperobject.biblemap.put("b13_15_18", new trsuperobject(13,15,18,"Onlara yardımcı olarak da kapı nöbetçileri kardeşlerinden Zekeriyayı, Yaazieli, Şemiramotu, Yehieli, Unniyi, Eliavı, Benayayı, Maaseyayı, Mattityayı, Elifelehuyu, Mikneyayı, Ovet-Edomu, Yeieli atadılar."));
trsuperobject.biblemap.put("b13_15_19", new trsuperobject(13,15,19,"Ezgicilerden Heman, Asaf, Etan tunç zil;"));
trsuperobject.biblemap.put("b13_15_20", new trsuperobject(13,15,20,"Zekeriya, Aziel, Şemiramot, Yehiel, Unni, Eliav, Maaseya, Benaya tiz perdeli çenk çalmak;"));
trsuperobject.biblemap.put("b13_15_21", new trsuperobject(13,15,21,"Mattitya, Elifelehu, Mikneya, Ovet-Edom, Yeiel, Azazya sekiz telli lirle önderlik yapmak üzere seçildiler."));
trsuperobject.biblemap.put("b13_15_22", new trsuperobject(13,15,22,"Levililerin önderi Kenanya ise ezgilerden sorumluydu. Bu konuda yetenekliydi. ‹‹Oktavlı›› anlamına da gelebilir."));
trsuperobject.biblemap.put("b13_15_23", new trsuperobject(13,15,23,"Berekya ile Elkana Antlaşma Sandığının bulunduğu yerin kapı nöbetçileriydi."));
trsuperobject.biblemap.put("b13_15_24", new trsuperobject(13,15,24,"Şevanya, Yoşafat, Netanel, Amasay, Zekeriya, Benaya, Eliezer adındaki kâhinler Tanrının Antlaşma Sandığı önünde borazan çalıyordu. Ovet-Edom ile Yehiya da Antlaşma Sandığının bulunduğu yerin kapı nöbetçileriydi."));
trsuperobject.biblemap.put("b13_15_25", new trsuperobject(13,15,25,"Böylece Davut, İsrail ileri gelenleri ve binbaşılar RABbin Antlaşma Sandığını Ovet-Edomun evinden sevinçle getirmeye gittiler."));
trsuperobject.biblemap.put("b13_15_26", new trsuperobject(13,15,26,"Tanrı, RABbin Antlaşma Sandığını taşıyan Levililere yardım ettiği için, yedi boğa ile yedi koç kurban ettiler."));
trsuperobject.biblemap.put("b13_15_27", new trsuperobject(13,15,27,"Davut, sandığı taşıyan Levililer, ezgiciler ve ezgilerden sorumlu olan Kenanya ince ketenden cüppeler giyinmişlerdi. Davut ince keten efod da kuşanmıştı."));
trsuperobject.biblemap.put("b13_15_28", new trsuperobject(13,15,28,"Böylece İsrailliler sevinç naraları atarak, boru, borazan, zil, çenk ve lirler çalarak RABbin Antlaşma Sandığını getiriyorlardı."));
trsuperobject.biblemap.put("b13_15_29", new trsuperobject(13,15,29,"RAB'bin Antlaşma Sandığı Davut Kenti'ne varınca, Saul'un kızı Mikal pencereden baktı. Oynayıp zıplayan Kral Davut'u görünce, onu içinden küçümsedi."));
trsuperobject.biblemap.put("b13_16_1", new trsuperobject(13,16,1,"Tanrının Antlaşma Sandığını getirip Davutun bu amaçla kurduğu çadırın içine koydular. Tanrıya yakmalık sunular ve esenlik sunuları sundular."));
trsuperobject.biblemap.put("b13_16_2", new trsuperobject(13,16,2,"Davut yakmalık sunuları ve esenlik sunularını sunmayı bitirince, RABbin adıyla halkı kutsadı."));
trsuperobject.biblemap.put("b13_16_3", new trsuperobject(13,16,3,"Ardından erkek, kadın her İsrailliye birer somun ekmekle birer hurma ve üzüm pestili dağıttı."));
trsuperobject.biblemap.put("b13_16_5", new trsuperobject(13,16,5,"Bunların önderi Asaf, yardımcısı Zekeriyaydı. Öbürleri Yeiel, Şemiramot, Yehiel, Mattitya, Eliav, Benaya, Ovet-Edom ve Yeieldi. Bunlar çenk ve lir, Asaf yüksek sesli zil,"));
trsuperobject.biblemap.put("b13_16_6", new trsuperobject(13,16,6,"Kâhin Benaya ile Yahaziel de Tanrının Antlaşma Sandığı önünde sürekli borazan çalacaklardı."));
trsuperobject.biblemap.put("b13_16_7", new trsuperobject(13,16,7,"O gün Davut RABbe şükretme işini ilk kez Asafla kardeşlerine verdi."));
trsuperobject.biblemap.put("b13_16_9", new trsuperobject(13,16,9,"Onu ezgilerle, ilahilerle övün,Bütün harikalarını anlatın!"));
trsuperobject.biblemap.put("b13_16_10", new trsuperobject(13,16,10,"Kutsal adıyla övünün,Sevinsin RABbe yönelenler!"));
trsuperobject.biblemap.put("b13_16_11", new trsuperobject(13,16,11,"RABbe ve Onun gücüne bakın,Durmadan Onun yüzünü arayın!"));
trsuperobject.biblemap.put("b13_16_12-13", new trsuperobject(13,16,12-13,"Ey sizler, kulu İsrailin soyu,Seçtiği Yakupoğulları,Onun yaptığı harikaları,Olağanüstü işleriniVe ağzından çıkan yargıları anımsayın!"));
trsuperobject.biblemap.put("b13_16_14", new trsuperobject(13,16,14,"Tanrımız RAB Odur,Yargıları bütün yeryüzünü kapsar."));
trsuperobject.biblemap.put("b13_16_15-16", new trsuperobject(13,16,15-16,"Onun antlaşmasını,Bin kuşak için verdiği sözü,İbrahimle yaptığı antlaşmayı,İshak için içtiği andı sonsuza dek anımsayın."));
trsuperobject.biblemap.put("b13_16_17-18", new trsuperobject(13,16,17-18,"‹‹Hakkınıza düşen mülk olarakKenan ülkesini size vereceğim›› diyerek,Bunu Yakup için bir kural,İsraille sonsuza dek geçerli bir antlaşma yaptı."));
trsuperobject.biblemap.put("b13_16_19", new trsuperobject(13,16,19,"O zaman bir avuç insandınız,Sayıca az ve ülkeye yabancıydınız."));
trsuperobject.biblemap.put("b13_16_20", new trsuperobject(13,16,20,"Bir ulustan öbürüne,Bir ülkeden ötekine dolaşıp durdular."));
trsuperobject.biblemap.put("b13_16_21", new trsuperobject(13,16,21,"RAB kimsenin onları ezmesine izin vermedi,Onlar için kralları bile payladı:"));
trsuperobject.biblemap.put("b13_16_22", new trsuperobject(13,16,22,"‹‹Meshettiklerime dokunmayın,Peygamberlerime kötülük etmeyin!›› dedi."));
trsuperobject.biblemap.put("b13_16_23", new trsuperobject(13,16,23,"Ey bütün dünya, ezgiler söyleyin RABbe!Her gün duyurun kurtarışını!"));
trsuperobject.biblemap.put("b13_16_24", new trsuperobject(13,16,24,"Görkemini uluslara,Harikalarını bütün halklara anlatın!"));
trsuperobject.biblemap.put("b13_16_25", new trsuperobject(13,16,25,"Çünkü RAB uludur, yalnız O övgüye değer,İlahlardan çok Ondan korkulur."));
trsuperobject.biblemap.put("b13_16_26", new trsuperobject(13,16,26,"Halkların bütün ilahları bir hiçtir,Oysa gökleri yaratan RABdir."));
trsuperobject.biblemap.put("b13_16_27", new trsuperobject(13,16,27,"Yücelik, ululuk Onun huzurundadır,Güç ve sevinç Onun konutundadır."));
trsuperobject.biblemap.put("b13_16_28", new trsuperobject(13,16,28,"Ey bütün halklar, RABbi övün,RABbin gücünü, yüceliğini övün,"));
trsuperobject.biblemap.put("b13_16_29", new trsuperobject(13,16,29,"RABbin görkemini adına yaraşır biçimde övün,Sunular getirip Onun önüne çıkın!Kutsal giysiler içinde RABbe tapının!"));
trsuperobject.biblemap.put("b13_16_30", new trsuperobject(13,16,30,"Titreyin Onun önünde, ey bütün yeryüzündekiler!Dünya sağlam kurulmuş, sarsılmaz."));
trsuperobject.biblemap.put("b13_16_31", new trsuperobject(13,16,31,"Sevinsin gökler, coşsun yeryüzü,Uluslar arasında, ‹‹RAB egemenlik sürüyor!›› densin."));
trsuperobject.biblemap.put("b13_16_32", new trsuperobject(13,16,32,"Gürlesin deniz içindekilerle birlikte,Bayram etsin kırlar ve üzerindekiler!"));
trsuperobject.biblemap.put("b13_16_33", new trsuperobject(13,16,33,"O zaman RABbin önünde ormanın ağaçlarıSevinçle haykıracak.Çünkü O yeryüzünü yargılamaya geliyor."));
trsuperobject.biblemap.put("b13_16_34", new trsuperobject(13,16,34,"RABbe şükredin, çünkü O iyidir,Sevgisi sonsuzdur."));
trsuperobject.biblemap.put("b13_16_35", new trsuperobject(13,16,35,"Şöyle seslenin:‹‹Kurtar bizi, ey kurtarıcımız Tanrı,Topla bizi, ulusların arasından çıkar.Kutsal adına şükredelim,Yüceliğinle övünelim."));
trsuperobject.biblemap.put("b13_16_36", new trsuperobject(13,16,36,"İsrailin Tanrısı RABbeÖncesizlikten sonsuza dek övgüler olsun!›› Bütün halk, ‹‹Amin!›› diyerek RABbe övgüler sundu."));
trsuperobject.biblemap.put("b13_16_37", new trsuperobject(13,16,37,"Davut RABbin Antlaşma Sandığının önünde günlük işlerde sürekli hizmet etmeleri için Asafla Levili kardeşlerini atadı."));
trsuperobject.biblemap.put("b13_16_38", new trsuperobject(13,16,38,"Onlarla birlikte hizmet etmeleri için Ovet-Edomla altmış sekiz Levili akrabasını da atadı. Yedutun oğlu Ovet-Edomla Hosa kapı nöbetçileriydi."));
trsuperobject.biblemap.put("b13_16_39", new trsuperobject(13,16,39,"Davut Kâhin Sadokla öbür kâhin kardeşlerini Givondaki tapınma yerinde, RABbin Çadırının bulunduğu yerde görevlendirdi."));
trsuperobject.biblemap.put("b13_16_40", new trsuperobject(13,16,40,"Bunlar RABbin İsraile verdiği yasada yazılanlar uyarınca, sabah akşam, düzenli olarak yakmalık sunu sunağında RABbe sunular sunacaklardı."));
trsuperobject.biblemap.put("b13_16_41", new trsuperobject(13,16,41,"Onlarla birlikte Hemanla Yedutunu ve RABbin sonsuz sevgisi için şükretsinler diye özel olarak seçilen öbürlerini de görevlendirdi."));
trsuperobject.biblemap.put("b13_16_42", new trsuperobject(13,16,42,"Hemanla Yedutun borazanlardan, zillerden ve Tanrıyı öven ezgiler için gereken öbür çalgılardan sorumluydu. Yedutunoğullarını da kapıda nöbetçi olarak görevlendirdi."));
trsuperobject.biblemap.put("b13_16_43", new trsuperobject(13,16,43,"Sonra herkes evine döndü. Davut da ailesini kutsamak için evine döndü."));
trsuperobject.biblemap.put("b13_17_1", new trsuperobject(13,17,1,"Davut sarayına yerleştikten sonra Peygamber Natana, ‹‹Bak, ben sedir ağacından yapılmış bir sarayda oturuyorum. Oysa RABbin Antlaşma Sandığı bir çadırın altında duruyor!›› dedi."));
trsuperobject.biblemap.put("b13_17_2", new trsuperobject(13,17,2,"Natan, ‹‹Tasarladığın her şeyi yap, çünkü Tanrı seninledir›› diye karşılık verdi."));
trsuperobject.biblemap.put("b13_17_3", new trsuperobject(13,17,3,"O gece Tanrı Natana şöyle seslendi:"));
trsuperobject.biblemap.put("b13_17_4", new trsuperobject(13,17,4,"‹‹Git, kulum Davuta şöyle de: ‹RAB diyor ki, oturmam için bana tapınak yapmayacaksın."));
trsuperobject.biblemap.put("b13_17_5", new trsuperobject(13,17,5,"İsrail halkını Mısırdan çıkardığım günden bu yana tapınakta oturmadım. Bir çadırdan öbür çadıra, orada burada konaklayarak dolaştım."));
trsuperobject.biblemap.put("b13_17_6", new trsuperobject(13,17,6,"İsraillilerle birlikte dolaştığım yerlerin herhangi birinde, halkımı gütmesini buyurduğum İsrail önderlerinden birine, neden bana sedir ağacından bir konut yapmadınız diye hiç sordum mu?›"));
trsuperobject.biblemap.put("b13_17_7", new trsuperobject(13,17,7,"‹‹Şimdi kulum Davuta şöyle diyeceksin: ‹Her Şeye Egemen RAB diyor ki: Halkım İsraile önder olasın diye seni otlaklardan ve koyun gütmekten aldım."));
trsuperobject.biblemap.put("b13_17_8", new trsuperobject(13,17,8,"Her nereye gittiysen seninleydim. Önünden bütün düşmanlarını yok ettim. Adını dünyadaki büyük adamların adı gibi büyük kılacağım."));
trsuperobject.biblemap.put("b13_17_9-10", new trsuperobject(13,17,9-10,"Halkım İsrail için bir yurt sağlayıp onları oraya yerleştireceğim. Bundan böyle kendi yurtlarında otursunlar, bir daha rahatsız edilmesinler. Kötü kişiler de halkım İsraile hakimler atadığım günden bu yana yaptıkları gibi, bir daha onlara baskı yapmasınlar. Bütün düşmanlarının sana boyun eğmesini sağlayacağım. RABbin senin için bir soy yetiştirecek, bilesin."));
trsuperobject.biblemap.put("b13_17_11", new trsuperobject(13,17,11,"‹‹Sen ölüp atalarına kavuşunca, senden sonra oğullarından birini ortaya çıkarıp krallığını pekiştireceğim."));
trsuperobject.biblemap.put("b13_17_12", new trsuperobject(13,17,12,"Benim için tapınak kuracak olan odur. Ben de onun tahtını sonsuza dek sürdüreceğim."));
trsuperobject.biblemap.put("b13_17_13", new trsuperobject(13,17,13,"Ben ona baba olacağım, o da bana oğul olacak. Senden önceki kraldan esirgediğim sevgiyi hiçbir zaman esirgemeyeceğim."));
trsuperobject.biblemap.put("b13_17_14", new trsuperobject(13,17,14,"Onu sonsuza dek tapınağımın ve krallığımın üzerine atayacağım; tahtı sonsuza dek sürecektir.››"));
trsuperobject.biblemap.put("b13_17_15", new trsuperobject(13,17,15,"Böylece Natan bütün bu sözleri ve görümleri Davuta aktardı."));
trsuperobject.biblemap.put("b13_17_16", new trsuperobject(13,17,16,"Bunun üzerine Kral Davut gelip RABbin önünde oturdu ve şöyle dedi: ‹‹Ya RAB Tanrı, ben kimim, ailem nedir ki, beni bu duruma getirdin?"));
trsuperobject.biblemap.put("b13_17_17", new trsuperobject(13,17,17,"Ey Tanrı, sanki bu yetmezmiş gibi, kulunun soyunun geleceği hakkında da söz verdin. Benimle de büyük bir adammışım gibi ilgilendin, ya RAB Tanrı!"));
trsuperobject.biblemap.put("b13_17_18", new trsuperobject(13,17,18,"Kulunu onurlandırdın, ben sana başka ne diyebilirim ki! Çünkü sen kulunu tanıyorsun."));
trsuperobject.biblemap.put("b13_17_19", new trsuperobject(13,17,19,"Ya RAB, kulunun hatırı için ve isteğin uyarınca bu büyüklüğü gösterdin ve bu büyük vaatleri bildirdin."));
trsuperobject.biblemap.put("b13_17_20", new trsuperobject(13,17,20,"‹‹Ya RAB, bir benzerin yok, senden başka tanrı da yok! Bunu kendi kulaklarımızla duyduk."));
trsuperobject.biblemap.put("b13_17_21", new trsuperobject(13,17,21,"Halkın İsraile benzer tek bir ulus yok dünyada. Kendi halkın olsun diye onları kurtarmaya gittin. Büyük ve görkemli işler yapmakla ün saldın. Mısırdan kurtardığın halkın önünden ulusları kovdun."));
trsuperobject.biblemap.put("b13_17_22", new trsuperobject(13,17,22,"Halkın İsraili sonsuza dek kendi halkın olarak seçtin ve sen de, ya RAB, onların Tanrısı oldun."));
trsuperobject.biblemap.put("b13_17_23", new trsuperobject(13,17,23,"‹‹Şimdi, ya RAB, kuluna ve onun soyuna ilişkin verdiğin sözü sonsuza dek tut, sözünü yerine getir."));
trsuperobject.biblemap.put("b13_17_24", new trsuperobject(13,17,24,"Öyle ki, insanlar, ‹İsraili kayıran, Her Şeye Egemen RAB Tanrı İsrailin Tanrısıdır!› diyerek adını sonsuza dek ansınlar, yüceltsinler ve kulun Davutun soyu da önünde sürsün."));
trsuperobject.biblemap.put("b13_17_25", new trsuperobject(13,17,25,"‹‹Sen, ey Tanrım, ben kulun için bir soy çıkaracağını bana açıkladın. Bundan dolayı kulun önünde sana dua etme yürekliliğini buldu."));
trsuperobject.biblemap.put("b13_17_26", new trsuperobject(13,17,26,"Ya RAB, sen Tanrısın! Kuluna bu iyi sözü verdin."));
trsuperobject.biblemap.put("b13_17_27", new trsuperobject(13,17,27,"Şimdi önünde sonsuza dek sürmesi için kulunun soyunu kutsamayı uygun gördün. Çünkü, ya RAB, onu kutsadığın için sonsuza dek kutlu kılınacak.››"));
trsuperobject.biblemap.put("b13_18_1", new trsuperobject(13,18,1,"Bir süre sonra, Davut Filistlileri yenip boyunduruğu altına aldı; Gatı ve çevresindeki köyleri Filistlilerin yönetiminden çıkardı."));
trsuperobject.biblemap.put("b13_18_2", new trsuperobject(13,18,2,"Moavlıları da bozguna uğrattı. Onlar Davutun haraç ödeyen köleleri oldular."));
trsuperobject.biblemap.put("b13_18_3", new trsuperobject(13,18,3,"Davut Fırata kadar krallığını pekiştirmeye giden Sova Kralı Hadadezeri de Hama yakınlarında yendi."));
trsuperobject.biblemap.put("b13_18_4", new trsuperobject(13,18,4,"Bin savaş arabasını, yedi bin atlısını, yirmi bin yaya askerini ele geçirdi. Yüz savaş arabası için gereken atların dışındaki bütün atları da sakatladı."));
trsuperobject.biblemap.put("b13_18_5", new trsuperobject(13,18,5,"Sova Kralı Hadadezere yardıma gelen Şam Aramlılarından yirmi iki bin kişiyi öldürdü."));
trsuperobject.biblemap.put("b13_18_6", new trsuperobject(13,18,6,"Sonra Şam Aramlılarının ülkesine askeri birlikler yerleştirdi. Onlar da Davutun haraç ödeyen köleleri oldular. RAB Davutu gittiği her yerde zafere ulaştırdı."));
trsuperobject.biblemap.put("b13_18_7", new trsuperobject(13,18,7,"Davut Hadadezerin komutanlarının taşıdığı altın kalkanları alıp Yeruşalime götürdü."));
trsuperobject.biblemap.put("b13_18_8", new trsuperobject(13,18,8,"Ayrıca Hadadezerin yönetimindeki Tivhat ve Kun kentlerinden bol miktarda tunç aldı. Süleyman bunu havuz, sütunlar ve çeşitli eşyalar yapmak için kullandı."));
trsuperobject.biblemap.put("b13_18_9", new trsuperobject(13,18,9,"Hama Kralı Tou, Davutun Sova Kralı Hadadezerin bütün ordusunu bozguna uğrattığını duydu."));
trsuperobject.biblemap.put("b13_18_10", new trsuperobject(13,18,10,"Tou Kral Davutu selamlamak ve Hadadezerle savaşıp yendiği için kutlamak üzere oğlu Hadoramı ona gönderdi. Çünkü Tou Hadadezerle sürekli savaşmıştı. Hadoram Davuta her türlü altın, gümüş, tunç armağanlar getirdi."));
trsuperobject.biblemap.put("b13_18_11", new trsuperobject(13,18,11,"Kral Davut bu armağanları bütün uluslardan -Edom, Moav, Ammonlular, Filistliler ve Amaleklilerden- ele geçirdiği altın ve gümüşle birlikte RABbe adadı."));
trsuperobject.biblemap.put("b13_18_12", new trsuperobject(13,18,12,"Seruya oğlu Avişay Tuz Vadisinde on sekiz bin Edomlu öldürdü."));
trsuperobject.biblemap.put("b13_18_13", new trsuperobject(13,18,13,"Edoma askeri birlikler yerleştirdi. Edomluların tümü Davutun köleleri oldular. RAB Davutu gittiği her yerde zafere ulaştırdı."));
trsuperobject.biblemap.put("b13_18_14", new trsuperobject(13,18,14,"Bütün İsrailde krallık yapan Davut halkına doğruluk ve adalet sağladı."));
trsuperobject.biblemap.put("b13_18_15", new trsuperobject(13,18,15,"Seruya oğlu Yoav ordu komutanı, Ahilut oğlu Yehoşafat devlet tarihçisiydi."));
trsuperobject.biblemap.put("b13_18_16", new trsuperobject(13,18,16,"Ahituv oğlu Sadokla Aviyatar oğlu Ahimelek kâhin, Şavşa yazmandı."));
trsuperobject.biblemap.put("b13_18_17", new trsuperobject(13,18,17,"Yehoyada oğlu Benaya Keretliler'le Peletliler'in komutanıydı. Davut'un oğulları da sarayda yüksek görevlere atanmışlardı."));
trsuperobject.biblemap.put("b13_19_1", new trsuperobject(13,19,1,"Bir süre sonra Ammon Kralı Nahaş öldü, yerine oğlu kral oldu."));
trsuperobject.biblemap.put("b13_19_2", new trsuperobject(13,19,2,"Davut, ‹‹Babası bana iyilik ettiği için ben de Nahaş oğlu Hanuna iyilik edeceğim›› diye düşünerek, babasının ölümünden dolayı baş sağlığı dilemek için Hanuna ulaklar gönderdi. Davutun ulakları Hanuna baş sağlığı dilemek için Ammonluların ülkesine varınca,"));
trsuperobject.biblemap.put("b13_19_3", new trsuperobject(13,19,3,"Ammon önderleri Hanuna şöyle dediler: ‹‹Davut sana baş sağlığı dileyen bu adamları gönderdi diye babana saygı duyduğunu mu sanıyorsun? Bu ulaklar ülkeyi araştırmak, casusluk etmek, yıkmak için buraya geldiler.››"));
trsuperobject.biblemap.put("b13_19_4", new trsuperobject(13,19,4,"Bunun üzerine Hanun Davutun ulaklarını yakalattı. Sakallarını tıraş edip giysilerinin kalçayı kapatan kesimini ortadan kesti ve onları öylece gönderdi."));
trsuperobject.biblemap.put("b13_19_5", new trsuperobject(13,19,5,"Davut bunu duyunca, ulakları karşılamak üzere adamlar gönderdi. Çünkü ulaklar çok utanıyorlardı. Kral, ‹‹Sakalınız uzayıncaya dek Erihada kalın, sonra dönün›› diye buyruk verdi."));
trsuperobject.biblemap.put("b13_19_6", new trsuperobject(13,19,6,"Ammonlular Davutun nefretini kazandıklarını anlayınca, Hanunla Ammonlular Aram-Naharayim, Aram-Maaka ve Sovadan savaş arabalarıyla atlılar kiralamak için bin talant gümüş gönderdiler."));
trsuperobject.biblemap.put("b13_19_7", new trsuperobject(13,19,7,"Otuz iki bin savaş arabası ve Maaka Kralıyla askerlerini kiraladılar. Maaka Kralıyla askerleri gelip Medevanın yakınında ordugah kurdular. Ammonlular da savaşmak üzere kentlerinden çıkıp bir araya geldiler."));
trsuperobject.biblemap.put("b13_19_8", new trsuperobject(13,19,8,"Davut bunu duyunca, Yoavı ve güçlü adamlardan oluşan bütün ordusunu onlara karşı gönderdi."));
trsuperobject.biblemap.put("b13_19_9", new trsuperobject(13,19,9,"Ammonlular çıkıp kent kapısında savaş düzeni aldılar. Yardıma gelen krallar da kırda savaş düzenine girdiler."));
trsuperobject.biblemap.put("b13_19_10", new trsuperobject(13,19,10,"Önde, arkada düşman birliklerini gören Yoav, İsrailin en iyi askerlerinden bazılarını seçerek Aramlıların karşısına yerleştirdi."));
trsuperobject.biblemap.put("b13_19_11", new trsuperobject(13,19,11,"Geri kalan birlikleri de kardeşi Avişayın komutasına vererek Ammonlulara karşı yerleştirdi."));
trsuperobject.biblemap.put("b13_19_12", new trsuperobject(13,19,12,"Yoav, ‹‹Aramlılar benden güçlü çıkarsa, yardımıma gelirsin›› dedi, ‹‹Ama Ammonlular senden güçlü çıkarsa, ben sana yardıma gelirim."));
trsuperobject.biblemap.put("b13_19_13", new trsuperobject(13,19,13,"Güçlü ol! Halkımızın ve Tanrımızın kentleri uğruna yürekli olalım! RAB gözünde iyi olanı yapsın.››"));
trsuperobject.biblemap.put("b13_19_14", new trsuperobject(13,19,14,"Yoavla yanındakiler Aramlılara karşı savaşmak için ileri atılınca, Aramlılar onlardan kaçtı."));
trsuperobject.biblemap.put("b13_19_15", new trsuperobject(13,19,15,"Aramlıların kaçıştığını gören Ammonlular da Yoavın kardeşi Avişaydan kaçarak kente girdiler. Yoav ise Yeruşalime döndü."));
trsuperobject.biblemap.put("b13_19_16", new trsuperobject(13,19,16,"İsraillilerin önünde bozguna uğradıklarını gören Aramlılar, ulaklar gönderip Fırat Irmağının karşı yakasında, Hadadezerin ordu komutanı Şofakın komutasındaki Aramlıları çağırdılar."));
trsuperobject.biblemap.put("b13_19_17", new trsuperobject(13,19,17,"Davut bunu duyunca, bütün İsrail ordusunu topladı. Şeria Irmağından geçerek onlara doğru ilerleyip karşılarında savaş düzeni aldı. Davut savaşmak için düzen alınca, Aramlılar onunla savaştılar."));
trsuperobject.biblemap.put("b13_19_18", new trsuperobject(13,19,18,"Ne var ki, Aramlılar İsraillilerin önünden kaçtılar. Davut onlardan yedi bin savaş arabası sürücüsü ile kırk bin yaya asker öldürdü. Ordu komutanı Şofakı da öldürdü."));
trsuperobject.biblemap.put("b13_19_19", new trsuperobject(13,19,19,"Hadadezer'in buyruğundaki krallar İsrailliler'in önünde bozguna uğradıklarını görünce, Davut'la barış yaparak ona boyun eğdiler. Aramlılar bundan böyle Ammonlular'a yardım etmekten kaçındılar."));
trsuperobject.biblemap.put("b13_20_1", new trsuperobject(13,20,1,"İlkbaharda, kralların savaşa gittiği dönemde Yoav, komutasındaki orduyla birlikte yola çıktı. Ammonluların ülkesini yerle bir edip Rabba Kentini kuşatırken Davut Yeruşalimde kalıyordu. Yoav Rabba Kentine saldırıp onu yerle bir etti."));
trsuperobject.biblemap.put("b13_20_2", new trsuperobject(13,20,2,"Davut Ammon Kralının başındaki tacı aldı. Değerli taşlarla süslü, ağırlığı bir talant altını bulan tacı Davutun başına koydular. Davut kentten çok miktarda mal yağmalayıp götürdü."));
trsuperobject.biblemap.put("b13_20_3", new trsuperobject(13,20,3,"Orada yaşayan halkı dışarı çıkarıp testereyle, demir kazma ve baltayla yapılan işlerde çalıştırdıfç. Davut bunu bütün Ammon kentlerinde uyguladı. Sonra ordusuyla birlikte Yeruşalime döndü. Masoretik metin ‹‹Kesti››."));
trsuperobject.biblemap.put("b13_20_4", new trsuperobject(13,20,4,"Bir süre sonra Filistlilerle Gezerde savaş çıktı. Bu savaş sırasında Huşalı Sibbekay, Rafa soyundan Sippayı öldürünce, Filistliler boyun eğdiler."));
trsuperobject.biblemap.put("b13_20_5", new trsuperobject(13,20,5,"İsraillilerle Filistliler arasında çıkan bir başka savaşta Yair oğlu Elhanan, Gatlı Golyatın kardeşi Lahmiyi öldürdü. Golyatın mızrağının sapı dokumacı tezgahının sırığı gibiydi."));
trsuperobject.biblemap.put("b13_20_6", new trsuperobject(13,20,6,"Gatta bir kez daha savaş çıktı. Orada dev gibi bir adam vardı. Elleri, ayakları altışar parmaklıydı. Toplam yirmi dört parmağı vardı. O da Rafa soyundandı."));
trsuperobject.biblemap.put("b13_20_7", new trsuperobject(13,20,7,"Adam İsraillilere meydan okuyunca, Davutun kardeşi Şimanın oğlu Yonatan onu öldürdü."));
trsuperobject.biblemap.put("b13_20_8", new trsuperobject(13,20,8,"Bunlar Gat'taki Rafa soyundandı. Davut'la adamları tarafından öldürüldüler."));
trsuperobject.biblemap.put("b13_21_1", new trsuperobject(13,21,1,"Şeytan İsraillilere karşı çıkıp İsrailde sayım yapması için Davutu kışkırttı."));
trsuperobject.biblemap.put("b13_21_2", new trsuperobject(13,21,2,"Davut Yoavla halkın önderlerine, ‹‹Gidin, Beer-Şevadan Dana dek İsraillileri sayın›› dedi, ‹‹Sonra bana bilgi verin ki, halkın sayısını bileyim.››"));
trsuperobject.biblemap.put("b13_21_3", new trsuperobject(13,21,3,"Ama Yoav, ‹‹RAB halkını yüz kat daha çoğaltsın›› diye karşılık verdi, ‹‹Ey efendim kral, bunlar hepsi senin kulların değil mi? Efendim neden bunu istiyor? Neden İsraili suça sürüklüyor?››"));
trsuperobject.biblemap.put("b13_21_4", new trsuperobject(13,21,4,"Gelgelelim kralın sözü Yoavın sözünden baskın çıktı. Böylece Yoav kralın yanından ayrılıp İsrailin her yanını dolaşmaya gitti. Sonra Yeruşalime dönerek"));
trsuperobject.biblemap.put("b13_21_5", new trsuperobject(13,21,5,"sayımın sonucunu Davuta bildirdi: İsrailde kılıç kuşanabilen bir milyon yüz bin, Yahudadaysa dört yüz yetmiş bin kişi vardı."));
trsuperobject.biblemap.put("b13_21_6", new trsuperobject(13,21,6,"Yoav Levililerle Benyaminlileri saymadı; çünkü kralın bu konudaki buyruğunu benimsememişti."));
trsuperobject.biblemap.put("b13_21_7", new trsuperobject(13,21,7,"Tanrı da yapılanı uygun görmedi ve bu yüzden İsraillileri cezalandırdı."));
trsuperobject.biblemap.put("b13_21_8", new trsuperobject(13,21,8,"Davut Tanrıya, ‹‹Bunu yapmakla büyük günah işledim!›› dedi, ‹‹Lütfen kulunun suçunu bağışla. Çünkü çok akılsızca davrandım.››"));
trsuperobject.biblemap.put("b13_21_10", new trsuperobject(13,21,10,"‹‹Gidip Davuta de ki, ‹RAB şöyle diyor: Önüne üç seçenek koyuyorum. Bunlardan birini seç de sana onu yapayım.››"));
trsuperobject.biblemap.put("b13_21_11", new trsuperobject(13,21,11,"Gad Davuta gidip şöyle dedi: ‹‹RAB diyor ki, ‹Hangisini istiyorsun?"));
trsuperobject.biblemap.put("b13_21_12", new trsuperobject(13,21,12,"Üç yıl kıtlık mı? Yoksa kılıçla seni kovalayan düşmanlarının önünde üç ay kaçıp yok olmak mı? Ya da RABbin kılıcının ve RABbin meleğinin bütün İsrail ülkesine üç gün salgın hastalık salmasını mı?› Beni gönderene ne yanıt vereyim, şimdi iyice düşün.››"));
trsuperobject.biblemap.put("b13_21_13", new trsuperobject(13,21,13,"Davut, ‹‹Sıkıntım büyük›› diye yanıtladı, ‹‹İnsan eline düşmektense, RABbin eline düşeyim. Çünkü Onun acıması çok büyüktür.››"));
trsuperobject.biblemap.put("b13_21_14", new trsuperobject(13,21,14,"Bunun üzerine RAB İsrail ülkesine salgın hastalık gönderdi. Yetmiş bin İsrailli öldü."));
trsuperobject.biblemap.put("b13_21_15", new trsuperobject(13,21,15,"Tanrı Yeruşalimi yok etmek için bir melek gönderdi. Ama melek yıkıma başlayacağı sırada RAB onu gördü. Göndereceği yıkımdan vazgeçerek halkı yok eden meleğe, ‹‹Yeter artık! Elini çek›› dedi. RABbin meleği Yevuslu Ornanın harman yerinde duruyordu."));
trsuperobject.biblemap.put("b13_21_16", new trsuperobject(13,21,16,"Davut başını kaldırıp baktı. Elinde yalın bir kılıç olan RABbin meleğini gördü. Melek elini Yeruşalimin üzerine uzatmış, yerle gök arasında duruyordu. Çula sarınmış Davutla halkın ileri gelenleri yüzüstü yere kapandılar."));
trsuperobject.biblemap.put("b13_21_17", new trsuperobject(13,21,17,"Davut Tanrıya şöyle seslendi: ‹‹Halkın sayılmasını buyuran ben değil miydim? Günah işleyen benim, kötülük yapan benim. Ama bu koyunlar ne yaptı ki? Ya RAB Tanrım, ne olur beni ve babamın soyunu cezalandır. Bu salgın hastalığı halkın üzerinden kaldır.››"));
trsuperobject.biblemap.put("b13_21_18", new trsuperobject(13,21,18,"RABbin meleği Gada, Davutun Yevuslu Ornanın harman yerine gidip RABbe bir sunak kurmasını buyurdu."));
trsuperobject.biblemap.put("b13_21_19", new trsuperobject(13,21,19,"Davut RABbin adıyla konuşan Gadın sözü uyarınca oraya gitti."));
trsuperobject.biblemap.put("b13_21_20", new trsuperobject(13,21,20,"Harman yerinde buğday döverken, Ornan arkasına dönüp meleği gördü. Yanındaki dört oğlu gizlendi."));
trsuperobject.biblemap.put("b13_21_21", new trsuperobject(13,21,21,"Davutun yaklaştığını gören Ornan, harman yerinden çıktı, varıp Davutun önünde yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b13_21_22", new trsuperobject(13,21,22,"Davut Ornana, ‹‹RABbe bir sunak kurmak üzere harman yerini bana sat›› dedi, ‹‹Öyle ki, salgın hastalık halkın üzerinden kalksın. Harman yerini bana tam değerine satacaksın.››"));
trsuperobject.biblemap.put("b13_21_23", new trsuperobject(13,21,23,"Ornan, ‹‹Senin olsun!›› diye karşılık verdi, ‹‹Efendim kral uygun gördüğünü yapsın. İşte yakmalık sunular için öküzleri, odun olarak dövenleri, tahıl sunusu olarak buğday veriyorum. Hepsini veriyorum.››"));
trsuperobject.biblemap.put("b13_21_24", new trsuperobject(13,21,24,"Ne var ki, Kral Davut, ‹‹Olmaz!›› dedi, ‹‹Tam değerini ödeyip alacağım. Çünkü senin olanı RABbe vermem. Karşılığını ödemeden yakmalık sunu sunmam.››"));
trsuperobject.biblemap.put("b13_21_25", new trsuperobject(13,21,25,"Böylece Davut harman yeri için Ornana altı yüz şekel altın ödedi."));
trsuperobject.biblemap.put("b13_21_26", new trsuperobject(13,21,26,"Davut orada RABbe bir sunak kurup yakmalık sunuları ve esenlik sunularını sundu. RABbe yakardı. RAB yakmalık sunu sunağında gökten gönderdiği ateşle onu yanıtladı."));
trsuperobject.biblemap.put("b13_21_27", new trsuperobject(13,21,27,"Bundan sonra RAB meleğe kılıcını kınına koymasını buyurdu. Melek buyruğa uydu."));
trsuperobject.biblemap.put("b13_21_28", new trsuperobject(13,21,28,"RABbin kendisine Yevuslu Ornanın harman yerinde yanıt verdiğini gören Davut, orada kurbanlar kesti."));
trsuperobject.biblemap.put("b13_21_29", new trsuperobject(13,21,29,"Musanın çölde RAB için yaptığı çadırla yakmalık sunu sunağı o sırada Givondaki tapınma yerindeydi."));
trsuperobject.biblemap.put("b13_21_30", new trsuperobject(13,21,30,"Ama Davut Tanrı'ya danışmak için oraya gidemedi. Çünkü RAB'bin meleğinin kılıcından korkuyordu."));
trsuperobject.biblemap.put("b13_22_1", new trsuperobject(13,22,1,"Davut, ‹‹RAB Tanrının Tapınağı ve İsrail için yakmalık sunu sunağı burada olacak›› dedi."));
trsuperobject.biblemap.put("b13_22_2", new trsuperobject(13,22,2,"Davut İsrailde yaşayan yabancıların toplanmasını buyurdu. Tanrının Tapınağını kurmak için onları yontma taşlar hazırlamakla görevlendirdi."));
trsuperobject.biblemap.put("b13_22_3", new trsuperobject(13,22,3,"Giriş kapılarının çivileri ve kenetleri için çok miktarda demir, tartılamayacak kadar çok tunç sağladı."));
trsuperobject.biblemap.put("b13_22_4", new trsuperobject(13,22,4,"Ayrıca sayısız sedir tomruğu da sağladı. Çünkü Saydalılarla Surlular Davuta çok sedir tomruğu getirmişlerdi."));
trsuperobject.biblemap.put("b13_22_5", new trsuperobject(13,22,5,"Davut, ‹‹Oğlum Süleyman genç ve deneyimsiz›› dedi, ‹‹RAB için kurulacak tapınak bütün ulusların gözünde çok büyük, ünlü ve görkemli olmalı. Onun için hazırlık yapmalıyım.›› Böylece, ölmeden önce, tapınağın yapımı için büyük hazırlık yaptı."));
trsuperobject.biblemap.put("b13_22_6", new trsuperobject(13,22,6,"Davut, oğlu Süleymanı yanına çağırdı. Onu İsrailin Tanrısı RAB için bir tapınak kurmakla görevlendirdi."));
trsuperobject.biblemap.put("b13_22_7", new trsuperobject(13,22,7,"Sonra Süleymana şöyle dedi: ‹‹Oğlum, Tanrım RABbin adına bir tapınak kurmak istedim."));
trsuperobject.biblemap.put("b13_22_8", new trsuperobject(13,22,8,"Ama RAB bana, ‹Sen çok kan döktün, büyük savaşlara katıldın› dedi, ‹Benim adıma tapınak kurmayacaksın. Çünkü yeryüzünde gözümün önünde çok kan döktün."));
trsuperobject.biblemap.put("b13_22_9", new trsuperobject(13,22,9,"Ama barışsever bir oğlun olacak. Onu her yandan kuşatan düşmanlarından kurtarıp rahata kavuşturacağım. Adı Süleyman olacak. Onun döneminde İsrailin barış ve güvenlik içinde yaşamasını sağlayacağım."));
trsuperobject.biblemap.put("b13_22_10", new trsuperobject(13,22,10,"Adıma bir tapınak kuracak olan odur. O bana oğul olacak, ben de ona baba olacağım. Onun krallığının tahtını İsrailde sonsuza dek sürdüreceğim.› gelmektedir."));
trsuperobject.biblemap.put("b13_22_11", new trsuperobject(13,22,11,"‹‹Şimdi, oğlum, RAB seninle olsun; Tanrın RAB kendisine tapınak kurman için verdiği söz uyarınca, seni başarılı kılsın."));
trsuperobject.biblemap.put("b13_22_12", new trsuperobject(13,22,12,"Tanrın RAB seni İsraile önder atadığı zaman yasasını yerine getirmen için sana sağgörü ve anlayış versin."));
trsuperobject.biblemap.put("b13_22_13", new trsuperobject(13,22,13,"RABbin İsraile Musa aracılığıyla verdiği kurallara, ilkelere dikkatle uyarsan, başarılı olacaksın. Güçlü ve yürekli ol! Korkma, yılma!"));
trsuperobject.biblemap.put("b13_22_14", new trsuperobject(13,22,14,"‹‹İşte sıkıntılar içinde RABbin Tapınağı için yüz bin talant altın, bir milyon talant gümüş, tartılamayacak kadar çok miktarda tunç, demir, tomruk ve taş sağladım. Sen de bunlara ekleyebilirsin."));
trsuperobject.biblemap.put("b13_22_15", new trsuperobject(13,22,15,"Birçok işçin var. Taşçıların, duvarcıların, marangozların ve her tür işte hünerli adamların var."));
trsuperobject.biblemap.put("b13_22_16", new trsuperobject(13,22,16,"Ölçülemeyecek kadar altının, gümüşün, tuncun, demirin de var. Haydi, işi başlat. RAB seninle olsun!››"));
trsuperobject.biblemap.put("b13_22_17", new trsuperobject(13,22,17,"Davut bütün İsrail önderlerine oğlu Süleymana yardım etmelerini buyurdu."));
trsuperobject.biblemap.put("b13_22_18", new trsuperobject(13,22,18,"Onlara, ‹‹Tanrınız RAB sizinle değil mi?›› dedi, ‹‹Her yanda sizi rahata kavuşturmadı mı? Çünkü bu ülkede yaşayanları elime teslim etti. Bu yüzden ülke RABbin ve halkının yönetimi altındadır."));
trsuperobject.biblemap.put("b13_22_19", new trsuperobject(13,22,19,"Şimdi yüreğinizi ve canınızı Tanrınız RAB'be adayarak O'na yönelin. RAB Tanrı'nın Tapınağı'nı yapmaya başlayın. Öyle ki, RAB'bin adına kuracağınız tapınağa RAB'bin Antlaşma Sandığı'nı ve Tanrı'ya ait kutsal eşyaları getiresiniz.››"));
trsuperobject.biblemap.put("b13_23_1", new trsuperobject(13,23,1,"Davut çok yaşlanınca, oğlu Süleymanı İsrail Kralı yaptı."));
trsuperobject.biblemap.put("b13_23_2", new trsuperobject(13,23,2,"Davut İsrailin bütün önderlerini, kâhinleri, Levilileri bir araya topladı."));
trsuperobject.biblemap.put("b13_23_3", new trsuperobject(13,23,3,"Otuz ve daha yukarı yaştaki Levililer sayıldı. Toplamı otuz sekiz bin erkekti."));
trsuperobject.biblemap.put("b13_23_4", new trsuperobject(13,23,4,"Bunlardan yirmi dört bini RABbin Tapınağının işlerini gözetecek, altı bini memur ve yargıç olacaktı;"));
trsuperobject.biblemap.put("b13_23_5", new trsuperobject(13,23,5,"dört bini kapı nöbetçisi olacak, dört bini de Davutun RABbi övmek için sağladığı çalgıları çalacaktı."));
trsuperobject.biblemap.put("b13_23_6", new trsuperobject(13,23,6,"Davut Levilileri Levinin oğullarına göre bölümlere ayırdı: Gerşon, Kehat, Merari."));
trsuperobject.biblemap.put("b13_23_7", new trsuperobject(13,23,7,"Gerşonlular: Ladan, Şimi."));
trsuperobject.biblemap.put("b13_23_8", new trsuperobject(13,23,8,"Ladanın oğulları: İlk oğlu Yehiel, Zetam, Yoel. Toplam üç kişiydi."));
trsuperobject.biblemap.put("b13_23_9", new trsuperobject(13,23,9,"Bunlar Ladan boyunun boy başlarıydı. Şiminin oğulları: Şelomit, Haziel, Haran. Toplam üç kişiydi."));
trsuperobject.biblemap.put("b13_23_10", new trsuperobject(13,23,10,"Şiminin öbür oğulları: Yahat, Ziza, Yeuş, Beria. Bu dördü Şiminin oğullarıydı."));
trsuperobject.biblemap.put("b13_23_11", new trsuperobject(13,23,11,"Yahat ilk, Ziza ikinci oğuldu. Ancak Yeuşla Berianın çok sayıda oğulları olmadığı için bir boy sayıldılar."));
trsuperobject.biblemap.put("b13_23_12", new trsuperobject(13,23,12,"Kehatın oğulları: Amram, Yishar, Hevron, Uzziel. Toplam dört kişi."));
trsuperobject.biblemap.put("b13_23_13", new trsuperobject(13,23,13,"Amramın oğulları: Harun, Musa. Harunla oğulları en kutsal eşyaları korumak, RABbin önünde buhur yakmak, Ona hizmet etmek ve sonsuza dek Onun adına halkı kutsamak için atandılar."));
trsuperobject.biblemap.put("b13_23_14", new trsuperobject(13,23,14,"Tanrı adamı Musanın oğulları da Levi oymağından sayıldı."));
trsuperobject.biblemap.put("b13_23_15", new trsuperobject(13,23,15,"Musanın oğulları: Gerşom, Eliezer."));
trsuperobject.biblemap.put("b13_23_16", new trsuperobject(13,23,16,"Gerşomun oğulları: Önder Şevuel."));
trsuperobject.biblemap.put("b13_23_17", new trsuperobject(13,23,17,"Eliezerin oğlu: Önder Rehavya. Eliezerin başka oğlu yoktu. Rehavyanınsa birçok oğlu vardı."));
trsuperobject.biblemap.put("b13_23_18", new trsuperobject(13,23,18,"Yisharın oğulları: Önder Şelomit."));
trsuperobject.biblemap.put("b13_23_19", new trsuperobject(13,23,19,"Hevronun oğulları: İlk oğlu Yeriya, ikincisi Amarya, üçüncüsü Yahaziel, dördüncüsü Yekamam."));
trsuperobject.biblemap.put("b13_23_20", new trsuperobject(13,23,20,"Uzzielin oğulları: İlk oğlu Mika, ikincisi Yişşiya."));
trsuperobject.biblemap.put("b13_23_21", new trsuperobject(13,23,21,"Merarinin oğulları: Mahli, Muşi. Mahlinin oğulları: Elazar, Kiş."));
trsuperobject.biblemap.put("b13_23_22", new trsuperobject(13,23,22,"Elazar oğul sahibi olmadan öldü. Ama kızları vardı. Amcaları Kişin oğulları onlarla evlendi."));
trsuperobject.biblemap.put("b13_23_23", new trsuperobject(13,23,23,"Muşinin oğulları: Mahli, Eder, Yeremot. Toplam üç kişi."));
trsuperobject.biblemap.put("b13_23_24", new trsuperobject(13,23,24,"Boylarına göre Levioğulları bunlardı. Boy başlarının her biri kendi adıyla sayıldı. Yirmi ve daha yukarı yaştaki Levililer, RABbin Tapınağının işlerinde görev aldılar."));
trsuperobject.biblemap.put("b13_23_25", new trsuperobject(13,23,25,"Çünkü Davut, ‹‹İsrailin Tanrısı RAB halkını rahata kavuşturdu›› demişti, ‹‹Yeruşalimi de sonsuza dek kendine konut seçti."));
trsuperobject.biblemap.put("b13_23_26", new trsuperobject(13,23,26,"Onun için Levililerin RABbin Çadırını ve tapınma hizmetinde kullanılan eşyaları taşımalarına artık gerek yok.››"));
trsuperobject.biblemap.put("b13_23_27", new trsuperobject(13,23,27,"Davutun son buyruğu uyarınca, yirmi ve daha yukarı yaştaki Levililer sayıldı."));
trsuperobject.biblemap.put("b13_23_28", new trsuperobject(13,23,28,"Levililerin görevi RABbin Tapınağının hizmetinde Harunoğullarına yardım etmekti: Avlulardan, odalardan, kutsal eşyaların arınmasından ve Tanrının Tapınağındaki öbür hizmetlerden sorumluydular."));
trsuperobject.biblemap.put("b13_23_29", new trsuperobject(13,23,29,"Adak ekmeklerinden, tahıl sunusu için kullanılan ince undan, mayasız ince ekmekten, sacda pişirilen yiyeceklerden ve zeytinyağıyla karıştırılan sunulardan, her tür hacim ve uzunluk ölçülerinden onlar sorumluydu."));
trsuperobject.biblemap.put("b13_23_30", new trsuperobject(13,23,30,"RABbe şükretmek, övgüler sunmak üzere her sabah ve akşam tapınakta hazır bulunacaklardı."));
trsuperobject.biblemap.put("b13_23_31", new trsuperobject(13,23,31,"Şabat Günü, Yeni Ay Töreni ve öbür bayramlarda RABbe yakmalık sunular sunulduğunda da hazır bulunacaklardı. RABbin önünde, kendileri için belirlenen ilkeler uyarınca uygun sayıda sürekli hizmet edeceklerdi."));
trsuperobject.biblemap.put("b13_23_32", new trsuperobject(13,23,32,"Böylece Levililer Buluşma Çadırı'na ve kutsal yere bakma, RAB'bin Tapınağı'nın hizmetinde kardeşleri Harunoğulları'na yardım etme görevini üstlendiler."));
trsuperobject.biblemap.put("b13_24_1", new trsuperobject(13,24,1,"Harunoğullarının bağlı oldukları bölükler: Harunun oğulları: Nadav, Avihu, Elazar, İtamar."));
trsuperobject.biblemap.put("b13_24_2", new trsuperobject(13,24,2,"Nadavla Avihu babalarından önce, oğul sahibi olamadan öldüler. Onun için Elazarla İtamar kâhinlik yaptılar."));
trsuperobject.biblemap.put("b13_24_3", new trsuperobject(13,24,3,"Davut Elazar soyundan Sadokla İtamar soyundan Ahimelekin yardımıyla Harunoğullarını yaptıkları göreve göre bölüklere ayırdı."));
trsuperobject.biblemap.put("b13_24_4", new trsuperobject(13,24,4,"Elazaroğulları arasında İtamaroğullarından daha çok önder olduğundan, buna göre bölündüler: Elazaroğullarından on altı boy başı, İtamaroğullarından ise sekiz boy başı çıktı."));
trsuperobject.biblemap.put("b13_24_5", new trsuperobject(13,24,5,"Gerek Elazaroğulları, gerekse İtamaroğulları arasında kutsal yerden ve Tanrıyla ilgili hizmetlerden sorumlu önderler vardı. Bu yüzden atanmaları kayırılmaksızın kurayla yapıldı."));
trsuperobject.biblemap.put("b13_24_6", new trsuperobject(13,24,6,"Levili Netanel oğlu Yazman Şemaya, kralın ve görevlileri Kâhin Sadok, Aviyatar oğlu Ahimelek, kâhinler ve Levililerin boy başlarının gözü önünde kura çekimini kaydetti. Kura sırayla, bir Elazar ailesinden, bir İtamar ailesinden çekildi."));
trsuperobject.biblemap.put("b13_24_7", new trsuperobject(13,24,7,"Birinci kura Yehoyarive düştü,İkincisi Yedayaya."));
trsuperobject.biblemap.put("b13_24_8", new trsuperobject(13,24,8,"Üçüncüsü Harime,Dördüncüsü Seorime,"));
trsuperobject.biblemap.put("b13_24_9", new trsuperobject(13,24,9,"Beşincisi Malkiyaya,Altıncısı Miyamine,"));
trsuperobject.biblemap.put("b13_24_10", new trsuperobject(13,24,10,"Yedincisi Hakkosa,Sekizincisi Aviyaya,"));
trsuperobject.biblemap.put("b13_24_11", new trsuperobject(13,24,11,"Dokuzuncusu Yeşuya,Onuncusu Şekanyaya,"));
