trsuperobject.biblemap.put("b13_24_18", new trsuperobject(13,24,18,"Yirmi üçüncüsü Delayaya,Yirmi dördüncüsü Maazyaya düştü."));
trsuperobject.biblemap.put("b13_24_19", new trsuperobject(13,24,19,"İsrailin Tanrısı RABbin buyruğu uyarınca ataları Harunun verdiği ilkelere göre RABbin Tapınağına gidip görev yapma sırası buydu."));
trsuperobject.biblemap.put("b13_24_20", new trsuperobject(13,24,20,"Öbür Levililer:"));
trsuperobject.biblemap.put("b13_24_21", new trsuperobject(13,24,21,"Rehavyaoğullarından önder Yişşiya."));
trsuperobject.biblemap.put("b13_24_22", new trsuperobject(13,24,22,"Yisharoğullarından Şelomot,"));
trsuperobject.biblemap.put("b13_24_23", new trsuperobject(13,24,23,"Hevronun oğulları: İlk oğlu Yeriya, ikincisi Amarya, üçüncüsü Yahaziel, dördüncüsü Yekamam."));
trsuperobject.biblemap.put("b13_24_24", new trsuperobject(13,24,24,"Uzzielin oğlu: Mika."));
trsuperobject.biblemap.put("b13_24_25", new trsuperobject(13,24,25,"Mikanın kardeşi: Yişşiya."));
trsuperobject.biblemap.put("b13_24_26", new trsuperobject(13,24,26,"Merarioğulları: Mahli, Muşi, Yaaziya."));
trsuperobject.biblemap.put("b13_24_27", new trsuperobject(13,24,27,"Merarinin torunlarından Yaaziyanın oğulları: Şoham, Zakkur, İvri."));
trsuperobject.biblemap.put("b13_24_28", new trsuperobject(13,24,28,"Mahliden: Elazar. Elazarın oğlu olmadı."));
trsuperobject.biblemap.put("b13_24_29", new trsuperobject(13,24,29,"Kişten: Kiş oğlu Yerahmeel."));
trsuperobject.biblemap.put("b13_24_30", new trsuperobject(13,24,30,"Muşinin oğulları: Mahli, Eder, Yerimot. Levi soyundan gelen boylar bunlardır."));
trsuperobject.biblemap.put("b13_24_31", new trsuperobject(13,24,31,"Bunlar da kardeşleri Harunoğulları gibi, Kral Davut'un, Sadok'un, Ahimelek'in, kâhinler ve Levililer'in boy başlarının gözü önünde kura çekti. Büyük boy başları da, kardeşleri olan küçük boy başları da kura çektiler."));
trsuperobject.biblemap.put("b13_25_1", new trsuperobject(13,25,1,"Davutla ordu komutanları hizmet için Asafın, Hemanın, Yedutunun bazı oğullarını ayırdılar. Bunlar lir, çenk ve ziller eşliğinde peygamberlikte bulunacaklardı. Bu göreve atananların listesi şuydu:"));
trsuperobject.biblemap.put("b13_25_2", new trsuperobject(13,25,2,"Asafın oğullarından: Zakkur, Yusuf, Netanya, Aşarela. Bunlar kralın buyruğu uyarınca peygamberlikte bulunan Asafın yönetimi altındaydılar."));
trsuperobject.biblemap.put("b13_25_3", new trsuperobject(13,25,3,"Yedutunun oğullarından: Gedalya, Seri, Yeşaya, Şimi, Haşavya, Mattitya. Toplam altı kişiydi. Lir eşliğinde peygamberlikte bulunan, RABbe şükür ve övgü sunan babaları Yedutunun sorumluluğu altındaydılar."));
trsuperobject.biblemap.put("b13_25_4", new trsuperobject(13,25,4,"Hemanın oğullarından: Bukkiya, Mattanya, Uzziel, Şevuel, Yerimot, Hananya, Hanani, Eliata, Giddalti, Romamti-Ezer, Yoşbekaşa, Malloti, Hotir, Mahaziot."));
trsuperobject.biblemap.put("b13_25_5", new trsuperobject(13,25,5,"Hepsi kralın bilicisi Hemanın oğullarıydı. Tanrının sözü uyarınca bu oğullar Hemanı güçlendirmek için ona verilmişti. Tanrı Hemana on dört oğulla üç kız verdi."));
trsuperobject.biblemap.put("b13_25_6", new trsuperobject(13,25,6,"Bunların tümü babalarının sorumluluğu altında RAB Tanrının Tapınağında hizmet etmek için zil, çenk ve lirler eşliğinde ezgi söylerdi. Asaf, Yedutun, Heman kralın sorumluluğu altındaydı."));
trsuperobject.biblemap.put("b13_25_8", new trsuperobject(13,25,8,"Bunların her biri, büyük küçük, öğretmen öğrenci ayrımı yapılmaksızın, görev dağıtımı için kura çekti."));
trsuperobject.biblemap.put("b13_25_9", new trsuperobject(13,25,9,"Birinci kura Asaf soyundan Yusufa düştü; oğulları ve kardeşleriyle birlikte 12 kişi. kişi››. Bu tümce Masoretik metinde geçmemektedir. İkincisi Gedalyaya; kendisi, kardeşleri ve oğullarıyla birlikte 12 kişi."));
trsuperobject.biblemap.put("b13_25_10", new trsuperobject(13,25,10,"Üçüncüsü Zakkura; oğulları ve kardeşleriyle birlikte 12 kişi."));
trsuperobject.biblemap.put("b13_25_11", new trsuperobject(13,25,11,"Dördüncüsü Yisriye; oğulları ve kardeşleriyle birlikte 12 kişi."));
