trsuperobject.biblemap.put("b13_25_17", new trsuperobject(13,25,17,"Onuncusu Şimiye; oğulları ve kardeşleriyle birlikte 12 kişi."));
trsuperobject.biblemap.put("b13_25_18", new trsuperobject(13,25,18,"On birincisi Azarele; oğulları ve kardeşleriyle birlikte 12 kişi."));
trsuperobject.biblemap.put("b13_25_19", new trsuperobject(13,25,19,"On ikincisi Haşavyaya; oğulları ve kardeşleriyle birlikte 12 kişi."));
