trsuperobject.biblemap.put("b13_25_21", new trsuperobject(13,25,21,"On dördüncüsü Mattityaya; oğulları ve kardeşleriyle birlikte 12 kişi."));
trsuperobject.biblemap.put("b13_25_22", new trsuperobject(13,25,22,"On beşincisi Yeremota; oğulları ve kardeşleriyle birlikte 12 kişi."));
