trsuperobject.biblemap.put("b14_1_3", new trsuperobject(14,1,3,"Sonra bütün toplulukla birlikte Givondaki tapınma yerine gitti. Çünkü RABbin kulu Musanın çölde yaptığı Tanrıyla Buluşma Çadırı oradaydı."));
trsuperobject.biblemap.put("b14_1_4", new trsuperobject(14,1,4,"Ancak Davut Tanrının Antlaşma Sandığını Kiryat-Yearimden getirip Yeruşalimde hazırladığı çadıra koymuştu."));
trsuperobject.biblemap.put("b14_1_5", new trsuperobject(14,1,5,"Hur oğlu Uri oğlu Besalelin yaptığı tunç sunağı da Givonda RABbin Konutunun önüne yerleştirmişti. Süleymanla topluluk orada RABbe danıştılar."));
