trsuperobject.biblemap.put("b14_1_15", new trsuperobject(14,1,15,"Krallığı döneminde Yeruşalimde altın ve gümüş taş değerine düştü. Sedir ağaçları Şefeladaki yabanıl incir ağaçları kadar bollaştı."));
trsuperobject.biblemap.put("b14_1_16", new trsuperobject(14,1,16,"Süleymanın atları Mısır ve Keveden getirilirdi. Kralın tüccarları atları Keveden satın alırdı."));
