trsuperobject.biblemap.put("b14_3_12", new trsuperobject(14,3,12,"Aynı şekilde öteki Keruvun da kanadı beş arşındı ve tapınağın duvarına erişiyordu. Öbür kanat da beş arşındı ve birinci Keruvun kanadına değiyordu."));
trsuperobject.biblemap.put("b14_3_13", new trsuperobject(14,3,13,"Ayakta duran ve açılmış kanatlarının uzunluğu yirmi arşın olan Keruvların yüzü ana bölüme bakıyordu."));
trsuperobject.biblemap.put("b14_3_14", new trsuperobject(14,3,14,"En Kutsal Yerin perdesi lacivert, mor, kırmızı kumaştan ve ince ketenden yapılmıştı. Üzerinde Keruv işlemeleri vardı."));
trsuperobject.biblemap.put("b14_3_15", new trsuperobject(14,3,15,"Süleyman otuz beşer arşın yüksekliğinde iki sütun yaptırıp tapınağın önüne diktirdi. Sütun başlıkları beşer arşın yüksekliğindeydi."));
trsuperobject.biblemap.put("b14_3_16", new trsuperobject(14,3,16,"Gerdanlığa benzerfö zincirler yaptırarak sütunların üzerine koydurdu. Yüz nar motifi yaptırıp zincirlere taktırdı."));
trsuperobject.biblemap.put("b14_3_17", new trsuperobject(14,3,17,"Sütunları tapınağın önüne diktirip sağdakine Yakin, soldakine Boaz adını verdi. gelebilir."));
trsuperobject.biblemap.put("b14_4_1", new trsuperobject(14,4,1,"Süleyman tunçtan bir sunak yaptırdı. Sunağın eniyle uzunluğu yirmişer arşın, yüksekliği on arşındıfş."));
trsuperobject.biblemap.put("b14_4_2", new trsuperobject(14,4,2,"Dökme tunçtan on arşınfş çapında, beş arşın derinliğinde, çevresi otuz arşın yuvarlak bir havuz yaptırdı."));
