trsuperobject.biblemap.put("b14_4_4", new trsuperobject(14,4,4,"Havuz üçü kuzeye, üçü batıya, üçü güneye, üçü de doğuya bakan on iki boğa heykeli üzerine oturtulmuştu. Boğaların sağrıları içe dönüktü."));
trsuperobject.biblemap.put("b14_4_5", new trsuperobject(14,4,5,"Havuzun çeperi dört parmak kalınlığındaydı; kenarları kâse kenarlarını, nilüferleri andırıyordu. Üç bin bat su alıyordu."));
trsuperobject.biblemap.put("b14_4_6", new trsuperobject(14,4,6,"Süleyman yıkama işleri için on kazan yaptırdı. Beşini sunağın güneyine, beşini kuzeyine yerleştirdi. Yakmalık sunuların parçaları bunlarda yıkanırdı. Havuz ise kâhinlerin yıkanması içindi."));
trsuperobject.biblemap.put("b14_4_7", new trsuperobject(14,4,7,"Süleyman tanıma uygun biçimde yaptırdığı on altın kandilliği tapınağın içine, beşi sağda, beşi solda olmak üzere yerleştirdi."));
trsuperobject.biblemap.put("b14_4_8", new trsuperobject(14,4,8,"Yaptırdığı on masanın beşini de tapınağın sağına, beşini soluna yerleştirdi. Ayrıca yüz altın çanak yaptırdı."));
trsuperobject.biblemap.put("b14_4_9", new trsuperobject(14,4,9,"Kâhinlerin avlusunu, büyük avluyla kapılarını yaptırdı. Kapıları tunçla kaplattı."));
