trsuperobject.biblemap.put("b14_4_11", new trsuperobject(14,4,11,"Hiram kovalar, kürekler, çanaklar yaptı. Böylece Kral Süleyman için üstlenmiş olduğu Tanrının Tapınağıyla ilgili işleri tamamlamış oldu:"));
trsuperobject.biblemap.put("b14_4_12", new trsuperobject(14,4,12,"İki sütun ve iki yuvarlak sütun başlığı, bu başlıkları süsleyen iki örgülü ağ,"));
trsuperobject.biblemap.put("b14_4_13", new trsuperobject(14,4,13,"Sütunların yuvarlak başlıklarını süsleyen iki örgülü ağın üzerini ikişer sıra halinde süsleyen dört yüz nar motifi,"));
trsuperobject.biblemap.put("b14_4_14", new trsuperobject(14,4,14,"On kazan ve ayaklıkları,"));
trsuperobject.biblemap.put("b14_4_15", new trsuperobject(14,4,15,"Havuz ve havuzu taşıyan on iki boğa heykeli,"));
trsuperobject.biblemap.put("b14_4_16", new trsuperobject(14,4,16,"Kovalar, kürekler, büyük çatallar. Huram-Avinin Kral Süleyman için RABbin Tapınağına yaptığı bütün eşyalar parlak tunçtandı."));
trsuperobject.biblemap.put("b14_4_17", new trsuperobject(14,4,17,"Kral bunları Şeria Ovasında, Sukkot ile Seredata arasındaki killi topraklarda döktürmüştü."));
trsuperobject.biblemap.put("b14_4_18", new trsuperobject(14,4,18,"Süleymanın yaptırdığı eşyalar o kadar çoktu ki, kullanılan tuncun hesabı tutulmadı."));
trsuperobject.biblemap.put("b14_4_19", new trsuperobject(14,4,19,"Süleymanın Tanrının Tapınağı için yaptırdığı altın eşyalar şunlardı: Altın sunak ve ekmeklerin Tanrının huzuruna konduğu masalar,"));
trsuperobject.biblemap.put("b14_4_20", new trsuperobject(14,4,20,"İç odanın önüne yerleştirilen ve kurala uygun olarak yakılan saf altından kandilliklerle kandilleri,"));
trsuperobject.biblemap.put("b14_4_21", new trsuperobject(14,4,21,"Çiçek süslemeleri, kandiller, maşalar. -Bunlar saf altındandı.-"));
trsuperobject.biblemap.put("b14_4_22", new trsuperobject(14,4,22,"Saf altın fitil maşaları, çanaklar, tabaklar, buhurdanlar ve tapınağın altın kapıları. En Kutsal Yer'in ve ana bölümün kapıları da altındandı."));
trsuperobject.biblemap.put("b14_5_2", new trsuperobject(14,5,2,"Süleyman RABbin Antlaşma Sandığını Davut Kenti olan Siyondan getirmek üzere İsrail halkının ileri gelenleriyle bütün oymak ve boy başlarını Yeruşalime çağırdı."));
