trsuperobject.biblemap.put("b14_5_4", new trsuperobject(14,5,4,"İsrailin bütün ileri gelenleri toplanınca, Levililer Antlaşma Sandığını yerden kaldırdılar."));
trsuperobject.biblemap.put("b14_5_5", new trsuperobject(14,5,5,"Sandığı, Buluşma Çadırını ve çadırdaki bütün kutsal eşyaları Levili kâhinler tapınağa taşıdılar."));
trsuperobject.biblemap.put("b14_5_6", new trsuperobject(14,5,6,"Kral Süleyman ve bütün İsrail topluluğu Antlaşma Sandığının önünde sayısız davar ve sığır kurban etti."));
trsuperobject.biblemap.put("b14_5_7", new trsuperobject(14,5,7,"Kâhinler RABbin Antlaşma Sandığını tapınağın iç odasına, En Kutsal Yere taşıyıp Keruvların kanatlarının altına yerleştirdiler."));
trsuperobject.biblemap.put("b14_5_8", new trsuperobject(14,5,8,"Keruvların kanatları sandığın konduğu yerin üstüne kadar uzanıyor ve sandığı da, sırıklarını da örtüyordu."));
trsuperobject.biblemap.put("b14_5_9", new trsuperobject(14,5,9,"Sırıklar öyle uzundu ki, uçları iç odanın önünden görünüyordu. Ancak dışarıdan görünmüyordu. Bunlar hâlâ oradadır."));
trsuperobject.biblemap.put("b14_5_10", new trsuperobject(14,5,10,"Sandığın içinde Musanın Horev Dağında koyduğu iki levhadan başka bir şey yoktu. Bunlar Mısırdan çıkışlarında RABbin İsraillilerle yaptığı antlaşmanın levhalarıydı."));
trsuperobject.biblemap.put("b14_5_11", new trsuperobject(14,5,11,"Kâhinler Kutsal Yerden çıktılar. Orada bulunan kâhinlerin hepsi, bölüklerinin sırasını beklemeden, kendilerini kutsamışlardı."));
trsuperobject.biblemap.put("b14_5_12", new trsuperobject(14,5,12,"Bütün Levili ezgiciler -Asaf, Heman, Yedutun, oğullarıyla kardeşleri- zillerle, çenk ve lirlerle, ince keten kuşanmış olarak sunağın doğusunda yerlerini almışlardı. Borazan çalan yüz yirmi kâhin onlara eşlik ediyordu."));
trsuperobject.biblemap.put("b14_5_13", new trsuperobject(14,5,13,"Borazan çalanlarla ezgiciler tek ses halinde RABbe şükredip övgüler sunmaya başladılar. Borazan, zil ve çalgıların eşliğinde seslerini yükselterek RABbi şöyle övdüler:  ‹‹RAB iyidir;  Sevgisi sonsuza dek kalıcıdır.›› O anda RABbin Tapınağını bir bulut doldurdu."));
