trsuperobject.biblemap.put("b14_6_1", new trsuperobject(14,6,1,"O zaman Süleyman şöyle dedi: ‹‹Ya RAB, karanlık bulutlarda otururum demiştin."));
trsuperobject.biblemap.put("b14_6_2", new trsuperobject(14,6,2,"Senin için görkemli bir tapınak, sonsuza dek yaşayacağın bir konut yaptım.››"));
trsuperobject.biblemap.put("b14_6_3", new trsuperobject(14,6,3,"Kral ayakta duran bütün İsrail topluluğuna dönerek onları kutsadıktan sonra"));
trsuperobject.biblemap.put("b14_6_4", new trsuperobject(14,6,4,"şöyle dedi: ‹‹Babam Davuta verdiği sözü tutan İsrailin Tanrısı RABbe övgüler olsun! RAB demişti ki,"));
trsuperobject.biblemap.put("b14_6_5", new trsuperobject(14,6,5,"‹Halkımı Mısırdan çıkardığım günden bu yana, içinde bulunacağım bir tapınak yaptırmak için İsrail oymaklarına ait kentlerden hiçbirini seçmedim. İçlerinden halkım İsraili yönetecek birini de seçmedim."));
trsuperobject.biblemap.put("b14_6_6", new trsuperobject(14,6,6,"Ancak adımın içinde bulunacağı yer olarak Yeruşalimi, halkım İsraili yönetmesi için Davutu seçtim.› Benzer ifadeler 6:6,20de de geçer."));
trsuperobject.biblemap.put("b14_6_7", new trsuperobject(14,6,7,"‹‹Babam Davut İsrailin Tanrısı RABbin adına bir tapınak yapmayı yürekten istiyordu."));
trsuperobject.biblemap.put("b14_6_8", new trsuperobject(14,6,8,"Ama RAB, babam Davuta, ‹Adıma bir tapınak yapmayı yürekten istemen iyi bir şey› dedi,"));
trsuperobject.biblemap.put("b14_6_9", new trsuperobject(14,6,9,"‹Ne var ki, adıma yapılacak bu tapınağı sen değil, öz oğlun yapacak.›"));
trsuperobject.biblemap.put("b14_6_10", new trsuperobject(14,6,10,"‹‹RAB verdiği sözü yerine getirdi. RABbin sözü uyarınca, babam Davuttan sonra İsrail tahtına ben geçtim ve İsrailin Tanrısı RABbin adına tapınağı ben yaptırdım."));
trsuperobject.biblemap.put("b14_6_11", new trsuperobject(14,6,11,"Ayrıca RABbin İsrail halkıyla yaptığı antlaşmanın içinde korunduğu sandığı oraya yerleştirdim.››"));
trsuperobject.biblemap.put("b14_6_12", new trsuperobject(14,6,12,"Süleyman RABbin sunağının önünde, İsrail topluluğunun karşısında durup ellerini göklere açtı."));
trsuperobject.biblemap.put("b14_6_13", new trsuperobject(14,6,13,"Beş arşın uzunluğunda, beş arşın eninde, üç arşın yüksekliğinde tunç bir kürsü yaptırıp avlunun ortasına kurdurmuştu. Bu kürsünün üstünde durdu, İsrail topluluğunun önünde diz çöküp ellerini göklere açtı."));
trsuperobject.biblemap.put("b14_6_14", new trsuperobject(14,6,14,"‹‹Ya RAB, İsrailin Tanrısı, yerde ve gökte sana benzer başka tanrı yoktur›› dedi, ‹‹Bütün yürekleriyle yolunu izleyen kullarınla yaptığın antlaşmaya bağlı kalırsın."));
trsuperobject.biblemap.put("b14_6_15", new trsuperobject(14,6,15,"Ağzınla kulun babam Davuta verdiğin sözü bugün ellerinle yerine getirdin."));
trsuperobject.biblemap.put("b14_6_16", new trsuperobject(14,6,16,"‹‹Şimdi, ya RAB, İsrailin Tanrısı, kulun babam Davuta verdiğin öbür sözü de tutmanı istiyorum. Ona, ‹Senin soyundan İsrail tahtına oturacakların ardı arkası kesilmeyecektir; yeter ki, çocukların yasam uyarınca önümde senin gibi dikkatle yürüsünler› demiştin."));
trsuperobject.biblemap.put("b14_6_17", new trsuperobject(14,6,17,"Ya RAB, İsrailin Tanrısı, şimdi kulun Davuta verdiğin sözü yerine getirmeni istiyorum."));
trsuperobject.biblemap.put("b14_6_18", new trsuperobject(14,6,18,"‹‹Tanrı gerçekten yeryüzünde, insanlar arasında yaşar mı? Sen göklere, göklerin göklerine bile sığmazsın. Benim yaptığım bu tapınak ne ki!"));
trsuperobject.biblemap.put("b14_6_19", new trsuperobject(14,6,19,"Ya RAB Tanrım, kulunun ettiği duayı, yalvarışı işit; duasına ve yakarışına kulak ver."));
trsuperobject.biblemap.put("b14_6_20", new trsuperobject(14,6,20,"Gözlerin gece gündüz, ‹Adımı oraya yerleştireceğim!› dediğin bu tapınağın üzerinde olsun. Kulunun buraya yönelerek ettiği duayı işit."));
trsuperobject.biblemap.put("b14_6_21", new trsuperobject(14,6,21,"Buraya yönelerek dua eden kulunun ve halkın İsrailin yakarışını işit. Göklerden, oturduğun yerden kulak ver; duyunca bağışla."));
trsuperobject.biblemap.put("b14_6_22", new trsuperobject(14,6,22,"‹‹Biri komşusuna karşı günah işleyip ant içmek zorunda kaldığında, gelip bu tapınakta, senin sunağının önünde ant içerse,"));
trsuperobject.biblemap.put("b14_6_23", new trsuperobject(14,6,23,"göklerden kulak ver ve gereğini yap. Suçluya karşılığını vererek, suçsuzu haklı çıkararak kullarını yargıla."));
trsuperobject.biblemap.put("b14_6_24", new trsuperobject(14,6,24,"‹‹Sana karşı günah işlediği için düşmanlarına yenik düşen halkın İsrail yine sana döner, adını anar, bu tapınakta dua edip yakararak önüne çıkarsa,"));
trsuperobject.biblemap.put("b14_6_25", new trsuperobject(14,6,25,"göklerden kulak ver, halkın İsrailin günahını bağışla. Onları kendilerine ve atalarına verdiğin ülkeye yine kavuştur."));
trsuperobject.biblemap.put("b14_6_26", new trsuperobject(14,6,26,"‹‹Halkın sana karşı günah işlediği için gökler kapanıp yağmur yağmazsa, sıkıntıya düşen halkın buraya yönelip dua eder, adını anar ve günahlarından dönerse,"));
trsuperobject.biblemap.put("b14_6_27", new trsuperobject(14,6,27,"göklerden kulak ver; kullarının, halkın İsrailin günahlarını bağışla. Onlara doğru yolda yürümeyi öğret, halkına mülk olarak verdiğin ülkene yağmurlarını gönder."));
trsuperobject.biblemap.put("b14_6_28", new trsuperobject(14,6,28,"‹‹Ülkeyi kıtlık, salgın hastalık, samyeli, küf, tırtıl ya da çekirgeler kavurduğunda, düşmanlar kentlerden birinde halkını kuşattığında, herhangi bir felaket ya da hastalık ortalığı sardığında,"));
trsuperobject.biblemap.put("b14_6_29", new trsuperobject(14,6,29,"halkından bir kişi ya da bütün halkın İsrail başına gelen felaketi, acıyı kavrar, dua edip yakararak ellerini bu tapınağa doğru açarsa,"));
trsuperobject.biblemap.put("b14_6_30", new trsuperobject(14,6,30,"göklerden, oturduğun yerden kulak ver ve bağışla. İnsanların yüreklerini yalnızca sen bilirsin. Onlara yaptıklarına göre davran ki,"));
trsuperobject.biblemap.put("b14_6_31", new trsuperobject(14,6,31,"atalarımıza verdiğin bu ülkede yaşadıkları sürece senden korksunlar ve senin yolunda yürüsünler."));
trsuperobject.biblemap.put("b14_6_32", new trsuperobject(14,6,32,"‹‹Halkın İsrailden olmayan, ama senin yüce adını, gücünü, kudretini duyup uzak ülkelerden gelen yabancılar bu tapınağa gelip dua ederlerse,"));
trsuperobject.biblemap.put("b14_6_33", new trsuperobject(14,6,33,"göklerden, oturduğun yerden kulak ver, yalvarışlarını yanıtla. Öyle ki, dünyanın bütün ulusları, halkın İsrail gibi, adını bilsin, senden korksun ve yaptırdığım bu tapınağın sana ait olduğunu öğrensin. çağrıldığını.››"));
trsuperobject.biblemap.put("b14_6_34", new trsuperobject(14,6,34,"‹‹Halkın, düşmanlarına karşı gösterdiğin yoldan savaşa giderken sana, seçtiğin bu kente ve adına yaptırdığım bu tapınağa yönelip dua ederse,"));
trsuperobject.biblemap.put("b14_6_35", new trsuperobject(14,6,35,"dualarına, yakarışlarına göklerden kulak ver ve onları kurtar."));
trsuperobject.biblemap.put("b14_6_36", new trsuperobject(14,6,36,"‹‹Sana karşı günah işlediklerinde -günah işlemeyen tek kişi yoktur- öfkelenip onları yakın ya da uzak bir ülkeye tutsak olarak götürecek düşmanlarının eline teslim edersen,"));
trsuperobject.biblemap.put("b14_6_37", new trsuperobject(14,6,37,"onlar da tutsak oldukları ülkede pişmanlık duyup günahlarından döner, ‹Günah işledik, yoldan sapıp kötülük yaptık› diyerek sana yakarırlarsa,"));
trsuperobject.biblemap.put("b14_6_38", new trsuperobject(14,6,38,"tutsak oldukları ülkede candan ve yürekten sana dönerlerse, atalarına verdiğin ülkelerine, seçtiğin kente ve adına yaptırdığım tapınağa yönelip dua ederlerse,"));
trsuperobject.biblemap.put("b14_6_39", new trsuperobject(14,6,39,"göklerden, oturduğun yerden dualarına, yakarışlarına kulak ver, onları kurtar. Sana karşı günah işlemiş olan halkını bağışla."));
trsuperobject.biblemap.put("b14_6_40", new trsuperobject(14,6,40,"‹‹Şimdi, ey Tanrım, bizi gör ve burada edilen duaya kulak ver."));
trsuperobject.biblemap.put("b14_6_41", new trsuperobject(14,6,41,"‹‹Çık, ya RAB Tanrı, yaşayacağın yere,Gücünü simgeleyen Sandıkla birlikte.Ya RAB Tanrı, kâhinlerin kurtuluşu kuşansın,Sadık kulların iyiliklerinle sevinsinler."));
trsuperobject.biblemap.put("b14_6_42", new trsuperobject(14,6,42,"Ya RAB Tanrı, meshettiğin krala yüz çevirme.Kulun Davut'a yaptığın iyilikleri anımsa.›› Davut'un yaptığı iyilikleri anımsa››."));
trsuperobject.biblemap.put("b14_7_1", new trsuperobject(14,7,1,"Süleyman duasını bitirince, gökten ateş yağdı; yakmalık sunularla kurbanları yiyip bitirdi. RABbin görkemi tapınağı doldurdu."));
trsuperobject.biblemap.put("b14_7_3", new trsuperobject(14,7,3,"Gökten yağan ateşi ve tapınağın üzerindeki RABbin görkemini gören İsrailliler avluda yüzüstü yere kapandılar; RABbe tapınarak Onu övdüler:  ‹‹RAB iyidir;Sevgisi sonsuza dek kalıcıdır.››"));
trsuperobject.biblemap.put("b14_7_4", new trsuperobject(14,7,4,"Kral ve bütün halk RABbin önünde kurban kestiler."));
trsuperobject.biblemap.put("b14_7_5", new trsuperobject(14,7,5,"Kral Süleyman yirmi iki bin sığır, yüz yirmi bin davar kurban etti. Böylece kral ve halk Tanrının Tapınağını adamış oldular."));
trsuperobject.biblemap.put("b14_7_6", new trsuperobject(14,7,6,"Kâhinler yerlerini almışlardı. Kral Davutun RABbi övmek için yaptırdığı ve ‹‹RABbin sevgisi sonsuza dek kalıcıdır›› diyerek överken kullandığı çalgıları alan Levililer de yerlerini almıştı. Levililerin karşısında duran kâhinler borazanlarını çalıyorlardı. Bu sırada bütün İsrailliler ayakta duruyordu."));
trsuperobject.biblemap.put("b14_7_7", new trsuperobject(14,7,7,"Süleyman RABbin Tapınağının önündeki avlunun orta kısmını kutsadı. Yakmalık sunularla esenlik sunularının yağlı parçalarını orada sundu. Çünkü yaptırdığı tunç sunak yakmalık sunuları, tahıl sunularını ve yağlı parçaları almadı."));
trsuperobject.biblemap.put("b14_7_8", new trsuperobject(14,7,8,"Süleyman, Levo-Hamattan Mısır Vadisine kadar her yerden gelen İsraillilerin oluşturduğu çok büyük bir toplulukla birlikte bayramı yedi gün kutladı."));
trsuperobject.biblemap.put("b14_7_9", new trsuperobject(14,7,9,"Sekizinci gün kutsal bir toplantı yaptılar. Sunağı adamaya yedi gün, bayramı kutlamaya da yedi gün ayırdılar."));
trsuperobject.biblemap.put("b14_7_10", new trsuperobject(14,7,10,"Kral yedinci ayın yirmi üçüncü günü halkı evlerine gönderdi. RABbin, Davut, Süleyman ve halkı İsrail için yapmış olduğu iyilikten dolayı hepsi mutluydu, sevinçle coşuyordu."));
trsuperobject.biblemap.put("b14_7_11", new trsuperobject(14,7,11,"Süleyman RABbin Tapınağını, sarayı ve RABbin Tapınağıyla kendi sarayında yapmayı istediği bütün işleri başarıyla bitirince,"));
trsuperobject.biblemap.put("b14_7_12", new trsuperobject(14,7,12,"RAB geceleyin ona görünerek şöyle dedi: ‹‹Duanı duydum. Burayı kendime kurban sunulan tapınak olarak seçtim."));
trsuperobject.biblemap.put("b14_7_13", new trsuperobject(14,7,13,"‹‹Yağmur yağmasın diye göğü kapadığımda, toprağın ürününü yiyip bitirmesi için çekirgelere buyruk verdiğimde ya da halkımın arasına salgın hastalık gönderdiğimde,"));
trsuperobject.biblemap.put("b14_7_14", new trsuperobject(14,7,14,"adımla çağrılan halkım alçakgönüllülüğü takınır, bana yönelip dua eder, kötü yollarından dönerse, gökten onları duyacağım, günahlarını bağışlayıp ülkelerini sağlığa kavuşturacağım."));
trsuperobject.biblemap.put("b14_7_15", new trsuperobject(14,7,15,"Gözlerim burada edilen duaya açık, kulaklarım işitici olacak."));
trsuperobject.biblemap.put("b14_7_16", new trsuperobject(14,7,16,"Adım sürekli orada bulunsun diye bu tapınağı seçip kutsal kıldım. Gözlerim onun üstünde, yüreğim her zaman orada olacaktır."));
trsuperobject.biblemap.put("b14_7_17", new trsuperobject(14,7,17,"Sana gelince, baban Davutun yaptığı gibi yollarımı izler, buyurduğum her şeyi yapar, kurallarıma ve ilkelerime uyarsan,"));
trsuperobject.biblemap.put("b14_7_18", new trsuperobject(14,7,18,"baban Davutla, ‹İsrail tahtından senin soyunun ardı arkası kesilmeyecektir› diye yaptığım antlaşmaya bağlı kalıp krallığını pekiştireceğim."));
trsuperobject.biblemap.put("b14_7_19", new trsuperobject(14,7,19,"‹‹Ama siz yollarımdan sapar, kurallarımı, buyruklarımı bırakır, gidip başka ilahlara kulluk eder, taparsanız,"));
trsuperobject.biblemap.put("b14_7_20", new trsuperobject(14,7,20,"size verdiğim ülkeden sizi söküp atacağım, adıma kutsal kıldığım bu tapınağı terk edeceğim; burayı bütün ulusların aşağılayıp alay ettiği bir yer durumuna getireceğim."));
trsuperobject.biblemap.put("b14_7_21", new trsuperobject(14,7,21,"Bu gösterişli tapınağın önünden geçenler hayretle, ‹RAB bu ülkeyi ve tapınağı neden bu duruma getirdi?› diye soracaklar."));
trsuperobject.biblemap.put("b14_7_22", new trsuperobject(14,7,22,"Ve diyecekler ki, ‹İsrail halkı, atalarını Mısır'dan çıkaran Tanrıları RAB'bi terk etti; başka ilahların ardından gitti, onlara tapıp kulluk etti. RAB bu yüzden bu kötülükleri başlarına getirdi.››"));
trsuperobject.biblemap.put("b14_8_1", new trsuperobject(14,8,1,"Süleyman RABbin Tapınağıyla kendi sarayını yirmi yılda bitirdi."));
trsuperobject.biblemap.put("b14_8_2", new trsuperobject(14,8,2,"Sonra Hiramın kendisine verdiği kentleri onarıp İsraillilerin buralara yerleşmesini sağladı."));
trsuperobject.biblemap.put("b14_8_3", new trsuperobject(14,8,3,"Ardından Hamat-Sova üzerine yürüyerek orayı ele geçirdi."));
trsuperobject.biblemap.put("b14_8_4", new trsuperobject(14,8,4,"Çölde Tadmor Kentini onardı. Hama yöresinde yaptırdığı bütün ambarlı kentlerin yapımını bitirdi."));
trsuperobject.biblemap.put("b14_8_5", new trsuperobject(14,8,5,"Yukarı ve Aşağı Beythoronu yeniden kurup çevrelerini surlarla, sürgülü kapılarla sağlamlaştırdı."));
trsuperobject.biblemap.put("b14_8_6", new trsuperobject(14,8,6,"Baalatı ve yönetimindeki bütün ambarlı kentleri, savaş arabalarıyla atların bulunduğu kentleri de onarıp güçlendirdi. Böylece Yeruşalimde, Lübnanda, yönetimi altındaki bütün topraklarda her istediğini yaptırmış oldu."));
trsuperobject.biblemap.put("b14_8_7", new trsuperobject(14,8,7,"İsrail halkından olmayan Hititler, Amorlular, Perizliler, Hivliler ve Yevuslulardan artakalanlara gelince,"));
trsuperobject.biblemap.put("b14_8_8", new trsuperobject(14,8,8,"Süleyman İsrail halkının yok etmediği bu insanların soyundan gelip ülkede kalanları angaryaya koştu. Bu durum bugün de sürüyor."));
trsuperobject.biblemap.put("b14_8_9", new trsuperobject(14,8,9,"Ancak İsrail halkından hiç kimseye kölelik yaptırmadı. Onlar savaşçı, birlik komutanı, savaş arabalarıyla atlıların komutanı olarak görev yaptılar."));
trsuperobject.biblemap.put("b14_8_10", new trsuperobject(14,8,10,"Kral Süleyman adına halkı denetleyen iki yüz elli görevli de İsrail halkındandı."));
trsuperobject.biblemap.put("b14_8_11", new trsuperobject(14,8,11,"Süleyman, ‹‹Karım İsrail Kralı Davutun sarayında kalmamalı. Çünkü RABbin Antlaşma Sandığının girdiği yerler kutsaldır›› diyerek firavunun kızını Davut Kentinden kendisi için yaptırdığı saraya getirtti."));
trsuperobject.biblemap.put("b14_8_12", new trsuperobject(14,8,12,"Tapınağın eyvanının önünde, yaptırdığı sunakta RABbe yakmalık sunular sundu."));
trsuperobject.biblemap.put("b14_8_13", new trsuperobject(14,8,13,"Şabat Günü, Yeni Ay, yılın üç bayramı -Mayasız Ekmek, Haftalar ve Çardak bayramları- için Musanın buyurduğu sunuları günü gününe sundu."));
trsuperobject.biblemap.put("b14_8_14", new trsuperobject(14,8,14,"Babası Davutun koyduğu kural uyarınca, kâhin bölüklerine ayrı ayrı görevler verdi. Levilileri Tanrıyı övme ve her günün gerektirdiği işlerde kâhinlere yardım etme görevine atadı. Kapı nöbetçilerini de bölüklerine göre değişik kapılarda görevlendirdi. Çünkü Tanrı adamı Davut böyle buyurmuştu."));
trsuperobject.biblemap.put("b14_8_15", new trsuperobject(14,8,15,"Bunlar, hazine odalarına ilişkin konular dahil, hiçbir konuda kralın kâhinlerle Levililere verdiği buyruklardan ayrılmadılar."));
trsuperobject.biblemap.put("b14_8_16", new trsuperobject(14,8,16,"RABbin Tapınağının temelinin atıldığı günden bitimine dek Süleymanın yapmak istediği her iş yerine getirildi. Böylece RABbin Tapınağının yapımı tamamlanmış oldu."));
trsuperobject.biblemap.put("b14_8_17", new trsuperobject(14,8,17,"Bundan sonra Süleyman Esyon-Gevere, Edom kıyısındaki Eylata gitti."));
trsuperobject.biblemap.put("b14_8_18", new trsuperobject(14,8,18,"Hiram ona denizi bilen gemiciler ve kendi görevlileri aracılığıyla gemiler gönderdi. Kral Süleyman'ın adamlarıyla birlikte Ofir'e giden bu gemiciler, dört yüz elli talantfç altın getirdiler."));
trsuperobject.biblemap.put("b14_9_1", new trsuperobject(14,9,1,"Saba Kraliçesi, Süleymanın ününü duyunca, onu çetin sorularla sınamak için Yeruşalime geldi. Çeşitli baharat, çok miktarda altın ve değerli taşlarla yüklü büyük bir kervan eşliğinde gelen kraliçe, aklından geçen her şeyi Süleymanla konuştu."));
trsuperobject.biblemap.put("b14_9_2", new trsuperobject(14,9,2,"Süleyman onun bütün sorularına karşılık verdi. Kralın ona yanıt bulmakta güçlük çektiği hiçbir konu olmadı."));
trsuperobject.biblemap.put("b14_9_3-4", new trsuperobject(14,9,3-4,"Süleymanın bilgeliğini, yaptırdığı sarayı, sofrasının zenginliğini, görevlilerinin oturup kalkışını, hizmetkârlarının ve sakilerinin özel giysileriyle yaptığı hizmeti, RABbin Tapınağında sunduğu yakmalık sunuları gören Saba Kraliçesi hayranlık içinde kaldı."));
trsuperobject.biblemap.put("b14_9_5", new trsuperobject(14,9,5,"Krala, ‹‹Ülkemdeyken, yaptıklarınla ve bilgeliğinle ilgili duyduklarım doğruymuş›› dedi,"));
trsuperobject.biblemap.put("b14_9_6", new trsuperobject(14,9,6,"‹‹Ama gelip kendi gözlerimle görünceye dek anlatılanlara inanmamıştım. Büyük bilgeliğinin yarısı bile bana anlatılmadı. Duyduklarımdan daha üstünsün."));
trsuperobject.biblemap.put("b14_9_7", new trsuperobject(14,9,7,"Ne mutlu adamlarına! Ne mutlu sana hizmet eden görevlilere! Çünkü sürekli bilgeliğine tanık oluyorlar."));
trsuperobject.biblemap.put("b14_9_8", new trsuperobject(14,9,8,"Senden hoşnut kalan, adına egemenlik sürmen için seni tahta oturtan Tanrın RABbe övgüler olsun! Tanrın İsraili sevdiği, sonsuza dek korumak istediği için, adaleti ve doğruluğu sağlaman için seni İsraile kral yaptı.››"));
trsuperobject.biblemap.put("b14_9_9", new trsuperobject(14,9,9,"Saba Kraliçesi krala 120 talant altın, çok büyük miktarda baharat ve değerli taşlar armağan etti. Krala armağan ettiği baharatın benzeri yoktu."));
trsuperobject.biblemap.put("b14_9_10", new trsuperobject(14,9,10,"Bu arada Hiramın adamlarıyla Süleymanın adamları Ofirden altın, algum kerestesiyle değerli taşlar getirdiler."));
trsuperobject.biblemap.put("b14_9_11", new trsuperobject(14,9,11,"Kral, RABbin Tapınağıyla sarayın basamaklarını, çalgıcıların lirleriyle çenklerini bu algum kerestesinden yaptırdı. Yahuda bölgesinde daha önce böylesi görülmemişti. ardıç ağacı olduğu sanılıyor."));
trsuperobject.biblemap.put("b14_9_12", new trsuperobject(14,9,12,"Kral Süleyman Saba Kraliçesinin her isteğini, her dileğini yerine getirdi. Kraliçenin kendisine getirdiklerinden daha fazlasını ona verdi. Bundan sonra kraliçe adamlarıyla birlikte oradan ayrılıp kendi ülkesine döndü."));
trsuperobject.biblemap.put("b14_9_13", new trsuperobject(14,9,13,"Süleymana bir yılda gelen altının miktarı 666 talantı buluyordu."));
trsuperobject.biblemap.put("b14_9_14", new trsuperobject(14,9,14,"Tüccarların ve alım satımla uğraşanların getirdiği altın bunun dışındaydı. Arabistanın bütün krallarıyla İsrail valileri de Süleymana altın, gümüş getiriyorlardı."));
trsuperobject.biblemap.put("b14_9_15", new trsuperobject(14,9,15,"Kral Süleyman dövme altından her biri altı yüz şekel ağırlığında iki yüz büyük kalkan yaptırdı."));
trsuperobject.biblemap.put("b14_9_16", new trsuperobject(14,9,16,"Ayrıca her biri üç yüz şekel ağırlığında dövme altından üç yüz küçük kalkan yaptırdı. Kral bu kalkanları Lübnan Ormanı adındaki saraya koydu."));
trsuperobject.biblemap.put("b14_9_17", new trsuperobject(14,9,17,"Kral fildişinden büyük bir taht yaptırıp saf altınla kaplattı."));
trsuperobject.biblemap.put("b14_9_18", new trsuperobject(14,9,18,"Tahtın altı basamağı, bir de altın ayak taburesi vardı. Bunlar tahta bağlıydı. Oturulan yerin iki yanında kollar, her kolun yanında birer aslan heykeli bulunuyordu."));
trsuperobject.biblemap.put("b14_9_19", new trsuperobject(14,9,19,"Altı basamağın iki yanında on iki aslan heykeli vardı. Hiçbir krallıkta böylesi yapılmamıştı."));
trsuperobject.biblemap.put("b14_9_20", new trsuperobject(14,9,20,"Kral Süleymanın kadehleriyle Lübnan Ormanı adındaki sarayın bütün eşyaları saf altından yapılmış, hiç gümüş kullanılmamıştı. Çünkü Süleymanın döneminde gümüşün değeri yoktu."));
trsuperobject.biblemap.put("b14_9_21", new trsuperobject(14,9,21,"Kralın gemileri Hiramın adamlarının yönetiminde Tarşişefı giderdi. Bu gemiler üç yılda bir altın, gümüş, fildişi ve türlü maymunlarla yüklü olarak dönerlerdi. tavuskuşlarıyla››."));
trsuperobject.biblemap.put("b14_9_22", new trsuperobject(14,9,22,"Kral Süleyman dünyanın bütün krallarından daha zengin, daha bilgeydi."));
trsuperobject.biblemap.put("b14_9_23", new trsuperobject(14,9,23,"Tanrının Süleymana verdiği bilgeliği dinlemek için dünyanın bütün kralları onu görmek isterlerdi."));
trsuperobject.biblemap.put("b14_9_24", new trsuperobject(14,9,24,"Onu görmeye gelenler her yıl armağan olarak altın ve gümüş eşya, giysi, silah, baharat, at, katır getirirlerdi."));
trsuperobject.biblemap.put("b14_9_25", new trsuperobject(14,9,25,"Süleymanın atlarla savaş arabaları için dört bin ahırı, on iki bin atlısı vardı. Bunların bir kısmını savaş arabaları için ayrılan kentlere, bir kısmını da kendi yanına, Yeruşalime yerleştirdi."));
trsuperobject.biblemap.put("b14_9_26", new trsuperobject(14,9,26,"Fırat Irmağından Filist bölgesine, oradan da Mısır sınırına dek uzanan bölgedeki bütün krallara egemendi."));
trsuperobject.biblemap.put("b14_9_27", new trsuperobject(14,9,27,"Onun krallığı döneminde Yeruşalimde gümüş taş değerine düştü. Sedir ağaçları Şefeladaki yabanıl incir ağaçları kadar bollaştı."));
trsuperobject.biblemap.put("b14_9_28", new trsuperobject(14,9,28,"Süleymanın atları Mısırdan ve bütün öbür ülkelerden getirilirdi."));
trsuperobject.biblemap.put("b14_9_29", new trsuperobject(14,9,29,"Süleymanın yaptığı öbür işler, başından sonuna dek, Peygamber Natanın tarihinde, Şilolu Ahiyanın peygamberlik yazılarında ve Bilici İddonun Nevat oğlu Yarovama ilişkin görümlerinde yazılıdır."));
trsuperobject.biblemap.put("b14_9_30", new trsuperobject(14,9,30,"Süleyman kırk yıl süreyle bütün İsraili Yeruşalimden yönetti."));
trsuperobject.biblemap.put("b14_9_31", new trsuperobject(14,9,31,"Süleyman ölüp atalarına kavuşunca babası Davut'un Kenti'nde gömüldü. Yerine oğlu Rehavam kral oldu."));
trsuperobject.biblemap.put("b14_10_1", new trsuperobject(14,10,1,"Rehavam Şekeme gitti. Çünkü bütün İsrailliler kendisini kral ilan etmek için orada toplanmışlardı."));
trsuperobject.biblemap.put("b14_10_2", new trsuperobject(14,10,2,"Kral Süleymandan kaçıp Mısıra yerleşen Nevat oğlu Yarovam bunu duyunca Mısırdan döndü."));
trsuperobject.biblemap.put("b14_10_3", new trsuperobject(14,10,3,"İsrailliler Yarovamı çağırttılar. Birlikte gidip Rehavama şöyle dediler:"));
trsuperobject.biblemap.put("b14_10_4", new trsuperobject(14,10,4,"‹‹Baban üzerimize ağır bir boyunduruk koydu. Ama babanın üzerimize yüklediği ağır yükü ve boyunduruğu hafifletirsen sana kul köle oluruz.››"));
trsuperobject.biblemap.put("b14_10_5", new trsuperobject(14,10,5,"Rehavam, ‹‹Üç gün sonra yine gelin›› yanıtını verince halk yanından ayrıldı."));
trsuperobject.biblemap.put("b14_10_6", new trsuperobject(14,10,6,"Kral Rehavam, babası Süleymana sağlığında danışmanlık yapan ileri gelenlere, ‹‹Bu halka nasıl yanıt vermemi öğütlersiniz?›› diye sordu."));
trsuperobject.biblemap.put("b14_10_7", new trsuperobject(14,10,7,"İleri gelenler, ‹‹Halka iyi davranır, onları hoşnut eder, olumlu yanıt verirsen, sana her zaman kul köle olurlar›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b14_10_8", new trsuperobject(14,10,8,"Ne var ki, Rehavam ileri gelenlerin öğüdünü reddederek birlikte büyüdüğü genç görevlilerine danıştı:"));
trsuperobject.biblemap.put("b14_10_9", new trsuperobject(14,10,9,"‹‹Siz ne yapmamı öğütlersiniz? ‹Babanın üzerimize koyduğu boyunduruğu hafiflet› diyen bu halka nasıl bir yanıt verelim?››"));
trsuperobject.biblemap.put("b14_10_10", new trsuperobject(14,10,10,"Birlikte büyüdüğü gençler ona şu karşılığı verdiler: ‹‹Sana, ‹Babanın üzerimize koyduğu boyunduruğu hafiflet› diyen halka de ki, ‹Benim küçük parmağım, babamın belinden daha kalındır."));
trsuperobject.biblemap.put("b14_10_11", new trsuperobject(14,10,11,"Babam size ağır bir boyunduruk yüklediyse, ben boyunduruğunuzu daha da ağırlaştıracağım. Babam sizi kırbaçla yola getirdiyse, ben sizi akreplerle yola getireceğim.››"));
trsuperobject.biblemap.put("b14_10_12", new trsuperobject(14,10,12,"Yarovamla bütün halk, kralın, ‹‹Üç gün sonra yine gelin›› sözü üzerine, üçüncü gün Rehavamın yanına geldiler."));
trsuperobject.biblemap.put("b14_10_13-14", new trsuperobject(14,10,13-14,"İleri gelenlerin öğüdünü reddeden Kral Rehavam, gençlerin öğüdüne uyarak halka sert bir yanıt verdi: ‹‹Babamın size yüklediği boyunduruğu ben daha da ağırlaştıracağım. Babam sizi kırbaçla yola getirdiyse, ben sizi akreplerle yola getireceğim.››"));
trsuperobject.biblemap.put("b14_10_15", new trsuperobject(14,10,15,"Kral halkı dinlemedi. Bu Tanrıdandı. Çünkü Şilolu Ahiya aracılığıyla Nevat oğlu Yarovama verdiği sözü yerine getirmek için RAB bu olayı düzenlemişti."));
trsuperobject.biblemap.put("b14_10_16", new trsuperobject(14,10,16,"Kralın kendilerini dinlemediğini görünce, bütün İsrailliler,  ‹‹İşay oğlu Davutla ne ilgimiz,Ne de payımız var!›› diye bağırdılar,‹‹Ey İsrail halkı, haydi evimize dönelim!Davutun soyu başının çaresine baksın.››"));
trsuperobject.biblemap.put("b14_10_17", new trsuperobject(14,10,17,"Rehavam da yalnızca Yahuda kentlerinde yaşayan İsraillilere krallık yapmaya başladı."));
trsuperobject.biblemap.put("b14_10_18", new trsuperobject(14,10,18,"İsrailliler Kral Rehavamın gönderdiği angaryacıbaşı Hadoramı taşa tutup öldürdüler. Bunun üzerine Kral Rehavam savaş arabasına atlayıp Yeruşalime kaçtı."));
trsuperobject.biblemap.put("b14_10_19", new trsuperobject(14,10,19,"İsrail halkı, Davut soyundan gelenlere hep başkaldırdı."));
trsuperobject.biblemap.put("b14_11_1", new trsuperobject(14,11,1,"Rehavam Yeruşalime varınca, İsraillilerle savaşıp onları yeniden egemenliği altına almak amacıyla Yahuda ve Benyamin oymaklarından yüz seksen bin seçkin savaşçı topladı."));
trsuperobject.biblemap.put("b14_11_2", new trsuperobject(14,11,2,"Bu arada RAB, Tanrı adamı Şemayaya şöyle seslendi:"));
trsuperobject.biblemap.put("b14_11_3", new trsuperobject(14,11,3,"‹‹Süleyman oğlu Yahuda Kralı Rehavama, Yahuda ve Benyamin bölgesinde yaşayan bütün İsraillilere şunu söyle:"));
trsuperobject.biblemap.put("b14_11_4", new trsuperobject(14,11,4,"‹RAB diyor ki, İsrailli kardeşlerinize saldırmayın, onlarla savaşmayın. Herkes evine dönsün! Çünkü bu olayı ben düzenledim.›› RABbin bu sözlerini duyan halk Yarovama karşı savaşmaktan vazgeçti."));
trsuperobject.biblemap.put("b14_11_5", new trsuperobject(14,11,5,"Rehavam Yeruşalimde yaşadı ve savunma amacıyla Yahudadaki şu kentleri onardı:"));
trsuperobject.biblemap.put("b14_11_6", new trsuperobject(14,11,6,"Beytlehem, Etam, Tekoa,"));
trsuperobject.biblemap.put("b14_11_7", new trsuperobject(14,11,7,"Beytsur, Soko, Adullam,"));
trsuperobject.biblemap.put("b14_11_8", new trsuperobject(14,11,8,"Gat, Mareşa, Zif,"));
trsuperobject.biblemap.put("b14_11_9", new trsuperobject(14,11,9,"Adorayim, Lakiş, Azeka,"));
trsuperobject.biblemap.put("b14_11_10", new trsuperobject(14,11,10,"Sora, Ayalon, Hevron. Bunlar Yahuda ve Benyamin bölgesinde surlu kentlerdi."));
trsuperobject.biblemap.put("b14_11_11", new trsuperobject(14,11,11,"Rehavam bu kentlerin surlarını güçlendirerek buralara komutanlar atadı; yiyecek, zeytinyağı, şarap depoladı."));
trsuperobject.biblemap.put("b14_11_12", new trsuperobject(14,11,12,"Her kent için kalkanlar, mızraklar sağladı. Kentleri iyice güçlendirdi. Böylece Yahuda ve Benyamin bölgeleri onun denetimi altında kaldı."));
trsuperobject.biblemap.put("b14_11_13", new trsuperobject(14,11,13,"İsrailin her bölgesinden kâhinlerle Levililer Rehavamdan yana geçtiler."));
trsuperobject.biblemap.put("b14_11_14", new trsuperobject(14,11,14,"Levililer otlaklarını, mallarını bırakıp Yahuda ve Yeruşalime gelmişlerdi. Çünkü Yarovamla oğulları onları RABbin kâhinliğinden uzaklaştırmıştı."));
trsuperobject.biblemap.put("b14_11_15", new trsuperobject(14,11,15,"Yarovam tapınma yerleri ve yaptırdığı teke ve buzağı biçimindeki putlar için kâhinler atamıştı."));
trsuperobject.biblemap.put("b14_11_16", new trsuperobject(14,11,16,"İsrailin her oymağından RABbe, İsrailin Tanrısına yönelmeye yürekten kararlı olanlar ise, atalarının Tanrısı RABbe kurban sunmak için kâhinlerle Levililerin ardından Yeruşalime geldiler."));
trsuperobject.biblemap.put("b14_11_17", new trsuperobject(14,11,17,"Üç yıl Davutla Süleymanın izinde yürüyen bu kişiler, Süleyman oğlu Rehavamı destekleyerek Yahuda Krallığını güçlendirdiler."));
trsuperobject.biblemap.put("b14_11_18", new trsuperobject(14,11,18,"Rehavam Davut oğlu Yerimotun kızı Mahalatla evlendi. Mahalatın annesi Avihayil, İşay oğlu Eliavın kızıydı."));
trsuperobject.biblemap.put("b14_11_19", new trsuperobject(14,11,19,"Mahalat Rehavama şu oğulları doğurdu: Yeuş, Şemarya, Zaham."));
trsuperobject.biblemap.put("b14_11_20", new trsuperobject(14,11,20,"Rehavam Mahalattan sonra Avşalomun kızı Maaka ile evlendi. Maaka ona Aviyayı, Attayı, Zizayı, Şelomiti doğurdu."));
trsuperobject.biblemap.put("b14_11_21", new trsuperobject(14,11,21,"Rehavam Avşalomun kızı Maakayı öbür eşleriyle cariyelerinin hepsinden daha çok severdi. Rehavamın on sekiz karısı, altmış cariyesi vardı. Bunlardan yirmi sekiz erkek, altmış kız çocuğu oldu."));
trsuperobject.biblemap.put("b14_11_22", new trsuperobject(14,11,22,"Rehavam Maakadan doğan Aviyayı kral yapmak amacıyla onu kardeşleri arasında önder ve tahta geçecek aday atadı."));
trsuperobject.biblemap.put("b14_11_23", new trsuperobject(14,11,23,"Bilgece davranarak oğullarının bazılarını Yahuda ve Benyamin topraklarına, surlu kentlere dağıttı. Onlara bol yiyecek sağladı ve birçok kadınla evlendirdi."));
trsuperobject.biblemap.put("b14_12_1", new trsuperobject(14,12,1,"Rehavam krallığını pekiştirip güçlenince, İsrail halkıyla birlikte RABbin Yasasına sırt çevirdi."));
trsuperobject.biblemap.put("b14_12_2", new trsuperobject(14,12,2,"Rehavamın krallığının beşinci yılında Mısır Kralı Şişak Yeruşalime saldırdı. Çünkü Rehavamla halk RABbe ihanet etmişti."));
trsuperobject.biblemap.put("b14_12_3", new trsuperobject(14,12,3,"Şişakın bin iki yüz savaş arabası, altmış bin atlısı ve Mısırdan onunla birlikte gelen Luvlu, Suklu, Kûşlu sayısız askeri vardı."));
trsuperobject.biblemap.put("b14_12_4", new trsuperobject(14,12,4,"Şişak Yahudanın surlu kentlerini ele geçirerek Yeruşalime kadar geldi."));
trsuperobject.biblemap.put("b14_12_5", new trsuperobject(14,12,5,"Bu sırada Peygamber Şemaya, Rehavama ve Şişak yüzünden Yeruşalimde toplanan Yahuda önderlerine gelip şöyle dedi: ‹‹RAB, ‹Siz beni bıraktınız. Ben de sizi bırakıp Şişakın eline teslim ettim› diyor.››"));
trsuperobject.biblemap.put("b14_12_6", new trsuperobject(14,12,6,"İsrail önderleriyle kral alçakgönüllü bir tutum takınarak, ‹‹RAB adildir›› dediler."));
trsuperobject.biblemap.put("b14_12_8", new trsuperobject(14,12,8,"Ama onları Şişaka köle edeceğim. Öyle ki, bana hizmet etmekle öbür ulusların krallarına hizmet etmek arasındaki farkı anlayabilsinler.››"));
trsuperobject.biblemap.put("b14_12_9", new trsuperobject(14,12,9,"Mısır Kralı Şişak Yeruşalime saldırdığında, Süleymanın yaptırmış olduğu altın kalkanlar dahil RABbin Tapınağının ve sarayın bütün hazinelerini boşaltıp götürdü."));
trsuperobject.biblemap.put("b14_12_10", new trsuperobject(14,12,10,"Kral Rehavam bunların yerine tunç kalkanlar yaptırarak sarayın kapı muhafızlarının komutanlarına emanet etti."));
trsuperobject.biblemap.put("b14_12_11", new trsuperobject(14,12,11,"Kral RABbin Tapınağına her gittiğinde, muhafızlar bu kalkanları taşıyarak ona eşlik eder, sonra muhafız odasına götürürlerdi."));
trsuperobject.biblemap.put("b14_12_12", new trsuperobject(14,12,12,"Rehavamın alçakgönüllü bir tutum takınması üzerine RABbin öfkesi dindi, onu büsbütün yok etmekten vazgeçti. Yahudada bazı iyi davranışlar da vardı."));
trsuperobject.biblemap.put("b14_12_13", new trsuperobject(14,12,13,"Kral Rehavam Yeruşalimde krallığını pekiştirerek sürdürdü. Kral olduğunda kırk bir yaşındaydı. RABbin adını yerleştirmek için bütün İsrail oymaklarının yaşadığı kentler arasından seçtiği Yeruşalim Kentinde on yedi yıl krallık yaptı. Annesi Ammonlu Naamaydı."));
trsuperobject.biblemap.put("b14_12_14", new trsuperobject(14,12,14,"Rehavam RABbe yönelmeye yürekten kararlı olmadığı için kötülük yaptı."));
trsuperobject.biblemap.put("b14_12_15", new trsuperobject(14,12,15,"Rehavamın yaptığı işler, başından sonuna dek, Peygamber Şemaya ve Bilici İddonun soyla ilgili tarihinde yazılıdır. Rehavamla Yarovam arasında sürekli savaş vardı."));
trsuperobject.biblemap.put("b14_12_16", new trsuperobject(14,12,16,"Rehavam ölüp atalarına kavuşunca, Davut Kenti'nde gömüldü. Rehavam'ın yerine oğlu Aviya kral oldu."));
trsuperobject.biblemap.put("b14_13_1", new trsuperobject(14,13,1,"İsrail Kralı Yarovamın krallığının on sekizinci yılında Aviya Yahuda Kralı oldu."));
trsuperobject.biblemap.put("b14_13_2", new trsuperobject(14,13,2,"Yeruşalimde üç yıl krallık yaptı. Annesi Givalı Urielin kızı Mikayaydı. Aviyayla Yarovam arasında savaş vardı."));
trsuperobject.biblemap.put("b14_13_3", new trsuperobject(14,13,3,"Aviya seçme yiğit askerlerden oluşan dört yüz bin kişilik bir orduyla savaşa çıktı. Yarovam da sekiz yüz bin seçme yiğit savaşçıdan oluşan bir orduyla ona karşı savaş düzenine girdi."));
trsuperobject.biblemap.put("b14_13_4", new trsuperobject(14,13,4,"Aviya Efrayim dağlık bölgesindeki Semarayim Dağına çıkıp şöyle seslendi: ‹‹Ey Yarovam ve bütün İsrailliler, beni dinleyin!"));
trsuperobject.biblemap.put("b14_13_5", new trsuperobject(14,13,5,"İsrailin Tanrısı RABbin bozulmaz bir antlaşmayla İsrail Krallığını sonsuza dek Davuta ve soyuna verdiğini bilmiyor musunuz?"));
trsuperobject.biblemap.put("b14_13_6", new trsuperobject(14,13,6,"Nevat oğlu Yarovam efendisi Davut oğlu Süleymana başkaldırdı."));
trsuperobject.biblemap.put("b14_13_7", new trsuperobject(14,13,7,"Bir takım işe yaramaz kötü kişiler çevresinde toplanıp Süleyman oğlu Rehavama baskı yaptılar. O sırada Rehavam onlara karşı koyamayacak kadar genç ve deneyimsizdi."));
trsuperobject.biblemap.put("b14_13_8", new trsuperobject(14,13,8,"‹‹Şimdi de siz Davut soyunun elindeki RABbin Krallığına karşı gelmeyi tasarlıyorsunuz. Büyük bir ordusunuz. Üstelik Yarovamın ilahlarınız olsun diye yaptırdığı altın buzağılar da yanınızda."));
trsuperobject.biblemap.put("b14_13_10", new trsuperobject(14,13,10,"‹‹Ama bizim Tanrımız RABdir, Onu bırakmadık. RABbe hizmet eden kâhinler Harun soyundandır. Levililer de onlara yardımcıdır."));
trsuperobject.biblemap.put("b14_13_11", new trsuperobject(14,13,11,"Onlar her sabah, her akşam RABbe yakmalık sunular sunar, hoş kokulu buhur yakar, dinsel açıdan temiz masanın üzerine adak ekmeklerini dizerler. Her akşam altın kandilliğin kandillerini yakarlar. Biz Tanrımız RABbin buyruklarını yerine getiriyoruz. Oysa siz Ona sırt çevirdiniz."));
trsuperobject.biblemap.put("b14_13_12", new trsuperobject(14,13,12,"Tanrı bizimledir, O önderimizdir. Onun kâhinleri borazanlarla size karşı savaş çağrısı yapacaklar. Ey İsrail halkı, atalarınızın Tanrısı RABbe karşı savaşmayın; çünkü başaramazsınız.››"));
trsuperobject.biblemap.put("b14_13_13", new trsuperobject(14,13,13,"Yarovam askerlerinin bir bölümüyle Yahudalıları önden karşılarken, öbür bölümünü arkalarında pusu kurmaya göndermişti."));
trsuperobject.biblemap.put("b14_13_14", new trsuperobject(14,13,14,"Yahudalılar önden, arkadan kuşatıldıklarını görünce, RABbe yakardılar. Kâhinler borazanlarını çaldı."));
trsuperobject.biblemap.put("b14_13_15", new trsuperobject(14,13,15,"Yahudalılar savaş çığlıkları attığı anda, Tanrı Yarovamla İsraillileri Aviyayla Yahudalıların önünde yenilgiye uğrattı."));
trsuperobject.biblemap.put("b14_13_16", new trsuperobject(14,13,16,"İsrailliler Yahudalıların önünden kaçtı. Tanrı onları Yahudalıların eline teslim etti."));
trsuperobject.biblemap.put("b14_13_17", new trsuperobject(14,13,17,"Aviyayla ordusu İsraillileri bozguna uğrattı. İsraillilerden beş yüz bin seçme asker öldürüldü."));
trsuperobject.biblemap.put("b14_13_18", new trsuperobject(14,13,18,"Böylece İsrailliler yenilgiye uğradı, Yahudalılarsa zafer kazandı. Çünkü Yahudalılar atalarının Tanrısı RABbe güvenmişlerdi."));
trsuperobject.biblemap.put("b14_13_19", new trsuperobject(14,13,19,"Aviya Yarovamı kovaladı. Yarovama ait Beytel, Yeşana, Efron kentleriyle çevrelerindeki köyleri ele geçirdi."));
trsuperobject.biblemap.put("b14_13_20", new trsuperobject(14,13,20,"Aviyanın krallığı döneminde Yarovam bir daha eski gücünü toparlayamadı. Sonunda RAB onu cezalandırdı, Yarovam öldü."));
trsuperobject.biblemap.put("b14_13_21", new trsuperobject(14,13,21,"Aviya ise gitgide krallığını güçlendirdi. On dört kadınla evlenip yirmi iki erkek, on altı kız babası oldu."));
trsuperobject.biblemap.put("b14_13_22", new trsuperobject(14,13,22,"Aviya'nın yaptığı öbür işler, uygulamaları ve söyledikleri, Peygamber İddo'nun yorumunda yazılıdır."));
trsuperobject.biblemap.put("b14_14_1", new trsuperobject(14,14,1,"Aviya ölüp atalarına kavuşunca, Davut Kentinde gömüldü, yerine oğlu Asa kral oldu. Ülke Asanın yönetimi altında on yıl barış içinde yaşadı."));
trsuperobject.biblemap.put("b14_14_2", new trsuperobject(14,14,2,"Asa Tanrısı RABbin gözünde iyi ve doğru olanı yaptı."));
trsuperobject.biblemap.put("b14_14_3", new trsuperobject(14,14,3,"Yabancı ilahların sunaklarını, puta tapılan yerleri kaldırdı. Dikili taşları parçaladı, Aşera putlarını devirdi."));
trsuperobject.biblemap.put("b14_14_4", new trsuperobject(14,14,4,"Yahudalılardan atalarının Tanrısı RABbe yönelmelerini, Onun yasasına ve buyruklarına uymalarını istedi."));
trsuperobject.biblemap.put("b14_14_5", new trsuperobject(14,14,5,"Yahudanın bütün kentlerinden puta tapılan yerlerle buhur sunaklarını kaldırdı. Ülke onun yönetimi altında barış içinde yaşadı."));
trsuperobject.biblemap.put("b14_14_6", new trsuperobject(14,14,6,"Ülke barış içinde olduğu için Asa Yahudadaki bazı kentleri surlarla çevirdi. O yıllarda kimse ona karşı savaş açmadı. Çünkü RAB ona esenlik vermişti."));
trsuperobject.biblemap.put("b14_14_7", new trsuperobject(14,14,7,"Asa Yahudalılara, ‹‹Bu kentleri onaralım›› dedi, ‹‹Onları surlarla kuşatıp kulelerle, kapılarla, sürgülerle güçlendirelim. Ülke hâlâ bizim elimizde, çünkü Tanrımız RABbe yöneldik, O da bizi her yandan esenlikle kuşattı.›› Böylece yapım işlerini başarıyla bitirdiler."));
trsuperobject.biblemap.put("b14_14_8", new trsuperobject(14,14,8,"Asanın Yahudalılarla Benyaminlilerden oluşan bir ordusu vardı. Yahudalılar büyük kalkan ve mızraklarla donanmış üç yüz bin kişiydi. Benyaminliler ise küçük kalkan ve yay taşıyan iki yüz seksen bin kişiydi. Bunların hepsi yiğit savaşçılardı."));
trsuperobject.biblemap.put("b14_14_9", new trsuperobject(14,14,9,"Kûşlu Zerah binlerce asker ve üç yüz savaş arabasıyla Mareşaya ilerledi."));
trsuperobject.biblemap.put("b14_14_10", new trsuperobject(14,14,10,"Asa ona karşı durmak için yola çıktı. İki ordu Mareşa yakınlarında Sefata Vadisinde savaş düzeni aldı."));
trsuperobject.biblemap.put("b14_14_11", new trsuperobject(14,14,11,"Asa, Tanrısı RABbe, ‹‹Ya RAB, güçlünün karşısında güçsüze yardım edebilecek senden başka kimse yoktur›› diye yakardı, ‹‹Ey Tanrımız RAB, bize yardım et, çünkü sana güveniyoruz. Senin adınla bu kalabalığa karşı çıktık. Ya RAB, sen bizim Tanrımızsın. İnsanlar sana karşı zafer kazanmasın.››"));
trsuperobject.biblemap.put("b14_14_12", new trsuperobject(14,14,12,"RAB Kûşluları Asayla Yahudalıların önünde bozguna uğrattı. Kûşlular kaçmaya başladı."));
trsuperobject.biblemap.put("b14_14_13", new trsuperobject(14,14,13,"Asa ordusuyla onları Gerara kadar kovaladı. Kûşlulardan kurtulan olmadı. RABbin ve ordusunun önünde kırıldılar. Yahudalılar çok miktarda mal yağmalayıp götürdüler."));
trsuperobject.biblemap.put("b14_14_14", new trsuperobject(14,14,14,"Gerarın çevresindeki bütün köyleri yerle bir ettiler. Çünkü RABbin dehşeti onları sarmıştı. Bu köylerde çok mal olduğundan onları yağmaladılar."));
trsuperobject.biblemap.put("b14_14_15", new trsuperobject(14,14,15,"Çobanların çadırlarına da saldırdılar. Çok sayıda davar ve deveyi alıp Yeruşalim'e döndüler."));
trsuperobject.biblemap.put("b14_15_1", new trsuperobject(14,15,1,"Tanrının Ruhu Odet oğlu Azaryanın üzerine indi."));
trsuperobject.biblemap.put("b14_15_2", new trsuperobject(14,15,2,"Azarya, Kral Asaya gidip şöyle dedi: ‹‹Ey Asa, ey Yahuda ve Benyamin halkı, beni dinleyin! RABle birlikte olduğunuz sürece, O da sizinle olacaktır. Onu ararsanız bulursunuz. Ama Onu bırakırsanız, O da sizi bırakır."));
trsuperobject.biblemap.put("b14_15_3", new trsuperobject(14,15,3,"İsrail halkı uzun süre gerçek Tanrıdan, eğitici kâhinlerden ve yasadan uzak yaşadı."));
trsuperobject.biblemap.put("b14_15_4", new trsuperobject(14,15,4,"Ama sıkıntıya düştüklerinde İsrailin Tanrısı RABbe döndüler, Onu arayıp buldular."));
trsuperobject.biblemap.put("b14_15_5", new trsuperobject(14,15,5,"O günlerde yolcuların güvenliği yoktu. Çünkü çevre ülkelerde yaşayanların tümü büyük kargaşa içindeydi."));
trsuperobject.biblemap.put("b14_15_6", new trsuperobject(14,15,6,"Ulus ulusu, kent kenti ezmeye çalışıyordu. Çünkü Tanrı onları çeşitli sıkıntılarla tedirgin ediyordu."));
trsuperobject.biblemap.put("b14_15_7", new trsuperobject(14,15,7,"Ama siz güçlü olun, cesaretinizi yitirmeyin. Yaptıklarınızın karşılığını alacaksınız.››"));
trsuperobject.biblemap.put("b14_15_8", new trsuperobject(14,15,8,"Asa bu sözleri, Peygamber Odetin oğlu Azaryanın peygamberliğini duyunca, cesaret buldu. Yahuda ve Benyamin topraklarından, Efrayimin dağlık bölgesinde ele geçirdiği kentlerden iğrenç putları kaldırdı. RABbin Tapınağının eyvanının önündeki RABbin sunağını onardı. Azaryanın››, Masoretik metin ‹‹Peygamber Odetin›› (bkz. 15:1)."));
trsuperobject.biblemap.put("b14_15_9", new trsuperobject(14,15,9,"Efrayimden, Manaşşeden, Şimondan gelen birçok İsrailli, Tanrısı RABbin Asayla birlikte olduğunu görünce onun tarafına geçti. Asa bu gelenlerle Yahuda ve Benyamin halkını bir araya topladı."));
trsuperobject.biblemap.put("b14_15_10", new trsuperobject(14,15,10,"Asanın krallığının on beşinci yılının üçüncü ayında Yeruşalimde toplandılar."));
trsuperobject.biblemap.put("b14_15_11", new trsuperobject(14,15,11,"Yağmalamış oldukları hayvanlardan yedi yüz sığırla yedi bin davarı o gün RABbe kurban ettiler."));
trsuperobject.biblemap.put("b14_15_12", new trsuperobject(14,15,12,"Bütün yürekleriyle, bütün canlarıyla atalarının Tanrısı RABbe yönelmek için antlaşma yaptılar."));
trsuperobject.biblemap.put("b14_15_13", new trsuperobject(14,15,13,"Büyük küçük, kadın erkek, kim İsrailin Tanrısı RABbe yönelmezse öldürülecekti."));
trsuperobject.biblemap.put("b14_15_14", new trsuperobject(14,15,14,"Yüksek sesle bağırarak, borazan ve boru çalarak RABbin önünde ant içtiler."));
trsuperobject.biblemap.put("b14_15_15", new trsuperobject(14,15,15,"Yahudalılar bütün yürekleriyle içtikleri ant için sevindiler. RABbi istekle arayıp buldular. O da onları her yandan esenlikle kuşattı."));
trsuperobject.biblemap.put("b14_15_16", new trsuperobject(14,15,16,"Kral Asa annesi Maakanın kraliçeliğini elinden aldı. Çünkü o Aşera için iğrenç bir put yaptırmıştı. Asa bu iğrenç putu kesip parçaladıktan sonra Kidron Vadisinde yaktı."));
trsuperobject.biblemap.put("b14_15_17", new trsuperobject(14,15,17,"Ancak İsrailden puta tapılan yerleri kaldırmadı. Ama yaşamı boyunca yüreğini RABbe adadı."));
trsuperobject.biblemap.put("b14_15_18", new trsuperobject(14,15,18,"Babasının ve kendisinin adadığı altını, gümüşü ve eşyaları Tanrının Tapınağına getirdi."));
trsuperobject.biblemap.put("b14_15_19", new trsuperobject(14,15,19,"Asa'nın krallığının otuz beşinci yılına kadar savaş olmadı."));
trsuperobject.biblemap.put("b14_16_1", new trsuperobject(14,16,1,"Yahuda Kralı Asanın krallığının otuz altıncı yılında İsrail Kralı Baaşa Yahudaya saldırmaya hazırlanıyordu. Asanın topraklarına giriş çıkışı engellemek amacıyla, Rama Kentini güçlendirmeye başladı."));
trsuperobject.biblemap.put("b14_16_2", new trsuperobject(14,16,2,"Bunun üzerine Asa, RABbin Tapınağının ve sarayın hazinelerindeki altın ve gümüşü çıkararak şu haberle birlikte Şamda oturan Aram Kralı Ben-Hadata gönderdi:"));
trsuperobject.biblemap.put("b14_16_3", new trsuperobject(14,16,3,"‹‹Babamla baban arasında olduğu gibi seninle benim aramızda da bir antlaşma olsun. Sana gönderdiğim bu altınlara, gümüşlere karşılık, sen de İsrail Kralı Baaşa ile yaptığın antlaşmayı boz, topraklarımdan askerlerini çeksin.››"));
trsuperobject.biblemap.put("b14_16_4", new trsuperobject(14,16,4,"Kral Asanın önerisini kabul eden Ben-Hadat, ordu komutanlarını İsrail kentlerinin üzerine gönderdi. İyonu, Danı, Avel-Mayimi, Naftalinin bütün ambarlı kentlerini ele geçirdiler."));
trsuperobject.biblemap.put("b14_16_5", new trsuperobject(14,16,5,"Baaşa bunu duyunca Ramanın yapımını durdurup işe son verdi."));
trsuperobject.biblemap.put("b14_16_6", new trsuperobject(14,16,6,"Kral Asa bütün Yahudalıları çağırttı; Baaşanın Ramanın yapımında kullandığı taşlarla keresteleri alıp götürdüler. Asa bunlarla Geva ve Mispa kentlerini onardı."));
trsuperobject.biblemap.put("b14_16_7", new trsuperobject(14,16,7,"O sırada Bilici Hanani Yahuda Kralı Asaya gelip şöyle dedi: ‹‹Tanrın RABbe güveneceğine Aram Kralına güvendin. Bu yüzden Aram Kralının ordusu elinden kurtuldu."));
trsuperobject.biblemap.put("b14_16_8", new trsuperobject(14,16,8,"Kûşlularla Luvlular, çok sayıda savaş arabaları, atlılarıyla büyük bir ordu değil miydiler? Ama sen RABbe güvendin, O da onları eline teslim etti."));
trsuperobject.biblemap.put("b14_16_10", new trsuperobject(14,16,10,"Asa biliciye öfkelenip onu cezaevine attırdı. Çünkü söyledikleri onu kızdırmıştı. Halktan bazı kişilere de baskı yaptı."));
trsuperobject.biblemap.put("b14_16_11", new trsuperobject(14,16,11,"Asanın yaptığı işler, başından sonuna dek, Yahuda ve İsrail krallarının tarihinde yazılıdır."));
trsuperobject.biblemap.put("b14_16_12", new trsuperobject(14,16,12,"Asa, krallığının otuz dokuzuncu yılında ayaklarından hastalandı. Durumu çok ağırdı. Hastalığında RABbe yöneleceğine hekimlere başvurdu."));
trsuperobject.biblemap.put("b14_16_13", new trsuperobject(14,16,13,"Asa krallığının kırk birinci yılında ölüp atalarına kavuştu."));
trsuperobject.biblemap.put("b14_16_14", new trsuperobject(14,16,14,"Onu özel olarak hazırlanmış, güzel kokulu çeşit çeşit baharat dolu bir sedyeye yatırarak Davut Kenti'nde kendisi için yaptırdığı mezara gömdüler. Onuruna çok büyük bir ateş yaktılar."));
trsuperobject.biblemap.put("b14_17_1", new trsuperobject(14,17,1,"Asanın yerine oğlu Yehoşafat kral oldu. Yehoşafat İsraile karşı krallığını pekiştirdi."));
trsuperobject.biblemap.put("b14_17_2", new trsuperobject(14,17,2,"Yahudanın bütün surlu kentlerine askeri birlikler yerleştirdi; Yahudaya ve babası Asanın ele geçirmiş olduğu Efrayim kentlerine de asker yerleştirdi."));
trsuperobject.biblemap.put("b14_17_4", new trsuperobject(14,17,4,"babasının Tanrısına yöneldi; İsrail halkının yaptıklarına değil, Tanrının buyruklarına uydu."));
trsuperobject.biblemap.put("b14_17_6", new trsuperobject(14,17,6,"Yüreği RABde cesaret buldu, puta tapılan yerleri ve Aşera putlarını Yahudadan kaldırdı."));
trsuperobject.biblemap.put("b14_17_7", new trsuperobject(14,17,7,"Yehoşafat krallığının üçüncü yılında halka öğretmek amacıyla görevlilerinden Ben-Hayili, Ovadyayı, Zekeriyayı, Netaneli, Mikayayı Yahuda kentlerine gönderdi."));
trsuperobject.biblemap.put("b14_17_8", new trsuperobject(14,17,8,"Onlarla birlikte şu Levilileri de gönderdi: Şemaya, Netanya, Zevadya, Asahel, Şemiramot, Yehonatan, Adoniya, Toviya, Tov-Adoniya. Kâhinlerden Elişama ile Yehoramı gönderdi."));
trsuperobject.biblemap.put("b14_17_10", new trsuperobject(14,17,10,"Yahudayı çevreleyen ülkelerin krallıklarını RAB korkusu sardı. Bu yüzden Yehoşafata karşı savaşamadılar."));
trsuperobject.biblemap.put("b14_17_11", new trsuperobject(14,17,11,"Bazı Filistliler Yehoşafata haraç olarak armağanlar ve gümüş verdiler. Araplar da ona davar getirdiler: Yedi bin yedi yüz koçla yedi bin yedi yüz teke."));
trsuperobject.biblemap.put("b14_17_12", new trsuperobject(14,17,12,"Yehoşafat giderek güçlendi. Yahudada kaleler, ambarlı kentler yaptırdı."));
trsuperobject.biblemap.put("b14_17_13", new trsuperobject(14,17,13,"Yahuda kentlerinde birçok işler yaptı. Yeruşalime deneyimli yiğit savaşçılar yerleştirdi."));
trsuperobject.biblemap.put("b14_17_14", new trsuperobject(14,17,14,"Bunlar boylarına göre şöyle kaydedilmişlerdi:  Yahudadan binbaşılar: Komutan Adna ve komutasında 300 000 yiğit asker;"));
trsuperobject.biblemap.put("b14_17_15", new trsuperobject(14,17,15,"Yanında Komutan Yehohanan ve komutasında 280 000 asker;"));
trsuperobject.biblemap.put("b14_17_16", new trsuperobject(14,17,16,"Onun yanında kendini RABbin hizmetine adayan Zikri oğlu Amatsya ve komutasında 200 000 yiğit asker."));
trsuperobject.biblemap.put("b14_17_17", new trsuperobject(14,17,17,"Benyamin oymağından: Yiğit bir savaşçı olan Elyada ile komutasında yay ve kalkanla silahlanmış 200 000 asker;"));
trsuperobject.biblemap.put("b14_17_18", new trsuperobject(14,17,18,"Yanında Yehozavat ve komutasında savaşmak üzere donatılmış 180 000 asker."));
trsuperobject.biblemap.put("b14_17_19", new trsuperobject(14,17,19,"Bunlar kralın surlu Yahuda kentlerine yerleştirdiği askerlerin yanısıra, ona hizmet ediyorlardı."));
trsuperobject.biblemap.put("b14_18_1", new trsuperobject(14,18,1,"Büyük bir zenginlik ve onura kavuşan Yehoşafat, evlilik bağıyla Ahava akraba oldu."));
trsuperobject.biblemap.put("b14_18_2", new trsuperobject(14,18,2,"Birkaç yıl sonra Samiriye Kentinde yaşayan Ahavı görmeye gitti. Ahav onun ve yanındakilerin onuruna birçok davar, sığır keserek Ramot-Gilata saldırmak için onu kışkırttı."));
trsuperobject.biblemap.put("b14_18_3", new trsuperobject(14,18,3,"İsrail Kralı Ahav, Yahuda Kralı Yehoşafata, ‹‹Ramot-Gilata karşı benimle birlikte savaşır mısın?›› diye sordu. Yehoşafat, ‹‹Beni kendin, halkımı halkın say. Savaşta sana eşlik edeceğiz›› diye yanıtladı,"));
trsuperobject.biblemap.put("b14_18_4", new trsuperobject(14,18,4,"‹‹Ama önce RABbe danışalım›› diye ekledi."));
trsuperobject.biblemap.put("b14_18_5", new trsuperobject(14,18,5,"İsrail Kralı Ahav dört yüz peygamber toplayıp, ‹‹Ramot-Gilata karşı savaşalım mı, yoksa vaz mı geçeyim?›› diye sordu. Peygamberler, ‹‹Savaş, çünkü Tanrı kenti senin eline teslim edecek›› diye yanıtladılar."));
trsuperobject.biblemap.put("b14_18_6", new trsuperobject(14,18,6,"Ama Yehoşafat, ‹‹Burada danışabileceğimiz RABbin başka peygamberi yok mu?›› diye sordu."));
trsuperobject.biblemap.put("b14_18_7", new trsuperobject(14,18,7,"İsrail Kralı, ‹‹Yimla oğlu Mikaya adında biri daha var›› diye yanıtladı, ‹‹Onun aracılığıyla RABbe danışabiliriz. Ama ben ondan nefret ederim. Çünkü benimle ilgili hiç iyi peygamberlik etmez, hep kötü şeyler söyler.›› Yehoşafat, ‹‹Böyle konuşmaman gerekir, ey kral!›› dedi."));
trsuperobject.biblemap.put("b14_18_8", new trsuperobject(14,18,8,"İsrail Kralı bir görevli çağırıp, ‹‹Hemen Yimla oğlu Mikayayı getir!›› diye buyurdu."));
trsuperobject.biblemap.put("b14_18_9", new trsuperobject(14,18,9,"İsrail Kralı Ahav ile Yahuda Kralı Yehoşafat kral giysileriyle Samiriye Kapısının girişinde, harman yerine konan tahtlarında oturuyorlardı. Bütün peygamberler de onların önünde peygamberlik ediyordu."));
trsuperobject.biblemap.put("b14_18_10", new trsuperobject(14,18,10,"Kenaana oğlu Sidkiya, yaptığı demir boynuzları göstererek şöyle dedi: ‹‹RAB diyor ki, ‹Aramlıları yok edinceye dek onları bu boynuzlarla vuracaksın.››"));
trsuperobject.biblemap.put("b14_18_11", new trsuperobject(14,18,11,"Öteki peygamberlerin hepsi de aynı şeyi söylediler: ‹‹Ramot- Gilata saldır, kazanacaksın! Çünkü RAB onları senin eline teslim edecek.››"));
trsuperobject.biblemap.put("b14_18_12", new trsuperobject(14,18,12,"Mikayayı çağırmaya giden görevli ona, ‹‹Bak! Peygamberler bir ağızdan kral için olumlu şeyler söylüyorlar›› dedi, ‹‹Rica ederim, senin sözün de onlarınkine uygun olsun; olumlu bir şey söyle.››"));
trsuperobject.biblemap.put("b14_18_13", new trsuperobject(14,18,13,"Mikaya, ‹‹Yaşayan RABbin hakkı için, Tanrım ne derse onu söyleyeceğim›› diye karşılık verdi."));
trsuperobject.biblemap.put("b14_18_14", new trsuperobject(14,18,14,"Mikaya gelince kral, ‹‹Mikaya, Ramot-Gilata karşı savaşa gidelim mi, yoksa vaz mı geçeyim?›› diye sordu. Mikaya, ‹‹Saldırın, kazanacaksınız! Çünkü onlar sizin elinize teslim edilecek›› diye yanıtladı."));
trsuperobject.biblemap.put("b14_18_15", new trsuperobject(14,18,15,"Bunun üzerine kral, ‹‹RABbin adına bana gerçeğin dışında bir şey söylemeyeceğine ilişkin sana kaç kez ant içireyim?›› diye sordu."));
trsuperobject.biblemap.put("b14_18_16", new trsuperobject(14,18,16,"Mikaya şöyle karşılık verdi: ‹‹İsraillileri dağlara dağılmış çobansız koyunlar gibi gördüm. RAB, ‹Bunların sahibi yok. Herkes güvenlik içinde evine dönsün› dedi.››"));
trsuperobject.biblemap.put("b14_18_17", new trsuperobject(14,18,17,"İsrail Kralı Ahav Yehoşafata, ‹‹Benimle ilgili iyi peygamberlik etmez, hep kötü şeyler söyler dememiş miydim?›› dedi."));
trsuperobject.biblemap.put("b14_18_18", new trsuperobject(14,18,18,"Mikaya konuşmasını sürdürdü: ‹‹Öyleyse RABbin sözünü dinleyin! Gördüm ki, RAB tahtında oturuyor, bütün göksel varlıklar da sağında, solunda duruyordu."));
trsuperobject.biblemap.put("b14_18_19", new trsuperobject(14,18,19,"RAB sordu: ‹Ramot-Gilata saldırıp ölsün diye İsrail Kralı Ahavı kim kandıracak?› ‹‹Kimi şöyle, kimi böyle derken,"));
trsuperobject.biblemap.put("b14_18_20", new trsuperobject(14,18,20,"bir ruh çıkıp RABbin önünde durdu ve, ‹Ben onu kandıracağım› dedi. ‹‹RAB, ‹Nasıl?› diye sordu."));
trsuperobject.biblemap.put("b14_18_21", new trsuperobject(14,18,21,"‹‹Ruh, ‹Aldatıcı ruh olarak gidip Ahavın bütün peygamberlerine yalan söyleteceğim› diye karşılık verdi. ‹‹RAB, ‹Onu kandırmayı başaracaksın› dedi, ‹Git, dediğini yap.›"));
trsuperobject.biblemap.put("b14_18_22", new trsuperobject(14,18,22,"‹‹İşte RAB bu peygamberlerinin ağzına aldatıcı bir ruh koydu. Çünkü sana kötülük etmeye karar verdi.››"));
trsuperobject.biblemap.put("b14_18_23", new trsuperobject(14,18,23,"Kenaana oğlu Sidkiya yaklaşıp Mikayanın yüzüne bir tokat attı. ‹‹RABbin Ruhu nasıl benden çıkıp da seninle konuştu?›› dedi."));
trsuperobject.biblemap.put("b14_18_24", new trsuperobject(14,18,24,"Mikaya, ‹‹Gizlenmek için bir iç odaya girdiğin gün göreceksin›› diye yanıtladı."));
trsuperobject.biblemap.put("b14_18_25", new trsuperobject(14,18,25,"Bunun üzerine İsrail Kralı, ‹‹Mikayayı kentin yöneticisi Amona ve kralın oğlu Yoaşa götürün›› dedi,"));
trsuperobject.biblemap.put("b14_18_26", new trsuperobject(14,18,26,"‹‹Ben güvenlik içinde dönünceye dek bu adamı cezaevinde tutmalarını, ona su ve ekmekten başka bir şey vermemelerini söyleyin!››"));
trsuperobject.biblemap.put("b14_18_27", new trsuperobject(14,18,27,"Mikaya, ‹‹Eğer sen güvenlik içinde dönersen, RAB benim aracılığımla konuşmamış demektir›› dedi ve, ‹‹Herkes bunu duysun!›› diye ekledi."));
trsuperobject.biblemap.put("b14_18_28", new trsuperobject(14,18,28,"İsrail Kralı Ahavla Yahuda Kralı Yehoşafat Ramot-Gilata saldırmak için yola çıktılar."));
trsuperobject.biblemap.put("b14_18_29", new trsuperobject(14,18,29,"İsrail Kralı, Yehoşafata, ‹‹Ben kılık değiştirip savaşa öyle gireceğim, ama sen kral giysilerini giy›› dedi. Böylece İsrail Kralı kılığını değiştirdi, sonra savaşa girdiler."));
trsuperobject.biblemap.put("b14_18_30", new trsuperobject(14,18,30,"Aram Kralı, savaş arabalarının komutanlarına, ‹‹İsrail Kralı dışında, büyük küçük hiç kimseye saldırmayın!›› diye buyruk vermişti."));
trsuperobject.biblemap.put("b14_18_31", new trsuperobject(14,18,31,"Savaş arabalarının komutanları Yehoşafatı görünce, İsrail Kralı sanıp saldırmak için ona döndüler. Yehoşafat yakarmaya başladı. RAB Tanrı ona yardım edip saldıranların yönünü değiştirdi."));
trsuperobject.biblemap.put("b14_18_32", new trsuperobject(14,18,32,"Komutanlar onun İsrail Kralı olmadığını anlayınca peşini bıraktılar."));
