trsuperobject.biblemap.put("b14_19_2", new trsuperobject(14,19,2,"Hanani oğlu Bilici Yehu, Kral Yehoşafatı karşılamaya giderek ona şöyle dedi: ‹‹Kötülere yardım edip RABden nefret edenleri mi sevmen gerekir? Bunun için RABbin öfkesi senin üstünde olacak."));
trsuperobject.biblemap.put("b14_19_3", new trsuperobject(14,19,3,"Ancak, bazı iyi yönlerin de var. Ülkeden Aşera putlarını kaldırıp attın ve yürekten Tanrıya yönelmeye karar verdin.››"));
trsuperobject.biblemap.put("b14_19_4", new trsuperobject(14,19,4,"Yehoşafat Yeruşalimde yaşadı. Beer-Şevadan Efrayim dağlık bölgesine kadar yine halkın arasında dolaşarak onları atalarının Tanrısı RABbe döndürdü."));
trsuperobject.biblemap.put("b14_19_5", new trsuperobject(14,19,5,"Ülkeye, Yahudanın bütün surlu kentlerine yargıçlar atadı."));
trsuperobject.biblemap.put("b14_19_6", new trsuperobject(14,19,6,"Onlara, ‹‹Yaptıklarınıza dikkat edin›› dedi, ‹‹Çünkü insan adına değil, yargı verirken sizinle olan RAB adına yargılayacaksınız."));
trsuperobject.biblemap.put("b14_19_7", new trsuperobject(14,19,7,"Onun için RABden korkun, dikkatle yargılayın. Çünkü Tanrımız RAB kimsenin haksızlık yapmasına, kimseyi kayırmasına, rüşvet almasına göz yummaz.››"));
trsuperobject.biblemap.put("b14_19_9", new trsuperobject(14,19,9,"Yehoşafat onlara şu buyrukları verdi: ‹‹Görevinizi RAB korkusuyla, bağlılıkla, bütün yüreğinizle yapmalısınız."));
trsuperobject.biblemap.put("b14_19_10", new trsuperobject(14,19,10,"Kentlerde yaşayan kardeşlerinizden gelen her davada -kan davası, Kutsal Yasa, buyruklar, kurallar ya da ilkelerle ilgili her konuda- onları RABbe karşı suç işlememeleri için uyarın; öyle ki RAB size de kardeşlerinize de öfkelenmesin. Böyle yaparsanız suç işlememiş olursunuz. döndüler››."));
trsuperobject.biblemap.put("b14_19_11", new trsuperobject(14,19,11,"‹‹RAB'be ilişkin her konuda Başkâhin Amarya, krala ilişkin her konuda ise Yahuda oymağının önderi İsmail oğlu Zevadya size başkanlık edecek. Levililer de görevli olarak size yardımcı olacaklar. Yürekli olun, bu buyrukları uygulayın. RAB doğru kişiyle olsun!››"));
trsuperobject.biblemap.put("b14_20_1", new trsuperobject(14,20,1,"Bundan sonra Moavlılar, Ammonlular ve Meunluların bir kısmı Yehoşafatla savaşmak için yola çıktılar."));
trsuperobject.biblemap.put("b14_20_2", new trsuperobject(14,20,2,"Birkaç kişi Yehoşafata gidip, ‹‹Gölün öbür yakasından, Edomdan sana saldırmak için büyük bir ordu geliyor. Şu anda Haseson-Tamarda -Eyn-Gedide-›› dediler."));
trsuperobject.biblemap.put("b14_20_3", new trsuperobject(14,20,3,"Korkuya kapılan Yehoşafat RABbe danışmaya karar verdi ve bütün Yahudada oruç ilan etti."));
trsuperobject.biblemap.put("b14_20_5", new trsuperobject(14,20,5,"Yehoşafat RABbin Tapınağında, yeni avlunun önünde, Yahuda ve Yeruşalim topluluğunun arasına gidip durdu."));
trsuperobject.biblemap.put("b14_20_6", new trsuperobject(14,20,6,"‹‹Ey atalarımızın Tanrısı RAB, sen göklerde oturan Tanrı değil misin?›› dedi, ‹‹Ulusların bütün krallıklarını yöneten sensin. Güç, kudret senin elinde. Kimse sana karşı duramaz."));
trsuperobject.biblemap.put("b14_20_7", new trsuperobject(14,20,7,"Ey Tanrımız, bu ülkede yaşayanları halkın İsrailin önünden kovan ve ülkeyi sonsuza dek dostun İbrahimin soyuna veren sen değil misin?"));
trsuperobject.biblemap.put("b14_20_8", new trsuperobject(14,20,8,"Onlar orada yaşadılar, adına bir tapınak kurdular ve,"));
trsuperobject.biblemap.put("b14_20_9", new trsuperobject(14,20,9,"‹Başımıza bela, savaş, yargı, salgın hastalık, kıtlık gelirse, adının bulunduğu bu tapınağın ve senin önünde duracağız› dediler, ‹Sıkıntıya düştüğümüzde sana yakaracağız, sen de duyup bizi kurtaracaksın.›"));
trsuperobject.biblemap.put("b14_20_10", new trsuperobject(14,20,10,"‹‹İşte Ammonlular, Moavlılar ve Seir dağlık bölgesinde yaşayanlar! Mısırdan çıktıktan sonra İsraillilerin onların ülkesine girmelerine izin vermedin. Bu yüzden atalarımız başka yöne döndü, onları yok etmedi."));
trsuperobject.biblemap.put("b14_20_11", new trsuperobject(14,20,11,"Ama bak, bunun karşılığını bize nasıl ödüyorlar! Bize miras olarak vermiş olduğun mülkünden bizi kovmaya geliyorlar."));
trsuperobject.biblemap.put("b14_20_12", new trsuperobject(14,20,12,"Ey Tanrımız, onları yargılamayacak mısın? Çünkü bize saldıran bu büyük orduya karşı koyacak gücümüz yok. Ne yapacağımızı bilemiyoruz. Gözümüz sende.››"));
trsuperobject.biblemap.put("b14_20_13", new trsuperobject(14,20,13,"Bütün Yahudalılar, çoluk çocuklarıyla birlikte RABbin önünde duruyordu."));
trsuperobject.biblemap.put("b14_20_14", new trsuperobject(14,20,14,"RABbin Ruhu topluluğun ortasında duran Asaf soyundan Mattanya oğlu Yeiel oğlu Benaya oğlu Zekeriya oğlu Levili Yahazielin üzerine indi."));
trsuperobject.biblemap.put("b14_20_15", new trsuperobject(14,20,15,"Yahaziel şöyle dedi: ‹‹Ey Kral Yehoşafat, ey Yahuda halkı ve Yeruşalimde oturanlar, dinleyin! RAB size şöyle diyor: ‹Bu büyük ordudan korkmayın, yılmayın! Çünkü savaş sizin değil, Tanrınındır."));
trsuperobject.biblemap.put("b14_20_16", new trsuperobject(14,20,16,"Yarın onlarla savaşmaya çıkın. Onları vadinin sonunda, Yeruel kırlarında, Sits Yokuşunu çıkarlarken bulacaksınız."));
trsuperobject.biblemap.put("b14_20_17", new trsuperobject(14,20,17,"Bu kez savaşmak zorunda kalmayacaksınız. Yerinizde durup bekleyin, RABbin size sağlayacağı kurtuluşu görün, ey Yahuda ve Yeruşalim halkı! Korkmayın, yılmayın. Yarın onlara karşı savaşa çıkın. RAB sizinle olacak!››"));
trsuperobject.biblemap.put("b14_20_18", new trsuperobject(14,20,18,"Yehoşafat yüzüstü yere kapandı. Yahuda halkıyla Yeruşalimde oturanlar da RABbin önünde yere kapanıp Ona tapındılar."));
trsuperobject.biblemap.put("b14_20_19", new trsuperobject(14,20,19,"Sonra Kehatoğullarından ve Korahoğullarından bazı Levililer ayağa kalkıp İsrailin Tanrısı RABbi yüksek sesle övdüler."));
trsuperobject.biblemap.put("b14_20_20", new trsuperobject(14,20,20,"Ertesi sabah erkenden kalkıp Tekoa kırlarına doğru yola çıktılar. Yola koyulduklarında Yehoşafat durup şöyle dedi: ‹‹Beni dinleyin, ey Yahuda halkı ve Yeruşalimde oturanlar! Tanrınız RABbe güvenin, güvenlikte olursunuz. Onun peygamberlerine güvenin, başarılı olursunuz.››"));
trsuperobject.biblemap.put("b14_20_21", new trsuperobject(14,20,21,"Yehoşafat halka danıştıktan sonra RABbe ezgi okumak, Onun kutsallığının görkemini övmek için adamlar atadı. Bunlar ordunun önünde yürüyerek şöyle diyorlardı:  ‹‹RABbe şükredin,  Çünkü sevgisi sonsuza dek kalıcıdır!››"));
trsuperobject.biblemap.put("b14_20_22", new trsuperobject(14,20,22,"Onlar ezgi okuyup övgüler sunmaya başladığında, RAB Yahudaya saldıran Ammonlulara, Moavlılara ve Seir dağlık bölgesinde yaşayanlara pusu kurmuştu. Hepsi bozguna uğratıldı."));
trsuperobject.biblemap.put("b14_20_23", new trsuperobject(14,20,23,"Ammonlularla Moavlılar, Seir dağlık bölgesinde yaşayan halkı büsbütün yok etmek için onlara saldırdılar. Seirlileri yok ettikten sonra da birbirlerini öldürmeye başladılar."));
trsuperobject.biblemap.put("b14_20_24", new trsuperobject(14,20,24,"Yahudalılar kırdaki gözcü kulesine varınca, o büyük orduya baktılar, ama sadece yere serilmiş cesetler gördüler. Tek kişi kurtulmamıştı."));
trsuperobject.biblemap.put("b14_20_25", new trsuperobject(14,20,25,"Malları yağmalamaya giden Yehoşafatla askerleri, ölülerin arasında çok miktarda mal, giysi ve değerli eşya buldular. Taşıyabileceklerinden çok mal topladılar. Yağma edilecek o kadar çok mal vardı ki, toplama işi üç gün sürdü."));
trsuperobject.biblemap.put("b14_20_26", new trsuperobject(14,20,26,"Dördüncü gün Beraka Vadisinde toplanarak RABbe övgüler sundular. Bu yüzden oranın adı bugün de Beraka Vadisi olarak kaldı."));
trsuperobject.biblemap.put("b14_20_27", new trsuperobject(14,20,27,"Bundan sonra bütün Yahuda ve Yeruşalim halkı Yehoşafatın önderliğinde sevinçle Yeruşalime döndü. Çünkü RAB düşmanlarını bozguna uğratarak onları sevindirmişti."));
trsuperobject.biblemap.put("b14_20_28", new trsuperobject(14,20,28,"Çenk, lir ve borazan çalarak Yeruşalime, RABbin Tapınağına gittiler."));
trsuperobject.biblemap.put("b14_20_29", new trsuperobject(14,20,29,"RABbin İsrailin düşmanlarına karşı savaştığını duyan ülkelerin krallıklarını Tanrı korkusu sardı."));
trsuperobject.biblemap.put("b14_20_30", new trsuperobject(14,20,30,"Yehoşafatın ülkesi ise barış içindeydi. Çünkü Tanrısı her yandan onu esenlikle kuşatmıştı."));
trsuperobject.biblemap.put("b14_20_31", new trsuperobject(14,20,31,"Yehoşafat Yahudayı yönetti. Otuz beş yaşında kral oldu ve Yeruşalimde yirmi beş yıl krallık yaptı. Annesi Şilhinin kızı Azuvaydı."));
trsuperobject.biblemap.put("b14_20_32", new trsuperobject(14,20,32,"Babası Asanın yollarını izleyen ve bunlardan sapmayan Yehoşafat RABbin gözünde doğru olanı yaptı."));
trsuperobject.biblemap.put("b14_20_33", new trsuperobject(14,20,33,"Ancak alışılagelen tapınma yerleri kaldırılmadı. Halk hâlâ atalarının Tanrısına bütün yüreğiyle yönelmemişti."));
trsuperobject.biblemap.put("b14_20_34", new trsuperobject(14,20,34,"Yehoşafatın yaptığı öbür işler, başından sonuna dek, İsrail kralları tarihinin bir bölümü olan Hanani oğlu Yehunun tarihinde yazılıdır."));
trsuperobject.biblemap.put("b14_20_35", new trsuperobject(14,20,35,"Yahuda Kralı Yehoşafat bir süre sonra kendini günaha veren İsrail Kralı Ahazya ile anlaşmaya vardı."));
