trsuperobject.biblemap.put("b14_20_37", new trsuperobject(14,20,37,"Mareşalı Dodavahu oğlu Eliezer, Yehoşafat'a karşı şöyle peygamberlik etti: ‹‹Ahazya ile anlaşmaya vardığın için RAB işini bozacak.›› Gemiler Tarşiş'efş gidemeden parçalandı."));
trsuperobject.biblemap.put("b14_21_1", new trsuperobject(14,21,1,"Yehoşafat ölüp atalarına kavuştu ve Davut Kentinde atalarının yanına gömüldü. Yerine oğlu Yehoram kral oldu."));
trsuperobject.biblemap.put("b14_21_2", new trsuperobject(14,21,2,"Yehoşafat oğlu Yehoramın kardeşleri şunlardı: Azarya, Yehiel, Zekeriya, Azaryahu, Mikael, Şefatya. Bunların tümü İsrail Kralı Yehoşafatın oğullarıydı."));
trsuperobject.biblemap.put("b14_21_3", new trsuperobject(14,21,3,"Babaları onlara çok miktarda altın, gümüş, değerli eşyalar armağan etmiş, ayrıca Yahudada surlu kentler vermişti. Ancak, Yehoram ilk oğlu olduğundan, krallığı ona bırakmıştı."));
trsuperobject.biblemap.put("b14_21_4", new trsuperobject(14,21,4,"Babasının yerine geçen Yehoram güçlenince, bütün kardeşlerini ve bazı İsrail önderlerini kılıçtan geçirtti."));
