trsuperobject.biblemap.put("b16_1_1", new trsuperobject(16,1,1,"Hakalya oğlu Nehemyanın anlattıkları: Pers Kralı Artahşastanın krallığının yirminci yılı, Kislev ayında Sus Kalesindeydim."));
trsuperobject.biblemap.put("b16_1_2", new trsuperobject(16,1,2,"Kardeşlerimden Hanani ve bazı Yahudalılar yanıma geldi. Onlara sürgünden kurtulup sağ kalan Yahudileri ve Yeruşalimin durumunu sordum."));
trsuperobject.biblemap.put("b16_1_3", new trsuperobject(16,1,3,"‹‹Sürgünden kurtulup Yahuda İline dönenler büyük sıkıntı ve utanç içinde›› diye karşılık verdiler, ‹‹Üstelik Yeruşalim surları yıkılmış, kapıları yakılmış.››"));
trsuperobject.biblemap.put("b16_1_4", new trsuperobject(16,1,4,"Bunları duyunca oturup ağladım, günlerce yas tuttum. Oruç tutup Göklerin Tanrısına dua ettim:"));
trsuperobject.biblemap.put("b16_1_5", new trsuperobject(16,1,5,"‹‹Ey Göklerin Tanrısı RAB! Yüce ve görkemli Tanrı! Seni sevenlerle, buyruklarına uyanlarla yaptığın antlaşmaya bağlı kalırsın."));
trsuperobject.biblemap.put("b16_1_6", new trsuperobject(16,1,6,"Ya RAB, halimi gör, gece gündüz kulların İsrail halkı için ettiğim duaya kulak ver. İtiraf ediyorum, İsrail halkı günah işledi, ben ve atalarım günah işledik."));
trsuperobject.biblemap.put("b16_1_7", new trsuperobject(16,1,7,"Sana çok kötülük yaptık. Kulun Musaya verdiğin buyruklara, kurallara, ilkelere uymadık."));
trsuperobject.biblemap.put("b16_1_8", new trsuperobject(16,1,8,"‹‹Kulun Musaya söylediklerini anımsa. Dedin ki, ‹Eğer bana ihanet ederseniz, sizi ulusların arasına dağıtacağım."));
trsuperobject.biblemap.put("b16_1_9", new trsuperobject(16,1,9,"Ama bana döner, buyruklarımı özenle yerine getirirseniz, dünyanın öbür ucuna sürülmüş olsanız bile sizleri toplayıp seçtiğim yere, bulunacağım yere getireceğim.›"));
trsuperobject.biblemap.put("b16_1_10", new trsuperobject(16,1,10,"‹‹Onlar senin kulların, kendi halkındır. Yüce kudretin ve güçlü elinle onları kurtardın."));
trsuperobject.biblemap.put("b16_1_11", new trsuperobject(16,1,11,"Ya Rab, bu kulunun, adını yüceltmekten sevinç duyan öbür kullarının dualarına kulak ver. Beni bugün başarılı kıl ve kralın önerimi kabul etmesini sağla.›› O günlerde kralın sakisiydim."));
trsuperobject.biblemap.put("b16_2_1", new trsuperobject(16,2,1,"Kral Artahşastanın krallığının yirminci yılı, Nisan ayıydı. Krala getirilen şarabı alıp kendisine sundum. O güne kadar beni hiç üzgün görmemişti."));
trsuperobject.biblemap.put("b16_2_2", new trsuperobject(16,2,2,"Bu yüzden, ‹‹Neden böyle üzgün görünüyorsun?›› diye sordu, ‹‹Hasta olmadığına göre, bir derdin olmalı.›› kapsardı. Çok korktum."));
trsuperobject.biblemap.put("b16_2_3", new trsuperobject(16,2,3,"Krala, ‹‹Tanrı sana uzun ömürler versin›› dedim, ‹‹Atalarımın gömüldüğü kent yıkıldı, kapıları yakıldı. Nasıl üzülmem?››"));
trsuperobject.biblemap.put("b16_2_4-5", new trsuperobject(16,2,4-5,"Kral, ‹‹Dileğin ne?›› diye sordu. Göklerin Tanrısına dua edip krala şöyle dedim: ‹‹Eğer uygun görüyorsan, benden hoşnut kaldınsa, lütfen beni Yahudaya, atalarımın gömüldüğü kente gönder; kenti onarayım.››"));
trsuperobject.biblemap.put("b16_2_6", new trsuperobject(16,2,6,"Kral kraliçeyle birlikte oturuyordu. ‹‹Yolculuğun ne kadar sürer?›› diye sordu, ‹‹Ne zaman dönersin?›› Böylece kral dileğimi uygun buldu ve beni göndermeyi kabul etti. Ona ne zaman döneceğimi söyledim."));
trsuperobject.biblemap.put("b16_2_7", new trsuperobject(16,2,7,"Sonra şöyle dedim: ‹‹Uygun görüyorsan, Yahudaya varmamı sağlamaları için, Fıratın batı yakasındaki valilere birer mektup yazılsın."));
trsuperobject.biblemap.put("b16_2_8", new trsuperobject(16,2,8,"Bir de kralın orman sorumlusu Asafa bir mektup götürmek istiyorum. Tapınağın yanındaki kalenin kapıları, kent surları ve oturacağım evin yapımı için bana kereste versin.›› Tanrım bana destek olduğu için kral dileklerimi yerine getirdi."));
trsuperobject.biblemap.put("b16_2_9", new trsuperobject(16,2,9,"Fıratın batı yakasındaki valilere gidip kralın mektuplarını verdim. Kral benimle birlikte komutanlar ve atlılar göndermişti."));
trsuperobject.biblemap.put("b16_2_10", new trsuperobject(16,2,10,"Horonlu Sanballat ile Ammonlu görevlilerden Toviya, İsrail halkının iyiliği için birinin çalışmaya geldiğini duyunca çok sıkıldılar."));
trsuperobject.biblemap.put("b16_2_11", new trsuperobject(16,2,11,"Yeruşalime gittim. Orada üç gün kaldıktan sonra,"));
trsuperobject.biblemap.put("b16_2_12", new trsuperobject(16,2,12,"gece kalkıp birkaç adamla birlikte işe koyuldum. Yeruşalim için yapacaklarıma ilişkin Tanrıdan aldığım esini kimseye açıklamadım. Bindiğim hayvandan başka hayvan götürmemiştim."));
trsuperobject.biblemap.put("b16_2_13", new trsuperobject(16,2,13,"Hâlâ karanlıktı. Dere Kapısından Ejder Pınarına, oradan Gübre Kapısına gittim. Yeruşalimin yıkılan surlarını, yanıp kül olan kapılarını gözden geçirdim."));
trsuperobject.biblemap.put("b16_2_14", new trsuperobject(16,2,14,"Sonra Pınar Kapısına, Kral Havuzuna doğru gittim. Ne var ki, yol bindiğim hayvanın geçmesine uygun değildi."));
trsuperobject.biblemap.put("b16_2_15", new trsuperobject(16,2,15,"Gece karanlığında dere boyunca ilerledim, surları gözden geçirip geri geldim. Sonunda Dere Kapısından girip yerime döndüm."));
trsuperobject.biblemap.put("b16_2_16", new trsuperobject(16,2,16,"Yetkililer nereye gittiğimi, ne yaptığımı bilmiyorlardı. Çünkü Yahudilere, kâhinlere, soylulara, yetkililere ve öteki görevlilere henüz hiçbir şey söylememiştim."));
trsuperobject.biblemap.put("b16_2_17", new trsuperobject(16,2,17,"Sonra onlara, ‹‹İçine düştüğümüz yıkımı görüyorsunuz›› dedim, ‹‹Yeruşalim yıkılmış, kapıları ateşe verilmiş. Gelin, Yeruşalim surlarını onaralım, utancımıza son verelim.››"));
trsuperobject.biblemap.put("b16_2_18", new trsuperobject(16,2,18,"Onlara Tanrının bana nasıl destek olduğunu ve kralın söylediklerini anlattım. Onlar da, ‹‹Haydi, onarmaya başlayalım›› dediler. Var güçleriyle bu hayırlı işe başladılar."));
trsuperobject.biblemap.put("b16_2_19", new trsuperobject(16,2,19,"Ama Horonlu Sanballat, Ammonlu görevlilerden Toviya, Arap Geşem yapacaklarımızı duyunca, bizi küçümseyip alay ettiler. ‹‹Ne yapıyorsunuz? Krala baş mı kaldırıyorsunuz?›› dediler."));
trsuperobject.biblemap.put("b16_2_20", new trsuperobject(16,2,20,"Onları şöyle yanıtladım: ‹‹Göklerin Tanrısı bizi başarılı kılacaktır. Biz O'nun kulları olarak onarımı başlatacağız. Ama sizin Yeruşalim üzerinde ne hakkınız, ne de payınız olacak, adınız bile anılmayacak.››"));
trsuperobject.biblemap.put("b16_3_1", new trsuperobject(16,3,1,"Başkâhin Elyaşiv ile öbür kâhinler işe koyulup Koyun Kapısını onardılar, kapı kanatlarını kutsayıp yerine taktılar. Hammea Kulesine ve Hananel Kulesine kadar surları kutsadılar."));
trsuperobject.biblemap.put("b16_3_2", new trsuperobject(16,3,2,"Bitişik bölümü Erihalılar, onun yanındakini de İmri oğlu Zakkur onardı."));
trsuperobject.biblemap.put("b16_3_3", new trsuperobject(16,3,3,"Balık Kapısını Senaalılar onardı. Kirişleri yerleştirip kapı kanatlarını yerine koydular, sürgülerle kapı kollarını taktılar."));
trsuperobject.biblemap.put("b16_3_4", new trsuperobject(16,3,4,"Bitişik bölümü Hakkos oğlu Uriya oğlu Meremot onardı. Onun yanındakini Meşezavel oğlu Berekya oğlu Meşullam onardı. Onun yanındakini Baana oğlu Sadok onardı."));
trsuperobject.biblemap.put("b16_3_5", new trsuperobject(16,3,5,"Onun yanındakini Tekoalılar onardı; ama soylular efendilerinin buyurduğu işlere el atmadılar."));
trsuperobject.biblemap.put("b16_3_6", new trsuperobject(16,3,6,"Eski Kapıyı Paseah oğlu Yoyada ile Besodya oğlu Meşullam onardı. Kirişleri yerleştirip kapı kanatlarını yerine koydular, sürgülerle kapı kollarını taktılar."));
trsuperobject.biblemap.put("b16_3_7", new trsuperobject(16,3,7,"Bitişik bölümü Givonlu Melatya, Meronotlu Yadon ve Fıratın batı yakasındaki bölge valisinin yönetiminde yaşayan Givonlularla Mispalılar onardı."));
trsuperobject.biblemap.put("b16_3_8", new trsuperobject(16,3,8,"Onların yanındakini kuyumculardan biri olan Harhaya oğlu Uzziel onardı. Onun yanındakini baharatçı Hananya onardı. Yeruşalim surlarını Geniş Duvara kadar onardılar."));
trsuperobject.biblemap.put("b16_3_9", new trsuperobject(16,3,9,"Onların yanındaki bölümü Yeruşalimin yarısını yöneten Hur oğlu Refaya onardı."));
trsuperobject.biblemap.put("b16_3_10", new trsuperobject(16,3,10,"Bunun yanındaki bölüm Harumaf oğlu Yedayanın evinin karşısına düşüyordu. O bölümü Yedaya onardı. Onun yanındakini Haşavneya oğlu Hattuş onardı."));
trsuperobject.biblemap.put("b16_3_11", new trsuperobject(16,3,11,"Harim oğlu Malkiya ile Pahat-Moav oğlu Haşşuv başka bir bölümü ve Fırınlar Kulesini onardılar."));
trsuperobject.biblemap.put("b16_3_12", new trsuperobject(16,3,12,"Onların yanındaki bölümü Yeruşalimin öbür yarısını yöneten Halloheş oğlu Şallum kızlarıyla birlikte onardı."));
trsuperobject.biblemap.put("b16_3_13", new trsuperobject(16,3,13,"Dere Kapısını Hanun ve Zanoahta yaşayanlar onardı. Kapı kanatlarını yerlerine yerleştirip sürgülerle kapı kollarını taktılar. Ayrıca Gübre Kapısına kadar uzanan surlarda bin arşınlıkfç yer onardılar."));
trsuperobject.biblemap.put("b16_3_14", new trsuperobject(16,3,14,"Gübre Kapısını Beythakkerem bölgesini yöneten Rekav oğlu Malkiya onardı. Kapı kanatlarını yerine yerleştirip sürgülerle kapı kollarını taktılar."));
trsuperobject.biblemap.put("b16_3_15", new trsuperobject(16,3,15,"Çeşme Kapısını Mispa bölgesini yöneten Kol-Hoze oğlu Şallun onardı. Üzerini bir saçakla kapadı. Kapı kanatlarını yerine yerleştirip sürgülerle kapı kollarını taktı. Kral Bahçesinin yanındaki Şelah Havuzunun duvarını Davut Kentinden inen merdivenlere kadar onardı."));
trsuperobject.biblemap.put("b16_3_16", new trsuperobject(16,3,16,"Oradan ötesini Beytsur bölgesinin yarısını yöneten Azbuk oğlu Nehemya, Davutun aile mezarlığından yapay havuza ve Yiğitler Evine kadar onardı."));
trsuperobject.biblemap.put("b16_3_17", new trsuperobject(16,3,17,"Surların sonraki bölümünü aşağıdaki Levililer onardı: Bani oğlu Rehum bir sonraki bölümü onardı. Bitişiğini Keila bölgesinin yarısını yöneten Haşavya kendi bölgesi adına onardı."));
trsuperobject.biblemap.put("b16_3_18", new trsuperobject(16,3,18,"Ondan sonraki bölümü, Keila bölgesinin öbür yarısını yöneten Henadat oğlu Bavvay yönetiminde kardeşleri onardılar."));
trsuperobject.biblemap.put("b16_3_19", new trsuperobject(16,3,19,"Onun bitişiğini -silah deposuna, surun döndüğü yere kadar çıkan yolun karşısını- Mispayı yöneten Yeşu oğlu Ezer onardı."));
trsuperobject.biblemap.put("b16_3_20", new trsuperobject(16,3,20,"Ondan sonraki, surun döndüğü yerden Başkâhin Elyaşivin evinin kapısına kadar uzanan bölümü büyük çaba harcayarak Zakkay oğlu Baruk onardı."));
trsuperobject.biblemap.put("b16_3_21", new trsuperobject(16,3,21,"Ondan sonrasını, Elyaşivin evinin kapısından evin sonuna kadar uzanan bölümü Hakkos oğlu Uriya oğlu Meremot onardı."));
trsuperobject.biblemap.put("b16_3_22", new trsuperobject(16,3,22,"Surların sonraki bölümünü çevrede yaşayan kâhinler onardı."));
trsuperobject.biblemap.put("b16_3_23", new trsuperobject(16,3,23,"Evlerinin karşısına düşen bölümü Benyamin ile Haşşuv onardılar. Onlardan sonra, evinin bitişiği olan bölümü Ananya oğlu Maaseya oğlu Azarya onardı."));
trsuperobject.biblemap.put("b16_3_24", new trsuperobject(16,3,24,"Ondan sonraki, Azaryanın evinden surun döndüğü köşeye kadar uzanan bölümü Henadat oğlu Binnuy onardı."));
trsuperobject.biblemap.put("b16_3_25", new trsuperobject(16,3,25,"Uzay oğlu Palal surun döndüğü köşeden sonraki bölümü ve yukarı saray muhafız avlusunun yanındaki gözetleme kulesini onardı. Ondan sonraki bölümü Paroş oğlu Pedaya onardı;"));
trsuperobject.biblemap.put("b16_3_26", new trsuperobject(16,3,26,"Pedaya ile Ofel Tepesinde yaşayan tapınak görevlileri doğuya doğru Su Kapısının önüne ve gözetleme kulesine kadarki bölümü onardılar."));
trsuperobject.biblemap.put("b16_3_27", new trsuperobject(16,3,27,"Ondan sonraki, büyük gözetleme kulesinden Ofel surlarına kadar uzanan bölümü Tekoalılar onardı."));
trsuperobject.biblemap.put("b16_3_28", new trsuperobject(16,3,28,"At Kapısının yukarısını kâhinler onardı. Her biri kendi evinin karşısını yaptı."));
trsuperobject.biblemap.put("b16_3_29", new trsuperobject(16,3,29,"Onlardan sonra, evinin karşısına düşen bölümü İmmer oğlu Sadok onardı. Ondan sonrasını Doğu Kapısının nöbetçisi Şekanya oğlu Şemaya onardı."));
trsuperobject.biblemap.put("b16_3_30", new trsuperobject(16,3,30,"Ondan sonraki bölümü Şelemya oğlu Hananya ile Salafın altıncı oğlu Hanun onardı. Ondan sonra, odasının karşısına düşen bölümü Berekya oğlu Meşullam onardı."));
trsuperobject.biblemap.put("b16_3_31", new trsuperobject(16,3,31,"Ondan sonraki bölümü, tapınak görevlileriyle tüccarların kaldığı eve, oradan Mifkat Kapısına, surun yukarı köşesindeki odaya kadar, kuyumculardan biri olan Malkiya onardı."));
trsuperobject.biblemap.put("b16_3_32", new trsuperobject(16,3,32,"Surun köşesindeki odayla Koyun Kapısı arasındaki bölümü kuyumcularla tüccarlar onardılar."));
trsuperobject.biblemap.put("b16_4_1", new trsuperobject(16,4,1,"Sanballat surları onardığımızı duyunca öfkeden deliye döndü. Bizimle alay etmeye başladı."));
trsuperobject.biblemap.put("b16_4_2", new trsuperobject(16,4,2,"Dostlarının ve Samiriye ordusunun önünde, ‹‹Bu zavallı Yahudiler ne yaptıklarını sanıyorlar?›› dedi, ‹‹Onlara izin verirler mi? Kurban mı kesecekler? Bir günde mi bitirecekler? Küle dönmüş molozların arasından taşları mı canlandıracaklar?››"));
trsuperobject.biblemap.put("b16_4_3", new trsuperobject(16,4,3,"Yanında duran Ammonlu Toviya, ‹‹Yaptıkları şu taş duvara bak!›› dedi, ‹‹Üzerine bir tilki çıksa yıkılır.››"));
trsuperobject.biblemap.put("b16_4_4", new trsuperobject(16,4,4,"O zaman şöyle dua ettim: ‹‹Ey Tanrımız, bize kulak ver! Hor görüyorlar bizi. Onların aşağılamalarını kendi başlarına döndür. Sürüldükleri ülkede yağmaya uğrasınlar."));
trsuperobject.biblemap.put("b16_4_5", new trsuperobject(16,4,5,"Suçlarını bağışlama, günahlarını unutma. Çünkü biz çalışanları aşağıladılar.››"));
trsuperobject.biblemap.put("b16_4_6", new trsuperobject(16,4,6,"Surun onarımına devam ettik; yarı yüksekliğe kadar suru tamamladık. Çünkü herkes canla başla çalışıyordu."));
trsuperobject.biblemap.put("b16_4_7", new trsuperobject(16,4,7,"Sanballat, Toviya, Araplar, Ammonlular ve Aşdotlular Yeruşalim surlarındaki onarımın ilerlediğini, gediklerin kapanmaya başladığını duyunca çok öfkelendiler."));
trsuperobject.biblemap.put("b16_4_8", new trsuperobject(16,4,8,"Hepsi bir araya gelerek Yeruşalime karşı savaşmak ve kentte karışıklık çıkarmak için düzen kurdular."));
trsuperobject.biblemap.put("b16_4_9", new trsuperobject(16,4,9,"Ama biz Tanrımıza dua ettik ve gece gündüz onları gözetlesinler diye nöbetçiler diktik."));
trsuperobject.biblemap.put("b16_4_10", new trsuperobject(16,4,10,"O sırada Yahudalılar, ‹‹Yük taşıyanların gücü tükendi›› dediler, ‹‹O kadar moloz var ki, artık surların onarımını sürdüremiyoruz.››"));
trsuperobject.biblemap.put("b16_4_11", new trsuperobject(16,4,11,"Düşmanlarımız ise, ‹‹Onlar anlamadan, bizi görmeden aralarına girip hepsini öldürerek bu işe son verelim›› diye düşünüyorlardı."));
trsuperobject.biblemap.put("b16_4_12", new trsuperobject(16,4,12,"Çevrede yaşayan Yahudiler gelip on kez bizi uyardılar. ‹‹Yanımıza gelin, yoksa size her yönden saldıracaklar›› dediler."));
trsuperobject.biblemap.put("b16_4_13", new trsuperobject(16,4,13,"Bu yüzden, surların en alçak yerlerinin arkasına, tamamlanmamış yerlere, çeşitli boylardan kılıçlı, mızraklı, yaylı adamlar yerleştirdim."));
trsuperobject.biblemap.put("b16_4_14", new trsuperobject(16,4,14,"Durumu görünce ayağa kalktım; soylulara, görevlilere ve geri kalan herkese, ‹‹Onlardan korkmayın!›› dedim, ‹‹Yüce ve görkemli Rabbi anımsayın. Kardeşleriniz, oğullarınız, kızlarınız, karılarınız, evleriniz için savaşın.››"));
trsuperobject.biblemap.put("b16_4_15", new trsuperobject(16,4,15,"Kurdukları düzeni anladığımız düşmanlarımızın kulağına gitti. Tanrı düzenlerini boşa çıkarmıştı. O zaman hepimiz surlara, işimizin başına döndük."));
trsuperobject.biblemap.put("b16_4_16", new trsuperobject(16,4,16,"O günden sonra adamlarımın yarısı çalışırken öbür yarısı mızraklı, kalkanlı, yaylı ve zırhlı olarak nöbet tuttu. Önderler Yahudalıların arkasında yer almıştı."));
trsuperobject.biblemap.put("b16_4_17", new trsuperobject(16,4,17,"Duvarcılar, yükleri taşıyanlar, yükleyenler bir eliyle çalışıyor, bir eliyle silah tutuyordu."));
trsuperobject.biblemap.put("b16_4_18", new trsuperobject(16,4,18,"Yapıcılar kılıç kuşanmış, öyle çalışıyorlardı. Boru çalansa benim yanımdaydı."));
trsuperobject.biblemap.put("b16_4_19", new trsuperobject(16,4,19,"Soylulara, görevlilere ve geri kalan herkese, ‹‹İş çok büyük ve dağınık›› dedim, ‹‹Surların üzerinde her birimiz ayrı yerde, birbirimizden uzaktayız."));
trsuperobject.biblemap.put("b16_4_20", new trsuperobject(16,4,20,"Nereden boru sesini işitirseniz, orada bize katılın. Tanrımız bizim için savaşacak.››"));
trsuperobject.biblemap.put("b16_4_21", new trsuperobject(16,4,21,"İşte böyle çalışıyorduk. Yarımız gün doğumundan yıldızlar görünene kadar mızraklarla nöbet tutuyordu."));
trsuperobject.biblemap.put("b16_4_22", new trsuperobject(16,4,22,"O sırada halka, ‹‹Herkes geceyi yardımcısıyla birlikte Yeruşalimde geçirsin›› dedim, ‹‹Gece bizim için nöbet tutsunlar, gündüz de çalışsınlar.››"));
trsuperobject.biblemap.put("b16_4_23", new trsuperobject(16,4,23,"Ne ben, ne kardeşlerim, ne adamlarım, ne de yanımdaki nöbetçiler, giysilerimizi çıkarmadık. Herkes suya bile silahıyla gitti."));
trsuperobject.biblemap.put("b16_5_1", new trsuperobject(16,5,1,"Bir süre sonra kadınlı erkekli halk Yahudi kardeşlerinden şiddetle yakınmaya başladı."));
trsuperobject.biblemap.put("b16_5_2", new trsuperobject(16,5,2,"Bazıları, ‹‹Biz kalabalığız›› diyordu, ‹‹Oğullarımız, kızlarımız çok. Yaşamak için buğdaya ihtiyacımız var.››"));
trsuperobject.biblemap.put("b16_5_3", new trsuperobject(16,5,3,"Bazıları da, ‹‹Kıtlıkta buğday almak için tarlalarımızı, bağlarımızı, evlerimizi ipotek ediyoruz›› diyordu."));
trsuperobject.biblemap.put("b16_5_4", new trsuperobject(16,5,4,"Bazıları ise, ‹‹Krala vergi ödemek için tarlalarımızı, bağlarımızı karşılık gösterip borç para aldık›› diyordu,"));
trsuperobject.biblemap.put("b16_5_5", new trsuperobject(16,5,5,"‹‹Yahudi kardeşlerimizle aynı kanı taşımıyor muyuz? Bizim çocuklarımızın onlarınkinden ne farkı var? Oğullarımızı kızlarımızı köle olarak satmak zorunda kaldık. Kızlarımızdan bazıları cariye olarak satıldı bile. Çaresiz kaldık. Çünkü tarlalarımız, bağlarımız başkalarının elinde.››"));
trsuperobject.biblemap.put("b16_5_6", new trsuperobject(16,5,6,"Onların bu dertlerini, yakınmalarını duyunca çok öfkelendim."));
trsuperobject.biblemap.put("b16_5_7", new trsuperobject(16,5,7,"Düşününce soylularla yetkilileri suçlu buldum. Onlara, ‹‹Kardeşlerinizden faiz alıyorsunuz!›› dedim. Onlara karşı herkesi bir araya topladım. Sonra şöyle dedim:"));
trsuperobject.biblemap.put("b16_5_8", new trsuperobject(16,5,8,"‹‹Biz yabancılara satılan Yahudi kardeşlerimizi elimizden geldiğince geri almaya çalışırken siz kardeşlerinizi satıyorsunuz. Yine bize satılsınlar diye mi?›› Sustular, söyleyecek söz bulamadılar."));
trsuperobject.biblemap.put("b16_5_9", new trsuperobject(16,5,9,"Sonra, ‹‹Yaptığınız doğru değil›› dedim, ‹‹Düşmanlarımız olan öteki ulusların aşağılamalarından kaçınmak için Tanrı korkusuyla yaşamanız gerekmez mi?"));
trsuperobject.biblemap.put("b16_5_10", new trsuperobject(16,5,10,"Kardeşlerim, adamlarım ve ben ödünç olarak halka para ve buğday veriyoruz. Lütfen faiz almaktan vazgeçelim!"));
trsuperobject.biblemap.put("b16_5_11", new trsuperobject(16,5,11,"Tarlalarını, bağlarını, zeytinliklerini, evlerini onlara hemen geri verin. Bir de faiz olarak aldığınız gümüşün, buğdayın, yeni şarabın, zeytinyağının yüzde birini verin.››"));
trsuperobject.biblemap.put("b16_5_12", new trsuperobject(16,5,12,"‹‹Veririz›› dediler, ‹‹Artık onlardan hiçbir şey istemeyeceğiz. Ne diyorsan öyle yapacağız.›› Kâhinleri çağırdım ve yetkililere kâhinlerin önünde verdikleri sözü tutacaklarına ilişkin ant içirdim."));
trsuperobject.biblemap.put("b16_5_13", new trsuperobject(16,5,13,"Sonra eteğimi silktim ve dedim ki, ‹‹Kim verdiği sözü tutmazsa, Tanrı da onu böyle silksin; malını mülkünü elinden alsın; tamtakır bıraksın.›› Herkes buna, ‹‹Amin›› dedi ve RABbe övgüler sundu. Ve sözlerini tuttular."));
trsuperobject.biblemap.put("b16_5_14", new trsuperobject(16,5,14,"Yahudada valilik yaptığım on iki yıl boyunca, ilk atandığım günden son güne kadar, Artahşastanın krallığının yirminci yılından otuz ikinci yılına dek, ne ben, ne kardeşlerim valiliğe ayrılan yiyecek bütçesine dokunmadık."));
trsuperobject.biblemap.put("b16_5_15", new trsuperobject(16,5,15,"Benden önce görev yapan valiler halka yük oldular. Onlardan kırk şekel gümüşün yanısıra yiyecek ve şarap da aldılar. Uşakları bile halkı ezdi. Ama ben Tanrıdan korktuğum için böyle davranmadım."));
trsuperobject.biblemap.put("b16_5_16", new trsuperobject(16,5,16,"Surların onarımını sürdürdüm. Adamlarımın hepsi işin başında durdu. Bir tarla bile satın almadık."));
trsuperobject.biblemap.put("b16_5_17", new trsuperobject(16,5,17,"Çevremizdeki uluslardan bize gelenlerin dışında Yahudilerden ve yetkililerden yüz elli kişi soframa otururdu."));
trsuperobject.biblemap.put("b16_5_18", new trsuperobject(16,5,18,"Benim için her gün bir boğa, altı seçme koyun, tavuklar kesilir, on günde bir de her türden bolca şarap hazırlanırdı. Bütün bunlara karşın valiliğin yiyecek bütçesine dokunmadım. Çünkü halk ağır yük altındaydı."));
trsuperobject.biblemap.put("b16_5_19", new trsuperobject(16,5,19,"Ey Tanrım, bu halk uğruna yaptıklarım için beni iyilikle an."));
trsuperobject.biblemap.put("b16_6_1", new trsuperobject(16,6,1,"Surları onardığım, gediklerini kapadığım haberi Sanballata, Toviyaya, Arap Geşeme ve öbür düşmanlarımıza ulaştı. O sırada kapı kanatlarını henüz takmamıştım."));
trsuperobject.biblemap.put("b16_6_2", new trsuperobject(16,6,2,"Sanballat ile Geşem bana haber göndererek, ‹‹Gel, Ono Ovasındaki köylerden birinde buluşalım›› dediler. Bana kötülük yapmayı düşünüyorlardı."));
trsuperobject.biblemap.put("b16_6_3", new trsuperobject(16,6,3,"Onlara haberciler göndererek, ‹‹Büyük bir iş yapıyorum, gelemem›› dedim, ‹‹Yanınıza gelirsem işi bırakmış olurum; niçin iş dursun?››"));
trsuperobject.biblemap.put("b16_6_4", new trsuperobject(16,6,4,"Bana dört kez bu haberi gönderdiler, ben de hep aynı yanıtı verdim."));
trsuperobject.biblemap.put("b16_6_5", new trsuperobject(16,6,5,"Sanballat beşinci kez aynı öneriyle habercisini gönderdi. Habercinin elinde açık bir mektup vardı."));
trsuperobject.biblemap.put("b16_6_6", new trsuperobject(16,6,6,"İçinde şunlar yazılıydı: ‹‹Çevredeki uluslar arasında Geşemin de doğruladığı bir söylenti var. Sen ve Yahudiler ayaklanmayı düşündüğünüz için surları onarıyormuşsunuz. Anlatılanlara göre kral olmak üzeresin."));
trsuperobject.biblemap.put("b16_6_7", new trsuperobject(16,6,7,"Yahuda Kralı olduğunu Yeruşalime duyurmak için peygamberler bile atamışsın. Bütün bunlar kralın kulağına gidecek. Onun için, gel de görüşelim.››"));
trsuperobject.biblemap.put("b16_6_8", new trsuperobject(16,6,8,"Ona şu yanıtı gönderdim: ‹‹Söylediklerinin hiçbiri doğru değil. Hepsini kendin uyduruyorsun.››"));
trsuperobject.biblemap.put("b16_6_9", new trsuperobject(16,6,9,"Hepsi bizi korkutmaya çalışıyorlardı. ‹‹İşi bırakacaklar, onarım duracak›› diye düşünüyorlardı. Ama ben, ‹‹Tanrım, ellerime güç ver›› diye dua ettim."));
trsuperobject.biblemap.put("b16_6_10", new trsuperobject(16,6,10,"Bir gün Mehetavel oğlu Delaya oğlu Şemayanın evine gittim. Evine kapanmıştı. Bana, ‹‹Tanrının evinde, tapınakta buluşalım›› dedi, ‹‹Tapınağın kapılarını kapatalım, çünkü seni öldürmeye gelecekler. Gece seni öldürmeye gelecekler.››"));
trsuperobject.biblemap.put("b16_6_11", new trsuperobject(16,6,11,"Ona, ‹‹Ben kaçacak adam değilim›› dedim, ‹‹Benim gibi biri canını kurtarmak için tapınağa sığınır mı? Gelmeyeceğim.››"));
trsuperobject.biblemap.put("b16_6_12", new trsuperobject(16,6,12,"Anladım ki, onu Tanrı göndermemiş. Bu sözleri bir peygamber gibi, benim kötülüğüm için söylemişti. Toviya ile Sanballat onu satın almışlardı."));
trsuperobject.biblemap.put("b16_6_13", new trsuperobject(16,6,13,"Bu yolla gözümü korkutup bana günah işleteceklerini düşünüyorlardı. Böylece beni kötülemek için ellerine fırsat geçmiş olacaktı."));
trsuperobject.biblemap.put("b16_6_14", new trsuperobject(16,6,14,"‹‹Ey Tanrım, Toviyayla Sanballatın yaptığı kötülüğü unutma›› diye dua ettim, ‹‹Beni korkutmak isteyen kadın peygamber Noadyayla öbür peygamberlerin yaptıklarını da unutma.››"));
trsuperobject.biblemap.put("b16_6_15", new trsuperobject(16,6,15,"Surların onarımı elli iki günde, yirmi beş Elulda bitti."));
trsuperobject.biblemap.put("b16_6_16", new trsuperobject(16,6,16,"Bütün düşmanlarımız bunu duydu, çevremizdeki ulusları korku sardı. Böylece düşmanlarımız özgüvenlerini büsbütün yitirdiler. Çünkü bu işi Tanrımızın yardımıyla başardığımızı anladılar. kapsardı."));
trsuperobject.biblemap.put("b16_6_17", new trsuperobject(16,6,17,"O günlerde Yahuda soylularıyla Toviya sık sık yazışıyorlardı."));
trsuperobject.biblemap.put("b16_6_18", new trsuperobject(16,6,18,"Birçok Yahudalı Toviyaya bağlı kalacağına ant içmişti. Çünkü Toviya, Arah oğlu Şekanyanın damadıydı. Oğlu Yehohanan da Berekya oğlu Meşullamın kızını almıştı."));
trsuperobject.biblemap.put("b16_6_19", new trsuperobject(16,6,19,"Soylular Toviya'nın iyiliklerini bana anlatıyor, benim söylediklerimi de ona iletiyorlardı. Toviya beni yıldırmak için sürekli mektup gönderiyordu."));
trsuperobject.biblemap.put("b16_7_1", new trsuperobject(16,7,1,"Surların onarımı bitip kapılar yerine takıldıktan sonra, kapı nöbetçileri, ezgiciler ve Levililer göreve atandı."));
trsuperobject.biblemap.put("b16_7_2", new trsuperobject(16,7,2,"Kardeşim Hananiyle kale komutanı Hananyayı Yeruşalime yönetici atadım. Hananya güvenilir bir kişiydi. Çoğu insandan daha çok Tanrıdan korkardı."));
trsuperobject.biblemap.put("b16_7_3", new trsuperobject(16,7,3,"Onlara, ‹‹Güneş ortalığı ısıtıncaya kadar Yeruşalim kapıları açılmasın›› dedim, ‹‹Kapı nöbetçileri görev başındayken kapıları kapalı tutsunlar. Kapıları siz sürgüleyin ve Yeruşalimde oturanlara nöbet görevi verin. Bazıları bu görevi yapsın, bazıları da evlerinin çevresinde nöbet tutsun.››"));
trsuperobject.biblemap.put("b16_7_4", new trsuperobject(16,7,4,"Yeruşalim geniş, büyük bir kentti, ama nüfusu azdı. İçindeki evler henüz onarılmamıştı."));
