trsuperobject.biblemap.put("b16_7_6", new trsuperobject(16,7,6,"Babil Kralı Nebukadnessarın sürgün ettiği insanlar yaşadıkları ilden Yeruşalim ve Yahudadaki kendi kentlerine döndü."));
