trsuperobject.biblemap.put("b2_8_20", new trsuperobject(2,8,20,"RAB Musaya şöyle dedi: ‹‹Sabah erkenden kalk, firavun ırmağa inerken onu karşıla ve şöyle de: ‹RAB diyor ki, halkımı salıver, bana tapsınlar."));
trsuperobject.biblemap.put("b2_8_21", new trsuperobject(2,8,21,"Halkımı salıvermezsen senin, görevlilerinin, halkının, evlerinin üzerine atsineği yağdıracağım. Mısırlıların evleri ve üzerinde yaşadıkları topraklar atsinekleriyle dolup taşacak."));
trsuperobject.biblemap.put("b2_8_22", new trsuperobject(2,8,22,"‹‹Ama o gün halkımın yaşadığı Goşen bölgesinde farklı davranacağım. Orada atsineği olmayacak. Böylece bileceksin ki, bu ülkede RAB benim."));
trsuperobject.biblemap.put("b2_8_23", new trsuperobject(2,8,23,"Kendi halkımla senin halkın arasına fark koyacağım. Yarın bu belirti gerçekleşecek.››"));
trsuperobject.biblemap.put("b2_8_24", new trsuperobject(2,8,24,"RAB dediğini yaptı. Firavunun sarayına, görevlilerinin evlerine sürü sürü atsineği gönderdi. Mısır atsineği yüzünden baştan sona harap oldu."));
trsuperobject.biblemap.put("b2_8_25", new trsuperobject(2,8,25,"Firavun Musayla Harunu çağırtıp, ‹‹Gidin, bu ülkede Tanrınıza kurban kesin›› dedi."));
trsuperobject.biblemap.put("b2_8_26", new trsuperobject(2,8,26,"Musa, ‹‹Bu doğru olmaz›› diye karşılık verdi, ‹‹Çünkü Mısırlılar Tanrımız RABbe kurban kesmeyi iğrenç sayıyorlar. İğrenç saydıkları bu şeyi gözlerinin önünde yaparsak bizi taşlamazlar mı?"));
trsuperobject.biblemap.put("b2_8_27", new trsuperobject(2,8,27,"Tanrımız RABbe kurban kesmek için, bize buyurduğu gibi üç gün çölde yol almalıyız.››"));
trsuperobject.biblemap.put("b2_8_28", new trsuperobject(2,8,28,"Firavun, ‹‹Çölde Tanrınız RABbe kurban kesmeniz için sizi salıveriyorum›› dedi, ‹‹Yalnız çok uzağa gitmeyeceksiniz. Şimdi benim için dua edin.››"));
trsuperobject.biblemap.put("b2_8_29", new trsuperobject(2,8,29,"Musa, ‹‹Yarın atsineklerini firavunun, görevlilerinin, halkının üzerinden uzaklaştırsın diye, yanından ayrılır ayrılmaz RABbe dua edeceğim›› dedi, ‹‹Yalnız firavun RABbe kurban kesmek için halkın gitmesini önleyerek bizi yine aldatmamalı.››"));
trsuperobject.biblemap.put("b2_8_30", new trsuperobject(2,8,30,"Musa firavunun yanından çıkıp RABbe dua etti."));
trsuperobject.biblemap.put("b2_8_31", new trsuperobject(2,8,31,"RAB Musanın isteğini yerine getirdi; firavunun, görevlilerinin, halkının üzerinden atsineklerini uzaklaştırdı. Tek sinek kalmadı."));
trsuperobject.biblemap.put("b2_8_32", new trsuperobject(2,8,32,"Öyleyken, firavun bir kez daha inatçılık etti ve halkı salıvermedi."));
trsuperobject.biblemap.put("b2_9_2", new trsuperobject(2,9,2,"Salıvermeyi reddeder, onları tutmakta diretirsen,"));
trsuperobject.biblemap.put("b2_9_6", new trsuperobject(2,9,6,"Ertesi gün RAB dediğini yaptı: Mısırlıların hayvanları büyük çapta öldü. Ama İsraillilerin hayvanlarından hiçbiri ölmedi."));
trsuperobject.biblemap.put("b2_9_7", new trsuperobject(2,9,7,"Firavun adam gönderdi, İsraillilerin bir tek hayvanının bile ölmediğini öğrendi. Öyleyken, inat etti ve halkı salıvermedi."));
trsuperobject.biblemap.put("b2_9_9", new trsuperobject(2,9,9,"Kurum bütün Mısırın üzerinde ince bir toza dönüşecek; ülkenin her yanındaki insanların, hayvanların bedenlerinde irinli çıbanlar çıkacak.››"));
trsuperobject.biblemap.put("b2_9_10", new trsuperobject(2,9,10,"Böylece Musayla Harun ocak kurumu alıp firavunun önünde durdular. Musa kurumu göğe doğru savurdu. İnsanlarda ve hayvanlarda irinli çıbanlar çıktı."));
trsuperobject.biblemap.put("b2_9_11", new trsuperobject(2,9,11,"Büyücüler çıbandan ötürü Musanın karşısında duramaz oldular. Çünkü bütün Mısırlılarda olduğu gibi onlarda da çıbanlar çıkmıştı."));
trsuperobject.biblemap.put("b2_9_12", new trsuperobject(2,9,12,"RAB firavunu inatçı yaptı, RABbin Musaya söylediği gibi, firavun Musayla Harunu dinlemedi."));
trsuperobject.biblemap.put("b2_9_13", new trsuperobject(2,9,13,"RAB Musaya şöyle dedi: ‹‹Sabah erkenden kalkıp firavunun huzuruna çık, de ki, ‹İbranilerin Tanrısı RAB şöyle diyor: Halkımı salıver, bana tapsınlar."));
trsuperobject.biblemap.put("b2_9_14", new trsuperobject(2,9,14,"Yoksa bu kez senin, görevlilerinin, halkının üzerine bütün belalarımı yağdıracağım. Öyle ki, bu dünyada benim gibisi olmadığını öğrenesin."));
trsuperobject.biblemap.put("b2_9_15", new trsuperobject(2,9,15,"Çünkü elimi kaldırıp seni ve halkını salgın hastalıkla vurmuş olsaydım, yeryüzünden silinmiş olurdun."));
trsuperobject.biblemap.put("b2_9_16", new trsuperobject(2,9,16,"Gücümü sana göstermek, adımı bütün dünyaya tanıtmak için seni ayakta tuttum."));
trsuperobject.biblemap.put("b2_9_17", new trsuperobject(2,9,17,"Hâlâ halkımı salıvermiyor, onlara üstünlük taslıyorsun."));
trsuperobject.biblemap.put("b2_9_18", new trsuperobject(2,9,18,"Bu yüzden, yarın bu saatlerde Mısıra tarihinde görülmemiş ağır bir dolu yağdıracağım."));
trsuperobject.biblemap.put("b2_9_19", new trsuperobject(2,9,19,"Şimdi buyruk ver, hayvanların ve kırda neyin varsa hepsi sığınaklara konsun. Dolu yağınca, eve getirilmeyen, kırda kalan bütün insanlarla hayvanlar ölecek.››"));
trsuperobject.biblemap.put("b2_9_20", new trsuperobject(2,9,20,"Firavunun görevlileri arasında RABbin uyarısından korkanlar köleleriyle hayvanlarını çabucak evlerine getirdiler."));
trsuperobject.biblemap.put("b2_9_21", new trsuperobject(2,9,21,"RABbin uyarısını önemsemeyenler ise köleleriyle hayvanlarını tarlada bıraktı."));
trsuperobject.biblemap.put("b2_9_22", new trsuperobject(2,9,22,"RAB Musaya, ‹‹Elini göğe doğru uzat›› dedi, ‹‹Mısırın her yerine, insanların, hayvanların, kırdaki bütün bitkilerin üzerine dolu yağsın.››"));
trsuperobject.biblemap.put("b2_9_23", new trsuperobject(2,9,23,"Musa değneğini göğe doğru uzatınca RAB gök gürlemeleri ve dolu gönderdi. Yıldırım düştü. RAB Mısıra dolu yağdırdı."));
trsuperobject.biblemap.put("b2_9_24", new trsuperobject(2,9,24,"Şiddetli dolu yağıyor, sürekli şimşek çakıyordu. Mısır Mısır olalı böylesi bir dolu görmemişti."));
trsuperobject.biblemap.put("b2_9_25", new trsuperobject(2,9,25,"Dolu Mısırda insandan hayvana dek kırdaki her şeyi, bütün bitkileri mahvetti, bütün ağaçları kırdı."));
trsuperobject.biblemap.put("b2_9_26", new trsuperobject(2,9,26,"Yalnız İsraillilerin yaşadığı Goşen bölgesine dolu düşmedi."));
trsuperobject.biblemap.put("b2_9_27", new trsuperobject(2,9,27,"Firavun Musayla Harunu çağırtarak, ‹‹Bu kez günah işledim›› dedi, ‹‹RAB haklı, ben ve halkım haksızız."));
trsuperobject.biblemap.put("b2_9_28", new trsuperobject(2,9,28,"RABbe dua edin, yeter bu gök gürlemeleri ve dolu. Sizi salıvereceğim, artık burada kalmayacaksınız.››"));
trsuperobject.biblemap.put("b2_9_29", new trsuperobject(2,9,29,"Musa, ‹‹Kentten çıkınca, ellerimi RABbe uzatacağım›› dedi, ‹‹Gök gürlemeleri duracak, artık dolu yağmayacak. Böylece dünyanın RABbe ait olduğunu bileceksin."));
trsuperobject.biblemap.put("b2_9_30", new trsuperobject(2,9,30,"Ama biliyorum, sen ve görevlilerin RAB Tanrıdan hâlâ korkmuyorsunuz.››"));
trsuperobject.biblemap.put("b2_9_31", new trsuperobject(2,9,31,"Keten ve arpa mahvolmuştu; çünkü arpa başak vermiş, keten çiçek açmıştı."));
trsuperobject.biblemap.put("b2_9_32", new trsuperobject(2,9,32,"Ama buğday ve kızıl buğday henüz bitmediği için zarar görmemişti."));
trsuperobject.biblemap.put("b2_9_33", new trsuperobject(2,9,33,"Musa firavunun yanından ayrılıp kentten çıktı. Ellerini RABbe uzattı. Gök gürlemesi ve dolu durdu, yağmur dindi."));
trsuperobject.biblemap.put("b2_9_34", new trsuperobject(2,9,34,"Firavun yağmurun, dolunun, gök gürlemesinin kesildiğini görünce, yine günah işledi. Hem kendisi, hem görevlileri inat ettiler."));
trsuperobject.biblemap.put("b2_9_35", new trsuperobject(2,9,35,"RAB'bin Musa aracılığıyla söylediği gibi, firavun inat ederek İsrailliler'i salıvermedi."));
trsuperobject.biblemap.put("b2_10_2", new trsuperobject(2,10,2,"Mısırla nasıl alay ettiğimi, aralarında gösterdiğim belirtileri sen de çocuklarına, torunlarına anlat ki, benim RAB olduğumu bilesiniz.››"));
trsuperobject.biblemap.put("b2_10_3", new trsuperobject(2,10,3,"Musayla Harun firavunun yanına varıp şöyle dediler: ‹‹İbranilerin Tanrısı RAB diyor ki, ‹Ne zamana dek alçakgönüllü olmayı reddedeceksin? Halkımı salıver, bana tapsınlar."));
trsuperobject.biblemap.put("b2_10_4", new trsuperobject(2,10,4,"Halkımı salıvermeyi reddedersen, yarın ülkene çekirgeler göndereceğim."));
trsuperobject.biblemap.put("b2_10_5", new trsuperobject(2,10,5,"Yeryüzünü öylesine kaplayacaklar ki, toprak görünmez olacak. Doludan kurtulan ürünlerinizi, kırda biten bütün ağaçlarınızı yiyecekler."));
trsuperobject.biblemap.put("b2_10_6", new trsuperobject(2,10,6,"Evlerine, bütün görevlilerinin, bütün Mısırlıların evlerine çekirge dolacak. Ne babaların, ne ataların ömürlerince böylesini görmediler.›› Sonra Musa dönüp firavunun yanından ayrıldı."));
trsuperobject.biblemap.put("b2_10_7", new trsuperobject(2,10,7,"Görevlileri firavuna, ‹‹Ne zamana dek bu adam bize tuzak kuracak?›› dediler, ‹‹Bırak gitsinler, Tanrıları RABbe tapsınlar. Mısır harap oldu, hâlâ anlamıyor musun?››"));
trsuperobject.biblemap.put("b2_10_8", new trsuperobject(2,10,8,"Böylece, Musayla Harunu firavunun yanına geri getirdiler. Firavun, ‹‹Gidin, Tanrınız RABbe tapın›› dedi, ‹‹Ama kimler gidecek?››"));
trsuperobject.biblemap.put("b2_10_9", new trsuperobject(2,10,9,"Musa, ‹‹Genç, yaşlı hep birlikte gideceğiz›› dedi, ‹‹Oğullarımızı, kızlarımızı, davarlarımızı, sığırlarımızı yanımıza alacağız. Çünkü RABbe bayram yapmalıyız.››"));
trsuperobject.biblemap.put("b2_10_10", new trsuperobject(2,10,10,"Firavun, ‹‹Alın çoluk çocuğunuzu, gidin gidebilirseniz, RAB yardımcınız olsun!›› dedi, ‹‹Bakın, kötü niyetiniz ne kadar açık."));
trsuperobject.biblemap.put("b2_10_11", new trsuperobject(2,10,11,"Olmaz. Yalnız erkekler gidip RABbe tapsın. Zaten istediğiniz de bu.›› Sonra Musayla Harun firavunun yanından kovuldular."));
trsuperobject.biblemap.put("b2_10_12", new trsuperobject(2,10,12,"RAB Musaya, ‹‹Elini Mısırın üzerine uzat›› dedi, ‹‹Çekirge yağsın; ülkenin bütün bitkilerini, doludan kurtulan her şeyi yesinler.››"));
trsuperobject.biblemap.put("b2_10_13", new trsuperobject(2,10,13,"Musa değneğini Mısırın üzerine uzattı. Bütün o gün ve gece RAB ülkede doğu rüzgarı estirdi. Sabah olunca da doğu rüzgarı çekirgeleri getirdi."));
trsuperobject.biblemap.put("b2_10_14", new trsuperobject(2,10,14,"Mısırın üzerinde uçuşan çekirgeler ülkeyi boydan boya kapladı. Öyle çoktular ki, böylesi hiçbir zaman görülmedi, kuşaklar boyu da görülmeyecek."));
trsuperobject.biblemap.put("b2_10_15", new trsuperobject(2,10,15,"Toprağın üzerini öyle kapladılar ki, ülke kapkara kesildi. Bütün bitkileri, dolunun zarar vermediği ağaçlarda kalan meyvelerin hepsini yediler. Mısırın hiçbir yerinde, ne ağaçlarda, ne de kırdaki bitkilerde yeşillik kalmadı."));
trsuperobject.biblemap.put("b2_10_16", new trsuperobject(2,10,16,"Firavun acele Musayla Harunu çağırttı. ‹‹Tanrınız RABbe ve size karşı günah işledim›› dedi,"));
trsuperobject.biblemap.put("b2_10_17", new trsuperobject(2,10,17,"‹‹Lütfen bir kez daha günahımı bağışlayın ve Tanrınız RABbe dua edin; bu ölümcül belayı üzerimden uzaklaştırsın.››"));
trsuperobject.biblemap.put("b2_10_18", new trsuperobject(2,10,18,"Musa firavunun yanından çıkıp RABbe dua etti."));
trsuperobject.biblemap.put("b2_10_19", new trsuperobject(2,10,19,"RAB rüzgarı çok şiddetli batı rüzgarına döndürdü. Rüzgar çekirgeleri sürükleyip Kızıldenize döktü. Mısırda tek çekirge kalmadı."));
trsuperobject.biblemap.put("b2_10_20", new trsuperobject(2,10,20,"Ama RAB firavunu inatçı yaptı. Firavun İsraillileri salıvermedi."));
trsuperobject.biblemap.put("b2_10_21", new trsuperobject(2,10,21,"RAB Musaya, ‹‹Elini göğe doğru uzat›› dedi, ‹‹Mısırı hissedilebilir bir karanlık kaplasın.››"));
trsuperobject.biblemap.put("b2_10_22", new trsuperobject(2,10,22,"Musa elini göğe doğru uzattı, Mısır üç gün koyu karanlığa gömüldü."));
trsuperobject.biblemap.put("b2_10_23", new trsuperobject(2,10,23,"Üç gün boyunca kimse kimseyi göremez, yerinden kımıldayamaz oldu. Yalnız İsraillilerin yaşadığı yerler aydınlıktı."));
trsuperobject.biblemap.put("b2_10_24", new trsuperobject(2,10,24,"Firavun Musayı çağırttı. ‹‹Gidin, RABbe tapın›› dedi, ‹‹Yalnız davarlarınızla sığırlarınız alıkonacak. Çoluk çocuğunuz sizinle birlikte gidebilir.››"));
trsuperobject.biblemap.put("b2_10_25", new trsuperobject(2,10,25,"Musa, ‹‹Ama Tanrımız RABbe kurban kesmemiz için bize kurbanlık ve yakmalık sunular da vermelisin›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b2_10_26", new trsuperobject(2,10,26,"‹‹Hayvanlarımızı da yanımıza almalıyız. Bir tırnak bile kalmamalı burada. Çünkü Tanrımız RABbe tapmak için bazı hayvanları kullanacağız. Oraya varmadıkça hangi hayvanları RABbe sunacağımızı bilemeyiz.››"));
trsuperobject.biblemap.put("b2_10_27", new trsuperobject(2,10,27,"Ancak RAB firavunu inatçı yaptı, firavun İsraillileri salıvermeye yanaşmadı."));
trsuperobject.biblemap.put("b2_10_28", new trsuperobject(2,10,28,"Musaya, ‹‹Git başımdan›› dedi, ‹‹Sakın bir daha karşıma çıkma. Yüzümü gördüğün gün ölürsün.››"));
trsuperobject.biblemap.put("b2_10_29", new trsuperobject(2,10,29,"Musa, ‹‹Dediğin gibi olsun›› diye karşılık verdi, ‹‹Bir daha yüzünü görmeyeceğim.››"));
trsuperobject.biblemap.put("b2_11_2", new trsuperobject(2,11,2,"Halkına söyle, kadın erkek herkes komşusundan altın, gümüş eşya istesin.››"));
trsuperobject.biblemap.put("b2_11_4", new trsuperobject(2,11,4,"Musa firavuna şöyle dedi: ‹‹RAB diyor ki, ‹Gece yarısı Mısırı boydan boya geçeceğim."));
trsuperobject.biblemap.put("b2_11_5", new trsuperobject(2,11,5,"Tahtında oturan firavunun ilk çocuğundan, değirmendeki kadın kölenin ilk çocuğuna kadar, hayvanlar dahil Mısırdaki bütün ilk doğanlar ölecek."));
trsuperobject.biblemap.put("b2_11_6", new trsuperobject(2,11,6,"Bütün Mısırda benzeri ne görülmüş, ne de görülecek büyük bir feryat kopacak."));
trsuperobject.biblemap.put("b2_11_7", new trsuperobject(2,11,7,"İsraillilere ya da hayvanlarına bir köpek bile havlamayacak.› O zaman RABbin İsraillilerle Mısırlılara nasıl farklı davrandığını anlayacaksınız."));
trsuperobject.biblemap.put("b2_11_8", new trsuperobject(2,11,8,"Bu görevlilerinin hepsi gelip önümde eğilecek, ‹Sen ve seni izleyenler, gidin!› diyecekler. Ondan sonra gideceğim.›› Musa firavunun yanından büyük bir öfkeyle ayrıldı."));
trsuperobject.biblemap.put("b2_11_10", new trsuperobject(2,11,10,"Musa'yla Harun firavunun önünde bütün bu şaşılası işleri yaptılar. Ama RAB firavunu inatçı yaptı. Firavun İsrailliler'i ülkesinden salıvermedi."));
trsuperobject.biblemap.put("b2_12_1-2", new trsuperobject(2,12,1-2,"RAB Mısırda Musayla Haruna, ‹‹Bu ay sizin için ilk ay, yılın ilk ayı olacak›› dedi,"));
trsuperobject.biblemap.put("b2_12_3", new trsuperobject(2,12,3,"‹‹Bütün İsrail topluluğuna bildirin: Bu ayın onunda herkes ailesine göre kendi ev halkına birer kuzu alacak."));
trsuperobject.biblemap.put("b2_12_4", new trsuperobject(2,12,4,"Eğer bir kuzu bir aileye çok geliyorsa, aile bireylerinin sayısı ve herkesin yiyeceği miktar hesaplanacak ve aile kuzuyu en yakın komşusuyla paylaşabilecek."));
trsuperobject.biblemap.put("b2_12_5", new trsuperobject(2,12,5,"Koyun ya da keçilerden seçeceğiniz hayvan kusursuz, erkek ve bir yaşında olmalı."));
trsuperobject.biblemap.put("b2_12_6", new trsuperobject(2,12,6,"Ayın on dördüne kadar ona bakacaksınız. O akşamüstü bütün İsrail topluluğu hayvanları boğazlayacak."));
trsuperobject.biblemap.put("b2_12_7", new trsuperobject(2,12,7,"Hayvanın kanını alıp, etin yeneceği evin yan ve üst kapı sövelerine sürecekler."));
trsuperobject.biblemap.put("b2_12_8", new trsuperobject(2,12,8,"O gece ateşte kızartılmış et mayasız ekmek ve acı otlarla yenmelidir."));
trsuperobject.biblemap.put("b2_12_9", new trsuperobject(2,12,9,"Eti çiğ veya haşlanmış olarak değil, başı, bacakları, bağırsakları ve işkembesiyle birlikte kızartarak yiyeceksiniz."));
trsuperobject.biblemap.put("b2_12_10", new trsuperobject(2,12,10,"Sabaha kadar bitirmelisiniz. Artakalan olursa, sabah ateşte yakacaksınız."));
trsuperobject.biblemap.put("b2_12_11", new trsuperobject(2,12,11,"Eti şöyle yemelisiniz: Beliniz kuşanmış, çarıklarınız ayağınızda, değneğiniz elinizde olmalı. Eti çabuk yemelisiniz. Bu RABbin Fısıh kurbanıdır. anlamına da gelir."));
trsuperobject.biblemap.put("b2_12_12", new trsuperobject(2,12,12,"‹‹O gece Mısırdan geçeceğim. Hem insanların hem de hayvanların bütün ilk doğanlarını öldüreceğim. Mısırın bütün ilahlarını yargılayacağım. Ben RABbim."));
trsuperobject.biblemap.put("b2_12_13", new trsuperobject(2,12,13,"Bulunduğunuz evlerin üzerindeki kan sizin için belirti olacak. Kanı görünce üzerinizden geçeceğim. Mısırı cezalandırırken ölüm saçan size hiçbir zarar vermeyecek."));
trsuperobject.biblemap.put("b2_12_14", new trsuperobject(2,12,14,"Bu gün sizin için anma günü olacak. Bu günü RABbin bayramı olarak kutlayacaksınız. Gelecek kuşaklarınız boyunca sürekli bir kural olarak bu günü kutlayacaksınız.›› kutlanmaktadır. Fısıh sözcüğü ‹‹Geçmek›› anlamına gelir."));
trsuperobject.biblemap.put("b2_12_15", new trsuperobject(2,12,15,"‹‹Yedi gün mayasız ekmek yiyeceksiniz. İlk gün evlerinizden mayayı kaldıracaksınız. Kim bu yedi gün içinde mayalı bir şey yerse, İsrailden atılacaktır."));
trsuperobject.biblemap.put("b2_12_16", new trsuperobject(2,12,16,"Birinci ve yedinci günler kutsal toplantı yapacaksınız. O günler hiçbir iş yapılmayacak. Herkes yalnız kendi yiyeceğini hazırlayacak."));
trsuperobject.biblemap.put("b2_12_17", new trsuperobject(2,12,17,"Mayasız Ekmek Bayramını kutlayacaksınız, çünkü sizi ordular halinde o gün Mısırdan çıkardım. Bu günü kalıcı bir kural olarak kuşaklarınız boyunca kutlayacaksınız."));
trsuperobject.biblemap.put("b2_12_18", new trsuperobject(2,12,18,"Birinci ayın on dördüncü gününün akşamından yirmi birinci gününün akşamına kadar mayasız ekmek yiyeceksiniz."));
trsuperobject.biblemap.put("b2_12_19", new trsuperobject(2,12,19,"Evlerinizde yedi gün maya bulunmayacak. Mayalı bir şey yiyen yerli yabancı herkes İsrail topluluğundan atılacaktır."));
trsuperobject.biblemap.put("b2_12_20", new trsuperobject(2,12,20,"Mayalı bir şey yemeyeceksiniz. Yaşadığınız her yerde mayasız ekmek yiyeceksiniz.››"));
trsuperobject.biblemap.put("b2_12_21", new trsuperobject(2,12,21,"Musa İsrailin bütün ileri gelenlerini çağırtarak onlara şöyle dedi: ‹‹Hemen gidin, aileleriniz için kendinize davarlar seçip Fısıh kurbanı olarak boğazlayın."));
trsuperobject.biblemap.put("b2_12_22", new trsuperobject(2,12,22,"Bir demet mercanköşkotu alın, leğendeki kana batırıp kanı kapılarınızın yan ve üst sövelerine sürün. Sabaha kadar kimse evinden çıkmasın."));
trsuperobject.biblemap.put("b2_12_23", new trsuperobject(2,12,23,"RAB Mısırlıları öldürmek için gelecek, kapılarınızın yan ve üst sövelerindeki kanı görünce üzerinden geçecek, ölüm saçanın evlerinize girip sizi öldürmesine izin vermeyecek."));
trsuperobject.biblemap.put("b2_12_24", new trsuperobject(2,12,24,"‹‹Sen ve çocukların kalıcı bir kural olarak bu olayı kutlayacaksınız."));
trsuperobject.biblemap.put("b2_12_25", new trsuperobject(2,12,25,"RABbin size söz verdiği topraklara girdiğiniz zaman bu töreye uyacaksınız."));
trsuperobject.biblemap.put("b2_12_26", new trsuperobject(2,12,26,"Çocuklarınız size, ‹Bu törenin anlamı nedir?› diye sorduklarında,"));
trsuperobject.biblemap.put("b2_12_27", new trsuperobject(2,12,27,"‹Bu RABbin Fısıh kurbanıdır› diyeceksiniz, ‹Çünkü RAB Mısırlıları öldürürken evlerimizin üzerinden geçerek bizi bağışladı.›› İsrailliler eğilip tapındılar."));
trsuperobject.biblemap.put("b2_12_28", new trsuperobject(2,12,28,"Sonra gidip RABbin Musayla Haruna verdiği buyruğu eksiksiz uyguladılar."));
trsuperobject.biblemap.put("b2_12_29", new trsuperobject(2,12,29,"Gece yarısı RAB tahtında oturan firavunun ilk çocuğundan zindandaki tutsağın ilk çocuğuna kadar Mısırdaki bütün insanların ve hayvanların ilk doğanlarını öldürdü."));
trsuperobject.biblemap.put("b2_12_30", new trsuperobject(2,12,30,"O gece firavunla görevlileri ve bütün Mısırlılar uyandı. Büyük feryat koptu. Çünkü ölüsü olmayan ev yoktu."));
trsuperobject.biblemap.put("b2_12_31", new trsuperobject(2,12,31,"Aynı gece firavun Musayla Harunu çağırttı ve, ‹‹Kalkın!›› dedi, ‹‹Siz ve İsrailliler halkımın arasından çıkıp gidin, istediğiniz gibi RABbe tapın."));
trsuperobject.biblemap.put("b2_12_32", new trsuperobject(2,12,32,"Dediğiniz gibi davarlarınızı, sığırlarınızı da alın götürün. Beni de kutsayın!››"));
trsuperobject.biblemap.put("b2_12_33", new trsuperobject(2,12,33,"İsraillilerin ülkeyi hemen terk etmesi için Mısırlılar diretti. ‹‹Yoksa hepimiz öleceğiz!›› diyorlardı."));
trsuperobject.biblemap.put("b2_12_34", new trsuperobject(2,12,34,"Böylece halk mayası henüz katılmamış hamurunu aldı, giysilere sarılı hamur teknelerini omuzlarında taşıdı."));
trsuperobject.biblemap.put("b2_12_35", new trsuperobject(2,12,35,"İsrailliler Musanın dediğini yapmış, Mısırlılardan altın, gümüş eşya ve giysi istemişlerdi."));
trsuperobject.biblemap.put("b2_12_36", new trsuperobject(2,12,36,"RAB İsraillilerin Mısırlıların gözünde lütuf bulmasını sağladı. Mısırlılar onlara istediklerini verdiler. Böylece İsrailliler onları soydular."));
trsuperobject.biblemap.put("b2_12_37", new trsuperobject(2,12,37,"İsrailliler kadın ve çocukların dışında altı yüz bin kadar erkekle yaya olarak Ramsesten Sukkota doğru yola çıktılar."));
trsuperobject.biblemap.put("b2_12_38", new trsuperobject(2,12,38,"Daha pek çok kişi de onlarla birlikte gitti. Yanlarında çok sayıda davar ve sığır vardı."));
trsuperobject.biblemap.put("b2_12_39", new trsuperobject(2,12,39,"Mısırdan getirdikleri hamurla mayasız pide pişirdiler. Maya yoktu. Çünkü Mısırdan kovulmuşlar, kendilerine azık hazırlayacak zaman bulamamışlardı."));
trsuperobject.biblemap.put("b2_12_40", new trsuperobject(2,12,40,"İsrailliler Mısırda dört yüz otuz yıl yaşadı."));
trsuperobject.biblemap.put("b2_12_41", new trsuperobject(2,12,41,"Dört yüz otuz yılın sonuncu günü RABbin halkı ordular halinde Mısırı terk etti."));
trsuperobject.biblemap.put("b2_12_42", new trsuperobject(2,12,42,"O gece RAB İsraillileri Mısırdan çıkarmak için sürekli bekledi. İsrailliler de kuşaklar boyunca aynı gece RABbi yüceltmek için uyanık olmalıdır."));
trsuperobject.biblemap.put("b2_12_43", new trsuperobject(2,12,43,"RAB Musayla Haruna şöyle dedi: ‹‹Fısıh Bayramının kuralları şunlardır: Hiçbir yabancı Fısıh etini yemeyecek."));
trsuperobject.biblemap.put("b2_12_44", new trsuperobject(2,12,44,"Ama satın aldığınız köleler sünnet edildikten sonra ondan yiyebilir."));
trsuperobject.biblemap.put("b2_12_45", new trsuperobject(2,12,45,"Konuklar ve ücretli işçiler ondan yemeyecek."));
trsuperobject.biblemap.put("b2_12_46", new trsuperobject(2,12,46,"Fısıh eti evde yenmeli, evin dışına çıkarılmamalı. Kemikleri kırmayacaksınız."));
trsuperobject.biblemap.put("b2_12_47", new trsuperobject(2,12,47,"Bütün İsrail topluluğu Fısıh Bayramını kutlayacak."));
trsuperobject.biblemap.put("b2_12_48", new trsuperobject(2,12,48,"Yanınızdaki yabancı bir konuk RABbin Fısıh Bayramını kutlamak isterse, önce evindeki bütün erkekler sünnet edilmeli; sonra yerel halktan biri gibi İsrail halkına katılıp bayramı kutlayabilir. Ama sünnetsiz biri Fısıh etini yemeyecektir."));
trsuperobject.biblemap.put("b2_12_49", new trsuperobject(2,12,49,"Ülkede doğan için de, aranızda yaşayan yabancı için de aynı kural geçerlidir.››"));
trsuperobject.biblemap.put("b2_12_50", new trsuperobject(2,12,50,"İsrailliler RABbin Musayla Haruna verdiği buyruğu eksiksiz yerine getirdiler."));
trsuperobject.biblemap.put("b2_12_51", new trsuperobject(2,12,51,"O gün RAB İsrailliler'i ordular halinde Mısır'dan çıkardı."));
trsuperobject.biblemap.put("b2_13_3", new trsuperobject(2,13,3,"Musa halka, ‹‹Mısırdan, köle olduğunuz ülkeden çıktığınız bugünü anımsayın›› dedi, ‹‹Çünkü RAB güçlü eliyle sizi oradan çıkardı. Mayalı hiçbir şey yenmeyecek."));
trsuperobject.biblemap.put("b2_13_4", new trsuperobject(2,13,4,"Bugün Aviv ayında buradan ayrılıyorsunuz."));
trsuperobject.biblemap.put("b2_13_6", new trsuperobject(2,13,6,"Yedi gün mayasız ekmek yiyecek, yedinci gün RABbe bayram yapacaksınız."));
trsuperobject.biblemap.put("b2_13_7", new trsuperobject(2,13,7,"O yedi gün içinde yalnız mayasız ekmek yiyeceksiniz. Aranızda ve ülkenizin hiçbir yerinde mayalı bir şey görülmeyecek."));
trsuperobject.biblemap.put("b2_13_8", new trsuperobject(2,13,8,"O gün oğullarınıza, ‹Mısırdan çıktığımızda RABbin bizim için yaptıklarından dolayı bunları yapıyoruz› diye anlatacaksınız."));
trsuperobject.biblemap.put("b2_13_9", new trsuperobject(2,13,9,"Bu elinizde bir belirti ve alnınızda bir anma işareti olacak; öyle ki, RABbin yasası hep ağzınızda olsun. Çünkü RAB güçlü eliyle sizi Mısırdan çıkardı."));
trsuperobject.biblemap.put("b2_13_10", new trsuperobject(2,13,10,"Siz de her yıl belirlenen tarihte bu kuralı uygulamalısınız."));
trsuperobject.biblemap.put("b2_13_11", new trsuperobject(2,13,11,"‹‹RAB size ve atalarınıza ant içerek söz verdiği gibi sizi Kenan topraklarına getirecektir. Orayı size verdiği zaman,"));
trsuperobject.biblemap.put("b2_13_12", new trsuperobject(2,13,12,"ilk doğan erkek çocuklarınızın ve hayvanlarınızın hepsini RABbe adayacaksınız. Çünkü bunlar RABbe aittir."));
trsuperobject.biblemap.put("b2_13_13", new trsuperobject(2,13,13,"İlk doğan her sıpanın bedelini bir kuzuyla ödeyin. Bedelini ödemezseniz, boynunu kırın. Bütün ilk doğan erkek çocuklarınızın bedelini ödemelisiniz."));
trsuperobject.biblemap.put("b2_13_14", new trsuperobject(2,13,14,"‹‹İlerde oğullarınız size, ‹Bunun anlamı ne?› diye sorduklarında, ‹RAB bizi güçlü eliyle Mısırdan, köle olduğumuz ülkeden çıkardı› diye yanıtlarsınız,"));
trsuperobject.biblemap.put("b2_13_15", new trsuperobject(2,13,15,"‹Firavun bizi salıvermemekte diretince, RAB Mısırda insanların ve hayvanların bütün ilk doğanlarını öldürdü. İşte bunun için hayvanların ilk doğan erkek yavrularını RABbe kurban ediyoruz. İlk doğan erkek çocuklarımızın bedelini ise bir hayvanla ödüyoruz.›"));
trsuperobject.biblemap.put("b2_13_16", new trsuperobject(2,13,16,"Bu uygulama elinizde bir belirti ve alnınızda bir anma işareti olacak; RABbin bizi Mısırdan güçlü eliyle çıkardığını anımsatacak.››"));
trsuperobject.biblemap.put("b2_13_17", new trsuperobject(2,13,17,"Firavun İsraillileri salıverdiğinde, Filist yöresi yakın olmasına karşın, Tanrı onları oradan götürmedi. Çünkü, ‹‹Halk savaşla karşılaşınca, düşüncelerini değiştirip Mısıra geri dönebilir›› diye düşündü."));
trsuperobject.biblemap.put("b2_13_18", new trsuperobject(2,13,18,"Halkı çöl yolundan Kızıldenize doğru dolaştırdı. İsrailliler Mısırdan silahlı çıkmışlardı."));
trsuperobject.biblemap.put("b2_13_19", new trsuperobject(2,13,19,"Musa Yusufun kemiklerini yanına almıştı. Çünkü Yusuf İsrailin oğullarına, ‹‹Tanrı kesinlikle size yardım edecek, kemiklerimi buradan götüreceksiniz›› diye sıkı sıkı ant içirmişti."));
trsuperobject.biblemap.put("b2_13_20", new trsuperobject(2,13,20,"Sukkottan ayrılıp çöl kenarında, Etamda konakladılar."));
trsuperobject.biblemap.put("b2_13_21", new trsuperobject(2,13,21,"Gece gündüz ilerlemeleri için, RAB gündüzün bir bulut sütunu içinde yol göstererek, geceleyin bir ateş sütunu içinde ışık vererek onlara öncülük ediyordu."));
trsuperobject.biblemap.put("b2_13_22", new trsuperobject(2,13,22,"Gündüz bulut sütunu, gece ateş sütunu halkın önünden eksik olmadı."));
trsuperobject.biblemap.put("b2_14_3", new trsuperobject(2,14,3,"Firavun şöyle düşünecek: ‹İsrailliler ülkede şaşkın şaşkın dolaşıyorlardır, çöl onları kuşatmıştır.›"));
trsuperobject.biblemap.put("b2_14_4", new trsuperobject(2,14,4,"Firavunu inatçı yapacağım. Onların peşine düşecek. Böylece firavunla ordusunu yenerek yücelik kazanacağım. Mısırlılar bilecek ki, ben RABbim.›› İsrailliler söyleneni yaptılar."));
trsuperobject.biblemap.put("b2_14_5", new trsuperobject(2,14,5,"Halkın kaçtığı Mısır Firavununa bildirilince, firavunla görevlileri onlara ilişkin düşüncelerini değiştirdiler: ‹‹Biz ne yaptık?›› dediler, ‹‹İsraillileri salıvermekle kölelerimizi kaybetmiş olduk!››"));
trsuperobject.biblemap.put("b2_14_6", new trsuperobject(2,14,6,"Firavun savaş arabasını hazırlattı, ordusunu yanına aldı."));
trsuperobject.biblemap.put("b2_14_7", new trsuperobject(2,14,7,"Seçme altı yüz savaş arabasının yanısıra, Mısırın bütün savaş arabalarını sorumlu sürücüleriyle birlikte yanına aldı."));
trsuperobject.biblemap.put("b2_14_9", new trsuperobject(2,14,9,"Mısırlılar firavunun bütün atları, savaş arabaları, atlıları, askerleriyle onların ardına düştüler ve deniz kıyısında, Pi-Hahirot yakınlarında, Baal-Sefonun karşısında konaklarken onlara yetiştiler."));
trsuperobject.biblemap.put("b2_14_10", new trsuperobject(2,14,10,"Firavun yaklaşırken, İsrailliler Mısırlıların arkalarından geldiğini görünce dehşete kapılarak RABbe feryat ettiler."));
trsuperobject.biblemap.put("b2_14_11", new trsuperobject(2,14,11,"Musaya, ‹‹Mısırda mezar mı yoktu da bizi çöle ölmeye getirdin?›› dediler, ‹‹Bak, Mısırdan çıkarmakla bize ne yaptın!"));
trsuperobject.biblemap.put("b2_14_12", new trsuperobject(2,14,12,"Mısırdayken sana, ‹Bırak bizi, Mısırlılara kulluk edelim› demedik mi? Çölde ölmektense Mısırlılara kulluk etsek bizim için daha iyi olurdu.››"));
trsuperobject.biblemap.put("b2_14_13", new trsuperobject(2,14,13,"Musa, ‹‹Korkmayın!›› dedi, ‹‹Yerinizde durup bekleyin, RAB bugün sizi nasıl kurtaracak görün. Bugün gördüğünüz Mısırlıları bir daha hiç görmeyeceksiniz."));
trsuperobject.biblemap.put("b2_14_14", new trsuperobject(2,14,14,"RAB sizin için savaşacak, siz sakin olun yeter.››"));
trsuperobject.biblemap.put("b2_14_15", new trsuperobject(2,14,15,"RAB Musaya, ‹‹Niçin bana feryat ediyorsun?›› dedi, ‹‹İsraillilere söyle, ilerlesinler."));
trsuperobject.biblemap.put("b2_14_16", new trsuperobject(2,14,16,"Sen değneğini kaldır, elini denizin üzerine uzat. Sular yarılacak ve İsrailliler kuru toprak üzerinde yürüyerek denizi geçecekler."));
trsuperobject.biblemap.put("b2_14_17", new trsuperobject(2,14,17,"Ben Mısırlıları inatçı yapacağım ki, artlarına düşsünler. Firavunu, bütün ordusunu, savaş arabalarını, atlılarını yenerek yücelik kazanacağım."));
trsuperobject.biblemap.put("b2_14_18", new trsuperobject(2,14,18,"Firavun, savaş arabaları ve atlılarından ötürü yücelik kazandığım zaman, Mısırlılar bilecek ki, ben RABbim.››"));
trsuperobject.biblemap.put("b2_14_19-20", new trsuperobject(2,14,19-20,"İsrail ordusunun önünde yürüyen Tanrının meleği yerini değiştirip arkaya geçti. Önlerindeki bulut sütunu da yerini değiştirip arkalarına, Mısır ve İsrail ordularının arasına geldi. Gece boyunca bulut bir yanı karartıyor, öbür yanı aydınlatıyordu. Bu yüzden, bütün gece iki taraf birbirine yaklaşamadı."));
trsuperobject.biblemap.put("b2_14_21", new trsuperobject(2,14,21,"Musa elini denizin üzerine uzattı. RAB bütün gece güçlü doğu rüzgarıyla suları geri itti, denizi karaya çevirdi. Sular ikiye bölündü,"));
trsuperobject.biblemap.put("b2_14_22", new trsuperobject(2,14,22,"İsrailliler kuru toprak üzerinde yürüyerek denizi geçtiler. Sular sağlarında, sollarında onlara duvar oluşturdu."));
trsuperobject.biblemap.put("b2_14_23", new trsuperobject(2,14,23,"Mısırlılar artlarından geliyordu. Firavunun bütün atları, savaş arabaları, atlıları denizde onları izliyordu."));
trsuperobject.biblemap.put("b2_14_24", new trsuperobject(2,14,24,"Sabah nöbetinde RAB ateş ve bulut sütunundan Mısır ordusuna baktı ve onları şaşkına çevirdi."));
trsuperobject.biblemap.put("b2_14_25", new trsuperobject(2,14,25,"Arabalarının tekerleklerini çıkardı; öyle ki, arabalarını zorlukla sürdüler. Mısırlılar, ‹‹İsraillilerden kaçalım!›› dediler, ‹‹Çünkü RAB onlar için bizimle savaşıyor.›› ‹‹Çamura saplanmasını sağladı›› anlamına da gelebilir."));
trsuperobject.biblemap.put("b2_14_26", new trsuperobject(2,14,26,"RAB Musaya, ‹‹Elini denizin üzerine uzat›› dedi, ‹‹Sular Mısırlıların, savaş arabalarının, atlılarının üzerine dönsün.››"));
trsuperobject.biblemap.put("b2_14_27", new trsuperobject(2,14,27,"Musa elini denizin üzerine uzattı. Sabaha karşı deniz olağan haline döndü. Mısırlılar sulardan kaçarken RAB onları denizin ortasında silkip attı."));
trsuperobject.biblemap.put("b2_14_28", new trsuperobject(2,14,28,"Geri dönen sular savaş arabalarını, atlıları, İsraillilerin peşinden denize dalan firavunun bütün ordusunu yuttu. Onlardan bir kişi bile sağ kalmadı."));
trsuperobject.biblemap.put("b2_14_29", new trsuperobject(2,14,29,"Ama İsrailliler denizi kuru toprakta yürüyerek geçmişlerdi. Sular sağlarında, sollarında onlara duvar oluşturmuştu."));
trsuperobject.biblemap.put("b2_14_30", new trsuperobject(2,14,30,"RAB o gün İsraillileri Mısırlıların elinden kurtardı. İsrailliler deniz kıyısında Mısırlıların ölülerini gördüler."));
trsuperobject.biblemap.put("b2_14_31", new trsuperobject(2,14,31,"RAB'bin Mısırlılar'a gösterdiği büyük gücü gören İsrail halkı RAB'den korkup O'na ve kulu Musa'ya güvendi."));
trsuperobject.biblemap.put("b2_15_1", new trsuperobject(2,15,1,"Musayla İsrailliler RABbe şu ezgiyi söylediler:  ‹‹Ezgiler sunacağım RABbe,Çünkü yüceldikçe yüceldi;Atları da, atlıları da denize döktü."));
trsuperobject.biblemap.put("b2_15_2", new trsuperobject(2,15,2,"Rab gücüm ve ezgimdir,O kurtardı beni.Odur Tanrım,Övgüler sunacağım Ona.Odur babamın Tanrısı,Yücelteceğim Onu."));
trsuperobject.biblemap.put("b2_15_3", new trsuperobject(2,15,3,"Savaş eridir RAB,Adı RABdir."));
trsuperobject.biblemap.put("b2_15_4", new trsuperobject(2,15,4,"‹‹Denize attı firavunun ordusunu,Savaş arabalarını.Kızıldenizde boğuldu seçme subayları."));
trsuperobject.biblemap.put("b2_15_5", new trsuperobject(2,15,5,"Derin sulara gömüldüler,Taş gibi dibe indiler."));
trsuperobject.biblemap.put("b2_15_6", new trsuperobject(2,15,6,"‹‹Senin sağ elin, ya RAB,Senin sağ elin korkunç güce sahiptir.Altında düşmanlar kırılır."));
trsuperobject.biblemap.put("b2_15_7", new trsuperobject(2,15,7,"Devrilir sana başkaldıranlar büyük görkemin karşısında,Gönderir gazabını anız gibi tüketirsin onları."));
trsuperobject.biblemap.put("b2_15_8", new trsuperobject(2,15,8,"Burnunun soluğu karşısında,Sular yığıldı bir araya.Kabaran sular duvarlara dönüştü,Denizin göbeğindeki derin sular dondu."));
trsuperobject.biblemap.put("b2_15_9", new trsuperobject(2,15,9,"Düşman böbürlendi:‹Peşlerine düşüp yakalayacağım onları› dedi,‹Bölüşeceğim çapulu,Dileğimce yağmalayacağım,Kılıcımı çekip yok edeceğim onları.›"));
trsuperobject.biblemap.put("b2_15_10", new trsuperobject(2,15,10,"Üfledin soluğunu, denize gömüldüler,Kurşun gibi engin sulara battılar."));
trsuperobject.biblemap.put("b2_15_11", new trsuperobject(2,15,11,"‹‹Var mı senin gibisi ilahlar arasında, ya RAB?Senin gibi kutsallıkta görkemli, heybetiyle övgüye değer,Harikalar yaratan var mı?"));
trsuperobject.biblemap.put("b2_15_12", new trsuperobject(2,15,12,"Sağ elini uzattın,Yer yuttu onları."));
trsuperobject.biblemap.put("b2_15_13", new trsuperobject(2,15,13,"Öncülük edeceksin sevginle kurtardığın halka,Kutsal konutunun yolunu göstereceksin gücünle onlara."));
trsuperobject.biblemap.put("b2_15_14", new trsuperobject(2,15,14,"Uluslar duyup titreyecekler,Filist halkını dehşet saracak."));
trsuperobject.biblemap.put("b2_15_15", new trsuperobject(2,15,15,"Edom beyleri korkuya kapılacak,Moav önderlerini titreme alacak,Kenanda yaşayanların tümü korkudan eriyecek."));
trsuperobject.biblemap.put("b2_15_16", new trsuperobject(2,15,16,"Korku ve dehşet düşecek üzerlerine,Senin halkın geçinceye dek, ya RAB,Sahip olduğun bu halk geçinceye dek,Bileğinin gücü karşısında taş kesilecekler."));
trsuperobject.biblemap.put("b2_15_17", new trsuperobject(2,15,17,"Ya RAB, halkını içeri alacaksın.Kendi dağına, yaşamak için seçtiğin yere,Ellerinle kurduğun kutsal yere dikeceksin, ya Rab!"));
trsuperobject.biblemap.put("b2_15_18", new trsuperobject(2,15,18,"RAB sonsuza dek egemen olacak.››"));
trsuperobject.biblemap.put("b2_15_19", new trsuperobject(2,15,19,"Firavunun atları, savaş arabaları, atlıları denize dalınca, RAB suları onların üzerine çevirdi. Ama İsrailliler denizi kuru toprakta yürüyerek geçtiler."));
trsuperobject.biblemap.put("b2_15_20", new trsuperobject(2,15,20,"Harunun kızkardeşi Peygamber Miryam tefini eline aldı, bütün kadınlar teflerle, oynayarak onu izlediler."));
trsuperobject.biblemap.put("b2_15_21", new trsuperobject(2,15,21,"Miryam onlara şu ezgiyi söyledi:  ‹‹Ezgiler sunun RABbe,Çünkü yüceldikçe yüceldi,Atları, atlıları denize döktü.››"));
trsuperobject.biblemap.put("b2_15_22", new trsuperobject(2,15,22,"Musa İsraillileri Kızıldenizin ötesine çıkardı. Şur Çölüne girdiler. Çölde üç gün yol aldılarsa da su bulamadılar."));
trsuperobject.biblemap.put("b2_15_23", new trsuperobject(2,15,23,"Maraya vardılar. Ama Maranın suyunu içemediler, çünkü su acıydı. Bu yüzden oraya Marafı adı verildi."));
trsuperobject.biblemap.put("b2_15_24", new trsuperobject(2,15,24,"Halk, ‹‹Ne içeceğiz?›› diye Musaya yakınmaya başladı."));
trsuperobject.biblemap.put("b2_15_25", new trsuperobject(2,15,25,"Musa RABbe yakardı. RAB ona bir ağaç parçası gösterdi. Musa onu suya atınca sular tatlı oldu. Orada RAB onlar için bir kural ve ilke koydu, hepsini sınadı."));
trsuperobject.biblemap.put("b2_15_26", new trsuperobject(2,15,26,"‹‹Ben, Tanrınız RABbin sözünü dikkatle dinler, gözümde doğru olanı yapar, buyruklarıma kulak verir, bütün kurallarıma uyarsanız, Mısırlılara verdiğim hastalıkların hiçbirini size vermeyeceğim›› dedi, ‹‹Çünkü size şifa veren RAB benim.››"));
trsuperobject.biblemap.put("b2_15_27", new trsuperobject(2,15,27,"Sonra Elim'e gittiler. Orada on iki su kaynağı, yetmiş hurma ağacı vardı. Su kıyısında konakladılar."));
trsuperobject.biblemap.put("b2_16_1", new trsuperobject(2,16,1,"Bütün İsrail topluluğu Elimden ayrıldı. Mısırdan çıktıktan sonra ikinci ayın on beşinci günü Elim ile Sina arasındaki Sin Çölüne vardılar."));
trsuperobject.biblemap.put("b2_16_2", new trsuperobject(2,16,2,"Çölde hepsi Musayla Haruna yakınmaya başladı."));
trsuperobject.biblemap.put("b2_16_3", new trsuperobject(2,16,3,"‹‹Keşke RAB bizi Mısırdayken öldürseydi›› dediler, ‹‹Hiç değilse orada et kazanlarının başına oturur, doyasıya yerdik. Ama siz bütün topluluğu açlıktan öldürmek için bizi bu çöle getirdiniz.››"));
trsuperobject.biblemap.put("b2_16_5", new trsuperobject(2,16,5,"Altıncı gün her gün topladıklarının iki katını toplayıp hazırlayacaklar.››"));
trsuperobject.biblemap.put("b2_16_6", new trsuperobject(2,16,6,"Musayla Harun İsraillilere, ‹‹Bu akşam sizi Mısırdan RABbin çıkardığını bileceksiniz›› dediler,"));
trsuperobject.biblemap.put("b2_16_7", new trsuperobject(2,16,7,"‹‹Sabah da RABbin görkemini göreceksiniz. Çünkü RAB kendisine söylendiğinizi duydu. Biz kimiz ki, bize söyleniyorsunuz?››"));
trsuperobject.biblemap.put("b2_16_8", new trsuperobject(2,16,8,"Sonra Musa, ‹‹Akşam size yemek için et, sabah da dilediğiniz kadar ekmek verilince, RABbin görkemini göreceksiniz›› dedi, ‹‹Çünkü RAB kendisine söylendiğinizi duydu. Biz kimiz ki? Siz bize değil, RABbe söyleniyorsunuz.››"));
trsuperobject.biblemap.put("b2_16_9", new trsuperobject(2,16,9,"Musa Haruna, ‹‹Bütün İsrail topluluğuna söyle, RABbin huzuruna gelsinler›› dedi, ‹‹Çünkü RAB söylendiklerini duydu.››"));
trsuperobject.biblemap.put("b2_16_10", new trsuperobject(2,16,10,"Harun İsrail topluluğuna bunları anlatırken, çöle doğru baktılar. RABbin görkemi bulutta görünüyordu."));
trsuperobject.biblemap.put("b2_16_11", new trsuperobject(2,16,11,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b2_16_12", new trsuperobject(2,16,12,"‹‹İsraillilerin yakınmalarını duydum. Onlara de ki, ‹Akşamüstü et yiyeceksiniz, sabah da ekmekle karnınızı doyuracaksınız. O zaman bileceksiniz ki, Tanrınız RAB benim.››"));
trsuperobject.biblemap.put("b2_16_13", new trsuperobject(2,16,13,"Akşam bıldırcınlar geldi, ordugahı sardı. Sabah ordugahın çevresini çiy kaplamıştı."));
trsuperobject.biblemap.put("b2_16_14", new trsuperobject(2,16,14,"Çiy eriyince, toprakta, çölün yüzeyinde kırağıya benzer ince pulcuklar göründü."));
trsuperobject.biblemap.put("b2_16_15", new trsuperobject(2,16,15,"Bunu görünce İsrailliler birbirlerine, ‹‹Bu da ne?›› diye sordular. Çünkü ne olduğunu anlayamamışlardı. Musa, ‹‹RABbin size yemek için verdiği ekmektir bu›› dedi,"));
trsuperobject.biblemap.put("b2_16_16", new trsuperobject(2,16,16,"‹‹RABbin buyruğu şudur: ‹Herkes yiyeceği kadar toplasın. Çadırınızdaki her kişi için birer omer alın.›› ifade 16:18,32,33,36da da geçer."));
trsuperobject.biblemap.put("b2_16_17", new trsuperobject(2,16,17,"İsrailliler söyleneni yaptılar. Kimi çok, kimi az topladı."));
trsuperobject.biblemap.put("b2_16_18", new trsuperobject(2,16,18,"Omerle ölçtüklerinde, çok toplayanın fazlası, az toplayanın da eksiği yoktu. Herkes yiyeceği kadar toplamıştı."));
trsuperobject.biblemap.put("b2_16_19", new trsuperobject(2,16,19,"Musa onlara, ‹‹Kimse sabaha bir parça bile bırakmasın›› dedi."));
trsuperobject.biblemap.put("b2_16_20", new trsuperobject(2,16,20,"Ama bazıları ona aldırmayıp sabaha bıraktılar. Bıraktıkları kurtlanıp kokmaya başlayınca Musa onlara öfkelendi."));
trsuperobject.biblemap.put("b2_16_21", new trsuperobject(2,16,21,"Her sabah herkes yiyeceği kadar topluyordu. Güneş ortalığı ısıtınca, yerde kalanlar eriyordu."));
trsuperobject.biblemap.put("b2_16_22", new trsuperobject(2,16,22,"Altıncı gün kişi başına iki omer, yani iki kat topladılar. Topluluğun önderleri gelip durumu Musaya bildirdiler."));
trsuperobject.biblemap.put("b2_16_23", new trsuperobject(2,16,23,"Musa, ‹‹RABbin buyruğu şudur›› dedi, ‹‹Yarın dinlenme günü, RAB için kutsal Şabat Günüdür. Pişireceğinizi pişirin, haşlayacağınızı haşlayın. Artakalanı bir kenara koyun, sabaha kalsın.››"));
trsuperobject.biblemap.put("b2_16_24", new trsuperobject(2,16,24,"Musanın buyurduğu gibi artakalanı sabaha bıraktılar. Ne koktu, ne kurtlandı."));
trsuperobject.biblemap.put("b2_16_25", new trsuperobject(2,16,25,"Musa, ‹‹Artakalanı bugün yiyin›› dedi, ‹‹Çünkü bugün RAB için Şabat Günüdür. Bugün dışarda ekmek bulamayacaksınız."));
trsuperobject.biblemap.put("b2_16_26", new trsuperobject(2,16,26,"Altı gün ekmek toplayacaksınız, ama yedinci gün olan Şabat Günü ekmek bulunmayacak.››"));
trsuperobject.biblemap.put("b2_16_27", new trsuperobject(2,16,27,"Yedinci gün bazıları ekmek toplamak için dışarı çıktı, ama hiçbir şey bulamadılar."));
trsuperobject.biblemap.put("b2_16_28", new trsuperobject(2,16,28,"RAB Musaya, ‹‹Ne zamana dek buyruklarıma ve yasalarıma uymayı reddedeceksiniz?›› dedi,"));
trsuperobject.biblemap.put("b2_16_29", new trsuperobject(2,16,29,"‹‹Size Şabat Gününü verdim. Bunun için altıncı gün size iki günlük ekmek veriyorum. Yedinci gün herkes neredeyse orada kalsın, dışarı çıkmasın.››"));
trsuperobject.biblemap.put("b2_16_30", new trsuperobject(2,16,30,"Böylece halk yedinci gün dinlendi."));
trsuperobject.biblemap.put("b2_16_31", new trsuperobject(2,16,31,"İsrailliler o ekmeğe man adını verdiler. Kişniş tohumu gibi beyazımsı, tadı ballı yufka gibiydi."));
trsuperobject.biblemap.put("b2_16_32", new trsuperobject(2,16,32,"Musa, ‹‹RABbin buyruğu şudur›› dedi, ‹‹Mısırdan sizi çıkardığımda, gelecek kuşakların çölde size yedirdiğim ekmeği görmesi için, bir omer saklansın.››"));
trsuperobject.biblemap.put("b2_16_33", new trsuperobject(2,16,33,"Musa Haruna, ‹‹Bir testi al, içine bir omer man doldur›› dedi, ‹‹Gelecek kuşaklar için saklanmak üzere onu RABbin huzuruna koy.››"));
trsuperobject.biblemap.put("b2_16_34", new trsuperobject(2,16,34,"RABbin Musaya buyurduğu gibi Harun manı saklanmak üzere Antlaşma Levhalarının önüne koydu."));
trsuperobject.biblemap.put("b2_16_35", new trsuperobject(2,16,35,"İsrailliler yerleştikleri Kenan topraklarına varıncaya dek kırk yıl man yediler."));
trsuperobject.biblemap.put("b2_16_36", new trsuperobject(2,16,36,"Bir omer efanın onda biridir.-"));
trsuperobject.biblemap.put("b2_17_2", new trsuperobject(2,17,2,"Musaya, ‹‹Bize içecek su ver›› diye çıkıştılar. Musa, ‹‹Niçin bana çıkışıyorsunuz?›› dedi, ‹‹Neden RABbi deniyorsunuz?››"));
trsuperobject.biblemap.put("b2_17_3", new trsuperobject(2,17,3,"Ama halk susamıştı. ‹‹Niçin bizi Mısırdan çıkardın?›› diye Musaya söylendiler, ‹‹Bizi, çocuklarımızı, hayvanlarımızı susuzluktan öldürmek için mi?››"));
trsuperobject.biblemap.put("b2_17_4", new trsuperobject(2,17,4,"Musa, ‹‹Bu halka ne yapayım?›› diye RABbe feryat etti, ‹‹Neredeyse beni taşlayacaklar.››"));
trsuperobject.biblemap.put("b2_17_6", new trsuperobject(2,17,6,"Ben Horev Dağında bir kayanın üzerinde, senin önünde duracağım. Kayaya vuracaksın, halk içsin diye su fışkıracak.›› Musa İsrail ileri gelenlerinin önünde denileni yaptı."));
trsuperobject.biblemap.put("b2_17_7", new trsuperobject(2,17,7,"Oraya Massa ve Meriva adı verildi. Çünkü İsrailliler orada Musaya çıkışmış ve, ‹‹Acaba RAB aramızda mı, değil mi?›› diye RABbi denemişlerdi."));
trsuperobject.biblemap.put("b2_17_8", new trsuperobject(2,17,8,"Amalekliler gelip Refidimde İsraillilere savaş açtılar."));
trsuperobject.biblemap.put("b2_17_9", new trsuperobject(2,17,9,"Musa Yeşuya, ‹‹Adam seç, git Amaleklilerle savaş›› dedi, ‹‹Yarın ben elimde Tanrının değneğiyle tepenin üzerinde duracağım.››"));
trsuperobject.biblemap.put("b2_17_10", new trsuperobject(2,17,10,"Yeşu Musanın buyurduğu gibi Amaleklilerle savaştı. Bu arada Musa, Harun ve Hur tepenin üzerine çıktılar."));
trsuperobject.biblemap.put("b2_17_11", new trsuperobject(2,17,11,"Musa elini kaldırdıkça İsrailliler, indirdikçe Amalekliler kazanıyordu."));
trsuperobject.biblemap.put("b2_17_12", new trsuperobject(2,17,12,"Ne var ki, Musanın elleri yoruldu. Bir taş getirip altına koydular. Musa üzerine oturdu. Bir yanda Harun, öbür yanda Hur Musanın ellerini yukarıda tuttular. Güneş batıncaya dek Musanın elleri yukarıda kaldı."));
trsuperobject.biblemap.put("b2_17_13", new trsuperobject(2,17,13,"Böylece Yeşu Amalek ordusunu yenip kılıçtan geçirdi."));
trsuperobject.biblemap.put("b2_17_14", new trsuperobject(2,17,14,"RAB Musaya, ‹‹Bunu anı olarak kayda geç›› dedi, ‹‹Yeşuya da söyle, Amaleklilerin adını yeryüzünden büsbütün sileceğim.››"));
trsuperobject.biblemap.put("b2_17_15", new trsuperobject(2,17,15,"Musa bir sunak yaptı, adını ‹‹Yahve nissi›› koydu."));
