trsuperobject.biblemap.put("b2_18_1", new trsuperobject(2,18,1,"Musanın kayınbabası Midyanlı Kâhin Yitro, Tanrının Musa ve halkı İsrail için yaptığı her şeyi, RABbin İsraillileri Mısırdan nasıl çıkardığını duydu."));
trsuperobject.biblemap.put("b2_18_2-3", new trsuperobject(2,18,2-3,"Musanın kendisine göndermiş olduğu karısı Sipporayı ve iki oğlunu yanına aldı. Musa, ‹‹Garibim bu yabancı diyarda›› diyerek oğullarından birine Gerşomfö adını vermişti."));
trsuperobject.biblemap.put("b2_18_4", new trsuperobject(2,18,4,"Sonra, ‹‹Babamın Tanrısı bana yardım etti, beni firavunun kılıcından esirgedi›› diyerek öbürüne de Eliezer adını koymuştu."));
trsuperobject.biblemap.put("b2_18_5", new trsuperobject(2,18,5,"Yitro Musanın karısı ve oğullarıyla birlikte Tanrı Dağına, Musanın konakladığı çöle geldi."));
trsuperobject.biblemap.put("b2_18_6", new trsuperobject(2,18,6,"Musaya şu haberi gönderdi: ‹‹Ben, kayınbaban Yitro, karın ve iki oğlunla birlikte sana geliyoruz.››"));
trsuperobject.biblemap.put("b2_18_7", new trsuperobject(2,18,7,"Musa kayınbabasını karşılamaya çıktı, önünde eğilip onu öptü. Birbirinin hatırını sorup çadıra girdiler."));
trsuperobject.biblemap.put("b2_18_8", new trsuperobject(2,18,8,"Musa İsrailliler uğruna RABbin firavunla Mısırlılara bütün yaptıklarını, yolda çektikleri sıkıntıları, RABbin kendilerini nasıl kurtardığını kayınbabasına bir bir anlattı."));
trsuperobject.biblemap.put("b2_18_9", new trsuperobject(2,18,9,"Yitro RABbin İsraillilere yaptığı iyiliklere, onları Mısırlıların elinden kurtardığına sevindi."));
trsuperobject.biblemap.put("b2_18_10", new trsuperobject(2,18,10,"‹‹Sizi Mısırlıların ve firavunun elinden kurtaran RABbe övgüler olsun›› dedi, ‹‹Halkı Mısırın boyunduruğundan O kurtardı."));
trsuperobject.biblemap.put("b2_18_11", new trsuperobject(2,18,11,"Artık biliyorum ki, RAB bütün ilahlardan büyüktür. Çünkü onların gurur duyduğu şeylerin üstesinden geldi.››"));
trsuperobject.biblemap.put("b2_18_12", new trsuperobject(2,18,12,"Sonra Tanrıya yakmalık sunu ve kurbanlar getirdi. Harunla bütün İsrail ileri gelenleri, Musanın kayınbabasıyla Tanrının huzurunda yemek yemeye geldiler."));
trsuperobject.biblemap.put("b2_18_13", new trsuperobject(2,18,13,"Ertesi gün Musa halkın davalarına bakmak için yargı kürsüsüne çıktı. Halk sabahtan akşama kadar çevresinde ayakta durdu."));
trsuperobject.biblemap.put("b2_18_14", new trsuperobject(2,18,14,"Kayınbabası Musanın halk için yaptıklarını görünce, ‹‹Nedir bu, halka yaptığın?›› dedi, ‹‹Neden sen tek başına yargıç olarak oturuyorsun da herkes sabahtan akşama kadar çevrende bekliyor?››"));
trsuperobject.biblemap.put("b2_18_15", new trsuperobject(2,18,15,"Musa, ‹‹Çünkü halk Tanrının istemini bilmek için bana geliyor›› diye yanıtladı,"));
trsuperobject.biblemap.put("b2_18_16", new trsuperobject(2,18,16,"‹‹Ne zaman bir sorunları olsa, bana gelirler. Ben de taraflar arasında karar veririm; Tanrının kurallarını, yasalarını onlara bildiririm.››"));
trsuperobject.biblemap.put("b2_18_17", new trsuperobject(2,18,17,"Kayınbabası, ‹‹Yaptığın iş iyi değil›› dedi,"));
trsuperobject.biblemap.put("b2_18_18", new trsuperobject(2,18,18,"‹‹Hem sen, hem de yanındaki halk tükeneceksiniz. Bu işi tek başına kaldıramazsın. Sana ağır gelir."));
trsuperobject.biblemap.put("b2_18_19", new trsuperobject(2,18,19,"Beni dinle, sana öğüt vereyim. Tanrı seninle olsun. Tanrının önünde halkı sen temsil etmeli, sorunlarını Tanrıya sen iletmelisin."));
trsuperobject.biblemap.put("b2_18_20", new trsuperobject(2,18,20,"Kuralları, yasaları halka öğret, izlemeleri gereken yolu, yapacakları işi göster."));
trsuperobject.biblemap.put("b2_18_21", new trsuperobject(2,18,21,"Bunun yanısıra halkın arasından Tanrıdan korkan, yetenekli, haksız kazançtan nefret eden dürüst adamlar seç; onları biner, yüzer, ellişer, onar kişilik toplulukların başına önder ata."));
trsuperobject.biblemap.put("b2_18_22", new trsuperobject(2,18,22,"Halka sürekli onlar yargıçlık etsin. Büyük davaları sana getirsinler, küçük davaları kendileri çözsünler. Böylece işini paylaşmış olurlar. Yükün hafifler."));
trsuperobject.biblemap.put("b2_18_23", new trsuperobject(2,18,23,"Eğer böyle yaparsan, Tanrı da buyurursa, dayanabilirsin. Herkes esenlik içinde evine döner.››"));
trsuperobject.biblemap.put("b2_18_24", new trsuperobject(2,18,24,"Musa kayınbabasının sözünü dinledi. Söylediği her şeyi yerine getirdi."));
trsuperobject.biblemap.put("b2_18_25", new trsuperobject(2,18,25,"İsrailliler arasından yetenekli adamlar seçti. Onları biner, yüzer, ellişer, onar kişilik toplulukların başına önder atadı."));
trsuperobject.biblemap.put("b2_18_26", new trsuperobject(2,18,26,"Halka sürekli yargıçlık eden bu kişiler zor davaları Musaya getirdiler, küçük davaları ise kendileri çözdüler."));
trsuperobject.biblemap.put("b2_18_27", new trsuperobject(2,18,27,"Sonra Musa kayınbabasını uğurladı. Yitro da ülkesine döndü."));
trsuperobject.biblemap.put("b2_19_1", new trsuperobject(2,19,1,"İsrailliler Mısırdan çıktıktan tam üç ay sonra Sina Çölüne vardılar."));
trsuperobject.biblemap.put("b2_19_2", new trsuperobject(2,19,2,"Refidimden yola çıkıp Sina Çölüne girdiler. Orada, Sina Dağının karşısında konakladılar."));
trsuperobject.biblemap.put("b2_19_3", new trsuperobject(2,19,3,"Musa Tanrının huzuruna çıktı. RAB dağdan kendisine seslendi: ‹‹Yakup soyuna, İsrail halkına şöyle diyeceksin:"));
trsuperobject.biblemap.put("b2_19_4", new trsuperobject(2,19,4,"Mısırlılara ne yaptığımı, sizi nasıl kartal kanatları üzerinde taşıyarak yanıma getirdiğimi gördünüz."));
trsuperobject.biblemap.put("b2_19_5", new trsuperobject(2,19,5,"Şimdi sözümü dikkatle dinler, antlaşmama uyarsanız, bütün uluslar içinde öz halkım olursunuz. Çünkü yeryüzünün tümü benimdir."));
trsuperobject.biblemap.put("b2_19_6", new trsuperobject(2,19,6,"Siz benim için kâhinler krallığı, kutsal ulus olacaksınız. İsraillilere böyle söyleyeceksin.›› Tanrıya kurban sunmak gibi dinsel işlerle uğraşan görevli. Kâhinin büyücülük, falcılık, sihirbazlık, gaipten haber vermek gibi işlerle uğraşması söz konusu değildi. Bu uygulamalar Yas.18:9-14 ayetlerinde yasaklanmıştır."));
trsuperobject.biblemap.put("b2_19_7", new trsuperobject(2,19,7,"Musa gidip halkın ileri gelenlerini çağırdı ve RABbin kendisine buyurduğu her şeyi onlara anlattı."));
trsuperobject.biblemap.put("b2_19_8", new trsuperobject(2,19,8,"Bütün halk bir ağızdan, ‹‹RABbin söylediği her şeyi yapacağız›› diye yanıtladılar. Musa halkın yanıtını RABbe iletti."));
trsuperobject.biblemap.put("b2_19_10", new trsuperobject(2,19,10,"RAB Musaya, ‹‹Git, bugün ve yarın halkı arındır›› dedi, ‹‹Giysilerini yıkasınlar."));
trsuperobject.biblemap.put("b2_19_11", new trsuperobject(2,19,11,"Üçüncü güne hazır olsunlar. Çünkü üçüncü gün bütün halkın gözü önünde ben, RAB Sina Dağına ineceğim."));
trsuperobject.biblemap.put("b2_19_12", new trsuperobject(2,19,12,"Dağın çevresine sınır çiz ve halka de ki, ‹Sakın dağa çıkmayın, dağın eteğine de yaklaşmayın! Kim dağa dokunursa, kesinlikle öldürülecektir."));
trsuperobject.biblemap.put("b2_19_13", new trsuperobject(2,19,13,"Ya taşlanacak, ya da okla vurulacak; ona insan eli değmeyecek. İster hayvan olsun ister insan, yaşamasına izin verilmeyecek.› Ancak boru uzun uzun çalınınca dağa çıkabilirler.››"));
trsuperobject.biblemap.put("b2_19_14", new trsuperobject(2,19,14,"Sonra Musa dağdan halkın yanına inip onları arındırdı. Herkes giysilerini yıkadı."));
trsuperobject.biblemap.put("b2_19_15", new trsuperobject(2,19,15,"Musa halka, ‹‹Üçüncü güne hazır olun›› dedi, ‹‹Bu süre içinde cinsel ilişkide bulunmayın.››"));
trsuperobject.biblemap.put("b2_19_16", new trsuperobject(2,19,16,"Üçüncü günün sabahı gök gürledi, şimşekler çaktı. Dağın üzerinde koyu bir bulut vardı. Derken, çok güçlü bir boru sesi duyuldu. Ordugahta herkes titremeye başladı."));
trsuperobject.biblemap.put("b2_19_17", new trsuperobject(2,19,17,"Musa halkın Tanrıyla görüşmek üzere ordugahtan çıkmasına öncülük etti. Dağın eteğinde durdular."));
trsuperobject.biblemap.put("b2_19_18", new trsuperobject(2,19,18,"Sina Dağının her yanından duman tütüyordu. Çünkü RAB dağın üstüne ateş içinde inmişti. Dağdan ocak dumanı gibi duman çıkıyor, bütün dağ şiddetle sarsılıyordu."));
trsuperobject.biblemap.put("b2_19_19", new trsuperobject(2,19,19,"Boru sesi gitgide yükselince, Musa konuştu ve Tanrı gök gürlemeleriyle onu yanıtladı."));
trsuperobject.biblemap.put("b2_19_20", new trsuperobject(2,19,20,"RAB Sina Dağının üzerine indi, Musayı dağın tepesine çağırdı. Musa tepeye çıktı."));
trsuperobject.biblemap.put("b2_19_21", new trsuperobject(2,19,21,"RAB, ‹‹Aşağı inip halkı uyar›› dedi, ‹‹Sakın beni görmek için sınırı geçmesinler, yoksa birçoğu ölür."));
trsuperobject.biblemap.put("b2_19_22", new trsuperobject(2,19,22,"Bana yaklaşan kâhinler de kendilerini kutsasınlar, yoksa onları şiddetle cezalandırırım.››"));
trsuperobject.biblemap.put("b2_19_23", new trsuperobject(2,19,23,"Musa, ‹‹Halk Sina Dağına çıkamaz›› diye karşılık verdi, ‹‹Çünkü sen, ‹Dağın çevresine sınır çiz, onu kutsal kıl› diyerek bizi uyardın.››"));
trsuperobject.biblemap.put("b2_19_24", new trsuperobject(2,19,24,"RAB, ‹‹Aşağı inip Harunu getir›› dedi, ‹‹Ama kâhinlerle halk huzuruma gelmek için sınırı geçmesinler. Yoksa onları şiddetle cezalandırırım.››"));
trsuperobject.biblemap.put("b2_19_25", new trsuperobject(2,19,25,"Bunun üzerine Musa aşağı inip durumu halka anlattı."));
trsuperobject.biblemap.put("b2_20_1", new trsuperobject(2,20,1,"Tanrı şöyle konuştu:"));
trsuperobject.biblemap.put("b2_20_2", new trsuperobject(2,20,2,"‹‹Seni Mısırdan, köle olduğun ülkeden çıkaran Tanrın RAB benim."));
trsuperobject.biblemap.put("b2_20_3", new trsuperobject(2,20,3,"‹‹Benden başka tanrın olmayacak."));
trsuperobject.biblemap.put("b2_20_4", new trsuperobject(2,20,4,"‹‹Kendine yukarıda gökyüzünde, aşağıda yeryüzünde ya da yer altındaki sularda yaşayan herhangi bir canlıya benzer put yapmayacaksın."));
trsuperobject.biblemap.put("b2_20_5", new trsuperobject(2,20,5,"Putların önünde eğilmeyecek, onlara tapmayacaksın. Çünkü ben, Tanrın RAB, kıskanç bir Tanrıyım. Benden nefret edenin babasının işlediği suçun hesabını çocuklarından, üçüncü, dördüncü kuşaklardan sorarım."));
trsuperobject.biblemap.put("b2_20_6", new trsuperobject(2,20,6,"Ama beni seven, buyruklarıma uyan binlerce kuşağa sevgi gösteririm."));
trsuperobject.biblemap.put("b2_20_7", new trsuperobject(2,20,7,"‹‹Tanrın RABbin adını boş yere ağzına almayacaksın. Çünkü RAB, adını boş yere ağzına alanları cezasız bırakmayacaktır."));
trsuperobject.biblemap.put("b2_20_8", new trsuperobject(2,20,8,"‹‹Şabat Gününü kutsal sayarak anımsa."));
trsuperobject.biblemap.put("b2_20_9", new trsuperobject(2,20,9,"Altı gün çalışacak, bütün işlerini yapacaksın."));
trsuperobject.biblemap.put("b2_20_10", new trsuperobject(2,20,10,"Ama yedinci gün bana, Tanrın RABbe Şabat Günü olarak adanmıştır. O gün sen, oğlun, kızın, erkek ve kadın kölen, hayvanların, aranızdaki yabancılar dahil, hiçbir iş yapmayacaksınız."));
trsuperobject.biblemap.put("b2_20_11", new trsuperobject(2,20,11,"Çünkü ben, RAB yeri göğü, denizi ve bütün canlıları altı günde yarattım, yedinci gün dinlendim. Bu yüzden Şabat Gününü kutsadım ve kutsal bir gün olarak belirledim."));
trsuperobject.biblemap.put("b2_20_12", new trsuperobject(2,20,12,"‹‹Annene babana saygı göster. Öyle ki, Tanrın RABbin sana vereceği ülkede ömrün uzun olsun."));
trsuperobject.biblemap.put("b2_20_13", new trsuperobject(2,20,13,"‹‹Adam öldürmeyeceksin."));
trsuperobject.biblemap.put("b2_20_14", new trsuperobject(2,20,14,"‹‹Zina etmeyeceksin."));
trsuperobject.biblemap.put("b2_20_15", new trsuperobject(2,20,15,"‹‹Çalmayacaksın."));
trsuperobject.biblemap.put("b2_20_16", new trsuperobject(2,20,16,"‹‹Komşuna karşı yalan yere tanıklık etmeyeceksin."));
trsuperobject.biblemap.put("b2_20_17", new trsuperobject(2,20,17,"‹‹Komşunun evine, karısına, erkek ve kadın kölesine, öküzüne, eşeğine, hiçbir şeyine göz dikmeyeceksin.››"));
trsuperobject.biblemap.put("b2_20_18", new trsuperobject(2,20,18,"Halk gök gürlemelerini, boru sesini duyup şimşekleri ve dağın başındaki dumanı görünce korkudan titremeye başladı. Uzakta durarak"));
trsuperobject.biblemap.put("b2_20_19", new trsuperobject(2,20,19,"Musaya, ‹‹Bizimle sen konuş, dinleyelim›› dediler, ‹‹Ama Tanrı konuşmasın, yoksa ölürüz.››"));
trsuperobject.biblemap.put("b2_20_20", new trsuperobject(2,20,20,"Musa, ‹‹Korkmayın!›› diye karşılık verdi, ‹‹Tanrı sizi denemek için geldi; Tanrı korkusu üzerinizde olsun, günah işlemeyesiniz diye.››"));
trsuperobject.biblemap.put("b2_20_21", new trsuperobject(2,20,21,"Musa Tanrının içinde bulunduğu koyu karanlığa yaklaşırken halk uzakta durdu."));
trsuperobject.biblemap.put("b2_20_22", new trsuperobject(2,20,22,"RAB Musaya şöyle dedi: ‹‹İsraillilere de ki, ‹Göklerden sizinle konuştuğumu gördünüz."));
trsuperobject.biblemap.put("b2_20_23", new trsuperobject(2,20,23,"Benim yanımsıra başka ilahlar yapmayacaksınız, altın ya da gümüş ilahlar dökmeyeceksiniz."));
trsuperobject.biblemap.put("b2_20_24", new trsuperobject(2,20,24,"Benim için toprak bir sunak yapacaksınız. Yakmalık ve esenlik sunularınızı, davarlarınızı, sığırlarınızı onun üzerinde sunacaksınız. Adımı anımsattığım her yere gelip sizi kutsayacağım."));
trsuperobject.biblemap.put("b2_20_25", new trsuperobject(2,20,25,"Eğer bana taş sunak yaparsanız, yontma taş kullanmayın. Çünkü kullanacağınız alet sunağın kutsallığını bozar."));
trsuperobject.biblemap.put("b2_20_26", new trsuperobject(2,20,26,"Sunağımın üzerine basamakla çıkmayacaksınız. Çünkü çıplak yeriniz görünebilir.››"));
trsuperobject.biblemap.put("b2_21_1", new trsuperobject(2,21,1,"‹‹İsraillilere şu ilkeleri bildir:"));
trsuperobject.biblemap.put("b2_21_2", new trsuperobject(2,21,2,"‹‹İbrani bir köle satın alırsan, altı yıl kölelik edecek, ama yedinci yıl karşılık ödemeden özgür olacak."));
trsuperobject.biblemap.put("b2_21_3", new trsuperobject(2,21,3,"Bekâr geldiyse, yalnız kendisi özgür olacak; evli geldiyse, karısı da özgür olacak."));
trsuperobject.biblemap.put("b2_21_4", new trsuperobject(2,21,4,"Efendisi kendisine bir kadın verir ve o kadından çocukları olursa, kadın ve çocuklar efendisinde kalacak, yalnız kendisi gidecek."));
trsuperobject.biblemap.put("b2_21_5", new trsuperobject(2,21,5,"‹‹Ama köle açıkça, ‹Ben efendimi, karımla çocuklarımı seviyorum, özgür olmak istemiyorum› derse,"));
trsuperobject.biblemap.put("b2_21_6", new trsuperobject(2,21,6,"efendisi onu yargıç huzurunafş çıkaracak. Kapıya ya da kapı sövesine yaklaştırıp kulağını bizle delecek. Böylece köle yaşam boyu efendisine hizmet edecek. tapınma yeri anlamına gelebilir."));
trsuperobject.biblemap.put("b2_21_7", new trsuperobject(2,21,7,"‹‹Eğer bir adam kızını cariye olarak satarsa, kız erkek köleler gibi özgür bırakılmayacak."));
trsuperobject.biblemap.put("b2_21_8", new trsuperobject(2,21,8,"Efendisi kızla nişanlanır, sonra kızdan hoşlanmazsa, kızın geri alınmasına izin vermelidir. Kızı aldattığı için onu yabancılara satamaz."));
trsuperobject.biblemap.put("b2_21_9", new trsuperobject(2,21,9,"Eğer cariyeyi oğluna nişanlarsa, ona kendi kızı gibi davranmalıdır."));
trsuperobject.biblemap.put("b2_21_10", new trsuperobject(2,21,10,"Eğer ikinci bir kadınla evlenirse, ilk karısını nafakadan, giysiden, karılık haklarından yoksun bırakmamalıdır."));
trsuperobject.biblemap.put("b2_21_11", new trsuperobject(2,21,11,"Eğer bu üç hakkı ona vermezse, kadın karşılıksız özgür olacaktır.››"));
trsuperobject.biblemap.put("b2_21_12", new trsuperobject(2,21,12,"‹‹Kim birini vurup öldürürse, kendisi de kesinlikle öldürülecektir."));
trsuperobject.biblemap.put("b2_21_13", new trsuperobject(2,21,13,"Ama olayda kasıt yoksa, ona ben izin vermişsem, size adamın kaçacağı yeri bildireceğim."));
trsuperobject.biblemap.put("b2_21_14", new trsuperobject(2,21,14,"Eğer bir adam komşusuna düzen kurar, kasıtlı olarak saldırıp onu öldürürse, sunağıma bile kaçmış olsa, onu çıkarıp öldüreceksiniz."));
trsuperobject.biblemap.put("b2_21_15", new trsuperobject(2,21,15,"‹‹Kim annesini ya da babasını döverse, kesinlikle öldürülecektir."));
trsuperobject.biblemap.put("b2_21_16", new trsuperobject(2,21,16,"‹‹Kim adam kaçırırsa, onu ister satmış olsun, ister elinde tutsun, kesinlikle öldürülecektir."));
trsuperobject.biblemap.put("b2_21_17", new trsuperobject(2,21,17,"‹‹Annesine ya da babasına lanet eden kesinlikle öldürülecektir."));
trsuperobject.biblemap.put("b2_21_18", new trsuperobject(2,21,18,"‹‹Kavga çıkar, bir adam komşusuna taşla ya da yumrukla vurur, vurulan adam ölmeyip yatağa düşer,"));
trsuperobject.biblemap.put("b2_21_19", new trsuperobject(2,21,19,"sonra kalkıp değnekle dışarıda gezebilirse, vuran adam suçsuz sayılacaktır. Yalnız yaralının kaybettiği zamanın karşılığını ödeyecek ve tümüyle iyileşmesini sağlayacaktır."));
trsuperobject.biblemap.put("b2_21_20", new trsuperobject(2,21,20,"‹‹Bir adam erkek ya da kadın kölesini değnekle döverken öldürürse, kesinlikle cezalandırılacaktır."));
trsuperobject.biblemap.put("b2_21_21", new trsuperobject(2,21,21,"Ama köle hemen ölmez, bir iki gün sonra ölürse, köle sahibi ceza görmeyecektir. Çünkü köle onun malı sayılır."));
trsuperobject.biblemap.put("b2_21_22", new trsuperobject(2,21,22,"‹‹İki kişi kavga ederken gebe bir kadına çarpar, kadın erken doğum yapar ama başka bir zarar görmezse, saldırgan, kadının kocasının istediği ve yargıçların onayladığı miktarda para cezasına çarptırılacaktır."));
trsuperobject.biblemap.put("b2_21_23-25", new trsuperobject(2,21,23-25,"Ama başka bir zarar varsa, cana karşılık can, göze karşılık göz, dişe karşılık diş, ele karşılık el, ayağa karşılık ayak, yanığa karşılık yanık, yaraya karşılık yara, bereye karşılık bere ödenecektir."));
trsuperobject.biblemap.put("b2_21_26", new trsuperobject(2,21,26,"‹‹Bir adam erkek ya da kadın kölesini gözüne vurarak kör ederse, gözüne karşılık onu özgür bırakacaktır."));
trsuperobject.biblemap.put("b2_21_27", new trsuperobject(2,21,27,"Eğer erkek ya da kadın kölesinin dişini kırarsa, dişine karşılık onu özgür bırakacaktır.››"));
trsuperobject.biblemap.put("b2_21_28", new trsuperobject(2,21,28,"‹‹Eğer bir boğa bir erkeği ya da kadını boynuzuyla vurup öldürürse, kesinlikle taşlanacak ve eti yenmeyecektir. Boğanın sahibi ise suçsuz sayılacaktır."));
trsuperobject.biblemap.put("b2_21_29", new trsuperobject(2,21,29,"Ama saldırganlığı bilinen bir boğanın sahibi uyarılmasına karşın boğasına sahip çıkmazsa ve boğası bir erkeği ya da kadını öldürürse, hem boğa taşlanacak, hem de sahibi öldürülecektir."));
trsuperobject.biblemap.put("b2_21_30", new trsuperobject(2,21,30,"Ancak, boğanın sahibinden para cezası istenirse, istenen miktarı ödeyerek canını kurtarabilir."));
trsuperobject.biblemap.put("b2_21_31", new trsuperobject(2,21,31,"Boğa ister erkek, ister kız çocuğunu öldürsün, aynı kural uygulanacaktır."));
trsuperobject.biblemap.put("b2_21_32", new trsuperobject(2,21,32,"Eğer boğa bir erkek ya da kadın köleyi öldürürse, kölenin efendisine otuz şekel gümüş verilecek ve boğa taşlanacaktır."));
trsuperobject.biblemap.put("b2_21_33", new trsuperobject(2,21,33,"‹‹Bir adam bir çukur açar ya da kazdığı çukurun üzerini örtmezse ve çukura bir boğa ya da bir eşek düşerse,"));
trsuperobject.biblemap.put("b2_21_34", new trsuperobject(2,21,34,"çukuru kazan hayvanın bedelini ödeyecektir. Parayı hayvanın sahibine verecek, ölü hayvan kendisinin olacaktır."));
trsuperobject.biblemap.put("b2_21_35", new trsuperobject(2,21,35,"‹‹Bir adamın boğası komşusunun boğasını yaralar, yaralı boğa ölürse, sağ boğayı satıp parasını paylaşacak, ölü hayvanı da bölüşeceklerdir."));
trsuperobject.biblemap.put("b2_21_36", new trsuperobject(2,21,36,"Eğer boğanın saldırgan olduğu ve sahibinin ona sahip çıkmadığı biliniyorsa, boğaya karşılık boğa verecek ve ölü hayvan kendisine kalacaktır.››"));
trsuperobject.biblemap.put("b2_22_1", new trsuperobject(2,22,1,"‹‹Bir adam öküz ya da davar çalıp boğazlar ya da satarsa, bir öküze karşılık beş öküz, bir koyuna karşılık dört koyun ödeyecektir."));
trsuperobject.biblemap.put("b2_22_2", new trsuperobject(2,22,2,"‹‹Bir hırsız bir eve girerken yakalanıp öldürülürse, öldüren kişi suçlu sayılmaz."));
trsuperobject.biblemap.put("b2_22_3", new trsuperobject(2,22,3,"Ancak olay güneş doğduktan sonra olmuşsa, kan dökmekten sorumlu sayılır. ‹‹Hırsız çaldığının karşılığını kesinlikle ödemelidir. Hiçbir şeyi yoksa, hırsızlık yaptığı için köle olarak satılacaktır."));
trsuperobject.biblemap.put("b2_22_4", new trsuperobject(2,22,4,"Çaldığı mal -öküz, eşek ya da koyun- sağ olarak elinde yakalanırsa, iki katını ödeyecektir."));
trsuperobject.biblemap.put("b2_22_5", new trsuperobject(2,22,5,"‹‹Tarlada ya da bağda hayvanlarını otlatan bir adam, hayvanlarının başkasının tarlasında otlamasına izin verirse, zararı kendi tarlasının ya da bağının en iyi ürünleriyle ödeyecektir."));
trsuperobject.biblemap.put("b2_22_6", new trsuperobject(2,22,6,"‹‹Birinin yaktığı ateş dikenlere sıçrar, ekin demetleri, tarladaki ekin ya da tarla yanarsa, yangın çıkaran kişi zararı ödeyecektir."));
trsuperobject.biblemap.put("b2_22_7", new trsuperobject(2,22,7,"‹‹Biri komşusuna saklasın diye parasını ya da eşyasını emanet eder ve bunlar komşusunun evinden çalınırsa, hırsız yakalandığında iki katını ödemelidir."));
trsuperobject.biblemap.put("b2_22_8", new trsuperobject(2,22,8,"Ama hırsız yakalanmazsa, komşusunun eşyasına el uzatıp uzatmadığının anlaşılması için ev sahibi yargıç huzuruna çıkmalıdır."));
trsuperobject.biblemap.put("b2_22_9", new trsuperobject(2,22,9,"Emanete ihanet edilen konularda, öküz, eşek, koyun, giysi, herhangi bir kayıp eşya için ‹Bu benimdir› diyen her iki taraf sorunu yargıcın huzuruna getirmelidir. Yargıcın suçlu bulduğu kişi komşusuna iki kat ödeyecektir."));
trsuperobject.biblemap.put("b2_22_10", new trsuperobject(2,22,10,"‹‹Bir adam komşusuna korusun diye eşek, öküz, koyun ya da herhangi bir hayvan emanet ettiğinde, hayvan ölür, sakatlanır ya da kimse görmeden çalınırsa,"));
trsuperobject.biblemap.put("b2_22_11", new trsuperobject(2,22,11,"komşusu adamın malına el uzatmadığına ilişkin RABbin huzurunda ant içmelidir. Mal sahibi bunu kabul edecek ve komşusu bir şey ödemeyecektir."));
trsuperobject.biblemap.put("b2_22_12", new trsuperobject(2,22,12,"Ama mal gerçekten ondan çalınmışsa, karşılığı sahibine ödenmelidir."));
trsuperobject.biblemap.put("b2_22_13", new trsuperobject(2,22,13,"Emanet hayvan parçalanmışsa, adam parçalarını kanıt olarak göstermelidir. Parçalanan hayvan için bir şey ödemeyecektir."));
trsuperobject.biblemap.put("b2_22_14", new trsuperobject(2,22,14,"‹‹Biri komşusundan bir hayvan ödünç alır, sahibi yokken hayvan sakatlanır ya da ölürse, karşılığını ödemelidir."));
trsuperobject.biblemap.put("b2_22_15", new trsuperobject(2,22,15,"Ama sahibi hayvanla birlikteyse, ödünç alan karşılığını ödemeyecektir. Hayvan kiralanmışsa, kayıp ödenen kiraya sayılmalıdır.››"));
trsuperobject.biblemap.put("b2_22_16", new trsuperobject(2,22,16,"‹‹Eğer biri nişanlı olmayan bir kızı aldatıp onunla yatarsa, başlık parasını ödemeli ve onunla evlenmelidir."));
trsuperobject.biblemap.put("b2_22_17", new trsuperobject(2,22,17,"Babası kızını ona vermeyi reddederse, adam normal başlık parası neyse onu ödemelidir."));
trsuperobject.biblemap.put("b2_22_18", new trsuperobject(2,22,18,"‹‹Büyücü kadını yaşatmayacaksınız."));
trsuperobject.biblemap.put("b2_22_19", new trsuperobject(2,22,19,"‹‹Hayvanlarla cinsel ilişki kuran herkes öldürülecektir."));
trsuperobject.biblemap.put("b2_22_20", new trsuperobject(2,22,20,"‹‹RABden başka bir ilaha kurban kesen ölüm cezasına çarptırılacaktır."));
trsuperobject.biblemap.put("b2_22_21", new trsuperobject(2,22,21,"‹‹Yabancıya haksızlık ve baskı yapmayacaksınız. Çünkü siz de Mısırda yabancıydınız."));
trsuperobject.biblemap.put("b2_22_22", new trsuperobject(2,22,22,"‹‹Dul ve öksüz hakkı yemeyeceksiniz."));
trsuperobject.biblemap.put("b2_22_23", new trsuperobject(2,22,23,"Yerseniz, bana feryat ettiklerinde onları kesinlikle işitirim."));
trsuperobject.biblemap.put("b2_22_24", new trsuperobject(2,22,24,"Öfkem alevlenir, sizi kılıçtan geçirtirim. Kadınlarınız dul, çocuklarınız öksüz kalır."));
trsuperobject.biblemap.put("b2_22_25", new trsuperobject(2,22,25,"‹‹Halkıma, aranızda yaşayan bir yoksula ödünç para verirseniz, ona tefeci gibi davranmayacaksınız. Üzerine faiz eklemeyeceksiniz."));
trsuperobject.biblemap.put("b2_22_26", new trsuperobject(2,22,26,"Komşunuzun abasını rehin alırsanız, gün batmadan geri vereceksiniz."));
trsuperobject.biblemap.put("b2_22_27", new trsuperobject(2,22,27,"Çünkü tek örtüsü abasıdır, ancak onunla örtünebilir. Onsuz nasıl yatar? Bana feryat ederse işiteceğim, çünkü ben iyilikseverim."));
trsuperobject.biblemap.put("b2_22_28", new trsuperobject(2,22,28,"‹‹Tanrıya sövmeyeceksiniz. Halkınızın önderine lanet etmeyeceksiniz."));
trsuperobject.biblemap.put("b2_22_29", new trsuperobject(2,22,29,"‹‹Ürününüzü ve şıranızı sunmakta gecikmeyeceksiniz. İlk doğan oğullarınızı bana vereceksiniz."));
trsuperobject.biblemap.put("b2_22_30", new trsuperobject(2,22,30,"Öküzlerinize, davarlarınıza da aynı şeyi yapacaksınız. Yedi gün analarıyla kalacaklar, sekizinci gün onları bana vereceksiniz."));
trsuperobject.biblemap.put("b2_22_31", new trsuperobject(2,22,31,"‹‹Benim kutsal halkım olacaksınız. Bunun içindir ki, kırda parçalanmış hayvanların etini yemeyecek, köpeklerin önüne atacaksınız.››"));
trsuperobject.biblemap.put("b2_23_1", new trsuperobject(2,23,1,"‹‹Yalan haber taşımayacaksınız. Haksız yere tanıklık ederek kötü kişiye yan çıkmayacaksınız."));
trsuperobject.biblemap.put("b2_23_2", new trsuperobject(2,23,2,"‹‹Kötülük yapan kalabalığı izlemeyeceksiniz. Bir davada çoğunluktan yana konuşarak adaleti saptırmayacaksınız."));
trsuperobject.biblemap.put("b2_23_3", new trsuperobject(2,23,3,"Duruşmada yoksulu kayırmayacaksınız."));
trsuperobject.biblemap.put("b2_23_4", new trsuperobject(2,23,4,"‹‹Düşmanınızın yolunu şaşırmış öküzüne ya da eşeğine rastlarsanız, onu kendisine geri götüreceksiniz."));
trsuperobject.biblemap.put("b2_23_5", new trsuperobject(2,23,5,"Sizden nefret eden kişinin eşeğini yük altında çökmüş görürseniz, kendi haline bırakıp gitmeyecek, ona yardımcı olacaksınız."));
trsuperobject.biblemap.put("b2_23_6", new trsuperobject(2,23,6,"‹‹Duruşmada yoksula karşı adaleti saptırmayacaksınız."));
trsuperobject.biblemap.put("b2_23_7", new trsuperobject(2,23,7,"Yalandan uzak duracak, suçsuz ve doğru kişiyi öldürmeyeceksiniz. Çünkü ben kötü kişiyi aklamam."));
trsuperobject.biblemap.put("b2_23_8", new trsuperobject(2,23,8,"‹‹Rüşvet almayacaksınız. Çünkü rüşvet göreni kör eder, haklıyı haksız çıkarır."));
trsuperobject.biblemap.put("b2_23_9", new trsuperobject(2,23,9,"‹‹Yabancıya baskı yapmayacaksınız. Yabancılığın ne olduğunu bilirsiniz. Çünkü siz de Mısırda yabancıydınız."));
trsuperobject.biblemap.put("b2_23_10", new trsuperobject(2,23,10,"‹‹Toprağınızı altı yıl ekecek, ürününü toplayacaksınız."));
trsuperobject.biblemap.put("b2_23_11", new trsuperobject(2,23,11,"Ama yedinci yıl nadasa bırakacaksınız; öyle ki, halkınızın arasındaki yoksullar yiyecek bulabilsin, onlardan artakalanı da yabanıl hayvanlar yesin. Bağınıza ve zeytinliğinize de aynı şeyi yapın."));
trsuperobject.biblemap.put("b2_23_12", new trsuperobject(2,23,12,"‹‹Altı gün çalışacak, yedinci gün dinleneceksiniz. Böylece hem öküzünüz, eşeğiniz dinlenir, hem de kadın kölenizin oğulları ve yabancılar rahat eder."));
trsuperobject.biblemap.put("b2_23_13", new trsuperobject(2,23,13,"‹‹Söylediğim her şeyi yerine getirin. Başka ilahların adını anmayın, ağzınıza almayın.››"));
trsuperobject.biblemap.put("b2_23_14", new trsuperobject(2,23,14,"‹‹Yılda üç kez bana bayram yapacaksınız."));
trsuperobject.biblemap.put("b2_23_15", new trsuperobject(2,23,15,"Size buyurduğum gibi, Aviv ayının belirli günlerinde yedi gün mayasız ekmek yiyerek Mayasız Ekmek Bayramını kutlayacaksınız. Çünkü Mısırdan o ay çıktınız. ‹‹Kimse huzuruma eli boş çıkmasın."));
trsuperobject.biblemap.put("b2_23_16", new trsuperobject(2,23,16,"‹‹Tarlaya ektiğiniz ürünleri biçtiğinizde ilk ürünlerle Hasat Bayramını kutlayacaksınız. ‹‹Yıl sonunda tarladan ürünlerinizi topladığınızda Ürün Devşirme Bayramını kutlayacaksınız."));
trsuperobject.biblemap.put("b2_23_17", new trsuperobject(2,23,17,"‹‹Bütün erkekleriniz yılda üç kez ben Egemen RABbin huzuruna çıkacaklar."));
trsuperobject.biblemap.put("b2_23_18", new trsuperobject(2,23,18,"‹‹Evinizde maya bulunduğu sürece bana kurban kesmeyeceksiniz. ‹‹Bayramda bana kurban edilen hayvanın yağı sabaha bırakılmamalı."));
trsuperobject.biblemap.put("b2_23_19", new trsuperobject(2,23,19,"‹‹Toprağınızın seçme ilk ürünlerini Tanrınız RABbin Tapınağına getireceksiniz. ‹‹Oğlağı anasının sütünde haşlamayacaksınız.››"));
trsuperobject.biblemap.put("b2_23_20", new trsuperobject(2,23,20,"‹‹Yolda sizi koruması, hazırladığım yere götürmesi için önünüzden bir melek gönderiyorum."));
trsuperobject.biblemap.put("b2_23_21", new trsuperobject(2,23,21,"Ona dikkat edin, sözünü dinleyin, başkaldırmayın. Çünkü beni temsil ettiği için başkaldırınızı bağışlamaz."));
trsuperobject.biblemap.put("b2_23_22", new trsuperobject(2,23,22,"Ama onun sözünü dikkatle dinler, bütün söylediklerimi yerine getirirseniz, düşmanlarınıza düşman, hasımlarınıza hasım olacağım."));
trsuperobject.biblemap.put("b2_23_23", new trsuperobject(2,23,23,"Meleğim önünüzden gidecek, sizi Amor, Hitit, Periz, Kenan, Hiv ve Yevus topraklarına götürecek. Onları yok edeceğim."));
trsuperobject.biblemap.put("b2_23_24", new trsuperobject(2,23,24,"Onların ilahları önünde eğilmeyecek, tapınmayacaksınız; törelerini izlemeyeceksiniz. Tersine, ilahlarını yok edecek, dikili taşlarını büsbütün parçalayacaksınız."));
trsuperobject.biblemap.put("b2_23_25", new trsuperobject(2,23,25,"Tanrınız RABbe tapacaksınız. Ekmeğinizi, suyunuzu bereketli kılacak, aranızdaki hastalıkları yok edeceğim."));
trsuperobject.biblemap.put("b2_23_26", new trsuperobject(2,23,26,"Ülkenizde kısır ve çocuk düşüren kadın olmayacak. Size uzun ömür vereceğim."));
trsuperobject.biblemap.put("b2_23_27", new trsuperobject(2,23,27,"‹‹Dehşetimi önünüzden gönderecek, karşılaşacağınız bütün halkları şaşkına çevireceğim. Düşmanlarınız önünüzden kaçacak."));
trsuperobject.biblemap.put("b2_23_28", new trsuperobject(2,23,28,"Hivlileri, Kenanlıları, Hititleri önünüzden kovmaları için önünüzsıra eşekarısıfü göndereceğim."));
trsuperobject.biblemap.put("b2_23_29", new trsuperobject(2,23,29,"Ama onları bir yıl içinde kovmayacağım. Yoksa ülke viran olur, yabanıl hayvanlar çoğaldıkça çoğalır, sayıları sizi aşar."));
trsuperobject.biblemap.put("b2_23_30", new trsuperobject(2,23,30,"Siz çoğalıncaya, toprağı yurt edininceye dek onları azar azar kovacağım. bilinmiyor. Dehşet ya da bir çeşit hastalık anlamına da gelebilir."));
trsuperobject.biblemap.put("b2_23_31", new trsuperobject(2,23,31,"‹‹Sınırlarınızı Kızıldenizden Filist Denizine, çölden Fırat Irmağına kadar genişleteceğim. Ülke halkını elinize teslim edeceğim. Onları önünüzden kovacaksınız."));
trsuperobject.biblemap.put("b2_23_32", new trsuperobject(2,23,32,"Onlarla ya da ilahlarıyla antlaşma yapmayacaksınız."));
trsuperobject.biblemap.put("b2_23_33", new trsuperobject(2,23,33,"Onları ülkenizde barındırmayacaksınız. Yoksa bana karşı günah işlemenize neden olurlar. İlahlarına taparsanız, size tuzak olur.››"));
trsuperobject.biblemap.put("b2_24_2", new trsuperobject(2,24,2,"Yalnız sen bana yaklaşacaksın. Ötekiler yaklaşmamalı. Halk seninle dağa çıkmamalı.››"));
trsuperobject.biblemap.put("b2_24_3", new trsuperobject(2,24,3,"Musa gidip RABbin bütün buyruklarını, ilkelerini halka anlattı. Herkes bir ağızdan, ‹‹RABbin her söylediğini yapacağız›› diye karşılık verdi."));
trsuperobject.biblemap.put("b2_24_4", new trsuperobject(2,24,4,"Musa RABbin bütün buyruklarını yazdı. Sabah erkenden kalkıp dağın eteğinde bir sunak kurdu, İsrailin on iki oymağını simgeleyen on iki taş sütun dikti."));
trsuperobject.biblemap.put("b2_24_5", new trsuperobject(2,24,5,"Sonra İsrailli gençleri gönderdi. Onlar da RABbe yakmalık sunular sundular, esenlik kurbanları olarak boğalar kestiler."));
trsuperobject.biblemap.put("b2_24_6", new trsuperobject(2,24,6,"Musa kanın yarısını leğenlere doldurdu, öbür yarısını sunağın üzerine döktü."));
trsuperobject.biblemap.put("b2_24_7", new trsuperobject(2,24,7,"Sonra antlaşma kitabını alıp halka okudu. Halk, ‹‹RABbin her söylediğini yapacağız, Onu dinleyeceğiz›› dedi."));
trsuperobject.biblemap.put("b2_24_8", new trsuperobject(2,24,8,"Musa leğenlerdeki kanı halkın üzerine serpti ve, ‹‹Bütün bu sözler uyarınca, RABbin sizinle yaptığı antlaşmanın kanı budur›› dedi."));
trsuperobject.biblemap.put("b2_24_9", new trsuperobject(2,24,9,"Sonra Musa, Harun, Nadav, Avihu ve İsrail ileri gelenlerinden yetmiş kişi dağa çıkarak"));
trsuperobject.biblemap.put("b2_24_10", new trsuperobject(2,24,10,"İsrailin Tanrısını gördüler. Tanrının ayakları altında laciverttaşını andıran bir döşeme vardı. Gök gibi duruydu."));
trsuperobject.biblemap.put("b2_24_11", new trsuperobject(2,24,11,"Tanrı İsrail soylularına zarar vermedi. Tanrıyı gördüler, sonra yiyip içtiler."));
trsuperobject.biblemap.put("b2_24_12", new trsuperobject(2,24,12,"RAB Musaya, ‹‹Dağa, yanıma gel›› dedi, ‹‹Burada bekle, halkın öğrenmesi için üzerine yasalarla buyrukları yazdığım taş levhaları sana vereceğim.››"));
trsuperobject.biblemap.put("b2_24_13", new trsuperobject(2,24,13,"Musayla yardımcısı Yeşu hazırlandılar. Musa Tanrı Dağına çıkarken,"));
trsuperobject.biblemap.put("b2_24_14", new trsuperobject(2,24,14,"İsrail ileri gelenlerine, ‹‹Geri dönünceye kadar bizi burada bekleyin›› dedi, ‹‹Harunla Hur aranızda; kimin sorunu olursa onlara başvursun.››"));
trsuperobject.biblemap.put("b2_24_15", new trsuperobject(2,24,15,"Musa dağa çıkınca, bulut dağı kapladı."));
trsuperobject.biblemap.put("b2_24_16", new trsuperobject(2,24,16,"RABbin görkemi Sina Dağının üzerine indi. Bulut dağı altı gün örttü. Yedinci gün RAB bulutun içinden Musaya seslendi."));
trsuperobject.biblemap.put("b2_24_17", new trsuperobject(2,24,17,"RABbin görkemi İsraillilere dağın doruğunda yakıcı bir ateş gibi görünüyordu."));
trsuperobject.biblemap.put("b2_24_18", new trsuperobject(2,24,18,"Musa bulutun içinden dağa çıktı. Kırk gün kırk gece dağda kaldı."));
trsuperobject.biblemap.put("b2_25_2", new trsuperobject(2,25,2,"‹‹İsraillilere söyle, bana armağan getirsinler. Gönülden veren herkesin armağanını alın."));
trsuperobject.biblemap.put("b2_25_3", new trsuperobject(2,25,3,"Onlardan alacağınız armağanlar şunlardır: Altın, gümüş, tunç;"));
trsuperobject.biblemap.put("b2_25_4", new trsuperobject(2,25,4,"lacivert, mor, kırmızı iplik; ince keten, keçi kılı,"));
trsuperobject.biblemap.put("b2_25_5", new trsuperobject(2,25,5,"deri, kırmızı boyalı koç derisi, akasya ağacı,"));
trsuperobject.biblemap.put("b2_25_6", new trsuperobject(2,25,6,"kandil için zeytinyağı, mesh yağıyla güzel kokulu buhur için baharat,"));
trsuperobject.biblemap.put("b2_25_7", new trsuperobject(2,25,7,"başkâhinin efoduyla göğüslüğü için oniks ve öbür kakma taşlar."));
trsuperobject.biblemap.put("b2_25_8", new trsuperobject(2,25,8,"‹‹Aralarında yaşamam için bana kutsal bir yer yapsınlar."));
trsuperobject.biblemap.put("b2_25_9", new trsuperobject(2,25,9,"Konutu ve eşyalarını sana göstereceğim örneğe tıpatıp uygun yapın.››"));
trsuperobject.biblemap.put("b2_25_10", new trsuperobject(2,25,10,"‹‹Akasya ağacından bir sandık yapsınlar. Boyu iki buçuk, eni ve yüksekliği birer buçuk arşın olsun."));
trsuperobject.biblemap.put("b2_25_11", new trsuperobject(2,25,11,"İçini de dışını da saf altınla kapla. Çevresine altın pervaz yap."));
trsuperobject.biblemap.put("b2_25_12", new trsuperobject(2,25,12,"Dört altın halka döküp dört ayağına tak. İkisi bir yanda, ikisi öbür yanda olacak."));
trsuperobject.biblemap.put("b2_25_13", new trsuperobject(2,25,13,"Akasya ağacından sırıklar yapıp altınla kapla."));
trsuperobject.biblemap.put("b2_25_14", new trsuperobject(2,25,14,"Sandığın taşınması için sırıkları yanlardaki halkalara geçir."));
trsuperobject.biblemap.put("b2_25_15", new trsuperobject(2,25,15,"Sırıklar sandığın halkalarında kalacak, çıkarılmayacak."));
trsuperobject.biblemap.put("b2_25_16", new trsuperobject(2,25,16,"Antlaşmanın taş levhalarını sana vereceğim. Onları sandığın içine koy."));
trsuperobject.biblemap.put("b2_25_17", new trsuperobject(2,25,17,"‹‹Saf altından bir Bağışlanma Kapağı yap. Boyu iki buçuk, eni bir buçuk arşın olacak."));
trsuperobject.biblemap.put("b2_25_18", new trsuperobject(2,25,18,"Kapağın iki kenarına dövme altından birer Keruv yap."));
trsuperobject.biblemap.put("b2_25_19", new trsuperobject(2,25,19,"Keruvlardan birini bir kenara, öbürünü öteki kenara, kapakla tek parça halinde yap."));
trsuperobject.biblemap.put("b2_25_20", new trsuperobject(2,25,20,"Keruvlar yukarı doğru açık kanatlarıyla kapağı örtecek. Yüzleri birbirine dönük olacak ve kapağa bakacak."));
trsuperobject.biblemap.put("b2_25_21", new trsuperobject(2,25,21,"Kapağı sandığın üzerine, sana vereceğim taş levhaları ise sandığın içine koy."));
trsuperobject.biblemap.put("b2_25_22", new trsuperobject(2,25,22,"Seninle orada, Levha Sandığının üstündeki Keruvlar arasında, Bağışlanma Kapağının üzerinde görüşeceğim ve İsrailliler için sana buyruklar vereceğim.›› ‹‹Kapporet›› sözcüğü Tanrının öfkesinin yatıştırıldığı, halkının günahlarının bağışlanıp Tanrıyla barıştırıldığı özel yeri ifade ediyordu."));
trsuperobject.biblemap.put("b2_25_23", new trsuperobject(2,25,23,"‹‹Akasya ağacından bir masa yap. Boyu ikifç, eni bir, yüksekliği bir buçuk arşın olacak."));
trsuperobject.biblemap.put("b2_25_24", new trsuperobject(2,25,24,"Masayı saf altınla kapla. Çevresine altın pervaz yap."));
trsuperobject.biblemap.put("b2_25_25", new trsuperobject(2,25,25,"Pervazın çevresine dört parmak eninde bir kenarlık yaparak altın pervazla çevir."));
trsuperobject.biblemap.put("b2_25_26", new trsuperobject(2,25,26,"Masa için dört altın halka yap, dört ayak üzerindeki dört köşeye yerleştir."));
trsuperobject.biblemap.put("b2_25_27", new trsuperobject(2,25,27,"Masanın taşınması için sırıkların içinden geçeceği halkalar kenarlığa yakın olmalı."));
trsuperobject.biblemap.put("b2_25_28", new trsuperobject(2,25,28,"Sırıkları akasya ağacından yap, altınla kapla. Masa onlarla taşınacak."));
trsuperobject.biblemap.put("b2_25_29", new trsuperobject(2,25,29,"Masa için saf altından tabaklar, sahanlar, dökmelik sunu testileri, tasları yap."));
trsuperobject.biblemap.put("b2_25_30", new trsuperobject(2,25,30,"Ekmekleri sürekli olarak huzuruma, masanın üzerine koyacaksın.››"));
trsuperobject.biblemap.put("b2_25_31", new trsuperobject(2,25,31,"‹‹Saf altından bir kandillik yap. Ayağı, gövdesi dövme altın olsun. Çanak, tomurcuk ve çiçek motifleri kendinden olsun."));
trsuperobject.biblemap.put("b2_25_32", new trsuperobject(2,25,32,"Kandillik üç kolu bir yanda, üç kolu öteki yanda olmak üzere altı kollu olacak."));
trsuperobject.biblemap.put("b2_25_33", new trsuperobject(2,25,33,"Her kolda badem çiçeğini andıran üç çanak, tomurcuk ve çiçek motifi bulunacak. Altı kol da aynı olacak."));
trsuperobject.biblemap.put("b2_25_34", new trsuperobject(2,25,34,"Kandilliğin gövdesinde badem çiçeğini andıran dört çanak, tomurcuk ve çiçek motifi olacak."));
trsuperobject.biblemap.put("b2_25_35", new trsuperobject(2,25,35,"Kandillikten yükselen ilk iki kolun, ikinci iki kolun, üçüncü iki kolun altında kendinden birer tomurcuk bulunacak. Toplam altı kol olacak."));
trsuperobject.biblemap.put("b2_25_36", new trsuperobject(2,25,36,"Tomurcukları, kolları tek parça olan kandillik saf dövme altından olacak."));
trsuperobject.biblemap.put("b2_25_37", new trsuperobject(2,25,37,"‹‹Kandillik için yedi kandil yap; kandiller karşısını aydınlatacak biçimde yerleştirilsin."));
trsuperobject.biblemap.put("b2_25_38", new trsuperobject(2,25,38,"Fitil maşaları, tablaları saf altından olacak."));
trsuperobject.biblemap.put("b2_25_39", new trsuperobject(2,25,39,"Bütün takımları dahil kandilliğe bir talant saf altın harcanacak."));
trsuperobject.biblemap.put("b2_25_40", new trsuperobject(2,25,40,"Her şeyi sana dağda gösterilen örneğe göre yapmaya dikkat et.››"));
trsuperobject.biblemap.put("b2_26_1", new trsuperobject(2,26,1,"‹‹Tanrının Konutunu on perdeden yap. Perdeler lacivert, mor, kırmızı iplikle özenle dokunmuş ince ketenden olsun, üzeri Keruvlarla ustaca süslensin."));
trsuperobject.biblemap.put("b2_26_2", new trsuperobject(2,26,2,"Her perdenin boyu yirmi sekiz, eni dört arşın olmalı. Bütün perdeler aynı ölçüde olacak."));
trsuperobject.biblemap.put("b2_26_3", new trsuperobject(2,26,3,"Perdeler beşer beşer birbirine eklenerek iki takım perde yapılacak."));
trsuperobject.biblemap.put("b2_26_4", new trsuperobject(2,26,4,"Birinci takımın kenarına lacivert ilmekler aç. Öbür takımın kenarına da aynı şeyi yap."));
trsuperobject.biblemap.put("b2_26_5", new trsuperobject(2,26,5,"Birinci takımın ilk perdesiyle ikinci takımın son perdesine ellişer ilmek aç. İlmekler birbirine karşı olmalı."));
trsuperobject.biblemap.put("b2_26_6", new trsuperobject(2,26,6,"Elli altın kopça yap, perdeleri kopçalayarak çadırı birleştir. Böylece konut tek parça haline gelecek."));
trsuperobject.biblemap.put("b2_26_7", new trsuperobject(2,26,7,"‹‹Konutun üstünü kaplayacak çadır için keçi kılından on bir perde yap."));
trsuperobject.biblemap.put("b2_26_8", new trsuperobject(2,26,8,"Her perdenin boyu otuzfı, eni dört arşın olacak. On bir perde de aynı ölçüde olmalı."));
trsuperobject.biblemap.put("b2_26_9", new trsuperobject(2,26,9,"Beş perde birbirine, altı perde birbirine birleştirilecek. Altıncı perdeyi çadırın önünde katla."));
trsuperobject.biblemap.put("b2_26_10", new trsuperobject(2,26,10,"Her iki perde takımının kenarlarına ellişer ilmek aç."));
trsuperobject.biblemap.put("b2_26_11", new trsuperobject(2,26,11,"Elli tunç kopça yap, kopçaları ilmeklere geçir ki, çadır tek parça haline gelsin."));
trsuperobject.biblemap.put("b2_26_12", new trsuperobject(2,26,12,"Çadırın perdelerinden artan yarım perde konutun arkasından sarkacak."));
trsuperobject.biblemap.put("b2_26_13", new trsuperobject(2,26,13,"Perdelerin uzun kenarlarından artan kumaş çadırın yanlarından birer arşın sarkarak konutu örtecek."));
trsuperobject.biblemap.put("b2_26_14", new trsuperobject(2,26,14,"Çadır için kırmızı boyalı koç derilerinden bir örtü, onun üstüne de deriden başka bir örtü yap."));
trsuperobject.biblemap.put("b2_26_15", new trsuperobject(2,26,15,"‹‹Konut için akasya ağacından dikine çerçeveler yap."));
trsuperobject.biblemap.put("b2_26_16", new trsuperobject(2,26,16,"Her çerçevenin boyu on, eni bir buçuk arşın olacak."));
trsuperobject.biblemap.put("b2_26_17", new trsuperobject(2,26,17,"Çerçevelerin birbirine uyan iki paralel çıkıntısı olacak. Konutun bütün çerçevelerini aynı biçimde yapacaksın."));
trsuperobject.biblemap.put("b2_26_18", new trsuperobject(2,26,18,"Konutun güneyi için yirmi çerçeve yap."));
trsuperobject.biblemap.put("b2_26_19", new trsuperobject(2,26,19,"Her çerçevenin altında iki çıkıntı için birer taban olmak üzere, yirmi çerçevenin altında kırk gümüş taban yap."));
trsuperobject.biblemap.put("b2_26_2-021", new trsuperobject(2,26,2-021,"Konutun öbür yanı, yani kuzeyi için de yirmi çerçeve ve her çerçevenin altında iki taban olmak üzere kırk gümüş taban yap."));
trsuperobject.biblemap.put("b2_26_22", new trsuperobject(2,26,22,"Konutun batıya bakacak arka tarafı için altı çerçeve yap."));
trsuperobject.biblemap.put("b2_26_23", new trsuperobject(2,26,23,"Arkada konutun köşeleri için iki çerçeve yap."));
trsuperobject.biblemap.put("b2_26_24", new trsuperobject(2,26,24,"Bu köşe çerçevelerinin alt tarafı ayrı kalacak, üst tarafı ise birinci halkayla birleştirilecek. İki köşeyi oluşturan iki çerçeve aynı biçimde olacak."));
trsuperobject.biblemap.put("b2_26_25", new trsuperobject(2,26,25,"Böylece sekiz çerçeve ve her çerçevenin altında iki taban olmak üzere on altı gümüş taban olacak."));
trsuperobject.biblemap.put("b2_26_26-27", new trsuperobject(2,26,26-27,"‹‹Konutun bir yanındaki çerçeveler için beş, öbür yanındaki çerçeveler için beş, batıya bakan arka tarafındaki çerçeveler için de beş olmak üzere akasya ağacından kirişler yap."));
trsuperobject.biblemap.put("b2_26_28", new trsuperobject(2,26,28,"Çerçevelerin ortasındaki kiriş çadırın bir ucundan öbür ucuna geçecek."));
trsuperobject.biblemap.put("b2_26_29", new trsuperobject(2,26,29,"Çerçeveleri ve kirişleri altınla kapla, kirişlerin geçeceği halkaları da altından yap."));
trsuperobject.biblemap.put("b2_26_30", new trsuperobject(2,26,30,"‹‹Konutu dağda sana gösterilen plana göre yap."));
trsuperobject.biblemap.put("b2_26_31", new trsuperobject(2,26,31,"‹‹Lacivert, mor, kırmızı iplikle özenle dokunmuş ince ketenden bir perde yap; üzerini Keruvlarla ustaca süsle."));
trsuperobject.biblemap.put("b2_26_32", new trsuperobject(2,26,32,"Dört gümüş taban üstünde duran akasya ağacından altın kaplı dört direk üzerine as. Çengelleri altın olacak."));
trsuperobject.biblemap.put("b2_26_33", new trsuperobject(2,26,33,"Perdeyi kopçaların altına asıp Levha Sandığını perdenin arkasına koy. Perde Kutsal Yerle En Kutsal Yeri birbirinden ayıracak."));
trsuperobject.biblemap.put("b2_26_34", new trsuperobject(2,26,34,"Bağışlanma Kapağını En Kutsal Yerde bulunan Levha Sandığının üzerine koy."));
trsuperobject.biblemap.put("b2_26_35", new trsuperobject(2,26,35,"Masayı perdenin öbür tarafına, konutun kuzeye bakan yanına yerleştir; kandilliği masanın karşısına, konutun güney tarafına koy."));
trsuperobject.biblemap.put("b2_26_36", new trsuperobject(2,26,36,"‹‹Çadırın giriş bölümüne lacivert, mor, kırmızı iplikle özenle dokunmuş ince ketenden nakışlı bir perde yap."));
trsuperobject.biblemap.put("b2_26_37", new trsuperobject(2,26,37,"Perdeyi asmak için akasya ağacından beş direk yap, altınla kapla. Çengelleri de altın olacak. Direkler için tunçtan beş taban dök.››"));
trsuperobject.biblemap.put("b2_27_1", new trsuperobject(2,27,1,"‹‹Sunağı akasya ağacından kare biçiminde yap. Eni ve boyu beşer arşın, yüksekliği üç arşın olacak."));
trsuperobject.biblemap.put("b2_27_2", new trsuperobject(2,27,2,"Dört üst köşesine kendinden boynuzlar yaparak hepsini tunçla kapla."));
trsuperobject.biblemap.put("b2_27_3", new trsuperobject(2,27,3,"Sunak için yağ ve kül kovaları, kürekler, çanaklar, büyük çatallar, ateş kapları yap. Tümü tunç olacak."));
trsuperobject.biblemap.put("b2_27_4", new trsuperobject(2,27,4,"Ağ biçiminde tunç bir ızgara da yap, dört köşesine birer tunç halka tak."));
trsuperobject.biblemap.put("b2_27_5", new trsuperobject(2,27,5,"Izgarayı sunağın kenarının altına koy. Öyle ki, aşağı doğru sunağın yarısına yetişsin."));
trsuperobject.biblemap.put("b2_27_6", new trsuperobject(2,27,6,"Sunak için akasya ağacından sırıklar yap, tunçla kapla."));
trsuperobject.biblemap.put("b2_27_7", new trsuperobject(2,27,7,"Sırıklar halkalara geçirilecek ve sunak taşınırken iki yanında olacak."));
trsuperobject.biblemap.put("b2_27_8", new trsuperobject(2,27,8,"Sunağı tahtadan, içi boş yapacaksın. Tıpkı dağda sana gösterildiği gibi olacak.››"));
trsuperobject.biblemap.put("b2_27_9", new trsuperobject(2,27,9,"‹‹Konuta bir avlu yap. Avlunun güney tarafı için yüz arşın boyunda, özenle dokunmuş ince keten perdeler yapacaksın."));
trsuperobject.biblemap.put("b2_27_10", new trsuperobject(2,27,10,"Perdeler için yirmi direk yapılacak; direklerin tabanları tunç, çengelleri ve çengel çemberleri gümüş olacak."));
trsuperobject.biblemap.put("b2_27_11", new trsuperobject(2,27,11,"Kuzey tarafı için yüz arşın boyunda perdeler, yirmi direk, direkler için yirmi tunç taban yapılacak. Direklerin çengelleriyle çemberleri gümüşten olacak."));
trsuperobject.biblemap.put("b2_27_12", new trsuperobject(2,27,12,"‹‹Avlunun batı tarafı için elli arşınfö boyunda perde, on direk, on taban yapılacak."));
trsuperobject.biblemap.put("b2_27_13", new trsuperobject(2,27,13,"Doğuya bakan tarafta avlunun eni elli arşın olacak."));
trsuperobject.biblemap.put("b2_27_14", new trsuperobject(2,27,14,"Girişin bir tarafında on beş arşın boyunda perde, üç direk ve üç taban olacak."));
trsuperobject.biblemap.put("b2_27_15", new trsuperobject(2,27,15,"Girişin öbür tarafında da on beş arşın boyunda perde, üç direk ve üç taban olacak."));
trsuperobject.biblemap.put("b2_27_16", new trsuperobject(2,27,16,"‹‹Avlunun girişinde lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden yirmi arşın boyunda nakışlı bir perde olacak. Dört direği ve dört tabanı bulunacak."));
trsuperobject.biblemap.put("b2_27_17", new trsuperobject(2,27,17,"Avlunun çevresindeki bütün direkler gümüş çemberlerle donatılacak. Çengelleri gümüş, tabanları tunç olacak."));
trsuperobject.biblemap.put("b2_27_18", new trsuperobject(2,27,18,"Avlunun boyu yüz, eni elli, çevresindeki perdelerin yüksekliği beş arşın olacak. Perdeleri özenle dokunmuş ince ketenden, tabanları tunçtan olacak."));
trsuperobject.biblemap.put("b2_27_19", new trsuperobject(2,27,19,"Konutta her türlü hizmet için kullanılacak bütün aletler, konutun ve avlunun bütün kazıkları da tunçtan olacak.››"));
trsuperobject.biblemap.put("b2_27_20", new trsuperobject(2,27,20,"‹‹İsrail halkına buyruk ver, kandilin sürekli yanıp ışık vermesi için saf sıkma zeytinyağı getirsinler."));
trsuperobject.biblemap.put("b2_27_21", new trsuperobject(2,27,21,"Harun'la oğulları kandilleri benim huzurumda, Buluşma Çadırı'ndafş, Levha Sandığı'nın önündeki perdenin dışında, akşamdan sabaha kadar yanar tutacaklar. İsrailliler için kuşaklar boyunca sürekli bir kural olacak bu.›› (bkz. Çık.33:7-10)."));
trsuperobject.biblemap.put("b2_28_1", new trsuperobject(2,28,1,"‹‹Bana kâhinlik etmeleri için İsrailliler arasından ağabeyin Harunu, oğulları Nadav, Avihu, Elazar ve İtamarı yanına al."));
trsuperobject.biblemap.put("b2_28_2", new trsuperobject(2,28,2,"Ağabeyin Haruna görkem ve saygınlık kazandırmak için kutsal giysiler yap."));
trsuperobject.biblemap.put("b2_28_3", new trsuperobject(2,28,3,"Bilgelik verdiğim becerikli adamlara söyle, Haruna giysi yapsınlar. Öyle ki, bana kâhinlik etmek için kutsal kılınmış olsun."));
trsuperobject.biblemap.put("b2_28_4", new trsuperobject(2,28,4,"Yapacakları giysiler şunlardır: Göğüslük, efod, kaftan, nakışlı mintan, sarık, kuşak. Bana kâhinlik etmeleri için ağabeyin Haruna ve oğullarına bu kutsal giysileri yapacaklar."));
trsuperobject.biblemap.put("b2_28_5", new trsuperobject(2,28,5,"Altın sırma, lacivert, mor, kırmızı iplik, ince keten kullanacaklar.››"));
trsuperobject.biblemap.put("b2_28_6", new trsuperobject(2,28,6,"‹‹Efodu altın sırmayla, lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden ustaca yapacaklar."));
trsuperobject.biblemap.put("b2_28_7", new trsuperobject(2,28,7,"Bağlanabilmesi için iki köşesine takılmış ikişer omuzluğu olacak."));
trsuperobject.biblemap.put("b2_28_8", new trsuperobject(2,28,8,"Efodun üzerinde efod gibi ustaca dokunmuş bir şerit olacak. Efodun bir parçası gibi lacivert, mor, kırmızı iplikle, altın sırmayla, özenle dokunmuş ince ketenden olacak."));
trsuperobject.biblemap.put("b2_28_9-10", new trsuperobject(2,28,9-10,"İki oniks taşı alacak, İsrailoğullarının adlarını, doğuş sırasına göre altısını birinin, altısını ötekinin üzerine oyacaksın."));
trsuperobject.biblemap.put("b2_28_11-12", new trsuperobject(2,28,11-12,"İsrailoğullarının adlarını bu iki taşın üzerine usta oymacıların mühür oyduğu gibi oyacaksın. Taşları altın yuvalar içine koyduktan sonra İsraillilerin anılması için efodun omuzluklarına tak. Harun, anılmaları için onların adlarını RABbin önünde iki omuzunda taşıyacak."));
trsuperobject.biblemap.put("b2_28_13-14", new trsuperobject(2,28,13-14,"Altın yuvalar ve saf altından iki zincir yap. Zincirleri örme kordon gibi yapıp yuvalara yerleştir.››"));
trsuperobject.biblemap.put("b2_28_15", new trsuperobject(2,28,15,"‹‹Usta işi bir karar göğüslüğü yap. Onu da efod gibi, altın sırmayla, lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden yap."));
trsuperobject.biblemap.put("b2_28_16", new trsuperobject(2,28,16,"Dört köşe, eni ve boyu birer karış olacak; ikiye katlanacak."));
trsuperobject.biblemap.put("b2_28_17", new trsuperobject(2,28,17,"Üzerine dört sıra taş yuvası kak. Birinci sırada yakut, topaz, zümrüt;"));
trsuperobject.biblemap.put("b2_28_18", new trsuperobject(2,28,18,"ikinci sırada firuze, laciverttaşı, aytaşı;"));
trsuperobject.biblemap.put("b2_28_19", new trsuperobject(2,28,19,"üçüncü sırada gökyakut, agat, ametist;"));
trsuperobject.biblemap.put("b2_28_20", new trsuperobject(2,28,20,"dördüncü sırada sarı yakut, oniks ve yeşim olacak. Taşlar altın yuvalara kakılacak."));
trsuperobject.biblemap.put("b2_28_21", new trsuperobject(2,28,21,"On iki taş olacak. Üzerlerine mühür oyar gibi İsrailoğullarının adları bir bir oyulacak. Bu taşlar İsrailin on iki oymağını simgeleyecek."));
trsuperobject.biblemap.put("b2_28_22", new trsuperobject(2,28,22,"‹‹Göğüslük için saf altından örme zincirler yap."));
trsuperobject.biblemap.put("b2_28_23", new trsuperobject(2,28,23,"İki altın halka yap, göğüslüğün üst iki köşesine birer halka koy."));
trsuperobject.biblemap.put("b2_28_24", new trsuperobject(2,28,24,"İki örme altın zinciri göğüslüğün köşelerindeki halkalara tak."));
trsuperobject.biblemap.put("b2_28_25", new trsuperobject(2,28,25,"Zincirlerin öteki iki ucunu iki yuvanın üzerinden geçirerek efodun ön tarafına, omuzlukların üzerine bağla."));
trsuperobject.biblemap.put("b2_28_26", new trsuperobject(2,28,26,"İki altın halka daha yap; her birini göğüslüğün alt iki köşesine, efoda bitişik iç kenarına tak."));
trsuperobject.biblemap.put("b2_28_27", new trsuperobject(2,28,27,"İki altın halka daha yap; efodun önündeki omuzluklara alttan, dikişe yakın, ustaca dokunmuş şeridin yukarısına tak."));
trsuperobject.biblemap.put("b2_28_28", new trsuperobject(2,28,28,"Göğüslüğün halkalarıyla efodun halkaları lacivert kordonla birbirine bağlanacak. Öyle ki, göğüslük efodun ustaca dokunmuş şeridinin yukarısında kalsın ve efoddan ayrılmasın."));
trsuperobject.biblemap.put("b2_28_29", new trsuperobject(2,28,29,"‹‹Harun Kutsal Yere girerken, İsrailoğullarının adlarının yazılı olduğu karar göğüslüğünü yüreğinin üzerinde taşıyacak. Öyle ki, ben, RAB halkımı sürekli anımsayayım."));
trsuperobject.biblemap.put("b2_28_30", new trsuperobject(2,28,30,"Urimle Tummimi karar göğüslüğünün içine koy; öyle ki, Harun ne zaman huzuruma çıksa yüreğinin üzerinde olsunlar. Böylece Harun İsrailoğullarının karar vermek için kullandıkları Urimle Tummimi RABbin huzurunda sürekli yüreğinin üzerinde taşıyacak.››"));
trsuperobject.biblemap.put("b2_28_31", new trsuperobject(2,28,31,"‹‹Efodun altına giyilen kaftanı salt lacivert iplikten yap."));
trsuperobject.biblemap.put("b2_28_32", new trsuperobject(2,28,32,"Ortasında baş geçecek kadar bir boşluk bırak. Yırtılmaması için boşluğun kenarlarını yaka gibi dokuyarak çevir."));
trsuperobject.biblemap.put("b2_28_33", new trsuperobject(2,28,33,"Kaftanın kenarını çepeçevre lacivert, mor, kırmızı iplikten nar motifleriyle beze, aralarına altın çıngıraklar tak."));
trsuperobject.biblemap.put("b2_28_34", new trsuperobject(2,28,34,"Eteğin ucu bir altın çıngırak, bir nar, bir altın çıngırak, bir nar olmak üzere çepeçevre kaplanacak."));
trsuperobject.biblemap.put("b2_28_35", new trsuperobject(2,28,35,"Harun hizmet ederken bu kaftanı giyecek. En Kutsal Yere, huzuruma girip çıkarken duyulan çıngırak sesi onun ölmediğini gösterecek."));
trsuperobject.biblemap.put("b2_28_36", new trsuperobject(2,28,36,"‹‹Saf altından bir levha yap ve üzerine mühür oyar gibi ‹RABbe adanmıştır› sözünü oy;"));
trsuperobject.biblemap.put("b2_28_37", new trsuperobject(2,28,37,"lacivert bir kordonla sarığın ön tarafına bağla."));
trsuperobject.biblemap.put("b2_28_38", new trsuperobject(2,28,38,"Harun onu alnında taşıyacak. İsrailliler kutsal bağışlarını getirirken suç işlemişlerse, suçlarını Harun taşıyacak; onlar önümde kabul görsün diye levha sürekli Harunun alnında bulunacak."));
trsuperobject.biblemap.put("b2_28_39", new trsuperobject(2,28,39,"‹‹İnce ketenden işlemeli bir mintan doku, ince ketenden bir sarık, bir de nakışlı kuşak yap."));
trsuperobject.biblemap.put("b2_28_40", new trsuperobject(2,28,40,"‹‹Harunun oğullarına mintanlar, kuşaklar, görkem ve saygınlık kazandıracak başlıklar yap."));
trsuperobject.biblemap.put("b2_28_41", new trsuperobject(2,28,41,"Bu giysileri ağabeyin Haruna ve oğullarına giydir; sonra bana kâhinlik etmeleri için onları meshedip ata ve kutsal kıl."));
trsuperobject.biblemap.put("b2_28_42", new trsuperobject(2,28,42,"‹‹Edep yerlerini örtmek için onlara keten donlar yap. Boyu belden uyluğa kadar olacak."));
trsuperobject.biblemap.put("b2_28_43", new trsuperobject(2,28,43,"Harun'la oğulları Buluşma Çadırı'na girdiklerinde ya da Kutsal Yer'de hizmet etmek üzere sunağa yaklaştıklarında, suç işleyip ölmemek için bu donları giyecekler. Harun ve soyundan gelenler için sürekli bir kural olacak bu.››"));
trsuperobject.biblemap.put("b2_29_1", new trsuperobject(2,29,1,"‹‹Bana kâhinlik edebilmeleri için, Harunla oğullarını kutsal kılmak üzere şunları yap: Bir boğa ile iki kusursuz koç al."));
trsuperobject.biblemap.put("b2_29_2", new trsuperobject(2,29,2,"İnce buğday unundan mayasız ekmek, zeytinyağıyla yoğrulmuş mayasız pideler, üzerine yağ sürülmüş mayasız yufkalar yap."));
trsuperobject.biblemap.put("b2_29_3", new trsuperobject(2,29,3,"Bunları bir sepete koyup boğa ve iki koçla birlikte bana getir."));
trsuperobject.biblemap.put("b2_29_4", new trsuperobject(2,29,4,"Harunla oğullarını Buluşma Çadırının giriş bölümüne getirip yıka."));
trsuperobject.biblemap.put("b2_29_5", new trsuperobject(2,29,5,"Giysileri al; mintanı, efodun altına giyilen kaftanı, efodu ve göğüslüğü Haruna giydir. Efodun ustaca dokunmuş şeridini bağla."));
trsuperobject.biblemap.put("b2_29_6", new trsuperobject(2,29,6,"Başına sarığı sar, üzerine de kutsal tacı koy."));
trsuperobject.biblemap.put("b2_29_7", new trsuperobject(2,29,7,"Sonra mesh yağını al, başına dökerek onu meshet."));
trsuperobject.biblemap.put("b2_29_8", new trsuperobject(2,29,8,"Harunun oğullarını öne çıkarıp onlara mintan giydir."));
trsuperobject.biblemap.put("b2_29_9", new trsuperobject(2,29,9,"Bellerine kuşak bağla, başlarına başlık koy. Kalıcı bir kural olarak kâhinlik onların işi olacak. Böylece Harunla oğullarını atamış olacaksın."));
trsuperobject.biblemap.put("b2_29_10", new trsuperobject(2,29,10,"‹‹Boğayı Buluşma Çadırının önüne getir, Harunla oğulları ellerini boğanın başına koysunlar."));
trsuperobject.biblemap.put("b2_29_11", new trsuperobject(2,29,11,"Boğayı huzurumda, Buluşma Çadırının giriş bölümünde keseceksin."));
trsuperobject.biblemap.put("b2_29_12", new trsuperobject(2,29,12,"Kanını parmağınla sunağın boynuzlarına sür, artan kanı sunağın dibine dök."));
trsuperobject.biblemap.put("b2_29_13", new trsuperobject(2,29,13,"Hayvanın bağırsak ve işkembe yağlarını, karaciğer perdesini, böbreklerini ve böbrek yağlarını sunağın üzerinde yakacaksın."));
trsuperobject.biblemap.put("b2_29_14", new trsuperobject(2,29,14,"Etini, derisini, gübresini de ordugahın dışında yak. Bu günah sunusudur."));
trsuperobject.biblemap.put("b2_29_15", new trsuperobject(2,29,15,"‹‹Bir koç getir, Harunla oğulları ellerini koçun başına koysunlar."));
trsuperobject.biblemap.put("b2_29_16", new trsuperobject(2,29,16,"Koçu sen kes. Kanını sunağın her yanına dök."));
trsuperobject.biblemap.put("b2_29_17", new trsuperobject(2,29,17,"Koçu parçalara ayırıp bağırsaklarını, işkembesini, ayaklarını yıka, başla öteki parçaların yanına koy."));
trsuperobject.biblemap.put("b2_29_18", new trsuperobject(2,29,18,"Sonra koçun tümünü sunağın üzerinde yak. Bu RABbe sunulan yakmalık sunu, RABbi hoşnut eden koku, Onun için yakılan sunudur."));
trsuperobject.biblemap.put("b2_29_19", new trsuperobject(2,29,19,"‹‹Öteki koçu getir, Harunla oğulları ellerini koçun başına koysunlar."));
trsuperobject.biblemap.put("b2_29_20", new trsuperobject(2,29,20,"Koçu sen kes. Kanını Harunla oğullarının sağ kulak memelerine, sağ el ve ayaklarının baş parmaklarına sür. Artan kanı sunağın her yanına dök."));
trsuperobject.biblemap.put("b2_29_21", new trsuperobject(2,29,21,"Sunağın üzerindeki kanı ve mesh yağını Harunla oğullarının ve giysilerinin üzerine serp. Böylece Harunla oğulları ve giysileri kutsal kılınmış olacak."));
trsuperobject.biblemap.put("b2_29_22", new trsuperobject(2,29,22,"‹‹Koçun yağını, kuyruk yağını, bağırsak ve işkembe yağlarını, karaciğer perdesini, böbreklerini, böbrek yağlarını ve sağ budunu al. -Çünkü bu, biri göreve atanırken kesilen koçtur.-"));
trsuperobject.biblemap.put("b2_29_23", new trsuperobject(2,29,23,"Huzurumdaki mayasız ekmek sepetinden bir somun, yağlı pide ve yufka al,"));
trsuperobject.biblemap.put("b2_29_24", new trsuperobject(2,29,24,"hepsini Harunla oğullarının eline ver. Bunları benim huzurumda sallamalık sunu olarak salla,"));
trsuperobject.biblemap.put("b2_29_25", new trsuperobject(2,29,25,"sonra ellerinden alıp sunakta yakmalık sunuyla birlikte beni hoşnut eden koku olarak yak. Bu, RAB için yakılan sunudur."));
trsuperobject.biblemap.put("b2_29_26", new trsuperobject(2,29,26,"‹‹Harunun atanması için sunulacak koçun döşünü huzurumda sallamalık sunu olarak salla. O döş senin payın olacak."));
trsuperobject.biblemap.put("b2_29_27", new trsuperobject(2,29,27,"Harunla oğullarının atanması için kesilen koçun sallanmış olan döşüyle bağış olarak sunulan budunu bana ayır."));
trsuperobject.biblemap.put("b2_29_28", new trsuperobject(2,29,28,"İsrailliler bunları sürekli Harunla oğullarının payına ayıracak. Bu, İsraillilerin RABbe sunduğu esenlik kurbanlarından biridir."));
trsuperobject.biblemap.put("b2_29_29", new trsuperobject(2,29,29,"‹‹Harunun kutsal giysileri, kendinden sonra oğullarına kalacak. Meshedilip atanırlarken bu giysileri giyecekler."));
trsuperobject.biblemap.put("b2_29_30", new trsuperobject(2,29,30,"Harunun yerine kâhin olan oğlu, Kutsal Yerde hizmet etmek üzere Buluşma Çadırına girdiğinde yedi gün bu giysileri giyecek."));
trsuperobject.biblemap.put("b2_29_31-32", new trsuperobject(2,29,31-32,"‹‹Harunla oğulları göreve atanırken kesilen koçun etini kutsal bir yerde haşlayacaksın. Haşlanan eti ve sepetteki ekmeği Buluşma Çadırının giriş bölümünde yiyecekler."));
trsuperobject.biblemap.put("b2_29_33", new trsuperobject(2,29,33,"Atanıp kutsal kılınmaları için günahları bağışlatan bu sunuları yalnız onlar yiyebilir. Yabancı biri yiyemez, çünkü bu sunular kutsaldır."));
trsuperobject.biblemap.put("b2_29_34", new trsuperobject(2,29,34,"Atanmaları için kesilen kurbanın etinden ya da ekmekten sabaha artan olursa, yakacaksın. Bunlar yenmeyecek, çünkü kutsaldır."));
trsuperobject.biblemap.put("b2_29_35", new trsuperobject(2,29,35,"‹‹Harunla oğulları için sana buyurduklarımın hepsini yap. Atanmaları yedi gün sürecek."));
trsuperobject.biblemap.put("b2_29_36", new trsuperobject(2,29,36,"Günah bağışlatmak için günah sunusu olarak her gün bir boğa sunacaksın. Sunağı arındırmak için günah sunusu sun, kutsal kılmak için de meshet."));
trsuperobject.biblemap.put("b2_29_37", new trsuperobject(2,29,37,"Yedi gün sunağı arındırarak kutsal kılacaksın. Böylece sunak çok kutsal olacak. Ona dokunan her şey de kutsal sayılacaktır.››"));
trsuperobject.biblemap.put("b2_29_38", new trsuperobject(2,29,38,"‹‹Düzenli olarak her gün sunağın üzerinde bir yaşında iki erkek kuzu sunacaksınız."));
trsuperobject.biblemap.put("b2_29_39", new trsuperobject(2,29,39,"Kuzunun birini sabah, öbürünü akşamüstü sunun."));
trsuperobject.biblemap.put("b2_29_40", new trsuperobject(2,29,40,"Kuzuyla birlikte dörtte bir hin sıkma zeytinyağıyla yoğrulmuş onda bir efafü ince un ve dökmelik sunu olarak dörtte bir hin şarap sunacaksınız."));
trsuperobject.biblemap.put("b2_29_41", new trsuperobject(2,29,41,"Öbür kuzuyu akşamüstü, beni hoşnut eden koku, yakılan sunu olarak, sabahki gibi tahıl sunusu ve dökmelik sunuyla birlikte bana sunacaksınız."));
trsuperobject.biblemap.put("b2_29_42", new trsuperobject(2,29,42,"‹‹Bu yakmalık sunu Buluşma Çadırının giriş bölümünde, RABbin huzurunda, kuşaklar boyu sürekli sunulacaktır. Musayla konuşmak için İsrail halkıyla orada buluşacağım."));
trsuperobject.biblemap.put("b2_29_43", new trsuperobject(2,29,43,"İsraillilerle buluşurken çadır görkemimle kutsal kılınacak."));
trsuperobject.biblemap.put("b2_29_44", new trsuperobject(2,29,44,"‹‹Buluşma Çadırını ve sunağı kutsal kılacak, Harunla oğullarını bana kâhinlik etmeleri için görevlendireceğim."));
trsuperobject.biblemap.put("b2_29_45", new trsuperobject(2,29,45,"İsrailliler arasında yaşayacak, onların Tanrısı olacağım."));
trsuperobject.biblemap.put("b2_29_46", new trsuperobject(2,29,46,"Anlayacaklar ki, aralarında yaşamak için onları Mısır'dan çıkaran Tanrıları RAB benim. Tanrıları RAB benim.››"));
trsuperobject.biblemap.put("b2_30_1", new trsuperobject(2,30,1,"‹‹Üzerinde buhur yakmak için akasya ağacından bir sunak yap."));
trsuperobject.biblemap.put("b2_30_2", new trsuperobject(2,30,2,"Kare biçiminde, boyu ve eni birer arşın, yüksekliği iki arşın, boynuzları kendinden olacak."));
trsuperobject.biblemap.put("b2_30_3", new trsuperobject(2,30,3,"Üstünü, yanlarını, boynuzlarını saf altınla kapla. Çevresine altın pervaz yap."));
trsuperobject.biblemap.put("b2_30_4", new trsuperobject(2,30,4,"İki yandaki pervazın altına iki altın halka yap. Bunlar sunağın taşınması için sırıkların geçmesine yarayacak."));
trsuperobject.biblemap.put("b2_30_5", new trsuperobject(2,30,5,"Sırıkları akasya ağacından yap ve altınla kapla."));
trsuperobject.biblemap.put("b2_30_6", new trsuperobject(2,30,6,"Sunağı Levha Sandığının karşısındaki perdenin, sandığın üzerindeki Bağışlanma Kapağının önüne, seninle buluşacağım yere koy."));
trsuperobject.biblemap.put("b2_30_7", new trsuperobject(2,30,7,"‹‹Harun her sabah kandillerin bakımını yaparken sunağın üzerinde güzel kokulu buhur yakacak."));
trsuperobject.biblemap.put("b2_30_8", new trsuperobject(2,30,8,"Akşamüstü kandilleri yakarken yine buhur yakacak. Böylece huzurumda kuşaklar boyunca sürekli buhur yanacak."));
trsuperobject.biblemap.put("b2_30_9", new trsuperobject(2,30,9,"Sunağın üzerinde başka buhur, yakmalık sunu ya da tahıl sunusu sunmayacaksınız; üzerine dökmelik sunu dökmeyeceksiniz."));
trsuperobject.biblemap.put("b2_30_10", new trsuperobject(2,30,10,"Harun yılda bir kez sunağın boynuzlarını arındıracak. Kuşaklarınız boyunca yılda bir kez günahları bağışlatmak için sunulan sununun kanıyla sunağı arındıracak. Sunak ben RAB için çok kutsaldır.››"));
trsuperobject.biblemap.put("b2_30_11", new trsuperobject(2,30,11,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b2_30_12", new trsuperobject(2,30,12,"‹‹İsraillilerin sayımını yaptığın zaman, herkes canına karşılık bana bedel ödeyecektir. Öyle ki, sayım yapılırken başlarına bela gelmesin."));
trsuperobject.biblemap.put("b2_30_13", new trsuperobject(2,30,13,"Sayılan herkes armağan olarak bana yarım kutsal yerin şekeli verecektir. -Bir şekel yirmi geradır.-"));
trsuperobject.biblemap.put("b2_30_14", new trsuperobject(2,30,14,"Sayılan yirmi yaşındaki ve daha yukarı yaştaki herkes bana armağan verecektir."));
trsuperobject.biblemap.put("b2_30_15", new trsuperobject(2,30,15,"Canlarınızın bedeli olarak bu armağanı verdiğinizde, zengin yarım şekelden fazla, yoksul yarım şekelden eksik vermeyecek."));
trsuperobject.biblemap.put("b2_30_16", new trsuperobject(2,30,16,"İsraillilerden bedel olarak verilen paraları toplayacak, Buluşma Çadırının hizmetinde kullanacaksın. Bu paralar canlarınızın bedeli olarak ben, RABbe İsraillileri hep anımsatacak.››"));
trsuperobject.biblemap.put("b2_30_17", new trsuperobject(2,30,17,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b2_30_18", new trsuperobject(2,30,18,"‹‹Yıkanmak için tunç bir kazan yap. Ayaklığı da tunçtan olacak. Buluşma Çadırı ile sunağın arasına koyup içine su doldur."));
trsuperobject.biblemap.put("b2_30_19", new trsuperobject(2,30,19,"Harunla oğulları ellerini, ayaklarını orada yıkayacaklar."));
trsuperobject.biblemap.put("b2_30_2-021", new trsuperobject(2,30,2-021,"Buluşma Çadırına girmeden ya da RAB için yakılan sunuyu sunarak hizmet etmek üzere sunağa yaklaşmadan önce, ölmemek için ellerini, ayaklarını yıkamalılar. Harunla soyunun bütün kuşakları boyunca sürekli bir kural olacak bu.››"));
trsuperobject.biblemap.put("b2_30_22", new trsuperobject(2,30,22,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b2_30_23", new trsuperobject(2,30,23,"‹‹Şu nadide baharatı al: 500 şekel sıvı mür, yarısı kadar, yani 250şer şekel güzel kokulu tarçın ve kamış,"));
trsuperobject.biblemap.put("b2_30_500", new trsuperobject(2,30,500,"kutsal yerin şekeli hıyarşembe, bir hin de zeytinyağı."));
trsuperobject.biblemap.put("b2_30_25", new trsuperobject(2,30,25,"Bunlardan ıtriyatçı ustalığıyla güzel kokulu kutsal bir mesh yağı yap. Ona kutsal mesh yağı denecek."));
trsuperobject.biblemap.put("b2_30_26-28", new trsuperobject(2,30,26-28,"Buluşma Çadırını, Levha Sandığını, masayla takımlarını, kandillikle takımlarını, buhur sunağını, yakmalık sunu sunağıyla bütün takımlarını, kazanı ve kazan ayaklığını hep bu yağla meshet."));
trsuperobject.biblemap.put("b2_30_29", new trsuperobject(2,30,29,"Onları kutsal kıl ki, çok kutsal olsunlar. Onlara değen her şey kutsal sayılacaktır."));
trsuperobject.biblemap.put("b2_30_30", new trsuperobject(2,30,30,"‹‹Bana kâhin olmaları için Harunla oğullarını meshedip kutsal kıl."));
trsuperobject.biblemap.put("b2_30_31", new trsuperobject(2,30,31,"İsraillilere de ki, ‹Kuşaklarınız boyunca bu kutsal mesh yağı yalnız benim için kullanılacak."));
trsuperobject.biblemap.put("b2_30_32", new trsuperobject(2,30,32,"İnsan bedenine dökülmeyecek. Aynı reçeteyle benzeri yapılmayacak. O kutsaldır ve sizin için kutsal olacaktır."));
trsuperobject.biblemap.put("b2_30_33", new trsuperobject(2,30,33,"Onun benzerini yapan ya da kâhin olmayan birinin üzerine döken herkes halkının arasından atılacaktır.››"));
trsuperobject.biblemap.put("b2_30_34", new trsuperobject(2,30,34,"Sonra RAB Musaya şöyle dedi: ‹‹Güzel kokulu baharat -kara günnük, onikafç, kasnı ve saf günnük- al. Hepsi aynı ölçüde olsun."));
trsuperobject.biblemap.put("b2_30_35", new trsuperobject(2,30,35,"Bir ıtriyatçı ustalığıyla bunlardan güzel kokulu bir buhur yap. Tuzlanmış, saf ve kutsal olacak."));
trsuperobject.biblemap.put("b2_30_36", new trsuperobject(2,30,36,"Birazını çok ince döv, Buluşma Çadırında seninle buluşacağım yere, Levha Sandığının önüne koy. Sizin için çok kutsal olacaktır."));
trsuperobject.biblemap.put("b2_30_37", new trsuperobject(2,30,37,"Aynı reçeteyle kendinize buhur yapmayacaksınız. Onu RAB için kutsal sayacaksınız."));
trsuperobject.biblemap.put("b2_30_38", new trsuperobject(2,30,38,"Kim koklamak için aynısını yaparsa halkının arasından atılacaktır.›› kabuğundan yapılan güzel kokulu bir baharat olduğu sanılıyor."));
trsuperobject.biblemap.put("b2_31_2", new trsuperobject(2,31,2,"‹‹Bak, Yahuda oymağından özellikle Hur oğlu Uri oğlu Besaleli seçtim."));
trsuperobject.biblemap.put("b2_31_3", new trsuperobject(2,31,3,"Beceri, anlayış, bilgi ve her türlü ustalık vermek için onu ruhumla doldurdum."));
trsuperobject.biblemap.put("b2_31_4", new trsuperobject(2,31,4,"Öyle ki, altın, gümüş, tunç işleyerek ustaca yapıtlar üretsin;"));
trsuperobject.biblemap.put("b2_31_5", new trsuperobject(2,31,5,"taş kesme ve kakmada, ağaç oymacılığında, her türlü sanat dalında çalışsın."));
trsuperobject.biblemap.put("b2_31_6", new trsuperobject(2,31,6,"Ayrıca Dan oymağından Ahisamak oğlu Oholiavı onunla çalışması için görevlendirdim. Sana buyurduğum işlerin hepsini yapabilsinler diye öteki becerikli adamlara üstün yetenek verdim."));
trsuperobject.biblemap.put("b2_31_7", new trsuperobject(2,31,7,"Buluşma Çadırını, Levha Sandığını, sandığın üzerindeki Bağışlanma Kapağını, çadırın bütün takımlarını,"));
trsuperobject.biblemap.put("b2_31_8", new trsuperobject(2,31,8,"masayla takımlarını, saf altın kandillikle takımlarını, buhur sunağını,"));
trsuperobject.biblemap.put("b2_31_9", new trsuperobject(2,31,9,"yakmalık sunu sunağıyla takımlarını, kazanla kazan ayaklığını,"));
trsuperobject.biblemap.put("b2_31_10", new trsuperobject(2,31,10,"dokunmuş giysileri -Kâhin Harunun kutsal giysileriyle oğullarının kâhin giysilerini-"));
trsuperobject.biblemap.put("b2_31_11", new trsuperobject(2,31,11,"mesh yağını, kutsal yer için güzel kokulu buhuru tam sana buyurduğum gibi yapsınlar.››"));
trsuperobject.biblemap.put("b2_31_12", new trsuperobject(2,31,12,"RAB Musaya şöyle buyurdu:"));
trsuperobject.biblemap.put("b2_31_13", new trsuperobject(2,31,13,"‹‹İsraillilere de ki, ‹Şabat günlerimi kesinlikle tutmalısınız. Çünkü o sizinle benim aramda kuşaklar boyu sürecek bir belirtidir. Böylece anlayacaksınız ki, sizi kutsal kılan RAB benim."));
trsuperobject.biblemap.put("b2_31_14", new trsuperobject(2,31,14,"‹‹Şabat Gününü tutmalısınız, çünkü sizin için kutsaldır. Kim onun kutsallığını bozarsa, kesinlikle öldürülmeli. O gün çalışan herkes halkının arasından atılmalı."));
trsuperobject.biblemap.put("b2_31_15", new trsuperobject(2,31,15,"Altı gün çalışılacak; ama yedinci gün RABbe adanmış Şabattır, dinlenme günüdür. Şabat Günü çalışan herkes kesinlikle öldürülmelidir."));
trsuperobject.biblemap.put("b2_31_16", new trsuperobject(2,31,16,"İsrailliler, sonsuza dek sürecek bir antlaşma gereği olarak, Şabat Gününü kuşaklar boyu kutlamaya özen gösterecekler."));
trsuperobject.biblemap.put("b2_31_17", new trsuperobject(2,31,17,"Bu, İsraillilerle benim aramda sürekli bir belirti olacaktır. Çünkü ben, RAB yeri göğü altı günde yarattım, yedinci gün işe son verip dinlendim.››"));
trsuperobject.biblemap.put("b2_31_18", new trsuperobject(2,31,18,"Tanrı Sina Dağı'nda Musa'yla konuşmasını bitirince, üzerine eliyle antlaşma koşullarını yazdığı iki taş levhayı ona verdi."));
trsuperobject.biblemap.put("b2_32_1", new trsuperobject(2,32,1,"Halk Musanın dağdan inmediğini, geciktiğini görünce, Harunun çevresine toplandı. Ona, ‹‹Kalk, bize öncülük edecek bir ilah yap›› dediler, ‹‹Bizi Mısırdan çıkaran adama, Musaya ne oldu bilmiyoruz!››"));
trsuperobject.biblemap.put("b2_32_2", new trsuperobject(2,32,2,"Harun, ‹‹Karılarınızın, oğullarınızın, kızlarınızın kulağındaki altın küpeleri çıkarıp bana getirin›› dedi."));
trsuperobject.biblemap.put("b2_32_3", new trsuperobject(2,32,3,"Herkes kulağındaki küpeyi çıkarıp Haruna getirdi."));
trsuperobject.biblemap.put("b2_32_4", new trsuperobject(2,32,4,"Harun altınları topladı, oymacı aletiyle buzağı biçiminde dökme bir put yaptı. Halk, ‹‹Ey İsrailliler, sizi Mısırdan çıkaran Tanrınız budur!›› dedi."));
trsuperobject.biblemap.put("b2_32_5", new trsuperobject(2,32,5,"Harun bunu görünce, buzağının önünde bir sunak yaptı ve, ‹‹Yarın RABbin onuruna bayram olacak›› diye ilan etti."));
trsuperobject.biblemap.put("b2_32_6", new trsuperobject(2,32,6,"Ertesi gün halk erkenden kalkıp yakmalık sunular sundu, esenlik sunuları getirdi. Yiyip içmeye oturdu, sonra kalkıp çılgınca eğlendi."));
trsuperobject.biblemap.put("b2_32_8", new trsuperobject(2,32,8,"Buyurduğum yoldan hemen saptılar. Kendilerine dökme bir buzağı yaparak önünde tapındılar, kurban kestiler. ‹Ey İsrailliler, sizi Mısırdan çıkaran ilahınız budur!› dediler.››"));
trsuperobject.biblemap.put("b2_32_10", new trsuperobject(2,32,10,"‹‹Şimdi bana engel olma, bırak öfkem alevlensin, onları yok edeyim. Sonra seni büyük bir ulus yapacağım.››"));
trsuperobject.biblemap.put("b2_32_11", new trsuperobject(2,32,11,"Musa Tanrısı RABbe yalvardı: ‹‹Ya RAB, niçin kendi halkına karşı öfken alevlensin? Onları Mısırdan büyük kudretinle, güçlü elinle çıkardın."));
trsuperobject.biblemap.put("b2_32_12", new trsuperobject(2,32,12,"Neden Mısırlılar, ‹Tanrı kötü amaçla, dağlarda öldürmek, yeryüzünden silmek için onları Mısırdan çıkardı› desinler? Öfkelenme, vazgeç halkına yapacağın kötülükten."));
trsuperobject.biblemap.put("b2_32_13", new trsuperobject(2,32,13,"Kulların İbrahimi, İshakı, İsraili anımsa. Onlara kendi üzerine ant içtin, ‹Soyunuzu gökteki yıldızlar kadar çoğaltacağım. Söz verdiğim bu ülkenin tümünü soyunuza vereceğim. Sonsuza dek onlara miras olacak› dedin.››"));
trsuperobject.biblemap.put("b2_32_14", new trsuperobject(2,32,14,"Böylece RAB halkına yapacağını söylediği kötülükten vazgeçti."));
trsuperobject.biblemap.put("b2_32_15", new trsuperobject(2,32,15,"Musa döndü, elinde antlaşma koşulları yazılı iki taş levhayla dağdan indi. Levhaların ön ve arka iki yüzü de yazılıydı."));
trsuperobject.biblemap.put("b2_32_16", new trsuperobject(2,32,16,"Onları Tanrı yapmıştı, üzerlerindeki oyma yazılar Onun yazısıydı."));
trsuperobject.biblemap.put("b2_32_17", new trsuperobject(2,32,17,"Yeşu, bağrışan halkın sesini duyunca, Musaya, ‹‹Ordugahtan savaş sesi geliyor!›› dedi."));
trsuperobject.biblemap.put("b2_32_18", new trsuperobject(2,32,18,"Musa şöyle yanıtladı:  ‹‹Ne yenenlerin,Ne de yenilenlerin sesidir bu;Ezgiler duyuyorum ben.››"));
trsuperobject.biblemap.put("b2_32_19", new trsuperobject(2,32,19,"Musa ordugaha yaklaşınca, buzağıyı ve oynayan insanları gördü; çok öfkelendi. Elindeki taş levhaları fırlatıp dağın eteğinde parçaladı."));
trsuperobject.biblemap.put("b2_32_20", new trsuperobject(2,32,20,"Yaptıkları buzağıyı alıp yaktı, toz haline gelinceye dek ezdi, sonra suya serperek İsraillilere içirdi."));
trsuperobject.biblemap.put("b2_32_21", new trsuperobject(2,32,21,"Haruna, ‹‹Bu halk sana ne yaptı ki, onları bu korkunç günaha sürükledin?›› dedi."));
trsuperobject.biblemap.put("b2_32_22", new trsuperobject(2,32,22,"Harun, ‹‹Öfkelenme, efendim!›› diye karşılık verdi, ‹‹Bilirsin, halk kötülüğe eğilimlidir."));
trsuperobject.biblemap.put("b2_32_23", new trsuperobject(2,32,23,"Bana, ‹Bize öncülük edecek bir ilah yap. Bizi Mısırdan çıkaran adama, Musaya ne oldu bilmiyoruz› dediler."));
trsuperobject.biblemap.put("b2_32_24", new trsuperobject(2,32,24,"Ben de, ‹Kimde altın varsa çıkarsın› dedim. Altınlarını bana verdiler. Ateşe atınca, bu buzağı ortaya çıktı!››"));
trsuperobject.biblemap.put("b2_32_25", new trsuperobject(2,32,25,"Musa halkın başıboş hale geldiğini gördü. Çünkü Harun onları dizginlememiş, düşmanlarına alay konusu olmalarına neden olmuştu."));
trsuperobject.biblemap.put("b2_32_26", new trsuperobject(2,32,26,"Musa ordugahın girişinde durdu, ‹‹RABden yana olanlar yanıma gelsin!›› dedi. Bütün Levililer çevresine toplandı."));
trsuperobject.biblemap.put("b2_32_27", new trsuperobject(2,32,27,"Musa şöyle dedi: ‹‹İsrailin Tanrısı RAB diyor ki, ‹Herkes kılıcını kuşansın. Ordugahta kapı kapı dolaşarak kardeşini, komşusunu, yakınını öldürsün.››"));
trsuperobject.biblemap.put("b2_32_28", new trsuperobject(2,32,28,"Levililer Musanın buyruğunu yerine getirdiler. O gün halktan üç bine yakın adam öldürüldü."));
trsuperobject.biblemap.put("b2_32_29", new trsuperobject(2,32,29,"Musa, ‹‹Bugün kendinizi RABbe adamış oldunuz›› dedi, ‹‹Herkes öz oğluna, öz kardeşine düşman kesildiği için bugün RAB sizi kutsadı.››"));
trsuperobject.biblemap.put("b2_32_30", new trsuperobject(2,32,30,"Ertesi gün halka, ‹‹Korkunç bir günah işlediniz›› dedi, ‹‹Şimdi RABbin huzuruna çıkacağım. Belki günahınızı bağışlatabilirim.››"));
trsuperobject.biblemap.put("b2_32_31", new trsuperobject(2,32,31,"Sonra RABbe dönerek, ‹‹Çok yazık, bu halk korkunç bir günah işledi›› dedi, ‹‹Kendilerine altın put yaptılar."));
trsuperobject.biblemap.put("b2_32_32", new trsuperobject(2,32,32,"Lütfen günahlarını bağışla, yoksa yazdığın kitaptan adımı sil.››"));
trsuperobject.biblemap.put("b2_32_33", new trsuperobject(2,32,33,"RAB, ‹‹Kim bana karşı günah işlediyse onun adını sileceğim›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b2_32_34", new trsuperobject(2,32,34,"‹‹Şimdi git, halkı sana söylediğim yere götür. Meleğim sana öncülük edecek. Ama zamanı gelince günahlarından ötürü onları cezalandıracağım.››"));
trsuperobject.biblemap.put("b2_32_35", new trsuperobject(2,32,35,"RAB halkı cezalandırdı. Çünkü Harun'a buzağı yaptırmışlardı."));
trsuperobject.biblemap.put("b2_33_2-3", new trsuperobject(2,33,2-3,"Süt ve bal akan ülkeye senden önce bir melek gönderecek, Kenan, Amor, Hitit, Periz, Hiv ve Yevus halklarını oradan kovacağım. Ben sizinle gelmeyeceğim, çünkü inatçı insanlarsınız. Belki sizi yolda yok ederim.››"));
trsuperobject.biblemap.put("b2_33_4", new trsuperobject(2,33,4,"Halk bu kötü haberi duyunca yasa büründü. Kimse takı takmadı."));
trsuperobject.biblemap.put("b2_33_5", new trsuperobject(2,33,5,"Çünkü RAB Musaya şöyle demişti: ‹‹İsraillilere de ki, ‹Siz inatçı insanlarsınız. Bir an aranızda kalsam, sizi yok ederim. Şimdi üzerinizdeki takıları çıkarın, size ne yapacağıma karar vereyim.››"));
trsuperobject.biblemap.put("b2_33_6", new trsuperobject(2,33,6,"Böylece Horev Dağından sonra İsrailliler takılarını çıkardı."));
trsuperobject.biblemap.put("b2_33_7", new trsuperobject(2,33,7,"Musa bir çadır alır, ordugahın dışına, biraz öteye kurardı. Ona ‹Buluşma Çadırı› derdi. Kim RABbe danışmak istese, ordugahın dışındaki Buluşma Çadırına giderdi."));
trsuperobject.biblemap.put("b2_33_8", new trsuperobject(2,33,8,"Musa ne zaman çadıra gitse, bütün halk kalkar, herkes çadırının girişinde durarak Musa içeri girinceye kadar arkasından bakardı."));
trsuperobject.biblemap.put("b2_33_9", new trsuperobject(2,33,9,"Musa çadıra girince, bulut sütunu aşağı iner, RAB Musayla konuştuğu sürece girişi kapardı."));
trsuperobject.biblemap.put("b2_33_10", new trsuperobject(2,33,10,"Bulut sütununun çadırın girişinde durduğunu gören herkes kalkar, kendi çadırının girişinde tapınırdı."));
trsuperobject.biblemap.put("b2_33_11", new trsuperobject(2,33,11,"RAB Musayla iki arkadaş gibi yüz yüze konuşurdu. Sonra Musa ordugaha dönerdi. Ama genç yardımcısı Nun oğlu Yeşu çadırdan çıkmazdı."));
trsuperobject.biblemap.put("b2_33_12", new trsuperobject(2,33,12,"Musa RABbe şöyle dedi: ‹‹Bana, ‹Bu halka öncülük et› diyorsun, ama kimi benimle göndereceğini söylemedin. Bana, ‹Seni adınla tanıyorum, senden hoşnudum› demiştin."));
trsuperobject.biblemap.put("b2_33_13", new trsuperobject(2,33,13,"Eğer benden hoşnutsan, lütfen şimdi bana yollarını göster ki, seni daha iyi tanıyıp hoşnut etmeye devam edeyim. Unutma, bu ulus senin halkındır.››"));
trsuperobject.biblemap.put("b2_33_14", new trsuperobject(2,33,14,"RAB, ‹‹Varlığım sana eşlik edecek›› diye yanıtladı, ‹‹Seni rahata kavuşturacağım.››"));
trsuperobject.biblemap.put("b2_33_15", new trsuperobject(2,33,15,"Musa, ‹‹Eğer varlığın bize eşlik etmeyecekse, bizi buradan çıkarma›› dedi,"));
trsuperobject.biblemap.put("b2_33_16", new trsuperobject(2,33,16,"‹‹Yoksa benden ve halkından hoşnut kaldığın nereden bilinecek? Bize eşlik etmenden, değil mi? Ancak o zaman benimle halkın yeryüzünün öteki halklarından ayırt edilebiliriz.››"));
trsuperobject.biblemap.put("b2_33_17", new trsuperobject(2,33,17,"RAB, ‹‹Söylediğin gibi yapacağım›› dedi, ‹‹Çünkü senden hoşnut kaldım, adınla tanıyorum seni.››"));
trsuperobject.biblemap.put("b2_33_18", new trsuperobject(2,33,18,"Musa, ‹‹Lütfen görkemini bana göster›› dedi."));
trsuperobject.biblemap.put("b2_33_19", new trsuperobject(2,33,19,"RAB, ‹‹Bütün iyiliğimi önünden geçireceğim›› diye karşılık verdi, ‹‹Adımı, RAB adını senin önünde duyuracağım. Merhamet ettiğime merhamet edeceğim, acıdığıma acıyacağım."));
trsuperobject.biblemap.put("b2_33_20", new trsuperobject(2,33,20,"Ancak, yüzümü görmene izin veremem. Çünkü yüzümü gören yaşayamaz.››"));
trsuperobject.biblemap.put("b2_33_21", new trsuperobject(2,33,21,"Sonra, ‹‹Yakınımda bir yer var›› dedi, ‹‹Orada, kayanın üzerinde dur."));
trsuperobject.biblemap.put("b2_33_22", new trsuperobject(2,33,22,"Görkemim oradan geçerken seni kayanın kovuğuna sokup geçinceye kadar elimle örteceğim."));
trsuperobject.biblemap.put("b2_33_23", new trsuperobject(2,33,23,"Elimi kaldırdığımda, sırtımı göreceksin. Ama yüzüm görülmeyecek.››"));
trsuperobject.biblemap.put("b2_34_2", new trsuperobject(2,34,2,"Sabaha kadar hazırlan, sabah olunca Sina Dağına çık; dağın tepesinde, huzurumda dur."));
trsuperobject.biblemap.put("b2_34_3", new trsuperobject(2,34,3,"Senden başka kimse dağa çıkmasın, dağın hiçbir yerinde kimse görülmesin. Dağın eteğinde davar ya da sığır da otlamasın.››"));
trsuperobject.biblemap.put("b2_34_4", new trsuperobject(2,34,4,"Musa öncekiler gibi iki taş levha kesti. RABbin buyurduğu gibi sabah erkenden kalktı, taş levhaları yanına alarak Sina Dağına çıktı."));
trsuperobject.biblemap.put("b2_34_6", new trsuperobject(2,34,6,"Musanın önünden geçerek, ‹‹Ben RABbim›› dedi, ‹‹RAB, acıyan, lütfeden, tez öfkelenmeyen, sevgisi engin ve sadık Tanrı."));
trsuperobject.biblemap.put("b2_34_7", new trsuperobject(2,34,7,"Binlercesine sevgi gösterir, suçlarını, isyanlarını, günahlarını bağışlarım. Hiçbir suçu cezasız bırakmam. Babaların işlediği suçun hesabını oğullarından, torunlarından, üçüncü, dördüncü kuşaklardan sorarım.››"));
trsuperobject.biblemap.put("b2_34_8", new trsuperobject(2,34,8,"Musa hemen yere kapanıp tapındı."));
trsuperobject.biblemap.put("b2_34_9", new trsuperobject(2,34,9,"‹‹Ya Rab, eğer benden hoşnutsan, lütfen bizimle gel›› dedi, ‹‹Bunlar inatçı insanlardır. Sen suçlarımızı, günahlarımızı bağışla. Bizi kendi mirasın olarak benimse.››"));
trsuperobject.biblemap.put("b2_34_10", new trsuperobject(2,34,10,"RAB, ‹‹Senin halkınla bir antlaşma yapıyorum›› dedi, ‹‹Onların önünde dünyada ve öteki uluslar arasında görülmemiş harikalar yapacağım. Arasında yaşadığın halk neler yapabileceğimi görecek. Senin için korkunç şeyler yapacağım."));
trsuperobject.biblemap.put("b2_34_11", new trsuperobject(2,34,11,"Bugün sana verdiğim buyruğu tut. Amor, Kenan, Hitit, Periz, Hiv ve Yevus halklarını senin önünden kovacağım."));
trsuperobject.biblemap.put("b2_34_12", new trsuperobject(2,34,12,"Gideceğin ülkedeki insanlarla antlaşma yapmaktan kaçın. Çünkü bu senin için bir tuzak olur."));
trsuperobject.biblemap.put("b2_34_13", new trsuperobject(2,34,13,"Onların sunaklarını yıkacak, dikili taşlarını parçalayacak, Aşera putlarını keseceksiniz."));
trsuperobject.biblemap.put("b2_34_14", new trsuperobject(2,34,14,"Başka ilahlara tapmayacaksınız. Çünkü ben adı Kıskanç bir RABbim, kıskanç bir Tanrıyım."));
trsuperobject.biblemap.put("b2_34_15", new trsuperobject(2,34,15,"Ülke halkıyla herhangi bir antlaşma yapmayın. Yoksa onlar başka ilahlara gönül verir, kurban keserken sizi de çağırırlar; siz de gider yersiniz."));
trsuperobject.biblemap.put("b2_34_16", new trsuperobject(2,34,16,"Kızlarını oğullarınıza alırsınız. Kızlar başka ilahlara gönül verirken oğullarınızı da artlarından sürükler."));
trsuperobject.biblemap.put("b2_34_17", new trsuperobject(2,34,17,"‹‹Dökme putlar yapmayacaksınız."));
trsuperobject.biblemap.put("b2_34_18", new trsuperobject(2,34,18,"‹‹Size buyurduğum gibi, Aviv ayının belirli günlerinde yedi gün mayasız ekmek yiyerek Mayasız Ekmek Bayramını kutlayacaksınız. Çünkü Mısırdan Aviv ayında çıktınız."));
trsuperobject.biblemap.put("b2_34_19", new trsuperobject(2,34,19,"‹‹Bütün ilk doğanlar benimdir; ister sığır, ister davar olsun, ilk doğan erkek hayvanlarınızın tümü bana aittir."));
trsuperobject.biblemap.put("b2_34_20", new trsuperobject(2,34,20,"İlk doğan sıpanın bedelini bir kuzuyla ödeyin. Bedelini ödemeyecekseniz, sıpanın boynunu kıracaksınız. Bütün ilk doğan oğullarınızın bedelini ödemelisiniz. ‹‹Kimse huzuruma eli boş çıkmasın."));
trsuperobject.biblemap.put("b2_34_21", new trsuperobject(2,34,21,"‹‹Altı gün çalışacak, yedinci gün dinleneceksiniz. Ekim, biçim vakti bile olsa dinleneceksiniz."));
trsuperobject.biblemap.put("b2_34_22", new trsuperobject(2,34,22,"‹‹İlk buğday biçiminde Haftalar Bayramı, yıl sonunda da Ürün Devşirme Bayramı yapacaksınız."));
trsuperobject.biblemap.put("b2_34_23", new trsuperobject(2,34,23,"Bütün erkekleriniz yılda üç kez İsrailin Tanrısı ben Egemen RABbin huzuruna çıkacaklar."));
trsuperobject.biblemap.put("b2_34_24", new trsuperobject(2,34,24,"Öteki ulusları önünüzden kovacak, sınırlarınızı genişleteceğim. Yılda üç kez Tanrınız RABbin önüne çıktığınız zaman, kimse ülkenize göz dikemeyecek."));
trsuperobject.biblemap.put("b2_34_25", new trsuperobject(2,34,25,"‹‹Evinizde maya bulunduğu sürece bana kurban kesmeyeceksiniz. Fısıh kurbanı sabaha bırakılmayacak."));
trsuperobject.biblemap.put("b2_34_26", new trsuperobject(2,34,26,"‹‹Toprağınızın seçme ilk ürünlerini Tanrınız RABbin Tapınağına getireceksiniz. ‹‹Oğlağı anasının sütünde haşlamayacaksınız.››"));
trsuperobject.biblemap.put("b2_34_27", new trsuperobject(2,34,27,"RAB Musaya, ‹‹Bunları yaz›› dedi, ‹‹Çünkü seninle ve İsraillilerle bu sözlere dayanarak antlaşma yaptım.››"));
trsuperobject.biblemap.put("b2_34_28", new trsuperobject(2,34,28,"Musa orada kırk gün kırk gece RABle birlikte kaldı. Ağzına ne ekmek koydu, ne de su. Antlaşma sözlerini, on buyruğu taş levhaların üzerine yazdı."));
trsuperobject.biblemap.put("b2_34_29", new trsuperobject(2,34,29,"Musa elinde iki antlaşma levhasıyla Sina Dağından indi. RABle konuştuğu için yüzü ışıldıyordu, ama kendisi bunun farkında değildi."));
trsuperobject.biblemap.put("b2_34_30", new trsuperobject(2,34,30,"Harunla İsrailliler Musanın ışıldayan yüzünü görünce, ona yaklaşmaya korktular."));
trsuperobject.biblemap.put("b2_34_31", new trsuperobject(2,34,31,"Musa onları yanına çağırdı. Harunla İsrail topluluğunun bütün önderleri çevresine toplandılar. Musa onlarla konuştu."));
trsuperobject.biblemap.put("b2_34_32", new trsuperobject(2,34,32,"Sonra herkes ona yaklaştı. Musa RABbin Sina Dağında kendisine bildirdiği bütün buyrukları onlara verdi."));
trsuperobject.biblemap.put("b2_34_33", new trsuperobject(2,34,33,"Konuşmasını bitirdikten sonra, yüzüne bir peçe taktı."));
trsuperobject.biblemap.put("b2_34_34", new trsuperobject(2,34,34,"Ama ne zaman konuşmak için RABbin huzuruna çıksa, ayrılıncaya kadar peçeyi kaldırırdı. Dönünce de kendisine verilen buyrukları İsraillilere bildirir,"));
trsuperobject.biblemap.put("b2_34_35", new trsuperobject(2,34,35,"İsrailliler de onun ışıldayan yüzünü görürlerdi. Sonra Musa içeri girip RAB'le görüşünceye kadar yine peçeyi takardı."));
trsuperobject.biblemap.put("b2_35_1", new trsuperobject(2,35,1,"Musa bütün İsrail topluluğunu çağırarak, ‹‹RABbin yapmanızı buyurduğu işler şunlardır›› dedi,"));
trsuperobject.biblemap.put("b2_35_2", new trsuperobject(2,35,2,"‹‹Altı gün çalışacaksınız. Ama yedinci gün sizin için kutsal Şabat, RABbe adanmış dinlenme günü olacaktır. O gün çalışan herkes öldürülecektir."));
trsuperobject.biblemap.put("b2_35_3", new trsuperobject(2,35,3,"Şabat Günü konutlarınızda ateş yakmayacaksınız.››"));
trsuperobject.biblemap.put("b2_35_4", new trsuperobject(2,35,4,"Musa bütün İsrail topluluğuna seslenerek şöyle dedi: ‹‹RABbin buyruğu şudur:"));
trsuperobject.biblemap.put("b2_35_5-7", new trsuperobject(2,35,5-7,"Aranızda armağanlar toplayıp RABbe sunacaksınız. İstekli olan herkes RABbe altın, gümüş, tunç; lacivert, mor, kırmızı iplik; ince keten, keçi kılı, deri, kırmızı boyalı koç derisi, akasya ağacı armağan etsin."));
trsuperobject.biblemap.put("b2_35_8", new trsuperobject(2,35,8,"Kandil için zeytinyağı; mesh yağı ve güzel kokulu buhur için baharat;"));
trsuperobject.biblemap.put("b2_35_9", new trsuperobject(2,35,9,"başkâhinin efoduyla göğüslüğü için oniks ve öbür kakma taşları getirsin."));
trsuperobject.biblemap.put("b2_35_10", new trsuperobject(2,35,10,"‹‹Aranızdaki bütün becerikli kişiler gelip RABbin buyurduğu her şeyi yapsın."));
trsuperobject.biblemap.put("b2_35_11", new trsuperobject(2,35,11,"Konutu, çadırın iç ve dış örtüsünü, kopçalarını, çerçevelerini, kirişlerini, direklerini, tabanlarını;"));
trsuperobject.biblemap.put("b2_35_12", new trsuperobject(2,35,12,"sandığı ve sırıklarını, Bağışlanma Kapağını, bölme perdesini,"));
trsuperobject.biblemap.put("b2_35_13", new trsuperobject(2,35,13,"masayla sırıklarını, bütün masa takımlarını, huzura konan ekmekleri;"));
trsuperobject.biblemap.put("b2_35_14", new trsuperobject(2,35,14,"ışık için kandilliği ve takımlarını, kandilleri, kandiller için zeytinyağını;"));
trsuperobject.biblemap.put("b2_35_15", new trsuperobject(2,35,15,"buhur sunağını ve sırıklarını, mesh yağını, güzel kokulu buhuru; konutun giriş bölümündeki perdeyi;"));
trsuperobject.biblemap.put("b2_35_16", new trsuperobject(2,35,16,"yakmalık sunu sunağını ve tunç ızgarasını, sırıklarını, bütün takımlarını, kazanı ve kazan ayaklığını;"));
trsuperobject.biblemap.put("b2_35_17", new trsuperobject(2,35,17,"avlunun çevresindeki perdeleri, direkleri, direk tabanlarını, avlu kapısındaki perdeyi,"));
trsuperobject.biblemap.put("b2_35_18", new trsuperobject(2,35,18,"konutun ve avlunun kazıklarıyla iplerini;"));
trsuperobject.biblemap.put("b2_35_19", new trsuperobject(2,35,19,"kutsal yerde hizmet etmek için dokunmuş giysileri -Kâhin Harunun giysileriyle oğullarının kâhin giysilerini- yapsınlar.››"));
trsuperobject.biblemap.put("b2_35_20", new trsuperobject(2,35,20,"İsrail topluluğu Musanın yanından ayrıldı."));
trsuperobject.biblemap.put("b2_35_21", new trsuperobject(2,35,21,"Her istekli, hevesli kişi Buluşma Çadırının yapımı, hizmeti ve kutsal giysiler için RABbe armağan getirdi."));
trsuperobject.biblemap.put("b2_35_22", new trsuperobject(2,35,22,"Kadın erkek herkes istekle geldi, RABbe her çeşit altın takı, broş, küpe, yüzük, kolye getirdi. RABbe armağan ettikleri bütün takılar altındı."));
trsuperobject.biblemap.put("b2_35_23", new trsuperobject(2,35,23,"Ayrıca kimde lacivert, mor, kırmızı iplik; ince keten, keçi kılı, deri, kırmızı boyalı koç derisi varsa getirdi."));
trsuperobject.biblemap.put("b2_35_24", new trsuperobject(2,35,24,"Gümüş ve tunç armağanlar sunan herkes onları RABbe adadı. Herhangi bir işte kullanılmak üzere kimde akasya ağacı varsa getirdi."));
trsuperobject.biblemap.put("b2_35_25", new trsuperobject(2,35,25,"Bütün becerikli kadınlar elleriyle eğirdikleri lacivert, mor, kırmızı ipliği, ince keteni getirdiler."));
trsuperobject.biblemap.put("b2_35_26", new trsuperobject(2,35,26,"İstekli, becerikli kadınlar da keçi kılı eğirdiler."));
trsuperobject.biblemap.put("b2_35_27", new trsuperobject(2,35,27,"Önderler efod ve göğüslük için oniks, kakma taşlar,"));
trsuperobject.biblemap.put("b2_35_28", new trsuperobject(2,35,28,"kandil, mesh yağı ve güzel kokulu buhur için baharat ve zeytinyağı getirdiler."));
trsuperobject.biblemap.put("b2_35_29", new trsuperobject(2,35,29,"Kadın erkek bütün istekli İsrailliler RABbin Musa aracılığıyla yapmalarını buyurduğu işler için RABbe gönülden verilen sunu sundular."));
trsuperobject.biblemap.put("b2_35_30", new trsuperobject(2,35,30,"Musa İsraillilere, ‹‹Bakın!›› dedi, ‹‹RAB Yahuda oymağından özellikle Hur oğlu Uri oğlu Besaleli seçti."));
trsuperobject.biblemap.put("b2_35_31", new trsuperobject(2,35,31,"Beceri, anlayış, bilgi ve her türlü ustalık vermek için onu kendi Ruhuyla doldurdu."));
trsuperobject.biblemap.put("b2_35_32", new trsuperobject(2,35,32,"Öyle ki, altın, gümüş, tunç işleyerek ustaca yapıtlar üretsin;"));
trsuperobject.biblemap.put("b2_35_33", new trsuperobject(2,35,33,"taş kesme ve kakmada, ağaç oymacılığında, her türlü sanat dalında çalışsın."));
trsuperobject.biblemap.put("b2_35_34", new trsuperobject(2,35,34,"RAB ona ve Dan oymağından Ahisamak oğlu Oholiava öğretme yeteneği de verdi."));
trsuperobject.biblemap.put("b2_35_35", new trsuperobject(2,35,35,"Onlara üstün beceri verdi. Öyle ki, ustalık isteyen her türlü işte, oymacılıkta, lacivert, mor, kırmızı iplik ve ince keten yapmada, dokuma ve nakış işlerinde, her sanat dalında yaratıcı olsunlar."));
trsuperobject.biblemap.put("b2_36_1", new trsuperobject(2,36,1,"‹‹Besalel, Oholiav ve kutsal yerin yapımında gereken işleri nasıl yapacaklarına ilişkin RABbin kendilerine bilgelik ve anlayış verdiği bütün becerikli kişiler her işi tam RABbin buyurduğu gibi yapacaklar.››"));
trsuperobject.biblemap.put("b2_36_2", new trsuperobject(2,36,2,"Musa Besaleli, Oholiavı, RABbin kendilerine bilgelik verdiği becerikli adamları ve çalışmaya istekli herkesi iş başına çağırdı."));
trsuperobject.biblemap.put("b2_36_3", new trsuperobject(2,36,3,"Gelenler kutsal yerin yapımında gereken işleri yapmak üzere İsraillilerin getirmiş olduğu bütün armağanları Musadan aldılar. İsrailliler gönülden verdikleri sunuları her sabah Musaya getirmeye devam ettiler."));
trsuperobject.biblemap.put("b2_36_4", new trsuperobject(2,36,4,"Öyle ki, kutsal yerdeki işleri yapmakta olan ustalar işlerini bırakıp bir bir Musanın yanına gelerek,"));
trsuperobject.biblemap.put("b2_36_5", new trsuperobject(2,36,5,"‹‹Halk RABbin yapılmasını buyurduğu iş için gereğinden fazla getiriyor›› dediler."));
trsuperobject.biblemap.put("b2_36_6", new trsuperobject(2,36,6,"Bunun üzerine Musa buyruk verdi: ‹‹Ne erkek, ne kadın hiç kimse kutsal yere armağan olarak artık bir şey vermesin.›› Buyruk ordugahta ilan edildi. Böylece halkın daha çok armağan getirmesine engel olundu."));
trsuperobject.biblemap.put("b2_36_7", new trsuperobject(2,36,7,"Çünkü o ana kadar getirilenler işi bitirmek için yeter de artardı bile."));
trsuperobject.biblemap.put("b2_36_8", new trsuperobject(2,36,8,"Çalışanlar arasındaki becerikli adamlar konutu on perdeden yaptılar. Besalel onları lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden yaptı, üzerini Keruvlarla ustaca süsledi."));
trsuperobject.biblemap.put("b2_36_9", new trsuperobject(2,36,9,"Her perdenin boyu yirmi sekiz, eni dört arşındı. Bütün perdeler aynı ölçüdeydi."));
trsuperobject.biblemap.put("b2_36_10", new trsuperobject(2,36,10,"Perdeleri beşer beşer birbirine ekleyerek iki takım perde yaptı."));
trsuperobject.biblemap.put("b2_36_11", new trsuperobject(2,36,11,"Birinci takımın kenarına lacivert ilmekler açtı. Öbür takımın kenarına da aynı şeyi yaptı."));
trsuperobject.biblemap.put("b2_36_12", new trsuperobject(2,36,12,"Birinci takımın ilk perdesiyle ikinci takımın son perdesine ellişer ilmek açtı; ilmekler birbirine karşıydı."));
trsuperobject.biblemap.put("b2_36_13", new trsuperobject(2,36,13,"Elli altın kopça yaptı, perdeleri kopçalayarak çadırı birleştirdi. Böylece konut tek parça haline geldi."));
trsuperobject.biblemap.put("b2_36_14", new trsuperobject(2,36,14,"Konutun üstünü kaplayacak çadır için keçi kılından on bir perde yaptı."));
trsuperobject.biblemap.put("b2_36_15", new trsuperobject(2,36,15,"Her perdenin boyu otuz, eni dört arşındı. On bir perde de aynı ölçüdeydi."));
trsuperobject.biblemap.put("b2_36_16", new trsuperobject(2,36,16,"Beş perdeyi birbirine, altı perdeyi birbirine birleştirdi."));
trsuperobject.biblemap.put("b2_36_17", new trsuperobject(2,36,17,"Her iki perde takımının kenarlarına ellişer ilmek açtı."));
trsuperobject.biblemap.put("b2_36_18", new trsuperobject(2,36,18,"Çadırı birleştirip tek parça haline getirmek için elli tunç kopça yaptı."));
trsuperobject.biblemap.put("b2_36_19", new trsuperobject(2,36,19,"Çadır için kırmızı boyalı koç derisinden bir örtü, onun üstüne de deriden başka bir örtü yaptı."));
trsuperobject.biblemap.put("b2_36_20", new trsuperobject(2,36,20,"Konut için akasya ağacından dikine çerçeveler yaptı."));
trsuperobject.biblemap.put("b2_36_21", new trsuperobject(2,36,21,"Her çerçevenin boyu onfı, eni bir buçuk arşındı."));
trsuperobject.biblemap.put("b2_36_22", new trsuperobject(2,36,22,"Çerçevelerin birbirine uyan iki paralel çıkıntısı vardı. Konutun bütün çerçevelerini aynı biçimde yaptı."));
trsuperobject.biblemap.put("b2_36_23", new trsuperobject(2,36,23,"Konutun güneyi için yirmi çerçeve yaptı."));
trsuperobject.biblemap.put("b2_36_24", new trsuperobject(2,36,24,"Her çerçevenin altında iki çıkıntı için birer taban olmak üzere, yirmi çerçevenin altında kırk gümüş taban yaptı."));
trsuperobject.biblemap.put("b2_36_25-26", new trsuperobject(2,36,25-26,"Konutun öbür yanı, yani kuzeyi için de yirmi çerçeve ve her çerçevenin altında iki taban olmak üzere kırk gümüş taban yaptı."));
trsuperobject.biblemap.put("b2_36_27", new trsuperobject(2,36,27,"Konutun batıya bakacak arka tarafı için altı çerçeve yaptı."));
trsuperobject.biblemap.put("b2_36_28", new trsuperobject(2,36,28,"Arkada konutun köşeleri için iki çerçeve yaptı."));
trsuperobject.biblemap.put("b2_36_29", new trsuperobject(2,36,29,"Bu köşe çerçevelerinin alt tarafı ayrı kaldı, üst tarafı ise birinci halkayla birleştirildi. İki köşeyi oluşturan iki çerçeveyi aynı biçimde yaptı."));
trsuperobject.biblemap.put("b2_36_30", new trsuperobject(2,36,30,"Böylece sekiz çerçeve ve her çerçevenin altında iki taban olmak üzere on altı gümüş taban yaptı."));
trsuperobject.biblemap.put("b2_36_31-32", new trsuperobject(2,36,31-32,"Konutun bir yanındaki çerçeveler için beş, öbür yanındaki çerçeveler için beş, batıya bakan arka tarafındaki çerçeveler için de beş olmak üzere akasya ağacından kirişler yaptı."));
trsuperobject.biblemap.put("b2_36_33", new trsuperobject(2,36,33,"Çerçevelerin ortasındaki kirişi konutun bir ucundan öbür ucuna geçirdi."));
trsuperobject.biblemap.put("b2_36_34", new trsuperobject(2,36,34,"Çerçevelerle kirişleri altınla kapladı, kirişlerin geçeceği halkaları da altından yaptı."));
trsuperobject.biblemap.put("b2_36_35", new trsuperobject(2,36,35,"Lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden bir perde yaptı, üzerini Keruvlarla ustaca süsledi."));
trsuperobject.biblemap.put("b2_36_36", new trsuperobject(2,36,36,"Perde için akasya ağacından dört direk yaparak altınla kapladı. Çengelleri de altındı. Direkler için dört gümüş taban döktü."));
trsuperobject.biblemap.put("b2_36_37", new trsuperobject(2,36,37,"Çadırın giriş bölümüne lacivert, mor, kırmızı iplikle, özenle dokunmuş ince ketenden nakışlı bir perde yaptı."));
trsuperobject.biblemap.put("b2_36_38", new trsuperobject(2,36,38,"Perdeyi asmak için çengelli beş direk yaparak başlıklarını, çemberlerini altınla kapladı. Direklere beş tunç taban yaptı."));
trsuperobject.biblemap.put("b2_37_1", new trsuperobject(2,37,1,"Besalel Antlaşma Sandığını akasya ağacından yaptı. Boyu iki buçuk, eni ve yüksekliği birer buçuk arşındı."));
trsuperobject.biblemap.put("b2_37_2", new trsuperobject(2,37,2,"İçini de dışını da saf altınla kapladı. Çevresine altın pervaz yaptı."));
trsuperobject.biblemap.put("b2_37_3", new trsuperobject(2,37,3,"İkisi bir yanda, ikisi öbür yanda olmak üzere sandığın dört köşesindeki ayaklara takmak için birer altın halka döktü."));
trsuperobject.biblemap.put("b2_37_4", new trsuperobject(2,37,4,"Akasya ağacından sırıklar yapıp altınla kapladı."));
trsuperobject.biblemap.put("b2_37_5", new trsuperobject(2,37,5,"Sandığın taşınması için sırıkları yanlardaki halkalara geçirdi."));
trsuperobject.biblemap.put("b2_37_6", new trsuperobject(2,37,6,"Bağışlanma Kapağını saf altından yaptı. Boyu iki buçuk, eni bir buçuk arşındı."));
trsuperobject.biblemap.put("b2_37_7", new trsuperobject(2,37,7,"Kapağın iki kenarına dövme altından birer Keruv yaptı."));
trsuperobject.biblemap.put("b2_37_8", new trsuperobject(2,37,8,"Keruvlardan birini bir kenara, öbürünü öteki kenara koyarak kapağı tek parça halinde yaptı."));
trsuperobject.biblemap.put("b2_37_9", new trsuperobject(2,37,9,"Keruvlar yukarı doğru açık kanatlarıyla kapağı örtüyor, yüzleri birbirine dönük kapağa bakıyorlardı."));
trsuperobject.biblemap.put("b2_37_10", new trsuperobject(2,37,10,"Besalel akasya ağacından bir masa yaptı. Boyu iki, eni bir, yüksekliği bir buçuk arşındı."));
trsuperobject.biblemap.put("b2_37_11", new trsuperobject(2,37,11,"Masayı saf altınla kapladı. Çevresine altın pervaz yaptı."));
trsuperobject.biblemap.put("b2_37_12", new trsuperobject(2,37,12,"Pervazın çevresine dört parmak eninde bir kenarlık yaparak altın pervazla çevirdi."));
trsuperobject.biblemap.put("b2_37_13", new trsuperobject(2,37,13,"Masa için dört altın halka dökerek dört ayak üzerindeki dört köşeye yerleştirdi."));
trsuperobject.biblemap.put("b2_37_14", new trsuperobject(2,37,14,"Masanın taşınması için sırıkların içinden geçeceği halkalar kenarlığa yakındı."));
trsuperobject.biblemap.put("b2_37_15", new trsuperobject(2,37,15,"Sırıkları akasya ağacından yaptı, altınla kapladı."));
trsuperobject.biblemap.put("b2_37_16", new trsuperobject(2,37,16,"Masa için saf altından tabaklar, sahanlar, dökmelik sunu testileri, tasları yaptı."));
trsuperobject.biblemap.put("b2_37_17", new trsuperobject(2,37,17,"Saf altından bir kandillik yaptı. Ayağı, gövdesi dövme altındı. Çanak, tomurcuk ve çiçek motifleri kendindendi."));
trsuperobject.biblemap.put("b2_37_18", new trsuperobject(2,37,18,"Üç kolu bir yanda, üç kolu öteki yanda olmak üzere altı kolluydu."));
trsuperobject.biblemap.put("b2_37_19", new trsuperobject(2,37,19,"Her kolda badem çiçeğini andıran üç çanak, tomurcuk ve çiçek motifi vardı. Altı kol da aynıydı."));
trsuperobject.biblemap.put("b2_37_20", new trsuperobject(2,37,20,"Kandilliğin gövdesinde badem çiçeğini andıran dört çanak, tomurcuk ve çiçek motifi bulunuyordu."));
trsuperobject.biblemap.put("b2_37_21", new trsuperobject(2,37,21,"Kandillikten yükselen ilk iki kolun, ikinci iki kolun, üçüncü iki kolun altında kendinden birer tomurcuk vardı. Toplam altı koldu."));
trsuperobject.biblemap.put("b2_37_22", new trsuperobject(2,37,22,"Tomurcukları, kolları tek parça olan kandillik saf dövme altındı."));
trsuperobject.biblemap.put("b2_37_23", new trsuperobject(2,37,23,"Kandillik için saf altından yedi kandil, fitil maşaları, tablalar yaptı."));
trsuperobject.biblemap.put("b2_37_24", new trsuperobject(2,37,24,"Bütün takımları dahil kandilliğe bir talant saf altın harcandı."));
