trsuperobject.biblemap.put("b23_5_17", new trsuperobject(23,5,17,"Kuzular kendi otlaklarındaymış gibi otlayacak, zenginlerin ıssız kalan konutlarını yabancılar ele geçirecek."));
trsuperobject.biblemap.put("b23_5_18", new trsuperobject(23,5,18,"Suçu yalanla örülmüş iplerle, günahı araba urganıyla çekenlerin vay haline!"));
trsuperobject.biblemap.put("b23_5_19", new trsuperobject(23,5,19,"Diyorlar ki, ‹‹Tanrı elini çabuk tutup işini hızlandırsın da görelim. İsrailin Kutsalı tasarladığını yapsın da görelim.››"));
trsuperobject.biblemap.put("b23_5_20", new trsuperobject(23,5,20,"Kötüye iyi, iyiye kötü diyenlerin, karanlığı ışık, ışığı karanlık yerine koyanların, acıya tatlı, tatlıya acı diyenlerin vay haline!"));
trsuperobject.biblemap.put("b23_5_21", new trsuperobject(23,5,21,"Kendilerini bilge görenlerin, akıllı sananların vay haline!"));
trsuperobject.biblemap.put("b23_5_22-23", new trsuperobject(23,5,22-23,"Şarap içmekte sınır tanımayanların, içkileri karıştırıp içmekten çekinmeyenlerin, rüşvet uğruna kötüyü haklı çıkaranların, haklıların hakkını elinden alanların vay haline!"));
trsuperobject.biblemap.put("b23_5_24", new trsuperobject(23,5,24,"Alev alev yanan ateş, samanı nasıl yiyip bitirirse, kuru ot alevin içinde nasıl birden tutuşup yok olursa, onlar da kökten çürüyüp gidecek, çiçekleri toz gibi havaya savrulacak. Çünkü Her Şeye Egemen RABbin yasasını reddettiler, İsrailin Kutsalının sözlerini küçümsediler."));
trsuperobject.biblemap.put("b23_5_25", new trsuperobject(23,5,25,"Bu yüzden RABbin halkına karşı öfkesi alevlendi, elini kaldırıp onları vurdu. Dağlar titriyor, cesetler çöp gibi sokaklara serildi. Bütün bunlara karşın RABbin öfkesi dinmedi, eli hâlâ kalkmış durumda."));
trsuperobject.biblemap.put("b23_5_26", new trsuperobject(23,5,26,"RAB uzaktaki ulusları bir sancak işaretiyle, dünyanın en uzağındakileri ıslık sesiyle çağıracak; hızla, hemen gelecekler."));
trsuperobject.biblemap.put("b23_5_27", new trsuperobject(23,5,27,"Aralarında yorulan, sendeleyen olmayacak; uyuklamayacak, uyumayacaklar. Gevşek kemer, kopuk çarık bağı olmayacak."));
trsuperobject.biblemap.put("b23_5_28", new trsuperobject(23,5,28,"Okları sivri, yayları kuruludur. Atlarının toynakları çakmaktaşı, arabalarının tekerlekleri kasırga gibidir."));
trsuperobject.biblemap.put("b23_5_29", new trsuperobject(23,5,29,"Askerleri dişi aslan gibi, genç aslanlar gibi kükrüyor, homurdanarak avlarını kapıp götürüyorlar. Kimse avlarını pençelerinden kurtaramıyor."));
trsuperobject.biblemap.put("b23_5_30", new trsuperobject(23,5,30,"O gün İsrail'e karşı denizin gürleyişi gibi gürleyecekler. Karaya bakan biri karanlık ve sıkıntı görecek. Işık karanlık bulutlarla kaplanacak."));
trsuperobject.biblemap.put("b23_6_1", new trsuperobject(23,6,1,"Kral Uzziyanın öldüğü yıl yüce ve görkemli Rabbi gördüm; tahtta oturuyordu, giysisinin etekleri tapınağı dolduruyordu."));
trsuperobject.biblemap.put("b23_6_2", new trsuperobject(23,6,2,"Üzerinde Seraflar duruyordu; her birinin altı kanadı vardı; ikisiyle yüzlerini, ikisiyle ayaklarını örtüyor, öbür ikisiyle de uçuyorlardı."));
trsuperobject.biblemap.put("b23_6_3", new trsuperobject(23,6,3,"Birbirlerine şöyle sesleniyorlardı:  ‹‹Her Şeye Egemen RABKutsal, kutsal, kutsaldır.Yüceliği bütün dünyayı dolduruyor.››"));
trsuperobject.biblemap.put("b23_6_4", new trsuperobject(23,6,4,"Serafların sesinden kapı söveleriyle eşikler sarsıldı, tapınak dumanla doldu."));
trsuperobject.biblemap.put("b23_6_5", new trsuperobject(23,6,5,"‹‹Vay başıma! Mahvoldum›› dedim, ‹‹Çünkü dudakları kirli bir adamım, dudakları kirli bir halkın arasında yaşıyorum. Buna karşın Kralı, Her Şeye Egemen RABbi gözlerimle gördüm.››"));
trsuperobject.biblemap.put("b23_6_6", new trsuperobject(23,6,6,"Seraflardan biri bana doğru uçtu, elinde sunaktan maşayla aldığı bir kor vardı;"));
trsuperobject.biblemap.put("b23_6_7", new trsuperobject(23,6,7,"onunla ağzıma dokunarak, ‹‹İşte bu kor dudaklarına değdi, suçun silindi, günahın bağışlandı›› dedi."));
trsuperobject.biblemap.put("b23_6_8", new trsuperobject(23,6,8,"Sonra Rabbin sesini işittim: ‹‹Kimi göndereyim? Bizim için kim gidecek?›› diyordu. ‹‹Ben! Beni gönder›› dedim."));
trsuperobject.biblemap.put("b23_6_9", new trsuperobject(23,6,9,"‹‹Git, bu halka şunu duyur›› dedi,  ‹‹Duyacak duyacak, ama anlamayacaksınız,Bakacak bakacak, ama görmeyeceksiniz!"));
trsuperobject.biblemap.put("b23_6_10", new trsuperobject(23,6,10,"Bu halkın yüreğini duygusuzlaştır,Kulaklarını ağırlaştır,Gözlerini kapat.Öyle ki, gözleri görmesin,Kulakları duymasın, yürekleri anlamasınVe bana dönüp şifa bulmasınlar.››"));
trsuperobject.biblemap.put("b23_6_11", new trsuperobject(23,6,11,"‹‹Ne vakte kadar, ya Rab?›› diye sordum. Rab yanıtladı:  ‹‹Kentler viraneye dönüp kimsesiz kalıncaya,Evler ıpıssız oluncaya,Toprak büsbütün kıraçlaşıncaya kadar."));
trsuperobject.biblemap.put("b23_6_12", new trsuperobject(23,6,12,"İnsanları çok uzaklara süreceğim,Ülke bomboş kalacak,"));
trsuperobject.biblemap.put("b23_6_13", new trsuperobject(23,6,13,"Halkın onda biri kalsa da ülke mahvolacak.Ama devrildiği zaman kütüğü kalanYabanıl fıstık ve meşe ağacı gibi,Kutsal soy kütüğünden çıkacak.››"));
trsuperobject.biblemap.put("b23_7_1", new trsuperobject(23,7,1,"Uzziya oğlu Yotam oğlu Ahaz Yahuda Kralıyken, Aram Kralı Resinle Remalya oğlu İsrail Kralı Pekah Yeruşalime saldırdılar, ama ele geçiremediler."));
trsuperobject.biblemap.put("b23_7_2", new trsuperobject(23,7,2,"Davutun torunları Aramın Efrayimlilerle güçbirliği ettiğini duydular. Ahazla halkının yürekleri rüzgarda sallanan orman ağaçları gibi titremeye başladı."));
trsuperobject.biblemap.put("b23_7_3", new trsuperobject(23,7,3,"Bu arada RAB Yeşayaya şöyle seslendi: ‹‹Ahazı karşılamak için oğlun Şear-Yaşuvla birlikte Yukarı Havuzun su yolunun sonuna, Çırpıcı Tarlasına giden yola çık."));
trsuperobject.biblemap.put("b23_7_4", new trsuperobject(23,7,4,"Ona de ki, ‹Dikkatli ve sakin ol, korkma! Şu tüten iki yanık odun parçasının -Aram Kralı Resinle Remalyanın oğlunun- öfkesinden korkma."));
trsuperobject.biblemap.put("b23_7_5", new trsuperobject(23,7,5,"Aram, Efrayim ve Remalyanın oğlu sizin için kötü şeyler tasarlıyor. Diyorlar ki,"));
trsuperobject.biblemap.put("b23_7_6", new trsuperobject(23,7,6,"Haydi, Yahudaya saldıralım, halkı korkutup ülkeyi ele geçirelim, Tavealın oğlunu kral ilan edelim."));
trsuperobject.biblemap.put("b23_7_7", new trsuperobject(23,7,7,"‹‹Buna karşılık Egemen RAB diyor ki, bu tasarı asla gerçekleşmeyecek."));
trsuperobject.biblemap.put("b23_7_8", new trsuperobject(23,7,8,"Çünkü Şam sadece Aramın başkenti, Resin de sadece Şamın başıdır. Efrayime gelince, altmış beş yıl içinde paramparça edilip halk olmaktan çıkacak."));
trsuperobject.biblemap.put("b23_7_9", new trsuperobject(23,7,9,"Samiriye sadece Efrayimin başkenti, Remalyanın oğlu da sadece Samiriyenin başıdır. Bana güvenmezseniz, güvenlikte olamazsınız.››"));
trsuperobject.biblemap.put("b23_7_10", new trsuperobject(23,7,10,"RAB Ahaza yine seslendi:"));
trsuperobject.biblemap.put("b23_7_11", new trsuperobject(23,7,11,"‹‹Tanrın RABden bir işaret iste; ölüler diyarı kadar derin, gökler kadar yüksek olsun.››"));
trsuperobject.biblemap.put("b23_7_12", new trsuperobject(23,7,12,"Ama Ahaz, ‹‹Hayır, istemem, RABbi sınamam›› dedi."));
trsuperobject.biblemap.put("b23_7_13", new trsuperobject(23,7,13,"Bunun üzerine Yeşaya, ‹‹Dinleyin, ey Davutun torunları!›› dedi, ‹‹İnsanların sabrını taşırmanız yetmezmiş gibi şimdi de Tanrımın sabrını mı taşırıyorsunuz?"));
trsuperobject.biblemap.put("b23_7_14", new trsuperobject(23,7,14,"Bundan ötürü Rabbin kendisi size bir belirti verecek: İşte, kız gebe kalıp bir oğul doğuracak; adını İmmanuel koyacak."));
trsuperobject.biblemap.put("b23_7_15", new trsuperobject(23,7,15,"Çocuk kötüyü reddedip iyiyi seçecek yaşa gelince tereyağı ve bal yiyecek."));
trsuperobject.biblemap.put("b23_7_16", new trsuperobject(23,7,16,"Ama çocuk kötüyü reddedip iyiyi seçecek yaşa gelmeden, seni dehşete düşüren o iki kralın toprakları ıssız kalacak."));
trsuperobject.biblemap.put("b23_7_17", new trsuperobject(23,7,17,"‹‹RAB seni, halkını ve babanın soyunu Efrayimin Yahudadan ayrıldığı günden bu yana görülmemiş bir felakete uğratacak; üzerinize Asur Kralını saldırtacak."));
trsuperobject.biblemap.put("b23_7_18", new trsuperobject(23,7,18,"‹‹O gün RAB Mısır ırmaklarının ta uçlarından sinekleri, Asur topraklarından arıları ıslıkla çağıracak."));
trsuperobject.biblemap.put("b23_7_19", new trsuperobject(23,7,19,"Akın akın gelip derin vadilerde, kaya kovuklarında, dikenli çalılıklarda, otlaklarda konaklayacaklar."));
trsuperobject.biblemap.put("b23_7_20", new trsuperobject(23,7,20,"‹‹O gün Rab Fıratın ötesinden kiraladığı usturayla -Asur Kralıyla- sakalınızı, saçlarınızı, beden kıllarınızı tıraş edecek."));
trsuperobject.biblemap.put("b23_7_21", new trsuperobject(23,7,21,"O günlerde bir inekle bir çift koyun besleyen"));
trsuperobject.biblemap.put("b23_7_22", new trsuperobject(23,7,22,"aldığı bol süt sayesinde tereyağı yiyecek. Ülkede kalan herkes bal ve tereyağıyla beslenecek."));
trsuperobject.biblemap.put("b23_7_23", new trsuperobject(23,7,23,"‹‹O gün bin gümüş değerinde bin asmaya sahip olan her bağ dikenli çalılarla dolacak."));
trsuperobject.biblemap.put("b23_7_24", new trsuperobject(23,7,24,"İnsanlar oralara okla, yayla gidecek. Çünkü ülkenin her yanı dikenli çalılarla kaplanacak."));
trsuperobject.biblemap.put("b23_7_25", new trsuperobject(23,7,25,"Bir zamanlar çapalanıp ekin ekilen tepeler korkudan kimsenin giremeyeceği dikenliklere dönecek, sığırın gezindiği, davarın çiğnediği yerler olacak.››"));
trsuperobject.biblemap.put("b23_8_2", new trsuperobject(23,8,2,"Kâhin Uriya ile Yeverekya oğlu Zekeriyayı kendime güvenilir tanık seçiyorum.›› anlamına gelir."));
trsuperobject.biblemap.put("b23_8_3", new trsuperobject(23,8,3,"Peygamber olan karım bundan bir süre sonra gebe kaldı ve bir erkek çocuk doğurdu. RAB bana, ‹‹Adını ‹Maher-Şalal-Haş-Baz› koy›› dedi,"));
trsuperobject.biblemap.put("b23_8_4", new trsuperobject(23,8,4,"‹‹Çocuk daha ‹Anne, baba› demesini öğrenmeden, Şamın serveti ve Samiriyenin ganimeti Asur Kralına götürülecek.››"));
trsuperobject.biblemap.put("b23_8_6", new trsuperobject(23,8,6,"‹‹Bu halk usul usul akan Şiloah sularını reddettiği, Resinle Remalyanın oğluyla mutlu olduğu için,"));
trsuperobject.biblemap.put("b23_8_7", new trsuperobject(23,8,7,"ben Rab, Fıratın kabaran güçlü sularını -bütün dehşetiyle Asur Kralını- üzerlerine salacağım. Yatağından taşan ırmak, kıyılarını su altında bırakacak."));
trsuperobject.biblemap.put("b23_8_8", new trsuperobject(23,8,8,"Yahudayı kaplayan sular her şeyi silip süpürerek adam boyu yükselecek, ülkeni boydan boya dolduracak, ey İmmanuel!›› için››."));
trsuperobject.biblemap.put("b23_8_9", new trsuperobject(23,8,9,"Ey halklar, yıkıma, bozguna uğrayacaksınız. Yeryüzünün en uç köşeleri, kulak verin. Savaşmaya, bozguna uğramaya hazırlanın. Evet, savaşa ve bozguna hazır olun."));
trsuperobject.biblemap.put("b23_8_10", new trsuperobject(23,8,10,"İstediğinizi tasarlayın, hepsi boşa gidecek. İstediğiniz kadar konuşun, hiçbiri gerçekleşmeyecek. Çünkü Tanrı bizimledir."));
trsuperobject.biblemap.put("b23_8_11", new trsuperobject(23,8,11,"RAB beni halkın tuttuğu yoldan gitmeme konusunda şiddetle uyararak şöyle dedi:"));
trsuperobject.biblemap.put("b23_8_12", new trsuperobject(23,8,12,"‹‹Onların entrika dediği her şeyeSiz entrika demeyin;Onların korktuğundan korkmayın, yılmayın."));
trsuperobject.biblemap.put("b23_8_13", new trsuperobject(23,8,13,"‹‹Her Şeye Egemen RABbi kutsal sayın.Korkunuz, yılgınız Ondan olsun."));
trsuperobject.biblemap.put("b23_8_14", new trsuperobject(23,8,14,"Tapınak O olacak.İsrailin iki krallığı içinseSürçme taşı ve tökezleme kayası,Yeruşalimde yaşayanlar içinKapan ve tuzak olacak."));
trsuperobject.biblemap.put("b23_8_15", new trsuperobject(23,8,15,"Birçokları sendeleyip düşecek, parçalanacak,Tuzağa düşüp ele geçecek.››"));
trsuperobject.biblemap.put("b23_8_16", new trsuperobject(23,8,16,"Ya RAB, öğrencilerim arasında bildirimi koru,Öğretimi mühürle!"));
trsuperobject.biblemap.put("b23_8_17", new trsuperobject(23,8,17,"Kendini Yakupun soyundan gizleyen RABbi özlemle bekliyorum, umudum Onda."));
trsuperobject.biblemap.put("b23_8_18", new trsuperobject(23,8,18,"Ben ve RABbin bana verdiği çocuklar, Siyon Dağında oturan Her Şeye Egemen RABbin İsraildeki belirtileri ve işaretleriyiz."));
trsuperobject.biblemap.put("b23_8_19", new trsuperobject(23,8,19,"Birileri size, ‹‹Fısıldaşıp mırıldanan medyumlarla ruh çağıranlara danışın›› dediğinde, ‹‹Halk kendi Tanrısına danışmaz mı; yaşayanlar için ölülere mi danışılır?›› deyin."));
trsuperobject.biblemap.put("b23_8_20", new trsuperobject(23,8,20,"Tanrının öğretisine ve bildirisine dönmek gerek! Böyle düşünmezlerse, onlar için hiç şafak sökmeyecek."));
trsuperobject.biblemap.put("b23_8_21", new trsuperobject(23,8,21,"Aç ve çaresiz, ülkede dolanıp duracaklar. Aç kalınca öfkelenip krallarına, Tanrılarına lanet edecekler. Yukarıya da"));
trsuperobject.biblemap.put("b23_8_22", new trsuperobject(23,8,22,"dünyaya da baksalar sıkıntıdan, karanlıktan, korkunç karanlıktan başka bir şey görmeyecekler. Kovulacakları yer koyu karanlıktır."));
trsuperobject.biblemap.put("b23_9_1", new trsuperobject(23,9,1,"Bununla birlikte sıkıntı çekmiş olan ülke karanlıkta kalmayacak. Geçmişte Zevulun ve Naftali bölgelerini alçaltan Tanrı, gelecekte Şeria Irmağının ötesinde, Deniz Yolunda, ulusların yaşadığı Celileyi onurlandıracak."));
trsuperobject.biblemap.put("b23_9_2", new trsuperobject(23,9,2,"Karanlıkta yürüyen halkBüyük bir ışık görecek;Ölümün gölgelediği diyardaYaşayanların üzerine ışık parlayacak."));
trsuperobject.biblemap.put("b23_9_3", new trsuperobject(23,9,3,"Ya RAB, ulusu çoğaltacak, sevincini artıracaksın.Ekin biçenlerin neşelendiği,Ganimet paylaşanların coştuğu gibi,Onlar da sevinecek senin önünde."));
trsuperobject.biblemap.put("b23_9_4", new trsuperobject(23,9,4,"Çünkü onlara yük olan boyunduruğu,Omuzlarını döven değneği,Onlara eziyet edenlerin sopasını paramparça edeceksin;Tıpkı Midyanlıları yenilgiye uğrattığın günkü gibi."));
trsuperobject.biblemap.put("b23_9_5", new trsuperobject(23,9,5,"Savaşta giyilen çizmeleriVe kana bulanmış giysileriYakılacak, ateşe yem olacak."));
trsuperobject.biblemap.put("b23_9_6", new trsuperobject(23,9,6,"Çünkü bize bir çocuk doğacak,Bize bir oğul verilecek.Yönetim onun omuzlarında olacak.Onun adı Harika Öğütçüfı, Güçlü Tanrı,Ebedi Baba, Esenlik Önderi olacak."));
trsuperobject.biblemap.put("b23_9_7", new trsuperobject(23,9,7,"Davutun tahtı ve ülkesi üzerinde egemenlik sürecek.Egemenliğinin ve esenliğinin büyümesi son bulmayacak.Egemenliğini adaletle, doğrulukla kuracakVe sonsuza dek sürdürecek.Her Şeye Egemen RABbin gayreti bunu sağlayacak."));
trsuperobject.biblemap.put("b23_9_8", new trsuperobject(23,9,8,"Rab İsrail için,Yakup soyu için yargısını bildirdi.Bu yargı yerine gelecek."));
trsuperobject.biblemap.put("b23_9_9", new trsuperobject(23,9,9,"Bütün halk, Efrayim ve Samiriyede yaşayanlar,Rabbin bu yargısını duyacak.Gururlu ve küstah olan bu halk diyor ki,"));
trsuperobject.biblemap.put("b23_9_10", new trsuperobject(23,9,10,"‹‹Kerpiç evler yıkıldı,Ama yerlerine yontma taştan evler yapacağız.Yabanıl incir ağaçları kesildi,Ama yerlerine sedir ağaçları dikeceğiz.››"));
trsuperobject.biblemap.put("b23_9_11-12", new trsuperobject(23,9,11-12,"Bundan dolayı RAB, Resinin hasımlarınıHalka karşı güçlendirecek;Düşmanlarını, doğudan Aramlıları,Batıdan Filistlileri ayaklandıracak.Bunlar ağızlarını ardına kadar açıp İsraili yutacaklar.Bütün bunlara karşın RABbin öfkesi dinmedi,Eli hâlâ kalkmış durumda."));
trsuperobject.biblemap.put("b23_9_13", new trsuperobject(23,9,13,"Halk kendisini cezalandıran RABbe dönmeyecek,Her Şeye Egemen RABbi aramayacak."));
trsuperobject.biblemap.put("b23_9_14", new trsuperobject(23,9,14,"Bunun için RAB İsrailden başı da kuyruğu daHurma dalını da sazı daBir günde kesip atacak."));
trsuperobject.biblemap.put("b23_9_15", new trsuperobject(23,9,15,"Baş ileri gelen saygın kişi,Kuyruksa öğretisi sahte olan peygamberdir."));
trsuperobject.biblemap.put("b23_9_16", new trsuperobject(23,9,16,"Çünkü bu halkı saptıranlar ona yol gösterenlerdir.Onları izleyenler de yem oluyor."));
trsuperobject.biblemap.put("b23_9_17", new trsuperobject(23,9,17,"Bu yüzden Rab onların gençleri için sevinç duymayacak,Öksüzlerine, dul kadınlarına acımayacak.Çünkü hepsi tanrısızdır, kötülük yaparlar.Her ağız saçmalıyor.Bütün bunlara karşın RABbin öfkesi dinmedi,Eli hâlâ kalkmış durumda."));
trsuperobject.biblemap.put("b23_9_18", new trsuperobject(23,9,18,"Kötülük dikenli çalıları yiyip bitiren ateş gibidir.Ormandaki çalılığı tutuşturur,Duman sütunları yükseltir."));
trsuperobject.biblemap.put("b23_9_19", new trsuperobject(23,9,19,"Her Şeye Egemen RABbin öfkesiÜlkeyi ateş gibi sardı.Halk ateşe yem olacak,Kardeş kardeşini esirgemeyecek."));
trsuperobject.biblemap.put("b23_9_20", new trsuperobject(23,9,20,"İnsanlar şurada burada bulduklarını yiyecekler,Ama aç kalacak, doymayacaklar.Herkes çocuğunun etini yiyecek:"));
trsuperobject.biblemap.put("b23_9_21", new trsuperobject(23,9,21,"Manaşşe Efrayim'i,Efrayim Manaşşe'yi yiyecek,Sonra birlikte Yahuda'nın üzerine yürüyecekler.Bütün bunlara karşın RAB'bin öfkesi dinmedi,Eli hâlâ kalkmış durumda."));
trsuperobject.biblemap.put("b23_10_1-2", new trsuperobject(23,10,1-2,"Yoksullardan adaleti esirgemek,Halkımın düşkünlerinin hakkını elinden almak,Dulları avlamak,Öksüzlerin malını yağmalamak içinHaksız kararlar alanların,Adil olmayan yasalar çıkaranların vay haline!"));
trsuperobject.biblemap.put("b23_10_3", new trsuperobject(23,10,3,"Yargı günüUzaklardan başınıza felaket geldiğinde ne yapacaksınız?Yardım için kime koşacaksınız,Servetinizi nereye saklayacaksınız?"));
trsuperobject.biblemap.put("b23_10_4", new trsuperobject(23,10,4,"Tutsaklar arasında bir köşeye sinmekYa da savaşta ölmekten başka çareniz kalmayacak.Bütün bunlara karşın RABbin öfkesi dinmedi,Eli hâlâ kalkmış durumda."));
trsuperobject.biblemap.put("b23_10_5", new trsuperobject(23,10,5,"‹‹Vay haline Asur, öfkemin değneği!Elindeki sopa benim gazabımdır."));
trsuperobject.biblemap.put("b23_10_6", new trsuperobject(23,10,6,"Asuru tanrısız ulusa karşı salacağım;Soyup yağma etmesi,Sokaktaki çamur gibi onları çiğnemesi,Öfkelendiğim halkın üzerine yürümesi içinBuyruk vereceğim.››"));
trsuperobject.biblemap.put("b23_10_7", new trsuperobject(23,10,7,"Ama Asur Kralı bundan da kötüsünü düşünüyor.Birçok ulusun kökünü kazıyıp yok etmeyi tasarlıyor."));
trsuperobject.biblemap.put("b23_10_8", new trsuperobject(23,10,8,"‹‹Komutanlarımın hepsi birer kral değil mi?›› diyor,"));
trsuperobject.biblemap.put("b23_10_9", new trsuperobject(23,10,9,"‹‹Kalnoyu, Karkamış gibi ele geçirmedim mi?Hamanın sonu Arpatınki,Samiriyenin sonu Şamınki gibi olmadı mı?"));
trsuperobject.biblemap.put("b23_10_10", new trsuperobject(23,10,10,"Putları Yeruşalim ve Samiriyeninkinden daha çok olan putperest ülkeleri nasıl ele geçirdimse,"));
trsuperobject.biblemap.put("b23_10_11", new trsuperobject(23,10,11,"Samiriyeye ve putlarına ne yaptımsa,Yeruşalime ve putlarına da yapamaz mıyım?››"));
trsuperobject.biblemap.put("b23_10_12", new trsuperobject(23,10,12,"Rab Siyon Dağına ve Yeruşalime karşı tasarladıklarını yapıp bitirdikten sonra şöyle diyecek:‹‹Asur Kralını kibirli yüreği,Övüngen bakışları yüzünden cezalandıracağım."));
trsuperobject.biblemap.put("b23_10_13", new trsuperobject(23,10,13,"Çünkü, ‹Her şeyi bileğimin gücüyle,Bilgeliğimle yaptım› diyor,‹Akıllıyım, ulusları ayıran sınırları yok ettim,Hazinelerini yağmaladım,Güçlü kralları tahtlarından indirdim."));
trsuperobject.biblemap.put("b23_10_14", new trsuperobject(23,10,14,"Elimi yuvaya sokup kuş yumurtalarını toplar gibiUlusların varını yoğunu topladım.Terk edilmiş yumurtaları nasıl toplarlarsa,Ben de bütün ülkeleri öyle topladım.Kanat çırpan, ağzını açan,Sesini çıkaran olmadı.››"));
trsuperobject.biblemap.put("b23_10_15", new trsuperobject(23,10,15,"Balta kendisini kullanana karşı övünür mü?Testere kendisini kullanana karşı büyüklenir mi?Sanki değnek kendisini kaldıranı sallayabilir,Sopa sahibini kaldırabilirmiş gibi..."));
trsuperobject.biblemap.put("b23_10_16", new trsuperobject(23,10,16,"Rab, Her Şeye Egemen RAB,Asurun güçlü adamlarınıYıpratıcı hastalıkla cezalandıracak.Orduları alev alev yanacak."));
trsuperobject.biblemap.put("b23_10_17", new trsuperobject(23,10,17,"İsrailin Işığı ateş,İsrailin Kutsalı alev olacak;Asurun dikenli çalılarınıBir gün içinde yakıp bitirecek."));
trsuperobject.biblemap.put("b23_10_18", new trsuperobject(23,10,18,"Görkemli ormanıyla verimli tarlaları,Ölümcül bir hastalığa yakalanmış insan gibiTümüyle harap olacak."));
trsuperobject.biblemap.put("b23_10_19", new trsuperobject(23,10,19,"Ormanda artakalan ağaçlarBir çocuğun bile sayabileceği kadar az olacak."));
trsuperobject.biblemap.put("b23_10_20", new trsuperobject(23,10,20,"O gün İsrailin sağ kalanları,Yakupun kaçıp kurtulan torunları,Kendilerini yok etmek isteyene değil,Artık içtenlikle RABbe, İsrailin Kutsalına dayanacaklar."));
trsuperobject.biblemap.put("b23_10_21", new trsuperobject(23,10,21,"Geriye kalanlar,Yakup soyundan sağ kalanlar,Güçlü Tanrıya dönecekler."));
trsuperobject.biblemap.put("b23_10_22", new trsuperobject(23,10,22,"Ey İsrail, halkın denizin kumu kadar çok olsa da,Ancak pek azı dönecek.Tümüyle adil bir yıkım kararlaştırıldı."));
trsuperobject.biblemap.put("b23_10_23", new trsuperobject(23,10,23,"Rab, Her Şeye Egemen RAB,Kararlaştırılan yıkımı bütün yeryüzünde gerçekleştirecek. sözcükleri genellikle ‹‹Sağ kalanlar dönecek›› diye çevrildi. Yeşaya bu sözcükleri ilk oğluna ad olarak verdi (bkz. 7:3)."));
trsuperobject.biblemap.put("b23_10_24", new trsuperobject(23,10,24,"Bu nedenle Rab,Her Şeye Egemen RAB şöyle diyor:‹‹Ey sen, Siyonda yaşayan halkım,Asurlular, Mısırlıların yaptığı gibiSana değnekle vurduklarında,Sopalarını sana karşı kaldırdıklarında korkma."));
trsuperobject.biblemap.put("b23_10_25", new trsuperobject(23,10,25,"Çünkü çok yakında gazabım sona erecek,Öfkem Asurluların yıkımını sağlayacak."));
trsuperobject.biblemap.put("b23_10_26", new trsuperobject(23,10,26,"Ben, Her Şeye Egemen RAB,Midyanlıları Orev Kayasında alt ettiğim gibi,Onları da kırbaçla alt edeceğim.Değneğimi Mısıra karşı nasıl denizin üzerine uzattımsa,Şimdi yine öyle yapacağım."));
trsuperobject.biblemap.put("b23_10_27", new trsuperobject(23,10,27,"O gün Asurun yükü sırtınızdan,Boyunduruğu boynunuzdan kalkacak;Semirdiğiniz için boyunduruk kırılacak.››"));
trsuperobject.biblemap.put("b23_10_28", new trsuperobject(23,10,28,"Ayat Kentine saldırdılar,Migrondan geçip ağırlıklarını Mikmasta bıraktılar."));
trsuperobject.biblemap.put("b23_10_29", new trsuperobject(23,10,29,"Geçidi aşarak Gevada konakladılar.Rama Kenti korkudan titredi,Saulun kenti Givada yaşayan halk kaçıştı."));
trsuperobject.biblemap.put("b23_10_30", new trsuperobject(23,10,30,"Ey Gallim halkı, sesini yükselt!Ey Layşa halkı, dinle!Zavallı Anatot halkı!"));
trsuperobject.biblemap.put("b23_10_31", new trsuperobject(23,10,31,"Madmena halkı kaçıyor,Hagevimde yaşayanlar sığınacak yer arıyor."));
trsuperobject.biblemap.put("b23_10_32", new trsuperobject(23,10,32,"Düşman bugün Novda duracak;Siyon Kentinin kurulduğu dağa,Yeruşalim Tepesine yumruk sallayacak."));
trsuperobject.biblemap.put("b23_10_33", new trsuperobject(23,10,33,"Rab, Her Şeye Egemen RAB düşmanıDal gibi kesip korkunç güçle yere çalacak.Uzun boyluları devirecek,Gururluları alçaltacak."));
trsuperobject.biblemap.put("b23_10_34", new trsuperobject(23,10,34,"Ormandaki çalılıkları baltayla keser gibiKesip devirecek onları.Lübnan, Güçlü Olan'ın önünde diz çökecek."));
trsuperobject.biblemap.put("b23_11_1", new trsuperobject(23,11,1,"İşayın kütüğünden yeni bir filiz çıkacak,Kökünden bir fidan meyve verecek."));
trsuperobject.biblemap.put("b23_11_4", new trsuperobject(23,11,4,"Yoksulları adaletle yargılayacak,Yeryüzünde ezilenler için dürüstçe karar verecek.Dünyayı ağzının değneğiyle cezalandıracak,Kötüleri soluğuyla öldürecek."));
trsuperobject.biblemap.put("b23_11_5", new trsuperobject(23,11,5,"Davranışının temeli adalet ve sadakat olacak. ‹‹Adalet ve sadakat belindeki kuşaktır››."));
trsuperobject.biblemap.put("b23_11_6", new trsuperobject(23,11,6,"Onun döneminde kurtla kuzu bir arada yaşayacak,Parsla oğlak birlikte yatacak,Buzağı, genç aslan ve besili sığır yanyana duracak,Onları küçük bir çocuk güdecek."));
trsuperobject.biblemap.put("b23_11_7", new trsuperobject(23,11,7,"İnekle ayı birlikte otlayacak,Yavruları bir arada yatacak.Aslan sığır gibi saman yiyecek."));
trsuperobject.biblemap.put("b23_11_8", new trsuperobject(23,11,8,"Emzikteki bebek kobra deliği üzerinde oynayacak,Sütten kesilmiş çocuk elini engerek kovuğuna sokacak."));
trsuperobject.biblemap.put("b23_11_9", new trsuperobject(23,11,9,"Kutsal dağımın hiçbir yerindeKimse zarar vermeyecek, yok etmeyecek.Çünkü sular denizi nasıl dolduruyorsa,Dünya da RABbin bilgisiyle dolacak."));
trsuperobject.biblemap.put("b23_11_10", new trsuperobject(23,11,10,"O gün İşayın kökü ortaya çıkacak,Halklara sancak olacak,Uluslar ona yönelecek.Kaldığı yer görkemli olacak."));
trsuperobject.biblemap.put("b23_11_11", new trsuperobject(23,11,11,"O gün Rab, Asurdan,Mısır, Patros, Kûş, Elam,Şinar, Hama ve deniz kıyılarındanHalkının sağ kalanlarını kurtarmak içinİkinci kez elini uzatacak."));
trsuperobject.biblemap.put("b23_11_12", new trsuperobject(23,11,12,"Uluslar için sancak kaldıracak,Sürgün İsraillileri toplayacak,Dağılmış YahudalılarıDünyanın dört bucağından bir araya getirecek."));
trsuperobject.biblemap.put("b23_11_13", new trsuperobject(23,11,13,"Efrayim halkının kıskançlığı yok olacak,Yahudalıları sıkıştıranlar ortadan kalkacak.Efrayim Yahudayı kıskanmayacak,Yahuda Efrayimi sıkıştırmayacak."));
trsuperobject.biblemap.put("b23_11_14", new trsuperobject(23,11,14,"Batıdaki Filistlilere saldırıpHep birlikte doğudakilerin her şeyini yağmalayacaklar.Edom ve Moav halklarının topraklarına el koyacak,Ammonlulara boyun eğdirecekler."));
trsuperobject.biblemap.put("b23_11_15", new trsuperobject(23,11,15,"RAB Mısırın Süveyş Körfezini tümüyle kurutacak.Elinin bir sallayışıyla estireceği kavurucu rüzgarlaFıratı süpürüp yedi dereye bölecek.Öyle ki, insanlar ırmak yatağından çarıkla geçebilsin."));
trsuperobject.biblemap.put("b23_11_16", new trsuperobject(23,11,16,"RAB'bin Asur'da sağ kalan halkı içinBir çıkış yolu olacak;Tıpkı Mısır'dan çıktıkları günİsrailliler'in de bir çıkış yolu olduğu gibi."));
trsuperobject.biblemap.put("b23_12_1", new trsuperobject(23,12,1,"İsrail halkı o gün,‹‹Ya RAB, sana şükrederiz›› diyecek,‹‹Bize öfkelenmiştin ama öfken dindi,Bizi avuttun."));
trsuperobject.biblemap.put("b23_12_2", new trsuperobject(23,12,2,"Tanrı kurtuluşumuzdur.Ona güvenecek, yılmayacağız.Çünkü RAB gücümüz ve ezgimizdir.O kurtardı bizi.››"));
trsuperobject.biblemap.put("b23_12_3", new trsuperobject(23,12,3,"Kurtuluş pınarlarından sevinçle su alacaksınız."));
trsuperobject.biblemap.put("b23_12_4", new trsuperobject(23,12,4,"O gün diyeceksiniz ki,‹‹RABbe şükredin, Onu adıyla çağırın,Halklara duyurun yaptıklarını,Adının yüce olduğunu duyurun!"));
trsuperobject.biblemap.put("b23_12_6", new trsuperobject(23,12,6,"Ey Siyon halkı, sesini yükselt, sevinçle haykır!Çünkü aranızda bulunan İsrail'in Kutsalı büyüktür.››"));
trsuperobject.biblemap.put("b23_13_1", new trsuperobject(23,13,1,"Amots oğlu Yeşayanın Babille ilgili bildirisi:"));
trsuperobject.biblemap.put("b23_13_2", new trsuperobject(23,13,2,"Çıplak dağın tepesine sancak dikin!Savaşçıları yüksek sesle çağırıpEl sallayın kiSoylulara ayrılan kapılardan içeri girsinler."));
trsuperobject.biblemap.put("b23_13_4", new trsuperobject(23,13,4,"Dağlardaki kalabalığın gürültüsünü dinleyin!Büyük bir halkın sesini andırıyor.Bir araya gelmiş uluslarınVe krallıkların gümbürtüsünü dinleyin!Her Şeye Egemen RAB bir orduyu savaşa hazırlıyor."));
trsuperobject.biblemap.put("b23_13_5", new trsuperobject(23,13,5,"Öfkesinin araçlarıyla uzak bir ülkeden,Dünyanın öbür ucundanBütün ülkeyi yerle bir etmek üzere geliyor."));
trsuperobject.biblemap.put("b23_13_6", new trsuperobject(23,13,6,"Feryat edin! Çünkü RABbin günü yakındır.Her Şeye Gücü Yetenin göndereceği yıkım gibi geliyor o gün."));
trsuperobject.biblemap.put("b23_13_7", new trsuperobject(23,13,7,"Bu yüzden ellerde derman kalmayacak,Her yürek eriyecek."));
trsuperobject.biblemap.put("b23_13_8", new trsuperobject(23,13,8,"Herkesi dehşet saracak,Hepsi acı ve ıstırap içinde boğulacak,Doğuran kadın gibi kıvranacak,Şaşkın şaşkın birbirlerine bakacaklar;Yüzleri kızaracak."));
trsuperobject.biblemap.put("b23_13_9", new trsuperobject(23,13,9,"İşte RABbin acımasız günü geliyor.Ülkeyi viraneye çeviripİçindeki günahkârları ortadan kaldıracağıGazap ve kızgın öfke dolu gün geliyor."));
trsuperobject.biblemap.put("b23_13_10", new trsuperobject(23,13,10,"Gökteki yıldızlarla takımyıldızlar ışımayacak,Doğan güneş kararacak, ay ışığını vermez olacak."));
trsuperobject.biblemap.put("b23_13_11", new trsuperobject(23,13,11,"RAB diyor ki, ‹‹Kötülüğünden ötürü dünyayı,Suçlarından ötürü kötüleri cezalandıracağım.Kibirlilerin küstahlığını sona erdirecek,Zalimlerin gururunu kıracağım."));
trsuperobject.biblemap.put("b23_13_12", new trsuperobject(23,13,12,"İnsanı saf altından,Ofir altınından daha ender kılacağım."));
trsuperobject.biblemap.put("b23_13_13", new trsuperobject(23,13,13,"Ben, Her Şeye Egemen RAB,Gazaba geldiğim, öfkemin alevlendiği günGökleri titreteceğim, yer yerinden oynayacak."));
trsuperobject.biblemap.put("b23_13_14", new trsuperobject(23,13,14,"‹‹Herkes kovalanan ceylan gibi,Çobansız koyunlar gibi halkına dönecek,Ülkesine kaçacak."));
trsuperobject.biblemap.put("b23_13_15", new trsuperobject(23,13,15,"Yakalananın bedeni delik deşik edilecek,Ele geçen kılıçtan geçirilecek."));
trsuperobject.biblemap.put("b23_13_16", new trsuperobject(23,13,16,"Yavruları gözleri önünde parçalanacak,Evleri yağmalanacak,Kadınlarının ırzına geçilecek."));
trsuperobject.biblemap.put("b23_13_17", new trsuperobject(23,13,17,"‹‹Gümüşe değer vermeyen,Altını sevmeyen MedleriOnlara karşı harekete geçireceğim."));
trsuperobject.biblemap.put("b23_13_18", new trsuperobject(23,13,18,"Oklarıyla gençleri parçalayacak,Bebeklere acımayacak,Çocukları esirgemeyecekler."));
trsuperobject.biblemap.put("b23_13_19", new trsuperobject(23,13,19,"Ben Tanrı, Sodom ve Gomorayı nasıl yerle bir ettimse,Kildanilerin yüce gururu,Krallıkların en güzeli olan Babili de yerle bir edeceğim."));
trsuperobject.biblemap.put("b23_13_20", new trsuperobject(23,13,20,"Orada bir daha kimse yaşamayacak,Kuşaklar boyu kimse oturmayacak,Bedeviler çadır kurmayacak,Çobanlar sürülerini dinlendirmeyecek."));
trsuperobject.biblemap.put("b23_13_21", new trsuperobject(23,13,21,"Orası yabanıl hayvanlara barınak olacak,Evler çakallarla dolacak,Baykuşlar yuva yapacak, tekeler oynaşacak orada. (bkz. Lev.17:7)."));
trsuperobject.biblemap.put("b23_13_22", new trsuperobject(23,13,22,"Kalelerinde sırtlanlar,Görkemli saraylarında çakallar uluyacak.Babil'in sonu yaklaştı, günleri uzatılmayacak.››"));
trsuperobject.biblemap.put("b23_14_1", new trsuperobject(23,14,1,"Çünkü RAB Yakup soyuna acıyacak,İsrail halkını yine seçipTopraklarına yerleştirecek.Yabancılar da Yakup soyuna katılıp onlara bağlanacak."));
trsuperobject.biblemap.put("b23_14_2", new trsuperobject(23,14,2,"Uluslar İsrail halkınıKendi topraklarına götürecekler.İsrail halkı RABbin verdiği topraklarda onlarıErkek ve kadın köle olarak sahiplenecek.Kendisini tutsak edenleri tutsak edecek,Kendisini ezenlere egemen olacak."));
trsuperobject.biblemap.put("b23_14_4", new trsuperobject(23,14,4,"Babil Kralını alaya alarak,‹‹Halkı ezenin nasıl da sonu geldi!›› diyecekler,‹‹Zorbalığı nasıl da sona erdi!››"));
trsuperobject.biblemap.put("b23_14_6", new trsuperobject(23,14,6,"O asa ki, halklara gazapla vurdukça vurdu,Ulusları öfkeyle, dinmeyen zulümle yönetti."));
trsuperobject.biblemap.put("b23_14_7", new trsuperobject(23,14,7,"Bütün dünya esenlik ve barış içindeSevinçle haykırıyor."));
trsuperobject.biblemap.put("b23_14_8", new trsuperobject(23,14,8,"Lübnanın çam ve sedir ağaçları bileKralın yok oluşuna seviniyor.‹‹Onun ölümünden beri kimse bizi kesmeye gelmiyor›› diyorlar."));
trsuperobject.biblemap.put("b23_14_9", new trsuperobject(23,14,9,"Toprağın altındaki ölüler diyarıBabil Kralını karşılamak için sabırsızlanıyor.Onun gelişi ölüleri,Dünyanın eski önderlerini heyecanlandırıyor;Ulusları yönetmiş krallarıTahtlarından ayağa kaldırıyor."));
trsuperobject.biblemap.put("b23_14_10", new trsuperobject(23,14,10,"Hepsi ona seslenip diyecekler ki,‹‹Sen de bizim gibi gücünü yitirdin,Bize benzedin.››"));
trsuperobject.biblemap.put("b23_14_11", new trsuperobject(23,14,11,"Görkemin de çenklerinin sesi deÖlüler diyarına indirildi.Altında kurtlar kaynaşacak,Üstünü kurtçuklar kaplayacak."));
trsuperobject.biblemap.put("b23_14_12", new trsuperobject(23,14,12,"Ey parlak yıldızfö, seherin oğlu,Göklerden nasıl da düştün!Ey ulusları ezip geçen,Nasıl da yere yıkıldın!"));
trsuperobject.biblemap.put("b23_14_13", new trsuperobject(23,14,13,"İçinden, ‹‹Göklere çıkacağım›› dedin,‹‹Tahtımı Tanrının yıldızlarından daha yükseğe koyacağım;İlahların toplandığı dağda,Safonun doruğunda oturacağım."));
trsuperobject.biblemap.put("b23_14_14", new trsuperobject(23,14,14,"Bulutların üstüne çıkacak,Kendimi Yüceler Yücesiyle eşit kılacağım.››"));
trsuperobject.biblemap.put("b23_14_15", new trsuperobject(23,14,15,"Ancak ölüler diyarına,Ölüm çukurunun dibineİndirilmiş bulunuyorsun."));
trsuperobject.biblemap.put("b23_14_16-17", new trsuperobject(23,14,16-17,"Seni görenler bakıp bakıp şöyle düşünecekler:‹‹Dünyayı sarsan, ülkeleri titreten,Yeryüzünü çöle çeviren,Kentleri yerle bir eden,Tutsakları evlerine salıvermeyen adam bu mu?››"));
trsuperobject.biblemap.put("b23_14_18", new trsuperobject(23,14,18,"Ulusların bütün kralları tek tek,Görkemli mezarlarda yatıyor."));
trsuperobject.biblemap.put("b23_14_19", new trsuperobject(23,14,19,"Ama sen reddedilen bir dal gibiMezarından dışarı atıldın;Bedenleri kılıçla delinipÖlüm çukurunun dibine atılmış ölülerle örtülüsün;Ayak altında çiğnenen leş gibisin."));
trsuperobject.biblemap.put("b23_14_20", new trsuperobject(23,14,20,"Ülkeni harap edip halkını katlettiğin içinBaşkaları gibi gömülmeyeceksin.  Kötülük yapan soy bir daha anılmayacak."));
trsuperobject.biblemap.put("b23_14_21", new trsuperobject(23,14,21,"Atalarının suçundan ötürüBabil Kralının oğullarını boğazlamak için yer hazırlayın.Kalkıp dünyayı sahiplenmesinler,Yeryüzünü kentlerle doldurmasınlar."));
trsuperobject.biblemap.put("b23_14_22", new trsuperobject(23,14,22,"‹‹Babil halkına karşı harekete geçeceğim››Diyor Her Şeye Egemen RAB,‹‹Babilin adını, sağ kalanlarını,Oğullarını, torunlarını dünyadan sileceğim.››Böyle diyor RAB."));
trsuperobject.biblemap.put("b23_14_23", new trsuperobject(23,14,23,"‹‹Babili baykuş yuvasına, bataklığa çevirecek,Yıkım süpürgesiyle süpüreceğim››Diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b23_14_24", new trsuperobject(23,14,24,"Her Şeye Egemen RAB ant içerek şöyle dedi:‹‹Düşündüğüm gibi olacak,Tasarladığım gibi gerçekleşecek."));
trsuperobject.biblemap.put("b23_14_25", new trsuperobject(23,14,25,"Asurluları kendi ülkemde ezecek,Dağlarımda çiğneyeceğim.Halkım Asurun boyunduruğundan,Omuzlarındaki yükten kurtulacak."));
trsuperobject.biblemap.put("b23_14_26", new trsuperobject(23,14,26,"İşte bütün dünya için belirlenen tasarı budur.Bütün uluslara karşı elim kalkmış durumda."));
trsuperobject.biblemap.put("b23_14_27", new trsuperobject(23,14,27,"Her Şeye Egemen RABbin tasarısını kim boşa çıkarabilir?Kalkmış durumdaki elini kim indirebilir?››"));
trsuperobject.biblemap.put("b23_14_28", new trsuperobject(23,14,28,"Kral Ahazın öldüğü yıl gelen bildiri:"));
trsuperobject.biblemap.put("b23_14_29", new trsuperobject(23,14,29,"Ey Filistliler, sizi döven değnek kırıldı diye sevinmeyin.Çünkü yılanın kökünden engerek türeyecek,Onun ürünü uçan yılan olacak."));
trsuperobject.biblemap.put("b23_14_30", new trsuperobject(23,14,30,"Yoksulların en yoksulu doyacak,Düşkünler güvenlikte yatacak.Ama sizin kökünüzü kıtlıkla kurutacağım,Sağ kalanlarınız da ölecek."));
trsuperobject.biblemap.put("b23_14_31", new trsuperobject(23,14,31,"Ulumaya başla ey kapı! Ey kent, feryat et!Ey Filistliler, eridiniz baştan başa.Kuzeyden toz duman yükseliyor,Düşman askerleri sıra sıra geliyor."));
trsuperobject.biblemap.put("b23_14_32", new trsuperobject(23,14,32,"O ulusun elçilerine ne yanıt verilecek?‹‹RAB Siyon'un temelini attı,Halkının düşkünleri oraya sığınacak›› denecek."));
trsuperobject.biblemap.put("b23_15_1", new trsuperobject(23,15,1,"Moavla ilgili bildiri:  Moavın Ar Kenti bir gecede viraneye döndü, yok oldu,Moavın Kîr Kenti bir gecede viraneye döndü, yok oldu."));
trsuperobject.biblemap.put("b23_15_2", new trsuperobject(23,15,2,"Bu yüzden Divon halkı ağlamak için tapınağa,Tapınma yerlerine çıktı.Moav halkı, Nevo ve Medeva için feryat ediyor.İnsanlar saçlarını sakallarını kesiyor."));
trsuperobject.biblemap.put("b23_15_3", new trsuperobject(23,15,3,"Çul giyiyorlar sokaklarda,Damlarda, meydanlarda herkes feryat ediyor,Gözyaşları sel gibi."));
trsuperobject.biblemap.put("b23_15_4", new trsuperobject(23,15,4,"Heşbon ve Elalenin haykırışlarıYahasa ulaşıyor.Moav askerleri bu yüzden feryat ediyor,Yürekleri korku içinde."));
trsuperobject.biblemap.put("b23_15_5", new trsuperobject(23,15,5,"Yüreğim sızlıyor Moav için.Kaçanlar Soara, Eglat-Şelişiyaya ulaştı,Ağlaya ağlaya çıkıyorlar Luhit Yokuşundan,Horonayim yolunda yıkımlarına ağıt yakıyorlar."));
trsuperobject.biblemap.put("b23_15_6", new trsuperobject(23,15,6,"Nimrim suları kuruduğu için otlar sararıp soldu.Taze ot kalmadı, yeşillik yok artık."));
trsuperobject.biblemap.put("b23_15_7", new trsuperobject(23,15,7,"Bu yüzden halk kazanıp biriktirdiği ne varsa,Kavak Vadisi üzerinden taşıyacak."));
trsuperobject.biblemap.put("b23_15_8", new trsuperobject(23,15,8,"Haykırışları Moav topraklarında yankılanıyor,Feryatları Eglayime, Beer-Elime dek ulaştı."));
trsuperobject.biblemap.put("b23_15_9", new trsuperobject(23,15,9,"Çünkü Dimon suları kan dolu,Ama başına daha beterini getireceğim.Moav'dan kaçıp kurtulanların,Ülkede sağ kalanların üzerine aslan salacağım."));
trsuperobject.biblemap.put("b23_16_1", new trsuperobject(23,16,1,"Seladan çöl yoluyla Siyon Kentinin kurulduğu dağa,Ülkenin hükümdarına kuzular gönderin."));
trsuperobject.biblemap.put("b23_16_2", new trsuperobject(23,16,2,"Moavlı kızlar yuvalarından atılmış,Öteye beriye uçuşan kuşlar gibiArnon Irmağının geçitlerinde dolaşıyor."));
trsuperobject.biblemap.put("b23_16_3", new trsuperobject(23,16,3,"‹‹Bize öğüt ver, bir karar al,Öğle sıcağında gece gibi gölge sal üstümüze.Kovulanları sakla, kaçakları ele verme›› diyorlar."));
trsuperobject.biblemap.put("b23_16_4", new trsuperobject(23,16,4,"‹‹Kovulanlarım seninle birlikte yaşasın.Kırıp geçirenlere karşıBiz Moavlılara sığınak ol.››Baskı ve yıkım son bulduğunda,Ülkeyi çiğneyenler yok olduğunda,Sevgiye dayanan bir yönetim kurulacak,"));
trsuperobject.biblemap.put("b23_16_5", new trsuperobject(23,16,5,"Davut soyundan biri sadakatle krallık yapacak.Yargılarken adaleti arayacak,Doğru olanı yapmakta tez davranacak."));
trsuperobject.biblemap.put("b23_16_6", new trsuperobject(23,16,6,"Moavın ne denli gururlanıp büyüklendiğini,Kendini ne denli beğendiğini,Kibirlenip küstahlaştığını duyduk.Övünmesi boşunadır."));
trsuperobject.biblemap.put("b23_16_7", new trsuperobject(23,16,7,"Bu yüzden Moavlılar Moav için feryat edecek,Hepsi feryat edecek.Kîr-Heresetin üzüm pestilleriniAnımsayıp üzülecek, yas tutacaklar."));
trsuperobject.biblemap.put("b23_16_8", new trsuperobject(23,16,8,"Çünkü Heşbonun tarlaları,Sivmanın asmaları kurudu.Ulusların beyleri onların seçkin dallarını kırdılar.O dallar ki, Yazere erişir, çöle uzanırdı,Filizleri yayılır, gölü aşardı."));
trsuperobject.biblemap.put("b23_16_9", new trsuperobject(23,16,9,"Bu yüzden Yazer için,Sivmanın asmaları için acı acı ağlıyorum.Sizleri gözyaşlarımla sulayacağım,Ey Heşbon ve Elale!Çünkü savaş çığlıkları yaz meyvelerinizin,Biçtiğiniz ekinin üzerine düştü."));
trsuperobject.biblemap.put("b23_16_10", new trsuperobject(23,16,10,"Meyve bahçelerindeki sevinç ve neşe yok oldu.Bağlarda ne şarkı söyleyen olacak,Ne sevinç çığlığı atan.Üzüm sıkma çukurlarında çalışan kalmayacak,Sevinç çığlıklarını susturdum."));
trsuperobject.biblemap.put("b23_16_11", new trsuperobject(23,16,11,"Yüreğim bir lir gibi inliyor Moav için,Kîr-Hereset için içim sızlıyor."));
trsuperobject.biblemap.put("b23_16_12", new trsuperobject(23,16,12,"Moav halkı tapınma yerine çıkarak kendini yoruyor,Dua etmek için tapınağa gidiyor, ama hepsi boşuna!"));
trsuperobject.biblemap.put("b23_16_13", new trsuperobject(23,16,13,"RABbin Moav için geçmişte söylediği budur."));
trsuperobject.biblemap.put("b23_16_14", new trsuperobject(23,16,14,"RAB şimdi diyor ki, ‹‹Moav'ın övündükleri de kalabalık halkı da tam üç yıl sonra rezil olacak. Sağ kalan çok az sayıda kişiyse güçsüz olacak.››"));
trsuperobject.biblemap.put("b23_17_1", new trsuperobject(23,17,1,"Şamla ilgili bildiri:  İşte Şam kent olmaktan çıkacak,Enkaz yığınına dönecek."));
trsuperobject.biblemap.put("b23_17_2", new trsuperobject(23,17,2,"Aroer kentleri terk edilecek,Hayvan sürüleri orada yatacak,Onları ürküten olmayacak."));
trsuperobject.biblemap.put("b23_17_3", new trsuperobject(23,17,3,"Efrayimde surlu kent kalmayacak,Şamın egemenliği yok olacak.Sağ kalan Aramlıların onuruİsrailin onuru gibi kırılacak.Her Şeye Egemen RAB böyle diyor."));
trsuperobject.biblemap.put("b23_17_4", new trsuperobject(23,17,4,"O gün Yakup soyunun görkemi sönecek,Hepsi bir deri bir kemik kalacak."));
trsuperobject.biblemap.put("b23_17_5", new trsuperobject(23,17,5,"İsrail, ekinin elle biçilipBaşakların devşirildiği bir tarla,Refaim Vadisinde hasattan sonraBaşakların toplandığı bir tarla gibi olacak."));
trsuperobject.biblemap.put("b23_17_6", new trsuperobject(23,17,6,"Çok az kişi kurtulacak.Artakalanların sayısı, dövüldükten sonra tepesinde iki üç,Dal uçlarında dört beş zeytin tanesi kalanZeytin ağacı gibi olacak.İsrailin Tanrısı RAB böyle diyor."));
trsuperobject.biblemap.put("b23_17_7", new trsuperobject(23,17,7,"O gün insanlar kendilerini yaratana bakacaklar, gözleri İsrailin Kutsalını görecek."));
trsuperobject.biblemap.put("b23_17_8", new trsuperobject(23,17,8,"Elleriyle yaptıkları sunaklara, parmaklarıyla biçim verdikleri Aşera putlarına, buhur sunaklarına bakmayacaklar."));
trsuperobject.biblemap.put("b23_17_9", new trsuperobject(23,17,9,"O gün İsrailin güçlü kentleriİsraillilerden kaçan Amorlularla HivlilerinTerk ettiği kentler gibi ıssız olacak. ‹‹Orman ve dağ başı››."));
trsuperobject.biblemap.put("b23_17_10", new trsuperobject(23,17,10,"Çünkü, ey İsrail, seni kurtaran Tanrıyı unuttun,Sığındığın Kayayı anmaz oldun.Bunun yerine, güzel fidanlar, ithal asmalar dikiyorsun. başka ulusların putları ya da başka uluslarla yapılan antlaşmalar kastediliyor."));
trsuperobject.biblemap.put("b23_17_11", new trsuperobject(23,17,11,"Onlar diktiğin gün filizlenipErtesi sabah tomurcuklanabilir.Ama hastalık ve dinmez acı gününde meyve vermeyecekler."));
trsuperobject.biblemap.put("b23_17_12", new trsuperobject(23,17,12,"Eyvah, çok sayıda ulus kükrüyor,Azgın deniz gibi gürlüyorlar.Halklar güçlü sular gibi çağlıyor."));
trsuperobject.biblemap.put("b23_17_13", new trsuperobject(23,17,13,"Halklar kabaran sular gibi çağlayabilir,Ama Tanrı onları azarlayınca uzaklara kaçacaklar.Rüzgarın önünde dağdaki saman ufağı gibi,Kasırganın önünde diken yumağı gibi savrulacaklar."));
trsuperobject.biblemap.put("b23_17_14", new trsuperobject(23,17,14,"Akşam dehşet saçıyorlardı,Sabah olmadan yok olup gittiler.Bizi yağmalayanların, bizi soyanların sonu budur."));
trsuperobject.biblemap.put("b23_18_1", new trsuperobject(23,18,1,"Kûş ırmaklarının ötesinde,Kanat vızıltılarının duyulduğu ülkenin vay haline!"));
trsuperobject.biblemap.put("b23_18_2", new trsuperobject(23,18,2,"O ülke ki, elçileriniSazdan kayıklarla Nil sularından gönderir.Ey ayağına tez ulaklar,Irmakların böldüğü ülkeye,Her yana korku saçan halka,Güçlü ve ezici ulusa,O uzun boylu, pürüzsüz tenli ulusa gidin!"));
trsuperobject.biblemap.put("b23_18_3", new trsuperobject(23,18,3,"Ey sizler, dünyada yaşayan herkes,Yeryüzünün ahalisi!Sancak dağların tepesine dikilince dikkat edin.Boru çalınınca dinleyin."));
trsuperobject.biblemap.put("b23_18_4", new trsuperobject(23,18,4,"Çünkü RAB bana şöyle dedi:‹‹Gün ışığında duru sıcaklık gibi,Hasat döneminin sıcaklığındakiÇiy bulutu gibi durgun olacakVe bulunduğum yerden seyredeceğim.››"));
trsuperobject.biblemap.put("b23_18_5", new trsuperobject(23,18,5,"Bağbozumundan önce çiçekler düşüpÜzümler olgunlaşmaya yüz tutunca,Asmanın dalları bıçakla kesilecek,Çubukları koparılıp atılacak."));
trsuperobject.biblemap.put("b23_18_6", new trsuperobject(23,18,6,"Hepsi dağın yırtıcı kuşlarına,Yerin yabanıl hayvanlarına terk edilecek.Yazın yırtıcı kuşlara,Kışın yabanıl hayvanlara yem olacaklar."));
trsuperobject.biblemap.put("b23_18_7", new trsuperobject(23,18,7,"O zaman ırmakların böldüğü ülke,Her yana korku saçan güçlü ve ezici halk,O uzun boylu, pürüzsüz tenli ulus,Her Şeye Egemen RAB'be armağanlar getirecek.Her Şeye Egemen RAB'bin adını koyduğuSiyon Dağı'na getirecekler armağanlarını."));
trsuperobject.biblemap.put("b23_19_1", new trsuperobject(23,19,1,"Mısırla ilgili bildiri:  İşte RAB hızla yol alan buluta binmiş Mısıra geliyor!Mısır putları Onun önünde titriyor,Mısırlıların yüreği hopluyor."));
trsuperobject.biblemap.put("b23_19_3", new trsuperobject(23,19,3,"Mısırlıların cesareti tükenecek,Tasarılarını boşa çıkaracağım.Yardım için putlara, ölülerin ruhlarına,Medyumlarla ruh çağıranlara danışacaklar."));
trsuperobject.biblemap.put("b23_19_4", new trsuperobject(23,19,4,"Mısırlıları acımasız bir efendiye teslim edeceğim,Katı yürekli bir kral onlara egemen olacak.››Rab, Her Şeye Egemen RAB böyle diyor."));
trsuperobject.biblemap.put("b23_19_5", new trsuperobject(23,19,5,"Nilin suları çekilecek,Kuruyup çatlayacak yatağı."));
trsuperobject.biblemap.put("b23_19_6", new trsuperobject(23,19,6,"Su kanalları kokacak,Kuruyacak ırmağın kolları,Kamışlarla sazlar solacak."));
trsuperobject.biblemap.put("b23_19_7", new trsuperobject(23,19,7,"Nil kıyısında, ırmağın ağzındaki sazlar,Nil boyunca ekili tarlalar kuruyacak,Savrulup yok olacak."));
trsuperobject.biblemap.put("b23_19_8", new trsuperobject(23,19,8,"Balıkçılar yas tutacak,Nile olta atanların hepsi ağlayacak,Suyun yüzüne ağ atanlar perişan olacak."));
trsuperobject.biblemap.put("b23_19_9", new trsuperobject(23,19,9,"Taranmış keten işleyenler,Beyaz bez dokuyanlar umutsuzluğa kapılacak."));
trsuperobject.biblemap.put("b23_19_10", new trsuperobject(23,19,10,"Dokumacılar bunalacak,Ücretliler sıkıntıya düşecek."));
trsuperobject.biblemap.put("b23_19_11", new trsuperobject(23,19,11,"Soanfş Kentinin önderleri ne kadar akılsız!Firavunun bilge danışmanlarıSaçma sapan öğütler veriyorlar.Nasıl olur da firavuna,‹‹Biz bilgelerin oğulları,Eski zaman krallarının torunlarıyız›› diyorlar?"));
trsuperobject.biblemap.put("b23_19_12", new trsuperobject(23,19,12,"Ey firavun, hani nerede senin bilgelerin?Her Şeye Egemen RAB Mısıra karşı neler tasarladı,Bildirsinler bakalım sana eğer biliyorlarsa."));
trsuperobject.biblemap.put("b23_19_13", new trsuperobject(23,19,13,"Soan Kentinin önderleri aptal olup çıktılar,Nof önderleri aldandılar,Mısır oymaklarının ileri gelenleri Mısırı saptırdılar."));
trsuperobject.biblemap.put("b23_19_14", new trsuperobject(23,19,14,"RAB onların aklını karıştırdı;Kendi kusmuğu içinde yalpalayan sarhoş nasılsa,Mısırı da her alanda saptırdılar."));
trsuperobject.biblemap.put("b23_19_15", new trsuperobject(23,19,15,"Mısırda kimsenin yapabileceği bir şey kalmadı;Ne başın ne kuyruğun, ne hurma dalının ne de sazın."));
trsuperobject.biblemap.put("b23_19_16", new trsuperobject(23,19,16,"O gün Mısırlılar kadın gibi olacaklar; Her Şeye Egemen RABbin kendilerine karşı kalkan elinin önünde titreyip dehşete kapılacaklar."));
trsuperobject.biblemap.put("b23_19_17", new trsuperobject(23,19,17,"Yahuda Mısırı dehşete düşürecek. Yahuda dendi mi, Her Şeye Egemen RABbin Mısıra karşı tasarladıklarını anımsayan herkes dehşete kapılacak."));
trsuperobject.biblemap.put("b23_19_18", new trsuperobject(23,19,18,"O gün Mısırda Kenan dilini konuşan beş kent olacak. Bu kentler Her Şeye Egemen RABbe bağlılık andı içecekler; içlerinden biri ‹Yıkım Kenti› diye adlandırılacak. Heliopolis anlamına gelebilir."));
trsuperobject.biblemap.put("b23_19_19", new trsuperobject(23,19,19,"O gün Mısırın ortasında RAB için bir sunak, sınırında da bir sütun dikilecek."));
trsuperobject.biblemap.put("b23_19_20", new trsuperobject(23,19,20,"Her Şeye Egemen RAB için Mısırda bir belirti ve tanık olacak bu. Halk kendine baskı yapanlardan ötürü RABbe yakarınca, RAB onları savunacak bir kurtarıcı gönderip özgür kılacak."));
trsuperobject.biblemap.put("b23_19_21", new trsuperobject(23,19,21,"RAB kendini Mısırlılara tanıtacak, onlar da o gün RABbi tanıyacak, kurbanlarla, sunularla Ona tapınacaklar. RABbe adak adayacak ve adaklarını yerine getirecekler."));
trsuperobject.biblemap.put("b23_19_22", new trsuperobject(23,19,22,"RAB Mısırlıları hastalıkla alabildiğine cezalandıracak, sonra iyileştirecek. RABbe yönelip yakaracaklar. RAB de onları iyileştirecek."));
trsuperobject.biblemap.put("b23_19_23", new trsuperobject(23,19,23,"O gün Mısırla Asur arasında bir yol olacak. Asurlu Mısıra, Mısırlı Asura gidip gelecek. Mısırlılarla Asurlular birlikte tapınacaklar."));
trsuperobject.biblemap.put("b23_19_24", new trsuperobject(23,19,24,"O gün Mısır ve Asurun yanısıra İsrail üçüncü ülke olacak. Dünya bu üçü sayesinde kutsanacak."));
trsuperobject.biblemap.put("b23_19_25", new trsuperobject(23,19,25,"Her Şeye Egemen RAB, ‹‹Halkım Mısır, ellerimin işi Asur ve mirasım İsrail kutsansın›› diyerek dünyayı kutsayacak."));
trsuperobject.biblemap.put("b23_20_1-2", new trsuperobject(23,20,1-2,"Asur ordusunun başkomutanı, Asur Kralı Sargonun buyruğuyla gelerek Aşdota saldırıp kenti ele geçirdiği yıl RAB Amots oğlu Yeşaya aracılığıyla şöyle dedi: ‹‹Git, belindeki çulu çöz, ayağındaki çarığı çıkar.›› Yeşaya denileni yaptı, çıplak ve yalınayak dolaşmaya başladı."));
trsuperobject.biblemap.put("b23_20_4", new trsuperobject(23,20,4,"Asur Kralı da Mısıra utanç olsun diye Mısırlı tutsaklarla Kûşlu sürgünleri genç yaşlı demeden, çıplak ve yalınayak, mahrem yerleri açık yürütecek."));
trsuperobject.biblemap.put("b23_20_5", new trsuperobject(23,20,5,"Kûşa bel bağlayan, Mısırla övünen halk hüsrana uğrayacak, utanç içinde kalacak."));
trsuperobject.biblemap.put("b23_20_6", new trsuperobject(23,20,6,"Bu kıyı bölgesinde yaşayanlar o gün, ‹‹Asur Kralı'nın elinden kurtulmak için yardımına sığındığımız, bel bağladığımız ulusların başına gelene bakın!›› diyecekler, ‹‹Biz nasıl kurtulacağız?››"));
trsuperobject.biblemap.put("b23_21_1", new trsuperobject(23,21,1,"Deniz kıyısındaki çölle ilgili bildiri:  Negevden fırtınalar nasıl üst üste gelirse,Çölden, korkunç ülkeden bir istilacı öyle geliyor."));
trsuperobject.biblemap.put("b23_21_2", new trsuperobject(23,21,2,"Korkunç bir görüm gördüm:Hain hainlik etmede,Harap eden harap etmede.Ey Elam, saldır!Ey Meday, onu kuşat!Onun neden olduğu iniltileri sona erdireceğim."));
trsuperobject.biblemap.put("b23_21_3", new trsuperobject(23,21,3,"Gördüklerimden ötürü belime ağrı saplandı,Doğuran kadının ağrıları gibi ağrılar tuttu beni.Duyduklarımdan sarsıldım,Gördüklerimden dehşete düştüm."));
trsuperobject.biblemap.put("b23_21_4", new trsuperobject(23,21,4,"Şaşkınım, titremeler sardı beni.Özlediğim alaca karanlık bana korku veriyor artık."));
trsuperobject.biblemap.put("b23_21_5", new trsuperobject(23,21,5,"Gördüğüm görümde sofrayı hazırlıyor,Halıları seriyor, yiyip içiyorlar.Kalkın, ey önderler, kalkanları yağlayın!"));
trsuperobject.biblemap.put("b23_21_6", new trsuperobject(23,21,6,"Rab bana dedi ki,‹‹Git, bir gözcü dik, gördüğünü bildirsin."));
trsuperobject.biblemap.put("b23_21_7", new trsuperobject(23,21,7,"Savaş arabalarının,Atlara, eşeklere, develere binmiş insanlarınÇifter çifter geldiğini görünce dikkat kesilsin.››"));
trsuperobject.biblemap.put("b23_21_8", new trsuperobject(23,21,8,"Gözcüfü, ‹‹Ey efendim,Her gün aralıksız gözcü kulesinde duruyor,Her gece yerimde nöbet tutuyorum›› diye bağırdı,"));
trsuperobject.biblemap.put("b23_21_9", new trsuperobject(23,21,9,"‹‹Bak, savaş arabalarıyla atlılarÇifter çifter geliyor!››Sonra, ‹‹Yıkıldı, Babil yıkıldı!›› diye haber verdi,‹‹Taptıkları bütün putlar yere çalınıp parçalandı!››"));
trsuperobject.biblemap.put("b23_21_10", new trsuperobject(23,21,10,"Ey halkım, harman yerindeBuğday gibi dövülmüş olan halkım!Her Şeye Egemen RABden,İsrailin Tanrısından duyduklarımıSize bildirdim."));
trsuperobject.biblemap.put("b23_21_11", new trsuperobject(23,21,11,"Duma ile ilgili bildiri: Arabistanda bir yer olduğu sanılıyor. ‹‹Sessizlik›› anlamına gelir. ‹‹Edom›› sözcüğünü çağrıştırıyor.  Biri Seirden bana sesleniyor:‹‹Ey gözcü, geceden geriye ne kaldı?Geceden geriye ne kaldı?››"));
trsuperobject.biblemap.put("b23_21_12", new trsuperobject(23,21,12,"Yanıtım şöyle: ‹‹Sabah olmak üzere,Ama yine gece olacak.Soracaksanız sorun, yine gelin.››"));
trsuperobject.biblemap.put("b23_21_13", new trsuperobject(23,21,13,"Arabistanla ilgili bildiri:  Arabistan çalılıklarında geceleyeceksiniz,Ey Dedan kervanları!"));
trsuperobject.biblemap.put("b23_21_14", new trsuperobject(23,21,14,"Ey Temada oturanlar,Su getirin, susamışları karşılayın,Kaçıp kurtulana ekmek verin."));
trsuperobject.biblemap.put("b23_21_15", new trsuperobject(23,21,15,"Çünkü onlar kılıçtan, yalın kılıçtan,Gerilmiş yaydan, çetin çarpışmalardan kaçtılar."));
trsuperobject.biblemap.put("b23_21_16", new trsuperobject(23,21,16,"Rab bana şöyle dedi: ‹‹Kedarın bütün övüncü tam bir yıl sonra sona erecek."));
trsuperobject.biblemap.put("b23_21_17", new trsuperobject(23,21,17,"Okçulardan, Kedar savaşçılarından pek az sağ kalan olacak.›› Bunu söyleyen, İsrail'in Tanrısı RAB'dir."));
trsuperobject.biblemap.put("b23_22_1-2", new trsuperobject(23,22,1-2,"Görüm Vadisiyle ilgili bildiri:  Gürültü patırtı içinde eğlenen kent halkı,Ne oldu size, neden hepiniz damlara çıktınız?Ölenleriniz ne kılıçtan geçirildi,Ne de savaşta öldü."));
trsuperobject.biblemap.put("b23_22_3", new trsuperobject(23,22,3,"Önderleriniz hep birlikte kaçtılar,Yaylarını kullanmadan tutsak alındılar.Uzağa kaçtığınız halde ele geçenlerin hepsi tutsak edildi."));
trsuperobject.biblemap.put("b23_22_4", new trsuperobject(23,22,4,"Bunun için dedim ki,‹‹Beni yalnız bırakın, acı acı ağlayayım.Halkımın uğradığı yıkımdan ötürüBeni avutmaya kalkmayın.››"));
trsuperobject.biblemap.put("b23_22_5", new trsuperobject(23,22,5,"Çünkü Rabbin, Her Şeye Egemen RABbinGörüm Vadisinde kargaşa, bozgunVe dehşet saçacağı gün,Duvarların yıkılacağı,Dağlara feryat edileceği gün geliyor."));
trsuperobject.biblemap.put("b23_22_6", new trsuperobject(23,22,6,"Elamlılar ok kılıflarını sırtlanıp savaş arabalarıyla,Atlılarıyla geldiler.Kîr halkı kalkanlarını açtı."));
trsuperobject.biblemap.put("b23_22_7", new trsuperobject(23,22,7,"Verimli vadileriniz savaş arabalarıyla doldu,Atlılar kent kapılarının karşısına dizildi."));
trsuperobject.biblemap.put("b23_22_9", new trsuperobject(23,22,9,"Davut Kentinin duvarlarındaÇok sayıda gedik olduğunu gördünüz,Aşağı Havuzda su depoladınız,"));
trsuperobject.biblemap.put("b23_22_10", new trsuperobject(23,22,10,"Yeruşalimdeki evleri saydınız,Surları onarmak için evleri yıktınız."));
trsuperobject.biblemap.put("b23_22_11", new trsuperobject(23,22,11,"Eski Havuzun suları içinİki surun arasında bir depo yaptınız.Ama bunu çok önceden tasarlayıpGerçekleştirmiş olan Tanrıya güvenmediniz,Onu umursamadınız."));
trsuperobject.biblemap.put("b23_22_12", new trsuperobject(23,22,12,"Rab, Her Şeye Egemen RABO gün sizi ağlayıp yas tutmaya,Saçlarınızı kesip çul kuşanmaya çağırdı."));
trsuperobject.biblemap.put("b23_22_13", new trsuperobject(23,22,13,"Oysa siz keyif çatıp eğlendiniz,‹‹Yiyelim içelim, nasıl olsa yarın öleceğiz›› diyerekSığır, koyun kestiniz,Et yiyip şarap içtiniz."));
trsuperobject.biblemap.put("b23_22_14", new trsuperobject(23,22,14,"Her Şeye Egemen RAB bana,‹‹Siz ölene dek bu suçunuz bağışlanmayacak›› diye seslendi.Rab, Her Şeye Egemen RAB böyle diyor."));
trsuperobject.biblemap.put("b23_22_15", new trsuperobject(23,22,15,"Rab, Her Şeye Egemen RAB diyor ki,‹‹Haydi, o kâhyaya,Sarayın sorumlusu Şevnaya git ve de ki,"));
trsuperobject.biblemap.put("b23_22_16", new trsuperobject(23,22,16,"‹Burada ne işin var?Kimin var ki, kendine burada mezar kazdın,Yüksekte kendine mezar, kayada konut oydun?"));
trsuperobject.biblemap.put("b23_22_17", new trsuperobject(23,22,17,"Ey güçlü kişi,RAB seni tuttuğu gibi şiddetle savuracak."));
trsuperobject.biblemap.put("b23_22_18", new trsuperobject(23,22,18,"Top gibi evirip çeviripGeniş bir ülkeye fırlatacak.Orada öleceksin,Gurur duyduğun arabaların orada kalacak.Efendinin evi için utanç nedenisin!"));
trsuperobject.biblemap.put("b23_22_19", new trsuperobject(23,22,19,"Seni görevden alacak,Makamından alaşağı edeceğim."));
trsuperobject.biblemap.put("b23_22_20", new trsuperobject(23,22,20,"‹‹O gün Hilkiya oğlu kulum Elyakimi çağırıp"));
trsuperobject.biblemap.put("b23_22_21", new trsuperobject(23,22,21,"Senin cüppeni ona giydireceğim.Senin kuşağınla onu güçlendiripYetkini ona vereceğim.Yeruşalimde yaşayanlaraVe Yahuda halkına o babalık yapacak."));
trsuperobject.biblemap.put("b23_22_22", new trsuperobject(23,22,22,"Davutun evinin anahtarını ona teslim edeceğim.Açtığını kimse kapayamayacak,Kapadığını kimse açamayacak."));
trsuperobject.biblemap.put("b23_22_23", new trsuperobject(23,22,23,"Onu sağlam yere çakılmış çadır kazığı yapacağım,Ailesi için onur kürsüsü olacak."));
trsuperobject.biblemap.put("b23_22_24", new trsuperobject(23,22,24,"Ailenin ağırlığı -soyundan türeyen herkes-Taslardan kâselere kadar her küçük kap ona asılacak.››"));
trsuperobject.biblemap.put("b23_22_25", new trsuperobject(23,22,25,"Her Şeye Egemen RAB diyor ki, ‹‹O gün sağlam yere çakılmış kazık yerinden çıkacak, kırılıp düşecek, ona asılan yük de yok olacak.›› Çünkü RAB böyle diyor."));
trsuperobject.biblemap.put("b23_23_1", new trsuperobject(23,23,1,"Sur Kentiyle ilgili bildiri:  Ey ticaret gemileri, feryat edin!Çünkü Sur Kenti evleriyle,Limanlarıyla birlikte yok oldu.Kittimden size haber geldi."));
trsuperobject.biblemap.put("b23_23_2", new trsuperobject(23,23,2,"Ey kıyı halkı ve denizcilerin zenginleştirdiğiSayda tüccarları, susun!"));
trsuperobject.biblemap.put("b23_23_3", new trsuperobject(23,23,3,"Şihorun tahılı, Nilin ürünüDenizleri aşar, Sura gelir sağlardı.Ulusların kârı ona akardı."));
trsuperobject.biblemap.put("b23_23_4", new trsuperobject(23,23,4,"Utan, ey Sayda, ey deniz kıyısındaki kale!Çünkü deniz sana sesleniyor:‹‹Ne doğum ağrısı çektim, ne de doğurdum.Ne delikanlılar büyüttüm, ne de kızlar.››"));
trsuperobject.biblemap.put("b23_23_5", new trsuperobject(23,23,5,"Surun haberi Mısıra ulaşınca,Yüreği burkulacak insanların."));
trsuperobject.biblemap.put("b23_23_6", new trsuperobject(23,23,6,"Tarşişe geçin, ey kıyıda oturanlar,Feryat edin."));
trsuperobject.biblemap.put("b23_23_7", new trsuperobject(23,23,7,"Uzak ülkeleri yurt edinmiş,Eğlenceye düşkün halkınız,Eski, tarihsel kentiniz bu mu?"));
trsuperobject.biblemap.put("b23_23_8", new trsuperobject(23,23,8,"Taçlar giydiren Sura karşı bu işi kim tasarladı?O kent ki, tüccarları prenslerdi,İş adamları dünyanın saygın kişileriydi."));
trsuperobject.biblemap.put("b23_23_9", new trsuperobject(23,23,9,"Görkeminin sonucu olan gururunu kırmak,Dünyaca ünlü bütün saygın kişilerini alçaltmak içinHer Şeye Egemen RAB tasarladı bunu."));
trsuperobject.biblemap.put("b23_23_10", new trsuperobject(23,23,10,"Kendi topraklarını Nil gibi basıp geç,Ey Tarşiş kızı, artık engel yok."));
trsuperobject.biblemap.put("b23_23_11", new trsuperobject(23,23,11,"RAB denizin üzerine elini uzatıp ülkeleri titretti,Kenan kalelerinin yıkılmasını buyurdu."));
trsuperobject.biblemap.put("b23_23_12", new trsuperobject(23,23,12,"‹‹Eğlencen sona erdi, ey Sayda, erden kız!›› dedi,‹‹Kirletildin.Kalk, Kittime geç,Orada bile rahat yüzü görmeyeceksin.››"));
trsuperobject.biblemap.put("b23_23_13", new trsuperobject(23,23,13,"Kildan ülkesine bak!O halk yok artık.Asurlular onların ülkesini yabanıl hayvanlara verdi,Kuşatma kuleleri diktiler, saraylarını soydular,Ülkeyi viraneye çevirdiler."));
trsuperobject.biblemap.put("b23_23_14", new trsuperobject(23,23,14,"Feryat edin, ey ticaret gemileri!Çünkü sığınağınız harap oldu."));
trsuperobject.biblemap.put("b23_23_15", new trsuperobject(23,23,15,"Bundan sonra Sur Kenti yetmiş yıl, bir kralın ömrü süresince unutulacak. Yetmiş yıl bitince, fahişe için bestelenen türküdeki gibi Sura şöyle denecek:"));
trsuperobject.biblemap.put("b23_23_16", new trsuperobject(23,23,16,"‹‹Bir lir al, dolaş kenti,Ey sen, unutulmuş fahişe!Güzel çal seni anımsamaları için,Türkü üstüne türkü söyle.››"));
trsuperobject.biblemap.put("b23_23_17", new trsuperobject(23,23,17,"Yetmiş yıl geçince RAB Surla ilgilenecek, ama Sur para için yine fahişeliğe dönecek. Dünyanın bütün krallıklarıyla fahişelik edecek."));
trsuperobject.biblemap.put("b23_23_18", new trsuperobject(23,23,18,"Kentin ticaretten ve fuhuştan kazandıkları RAB'be adanacak. Bunlar biriktirilmeyecek, hazineye konmayacak. Ticaretten kazandıklarını doyuncaya dek yesinler, güzel güzel giyinsinler diye RAB'bin önünde yaşayanlara verilecek."));
trsuperobject.biblemap.put("b23_24_1", new trsuperobject(23,24,1,"İşte RAB yeryüzünü harap edip viraneye çevirecek,Yeryüzünü altüst edecek,Üzerinde yaşayanları darmadağın edecek."));
trsuperobject.biblemap.put("b23_24_2", new trsuperobject(23,24,2,"Ayrım yapılmayacak;Ne halkla kâhin arasında,Ne köleyle efendi arasında,Ne hizmetçiyle hanım arasında,Ne alıcıyla satıcı arasında,Ne ödünç alanla ödünç veren arasında,Ne faizciyle borç alan arasında."));
trsuperobject.biblemap.put("b23_24_3", new trsuperobject(23,24,3,"Dünya tümüyle yağmalanıp viraneye çevrilecek.RAB böyle söyledi."));
trsuperobject.biblemap.put("b23_24_4", new trsuperobject(23,24,4,"Dünya kuruyup büzülüyor,Yeryüzü solup büzülüyor,Dünyadaki soylular güçlerini yitiriyor."));
trsuperobject.biblemap.put("b23_24_5", new trsuperobject(23,24,5,"Dünyada yaşayanlar onu kirletti.Çünkü Tanrının yasalarını çiğnediler,Kurallarını ayaklar altına aldılar,Ebedi antlaşmayı bozdular."));
trsuperobject.biblemap.put("b23_24_6", new trsuperobject(23,24,6,"Bu yüzden lanet dünyayı yiyip bitirdi,Orada yaşayanlar suçlarının cezasını çekiyorlar.Yaşayanlar bu nedenle yanıyor, pek azı kurtulacak."));
trsuperobject.biblemap.put("b23_24_7", new trsuperobject(23,24,7,"Yeni şarabın sonu geldi,Asmalar soldu,Bir zamanlar sevinçli olanların hepsi inliyor."));
trsuperobject.biblemap.put("b23_24_8", new trsuperobject(23,24,8,"Tefin coşkun sesi kesildi,Eğlenenlerin gürültüsü durdu,Lirin coşkun sesi kesildi."));
trsuperobject.biblemap.put("b23_24_9", new trsuperobject(23,24,9,"Ezgi eşliğinde şarap içilmiyor artık,İçkinin tadı içene acı geliyor."));
trsuperobject.biblemap.put("b23_24_10", new trsuperobject(23,24,10,"Yıkılan kent perişan durumda,Kimse girmesin diye her evin girişi kapandı."));
trsuperobject.biblemap.put("b23_24_11", new trsuperobject(23,24,11,"İnsanlar şarap özlemiyle sokaklarda bağrışıyor,Sevinçten eser kalmadı,Dünyanın coşkusu yok oldu."));
trsuperobject.biblemap.put("b23_24_12", new trsuperobject(23,24,12,"Kent viraneye döndü,Kapıları paramparça oldu."));
trsuperobject.biblemap.put("b23_24_13", new trsuperobject(23,24,13,"Çünkü zeytinler dökülsün diye dövülen ağaç nasılsa,Bağbozumundan artakalan üzümler nasılsa,Dünyadaki bütün uluslar da öyle olacak."));
trsuperobject.biblemap.put("b23_24_14", new trsuperobject(23,24,14,"Sağ kalanlar seslerini yükseltipSevinç çığlıkları atacak,Batıda yaşayanlar RABbin büyüklüğü karşısındaHayranlıkla bağıracak."));
trsuperobject.biblemap.put("b23_24_15", new trsuperobject(23,24,15,"Onun için, doğuda yaşayanlar RABbi yüceltin,Deniz kıyısındakiler,İsrailin Tanrısı RABbin adını yüceltin."));
trsuperobject.biblemap.put("b23_24_16", new trsuperobject(23,24,16,"Dünyanın en uzak köşelerinden ezgiler işitiyoruz:‹‹Doğru Olana övgüler olsun!››  Ama ben, ‹‹Bittim, bittim! Vay halime!›› dedim,‹‹Hainler hainliklerini sürdürüyor.Evet, hainler sürekli hainlik ediyorlar.››"));
trsuperobject.biblemap.put("b23_24_17", new trsuperobject(23,24,17,"Ey dünyada yaşayanlar,Önünüzde dehşet, çukur ve tuzak var."));
trsuperobject.biblemap.put("b23_24_18", new trsuperobject(23,24,18,"Dehşet haberinden kaçan çukura düşecek,Çukurdan çıkan tuzağa yakalanacak.Göklerin kapakları açılacak,Dünyanın temelleri sarsılacak."));
trsuperobject.biblemap.put("b23_24_19", new trsuperobject(23,24,19,"Yeryüzü büsbütün çatlayıp yarılacak,Sarsıldıkça sarsılacak."));
trsuperobject.biblemap.put("b23_24_20", new trsuperobject(23,24,20,"Dünya sarhoş gibi yalpalayacak,Bir kulübe gibi sallanacak,İsyanlarının ağırlığı altında çökecekVe bir daha kalkamayacak."));
trsuperobject.biblemap.put("b23_24_21", new trsuperobject(23,24,21,"O gün RAB yukarıda, gökteki güçleriVe aşağıda, yeryüzündeki kralları cezalandıracak."));
trsuperobject.biblemap.put("b23_24_22", new trsuperobject(23,24,22,"Zindana tıkılan tutsaklar gibiCezaevine kapatılacakVe uzun süre sonra cezalandırılacaklar."));
trsuperobject.biblemap.put("b23_24_23", new trsuperobject(23,24,23,"Ayın yüzü kızaracak, güneş utanacak.Çünkü Her Şeye Egemen RAB Siyon Dağı'nda,Yeruşalim'de krallık edecek.Halkın ileri gelenleriO'nun yüceliğini görecek."));
trsuperobject.biblemap.put("b23_25_1", new trsuperobject(23,25,1,"Ya RAB, sensin benim Tanrım,Seni yüceltir, adını överim.Çünkü sen eskiden beri tasarladığın harikalarıTam bir sadakatle gerçekleştirdin."));
trsuperobject.biblemap.put("b23_25_2", new trsuperobject(23,25,2,"Kenti bir taş yığınına,Surlu kenti viraneye çevirdin.Yabancıların kalesiydi, kent olmaktan çıktı.Bir daha da onarılmayacak."));
trsuperobject.biblemap.put("b23_25_3", new trsuperobject(23,25,3,"Bundan ötürü güçlü uluslar seni onurlandıracak,Acımasız ulusların kentleri senden korkacak."));
trsuperobject.biblemap.put("b23_25_4", new trsuperobject(23,25,4,"Çünkü onların öfkesiDuvara çarpan sağanak gibi yükselince,Sen yoksulun, sıkıntı içindeki düşkünün kalesi,Sağanağa karşı sığınak,Sıcağa karşı gölgelik oldun."));
trsuperobject.biblemap.put("b23_25_5", new trsuperobject(23,25,5,"Yabancıların gürültüsünü çöl sıcağı gibi bastırırsın.Bulutun gölgesi sıcağı nasıl kırarsa,Bu acımasız adamların türküsü de öyle diniyor."));
trsuperobject.biblemap.put("b23_25_6", new trsuperobject(23,25,6,"Her Şeye Egemen RAB bu dağdaBütün uluslara yağlı yemeklerinVe dinlendirilmiş seçkin şarapların sunulduğuZengin bir şölen verecek."));
trsuperobject.biblemap.put("b23_25_7", new trsuperobject(23,25,7,"Bütün halkların üzerindeki örtüyü,Bütün ulusların üzerine örülmüş olan örtüyüBu dağda kaldıracak."));
trsuperobject.biblemap.put("b23_25_8", new trsuperobject(23,25,8,"Ölümü sonsuza dek yutacak.Egemen RAB bütün yüzlerden gözyaşlarını silecek.Halkının utancını bütün yeryüzünden kaldıracak.Çünkü RAB böyle diyor."));
trsuperobject.biblemap.put("b23_25_9", new trsuperobject(23,25,9,"O gün diyecekler ki,‹‹İşte Tanrımız budur;Ona umut bağlamıştık, bizi kurtardı,RAB Odur, Ona umut bağlamıştık,Onun kurtarışıyla sevinip coşalım.››"));
trsuperobject.biblemap.put("b23_25_10", new trsuperobject(23,25,10,"RABbin eli bu dağın üzerinde kalacak,Ama Moav gübre çukurundaki saman gibiKendi yerinde çiğnenecek."));
trsuperobject.biblemap.put("b23_25_11", new trsuperobject(23,25,11,"Oracıkta yüzmek isteyen biri gibi ellerini uzatacak,Ama kurnazlığına karşın RAB onun gururunu kıracak."));
trsuperobject.biblemap.put("b23_25_12", new trsuperobject(23,25,12,"RAB surlarındaki yüksek burçlarıDevirip yıkacak, yerle bir edecek."));
trsuperobject.biblemap.put("b23_26_1", new trsuperobject(23,26,1,"O gün Yahudada şu ilahi söylenecek:  Güçlü bir kentimiz var.Çünkü Tanrının kurtarışıKente sur ve duvar gibidir."));
trsuperobject.biblemap.put("b23_26_2", new trsuperobject(23,26,2,"Açın kentin kapılarını,Sadık kalan doğru ulus içeri girsin."));
trsuperobject.biblemap.put("b23_26_3", new trsuperobject(23,26,3,"Sana güvendiği içinDüşüncelerinde sarsılmaz olanıTam bir esenlik içinde korursun."));
trsuperobject.biblemap.put("b23_26_5", new trsuperobject(23,26,5,"Yüksekte oturanı alçaltır,Yüce kenti yıkar,Yerle bir eder."));
trsuperobject.biblemap.put("b23_26_6", new trsuperobject(23,26,6,"O kent ayak altında,Mazlumların ayakları,Yoksulların adımları altında çiğnenecek."));
trsuperobject.biblemap.put("b23_26_7", new trsuperobject(23,26,7,"Doğru adamın yolu düzdür,Ey Dürüst Olan, doğru adamın yolunu sen düzlersin."));
trsuperobject.biblemap.put("b23_26_8", new trsuperobject(23,26,8,"Evet, ya RAB, ilkelerinin çizdiği yolda sana umut bağladık,Adın ve ünündür yüreğimizin dileği,"));
trsuperobject.biblemap.put("b23_26_9", new trsuperobject(23,26,9,"Geceleri canım sana susar,Evet, içimde ruhum seni özler;Çünkü senin ilkelerin yeryüzünde oldukça,Orada oturanlar doğruluğu öğrenir."));
trsuperobject.biblemap.put("b23_26_10", new trsuperobject(23,26,10,"Kötüler lütfedilse bile doğruluğu öğrenmez.Dürüstlüğün egemen olduğu diyarda haksızlık eder,RABbin büyüklüğünü görmezler."));
trsuperobject.biblemap.put("b23_26_11", new trsuperobject(23,26,11,"Ya RAB, elin yükseldi, ama görmüyorlar,Halkın için gösterdiğin gayreti görüp utansınlar.Evet, düşmanların için yaktığın ateş onları yiyip bitirecek."));
trsuperobject.biblemap.put("b23_26_12", new trsuperobject(23,26,12,"Ya RAB, bizi esenliğe çıkaracak sensin,Çünkü ne yaptıysak hepsi senin başarındır."));
trsuperobject.biblemap.put("b23_26_13", new trsuperobject(23,26,13,"Ey Tanrımız RAB, senden başka efendiler bizi yönetti,Ama yalnız sana, senin adına yakaracağız."));
trsuperobject.biblemap.put("b23_26_14", new trsuperobject(23,26,14,"O efendiler öldü, artık yaşamıyorlar,Dirilmeyecek onlar.Çünkü onları cezalandırıp yok ettin,Anılmalarına son verdin."));
trsuperobject.biblemap.put("b23_26_15", new trsuperobject(23,26,15,"Ulusu çoğalttın, ya RAB,Evet, ulusu çoğalttın ve yüceltildin.Her yönde ülkenin sınırlarını genişlettin."));
trsuperobject.biblemap.put("b23_26_16", new trsuperobject(23,26,16,"Ya RAB, sıkıntıdayken seni aradılar.Onları terbiye ettiğinde sessizce yakararak içlerini döktüler."));
trsuperobject.biblemap.put("b23_26_17", new trsuperobject(23,26,17,"Doğum vakti yaklaşan gebe kadınÇektiği sancıdan ötürü nasıl kıvranır, feryat ederse,Senin önünde biz de öyle olduk, ya RAB."));
trsuperobject.biblemap.put("b23_26_18", new trsuperobject(23,26,18,"Gebe kaldık, kıvrandık,Rüzgardan başka bir şey doğurmadık sanki.Ne dünyaya kurtuluş sağlayabildik,Ne de dünyada yaşayanları yaşama kavuşturabildik."));
trsuperobject.biblemap.put("b23_26_19", new trsuperobject(23,26,19,"Ama senin ölülerin yaşayacak,Bedenleri dirilecek.Ey sizler, toprak altında yatanlar,Uyanın, ezgiler söyleyin.Çünkü senin çiyin sabah çiyine benzer,Toprak ölülerini yaşama kavuşturacak."));
trsuperobject.biblemap.put("b23_26_20", new trsuperobject(23,26,20,"Haydi halkım, iç odalarınıza giripArdınızdan kapılarınızı kapatın,RABbin öfkesi geçene dek kısa süre gizlenin."));
trsuperobject.biblemap.put("b23_26_21", new trsuperobject(23,26,21,"Çünkü dünyada yaşayanlarıSuçlarından ötürü cezalandırmak içinRAB bulunduğu yerden geliyor.Dünya üzerine dökülen kanı açığa vuracak,Öldürülenleri artık saklamayacak."));
trsuperobject.biblemap.put("b23_27_1", new trsuperobject(23,27,1,"O gün RAB Livyatanı, o kaçan yılanı,Evet Livyatanı, o kıvrıla kıvrıla giden yılanıAcımasız, kocaman, güçlü kılıcıyla cezalandıracak,Denizdeki canavarı öldürecek."));
trsuperobject.biblemap.put("b23_27_2", new trsuperobject(23,27,2,"O gün RAB,‹‹Sevdiğim bağ için ezgiler söyleyin›› diyecek,"));
trsuperobject.biblemap.put("b23_27_3", new trsuperobject(23,27,3,"‹‹Ben RAB, bağın koruyucusuyum,Onu sürekli sularım.Kimse zarar vermesin diyeGece gündüz beklerim."));
trsuperobject.biblemap.put("b23_27_4", new trsuperobject(23,27,4,"Kızgın değilim.Keşke karşıma dikenli çalılar çıksa!Onların üzerine yürür,Tümünü ateşe verirdim."));
trsuperobject.biblemap.put("b23_27_5", new trsuperobject(23,27,5,"Ya da koruyuculuğuma sarılsınlar,Barışsınlar benimle,Evet, benimle barışsınlar.››"));
trsuperobject.biblemap.put("b23_27_6", new trsuperobject(23,27,6,"Yakup soyu gelecekte kök salacak,İsrail filizlenip çiçeklenecek,Yeryüzünü meyvesiyle dolduracak."));
trsuperobject.biblemap.put("b23_27_9", new trsuperobject(23,27,9,"Böylece Yakup soyunun suçu bağışlanacak.Günahlarının kaldırılmasının sonucu şöyle olacak:Sunağın taşlarını tebeşir taşı gibi un ufak ettiklerindeNe Aşera putu ne de buhur sunağı kalacak."));
trsuperobject.biblemap.put("b23_27_10", new trsuperobject(23,27,10,"Surlu kent terk edildi,Çöl kadar ıssız, sahipsiz bir yurt oldu.Dana orada otlayıp uzanacak,Filizlerini yiyip bitirecek."));
trsuperobject.biblemap.put("b23_27_11", new trsuperobject(23,27,11,"Kuruyan dalları koparılacak,Kadınlar gelip bunları yakacaklar.Çünkü bu halk akıllı bir halk değil.Bu yüzden onları yaratan kendilerine acımayacak,Onlara biçim veren onları kayırmayacak."));
trsuperobject.biblemap.put("b23_27_12", new trsuperobject(23,27,12,"Ey İsrailoğulları, o gün RAB gürül gürül akan Fırat ile Mısır Vadisi arasında harman döver gibi, sizi birer birer toplayacak."));
trsuperobject.biblemap.put("b23_27_13", new trsuperobject(23,27,13,"Evet, o gün büyük bir boru çalınacak; Asur'da yitenlerle Mısır'a sürgün edilenler gelip kutsal dağda, Yeruşalim'de RAB'be tapınacaklar."));
trsuperobject.biblemap.put("b23_28_1", new trsuperobject(23,28,1,"Vay haline verimli vadinin başındaki kentin, Efrayimli sarhoşların gurur tacının! Şaraba yenilmişlerin yüce ve görkemli tacı, solmakta olan çiçeği andırıyor."));
trsuperobject.biblemap.put("b23_28_2", new trsuperobject(23,28,2,"Rabbin güçlü kudretli bir adamı var. Dolu fırtınası gibi, harap eden kasırga gibi, silip süpüren güçlü sel gibi o kenti şiddetle yere çalacak."));
trsuperobject.biblemap.put("b23_28_3", new trsuperobject(23,28,3,"Efrayimli sarhoşların gurur tacı ayaklar altında çiğnenecek."));
trsuperobject.biblemap.put("b23_28_4", new trsuperobject(23,28,4,"Verimli vadinin başındaki kent, yüce ve görkemli taç, artık solmakta olan çiçeği andıran kent, mevsiminden önce olgunlaşmış incir gibi görülür görülmez koparılıp yutulacak."));
trsuperobject.biblemap.put("b23_28_5", new trsuperobject(23,28,5,"O gün Her Şeye Egemen RAB, halkından sağ kalanlar için yücelik tacı, güzellik çelengi olacak."));
trsuperobject.biblemap.put("b23_28_6", new trsuperobject(23,28,6,"Yargı kürsüsünde oturanlar için adalet ruhu, kent kapılarında saldırıları geri püskürtenler için cesaret kaynağı olacak."));
trsuperobject.biblemap.put("b23_28_7", new trsuperobject(23,28,7,"Kâhinlerle peygamberler bile şarabın ve içkinin etkisiyle yalpalayıp sendeliyor; içkinin etkisiyle yalpalayıp sendeliyorlar, şaraba yenik düşmüşler. Yanlış görümler görüyorlar, kararlarında tutarsızlar."));
trsuperobject.biblemap.put("b23_28_8", new trsuperobject(23,28,8,"Sofralar kusmuk dolu, pisliğe bulaşmamış yer yok!"));
trsuperobject.biblemap.put("b23_28_9", new trsuperobject(23,28,9,"‹‹Kimi eğitmeye çalışıyor?›› diyorlar, ‹‹Kime iletiyor bildirisini? Sütten yeni kesilmiş, memeden yeni ayrılmış çocuklara mı?"));
trsuperobject.biblemap.put("b23_28_10", new trsuperobject(23,28,10,"Çünkü bütün söylediği buyruk üstüne buyruk, buyruk üstüne buyruk, kural üstüne kural, kural üstüne kural, biraz şurdan, biraz burdan...›› ‹‹Sav lasav, sav lasav, kav lakav, kav lakav, zeer şam, zeer şam!››"));
trsuperobject.biblemap.put("b23_28_11", new trsuperobject(23,28,11,"Öyle olsun, o zaman RAB bu halka yabancı dudaklarla, anlaşılmaz bir dille seslenecek."));
trsuperobject.biblemap.put("b23_28_12", new trsuperobject(23,28,12,"Onlara, ‹‹Rahatlık budur, yorgunların rahat etmelerini sağlayın, huzur budur›› dedi, ama dinlemek istemediler."));
trsuperobject.biblemap.put("b23_28_13", new trsuperobject(23,28,13,"Bu yüzden RABbin sözü onlar için ‹‹Buyruk üstüne buyruk, buyruk üstüne buyruk, kural üstüne kural, kural üstüne kural, biraz şurdan, biraz burdan››dır. Madem öyle, varsın sırtüstü düşüp yaralansınlar, kapana kısılıp tutsak olsunlar."));
trsuperobject.biblemap.put("b23_28_14", new trsuperobject(23,28,14,"Bundan ötürü, ey alaycılar, Yeruşalimdeki bu halkı yöneten sizler, RABbin sözüne kulak verin."));
trsuperobject.biblemap.put("b23_28_15", new trsuperobject(23,28,15,"Şöyle diyorsunuz: ‹‹Ölümle antlaşma yaptık, ölüler diyarıyla uyuştuk; öyle ki, büyük bela ülkeden geçerken bize zarar vermeyecek. Çünkü yalanları kendimize sığınak yaptık, hilenin ardına gizlendik.››"));
trsuperobject.biblemap.put("b23_28_16", new trsuperobject(23,28,16,"Bu yüzden Egemen RAB diyor ki, ‹‹İşte Siyona sağlam temel olarak bir taş, denenmiş bir taş, değerli bir köşe taşı yerleştiriyorum. Ona güvenen yenilmeyecek."));
trsuperobject.biblemap.put("b23_28_17", new trsuperobject(23,28,17,"Adaleti ölçü ipi, doğruluğu çekül yapacağım. Yalanlara dayanan sığınağı dolu süpürüp götürecek, gizlendiğiniz yerleri sel basacak."));
trsuperobject.biblemap.put("b23_28_18", new trsuperobject(23,28,18,"Ölümle yaptığınız antlaşma yürürlükten kaldırılacak, ölüler diyarıyla uyuşmanız geçerli sayılmayacak. Büyük bela ülkeden geçerken sizi çiğneyecek."));
trsuperobject.biblemap.put("b23_28_19", new trsuperobject(23,28,19,"Bu bela her geldiğinde sizi süpürüp götürecek. Her gün, gece gündüz gelecek. Bu bildiriyi anlayan dehşete kapılacak."));
trsuperobject.biblemap.put("b23_28_20", new trsuperobject(23,28,20,"Yatak uzanamayacağınız kadar kısa, örtü sarınamayacağınız kadar dar olacak."));
trsuperobject.biblemap.put("b23_28_21", new trsuperobject(23,28,21,"Çünkü RAB, Perasim Dağında olduğu gibi kalkacak, Givon Vadisinde olduğu gibi öfkelenecek. Ne kadar garip olsa da işini tamamlayacak, ne kadar tuhaf olsa da yapacağını yapacak."));
trsuperobject.biblemap.put("b23_28_22", new trsuperobject(23,28,22,"Alay etmeyin artık, yoksa zincirleriniz daha da kalınlaşır. Çünkü bütün ülkenin kesin bir yıkıma uğrayacağını Rabden, Her Şeye Egemen RABden duydum."));
trsuperobject.biblemap.put("b23_28_23", new trsuperobject(23,28,23,"Kulak verin, sesimi işitin, dikkat edin, ne söylediğimi dinleyin."));
trsuperobject.biblemap.put("b23_28_24", new trsuperobject(23,28,24,"Çiftçi ekin ekmek için durmadan toprağı sürer mi, boyuna eşeleyip tırmıklar mı?"));
trsuperobject.biblemap.put("b23_28_25", new trsuperobject(23,28,25,"Toprağı düzledikten sonra çörekotunu, kimyonu serpmez mi? Buğdayı sıra sıra, arpayı ayırdığı yere, kızıl buğdayı da onun yanına ekmez mi?"));
trsuperobject.biblemap.put("b23_28_26", new trsuperobject(23,28,26,"Tanrısı ona uygun olanı gösterir, onu eğitir."));
trsuperobject.biblemap.put("b23_28_27", new trsuperobject(23,28,27,"Çünkü çörekotu harmanda keskin aletle dövülmez, kimyonun üzerinden tekerlekle geçilmez. Çörekotu değnekle, kimyon çubukla dövülür."));
trsuperobject.biblemap.put("b23_28_28", new trsuperobject(23,28,28,"Buğday ekmek yapmak için öğütülür, ama boyuna dövülmez. Harmanın üzerinden tekerlek ve atlar geçse de buğdayı ezmez."));
trsuperobject.biblemap.put("b23_28_29", new trsuperobject(23,28,29,"Bu işteki bilgelik de Her Şeye Egemen RAB'den gelir. O'nun tasarıları harikadır, bilgelikte üstündür."));
trsuperobject.biblemap.put("b23_29_1", new trsuperobject(23,29,1,"Ariel, Ariel,Davutun ordugah kurduğu kent, vay haline!Sen yıla yıl kat, bayramların süredursun. anlamına gelebilir. Yeruşalim Kentini simgeliyor."));
trsuperobject.biblemap.put("b23_29_2", new trsuperobject(23,29,2,"Ama seni sıkıntıya sokacağım.Feryat, figan edeceksin,Benim için sunak ocağı gibi olacaksın."));
trsuperobject.biblemap.put("b23_29_3", new trsuperobject(23,29,3,"Sana karşı çepeçevre ordugah kuracak,Çevreni rampalarla, kulelerle kuşatacağım."));
trsuperobject.biblemap.put("b23_29_4", new trsuperobject(23,29,4,"Alçaltılacaksın, yerin altından konuşacak,Toz toprak içinden boğuk boğuk sesleneceksin.Sesin ölü sesi gibi yerden,Sözlerin fısıltı gibi toprağın içinden çıkacak."));
trsuperobject.biblemap.put("b23_29_5", new trsuperobject(23,29,5,"Ama sayısız düşmanların ince toz,Acımasız orduları savrulmuş saman ufağı gibi olacak.Bir anda, ansızın,"));
trsuperobject.biblemap.put("b23_29_6", new trsuperobject(23,29,6,"Her Şeye Egemen RAB gök gürlemesiyle,Depremle, büyük gümbürtü, kasırga ve fırtınayla,Her şeyi yiyip bitiren ateş aleviyle seni cezalandıracak."));
trsuperobject.biblemap.put("b23_29_7", new trsuperobject(23,29,7,"Sonra Ariele karşı savaşan çok sayıda ulus,Ona ve kalesine saldıranların hepsi,Onu sıkıntıya sokanlar bir rüya gibi,Gece görülen görüm gibi yok olup gidecekler."));
trsuperobject.biblemap.put("b23_29_8", new trsuperobject(23,29,8,"Rüyada yemek yediğini gören aç kişi,Uyandığında hâlâ açtır;Rüyada su içtiğini gören susuz kişi,Uyandığında susuzluktan hâlâ baygındır.İşte Siyon Dağına karşı savaşanKalabalık uluslar da böyle olacak."));
trsuperobject.biblemap.put("b23_29_9", new trsuperobject(23,29,9,"Şaşırın, şaşkına dönün,Kendinizi kör edin, görmez olun.Şarap içmeden sarhoş olun,İçki içmeden sendeleyin."));
trsuperobject.biblemap.put("b23_29_10", new trsuperobject(23,29,10,"Çünkü RAB size uyuşukluk ruhu verdi;Gözlerinizi mühürledi, ey peygamberler,Başlarınızı örttü, ey biliciler."));
trsuperobject.biblemap.put("b23_29_11", new trsuperobject(23,29,11,"Sizin için bütün görümMühürlenmiş bir kitabın sözleri gibi oldu.İnsanlar böyle bir kitabıOkuma bilen birine verip,‹‹Rica etsek şunu okur musun?›› diye sorduklarında,‹‹Okuyamam, çünkü mühürlenmiş›› yanıtını alırlar."));
trsuperobject.biblemap.put("b23_29_12", new trsuperobject(23,29,12,"Kitabı okuma bilmeyen birine verip,‹‹Rica etsek şunu okur musun?›› diye sorduklarında ise,‹‹Okuma bilmem›› yanıtını alırlar."));
trsuperobject.biblemap.put("b23_29_13", new trsuperobject(23,29,13,"Rab diyor ki, ‹‹Bu halk bana yaklaşıpAğızlarıyla, dudaklarıyla beni sayar,Ama yürekleri benden uzak.Benden korkmaları daİnsanlardan öğrendikleri buyrukların sonucudur."));
trsuperobject.biblemap.put("b23_29_14", new trsuperobject(23,29,14,"Onun için ben de bu halkın arasında yine bir harika,Evet, şaşılacak bir şey yapacağım.Bilgelerin bilgeliği yok olacak,Akıllının aklı duracak.››"));
trsuperobject.biblemap.put("b23_29_15", new trsuperobject(23,29,15,"Tasarılarını RABden gizlemeye uğraşanların vay haline!Karanlıkta iş gören bu adamlar,‹‹Bizi kim görecek, kim tanıyacak?›› diye düşünürler."));
trsuperobject.biblemap.put("b23_29_16", new trsuperobject(23,29,16,"Ne kadar ters düşünceler!Çömlekçi balçıkla bir tutulur mu?Yapı, kendini yapan için,‹‹Beni o yapmadı›› diyebilir mi?Çömlek kendine biçim veren için,‹‹O bir şeyden anlamaz›› diyebilir mi?"));
trsuperobject.biblemap.put("b23_29_17", new trsuperobject(23,29,17,"Lübnan pek yakında meyve bahçesine,Meyve bahçesi ormana dönmeyecek mi?"));
trsuperobject.biblemap.put("b23_29_18", new trsuperobject(23,29,18,"O gün sağırlar kitabın sözlerini işitecek,Körler koyu karanlıkta görecek."));
trsuperobject.biblemap.put("b23_29_19", new trsuperobject(23,29,19,"Düşkünlerin RABde buldukları sevinç artacak,Yoksullar İsrailin Kutsalı sayesinde coşacak."));
trsuperobject.biblemap.put("b23_29_20", new trsuperobject(23,29,20,"Çünkü acımasızlar yok olacak, alaycılar silinecek,Kötülüğe fırsat kollayanların hepsi kesilip atılacak."));
trsuperobject.biblemap.put("b23_29_21", new trsuperobject(23,29,21,"Onlar ki, insanı tek sözle davasında suçlu çıkarır,Kent kapısında haksızı azarlayana tuzak kurar,Yok yere haklının hakkını çiğnerler."));
trsuperobject.biblemap.put("b23_29_22", new trsuperobject(23,29,22,"Bundan dolayı, İbrahimi kurtarmış olan RABYakup soyuna diyor ki,‹‹Yakup soyu artık utanmayacak,Yüzleri korkudan sararmayacak."));
trsuperobject.biblemap.put("b23_29_23", new trsuperobject(23,29,23,"Elimin yapıtı olan çocuklarınıAralarında gördüklerindeAdımı kutsal sayacaklar;Evet, Yakupun Kutsalını kutsal sayacak,İsrailin Tanrısından korkacaklar."));
trsuperobject.biblemap.put("b23_29_24", new trsuperobject(23,29,24,"Yoldan sapmış olanlar kavrayışa,Yakınıp duranlar bilgiye kavuşacak.››"));
trsuperobject.biblemap.put("b23_30_2", new trsuperobject(23,30,2,"Bana danışmadan firavunun koruması altına girmek,Mısırın gölgesine sığınmak için oraya gidiyorlar."));
trsuperobject.biblemap.put("b23_30_3", new trsuperobject(23,30,3,"Ne var ki, firavunun koruması onlar için utanç,Mısırın gölgesine sığınmaları onlar için rezillik olacak."));
trsuperobject.biblemap.put("b23_30_4", new trsuperobject(23,30,4,"Önderleri Soanda olduğu,Elçileri Hanese ulaştığı halde,"));
trsuperobject.biblemap.put("b23_30_5", new trsuperobject(23,30,5,"Kendilerine yararı olmayan bir halk yüzünden hepsi utanacak.O halkın onlara ne yardımı ne de yararı olacak,Ancak onları utandırıp rezil edecek.››"));
trsuperobject.biblemap.put("b23_30_6", new trsuperobject(23,30,6,"Negevdeki hayvanlara ilişkin bildiri:‹‹Elçiler erkek ve dişi aslanların,Engereklerin, uçan yılanların yaşadığıÇetin ve sıkıntılı bir bölgeden geçerler.Servetlerini eşeklerin sırtına,Hazinelerini develerin hörgücüne yükleyipKendilerine hiç yararı olmayan halka taşırlar."));
trsuperobject.biblemap.put("b23_30_7", new trsuperobject(23,30,7,"Mısırın yardımı boş ve yararsızdır,Bu yüzden Mısıra ‹Haylaz Rahavfç› adını verdim. ‹‹Fırtına›› ya da ‹‹Küstah›› anlamına gelir."));
trsuperobject.biblemap.put("b23_30_8", new trsuperobject(23,30,8,"‹‹Şimdi git, söylediğimi onların önündeBir levhaya yazıp kitaba geçir ki,Gelecekte kalıcı bir tanık olsun."));
trsuperobject.biblemap.put("b23_30_9", new trsuperobject(23,30,9,"Çünkü o asi bir halk, yalancı bir soy,RABbin yasasını duymak istemeyen bir soydur."));
trsuperobject.biblemap.put("b23_30_10", new trsuperobject(23,30,10,"Bilicilere, ‹Artık görüm görmeyin›,Görenlere, ‹Bizim için doğru şeyler görmeyin,Bize güzel şeyler söyleyin, asılsız şeyler açıklayın› diyorlar,"));
trsuperobject.biblemap.put("b23_30_11", new trsuperobject(23,30,11,"‹Yoldan çekilin, yolu açın,Bizi İsrailin Kutsalıyla yüzleştirmekten vazgeçin.››"));
trsuperobject.biblemap.put("b23_30_12", new trsuperobject(23,30,12,"Bu nedenle İsrailin Kutsalı diyor ki,‹‹Madem bu bildiriyi reddettiniz,Baskıya ve hileye güvenip dayandınız;"));
trsuperobject.biblemap.put("b23_30_13", new trsuperobject(23,30,13,"Bu suçunuz yüksek bir surdaSırt veren çatlağa benziyor.Böyle bir sur birdenbire yıkılıverir."));
trsuperobject.biblemap.put("b23_30_14", new trsuperobject(23,30,14,"O, toprak çömlek gibi parçalanacak.Parçalanması öyle şiddetli olacak ki,Ocaktan ateş almaya ya da sarnıçtan su çıkarmayaYetecek büyüklükte bir parça kalmayacak.››"));
trsuperobject.biblemap.put("b23_30_15", new trsuperobject(23,30,15,"Egemen RAB, İsrailin Kutsalı şöyle diyor:‹‹Bana dönün, huzur bulun, kurtulursunuz.Kaygılanmayın, bana güvenin, güçlü olursunuz.Ama bunu yapmak istemiyorsunuz."));
trsuperobject.biblemap.put("b23_30_16", new trsuperobject(23,30,16,"‹Hayır, atlara binip kaçarız› diyorsunuz,Bu yüzden kaçmak zorunda kalacaksınız.‹Hızlı atlara bineriz› diyorsunuz,Bu yüzden sizi kovalayanlar da hızlı olacak."));
trsuperobject.biblemap.put("b23_30_17", new trsuperobject(23,30,17,"Bir kişinin tehdidiyle bin kişi kaçacak,Beş kişinin tehdidiyle hepiniz kaçacaksınız;Dağ başında bir gönder,Tepede bir sancak gibi kalana dek kaçacaksınız."));
trsuperobject.biblemap.put("b23_30_18", new trsuperobject(23,30,18,"‹‹Yine de RAB size lütfetmeyi özlemle bekliyor,Size merhamet göstermek için harekete geçiyor.Çünkü RAB adil Tanrıdır.Ne mutlu Onu özlemle bekleyenlere!››"));
trsuperobject.biblemap.put("b23_30_19", new trsuperobject(23,30,19,"‹‹Ey Yeruşalimde oturan Siyon halkı,Artık ağlamayacaksın!Feryat ettiğinde Rab sana nasıl da lütfedecek!Feryadını duyar duymaz seni yanıtlayacak."));
trsuperobject.biblemap.put("b23_30_20", new trsuperobject(23,30,20,"Rab ekmeği sıkıntıyla,Suyu cefayla verse de,Öğretmeniniz artık gizlenmeyecek,Gözünüzle göreceksiniz onu. ‹‹Öğretmenlerinizi artık dışlamayacaksınız››."));
trsuperobject.biblemap.put("b23_30_21", new trsuperobject(23,30,21,"Sağa ya da sola sapacağınız zaman,Arkanızdan, ‹Yol budur, bu yoldan gidin›Diyen sesini duyacaksınız."));
trsuperobject.biblemap.put("b23_30_22", new trsuperobject(23,30,22,"Gümüş kaplı oyma putlarınızı,Altın kaplama dökme putlarınızı‹Kirli› ilan edecek,Kirli bir âdet bezi gibi atıp‹Defol› diyeceksiniz."));
trsuperobject.biblemap.put("b23_30_23", new trsuperobject(23,30,23,"Rab toprağa ektiğiniz tohum için yağmur verecek,Toprağın ürünü olan yiyecek bol ve zengin olacak.O gün sığırlarınız geniş otlaklarda otlanacak."));
trsuperobject.biblemap.put("b23_30_24", new trsuperobject(23,30,24,"Toprağı işleyen öküzlerle eşeklerKürekle, yabayla savrulmuş,Tuzlanmış yem yiyecekler."));
trsuperobject.biblemap.put("b23_30_25", new trsuperobject(23,30,25,"Kalelerin düştüğü o büyük kıyım günüHer yüksek dağda, her yüce tepedeAkarsular olacak."));
trsuperobject.biblemap.put("b23_30_26", new trsuperobject(23,30,26,"RAB halkının kırıklarını sardığı,Vuruşuyla açtığı yaraları iyileştirdiği gün,Ay güneş gibi parlayacak,Güneş yedi kat, yedi günün toplam parlaklığı kadar parlak olacak.››"));
trsuperobject.biblemap.put("b23_30_27", new trsuperobject(23,30,27,"Bakın, RABbin kendisi uzaktan geliyor,Kızgın öfkeyle kara bulut içinde.Dudakları gazap dolu,Dili her şeyi yiyip bitiren ateş sanki."));
trsuperobject.biblemap.put("b23_30_28", new trsuperobject(23,30,28,"Soluğu adam boynuna dek yükselmiş taşkın ırmak gibi.Ulusları elekten geçirecek, değersizleri ayıracak,Halkların ağzına yoldan saptıran bir gem takacak."));
trsuperobject.biblemap.put("b23_30_29", new trsuperobject(23,30,29,"Ama sizler bayram gecesini kutlar gibiEzgiler söyleyeceksiniz.RABbin dağına, İsrailin KayasınaKaval eşliğinde çıktığınız gibiİçten sevineceksiniz."));
trsuperobject.biblemap.put("b23_30_30", new trsuperobject(23,30,30,"RAB heybetli sesini işittirecek;Kızgın öfkeyle, her şeyi yiyip bitiren ateş aleviyle,Sağanak yağmurla, fırtına ve doluylaBileğinin gücünü gösterecek."));
trsuperobject.biblemap.put("b23_30_31", new trsuperobject(23,30,31,"Asur RABbin sesiyle dehşete düşecek,Onun değneğiyle vurulacak."));
trsuperobject.biblemap.put("b23_30_32", new trsuperobject(23,30,32,"RABbin terbiye değneğiyle onlara indirdiği her darbeyeTef ve lir eşlik edecek.RAB silahlarını savura savura onlarla savaşacak."));
trsuperobject.biblemap.put("b23_30_33", new trsuperobject(23,30,33,"Tofet çoktan hazırlandı,Evet, kral için hazırlandı.Geniş ve yüksektir odun yığını,Ateşi, odunu boldur.RAB kızgın kükürt selini andıranSoluğuyla tutuşturacak onu."));
trsuperobject.biblemap.put("b23_31_1", new trsuperobject(23,31,1,"Vay haline yardım bulmak için Mısıra inenlerin!Atlara, çok sayıdaki savaş arabalarına,Kalabalık atlılara güveniyorlar,Ama İsrailin Kutsalına güvenmiyor,RABbe yönelmiyorlar."));
trsuperobject.biblemap.put("b23_31_2", new trsuperobject(23,31,2,"Oysa bilge olan RABdir.Felaket getirebilir ve sözünü geri almaz.Kötülük yapan soya,Suç işleyenlerin yardımına karşı çıkar."));
trsuperobject.biblemap.put("b23_31_3", new trsuperobject(23,31,3,"Mısırlılar Tanrı değil, insandır,Atları da ruh değil, et ve kemiktir.RABbin eli kalkınca yardım eden tökezler,Yardım gören düşer, hep birlikte yok olurlar."));
trsuperobject.biblemap.put("b23_31_4", new trsuperobject(23,31,4,"Çünkü RAB bana dedi ki,‹‹Avının başında homurdanan aslanBir araya çağrılan çobanlar topluluğununBağırıp çağırmasından yılmadığı, gürültüsüne aldırmadığı gibi,Her Şeye Egemen RAB deSiyon Dağının doruğuna inip savaşacak."));
trsuperobject.biblemap.put("b23_31_5", new trsuperobject(23,31,5,"Her Şeye Egemen RABKanat açmış kuşlar gibi koruyacak Yeruşalimi.Koruyup özgür kılacak, esirgeyip kurtaracak onu.››"));
trsuperobject.biblemap.put("b23_31_6", new trsuperobject(23,31,6,"Ey İsrailoğulları,Bunca vefasızlık ettiğiniz RABbe dönün."));
trsuperobject.biblemap.put("b23_31_7", new trsuperobject(23,31,7,"Çünkü hepiniz günahkâr ellerinizle yaptığınızAltın ve gümüş putları o gün reddedip atacaksınız."));
trsuperobject.biblemap.put("b23_31_8", new trsuperobject(23,31,8,"Asur kılıca yenik düşecek,Ama insan kılıcına değil.Halkı kılıçtan geçirilecek,Ama bu insan kılıcı olmayacak.Kimileri kaçıp kurtulacak,Gençleri de angaryaya koşulacak."));
trsuperobject.biblemap.put("b23_31_9", new trsuperobject(23,31,9,"Asur Kralı dehşet içinde kaçacak,Önderleri sancağı görünce dehşete kapılacak.Siyon'da ateşi,Yeruşalim'de ocağı bulunan RAB söylüyor bunları."));
trsuperobject.biblemap.put("b23_32_1", new trsuperobject(23,32,1,"İşte kral doğrulukla krallık yapacak,Önderler adaletle yönetecek."));
trsuperobject.biblemap.put("b23_32_2", new trsuperobject(23,32,2,"Her biri rüzgara karşı bir sığınak,Fırtınaya karşı bir barınak, çölde akarsu,Çorak yerde gölge salanBüyük bir kaya gibi olacak."));
trsuperobject.biblemap.put("b23_32_3", new trsuperobject(23,32,3,"Artık görenlerin gözleri kapanmayacak,Dinleyenler kulak kesilecek."));
trsuperobject.biblemap.put("b23_32_4", new trsuperobject(23,32,4,"Düşüncesizin aklı bilgiye erecek,Kekeme açık seçik, akıcı konuşacak."));
trsuperobject.biblemap.put("b23_32_5", new trsuperobject(23,32,5,"Artık budalaya soylu,Alçağa saygın denmeyecek."));
trsuperobject.biblemap.put("b23_32_6", new trsuperobject(23,32,6,"Çünkü budala saçmalıyor,Aklı fikri hep kötülükte.İşi gücü fesat işlemek,RABbe ilişkin yanlış sözler söylemek,Açları aç bırakmak,Susamışlardan suyu esirgemek."));
trsuperobject.biblemap.put("b23_32_7", new trsuperobject(23,32,7,"Alçağın yöntemleri kötüdür;Yoksul davasında haklı olsa daOnu yalanlarla yok etmek içinKötü düzenler tasarlar."));
trsuperobject.biblemap.put("b23_32_8", new trsuperobject(23,32,8,"Soylu kişiyse soylu şeyler tasarlar,Yaptığı soylu işlerle ayakta kalır."));
trsuperobject.biblemap.put("b23_32_9", new trsuperobject(23,32,9,"Ey tasasızca yaşayan kadınlar,Kalkın, sesimi işitin;Ey kaygısız kızlar, sözüme kulak verin!"));
trsuperobject.biblemap.put("b23_32_10", new trsuperobject(23,32,10,"Bir yıl kadar sonra sarsılacaksınız,Ey kaygısız kadınlar.Çünkü bağbozumu olmayacak,Devşirecek meyve bulunmayacak."));
trsuperobject.biblemap.put("b23_32_11", new trsuperobject(23,32,11,"Titreyin, ey tasasızca yaşayan kadınlar,Sarsılın, ey kaygısızlar.Giysilerinizi çıkarın, soyunup belinize çul kuşanın."));
trsuperobject.biblemap.put("b23_32_12-13", new trsuperobject(23,32,12-13,"Güzel tarlalar, verimli asmalar,Halkımın diken ve çalı bitmiş toprakları için,Neşeli kentteki mutluluk dolu evler için göğsünüzü dövün."));
trsuperobject.biblemap.put("b23_32_14", new trsuperobject(23,32,14,"Çünkü saray ıssız,Kalabalık kent bomboş kalacak.Ofel Mahallesiyle gözcü kulesiBir çayırlığa dönecek;Yaban eşeklerinin keyifle gezindiği,Sürülerin otladığı bir yer olacak."));
trsuperobject.biblemap.put("b23_32_15", new trsuperobject(23,32,15,"Ta ki yukarıdan üzerimize ruh dökülene dek;O zaman çöl meyve bahçesine,Meyve bahçesi ormana dönecek."));
trsuperobject.biblemap.put("b23_32_16", new trsuperobject(23,32,16,"O zaman adalet çöle dek yayılacak,Doğruluk meyve bahçesinde yurt bulacak."));
trsuperobject.biblemap.put("b23_32_17", new trsuperobject(23,32,17,"Doğruluğun ürünü esenlik,Sonucu, sürekli huzur ve güven olacaktır."));
trsuperobject.biblemap.put("b23_32_18", new trsuperobject(23,32,18,"Halkım esenlik dolu evlerde,Güvenli ve rahat yerlerde yaşayacak."));
trsuperobject.biblemap.put("b23_32_19", new trsuperobject(23,32,19,"Dolu ormanları harap etse,Kent yerle bir olsa da,"));
trsuperobject.biblemap.put("b23_32_20", new trsuperobject(23,32,20,"Sulak yerde tohum eken,Sığırını, eşeğini özgürce çayıra salan sizlere ne mutlu!"));
trsuperobject.biblemap.put("b23_33_1", new trsuperobject(23,33,1,"Vay sana, yıkıp yok edenAma kendisi yıkılmamış olan!Vay sana, ihanete uğramamış hain!Yıkıma son verir vermez sen de yıkılacaksın,İhanetin sona erer ermez sen de ihanete uğrayacaksın."));
trsuperobject.biblemap.put("b23_33_2", new trsuperobject(23,33,2,"Ya RAB, lütfet bize,Çünkü sana umut bağladık,Gün be gün gücümüz ol!Sıkıntıya düştüğümüzde bizi kurtar."));
trsuperobject.biblemap.put("b23_33_3", new trsuperobject(23,33,3,"Kükreyişinden halklar kaçışır,Sen ayağa kalkınca uluslar darmadağın olur."));
trsuperobject.biblemap.put("b23_33_4", new trsuperobject(23,33,4,"Çekirgeler tarlayı nasıl yağmalarsa,Ganimetiniz de öyle yağmalanacak, ey uluslar.Malınızın üzerine çekirge sürüsü gibi saldıracaklar."));
trsuperobject.biblemap.put("b23_33_5", new trsuperobject(23,33,5,"Yükseklerde oturan RAB yücedir,Siyonu adalet ve doğrulukla doldurur."));
trsuperobject.biblemap.put("b23_33_6", new trsuperobject(23,33,6,"Yaşadığınız sürenin güvencesi Odur.Bol bol kurtuluş, bilgi ve bilgelik sağlayacak.Halkın hazinesi RAB korkusudur."));
trsuperobject.biblemap.put("b23_33_7", new trsuperobject(23,33,7,"İşte, en yiğitleri sokaklarda feryat ediyor,Barış elçileri acı acı ağlıyor."));
trsuperobject.biblemap.put("b23_33_8", new trsuperobject(23,33,8,"Anayollar bomboş,Yolculuk eden kimse kalmadı.Düşman antlaşmayı bozdu, kentleri hor gördü,İnsanları hiçe saydı."));
trsuperobject.biblemap.put("b23_33_9", new trsuperobject(23,33,9,"Ülke yas tutuyor, zayıflıyor.Lübnan utancından soldu,Şaron Ovası çöle döndü,Başan ve Karmelde ağaçlar yaprak döküyor."));
trsuperobject.biblemap.put("b23_33_10", new trsuperobject(23,33,10,"RAB diyor ki, ‹‹Şimdi harekete geçeceğim,Ne denli yüce ve üstün olduğumu göstereceğim."));
trsuperobject.biblemap.put("b23_33_11", new trsuperobject(23,33,11,"Samana gebe kalıp anız doğuracaksınız,Soluğunuz sizi yiyip bitiren bir ateş olacak."));
trsuperobject.biblemap.put("b23_33_12", new trsuperobject(23,33,12,"Halklar yanıp kül olacak,Kesilip yakılan dikenli çalı gibi olacak."));
trsuperobject.biblemap.put("b23_33_13", new trsuperobject(23,33,13,"‹‹Ey uzaktakiler, ne yaptığımı işitin,Ey yakındakiler, gücümü anlayın.››"));
trsuperobject.biblemap.put("b23_33_14", new trsuperobject(23,33,14,"Siyondaki günahkârlar dehşet içinde,Tanrısızları titreme aldı.‹‹Her şeyi yiyip bitiren ateşin yanındaHangimiz oturabilir?Sonsuza dek sönmeyecek alevin yanındaHangimiz yaşayabilir?›› diye soruyorlar."));
trsuperobject.biblemap.put("b23_33_15", new trsuperobject(23,33,15,"Ama doğru yolda yürüyüp doğru dürüst konuşan,Zorbalıkla edinilen kazancı reddeden,Elini rüşvetten uzak tutan,Kan dökenlerin telkinlerine kulak vermeyen,Kötülük görmeye dayanamayan,"));
trsuperobject.biblemap.put("b23_33_16", new trsuperobject(23,33,16,"Yükseklerde oturacak;Uçurumun başındaki kaleler onun korunağı olacak,Ekmeği sağlanacak, hiç susuz kalmayacak."));
trsuperobject.biblemap.put("b23_33_17", new trsuperobject(23,33,17,"Kralı bütün güzelliğiyle görecek,Uçsuz bucaksız ülkeyi seyredeceksin."));
trsuperobject.biblemap.put("b23_33_18", new trsuperobject(23,33,18,"‹‹Haracı tartıp kaydeden nerede,Kulelerden sorumlu olan nerede?›› diyerekGeçmişteki dehşetli günleri düşüneceksin."));
trsuperobject.biblemap.put("b23_33_19", new trsuperobject(23,33,19,"Garip, anlaşılmaz bir yabancı dil konuşanO küstah halkı artık görmeyeceksin."));
trsuperobject.biblemap.put("b23_33_20", new trsuperobject(23,33,20,"Bayramlarımızın kenti olan Siyona bak!Yeruşalimi bir esenlik yurdu,Kazıkları asla yerinden sökülmeyen,Gergi ipleri hiç kopmayan,Sarsılmaz bir çadır olarak görecek gözlerin."));
trsuperobject.biblemap.put("b23_33_21", new trsuperobject(23,33,21,"Heybetli RAB orada bizden yana olacak.Orası geniş ırmakların, çayların yeri olacak.Bunların üzerinden ne kürekli tekneler,Ne de büyük gemiler geçecek."));
trsuperobject.biblemap.put("b23_33_22", new trsuperobject(23,33,22,"Çünkü yargıcımız RABdir;Yasamızı koyan RABdir,Kralımız RABdir, bizi O kurtaracak."));
trsuperobject.biblemap.put("b23_33_23", new trsuperobject(23,33,23,"Senin gemilerinin halatları gevşedi,Direklerinin dibini pekiştirmediler,Yelkenleri açmadılar.O zaman büyük ganimet paylaşılacak,Topallar bile yağmaya katılacak."));
trsuperobject.biblemap.put("b23_33_24", new trsuperobject(23,33,24,"Siyon'da oturan hiç kimse ‹‹Hastayım›› demeyecek,Orada yaşayan halkın suçu bağışlanacak."));
trsuperobject.biblemap.put("b23_34_1", new trsuperobject(23,34,1,"Ey uluslar, işitmek için yaklaşın!Ey halklar, kulak verin!Dünya ve üzerindeki herkes,Yeryüzü ve ondan türeyenlerin hepsi işitsin!"));
trsuperobject.biblemap.put("b23_34_3", new trsuperobject(23,34,3,"Ölüleri dışarı atılacak,Pis kokacak cesetleri;Dağlar kanlarıyla sulanacak."));
trsuperobject.biblemap.put("b23_34_4", new trsuperobject(23,34,4,"Bütün gök cisimleri küçülecek,Gökler bir tomar gibi dürülecek;Gök cisimleri, asma yaprağı,İncir yaprağı gibi dökülecek."));
trsuperobject.biblemap.put("b23_34_5", new trsuperobject(23,34,5,"‹‹Kılıcım göklerde kanıncaya kadar içti.Şimdi de Edomun,Tümüyle yıkmaya karar verdiğim halkınÜzerine inecek›› diyor RAB."));
trsuperobject.biblemap.put("b23_34_7", new trsuperobject(23,34,7,"Onlarla birlikte yaban öküzleri,Körpe boğalarla güçlü boğalar da yere serilecek.Toprakları kana doyacak, yağla sulanacak."));
trsuperobject.biblemap.put("b23_34_8", new trsuperobject(23,34,8,"Çünkü RABbin bir öç günü, Siyonun davasını güdeceği bir karşılık yılı olacak."));
trsuperobject.biblemap.put("b23_34_9", new trsuperobject(23,34,9,"Edom dereleri zifte, toprağı kükürde dönecek; ülkenin her yanı yanan zift olacak."));
trsuperobject.biblemap.put("b23_34_10", new trsuperobject(23,34,10,"Zift gece gündüz sönmeyecek, dumanı hep tütecek. Ülke kuşaklar boyu ıssız kalacak, sonsuza dek oradan kimse geçmeyecek."));
trsuperobject.biblemap.put("b23_34_11", new trsuperobject(23,34,11,"Baykuşların mülkü olacak orası, büyük baykuşlarla kargalar yaşayacak orada. RAB, Edomun üzerine kargaşa ipini, boşluk çekülünü gerecek."));
trsuperobject.biblemap.put("b23_34_12", new trsuperobject(23,34,12,"Kral ilan edebilecekleri soylular kalmayacak, bütün önderlerinin sonu gelecek."));
trsuperobject.biblemap.put("b23_34_13", new trsuperobject(23,34,13,"Saraylarında dikenler, kalelerinde ısırganlarla böğürtlenler bitecek. Orası çakalların barınağı, baykuşların yurdu olacak."));
trsuperobject.biblemap.put("b23_34_14", new trsuperobject(23,34,14,"Yabanıl hayvanlarla sırtlanlar orada buluşacak, tekeler karşılıklı böğürecek. Lilitfı oraya yerleşip rahata kavuşacak."));
trsuperobject.biblemap.put("b23_34_15", new trsuperobject(23,34,15,"Baykuşlar orada yuva kurup yumurtlayacak, kuluçkaya yatıp yavrularını kendi gölgelerinde toplayacak. Çaylaklar eşleşip orada toplanacaklar. Lev.17:7)."));
trsuperobject.biblemap.put("b23_34_16", new trsuperobject(23,34,16,"RABbin kitabını okuyup araştırın: Bunlardan hiçbiri eksik kalmayacak, eşten yoksun hiçbir hayvan olmayacak. Çünkü bu buyruk RABbin ağzından çıktı, Ruhu da onları toplayacak."));
trsuperobject.biblemap.put("b23_34_17", new trsuperobject(23,34,17,"RAB onlar için kura çekti, ülkeyi ölçüp aralarında pay etti. Orayı sonsuza dek sahiplenip kuşaklar boyu orada yaşayacaklar."));
trsuperobject.biblemap.put("b23_35_1", new trsuperobject(23,35,1,"Çöl ve kurak toprak sevinecek,Bozkır coşup çiğdem gibi çiçeklenecek."));
trsuperobject.biblemap.put("b23_35_2", new trsuperobject(23,35,2,"Her yanı çiçeklenip sevinçle coşacak,Sevincini haykıracak.Lübnanın yüceliği,Karmel ve Şaronun görkemi ona verilecek.İnsanlar RABbin yüceliğini,Tanrımızın görkemini görecek."));
trsuperobject.biblemap.put("b23_35_3", new trsuperobject(23,35,3,"Gevşek elleri güçlendirin,Pekiştirin çözülen dizleri."));
trsuperobject.biblemap.put("b23_35_4", new trsuperobject(23,35,4,"Yüreği kaygılı olanlara,‹‹Güçlü olun, korkmayın›› deyin,‹‹İşte Tanrınız geliyor!Öç almaya, karşılık vermeye geliyor.Sizi O kurtaracak.››"));
trsuperobject.biblemap.put("b23_35_5", new trsuperobject(23,35,5,"O zaman körlerin gözleri,Sağırların kulakları açılacak;"));
trsuperobject.biblemap.put("b23_35_6", new trsuperobject(23,35,6,"Topallar geyik gibi sıçrayacak,Sevinçle haykıracak dilsizlerin dili.Çünkü çölde sular fışkıracak,Irmaklar akacak bozkırda."));
trsuperobject.biblemap.put("b23_35_7", new trsuperobject(23,35,7,"Kızgın kum havuza,Susuz toprak pınara dönüşecek.Çakalların yattığı yerlerdeKamış, saz ve ot bitecek."));
trsuperobject.biblemap.put("b23_35_8", new trsuperobject(23,35,8,"Orada bir yol, bir anayol olacak,‹‹Kutsal yol›› diye anılacak,Murdar kişiler geçemeyecek oradan.O yol kurtulmuş olanların yoludur.O yolda yürüyenler, bön kişiler de olsa yoldan sapmayacak."));
trsuperobject.biblemap.put("b23_35_9", new trsuperobject(23,35,9,"Aslan olmayacak orada,Yırtıcı hayvan o yola çıkmayacak;Orada bulunmayacaklar.Ancak kurtulmuş olanlar yürüyecek o yolda."));
trsuperobject.biblemap.put("b23_35_10", new trsuperobject(23,35,10,"RAB'bin kurtardıkları dönecek,Sevinçle haykırarak Siyon'a varacaklar.Yüzlerinde sonsuz sevinç olacak.Onların olacak coşku ve sevinç,Üzüntü ve inilti kaçacak."));
trsuperobject.biblemap.put("b23_36_1", new trsuperobject(23,36,1,"Hizkiyanın krallığının on dördüncü yılında Asur Kralı Sanherib, Yahudanın surlu kentlerine saldırıp hepsini ele geçirdi."));
trsuperobject.biblemap.put("b23_36_2", new trsuperobject(23,36,2,"Komutanını büyük bir orduyla Lakişten Yeruşalime, Kral Hizkiyaya gönderdi. Komutan Çırpıcı Tarlası yolunda, Yukarı Havuzun su yolunun yanında durdu."));
trsuperobject.biblemap.put("b23_36_3", new trsuperobject(23,36,3,"Saray sorumlusu Hilkiya oğlu Elyakim, Yazman Şevna ve devlet tarihçisi Asaf oğlu Yoah onu karşılamaya çıktı."));
trsuperobject.biblemap.put("b23_36_4", new trsuperobject(23,36,4,"Komutan onlara şöyle dedi: ‹‹Hizkiyaya söyleyin. ‹Büyük kral, Asur Kralı diyor ki: Güvendiğin şey ne, neye güveniyorsun?"));
trsuperobject.biblemap.put("b23_36_5", new trsuperobject(23,36,5,"Savaş tasarıların ve gücün boş laftan başka birşey değil diyorum. Kime güveniyorsun da bana karşı ayaklanıyorsun?"));
trsuperobject.biblemap.put("b23_36_6", new trsuperobject(23,36,6,"İşte sen şu kırık kamış değneğe, Mısıra güveniyorsun. Bu değnek kendisine yaslanan herkesin eline batar, deler. Firavun da kendisine güvenenler için böyledir."));
trsuperobject.biblemap.put("b23_36_7", new trsuperobject(23,36,7,"Yoksa bana, Tanrımız RABbe güveniyoruz mu diyeceksiniz? Hizkiyanın Yahuda ve Yeruşalim halkına, yalnız bu sunağın önünde tapınacaksınız diyerek tapınma yerlerini, sunaklarını ortadan kaldırdığı Tanrı değil mi bu?›"));
trsuperobject.biblemap.put("b23_36_8", new trsuperobject(23,36,8,"‹‹Haydi, efendim Asur Kralıyla bahse giriş. Binicileri sağlayabilirsen sana iki bin at veririm."));
trsuperobject.biblemap.put("b23_36_9", new trsuperobject(23,36,9,"Mısırın savaş arabalarıyla atlıları sağlayacağına güvensen bile, efendimin en küçük rütbeli komutanlarından birini yenemezsin!"));
trsuperobject.biblemap.put("b23_36_10", new trsuperobject(23,36,10,"Dahası var: RABbin buyruğu olmadan mı saldırıp ülkeyi yıkmak için yola çıktığımı sanıyorsun? RAB, ‹Git, o ülkeyi yık› dedi.››"));
trsuperobject.biblemap.put("b23_36_11", new trsuperobject(23,36,11,"Elyakim, Şevna ve Yoah, ‹‹Lütfen biz kullarınla Aramice konuş›› diye karşılık verdiler, ‹‹Çünkü biz bu dili anlarız. Yahudice konuşma. Surların üzerindeki halk bizi dinliyor.››"));
trsuperobject.biblemap.put("b23_36_12", new trsuperobject(23,36,12,"Komutan, ‹‹Efendim bu sözleri yalnız size ve efendinize söyleyeyim diye mi gönderdi beni?›› dedi, ‹‹Surların üzerinde oturan bu halka, sizin gibi dışkısını yemek, idrarını içmek zorunda kalacak olan herkese gönderdi.››"));
trsuperobject.biblemap.put("b23_36_13", new trsuperobject(23,36,13,"Sonra ayağa kalkıp Yahudi dilinde bağırdı: ‹‹Büyük kralın, Asur Kralının sözlerini dinleyin!"));
trsuperobject.biblemap.put("b23_36_14", new trsuperobject(23,36,14,"Kral diyor ki, ‹Hizkiya sizi aldatmasın, o sizi kurtaramaz."));
trsuperobject.biblemap.put("b23_36_15", new trsuperobject(23,36,15,"RAB bizi mutlaka kurtaracak, bu kent Asur Kralının eline geçmeyecek diyen Hizkiyaya kanmayın, RABbe güvenmeyin."));
trsuperobject.biblemap.put("b23_36_16-17", new trsuperobject(23,36,16-17,"Hizkiyayı dinlemeyin.› Çünkü Asur Kralı diyor ki, ‹Teslim olun, bana gelin. Böylece ben gelip sizi kendi ülkeniz gibi bir ülkeye -tahıl ve yeni şarap, ekmek ve üzüm dolu bir ülkeye- götürene kadar herkes kendi asmasından, kendi incir ağacından yiyecek, kendi sarnıcından içecek."));
trsuperobject.biblemap.put("b23_36_18", new trsuperobject(23,36,18,"‹‹Hizkiya, RAB bizi kurtaracak diyerek sizi aldatmasın. Ulusların ilahları ülkelerini Asur Kralının elinden kurtarabildi mi?"));
trsuperobject.biblemap.put("b23_36_19", new trsuperobject(23,36,19,"Hani nerede Hamanın, Arpatın ilahları? Sefarvayimin ilahları nerede? Samiriyeyi elimden kurtarabildiler mi?"));
trsuperobject.biblemap.put("b23_36_20", new trsuperobject(23,36,20,"Bütün bu ülkelerin ilahlarından hangisi ülkesini elimden kurtardı ki, RAB Yeruşalimi elimden kurtarabilsin?›› halk (bkz. 2Kr.17:24,31; 18:34)."));
trsuperobject.biblemap.put("b23_36_21", new trsuperobject(23,36,21,"Herkes sustu, komutana tek sözle bile karşılık veren olmadı. Çünkü Kral Hizkiya, ‹‹Karşılık vermeyin›› diye buyurmuştu."));
trsuperobject.biblemap.put("b23_36_22", new trsuperobject(23,36,22,"Sonra saray sorumlusu Hilkiya oğlu Elyakim, Yazman Şevna ve devlet tarihçisi Asaf oğlu Yoah giysilerini yırttılar ve gidip komutanın söylediklerini Hizkiya'ya bildirdiler."));
trsuperobject.biblemap.put("b23_37_1", new trsuperobject(23,37,1,"Kral Hizkiya olanları duyunca giysilerini yırttı, çul kuşanıp RABbin Tapınağına girdi."));
trsuperobject.biblemap.put("b23_37_2", new trsuperobject(23,37,2,"Saray sorumlusu Elyakimi, Yazman Şevnayı ve ileri gelen kâhinleri Amots oğlu Peygamber Yeşayaya gönderdi. Hepsi çul kuşanmıştı."));
trsuperobject.biblemap.put("b23_37_3", new trsuperobject(23,37,3,"Yeşayaya şöyle dediler: ‹‹Hizkiya diyor ki, ‹Bugün sıkıntı, azar ve utanç günü. Çünkü çocukların doğum vakti geldi, ama doğuracak güç yok."));
trsuperobject.biblemap.put("b23_37_4", new trsuperobject(23,37,4,"Yaşayan Tanrıyı aşağılamak için efendisi Asur Kralının gönderdiği komutanın söylediklerini belki Tanrın RAB duyar da duyduğu sözlerden ötürü onları cezalandırır. Bu nedenle sağ kalanlarımız için dua et.››"));
trsuperobject.biblemap.put("b23_37_5-6", new trsuperobject(23,37,5-6,"Yeşaya, Kral Hizkiyadan gelen görevlilere şöyle dedi: ‹‹Efendinize şunları söyleyin: ‹RAB diyor ki, Asur Kralının adamlarından benimle ilgili duyduğunuz küfürlerden korkma."));
trsuperobject.biblemap.put("b23_37_7", new trsuperobject(23,37,7,"Onun içine öyle bir ruh koyacağım ki, bir haber üzerine kendi ülkesine dönecek. Orada onu kılıçla öldürteceğim.››"));
trsuperobject.biblemap.put("b23_37_8", new trsuperobject(23,37,8,"Komutan, Asur Kralının Lakişten ayrılıp Livnaya karşı savaştığını duydu. Krala danışmak için oraya gitti."));
trsuperobject.biblemap.put("b23_37_9", new trsuperobject(23,37,9,"Kûş Kralı Tirhakanın kendisiyle savaşmak üzere yola çıktığını haber alan Asur Kralı, Hizkiyaya ulaklar göndererek şöyle dedi:"));
trsuperobject.biblemap.put("b23_37_10", new trsuperobject(23,37,10,"‹‹Yahuda Kralı Hizkiyaya deyin ki, ‹Güvendiğin Tanrın, Yeruşalim Asur Kralının eline teslim edilmeyecek diyerek seni aldatmasın."));
trsuperobject.biblemap.put("b23_37_11", new trsuperobject(23,37,11,"Asur krallarının bütün ülkelere neler yaptığını, onları nasıl yerle bir ettiğini duymuşsundur. Sen kurtulacağını mı sanıyorsun?"));
trsuperobject.biblemap.put("b23_37_12", new trsuperobject(23,37,12,"Atalarımın yok ettiği ulusları -Gozanlıları, Harranlıları, Reseflileri, Telassarda yaşayan Edenlileri- ilahları kurtarabildi mi?"));
trsuperobject.biblemap.put("b23_37_13", new trsuperobject(23,37,13,"Hani nerede Hama ve Arpat kralları? Lair, Sefarvayim, Hena, İvva kralları nerede?››"));
trsuperobject.biblemap.put("b23_37_14", new trsuperobject(23,37,14,"Hizkiya mektubu ulakların elinden alıp okuduktan sonra RABbin Tapınağına çıktı. RABbin önünde mektubu yere yayarak"));
trsuperobject.biblemap.put("b23_37_15", new trsuperobject(23,37,15,"şöyle dua etti:"));
trsuperobject.biblemap.put("b23_37_16", new trsuperobject(23,37,16,"‹‹Ey Keruvlar arasında taht kuran İsrailin Tanrısı, Her Şeye Egemen RAB, bütün dünya krallıklarının tek Tanrısı sensin. Yeri, göğü sen yarattın."));
trsuperobject.biblemap.put("b23_37_17", new trsuperobject(23,37,17,"Ya RAB, kulak ver de işit, gözlerini aç da gör, ya RAB; Sanheribin söylediklerini, yaşayan Tanrıyı nasıl aşağıladığını duy."));
trsuperobject.biblemap.put("b23_37_18", new trsuperobject(23,37,18,"Ya RAB, gerçek şu ki, Asur kralları bütün ulusları ve ülkelerini viraneye çevirdiler."));
trsuperobject.biblemap.put("b23_37_19", new trsuperobject(23,37,19,"İlahlarını yakıp yok ettiler. Çünkü onlar tanrı değil, insan eliyle biçimlendirilmiş tahta ve taşlardı."));
trsuperobject.biblemap.put("b23_37_20", new trsuperobject(23,37,20,"Ya RAB Tanrımız, şimdi bizi Sanheribin elinden kurtar ki, bütün dünya krallıkları senin tek RAB olduğunu anlasın.››"));
trsuperobject.biblemap.put("b23_37_21-22", new trsuperobject(23,37,21-22,"Bunun üzerine Amots oğlu Yeşaya, Hizkiyaya şu haberi gönderdi: ‹‹İsrailin Tanrısı RAB şöyle diyor: ‹Asur Kralı Sanherible ilgili olarak bana yalvardığın için diyorum ki,  ‹‹Erden kız Siyon seni hor görüyor,Alay ediyor seninle.Yeruşalim kızı ardından alayla baş sallıyor."));
trsuperobject.biblemap.put("b23_37_23", new trsuperobject(23,37,23,"Sen kimi aşağıladın, kime küfrettin?Kime sesini yükselttin?İsrailin Kutsalına tepeden baktın!"));
trsuperobject.biblemap.put("b23_37_24", new trsuperobject(23,37,24,"Uşakların aracılığıyla Rabbi aşağıladın.Bir sürü savaş arabamla dağların tepesine,Lübnanın doruklarına çıktım, dedin.Yüksek sedir ağaçlarını, seçme çamlarını kestim,Lübnanın en uzak tepelerine,Gür ormanlarına ulaştım."));
trsuperobject.biblemap.put("b23_37_25", new trsuperobject(23,37,25,"Kuyular kazdım, sular içtim,Mısırın bütün ırmaklarını ayağımın tabanıyla kuruttum, dedin."));
trsuperobject.biblemap.put("b23_37_26", new trsuperobject(23,37,26,"‹‹Bütün bunları çoktan yaptığımı,Çok önceden tasarladığımı duymadın mı?Surlu kentleri enkaz yığınlarına çevirmeniŞimdi ben gerçekleştirdim."));
trsuperobject.biblemap.put("b23_37_27", new trsuperobject(23,37,27,"O kentlerde yaşayanların kolu kanadı kırıldı.Yılgınlık ve utanç içindeydiler;Kır otuna, körpe filizlere,Damlarda büyümeden kavrulup giden ota döndüler."));
trsuperobject.biblemap.put("b23_37_28", new trsuperobject(23,37,28,"Senin oturuşunu, kalkışını,Ne zaman gidip geldiğini,Bana nasıl öfkelendiğini biliyorum."));
trsuperobject.biblemap.put("b23_37_29", new trsuperobject(23,37,29,"Bana duyduğun öfkeden,Kulağıma erişen küstahlığından ötürüHalkamı burnuna, gemimi ağzına takacak,Seni geldiğin yoldan geri çevireceğim."));
trsuperobject.biblemap.put("b23_37_30", new trsuperobject(23,37,30,"‹‹Senin için belirti şu olacak, ey Hizkiya:Bu yıl kendiliğinden yetişeni yiyeceksiniz,İkinci yıl ise ardından biteni.Üçüncü yıl ekip biçin,Bağlar dikip ürününü yiyin."));
trsuperobject.biblemap.put("b23_37_31", new trsuperobject(23,37,31,"Yahudalıların kurtulup sağ kalanlarıYine aşağıya doğru kök salacak,Yukarıya doğru meyve verecek."));
trsuperobject.biblemap.put("b23_37_32", new trsuperobject(23,37,32,"Çünkü sağ kalanlar Yeruşalimden,Kurtulanlar Siyon Dağından çıkacak.Her Şeye Egemen RABbin gayretiyle olacak bu.›"));
trsuperobject.biblemap.put("b23_37_33", new trsuperobject(23,37,33,"‹‹Bundan dolayı RAB Asur Kralına ilişkin şöyle diyor:‹Bu kente girmeyecek, ok atmayacak.Kente kalkanla yaklaşmayacak,Karşısında rampa kurmayacak."));
trsuperobject.biblemap.put("b23_37_34", new trsuperobject(23,37,34,"Geldiği yoldan dönecek ve kente girmeyecek› diyor RAB,"));
trsuperobject.biblemap.put("b23_37_35", new trsuperobject(23,37,35,"‹Kendim için ve kulum Davutun hatırı içinBu kenti savunup kurtaracağım› diyor.››"));
trsuperobject.biblemap.put("b23_37_36", new trsuperobject(23,37,36,"RABbin meleği gidip Asur ordugahında yüz seksen beş bin kişiyi öldürdü. Ertesi sabah uyananlar salt cesetlerle karşılaştılar."));
trsuperobject.biblemap.put("b23_37_37", new trsuperobject(23,37,37,"Bunun üzerine Asur Kralı Sanherib ordugahını bırakıp çekildi. Ninovaya döndü ve orada kaldı."));
trsuperobject.biblemap.put("b23_37_38", new trsuperobject(23,37,38,"Bir gün ilahı Nisrok'un tapınağında tapınırken, oğullarından Adrammelek'le Şareser, onu kılıçla öldürüp Ararat ülkesine kaçtılar. Yerine oğlu Esarhaddon kral oldu."));
trsuperobject.biblemap.put("b23_38_1", new trsuperobject(23,38,1,"O günlerde Hizkiya ölümcül bir hastalığa yakalandı. Amots oğlu Peygamber Yeşaya ona gidip şöyle dedi: ‹‹RAB diyor ki, ‹Ev işlerini düzene sok. Çünkü iyileşmeyecek, öleceksin.››"));
trsuperobject.biblemap.put("b23_38_2", new trsuperobject(23,38,2,"Hizkiya yüzünü duvara dönüp RABbe yalvardı:"));
trsuperobject.biblemap.put("b23_38_3", new trsuperobject(23,38,3,"‹‹Ya RAB, yürekten bir sadakatle önünde nasıl yaşadığımı, gözünde iyi olanı yaptığımı anımsa lütfen.›› Sonra acı acı ağlamaya başladı."));
trsuperobject.biblemap.put("b23_38_4", new trsuperobject(23,38,4,"Bunun üzerine RAB Yeşayaya seslendi:"));
trsuperobject.biblemap.put("b23_38_5", new trsuperobject(23,38,5,"‹‹Git, Hizkiyaya şunu söyle: ‹Atan Davutun Tanrısı RAB diyor ki: Duanı işittim, gözyaşlarını gördüm. Bak, ömrünü on beş yıl daha uzatacağım."));
trsuperobject.biblemap.put("b23_38_6", new trsuperobject(23,38,6,"Bu kenti savunacak, seni de kenti de Asur Kralının elinden kurtaracağım."));
trsuperobject.biblemap.put("b23_38_7", new trsuperobject(23,38,7,"Sözümü gerçekleştireceğime ilişkin sana vereceğim belirti şu olacak:"));
trsuperobject.biblemap.put("b23_38_9", new trsuperobject(23,38,9,"Yahuda Kralı Hizkiya hastalanıp iyileştikten sonra şunları yazdı:"));
trsuperobject.biblemap.put("b23_38_10", new trsuperobject(23,38,10,"‹‹Hayatımın baharında ölüler diyarının kapılarından geçipÖmrümün geri kalan yıllarından yoksun mu kalmalıyım?›› demiştim,"));
trsuperobject.biblemap.put("b23_38_11", new trsuperobject(23,38,11,"‹‹Yaşayanlar diyarında RABbi, evet, RABbi bir daha görmeyeceğim,Bu dünyada yaşayanlar gibi insan yüzü görmeyeceğim bir daha."));
trsuperobject.biblemap.put("b23_38_12", new trsuperobject(23,38,12,"Evim bir çoban çadırı gibi bozuldu, alındı elimden.Dokumacı gibi dürdüm yaşamımı, RAB tezgahtan beni kesti,Bir gün içinde sonumu getiriverdi."));
trsuperobject.biblemap.put("b23_38_13", new trsuperobject(23,38,13,"Sabırla bekledim sabaha kadar,RAB bir aslan gibi kırdı bütün kemiklerimi,Bir gün içinde sonumu getiriverdi."));
trsuperobject.biblemap.put("b23_38_14", new trsuperobject(23,38,14,"Kırlangıç gibi, turna gibi acı acı öttüm,Güvercin gibi inledim, gözlerim yoruldu yukarı bakmaktan.Ya Rab, eziyet çekiyorum,Yardım et bana."));
trsuperobject.biblemap.put("b23_38_15", new trsuperobject(23,38,15,"‹‹Ne diyeyim? Bana seslenen de bunu yapan da Rabdir.Tattığım bu acılardan sonra daha dikkatli yaşayacağım."));
trsuperobject.biblemap.put("b23_38_16", new trsuperobject(23,38,16,"Ya Rab, insanlar bunlarla yaşarlar.Canım da bunların sayesinde yaşıyor.İyileştirdin, yaşattın beni!"));
trsuperobject.biblemap.put("b23_38_17", new trsuperobject(23,38,17,"Çektiğim bunca acı esenlik bulmam içindi.Beni sevdiğin için yıkım çukuruna düşmekten alıkoydun,Günahlarımı arkana attın."));
trsuperobject.biblemap.put("b23_38_18", new trsuperobject(23,38,18,"Çünkü ölüler diyarı sana şükredemez,Ölüm övgüler sunmaz sana.Ölüm çukuruna inenler senin sadakatine umut bağlayamaz."));
trsuperobject.biblemap.put("b23_38_19", new trsuperobject(23,38,19,"Diriler, yalnız dirilerBugün benim yaptığım gibi sana şükreder;Babalar senin sadakatini çocuklarına anlatır."));
trsuperobject.biblemap.put("b23_38_20", new trsuperobject(23,38,20,"Beni kurtaracak olan RABdir.Ömrümüz boyunca Onun tapınağındaTelli çalgılarımızı çalacağız.››"));
trsuperobject.biblemap.put("b23_38_21", new trsuperobject(23,38,21,"Yeşaya, ‹‹İncir pestili getirin, Hizkiyanın çıbanına koyun, iyileşir›› demişti."));
trsuperobject.biblemap.put("b23_38_22", new trsuperobject(23,38,22,"Hizkiya da, ‹‹RAB'bin Tapınağı'na çıkacağıma ilişkin belirti nedir?›› diye sormuştu."));
trsuperobject.biblemap.put("b23_39_1", new trsuperobject(23,39,1,"O sırada Hizkiyanın hastalanıp iyileştiğini duyan Baladan oğlu Babil Kralı Merodak-Baladan, ona mektuplarla birlikte bir armağan gönderdi."));
trsuperobject.biblemap.put("b23_39_2", new trsuperobject(23,39,2,"Bunlar Hizkiyayı sevindirdi. O da deposundaki bütün değerli eşyaları -altını, gümüşü, baharatı, değerli yağı, silah deposunu ve hazine odalarındaki her şeyi- elçilere gösterdi. Sarayında da krallığında da onlara göstermediği hiçbir şey kalmadı."));
trsuperobject.biblemap.put("b23_39_3", new trsuperobject(23,39,3,"Peygamber Yeşaya Kral Hizkiyaya gidip, ‹‹Bu adamlar sana ne dediler, nereden gelmişler?›› diye sordu. Hizkiya, ‹‹Uzak bir ülkeden, Babilden gelmişler›› diye karşılık verdi."));
trsuperobject.biblemap.put("b23_39_4", new trsuperobject(23,39,4,"Yeşaya, ‹‹Sarayında ne gördüler?›› diye sordu. Hizkiya, ‹‹Sarayımdaki her şeyi gördüler, hazinelerimde onlara göstermediğim hiçbir şey kalmadı›› diye yanıtladı."));
trsuperobject.biblemap.put("b23_39_5", new trsuperobject(23,39,5,"Bunun üzerine Yeşaya şöyle dedi: ‹‹Her Şeye Egemen RABbin sözüne kulak ver."));
trsuperobject.biblemap.put("b23_39_7", new trsuperobject(23,39,7,"Soyundan gelen bazı çocuklar alınıp götürülecek, Babil Kralının sarayında hadım edilecek.››"));
trsuperobject.biblemap.put("b23_39_8", new trsuperobject(23,39,8,"Hizkiya, ‹‹RAB'den ilettiğin bu söz iyi›› dedi. Çünkü, ‹‹Nasıl olsa yaşadığım sürece barış ve güvenlik olacak›› diye düşünüyordu."));
trsuperobject.biblemap.put("b23_40_1", new trsuperobject(23,40,1,"‹‹Avutun halkımı›› diyor Tanrınız,‹‹Avutun!"));
trsuperobject.biblemap.put("b23_40_2", new trsuperobject(23,40,2,"Yeruşalim halkına dokunaklı sözler söyleyin.Angaryanın bittiğini,Suçlarının cezasını ödediklerini,Günahlarının cezasını RABbin elindenİki katıyla aldıklarını ilan edin.››"));
trsuperobject.biblemap.put("b23_40_3", new trsuperobject(23,40,3,"Şöyle haykırıyor bir ses:‹‹Çölde RABbin yolunu hazırlayın,Bozkırda Tanrımız için düz bir yol açın. hazırlayın, bozkırda Tanrımız için düz bir yol açın›› ya da ‹‹Çölde haykıran, ‹RABbin yolunu hazırlayın, geçeceği patikaları düzleyin› diye sesleniyor››."));
trsuperobject.biblemap.put("b23_40_4", new trsuperobject(23,40,4,"Her vadi yükseltilecek,Her dağ, her tepe alçaltılacak.Böylelikle engebeler düzleştirilecek,Sarp yerler ovaya dönüştürülecek."));
trsuperobject.biblemap.put("b23_40_5", new trsuperobject(23,40,5,"O zaman RABbin yüceliği görünecek,Bütün insanlar hep birlikte onu görecek.Bunu söyleyen RABdir.››"));
trsuperobject.biblemap.put("b23_40_6", new trsuperobject(23,40,6,"Ses, ‹‹Duyur›› diyor.‹‹Neyi duyurayım?›› diye soruyorum.‹‹İnsan soyu ota benzer,Bütün vefası kır çiçeği gibidir. metin ‹‹Sordu››."));
trsuperobject.biblemap.put("b23_40_8", new trsuperobject(23,40,8,"Ot kurur, çiçek solar,Ama Tanrımızın sözü sonsuza dek durur.››"));
trsuperobject.biblemap.put("b23_40_9", new trsuperobject(23,40,9,"Ey Siyona müjde getiren,Yüksek dağa çık!Ey Yeruşalime müjde getiren,Yükselt sesini, bağır,Sesini yükselt, korkma.Yahuda kentlerine, ‹‹İşte, Tanrınız!›› de. Ey Yeruşalime müjde getiren›› ya da ‹‹Ey müjde getiren Siyon halkı, yüksek dağa çık! Ey müjde getiren Yeruşalim halkı››."));
trsuperobject.biblemap.put("b23_40_10", new trsuperobject(23,40,10,"İşte Egemen RAB gücüyle geliyor,Kudretiyle egemenlik sürecek.Ücreti kendisiyle birlikte,Ödülü önündedir."));
trsuperobject.biblemap.put("b23_40_11", new trsuperobject(23,40,11,"Sürüsünü çoban gibi güdecek,Kollarına alacak kuzuları,Bağrında taşıyacak;Usul usul yol gösterecek emziklilere."));
trsuperobject.biblemap.put("b23_40_12", new trsuperobject(23,40,12,"Kim denizleri avucuyla,Gökleri karışıyla ölçebildi?Yerin toprağını ölçeğe sığdıran,Dağları kantarla,Tepeleri teraziyle tartabilen var mı?"));
trsuperobject.biblemap.put("b23_40_13", new trsuperobject(23,40,13,"RABbin düşüncesine kim akıl erdirebildi?Ona öğüt verip öğretebilen var mı?"));
trsuperobject.biblemap.put("b23_40_14", new trsuperobject(23,40,14,"Akıl almak, adalet yolunu öğrenmek içinRAB kime danıştı ki?Ona bilgi veren, anlayış yolunu bildiren var mı?"));
trsuperobject.biblemap.put("b23_40_15", new trsuperobject(23,40,15,"RAB için uluslar kovada bir damla su,Terazideki toz zerreciği gibidir.Adaları ince toz gibi tartar."));
trsuperobject.biblemap.put("b23_40_16", new trsuperobject(23,40,16,"Adakları yakmaya yetmez Lübnan ormanı,Yakmalık sunu için az gelir hayvanları."));
trsuperobject.biblemap.put("b23_40_17", new trsuperobject(23,40,17,"RABbin önünde bütün uluslar bir hiç gibidir,Hiçten bile aşağı, değersiz sayılır."));
trsuperobject.biblemap.put("b23_40_18", new trsuperobject(23,40,18,"Öyleyse Tanrıyı kime benzeteceksiniz?Neyle karşılaştıracaksınız Onu?"));
trsuperobject.biblemap.put("b23_40_19", new trsuperobject(23,40,19,"Putu döküm işçisi yapar,Kuyumcu altınla kaplar,Gümüş zincirler döker."));
trsuperobject.biblemap.put("b23_40_20", new trsuperobject(23,40,20,"Böyle bir sunuya gücü yetmeyen yoksul kişiÇürümez bir ağaç parçası seçer.Yerinden kımıldamaz bir put yapsın diyeUsta bir işçi arar."));
trsuperobject.biblemap.put("b23_40_21", new trsuperobject(23,40,21,"Bilmiyor musunuz, duymadınız mı?Başlangıçtan beri size bildirilmedi mi?Dünyanın temelleri atılalı beri anlamadınız mı?"));
trsuperobject.biblemap.put("b23_40_22", new trsuperobject(23,40,22,"Gökkubbenin üstünde oturan RABdir,Yeryüzünde yaşayanlarsa çekirge gibidir.Gökleri perde gibi geren,Oturmak için çadır gibi kuran Odur."));
trsuperobject.biblemap.put("b23_40_23", new trsuperobject(23,40,23,"Odur önderleri bir hiç eden,Dünyanın egemenlerini sıfıra indirgeyen."));
trsuperobject.biblemap.put("b23_40_24", new trsuperobject(23,40,24,"O önderler ki, yeni dikilmiş, yeni ekilmiş ağaç gibi,Gövdeleri yere yeni kök salmışkenRABbin soluğu onları kurutuverir,Kasırga saman gibi savurur."));
trsuperobject.biblemap.put("b23_40_25", new trsuperobject(23,40,25,"‹‹Beni kime benzeteceksiniz ki,Eşitim olsun?›› diyor Kutsal Olan."));
trsuperobject.biblemap.put("b23_40_26", new trsuperobject(23,40,26,"Başınızı kaldırıp göklere bakın.Kim yarattı bütün bunları?Yıldızları sırayla görünür kılıyor,Her birini adıyla çağırıyor.Büyük kudreti, üstün gücü sayesinde hepsi yerli yerinde duruyor."));
trsuperobject.biblemap.put("b23_40_27", new trsuperobject(23,40,27,"Ey Yakup soyu, ey İsrail!Neden, ‹‹RAB başıma gelenleri görmüyor,Tanrı hakkımı gözetmiyor?›› diye yakınıyorsun?"));
trsuperobject.biblemap.put("b23_40_28", new trsuperobject(23,40,28,"Bilmiyor musun, duymadın mı?Ebedi Tanrı, RAB, bütün dünyayı yaratan,Ne yorulur ne de zayıflar,Onun bilgisi kavranamaz."));
trsuperobject.biblemap.put("b23_40_29", new trsuperobject(23,40,29,"Yorulanı güçlendirir,Takati olmayanın kudretini artırır."));
trsuperobject.biblemap.put("b23_40_30", new trsuperobject(23,40,30,"Gençler bile yorulup zayıf düşer,Yiğitler tökezleyip düşerler."));
trsuperobject.biblemap.put("b23_40_31", new trsuperobject(23,40,31,"RAB'be umut bağlayanlarsa taze güce kavuşur,Kanat açıp yükselirler kartallar gibi.Koşar ama zayıf düşmez,Yürür ama yorulmazlar."));
trsuperobject.biblemap.put("b23_41_2", new trsuperobject(23,41,2,"‹‹Doğudan adaleti harekete geçiren,Hizmete koşan kim?Ulusları önüne katıyor, krallara baş eğdiriyor.Kılıcıyla toz ediyor onları,Yayıyla savrulan samana çeviriyor. simgeliyor (bkz. 45:1)."));
trsuperobject.biblemap.put("b23_41_3", new trsuperobject(23,41,3,"Kovalıyor onları,Ayak basmadığı bir yoldan esenlikle geçiyor."));
trsuperobject.biblemap.put("b23_41_4", new trsuperobject(23,41,4,"Bunları yapıp gerçekleştiren,Kuşakları başlangıçtan beri çağıran kim?Ben RAB, ilkim; sonuncularla da yine Ben olacağım.››"));
trsuperobject.biblemap.put("b23_41_5", new trsuperobject(23,41,5,"Kıyı halkları bunu görüp korktu.Dünyanın dört bucağı titriyor.Yaklaşıyor, geliyorlar."));
trsuperobject.biblemap.put("b23_41_6", new trsuperobject(23,41,6,"Herkes komşusuna yardım ediyor,Kardeşine, ‹‹Güçlü ol›› diyor."));
trsuperobject.biblemap.put("b23_41_7", new trsuperobject(23,41,7,"Zanaatçı kuyumcuyu yüreklendiriyor,Madeni çekiçle düzleyen,‹‹Lehim iyi oldu›› diyerek örse vuranı yüreklendiriyor.Kımıldamasın diye putu yerine çiviliyor."));
trsuperobject.biblemap.put("b23_41_8", new trsuperobject(23,41,8,"‹‹Ama sen, ey kulum İsrail,Seçtiğim Yakup soyu,Dostum İbrahimin torunları!"));
trsuperobject.biblemap.put("b23_41_9", new trsuperobject(23,41,9,"Sizleri dünyanın dört bucağından topladım,En uzak yerlerden çağırdım.Dedim ki, ‹Sen kulumsun, seni seçtim,Seni reddetmedim.›"));
trsuperobject.biblemap.put("b23_41_10", new trsuperobject(23,41,10,"Korkma, çünkü ben seninleyim,Yılma, çünkü Tanrın benim.Seni güçlendireceğim, evet, sana yardım edeceğim;Zafer kazanan sağ elimle sana destek olacağım."));
trsuperobject.biblemap.put("b23_41_11", new trsuperobject(23,41,11,"‹‹Sana öfkelenenlerin hepsi utanacak, rezil olacak.Sana karşı çıkanlar hiçe sayılıp yok olacak."));
trsuperobject.biblemap.put("b23_41_12", new trsuperobject(23,41,12,"Seninle çekişenleri arasan da bulamayacaksın.Seninle savaşanlar hiçten beter olacak."));
trsuperobject.biblemap.put("b23_41_13", new trsuperobject(23,41,13,"Çünkü sağ elinden tutan,‹Korkma, sana yardım edeceğim› diyen Tanrın RAB benim."));
trsuperobject.biblemap.put("b23_41_14", new trsuperobject(23,41,14,"‹‹Ey Yakup soyu, toprak kurdu,Ey İsrail halkı, korkma!Sana yardım edeceğim›› diyor RAB,Seni kurtaran İsrailin Kutsalı."));
trsuperobject.biblemap.put("b23_41_15", new trsuperobject(23,41,15,"‹‹İşte, seni dişli, keskin, yepyeni bir harman döveni yaptım.Harman edip ufalayacaksın dağları,Tepeleri samana çevireceksin."));
trsuperobject.biblemap.put("b23_41_16", new trsuperobject(23,41,16,"Onları savurduğunda rüzgar alıp götürecek,Darmadağın edecek hepsini kasırga.Sense RABde sevinç bulacak,İsrailin Kutsalıyla övüneceksin."));
trsuperobject.biblemap.put("b23_41_17", new trsuperobject(23,41,17,"‹‹Düşkünlerle yoksullar su arıyor, ama yok.Dilleri kurumuş susuzluktan.Ben RAB, onları yanıtlayacağım,Ben, İsrailin Tanrısı, onları bırakmayacağım."));
trsuperobject.biblemap.put("b23_41_18", new trsuperobject(23,41,18,"Çıplak tepeler üzerinde ırmaklar,Vadilerde su kaynakları yapacağım.Çölü havuza,Kurak toprağı pınara çevireceğim."));
trsuperobject.biblemap.put("b23_41_19", new trsuperobject(23,41,19,"Çölü sedir, akasya,Mersin ve iğde ağaçlarına kavuşturacağım.Bozkıra çam, köknarVe selviyi bir arada dikeceğim."));
trsuperobject.biblemap.put("b23_41_20", new trsuperobject(23,41,20,"Öyle ki, insanlar görüp bilsinler,Hep birlikte düşünüp anlasınlar ki,Bütün bunları RABbin eli yapmış,İsrailin Kutsalı yaratmıştır.››"));
trsuperobject.biblemap.put("b23_41_21", new trsuperobject(23,41,21,"‹‹Davanızı sunun›› diyor RAB,‹‹Kanıtlarınızı ortaya koyun›› diyor Yakupun Kralı,"));
trsuperobject.biblemap.put("b23_41_22", new trsuperobject(23,41,22,"‹‹Putlarınızı getirin de olacakları bildirsinler bize.Olup bitenleri bildirsinler ki düşünelim,Sonuçlarını bilelim.Ya da gelecekte olacakları duyursunlar bize."));
trsuperobject.biblemap.put("b23_41_23", new trsuperobject(23,41,23,"Ey putlar, bundan sonra olacakları bize bildirin de,İlah olduğunuzu bilelim!Haydi bir iyilik ya da kötülük edin de,Hepimiz korkup dehşete düşelim."));
trsuperobject.biblemap.put("b23_41_24", new trsuperobject(23,41,24,"Siz de yaptıklarınız da hiçten betersiniz,Sizi yeğleyen iğrençtir."));
trsuperobject.biblemap.put("b23_41_25", new trsuperobject(23,41,25,"‹‹Kuzeyden birini harekete geçirdim, geliyor,Gün doğusundan beni adımla çağıran biri.Çömlekçinin balçığı çiğnediği gibi,Önderleri çamur gibi çiğneyecek ayağıyla."));
trsuperobject.biblemap.put("b23_41_26", new trsuperobject(23,41,26,"Hanginiz bunu başlangıçtan bildirdi ki, bilelim,Kim önceden bildirdi ki, ‹Haklısın› diyelim?Konuştuğunuzu bildiren deDuyuran daDuyan da olmadı hiç."));
trsuperobject.biblemap.put("b23_41_27", new trsuperobject(23,41,27,"Siyona ilk, ‹İşte, geldiler› diyen benim.Yeruşalime müjdeci gönderdim."));
trsuperobject.biblemap.put("b23_41_28", new trsuperobject(23,41,28,"Bakıyorum, aralarında öğüt verebilecek kimse yok ki,Onlara danışayım, onlar da yanıtlasınlar."));
trsuperobject.biblemap.put("b23_41_29", new trsuperobject(23,41,29,"Hepsi bomboş, yaptıkları da bir hiç.Halkın putları yalnızca yeldir, sıfırdır.››"));
trsuperobject.biblemap.put("b23_42_1", new trsuperobject(23,42,1,"‹‹İşte kendisine destek olduğum,Gönlümün hoşnut olduğu seçtiğim kulum!Ruhumu onun üzerine koydum.Adaleti uluslara ulaştıracak."));
trsuperobject.biblemap.put("b23_42_2", new trsuperobject(23,42,2,"Bağırıp çağırmayacak,Sokakta sesini yükseltmeyecek."));
trsuperobject.biblemap.put("b23_42_3", new trsuperobject(23,42,3,"Ezilmiş kamışı kırmayacak,Tüten fitili söndürmeyecek.Adaleti sadakatle ulaştıracak."));
trsuperobject.biblemap.put("b23_42_4", new trsuperobject(23,42,4,"Yeryüzünde adaleti sağlayana dekUmudunu, cesaretini yitirmeyecek.Kıyı halkları onun yasasına umut bağlayacak.››"));
trsuperobject.biblemap.put("b23_42_5", new trsuperobject(23,42,5,"Gökleri yaratıp geren,Yeryüzünü ve ürününü seren,Dünyadaki insanlara soluk,Orada yaşayanlara ruh veren RAB Tanrı diyor ki,"));
trsuperobject.biblemap.put("b23_42_6", new trsuperobject(23,42,6,"‹‹Ben, RAB, seni doğrulukla çağırdım,Elinden tutacak,Seni koruyacağım.Seni halka antlaşma,Uluslara ışık yapacağım."));
trsuperobject.biblemap.put("b23_42_7", new trsuperobject(23,42,7,"Öyle ki, kör gözleri açasın,Zindandaki tutsakları,Cezaevi karanlığında yaşayanları özgür kılasın."));
trsuperobject.biblemap.put("b23_42_8", new trsuperobject(23,42,8,"‹‹Ben RABbim, adım budur.Onurumu bir başkasına,Övgülerimi putlara bırakmam."));
trsuperobject.biblemap.put("b23_42_9", new trsuperobject(23,42,9,"Bakın, önceden bildirdiklerim gerçekleşti.Şimdi de yenilerini bildiriyorum;Bunlar ortaya çıkmadan önce size duyuruyorum.››"));
trsuperobject.biblemap.put("b23_42_10", new trsuperobject(23,42,10,"Ey denizlere açılanlar ve denizlerdeki her şey,Kıyılar ve kıyı halkları,RABbe yeni bir ilahi söyleyin,Dünyanın dört bucağından Onu ezgilerle övün."));
trsuperobject.biblemap.put("b23_42_11", new trsuperobject(23,42,11,"Bozkır ve bozkırdaki kentler,Kedar köylerinde yaşayan halkSesini yükseltsin.Selada oturanlar sevinçle haykırsın,Bağırsın dağların doruklarından."));
trsuperobject.biblemap.put("b23_42_12", new trsuperobject(23,42,12,"Hepsi RABbi onurlandırsın,Kıyı halkları Onu övsün."));
trsuperobject.biblemap.put("b23_42_13", new trsuperobject(23,42,13,"Yiğit gibi çıkagelecek RAB,Savaşçı gibi gayrete gelecek.Bağırıp savaş çığlığı atacak,Düşmanlarına üstünlüğünü gösterecek."));
trsuperobject.biblemap.put("b23_42_14", new trsuperobject(23,42,14,"‹‹Uzun zamandır ses çıkarmadım,Sustum, kendimi tuttum.Ama şimdi feryat edeceğim doğuran kadın gibi,Nefesim tutulacak, kesik kesik soluyacağım."));
trsuperobject.biblemap.put("b23_42_15", new trsuperobject(23,42,15,"Harap edeceğim dağları, tepeleri,Bütün yeşilliklerini kurutacağım.Irmakları adalara çevirip havuzları kurutacağım."));
trsuperobject.biblemap.put("b23_42_16", new trsuperobject(23,42,16,"Körlere bilmedikleri yolda rehberlik edeceğim,Onlara kılavuz olacağım bilmedikleri yollarda,Karanlığı önlerinde ışığa,Engebeleri düzlüğe çevireceğim.Yerine getireceğim sözler bunlardır.Onlardan geri dönmem."));
trsuperobject.biblemap.put("b23_42_17", new trsuperobject(23,42,17,"Oyma putlara güvenenler,Dökme putlara, ‹İlahlarımız sizsiniz› diyenlerseGeri döndürülüp büsbütün utandırılacaklar.››"));
trsuperobject.biblemap.put("b23_42_18", new trsuperobject(23,42,18,"‹‹Ey sağırlar, işitin,Ey körler, bakın da görün!"));
trsuperobject.biblemap.put("b23_42_19", new trsuperobject(23,42,19,"Kulum kadar kör olan var mı?Gönderdiğim ulak kadar sağır olan var mı?Benimle barışık olan kadar,RABbin kulu kadar kör olan kim var?"));
trsuperobject.biblemap.put("b23_42_20", new trsuperobject(23,42,20,"Pek çok şey gördünüz, ama aldırmıyorsunuz,Kulaklarınız açık, ama işitmiyorsunuz.››"));
trsuperobject.biblemap.put("b23_42_21", new trsuperobject(23,42,21,"Kendi doğruluğu uğruna Kutsal YasayıBüyük ve yüce kılmak RABbi hoşnut etti."));
trsuperobject.biblemap.put("b23_42_22", new trsuperobject(23,42,22,"Ama bu yağmalanmış, soyulmuş bir halktır.Hepsi deliklere, cezaevlerine kapatılmışlardır.Yağmalanmak için varlar, kurtaran yok.Soyulmak içinler, ‹‹Geri verin›› diyen yok."));
trsuperobject.biblemap.put("b23_42_23", new trsuperobject(23,42,23,"Hanginiz kulak verecek?Gelecekte kim can kulağıyla dinleyecek?"));
trsuperobject.biblemap.put("b23_42_24", new trsuperobject(23,42,24,"Yakup soyunun soyulmasına,İsrailin yağmalanmasına kim olur verdi?Kendisine karşı günah işlediğimiz RAB değil mi?Çünkü Onun yolunda yürümek istemediler,Yasasına kulak asmadılar."));
trsuperobject.biblemap.put("b23_42_25", new trsuperobject(23,42,25,"Bu yüzden kızgın öfkesini,Savaşın şiddetini üzerlerine yağdırdı.Ama ateş çemberi içinde olduklarını farketmediler,Aldırmadılar kendilerini yakıp bitiren ateşe."));
trsuperobject.biblemap.put("b23_43_1", new trsuperobject(23,43,1,"Ey Yakup soyu, seni yaratan,Ey İsrail, sana biçim veren RAB şimdi şöyle diyor:‹‹Korkma, çünkü seni kurtardım,Seni adınla çağırdım, sen benimsin."));
trsuperobject.biblemap.put("b23_43_2", new trsuperobject(23,43,2,"Suların içinden geçerken seninle olacağım,Irmakların içinden geçerken su boyunu aşmayacak.Ateşin içinde yürürken yanmayacaksın,Alevler seni yakmayacak."));
trsuperobject.biblemap.put("b23_43_3", new trsuperobject(23,43,3,"Çünkü senin Tanrın, İsrailin Kutsalı,Seni kurtaran RAB benim.Fidyen olarak Mısırı,Sana karşılık Kûş ve Seva diyarlarını verdim."));
trsuperobject.biblemap.put("b23_43_4", new trsuperobject(23,43,4,"Gözümde değerli ve saygın olduğun,Seni sevdiğim için, senin yerine insanlar,Canın karşılığında halklar vereceğim."));
trsuperobject.biblemap.put("b23_43_5", new trsuperobject(23,43,5,"Korkma, çünkü seninleyim,Soyundan olanları doğudan getireceğim,Sizleri de batıdan toplayacağım."));
trsuperobject.biblemap.put("b23_43_6", new trsuperobject(23,43,6,"‹‹Kuzeye, ‹Ver›, güneye, ‹Alıkoyma;Oğullarımı uzaktan,Kızlarımı dünyanın dört bucağından getir› diyeceğim."));
trsuperobject.biblemap.put("b23_43_7", new trsuperobject(23,43,7,"‹Yüceliğim için yaratıp biçim verdiğim,Adımla çağrılan herkesi,Evet, oluşturduğum herkesi getirin› diyeceğim.››"));
trsuperobject.biblemap.put("b23_43_8", new trsuperobject(23,43,8,"Gözleri olduğu halde kör,Kulakları olduğu halde sağır olan halkı öne getir."));
trsuperobject.biblemap.put("b23_43_9", new trsuperobject(23,43,9,"Bütün uluslar bir araya gelsin, halklar toplansın.İçlerinden hangisi bunları bildirebilir,Olup bitenleri bize duyurabilir?Tanıklarını çağırıp haklı olduklarını kanıtlasınlar,Ötekiler de duyup, ‹‹Doğrudur›› desinler."));
trsuperobject.biblemap.put("b23_43_10", new trsuperobject(23,43,10,"‹‹Tanıklarım sizlersiniz›› diyor RAB,‹‹Seçtiğim kullar sizsiniz.Öyle ki beni tanıyıp bana güvenesiniz,Benim O olduğumu anlayasınız.Benden önce bir tanrı olmadı,Benden sonra da olmayacak."));
trsuperobject.biblemap.put("b23_43_11", new trsuperobject(23,43,11,"‹‹Ben, yalnız ben RABbim,Benden başka kurtarıcı yoktur."));
trsuperobject.biblemap.put("b23_43_12", new trsuperobject(23,43,12,"Ben bildirdim, ben kurtardım, ben duyurdum,Aranızdaki yabancı ilahlar değil.Tanıklarım sizsiniz›› diyor RAB,‹‹Tanrı benim,"));
trsuperobject.biblemap.put("b23_43_13", new trsuperobject(23,43,13,"Gün gün olalı ben Oyum.Elimden kimse kurtaramaz.Ben yaparım, kim engel olabilir?››"));
trsuperobject.biblemap.put("b23_43_14", new trsuperobject(23,43,14,"Kurtarıcınız RAB, İsrailin Kutsalı diyor ki,‹‹Uğrunuza Babil üzerine bir ordu göndereceğim.Övündükleri gemilerle kaçan bütün KildanilereBoyun eğdireceğim."));
trsuperobject.biblemap.put("b23_43_15", new trsuperobject(23,43,15,"Kutsalınız, İsrailin Yaratıcısı,Kralınız RAB benim.››"));
trsuperobject.biblemap.put("b23_43_16-17", new trsuperobject(23,43,16-17,"Denizde geçit, azgın sularda yol açan,Atlarla savaş arabalarını,Yiğit savaşçıları ve orduyuYola çıkaran RAB şöyle diyor:‹‹Onlar yattı, kalkamaz oldu,Fitil gibi bastırılıp söndürüldüler."));
trsuperobject.biblemap.put("b23_43_18", new trsuperobject(23,43,18,"‹‹Olup bitenlerin üzerinde durmayın,Düşünmeyin eski olayları."));
trsuperobject.biblemap.put("b23_43_19", new trsuperobject(23,43,19,"Bakın, yeni bir şey yapıyorum!Olmaya başladı bile, farketmiyor musunuz?Çölde yol, kurak topraklarda ırmaklar yapacağım."));
trsuperobject.biblemap.put("b23_43_20", new trsuperobject(23,43,20,"Kır hayvanları, çakallarla baykuşlar beni yüceltecek.Çünkü seçtiğim halkın içmesi için çölde su,Kurak yerlerde ırmaklar sağladım."));
trsuperobject.biblemap.put("b23_43_21", new trsuperobject(23,43,21,"Kendim için biçim verdiğim bu halkBana ait olan övgüleri ilan edecek.››"));
trsuperobject.biblemap.put("b23_43_22", new trsuperobject(23,43,22,"‹‹Ne var ki, ey Yakup soyu,Yakardığın ben değildim,Benden usandın, ey İsrail."));
trsuperobject.biblemap.put("b23_43_23", new trsuperobject(23,43,23,"Yakmalık sunu için bana davar getirmediniz,Kurbanlarınızla beni onurlandırmadınız.Sizi sunularla uğraştırmadım,Günnük isteyerek sizi usandırmadım."));
trsuperobject.biblemap.put("b23_43_24", new trsuperobject(23,43,24,"Benim için güzel kokulu kamış satın almadınız,Doyurmadınız beni kurbanlarınızın yağıyla.Tersine, beni günahlarınızla uğraştırdınız,Suçlarınızla usandırdınız."));
trsuperobject.biblemap.put("b23_43_25", new trsuperobject(23,43,25,"Kendi uğruna suçlarınızı silen benim, evet benim,Günahlarınızı anmaz oldum."));
trsuperobject.biblemap.put("b23_43_26", new trsuperobject(23,43,26,"‹‹Geçmişi bana anımsatın, hesaplaşalım,Haklı çıkmak için davanızı anlatın."));
trsuperobject.biblemap.put("b23_43_27", new trsuperobject(23,43,27,"İlk atanız günah işledi,Sözcüleriniz bana başkaldırdı."));
trsuperobject.biblemap.put("b23_43_28", new trsuperobject(23,43,28,"Bu yüzden tapınak görevlilerini bayağılaştırdım;Yakup soyunu bütünüyle yıkıma,İsrail'i rezilliğe mahkûm ettim.››"));
trsuperobject.biblemap.put("b23_44_1", new trsuperobject(23,44,1,"‹‹Şimdi, ey kulum Yakup soyu,Seçtiğim İsrail halkı, dinle!"));
trsuperobject.biblemap.put("b23_44_2", new trsuperobject(23,44,2,"Seni yaratan, rahimde sana biçim veren,Sana yardım edecek olan RAB şöyle diyor:‹Korkma, ey kulum Yakup soyu,Ey seçtiğim Yeşurun! verilen bir addır."));
trsuperobject.biblemap.put("b23_44_3", new trsuperobject(23,44,3,"‹‹Susamış toprağı sulayacak,Kurumuş toprakta dereler akıtacağım.Çocuklarının üzerine Ruhumu dökecek,Soyunu kutsayacağım."));
trsuperobject.biblemap.put("b23_44_4", new trsuperobject(23,44,4,"Akarsu kıyısında otlar arasında yükselenKavaklar gibi boy atacaklar.›"));
trsuperobject.biblemap.put("b23_44_5", new trsuperobject(23,44,5,"‹‹Kimi, ‹Ben RABbe aitim› diyecek,Kimi Yakup adını alacak,Kimi de eline ‹RABbe ait› yazıpİsrail adını benimseyecek.››"));
trsuperobject.biblemap.put("b23_44_7", new trsuperobject(23,44,7,"Benim gibi olan var mı? Haber versin.Ezeli halkımı var ettiğimden beri olup bitenleri,Bundan sonra olacakları söyleyip sıralasın,Evet, gelecek olayları bildirsin!"));
trsuperobject.biblemap.put("b23_44_8", new trsuperobject(23,44,8,"Yılmayın, korkmayın!Size çok önceden beri söyleyip açıklamadım mı?Tanıklarım sizsiniz.Benden başka Tanrı var mı?Hayır, başka Kaya yok;Ben bir başkasını bilmiyorum.››"));
trsuperobject.biblemap.put("b23_44_9", new trsuperobject(23,44,9,"Putlara biçim verenlerin hepsi boş insanlardır.Değer verdikleri nesneler hiçbir işe yaramaz.Putların tanıkları onlardır;Ne bir şey görür ne de bir şey bilirler.Bunun sonucunda utanç içinde kalacaklar."));
trsuperobject.biblemap.put("b23_44_10", new trsuperobject(23,44,10,"Kim yararsız ilaha biçim vermek,Dökme put yapmak ister?"));
trsuperobject.biblemap.put("b23_44_11", new trsuperobject(23,44,11,"Bakın, bu putlarla uğraşanların hepsi utanacak.Onları yapanlar salt insan.Hepsi toplanıp yargılanmaya gelsin.Dehşete düşecek, utanacaklar birlikte."));
trsuperobject.biblemap.put("b23_44_12", new trsuperobject(23,44,12,"Demirci aletini alır,Kömür ateşinde çalışır,Çekiçle demire biçim verir.Güçlü koluyla onu işler.Acıkır, güçsüz kalır, su içmeyince tükenir."));
trsuperobject.biblemap.put("b23_44_13", new trsuperobject(23,44,13,"Marangoz iple ölçü alır,Tahtayı tebeşirle çizer.Raspayla tahtayı biçimlendirir,Pergelle işaretler, insan biçimi verir.İnsan güzelliğinde,Evde duracak bir put yapar."));
trsuperobject.biblemap.put("b23_44_14", new trsuperobject(23,44,14,"İnsan kendisi için sedir ağaçları keser,Palamut, meşe ağaçları alır.Ormanda kendine bir ağaç seçer.Bir çam diker, ama ağacı büyüten yağmurdur."));
trsuperobject.biblemap.put("b23_44_15", new trsuperobject(23,44,15,"Sonra ağaç odun olarak kullanılır.İnsan aldığı odunla hem ısınır,Hem tutuşturup ekmek pişirir,Hem de bir ilah yapıp tapınır.Yaptığı putun önünde yere kapanır."));
trsuperobject.biblemap.put("b23_44_16", new trsuperobject(23,44,16,"Odunun bir kısmını yakar,Ateşinde et kızartıp karnını doyurur.Isınınca bir oh çeker,‹‹Isındım, ateşin sıcaklığını duyuyorum›› der."));
trsuperobject.biblemap.put("b23_44_17", new trsuperobject(23,44,17,"Artakalan odundan kendine bir ilah,Oyma put yapar;Önünde yere kapanıp ona tapınır,‹‹Beni kurtar, çünkü ilahım sensin›› diye yakarır."));
trsuperobject.biblemap.put("b23_44_18", new trsuperobject(23,44,18,"Böyleleri anlamaz, bilmez.Çünkü gözleri de zihinleri de öylesine kapalı ki,Görmez, anlamazlar."));
trsuperobject.biblemap.put("b23_44_19", new trsuperobject(23,44,19,"Durup düşünmez, bilmez,Anlamazlar ki şöyle desinler:‹‹Odunun bir kısmını yakıpAteşinde ekmek pişirdim, et kızartıp yedim.Artakalanından iğrenç bir şey mi yapayım?Bir odun parçasının önünde yere mi kapanayım?››"));
trsuperobject.biblemap.put("b23_44_20", new trsuperobject(23,44,20,"Külle besleniyorlar.Aldanan yürekleri onları saptırıyor.Canlarını kurtaramaz,‹‹Sağ elimdeki şu nesne aldatıcı değil mi?›› diyemezler."));
trsuperobject.biblemap.put("b23_44_21", new trsuperobject(23,44,21,"‹‹Ey Yakup soyu, ey İsrail,Söylediklerimi anımsayın, çünkü kulumsunuz.Size ben biçim verdim, kulumsunuz;Seni unutmam, ey İsrail."));
trsuperobject.biblemap.put("b23_44_22", new trsuperobject(23,44,22,"İsyanlarınızı bulut gibi,Günahlarınızı sis gibi sildim.Bana dönün, çünkü sizi kurtardım.››"));
trsuperobject.biblemap.put("b23_44_23", new trsuperobject(23,44,23,"Sevinçle haykırın, ey gökler,Çünkü bunu RAB yaptı.Haykırın, ey yerin derinlikleri.Ey dağlar, ey orman, ormandaki her ağaç,Sevinç çığlıklarına katılın.Çünkü RAB Yakup soyunu kurtararakİsrailde görkemini gösterdi."));
trsuperobject.biblemap.put("b23_44_24", new trsuperobject(23,44,24,"Sizi kurtaran,Size rahimde biçim veren RAB diyor ki,‹‹Her şeyi yaratan,Gökleri yalnız başına geren,Yeryüzünü tek başına seren,Sahte peygamberlerin belirtilerini boşa çıkaran,Falcılarla alay eden,Bilgeleri geri çeviren,Bilgilerini saçmalığa dönüştüren,Kulunun sözlerini yerine getiren,Ulaklarının peygamberlik sözlerini gerçekleştiren,Yeruşalim için, ‹İçinde oturulacak›,Yahuda kentleri için, ‹Yeniden kurulacak,Yıkıntılarını onaracağım› diyen;Engine, ‹Kuru! Sularını kurutacağım› diyen,Koreş için, ‹O çobanımdır,Her istediğimi yerine getirecek›,Yeruşalim için, ‹Yeniden kurulacak›,Tapınak için, ‹Temeli atılacak› diyen RAB benim.››"));
trsuperobject.biblemap.put("b23_45_2", new trsuperobject(23,45,2,"‹‹Senin önünsıra gidipDağları düzleyecek,Tunç kapıları kırıpDemir sürgülerini parçalayacağım."));
trsuperobject.biblemap.put("b23_45_3", new trsuperobject(23,45,3,"Seni adınla çağıranınBen RAB, İsrailin Tanrısı olduğumu anlayasın diyeKaranlıkta kalmış hazineleri,Gizli yerlerde saklı zenginlikleri sana vereceğim."));
trsuperobject.biblemap.put("b23_45_4", new trsuperobject(23,45,4,"Sen beni tanımadığın haldeKulum Yakup soyu ve seçtiğim İsrail uğrunaSeni adınla çağırıp onurlu bir unvan vereceğim."));
trsuperobject.biblemap.put("b23_45_6", new trsuperobject(23,45,6,"Öyle ki, doğudan batıya dekBenden başkası olmadığını herkes bilsin.RAB benim, başkası yok."));
trsuperobject.biblemap.put("b23_45_7", new trsuperobject(23,45,7,"Işığı biçimlendiren, karanlığı yapan,Esenliği ve felaketi yaratan,Bütün bunları yapan RAB benim."));
trsuperobject.biblemap.put("b23_45_8", new trsuperobject(23,45,8,"Ey gökler, yukarıdan doğruluk damlatın,Ey bulutlar, doğruluk yağdırın.Toprak yarılsın, kurtuluş meyvesi versin,Onunla birlikte doğruluk yetiştirsin.Bunları yaratan RAB benim.››"));
trsuperobject.biblemap.put("b23_45_9", new trsuperobject(23,45,9,"Kendine biçim verenle çekişenin vay haline!Kil, topraktan yapılmış çömlek parçası,Kendisine biçim verene, ‹‹Ne yapıyorsun?Yarattığın nesnenin tutacağı yok›› diyebilir mi?"));
trsuperobject.biblemap.put("b23_45_10", new trsuperobject(23,45,10,"Babasına, ‹‹Dünyaya ne getirdin?››Ya da annesine, ‹‹Ne biçim şey doğurdun?››Diyenin vay haline!"));
trsuperobject.biblemap.put("b23_45_11", new trsuperobject(23,45,11,"İsrailin Kutsalı,Ona biçim veren RAB diyor ki,‹‹Çocuklarımın geleceği hakkında beni sorgulayabilir,Ellerimin yapıtları hakkında bana buyruk verebilir misiniz?"));
trsuperobject.biblemap.put("b23_45_12", new trsuperobject(23,45,12,"Dünyayı ben yaptım,Üzerindeki insanı ben yarattım.Benim ellerim gerdi gökleri,Bütün gök cisimleri benim buyruğumda."));
trsuperobject.biblemap.put("b23_45_13", new trsuperobject(23,45,13,"Koreşi doğrulukla harekete geçirecek,Yollarını düzleyeceğim.Kentimi o onaracak,Sürgünlerimi ücret ya da ödül almadan o özgür kılacak.››Böyle diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b23_45_14", new trsuperobject(23,45,14,"RAB diyor ki,‹‹Mısırın ürettikleri,Kûşun ticaret gelirleriVe uzun boylu Sevalılar size gelecek, sizin olacak.Zincire vurulmuş olarak ardınızsıra yürüyecekler.Önünüzde yere kapanıp yalvaracaklar:‹Tanrı yalnız sizinledir,Başkası, başka Tanrı yok.››"));
trsuperobject.biblemap.put("b23_45_15", new trsuperobject(23,45,15,"Gerçekten sen kendini gizleyen bir Tanrısın,Ey İsrailin Tanrısı, ey Kurtarıcı!"));
trsuperobject.biblemap.put("b23_45_16", new trsuperobject(23,45,16,"Put yapanların hepsi utandırılacak, rezil olacak.Utanç içinde uzaklaşacaklar."));
trsuperobject.biblemap.put("b23_45_17", new trsuperobject(23,45,17,"Ama İsrail RAB tarafından kurtarılacak,Sonsuza dek sürecek kurtuluşu.Çağlar boyunca utandırılmayacak,Asla rezil olmayacak."));
trsuperobject.biblemap.put("b23_45_18", new trsuperobject(23,45,18,"Çünkü gökleri yaratan RAB,Dünyayı yaratıp biçimlendiren, pekiştiren,Üzerinde yaşanmasın diye değil, yaşansın diyeBiçimlendiren RAB -Tanrı Odur- şöyle diyor:‹‹RAB benim, başkası yok."));
trsuperobject.biblemap.put("b23_45_19", new trsuperobject(23,45,19,"Ben gizlide,Karanlıklar ülkesinin bir köşesinde konuşmadım.Yakup soyuna, ‹Beni olmayacak yerlerde arayın› demedim.Doğru olanı söyleyen, adil olanı bildiren RAB benim.››"));
trsuperobject.biblemap.put("b23_45_20", new trsuperobject(23,45,20,"‹‹Ey sizler, uluslardan kaçıp kurtulanlar,Toplanıp gelin, birlikte yaklaşın!Tahtadan oyma putlar taşıyan,Kurtaramayan ilahlara yakaranlar bilgisizdir."));
trsuperobject.biblemap.put("b23_45_21", new trsuperobject(23,45,21,"Konuşun, davanızı sunun,Birbirinize danışın.Bunları çok önceden duyurup bildiren kim?Ben RAB, bildirmedim mi?Benden başka Tanrı yok, adil Tanrı ve Kurtarıcı benim.Yok benden başkası."));
trsuperobject.biblemap.put("b23_45_22", new trsuperobject(23,45,22,"‹‹Ey dünyanın dört bucağındakiler,Bana dönün, kurtulursunuz.Çünkü Tanrı benim, başkası yok."));
trsuperobject.biblemap.put("b23_45_23", new trsuperobject(23,45,23,"Kendi üzerime ant içtim,Ağzımdan çıkan söz doğrudur, boşa çıkmaz:Her diz önümde çökecek,Her dil bana ant içecek."));
trsuperobject.biblemap.put("b23_45_24", new trsuperobject(23,45,24,"‹‹Benim için şöyle diyecekler:‹Doğruluk ve güç yalnız RABdedir›,İnsanlar Ona gelecek.RABbe öfkelenenlerin hepsi utandırılacak."));
trsuperobject.biblemap.put("b23_45_25", new trsuperobject(23,45,25,"Ama bütün İsrail soyuRAB tarafından aklanacak,O'nunla övünecek."));
trsuperobject.biblemap.put("b23_46_1", new trsuperobject(23,46,1,"‹‹İlah Bel diz çökmüş, ilah Nebo sinmiş,Putları hayvanlara, öküzlere yüklenmiş gidiyor.Taşınan bu nesneleriniz ağırlık,Yorgun hayvana yük oldu."));
trsuperobject.biblemap.put("b23_46_2", new trsuperobject(23,46,2,"Birlikte sinmiş, diz çökmüşler,Putlarını yük olmaktan kurtaramıyorlar.Sürgüne gidecek onlar."));
trsuperobject.biblemap.put("b23_46_3", new trsuperobject(23,46,3,"‹‹Ey Yakup soyu, İsrailin sağ kalanları,Doğdunuz doğalı yüklendiğim,Rahimden çıktınız çıkalı taşıdığım sizler,Dinleyin beni:"));
trsuperobject.biblemap.put("b23_46_4", new trsuperobject(23,46,4,"Siz yaşlanıncaya dek ben Oyum;Saçlarınız ağarıncaya dekBen yükleneceğim sizi.Sizi ben yarattım, ben taşıyacağım,Evet, sizi ben yüklenecek, ben kurtaracağım."));
trsuperobject.biblemap.put("b23_46_5", new trsuperobject(23,46,5,"‹‹Beni kime benzetecek,Kime denk tutacaksınız?Kiminle karşılaştıracaksınız ki, benzer olalım?"));
trsuperobject.biblemap.put("b23_46_6", new trsuperobject(23,46,6,"Kimisi bol keseden harcadığı altından,Terazide tarttığı gümüştenÜcret karşılığında kuyumcuya ilah yaptırır,Önünde yere kapanıp tapınır."));
trsuperobject.biblemap.put("b23_46_7", new trsuperobject(23,46,7,"Onu omuzlayıp taşır, yerine koyar.Öylece durur put, yerinden kımıldamaz.Kendisine yakarana yanıt veremez,Onu sıkıntısından kurtaramaz."));
trsuperobject.biblemap.put("b23_46_8", new trsuperobject(23,46,8,"‹‹Bunu anımsayın, ey başkaldıranlar,Adam olun, aklınızdan çıkarmayın!"));
trsuperobject.biblemap.put("b23_46_9", new trsuperobject(23,46,9,"Çok önceden beri olup bitenleri anımsayın.Çünkü Tanrı benim, başkası yok.Tanrı benim, benzerim yok."));
trsuperobject.biblemap.put("b23_46_10", new trsuperobject(23,46,10,"Sonu ta başlangıçtan,Henüz olmamış olayları çok önceden bildiren,‹Tasarım gerçekleşecek,İstediğim her şeyi yapacağım› diyen benim."));
trsuperobject.biblemap.put("b23_46_11", new trsuperobject(23,46,11,"Doğudan yırtıcı kuşu,Uzak bir ülkedenTasarımı gerçekleştirecek adamı çağıran benim.Evet, bunları söyledim,Kesinlikle yerine getirecek,Tasarladığımı yapacağım mutlaka."));
trsuperobject.biblemap.put("b23_46_12", new trsuperobject(23,46,12,"‹‹Ey dikbaşlılar, doğruluktan uzak olanlar,Dinleyin beni!"));
trsuperobject.biblemap.put("b23_46_13", new trsuperobject(23,46,13,"Zaferim yaklaştı, uzak değil;Kurtarışım gecikmeyecek.Güzelliğim olan İsrail içinSiyon'u kurtaracağım.››"));
trsuperobject.biblemap.put("b23_47_1", new trsuperobject(23,47,1,"‹‹Ey Babil, erden kız,İn aşağı, toprağa otur.Ey Kildani kızı,Tahtın yok artık, yere otur.Bundan böyle, ‹Nazik, narin› demeyecekler sana."));
trsuperobject.biblemap.put("b23_47_2", new trsuperobject(23,47,2,"Bir çift değirmen taşı al da un öğüt,Çıkar peçeni, kaldır eteğini.Baldırını aç, ırmaklardan geç."));
trsuperobject.biblemap.put("b23_47_3", new trsuperobject(23,47,3,"Çıplaklığın sergilenecek, mahrem yerlerin görünecek.Öç alacağım, kimseyi esirgemeyeceğim.››"));
trsuperobject.biblemap.put("b23_47_4", new trsuperobject(23,47,4,"Bizim kurtarıcımızİsrailin Kutsalıdır.Onun adı ‹‹Her Şeye Egemen RABdir!››"));
trsuperobject.biblemap.put("b23_47_6", new trsuperobject(23,47,6,"Halkıma öfkelenmiş,Mirasım olduğu halde onu bayağılaştırıpEline teslim etmiştim.Ama sen onlara acımadın,Yaşlılara bile çok ağır bir boyunduruk yükledin."));
trsuperobject.biblemap.put("b23_47_7", new trsuperobject(23,47,7,"‹Sonsuza dek kraliçe olacağım› diye düşünüyordun,Bunları aklına getirmedin, sonuçlarını düşünmedin."));
trsuperobject.biblemap.put("b23_47_8", new trsuperobject(23,47,8,"‹‹Ey şimdi güvenlikte yaşayan zevk düşkünü,İçinden, ‹Kraliçe benim, başkası yok;Hiç dul kalmayacak,Evlat acısı görmeyeceğim› diyorsun.Dinle şimdi:"));
trsuperobject.biblemap.put("b23_47_9", new trsuperobject(23,47,9,"Bir gün içinde ikisi birden başına gelecek:Çok sayıda büyüye, etkili muskalarına karşınHem dul kalacak,Hem evlat acısını alabildiğine yaşayacaksın."));
trsuperobject.biblemap.put("b23_47_10", new trsuperobject(23,47,10,"‹‹Kötülüğüne güvendin,‹Beni gören yok› diye düşündün.Bilgin ve bilgeliğin seni saptırdı.İçinden, ‹Kraliçe benim, başkası yok› diyordun."));
trsuperobject.biblemap.put("b23_47_11", new trsuperobject(23,47,11,"Ne var ki, felakete uğrayacaksın.Onu durduracak büyü yok elinde,Başına gelecek belayı önleyemeyeceksin.Üzerine ansızın hiç beklemediğin bir yıkım gelecek."));
trsuperobject.biblemap.put("b23_47_12", new trsuperobject(23,47,12,"Gençliğinden beri emek verdiğinMuskalarına, çok sayıda büyüye devam et;Belki yararını görür,Kimilerini titretirsin."));
trsuperobject.biblemap.put("b23_47_13", new trsuperobject(23,47,13,"Aldığın öğütlerin çokluğuSeni tüketti.Yıldız falcıların, yıldızbilimcilerin,Ay başlarında ne olacağını bildirenlerin,Şimdi kalksınlar daBaşına geleceklerden seni kurtarsınlar."));
trsuperobject.biblemap.put("b23_47_14", new trsuperobject(23,47,14,"‹‹Bak, hepsi anızdan farksız,Ateş yakacak onları.Canlarını alevden kurtaramayacaklar.Ne ısınmak için kor,Ne de karşısında oturulacak ateş olacak."));
trsuperobject.biblemap.put("b23_47_15", new trsuperobject(23,47,15,"Emek verdiğin adamlar böyle olacak.Gençliğinden beri alışveriş ettiğin herkesKendi yoluna gidecek,Seni kurtaran olmayacak.››"));
trsuperobject.biblemap.put("b23_48_1", new trsuperobject(23,48,1,"‹‹Dinle, ey Yakup soyu!İsrail adıyla anılan, Yahuda soyundan gelen,RABbin adıyla ant içen sizler,İsrailin Tanrısına yakarır,Ama bunu doğrulukla, içtenlikle yapmazsınız."));
trsuperobject.biblemap.put("b23_48_2", new trsuperobject(23,48,2,"Kutsal kentli olduğunuzu,İsrailin Tanrısına dayandığınızı ileri sürersiniz.Onun adı Her Şeye Egemen RABdir."));
trsuperobject.biblemap.put("b23_48_3", new trsuperobject(23,48,3,"Olup bitenleri çok önceden bildirdim,Ağzımı açıp duyurdum.Ansızın yaptım ve gerçekleştiler."));
trsuperobject.biblemap.put("b23_48_4", new trsuperobject(23,48,4,"İnatçı olduğunuzu,Tunç alınlı, demir boyunlu olduğunuzu bildiğim için"));
trsuperobject.biblemap.put("b23_48_5", new trsuperobject(23,48,5,"Bunları size çok önceden bildirdim,Olmadan önce duyurdum.Yoksa, ‹Bunları yapan putlarımızdır,Olmalarını buyuranOyma ve dökme putlarımızdır› derdiniz."));
trsuperobject.biblemap.put("b23_48_6", new trsuperobject(23,48,6,"Bunları duydunuz, hepsini inceleyin.Peki, kabul etmeyecek misiniz?Şimdiden size yeni şeyler,Bilmediğiniz gizli şeyler açıklayacağım."));
trsuperobject.biblemap.put("b23_48_7", new trsuperobject(23,48,7,"Bunlar şimdi yaratılıyor,Geçmişte değil;Bugüne kadar duymadınız,Yoksa, ‹Bunları biliyorduk› derdiniz."));
trsuperobject.biblemap.put("b23_48_8", new trsuperobject(23,48,8,"Ne duydunuz, ne de anladınız,Öteden beri kulaklarınız tıkalı.Ne denli hain olduğunuzu biliyorum,Doğuştan isyankâr olduğunuz biliniyor."));
trsuperobject.biblemap.put("b23_48_9", new trsuperobject(23,48,9,"Adım uğruna öfkemi geciktiriyorum.Ünümden ötürü kendimi tutuyorum,Yoksa sizi yok ederdim."));
trsuperobject.biblemap.put("b23_48_10", new trsuperobject(23,48,10,"Bakın, gümüşü arıtır gibi olmasa da sizleri arıttım,Sıkıntı ocağında denedim."));
trsuperobject.biblemap.put("b23_48_11", new trsuperobject(23,48,11,"Bunu kendim için, evet, kendim için yapıyorum.Adımı bayağılaştırmanızı nasıl hoş görebilirim?Bana ait olan onuru başkasına vermem.››"));
trsuperobject.biblemap.put("b23_48_12", new trsuperobject(23,48,12,"‹‹Ey Yakup soyu, çağırdığım İsrail, beni dinle:Ben Oyum; ilk Benim, son da Benim."));
trsuperobject.biblemap.put("b23_48_13", new trsuperobject(23,48,13,"Yeryüzünün temelini elimle attım,Gökleri sağ elim gerdi.Onları çağırdığımdaBirlikte önümde dikilirler."));
trsuperobject.biblemap.put("b23_48_14", new trsuperobject(23,48,14,"‹‹Toplanıp dinleyin hepiniz:Putlardan hangisi bunları önceden bildirebildi?RABbin sevdiği kişiOnun Babile karşı tasarladığını yerine getirecek.Gücünü Kildanilere karşı kullanacak."));
trsuperobject.biblemap.put("b23_48_15", new trsuperobject(23,48,15,"Ben, evet, ben söyledim, onu ben çağırdım,Onu getirdim, görevini başaracak."));
trsuperobject.biblemap.put("b23_48_16", new trsuperobject(23,48,16,"‹‹Yaklaşın bana, dinleyin söyleyeceklerimi:Başlangıçtan beri açıkça konuştum,O zamandan bu yana oradayım.››Egemen RAB şimdi beni ve Ruhunu gönderiyor."));
trsuperobject.biblemap.put("b23_48_17", new trsuperobject(23,48,17,"Sizleri kurtaran İsrailin Kutsalı RAB diyor ki,‹‹Yararlı olanı size öğreten,Gitmeniz gereken yolda sizi yürütenTanrınız RAB benim."));
trsuperobject.biblemap.put("b23_48_18", new trsuperobject(23,48,18,"‹‹Keşke buyruklarıma dikkat etseydiniz!O zaman esenliğiniz ırmak gibi,Doğruluğunuz denizin dalgaları gibi olurdu."));
trsuperobject.biblemap.put("b23_48_19", new trsuperobject(23,48,19,"Soyunuz kum gibi,Torunlarınız kum taneleri gibi olurdu.Adları ne unutulur,Ne de huzurumdan yok olurdu.››"));
trsuperobject.biblemap.put("b23_48_20", new trsuperobject(23,48,20,"Babilden çıkın, Kildanilerden kaçın,Sevinç çığlıklarıyla ilan edin bunu,Haberini duyurun, dünyanın dört bucağına yayın.‹‹RAB, kulu Yakupun soyunu kurtardı›› deyin."));
trsuperobject.biblemap.put("b23_48_21", new trsuperobject(23,48,21,"Onları çöllerden geçirirken susuzluk çekmediler,Onlar için sular akıttı kayadan,Kayayı yardı, sular fışkırdı."));
trsuperobject.biblemap.put("b23_48_22", new trsuperobject(23,48,22,"‹‹Kötülere esenlik yoktur›› diyor RAB."));
trsuperobject.biblemap.put("b23_49_1", new trsuperobject(23,49,1,"Ey kıyı halkları, işitin beni,Uzaktaki halklar, iyi dinleyin.RAB beni ana rahmindeyken çağırdı,Annemin karnındayken adımı koydu."));
trsuperobject.biblemap.put("b23_49_2", new trsuperobject(23,49,2,"Ağzımı keskin kılıç yaptı,Elinin gölgesinde gizledi beni.Beni keskin bir ok yaptı,Kendi ok kılıfına sakladı."));
trsuperobject.biblemap.put("b23_49_3", new trsuperobject(23,49,3,"Bana, ‹‹Kulumsun, ey İsrail,Görkemimi senin aracılığınla göstereceğim›› dedi."));
trsuperobject.biblemap.put("b23_49_4", new trsuperobject(23,49,4,"Ama ben, ‹‹Boşuna emek verdim›› dedim,‹‹Gücümü boş yere, bir hiç için tükettim.RAB yine de hakkımı savunur,Tanrım yaptıklarımın karşılığını verir.››"));
trsuperobject.biblemap.put("b23_49_5", new trsuperobject(23,49,5,"Kulu olmam için,Yakup soyunu kendisine geri getirmem,İsraili önünde toplamam içinRahimde beni biçimlendiren RAB şimdi şöyle diyor:-Onun gözünde onurluyum,Tanrım bana güç kaynağı oldu.-"));
trsuperobject.biblemap.put("b23_49_6", new trsuperobject(23,49,6,"‹‹Yakupun oymaklarını canlandırmak,Sağ kalan İsraillileri geri getirmek içinKulum olman yeterli değil.Seni uluslara ışık yapacağım.Öyle ki, kurtarışım yeryüzünün dört bucağına ulaşsın.››"));
trsuperobject.biblemap.put("b23_49_7", new trsuperobject(23,49,7,"İnsanların hor gördüğüne,Ulusların iğrendiğine,Egemenlerin kulu olanaİsrailin Kurtarıcısı ve Kutsalı RAB diyor ki,‹‹Seni seçmiş olan İsrailin Kutsalı sadık RABden ötürüKrallar seni görünce ayağa kalkacak,Önderler yere kapanacak.››"));
trsuperobject.biblemap.put("b23_49_9", new trsuperobject(23,49,9,"Tutsaklara, ‹Çıkın›,Karanlıktakilere, ‹Dışarı çıkın› diyeceksin.Yol boyunca beslenecek,Her çıplak tepede otlak bulacaklar."));
trsuperobject.biblemap.put("b23_49_10", new trsuperobject(23,49,10,"Acıkmayacak, susamayacaklar,Kavurucu sıcak ve güneş çarpmayacak onları.Çünkü onlara merhamet eden kendilerine yol gösterecekVe onları pınarlara götürecek."));
trsuperobject.biblemap.put("b23_49_11", new trsuperobject(23,49,11,"Bütün dağlarımı yola dönüştüreceğim,Anayollarım yükseltilecek."));
trsuperobject.biblemap.put("b23_49_12", new trsuperobject(23,49,12,"İşte halkım ta uzaklardan,Kimi kuzeyden, kimi batıdan, kimi de Sinimden gelecek.››"));
trsuperobject.biblemap.put("b23_49_13", new trsuperobject(23,49,13,"Ey gökler, sevinçle haykırın,Neşeyle coş, ey yeryüzü!Ey dağlar, sevinç çığlıklarına katılın,Çünkü RAB halkını avutacak,Ezilene merhamet gösterecek."));
trsuperobject.biblemap.put("b23_49_14", new trsuperobject(23,49,14,"Oysa Siyon, ‹‹RAB beni terk etti,Rab beni unuttu›› diyordu."));
trsuperobject.biblemap.put("b23_49_15", new trsuperobject(23,49,15,"Ama RAB, ‹‹Kadın emzikteki çocuğunu unutabilir mi?›› diyor,‹‹Rahminden çıkan çocuktan sevecenliği esirger mi?Kadın unutabilir,Ama ben seni asla unutmam."));
trsuperobject.biblemap.put("b23_49_16", new trsuperobject(23,49,16,"Bak, adını avuçlarıma kazıdım,Duvarlarını gözlüyorum sürekli."));
trsuperobject.biblemap.put("b23_49_17", new trsuperobject(23,49,17,"Oğulların koşar adım geliyor,Seni yıkıp viran edenlerse çıkıp gidecek."));
trsuperobject.biblemap.put("b23_49_18", new trsuperobject(23,49,18,"Başını kaldır da çevrene bir bak:Hepsi toplanmış sana geliyor.Ben RAB, varlığım hakkı için diyorum ki,Onların hepsi senin süsün olacak,Bir gelin gibi takınacaksın onları."));
trsuperobject.biblemap.put("b23_49_19", new trsuperobject(23,49,19,"‹‹Çünkü yıkılmış, viraneye dönmüştün,Ülken yerle bir olmuştu.Ama şimdi halkına dar geleceksin,Seni harap etmiş olanlar senden uzak duracaklar."));
trsuperobject.biblemap.put("b23_49_20", new trsuperobject(23,49,20,"Yitirdiğini sandığın çocuklarının sesini yine duyacaksın:‹Burası bize dar geliyor,Yaşayacak bir yer ver bize› diyecekler."));
trsuperobject.biblemap.put("b23_49_21", new trsuperobject(23,49,21,"O zaman içinden,‹Kim doğurdu bunları bana?› diyeceksin,‹Çocuklarımı yitirmiştim, doğuramıyordum.Sürgüne gönderilmiş, dışlanmıştım.Öyleyse bunları kim büyüttü?Yapayalnız kalmıştım,Nereden çıkıp geldi bunlar?››"));
trsuperobject.biblemap.put("b23_49_22", new trsuperobject(23,49,22,"Egemen RAB diyor ki,‹‹Bakın, uluslara elimle işaret verdiğimde,Sancağımı yükselttiğimde halklara,Senin oğullarını kucaklarında getirecek,Kızlarını omuzlarında taşıyacaklar."));
trsuperobject.biblemap.put("b23_49_23", new trsuperobject(23,49,23,"Krallar size babalık,Prensesler sütannelik yapacak,Yüzüstü yere kapanıpAyaklarının tozunu yalayacaklar.O zaman benim RAB olduğumu anlayacaksın.Bana umut bağlayan utandırılmayacak.››"));
trsuperobject.biblemap.put("b23_49_24", new trsuperobject(23,49,24,"Güçlünün ganimeti elinden alınabilir mi?Zorbanın elindeki tutsak kurtulabilir mi? ‹‹Doğru olanın›› (bkz. 25. ayet)."));
trsuperobject.biblemap.put("b23_49_25", new trsuperobject(23,49,25,"Ama RAB diyor ki,‹‹Evet, güçlünün elindeki tutsaklar alınacak,Zorbanın aldığı ganimet de kurtarılacak.Seninle çekişenle ben çekişeceğim,Senin çocuklarını ben kurtaracağım."));
trsuperobject.biblemap.put("b23_49_26", new trsuperobject(23,49,26,"Sana zulmedenlere kendi etlerini yedireceğim,Tatlı şarap içmiş gibi kendi kanlarıyla sarhoş olacaklar.Böylece bütün insanlar bilecek kiSeni kurtaran RAB benim;Kurtarıcın, Yakup'un Güçlüsü benim.››"));
trsuperobject.biblemap.put("b23_50_2", new trsuperobject(23,50,2,"Geldiğimde neden kimse yoktu,Çağırdığımda niçin yanıt veren olmadı?Sizi kurtaramayacak kadar kısa mı elim,Ya da gücüm yok mu sizi özgür kılmaya?Azarlayarak denizi kurutur,Irmakları çöle çeviririm.Su kalmayınca balıklar ölür ve kokar."));
trsuperobject.biblemap.put("b23_50_3", new trsuperobject(23,50,3,"Göklere karalar giydirir,Çul ederim onların örtüsünü.››"));
trsuperobject.biblemap.put("b23_50_4", new trsuperobject(23,50,4,"Yorgunlara sözle destek olmayı bileyim diyeEgemen RAB bana eğitilmişlerin dilini verdi.Eğitilenler gibi dinleyeyim diye kulağımı uyandırır her sabah."));
trsuperobject.biblemap.put("b23_50_5", new trsuperobject(23,50,5,"Egemen RAB kulağımı açtı,Karşı koymadım, geri çekilmedim."));
trsuperobject.biblemap.put("b23_50_6", new trsuperobject(23,50,6,"Bana vuranlara sırtımı açtım,Yanaklarımı uzattım sakalımı yolanlara.Aşağılamalardan, tükürükten yüzümü gizlemedim."));
trsuperobject.biblemap.put("b23_50_7", new trsuperobject(23,50,7,"Egemen RAB bana yardım ettiği içinUtanç duymam.Kararımdan dönmemfş,Utandırılmayacağımı bilirim. ettim››."));
trsuperobject.biblemap.put("b23_50_8", new trsuperobject(23,50,8,"Beni haklı çıkaran yakınımda.Benden davacı olan kim, yüzleşelim,Kimdir hasmım, karşıma çıksın."));
trsuperobject.biblemap.put("b23_50_9", new trsuperobject(23,50,9,"Bana yardım eden Egemen RABdir,Kim suçlu çıkaracak beni?Onların hepsi giysi gibi eskiyecek,Tümünü güve yiyip bitirecek."));
trsuperobject.biblemap.put("b23_50_10", new trsuperobject(23,50,10,"Aranızda RABden korkan,Kulunun sözünü dinleyen kim var?Karanlıkta yürüyen, ışığı olmayan,RABbin adına güvensin, Tanrısına dayansın."));
trsuperobject.biblemap.put("b23_50_11", new trsuperobject(23,50,11,"Ama ateş yakan,Alevli oklar kuşanan sizler, hepiniz,Ateşinizin aydınlığında,Tutuşturduğunuz alevli okların arasında yürüyün.Benden alacağınız şudur:Azap içinde yatacaksınız."));
trsuperobject.biblemap.put("b23_51_1", new trsuperobject(23,51,1,"‹‹Doğruluğun ardından giden,RABbe yönelen sizler, beni dinleyin:Yontulduğunuz kayaya,Çıkarıldığınız taş ocağına bakın."));
trsuperobject.biblemap.put("b23_51_2", new trsuperobject(23,51,2,"Atanız İbrahime, sizi doğuran Saraya bakın.Çağırdığımda tek kişiydi İbrahim,Ama ben onu kutsayıp çoğalttım.››"));
trsuperobject.biblemap.put("b23_51_4", new trsuperobject(23,51,4,"‹‹Beni dinle, ey halkım,Bana kulak ver, ey ulusum!Yasa benden çıkacak,Halklara ışık olarak adaletimi yerleştireceğim."));
trsuperobject.biblemap.put("b23_51_5", new trsuperobject(23,51,5,"Zaferim yaklaştı,Kurtarışım ortaya çıktı.Halkları gücümle yöneteceğim.Kıyı halkları bana umut bağladı,Umutla gücümü bekliyorlar."));
trsuperobject.biblemap.put("b23_51_6", new trsuperobject(23,51,6,"Başınızı kaldırıp göklere bakın,Aşağıya, yeryüzüne bakın.Çünkü bu gökler duman gibi dağılacak,Giysi gibi eskiyecek yeryüzü;Üzerinde yaşayanlar sinek gibi ölecek.Ama benim kurtarışım sonsuz olacak,Ardı kesilmeyecek zaferimin."));
trsuperobject.biblemap.put("b23_51_7", new trsuperobject(23,51,7,"‹‹Ey sizler, doğru olanı bilenler,Yasamı yüreğinde taşıyan halk, dinleyin beni!İnsanların aşağılamalarından korkmayın,Yılmayın sövgülerinden."));
trsuperobject.biblemap.put("b23_51_8", new trsuperobject(23,51,8,"Güvenin yediği giysi gibi,Kurtçuğun yediği yapağı gibi yitecekler.Oysa zaferim sonsuza dek kalacak,Kurtarışım kuşaklar boyu sürecek.››"));
trsuperobject.biblemap.put("b23_51_9", new trsuperobject(23,51,9,"Uyan, ey RABbin gücü, uyan, kudreti kuşan!Eski günlerde, önceki kuşaklar döneminde olduğu gibi uyan!Rahavı parçalayan,Deniz canavarının bedenini deşen sen değil miydin?"));
trsuperobject.biblemap.put("b23_51_10", new trsuperobject(23,51,10,"Denizi, engin suların derinliklerini kurutan,Kurtulanların geçmesi içinDenizin derinliklerini yola çeviren sen değil miydin?"));
trsuperobject.biblemap.put("b23_51_11", new trsuperobject(23,51,11,"RABbin kurtardıkları dönecek,Sevinçle haykırarak Siyona varacaklar.Yüzlerinde sonsuz sevinç olacak.Onların olacak coşku ve sevinç,Üzüntü ve inilti kaçacak."));
trsuperobject.biblemap.put("b23_51_12", new trsuperobject(23,51,12,"RAB diyor ki,‹‹Sizi avutan benim, evet benim.Siz kimsiniz ki, ölümlü insandan,Ottan farksız insanoğlundan korkarsınız?"));
trsuperobject.biblemap.put("b23_51_13", new trsuperobject(23,51,13,"Sizi yaratan, gökleri geren,Dünyanın temellerini atan RABbiNasıl olur da unutursunuz?Sizi yok etmeye hazırlanan zalimin öfkesindenNeden gün boyu yılıp duruyorsunuz?Hani nerede zalimin gazabı?"));
trsuperobject.biblemap.put("b23_51_14", new trsuperobject(23,51,14,"Zincire vurulmuş tutsaklarÇok yakında özgürlüğe kavuşacak.Ölüm çukuruna inmeyecek,Aç kalmayacaklar."));
trsuperobject.biblemap.put("b23_51_15", new trsuperobject(23,51,15,"Tanrınız RAB benim.Dalgalar gürlesin diye denizi çalkalayan benim.››Onun adı Her Şeye Egemen RABdir!"));
trsuperobject.biblemap.put("b23_51_16", new trsuperobject(23,51,16,"‹‹Sözlerimi ağzına koydum,Seni elimin gölgesiyle örttüm;Gökleri yerleştirmen,Yeryüzünün temellerini atmanVe Siyona, ‹Halkım sensin› demen için...››"));
trsuperobject.biblemap.put("b23_51_17", new trsuperobject(23,51,17,"Uyan, ey Yeruşalim, uyan, kalk ayağa!Sen ki, RABbin gazap kâsesini Onun elinden içtin.Tamamını içtin sersemleten kâsenin."));
trsuperobject.biblemap.put("b23_51_18", new trsuperobject(23,51,18,"Doğurduğun bunca oğuldan sana yol gösteren yok,Elinden tutan da yok büyüttüğün bunca oğuldan."));
trsuperobject.biblemap.put("b23_51_19", new trsuperobject(23,51,19,"Başına çifte felaket geldi, kim başsağlığı dileyecek?Yıkım ve kırım, kıtlık ve kılıç.Nasıl avutayım seni?"));
trsuperobject.biblemap.put("b23_51_20", new trsuperobject(23,51,20,"Oğulların baygın, ağa düşmüş ahular gibiHer sokak başında yatıyor.RABbin öfkesine deTanrının azarlayışına da doymuşlar."));
trsuperobject.biblemap.put("b23_51_21", new trsuperobject(23,51,21,"Bu nedenle, ey ezilmiş Yeruşalim,Şarapsız sarhoş olmuş halk, şunu dinle!"));
trsuperobject.biblemap.put("b23_51_22", new trsuperobject(23,51,22,"Egemenin RAB, kendi halkını savunan Tanrın diyor ki,‹‹Seni sersemleten kâseyi, gazabımın kâsesiniElinden aldım.Bir daha asla içmeyeceksin ondan."));
trsuperobject.biblemap.put("b23_51_23", new trsuperobject(23,51,23,"Onu sana eziyet edenlerin eline vereceğim;Onlar ki sana, ‹Yere yat daÜzerinden geçelim› dediklerinde,Sırtını toprak, yol ettin.››"));
trsuperobject.biblemap.put("b23_52_1", new trsuperobject(23,52,1,"Uyan, ey Siyon, uyan, kudretini kuşan.Ey Yeruşalim, kutsal kent, güzel giysilerini giy.Çünkü sünnetsizlerle murdarlarKapılarından asla içeri girmeyecek artık."));
trsuperobject.biblemap.put("b23_52_2", new trsuperobject(23,52,2,"Üzerindeki tozu silk!Kalk, ey Yeruşalim, tahtına otur,Boynundaki zinciri çöz,Ey Siyon, tutsak kız."));
trsuperobject.biblemap.put("b23_52_4", new trsuperobject(23,52,4,"Egemen RAB diyor ki,‹‹Halkım gurbette yaşamak için önce Mısıra inmişti.Şimdi de Asurlular onları ezdi."));
trsuperobject.biblemap.put("b23_52_5", new trsuperobject(23,52,5,"Halkım boş yere alınıp götürüldü,Benim burayla ne ilgim kaldı?›› diyor RAB,‹‹Yöneticileri feryat ediyor,Adıma günboyu sövülüyor›› diyor RAB."));
trsuperobject.biblemap.put("b23_52_6", new trsuperobject(23,52,6,"‹‹Bundan ötürü halkım adımı bilecek,O gün, ‹İşte ben› diyenin ben olduğumu anlayacak.››"));
trsuperobject.biblemap.put("b23_52_7", new trsuperobject(23,52,7,"Dağları aşıp gelen müjdecinin ayakları ne güzeldir!O müjdeci ki, esenlik duyuruyor.İyilik müjdesi getiriyor, kurtuluş haberi veriyor.Siyon halkına, ‹‹Tanrınız egemenlik sürüyor!›› diye ilan ediyor."));
trsuperobject.biblemap.put("b23_52_8", new trsuperobject(23,52,8,"Dinleyin! Bekçileriniz seslerini yükseltiyor,Hep birlikte sevinçle haykırıyorlar.Çünkü RABbin Siyona dönüşünü gözleriyle görmekteler!"));
trsuperobject.biblemap.put("b23_52_9", new trsuperobject(23,52,9,"Ey Yeruşalim yıkıntıları,Hep birlikte sevinçle haykırıp bağırın!Çünkü RAB halkını avuttu,Yeruşalimi kurtardı."));
trsuperobject.biblemap.put("b23_52_10", new trsuperobject(23,52,10,"Bütün ulusların gözü önündeKutsal kolunu sıvadı,Dünyanın dört bucağıTanrımızın kurtarışını görecek."));
trsuperobject.biblemap.put("b23_52_11", new trsuperobject(23,52,11,"Çekilin, çekilin, oradan çıkın,Murdara dokunmayın.Oradan çıkıp temizlenin,Ey RABbe tapınma araçlarını taşıyan sizler!"));
trsuperobject.biblemap.put("b23_52_12", new trsuperobject(23,52,12,"Aceleyle çıkmayacak,Kaçıp gitmeyeceksiniz;Çünkü RAB önünüzden gidecek,İsrailin Tanrısı artçınız olacak."));
trsuperobject.biblemap.put("b23_52_13", new trsuperobject(23,52,13,"Bakın, kulum başarılı olacak;Üstün olacak, el üstünde tutulup alabildiğine yüceltilecek."));
trsuperobject.biblemap.put("b23_52_14", new trsuperobject(23,52,14,"Birçokları onun karşısında dehşete düşüyor;Biçimi, görünüşü öyle bozuldu ki,İnsana benzer yanı kalmadı;"));
trsuperobject.biblemap.put("b23_52_15", new trsuperobject(23,52,15,"Pek çok ulus ona şaşacak,Onun önünde kralların ağızları kapanacak.Çünkü kendilerine anlatılmamış olanı görecek,Duymadıklarını anlayacaklar."));
trsuperobject.biblemap.put("b23_53_1", new trsuperobject(23,53,1,"Verdiğimiz habere kim inandı?RABbin gücü kime açıklandı?"));
trsuperobject.biblemap.put("b23_53_2", new trsuperobject(23,53,2,"O RABbin önünde bir fidan gibi,Kurak yerdeki kök gibi büyüdü.Bakılacak biçimden, güzellikten yoksundu.Gönlümüzü çeken bir görünüşü de yoktu."));
trsuperobject.biblemap.put("b23_53_3", new trsuperobject(23,53,3,"İnsanlarca hor görüldü,Yapayalnız bırakıldı.Acılar adamıydı, hastalığı yakından tanıdı.İnsanların yüz çevirdiği biri gibi hor görüldü,Ona değer vermedik."));
trsuperobject.biblemap.put("b23_53_4", new trsuperobject(23,53,4,"Aslında hastalıklarımızı o üstlendi,Acılarımızı o yüklendi.Bizse Tanrı tarafından cezalandırıldığını,Vurulup ezildiğini sandık."));
trsuperobject.biblemap.put("b23_53_5", new trsuperobject(23,53,5,"Oysa, bizim isyanlarımız yüzünden onun bedeni deşildi,Bizim suçlarımız yüzünden o eziyet çekti.Esenliğimiz için gerekli olan cezaOna verildi.Bizler onun yaralarıyla şifa bulduk."));
trsuperobject.biblemap.put("b23_53_6", new trsuperobject(23,53,6,"Hepimiz koyun gibi yoldan sapmıştık,Her birimiz kendi yoluna döndü.Yine de RAB hepimizin cezasını ona yükledi."));
trsuperobject.biblemap.put("b23_53_7", new trsuperobject(23,53,7,"O baskı görüp eziyet çektiyse deAğzını açmadı.Kesime götürülen kuzu gibi,Kırkıcıların önünde sessizce duran koyun gibiAçmadı ağzını."));
trsuperobject.biblemap.put("b23_53_8", new trsuperobject(23,53,8,"Acımasızca yargılanıp ölüme götürüldü.Halkımın isyanı ve hak ettiği ceza yüzündenYaşayanlar diyarından atıldı.Onun kuşağından bunu düşünen oldu mu?"));
trsuperobject.biblemap.put("b23_53_9", new trsuperobject(23,53,9,"Şiddete başvurmadığı,Ağzından hileli söz çıkmadığı halde,Ona kötülerin yanında bir mezar verildi,Ama öldüğünde zenginin yanındaydı."));
trsuperobject.biblemap.put("b23_53_10", new trsuperobject(23,53,10,"Ne var ki, RAB onun ezilmesini uygun gördü,Acı çekmesini istedi.Canını suç sunusu olarak sunarsaSoyundan gelenleri görecek ve günleri uzayacak.RABbin istemi onun aracılığıyla gerçekleşecek."));
trsuperobject.biblemap.put("b23_53_11", new trsuperobject(23,53,11,"Canını feda ettiği içinGördükleriyle hoşnut olacak.RABbin doğru kulu, kendisini kabul eden birçoklarını aklayacak.Çünkü onların suçlarını o üstlendi."));
trsuperobject.biblemap.put("b23_53_12", new trsuperobject(23,53,12,"Bundan dolayı ona ünlüler arasında bir pay vereceğim,Ganimeti güçlülerle paylaşacak.Çünkü canını feda etti, başkaldıranlarla bir sayıldı.Pek çoklarının günahını o üzerine aldı,Başkaldıranlar için de yalvardı."));
trsuperobject.biblemap.put("b23_54_1", new trsuperobject(23,54,1,"‹‹Çocuk doğurmayan ey kısır kadın,Sevinç çığlıkları at;Ey doğum ağrısı nedir bilmeyen sen,Sevinçle haykır, bağır.Çünkü terk edilmiş kadının,Evli kadından daha çok çocuğu olacaktır›› diyor RAB."));
trsuperobject.biblemap.put("b23_54_2", new trsuperobject(23,54,2,"‹‹Çadırının alanını genişlet,Perdelerini uzat, çekinme.Gergi iplerini de uzat, kazıklarını sağlamlaştır."));
trsuperobject.biblemap.put("b23_54_3", new trsuperobject(23,54,3,"Çünkü sağa sola yayılacaksın,Soyundan gelenler ulusları mülk edinecek,Issız kentlere yerleşecek."));
trsuperobject.biblemap.put("b23_54_4", new trsuperobject(23,54,4,"‹‹Korkma, ayıplanmayacaksın,Utanma, aşağılanmayacaksın.Unutacaksın gençliğinde yaşadığın utancı,Dulluk ayıbını artık anmayacaksın."));
trsuperobject.biblemap.put("b23_54_5", new trsuperobject(23,54,5,"Çünkü kocan, seni yaratandır.Onun adı Her Şeye Egemen RABdir,İsrailin Kutsalıdır seni kurtaran.Ona bütün dünyanın Tanrısı denir.››"));
trsuperobject.biblemap.put("b23_54_6", new trsuperobject(23,54,6,"Tanrın diyor ki, ‹‹RAB seni terk edilmiş,Ruhu kederli bir kadın,Genç yaşta evlenip sonra dışlanmışBir kadın olarak çağırıyor:"));
trsuperobject.biblemap.put("b23_54_7", new trsuperobject(23,54,7,"‹Bir an için seni terk ettim,Ama büyük sevecenlikle geri getireceğim."));
trsuperobject.biblemap.put("b23_54_8", new trsuperobject(23,54,8,"Bir anlık taşkın öfkeyle senden yüz çevirmiştim,Ama sonsuz sadakatle sana sevecenlik göstereceğim.››Seni kurtaran RAB böyle diyor."));
trsuperobject.biblemap.put("b23_54_9", new trsuperobject(23,54,9,"‹‹Bu benim için Nuh tufanı gibidir.Nuh tufanının bir daha yeryüzünüKaplamayacağına nasıl ant içtimse,Sana öfkelenmeyeceğime,Seni azarlamayacağıma da ant içiyorum."));
trsuperobject.biblemap.put("b23_54_10", new trsuperobject(23,54,10,"Dağlar yerinden kalksa, tepeler sarsılsa daSadakatim senin üzerinden kalkmaz,Esenlik antlaşmam sarsılmaz››Diyor sana merhamet eden RAB."));
trsuperobject.biblemap.put("b23_54_11", new trsuperobject(23,54,11,"‹‹Ey kasırgaya tutulmuş,Avuntu bulmamış ezik kent!Taşlarını koyu harçla yerine koyacak,Temellerini laciverttaşıyla atacağım."));
trsuperobject.biblemap.put("b23_54_12", new trsuperobject(23,54,12,"Kale burçlarını yakuttan,Kapılarını mücevherden,Surlarını değerli taşlardan yapacağım."));
trsuperobject.biblemap.put("b23_54_13", new trsuperobject(23,54,13,"Bütün çocuklarını ben RAB eğiteceğim,Esenlikleri tam olacak."));
trsuperobject.biblemap.put("b23_54_14", new trsuperobject(23,54,14,"Doğrulukla güçlenecek,Baskıdan uzak olacak, korkmayacaksın.Dehşet senden uzak kalacak, sana yaklaşmayacak."));
trsuperobject.biblemap.put("b23_54_15", new trsuperobject(23,54,15,"Sana saldıran olursa, benden olmadığını bil.Sana saldıran herkes önünde yenilgiye uğrayacak."));
trsuperobject.biblemap.put("b23_54_16", new trsuperobject(23,54,16,"‹‹İşte, kor halindeki ateşi üfleyen,Amaca uygun silah yapan demirciyi ben yarattım.Yok etsin diye yıkıcıyı da ben yarattım."));
trsuperobject.biblemap.put("b23_54_17", new trsuperobject(23,54,17,"Ama sana karşı yapılan hiçbir silah işe yaramayacak,Mahkemede seni suçlayan her diliSuçlu çıkaracaksın.RAB'be kulluk edenlerin mirası şudur:Onların gönenci bendendir›› diyor RAB."));
trsuperobject.biblemap.put("b23_55_1", new trsuperobject(23,55,1,"‹‹Ey susamış olanlar, sulara gelin,Parası olmayanlar, gelin, satın alın, yiyin.Gelin, şarabı ve sütü parasız, bedelsiz alın."));
trsuperobject.biblemap.put("b23_55_2", new trsuperobject(23,55,2,"Paranızı neden ekmek olmayana,Emeğinizi doyurmayana harcıyorsunuz?Beni iyi dinleyin ki, iyi olanı yiyesiniz,Bolluğun tadını çıkarasınız!"));
trsuperobject.biblemap.put("b23_55_3", new trsuperobject(23,55,3,"‹‹Kulak verin, bana gelin.Dinleyin ki yaşayasınız.Ben de sizinle sonsuz bir antlaşma,Davuta söz verdiğim kalıcı iyilikleri içeren bir antlaşma yapayım."));
trsuperobject.biblemap.put("b23_55_4", new trsuperobject(23,55,4,"Bakın, onu halklara tanık,Önder ve komutan yaptım."));
trsuperobject.biblemap.put("b23_55_5", new trsuperobject(23,55,5,"Tanımadığınız ulusları çağıracaksınız,Sizi tanımayan uluslar koşa koşa size gelecek.Tanrınız RABden,İsrailin Kutsalından ötürü gelecekler.Çünkü RAB sizleri yüceltecek.››"));
trsuperobject.biblemap.put("b23_55_6", new trsuperobject(23,55,6,"Bulma fırsatı varken RABbi arayın,Yakındayken Ona yakarın."));
trsuperobject.biblemap.put("b23_55_7", new trsuperobject(23,55,7,"Kötü kişi yolunu,Fesatçı düşüncelerini bıraksın;RABbe dönsün, merhamet bulur,Tanrımıza dönsün, bol bol bağışlanır."));
trsuperobject.biblemap.put("b23_55_8", new trsuperobject(23,55,8,"‹‹Çünkü benim düşüncelerimSizin düşünceleriniz değil,Sizin yollarınız benim yollarım değil›› diyor RAB."));
trsuperobject.biblemap.put("b23_55_9", new trsuperobject(23,55,9,"‹‹Çünkü gökler nasıl yeryüzünden yüksekse,Yollarım da sizin yollarınızdan,Düşüncelerim düşüncelerinizden yüksektir."));
trsuperobject.biblemap.put("b23_55_10", new trsuperobject(23,55,10,"Gökten inen yağmur ve kar,Toprağı sulamadan, yeri yeşertmeden,Ekinciye tohum, yiyene ekmek vermedenNasıl göğe dönmezse,"));
trsuperobject.biblemap.put("b23_55_11", new trsuperobject(23,55,11,"Ağzımdan çıkan söz de öyle olacaktır.Bana boş dönmeyecek,İstemimi yerine getirecek,Yapması için onu gönderdiğim işi başaracaktır."));
trsuperobject.biblemap.put("b23_55_12", new trsuperobject(23,55,12,"Sevinçle çıkacak,Esenlikle geri götürüleceksiniz.Dağlar, tepeler önünüzde sevinçle çığıracak,Kırdaki bütün ağaçlar alkış tutacak."));
trsuperobject.biblemap.put("b23_55_13", new trsuperobject(23,55,13,"Dikenli çalı yerine çam,Isırgan yerine mersin ağacı bitecek.Bunlar bana ün getirecek,Yok olmayan sonsuz bir belirti olacak.››"));
trsuperobject.biblemap.put("b23_56_2", new trsuperobject(23,56,2,"Bunu yapan insana,Buna sımsıkı sarılan insanoğluna ne mutlu!Şabat Gününü tutar, bayağılaştırmaz,Her türlü kötülükten sakınır.››"));
trsuperobject.biblemap.put("b23_56_4", new trsuperobject(23,56,4,"Çünkü RAB diyor ki,‹‹Şabat günlerimi tutan,Beni hoşnut edeni seçen,Antlaşmama sımsıkı bağlı kalan hadıma"));
trsuperobject.biblemap.put("b23_56_5", new trsuperobject(23,56,5,"Evimde, evimin dört duvarı arasındaOğullardan da kızlardan da daha iyi bir anıt ve ad vereceğim;Yok edilemez, ebedi bir ad olacak bu."));
trsuperobject.biblemap.put("b23_56_6", new trsuperobject(23,56,6,"‹‹RABbe hizmet etmek,Onun adını sevmek,Kulu olmak için Ona bağlanan yabancıları,Şabat Gününü tutan, bayağılaştırmayan,Antlaşmama sımsıkı bağlı kalan herkesi,"));
trsuperobject.biblemap.put("b23_56_7", new trsuperobject(23,56,7,"Kutsal dağıma getiripDua evimde sevindireceğim.Yakmalık sunularıyla kurbanlarıSunağımda kabul edilecek,Çünkü evime ‹Bütün ulusların dua evi› denecek.››"));
trsuperobject.biblemap.put("b23_56_8", new trsuperobject(23,56,8,"İsrailin sürgünlerini toplayan Egemen RAB diyor ki,‹‹Toplanmış olanlara katmak üzereDaha başkalarını da toplayacağım.››"));
trsuperobject.biblemap.put("b23_56_9", new trsuperobject(23,56,9,"Ey bütün kır hayvanları,Ormanda yaşayan bütün hayvanlar,Yiyip bitirmek için gelin!"));
trsuperobject.biblemap.put("b23_56_10", new trsuperobject(23,56,10,"İsrailin bekçileri kördür, hepsi bilgisizdir.Havlayamayan dilsiz köpekler gibidirler.Uzanıp düş görürler,Uykuyu pek severler!"));
trsuperobject.biblemap.put("b23_56_11", new trsuperobject(23,56,11,"Doymak bilmeyen azgın köpeklere benzerler,Aklı kıt çobanlar bunlar!Kendi yollarına döndüler,Her biri yalnız kendi çıkarını düşünüyor."));
trsuperobject.biblemap.put("b23_56_12", new trsuperobject(23,56,12,"Birbirlerine, ‹‹Haydi, şarap getirelim,Bol bol içki içelim!Yarın da bugün gibi geçecek,Hatta çok daha iyi olacak›› diyorlar."));
trsuperobject.biblemap.put("b23_57_1", new trsuperobject(23,57,1,"Doğru kişi ölüp gidiyor,Kimsenin umurunda değil.Sadık adamlar da göçüp gidiyor;Kimse doğru kişinin göçüp gitmekleKötülükten kurtulduğunun farkında değil."));
trsuperobject.biblemap.put("b23_57_2", new trsuperobject(23,57,2,"Doğru kişi esenliğe kavuşur,Doğru yolda yürümüş olan mezarındafü rahat uyur."));
trsuperobject.biblemap.put("b23_57_3", new trsuperobject(23,57,3,"Ama siz, ey falcı kadının çocukları,Fahişelik ve zina edenlerin soyu, buraya gelin!"));
trsuperobject.biblemap.put("b23_57_4-5", new trsuperobject(23,57,4-5,"Siz kiminle alay ediyorsunuz?Kime dudak büküyor, dil çıkarıyorsunuz?Ağaçlar arasında, bol yapraklı her ağacın altındaŞehvetle yanıp tutuşan,Vadilerde, kaya kovuklarında çocuklarını kurban eden,İsyan torunları, yalan soyu değil misiniz siz?"));
trsuperobject.biblemap.put("b23_57_6", new trsuperobject(23,57,6,"Sizin payınızVadinin düzgün taşlarından yapılan putlardır,Evet, sizin nasibiniz onlardır!Onlara dökmelik sunular döktünüz,Tahıl sunuları sundunuz.Bütün bunlardan sonra sizi cezalandırmaktan çekineceğimi mi sanıyorsunuz?"));
trsuperobject.biblemap.put("b23_57_7", new trsuperobject(23,57,7,"Yatağınızı ulu, yüksek dağa serdiniz,Oraya bile kurban kesmeye gidiyorsunuz."));
trsuperobject.biblemap.put("b23_57_8", new trsuperobject(23,57,8,"Kapılarınızın, sövelerinizin arkasınaİğrenç simgeler koydunuz.Beni bıraktınız,Yataklarınızı ardına kadar açıp içine girdiniz,Oynaşlarınızla anlaşıp birlikte yatmaya can atıyorsunuz.Onların çıplaklığını seyrettiniz."));
trsuperobject.biblemap.put("b23_57_9", new trsuperobject(23,57,9,"Çeşit çeşit hoş kokular sürünüp ilah Moleke yağ götürdünüz.Elçilerinizi ta uzaklara gönderdiniz,Ölüler diyarına dek alçalttınız kendinizi."));
trsuperobject.biblemap.put("b23_57_10", new trsuperobject(23,57,10,"Uzun yolculuklar sizi yorduğu halde,‹‹Pes ettim›› demediniz.Gücünüzü tazeleyip durdunuz,Bu nedenle de tükenmediniz."));
trsuperobject.biblemap.put("b23_57_11", new trsuperobject(23,57,11,"‹‹Sizi kaygılandıran, korkutan kim ki,Bana ihanet ediyor, beni anmıyor,Yüreğinizde bana yer vermiyorsunuz?Benden korkmamanızın nedeniUzun zamandır suskun kalışım değil mi?"));
trsuperobject.biblemap.put("b23_57_12", new trsuperobject(23,57,12,"Sözde doğruluğunuzu da yaptıklarınızı da ilan edeceğim,Bunların size yararı olmayacak."));
trsuperobject.biblemap.put("b23_57_13", new trsuperobject(23,57,13,"Feryat ettiğinizdeTopladığınız putlar sizi kurtarsın bakalım!Rüzgar hepsini silip süpürecek,Bir soluk onları alıp götürecek.Bana sığınansa ülkeyi mülk edinecek,Kutsal dağımı miras alacak.››"));
trsuperobject.biblemap.put("b23_57_14", new trsuperobject(23,57,14,"RAB diyor ki,‹‹Toprak yığıp yol yapın,Halkımın yolundaki engelleri kaldırın.››"));
trsuperobject.biblemap.put("b23_57_15", new trsuperobject(23,57,15,"Yüce ve görkemli Olan,Sonsuzlukta yaşayan, adı Kutsal Olan diyor ki,‹‹Yüksek ve kutsal yerde yaşadığım halde,Alçakgönüllülerle, ezilenlerle birlikteyim.Yüreklerini sevindirmek için ezilenlerin yanındayım."));
trsuperobject.biblemap.put("b23_57_16", new trsuperobject(23,57,16,"Çünkü sonsuza dek davacı ve öfkeli olacak değilim,Öyle olsa, yarattığım canlarla ruhlar karşımda dayanamazdı."));
trsuperobject.biblemap.put("b23_57_17", new trsuperobject(23,57,17,"Haksız kazanç suçuna öfkelenip halkı cezalandırdım,Öfkeyle yüzümü çevirdim onlardan.Ne var ki, inatla kendi yollarından gittiler."));
trsuperobject.biblemap.put("b23_57_18", new trsuperobject(23,57,18,"‹‹Yaptıklarını gördüm,Ama onları iyileştirip yol göstereceğim.Karşılık olarak hem onlarıHem de aralarında yas tutanları avutacağım."));
trsuperobject.biblemap.put("b23_57_19", new trsuperobject(23,57,19,"Dudaklardan övgü sözleri döktüreceğim.Uzaktakine de yakındakine deTam esenlik olsun›› diyor RAB,‹‹Hepsini iyileştireceğim.››"));
trsuperobject.biblemap.put("b23_57_20", new trsuperobject(23,57,20,"Ama kötüler çalkalanan deniz gibidir,O deniz ki, rahat duramaz, suları çamur ve pislik savurur."));
trsuperobject.biblemap.put("b23_57_21", new trsuperobject(23,57,21,"‹‹Kötülere esenlik yoktur›› diyor Tanrım."));
trsuperobject.biblemap.put("b23_58_1", new trsuperobject(23,58,1,"‹‹Avaz avaz bağırın, çekinmeyin,Sesinizi boru sesi gibi yükseltin;Halkıma isyanlarını,Yakup soyuna günahlarını bildirin."));
trsuperobject.biblemap.put("b23_58_2", new trsuperobject(23,58,2,"Bana her gün danışıyor,Yollarımı öğrenmekten zevk duyuyorlarmış!Doğru davranan,Tanrısının buyruğundan ayrılmayan bir ulusmuş gibi...Benden adil yargılar diliyor,Bana yaklaşmaktan zevk alıyorlarmış. yakın olmamı istiyorlarmış››."));
trsuperobject.biblemap.put("b23_58_3", new trsuperobject(23,58,3,"Diyorlar ki, ‹Oruç tuttuğumuzu neden görmüyor,İsteklerimizi denetlediğimizi neden farketmiyorsun?›  ‹‹Bakın, oruç tuttuğunuz gün keyfinize bakıyor,İşçilerinizi eziyorsunuz."));
trsuperobject.biblemap.put("b23_58_4", new trsuperobject(23,58,4,"Orucunuz kavgayla, çekişmeyle,Şiddetli yumruklaşmayla bitiyor.Bugünkü gibi oruç tutmaklaSesinizi yükseklere duyuramazsınız."));
trsuperobject.biblemap.put("b23_58_5", new trsuperobject(23,58,5,"İstediğim oruç bu mu sanıyorsunuz?İnsanın isteklerini denetlemesi gereken gün böyle mi olmalı?Kamış gibi baş eğip çul ve kül üzerine mi oturmalı?Siz buna mı oruç, RABbi hoşnut eden gün diyorsunuz?"));
trsuperobject.biblemap.put("b23_58_6", new trsuperobject(23,58,6,"Benim istediğim oruç,Haksız yere zincire, boyunduruğa vurulanları salıvermek,Ezilenleri özgürlüğe kavuşturmak,Her türlü boyunduruğu kırmak değil mi?"));
trsuperobject.biblemap.put("b23_58_7", new trsuperobject(23,58,7,"Yiyeceğinizi açla paylaşmak değil mi?Barınaksız yoksulları evinize alır,Çıplak gördüğünüzü giydirir,Yakınlarınızdan yardımınızı esirgemezseniz,"));
trsuperobject.biblemap.put("b23_58_8", new trsuperobject(23,58,8,"Işığınız tan gibi ağaracak,Çabucak şifa bulacaksınız.Doğruluğunuz önünüzden gidecek,RABbin yüceliği artçınız olacak."));
trsuperobject.biblemap.put("b23_58_9", new trsuperobject(23,58,9,"O zaman yardım çağrılarınızı RAB yanıtlayacak,Feryat ettiğinizde, ‹İşte buradayım› diyecek.  ‹‹Eğer boyunduruğa, başkalarını suçlamaya,Kötücül konuşmalara son verirseniz,"));
trsuperobject.biblemap.put("b23_58_10", new trsuperobject(23,58,10,"Açlar uğruna kendinizi feda eder,Yoksulların gereksinimini karşılarsanız,Işığınız karanlıkta parlayacak,Karanlığınız öğlen gibi ışıyacak."));
trsuperobject.biblemap.put("b23_58_11", new trsuperobject(23,58,11,"RAB her zaman size yol gösterecek,Kurak topraklarda sizi doyurup güçlendirecek.İyi sulanmış bahçe gibi,Tükenmez su kaynağı gibi olacaksınız."));
trsuperobject.biblemap.put("b23_58_12", new trsuperobject(23,58,12,"Halkınız eski yıkıntıları onaracak,Geçmiş kuşakların temelleri üzerineYeni yapılar dikeceksiniz.‹Duvardaki gedikleri onaran,Sokakları oturulacak hale getiren› denecek sizlere."));
trsuperobject.biblemap.put("b23_58_13", new trsuperobject(23,58,13,"‹‹Kutsal günümde dilediğinizi yapmaz, Şabat Gününü çiğnemezseniz,Şabat Gününe ‹Zevkli›,RABbin kutsal gününe ‹Onurlu› derseniz,Kendi yolunuzdan gitmez,Keyfinize bakmayıp boş konulara dalmaz,O günü yüceltirseniz,"));
trsuperobject.biblemap.put("b23_58_14", new trsuperobject(23,58,14,"RAB'den zevk alırsınız.O zaman sizi yeryüzünün yüksek yerlerine çıkarır,Atanız Yakup'un mirasıyla doyururum.››Bunu söyleyen RAB'dir."));
trsuperobject.biblemap.put("b23_59_1", new trsuperobject(23,59,1,"Bakın, RABbin eli kurtaramayacak kadar kısa,Kulağı duyamayacak kadar sağır değildir."));
trsuperobject.biblemap.put("b23_59_2", new trsuperobject(23,59,2,"Ama suçlarınız sizi Tanrınızdan ayırdı.Günahlarınızdan ötürü Onun yüzünü göremez,Sesinizi işittiremez oldunuz."));
trsuperobject.biblemap.put("b23_59_3", new trsuperobject(23,59,3,"Çünkü elleriniz kanla,Parmaklarınız suçla kirlendi.Dudaklarınız yalan söyledi,Diliniz kötülük mırıldanıyor."));
trsuperobject.biblemap.put("b23_59_4", new trsuperobject(23,59,4,"Adaletle dava açan,Davasını dürüstçe savunan yok.Boş laflara güveniyor, yalan söylüyorlar.Fesada gebe kalıp kötülük doğuruyorlar."));
trsuperobject.biblemap.put("b23_59_5", new trsuperobject(23,59,5,"Engerek yumurtaları üzerinde kuluçkaya yatıyor,Örümcek ağı dokuyorlar.Onların yumurtalarından yiyen ölür,Kırılan yumurtadan engerek yavrusu çıkar."));
trsuperobject.biblemap.put("b23_59_6", new trsuperobject(23,59,6,"Dokudukları ağdan giysi olmaz,Elleriyle yaptıklarıyla örtünemezler.Eylemleri kötü eylemlerdir,Elleri zorbalığın araçlarıdır."));
trsuperobject.biblemap.put("b23_59_7", new trsuperobject(23,59,7,"Ayakları kötülüğe koşar,Çekinmeden suçsuz kanı dökerler.Akılları fikirleri hep kötülükte,Şiddet ve yıkım var yollarında."));
trsuperobject.biblemap.put("b23_59_8", new trsuperobject(23,59,8,"Esenlik yolunu bilmezler,İzledikleri yolda adalet yoktur.Kendilerine çarpık yollar yaptılar,O yoldan gidenlerin hiçbiri esenlik nedir bilmez."));
trsuperobject.biblemap.put("b23_59_9", new trsuperobject(23,59,9,"Diyorlar ki, ‹‹Bu yüzden adalet bizden uzak,Doğruluk bize erişemiyor.Işık bekliyoruz, yalnız karanlık var;Parıltı bekliyor, koyu karanlıkta yürüyoruz."));
trsuperobject.biblemap.put("b23_59_10", new trsuperobject(23,59,10,"Kör gibi duvarı el yordamıyla arıyor,Yolumuzu bulmaya çalışıyoruz.Öğle vakti alaca karanlıktaymış gibi tökezliyoruz,Güçlüler arasında ölüler gibiyiz."));
trsuperobject.biblemap.put("b23_59_11", new trsuperobject(23,59,11,"Hepimiz ayı gibi homurdanıyor,Güvercin gibi inim inim inliyoruz.Adalet bekliyoruz, ortada yok;Kurtuluş bekliyoruz, bizden uzak."));
trsuperobject.biblemap.put("b23_59_12", new trsuperobject(23,59,12,"Çünkü sana çok kez başkaldırdık,Günahlarımız bize karşı tanıklık ediyor,İsyanlarımız hep yanıbaşımızda.Suçlarımızı kabul ediyoruz."));
trsuperobject.biblemap.put("b23_59_13", new trsuperobject(23,59,13,"Başkaldırıp RABbi yadsıdık,Tanrımızı izlemez olduk.Zorbalık, isyan dolu sözler söyledik,Yüreğimizde tasarladığımız yalanları mırıldandık."));
trsuperobject.biblemap.put("b23_59_14", new trsuperobject(23,59,14,"Adalet püskürtüldü, doğruluk bizden uzak duruyor.Çünkü gerçek, kent meydanında sendeleyip düştü,Dürüstlük aramıza giremez oldu."));
trsuperobject.biblemap.put("b23_59_15", new trsuperobject(23,59,15,"Hiçbir yerde gerçek yok,Kötülükten çekinen soyuluyor!››  RAB olanları gördü ve adaletin yokluğuna üzüldü."));
trsuperobject.biblemap.put("b23_59_16", new trsuperobject(23,59,16,"Kimsenin olmadığını gördü,Aracılık edecek birinin olmadığına şaştı.Kendi gücüyle kurtuluş sağladı,Doğruluğu Ona destek oldu."));
trsuperobject.biblemap.put("b23_59_17", new trsuperobject(23,59,17,"Doğruluğu göğüslük gibi kuşandı,Kurtuluş miğferini başına taktı,Öç giysisini giydi,Gayreti kaftan gibi sarındı."));
trsuperobject.biblemap.put("b23_59_18", new trsuperobject(23,59,18,"Herkese yaptıklarının karşılığını verecek.Düşmanlarına öfkeyle,Hasımlarına ve kıyı halklarına cezayla karşılık verecek."));
trsuperobject.biblemap.put("b23_59_19", new trsuperobject(23,59,19,"Böylece batıdan doğuya kadar insanlarRABbin adından ve yüceliğinden korkacak.Çünkü düşman azgın bir ırmak gibi geldiğinde,RABbin Ruhu onu kaçırtacak. ‹‹Soluk›› anlamına da gelir."));
trsuperobject.biblemap.put("b23_59_20", new trsuperobject(23,59,20,"RAB diyor ki, ‹‹Kurtarıcı Siyona,Yakup soyundan olup başkaldırmaktan vazgeçenlere gelecek."));
trsuperobject.biblemap.put("b23_59_21", new trsuperobject(23,59,21,"Bana gelince, onlarla yapacağım antlaşma şudur:Üzerindeki Ruhum, ağzına koyduğum sözlerŞimdiden sonsuza dek senin, çocuklarının,Torunlarının ağzından düşmeyecek.››"));
trsuperobject.biblemap.put("b23_60_1", new trsuperobject(23,60,1,"‹‹Kalk, parla;Çünkü Işığın geliyor,RABbin yüceliği üzerine doğuyor."));
trsuperobject.biblemap.put("b23_60_2", new trsuperobject(23,60,2,"Dünyayı karanlık, halkları koyu karanlık örtüyor;Oysa RAB senin üzerine doğacak,Yüceliği üzerinde görünecek."));
trsuperobject.biblemap.put("b23_60_3", new trsuperobject(23,60,3,"Uluslar senin Işığına,Krallar üzerine doğan aydınlığa gelecek."));
trsuperobject.biblemap.put("b23_60_4", new trsuperobject(23,60,4,"‹‹Başını kaldır da çevrene bir bak,Hepsi toplanmış sana geliyor.Oğulların uzaktan geliyor,Kızların kucakta taşınıyor."));
trsuperobject.biblemap.put("b23_60_5", new trsuperobject(23,60,5,"Bunu görünce yüzün parlayacak,Yüreğin heyecandan hızlı hızlı çarpacak;Çünkü denizin zenginlikleri senin olacak,Ulusların serveti sana akacak."));
trsuperobject.biblemap.put("b23_60_6", new trsuperobject(23,60,6,"‹‹Deve sürüleri, Midyanın ve Efanın deve yavrularıSenin topraklarını dolduracak.Bütün Saba halkı geliyor,Altın ve günnük getiriyor,RABbin erdemlerini ilan ediyorlar."));
trsuperobject.biblemap.put("b23_60_7", new trsuperobject(23,60,7,"Kedarın bütün sürüleri sana gelecek,Nevayotun koçları senin buyruğunda olacak,Sunağımın üzerinde kabul edilen sunular olarak sunulacak.Böylece görkemli tapınağımı daha görkemli kılacağım."));
trsuperobject.biblemap.put("b23_60_8", new trsuperobject(23,60,8,"‹‹Nedir bunlar, bulut gibi,Yuvalarına yaklaşan güvercinler gibi süzülüp gelenler?"));
trsuperobject.biblemap.put("b23_60_9", new trsuperobject(23,60,9,"Bana umut bağlayan kıyı halklarının,Ticaret gemileri öncülüğündeSenin çocuklarını altınlarıyla, gümüşleriyle birlikteTanrın RABbin adı için, İsrailin Kutsalı içinUzaktan getiren gemileridir bunlar.RAB seni görkemli kıldı."));
trsuperobject.biblemap.put("b23_60_10", new trsuperobject(23,60,10,"‹‹Yabancılar senin surlarını onaracak,Kralları sana hizmet edecek.Öfkelendiğimde seni cezalandırdıysam da,Kabul ettiğimde sana merhamet göstereceğim."));
trsuperobject.biblemap.put("b23_60_11", new trsuperobject(23,60,11,"Kapıların hep açık duracak,Ulusların serveti ve zafer alayları ardında yürütülen yenik krallarGece gündüz açık kalan bu kapılardan girsin diye."));
trsuperobject.biblemap.put("b23_60_12", new trsuperobject(23,60,12,"Çünkü sana kulluk etmeyen ulus ya da krallık yok olacak,Evet, o uluslar tam bir yıkıma uğrayacak."));
trsuperobject.biblemap.put("b23_60_13", new trsuperobject(23,60,13,"‹‹Lübnanın görkemi olan çam, köknar ve selvi ağaçları,Tapınağımı süslemek için hep birlikte sana taşınacak.Ayak bastığım yeri görkemli kılacağım."));
trsuperobject.biblemap.put("b23_60_14", new trsuperobject(23,60,14,"Seni ezenlerin çocuklarıGelip önünde eğilecekler;Seni hor görenlerin hepsi,‹RABbin kenti, İsrailin Kutsalının Siyonu›Diyerek ayaklarına kapanacaklar."));
trsuperobject.biblemap.put("b23_60_15", new trsuperobject(23,60,15,"‹‹Kimsenin uğramadığı, terk edilmiş,Nefret edilen bir yer olduğun haldeSeni sonsuz bir övünç kaynağı,Bütün kuşakların sevinci kılacağım."));
trsuperobject.biblemap.put("b23_60_16", new trsuperobject(23,60,16,"Uluslar ve krallıklarBir anne gibi seni emzirecekler.O zaman bileceksin ki, seni kurtaran RAB,Seni fidyeyle kurtaran, Yakupun Güçlüsü benim."));
trsuperobject.biblemap.put("b23_60_17", new trsuperobject(23,60,17,"Sana tunç yerine altın,Demir yerine gümüş, ağaç yerine tunç,Taş yerine demir getireceğim.Barışı yöneticin, doğruluğu önderin yapacağım."));
trsuperobject.biblemap.put("b23_60_18", new trsuperobject(23,60,18,"Ülkenden şiddet, sınır boylarındanSoygun ve yıkım haberleri duyulmayacak artık.Surlarına Kurtuluş, kapılarına Övgü adını vereceksin."));
trsuperobject.biblemap.put("b23_60_19", new trsuperobject(23,60,19,"‹‹Gündüz ışığın güneş olmayacak artık,Ay da aydınlatmayacak seni;Çünkü RAB sonsuz ışığın,Tanrın görkemin olacak."));
trsuperobject.biblemap.put("b23_60_20", new trsuperobject(23,60,20,"Artık güneşin batmayacak, ayın çekilmeyecek,Çünkü RAB sonsuz ışığın olacak,Sona erecek yas günlerin."));
trsuperobject.biblemap.put("b23_60_21", new trsuperobject(23,60,21,"Halkının hepsi doğru kişiler olacak;El emeğim, görkemimi göstermek için diktiğim fidan,Ülkeyi sonsuza dek mülk edinecek."));
trsuperobject.biblemap.put("b23_60_22", new trsuperobject(23,60,22,"En küçük ailen bini bulacak,Sayıca en az olanı koca bir ulus olacak.Ben RAB, zamanı gelince bunu hızlandıracağım.››"));
trsuperobject.biblemap.put("b23_61_1-3", new trsuperobject(23,61,1-3,"Egemen RABbin Ruhu üzerimdedir.Çünkü O beni yoksullara müjde iletmek için meshetti.Yüreği ezik olanların yaralarını sarmak için,Tutsaklara serbest bırakılacaklarını,Zindanlarda bulunanlara kurtulacaklarını,RABbin lütuf yılını,Tanrımızın öç alacağı günü ilan etmek,Yas tutanların hepsini avutmak,Siyonda yas tutanlara yardım sağlamak-Kül yerine çelenk,Yas yerine sevinç yağı,Çaresizlik ruhu yerineOnlara övgü giysisini vermek- içinRAB beni gönderdi.Öyle ki, RABbin görkemini yansıtmak için,Onlara ‹‹RABbin diktiği doğruluk ağaçları›› densin."));
trsuperobject.biblemap.put("b23_61_4", new trsuperobject(23,61,4,"O zaman eski yıkıntıları yeniden inşa edecek,Çoktan viraneye dönmüş yerleri yeniden kuracak,Kuşaklar boyu yıkık kalmış kentleri onaracaklar."));
trsuperobject.biblemap.put("b23_61_5", new trsuperobject(23,61,5,"Yabancılar sürülerinizi güdecek,Irgatınız, bağcınız olacaklar."));
trsuperobject.biblemap.put("b23_61_6", new trsuperobject(23,61,6,"Sizlerse RABbin kâhinleri,Tanrımızın görevlileri diye çağrılacaksınız.Ulusların servetiyle beslenecek,Zenginlikleriyle övüneceksiniz."));
trsuperobject.biblemap.put("b23_61_7", new trsuperobject(23,61,7,"Utanç yerine iki kat onur bulacaksınız,Aşağılanma yerine payınızla sevineceksiniz,Böylece ülkenizde iki kat mülk edineceksiniz;Sevinciniz sonsuz olacak."));
trsuperobject.biblemap.put("b23_61_8", new trsuperobject(23,61,8,"‹‹Çünkü ben RAB adaleti severim,Nefret ederim soygun ve haksızlıktan.Sözümde durup hak ettiklerini verecek,Onlarla ebedi bir antlaşma yapacağım. yakmalık sunulardan››."));
trsuperobject.biblemap.put("b23_61_9", new trsuperobject(23,61,9,"Soylarından gelenler uluslar arasında,Torunları halklar arasında tanınacak.Onları gören herkesRABbin kutsadığı soy olduklarını anlayacak.››"));
trsuperobject.biblemap.put("b23_61_10", new trsuperobject(23,61,10,"RABde büyük sevinç bulacağım,Tanrımla yüreğim coşacak.Çünkü çelenkle süslenmiş güvey gibi,Takılarını kuşanmış gelin gibi,Bana kurtuluş giysisini giydirdi,Beni doğruluk kaftanıyla örttü."));
trsuperobject.biblemap.put("b23_61_11", new trsuperobject(23,61,11,"Toprak filizlerini nasıl çıkartır,Bahçe ekilen tohumları nasıl yetiştirirse,Egemen RAB de doğruluk ve övgüyüBütün ulusların önünde öyle yetiştirecek."));
trsuperobject.biblemap.put("b23_62_1", new trsuperobject(23,62,1,"Zaferi ışık gibi parlayıncaya,Kurtuluşu meşale gibi yanıncaya dekSiyon uğruna susmayacak,Yeruşalim uğruna sessiz kalmayacağım."));
trsuperobject.biblemap.put("b23_62_2", new trsuperobject(23,62,2,"Uluslar senin zaferini,Bütün krallar görkemini görecek.RABbin kendi ağzıyla belirlediği yeni bir adla anılacaksın."));
trsuperobject.biblemap.put("b23_62_4", new trsuperobject(23,62,4,"Artık sana ‹‹Terk edilmiş››,Ülkene ‹‹Virane›› denmeyecek;Bunun yerine sana ‹‹Sevdiğim››,Ülkene ‹‹Evli›› denecek.Çünkü RAB seni seviyor,Ülken de evli sayılacak."));
trsuperobject.biblemap.put("b23_62_5", new trsuperobject(23,62,5,"Bir delikanlı bir kızla nasıl evlenirse,Oğulların da seninle öyle evlenecek.Güvey gelinle nasıl sevinirse,Tanrın da seninle öyle sevinecek. veren seninle evlenecek››."));
trsuperobject.biblemap.put("b23_62_6-7", new trsuperobject(23,62,6-7,"Ey Yeruşalim, surlarına bekçiler diktim,Gece gündüz hiç susmayacaklar.Ey RABbe sözünü anımsatanlar, Yeruşalimi pekiştirene,Onu yeryüzünün övüncü kılana dekDurup dinlenmeden RABbe yakarın, Ona rahat vermeyin."));
trsuperobject.biblemap.put("b23_62_9", new trsuperobject(23,62,9,"Tahılı devşiren yiyecekVe RABbe övgüler sunacak.Üzümü toplayan,Şarabını kutsal avlularımda içecek.››"));
trsuperobject.biblemap.put("b23_62_10", new trsuperobject(23,62,10,"Geçin, geçin kent kapılarından!Halkın yolunu açın!Toprak yığıp yol yapın,Taşları ayıklayın, uluslar için sancak dikin!"));
trsuperobject.biblemap.put("b23_62_11", new trsuperobject(23,62,11,"RAB çağrısını dünyanın dört bucağına duyurdu:‹‹Siyon kızına, ‹İşte kurtuluşun geliyor› deyin,‹Ücreti kendisiyle birlikte, ödülü önündedir.››"));
trsuperobject.biblemap.put("b23_62_12", new trsuperobject(23,62,12,"Siyon halkına, ‹‹RAB'bin fidyeyle kurtardığı kutsal halk›› diyecekler.Ve sen Yeruşalim,‹‹Aranan, terk edilmemiş kent›› diye anılacaksın."));
trsuperobject.biblemap.put("b23_63_1", new trsuperobject(23,63,1,"Edomdanfç, BosradanfçAl giysiler içinde bu gelen kim?Göz kamaştırıcı giysiler içinde,Büyük güçle yürüyen kim?‹‹O benim! Adaleti duyuran,Kurtarmaya gücü olan.›› kentlerinden olan Bosra ise ‹‹Bağbozumu›› sözcüğünü çağrıştırıyor."));
trsuperobject.biblemap.put("b23_63_2", new trsuperobject(23,63,2,"Giysilerin neden kırmızı?Üstün başın neden çukurda üzüm çiğneyen biri gibi kızıla bulanmış?"));
trsuperobject.biblemap.put("b23_63_3", new trsuperobject(23,63,3,"‹‹Çukurda üzümü tek başıma çiğnedim,Yanımda halklardan kimse yoktu.Öfkeyle çiğnedim onları,Gazapla ayaklarımın altına aldım.Kanları giysilerime sıçradı, bütün elbisemi kirletti."));
trsuperobject.biblemap.put("b23_63_4", new trsuperobject(23,63,4,"Çünkü öç alma günü yüreğimdeydi,Halkımı kurtaracağım yıl gelmişti."));
trsuperobject.biblemap.put("b23_63_5", new trsuperobject(23,63,5,"Baktım, yardım edecek kimse yoktu,Destek verecek kimsenin olmayışına şaştım;Gücüm kurtuluş sağladı,Gazabım bana destek oldu."));
trsuperobject.biblemap.put("b23_63_6", new trsuperobject(23,63,6,"Öfkeyle halkları çiğnedim,Onları gazapla sarhoş ettim,Yere akıttım kanlarını.››"));
trsuperobject.biblemap.put("b23_63_7", new trsuperobject(23,63,7,"Şefkati ve iyiliği uyarıncaBizim için yaptıklarından, evet,İsrail halkı için yaptığı bütün iyiliklerinden ötürüRABbin iyiliklerini ve övülesi işlerini anacağım."));
trsuperobject.biblemap.put("b23_63_9", new trsuperobject(23,63,9,"Sıkıntı çektiklerinde O da sıkıntı çekti.Huzurundan çıkan melek onları kurtardı.Sevgisi ve merhametinden ötürü onları kurtardı,Geçmişte onları sürekli yüklenip taşıdı."));
trsuperobject.biblemap.put("b23_63_10", new trsuperobject(23,63,10,"Ama başkaldırıp Onun Kutsal Ruhunu incittiler.O da düşmanları olup onlara karşı savaştı."));
trsuperobject.biblemap.put("b23_63_11-13", new trsuperobject(23,63,11-13,"Sonra halkı eski günleri,Musanın dönemini anımsadı.‹‹Çobanlarıyla birlikte onları denizden geçiren,Kutsal Ruhunu aralarına yerleştiren,Görkemli gücüyle Musanın sağında yol alan,Sonsuz onur kazanmak için önlerinde suları yaran,Bir at nasıl tökezlemeden kırdan geçerseOnları deniz yatağından öyle geçiren RAB nerede?››Diye sordular."));
trsuperobject.biblemap.put("b23_63_14", new trsuperobject(23,63,14,"Ovaya götürülen sürü gibiRABbin Ruhu onları rahata kavuşturdu.İşte adını onurlandırmak içinHalkına böyle yol gösterdi."));
trsuperobject.biblemap.put("b23_63_15", new trsuperobject(23,63,15,"Ya RAB, gökten bak,Kutsal, görkemli ve yüce yerinden bizi gör!Gayretin, gücün nerede?Gönlündeki özlem ve merhametiBizden esirgedin."));
trsuperobject.biblemap.put("b23_63_16", new trsuperobject(23,63,16,"Babamız sensin.İbrahim bizi tanımasa da,İsrail bizi kabul etmese de,Babamızsın, ya RAB,Ezelden beri adın ‹‹Kurtarıcımız››dır."));
trsuperobject.biblemap.put("b23_63_17", new trsuperobject(23,63,17,"Ya RAB, neden bizi yolundan saptırıyor,İnatçı kılıyor,Senden korkmamızı engelliyorsun?Kulların uğruna,Mirasın olan oymakların uğruna geri dön."));
trsuperobject.biblemap.put("b23_63_18", new trsuperobject(23,63,18,"Kutsal halkın kısa süre tapınağına sahip oldu,Ama düşmanlarımız onu çiğnedi."));
trsuperobject.biblemap.put("b23_63_19", new trsuperobject(23,63,19,"Öteden beri yönetmediğin,Sana ait olmayan bir halk gibi olduk."));
trsuperobject.biblemap.put("b23_64_1-2", new trsuperobject(23,64,1-2,"Ya RAB, adını düşmanlarına duyurmak içinKeşke gökleri yarıp insen!Dağlar önünde sarsılsa!Gelişin, ateşin çalıları tutuşturmasına,Suyu kaynatmasına benzese!Uluslar senin önünde titrese!"));
trsuperobject.biblemap.put("b23_64_3", new trsuperobject(23,64,3,"Beklemediğimiz olağanüstü işler yaparakYeryüzüne indin, dağlar önünde sarsıldı."));
trsuperobject.biblemap.put("b23_64_4", new trsuperobject(23,64,4,"Çünkü kendisine umut bağlayanlar içinEtkin olan tek Tanrı sensin;Senden başkasını hiçbir zaman hiç kimse işitmedi,Hiçbir kulak duymadı, hiçbir göz görmedi."));
trsuperobject.biblemap.put("b23_64_5", new trsuperobject(23,64,5,"Doğru olanı sevinçle yapanların,Senin yollarından yürüyüp seni unutmayanların yardımına koşarsın.Ama onlara karşı uzun süre günah işlediğimizde öfkelendin.Nasıl kurtuluruz?"));
trsuperobject.biblemap.put("b23_64_6", new trsuperobject(23,64,6,"Hepimiz murdar olanlara benzedik,Bütün doğru işlerimiz kirli âdet bezi gibi.Yaprak gibi soluyoruz,Suçlarımız rüzgar gibi sürükleyip götürüyor bizi."));
trsuperobject.biblemap.put("b23_64_7", new trsuperobject(23,64,7,"Adınla seni çağıran, sana tutunmak için çaba gösteren yok;Çünkü bizden yüz çevirdin,Suçlarımız yüzünden bizi tükettin."));
trsuperobject.biblemap.put("b23_64_8", new trsuperobject(23,64,8,"Yine de Babamız sensin, ya RAB,Biz kiliz, sen çömlekçisin.Hepimiz senin ellerinin eseriyiz."));
trsuperobject.biblemap.put("b23_64_9", new trsuperobject(23,64,9,"Ya RAB, fazla öfkelenme,Suçlarımızı sonsuza dek anma.Lütfen bak bize, hepimiz senin halkınız."));
trsuperobject.biblemap.put("b23_64_10", new trsuperobject(23,64,10,"Kutsal kentlerin çölleşti,Siyon çöl oldu,Yeruşalim viraneye döndü."));
trsuperobject.biblemap.put("b23_64_11", new trsuperobject(23,64,11,"Atalarımızın sana övgü sunduğuKutsal ve görkemli tapınağımız yandı,Değer verdiğimiz her yer yıkıntıya döndü."));
trsuperobject.biblemap.put("b23_64_12", new trsuperobject(23,64,12,"Bunlara karşın, ya RAB,Hâlâ kendini tutacak mısın,Suskun kalıp bize alabildiğine eziyet çektirecek misin?"));
trsuperobject.biblemap.put("b23_65_1", new trsuperobject(23,65,1,"‹‹Beni sormayanlara göründüm,Aramayanlar beni buldu.Adımla anılmayan bir ulusa,‹Buradayım, buradayım› dedim."));
trsuperobject.biblemap.put("b23_65_2", new trsuperobject(23,65,2,"Kötü yolda yürüyen,Kendi tasarılarının ardınca gidenAsi bir halkaBütün gün ellerimi uzatıp durdum."));
trsuperobject.biblemap.put("b23_65_3", new trsuperobject(23,65,3,"O halk ki, bahçelerde kurban keserek,Tuğlalar üzerinde buhur yakarakGözümün içine baka baka boyuna öfkelendirir beni."));
trsuperobject.biblemap.put("b23_65_4", new trsuperobject(23,65,4,"Mezarlıkta oturur,Gizli yerlerde geceler,Domuz eti yerler;Kaplarında haram et var."));
trsuperobject.biblemap.put("b23_65_5", new trsuperobject(23,65,5,"Birbirlerine, ‹Uzak dur, yaklaşma› derler,‹Çünkü ben senden daha kutsalım.›Böyleleri burnumda duman,Bütün gün yanan ateştir."));
trsuperobject.biblemap.put("b23_65_6-7", new trsuperobject(23,65,6-7,"‹‹Bakın, yanıt önümde yazılı duruyor.Susmayacak, suçlarının karşılığını vereceğim.Onların da atalarının da suçlarının cezasınıBaşlarına getireceğim›› diyor RAB.‹‹Çünkü dağların üzerinde buhur yaktılar,Tepelerin üzerinde beni aşağıladılar.Bu nedenle eskiden yaptıklarının karşılığınıBaşlarına getireceğim.››"));
trsuperobject.biblemap.put("b23_65_9", new trsuperobject(23,65,9,"Yakup soyunu sürdürecek,Dağlarımı miras alacak olanlarıYahuda soyuna bırakacağım.Seçtiklerim oraları miras alacak,Kullarım orada yaşayacak."));
trsuperobject.biblemap.put("b23_65_10", new trsuperobject(23,65,10,"Şaron, bana yönelen halkımın sürülerine ağıl,Akor Vadisi sığırlarına barınak olacak."));
trsuperobject.biblemap.put("b23_65_11", new trsuperobject(23,65,11,"‹‹Ama sizler, RABbi terk edenler,Kutsal dağımı unutanlar,Talih ilahına sofra kuranlar,Kısmet ilahına karışık şarap sunanlar,"));
trsuperobject.biblemap.put("b23_65_12", new trsuperobject(23,65,12,"Ben de sizi kılıca kısmet edeceğim,Boğazlanmak üzere eğileceksiniz hepiniz.Çünkü çağırdığımda yanıt vermediniz,Konuştuğumda dinlemediniz;Gözümde kötü olanı yaptınız,Hoşlanmadığımı seçtiniz.››"));
trsuperobject.biblemap.put("b23_65_13", new trsuperobject(23,65,13,"Bu yüzden Egemen RAB diyor ki,‹‹Bakın, kullarım yemek yiyecek,Ama siz aç kalacaksınız.Kullarım içecek,Ama siz susuz kalacaksınız.Kullarım sevinecek,Ama sizin yüzünüz kızaracak."));
trsuperobject.biblemap.put("b23_65_14", new trsuperobject(23,65,14,"Kullarım mutluluk içinde ezgiler söyleyecek,Ama siz yürek acısından feryat edecek,Ezik bir ruhla haykıracaksınız."));
trsuperobject.biblemap.put("b23_65_15", new trsuperobject(23,65,15,"Adınız seçtiklerimin ağzında ancak lanet olarak kalacak.Egemen RAB sizi öldürecek,Ama kullarına başka bir ad verecek."));
trsuperobject.biblemap.put("b23_65_16", new trsuperobject(23,65,16,"Öyle ki, ülkede kim bereket isteseSadık Tanrıdan isteyecek;Ülkede kim ant içse,Sadık Tanrı üzerine ant içecek.Çünkü geçmiş sıkıntılar unutulupGözümden saklanacak.››"));
trsuperobject.biblemap.put("b23_65_17", new trsuperobject(23,65,17,"‹‹Çünkü bakın, yeni bir yeryüzü,Yeni bir gök yaratmak üzereyim;Geçmiştekiler anılmayacak, akla bile gelmeyecek."));
trsuperobject.biblemap.put("b23_65_18", new trsuperobject(23,65,18,"Yaratacaklarımla sonsuza dek sevinip coşun;Çünkü Yeruşalimi coşku,Halkını sevinç kaynağı olarak yaratacağım."));
trsuperobject.biblemap.put("b23_65_19", new trsuperobject(23,65,19,"Yeruşalim için sevinecek,Halkım için coşacağım.Orada ağlayış ve feryat duyulmayacak artık."));
trsuperobject.biblemap.put("b23_65_20", new trsuperobject(23,65,20,"Orada birkaç gün yaşayıp ölen bebekler olmayacak,Yaşını başını almadan kimse ölümü tatmayacak.Yüz yaşında ölen genç,Yüz yaşına basmayan kişi lanetli sayılacak."));
trsuperobject.biblemap.put("b23_65_21", new trsuperobject(23,65,21,"Evler yapıp içlerinde yaşayacak,Bağlar dikip meyvesini yiyecekler."));
trsuperobject.biblemap.put("b23_65_22", new trsuperobject(23,65,22,"Yaptıkları evlerde başkası oturmayacak,Diktikleri bağın meyvesini başkası yemeyecek.Çünkü halkım ağaçlar gibi uzun yaşayacak,Seçtiklerim, elleriyle ürettiklerinin tadını çıkaracaklar."));
trsuperobject.biblemap.put("b23_65_23", new trsuperobject(23,65,23,"Emek vermeyecekler boş yere,Felakete uğrayan çocuklar doğurmayacaklar.Çünkü kendileri de çocukları daRABbin kutsadığı soy olacak."));
trsuperobject.biblemap.put("b23_65_24", new trsuperobject(23,65,24,"Onlar bana yakarmadan yanıt verecek,Daha konuşurlarken işiteceğim onları."));
trsuperobject.biblemap.put("b23_65_25", new trsuperobject(23,65,25,"Kurtla kuzu birlikte otlayacak,Aslan sığır gibi saman yiyecek.Yılanın yiyeceğiyse toprak olacak.Kutsal dağımın hiçbir yerindeKimse zarar vermeyecek, yok etmeyecek.››Böyle diyor RAB."));
trsuperobject.biblemap.put("b23_66_2", new trsuperobject(23,66,2,"Çünkü bütün bunları ellerim yaptı,Hepsi böylece var oldu›› diyor RAB.  ‹‹Ancak ben alçakgönüllüye, ruhu ezik olana,Sözümden titreyen kişiye değer veririm."));
trsuperobject.biblemap.put("b23_66_3", new trsuperobject(23,66,3,"Sığır boğazlayan, adam öldüren gibidir,Davar kurban eden, köpek boynu kıran,Tahıl sunusu getiren, domuz kanı sunan,Anma sunusu olarak günnük yakan, putperest gibidir.Evet, bunlar kendi yollarını seçtiler,Yaptıkları iğrençliklerden hoşlanıyorlar."));
trsuperobject.biblemap.put("b23_66_4", new trsuperobject(23,66,4,"Ben de onlar için yıkımı seçecek,Korktuklarını başlarına getireceğim.Çünkü çağırdığımda yanıt veren olmadı,Konuştuğumda dinlemediler,Gözümde kötü olanı yaptılar,Hoşlanmadığımı seçtiler.››"));
trsuperobject.biblemap.put("b23_66_6", new trsuperobject(23,66,6,"Kentten gürültülü sesler,Tapınaktan bir ses yükseliyor!Düşmanlarına hak ettikleri karşılığı verenRABbin sesidir bu."));
trsuperobject.biblemap.put("b23_66_7", new trsuperobject(23,66,7,"‹‹Doğum sancısı çekmeden doğurdu,Sancısı tutmadan bir erkek çocuk doğurdu."));
trsuperobject.biblemap.put("b23_66_8", new trsuperobject(23,66,8,"Kim böyle bir şey duydu?Kim böyle şeyler gördü?Bir ülke bir günde doğar mı,Bir anda doğar mı bir ulus?Ama Siyon, ağrısı tutar tutmaz çocuklarını doğurdu."));
trsuperobject.biblemap.put("b23_66_9", new trsuperobject(23,66,9,"Doğum anına dek getiririm deDoğuracak gücü vermez miyim?›› diyor RAB.‹‹Doğuracak güç veren ben, rahmi kapatır mıyım?›› diyor Tanrın."));
trsuperobject.biblemap.put("b23_66_10", new trsuperobject(23,66,10,"‹‹Yeruşalimle birlikte sevinin,Onu sevenler, hepiniz onun için coşun,Yeruşalim için yas tutanlar, onunla sevinçle coşun."));
