trsuperobject.biblemap.put("b23_66_17", new trsuperobject(23,66,17,"‹‹Bahçelere girmek için kendilerini arıtıp kutsayanlar, domuz, fare ve öteki iğrenç hayvanların etini yiyenlerin ortasında duranı izleyenler hep birlikte yok olacaklar›› diyor RAB,"));
trsuperobject.biblemap.put("b23_66_18", new trsuperobject(23,66,18,"‹‹Çünkü ben onların eylemlerini de düşüncelerini de bilirim. Bütün ulusları ve dilleri bir araya toplayacağım an geliyor; gelip yüceliğimi görecekler."));
trsuperobject.biblemap.put("b23_66_19", new trsuperobject(23,66,19,"‹‹Aralarına bir belirti koyacağım. Onlardan kaçıp kurtulanları uluslara, Tarşişe, Pûla, Luda -yay gerenlere- Tuvala, Yâvana, ünümü duymamış, yüceliğimi görmemiş uzak kıyı halklarına göndereceğim. Uluslar arasında yüceliğimi ilan edecekler."));
trsuperobject.biblemap.put("b23_66_20", new trsuperobject(23,66,20,"İsrailoğulları tahıl sunularını pak kaplar içinde RABbin Tapınağına nasıl getiriyorsa, onlar da bütün kardeşlerinizi uluslardan atlarla, savaş arabalarıyla, at arabalarıyla, katırlarla, develerle kutsal dağıma, Yeruşalime, RABbe sunu olarak getirecekler.›› Böyle diyor RAB."));
