trsuperobject.biblemap.put("b24_4_9", new trsuperobject(24,4,9,"‹‹O gün›› diyor RAB,‹‹Kral da önderler de yılacak,Kâhinler şaşkına dönecek,Peygamberler donakalacak.››"));
trsuperobject.biblemap.put("b24_4_10", new trsuperobject(24,4,10,"O zaman, ‹‹Ah, Egemen RAB›› dedim, ‹‹Esenlikte olacaksınız› diyerek bu halkı da Yeruşalimi de tam anlamıyla aldattın. Çünkü kılıç boğazımıza dayandı.››"));
trsuperobject.biblemap.put("b24_4_11", new trsuperobject(24,4,11,"O zaman bu halka ve Yeruşalime, ‹‹Çöldeki çıplak tepelerden halkıma doğru sıcak bir rüzgar esiyor, ama harman savurmak ya da ayırmak için değil›› denecek,"));
trsuperobject.biblemap.put("b24_4_12", new trsuperobject(24,4,12,"‹‹Benden gelen bu rüzgar çok daha güçlü olacak. Şimdi bu halka yargılarımı bildiriyorum.››"));
trsuperobject.biblemap.put("b24_4_13", new trsuperobject(24,4,13,"İşte düşman bulut gibi ilerliyor;Savaş arabaları kasırga sanki,Atları kartallardan daha çevik.Vay başımıza! Mahvolduk!"));
trsuperobject.biblemap.put("b24_4_14", new trsuperobject(24,4,14,"Ey Yeruşalim, yüreğini kötülükten arındır ki,Kurtulasın.Ne zamana dek yüreğinde kötü düşünceler barındıracaksın?"));
trsuperobject.biblemap.put("b24_4_15", new trsuperobject(24,4,15,"Dandan bir ses bildiriyor,Efrayim dağlarından kötü haber duyuruyor!"));
trsuperobject.biblemap.put("b24_4_16", new trsuperobject(24,4,16,"‹‹Uluslara duyurun,Yeruşalime bildirin:‹Uzak bir ülkeden gelen ordu çevresini kuşatacak,Yahuda kentlerine karşıSavaş naraları atacaklar."));
trsuperobject.biblemap.put("b24_4_17", new trsuperobject(24,4,17,"Bir tarlayı koruyanlar gibiKuşatacaklar Yeruşalimi.Çünkü Yeruşalim bana başkaldırdı›› diyor RAB."));
trsuperobject.biblemap.put("b24_4_18", new trsuperobject(24,4,18,"‹‹Kendi davranışların, kendi yaptıklarınBaşına gelmesine neden oldu bunların.Cezan bu.Ne acı!Nasıl da yüreğine işliyor!››"));
trsuperobject.biblemap.put("b24_4_19", new trsuperobject(24,4,19,"Ah, içim, içim!Acıdan kıvranıyorum.Ah, yüreğim, yüreğim çarpıyor.Sessiz duramıyorum!Çünkü boru sesini, savaş naralarını işittim!"));
trsuperobject.biblemap.put("b24_4_20", new trsuperobject(24,4,20,"Felaket felaketi izliyor,Bütün ülke viran oldu.Bir anda çadırlarım,Perdelerim yok oldu."));
trsuperobject.biblemap.put("b24_4_21", new trsuperobject(24,4,21,"Ne zamana dek düşman sancağını görmek,Boru sesini duymak zorunda kalacağım?"));
trsuperobject.biblemap.put("b24_4_22", new trsuperobject(24,4,22,"‹‹Halkım akılsızdır,Beni tanımıyor.Aptal çocuklardır,Akılları yok.Kötülük etmeyi iyi bilir,İyilik etmeyi bilmezler›› diyor RAB."));
trsuperobject.biblemap.put("b24_4_23", new trsuperobject(24,4,23,"Ben Yeremya yere baktım, şekilsizdi, boştu,Göğe baktım, ışık yoktu."));
trsuperobject.biblemap.put("b24_4_24", new trsuperobject(24,4,24,"Dağlara baktım, titriyorlardı,Bütün tepeler sarsılıyordu."));
trsuperobject.biblemap.put("b24_4_25", new trsuperobject(24,4,25,"Baktım, insan yoktu,Gökte uçan bütün kuşlar kaçmıştı."));
trsuperobject.biblemap.put("b24_4_26", new trsuperobject(24,4,26,"Baktım, verimli toprak çöle dönmüş,Bütün kentler yıkılmıştı.Bütün bunlar RABbin yüzünden,Onun kızgın öfkesi yüzünden olmuştu."));
trsuperobject.biblemap.put("b24_4_27", new trsuperobject(24,4,27,"RAB diyor ki,‹‹Bütün ülke viran olacak,Ama onu büsbütün yok etmeyeceğim."));
trsuperobject.biblemap.put("b24_4_28", new trsuperobject(24,4,28,"Bu yüzden yeryüzü yasa gömülecek,Gök kararacak;Çünkü ben söyledim, ben tasarladım.Fikrimi değiştirmeyecek,Verdiğim karardan dönmeyeceğim.››"));
trsuperobject.biblemap.put("b24_4_29", new trsuperobject(24,4,29,"Her kentin halkı,Atlılarla okçuların gürültüsünden kaçıyor.Kimi çalılıklara giriyor,Kimi kayalıklara tırmanıyor.Bütün kentler terk edildi,Oralarda kimse yaşamıyor."));
trsuperobject.biblemap.put("b24_4_30", new trsuperobject(24,4,30,"Ey sen, viran olmuş kent,Kırmızı giysiler giymekle,Altın süsler bezenmekle,Gözüne sürme çekmekle ne elde edeceksin?Kendini böyle güzelleştirmen boşuna.Oynaşların seni küçümsüyor,Canını almak istiyorlar."));
trsuperobject.biblemap.put("b24_4_31", new trsuperobject(24,4,31,"Sancı çeken kadının haykırışını,İlk çocuğunu doğuran kadının çektiği acıyı,Ellerini uzatmış, soluğu kesilmiş Siyon kızının,‹‹Eyvah! Katillerin karşısında bayılıyorum››Diye haykırdığını işitir gibi oldum."));
trsuperobject.biblemap.put("b24_5_1", new trsuperobject(24,5,1,"‹‹Yeruşalim sokaklarında dolaşın,Çevrenize bakıp düşünün,Kent meydanlarını araştırın.Eğer adil davranan,Gerçeği arayan bir kişi bulursanız,Bu kenti bağışlayacağım."));
trsuperobject.biblemap.put("b24_5_2", new trsuperobject(24,5,2,"‹RABbin varlığı hakkı için› deseler de,Aslında yalan yere ant içiyorlar.››"));
trsuperobject.biblemap.put("b24_5_3", new trsuperobject(24,5,3,"Ya RAB, gözlerin gerçeği arıyor.Onları vurdun, ama incinmediler,Onları yiyip bitirdin,Ama yola gelmeyi reddettiler.Yüzlerini kayadan çok sertleştirdiler,Geri dönmek istemediler."));
trsuperobject.biblemap.put("b24_5_4", new trsuperobject(24,5,4,"‹‹Bunlar sadece yoksul kişiler,Akılsızlar›› dedim,‹‹Çünkü RABbin yolunu,Tanrılarının buyruklarını bilmiyorlar."));
trsuperobject.biblemap.put("b24_5_5", new trsuperobject(24,5,5,"Büyüklere gidip onlarla konuşayım.RABbin yolunu,Tanrılarının buyruklarını bilirler kuşkusuz.››Gelgelelim onlar da boyunduruğu kırmış,Bağları koparmıştı."));
trsuperobject.biblemap.put("b24_5_6", new trsuperobject(24,5,6,"Bu yüzden ormandan bir aslan çıkıp onlara saldıracak,Çölden gelen bir kurt onları parça parça edecek,Bir pars kentlerinin önünde pusu kuracak,Oradan çıkan herkes parçalanacak.Çünkü isyanları çok,Döneklikleri sayısızdır."));
trsuperobject.biblemap.put("b24_5_7", new trsuperobject(24,5,7,"‹‹Yaptıklarından ötürü neden bağışlayayım seni?Çocukların beni terk etti,Tanrı olmayan ilahların adıyla ant içtiler.Onları doyurduğumda zina ettiler,Fahişelerin evlerine doluştular."));
trsuperobject.biblemap.put("b24_5_8", new trsuperobject(24,5,8,"Şehvet düşkünü, besili aygırlar!Her biri komşusunun karısına kişniyor."));
trsuperobject.biblemap.put("b24_5_9", new trsuperobject(24,5,9,"Bu yüzden onları cezalandırmayayım mı?›› diyor RAB,‹‹Böyle bir ulustan öcümü almayayım mı?"));
trsuperobject.biblemap.put("b24_5_10", new trsuperobject(24,5,10,"‹‹Bağlarını dolaşıpAsmalarını kesin,Ama büsbütün yok etmeyin.Dallarını koparıp atın,Çünkü onlar RABbe ait değil."));
trsuperobject.biblemap.put("b24_5_11", new trsuperobject(24,5,11,"İsrail ve Yahuda halkıBana sürekli ihanet etti›› diyor RAB."));
trsuperobject.biblemap.put("b24_5_12", new trsuperobject(24,5,12,"RAB için yalan söyleyerek,‹‹O bir şey yapmaz.Felaket bize uğramayacak,Kılıç da kıtlık da görmeyeceğiz›› dediler."));
trsuperobject.biblemap.put("b24_5_13", new trsuperobject(24,5,13,"Peygamberler lafebesidir,Tanrının sözü onlarda değil.Onlara böyle yapılacak."));
trsuperobject.biblemap.put("b24_5_14", new trsuperobject(24,5,14,"Bu yüzden, Her Şeye Egemen RAB Tanrı diyor ki,‹‹Madem böyle şeyler konuşuyorsunuz,Ben de sözümü ağzınıza ateş,Bu halkı da odun edeceğim;Ateş onları yakıp yok edecek."));
trsuperobject.biblemap.put("b24_5_15", new trsuperobject(24,5,15,"Ey İsrail halkı,Uzaktan gelecek bir ulusuÜzerinize saldırtacağım›› diyor RAB,‹‹Köklü, eski bir ulus;Sen onların dilini bilmez,Ne dediklerini anlamazsın."));
trsuperobject.biblemap.put("b24_5_16", new trsuperobject(24,5,16,"Oklarının kılıfı açık bir mezar gibidir,Hepsi birer yiğittir."));
trsuperobject.biblemap.put("b24_5_17", new trsuperobject(24,5,17,"Ürününü, yiyeceklerini tüketecek,Oğullarını, kızlarını öldürecekler;Davarlarını, sığırlarını,Asmalarının, incir ağaçlarının meyvesini yiyecek,Güvendiğin surlu kentleriniKılıçla yerle bir edecekler."));
trsuperobject.biblemap.put("b24_5_18", new trsuperobject(24,5,18,"‹‹Ama o günlerde bile sizi büsbütün yok etmeyeceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_5_19", new trsuperobject(24,5,19,"‹‹Tanrımız RAB neden bize bütün bunları yaptı?› diye sorduklarında, şöyle yanıtlayacaksın: ‹Beni nasıl bıraktınız, ülkenizde yabancı ilahlara nasıl kulluk ettinizse, siz de kendinize ait olmayan bir ülkede yabancılara öyle kulluk edeceksiniz.›"));
trsuperobject.biblemap.put("b24_5_20", new trsuperobject(24,5,20,"‹‹Yakup soyuna bildirin,Yahuda halkına duyurun:"));
trsuperobject.biblemap.put("b24_5_21", new trsuperobject(24,5,21,"Ey gözleri olan ama görmeyen,Kulakları olan ama işitmeyen,Sağduyudan yoksun akılsız halk,Şunu dinle:"));
trsuperobject.biblemap.put("b24_5_22", new trsuperobject(24,5,22,"Benden korkman gerekmez mi?›› diyor RAB,‹‹Huzurumda titremen gerekmez mi?Ben ki, sonsuza dek geçerli bir kurallaDenize sınır olarak kumu koydum.Deniz sınırı geçemez;Dalgalar kabarsa da üstün gelemez,Kükrese de sınırı aşamaz."));
trsuperobject.biblemap.put("b24_5_23", new trsuperobject(24,5,23,"Ama bu halkın yüreği asi ve inatçı.Sapmışlar, kendi yollarına gitmişler."));
trsuperobject.biblemap.put("b24_5_24", new trsuperobject(24,5,24,"İçlerinden,‹İlk ve son yağmurları zamanında yağdıran,Belli ürün biçme haftalarını bizim için koruyanTanrımız RABden korkalım› demiyorlar."));
trsuperobject.biblemap.put("b24_5_25", new trsuperobject(24,5,25,"Bunları uzaklaştıran suçlarınızdı,Bu iyilikten sizi yoksun bırakan günahlarınızdı."));
trsuperobject.biblemap.put("b24_5_26", new trsuperobject(24,5,26,"‹‹Halkım arasında kötü kişiler var.Kuş avlamak için pusuya yatanlar gibiTuzak kuruyor, insan yakalıyorlar."));
trsuperobject.biblemap.put("b24_5_27", new trsuperobject(24,5,27,"Kuş dolu bir kafes nasılsa,Onların evleri de hileyle dolu.Bu sayede güçlenip zengin oldular,"));
trsuperobject.biblemap.put("b24_5_28", new trsuperobject(24,5,28,"Semirip parladılar,Yaptıkları kötülüklerle sınırı aştılar.Kazanabilecekleri halde öksüzün davasına bakmıyor,Yoksulun hakkını savunmuyorlar."));
trsuperobject.biblemap.put("b24_5_29", new trsuperobject(24,5,29,"Bu yüzden onları cezalandırmayayım mı?›› diyor RAB,‹‹Böyle bir ulustan öcümü almayayım mı?"));
trsuperobject.biblemap.put("b24_5_30", new trsuperobject(24,5,30,"‹‹Ülkede korkunç, dehşet verici bir şey oldu:"));
trsuperobject.biblemap.put("b24_5_31", new trsuperobject(24,5,31,"Peygamberler yalan peygamberlik ediyor,Halkı başına buyruk kâhinler yönetiyor,Halkım da bunu benimsiyor.Ama bunun sonunda ne yapacaksınız?››"));
trsuperobject.biblemap.put("b24_6_1", new trsuperobject(24,6,1,"‹‹Güvenliğiniz için kaçın, ey Benyamin halkı!Yeruşalimden kaçın!Tekoada boru çalın!Beythakkereme bir işaret koyun.Çünkü kuzeyden bir felaket,Büyük bir yıkım gelecek gibi görünüyor."));
trsuperobject.biblemap.put("b24_6_2", new trsuperobject(24,6,2,"Siyon kızını, o güzel, narin kızı yok edeceğim."));
trsuperobject.biblemap.put("b24_6_3", new trsuperobject(24,6,3,"Çobanlar sürüleriyle ona geliyor,Çevresinde çadırlarını kuracaklar.Herkes kendi sürüsünü otlatacak.››"));
trsuperobject.biblemap.put("b24_6_4", new trsuperobject(24,6,4,"‹‹Yeruşalime karşı savaş hazırlığı yapın!Kalkın, öğleyin saldırıya geçelim!Vay halimize, gün kararıyor!Akşamın gölgeleri gitgide uzuyor."));
trsuperobject.biblemap.put("b24_6_5", new trsuperobject(24,6,5,"Haydi, gece saldırıya geçelim,Kentin kalelerini yerle bir edelim.››"));
trsuperobject.biblemap.put("b24_6_6", new trsuperobject(24,6,6,"Her Şeye Egemen RAB diyor ki,‹‹Ağaçları kesin,Yeruşalime karşı kuşatma rampaları yapın.Bu kent cezalandırılmalı,İçinde zorbalıktan başka bir şey yok."));
trsuperobject.biblemap.put("b24_6_7", new trsuperobject(24,6,7,"Kuyu suyunu nasıl taze tutuyorsa,Yeruşalim de kötülüğünü öyle taze tutuyor.Şiddet ve yıkım yankılanıyor orada,Karşımda hep hastalık ve yaralar var."));
trsuperobject.biblemap.put("b24_6_8", new trsuperobject(24,6,8,"Uyarılara kulak ver, ey Yeruşalim!Yoksa seni bırakacağım,Seni bir viraneye,Oturulmaz bir ülkeye çevireceğim.››"));
trsuperobject.biblemap.put("b24_6_9", new trsuperobject(24,6,9,"Her Şeye Egemen RAB diyor ki,‹‹Asmadan nasıl üzüm toplanırsa,İsrail halkından geride kalanları da öyle toplayacaklar.Üzüm toplayan biri gibiElini yine asma dallarına uzat.››"));
trsuperobject.biblemap.put("b24_6_10", new trsuperobject(24,6,10,"İşitsinler diye kiminle konuşayım,Kimi uyarayım?Kulakları tıkalı, işitemiyorlar.RABbin sözünü aşağılıyor,Ondan hoşlanmıyorlar."));
trsuperobject.biblemap.put("b24_6_11", new trsuperobject(24,6,11,"Bu yüzden RABbin öfkesiyle doluyum,Kendimi tutmaktan yoruldum.‹‹Sokaktaki çocukların,Toplanan gençlerin üzerine boşalt öfkeni.Nasıl olsa karı da koca da,Yaşlı da yıllarca yaşamış olan da kurtulamayacak."));
trsuperobject.biblemap.put("b24_6_12", new trsuperobject(24,6,12,"Evleri, tarlaları, karılarıBaşkalarına verilecek,Çünkü ülkede yaşayanlara karşıElimi kaldıracağım›› diyor RAB."));
trsuperobject.biblemap.put("b24_6_13", new trsuperobject(24,6,13,"‹‹Küçük büyük herkes kazanç peşinde,Peygamberler, kâhinler, hepsi halkı aldatıyor."));
trsuperobject.biblemap.put("b24_6_14", new trsuperobject(24,6,14,"Esenlik yokken,‹Esenlik, esenlik› diyerekHalkımın yarasını sözde iyileştirdiler."));
trsuperobject.biblemap.put("b24_6_15", new trsuperobject(24,6,15,"Yaptıkları iğrençliklerden utandılar mı?Hayır, ne utanması?Kızarıp bozarmanın ne olduğunu bile bilmiyorlar.Bu yüzden onlar da düşenlerin arasında yer alacak,Onları cezalandırdığımda sendeleyip düşecekler›› diyor RAB."));
trsuperobject.biblemap.put("b24_6_16", new trsuperobject(24,6,16,"RAB diyor ki,‹‹Yol kavşaklarında durup bakın,Eski yolları sorun,İyi yol nerede, öğrenin,O yolda yürüyün,Canlarınız rahata kavuşur.Ama onlar, ‹O yolda yürümeyiz› dediler."));
trsuperobject.biblemap.put("b24_6_17", new trsuperobject(24,6,17,"Size bekçiler atayıp,‹Boru sesini dinleyin› dedim,Ama onlar, ‹Dinlemeyiz› dediler."));
trsuperobject.biblemap.put("b24_6_18", new trsuperobject(24,6,18,"Bundan ötürü, ey uluslar,Başlarına neler geleceğini işitin!Sen de anla, ey topluluk!"));
trsuperobject.biblemap.put("b24_6_19", new trsuperobject(24,6,19,"Dinle, ey yeryüzü!Bu halkın üzerine felaket,Kendi kurduğu düzenin sonucunu getirmek üzereyim.Çünkü sözlerime kulak asmadılar,Kutsal Yasamı reddettiler."));
trsuperobject.biblemap.put("b24_6_20", new trsuperobject(24,6,20,"Neden bana Sabadan günnük,Uzak bir ülkeden güzel kokulu kamış getiriliyor?Yakmalık sunularınızı kabul etmiyorum,Kurbanlarınızdan hoşnut değilim.››"));
trsuperobject.biblemap.put("b24_6_21", new trsuperobject(24,6,21,"Bu yüzden RAB diyor ki,‹‹Bu halkın önüne tökezler koyacağım,Babalar da oğullar daTökezleyip birlikte düşecek,Komşu dostuyla birlikte yok olacak.››"));
trsuperobject.biblemap.put("b24_6_22", new trsuperobject(24,6,22,"RAB diyor ki,‹‹İşte kuzeyden bir ordu geliyor.Dünyanın uçlarındanBüyük bir ulus harekete geçiyor."));
trsuperobject.biblemap.put("b24_6_23", new trsuperobject(24,6,23,"Yay, pala kuşanmışlar,Gaddar ve acımasızlar.Atlara binmiş gelirken,Kükreyen denizi andırıyor sesleri.Savaşa hazır savaşçılarKarşına dizilecekler, ey Siyon kızı!››"));
trsuperobject.biblemap.put("b24_6_24", new trsuperobject(24,6,24,"Haberlerini aldık,Ellerimizde derman kalmadı.Doğuran kadın gibiÜzüntü, sancı sardı bizi."));
trsuperobject.biblemap.put("b24_6_25", new trsuperobject(24,6,25,"Kırlara çıkmayın,Yolda yürümeyin!Düşmanın kılıcı orada,Her yer dehşet içinde."));
trsuperobject.biblemap.put("b24_6_26", new trsuperobject(24,6,26,"Ey halkım, çula sarın,Kül içinde yuvarlan.Biricik oğul için yas tutar gibiAcı acı dövün.Çünkü yok edici ansızın gelecek üzerimize."));
trsuperobject.biblemap.put("b24_6_27", new trsuperobject(24,6,27,"‹‹Seni halkımı deneyesin diye atadım,Öyle ki, onları tanıyıp yollarını sınayasın."));
trsuperobject.biblemap.put("b24_6_28", new trsuperobject(24,6,28,"Hepsi de çok dikbaşlı,Onu bunu çekiştirerek dolaşan insanlardır,Tunç kadar, demir kadar katıdırlar.Hepsi baştan çıkmıştır."));
trsuperobject.biblemap.put("b24_6_29", new trsuperobject(24,6,29,"Körük üfürdükçe üfürüyor,Kurşunu ateşte eritiyor,Ama boşunadır yapılan işlem,Çünkü kötüler arınmıyor."));
trsuperobject.biblemap.put("b24_6_30", new trsuperobject(24,6,30,"Onlara gümüş artığı denecek,Çünkü RAB onları reddetti.››"));
trsuperobject.biblemap.put("b24_7_2", new trsuperobject(24,7,2,"‹‹RABbin Tapınağının kapısında durup şu sözü duyur. De ki, ‹‹RABbin sözünü dinleyin, ey RABbe tapınmak için bu kapılardan giren Yahuda halkı!"));
trsuperobject.biblemap.put("b24_7_3", new trsuperobject(24,7,3,"İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki: Yaşantınızı ve uygulamalarınızı düzeltin. O zaman burada kalmanızı sağlarımfı."));
trsuperobject.biblemap.put("b24_7_4", new trsuperobject(24,7,4,"‹‹RABbin Tapınağı, RABbin Tapınağı, RABbin Tapınağı buradadır!›› gibi aldatıcı sözlere güvenmeyin."));
trsuperobject.biblemap.put("b24_7_5", new trsuperobject(24,7,5,"Eğer yaşantınızı ve uygulamalarınızı gerçekten düzeltir, birbirinize karşı adil davranır,"));
trsuperobject.biblemap.put("b24_7_6", new trsuperobject(24,7,6,"yabancıya, öksüze, dula haksızlık etmez, burada suçsuz kanı akıtmaz, sizi yıkıma götüren başka ilahların ardınca gitmezseniz,"));
trsuperobject.biblemap.put("b24_7_7", new trsuperobject(24,7,7,"burada, sonsuza dek atalarınıza vermiş olduğum ülkede kalmanızı sağlarımfı."));
trsuperobject.biblemap.put("b24_7_8", new trsuperobject(24,7,8,"Ne var ki, sizler işe yaramaz aldatıcı sözlere güveniyorsunuz."));
trsuperobject.biblemap.put("b24_7_9-10", new trsuperobject(24,7,9-10,"‹‹Çalmak, adam öldürmek, zina etmek, yalan yere ant içmek, Baala buhur yakmak, tanımadığınız başka ilahların ardınca gitmek, bütün bu iğrençlikleri yapmak için mi bana ait olan tapınağa gelip önümde duruyor, güvenlikteyiz diyorsunuz?"));
trsuperobject.biblemap.put("b24_7_11", new trsuperobject(24,7,11,"Bana ait olan bu tapınak sizin için bir haydut ini mi oldu? Ama ben görüyorum neler yaptığınızı!› diyor RAB."));
trsuperobject.biblemap.put("b24_7_12", new trsuperobject(24,7,12,"‹‹Daha önce adımı yerleştirmiş olduğum Şilodaki yerime gidin. Halkım İsrailin kötülüğü yüzünden ona ne yaptığımı görün."));
trsuperobject.biblemap.put("b24_7_13", new trsuperobject(24,7,13,"Bütün bunları yaptınız, diyor RAB, size defalarca seslendim ama dinlemediniz; sizi çağırdım ama yanıt vermediniz."));
trsuperobject.biblemap.put("b24_7_14", new trsuperobject(24,7,14,"Bu yüzden Şiloya ne yaptımsa, bana ait olan, güvendiğiniz bu tapınağa da -sizlere, atalarınıza vermiş olduğum bu yere de- aynısını yapacağım."));
trsuperobject.biblemap.put("b24_7_15", new trsuperobject(24,7,15,"Kardeşlerinizi, bütün Efrayim soyunu nasıl attıysam, sizleri de öyle atacağım huzurumdan.›"));
trsuperobject.biblemap.put("b24_7_16", new trsuperobject(24,7,16,"‹‹Sana gelince, ey Yeremya, bu halk için yalvarma; onlar için ne yakar ne de dilekte bulun; bana yalvarıp yakarma, çünkü seni dinlemeyeceğim."));
trsuperobject.biblemap.put("b24_7_17", new trsuperobject(24,7,17,"Onların Yahuda kentlerinde, Yeruşalim sokaklarında neler yaptıklarını görmüyor musun?"));
trsuperobject.biblemap.put("b24_7_18", new trsuperobject(24,7,18,"Çocuklar odun topluyor, babalar ateş yakıyor, kadınlar Gök Kraliçesine pide pişirmek için hamur yoğuruyor. Beni öfkelendirmek için başka ilahlara dökmelik sunular sunuyorlar."));
trsuperobject.biblemap.put("b24_7_19", new trsuperobject(24,7,19,"İncittikleri ben miyim, diyor RAB. Hayır, kendilerini inciterek utanca boğuyorlar."));
trsuperobject.biblemap.put("b24_7_20", new trsuperobject(24,7,20,"‹‹Bu yüzden Egemen RAB diyor ki, ‹Buranın üzerine, insanın, hayvanın, kırdaki ağaçların, toprağın ürününün üzerine kızgın öfkemi yağdıracağım. Yakıp yok edecek her şeyi, sönmeyecek.›"));
trsuperobject.biblemap.put("b24_7_21", new trsuperobject(24,7,21,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Yakmalık sunularınızı öbür kurbanlarınıza ekleyin de et yiyin."));
trsuperobject.biblemap.put("b24_7_22", new trsuperobject(24,7,22,"Çünkü atalarınızı Mısırdan çıkardığımda, yakmalık sunularla kurbanlar hakkında onlara seslenip buyruk vermedim."));
trsuperobject.biblemap.put("b24_7_23", new trsuperobject(24,7,23,"Onlara şunu buyurdum: Sözümü dinlerseniz, ben sizin Tanrınız, siz de benim halkım olursunuz. İyilik bulmanız için her konuda size buyurduğum yolda yürüyün."));
trsuperobject.biblemap.put("b24_7_24", new trsuperobject(24,7,24,"Ne var ki, dinlemediler, kulak asmadılar; kendi isteklerinin, kötü yüreklerinin inadı doğrultusunda yürüdüler. İleri değil, geri gittiler."));
trsuperobject.biblemap.put("b24_7_25", new trsuperobject(24,7,25,"Atalarınızın Mısırdan çıktığı günden bu yana, size her gün defalarca peygamber kullarımı gönderdim."));
trsuperobject.biblemap.put("b24_7_26", new trsuperobject(24,7,26,"Ama beni dinlemediniz, kulak asmadınız. İnat ederek atalarınızdan daha çok kötülük yaptınız.›"));
trsuperobject.biblemap.put("b24_7_27", new trsuperobject(24,7,27,"‹‹Onlara bütün bunları söyleyeceksin ama seni dinlemeyecekler. Onları çağıracaksın ama yanıt vermeyecekler."));
trsuperobject.biblemap.put("b24_7_28", new trsuperobject(24,7,28,"Bunun için onlara de ki, ‹Tanrısı RABbin sözünü dinlemeyen, ders almayan ulus işte budur. Bana bağlılıkları yok oldu, bağlılıktan söz etmez oldular."));
trsuperobject.biblemap.put("b24_7_29", new trsuperobject(24,7,29,"‹‹Saçını kes ve at, ey Yeruşalim,Çıplak tepeler üzerinde ağıt yak.Çünkü RAB, öfkesine uğramış kuşağıReddedip terk etti.››"));
trsuperobject.biblemap.put("b24_7_30", new trsuperobject(24,7,30,"‹‹Yahuda halkı gözümde kötü olanı yaptı, diyor RAB. Bana ait olan bu tapınağa iğrenç putlarını yerleştirerek onu kirlettiler."));
trsuperobject.biblemap.put("b24_7_31", new trsuperobject(24,7,31,"Oğullarını, kızlarını ateşte kurban etmek için Ben-Hinnom Vadisinde, Tofette puta tapılan yerler kurdular. Böyle bir şeyi ne buyurdum ne de aklımdan geçirdim."));
trsuperobject.biblemap.put("b24_7_32", new trsuperobject(24,7,32,"Bundan ötürü oraya artık Tofet ya da Ben-Hinnom Vadisi değil, Kıyım Vadisi deneceği günler geliyor, diyor RAB. Tofette yer kalmayana dek gömecekler ölüleri."));
trsuperobject.biblemap.put("b24_7_33", new trsuperobject(24,7,33,"Bu halkın ölüleri yırtıcı kuşlara, yabanıl hayvanlara yem olacak; onları korkutup kaçıran kimse olmayacak."));
trsuperobject.biblemap.put("b24_7_34", new trsuperobject(24,7,34,"Yahuda kentlerinde, Yeruşalim sokaklarında sevinç ve neşe sesine, gelin güvey sesine son vereceğim; ülke viraneye dönecek."));
trsuperobject.biblemap.put("b24_8_1", new trsuperobject(24,8,1,"‹‹O zaman, diyor RAB, Yahuda krallarıyla önderlerinin, kâhinlerin, peygamberlerin, Yeruşalimde yaşamış olanların kemikleri mezarlarından çıkarılacak."));
trsuperobject.biblemap.put("b24_8_2", new trsuperobject(24,8,2,"Toplanmayacak, gömülmeyecek kemikler, toprağın üzerinde gübre gibi olacaklar. Yeruşalim halkının sevdiği, kulluk ettiği, izlediği, danıştığı, taptığı güneşin, ayın, gök cisimlerinin önüne serilecekler."));
trsuperobject.biblemap.put("b24_8_3", new trsuperobject(24,8,3,"Bu kötü ulustan bütün sağ kalanlar, kendilerini sürdüğüm yerlerde yaşayanlar, ölümü yaşama yeğleyecekler. Her Şeye Egemen RAB böyle diyor.›"));
trsuperobject.biblemap.put("b24_8_4", new trsuperobject(24,8,4,"‹‹Onlara de ki, ‹RAB şöyle diyor:  ‹‹İnsan yere düşer de kalkmaz mı,Yoldan sapar da geri dönmez mi?"));
trsuperobject.biblemap.put("b24_8_5", new trsuperobject(24,8,5,"Öyleyse neden bu halk yoldan saptı?Neden Yeruşalim sürekli döneklik ediyor?Hileye yapışıyor,Geri dönmeyi reddediyorlar."));
trsuperobject.biblemap.put("b24_8_6", new trsuperobject(24,8,6,"Dikkatle dinledim,Ama doğru söylemiyorlar.Kimse, ne yaptım, diyerek kötülüğünden pişmanlık duymuyor.Savaşta seğirten at gibiHerkes kendi yoluna gidiyor."));
trsuperobject.biblemap.put("b24_8_7", new trsuperobject(24,8,7,"Gökteki leylek bileBelli mevsimlerini bilir.Kumru da kırlangıç da turna daGöç etme zamanını gözetir.Oysa halkım buyruklarımı bilmez."));
trsuperobject.biblemap.put("b24_8_8", new trsuperobject(24,8,8,"‹‹Nasıl, biz bilge kişileriz,RABbin Yasası bizdedir, diyebiliyorsunuz?İşte, bilginlerin yalancı kalemiYasayı yalana çevirmiş."));
trsuperobject.biblemap.put("b24_8_9", new trsuperobject(24,8,9,"Bilgeler utandırıldı,Yıldırılıp ele geçirildi.RABbin sözünü reddettiler.Nasıl bir bilgelikmiş onlarınki?"));
trsuperobject.biblemap.put("b24_8_10", new trsuperobject(24,8,10,"Bundan ötürü karılarını başkalarına,Tarlalarını sahiplenecek yeni kişilere vereceğim.Küçük büyük herkes kazanç peşinde,Peygamberler, kâhinler, hepsi halkı aldatıyor."));
trsuperobject.biblemap.put("b24_8_11", new trsuperobject(24,8,11,"Esenlik yokken,Esenlik, esenlik, diyerekHalkımın yarasını sözde iyileştirdiler."));
trsuperobject.biblemap.put("b24_8_12", new trsuperobject(24,8,12,"Yaptıkları iğrençliklerden utandılar mı?Hayır, ne utanması?Kızarıp bozarmanın ne olduğunu bile bilmiyorlar.Bu yüzden onlar da düşenlerin arasında yer alacak,Cezalandırıldıklarında sendeleyip düşecekler› diyor RAB."));
trsuperobject.biblemap.put("b24_8_13", new trsuperobject(24,8,13,"‹‹Onları büsbütün yok edeceğim, diyor RAB,Ne asmada üzüm kalacak,Ne incir ağacında incir.Yaprakları solup kuruyacak.Onlara ne verdiysem,Ellerinden alınacak.››"));
trsuperobject.biblemap.put("b24_8_14", new trsuperobject(24,8,14,"‹‹Neden burada oturup duruyoruz?Toplanalım da surlu kentlere kaçalım,Orada ölelim!Tanrımız RAB bizi ölüme terk etti,Bize zehirli su içirdi.Çünkü Ona karşı günah işledik."));
trsuperobject.biblemap.put("b24_8_15", new trsuperobject(24,8,15,"Esenlik bekledik, iyilik gelmedi.Şifa umduk, yılgınlık bulduk."));
trsuperobject.biblemap.put("b24_8_16", new trsuperobject(24,8,16,"Düşman atlarının hırıltısıDan bölgesinden duyuluyor,Aygırlarının kişnemesindenBütün ülke titriyor.Ülkeyi ve içindeki her şeyi,Kenti ve orada yaşayanlarıYok etmeye geliyorlar.››"));
trsuperobject.biblemap.put("b24_8_17", new trsuperobject(24,8,17,"‹‹Bakın, aranıza yılanlar,Büyüden etkilenmeyen engerekler göndereceğim,Sizi sokacaklar›› diyor RAB."));
trsuperobject.biblemap.put("b24_8_18", new trsuperobject(24,8,18,"Üzüntüm avutulamaz,Yüreğim baygın,"));
trsuperobject.biblemap.put("b24_8_19", new trsuperobject(24,8,19,"Ülkenin en uzak köşelerindenHalkımın feryadını dinleyin:‹‹RAB Siyonda değil mi?Kralı orada değil mi?››RAB, ‹‹Putlarıyla,İşe yaramaz yabancı ilahlarıylaNeden öfkelendiriyorlar beni?›› diyor."));
trsuperobject.biblemap.put("b24_8_20", new trsuperobject(24,8,20,"‹‹Ürün biçme zamanı geçti,Yaz sona erdi,Biz ise kurtulmadık›› diye haykırıyorlar."));
trsuperobject.biblemap.put("b24_8_21", new trsuperobject(24,8,21,"Halkımın yarasından ben de yaralandım.Yasa büründüm, dehşete düştüm."));
trsuperobject.biblemap.put("b24_8_22", new trsuperobject(24,8,22,"Gilat'ta merhem yok mu,Hekim yok mu?Öyleyse halkımın yarası neden iyi edilmedi?"));
trsuperobject.biblemap.put("b24_9_1", new trsuperobject(24,9,1,"Keşke başım bir pınar,Gözlerim bir gözyaşı kaynağı olsa!Halkımın öldürülenleri içinAğlasam gece gündüz!"));
trsuperobject.biblemap.put("b24_9_2", new trsuperobject(24,9,2,"Keşke halkımı bırakabilmem,Onlardan uzaklaşabilmem içinÇölde konaklayacak bir yerim olsa!Hepsi zina ediyor,Hain bir topluluk!"));
trsuperobject.biblemap.put("b24_9_3", new trsuperobject(24,9,3,"‹‹Yalan söylemek için ülkedeDillerini yay gibi geriyor,Güçlerini gerçek yolunda kullanmıyorlar.Kötülük üstüne kötülük yapıyor,Beni tanımıyorlar›› diyor RAB."));
trsuperobject.biblemap.put("b24_9_4", new trsuperobject(24,9,4,"‹‹Herkes dostundan sakınsın,Kardeşlerinizin hiçbirine güvenmeyin.Çünkü her kardeş Yakup gibi aldatıcı,Her dost iftiracıdır."));
trsuperobject.biblemap.put("b24_9_5", new trsuperobject(24,9,5,"Dost dostu aldatıyor,Kimse gerçeği söylemiyor.Dillerine yalan söylemeyi öğrettiler,Suç işleye işleye yorgun düştüler."));
trsuperobject.biblemap.put("b24_9_6", new trsuperobject(24,9,6,"Sen, ey Yeremya,Aldatıcılığın ortasında yaşıyorsun.Aldatıcılıkları yüzündenBeni tanımak istemiyorlar.››Böyle diyor RAB."));
trsuperobject.biblemap.put("b24_9_7", new trsuperobject(24,9,7,"Bundan ötürü Her Şeye Egemen RAB diyor ki,‹‹İşte, onları arıtıp sınayacağım,Halkımın günahı yüzündenBaşka ne yapabilirim ki?"));
trsuperobject.biblemap.put("b24_9_8", new trsuperobject(24,9,8,"Dilleri öldürücü bir ok,Hep aldatıyor.Komşusuna esenlik diliyor,Ama içinden ona tuzak kuruyor."));
trsuperobject.biblemap.put("b24_9_9", new trsuperobject(24,9,9,"Bu yüzden onları cezalandırmayayım mı?›› diyor RAB,‹‹Böyle bir ulustan öcümü almayayım mı?››"));
trsuperobject.biblemap.put("b24_9_10", new trsuperobject(24,9,10,"Dağlar için ağlayıp yas tutacağım,Otlaklar için ağıt yakacağım.Çöle dönüştüler,Kimse geçmiyor oralardan.Sığırların böğürmesi duyulmuyor,Kuşlar, yabanıl hayvanlar kaçıp gitti."));
trsuperobject.biblemap.put("b24_9_11", new trsuperobject(24,9,11,"‹‹Yeruşalimi taş yığını,Çakalların barınağı haline getireceğim.Yahuda kentleriniKimsenin yaşayamayacağı bir viraneye döndüreceğim.››"));
trsuperobject.biblemap.put("b24_9_12", new trsuperobject(24,9,12,"Hangi bilge kişi buna akıl erdirecek? RABbin seslendiği kişi kim ki, sözünü açıklayabilsin? Ülke neden yıkıldı? Neden kimsenin geçemediği bir çöle dönüştü?"));
trsuperobject.biblemap.put("b24_9_13", new trsuperobject(24,9,13,"RAB, ‹‹Kendilerine verdiğim yasayı bıraktılar, sözümü dinlemediler, yasamı izlemediler›› diyor,"));
trsuperobject.biblemap.put("b24_9_14", new trsuperobject(24,9,14,"‹‹Onun yerine yüreklerinin inadını, atalarının öğrettiği gibi Baalları izlediler.››"));
trsuperobject.biblemap.put("b24_9_15", new trsuperobject(24,9,15,"Bunun için İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹‹Bu halka pelinotu yedirecek, zehirli su içireceğim."));
trsuperobject.biblemap.put("b24_9_16", new trsuperobject(24,9,16,"Onları kendilerinin de atalarının da tanımadığı ulusların arasına dağıtacak, tümünü yok edene dek peşlerine kılıcı salacağım.››"));
trsuperobject.biblemap.put("b24_9_17", new trsuperobject(24,9,17,"Her Şeye Egemen RAB diyor ki,‹‹İyi düşünün!Ağıt yakan kadınları çağırın gelsinler.En iyilerini çağırın gelsinler."));
trsuperobject.biblemap.put("b24_9_18", new trsuperobject(24,9,18,"Hemen gelip bizim için ağıt yaksınlar;Gözlerimiz gözyaşı döksün,Gözkapaklarımızdan sular aksın."));
trsuperobject.biblemap.put("b24_9_19", new trsuperobject(24,9,19,"Siyondan ağlama sesi duyuluyor:‹Yıkıma uğradık!Büyük utanç içindeyiz,Çünkü ülkemizi terk ettik,Evlerimiz yerle bir oldu.››"));
trsuperobject.biblemap.put("b24_9_20", new trsuperobject(24,9,20,"Ey kadınlar, RABbin sözünü dinleyin!Ağzından çıkan her söze kulak verin.Kızlarınıza yas tutmayı,Komşunuza ağıt yakmayı öğretin."));
trsuperobject.biblemap.put("b24_9_21", new trsuperobject(24,9,21,"Ölüm pencerelerimize tırmandı,Kalelerimize girdi;Sokakları çocuksuz,Meydanları gençsiz bıraktı."));
trsuperobject.biblemap.put("b24_9_22", new trsuperobject(24,9,22,"Onlara de ki, ‹‹RAB şöyle diyor:‹İnsan cesetleri gübre gibi,Biçicinin ardındaki demetler gibi toprağa serilecek.Onları toplayacak kimse olmayacak.››"));
trsuperobject.biblemap.put("b24_9_23", new trsuperobject(24,9,23,"RAB şöyle diyor:‹‹Bilge kişi bilgeliğiyle,Güçlü kişi gücüyle,Zengin kişi zenginliğiyle övünmesin."));
trsuperobject.biblemap.put("b24_9_24", new trsuperobject(24,9,24,"Dünyada iyilik yapanın,Adaleti, doğruluğu sağlayanınBen RAB olduğumu anlamaklaVe beni tanımakla övünsün övünen.Çünkü ben bunlardan hoşlanırım›› diyor RAB."));
trsuperobject.biblemap.put("b24_9_25", new trsuperobject(24,9,25,"‹‹Yalnız bedence sünnetli olanları cezalandıracağım günler geliyor›› diyor RAB."));
trsuperobject.biblemap.put("b24_9_26", new trsuperobject(24,9,26,"‹‹Mısır'ı, Yahuda'yı, Edom'u, Ammon'u, Moav'ı, çölde yaşayan ve zülüflerini kesenlerin hepsini cezalandıracağım. Çünkü bütün bu uluslar gerçekte sünnetsiz, bütün İsrail halkı da yürekte sünnetsizdir.››"));
trsuperobject.biblemap.put("b24_10_3", new trsuperobject(24,10,3,"Ulusların töreleri yararsızdır.Ormandan ağaç keserler,Usta keskisiyle ona biçim verir."));
trsuperobject.biblemap.put("b24_10_4", new trsuperobject(24,10,4,"Altınla, gümüşle süsler,Çekiçle, çivilerle sağlamlaştırırlar;Yerinden kımıldamasın diye."));
trsuperobject.biblemap.put("b24_10_5", new trsuperobject(24,10,5,"Salatalık bostanındaki korkuluk gibidir putları,Konuşamazlar;Onları taşımak gerek, çünkü yürüyemezler.Onlardan korkmayın, zarar veremezler;İyilik de edemezler.››"));
trsuperobject.biblemap.put("b24_10_6", new trsuperobject(24,10,6,"Senin gibisi yok, ya RAB,Sen büyüksün,Adın da büyüktür gücün sayesinde."));
trsuperobject.biblemap.put("b24_10_7", new trsuperobject(24,10,7,"Senden kim korkmaz,Ey ulusların kralı?Bu sana yakışır.Ulusların bilgeleri arasında,Bütün ülkelerindeSenin gibisi yok."));
trsuperobject.biblemap.put("b24_10_8", new trsuperobject(24,10,8,"Hepsi budala ve akılsız.Yararsız putlardan ne öğrenilebilir ki?Ağaçtan yapılmış onlar!"));
trsuperobject.biblemap.put("b24_10_9", new trsuperobject(24,10,9,"Tarşişten dövme gümüş,Ufazdan altın getirilir.Ustayla kuyumcunun yaptığı nesnenin üzerineLacivert, mor giydirilir,Hepsi usta işidir."));
trsuperobject.biblemap.put("b24_10_10", new trsuperobject(24,10,10,"Ama gerçek Tanrı RABdir.O yaşayan Tanrıdır,Sonsuza dek kral Odur.O öfkelenince yeryüzü titrer,Uluslar dayanamaz gazabına."));
trsuperobject.biblemap.put("b24_10_11", new trsuperobject(24,10,11,"‹‹Onlara şunu diyeceksin,‹Yeri, göğü yaratmayan bu ilahlar,Yerden de göğün altından da yok olacaklar.››"));
trsuperobject.biblemap.put("b24_10_12", new trsuperobject(24,10,12,"Gücüyle yeryüzünü yaratan,Bilgeliğiyle dünyayı kuran,Aklıyla gökleri yayan RABdir."));
trsuperobject.biblemap.put("b24_10_13", new trsuperobject(24,10,13,"O gürleyince gökteki sular çağıldar,Yeryüzünün dört bucağından bulutlar yükseltir,Yağmur için şimşek çaktırır,Ambarlarından rüzgar estirir."));
trsuperobject.biblemap.put("b24_10_14", new trsuperobject(24,10,14,"Hepsi budala, bilgisiz,Her kuyumcu yaptığı puttan utanacak.O putlar yapmacıktır,Soluk yoktur onlarda."));
trsuperobject.biblemap.put("b24_10_15", new trsuperobject(24,10,15,"Yararsız, alay edilesi nesnelerdir,Cezalandırılınca yok olacaklar."));
trsuperobject.biblemap.put("b24_10_16", new trsuperobject(24,10,16,"Yakupun Payı onlara benzemez.Her şeye biçim veren Odur,Onun mirasıdır İsrail oymağı,Her Şeye Egemen RABdir adı."));
trsuperobject.biblemap.put("b24_10_17", new trsuperobject(24,10,17,"Kuşatma altında olan sizler,Eşyalarınızı toplayın yerden."));
trsuperobject.biblemap.put("b24_10_18", new trsuperobject(24,10,18,"RAB diyor ki,‹‹İşte bu kez bu ülkede yaşayanlarıFırlatıp atacağım;Ele geçirilmeleri içinOnları sıkıştıracağım.››"));
trsuperobject.biblemap.put("b24_10_19", new trsuperobject(24,10,19,"Yaramdan ötürü vay başıma gelen!Derdim iyileşmez!Ama, ‹Dert benim derdim,Dayanmalıyım› dedim."));
trsuperobject.biblemap.put("b24_10_20", new trsuperobject(24,10,20,"Çadırım yıkıldı, ipleri koptu.Çocuklarım benden ayrıldı,Yok artık onlar.Çadırımı kuracak,Perdelerimi takacak kimse kalmadı."));
trsuperobject.biblemap.put("b24_10_21", new trsuperobject(24,10,21,"Çobanlar budala,RABbe danışmıyorlar.Bu yüzden işleri yolunda gitmiyor,Bütün sürüleri dağıldı."));
trsuperobject.biblemap.put("b24_10_22", new trsuperobject(24,10,22,"Dinle! Haber geliyor!Kuzey ülkesinden büyük patırtı geliyor!Yahuda kentlerini viraneye çevirecek,Çakallara barınak edecek."));
trsuperobject.biblemap.put("b24_10_23", new trsuperobject(24,10,23,"İnsanın yaşamının kendi elinde olmadığını,Adımlarına yön vermenin ona düşmediğiniBiliyorum, ya RAB."));
trsuperobject.biblemap.put("b24_10_24", new trsuperobject(24,10,24,"Beni öfkenle değil,Yalnız adaletinle yola getir, ya RAB,Yoksa beni hiçe indirirsin."));
trsuperobject.biblemap.put("b24_10_25", new trsuperobject(24,10,25,"Öfkeni seni tanımayan ulusların,Adını anmayan toplulukların üzerine dök.Çünkü onlar Yakup soyunu yiyip bitirdiler,Onu tümüyle yok ettiler,Yurdunu viraneye çevirdiler."));
trsuperobject.biblemap.put("b24_11_2", new trsuperobject(24,11,2,"‹‹Bu antlaşmanın koşullarını dinle. Yahuda halkına ve Yeruşalimde yaşayanlara açıkla."));
trsuperobject.biblemap.put("b24_11_3", new trsuperobject(24,11,3,"Onlara diyeceksin ki, ‹İsrailin Tanrısı RAB şöyle diyor: Bu antlaşmanın koşullarına uymayan lanet altındadır!"));
trsuperobject.biblemap.put("b24_11_4", new trsuperobject(24,11,4,"Atalarınızı Mısırdan, demir eritme ocağından çıkardığımda bu antlaşmaya bağlı kalmalarını buyurdum. Onlara dedim ki: Sözümü dinleyin, buyurduğum her şeyi yerine getirin. Böylece siz benim halkım olursunuz, ben de sizin Tanrınız olurum."));
trsuperobject.biblemap.put("b24_11_5", new trsuperobject(24,11,5,"İşte o zaman süt ve bal akan ülkeyi -bugün sizin olan ülkeyi- atalarınıza vereceğime ilişkin içtiğim andı yerine getirmiş olacağım.›› ‹‹Amin, ya RAB›› diye karşılık verdim."));
trsuperobject.biblemap.put("b24_11_7", new trsuperobject(24,11,7,"Atalarınızı Mısırdan çıkardığım günden bu yana sözümü dinlemeleri için onları defalarca uyardım."));
trsuperobject.biblemap.put("b24_11_8", new trsuperobject(24,11,8,"Ama dinlemediler, kulak asmadılar. Bunun yerine kötü yüreklerinin inadı uyarınca davrandılar. Ben de uymalarını buyurduğum, ama uymadıkları bu antlaşmada açıklanan bütün lanetleri başlarına getirdim.››"));
trsuperobject.biblemap.put("b24_11_10", new trsuperobject(24,11,10,"Sözlerimi dinlemek istemeyen atalarının suçlarına döndüler. Başka ilahların ardınca gidip onlara taptılar. İsrail halkıyla Yahuda halkı, atalarıyla yaptığım antlaşmayı bozdu."));
trsuperobject.biblemap.put("b24_11_11", new trsuperobject(24,11,11,"Bu yüzden RAB, ‹Kaçıp kurtulamayacakları bir yıkım getireceğim başlarına› diyor, ‹Bana yakarsalar da onları dinlemeyeceğim."));
trsuperobject.biblemap.put("b24_11_12", new trsuperobject(24,11,12,"Yahuda kentlerinde oturan halk da Yeruşalimde yaşayanlar da gidip buhur yaktıkları ilahlara yalvaracaklar. Ama yıkım geldiğinde, bu ilahlar onlara yardım edemez."));
trsuperobject.biblemap.put("b24_11_13", new trsuperobject(24,11,13,"Kentlerinin sayısı kadar ilahın var, ey Yahuda! O utanılası ilaha, Baala buhur yakmak için Yeruşalim sokaklarının sayısı kadar sunak kurdunuz."));
trsuperobject.biblemap.put("b24_11_14", new trsuperobject(24,11,14,"‹‹Sana gelince, ey Yeremya, bu halk için yalvarma; ne yakar ne de dilekte bulun. Sıkıntılı zamanlarında beni çağırdıklarında onları dinlemeyeceğim."));
trsuperobject.biblemap.put("b24_11_15", new trsuperobject(24,11,15,"‹‹Sevgilim kötü düzenler kuruyor,Öyleyse tapınağımda işi ne?Adaklar ve kutsanmış et uğrayacağın felaketi önleyebilir mi?Felaket gelince sevinecek misin?››"));
trsuperobject.biblemap.put("b24_11_16", new trsuperobject(24,11,16,"RAB sana meyvesi ve biçimi güzel,Yaprağı bol zeytin ağacı adını vermişti.Ama güçlü fırtına koptuğundaAğacı tutuşturacak;Dalları kırılacak."));
trsuperobject.biblemap.put("b24_11_17", new trsuperobject(24,11,17,"Seni dikmiş olan Her Şeye Egemen RAB,Başına felaket getirmeye karar verdi.Çünkü İsrail ve Yahuda halklarıKötülük yaptı,Baala buhur yakarak beni öfkelendirdiler."));
trsuperobject.biblemap.put("b24_11_18", new trsuperobject(24,11,18,"Benim için kurdukları düzeni RAB bana açıkladı. Haberim vardı, çünkü ne yaptıklarını bana gösterdi."));
trsuperobject.biblemap.put("b24_11_19", new trsuperobject(24,11,19,"Kesime götürülen uysal bir kuzu gibiydim. Bana düzen kurduklarını anlamamıştım. Şöyle diyorlardı:  ‹‹Ağacı da meyvesini de yok edelim,Bir daha adı anılmasın diyeOnu yaşayanlar diyarından kesip atalım.››"));
trsuperobject.biblemap.put("b24_11_20", new trsuperobject(24,11,20,"Adaletle yargılayan,Yüreği ve düşünceyi sınayan,Her Şeye Egemen RAB,Davamı senin eline bırakıyorum.Onlardan alacağın öcü göreyim!"));
trsuperobject.biblemap.put("b24_11_21", new trsuperobject(24,11,21,"‹‹Seni öldürmek isteyen Anatot halkı için RAB diyor ki, ‹Onlar, RABbin adına peygamberlik etme, yoksa seni öldürürüz diyorlardı.›"));
trsuperobject.biblemap.put("b24_11_22", new trsuperobject(24,11,22,"Her Şeye Egemen RAB, ‹Onları cezalandıracağım› diyor, ‹Gençleri kılıçtan geçirilecek, oğullarıyla kızları kıtlıktan ölecek."));
trsuperobject.biblemap.put("b24_11_23", new trsuperobject(24,11,23,"Sağ kalan olmayacak. Cezalandırılacakları yıl Anatot halkının başına felaket getireceğim.››"));
trsuperobject.biblemap.put("b24_12_1", new trsuperobject(24,12,1,"Davamı önüne getirsem,Haklı çıkarsın, ya RAB.Ama adalet konusundaSeninle tartışmak istiyorum.Neden kötülerin işi iyi gidiyor?Neden hainler tasasızca yaşıyor?"));
trsuperobject.biblemap.put("b24_12_2", new trsuperobject(24,12,2,"Onları sen diktin, kök saldılar,Büyüyüp ürün verdiler.Adın ağızlarından düşmüyor,Yürekleriyse senden uzak."));
trsuperobject.biblemap.put("b24_12_3", new trsuperobject(24,12,3,"Beni tanırsın, ya RAB,Beni görür, yüreğimin seninle olduğunu bilirsin.Kasaplık koyun gibi ayır onları,Kesim gününe hazırla!"));
trsuperobject.biblemap.put("b24_12_4", new trsuperobject(24,12,4,"İçinde yaşayanların kötülüğü yüzünden,Ülke ne zamana dek yas tutacak,Otlar ne zamana dek sararıp solacak?Hayvanlarla kuşlar yok oldu.Çünkü bu halk,‹‹O başımıza neler geleceğini görmüyor›› dedi."));
trsuperobject.biblemap.put("b24_12_5", new trsuperobject(24,12,5,"‹‹Ey Yeremya,İnsanlarla yarışa girip yoruldunsa,Atlarla nasıl yarışacaksın?Güvenli bir ülkede sendelersen,Şeria çalılıklarıyla nasıl başa çıkacaksın?"));
trsuperobject.biblemap.put("b24_12_6", new trsuperobject(24,12,6,"Kardeşlerin, öz ailen bile sana ihanet etti,Arkandan seslerini yükselttiler.Yüzüne karşı olumlu konuşsalar bile onlara güvenme."));
trsuperobject.biblemap.put("b24_12_7", new trsuperobject(24,12,7,"Evimi terk ettim,Mirasımı reddettim,Sevgilimi düşmanlarının eline verdim."));
trsuperobject.biblemap.put("b24_12_8", new trsuperobject(24,12,8,"Mirasım karşımdaOrmandaki aslan gibi oldu;Kükreyip üzerime saldırdı.Bu yüzden ondan nefret ediyorum."));
trsuperobject.biblemap.put("b24_12_9", new trsuperobject(24,12,9,"Mirasım sırtlan ya da yırtıcı kuş mu oldu karşımda?Çevresindeki yırtıcı kuşlar saldırıyor ona.Gidin, bütün yabanıl hayvanları toplayıp getirin,Yiyip bitirsinler onu."));
trsuperobject.biblemap.put("b24_12_10", new trsuperobject(24,12,10,"Pek çok çoban bağımı bozdu,Tarlamı çiğnedi,Güzelim tarlamı ıssız çöle döndürdü."));
trsuperobject.biblemap.put("b24_12_11", new trsuperobject(24,12,11,"Onu viraneye çevirdiler,Önümde viran olmuş ağlıyor;Bütün ülke viran olmuş,Yine de aldıran yok."));
trsuperobject.biblemap.put("b24_12_12", new trsuperobject(24,12,12,"Çöldeki çıplak tepelereYıkıcılar geldi.RABbin kılıcı ülkeyiBir uçtan bir uca yiyip bitiriyor.Kimse kavuşmayacak esenliğe."));
trsuperobject.biblemap.put("b24_12_13", new trsuperobject(24,12,13,"Halkım buğday ekip diken biçti,Emek verip yarar görmedi.RABbin kızgın öfkesi yüzündenÜrününüzden utanacaksınız.››"));
trsuperobject.biblemap.put("b24_12_14", new trsuperobject(24,12,14,"RAB diyor ki, ‹‹Halkım İsraile verdiğim mülke el koyan bütün kötü komşularımı ülkelerinden söküp atacak, Yahuda halkını da atacağım."));
trsuperobject.biblemap.put("b24_12_15", new trsuperobject(24,12,15,"Hepsini söküp attıktan sonra Yahudaya yine acıyacak, her birini kendi mülküne, kendi ülkesine geri getireceğim."));
trsuperobject.biblemap.put("b24_12_16", new trsuperobject(24,12,16,"Halkıma Baalın adıyla ant içmeyi öğrettiler. Bunun gibi, halkımın yolunda yürümeyi ve ‹RABbin varlığı hakkı için› diyerek benim adımla ant içmeyi de iyice öğrenirlerse, halkımın arasında sağlam yerleri olacak."));
trsuperobject.biblemap.put("b24_12_17", new trsuperobject(24,12,17,"Ama kulak asmayan her ulusu kökünden söküp atacak, yok edeceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_13_4", new trsuperobject(24,13,4,"‹‹Satın aldığın belindeki kuşağı al, Perata git. Kuşağı orada bir kaya kovuğuna gizle.››"));
trsuperobject.biblemap.put("b24_13_6", new trsuperobject(24,13,6,"Uzun süre sonra RAB bana, ‹‹Kalk, Perata git, gizlemeni buyurduğum kuşağı al›› dedi."));
trsuperobject.biblemap.put("b24_13_7", new trsuperobject(24,13,7,"Bunun üzerine Perata gittim, gizlediğim yeri kazıp kuşağı aldım. Ancak kuşak çürümüştü, hiçbir işe yaramazdı."));
trsuperobject.biblemap.put("b24_13_9", new trsuperobject(24,13,9,"‹‹RAB diyor ki, ‹İşte Yahudanın gururunu da Yeruşalimin büyük gururunu da böyle çürüteceğim."));
trsuperobject.biblemap.put("b24_13_10", new trsuperobject(24,13,10,"Sözümü dinlemek istemeyen, yüreklerinin inadı uyarınca davranan, başka ilahları izleyip onlara kulluk eden, tapan bu kötü halk, bu işe yaramaz kuşak gibi olacak."));
trsuperobject.biblemap.put("b24_13_11", new trsuperobject(24,13,11,"Kuşak insanın beline nasıl yapışırsa, ben de İsrail ve Yahuda halklarını kendime öyle yapıştırdım› diyor RAB, ‹Öyle ki, bana ün, övgü, onur getirecek bir halk olsunlar. Ama dinlemediler.››"));
trsuperobject.biblemap.put("b24_13_12", new trsuperobject(24,13,12,"‹‹Onlara de ki, ‹İsrailin Tanrısı RAB, Her tulum şarapla dolacak, diyor.› Eğer sana, ‹Her tulumun şarapla dolacağını bilmiyor muyuz sanki?› derlerse,"));
trsuperobject.biblemap.put("b24_13_13", new trsuperobject(24,13,13,"onlara de ki, ‹Bu ülkede yaşayan herkesi -Davutun tahtında oturan kralları, kâhinleri, peygamberleri, Yeruşalimde yaşayanların tümünü- sarhoş olana dek şarapla dolduracağım› diyor RAB."));
trsuperobject.biblemap.put("b24_13_14", new trsuperobject(24,13,14,"‹Onları -babalarla çocukları- birbirlerine çarpacağım. Acımadan, esirgemeden, sevecenlik göstermeden hepsini yok edeceğim› diyor RAB.››"));
trsuperobject.biblemap.put("b24_13_15", new trsuperobject(24,13,15,"Dinleyin, kulak verin,Gururlanmayın,Çünkü RAB konuştu."));
trsuperobject.biblemap.put("b24_13_16", new trsuperobject(24,13,16,"Karanlık basmadan,Kararan dağlardaAyaklarınız tökezlemedenTanrınız RABbi onurlandırın.Siz ışık beklerken,RAB onu kopkoyu, zifiri karanlığa çevirecek."));
trsuperobject.biblemap.put("b24_13_17", new trsuperobject(24,13,17,"Ama bu uyarıyı dinlemezseniz,Gururunuz yüzünden ağlayacağım gizlice,Gözlerim acı acı gözyaşı dökecek,Gözyaşlarım sel gibi akacak.Çünkü RABbin sürüsü sürgüne gönderilecek."));
trsuperobject.biblemap.put("b24_13_18", new trsuperobject(24,13,18,"Krala ve ana kraliçeye söyle:‹‹Tahtlarınızdan inin,Çünkü görkemli taçlarınız başınızdan düştü.››"));
trsuperobject.biblemap.put("b24_13_19", new trsuperobject(24,13,19,"Negevdeki kentler kapanacak,Onları açan olmayacak.Sürgüne gönderilecek Yahuda,Tamamı sürgüne gönderilecek."));
trsuperobject.biblemap.put("b24_13_20", new trsuperobject(24,13,20,"Gözlerinizi kaldırıp bakın,Kuzeyden gelenleri görün.Nerede sana emanet edilen sürü?Övündüğün kuzular nerede?"));
trsuperobject.biblemap.put("b24_13_21", new trsuperobject(24,13,21,"Sana dost olması için yetiştirdiğin kişileriRAB başına yönetici atayınca ne diyeceksin?Doğuran kadının çektiği sancı gibiSeni de ağrı tutmayacak mı?"));
trsuperobject.biblemap.put("b24_13_22", new trsuperobject(24,13,22,"‹‹Neden bütün bunlar başıma geldi?›› dersen,Günahlarının çokluğu yüzünden eteklerin açıldı,Tecavüze uğradın."));
trsuperobject.biblemap.put("b24_13_23", new trsuperobject(24,13,23,"Kûşlu derisinin rengini,Pars beneklerini değiştirebilir mi?Kötülük etmeye alışmış olan sizler de iyilik edemezsiniz."));
trsuperobject.biblemap.put("b24_13_24", new trsuperobject(24,13,24,"‹‹Çöl rüzgarının savurduğu saman çöpü gibiDağıtacağım sizleri."));
trsuperobject.biblemap.put("b24_13_25", new trsuperobject(24,13,25,"Payın, sana ayırdığım pay bu olacak›› diyor RAB.‹‹Çünkü beni unuttun,Sahte ilahlara güvendin."));
trsuperobject.biblemap.put("b24_13_26", new trsuperobject(24,13,26,"Ayıbın ortaya çıksın diyeEteklerini yüzüne dek kaldıracağım."));
trsuperobject.biblemap.put("b24_13_27", new trsuperobject(24,13,27,"Kırdaki tepeler üzerindeYaptığın iğrençlikleri -zinalarını,Çapkın çapkın kişneyişini, yüzsüz fahişeliklerini- gördüm.Vay başına geleceklere, ey Yeruşalim!Ne zamana dek böyle kirli kalacaksın?››"));
trsuperobject.biblemap.put("b24_14_2", new trsuperobject(24,14,2,"‹‹Yahuda yas tutuyor,Kentleri bitkin;Halkı karalar giymiş, yerlere oturmuş,Yeruşalimin haykırışı yükseliyor."));
trsuperobject.biblemap.put("b24_14_3", new trsuperobject(24,14,3,"Soylular uşaklarını suya gönderiyorlar.Sarnıçlara gidiyor, ama su bulamıyor,Kapları boş dönüyorlar.Aşağılanmış, utanç içinde,Başlarını örtüyorlar."));
trsuperobject.biblemap.put("b24_14_4", new trsuperobject(24,14,4,"Ülke yağmursuz, toprak çatlamış,Irgatlar utanç içinde başlarını örtüyorlar."));
trsuperobject.biblemap.put("b24_14_5", new trsuperobject(24,14,5,"Kırdaki geyik bileYeni doğmuş yavrusunu bırakıyor,Çünkü ot yok."));
trsuperobject.biblemap.put("b24_14_6", new trsuperobject(24,14,6,"Yaban eşekleri çıplak tepelerde durmuş,Çakal gibi soluyorlar;Gözlerinin feri sönmüş,Çünkü otlak yok.››"));
trsuperobject.biblemap.put("b24_14_7", new trsuperobject(24,14,7,"Suçlarımız bize karşı tanıklık etse de,Adın uğruna bir şeyler yap, ya RAB!Pek çok döneklik ettik,Sana karşı günah işledik."));
trsuperobject.biblemap.put("b24_14_8", new trsuperobject(24,14,8,"Ey İsrailin Umudu,Sıkıntı anlarındaki Kurtarıcısı!Neden ülkede bir yabancı,Ancak bir gece konaklayan yolcu gibisin?"));
trsuperobject.biblemap.put("b24_14_9", new trsuperobject(24,14,9,"Neden şaşırmış biri gibi,Kurtarmaya gücü yetmeyen savaşçı gibisin?Aramızdasın sen, ya RAB,Seniniz, bırakma bizi!"));
trsuperobject.biblemap.put("b24_14_10", new trsuperobject(24,14,10,"Bu halk için RAB diyor ki,‹‹Gezip tozmayı pek sever,Ayaklarını dolaşmaktan esirgemezler.Bu yüzden RAB onlardan hoşnut değil,Şimdi anımsayacak suçlarını,Günahları için onları cezalandıracak.››"));
trsuperobject.biblemap.put("b24_14_11", new trsuperobject(24,14,11,"Sonra RAB bana, ‹‹Bu halkın iyiliği için yalvarma›› dedi,"));
trsuperobject.biblemap.put("b24_14_12", new trsuperobject(24,14,12,"‹‹Oruç tutsalar bile feryatlarına kulak vermeyeceğim. Yakmalık sunu, tahıl sunusu sunsalar bile kabul etmeyeceğim. Tersine, kılıçla, kıtlıkla, salgın hastalıkla yok edeceğim onları.››"));
trsuperobject.biblemap.put("b24_14_13", new trsuperobject(24,14,13,"Bunun üzerine, ‹‹Ah, Egemen RAB, peygamberler bu halka, ‹Kılıç yüzü görmeyecek, kıtlık çekmeyeceksiniz; burada size kalıcı esenlik sağlayacağım› diyorlar›› dedim."));
trsuperobject.biblemap.put("b24_14_14", new trsuperobject(24,14,14,"RAB, ‹‹Peygamberler benim adımla yalan peygamberlik ediyorlar›› dedi, ‹‹Onları ne gönderdim, ne onlara buyruk verdim, ne de seslendim. Size uydurma görümlerden, falcılıktan, boş şeylerden, akıllarından geçen hayallerden söz ediyorlar."));
trsuperobject.biblemap.put("b24_14_15", new trsuperobject(24,14,15,"Adımla konuşan peygamberler için ben RAB diyorum ki, onları göndermediğim halde, ‹Bu ülkede kılıç da kıtlık da olmayacak› diyorlar. Ama kendileri de kılıçla, kıtlıkla yok olacaklar."));
trsuperobject.biblemap.put("b24_14_16", new trsuperobject(24,14,16,"Peygamberlik ettikleri halk da kıtlık ve kılıç yüzünden Yeruşalim sokaklarına atılacak. Onları da karılarını, oğullarını, kızlarını da gömecek kimse olmayacak. Yaptıkları kötülüğü kendi başlarına getireceğim."));
trsuperobject.biblemap.put("b24_14_17", new trsuperobject(24,14,17,"‹‹Onlara de ki,  ‹‹Gözlerim gece gündüzDurmadan gözyaşı döksün,Çünkü erden kızım, halkımAğır bir yara aldı,Ezici bir darbe yedi."));
trsuperobject.biblemap.put("b24_14_18", new trsuperobject(24,14,18,"Kıra çıksam, kılıçtan geçirilenleri,Kente girsem, kıtlıktan kırılanları görüyorum.Olup bitenden habersiz peygamberlerle kâhinlerseÜlkeyi dolaşıp duruyorlar.››"));
trsuperobject.biblemap.put("b24_14_19", new trsuperobject(24,14,19,"Yahudayı büsbütün mü reddettin?Siyondan tiksiniyor musun?Neden şifa bulmayacak kadar yaraladın bizi?Esenlik bekledik, iyilik gelmedi.Şifa umduk, yılgınlık bulduk."));
trsuperobject.biblemap.put("b24_14_20", new trsuperobject(24,14,20,"Yaptığımız kötülükleri,Atalarımızın suçlarını biliyoruz, ya RAB;Gerçekten sana karşı günah işledik."));
trsuperobject.biblemap.put("b24_14_21", new trsuperobject(24,14,21,"Adın uğruna bizi küçümseme,Görkemli tahtının hor görülmesine izin verme.Bizimle yaptığın antlaşmayı anımsa,Bozma onu."));
trsuperobject.biblemap.put("b24_14_22", new trsuperobject(24,14,22,"Ulusların değersiz putlarından herhangi biriYağmur yağdırabilir mi?Gökler kendiliğindenSağanak yağdırabilir mi?Bunu yalnız sen yapabilirsin,Ya RAB Tanrımız.Umudumuz sende,Çünkü bütün bunları yapan sensin."));
trsuperobject.biblemap.put("b24_15_2", new trsuperobject(24,15,2,"Sana, ‹Nereye gidelim?› diye sorarlarsa de ki, ‹RAB şöyle diyor:  ‹‹Ölüm için ayrılanlar ölüme,Kılıç için ayrılanlar kılıca,Kıtlık için ayrılanlar kıtlığa,Sürgün için ayrılanlar sürgüne.›"));
trsuperobject.biblemap.put("b24_15_3", new trsuperobject(24,15,3,"‹‹Onların başına dört tür yıkım getirmeye karar verdim›› diyor RAB, ‹‹Öldürmek için kılıcı, paralamak için köpekleri, yiyip bitirmek, yok etmek için yırtıcı kuşlarla yabanıl hayvanları salacağım üzerlerine."));
trsuperobject.biblemap.put("b24_15_4", new trsuperobject(24,15,4,"Yahuda Kralı Hizkiya oğlu Manaşşenin Yeruşalimde yaptıkları yüzünden bütün yeryüzü krallıklarını dehşete düşüreceğim."));
trsuperobject.biblemap.put("b24_15_5", new trsuperobject(24,15,5,"‹‹Kim acıyacak sana, ey Yeruşalim?Kim yas tutacak senin için?Hal hatır sormak içinKim yolundan dönüp sana gelecek?"));
trsuperobject.biblemap.put("b24_15_6", new trsuperobject(24,15,6,"Sen beni reddettin›› diyor RAB,‹‹Gerisingeri gidiyorsun.Ben de elimi sana karşı kaldıracak,Seni yok edeceğim;Merhamet ede ede yoruldum."));
trsuperobject.biblemap.put("b24_15_7", new trsuperobject(24,15,7,"Ülkenin kapılarında,Halkımı yabayla savuracak,Çocuksuz bırakacak, yok edeceğim;Çünkü yollarından dönmediler."));
trsuperobject.biblemap.put("b24_15_8", new trsuperobject(24,15,8,"Dul kadınlarının sayısı denizin kumundan çok olacak.Gençlerinin annelerineÖğle vakti yok ediciyi göndereceğim;Üzerlerine ansızın acı, dehşet salacağım."));
trsuperobject.biblemap.put("b24_15_9", new trsuperobject(24,15,9,"Yedi çocuklu kadınBayılıp son soluğunu verecek;Daha gündüzken güneşi batacak,Utandırılıp alçaltılacak.Sağ kalanları düşmanlarının önündeKılıca teslim edeceğim.››Böyle diyor RAB."));
trsuperobject.biblemap.put("b24_15_10", new trsuperobject(24,15,10,"Vay başıma!Herkesle çekişip davacı olayım diyeDoğurmuşsun beni, ey annem!Ne ödünç aldım, ne de verdim,Yine de herkes lanet okuyor bana."));
trsuperobject.biblemap.put("b24_15_11", new trsuperobject(24,15,11,"RAB şöyle dedi:‹‹Kuşkun olmasın, iyilik için seni özgür kılacağım,Yıkım ve sıkıntı zamanındaDüşmanlarını sana yalvartacağım."));
trsuperobject.biblemap.put("b24_15_12", new trsuperobject(24,15,12,"‹‹Demiri, kuzeyden gelen demiriYa da tuncu kimse kırabilir mi?"));
trsuperobject.biblemap.put("b24_15_13", new trsuperobject(24,15,13,"Ülkende işlenen günahlar yüzündenServetini de hazinelerini de karşılıksız,Çapul malı olarak vereceğim."));
trsuperobject.biblemap.put("b24_15_14", new trsuperobject(24,15,14,"Bilmediğin bir ülkedeDüşmanlarına köle edeceğim seni.Çünkü size karşı öfkemAteş gibi tutuşup yanacak.›› ‹‹Geçireceğim›› (bkz. 17:4)."));
trsuperobject.biblemap.put("b24_15_15", new trsuperobject(24,15,15,"Sen bilirsin, ya RAB,Beni anımsa, beni kolla.Bana eziyet edenlerden öcümü al.Sabrınla beni canımdan etme,Senin uğruna aşağılandığımı unutma."));
trsuperobject.biblemap.put("b24_15_16", new trsuperobject(24,15,16,"Sözlerini bulur bulmaz yuttum,Bana neşe, yüreğime sevinç oldu.Çünkü seninim ben,Ya RAB, Her Şeye Egemen Tanrı!"));
trsuperobject.biblemap.put("b24_15_17", new trsuperobject(24,15,17,"Eğlenenlerin arasında oturmadım,Onlarla sevinip coşmadım.Elin üzerimde olduğu içinTek başıma oturdum,Çünkü beni öfkeyle doldurmuştun."));
trsuperobject.biblemap.put("b24_15_18", new trsuperobject(24,15,18,"Neden sürekli acı çekiyorum?Neden yaram ağır ve umarsız?Benim için aldatıcı bir dere,Güvenilmez bir pınar mı olacaksın?"));
trsuperobject.biblemap.put("b24_15_19", new trsuperobject(24,15,19,"Bu yüzden RAB diyor ki,‹‹Eğer dönersen seni yine hizmetime alırım;İşe yaramaz sözler değil,Değerli sözler söylersen,Benim sözcüm olursun.Bu halk sana dönecek,Ama sen onlara dönmemelisin."));
trsuperobject.biblemap.put("b24_15_20", new trsuperobject(24,15,20,"Bu halkın karşısındaSağlamlaştırılmış tunç bir duvar kılacağım seni;Seninle savaşacak ama yenemeyecekler,Çünkü yardım etmek, kurtarmak içinBen seninleyim›› diyor RAB."));
trsuperobject.biblemap.put("b24_15_21", new trsuperobject(24,15,21,"‹‹Seni kötünün elinden kurtaracak,Acımasızın avucundan kurtaracağım.››"));
trsuperobject.biblemap.put("b24_16_2", new trsuperobject(24,16,2,"‹‹Kendine karı alma, burada oğulların, kızların olmasın.››"));
trsuperobject.biblemap.put("b24_16_3", new trsuperobject(24,16,3,"Bu ülkede doğan oğullarla kızlar ve anne babaları için RAB diyor ki,"));
trsuperobject.biblemap.put("b24_16_4", new trsuperobject(24,16,4,"‹‹Ölümcül hastalıklardan ölecekler. Onlar için yas tutulmayacak, gömülmeyecekler. Cesetleri toprağın üzerinde gübre gibi kalacak. Kılıçla, kıtlıkla yok olacaklar; cesetleri yırtıcı kuşlara, yabanıl hayvanlara yem olacak.››"));
trsuperobject.biblemap.put("b24_16_5", new trsuperobject(24,16,5,"Çünkü RAB diyor ki, ‹‹Cenaze yemeğinin verildiği eve gitme, dövünmek için gitme, başsağlığı dileme. Çünkü ben bu halktan esenliğimi, sevgimi, sevecenliğimi geri çektim›› diyor RAB."));
trsuperobject.biblemap.put("b24_16_6", new trsuperobject(24,16,6,"‹‹Bu ülkede büyükler de küçükler de ölecek, gömülmeyecekler. Onlar için yas tutan, dövünüp bedenini yaralayan, başını tıraş eden olmayacak."));
trsuperobject.biblemap.put("b24_16_7", new trsuperobject(24,16,7,"Ölene yas tutanı avutmak için kimse onunla yemek yemeyecek. Anne babasını yitirene kimse avunç kâsesini sunmayacak."));
trsuperobject.biblemap.put("b24_16_8", new trsuperobject(24,16,8,"‹‹Şölen evine de gitme, onlarla oturma, yiyip içme."));
trsuperobject.biblemap.put("b24_16_9", new trsuperobject(24,16,9,"Çünkü İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Burada sevinç ve neşe sesine, gelin güvey sesine senin günlerinde gözünün önünde son vereceğim.›"));
trsuperobject.biblemap.put("b24_16_10", new trsuperobject(24,16,10,"‹‹Bütün bunları bu halka bildirdiğinde, ‹RAB neden başımıza bu büyük felaketi getireceğini bildirdi? Suçumuz ne? Tanrımız RABbe karşı işlediğimiz günah ne?› diye sorarlarsa,"));
trsuperobject.biblemap.put("b24_16_11", new trsuperobject(24,16,11,"de ki, ‹Atalarınız beni terk etti› diyor RAB, ‹Başka ilahların ardınca gittiler, onlara kulluk edip taptılar. Beni terk ettiler, Kutsal Yasama uymadılar."));
trsuperobject.biblemap.put("b24_16_12", new trsuperobject(24,16,12,"Sizse atalarınızdan daha çok kötülük yaptınız. Beni dinleyeceğinize, kötü yüreğinizin inadı uyarınca davrandınız."));
trsuperobject.biblemap.put("b24_16_13", new trsuperobject(24,16,13,"Bu yüzden sizi bu ülkeden sizin de atalarınızın da bilmediği bir ülkeye atacağım. Orada gece gündüz başka ilahlara kulluk edeceksiniz, çünkü size lütfetmeyeceğim.›"));
trsuperobject.biblemap.put("b24_16_14", new trsuperobject(24,16,14,"‹‹Artık insanların, ‹İsrail halkını Mısırdan çıkaran RABbin varlığı hakkı için› demeyeceği günler geliyor›› diyor RAB."));
trsuperobject.biblemap.put("b24_16_15", new trsuperobject(24,16,15,"‹‹Bunun yerine, ‹İsrail halkını kuzey ülkesinden ve sürdüğü bütün öbür ülkelerden geri getiren RABbin varlığı hakkı için› diyecekler. Çünkü atalarına vermiş olduğum topraklara onları geri getireceğim."));
trsuperobject.biblemap.put("b24_16_16", new trsuperobject(24,16,16,"‹‹Birçok balıkçı çağırmak üzereyim. Onları yakalayacaklar›› diyor RAB, ‹‹Ardından birçok avcı çağıracağım. Her dağın, her tepenin üzerinden, kaya kovuklarından avlayacaklar onları."));
trsuperobject.biblemap.put("b24_16_17", new trsuperobject(24,16,17,"Bütün yaptıklarını görüyorum; hiçbiri benden gizli değil. Günahları da gözümden kaçmıyor."));
trsuperobject.biblemap.put("b24_16_18", new trsuperobject(24,16,18,"İlkin suçlarını, günahlarını iki katıyla onlara ödeteceğim. Çünkü tiksindirici cansız ilahlarıyla ülkemi kirlettiler, mülkümü iğrenç putlarıyla doldurdular."));
trsuperobject.biblemap.put("b24_16_19", new trsuperobject(24,16,19,"‹‹Ya RAB, sen benim gücüm,Kalem, sıkıntı gününde sığınağımsın.Dünyanın dört bucağındanUluslar sana gelip,‹Atalarımız yalnız yalanları,Kendilerine hiçbir yararı olmayanDeğersiz putları miras aldılar› diyecekler,"));
trsuperobject.biblemap.put("b24_16_20", new trsuperobject(24,16,20,"‹İnsan kendine ilah yapar mı?Onlar ilah değil ki!›"));
trsuperobject.biblemap.put("b24_16_21", new trsuperobject(24,16,21,"‹‹Onun için bu kez onlaraGücümü, kudretimi tanıtacağım.O zaman adımın RAB olduğunu anlayacaklar.››"));
trsuperobject.biblemap.put("b24_17_1", new trsuperobject(24,17,1,"‹‹Yahudanın günahı demir kalemle yazıldı;Yüreklerinin levhaları,Sunaklarının boynuzları üzerineElmas uçlu aletle oyuldu."));
trsuperobject.biblemap.put("b24_17_2", new trsuperobject(24,17,2,"Bol yapraklı her ağacın yanında,Her yüksek tepedeki sunaklarla,Aşera putlarıylaÇocuklarıymış gibi ilgileniyorlar."));
trsuperobject.biblemap.put("b24_17_3", new trsuperobject(24,17,3,"Ey kırdaki dağım, ülkende işlenen günahlar yüzündenServetini, bütün hazineleriniVe puta tapılan yerlerini bırakacağım, yağmalansın."));
trsuperobject.biblemap.put("b24_17_4", new trsuperobject(24,17,4,"Sana verdiğim mülkü kendi suçunla yitireceksin.Bilmediğin bir ülkedeDüşmanlarına köle edeceğim seni.Çünkü öfkemi alevlendirdiniz,Tutuşup sonsuza dek yanacak.››"));
trsuperobject.biblemap.put("b24_17_6", new trsuperobject(24,17,6,"Böylesi bozkırdaki çalı gibidir,İyilik geldiği zaman görmeyecek;Kurak çöle,Kimsenin yaşamadığı tuzlaya yerleşecek."));
trsuperobject.biblemap.put("b24_17_7", new trsuperobject(24,17,7,"‹‹Ne mutlu RABbe güvenen insana,Güveni yalnız RAB olana!"));
trsuperobject.biblemap.put("b24_17_8", new trsuperobject(24,17,8,"Böylesi su kıyılarına dikilmiş ağaca benzer,Köklerini akarsulara salar.Sıcak gelince korkmaz,Yaprakları hep yeşildir.Kuraklık yılında kaygılanmaz,Meyve vermekten geri durmaz.››"));
trsuperobject.biblemap.put("b24_17_9", new trsuperobject(24,17,9,"Yürek her şeyden daha aldatıcıdır, iyileşmez,Onu kim anlayabilir?"));
trsuperobject.biblemap.put("b24_17_10", new trsuperobject(24,17,10,"‹‹Ben RAB, herkesi davranışlarına,Yaptıklarının sonucuna göre ödüllendirmek içinYüreği yoklar, düşünceyi denerim.››"));
trsuperobject.biblemap.put("b24_17_11", new trsuperobject(24,17,11,"Yumurtlamadığı yumurtaların üzerinde oturan keklik nasılsa,Haksız servet edinen kişi de öyledir.Yaşamının ortasında serveti onu bırakır,Yaşamının sonunda kendisi aptal çıkar."));
trsuperobject.biblemap.put("b24_17_12", new trsuperobject(24,17,12,"Tapınağımızın yeriBaşlangıçtan yüceltilmiş görkemli bir tahttır."));
trsuperobject.biblemap.put("b24_17_13", new trsuperobject(24,17,13,"Ey İsrailin umudu RAB,Seni bırakanların hepsiUtanılacak duruma düşecek.Sana sırtını dönenler toprağa yazılacak,Çünkü RABbi, diri su pınarını bıraktılar."));
trsuperobject.biblemap.put("b24_17_14", new trsuperobject(24,17,14,"Şifa ver bana, ya RAB,O zaman iyi olurum;Kurtar beni, kurtuluş bulurum,Çünkü övgüm sensin."));
trsuperobject.biblemap.put("b24_17_15", new trsuperobject(24,17,15,"Bana, ‹‹Hani, RABbin sözü nerede?Haydi, gelsin yerine bakalım›› deyip duruyorlar."));
trsuperobject.biblemap.put("b24_17_16", new trsuperobject(24,17,16,"Senin hizmetinde çoban olmaktan kaçınmadım,Felaket gününü de ben istemedim.Dudaklarımdan çıkan her sözü bilirsin, ya RAB.O söz zaten senin ağzındaydı."));
trsuperobject.biblemap.put("b24_17_17", new trsuperobject(24,17,17,"Dehşet verme bana,Felaket gününde sığınağım sensin."));
trsuperobject.biblemap.put("b24_17_18", new trsuperobject(24,17,18,"Bana eziyet edenler utandırılsın,Ama beni utandırma;Onları yılgınlığa düşür,Ama beni düşürme.Felaket gününü getir üzerlerine,Onları iki kat yıkımla ez."));
trsuperobject.biblemap.put("b24_17_19", new trsuperobject(24,17,19,"RAB bana şöyle dedi: ‹‹Yahuda krallarının girip çıktığı Halk Kapısına ve Yeruşalimin öbür kapılarına git, orada dur."));
trsuperobject.biblemap.put("b24_17_20", new trsuperobject(24,17,20,"Halka de ki, ‹Ey Yahuda kralları, Yahuda halkı, Yeruşalimde oturup bu kapılardan girenler, RABbin sözünü dinleyin!"));
trsuperobject.biblemap.put("b24_17_21", new trsuperobject(24,17,21,"RAB diyor ki, Şabat Günü yük taşımamaya, Yeruşalim kapılarından içeri bir şey sokmamaya dikkat edin."));
trsuperobject.biblemap.put("b24_17_22", new trsuperobject(24,17,22,"Şabat Günü evinizden yük çıkarmayın, hiç iş yapmayın. Atalarınıza buyurduğum gibi Şabat Gününü kutsal sayacaksınız."));
trsuperobject.biblemap.put("b24_17_23", new trsuperobject(24,17,23,"Ne var ki, onlar sözümü dinlemediler, kulak asmadılar. Dikbaşlılık ederek beni dinlemediler, yola gelmek istemediler."));
trsuperobject.biblemap.put("b24_17_24", new trsuperobject(24,17,24,"Beni iyi dinlerseniz, diyor RAB, Şabat Günü bu kentin kapılarından yük taşımayıp hiç iş yapmayarak Şabat Gününü kutsal sayarsanız,"));
trsuperobject.biblemap.put("b24_17_25", new trsuperobject(24,17,25,"Davutun tahtında oturan krallarla önderler savaş arabalarına, atlara binip Yahuda halkı ve Yeruşalimde yaşayanlarla birlikte bu kentin kapılarından girecekler. Bu kentte sonsuza dek insanlar yaşayacak."));
trsuperobject.biblemap.put("b24_17_26", new trsuperobject(24,17,26,"Yahuda kentlerinden, Yeruşalim çevresinden, Benyamin topraklarından, Şefeladan, dağlık bölgeden, Negevden gelip RABbin Tapınağına yakmalık sunular, kurbanlar, tahıl sunuları, günnük ve şükran sunuları getirecekler."));
trsuperobject.biblemap.put("b24_17_27", new trsuperobject(24,17,27,"Ancak beni dinlemez, Şabat Günü Yeruşalim kapılarından yük taşıyarak girer, o günü kutsal saymazsanız, kentin kapılarını ateşe vereceğim. Yeruşalim saraylarını yakıp yok edecek, hiç sönmeyecek ateş.››"));
trsuperobject.biblemap.put("b24_18_2", new trsuperobject(24,18,2,"‹‹Kalk, çömlekçinin işliğine git; orada sana sesleneceğim.››"));
trsuperobject.biblemap.put("b24_18_3", new trsuperobject(24,18,3,"Bunun üzerine çömlekçinin işliğine gittim. Çark üzerinde çalışıyordu."));
trsuperobject.biblemap.put("b24_18_4", new trsuperobject(24,18,4,"Yaptığı balçıktan kap elinde bozulunca çömlekçi balçığa istediği biçimi vererek başka bir kap yaptı."));
trsuperobject.biblemap.put("b24_18_6", new trsuperobject(24,18,6,"‹‹Bu çömlekçinin yaptığını ben de size yapamaz mıyım, ey İsrail halkı? diyor RAB. Çömlekçinin elinde balçık neyse, siz de benim elimde öylesiniz, ey İsrail halkı!"));
trsuperobject.biblemap.put("b24_18_7", new trsuperobject(24,18,7,"Bir ulusun ya da krallığın kökünden söküleceğini, yıkılıp yok edileceğini duyururum da,"));
trsuperobject.biblemap.put("b24_18_8", new trsuperobject(24,18,8,"uyardığım ulus kötülüğünden dönerse, başına felaket getirme kararımdan vazgeçerim."));
trsuperobject.biblemap.put("b24_18_9", new trsuperobject(24,18,9,"Öte yandan, bir ulusun ya da krallığın kurulup dikileceğini duyururum da,"));
trsuperobject.biblemap.put("b24_18_10", new trsuperobject(24,18,10,"o ulus sözümü dinlemeyip gözümde kötü olanı yaparsa, ona söz verdiğim iyiliği yapmaktan vazgeçerim."));
trsuperobject.biblemap.put("b24_18_11", new trsuperobject(24,18,11,"‹‹Bu nedenle Yahuda halkıyla Yeruşalimde yaşayanlara de ki, ‹RAB şöyle diyor: İşte size bir felaket tasarlıyor, size karşı bir düzen kuruyorum. Onun için her biriniz kötü yolundan dönsün, yaşantınızı da davranışlarınızı da düzeltin.›"));
trsuperobject.biblemap.put("b24_18_12", new trsuperobject(24,18,12,"Ama onlar, ‹Boş ver! Biz kendi tasarlarımızı sürdüreceğiz; her birimiz kötü yüreğinin inadı uyarınca davranacak› diyecekler.››"));
trsuperobject.biblemap.put("b24_18_13", new trsuperobject(24,18,13,"Bu yüzden RAB diyor ki,‹‹Uluslar arasında soruşturun:Böylesini kim duydu?Erden kız İsrailÇok korkunç bir şey yaptı."));
trsuperobject.biblemap.put("b24_18_14", new trsuperobject(24,18,14,"Kayalık bayırlardanLübnanın karı hiç eksik olur mu?Uzaktan akan soğuk sular hiç kesilir mi?"));
trsuperobject.biblemap.put("b24_18_15", new trsuperobject(24,18,15,"Oysa halkım beni unuttu,Değersiz ilahlara buhur yaktı.Bu ilahlar gidecekleri yollarda,Eski yollarda sendelemelerine neden oldu;Onları sapa, bitmemiş yollarda yürüttü."));
trsuperobject.biblemap.put("b24_18_16", new trsuperobject(24,18,16,"Ülkeleri viran edilecek,Sürekli alay konusu olacak;Oradan her geçen şaşkın şaşkınBaşını sallayacak."));
trsuperobject.biblemap.put("b24_18_17", new trsuperobject(24,18,17,"Onları düşmanlarının önündeDoğu rüzgarı gibi dağıtacağım;Yıkım günü yüzümü değil,Sırtımı çevireceğim onlara.››"));
trsuperobject.biblemap.put("b24_18_18", new trsuperobject(24,18,18,"Bunun üzerine, ‹‹Haydi, Yeremyaya karşı bir düzen kuralım!›› dediler, ‹‹Çünkü yasayı öğretecek kâhin, öğüt verecek bilge, Tanrı sözünü bildirecek peygamber hiç eksik olmayacak. Gelin, ona sözle saldıralım, söylediklerini de dinlemeyelim.››"));
trsuperobject.biblemap.put("b24_18_19", new trsuperobject(24,18,19,"Dinle beni, ya RAB,Beni suçlayanların dediklerini işit!"));
trsuperobject.biblemap.put("b24_18_20", new trsuperobject(24,18,20,"İyiliğe karşı kötülük mü yapmalı?Ama onlar bana çukur kazdılar.Onlara duyduğun öfkeyi yatıştırmak,Onların iyiliğini dilemek içinSenin önünde nasıl durduğumu anımsa."));
trsuperobject.biblemap.put("b24_18_21", new trsuperobject(24,18,21,"Bu yüzden çocuklarını kıtlığa ver,Kılıcın ağzına at.Karıları çocuksuz, dul kalsın,Erkeklerini ölüm alıp götürsün,Gençleri savaşta kılıçtan geçirilsin."));
trsuperobject.biblemap.put("b24_18_22", new trsuperobject(24,18,22,"Sen üzerlerine ansızın akıncılar gönderdiğinde,Evlerinden çığlıklar duyulsun.Çünkü beni yakalamak için çukur kazdılar,Ayaklarıma gizli tuzak kurdular."));
trsuperobject.biblemap.put("b24_18_23", new trsuperobject(24,18,23,"Beni öldürmek için kurdukları düzenlerin hepsiniBiliyorsun, ya RAB.Bağışlama suçlarını,Günahlarını önünden silme.Yığılıp kalsınlar senin önünde.Öfkeliyken uğraş onlarla."));
trsuperobject.biblemap.put("b24_19_2", new trsuperobject(24,19,2,"Harsit Kapısına yakın Ben-Hinnom Vadisine git. Sana söyleyeceklerimi orada duyur."));
trsuperobject.biblemap.put("b24_19_3", new trsuperobject(24,19,3,"De ki, ‹RABbin sözünü dinleyin, ey Yahuda kralları ve Yeruşalimde yaşayanlar! İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: Dinleyin! Buraya, her duyanı şaşkına çevirecek bir felaket göndermek üzereyim."));
trsuperobject.biblemap.put("b24_19_4", new trsuperobject(24,19,4,"Çünkü beni terk ettiler, burayı yabancı bir ülke haline getirdiler. Kendilerinin de atalarıyla Yahuda krallarının da tanımadığı başka ilahlara burada buhur yaktılar, burayı döktükleri suçsuz kanıyla doldurdular."));
trsuperobject.biblemap.put("b24_19_5", new trsuperobject(24,19,5,"Çocuklarını ateşte Baala kurban etmek için tapınma yerleri kurdular. Böyle bir şey ne buyurdum ne sözünü ettim ne de aklımdan geçirdim."));
trsuperobject.biblemap.put("b24_19_6", new trsuperobject(24,19,6,"Bundan ötürü buranın artık Tofet ya da Ben-Hinnom Vadisi değil, Kıyım Vadisi diye anılacağı günler geliyor, diyor RAB."));
trsuperobject.biblemap.put("b24_19_7", new trsuperobject(24,19,7,"Yahuda ve Yeruşalimin tasarılarını burada boşa çıkaracağım. Onları canlarına susayanların eline verecek, düşmanlarının önünde kılıçla düşüreceğim. Cesetlerini yem olarak yırtıcı kuşlara, yabanıl hayvanlara vereceğim."));
trsuperobject.biblemap.put("b24_19_8", new trsuperobject(24,19,8,"Bu kenti viraneye çevirecek, alay konusu edeceğim; oradan her geçen şaşkın şaşkın bakıp başına gelen belalardan ötürü onunla alay edecek."));
trsuperobject.biblemap.put("b24_19_9", new trsuperobject(24,19,9,"Onlara oğullarının, kızlarının etini yedireceğim. Canlarına susamış düşmanları onları kuşattığında sıkıntıdan birbirlerini yiyecekler.›"));
trsuperobject.biblemap.put("b24_19_10", new trsuperobject(24,19,10,"‹‹O zaman seninle gidenlerin önünde çömleği kır"));
trsuperobject.biblemap.put("b24_19_11", new trsuperobject(24,19,11,"ve onlara de ki, ‹Her Şeye Egemen RAB şöyle diyor: Çömlekçinin çömleği nasıl kırılıp bir daha onarılamazsa, ben de bu halkı ve bu kenti öyle kıracağım. Ölüleri yer kalmayana dek Tofette gömecekler."));
trsuperobject.biblemap.put("b24_19_12", new trsuperobject(24,19,12,"Bu kente de içinde yaşayanlara da böyle davranacağım, diyor RAB. Bu kenti Tofet gibi yapacağım."));
trsuperobject.biblemap.put("b24_19_13", new trsuperobject(24,19,13,"Yeruşalimin evleri de Yahuda krallarının sarayları da Tofet gibi kirli sayılacak. Çünkü bu evlerin damlarında gök cisimlerine buhur yaktılar, başka ilahlara dökmelik sunular sundular.››"));
trsuperobject.biblemap.put("b24_19_14", new trsuperobject(24,19,14,"Peygamberlik etmesi için RABbin Tofete gönderdiği Yeremya oradan döndü. RABbin Tapınağının avlusunda durup halka şöyle dedi:"));
trsuperobject.biblemap.put("b24_19_15", new trsuperobject(24,19,15,"‹‹İsrail'in Tanrısı, Her Şeye Egemen RAB diyor ki, ‹İşte bu kente ve çevresindeki köylere sözünü ettiğim bütün felaketleri getireceğim. Çünkü dikbaşlılık edip sözümü dinlemediler.››"));
trsuperobject.biblemap.put("b24_20_2", new trsuperobject(24,20,2,"onun dövülüp RABbin Tapınağının Yukarı Benyamin Kapısındaki tomruğa vurulmasını buyurdu."));
trsuperobject.biblemap.put("b24_20_3", new trsuperobject(24,20,3,"Ertesi gün Paşhur kendisini tomruktan salıverince, Yeremya ona, ‹‹RAB sana Paşhur değil, Magor-Missaviv adını verdi›› dedi,"));
trsuperobject.biblemap.put("b24_20_4", new trsuperobject(24,20,4,"‹‹RAB diyor ki, ‹Seni de dostlarını da yıldıracağım. Dostlarının düşman kılıcıyla düştüğünü gözlerinle göreceksin. Bütün Yahudayı Babil Kralının eline teslim edeceğim; onları Babile sürecek ya da kılıçtan geçirecek."));
trsuperobject.biblemap.put("b24_20_5", new trsuperobject(24,20,5,"Bu kentin bütün zenginliğini -ürününü, değerli eşyalarını, Yahuda krallarının hazinelerini- düşmanlarının eline vereceğim. Hepsini yağmalayıp Babile götürecekler."));
trsuperobject.biblemap.put("b24_20_6", new trsuperobject(24,20,6,"Sana gelince, ey Paşhur, sen de evinde yaşayanların hepsi de Babile sürüleceksiniz. Sen de kendilerine yalan peygamberlik ettiğin bütün dostların da orada ölüp gömüleceksiniz.›› gelir. Aynı ifade 20:10da da geçer."));
trsuperobject.biblemap.put("b24_20_7", new trsuperobject(24,20,7,"Beni kandırdın, ya RAB,Ben de kandım.Bana üstün geldin, beni yendin.Bütün gün alay konusu oluyorum,Herkes benimle eğleniyor."));
trsuperobject.biblemap.put("b24_20_8", new trsuperobject(24,20,8,"Çünkü konuştukça feryat ediyor,Şiddet diye, yıkım diye haykırıyorum.RABbin sözü yüzünden bütün gün yeriliyor,Gülünç duruma düşüyorum."));
trsuperobject.biblemap.put("b24_20_9", new trsuperobject(24,20,9,"‹‹Bir daha onu anmayacak,Onun adına konuşmayacağım›› desem,Sözü kemiklerimin içine hapsedilmiş,Yüreğimde yanan bir ateş sanki.Onu içimde tutmaktan yoruldum,Yapamıyorum artık."));
trsuperobject.biblemap.put("b24_20_10", new trsuperobject(24,20,10,"Birçoğunun, ‹‹Her yer dehşet içinde!Suçlayın! Suçlayalım onu!›› diye fısıldaştığını duydum.Bütün güvendiğim insanlar düşmemi gözlüyor,‹‹Belki kanar, onu yeneriz,Sonra da öcümüzü alırız›› diyorlar."));
trsuperobject.biblemap.put("b24_20_11", new trsuperobject(24,20,11,"Ama RAB güçlü bir savaşçı gibi benimledir.Bu yüzden bana eziyet edenler tökezleyecek,Üstün gelemeyecek,Başarısızlığa uğrayıp büyük utanca düşecekler;Onursuzlukları sonsuza dek unutulmayacak."));
trsuperobject.biblemap.put("b24_20_12", new trsuperobject(24,20,12,"Ey doğru kişiyi sınayan,Yüreği ve düşünceyi gören Her Şeye Egemen RAB!Davamı senin eline bırakıyorum.Onlardan alacağın öcü göreyim!"));
trsuperobject.biblemap.put("b24_20_13", new trsuperobject(24,20,13,"Ezgiler okuyun RABbe!Övün RABbi!Çünkü yoksulun canını kötülerin elinden O kurtardı."));
trsuperobject.biblemap.put("b24_20_14", new trsuperobject(24,20,14,"Lanet olsun doğduğum güne!Kutlu olmasın annemin beni doğurduğu gün!"));
trsuperobject.biblemap.put("b24_20_15", new trsuperobject(24,20,15,"‹‹Bir oğlun oldu!›› diyerek babama haber getiren,Onu sevince boğan adama lanet olsun!"));
trsuperobject.biblemap.put("b24_20_16", new trsuperobject(24,20,16,"RABbin acımadan yerle bir ettiğiKentler gibi olsun o adam!Sabah feryatlar,Öğlen savaş naraları duysun!"));
trsuperobject.biblemap.put("b24_20_17", new trsuperobject(24,20,17,"Çünkü beni annemin rahminde öldürmedi;Annem mezarım olur,Rahmi hep gebe kalırdı."));
trsuperobject.biblemap.put("b24_20_18", new trsuperobject(24,20,18,"Neden ana rahminden çıktım?Dert, üzüntü görmek,Ömrümü utanç içinde geçirmek için mi?"));
trsuperobject.biblemap.put("b24_21_1", new trsuperobject(24,21,1,"Kral Sidkiya Malkiya oğlu Paşhurla Maaseya oğlu Kâhin Sefanyayı Yeremyaya gönderince, RAB Yeremyaya seslendi. Paşhurla Sefanya ona şöyle demişti:"));
trsuperobject.biblemap.put("b24_21_2", new trsuperobject(24,21,2,"‹‹Lütfen bizim için RABbe danış. Çünkü Babil Kralı Nebukadnessar bize saldırıyor. Belki RAB bizim için şaşılacak işlerinden birini yapar da Nebukadnessar ülkemizden çekilir.››"));
trsuperobject.biblemap.put("b24_21_3", new trsuperobject(24,21,3,"Yeremya şu karşılığı verdi: ‹‹Sidkiyaya deyin ki,"));
trsuperobject.biblemap.put("b24_21_4", new trsuperobject(24,21,4,"‹İsrailin Tanrısı RAB şöyle diyor: Surların dışında sizi kuşatan Babil Kralı ve Kildanilerle savaşmakta kullandığınız silahları size karşı çevireceğim; hepsini bu kentin ortasına toplayacağım."));
trsuperobject.biblemap.put("b24_21_5", new trsuperobject(24,21,5,"Ben de elimi size karşı kaldıracağım; kudretle, kızgınlıkla, gazapla, büyük öfkeyle sizinle savaşacağım."));
trsuperobject.biblemap.put("b24_21_6", new trsuperobject(24,21,6,"Bu kentte yaşayanları yok edeceğim; insan da, hayvan da korkunç bir salgın hastalıktan ölecek."));
trsuperobject.biblemap.put("b24_21_7", new trsuperobject(24,21,7,"Ondan sonra da, diyor RAB, Yahuda Kralı Sidkiyayla görevlilerini, bu kentte salgından, kılıçtan, kıtlıktan sağ çıkan halkı Babil Kralı Nebukadnessarın ve canlarına susamış düşmanlarının eline teslim edeceğim. Hepsini kılıçtan geçirecek, canlarını bağışlamayacak, merhamet etmeyecek, acımayacak.›"));
trsuperobject.biblemap.put("b24_21_8", new trsuperobject(24,21,8,"‹‹Bunun yanısıra halka şunları da söyle: ‹RAB diyor ki: İşte yaşama giden yolu da ölüme giden yolu da önünüze koyuyorum."));
trsuperobject.biblemap.put("b24_21_9", new trsuperobject(24,21,9,"Bu kentte kalan kılıçtan, kıtlıktan, salgından ölecek; dışarı çıkıp kenti kuşatan Kildanilere teslim olansa yaşayacak, hiç değilse canını kurtarmış olacak."));
trsuperobject.biblemap.put("b24_21_10", new trsuperobject(24,21,10,"Bu kente iyilik değil, kötülük etmeye karar verdim, diyor RAB. Bu kenti Babil Kralı ele geçirip ateşe verecek.››"));
trsuperobject.biblemap.put("b24_21_11", new trsuperobject(24,21,11,"‹‹Yahuda Kralının ailesine de ki, ‹RABbin sözünü dinleyin:"));
trsuperobject.biblemap.put("b24_21_12", new trsuperobject(24,21,12,"RAB şöyle diyor, ey Davut soyu:  ‹‹Her sabah adaleti uygulayın,Soyguna uğramış kişiyi zorbanın elinden kurtarın.Yoksa yaptığınız kötülük yüzündenÖfkem ateş gibi tutuşup yanacak,Söndüren olmayacak."));
trsuperobject.biblemap.put("b24_21_13", new trsuperobject(24,21,13,"Ey vadinin üstünde,Kayalık ovada oturan Yeruşalim,Sana karşıyım diyor RAB,Siz ki, kim bize saldırabilir?Sığınağımıza kim girebilir, diyorsunuz."));
trsuperobject.biblemap.put("b24_21_14", new trsuperobject(24,21,14,"Sizi yaptıklarınıza göre cezalandıracağım, diyor RAB.Bütün çevresini yakıp yok edecekBir ateş tutuşturacağım kentin ormanında.››"));
trsuperobject.biblemap.put("b24_22_2", new trsuperobject(24,22,2,"‹RABbin sözünü dinleyin, ey Davutun tahtında oturan Yahuda Kralıyla görevlileri ve bu kapılardan giren halk!"));
trsuperobject.biblemap.put("b24_22_4", new trsuperobject(24,22,4,"Bu buyrukları özenle yerine getirirseniz, Davutun tahtında oturan krallar savaş arabalarıyla, atlarıyla bu sarayın kapılarından girecekler; görevlileriyle halkları da onları izleyecek."));
trsuperobject.biblemap.put("b24_22_5", new trsuperobject(24,22,5,"Ancak bu buyruklara uymazsanız, diyor RAB, adım üzerine ant içerim ki, bu saray viraneye dönecek.››"));
trsuperobject.biblemap.put("b24_22_6", new trsuperobject(24,22,6,"Çünkü Yahuda Kralının sarayı için RAB diyor ki,  ‹‹Sen benim için Gilat gibisin,Lübnanın doruğu gibi.Ama hiç kuşkun olmasın, seni çöle döndürecek,Kimsenin yaşamadığı kentlere çevireceğim."));
trsuperobject.biblemap.put("b24_22_7", new trsuperobject(24,22,7,"Eli silahlı yok ediciler görevlendireceğim sana karşı.En iyi sedir ağaçlarını kesecek,Ateşe atacaklar."));
trsuperobject.biblemap.put("b24_22_8", new trsuperobject(24,22,8,"‹‹Bu kentten geçen birçok ulus birbirlerine, ‹RAB bu büyük kente neden bunu yaptı?› diye soracaklar."));
trsuperobject.biblemap.put("b24_22_9", new trsuperobject(24,22,9,"‹‹Yanıt şöyle olacak: ‹Çünkü Tanrıları RABbin antlaşmasını bıraktılar, başka ilahlara tapıp kulluk ettiler.››"));
trsuperobject.biblemap.put("b24_22_10", new trsuperobject(24,22,10,"Ölen için ağlamayın, yasa bürünmeyin;Ancak sürgüne giden için ağlayın acı acı.Çünkü bir daha dönmeyecek,Anayurdunu görmeyecek."));
trsuperobject.biblemap.put("b24_22_11", new trsuperobject(24,22,11,"Babası Yoşiyanın yerine Yahuda Kralı olan ve buradan çıkıp giden Yoşiya oğlu Şallumfö için RAB diyor ki, ‹‹Bir daha dönmeyecek buraya."));
trsuperobject.biblemap.put("b24_22_12", new trsuperobject(24,22,12,"Sürgüne gönderildiği yerde ölecek, bir daha bu ülkeyi görmeyecek.›› 30)."));
trsuperobject.biblemap.put("b24_22_13", new trsuperobject(24,22,13,"‹‹Sarayını haksızlıkla,Yukarı odalarını adaletsizlikle yapan,Komşusunu parasız çalıştıran,Ücretini ödemeyen adamın vay başına!"));
trsuperobject.biblemap.put("b24_22_14", new trsuperobject(24,22,14,"‹Kendim için yukarı odaları havadar,Geniş bir saray yapacağım› diyenin vay başına!Sarayına büyük pencereler açar,Sedir ağacıyla kaplar,Kırmızıya boyar."));
trsuperobject.biblemap.put("b24_22_15", new trsuperobject(24,22,15,"‹‹Bol bol sedir ağacı kullandın diyeKral mı oldun sanırsın?Baban doyasıya yiyip içti,Ama iyi ve doğru olanı yaptı;Onun için de işleri iyi gitti."));
trsuperobject.biblemap.put("b24_22_16", new trsuperobject(24,22,16,"Ezilenin, yoksulun davasını savundu,Onun için de işleri iyi gitti.Beni tanımak bu değil midir?›› diyor RAB."));
trsuperobject.biblemap.put("b24_22_17", new trsuperobject(24,22,17,"‹‹Seninse gözlerin de yüreğin de yalnız kazanca,Suçsuz kanı dökmeye,Baskı, zorbalık yapmaya yönelik.››"));
trsuperobject.biblemap.put("b24_22_18", new trsuperobject(24,22,18,"Bu yüzden RAB Yahuda Kralı Yoşiya oğlu Yehoyakim için diyor ki,  ‹‹Onun için kimse, ‹Ah kardeşim!Vah kızkardeşim!› diye dövünmeyecek.Onun için kimse, ‹Ah efendim!Vah onun görkemi!› diye dövünmeyecek."));
trsuperobject.biblemap.put("b24_22_19", new trsuperobject(24,22,19,"Sürüklenip Yeruşalim kapılarından dışarı atılacak,Eşek gömülür gibi gömülecek o.››"));
trsuperobject.biblemap.put("b24_22_20", new trsuperobject(24,22,20,"‹‹Lübnana git, feryat et,Sesin Başandan duyulsun,Haykır Avarimden,Çünkü bütün oynaşların ezildi."));
trsuperobject.biblemap.put("b24_22_21", new trsuperobject(24,22,21,"Kendini güvenlikte sandığında seni uyardım.Ama, ‹Dinlemem› dedin.Gençliğinden bu yana böyleydi tutumun,Sözümü hiç dinlemedin."));
trsuperobject.biblemap.put("b24_22_22", new trsuperobject(24,22,22,"Rüzgar bütün çobanlarını alıp götürecek,Oynaşların sürgüne gidecek.İşte o zaman yaptığın kötülükler yüzündenUtanacak, aşağılanacaksın."));
trsuperobject.biblemap.put("b24_22_23", new trsuperobject(24,22,23,"Ey sen, Lübnanda yaşayan,Yuvasını sedir ağacından kuran adam!Sana doğuran kadın gibi acılar, sancılar geldiğinde,Nasıl da inleyeceksin!›› sanılıyor. Masoretik metin ‹‹Acınacaksın››."));
trsuperobject.biblemap.put("b24_22_24", new trsuperobject(24,22,24,"‹‹Varlığım hakkı için derim ki›› diyor RAB, ‹‹Ey Yahuda Kralı Yehoyakim oğlu Yehoyakin, sağ elimdeki mühür yüzüğü olsan bile, çıkarıp atardım seni."));
trsuperobject.biblemap.put("b24_22_25", new trsuperobject(24,22,25,"Seni can düşmanlarının, korktuğun kişilerin, Babil Kralı Nebukadnessarla Kildanilerin eline teslim edeceğim."));
trsuperobject.biblemap.put("b24_22_26", new trsuperobject(24,22,26,"Seni de seni doğuran anneni de doğmadığınız bir ülkeye atacağım; orada öleceksiniz."));
trsuperobject.biblemap.put("b24_22_27", new trsuperobject(24,22,27,"Dönmeye can attığınız ülkeye bir daha dönemeyeceksiniz.››"));
trsuperobject.biblemap.put("b24_22_28", new trsuperobject(24,22,28,"Bu mu Yehoyakin? Bu hor görülmüş kırık çömlek,Kimsenin istemediği kap?Neden kendisi de çocukları daBilmedikleri bir ülkeye atıldılar?"));
trsuperobject.biblemap.put("b24_22_29", new trsuperobject(24,22,29,"Ülke, ey ülke,RABbin sözünü dinle, ey ülke!"));
trsuperobject.biblemap.put("b24_22_30", new trsuperobject(24,22,30,"RAB diyor ki,‹‹Bu adamı çocuksuz,Ömrünce başarısız biri olarak yazın.Çünkü soyundan gelen hiç kimse başarılı olmayacak,Soyundan gelen hiç kimseDavut'un tahtında oturamayacak,Yahuda'da bir daha krallık etmeyecek.››"));
trsuperobject.biblemap.put("b24_23_1", new trsuperobject(24,23,1,"‹‹Otlağımın koyunlarını yok edip dağıtan çobanların vay başına!›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_2", new trsuperobject(24,23,2,"Halkımı güden çobanlar için İsrailin Tanrısı RAB şöyle diyor: ‹‹Sürümü dağıtıp sürdünüz, onlarla ilgilenmediniz. Şimdi ben sizinle ilgileneceğim, yaptığınız kötülük yüzünden sizi cezalandıracağım.›› RAB böyle diyor."));
trsuperobject.biblemap.put("b24_23_3", new trsuperobject(24,23,3,"‹‹Sürmüş olduğum bütün ülkelerden sürümün sağ kalanlarını toplayıp otlaklarına geri getireceğim; orada verimli olup çoğalacaklar."));
trsuperobject.biblemap.put("b24_23_4", new trsuperobject(24,23,4,"Onları güdecek çobanlar koyacağım başlarına. Bundan böyle korkmayacak, yılgınlığa düşmeyecekler. Bir tanesi bile eksilmeyecek›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_5", new trsuperobject(24,23,5,"‹‹İşte Davut için doğru bir dalÇıkaracağım günler geliyor›› diyor RAB.‹‹Bu kral bilgece egemenlik sürecek,Ülkede adil ve doğru olanı yapacak."));
trsuperobject.biblemap.put("b24_23_6", new trsuperobject(24,23,6,"Onun döneminde Yahuda kurtulacak,İsrail güvenlik içinde yaşayacak.O, ‹Yahve sidkenufş› adıyla anılacak."));
trsuperobject.biblemap.put("b24_23_7", new trsuperobject(24,23,7,"‹‹Artık insanların, ‹İsrail halkını Mısırdan çıkaran RABbin varlığı hakkı için› demeyecekleri günler geliyor›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_8", new trsuperobject(24,23,8,"‹‹Bunun yerine, ‹İsrail soyunu kuzey ülkesinden ve sürdüğü bütün öbür ülkelerden geri getiren RABbin varlığı hakkı için› diyecekler. Böylece kendi topraklarında yaşayacaklar.››"));
trsuperobject.biblemap.put("b24_23_9", new trsuperobject(24,23,9,"Peygamberlere gelince,Yüreğim paramparça,Bütün kemiklerim titriyor.RABbin yüzünden,Onun kutsal sözleri yüzündenSarhoş gibi,Şaraba yenik düşen bir adam gibiyim."));
trsuperobject.biblemap.put("b24_23_10", new trsuperobject(24,23,10,"Çünkü ülke zina edenlerle dolu,Lanet yüzünden yas tutuyor.Otlaklar kurumuş.İzledikleri yol kötü,Güçlerini haksızca kullanıyorlar."));
trsuperobject.biblemap.put("b24_23_11", new trsuperobject(24,23,11,"‹‹Peygamber de kâhin de tanrısız;Tapınağımda bile kötülüklerini gördüm›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_12", new trsuperobject(24,23,12,"‹‹Bu yüzden izledikleri yolOnlar için kaygan olacak;Karanlığa sürülecek,Orada tökezleyip düşecekler.Çünkü cezalandırılacakları yılBaşlarına felaket getireceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_13", new trsuperobject(24,23,13,"‹‹Samiriye peygamberleri arasındaŞu iğrençliği gördüm:Baal adına peygamberlik ederekHalkım İsraili baştan çıkarıyorlar."));
trsuperobject.biblemap.put("b24_23_14", new trsuperobject(24,23,14,"Yeruşalim peygamberleri arasındaŞu korkunç şeyi gördüm:Zina ediyorlar, yalan peşindeler.Kötülük edenleri güçlendirdiklerinden,Kimse kötülüğünden dönmüyor.Benim için hepsi Sodom gibi,Yeruşalim halkı Gomora gibi oldu.››"));
trsuperobject.biblemap.put("b24_23_15", new trsuperobject(24,23,15,"Bu nedenle Her Şeye Egemen RAB peygamberler için şöyle diyor:  ‹‹Onlara pelinotu yedirecek,Zehirli su içireceğim.Çünkü Yeruşalim peygamberleriTanrısızlığın bütün ülkeye yayılmasına neden oldular.››"));
trsuperobject.biblemap.put("b24_23_16", new trsuperobject(24,23,16,"Her Şeye Egemen RAB diyor ki,‹‹Size peygamberlik eden peygamberlerinDediklerine kulak asmayın,Onlar sizi aldatıyor.RABbin ağzından çıkanları değil,Kendi hayal ettikleri görümleri anlatıyorlar."));
trsuperobject.biblemap.put("b24_23_17", new trsuperobject(24,23,17,"Beni küçümseyenlere sürekli,‹RAB diyor ki: Size esenlik olacak!› diyorlar.Yüreklerinin inatçılığı doğrultusunda davrananlara,‹Başınıza felaket gelmeyecek› diyorlar."));
trsuperobject.biblemap.put("b24_23_18", new trsuperobject(24,23,18,"RABbin sözünü duyup anlamak içinRABbin meclisinde kim bulundu ki?Onun sözüne kulak verip duyan kim?"));
trsuperobject.biblemap.put("b24_23_19", new trsuperobject(24,23,19,"İşte, RABbin fırtınası öfkeyle kopacak,Kasırgası döne döne kötülerin başına patlayacak."));
trsuperobject.biblemap.put("b24_23_20", new trsuperobject(24,23,20,"Aklının tasarladığını tümüyle yapana dekRABbin öfkesi dinmeyecek.Son günlerde açıkça anlayacaksınız bunu."));
trsuperobject.biblemap.put("b24_23_21", new trsuperobject(24,23,21,"Bu peygamberleri ben göndermedim,Ama çabucak ortaya çıktılar.Onlara hiç seslenmedim,Yine de peygamberlik ettiler."));
trsuperobject.biblemap.put("b24_23_22", new trsuperobject(24,23,22,"Ama meclisimde dursalardı,Sözlerimi halkıma bildirir,Onları kötü yollarından ve davranışlarındanDöndürürlerdi."));
trsuperobject.biblemap.put("b24_23_23", new trsuperobject(24,23,23,"Ben yalnızca yakındaki Tanrı mıyım?Uzaktaki Tanrı da değil miyim?›› diyor RAB,"));
trsuperobject.biblemap.put("b24_23_24", new trsuperobject(24,23,24,"‹‹Kim gizli yere saklanır daOnu görmem?›› diyor RAB,‹‹Yeri göğü doldurmuyor muyum?›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_25", new trsuperobject(24,23,25,"‹‹Adımla yalancı peygamberlik edenlerin ne dediklerini duydum. ‹Bir düş gördüm! Bir düş!› diyorlar."));
trsuperobject.biblemap.put("b24_23_26", new trsuperobject(24,23,26,"Kafalarından uydurdukları hileleri aktaran bu yalancı peygamberler ne zamana dek sürdürecekler bunu?"));
trsuperobject.biblemap.put("b24_23_27", new trsuperobject(24,23,27,"Ataları nasıl Baal yüzünden adımı unuttuysa, onlar da birbirlerine düşlerini anlatarak halkıma adımı unutturmayı tasarlıyorlar."));
trsuperobject.biblemap.put("b24_23_28", new trsuperobject(24,23,28,"Düşü olan peygamber düşünü anlatsın; ama sözümü alan onu sadakatle bildirsin. Buğdayın yanında saman nedir ki?›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_29", new trsuperobject(24,23,29,"‹‹Benim sözüm ateş gibi değil mi? Kayaları paramparça eden balyoz gibi değil mi?›› RAB böyle diyor."));
trsuperobject.biblemap.put("b24_23_30", new trsuperobject(24,23,30,"‹‹İşte bunun için sözlerimi birbirlerinden çalan peygamberlere karşıyım›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_31", new trsuperobject(24,23,31,"‹‹Evet, kendi sözlerini söyleyip, ‹RAB böyle diyor› diyen peygamberlere karşıyım›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_32", new trsuperobject(24,23,32,"‹‹Uydurma düşler gören peygamberlere karşıyım›› diyor RAB. ‹‹Bu düşleri anlatıyor, yalanlarla, boş övünmelerle halkımı baştan çıkarıyorlar. Ben onları ne gönderdim, ne de atadım. Bu halka hiç mi hiç yararları yok›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_33", new trsuperobject(24,23,33,"‹‹Halktan biri, bir peygamber ya da kâhin, ‹RABbin bildirisi nedir?› diye sorarsa, ‹Ne bildirisi?› diye karşılık vereceksin. Sizi başımdan atacağım›› diyor RAB."));
trsuperobject.biblemap.put("b24_23_34", new trsuperobject(24,23,34,"‹‹Eğer bir peygamber, kâhin ya da başka biri, ‹Bu RABbin bildirisidir› derse, onu da ailesini de cezalandıracağım."));
trsuperobject.biblemap.put("b24_23_35", new trsuperobject(24,23,35,"Her biriniz komşunuza ve kardeşinize, ‹RAB ne yanıt verdi?› ya da, ‹RAB ne söyledi?› demelisiniz."));
trsuperobject.biblemap.put("b24_23_36", new trsuperobject(24,23,36,"Bundan böyle, ‹RABbin bildirisi› lafını ağzınıza almayacaksınız. Herkesin sözü kendi bildirisi olacak. Yaşayan Tanrının, Her Şeye Egemen RABbin, Tanrımızın sözlerini çarpıtıyorsunuz siz."));
trsuperobject.biblemap.put("b24_23_37", new trsuperobject(24,23,37,"Bir peygambere, ‹RAB sana ne yanıt verdi?› ya da, ‹RAB ne söyledi?› demelisiniz."));
trsuperobject.biblemap.put("b24_23_38", new trsuperobject(24,23,38,"Ama, ‹RABbin bildirisidir› derseniz, RAB diyor ki, ‹RABbin bildirisidir› diyorsunuz. Oysa, ‹RABbin bildirisidir› demeyeceksiniz diye sizi uyarmıştım."));
trsuperobject.biblemap.put("b24_23_39", new trsuperobject(24,23,39,"Bu yüzden sizi büsbütün unutacağım, sizi de size ve atalarınıza verdiğim kenti de önümden söküp atacağım."));
trsuperobject.biblemap.put("b24_23_40", new trsuperobject(24,23,40,"Sizi hiç unutulmayacak bir utanca düşürecek, sürekli alay konusu edeceğim.››"));
trsuperobject.biblemap.put("b24_24_1", new trsuperobject(24,24,1,"Babil Kralı Nebukadnessar Yahuda Kralı Yehoyakim oğlu Yehoyakinle Yahuda önderlerini, zanaatçıları, demircileri Yeruşalimden Babile sürdükten sonra, RAB bana tapınağının önüne konmuş iki sepet incir gösterdi."));
trsuperobject.biblemap.put("b24_24_2", new trsuperobject(24,24,2,"Sepetlerin birinde ilk ürüne benzer çok iyi incirler vardı; ötekindeyse çok kötü, yenmeyecek kadar çürük incirler vardı."));
trsuperobject.biblemap.put("b24_24_4", new trsuperobject(24,24,4,"Bunun üzerine RAB bana şöyle seslendi:"));
trsuperobject.biblemap.put("b24_24_5", new trsuperobject(24,24,5,"‹‹İsrailin Tanrısı RAB diyor ki, ‹Buradan Kildan ülkesine sürgüne gönderdiğim Yahuda sürgünlerini bu iyi incirler gibi iyi sayacağım."));
trsuperobject.biblemap.put("b24_24_6", new trsuperobject(24,24,6,"İyilik bulmaları için onları gözetecek, bu ülkeye geri getireceğim. Onları bina edeceğim, yıkmayacağım; onları dikeceğim, kökünden sökmeyeceğim."));
trsuperobject.biblemap.put("b24_24_7", new trsuperobject(24,24,7,"Benim RAB olduğumu anlayacak bir yürek vereceğim onlara. Onlar benim halkım olacaklar, ben de onların Tanrısı olacağım. Çünkü bütün yürekleriyle bana dönecekler."));
trsuperobject.biblemap.put("b24_24_8", new trsuperobject(24,24,8,"‹‹Ama Yahuda Kralı Sidkiyayla önderlerini, Yeruşalimden sağ çıkıp da bu ülkede ya da Mısırda yaşayanları yenmeyecek kadar çürük incir gibi yapacağım› diyor RAB,"));
trsuperobject.biblemap.put("b24_24_9", new trsuperobject(24,24,9,"‹Onları bütün ülkelerin gözünde iğrenç, korkunç bir duruma düşüreceğim. Onları sürdüğüm her yerde ayıplanacak, ibret olacak, alaya alınacak, lanetlenecekler."));
trsuperobject.biblemap.put("b24_24_10", new trsuperobject(24,24,10,"Kendilerine ve atalarına verdiğim topraktan yok olana dek üzerlerine kılıç, kıtlık, salgın hastalık salacağım.››"));
trsuperobject.biblemap.put("b24_25_1", new trsuperobject(24,25,1,"Yahuda Kralı Yoşiya oğlu Yehoyakimin döneminin dördüncü yılında, RAB Yahuda halkıyla ilgili olarak Yeremyaya seslendi. Nebukadnessarın Babil Kralı oluşunun birinci yılıydı bu."));
trsuperobject.biblemap.put("b24_25_2", new trsuperobject(24,25,2,"Peygamber Yeremya Yahuda halkına ve Yeruşalimde yaşayanlara RABbin sözünü aktararak şöyle dedi:"));
trsuperobject.biblemap.put("b24_25_3", new trsuperobject(24,25,3,"Yirmi üç yıldır, Yahuda Kralı Amon oğlu Yoşiyanın döneminin on üçüncü yılından bugüne dek, RAB bana sesleniyor. Ben de RABbin sözünü defalarca size aktardım, ama dinlemediniz."));
trsuperobject.biblemap.put("b24_25_5", new trsuperobject(24,25,5,"Sizi uyardılar, ‹‹Şimdi herkes kötü yolundan, kötü işlerinden dönsün ki, RABbin sonsuza dek size ve atalarınıza verdiği toprakta yaşayasınız›› dediler,"));
trsuperobject.biblemap.put("b24_25_6", new trsuperobject(24,25,6,"‹‹Kulluk etmek, tapınmak için başka ilahların ardınca gitmeyin; elinizle yaptığınız putlarla beni öfkelendirmeyin ki, ben de size zarar vermeyeyim.››"));
trsuperobject.biblemap.put("b24_25_7", new trsuperobject(24,25,7,"‹‹Ama beni dinlemediniz›› diyor RAB, ‹‹Sonuç olarak elinizle yaptığınız putlarla beni öfkelendirip kendinizi zarara soktunuz.››"));
trsuperobject.biblemap.put("b24_25_8", new trsuperobject(24,25,8,"Bunun için Her Şeye Egemen RAB diyor ki, ‹‹Madem sözlerimi dinlemediniz,"));
trsuperobject.biblemap.put("b24_25_9", new trsuperobject(24,25,9,"ben de bütün kuzeydeki halkları ve kulum Babil Kralı Nebukadnessarı çağırtacağım›› diyor RAB, ‹‹Onları bu ülkeye de burada yaşayanlarla çevresindeki bütün uluslara da karşı getireceğim. Bu halkı tamamen yok edeceğim, ülkelerini dehşet ve alay konusu edip sonsuz bir viraneye çevireceğim."));
trsuperobject.biblemap.put("b24_25_10", new trsuperobject(24,25,10,"Sevinç ve neşe sesini, gelin güvey sesini, değirmen taşlarının sesini, kandil ışığını onlardan uzaklaştıracağım."));
trsuperobject.biblemap.put("b24_25_11", new trsuperobject(24,25,11,"Bütün ülke bir virane, dehşet verici bir yer olacak. Bu uluslar Babil Kralına yetmiş yıl kulluk edecekler."));
trsuperobject.biblemap.put("b24_25_12", new trsuperobject(24,25,12,"‹‹Ama yetmiş yıl dolunca›› diyor RAB, ‹‹Suçları yüzünden Babil Kralıyla ulusunu, Kildan ülkesini cezalandıracak, sonsuz bir viranelik haline getireceğim."));
trsuperobject.biblemap.put("b24_25_13", new trsuperobject(24,25,13,"O ülke için söylediklerimin hepsini, Yeremyanın uluslara ettiği bu kitapta yazılı bütün peygamberlik sözlerini ülkenin başına getireceğim."));
trsuperobject.biblemap.put("b24_25_14", new trsuperobject(24,25,14,"Pek çok ulus, büyük krallar onları köle edinecek. Yaptıklarına ve ellerinden çıkan işe göre karşılık vereceğim onlara.››"));
trsuperobject.biblemap.put("b24_25_15", new trsuperobject(24,25,15,"İsrailin Tanrısı RAB bana şöyle dedi: ‹‹Elimdeki öfke şarabıyla dolu kâseyi al, seni göndereceğim bütün uluslara içir."));
trsuperobject.biblemap.put("b24_25_16", new trsuperobject(24,25,16,"Şarabı içince sendeleyecek, üzerlerine göndereceğim kılıç yüzünden çıldıracaklar.››"));
trsuperobject.biblemap.put("b24_25_17", new trsuperobject(24,25,17,"Böylece kâseyi RABbin elinden alıp beni gönderdiği bütün uluslara içirdim:"));
trsuperobject.biblemap.put("b24_25_18", new trsuperobject(24,25,18,"Bugün olduğu gibi viranelik, dehşet ve alay konusu, lanetlik olsunlar diye Yeruşalime, Yahuda kentlerine, krallarıyla önderlerine;"));
trsuperobject.biblemap.put("b24_25_19", new trsuperobject(24,25,19,"Firavunla görevlilerine, önderlerine ve halkına,"));
trsuperobject.biblemap.put("b24_25_20", new trsuperobject(24,25,20,"Mısırda yaşayan bütün yabancılara; Ûs krallarına, Filist krallarına -Aşkelona, Gazzeye, Ekrona, Aşdottan sağ kalanlara-"));
trsuperobject.biblemap.put("b24_25_21", new trsuperobject(24,25,21,"Edoma, Moava, Ammona;"));
trsuperobject.biblemap.put("b24_25_22", new trsuperobject(24,25,22,"bütün Sur ve Sayda krallarına, deniz aşırı ülkelerin krallarına;"));
trsuperobject.biblemap.put("b24_25_23", new trsuperobject(24,25,23,"Dedana, Temaya, Bûza, zülüflerini kesen bütün halklara;"));
trsuperobject.biblemap.put("b24_25_24", new trsuperobject(24,25,24,"Arabistan krallarına, çölde yaşayan yabancı halkın krallarına;"));
trsuperobject.biblemap.put("b24_25_25", new trsuperobject(24,25,25,"Zimri, Elam, Med krallarına;"));
trsuperobject.biblemap.put("b24_25_26", new trsuperobject(24,25,26,"sırasıyla uzak yakın bütün kuzey krallarına, yeryüzündeki bütün ulusların krallarına içirdim. Hepsinden sonra Şeşak Kralı da içecektir."));
trsuperobject.biblemap.put("b24_25_27", new trsuperobject(24,25,27,"‹‹Sonra onlara de ki, ‹İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: Üzerinize salacağım kılıç yüzünden sarhoş olana dek için, kusun, düşüp kalkmayın.›"));
trsuperobject.biblemap.put("b24_25_28", new trsuperobject(24,25,28,"Eğer kâseyi elinden alır, içmek istemezlerse, de ki, ‹Her Şeye Egemen RAB şöyle diyor: Kesinlikle içeceksiniz!"));
trsuperobject.biblemap.put("b24_25_29", new trsuperobject(24,25,29,"Bana ait olan kentin üzerine felaket getirmeye başlıyorum. Cezasız kalacağınızı mı sanıyorsunuz? Sizi cezasız bırakmayacağım. İşte dünyada yaşayan herkesin üzerine kılıcı çağırıyorum. Her Şeye Egemen RAB böyle diyor.›"));
trsuperobject.biblemap.put("b24_25_30", new trsuperobject(24,25,30,"‹‹Bütün bu peygamberlik sözlerini onlara ilet:  ‹‹Yükseklerden kükreyecek RAB,Kutsal konutundan gürleyecek,Ağılına şiddetle kükreyecek.Dünyada yaşayanların tümüneÜzüm ezenler gibi bağıracak."));
trsuperobject.biblemap.put("b24_25_31", new trsuperobject(24,25,31,"Gürültü yeryüzünün dört yanında yankılanacak.Çünkü RAB uluslara dava açacak;Herkesi yargılayacakVe kötüleri kılıca teslim edecek›› diyor RAB."));
trsuperobject.biblemap.put("b24_25_32", new trsuperobject(24,25,32,"Her Şeye Egemen RAB diyor ki,‹‹İşte felaket ulustan ulusa yayılıyor!Dünyanın dört bucağından büyük kasırga kopuyor.››"));
trsuperobject.biblemap.put("b24_25_33", new trsuperobject(24,25,33,"O gün RAB dünyayı bir uçtan bir uca öldürülenlerle dolduracak. Onlar için yas tutulmayacak, toplanıp gömülmeyecekler. Toprağın üzerinde gübre gibi kalacaklar."));
trsuperobject.biblemap.put("b24_25_34", new trsuperobject(24,25,34,"Haykırın, ey çobanlar,Acı acı bağırın!Toprakta yuvarlanın, ey sürü başları!Çünkü boğazlanma zamanınız doldu,Değerli bir kap gibi düşüp parçalanacaksınız."));
trsuperobject.biblemap.put("b24_25_35", new trsuperobject(24,25,35,"Çobanlar kaçamayacak,Sürü başları kurtulamayacak!"));
trsuperobject.biblemap.put("b24_25_36", new trsuperobject(24,25,36,"Duy çobanların haykırışını,Sürü başlarının bağırışını!RAB onların otlaklarını yok ediyor."));
trsuperobject.biblemap.put("b24_25_37", new trsuperobject(24,25,37,"RABbin kızgın öfkesi yüzündenGüvenlikte oldukları ağıllar yok oldu."));
trsuperobject.biblemap.put("b24_25_38", new trsuperobject(24,25,38,"İnini terk eden genç aslan gibi,RAB inini bıraktı.Zorbanın kılıcıVe RAB'bin kızgın öfkesi yüzündenÜlkeleri viraneye döndü."));
trsuperobject.biblemap.put("b24_26_1", new trsuperobject(24,26,1,"Yahuda Kralı Yoşiya oğlu Yehoyakimin krallığının başlangıcında RAB şöyle seslendi:"));
trsuperobject.biblemap.put("b24_26_2", new trsuperobject(24,26,2,"‹‹RAB diyor ki, RABbin Tapınağının avlusunda dur, tapınmak için Yahuda kentlerinden oraya gelen herkese seslen. Sana buyurduğum her şeyi tek söz eksiltmeden onlara bildir."));
trsuperobject.biblemap.put("b24_26_3", new trsuperobject(24,26,3,"Belki dinler de kötü yollarından dönerler. O zaman ben de yaptıkları kötülükler yüzünden başlarına getirmeyi tasarladığım felaketten vazgeçerim."));
trsuperobject.biblemap.put("b24_26_4", new trsuperobject(24,26,4,"Onlara de ki, ‹RAB şöyle diyor: Size verdiğim yasa uyarınca yürümez, beni dinlemez,"));
trsuperobject.biblemap.put("b24_26_5", new trsuperobject(24,26,5,"size defalarca gönderdiğim kullarım peygamberlerin sözlerine kulak vermezseniz, ki kulak vermiyorsunuz,"));
trsuperobject.biblemap.put("b24_26_6", new trsuperobject(24,26,6,"bu tapınağa Şilodakine yaptığımın aynısını yapar, bu kenti bütün dünya ulusları arasında lanetlik ederim.››"));
trsuperobject.biblemap.put("b24_26_7", new trsuperobject(24,26,7,"Kâhinler, peygamberler ve bütün halk Yeremyanın RABbin Tapınağında söylediği bu sözleri duydular."));
trsuperobject.biblemap.put("b24_26_8", new trsuperobject(24,26,8,"Yeremya Tanrının halka iletmesini buyurduğu sözleri bitirince, kâhinlerle peygamberler ve halk onu yakalayıp, ‹‹Ölmen gerek!›› dediler,"));
trsuperobject.biblemap.put("b24_26_9", new trsuperobject(24,26,9,"‹‹Neden bu tapınak Şilodaki gibi olacak, bu kent de içinde kimsenin yaşamayacağı bir viraneye dönecek diyerek RABbin adıyla peygamberlik ediyorsun?›› Bütün halk RABbin Tapınağında Yeremyanın çevresinde toplanmıştı."));
trsuperobject.biblemap.put("b24_26_10", new trsuperobject(24,26,10,"Yahuda önderleri olup bitenleri duyunca, saraydan RABbin Tapınağına gidip tapınağın Yeni Kapı girişinde yerlerini aldılar."));
trsuperobject.biblemap.put("b24_26_11", new trsuperobject(24,26,11,"Bunun üzerine kâhinlerle peygamberler, önderlere ve halka, ‹‹Bu adam ölüm cezasına çarptırılmalı›› dediler, ‹‹Çünkü bu kente karşı peygamberlik etti. Kendi kulaklarınızla işittiniz bunu.››"));
trsuperobject.biblemap.put("b24_26_12", new trsuperobject(24,26,12,"Bunun üzerine Yeremya önderlerle halka, ‹‹Bu tapınağa ve kente karşı işittiğiniz peygamberlik sözlerini iletmem için beni RAB gönderdi›› dedi,"));
trsuperobject.biblemap.put("b24_26_13", new trsuperobject(24,26,13,"‹‹Şimdi yollarınızı, davranışlarınızı düzeltin, Tanrınız RABbin sözüne kulak verin. O zaman RAB başınıza getireceğini söylediği felaketten vazgeçecek."));
trsuperobject.biblemap.put("b24_26_14", new trsuperobject(24,26,14,"Bana gelince, işte elinizdeyim! Gözünüzde iyi ve doğru olan neyse, bana öyle yapın."));
trsuperobject.biblemap.put("b24_26_15", new trsuperobject(24,26,15,"Ancak şunu kesinlikle bilin ki, eğer beni öldürürseniz, siz de bu kent ve içinde yaşayanlar da suçsuz birinin kanını dökmekten sorumlu tutulacaksınız. Çünkü bütün bu sözleri bildirmem için beni gerçekten RAB size gönderdi.››"));
trsuperobject.biblemap.put("b24_26_16", new trsuperobject(24,26,16,"Bunun üzerine önderlerle halk, kâhinlerle peygamberlere, ‹‹Bu adam ölüm cezasına çarptırılmamalı›› dediler, ‹‹Çünkü bizimle Tanrımız RABbin adına konuştu.››"));
trsuperobject.biblemap.put("b24_26_17", new trsuperobject(24,26,17,"Halkın ileri gelenlerinden birkaçı öne çıkıp orada toplanmış halka,"));
trsuperobject.biblemap.put("b24_26_18", new trsuperobject(24,26,18,"‹‹Moreşetli Mika Yahuda Kralı Hizkiya döneminde peygamberlik etti›› dediler, ‹‹Yahuda halkına dedi ki, Her Şeye Egemen RAB şöyle diyor,  ‹‹Siyon tarla gibi sürülecek,Taş yığınına dönecek Yeruşalim,Tapınağın kurulduğu dağÇalılarla kaplanacak.›"));
trsuperobject.biblemap.put("b24_26_19", new trsuperobject(24,26,19,"‹‹Yahuda Kralı Hizkiya ya da Yahuda halkından biri onu öldürdü mü? Bunun yerine Hizkiya RABden korkarak Onun lütfunu diledi. RAB de onlara bildirdiği felaketten vazgeçti. Bizse, üzerimize büyük bir yıkım getirmek üzereyiz.››"));
trsuperobject.biblemap.put("b24_26_20", new trsuperobject(24,26,20,"Kiryat-Yearimli Şemaya oğlu Uriya adında peygamberlik eden bir adam daha vardı. Tıpkı Yeremya gibi o da RABbin adına bu kente ve ülkeye karşı peygamberlik etti."));
trsuperobject.biblemap.put("b24_26_21", new trsuperobject(24,26,21,"Kral Yehoyakimle askerleri ve komutanları Uriyanın sözlerini duydular. Kral onu öldürmek istedi. Bunu duyan Uriya korkuya kapılarak kaçıp Mısıra gitti."));
trsuperobject.biblemap.put("b24_26_22", new trsuperobject(24,26,22,"Bunun üzerine Kral Yehoyakim peşinden adamlarını -Akbor oğlu Elnatanla başkalarını- Mısıra gönderdi."));
trsuperobject.biblemap.put("b24_26_23", new trsuperobject(24,26,23,"Uriyayı Mısırdan çıkarıp Kral Yehoyakime getirdiler. Kral onu kılıçla öldürtüp cesedini sıradan halk mezarlığına attırdı."));
trsuperobject.biblemap.put("b24_26_24", new trsuperobject(24,26,24,"Ancak Şafan oğlu Ahikam Yeremya'yı korudu. Böylece Yeremya öldürülmek üzere halkın eline teslim edilmedi."));
trsuperobject.biblemap.put("b24_27_1", new trsuperobject(24,27,1,"Yahuda Kralı Yoşiya oğlu Sidkiyanın krallığının başlangıcında RAB Yeremyaya seslendi:"));
trsuperobject.biblemap.put("b24_27_3", new trsuperobject(24,27,3,"Sonra Yeruşalime, Yahuda Kralı Sidkiyaya gelen ulaklar aracılığıyla Edom, Moav, Ammon, Sur ve Sayda krallarına haber gönder."));
trsuperobject.biblemap.put("b24_27_4", new trsuperobject(24,27,4,"Efendilerine şunu bildirmelerini buyur: İsrailin Tanrısı Her Şeye Egemen RAB diyor ki, ‹Efendilerinize söyleyin:"));
trsuperobject.biblemap.put("b24_27_5", new trsuperobject(24,27,5,"Yeryüzünü de üstünde yaşayan insanlarla hayvanları da büyük gücümle, kudretli elimle ben yarattım. Onu uygun gördüğüm kişiye veririm."));
trsuperobject.biblemap.put("b24_27_6", new trsuperobject(24,27,6,"Şimdi bütün bu ülkeleri Babil Kralı kulum Nebukadnessara vereceğim. Yabanıl hayvanları da kulluk etsinler diye ona vereceğim."));
trsuperobject.biblemap.put("b24_27_7", new trsuperobject(24,27,7,"Ülkesi için saptanan zaman gelinceye dek bütün uluslar ona, oğluna, torununa kulluk edecek. Sonra birçok ulus, büyük krallar onu köle edecekler."));
trsuperobject.biblemap.put("b24_27_8", new trsuperobject(24,27,8,"‹‹Hangi ulus ya da krallık Babil Kralı Nebukadnessara kulluk edip boyunduruğuna girmezse, o ulusu onun eline teslim edene dekfü kılıçla, kıtlıkla, salgın hastalıkla cezalandıracağım, diyor RAB."));
trsuperobject.biblemap.put("b24_27_9", new trsuperobject(24,27,9,"Size gelince, peygamberlerinizi, falcılarınızı, düş görenlerinizi, medyumlarınızı, büyücülerinizi dinlemeyin! Onlar size, Babil Kralına kulluk etmeyeceksiniz diyorlar."));
trsuperobject.biblemap.put("b24_27_10", new trsuperobject(24,27,10,"Size yalan peygamberlik ediyorlar. Bunun sonucu sizi ülkenizden uzaklaştırmak oluyor. Sizi süreceğim, yok olacaksınız."));
trsuperobject.biblemap.put("b24_27_11", new trsuperobject(24,27,11,"Ama Babil Kralının boyunduruğuna girip ona kulluk eden ulusu kendi toprağında bırakacağım, diyor RAB. O ulus toprağını işleyecek, orada yaşayacak.›› Masoretik metin ‹‹Onun eliyle yok edilişi tamamlanana dek››."));
trsuperobject.biblemap.put("b24_27_12", new trsuperobject(24,27,12,"Yahuda Kralı Sidkiyaya bütün bu sözleri ilettim. Dedim ki, ‹‹Boyunlarınızı Babil Kralının boyunduruğu altına koyun. Ona ve halkına kulluk edin ki sağ kalasınız."));
trsuperobject.biblemap.put("b24_27_13", new trsuperobject(24,27,13,"RABbin Babil Kralına kulluk etmeyen her ulus için dediği gibi, niçin sen ve halkın kılıç, kıtlık, salgın hastalık yüzünden ölesiniz?"));
trsuperobject.biblemap.put("b24_27_14", new trsuperobject(24,27,14,"Size, ‹Babil Kralına kulluk etmeyeceksiniz› diyen peygamberlerin sözlerine kulak asmayın. Onlar size yalan peygamberlik ediyorlar."));
trsuperobject.biblemap.put("b24_27_15", new trsuperobject(24,27,15,"‹Onları ben göndermedim› diyor RAB, ‹Adımla yalan peygamberlik ediyorlar. Bu yüzden sizi de size peygamberlik eden peygamberleri de süreceğim, hepiniz yok olacaksınız.››"));
trsuperobject.biblemap.put("b24_27_16", new trsuperobject(24,27,16,"Sonra kâhinlerle halka şöyle dedim: ‹‹RAB diyor ki, ‹İşte RABbin Tapınağının eşyaları yakında Babilden geri getirilecek› diyen peygamberlerinize kulak asmayın. Onlar size yalan peygamberlik ediyorlar."));
trsuperobject.biblemap.put("b24_27_17", new trsuperobject(24,27,17,"Onları dinlemeyin. Sağ kalmak için Babil Kralına kulluk edin. Bu kent neden viraneye çevrilsin?"));
trsuperobject.biblemap.put("b24_27_18", new trsuperobject(24,27,18,"Eğer bunlar peygamberse ve RABbin sözü onlardaysa, RABbin Tapınağında, Yahuda Kralının sarayında ve Yeruşalimde kalan eşyalar Babile götürülmesin diye Her Şeye Egemen RABbe yalvarsınlar."));
trsuperobject.biblemap.put("b24_27_19-20", new trsuperobject(24,27,19-20,"‹‹Çünkü Babil Kralı Nebukadnessar Yahuda Kralı Yehoyakim oğlu Yehoyakini Yahuda ve Yeruşalim soylularıyla birlikte Yeruşalimden Babile sürdüğünde alıp götürmediği sütunlar, havuz, ayaklıklar ve kentte kalan öbür eşyalar için Her Şeye Egemen RAB şöyle diyor."));
trsuperobject.biblemap.put("b24_27_21", new trsuperobject(24,27,21,"Evet, RABbin Tapınağında, Yahuda Kralının sarayında ve Yeruşalimde kalan eşyalar için İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor:"));
trsuperobject.biblemap.put("b24_27_22", new trsuperobject(24,27,22,"‹Bunlar Babil'e taşınacak, aldıracağım güne dek de orada kalacaklar› diyor RAB, ‹O zaman onları alacak, yeniden buraya yerleştireceğim.››"));
trsuperobject.biblemap.put("b24_28_1", new trsuperobject(24,28,1,"Aynı yıl Yahuda Kralı Sidkiyanın krallığının başlangıcında, dördüncü yılının beşinci ayında Givonlu Azzur oğlu Peygamber Hananya RABbin Tapınağında kâhinlerle bütün halkın önünde bana şöyle dedi:"));
trsuperobject.biblemap.put("b24_28_2", new trsuperobject(24,28,2,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Babil Kralının boyunduruğunu kıracağım."));
trsuperobject.biblemap.put("b24_28_3", new trsuperobject(24,28,3,"Babil Kralı Nebukadnessarın buradan alıp Babile götürdüğü RABbin Tapınağına ait bütün eşyaları iki yıl içinde buraya geri getireceğim."));
trsuperobject.biblemap.put("b24_28_4", new trsuperobject(24,28,4,"Yahuda Kralı Yehoyakim oğlu Yehoyakinle Babile sürgüne giden bütün Yahudalıları buraya geri getireceğim› diyor RAB, ‹Çünkü Babil Kralının boyunduruğunu kıracağım.››"));
trsuperobject.biblemap.put("b24_28_5", new trsuperobject(24,28,5,"Bunun üzerine Peygamber Yeremya, kâhinlerin ve RABbin Tapınağındaki halkın önünde Peygamber Hananyayı yanıtladı."));
trsuperobject.biblemap.put("b24_28_6", new trsuperobject(24,28,6,"Yeremya şöyle dedi: ‹‹Amin! RAB aynısını yapsın! RAB, tapınağına ait eşyalarla bütün sürgünleri Babilden buraya geri getirerek ettiğin peygamberlik sözlerini gerçekleştirsin!"));
trsuperobject.biblemap.put("b24_28_7", new trsuperobject(24,28,7,"Yalnız şimdi sana ve halka söyleyeceğim şu sözü dinle:"));
trsuperobject.biblemap.put("b24_28_8", new trsuperobject(24,28,8,"Çok önce, benden de senden de önce yaşamış peygamberler birçok ülke ve büyük krallığın başına savaş, felaket, salgın hastalık gelecek diye peygamberlik ettiler."));
trsuperobject.biblemap.put("b24_28_9", new trsuperobject(24,28,9,"Ancak esenlik olacağını söyleyen peygamberin sözü yerine gelirse, onun gerçekten RABbin gönderdiği peygamber olduğu anlaşılır.››"));
trsuperobject.biblemap.put("b24_28_10", new trsuperobject(24,28,10,"Bunun üzerine Peygamber Hananya, Peygamber Yeremyanın boynundan boyunduruğu çıkarıp kırdı."));
trsuperobject.biblemap.put("b24_28_11", new trsuperobject(24,28,11,"Sonra halkın önünde şöyle dedi: ‹‹RAB diyor ki, ‹Babil Kralı Nebukadnessarın bütün ulusların boynuna taktığı boyunduruğu iki yıl içinde işte böyle kıracağım.›› Böylece Peygamber Yeremya yoluna gitti."));
trsuperobject.biblemap.put("b24_28_12", new trsuperobject(24,28,12,"Peygamber Hananya Peygamber Yeremyanın boynundaki boyunduruğu kırdıktan sonra RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_28_13", new trsuperobject(24,28,13,"‹‹Git, Hananyaya de ki, ‹RAB şöyle diyor: Sen tahtadan yapılmış boyunduruğu kırdın, ama yerine demir boyunduruk yapacaksın!"));
trsuperobject.biblemap.put("b24_28_14", new trsuperobject(24,28,14,"Çünkü İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, Babil Kralı Nebukadnessara kulluk etmeleri için bütün bu ulusların boynuna demir boyunduruk geçirdim, ona kulluk edecekler. Yabanıl hayvanları da onun denetimine vereceğim.››"));
trsuperobject.biblemap.put("b24_28_15", new trsuperobject(24,28,15,"Peygamber Yeremya Peygamber Hananyaya, ‹‹Dinle, ey Hananya!›› dedi, ‹‹Seni RAB göndermedi. Ama sen bu ulusu yalana inandırdın."));
trsuperobject.biblemap.put("b24_28_16", new trsuperobject(24,28,16,"Bu nedenle RAB diyor ki, ‹Seni yeryüzünden silip atacağım. Bu yıl öleceksin. Çünkü halkı RABbe karşı kışkırttın.››"));
trsuperobject.biblemap.put("b24_28_17", new trsuperobject(24,28,17,"Peygamber Hananya o yılın yedinci ayında öldü."));
trsuperobject.biblemap.put("b24_29_1", new trsuperobject(24,29,1,"Peygamber Yeremyanın sürgünde sağ kalan halkın ileri gelenlerine, kâhinlerle peygamberlere ve Nebukadnessarın Yeruşalimden Babile sürdüğü bütün halka Yeruşalimden gönderdiği mektubun metni aşağıda yazılıdır."));
trsuperobject.biblemap.put("b24_29_2", new trsuperobject(24,29,2,"Bu mektup Kral Yehoyakinin, ana kraliçenin, saray görevlilerinin, Yahuda ve Yeruşalim önderlerinin, zanaatçılarla demircilerin Yeruşalimden sürgüne gitmelerinden sonra,"));
trsuperobject.biblemap.put("b24_29_3", new trsuperobject(24,29,3,"Yahuda Kralı Sidkiyanın Babil Kralı Nebukadnessara gönderdiği Şafan oğlu Elasa ve Hilkiya oğlu Gemarya eliyle gönderildi:"));
trsuperobject.biblemap.put("b24_29_4", new trsuperobject(24,29,4,"İsrailin Tanrısı, Her Şeye Egemen RAB Yeruşalimden Babile sürdüğü herkese şöyle diyor:"));
trsuperobject.biblemap.put("b24_29_5", new trsuperobject(24,29,5,"‹‹Evler yapıp içinde oturun, bahçe dikip ürününü yiyin."));
trsuperobject.biblemap.put("b24_29_6", new trsuperobject(24,29,6,"Evlenin, oğullarınız, kızlarınız olsun; oğullarınızı, kızlarınızı evlendirin. Onların da oğulları, kızları olsun. Orada çoğalın, azalmayın."));
trsuperobject.biblemap.put("b24_29_7", new trsuperobject(24,29,7,"Sizi sürmüş olduğum kentin esenliği için uğraşın. O kent için RABbe dua edin. Çünkü esenliğiniz onunkine bağlıdır.››"));
trsuperobject.biblemap.put("b24_29_8", new trsuperobject(24,29,8,"Evet, İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹‹Aranızdaki peygamberlerle falcılara aldanmayın. Düş görmeye özendirdiğiniz kişilere kulak asmayın."));
trsuperobject.biblemap.put("b24_29_9", new trsuperobject(24,29,9,"Çünkü onlar adımı kullanarak size yalan peygamberlik ediyorlar. Onları ben göndermedim.›› RAB böyle diyor."));
trsuperobject.biblemap.put("b24_29_10", new trsuperobject(24,29,10,"RAB diyor ki, ‹‹Babilde yetmiş yılınız dolunca sizinle ilgilenecek, buraya sizi geri getirmek için verdiğim iyi sözü tutacağım."));
trsuperobject.biblemap.put("b24_29_11", new trsuperobject(24,29,11,"Çünkü sizin için düşündüğüm tasarıları biliyorum›› diyor RAB. ‹‹Kötü tasarılar değil, size umutlu bir gelecek sağlayan esenlik tasarıları bunlar."));
trsuperobject.biblemap.put("b24_29_12", new trsuperobject(24,29,12,"O zaman beni çağıracak, gelip bana yakaracaksınız. Ben de sizi işiteceğim."));
trsuperobject.biblemap.put("b24_29_13", new trsuperobject(24,29,13,"Beni arayacaksınız, bütün yüreğinizle arayınca beni bulacaksınız."));
trsuperobject.biblemap.put("b24_29_14", new trsuperobject(24,29,14,"Kendimi size buldurtacağım›› diyor RAB. ‹‹Sizi eski gönencinize kavuşturacağım. Sizi sürdüğüm bütün yerlerden ve uluslardan toplayacağım›› diyor RAB. ‹‹Ve sizi sürgün ettiğim yerden geri getireceğim.››"));
trsuperobject.biblemap.put("b24_29_15", new trsuperobject(24,29,15,"Madem, ‹‹RAB bizim için Babilde peygamberler çıkardı›› diyorsunuz,"));
trsuperobject.biblemap.put("b24_29_16", new trsuperobject(24,29,16,"Davutun tahtında oturan kral, bu kentte kalan halk ve sizinle sürgüne gitmeyen yurttaşlarınız için RAB şöyle diyor."));
trsuperobject.biblemap.put("b24_29_17", new trsuperobject(24,29,17,"Evet, Her Şeye Egemen RAB diyor ki, ‹‹Üzerlerine kılıç, kıtlık, salgın hastalık göndereceğim. Onları yenilmeyecek kadar çürük, bozuk incir gibi edeceğim."));
trsuperobject.biblemap.put("b24_29_18", new trsuperobject(24,29,18,"Kılıçla, kıtlıkla, salgın hastalıkla peşlerine düşeceğim. Yeryüzündeki bütün krallıklara dehşet saçacak, kendilerini sürdüğüm bütün ülkeler arasında lanetlenecek, dehşet ve alay konusu olacak, aşağılanacaklar."));
trsuperobject.biblemap.put("b24_29_19", new trsuperobject(24,29,19,"Çünkü sözlerime kulak asmadılar›› diyor RAB. ‹‹Kullarım peygamberler aracılığıyla sözlerimi defalarca gönderdim, ama dinlemediniz.›› RAB böyle diyor."));
trsuperobject.biblemap.put("b24_29_20", new trsuperobject(24,29,20,"Bunun için, ey sizler, Yeruşalimden Babile gönderdiğim sürgünler, RABbin sözüne kulak verin!"));
trsuperobject.biblemap.put("b24_29_21", new trsuperobject(24,29,21,"Adımı kullanarak size yalan peygamberlik eden Kolaya oğlu Ahavla Maaseya oğlu Sidkiya için İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹‹Onları Babil Kralı Nebukadnessarın eline teslim edeceğim, gözünüzün önünde ikisini de öldürecek."));
trsuperobject.biblemap.put("b24_29_22", new trsuperobject(24,29,22,"Onlardan ötürü Babildeki bütün Yahuda sürgünleri, ‹RAB seni Babil Kralının diri diri yaktırdığı Sidkiya ve Ahavla aynı duruma düşürsün› diye lanet edecek."));
trsuperobject.biblemap.put("b24_29_23", new trsuperobject(24,29,23,"Çünkü İsrailde çirkin şeyler yaptılar; komşularının karılarıyla zina ettiler, onlara buyurmadığım halde adımla yalan sözler söylediler. Bunu biliyorum ve buna tanığım›› diyor RAB."));
trsuperobject.biblemap.put("b24_29_24", new trsuperobject(24,29,24,"Nehelamlı Şemayaya diyeceksin ki,"));
trsuperobject.biblemap.put("b24_29_25", new trsuperobject(24,29,25,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: Sen Yeruşalim halkına, Maaseya oğlu Kâhin Sefanyayla bütün öbür kâhinlere kendi adına mektuplar göndererek şöyle yazmıştın:"));
trsuperobject.biblemap.put("b24_29_26", new trsuperobject(24,29,26,"‹‹RAB tapınağın sorumlusu olarak Yehoyada yerine seni kâhin atadı. Peygamber gibi davranan her deliyi tomruğa, demir boyunduruğa vurmak görevindir."));
trsuperobject.biblemap.put("b24_29_27", new trsuperobject(24,29,27,"Öyleyse aranızda kendini peygamber ilan eden Anatotlu Yeremyayı neden azarlamadın?"));
trsuperobject.biblemap.put("b24_29_28", new trsuperobject(24,29,28,"Çünkü Yeremya biz Babildekilere şu haberi gönderdi: Sürgün uzun olacak. Onun için evler yapıp içinde oturun; bahçe dikip ürününü yiyin.››"));
trsuperobject.biblemap.put("b24_29_29", new trsuperobject(24,29,29,"Kâhin Sefanya mektubu Peygamber Yeremyaya okuyunca,"));
trsuperobject.biblemap.put("b24_29_30", new trsuperobject(24,29,30,"RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_29_31", new trsuperobject(24,29,31,"‹‹Bütün sürgünlere şu haberi gönder: ‹Nehelamlı Şemaya için RAB diyor ki: Madem ben göndermediğim halde Şemaya peygamberlik edip sizleri yalana inandırdı,"));
trsuperobject.biblemap.put("b24_29_32", new trsuperobject(24,29,32,"ben de Nehelamlı Şemaya'yı ve bütün soyunu cezalandıracağım: Bu halkın arasında soyundan kimse sağ kalmayacak, halkıma yapacağım iyiliği görmeyecek, diyor RAB. Çünkü o halkı bana karşı kışkırttı.››"));
trsuperobject.biblemap.put("b24_30_2", new trsuperobject(24,30,2,"‹‹İsrailin Tanrısı RAB diyor ki, ‹Sana bildirdiğim bütün sözleri bir kitaba yaz."));
trsuperobject.biblemap.put("b24_30_3", new trsuperobject(24,30,3,"İşte halkım İsraili ve Yahudayı eski gönençlerine kavuşturacağım günler yaklaşıyor› diyor RAB, ‹Onları atalarına verdiğim topraklara geri getireceğim, orayı yurt edinecekler› diyor RAB.››"));
trsuperobject.biblemap.put("b24_30_4", new trsuperobject(24,30,4,"İsrail ve Yahuda için RABbin bildirdiği sözler şunlardır:"));
trsuperobject.biblemap.put("b24_30_5", new trsuperobject(24,30,5,"‹‹RAB diyor ki,‹Korku sesi duyduk,Esenlik değil, dehşet sesi."));
trsuperobject.biblemap.put("b24_30_6", new trsuperobject(24,30,6,"Sorun da görün:Erkek, çocuk doğurur mu?Öyleyse neden doğuran kadın gibiHer erkeğin ellerini belinde görüyorum?Neden her yüz solmuş?"));
trsuperobject.biblemap.put("b24_30_7", new trsuperobject(24,30,7,"Ah, ne korkunç gün!Onun gibisi olmayacak.Yakup soyu için sıkıntı dönemi olacak,Yine de sıkıntıdan kurtulacak."));
trsuperobject.biblemap.put("b24_30_8", new trsuperobject(24,30,8,"‹‹O gün› diyor Her Şeye Egemen RAB,‹Boyunlarındaki boyunduruğu kıracak,Bağlarını koparacağım.Bundan böyle yabancılar onlarıKendilerine köle etmeyecekler."));
trsuperobject.biblemap.put("b24_30_9", new trsuperobject(24,30,9,"Onun yerine Tanrıları RABbeVe başlarına atayacağım kralları DavutaKulluk edecekler."));
trsuperobject.biblemap.put("b24_30_10", new trsuperobject(24,30,10,"‹‹Korkma, ey kulum Yakup,Yılma, ey İsrail› diyor RAB.‹Çünkü seni uzak yerlerden,Soyunu sürgün edildiği ülkeden kurtaracağım.Yakup yine huzur ve güvenlik içinde olacak,Kimse onu korkutmayacak."));
trsuperobject.biblemap.put("b24_30_11", new trsuperobject(24,30,11,"Çünkü ben seninleyim,Seni kurtaracağım› diyor RAB.‹Seni aralarına dağıttığım bütün uluslarıTümüyle yok etsem de,Seni büsbütün yok etmeyecek,Adaletle yola getirecek,Hiç cezasız bırakmayacağım.›"));
trsuperobject.biblemap.put("b24_30_12", new trsuperobject(24,30,12,"‹‹RAB diyor ki,‹Senin yaran şifa bulmaz,Beren iyileşmez."));
trsuperobject.biblemap.put("b24_30_13", new trsuperobject(24,30,13,"Davanı görecek kimse yok,Yaran umarsız, şifa bulmaz."));
trsuperobject.biblemap.put("b24_30_14", new trsuperobject(24,30,14,"Bütün oynaşların unuttu seni,Arayıp sormuyorlar.Seni düşman vururcasına vurdum,Acımasızca cezalandırdım.Çünkü suçun çok,Günahların sayısız."));
trsuperobject.biblemap.put("b24_30_15", new trsuperobject(24,30,15,"Neden haykırıyorsun yarandan ötürü?Yaran şifa bulmaz.Suçlarının çokluğu ve sayısız günahın yüzündenGetirdim bunları başına."));
trsuperobject.biblemap.put("b24_30_16", new trsuperobject(24,30,16,"Ama seni yiyenlerin hepsi yem olacak,Bütün düşmanların sürgüne gidecek.Seni soyanlar soyulacak,Yağmalayanlar yağmalanacak."));
trsuperobject.biblemap.put("b24_30_17", new trsuperobject(24,30,17,"Ama ben seni sağlığına kavuşturacak,Yaralarını iyileştireceğim› diyor RAB,‹Çünkü Siyon itilmiş,Onu arayan soran yok diyorlar.›"));
trsuperobject.biblemap.put("b24_30_18", new trsuperobject(24,30,18,"‹‹RAB diyor ki,‹Yakupun çadırlarını eski gönencine kavuşturacağım,Konutlarına acıyacağım.Yeruşalim höyük üzerinde yeniden kurulacak,Saray kendi yerinde duracak."));
trsuperobject.biblemap.put("b24_30_19", new trsuperobject(24,30,19,"Oralardan şükran ve sevinç sesleri duyulacak.Sayılarını çoğaltacağım, azalmayacaklar,Onları onurlandıracağım, küçümsenmeyecekler."));
trsuperobject.biblemap.put("b24_30_20", new trsuperobject(24,30,20,"Çocukları eskisi gibi olacak,Toplulukları önümde sağlam duracak;Onlara baskı yapanların hepsini cezalandıracağım."));
trsuperobject.biblemap.put("b24_30_21", new trsuperobject(24,30,21,"Önderleri kendilerinden biri olacak,Yöneticileri kendi aralarından çıkacak.Onu kendime yaklaştıracağım,Bana yaklaşacak.Kim canı pahasına yaklaşabilir bana?› diyor RAB."));
trsuperobject.biblemap.put("b24_30_22", new trsuperobject(24,30,22,"‹‹Böylece siz benim halkım olacaksınız,Ben de sizin Tanrınız olacağım.›"));
trsuperobject.biblemap.put("b24_30_23", new trsuperobject(24,30,23,"İşte RABbin fırtınası öfkeyle kopacak,Şiddetli kasırgası kötülerin başında patlayacak."));
trsuperobject.biblemap.put("b24_30_24", new trsuperobject(24,30,24,"Aklının tasarladığını tümüyle yapana dek,RAB'bin kızgın öfkesi dinmeyecek.Son günlerde bunu anlayacaksınız.››"));
trsuperobject.biblemap.put("b24_31_1", new trsuperobject(24,31,1,"‹‹O zaman›› diyor RAB, ‹‹Bütün İsrail boylarının Tanrısı olacağım, onlar da benim halkım olacaklar.››"));
trsuperobject.biblemap.put("b24_31_3", new trsuperobject(24,31,3,"Ona uzaktan görünüp şöyle dedim:Seni sonsuz bir sevgiyle sevdim,Bu nedenle sevecenlikle seni kendime çektim."));
trsuperobject.biblemap.put("b24_31_4", new trsuperobject(24,31,4,"Seni yeniden bina edeceğim,Yeniden bina edileceksin, ey erden kız İsrail!Yine teflerini alacak,Sevinçle coşup oynayanlara katılacaksın."));
trsuperobject.biblemap.put("b24_31_5", new trsuperobject(24,31,5,"Samiriye dağlarında yine bağ dikeceksin;Bağ dikenler üzümünü yiyecekler."));
trsuperobject.biblemap.put("b24_31_6", new trsuperobject(24,31,6,"Efrayimin dağlık bölgesindeki bekçilerin,‹Haydi, Siyona, Tanrımız RABbe çıkalımDiye bağıracakları bir gün var.››"));
trsuperobject.biblemap.put("b24_31_8", new trsuperobject(24,31,8,"İşte, onları kuzey ülkesindenGeri getirmek üzereyim;Onları dünyanın dört bucağından toplayacağım.Aralarında kör, topal,Gebe kadın da, doğuran kadın da olacak.Büyük bir topluluk olarak buraya dönecekler."));
trsuperobject.biblemap.put("b24_31_9", new trsuperobject(24,31,9,"Ağlaya ağlaya gelecekler,Benden yardım dileyenleri geri getireceğim.Akarsular boyunca tökezlemeyecekleriDüz bir yolda yürüteceğim onları.Çünkü ben İsrailin babasıyım,Efrayim de ilk oğlumdur."));
trsuperobject.biblemap.put("b24_31_10", new trsuperobject(24,31,10,"‹‹RABbin sözünü dinleyin, ey uluslar!Uzaktaki kıyılara duyurun:‹İsraili dağıtan onu toplayacak,Sürüsünü kollayan çoban gibi kollayacak onu› deyin."));
trsuperobject.biblemap.put("b24_31_11", new trsuperobject(24,31,11,"Çünkü RAB Yakupu kurtaracak,Onu kendisinden güçlü olanın elinden özgür kılacak."));
trsuperobject.biblemap.put("b24_31_12", new trsuperobject(24,31,12,"Siyonun yüksek tepelerine gelipSevinçle haykıracaklar.RABbin verdiği iyilikler karşısında-Tahıl, yeni şarap, zeytinyağı,Davar ve sığır yavruları karşısında-Yüzleri sevinçle parlayacak.Sulanmış bahçe gibi olacak,Bir daha solmayacaklar."));
trsuperobject.biblemap.put("b24_31_13", new trsuperobject(24,31,13,"O zaman erden kızlar, genç yaşlı erkeklerHep birlikte oynayıp sevinecek.Yaslarını coşkuya çevirecek,Üzüntülerini avutup onları sevindireceğim."));
trsuperobject.biblemap.put("b24_31_14", new trsuperobject(24,31,14,"Kâhinleri bol yiyecekle doyuracağım,Halkım iyiliklerimle doyacak›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_15", new trsuperobject(24,31,15,"RAB diyor ki,‹‹Ramada bir ses duyuldu,Ağlayış ve acı feryat sesleri!Çocukları için ağlayan Rahel avutulmak istemiyor.Çünkü onlar yok artık!››"));
trsuperobject.biblemap.put("b24_31_16", new trsuperobject(24,31,16,"RAB diyor ki,‹‹Sesini ağlamaktan,Gözlerini yaş dökmekten alıkoy.Çünkü verdiğin emek ödüllendirilecek›› diyor RAB.‹‹Halkım düşman ülkesinden geri dönecek."));
trsuperobject.biblemap.put("b24_31_17", new trsuperobject(24,31,17,"Geleceğin için umut var›› diyor RAB.‹‹Çocukların yurtlarına dönecekler."));
trsuperobject.biblemap.put("b24_31_18", new trsuperobject(24,31,18,"‹‹Efrayimin inlemelerini kuşkusuz duydum:‹Beni eğitilmemiş dana gibi yola getirdinVe yola geldim.Beni geri getir, döneyim.Çünkü RAB Tanrım sensin."));
trsuperobject.biblemap.put("b24_31_19", new trsuperobject(24,31,19,"Yanlış yola saptıktan sonra pişman oldum.Aklım başıma gelince bağrımı dövdüm.Gençliğimdeki ayıplarımdan utandım,Rezil oldum.›"));
trsuperobject.biblemap.put("b24_31_20", new trsuperobject(24,31,20,"‹‹Efrayim değerli oğlum değil mi?Hoşnut olduğum çocuk değil mi?Kendisi için ne dersem diyeyim,Onu hiç unutmuyorum.Bu yüzden yüreğim sızlıyor,Çok acıyorum ona›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_21", new trsuperobject(24,31,21,"‹‹Kendin için yol işaretleri koy,Direkler dik.Yolunu, gittiğin yolu iyi düşün.Geri dön, ey erden kız İsrail, kentlerine dön!"));
trsuperobject.biblemap.put("b24_31_22", new trsuperobject(24,31,22,"Ne zamana dek bocalayıp duracaksın, ey dönek kız?RAB dünyada yeni bir şey yarattı:Kadın erkeği koruyacak.››"));
trsuperobject.biblemap.put("b24_31_23", new trsuperobject(24,31,23,"İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: ‹‹Yahuda ve kentlerindeki halkı eski gönençlerine kavuşturduğum zaman yine şu sözleri söyleyecekler:  ‹RAB sizi kutsasın,Ey doğruluk yurdu, ey kutsal dağ!›"));
trsuperobject.biblemap.put("b24_31_24", new trsuperobject(24,31,24,"Halk, ırgatlar, sürüleriyle dolaşan çobanlar Yahudada ve kentlerinde birlikte yaşayacak."));
trsuperobject.biblemap.put("b24_31_25", new trsuperobject(24,31,25,"Yorgun cana kana kana içirecek, bitkin canı doyuracağım.››"));
trsuperobject.biblemap.put("b24_31_26", new trsuperobject(24,31,26,"Bunun üzerine uyanıp baktım. Uykum bana tatlı geldi."));
trsuperobject.biblemap.put("b24_31_27", new trsuperobject(24,31,27,"‹‹İsrail ve Yahudada insan ve hayvan tohumu ekeceğim günler yaklaşıyor›› diyor RAB,"));
trsuperobject.biblemap.put("b24_31_28", new trsuperobject(24,31,28,"‹‹Kökünden söküp yok etmek, yerle bir edip yıkmak, yıkıma uğratmak için onları nasıl gözledimse, kurup dikmek için de gözleyeceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_29", new trsuperobject(24,31,29,"‹‹O günler insanlar artık,  ‹Babalar koruk yedi,Çocukların dişleri kamaştı"));
trsuperobject.biblemap.put("b24_31_30", new trsuperobject(24,31,30,"Herkes kendi suçu yüzünden ölecek. Koruk yiyenin dişleri kamaşacak."));
trsuperobject.biblemap.put("b24_31_31", new trsuperobject(24,31,31,"‹‹İsrail halkıyla ve Yahuda halkıylaYeni bir antlaşma yapacağım günler geliyor›› diyor RAB,"));
trsuperobject.biblemap.put("b24_31_32", new trsuperobject(24,31,32,"‹‹Atalarını Mısırdan çıkarmak içinEllerinden tuttuğum günOnlarla yaptığım antlaşmaya benzemeyecek.Onların kocası olmama karşın,Bozdular o antlaşmamı›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_33", new trsuperobject(24,31,33,"‹‹Ama o günlerden sonra İsrail halkıylaYapacağım antlaşma şudur›› diyor RAB,‹‹Yasamı içlerine yerleştirecek,Yüreklerine yazacağım.Ben onların Tanrısı olacağım,Onlar da benim halkım olacak."));
trsuperobject.biblemap.put("b24_31_34", new trsuperobject(24,31,34,"Bundan böyle kimse komşusunu ya da kardeşini,‹RABbi tanıyın› diye eğitmeyecek.Çünkü küçük büyük hepsiTanıyacak beni›› diyor RAB.‹‹Çünkü suçlarını bağışlayacağım,Günahlarını artık anmayacağım.››"));
trsuperobject.biblemap.put("b24_31_35", new trsuperobject(24,31,35,"Gündüz ışık olsun diye güneşi sağlayan,Gece ışık olsun diye ayı, yıldızları düzene koyan,Dalgaları kükresin diye denizi kabartan RAB-Onun adı Her Şeye Egemen RABdir- diyor ki,"));
trsuperobject.biblemap.put("b24_31_36", new trsuperobject(24,31,36,"‹‹Eğer kurulan bu düzen önümden kalkarsa,İsrail soyu sonsuza dekÖnümde ulus olmaktan çıkar›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_37", new trsuperobject(24,31,37,"RAB şöyle diyor:‹‹Gökler ölçülebilse,Dünyanın temelleri incelenip anlaşılabilse,İsrail soyunu bütün yaptıkları yüzündenReddederim›› diyor RAB."));
trsuperobject.biblemap.put("b24_31_38", new trsuperobject(24,31,38,"‹‹Yeruşalim Kentinin Hananel Kulesinden Köşe Kapısına dek benim için yeniden kurulacağı günler geliyor›› diyor RAB,"));
trsuperobject.biblemap.put("b24_31_39", new trsuperobject(24,31,39,"‹‹Ölçü ipi oradan Garev Tepesine doğru uzayıp Goaya dönecek."));
trsuperobject.biblemap.put("b24_31_40", new trsuperobject(24,31,40,"Ölülerle küllerin atıldığı bütün vadi, Kidron Vadisi'ne dek uzanan tarlalar, doğuda At Kapısı'nın köşesine dek RAB için kutsal olacak. Kent bir daha kökünden sökülmeyecek, sonsuza dek yıkılmayacak.››"));
trsuperobject.biblemap.put("b24_32_1", new trsuperobject(24,32,1,"Yahuda Kralı Sidkiyanın onuncu, Nebukadnessarın on sekizinci yılında RAB Yeremyaya seslendi."));
trsuperobject.biblemap.put("b24_32_2", new trsuperobject(24,32,2,"O sırada Babil Kralının ordusu Yeruşalimi kuşatmaktaydı. Peygamber Yeremya Yahuda Kralının sarayındaki muhafız avlusunda tutukluydu."));
trsuperobject.biblemap.put("b24_32_3", new trsuperobject(24,32,3,"Yahuda Kralı Sidkiya onu orada tutuklatmıştı. ‹‹Neden böyle peygamberlik ediyorsun?›› demişti, ‹‹Sen diyorsun ki, ‹RAB şöyle diyor: Bu kenti Babil Kralının eline teslim etmek üzereyim, onu ele geçirecek."));
trsuperobject.biblemap.put("b24_32_4", new trsuperobject(24,32,4,"Yahuda Kralı Sidkiya Kildanilerin elinden kaçıp kurtulamayacak, kesinlikle Babil Kralının eline teslim edilecek; onunla yüzyüze konuşacak, onu gözleriyle görecek."));
trsuperobject.biblemap.put("b24_32_5", new trsuperobject(24,32,5,"Sidkiya Babile götürülecek, ben onunla ilgilenene dek orada kalacak, Kildanilerle savaşsanız bile başarılı olamayacaksınız diyor RAB.››"));
trsuperobject.biblemap.put("b24_32_6", new trsuperobject(24,32,6,"Yeremya, ‹‹RAB bana şöyle seslendi›› diye yanıtladı,"));
trsuperobject.biblemap.put("b24_32_7", new trsuperobject(24,32,7,"‹‹Amcan Şallum oğlu Hanamel sana gelip, ‹Anatottaki tarlamı satın al. Çünkü en yakın akrabam olarak tarlayı satın alma hakkı senindir› diyecek."));
trsuperobject.biblemap.put("b24_32_8", new trsuperobject(24,32,8,"‹‹Sonra RABbin sözü uyarınca amcamın oğlu Hanamel muhafız avlusunda yanıma gelip, ‹Benyamin bölgesinde, Anatottaki tarlamı satın al› dedi, ‹Çünkü miras hakkı da en yakın akrabalık hakkı da senindir. Onu kendin için satın al.› ‹‹O zaman RABbin sözünün yerine geldiğini anladım."));
trsuperobject.biblemap.put("b24_32_9", new trsuperobject(24,32,9,"Böylece Anatottaki tarlayı amcamın oğlu Hanamelden satın aldım. Tarlaya karşılık kendisine on yedi şekel gümüş tartıp ödedim."));
trsuperobject.biblemap.put("b24_32_10", new trsuperobject(24,32,10,"Satış belgesini çağırdığım tanıkların önünde imzalayıp mühürledim, gümüşü terazide tarttım."));
trsuperobject.biblemap.put("b24_32_11", new trsuperobject(24,32,11,"Satış belgesini -kural ve koşulları içeren mühürlenmiş kâğıdı ve açık sözleşme belgesini- aldım."));
trsuperobject.biblemap.put("b24_32_12", new trsuperobject(24,32,12,"Amcamın oğlu Hanamelin, satış belgesini imzalayan tanıkların ve muhafız avlusunda oturan bütün Yahudilerin gözü önünde satış belgesini Mahseya oğlu Neriya oğlu Baruka verdim."));
trsuperobject.biblemap.put("b24_32_13", new trsuperobject(24,32,13,"‹‹Hepsinin gözü önünde Baruka şu buyrukları verdim:"));
trsuperobject.biblemap.put("b24_32_14", new trsuperobject(24,32,14,"‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, Bu satış belgesini -mühürlenmiş, açık olanını- al, uzun süre durmak üzere bir çömleğe koy."));
trsuperobject.biblemap.put("b24_32_15", new trsuperobject(24,32,15,"Çünkü İsrailin Tanrısı, Her Şeye Egemen RAB söz veriyor, bu ülkede yine evler, tarlalar, bağlar satın alınacak› diyor."));
trsuperobject.biblemap.put("b24_32_16", new trsuperobject(24,32,16,"‹‹Tarlanın satış belgesini Neriya oğlu Baruka verdikten sonra RABbe şöyle yakardım:"));
trsuperobject.biblemap.put("b24_32_17", new trsuperobject(24,32,17,"‹‹Ey Egemen RAB! Büyük gücünle, kudretinle yeri göğü yarattın. Yapamayacağın hiçbir şey yok."));
trsuperobject.biblemap.put("b24_32_18", new trsuperobject(24,32,18,"Binlerce insana sevgi gösterir, ama babaların işlediği günahların karşılığını çocuklarına ödetirsin. Ey büyük ve güçlü Tanrı! Her Şeye Egemen RABdir senin adın."));
trsuperobject.biblemap.put("b24_32_19", new trsuperobject(24,32,19,"Tasarıların ne büyük, işlerin ne güçlü! Gözlerin insanların bütün yaptıklarına açıktır. Herkese davranışlarına, yaptıklarının sonucuna göre karşılığını verirsin."));
trsuperobject.biblemap.put("b24_32_20", new trsuperobject(24,32,20,"Sen ki, Mısırda, İsrailde, bütün insanlar arasında bugüne dek mucizeler, harikalar yarattın. Bugün olduğu gibi ün kazandın."));
trsuperobject.biblemap.put("b24_32_21", new trsuperobject(24,32,21,"Halkın İsraili belirtilerle, şaşılası işlerle, güçlü, kudretli elinle, büyük korku saçarak Mısırdan çıkardın."));
trsuperobject.biblemap.put("b24_32_22", new trsuperobject(24,32,22,"Atalarına vereceğine ant içtiğin bu toprakları, süt ve bal akan ülkeyi onlara verdin."));
trsuperobject.biblemap.put("b24_32_23", new trsuperobject(24,32,23,"Gelip ülkeyi mülk edindiler, ama senin sözünü dinlemediler, Kutsal Yasan uyarınca yürümediler. Yapmalarını buyurduğun şeylerin hiçbirini yapmadılar. Bu yüzden bütün bu felaketleri getirdin başlarına."));
trsuperobject.biblemap.put("b24_32_24", new trsuperobject(24,32,24,"‹‹İşte, kenti ele geçirmek için kuşatma rampaları yapıldı. Kılıç, kıtlık, salgın hastalık yüzünden kent saldıran Kildanilere teslim edilecek. Söylediklerin yerine geldi, sen de görüyorsun!"));
trsuperobject.biblemap.put("b24_32_25", new trsuperobject(24,32,25,"Yine de, Egemen RAB, kent Kildanilere teslim edileceği halde sen bana, ‹Tarlayı çağırdığın tanıklar önünde gümüşle satın al› dedin.››"));
trsuperobject.biblemap.put("b24_32_26", new trsuperobject(24,32,26,"Bunun üzerine RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_32_27", new trsuperobject(24,32,27,"‹‹Bütün insanlığın Tanrısı RAB benim. Var mı yapamayacağım bir şey?"));
trsuperobject.biblemap.put("b24_32_28", new trsuperobject(24,32,28,"Bu yüzden RAB diyor ki: Bak, bu kenti Kildanilerle Babil Kralı Nebukadnessarın eline vermek üzereyim; onu ele geçirsin."));
trsuperobject.biblemap.put("b24_32_29", new trsuperobject(24,32,29,"Kente saldıran Kildaniler gelip onu ateşe verecekler. Kenti de damlarında Baalın onuruna buhur yakıp başka ilahlara dökmelik sunular sunarak beni öfkelendirdikleri evleri de yakacaklar."));
trsuperobject.biblemap.put("b24_32_30", new trsuperobject(24,32,30,"‹‹Çünkü İsrail ve Yahuda halkları gençliklerinden beri hep gözümde kötü olanı yapıyor; İsrail halkı ellerinin yaptıklarıyla beni sürekli öfkelendiriyor, diyor RAB."));
trsuperobject.biblemap.put("b24_32_31", new trsuperobject(24,32,31,"Evet, bu kent kurulduğundan bu yana beni öyle öfkelendirdi, kızdırdı ki onu önümden söküp atacağım."));
trsuperobject.biblemap.put("b24_32_32", new trsuperobject(24,32,32,"Çünkü İsrail ve Yahuda halklarının -kendilerinin, krallarının, önderlerinin, kâhinlerinin, peygamberlerinin, Yahuda ve Yeruşalimde yaşayanların- beni öfkelendirmek için yaptıkları kötülüklerin haddi hesabı yok."));
trsuperobject.biblemap.put("b24_32_33", new trsuperobject(24,32,33,"Bana yüzlerini değil, sırtlarını çevirdiler. Onları defalarca uyarmama karşın dinlemediler, yola gelmediler."));
trsuperobject.biblemap.put("b24_32_34", new trsuperobject(24,32,34,"Bana ait olan bu tapınağa iğrenç putlarını yerleştirerek onu kirlettiler."));
trsuperobject.biblemap.put("b24_32_35", new trsuperobject(24,32,35,"Ben-Hinnom Vadisinde ilah Moleke sunu olarak oğullarını, kızlarını ateşte kurban etmek için Baalın tapınma yerlerini kurdular. Böyle iğrenç şeyler yaparak Yahudayı günaha sürüklemelerini ne buyurdum, ne de aklımdan geçirdim."));
trsuperobject.biblemap.put("b24_32_36", new trsuperobject(24,32,36,"‹‹Siz bu kent için, ‹Kılıçla, kıtlıkla, salgın hastalıkla Babil Kralının eline veriliyor› diyorsunuz. Ama şimdi İsrailin Tanrısı RAB diyor ki:"));
trsuperobject.biblemap.put("b24_32_37", new trsuperobject(24,32,37,"Kızgınlıkla, gazapla, büyük öfkeyle onları sürdüğüm ülkelerden hepsini toplayacağım. Onları buraya geri getirip güvenlik içinde yaşamalarını sağlayacağım."));
trsuperobject.biblemap.put("b24_32_38", new trsuperobject(24,32,38,"Onlar benim halkım olacak, ben de onların Tanrısı olacağım."));
trsuperobject.biblemap.put("b24_32_39", new trsuperobject(24,32,39,"Tek bir yürek, tek bir yaşam tarzı vereceğim onlara; gerek kendilerinin gerekse çocuklarının iyiliği için benden hep korksunlar."));
trsuperobject.biblemap.put("b24_32_40", new trsuperobject(24,32,40,"Onlarla kalıcı bir antlaşma yapacağım: Onlara iyilik etmekten vazgeçmeyecek, benden hiç ayrılmasınlar diye yüreklerine Tanrı korkusu salacağım."));
trsuperobject.biblemap.put("b24_32_41", new trsuperobject(24,32,41,"Onlara iyilik etmekten sevinç duyacağım; gerçekten bütün yüreğimle, bütün canımla onları bu ülkede dikeceğim."));
trsuperobject.biblemap.put("b24_32_42", new trsuperobject(24,32,42,"‹‹RAB diyor ki: Bu halkın başına bütün bu büyük felaketleri nasıl getirdiysem, onlara söz verdiğim bütün iyilikleri de öyle sağlayacağım."));
trsuperobject.biblemap.put("b24_32_43", new trsuperobject(24,32,43,"Sizlerin, ‹Viran olmuş, insansız, hayvansız, Kildanilerin eline verilmiş› dediğiniz bu ülkede yine tarlalar satın alınacak."));
trsuperobject.biblemap.put("b24_32_44", new trsuperobject(24,32,44,"Benyamin bölgesinde, Yeruşalim çevresindeki köylerde, Yahuda kentlerinde, dağlık bölgenin, Şefela'nın ve Negev'in kentlerinde gümüşle tarlalar satın alınacak, satış belgeleri tanıkların önünde imzalanıp mühürlenecek. Çünkü eski gönençlerine kavuşturacağım onları›› diyor RAB."));
trsuperobject.biblemap.put("b24_33_1", new trsuperobject(24,33,1,"Yeremya muhafız avlusunda tutukluyken, RAB ona ikinci kez seslendi:"));
trsuperobject.biblemap.put("b24_33_2", new trsuperobject(24,33,2,"‹‹Dünyayı yaratan, yerini alsın diye ona biçim veren, adı RAB olan şöyle diyor:"));
trsuperobject.biblemap.put("b24_33_3", new trsuperobject(24,33,3,"‹Bana yakar da seni yanıtlayayım; bilmediğin büyük, akıl almaz şeyleri sana bildireyim.›"));
trsuperobject.biblemap.put("b24_33_4", new trsuperobject(24,33,4,"Kuşatma rampalarına, kılıca karşı siper olsun diye bu kentin yıkılmış olan evleriyle Yahuda krallarının sarayları için İsrailin Tanrısı RAB şöyle diyor:"));
trsuperobject.biblemap.put("b24_33_5", new trsuperobject(24,33,5,"‹Kildaniler savaşmak, evleri öfke ve kızgınlıkla vurduğum insanların cesetleriyle doldurmak üzere gelecekler. O insanlar ki, yaptıkları kötülükler yüzünden bu kentten yüzümü çevirdim. ‹‹Kildanilerle››."));
trsuperobject.biblemap.put("b24_33_6", new trsuperobject(24,33,6,"‹‹Yine de bu kenti iyileştirip sağlığa kavuşturacağım. Halkına şifa verecek, bol esenlik, güvenlik içinde yaşamalarını sağlayacağım."));
trsuperobject.biblemap.put("b24_33_7", new trsuperobject(24,33,7,"Yahudayı ve İsraili eski gönencine kavuşturacak, önceden olduğu gibi bina edeceğim."));
trsuperobject.biblemap.put("b24_33_8", new trsuperobject(24,33,8,"Onları bana karşı işledikleri bütün günahlardan arındıracak, bana karşı işledikleri günahları da isyanlarını da bağışlayacağım."));
trsuperobject.biblemap.put("b24_33_9", new trsuperobject(24,33,9,"Dünyadaki bütün ulusların önünde bu kent benim için sevinç, övgü ve onur kaynağı olacak. Bu uluslar Yeruşalim halkına yaptığım iyilikleri, sağladığım gönenci duyunca, korkuya kapılıp titreyecekler.›"));
trsuperobject.biblemap.put("b24_33_1-011", new trsuperobject(24,33,1-011,"‹‹RAB şöyle diyor: ‹Bu kent viran olmuş, insansız, hayvansız kalmış diyorsunuz. Ne var ki, terk edilmiş, insansız, hayvansız Yahuda kentlerinde, Yeruşalim sokaklarında sevinç ve neşe sesi, gelin güvey sesi, RABbin Tapınağına şükran sunuları getirenlerin sesi yine duyulacak:  ‹Her Şeye Egemen RABbe şükredin,Çünkü O iyidir,Sevgisi sonsuza dek kalıcıdır."));
trsuperobject.biblemap.put("b24_33_12", new trsuperobject(24,33,12,"‹‹Her Şeye Egemen RAB diyor ki, ‹Viran olmuş, insansız, hayvansız kalmış bu ülkenin bütün kentlerinde çobanların sürülerini dinlendireceği otlaklar olacak yeniden."));
trsuperobject.biblemap.put("b24_33_13", new trsuperobject(24,33,13,"Dağlık bölgede, Şefela, Negev, Benyamin bölgesindeki kentlerde, Yeruşalimin çevresindeki köylerde, Yahuda kentlerinde çoban değneğinin altından geçen sürüler olacak› diyor RAB."));
trsuperobject.biblemap.put("b24_33_14", new trsuperobject(24,33,14,"‹‹İsrail ve Yahuda halkına verdiğim güzel sözü yerine getireceğim günler geliyor› diyor RAB."));
trsuperobject.biblemap.put("b24_33_15", new trsuperobject(24,33,15,"‹‹O günlerde, o zamanda,Davut için doğru bir dal yetiştireceğim;Ülkede adil ve doğru olanı yapacak."));
trsuperobject.biblemap.put("b24_33_16", new trsuperobject(24,33,16,"O günlerde Yahuda kurtulacak,Yeruşalim güvenlik içinde yaşayacak.O, Yahve sidkenu adıyla anılacak.›"));
trsuperobject.biblemap.put("b24_33_17", new trsuperobject(24,33,17,"RAB şöyle diyor: ‹İsrail tahtı üzerinde oturan Davut soyunun ardı arkası kesilmeyecek."));
trsuperobject.biblemap.put("b24_33_18", new trsuperobject(24,33,18,"Levili kâhinlerden önümde yakmalık sunu sunacak, tahıl sunusu yakacak, kurban kesecek biri hiç eksik olmayacak.››"));
trsuperobject.biblemap.put("b24_33_19", new trsuperobject(24,33,19,"RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_33_2-021", new trsuperobject(24,33,2-021,"‹‹RAB diyor ki, ‹Eğer belirlenmiş zamanlarda gece ve gündüz olması için gece ve gündüzle yaptığım antlaşma bozulabilirse, tahtında oturan oğulları krallık yapsın diye kulum Davutla ve bana hizmet eden Levili kâhinlerle yaptığım antlaşma da ancak o zaman bozulabilir."));
trsuperobject.biblemap.put("b24_33_22", new trsuperobject(24,33,22,"Kulum Davutun soyunu ve bana hizmet eden Levilileri sayılamaz gök cisimleri kadar, ölçülemez deniz kumu kadar çoğaltacağım.››"));
trsuperobject.biblemap.put("b24_33_23", new trsuperobject(24,33,23,"RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_33_24", new trsuperobject(24,33,24,"‹‹Bu halkın, ‹RAB seçtiği iki aileyi de reddetti› dediğini görmüyor musun? Halkımı öyle küçümsüyorlar ki, artık bir ulus saymıyorlar onu."));
trsuperobject.biblemap.put("b24_33_25", new trsuperobject(24,33,25,"RAB diyor ki, ‹Gece ve gündüzle bir antlaşma yapıp yerin, göğün kurallarını saptamasaydım,"));
trsuperobject.biblemap.put("b24_33_26", new trsuperobject(24,33,26,"Yakup soyuyla kulum Davut'u da reddeder, Davut'un oğullarından birinin İbrahim, İshak, Yakup soyuna krallık etmesini sağlamazdım. Ama ben onları eski gönençlerine kavuşturacak, onlara acıyacağım.››"));
trsuperobject.biblemap.put("b24_34_1", new trsuperobject(24,34,1,"Babil Kralı Nebukadnessarla bütün ordusu, krallığı altındaki bütün uluslarla halklar, Yeruşalim ve çevresindeki kentlere karşı savaşırken RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_34_2", new trsuperobject(24,34,2,"‹‹İsrailin Tanrısı RAB diyor ki, ‹Git, Yahuda Kralı Sidkiyaya RAB şöyle diyor de: Bu kenti Babil Kralının eline teslim etmek üzereyim, onu ateşe verecek."));
trsuperobject.biblemap.put("b24_34_3", new trsuperobject(24,34,3,"Ve sen Sidkiya, onun elinden kaçıp kurtulamayacaksın; kesinlikle yakalanacak, onun eline teslim edileceksin. Babil Kralını gözünle görecek, onunla yüzyüze konuşacaksın. Sonra Babile götürüleceksin."));
trsuperobject.biblemap.put("b24_34_4", new trsuperobject(24,34,4,"‹‹Ancak, ey Yahuda Kralı Sidkiya, RABbin sözünü dinle! RAB senin için şöyle diyor: Kılıçla ölmeyeceksin,"));
trsuperobject.biblemap.put("b24_34_5", new trsuperobject(24,34,5,"esenlikle öleceksin. Ataların olan senden önceki kralların onuruna ateş yaktıkları gibi, senin onuruna da ateş yakıp senin için ah efendimiz diyerek ağıt tutacaklar. Ben RAB söylüyorum bunu.››"));
trsuperobject.biblemap.put("b24_34_6", new trsuperobject(24,34,6,"Peygamber Yeremya bütün bunları Yeruşalimde Yahuda Kralı Sidkiyaya söyledi."));
trsuperobject.biblemap.put("b24_34_7", new trsuperobject(24,34,7,"O sırada Babil Kralının ordusu Yeruşalime ve Yahudanın henüz ele geçirilmemiş kentlerine -Lakişe, Azekaya- saldırmaktaydı. Yahudada surlu kent olarak yalnız bunlar kalmıştı."));
trsuperobject.biblemap.put("b24_34_8", new trsuperobject(24,34,8,"Kral Sidkiya Yeruşalimdeki halkla kölelerin özgürlüğünü ilan eden bir antlaşma yaptıktan sonra RAB Yeremyaya seslendi."));
trsuperobject.biblemap.put("b24_34_9", new trsuperobject(24,34,9,"Bu antlaşmaya göre herkes kadın, erkek İbrani kölelerini özgür bırakacak, hiç kimse Yahudi kardeşini yanında köle olarak tutmayacaktı."));
trsuperobject.biblemap.put("b24_34_10", new trsuperobject(24,34,10,"Böylece bu antlaşmanın yükümlülüğü altına giren bütün önderlerle halk kadın, erkek kölelerini özgür bırakarak antlaşmaya uydular. Artık kimseyi köle olarak tutmadılar. Antlaşmaya uyarak köleleri özgür bıraktılar."));
trsuperobject.biblemap.put("b24_34_11", new trsuperobject(24,34,11,"Ama sonra düşüncelerini değiştirerek özgür bıraktıkları kadın, erkek köleleri geri alıp zorla köleleştirdiler."));
trsuperobject.biblemap.put("b24_34_12", new trsuperobject(24,34,12,"Bunun üzerine RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_34_13", new trsuperobject(24,34,13,"‹‹İsrailin Tanrısı RAB diyor ki: Atalarınızı Mısırdan, köle oldukları ülkeden çıkardığımda onlarla bir antlaşma yaptım. Onlara dedim ki,"));
trsuperobject.biblemap.put("b24_34_14", new trsuperobject(24,34,14,"‹Size satılıp altı yıl kölelik eden İbrani kardeşlerinizi yedinci yıl özgür bırakacaksınız.› Ama atalarınız beni dinlemediler, kulak asmadılar."));
trsuperobject.biblemap.put("b24_34_15", new trsuperobject(24,34,15,"Sizse sonradan yola gelip gözümde doğru olanı yaptınız: Hepiniz İbrani kardeşlerinizin özgürlüğünü ilan ettiniz. Önümde, bana ait olan tapınakta bu doğrultuda bir antlaşma yapmıştınız."));
trsuperobject.biblemap.put("b24_34_16", new trsuperobject(24,34,16,"Ama düşüncenizi değiştirerek adıma saygısızlık ettiniz. Kendi isteğinizle özgür bıraktığınız kadın, erkek kölelerinizi geri alıp zorla köleleştirdiniz."));
trsuperobject.biblemap.put("b24_34_17", new trsuperobject(24,34,17,"‹‹Bu nedenle RAB diyor ki, İbrani köle kardeşlerinizi, yurttaşlarınızı özgür bırakmayarak beni dinlemediniz. Şimdi ben size ‹özgürlük› -kılıç, kıtlık ve salgın hastalıkla yok olmanız için ‹özgürlük›- ilan edeceğim, diyor RAB. Sizi dünyadaki bütün krallıklara dehşet verici bir örnek yapacağım."));
trsuperobject.biblemap.put("b24_34_18-19", new trsuperobject(24,34,18-19,"Antlaşmamı bozan, danayı ikiye ayırıp parçaları arasından geçerek önümde yaptıkları antlaşmanın koşullarını yerine getirmeyen bu adamları -Yahuda ve Yeruşalim önderlerini, saray görevlilerini, kâhinleri ve dana parçalarının arasından geçen bütün ülke halkını-"));
trsuperobject.biblemap.put("b24_34_20", new trsuperobject(24,34,20,"can düşmanlarının eline teslim edeceğim. Cesetleri yırtıcı kuşlara, yabanıl hayvanlara yem olacak."));
trsuperobject.biblemap.put("b24_34_21", new trsuperobject(24,34,21,"‹‹Yahuda Kralı Sidkiyayla önderlerini de can düşmanlarının eline, üzerinizden çekilen Babil ordusunun eline teslim edeceğim."));
trsuperobject.biblemap.put("b24_34_22", new trsuperobject(24,34,22,"Buyruğu ben vereceğim diyor RAB. Babilliler'i bu kente geri getireceğim. Saldırıp kenti ele geçirecek, ateşe verecekler. Yahuda kentlerini içinde kimsenin yaşamayacağı bir viraneye çevireceğim.››"));
trsuperobject.biblemap.put("b24_35_1", new trsuperobject(24,35,1,"Yahuda Kralı Yoşiya oğlu Yehoyakim döneminde RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_35_2", new trsuperobject(24,35,2,"‹‹Rekavlıların evine gidip onlarla konuş. Onları RABbin Tapınağının odalarından birine götürüp şarap içir.››"));
trsuperobject.biblemap.put("b24_35_3", new trsuperobject(24,35,3,"Bunun üzerine Havassinya oğlu Yirmeya oğlu Yaazanyayı, kardeşlerini, bütün çocuklarını ve Rekav ailesinin öbür üyelerini yanıma alıp"));
trsuperobject.biblemap.put("b24_35_4", new trsuperobject(24,35,4,"Tanrı adamı Yigdalya oğlu Hananın oğullarının RABbin Tapınağındaki odasına götürdüm. Bu oda önderlerin odasının bitişiğinde, kapı görevlisi Şallum oğlu Maaseyanın odasının üstündeydi."));
trsuperobject.biblemap.put("b24_35_5", new trsuperobject(24,35,5,"Rekav ailesinin üyelerinin önüne şarap dolu testiler, kâseler koyarak, ‹‹Buyrun, şarap için›› dedim."));
trsuperobject.biblemap.put("b24_35_6", new trsuperobject(24,35,6,"Ne var ki, ‹‹Biz şarap içmeyiz›› diye karşılık verdiler, ‹‹Çünkü atamız Rekav oğlu Yehonadav bize şu buyruğu verdi: ‹Siz de soyunuzdan gelenler de asla şarap içmeyeceksiniz!"));
trsuperobject.biblemap.put("b24_35_7", new trsuperobject(24,35,7,"Ayrıca ev yapmayacak, tohum ekmeyecek, bağ dikmeyeceksiniz. Böyle şeyler edinmeyecek, ömür boyu çadırlarda yaşayacaksınız. Öyle ki, göç ettiğiniz topraklarda uzun süre yaşayasınız.›"));
trsuperobject.biblemap.put("b24_35_8", new trsuperobject(24,35,8,"Atamız Rekav oğlu Yehonadavın bize buyurduğu her şeyi yaptık. Kendimiz de karılarımız, oğullarımız, kızlarımız da hiç şarap içmedik."));
trsuperobject.biblemap.put("b24_35_9", new trsuperobject(24,35,9,"İçinde oturmak için evler yapmadık, bağlar, tarlalar, ekinler edinmedik."));
trsuperobject.biblemap.put("b24_35_10", new trsuperobject(24,35,10,"Çadırlarda yaşadık; atamız Yehonadav ne buyurduysa hepsini yaptık."));
trsuperobject.biblemap.put("b24_35_11", new trsuperobject(24,35,11,"Ama Babil Kralı Nebukadnessar bu ülkeye saldırınca, ‹Haydi, Kildan ve Aram ordusundan kaçmak için Yeruşalime gidelim› dedik. Bunun için Yeruşalimde kaldık.››"));
trsuperobject.biblemap.put("b24_35_12", new trsuperobject(24,35,12,"Bundan sonra RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_35_13", new trsuperobject(24,35,13,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: ‹Git, Yahuda halkına ve Yeruşalimde yaşayanlara şunları söyle: Sözlerimi dinleyerek hiç ders almayacak mısınız, diyor RAB."));
trsuperobject.biblemap.put("b24_35_14", new trsuperobject(24,35,14,"Rekav oğlu Yehonadav, soyuna şarap içmemelerini buyurdu; buyruğuna uyuldu. Bugüne dek şarap içmediler. Çünkü atalarının buyruğuna uydular. Bense size defalarca seslendiğim halde beni dinlemediniz."));
trsuperobject.biblemap.put("b24_35_15", new trsuperobject(24,35,15,"Defalarca size kullarım peygamberleri gönderdim. Kötü yolunuzdan dönmeniz, davranışlarınızı düzeltmeniz, başka ilahların ardınca gidip onlara tapınmamanız için hepinizi uyardılar. Ancak o zaman size ve atalarınıza verdiğim toprakta yaşayacaksınız. Ama kulak verip beni dinlemediniz."));
trsuperobject.biblemap.put("b24_35_16", new trsuperobject(24,35,16,"Rekav oğlu Yehonadavın soyu atalarının verdiği buyruğu tuttu, ama bu halk beni dinlemedi.›"));
trsuperobject.biblemap.put("b24_35_17", new trsuperobject(24,35,17,"‹‹Bu yüzden İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹İşte, Yahuda ve Yeruşalimde yaşayan herkesin başına sözünü ettiğim her felaketi getirmek üzereyim. Çünkü onları uyardım, ama dinlemediler; onları çağırdım, ama yanıt vermediler.››"));
trsuperobject.biblemap.put("b24_35_18", new trsuperobject(24,35,18,"Yeremya Rekav ailesine şöyle dedi: ‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Atanız Yehonadavın buyruğuna uydunuz, onun bütün uyarılarını dikkate aldınız, size buyurduğu her şeyi yaptınız.›"));
trsuperobject.biblemap.put("b24_35_19", new trsuperobject(24,35,19,"Bunun için İsrail'in Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Rekav oğlu Yehonadav'ın soyundan önümde hizmet edecek olanlar hiçbir zaman eksilmeyecek.››"));
trsuperobject.biblemap.put("b24_36_1", new trsuperobject(24,36,1,"Yahuda Kralı Yoşiya oğlu Yehoyakimin krallığının dördüncü yılında RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_36_2", new trsuperobject(24,36,2,"‹‹Bir tomar al, Yoşiyanın döneminden bu yana İsrail, Yahuda ve öteki uluslarla ilgili sana söylediğim her şeyi yaz."));
trsuperobject.biblemap.put("b24_36_3", new trsuperobject(24,36,3,"Belki Yahuda halkı başına getirmeyi tasarladığım bütün felaketleri duyar da kötü yolundan döner; ben de suçlarını, günahlarını bağışlarım.››"));
trsuperobject.biblemap.put("b24_36_4", new trsuperobject(24,36,4,"Yeremya Neriya oğlu Baruku çağırıp RABbin kendisine söylediği bütün sözleri tomara yazdırdı."));
trsuperobject.biblemap.put("b24_36_5", new trsuperobject(24,36,5,"Sonra Baruka şu buyruğu verdi: ‹‹Ben tutukluyum, RABbin Tapınağına gidemem."));
trsuperobject.biblemap.put("b24_36_6", new trsuperobject(24,36,6,"Sen oruç günü RABbin Tapınağına git. Oradaki halka sana yazdırdığım RABbin sözlerini tomardan oku. Yahuda kentlerinden gelen halka da oku."));
trsuperobject.biblemap.put("b24_36_7", new trsuperobject(24,36,7,"Belki RABbe yalvarır, kötü yollarından dönerler. Çünkü RABbin bu halka karşı sözünü ettiği öfke ve kızgınlığı büyüktür.››"));
trsuperobject.biblemap.put("b24_36_8", new trsuperobject(24,36,8,"Neriya oğlu Baruk, Peygamber Yeremyanın buyurduğu her şeyi yaptı. RABbin tomarda yazılı sözlerini tapınakta okudu."));
trsuperobject.biblemap.put("b24_36_9", new trsuperobject(24,36,9,"Yahuda Kralı Yoşiya oğlu Yehoyakimin krallığının beşinci yılının dokuzuncu ayı, Yeruşalimde yaşayan bütün halk ve Yahuda kentlerinden Yeruşalime gelen herkes için RABbin önünde oruç ilan edildi."));
trsuperobject.biblemap.put("b24_36_10", new trsuperobject(24,36,10,"Şafan oğlu Yazman Gemaryanın yukarı avluda, tapınağın Yeni Kapısının girişindeki odasında Baruk, Yeremyanın sözlerini tomardan RABbin Tapınağındaki bütün halka okudu."));
trsuperobject.biblemap.put("b24_36_11", new trsuperobject(24,36,11,"Şafan oğlu Gemarya oğlu Mikaya tomardan okunan RABbin sözlerini duyunca,"));
trsuperobject.biblemap.put("b24_36_12", new trsuperobject(24,36,12,"aşağıya inip saraydaki yazman odasına gitti. Bütün önderler orada toplantı halindeydi: Yazman Elişama, Şemaya oğlu Delaya, Akbor oğlu Elnatan, Şafan oğlu Gemarya, Hananya oğlu Sidkiya ve öbürleri."));
trsuperobject.biblemap.put("b24_36_13", new trsuperobject(24,36,13,"Mikaya Barukun halka okuduğu tomardan duyduğu her şeyi onlara iletti."));
trsuperobject.biblemap.put("b24_36_14", new trsuperobject(24,36,14,"Bunun üzerine bütün önderler Kuşi oğlu Şelemya oğlu Netanya oğlu Yehudiyi Baruka gönderdiler. Yehudi Baruka, ‹‹Halka okuduğun tomarı al, gel›› dedi. Neriya oğlu Baruk tomarı alıp yanlarına gitti."));
trsuperobject.biblemap.put("b24_36_15", new trsuperobject(24,36,15,"Önderler, ‹‹Lütfen otur, bize de oku›› dediler. Baruk da tomarı onlara okudu."));
trsuperobject.biblemap.put("b24_36_16", new trsuperobject(24,36,16,"Bütün bu sözleri duyar duymaz, korkuyla birbirlerine bakıp Baruka, ‹‹Bütün bu sözleri kesinlikle krala bildirmemiz gerek›› dediler."));
trsuperobject.biblemap.put("b24_36_17", new trsuperobject(24,36,17,"Sonra Baruka, ‹‹Söyle bize, bütün bunları nasıl yazdın? Yeremya mı yazdırdı sana?›› diye sordular."));
trsuperobject.biblemap.put("b24_36_18", new trsuperobject(24,36,18,"Baruk, ‹‹Evet›› diye yanıtladı, ‹‹Bütün bu sözleri o söyledi. Ben de hepsini mürekkeple tomara yazdım.››"));
trsuperobject.biblemap.put("b24_36_19", new trsuperobject(24,36,19,"Bunun üzerine Baruka, ‹‹Git, sen de Yeremya da gizlenin. Nerede olduğunuzu kimse bilmesin›› dediler."));
trsuperobject.biblemap.put("b24_36_20", new trsuperobject(24,36,20,"Tomarı Yazman Elişamanın odasında bırakıp avluda bulunan kralın yanına gittiler. Ona duydukları her sözü ilettiler."));
trsuperobject.biblemap.put("b24_36_21", new trsuperobject(24,36,21,"Kral tomarı alıp getirmesi için Yehudiyi gönderdi. Yehudi tomarı Yazman Elişamanın odasından getirdi. Krala ve yanındaki önderlere okudu."));
trsuperobject.biblemap.put("b24_36_22", new trsuperobject(24,36,22,"Dokuzuncu aydı, kral kışlık sarayındaydı. Önünde mangalda ateş yanıyordu."));
trsuperobject.biblemap.put("b24_36_23", new trsuperobject(24,36,23,"Yehudi tomardan üç dört sütun okur okumaz kral yazman çakısıyla onları kesip mangaldaki ateşe attı. Tomarın tümü yanıp bitene dek bu işi sürdürdü."));
trsuperobject.biblemap.put("b24_36_24", new trsuperobject(24,36,24,"Tomardaki sözleri duyan kralla görevlileri ne korktular ne de giysilerini yırttılar."));
trsuperobject.biblemap.put("b24_36_25", new trsuperobject(24,36,25,"Elnatan, Delaya ve Gemarya tomarı yakmaması için yalvardılarsa da kral onları dinlemedi."));
trsuperobject.biblemap.put("b24_36_26", new trsuperobject(24,36,26,"Yazman Baruku ve Peygamber Yeremyayı tutuklamaları için oğlu Yerahmeele, Azriel oğlu Serayaya ve Avdeel oğlu Şelemyaya buyruk verdi. Oysa RAB onları gizlemişti."));
trsuperobject.biblemap.put("b24_36_27", new trsuperobject(24,36,27,"Kral, Yeremyanın Baruka yazdırdığı sözleri içeren tomarı yaktıktan sonra, RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_36_28", new trsuperobject(24,36,28,"‹‹Başka bir tomar al, Yahuda Kralı Yehoyakimin yaktığı ilk tomardaki bütün sözleri yazdır."));
trsuperobject.biblemap.put("b24_36_29", new trsuperobject(24,36,29,"Yahuda Kralı Yehoyakim için de ki, ‹RAB şöyle diyor: Babil Kralının kesinlikle gelip bu ülkeyi viraneye çevireceğini, içindeki insanı da hayvanı da yok edeceğini neden tomara yazdın diye sorup tomarı yaktın."));
trsuperobject.biblemap.put("b24_36_30", new trsuperobject(24,36,30,"Bu yüzden RAB Yahuda Kralı Yehoyakim için şöyle diyor: Davutun tahtında oturan kimsesi olmayacak; ölüsü gündüzün sıcağa, gece ayaza bırakılacak."));
trsuperobject.biblemap.put("b24_36_31", new trsuperobject(24,36,31,"İşledikleri suçlar yüzünden kendisini de çocuklarıyla görevlilerini de cezalandıracağım. Onların, Yeruşalimde yaşayanların ve Yahuda halkının başına sözünü ettiğim bütün felaketleri getireceğim. Çünkü beni dinlemediler.››"));
trsuperobject.biblemap.put("b24_36_32", new trsuperobject(24,36,32,"Bunun üzerine Yeremya başka bir tomar alıp Neriya oğlu Yazman Baruk'a verdi. Yahuda Kralı Yehoyakim'in ateşe atıp yaktığı tomardaki bütün sözleri Baruk Yeremya'nın ağzından tomara yazdı. Bu sözlere, benzer birçok söz daha eklendi."));
trsuperobject.biblemap.put("b24_37_1", new trsuperobject(24,37,1,"Babil Kralı Nebukadnessarın Yahudaya atadığı Yoşiya oğlu Sidkiya, Yehoyakim oğlu Yehoyakinin yerine kral oldu."));
trsuperobject.biblemap.put("b24_37_2", new trsuperobject(24,37,2,"Ama kendisi de görevlileriyle ülke halkı da RABbin Peygamber Yeremya aracılığıyla söylediği sözleri dikkate almadılar."));
trsuperobject.biblemap.put("b24_37_3", new trsuperobject(24,37,3,"Kral Sidkiya, Şelemya oğlu Yehukalla Maaseya oğlu Kâhin Sefanyayı şu haberle Peygamber Yeremyaya gönderdi: ‹‹Lütfen bizim için Tanrımız RABbe yalvar.››"));
trsuperobject.biblemap.put("b24_37_4", new trsuperobject(24,37,4,"O sırada Yeremya daha cezaevine konmamıştı, halk arasında dolaşıyordu."));
trsuperobject.biblemap.put("b24_37_5", new trsuperobject(24,37,5,"Firavunun ordusu Mısırdan çıkmıştı. Yeruşalimi kuşatma altında tutan Kildaniler bu haberi duyunca Yeruşalimden çekildiler."));
trsuperobject.biblemap.put("b24_37_6", new trsuperobject(24,37,6,"Derken RAB Peygamber Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_37_7", new trsuperobject(24,37,7,"‹‹İsrailin Tanrısı RAB diyor ki: Danışmak için sizi bana gönderen Yahuda Kralına şöyle deyin: ‹Size yardım etmek için Mısırdan çıkıp gelen firavunun ordusu ülkesine dönecek."));
trsuperobject.biblemap.put("b24_37_8", new trsuperobject(24,37,8,"Kildaniler de dönecek; bu kentle savaşıp onu ele geçirecek, ateşe verecekler.›"));
trsuperobject.biblemap.put("b24_37_9", new trsuperobject(24,37,9,"‹‹RAB diyor ki, ‹Kildaniler üzerimizden çekilip gidecek› diyerek kendinizi aldatmayın. Çünkü çekilip gitmeyecekler!"));
trsuperobject.biblemap.put("b24_37_10", new trsuperobject(24,37,10,"Sizinle savaşan bütün Babil ordusunu bozguna uğratsanız, çadırlarında yalnız yaralılar kalsa bile, bunlar çadırlardan çıkıp bu kenti ateşe verecekler.››"));
trsuperobject.biblemap.put("b24_37_11", new trsuperobject(24,37,11,"Firavunun ordusu yüzünden Babil ordusu Yeruşalimden çekilince,"));
trsuperobject.biblemap.put("b24_37_12-13", new trsuperobject(24,37,12-13,"Peygamber Yeremya Benyamin topraklarındaki halkın arasında payına düşen mirası almak üzere Yeruşalimden gitmek istedi. Benyamin Kapısına vardığında, Hananya oğlu Şelemya oğlu bekçibaşı Yiriya, ‹‹Sen Kildanilerin tarafına geçiyorsun!›› diyerek onu tutukladı."));
trsuperobject.biblemap.put("b24_37_14", new trsuperobject(24,37,14,"Yeremya, ‹‹Yalan!›› dedi, ‹‹Ben Kildanilerin tarafına geçmiyorum.›› Ama Yiriya onu dinlemedi. Yeremyayı tutuklayıp önderlere götürdü."));
trsuperobject.biblemap.put("b24_37_15", new trsuperobject(24,37,15,"Yeremyaya öfkelenen önderler onu dövdürtüp cezaevine çevirdikleri Yazman Yonatanın evine kapattılar."));
trsuperobject.biblemap.put("b24_37_16", new trsuperobject(24,37,16,"Böylece bodrumda bir hücreye kapatılan Yeremya uzun süre orada kaldı."));
trsuperobject.biblemap.put("b24_37_17", new trsuperobject(24,37,17,"Sonra Kral Sidkiya Yeremyayı sarayına getirtti. Orada kendisine gizlice, ‹‹RABden bir söz var mı?›› diye sordu. ‹‹Evet›› diye yanıtladı Yeremya, ‹‹Babil Kralının eline verileceksin.››"));
trsuperobject.biblemap.put("b24_37_18", new trsuperobject(24,37,18,"Sonra Kral Sidkiyaya şöyle dedi: ‹‹Sana, görevlilerine ve bu halka karşı ne günah işledim ki beni cezaevine kapattınız?"));
trsuperobject.biblemap.put("b24_37_19", new trsuperobject(24,37,19,"‹Babil Kralı sana da bu ülkeye de saldırmayacak› diyen peygamberlerin hani nerede?"));
trsuperobject.biblemap.put("b24_37_20", new trsuperobject(24,37,20,"Şimdi lütfen beni dinle, ey efendim kral! Lütfen dileğimi kabul et. Beni Yazman Yonatanın evine geri gönderme. Orada ölmek istemiyorum.››"));
trsuperobject.biblemap.put("b24_37_21", new trsuperobject(24,37,21,"Bunun üzerine Kral Sidkiya Yeremya'nın muhafız avlusuna kapatılmasını, kentteki ekmek bitene dek her gün fırıncılar sokağından kendisine bir ekmek verilmesini buyurdu. Böylece Yeremya muhafız avlusunda kaldı."));
trsuperobject.biblemap.put("b24_38_1", new trsuperobject(24,38,1,"Mattan oğlu Şefatya, Paşhur oğlu Gedalya, Şelemya oğlu Yehukal ve Malkiya oğlu Paşhur Yeremyanın halka söylediği şu sözleri duydular:"));
trsuperobject.biblemap.put("b24_38_2", new trsuperobject(24,38,2,"‹‹RAB diyor ki, ‹Bu kentte kalan kılıçtan, kıtlıktan, salgından ölecek. Kildanilere gidense sağ kalacak, canını kurtarıp yaşayacak.›"));
trsuperobject.biblemap.put("b24_38_4", new trsuperobject(24,38,4,"Önderler krala, ‹‹Bu adam öldürülmeli›› dediler, ‹‹Çünkü söylediği bu sözlerle kentte kalan askerlerin ve halkın cesaretini kırıyor. Bu adam halkın yararını değil, zararını istiyor.››"));
trsuperobject.biblemap.put("b24_38_5", new trsuperobject(24,38,5,"Kral Sidkiya, ‹‹İşte o sizin elinizde›› diye yanıtladı, ‹‹Kral size engel olamaz ki.››"));
trsuperobject.biblemap.put("b24_38_6", new trsuperobject(24,38,6,"Böylece Yeremyayı alıp kralın oğlu Malkiyanın muhafız avlusundaki sarnıcına halatlarla sarkıtarak indirdiler. Sarnıçta su yoktu, yalnız çamur vardı. Yeremya çamura battı."));
trsuperobject.biblemap.put("b24_38_7", new trsuperobject(24,38,7,"Sarayda görevli hadım Kûşlu Ebet-Melek Yeremyanın sarnıca atıldığını duydu. Kral Benyamin Kapısında otururken,"));
trsuperobject.biblemap.put("b24_38_8", new trsuperobject(24,38,8,"Ebet-Melek saraydan çıkıp kralın yanına gitti ve ona şöyle dedi:"));
trsuperobject.biblemap.put("b24_38_9", new trsuperobject(24,38,9,"‹‹Efendim kral, bu adamların Peygamber Yeremyaya yaptıkları kötüdür. Onu sarnıca attılar, orada açlıktan ölecek. Çünkü kentte ekmek kalmadı.››"));
trsuperobject.biblemap.put("b24_38_10", new trsuperobject(24,38,10,"Bunun üzerine kral, ‹‹Buradan yanına üçfç adam al, Peygamber Yeremyayı ölmeden sarnıçtan çıkarın›› diye ona buyruk verdi."));
trsuperobject.biblemap.put("b24_38_11", new trsuperobject(24,38,11,"Ebet-Melek yanına adamları alarak saray hazinesinin alt odasına gitti. Oradan eski bezler, yırtık pırtık giysiler alıp halatlarla sarnıca, Yeremyaya sarkıttı."));
trsuperobject.biblemap.put("b24_38_12", new trsuperobject(24,38,12,"Sonra Yeremyaya, ‹‹Bu eski bezleri, yırtık giysileri halatlarla bağlayıp koltuklarının altına geçir›› diye seslendi. Yeremya söyleneni yaptı."));
trsuperobject.biblemap.put("b24_38_13", new trsuperobject(24,38,13,"Onu halatlarla çekip sarnıçtan çıkardılar. Yeremya muhafız avlusunda kaldı."));
trsuperobject.biblemap.put("b24_38_14", new trsuperobject(24,38,14,"Kral Sidkiya Peygamber Yeremyayı RABbin Tapınağının üçüncü girişine getirterek, ‹‹Sana bir şey soracağım›› dedi, ‹‹Benden bir şey gizleme.››"));
trsuperobject.biblemap.put("b24_38_15", new trsuperobject(24,38,15,"Yeremya, ‹‹Sana bir şey bildirirsem, beni öldürmeyecek misin?›› diye karşılık verdi, ‹‹Üstelik öğüt versem bile beni dinlemeyeceksin.››"));
trsuperobject.biblemap.put("b24_38_16", new trsuperobject(24,38,16,"Kral Sidkiya, ‹‹Bize yaşam veren RABbin varlığı hakkı için seni öldürmeyeceğim, canının peşinde olan bu adamların eline seni teslim etmeyeceğim›› diyerek gizlice ant içti."));
trsuperobject.biblemap.put("b24_38_17", new trsuperobject(24,38,17,"Bunun üzerine Yeremya Sidkiyaya şu karşılığı verdi: ‹‹İsrailin Tanrısı, Her Şeye Egemen RAB Tanrı diyor ki, ‹Babil Kralının komutanlarına teslim olursan, canın bağışlanacak, bu kent de ateşe verilmeyecek. Sen de ailen de sağ kalacaksınız."));
trsuperobject.biblemap.put("b24_38_18", new trsuperobject(24,38,18,"Ama Babil Kralının komutanlarına teslim olmazsan, kent Kildanilere teslim edilecek, onu ateşe verecekler. Sen de onlardan kaçıp kurtulamayacaksın.››"));
trsuperobject.biblemap.put("b24_38_19", new trsuperobject(24,38,19,"Kral Sidkiya, ‹‹Kildanilerin tarafına geçen Yahudilerden korkuyorum›› dedi, ‹‹Kildaniler beni onların eline verebilir, onlar da bana kötü davranırlar.››"));
trsuperobject.biblemap.put("b24_38_20", new trsuperobject(24,38,20,"‹‹Vermezler›› diye yanıtladı Yeremya, ‹‹Lütfen sana aktardığım RABbin sözünü işit. O zaman sağ kalır, iyilik görürsün."));
trsuperobject.biblemap.put("b24_38_21", new trsuperobject(24,38,21,"Ama teslim olmak istemezsen, RAB bana şunu açıkladı:"));
trsuperobject.biblemap.put("b24_38_22", new trsuperobject(24,38,22,"Yahuda Kralının sarayında kalan bütün kadınlar Babil Kralının komutanlarına çıkarılacak. O kadınlar sana,  ‹‹Güvendiğin insanlarSeni aldatıp yenilgiye uğrattı;Çamura battı ayakların,Güvendiğin insanlar seni bırakıp gitti›"));
trsuperobject.biblemap.put("b24_38_23", new trsuperobject(24,38,23,"‹‹Bütün karıların, çocukların Kildanilere teslim edilecek. Sen de onlardan kaçıp kurtulamayacak, Babil Kralının eliyle yakalanacaksın. Bu kent ateşe verilecek.››"));
trsuperobject.biblemap.put("b24_38_24", new trsuperobject(24,38,24,"Sidkiya, ‹‹Ölmek istemiyorsan, konuştuklarımızı kimse duymasın›› dedi,"));
trsuperobject.biblemap.put("b24_38_25", new trsuperobject(24,38,25,"‹‹Görevliler seninle konuştuğumu duyup da gelir, ‹Krala ne söyledin, kral sana ne dedi, açıkla bize, bizden gizleme! Yoksa seni öldürürüz› derlerse,"));
trsuperobject.biblemap.put("b24_38_26", new trsuperobject(24,38,26,"‹Beni Yonatanın evine geri gönderme, yoksa orada ölürüm diye krala yalvardım› dersin.››"));
trsuperobject.biblemap.put("b24_38_27", new trsuperobject(24,38,27,"Bütün görevliler gelip Yeremyayı sorguya çektiler. Yeremya kralın kendisine söylemesini buyurduğu her şeyi onlara anlattı. Sorguyu bıraktılar. Çünkü kralla yaptığı konuşma duyulmamıştı."));
trsuperobject.biblemap.put("b24_38_28", new trsuperobject(24,38,28,"Yeremya Yeruşalim'in ele geçirildiği güne dek muhafız avlusunda kaldı."));
trsuperobject.biblemap.put("b24_39_1", new trsuperobject(24,39,1,"Yahuda Kralı Sidkiyanın dokuzuncu yılının onuncu ayında Babil Kralı Nebukadnessar bütün ordusuyla Yeruşalim önlerine gelerek kenti kuşattı."));
trsuperobject.biblemap.put("b24_39_2", new trsuperobject(24,39,2,"Sidkiyanın krallığının on birinci yılında, dördüncü ayın dokuzuncu günü kent surlarında gedik açıldı."));
trsuperobject.biblemap.put("b24_39_3", new trsuperobject(24,39,3,"Yeruşalim ele geçirilince Babil Kralının bütün komutanları -Samgarlı Nergal-Sareser, askeri danışman Nebo- Sarsekim, baş görevli Nergal-Sareser ve bütün öteki görevliler- içeri girip Orta Kapıda oturdular."));
trsuperobject.biblemap.put("b24_39_4", new trsuperobject(24,39,4,"Yahuda Kralı Sidkiyayla askerler onları görünce kaçtılar. Gece kral bahçesinin yolundan iki duvarın arasındaki kapıdan kaçarak Arava yoluna çıktılar."));
trsuperobject.biblemap.put("b24_39_5", new trsuperobject(24,39,5,"Ama artlarına düşen Kildani ordusu Eriha ovalarında Sidkiyaya yetişti, onu yakalayıp Hama topraklarında, Rivlada Babil Kralı Nebukadnessarın huzuruna çıkardılar. Nebukadnessar onun hakkında karar verdi:"));
trsuperobject.biblemap.put("b24_39_6", new trsuperobject(24,39,6,"Rivlada Sidkiyanın gözü önünde oğullarını, sonra da bütün Yahuda ileri gelenlerini öldürttü."));
trsuperobject.biblemap.put("b24_39_7", new trsuperobject(24,39,7,"Sidkiyanın gözlerini oydu, zincire vurup Babile götürdü."));
trsuperobject.biblemap.put("b24_39_8", new trsuperobject(24,39,8,"Kildaniler sarayla halkın evlerini ateşe verdiler, Yeruşalim surlarını yıktılar."));
trsuperobject.biblemap.put("b24_39_9", new trsuperobject(24,39,9,"Komutan Nebuzaradan kentte sağ kalanları, kendi safına geçen kaçakları ve geri kalan halkı Babile sürgün etti."));
trsuperobject.biblemap.put("b24_39_10", new trsuperobject(24,39,10,"Ancak hiçbir şeyi olmayan bazı yoksulları Yahudada bıraktı, onlara bağ ve tarla verdi."));
trsuperobject.biblemap.put("b24_39_11", new trsuperobject(24,39,11,"Babil Kralı Nebukadnessar, muhafız birliği komutanı Nebuzaradan aracılığıyla Yeremyayla ilgili şu buyruğu verdi:"));
trsuperobject.biblemap.put("b24_39_12", new trsuperobject(24,39,12,"‹‹Onu sorumluluğun altına al, ona iyi bak, hiç zarar verme, senden ne dilerse yap.››"));
trsuperobject.biblemap.put("b24_39_13", new trsuperobject(24,39,13,"Bunun üzerine muhafız birliği komutanı Nebuzaradan, askeri danışman Nebuşazban, baş görevli Nergal-Sareser ve Babil Kralının öbür görevlileri"));
trsuperobject.biblemap.put("b24_39_14", new trsuperobject(24,39,14,"adam gönderip Yeremyayı muhafız avlusundan getirttiler. Evine geri götürmesi için Şafan oğlu Ahikam oğlu Gedalyanın koruyuculuğuna verdiler. Böylece Yeremya halkı arasında yaşamını sürdürdü."));
trsuperobject.biblemap.put("b24_39_15", new trsuperobject(24,39,15,"Yeremya daha muhafız avlusunda tutukluyken RAB ona şöyle seslenmişti:"));
trsuperobject.biblemap.put("b24_39_16", new trsuperobject(24,39,16,"‹‹Git, Kûşlu Ebet-Meleke de ki, ‹İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: Bu kent üzerine yarar değil, zarar verecek sözlerimi yerine getirmek üzereyim. O gün olanları sen de göreceksin."));
trsuperobject.biblemap.put("b24_39_17", new trsuperobject(24,39,17,"Ama o gün seni kurtaracağım diyor RAB. Korktuğun adamların eline teslim edilmeyeceksin."));
trsuperobject.biblemap.put("b24_39_18", new trsuperobject(24,39,18,"Seni kesinlikle kurtaracağım, kılıçla öldürülmeyeceksin. Hiç değilse canını kurtarmış olacaksın. Çünkü bana güvendin, diyor RAB.››"));
trsuperobject.biblemap.put("b24_40_1", new trsuperobject(24,40,1,"Muhafız birliği komutanı Nebuzaradan Yeremyayı Ramada salıverdikten sonra RAB Yeremyaya seslendi. Nebuzaradan onu Babile sürülen Yeruşalim ve Yahuda halkıyla birlikte zincire vurulmuş olarak Ramaya götürmüştü."));
trsuperobject.biblemap.put("b24_40_2", new trsuperobject(24,40,2,"Muhafız birliği komutanı Yeremyayı yanına çağırtıp, ‹‹Tanrın RAB buraya karşı bu felaketi belirledi›› dedi,"));
trsuperobject.biblemap.put("b24_40_3", new trsuperobject(24,40,3,"‹‹Şimdi dediğini yaptı, yapacağını söylediği her şeyi yerine getirdi. Çünkü RABbe karşı günah işlediniz, Onun sözünü dinlemediniz. Bütün bunlar bu yüzden başınıza geldi."));
trsuperobject.biblemap.put("b24_40_4", new trsuperobject(24,40,4,"İşte ellerindeki zincirleri çözüyorum. Benimle Babile gelmeyi yeğlersen gel, sana iyi bakarım; eğer benimle Babile gelmek istemezsen de sorun yok. Bak, bütün ülke önünde! İyi ve doğru bildiğin yere git."));
trsuperobject.biblemap.put("b24_40_5", new trsuperobject(24,40,5,"Ama burada kalırsan Babil Kralının Yahuda kentlerine vali atadığı Şafan oğlu Ahikam oğlu Gedalyanın yanına dönüp onunla birlikte halkın arasında yaşa ya da istediğin yere git.›› ve››. Muhafız birliği komutanı Yeremyaya yiyecek ve armağan verip yoluna gönderdi."));
trsuperobject.biblemap.put("b24_40_6", new trsuperobject(24,40,6,"Yeremya Mispaya, Ahikam oğlu Gedalyanın yanına gitti. Onunla ve ülkede kalan halkla birlikte orada yaşamaya başladı."));
trsuperobject.biblemap.put("b24_40_7", new trsuperobject(24,40,7,"Kırdaki ordu komutanlarıyla adamları, Babil Kralının Ahikam oğlu Gedalyayı ülkeye vali atadığını, Babile sürülmemiş yoksul kadın, erkek ve çocukları ona emanet ettiğini duyunca,"));
trsuperobject.biblemap.put("b24_40_8", new trsuperobject(24,40,8,"Mispaya, Gedalyanın yanına geldiler. Gelenler Netanya oğlu İsmail, Kareahın oğulları Yohanan ve Yonatan, Tanhumet oğlu Seraya, Netofalı Efayın oğulları, Maakalı oğlu Yaazanya ve adamlarıydı."));
trsuperobject.biblemap.put("b24_40_9", new trsuperobject(24,40,9,"Şafan oğlu Ahikam oğlu Gedalya onlara ve adamlarına ant içerek, ‹‹Kildanilere kulluk etmekten korkmayın›› dedi, ‹‹Ülkeye yerleşip Babil Kralına hizmet edin. Böylesi sizin için daha iyi olur."));
trsuperobject.biblemap.put("b24_40_10", new trsuperobject(24,40,10,"Bana gelince, Mispada kalacağım, gelecek Kildanilerin önünde sizi temsil edeceğim. Siz şarap, yaz meyveleri, zeytinyağı toplayıp kaplarınızda depolayın ve aldığınız kentlerde yaşayın.››"));
trsuperobject.biblemap.put("b24_40_11", new trsuperobject(24,40,11,"Moav, Ammon, Edom ve öbür ülkelerde yaşayan Yahudilerin hepsi, Babil Kralının Yahudada bir kesim halkı sağ bıraktığını ve Şafan oğlu Ahikam oğlu Gedalyayı onlara vali atadığını duyunca,"));
trsuperobject.biblemap.put("b24_40_12", new trsuperobject(24,40,12,"sürülmüş oldukları ülkelerden geri dönüp Yahudaya, Mispada bulunan Gedalyanın yanına geldiler. Sonra bol bol şarap ve yaz meyvesi topladılar."));
trsuperobject.biblemap.put("b24_40_13", new trsuperobject(24,40,13,"Kareah oğlu Yohananla kırdaki bütün ordu komutanları da Mispada bulunan Gedalyanın yanına geldiler."));
trsuperobject.biblemap.put("b24_40_14", new trsuperobject(24,40,14,"Ona, ‹‹Ammon Kralı Baalisin seni öldürmek için Netanya oğlu İsmaili gönderdiğini bilmiyor musun?›› dediler. Gelgelelim Ahikam oğlu Gedalya onlara inanmadı."));
trsuperobject.biblemap.put("b24_40_15", new trsuperobject(24,40,15,"Kareah oğlu Yohanan Mispada Gedalyaya gizlice, ‹‹İzin ver de gidip Netanya oğlu İsmaili öldüreyim›› dedi, ‹‹Kimse bilmeyecek! Neden seni öldürsün de çevrende toplanan bütün Yahudiler dağılsın, Yahudada sağ kalmış olanlar yok olsun?››"));
trsuperobject.biblemap.put("b24_40_16", new trsuperobject(24,40,16,"Ama Ahikam oğlu Gedalya, ‹‹Böyle birşey yapma! İsmail'le ilgili söylediklerin yalan›› dedi."));
trsuperobject.biblemap.put("b24_41_1", new trsuperobject(24,41,1,"O yılın yedinci ayında kral soyundan ve kralın baş görevlilerinden Elişama oğlu Netanya oğlu İsmail, on adamıyla birlikte Mispaya, Ahikam oğlu Gedalyanın yanına gitti. Orada, Mispada birlikte yemek yerlerken,"));
trsuperobject.biblemap.put("b24_41_2", new trsuperobject(24,41,2,"Netanya oğlu İsmaille yanındaki on adam ayağa kalkıp Babil Kralının ülkeye vali atadığı Şafan oğlu Ahikam oğlu Gedalyayı kılıçla öldürdüler."));
trsuperobject.biblemap.put("b24_41_3", new trsuperobject(24,41,3,"İsmail Mispada Gedalyayla birlikte olan bütün Yahudileri ve oradaki Kildan askerlerini de öldürdü."));
trsuperobject.biblemap.put("b24_41_4", new trsuperobject(24,41,4,"Gedalya öldürüldükten bir gün sonra, ölüm haberi duyulmadan önce"));
trsuperobject.biblemap.put("b24_41_5", new trsuperobject(24,41,5,"Şekemden, Şilodan, Samiriyeden sakallarını tıraş etmiş, giysilerini yırtmış, bedenlerinde yaralar açmış seksen adam geldi. RABbin Tapınağında sunmak için yanlarında tahıl, günnük getirmişlerdi."));
trsuperobject.biblemap.put("b24_41_6", new trsuperobject(24,41,6,"Netanya oğlu İsmail Mispadan ağlaya ağlaya onları karşılamaya çıktı. Onları görünce, ‹‹Ahikam oğlu Gedalyaya gelin›› dedi."));
trsuperobject.biblemap.put("b24_41_7", new trsuperobject(24,41,7,"Kente girince, Netanya oğlu İsmail ve yanındakiler onları öldürüp bir sarnıca attılar."));
trsuperobject.biblemap.put("b24_41_8", new trsuperobject(24,41,8,"Ancak onlardan on kişi İsmaile, ‹‹Bizi öldürme!›› dediler, ‹‹Tarlada saklı buğdayımız, arpamız, zeytinyağımız ve balımız var.›› Böylece İsmail vazgeçip onları öbürleriyle birlikte öldürmedi."));
trsuperobject.biblemap.put("b24_41_9", new trsuperobject(24,41,9,"İsmailin öldürdüğü adamların bedenlerini atmış olduğu sarnıç büyüktü. Kral Asa, bu sarnıcı İsrail Kralı Baaşadan korunmak için kazmıştı. Netanya oğlu İsmail orayı ölülerle doldurdu. aracılığıyla››."));
trsuperobject.biblemap.put("b24_41_10", new trsuperobject(24,41,10,"İsmail, muhafız birliği komutanı Nebuzaradanın Ahikam oğlu Gedalyanın sorumluluğuna bıraktığı Mispadaki bütün halkı ve kral kızlarını tutsak aldı. Netanya oğlu İsmail tümünü tutsak alıp Ammonlulara sığınmak üzere yola çıktı."));
trsuperobject.biblemap.put("b24_41_11", new trsuperobject(24,41,11,"Kareah oğlu Yohanan ve yanındaki ordu komutanları, Netanya oğlu İsmailin işlediği cinayetleri duyunca,"));
trsuperobject.biblemap.put("b24_41_12", new trsuperobject(24,41,12,"bütün adamlarını alıp Netanya oğlu İsmaille savaşmaya gittiler. Givondaki büyük havuzun yakınında ona yetiştiler."));
trsuperobject.biblemap.put("b24_41_13", new trsuperobject(24,41,13,"İsmailin yanındaki adamlar, Kareah oğlu Yohanan ve yanındaki ordu komutanlarını görünce sevindiler."));
trsuperobject.biblemap.put("b24_41_14", new trsuperobject(24,41,14,"İsmailin Mispadan tutsak olarak götürdüğü herkes geri dönüp Kareah oğlu Yohanana katıldı."));
trsuperobject.biblemap.put("b24_41_15", new trsuperobject(24,41,15,"Netanya oğlu İsmail ve sekiz adamıysa Yohanandan kaçıp Ammonlulara sığındılar."));
trsuperobject.biblemap.put("b24_41_16", new trsuperobject(24,41,16,"Kareah oğlu Yohananla yanındaki ordu komutanları sağ kalanların hepsini -Ahikam oğlu Gedalyayı öldüren Netanya oğlu İsmailden kurtarıp Givondan geri getirdiği yiğit askerleri, kadınları, çocukları, saray görevlilerini- Mispadan alıp götürdüler."));
trsuperobject.biblemap.put("b24_41_17", new trsuperobject(24,41,17,"Kildaniler'den kaçmak için Mısır'a doğru yola çıktılar. Beytlehem yakınında, Gerut-Kimham'da durdular. Kildaniler'den korkuyorlardı. Çünkü Netanya oğlu İsmail, Babil Kralı'nın ülkeye vali atadığı Ahikam oğlu Gedalya'yı öldürmüştü."));
trsuperobject.biblemap.put("b24_42_1", new trsuperobject(24,42,1,"Ordu komutanları, Kareah oğlu Yohanan, Hoşaya oğlu Azarya ve küçük büyük bütün halk yaklaşıp"));
trsuperobject.biblemap.put("b24_42_2", new trsuperobject(24,42,2,"Peygamber Yeremyaya şöyle dediler: ‹‹Lütfen dileğimizi kabul et! Bizim için, bütün sağ kalan bu halk için Tanrın RABbe yakar. Çünkü bir zamanlar sayıca çok olan bizler gördüğün gibi şimdi azınlıkta kaldık."));
trsuperobject.biblemap.put("b24_42_3", new trsuperobject(24,42,3,"Tanrın RAB nereye gideceğimizi, ne yapacağımızı bize bildirsin.›› 43:2)."));
trsuperobject.biblemap.put("b24_42_4", new trsuperobject(24,42,4,"Peygamber Yeremya, ‹‹Olur›› dedi, ‹‹İsteğiniz uyarınca Tanrınız RABbe yakaracağım. RAB bana ne yanıt verirse, bir şey saklamadan size bildireceğim.››"));
trsuperobject.biblemap.put("b24_42_5", new trsuperobject(24,42,5,"Bunun üzerine, ‹‹Tanrın RABbin senin aracılığınla bize bildireceği her sözü yerine getirmezsek, RAB aramızda gerçek ve güvenilir tanık olsun›› dediler,"));
trsuperobject.biblemap.put("b24_42_6", new trsuperobject(24,42,6,"‹‹Seni kendisine gönderdiğimiz Tanrımız RABbin sözünü beğensek de beğenmesek de dinleyeceğiz ki, üzerimize iyilik gelsin. Evet, Tanrımız RABbin sözünü dinleyeceğiz.››"));
trsuperobject.biblemap.put("b24_42_7", new trsuperobject(24,42,7,"On gün sonra RAB Yeremyaya seslendi."));
trsuperobject.biblemap.put("b24_42_8", new trsuperobject(24,42,8,"Yeremya, Kareah oğlu Yohananla yanındaki ordu komutanlarını ve küçük büyük bütün halkı çağırdı."));
trsuperobject.biblemap.put("b24_42_9", new trsuperobject(24,42,9,"Onlara şöyle dedi: ‹‹Dileğinizi önüne sunmam için beni kendisine gönderdiğiniz İsrailin Tanrısı RAB diyor ki,"));
trsuperobject.biblemap.put("b24_42_10", new trsuperobject(24,42,10,"‹Bu ülkede kalırsanız, sizi bina ederim, yıkmam; dikerim, sökmem. Çünkü başınıza getirdiğim felakete üzülüyorum."));
trsuperobject.biblemap.put("b24_42_11", new trsuperobject(24,42,11,"Korktuğunuz Babil Kralından artık korkmayın, ondan korkmayın diyor RAB. Çünkü ben sizinleyim, sizi kurtaracak, onun elinden özgür kılacağım."));
trsuperobject.biblemap.put("b24_42_12", new trsuperobject(24,42,12,"Size sevecenlik göstereceğim. Şöyle ki, Babil Kralı size acıyacak, sizi topraklarınıza geri gönderecek.›"));
trsuperobject.biblemap.put("b24_42_13", new trsuperobject(24,42,13,"‹‹Ama, ‹Bu ülkede kalmayacağız› der, Tanrınız RABbin sözünü dinlemezseniz,"));
trsuperobject.biblemap.put("b24_42_14", new trsuperobject(24,42,14,"‹Savaş görmeyeceğimiz, boru sesi duymayacağımız, açlık çekmeyeceğimiz Mısıra gidip orada yaşayacağız› derseniz,"));
trsuperobject.biblemap.put("b24_42_15", new trsuperobject(24,42,15,"RABbin sözünü dinleyin, ey Yahudadan sağ kalanlar! İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: ‹Eğer Mısıra gidip orada yerleşmeye kesin kararlıysanız,"));
trsuperobject.biblemap.put("b24_42_16", new trsuperobject(24,42,16,"korktuğunuz kılıç size orada yetişecek, tasalandığınız kıtlık Mısırda yakanıza yapışacak, orada öleceksiniz."));
trsuperobject.biblemap.put("b24_42_17", new trsuperobject(24,42,17,"Yerleşmek üzere Mısıra gitmeye kararlı olan herkes kılıçtan, kıtlıktan, salgın hastalıktan ölecek. Başlarına getireceğim felaketten kurtulup sağ kalan olmayacak.›"));
trsuperobject.biblemap.put("b24_42_18", new trsuperobject(24,42,18,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Öfkem, kızgınlığım Yeruşalimde yaşayanların üzerine döküldüğü gibi, siz Mısıra gidenlerin üzerine de dökülecek. Siz lanetlik, dehşet konusu olacak, aşağılanacak, yerileceksiniz. Burayı bir daha görmeyeceksiniz.›"));
trsuperobject.biblemap.put("b24_42_19", new trsuperobject(24,42,19,"Ey Yahudadan sağ kalanlar, RAB size, ‹Mısıra gitmeyin!› diye buyurmuştur. Bunu iyi bilin. Bugün sizi uyarıyorum:"));
trsuperobject.biblemap.put("b24_42_20", new trsuperobject(24,42,20,"Beni Tanrınız RABbe gönderip, ‹Bizim için Tanrımız RABbe yakar. Onun bize söyleyeceği her şeyi bildir, yapacağız› demekle kendinizi aldatıyorsunuz!"));
trsuperobject.biblemap.put("b24_42_21", new trsuperobject(24,42,21,"Bugün size bildirdim, ama Tanrınız RABbin benim aracılığımla size ilettiği sözlerin hiçbirini dinlemediniz."));
trsuperobject.biblemap.put("b24_42_22", new trsuperobject(24,42,22,"Şimdi iyi bilin ki, yerleşmek üzere gitmeye can attığınız yerde kılıçtan, kıtlıktan, salgın hastalıktan öleceksiniz.››"));
trsuperobject.biblemap.put("b24_43_1", new trsuperobject(24,43,1,"Yeremya Tanrıları RABbin bütün bu sözlerini -Tanrıları RABbin onun aracılığıyla kendilerine ilettiği her şeyi- halka bildirmeyi bitirince"));
trsuperobject.biblemap.put("b24_43_2", new trsuperobject(24,43,2,"Hoşaya oğlu Azarya, Kareah oğlu Yohanan ve bütün küstah adamlar ona, ‹‹Yalan söylüyorsun!›› dediler, ‹‹Tanrımız RAB, ‹Yerleşmek üzere Mısıra gitmeyin› demek için göndermedi seni bize."));
trsuperobject.biblemap.put("b24_43_3", new trsuperobject(24,43,3,"Bizi öldürsünler, Babile sürsünler diye Kildanilerin eline teslim etmek için Neriya oğlu Baruk seni bize karşı kışkırtıyor.››"));
trsuperobject.biblemap.put("b24_43_4", new trsuperobject(24,43,4,"Böylece Kareah oğlu Yohanan, bütün ordu komutanları ve halk RABbin Yahudada kalmalarına ilişkin buyruğuna karşı geldiler."));
trsuperobject.biblemap.put("b24_43_5", new trsuperobject(24,43,5,"Kareah oğlu Yohananla bütün ordu komutanları, sürüldükleri uluslardan yerleşmek üzere Yahudaya geri dönen Yahuda halkını alıp götürdüler."));
trsuperobject.biblemap.put("b24_43_6", new trsuperobject(24,43,6,"Muhafız birliği komutanı Nebuzaradanın Şafan oğlu Ahikam oğlu Gedalyanın sorumluluğuna bırakmış olduğu bütün kadınları, erkekleri, çocukları, kral kızlarını da götürdüler. Peygamber Yeremyayla Neriya oğlu Baruku da alıp"));
trsuperobject.biblemap.put("b24_43_8", new trsuperobject(24,43,8,"Tahpanheste RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_43_9", new trsuperobject(24,43,9,"‹‹Yahudilerin gözü önünde eline büyük taşlar al, Tahpanheste firavun sarayının girişindeki tuğla kaldırımın harcına göm."));
trsuperobject.biblemap.put("b24_43_10", new trsuperobject(24,43,10,"Onlara de ki, ‹İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor: İşte kulum Babil Kralı Nebukadnessarı buraya getirtip tahtını harca gömdüğüm bu taşların üzerine kuracağım. Nebukadnessar otağını bu taşların üzerine kuracak."));
trsuperobject.biblemap.put("b24_43_11", new trsuperobject(24,43,11,"Gelip Mısırı bozguna uğratacak.  Ölüm için ayrılanlar ölüme,  Sürgün için ayrılanlar sürgüne,  Kılıç için ayrılanlar kılıca gidecek."));
trsuperobject.biblemap.put("b24_43_12", new trsuperobject(24,43,12,"Mısır ilahlarının tapınaklarını ateşe verip yakacak, ilahları alıp götürecek. Çoban giysisiyle kendisini nasıl örterse, o da Mısırı öyle örtecek. Sonra oradan sağ salim çıkacak."));
trsuperobject.biblemap.put("b24_43_13", new trsuperobject(24,43,13,"Mısır'daki Güneş Tapınağı'nın dikili taşlarını kıracak, Mısır ilahlarının tapınaklarını ateşe verecek.›› metin ‹‹Yakacağım››."));
trsuperobject.biblemap.put("b24_44_1", new trsuperobject(24,44,1,"Mısırın Migdol, Tahpanhes, Noffı kentlerinde ve Patros bölgesinde yaşayan Yahudilere ilişkin RAB Yeremyaya şöyle seslendi:"));
trsuperobject.biblemap.put("b24_44_2-3", new trsuperobject(24,44,2-3,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, Yeruşalim ve Yahuda kentlerine getirdiğim bütün felaketleri gördünüz. İşte yaptıkları kötülük yüzünden kentler bugün yıkık; içlerinde oturan yok. Sizin de kendilerinin ve atalarının da önceden tanımadığınız başka ilahlara buhur yakıp taparak beni öfkelendirdiler."));
trsuperobject.biblemap.put("b24_44_4", new trsuperobject(24,44,4,"Peygamber kullarımı defalarca gönderip, ‹Nefret ettiğim bu iğrençlikleri yapmayın!› diyerek onları uyardım."));
trsuperobject.biblemap.put("b24_44_5", new trsuperobject(24,44,5,"Ama dinlemediler, kulak asmadılar. Kötülüklerinden dönmediler, başka ilahlara buhur yakmaktan vazgeçmediler."));
trsuperobject.biblemap.put("b24_44_6", new trsuperobject(24,44,6,"Bu yüzden kızgın öfkemi döktüm; Yahuda kentlerine, Yeruşalim sokaklarına karşı öfkem giderek şiddetlendi. Onlar bugün olduğu gibi yıkık ve ıssız bırakıldı."));
trsuperobject.biblemap.put("b24_44_7", new trsuperobject(24,44,7,"‹‹İsrailin Tanrısı RAB, Her Şeye Egemen Tanrı şöyle diyor: Neden bu büyük felaketi başınıza getiriyorsunuz? Kadın erkek, çoluk çocuk Yahuda halkından kesilip atılacak, sizden sağ kalan olmayacak."));
trsuperobject.biblemap.put("b24_44_8", new trsuperobject(24,44,8,"Yerleşmek üzere geldiğiniz Mısırda ellerinizin yaptıklarıyla, başka ilahlara buhur yakmakla beni öfkelendiriyorsunuz. Başınıza felaket getiriyorsunuz. Dünyadaki uluslarca aşağılanacak, yerileceksiniz."));
trsuperobject.biblemap.put("b24_44_9", new trsuperobject(24,44,9,"Yahudada, Yeruşalim sokaklarında atalarınızın, Yahuda krallarıyla karılarının, kendinizin, karılarınızın yaptığınız kötülükleri unuttunuz mu?"));
trsuperobject.biblemap.put("b24_44_10", new trsuperobject(24,44,10,"Bugüne dek pişmanlık duymadılar, benden korkmadılar. Size ve atalarınıza verdiğim yasa ve kurallar uyarınca yaşamadılar."));
trsuperobject.biblemap.put("b24_44_11", new trsuperobject(24,44,11,"‹‹Bu yüzden İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki: Başınıza yıkım getirmeye, bütün Yahuda halkını yok etmeye kararlıyım."));
trsuperobject.biblemap.put("b24_44_12", new trsuperobject(24,44,12,"Yerleşmek üzere Mısıra gelmeye kararlı olan Yahudanın sağ kalanlarını ele alacağım. Hepsi Mısırda yok olacak; kılıçtan geçirilecek ya da kıtlıktan ölecek. Küçük büyük hepsi kılıçtan, kıtlıktan ölecek. Lanetlenecek, dehşet konusu olacak, aşağılanacak, yerilecekler."));
trsuperobject.biblemap.put("b24_44_13", new trsuperobject(24,44,13,"Yeruşalimi cezalandırdığım gibi, Mısırda yaşayanları da kılıçla, kıtlıkla, salgın hastalıkla cezalandıracağım."));
trsuperobject.biblemap.put("b24_44_14", new trsuperobject(24,44,14,"Yerleşmek için Mısıra gelen Yahuda halkının sağ kalanlarından hiçbiri kurtulmayacak, hiç kimse sağ kalıp Yahudaya dönmeyecek. Yerleşmek üzere oraya dönmek isteseler de, kaçıp kurtulan birkaç kişi dışında dönen olmayacak.››"));
trsuperobject.biblemap.put("b24_44_15", new trsuperobject(24,44,15,"Karılarının başka ilahlara buhur yaktığını bilen erkekler, orada duran kadınlar, Mısırın Patros bölgesinde yaşayan bütün halk -ki büyük bir topluluktu- Yeremyaya şu karşılığı verdi:"));
trsuperobject.biblemap.put("b24_44_16", new trsuperobject(24,44,16,"‹‹RABbin adıyla bize söylediklerini dinlemeyeceğiz!"));
trsuperobject.biblemap.put("b24_44_17", new trsuperobject(24,44,17,"Tersine, yapacağımızı söylediğimiz her şeyi kesinlikle yapacağız: Gök Kraliçesine buhur yakacak, atalarımızın, krallarımızın, önderlerimizin ve kendimizin Yahuda kentlerinde, Yeruşalim sokaklarında yaptığımız gibi ona dökmelik sunular dökeceğiz. O zamanlar bol yiyeceğimiz vardı, her işimiz yolundaydı, sıkıntı çekmiyorduk."));
trsuperobject.biblemap.put("b24_44_18", new trsuperobject(24,44,18,"Oysa Gök Kraliçesine buhur yakmayı, dökmelik sunular dökmeyi bıraktığımız günden bu yana her yönden yokluk çekiyoruz; kılıçtan, kıtlıktan yok oluyoruz.››"));
trsuperobject.biblemap.put("b24_44_19", new trsuperobject(24,44,19,"Kadınlar, ‹‹Evet, Gök Kraliçesine buhur yakıp dökmelik sunular dökeceğiz! Ona benzer pideler pişirip kendisine dökmelik sunular döktüğümüzü kocalarımız bilmiyor muydu sanki?›› diye eklediler."));
trsuperobject.biblemap.put("b24_44_20", new trsuperobject(24,44,20,"Bunun üzerine Yeremya ona karşılık veren kadın erkek bütün halka şöyle dedi:"));
trsuperobject.biblemap.put("b24_44_21", new trsuperobject(24,44,21,"‹‹Sizin, atalarınızın, krallarınızın, önderlerinizin, ülke halkının Yahuda kentlerinde, Yeruşalim sokaklarında yaktığınız buhuru RAB unuttu mu? Haberi yok muydu?"));
trsuperobject.biblemap.put("b24_44_22", new trsuperobject(24,44,22,"RAB yaptığınız kötülüklere, iğrençliklere artık dayanamadığı için, bugün olduğu gibi ülkeniz aşağılanıp yerildi, kimsenin yaşamadığı dehşet verici bir viranelik oldu."));
trsuperobject.biblemap.put("b24_44_23", new trsuperobject(24,44,23,"Siz başka ilahlara buhur yaktınız, RABbe karşı günah işlediniz; Onun sözünü dinlemediniz, yasasına, kurallarına, antlaşma koşullarına uymadınız. Bu yüzden bugün olduğu gibi başınıza felaket geldi.››"));
trsuperobject.biblemap.put("b24_44_24", new trsuperobject(24,44,24,"Yeremya bütün halka, özellikle de kadınlara, ‹‹RABbin sözüne kulak verin, ey Mısırda yaşayan Yahudalılar›› dedi,"));
trsuperobject.biblemap.put("b24_44_25", new trsuperobject(24,44,25,"‹‹İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹Gök Kraliçesine buhur yakacağız, dökmelik sunular dökeceğiz, adaklarımızı kesinlikle yerine getireceğiz› diyerek siz de karılarınız da verdiğiniz sözü yerine getirdiniz. ‹‹Öyleyse verdiğiniz sözü tutun! Adadığınız adakları tümüyle yerine getirin!"));
trsuperobject.biblemap.put("b24_44_26", new trsuperobject(24,44,26,"Mısırda yaşayan Yahudiler, RABbin sözünü dinleyin! ‹Büyük adım üzerine ant içiyorum ki› diyor RAB, ‹Mısırda yaşayan Yahudilerden hiçbiri bundan böyle adımı ağzına alıp Egemen RABbin varlığı hakkı için diye ant içmeyecek."));
trsuperobject.biblemap.put("b24_44_27", new trsuperobject(24,44,27,"Çünkü onların yararını değil, zararını gözlüyorum; Mısırda yaşayan Yahudiler yok olana dek kılıçtan, kıtlıktan ölecek."));
trsuperobject.biblemap.put("b24_44_28", new trsuperobject(24,44,28,"Kılıçtan kurtulup da Mısırdan Yahudaya dönenlerin sayısı pek az olacak. Mısıra yerleşmeye gelen Yahuda halkından sağ kalanlar o zaman kimin sözünün yerine geldiğini anlayacak: Benim sözümün mü, yoksa onlarınkinin mi?"));
trsuperobject.biblemap.put("b24_44_29", new trsuperobject(24,44,29,"‹‹Başınıza yıkım getireceğim; sözümün yerine geleceğini bilesiniz diye› diyor RAB, ‹Sizi burada cezalandıracağıma ilişkin belirti şu olacak.›"));
trsuperobject.biblemap.put("b24_44_30", new trsuperobject(24,44,30,"RAB diyor ki, ‹Yahuda Kralı Sidkiya'yı can düşmanı Babil Kralı Nebukadnessar'ın eline nasıl teslim ettimse, Mısır Firavunu Hofra'yı da can düşmanlarının eline öyle teslim edeceğim.››"));
trsuperobject.biblemap.put("b24_45_1", new trsuperobject(24,45,1,"Yahuda Kralı Yoşiya oğlu Yehoyakimin dördüncü yılında Neriya oğlu Baruk, Peygamber Yeremyanın kendisine söylediği sözleri tomara yazdıktan sonra Yeremya ona şunları söyledi:"));
trsuperobject.biblemap.put("b24_45_2", new trsuperobject(24,45,2,"‹‹Ey Baruk, İsrailin Tanrısı RAB sana şöyle diyor:"));
trsuperobject.biblemap.put("b24_45_3", new trsuperobject(24,45,3,"Sen, ‹Vay başıma! Çünkü RAB acıma acı kattı. İnlemekten bitkin düştüm, bana rahat yok› dedin."));
trsuperobject.biblemap.put("b24_45_4", new trsuperobject(24,45,4,"‹‹RAB bana, ‹Ona şöyle diyeceksin› dedi: ‹RAB diyor ki, bütün ülkeyi yıkacağım; bina ettiğimi yıkacak, diktiğimi sökeceğim."));
trsuperobject.biblemap.put("b24_45_5", new trsuperobject(24,45,5,"Sana gelince, büyük şeyler peşinde mi koşuyorsun? Sakın koşma! Çünkü bütün halkın üzerine felaket getirmek üzereyim› diyor RAB, ‹Ama sen nereye gidersen git, canını bağışlayacağım.››"));
trsuperobject.biblemap.put("b24_46_2", new trsuperobject(24,46,2,"Mısıra ilişkin: Yahuda Kralı Yoşiya oğlu Yehoyakimin dördüncü yılında, Babil Kralı Nebukadnessarın Fırat kıyısında, Karkamışta yenilgiye uğrattığı Firavun Nekonun ordusuyla ilgili bildiri:"));
trsuperobject.biblemap.put("b24_46_3", new trsuperobject(24,46,3,"‹‹Küçük büyük kalkanları dizin,Savaşmak için ilerleyin!"));
trsuperobject.biblemap.put("b24_46_4", new trsuperobject(24,46,4,"Atları koşun, beygirlere binin!Miğferlerinizi takın, yerinizi alın!Mızraklarınızı cilalayın,Zırhlarınızı kuşanın!"));
trsuperobject.biblemap.put("b24_46_5", new trsuperobject(24,46,5,"Ne görüyorum?Dehşete düştüler, geri çekiliyorlar!Yiğitleri bozguna uğramış,Arkalarına bakmadan kaçışıyorlar.Her yer dehşet içinde›› diyor RAB."));
trsuperobject.biblemap.put("b24_46_6", new trsuperobject(24,46,6,"‹‹Ayağı tez olan kaçamıyor,Yiğit kaçıp kurtulamıyor.Kuzeyde, Fırat kıyısındaTökezleyip düştüler."));
trsuperobject.biblemap.put("b24_46_7", new trsuperobject(24,46,7,"Nil gibi yükselen,Irmak gibi suları çalkalanan kim?"));
trsuperobject.biblemap.put("b24_46_8", new trsuperobject(24,46,8,"Mısırdır Nil gibi yükselen,Irmak gibi suları çalkalanan.‹Yükselip yeryüzünü kaplayacağım;Kentleri de içlerinde oturanları daYok edeceğim› diyor Mısır."));
trsuperobject.biblemap.put("b24_46_9", new trsuperobject(24,46,9,"Şahlanın, ey atlar!Çılgınca saldırın, ey savaş arabaları!Ey kalkan taşıyan Kûşlu, Pûtlu yiğitler,Yay çeken Ludlular, ilerleyin!"));
trsuperobject.biblemap.put("b24_46_10", new trsuperobject(24,46,10,"‹‹Çünkü o gün Her Şeye Egemen Egemen RABbin günüdür.Düşmanlarından öç alması içinÖç günüdür.Kılıç doyana dek yiyecek,Kanlarını kana kana içecek.Çünkü Rab, Her Şeye Egemen RABKuzeyde, Fırat kıyısında kurban hazırlıyor."));
trsuperobject.biblemap.put("b24_46_11", new trsuperobject(24,46,11,"‹‹Ey erden kız Mısır,Gilata git de merhem al!Ama boşuna çok ilaç kullanıyorsun,Senin için şifa yok."));
trsuperobject.biblemap.put("b24_46_12", new trsuperobject(24,46,12,"Uluslar utancını duydu,Feryadınla doldu yeryüzü.Yiğit yiğide tökezleyipİkisi birlikte yere seriliyor.››"));
trsuperobject.biblemap.put("b24_46_13", new trsuperobject(24,46,13,"Babil Kralı Nebukadnessarın gelip Mısıra saldıracağına ilişkin RABbin Peygamber Yeremyaya bildirdiği söz şudur:"));
trsuperobject.biblemap.put("b24_46_14", new trsuperobject(24,46,14,"‹‹Mısırda bildirin,Migdolda duyurun,Nofta, Tahpanheste duyurun:‹Yerini al, hazırlan,Çünkü çevrendekileri yiyip bitiriyor kılıç!›"));
trsuperobject.biblemap.put("b24_46_15", new trsuperobject(24,46,15,"İlahın Apis neden kaçtı?Boğan neden ayakta kalamadı?Çünkü RAB onu yere serdi! ayakta kalamadı? Çünkü RAB onu yere serdi!›› (bkz. Septuaginta), Masoretik metin ‹‹Yiğitlerin neden yere serildi? Ayakta duracak halleri yok, çünkü RAB onları yere serdi!››"));
trsuperobject.biblemap.put("b24_46_16", new trsuperobject(24,46,16,"Boyuna tökezleyip birbirlerinin üzerine düşecekler.‹Kalkın, acımasızların kılıcı yüzünden halkımıza,Yurdumuza dönelim› diyecekler."));
trsuperobject.biblemap.put("b24_46_17", new trsuperobject(24,46,17,"‹Firavun yaygaracının biri,Fırsatı kaçırdı› diyecekler."));
trsuperobject.biblemap.put("b24_46_18", new trsuperobject(24,46,18,"‹‹Varlığım hakkı için›› diyor Kral,Adı Her Şeye Egemen RAB,‹‹Dağlar arasında Tavor Dağı nasılsa,Karmel Dağı deniz kıyısında nasılsa,Size saldıracak kişi de öyledir."));
trsuperobject.biblemap.put("b24_46_19", new trsuperobject(24,46,19,"Ey sizler, Mısırda yaşayanlar,Toplayın eşyanızı, sürgüne gideceksiniz!Nof öyle viran olup yanacak ki,Kimse oturmayacak içinde."));
trsuperobject.biblemap.put("b24_46_20", new trsuperobject(24,46,20,"‹‹Mısır güzel bir düve,Ama kuzeyden atsineği geliyor ona."));
trsuperobject.biblemap.put("b24_46_21", new trsuperobject(24,46,21,"Ücretli askerleri besili danalar gibi.Onlar da geri dönüp birlikte kaçacak,Yerlerinde durmayacaklar.Çünkü üzerlerine yıkım günü,Cezalandırılacakları an gelecek."));
trsuperobject.biblemap.put("b24_46_22", new trsuperobject(24,46,22,"Düşman ordusu ilerleyince,Mısır yılan gibi tıslayarak kaçacak.Ağaç kesen adamlar gibiBaltalarla ona saldıracaklar."));
trsuperobject.biblemap.put("b24_46_23", new trsuperobject(24,46,23,"Gür olsa bile kesecekler ormanını›› diyor RAB,‹‹Çünkü çekirgelerden daha çok onlar,Sayıya vurulamazlar."));
trsuperobject.biblemap.put("b24_46_24", new trsuperobject(24,46,24,"Mısır utandırılacak,Kuzey halkının eline teslim edilecek.››"));
trsuperobject.biblemap.put("b24_46_25", new trsuperobject(24,46,25,"İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki, ‹‹İşte No Kentinin ilahı Amonu, firavunu, Mısırla ilahlarını, krallarını ve firavuna güvenenleri cezalandırmak üzereyim."));
trsuperobject.biblemap.put("b24_46_26", new trsuperobject(24,46,26,"Hepsini can düşmanları Babil Kralı Nebukadnessarla görevlilerinin eline teslim edeceğim. Ama sonra, eskiden olduğu gibi insanlar yine Mısırda yaşayacak›› diyor RAB."));
trsuperobject.biblemap.put("b24_46_27", new trsuperobject(24,46,27,"‹‹Korkma, ey kulum Yakup,Yılma, ey İsrail.Çünkü seni uzak yerlerden,Soyunu sürgün edildiği ülkeden kurtaracağım.Yakup yine huzur ve güvenlik içinde olacak,Kimse onu korkutmayacak."));
trsuperobject.biblemap.put("b24_46_28", new trsuperobject(24,46,28,"Korkma, ey kulum Yakup,Çünkü ben seninleyim›› diyor RAB.‹‹Seni aralarına sürdüğüm ulusların hepsiniTümüyle yok etsem de,Seni büsbütün yok etmeyeceğim.Adaletle yola getirecek,Hiç cezasız bırakmayacağım seni.››"));
trsuperobject.biblemap.put("b24_47_1", new trsuperobject(24,47,1,"Firavun Gazzeye saldırmadan önce RABbin Peygamber Yeremyaya bildirdiği Filistlilere ilişkin söz şudur:"));
trsuperobject.biblemap.put("b24_47_3", new trsuperobject(24,47,3,"Dörtnala koşan aygırlarınToynak seslerinden,Savaş arabalarının takırtısından,Tekerleklerin gürültüsündenBabalar dönüp çocuklarına bakmayacak;Ellerinde derman kalmayacak."));
trsuperobject.biblemap.put("b24_47_4", new trsuperobject(24,47,4,"Çünkü Filistlilerin yok edileceği gün geliyor.Sur ve Saydaya yardım edebilecekSağ kalan herkes kesilip yok edilecek.RAB Kaftor kıyısından gelen FilistlilerinSağ kalanlarını yok edecek."));
trsuperobject.biblemap.put("b24_47_5", new trsuperobject(24,47,5,"Gazze yastan saçını yolacak,Aşkelon susturulacak.Ey ovada sağ kalanlar,Ne zamana dek bedenlerinizi yaralayacaksınız?"));
trsuperobject.biblemap.put("b24_47_6", new trsuperobject(24,47,6,"Ah, RABbin kılıcı!Yatışmana daha ne kadar zaman var?Dön kınına! Dur ve sessiz ol!"));
trsuperobject.biblemap.put("b24_47_7", new trsuperobject(24,47,7,"Ama RAB ona buyruk vermişken,Aşkelon'a, deniz kıyısınaSaldırmak üzere görevlendirmişkenKılıç nasıl yatışabilir?››"));
trsuperobject.biblemap.put("b24_48_1", new trsuperobject(24,48,1,"Moava ilişkin: İsrailin Tanrısı, Her Şeye Egemen RAB şöyle diyor:  ‹‹Vay Nevonun başına gelenlere!Çünkü viraneye çevrilecek.Kiryatayim utandırılacak, ele geçirilecek.Misgav utandırılacak, kırılıp dökülecek."));
trsuperobject.biblemap.put("b24_48_2", new trsuperobject(24,48,2,"Moav artık övülmeyecek,Heşbonda onun yıkımı için düzen kuracak,‹Haydi, şu Moav ulusuna son verelim› diyecekler.Ey Madmenfö, sen de susturulacaksın,Kılıç kovalayacak seni. çağrıştırıyor."));
trsuperobject.biblemap.put("b24_48_3", new trsuperobject(24,48,3,"Horonayimden feryat duyulacak:‹Kent mahvoldu, büyük yıkıma uğradı!›"));
trsuperobject.biblemap.put("b24_48_4", new trsuperobject(24,48,4,"‹‹Moav yıkılacak,Yavrularının ağlayışı duyulacak."));
trsuperobject.biblemap.put("b24_48_5", new trsuperobject(24,48,5,"Ağlaya ağlaya çıkıyorlar Luhit Yokuşundan,Horonayim inişindeYıkımın neden olduğu acı feryatlar duyuluyor."));
trsuperobject.biblemap.put("b24_48_6", new trsuperobject(24,48,6,"Kaçın, canınızı kurtarın!Çölde yaban eşeğifö gibi koşun! ağacı››."));
trsuperobject.biblemap.put("b24_48_7", new trsuperobject(24,48,7,"‹‹Evet, başarılarına, mal varlığına güvendiğin içinSen de ele geçirileceksin.İlahın Kemoş da kâhinleri ve görevlileriyle birlikteSürgün edilecek."));
trsuperobject.biblemap.put("b24_48_8", new trsuperobject(24,48,8,"Yok edici her kente uğrayacak,Tek kent kurtulmayacak.Vadi yerle bir olacak,Yayla altüst edilecek›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_9", new trsuperobject(24,48,9,"‹‹Moav toprağına tuz dökün, kısırlaşsın,Kentleri öyle viran olacak ki,Kimse yaşamayacak oralarda."));
trsuperobject.biblemap.put("b24_48_10", new trsuperobject(24,48,10,"Lanet olsun RABbin işini savsaklayana!Kılıcını kan dökmekten alıkoyana lanet olsun!"));
trsuperobject.biblemap.put("b24_48_11", new trsuperobject(24,48,11,"Moav gençliğinden bu yana güvenlikteydi,Şarap tortusu gibi durgun kaldı,Bir kaptan öbürüne boşaltılmadı,Sürgüne gönderilmedi.O yüzden tadını yitirmedi, kokusu bozulmadı."));
trsuperobject.biblemap.put("b24_48_12", new trsuperobject(24,48,12,"‹‹Ama onu boşaltacak adamları göndereceğim günler geliyor›› diyor RAB, ‹‹Onu boşaltacaklar. Kaplarını boşaltacak, küplerini paramparça edecekler."));
trsuperobject.biblemap.put("b24_48_13", new trsuperobject(24,48,13,"İsrail halkı güvendiği Beytelden nasıl utandıysa, Moav da Kemoş ilahından öyle utanacak."));
trsuperobject.biblemap.put("b24_48_14", new trsuperobject(24,48,14,"‹‹Nasıl, ‹Biz yiğidiz,Savaşa hazır askerleriz› dersiniz?"));
trsuperobject.biblemap.put("b24_48_15", new trsuperobject(24,48,15,"Moav ve kentlerini yerle bir eden,Saldırıya geçti.En seçkin gençleri kesime gidecek.Adı Her Şeye Egemen RAB olan Kral böyle diyor."));
trsuperobject.biblemap.put("b24_48_16", new trsuperobject(24,48,16,"Moavın yıkımı yakında geliyor,Uğrayacağı felaket hızla yaklaşıyor."));
trsuperobject.biblemap.put("b24_48_17", new trsuperobject(24,48,17,"Dövünün onun için,Ey çevresinde yaşayan, ününü bilen sizler!‹Kudret asası,Görkemli değnek nasıl da kırıldı!› deyin."));
trsuperobject.biblemap.put("b24_48_18", new trsuperobject(24,48,18,"‹‹Ey Divon Kentinde yaşayan halk,Görkeminden in,Kuru toprak üstünde otur.Çünkü Moavı yerle bir eden sana da saldıracak,Kalelerini yıkacak."));
trsuperobject.biblemap.put("b24_48_19", new trsuperobject(24,48,19,"Ey sen, Aroerde oturan,Yol kenarında dur da gözle!Kaçan adama, kurtulan kadına,‹Ne oldu?› diye sor."));
trsuperobject.biblemap.put("b24_48_20", new trsuperobject(24,48,20,"Moav utandırıldı, darmadağın oldu.Feryat et, haykır!Moavın yıkıldığını Arnon Vadisinde duyur."));
trsuperobject.biblemap.put("b24_48_21-24", new trsuperobject(24,48,21-24,"‹‹Yayladaki kentler -Holan, Yahas, Mefaat, Divon, Nevo, Beytdivlatayim, Kiryatayim, Beytgamul, Beytmeon, Keriyot, Bosra, uzak yakın bütün Moav kentleri- yargılanacak."));
trsuperobject.biblemap.put("b24_48_25", new trsuperobject(24,48,25,"Moavın boynuzu kesildi, kolu kırıldı›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_26", new trsuperobject(24,48,26,"‹‹Moavı sarhoş edin,Çünkü RABbe büyüklük tasladı.Moav kendi kusmuğunda yuvarlanacak,Alay konusu olacak."));
trsuperobject.biblemap.put("b24_48_27", new trsuperobject(24,48,27,"İsrail senin için gülünesi bir ulus mu oldu?Hırsızlar arasında mı yakalandı ki,Ondan söz ettikçe baş sallıyorsun?"));
trsuperobject.biblemap.put("b24_48_28", new trsuperobject(24,48,28,"‹‹Ey Moavda yaşayanlar,Kentlerinizi terk edip kayalara sığının.Uçurumun ağzında yuvasını yapanGüvercin gibi olun."));
trsuperobject.biblemap.put("b24_48_29", new trsuperobject(24,48,29,"Moavın ne denli gururlanıp büyüklendiğini,Kendini ne denli beğendiğini,Kibirlenip küstahlaştığını,Övünüp kabardığını duyduk."));
trsuperobject.biblemap.put("b24_48_30", new trsuperobject(24,48,30,"Küstahlığını biliyorum›› diyor RAB,‹‹Övünmesi boşunadır, yaptıkları da."));
trsuperobject.biblemap.put("b24_48_31", new trsuperobject(24,48,31,"Bu yüzden Moav için haykıracak,Bütün Moav için feryat edeceğim.Ağlayacağım Kîr-Hereset halkı için."));
trsuperobject.biblemap.put("b24_48_32", new trsuperobject(24,48,32,"Ey Sivma asması,Senin için Yazer halkından çok ağlayacağım.Filizlerin gölü aşıpYazere ulaştı.Yok edici yaz meyvelerini, üzümünü yok etti."));
trsuperobject.biblemap.put("b24_48_33", new trsuperobject(24,48,33,"Moavın meyve bahçelerinden, tarlalarındanSevinç ve neşe yok oldu.Üzüm sıkma çukurlarından şarap akışını durdurdum;Kimse sevinç çığlıklarıyla üzüm ezmiyor,Çığlıklar var, ama sevinç çığlıkları değil."));
trsuperobject.biblemap.put("b24_48_34", new trsuperobject(24,48,34,"‹‹Heşbon ve Elalenin haykırışlarıYahasa ulaşıyor.Soardan Horonayime,Eglat-Şelişiyaya dek çığlıklar yükseliyor.Çünkü Nimrim suları bile kurudu."));
trsuperobject.biblemap.put("b24_48_35", new trsuperobject(24,48,35,"Moavda puta tapılan yerlerdeSunu sunanları,İlahlarına buhur yakanlarıYok edeceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_36", new trsuperobject(24,48,36,"‹‹Bu yüzden yüreğim ney gibiİnliyor Moav için;Kîr-Hereset halkı için ney gibiİnliyor yüreğim.Çünkü elde ettikleri zenginlik uçup gitti."));
trsuperobject.biblemap.put("b24_48_37", new trsuperobject(24,48,37,"‹‹Herkes saçını sakalını kesecek,Elini yaralayacak,Beline çul saracak."));
trsuperobject.biblemap.put("b24_48_38", new trsuperobject(24,48,38,"Moav damlarında, meydanlarındaYalnız ağlayış var.Çünkü Moavı kimsenin beğenmediğiBir kap gibi kırdım›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_39", new trsuperobject(24,48,39,"‹‹Nasıl da darmadağın oldu Moav!Nasıl acıyla feryat ediyor!Nasıl da sırtını dönüyor utançtan!Moav çevresindekilere alay konusu,Dehşet verici bir örnek oldu.››"));
trsuperobject.biblemap.put("b24_48_40", new trsuperobject(24,48,40,"RAB diyor ki,‹‹Bakın! Düşman birden çullanan bir kartal gibiKanatlarını Moavın üzerine açacak."));
trsuperobject.biblemap.put("b24_48_41", new trsuperobject(24,48,41,"Keriyot ele geçirilecek,Kaleler alınacak.O gün Moavlı askerlerin yüreği,Doğum sancısı çeken kadının yüreği gibi olacak."));
trsuperobject.biblemap.put("b24_48_42", new trsuperobject(24,48,42,"Moav yıkıma uğrayacak,Halk olmaktan çıkacak;Çünkü RABbe karşı büyüklük tasladı."));
trsuperobject.biblemap.put("b24_48_43", new trsuperobject(24,48,43,"Önünde dehşet, çukur ve tuzak var,Ey Moav halkı!›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_44", new trsuperobject(24,48,44,"‹‹Dehşetten kaçan çukura düşecek,Çukurdan çıkan tuzağa yakalanacak;Çünkü Moavın üzerineCezalandırma yılını getireceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_48_45", new trsuperobject(24,48,45,"‹‹Heşbonun gölgesindeBitkin düşmüş kaçkınlar.Çünkü Heşbondan ateş,Sihonun ortasından alev çıktı;Moavlıların alınlarını,Kargaşa çıkaranların başlarını yakıp yok etti."));
trsuperobject.biblemap.put("b24_48_46", new trsuperobject(24,48,46,"Vay sana, ey Moav!İlah Kemoşun halkı yok oldu,Oğulların sürgüne gönderildi,Kızların tutsak alındı."));
trsuperobject.biblemap.put("b24_48_47", new trsuperobject(24,48,47,"Ama son günlerdeYine eski gönencine kavuşturacağım Moav'ı›› diyor RAB. Moav'ın yargısı burada sona eriyor."));
trsuperobject.biblemap.put("b24_49_2", new trsuperobject(24,49,2,"İşte bu nedenle›› diyor RAB,‹‹Ammonluların Rabba Kentine karşıSavaş narasını işittireceğim günler geliyor.Rabba ıssız bir höyük olacak,Köyleri ateşe verilecek.Böylece İsrail, kendisini mülk edinenleriMülk edinecek›› diyor RAB."));
trsuperobject.biblemap.put("b24_49_3", new trsuperobject(24,49,3,"‹‹Haykır, ey Heşbon!Ay Kenti yıkıldı!Feryat edin, ey Rabba kızları!Çul sarınıp yas tutun.Duvarların arasında oraya buraya koşuşun.Çünkü Molek kâhinleri ve görevlileriyle birlikteSürgüne gönderilecek."));
trsuperobject.biblemap.put("b24_49_4", new trsuperobject(24,49,4,"Verimli vadilerinle ne kadar övünüyorsun,Ey dönek kız!Servetine güvenerek,‹Bana kim saldırabilir?› diyorsun."));
trsuperobject.biblemap.put("b24_49_5", new trsuperobject(24,49,5,"Bütün çevrendenDehşet saçacağım üzerine››Diyor Her Şeye Egemen Egemen RAB.‹‹Her biriniz apar topar sürülecek,Kaçkınları toplayan olmayacak."));
trsuperobject.biblemap.put("b24_49_6", new trsuperobject(24,49,6,"Ama sonra AmmonlularıEski gönencine kavuşturacağım›› diyor RAB."));
trsuperobject.biblemap.put("b24_49_7", new trsuperobject(24,49,7,"Her Şeye Egemen RAB Edoma ilişkin şöyle diyor:  ‹‹Teman Kentinde bilgelik kalmadı mı artık?Akıllı kişilerde öğüt tükendi mi?Bilgelikleri yozlaştı mı?"));
trsuperobject.biblemap.put("b24_49_8", new trsuperobject(24,49,8,"Kaçın, geri dönün, derinliklere sığının,Ey Dedanda yaşayanlar!Çünkü Esavı cezalandırdığımdaBaşına felaket getireceğim."));
trsuperobject.biblemap.put("b24_49_9", new trsuperobject(24,49,9,"Üzüm toplayanlar bağına girseydi,Birkaç salkım bırakmazlar mıydı?Gece hırsızlar gelselerdi,Yalnızca gereksindiklerini çalmazlar mıydı?"));
trsuperobject.biblemap.put("b24_49_10", new trsuperobject(24,49,10,"Oysa ben Esavı çırılçıplak soyacak,Gizli yerlerini açığa çıkaracağım,Gizlenemeyecek.Çocukları, akrabaları, komşularıYıkıma uğrayacak.Kendisi de yok olacak!"));
trsuperobject.biblemap.put("b24_49_11", new trsuperobject(24,49,11,"Öksüz çocuklarını bırak,Ben yaşatırım onları.Dul kadınların da bana güvensinler.››"));
trsuperobject.biblemap.put("b24_49_12", new trsuperobject(24,49,12,"RAB diyor ki,‹‹Hak etmeyenler bile kâseyi içmek zorundayken,Sen mi cezasız kalacaksın?Hayır, cezasız kalmayacaksın,Kesinlikle içeceksin kâseyi."));
trsuperobject.biblemap.put("b24_49_13", new trsuperobject(24,49,13,"Adım üzerine ant içerim ki›› diyor RAB,‹‹Bosra dehşet konusu olacak, yerilecek,Viraneye dönecek, aşağılanacak.Bütün kentleri sonsuza dek yıkık kalacak.››"));
trsuperobject.biblemap.put("b24_49_14", new trsuperobject(24,49,14,"RABden bir haber aldım:Uluslara gönderdiği haberci,‹‹Edoma saldırmak için toplanın,Savaşa hazırlanın!›› diyor."));
trsuperobject.biblemap.put("b24_49_15", new trsuperobject(24,49,15,"‹‹Bak, seni uluslar arasında küçük düşüreceğim,İnsanlar seni hor görecek."));
trsuperobject.biblemap.put("b24_49_16", new trsuperobject(24,49,16,"Saçtığın dehşet ve yüreğindeki gururSeni aldattı.Sen ki, kaya kovuklarında yaşıyor,Tepenin doruğunu elinde tutuyorsun.Yuvanı kartal gibi yükseklerde kursan da,Oradan indireceğim seni›› diyor RAB. Edomun başkentinin adıydı."));
trsuperobject.biblemap.put("b24_49_17", new trsuperobject(24,49,17,"‹‹Edom dehşet konusu olacak,Oradan geçen herkes şaşkın şaşkın bakıpBaşına gelen belalardan ötürüOnunla alay edecek."));
trsuperobject.biblemap.put("b24_49_18", new trsuperobject(24,49,18,"Sodomla Gomorayı ve çevredeki köyleriNasıl yerle bir ettimse›› diyor RAB,‹‹Orada da kimse oturmayacak,İnsan oraya yerleşmeyecek."));
trsuperobject.biblemap.put("b24_49_19", new trsuperobject(24,49,19,"‹‹Şeria çalılıklarındanSulak otlağa çıkan aslan gibiEdomu bir anda yurdundan kovacağım.Seçeceğim kişiyi ona yönetici atayacağım.Var mı benim gibisi?Var mı bana dava açacak biri,Bana karşı duracak çoban?››"));
trsuperobject.biblemap.put("b24_49_20", new trsuperobject(24,49,20,"Bu yüzden RABbin Edoma karşı ne tasarladığını,Temanda yaşayanlara karşı ne amaçladığını işitin:‹‹Sürünün küçükleri bile sürülecek,Halkı yüzünden Edom otlakları çöle dönüştürülecek."));
trsuperobject.biblemap.put("b24_49_21", new trsuperobject(24,49,21,"Yıkılışlarının gürültüsünden yeryüzü titreyecek,Çığlıkları Kızıldenize dek duyulacak."));
trsuperobject.biblemap.put("b24_49_22", new trsuperobject(24,49,22,"Düşman kartal gibi üzerlerine çullanacak,Kanatlarını Bosraya karşı açacak.O gün Edomlu askerlerin yüreği,Doğum sancısı çeken kadının yüreği gibi olacak.››"));
trsuperobject.biblemap.put("b24_49_23", new trsuperobject(24,49,23,"Şama ilişkin:  ‹‹Hama ve Arpat utanacak,Çünkü kötü haber işittiler.Korkudan eridiler,Sessiz duramayan deniz gibiKaygıyla sarsıldılar."));
trsuperobject.biblemap.put("b24_49_24", new trsuperobject(24,49,24,"‹‹Şam güçsüz düştü,Kaçmak için döndü;Telaşa kapıldı,Doğuran kadın gibiSancı ve acılar sardı onu."));
trsuperobject.biblemap.put("b24_49_25", new trsuperobject(24,49,25,"Nasıl oldu da sevinç bulduğum ünlü kentTerk edilmedi?"));
trsuperobject.biblemap.put("b24_49_26", new trsuperobject(24,49,26,"Bu yüzden gençleri meydanlarda düşecek,Bütün savaşçıları susturulacak o gün››Diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b24_49_27", new trsuperobject(24,49,27,"‹‹Şam surlarını ateşe vereceğim,Yakıp yok edecek Ben-Hadatın saraylarını.››"));
trsuperobject.biblemap.put("b24_49_28", new trsuperobject(24,49,28,"Babil Kralı Nebukadnessarın bozguna uğrattığı Kedar ve Hasor krallıklarına ilişkin RAB şöyle diyor:  ‹‹Kalkın, Kedara saldırın,Doğu halkını yok edin."));
trsuperobject.biblemap.put("b24_49_29", new trsuperobject(24,49,29,"Çadırlarıyla sürüleri alınacak,Çadır perdeleri,Eşyalarıyla develeri alınıp götürülecek.İnsanlar, ‹Her yer dehşet içinde!› diye bağıracaklar onlara."));
trsuperobject.biblemap.put("b24_49_30", new trsuperobject(24,49,30,"Kaçın, uzaklaşın!Derinliklere sığının,Ey Hasorda oturanlar!›› diyor RAB.‹‹Çünkü Babil Kralı NebukadnessarSize düzen kurdu;Sizin için bir tasarısı var."));
trsuperobject.biblemap.put("b24_49_31", new trsuperobject(24,49,31,"Kalkın, tasasız ve güvenlik içindeYaşayan ulusa saldırın›› diyor RAB.‹‹Onun kent kapıları, sürgüleri yok,Halkı tek başına yaşıyor."));
trsuperobject.biblemap.put("b24_49_32", new trsuperobject(24,49,32,"Develeri yağma edilecek,Sayısız sürüleri çapul malı olacak.Zülüflerini kesenleriDört yana dağıtacağım,Her yandan felaket getireceğim başlarına›› diyor RAB."));
trsuperobject.biblemap.put("b24_49_33", new trsuperobject(24,49,33,"‹‹Çakalların uğrağı Hasor,Sonsuza dek viran kalacak,Orada kimse oturmayacak,İnsan oraya yerleşmeyecek.››"));
trsuperobject.biblemap.put("b24_49_34", new trsuperobject(24,49,34,"Yahuda Kralı Sidkiyanın krallığının başlangıcında RABbin Peygamber Yeremyaya bildirdiği Elama ilişkin söz şudur:"));
trsuperobject.biblemap.put("b24_49_35", new trsuperobject(24,49,35,"Her Şeye Egemen RAB diyor ki,‹‹Bakın, Elamın yayını,Asıl gücünü kıracağım."));
trsuperobject.biblemap.put("b24_49_36", new trsuperobject(24,49,36,"Üzerine göğün dört ucundanDört rüzgarı gönderecek,Halkını bu rüzgarlara dağıtacağım.Elam sürgünlerinin gitmediğiBir ulus kalmayacak."));
trsuperobject.biblemap.put("b24_49_37", new trsuperobject(24,49,37,"Düşmanlarının önünde,Can düşmanlarının önündeElamı darmadağın edeceğim.Başlarına felaket gönderecek,Şiddetli öfkemi yağdıracağım›› diyor RAB,‹‹Onları büsbütün yok edene dekPeşlerine kılıcı salacağım."));
trsuperobject.biblemap.put("b24_49_38", new trsuperobject(24,49,38,"Elamda tahtımı kuracak,Elam Kralıyla önderleriniYok edeceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_49_39", new trsuperobject(24,49,39,"‹‹Ama son günlerdeElam'ı eski gönencine kavuşturacağım›› diyor RAB."));
trsuperobject.biblemap.put("b24_50_2", new trsuperobject(24,50,2,"‹‹Uluslara duyurun, haberi bildirin!Sancak dikip duyurun, hiçbir şey gizlemeyin!‹Babil ele geçirilecek› deyin,‹İlahı Bel utandırılacak,İlahı Marduk paramparça olacak.Putları utandırılacak,İlahları paramparça olacak.›"));
trsuperobject.biblemap.put("b24_50_3", new trsuperobject(24,50,3,"Çünkü kuzeyden gelen bir ulus ona saldıracak,Ülkesini viran edecek.Orada kimse yaşamayacak,İnsan da hayvan da kaçıp gidecek."));
trsuperobject.biblemap.put("b24_50_4", new trsuperobject(24,50,4,"O günlerde, o zamanda›› diyor RAB,‹‹İsrail halkıyla Yahuda halkı birlikte gelecek;Tanrıları RABbi aramak içinAğlaya ağlaya gelecekler."));
trsuperobject.biblemap.put("b24_50_5", new trsuperobject(24,50,5,"Yüzleri Siyona dönük,Oraya giden yolu soracak,Kalıcı, unutulmaz bir antlaşmaylaRABbe bağlanmak için gelecekler."));
trsuperobject.biblemap.put("b24_50_6", new trsuperobject(24,50,6,"‹‹Halkım yitik koyunlardır,Çobanları onları baştan çıkardı.Dağlarda başıboş dolandırdılar onları,Dağ, tepe avare dolaştılar,Kendi ağıllarını unuttular."));
trsuperobject.biblemap.put("b24_50_7", new trsuperobject(24,50,7,"Kim bulduysa yedi onları.Düşmanları, ‹Biz suçlu değiliz› dediler,‹Çünkü onlar gerçek otlakları olan RABbe,Atalarının umudu RABbe karşı günah işlediler.›"));
trsuperobject.biblemap.put("b24_50_8", new trsuperobject(24,50,8,"‹‹Babilden kaçıp kurtulun!Kildan ülkesini terk edin,Sürüye yön veren teke gibi olun!"));
trsuperobject.biblemap.put("b24_50_9", new trsuperobject(24,50,9,"Çünkü birbiriyle anlaşmış büyük uluslarıKuzeydeki topraklardan kışkırtıpBabilin karşısına çıkaracağım.Babille savaşmak üzere karşısına dizilecek,Onu kuzeyden ele geçirecekler.Okları usta savaşçı oku gibidir,Hiçbiri boş dönmeyecek."));
trsuperobject.biblemap.put("b24_50_10", new trsuperobject(24,50,10,"Kildan ülkesi yağmaya uğrayacak,Onu yağmalayanlar mala doyacak›› diyor RAB."));
trsuperobject.biblemap.put("b24_50_11", new trsuperobject(24,50,11,"‹‹Ey mirasımı yağmalayan sizler!Madem sevinip coşuyorsunuz,Harman döven düve gibi sıçrıyor,Aygır gibi kişniyorsunuz;"));
trsuperobject.biblemap.put("b24_50_12", new trsuperobject(24,50,12,"Anneniz büyük utanca boğulacak,Sizi doğuranın yüzü kızaracak.Ulusların en önemsizi,Kurak, bozkır, çöl olacak."));
trsuperobject.biblemap.put("b24_50_13", new trsuperobject(24,50,13,"RABbin öfkesi yüzünden kimse yaşamayacak orada,Büsbütün ıssız kalacak.Her geçen, Babilin aldığı yaraları görünce şaşacak,Hayrete düşecek."));
trsuperobject.biblemap.put("b24_50_14", new trsuperobject(24,50,14,"Babilin çevresinde savaşmak üzere dizilin,Ey bütün yay çekenler!Oklarla saldırın ona, oklarınızı esirgemeyin!Çünkü o RABbe karşı günah işledi."));
trsuperobject.biblemap.put("b24_50_15", new trsuperobject(24,50,15,"Her yandan ona karşı savaş narası yükseltin!Teslim oldu, kuleleri düştü,Surları yerle bir oldu.Çünkü RABbin öcüdür bu.Ondan öç alın.Yaptığının aynısını yapın ona."));
trsuperobject.biblemap.put("b24_50_16", new trsuperobject(24,50,16,"Ekin ekeni biçim vakti orakçıyla birlikteBabilden atın.Zorbanın kılıcı yüzündenHerkes halkına dönsün,Ülkesine kaçsın.››"));
trsuperobject.biblemap.put("b24_50_17", new trsuperobject(24,50,17,"‹‹İsrail aslanların kovaladığıDağılmış bir sürüdür.Önce Asur Kralı yedi onu.Sonra Babil Kralı Nebukadnessar kemiklerini ezdi.››"));
trsuperobject.biblemap.put("b24_50_18", new trsuperobject(24,50,18,"Bu yüzden İsrailin Tanrısı,Her Şeye Egemen RAB diyor ki,‹‹Asur Kralını nasıl cezalandırdıysam,Babil Kralıyla ülkesini de öyle cezalandıracağım."));
trsuperobject.biblemap.put("b24_50_19", new trsuperobject(24,50,19,"İsraili yeniden otlağına kavuşturacağım,Karmelde, Başanda otlayacak;Efrayim ve Gilat dağlık bölgelerindeİstediği kadar yiyip doyacak."));
trsuperobject.biblemap.put("b24_50_20", new trsuperobject(24,50,20,"O günlerde, o zamanda›› diyor RAB,‹‹İsrailin suçu araştırılacakAma bulunamayacak;Yahudanın günahları da araştırılacakAma bulunamayacak.Çünkü sağ bıraktıklarımı bağışlayacağım.››"));
trsuperobject.biblemap.put("b24_50_21", new trsuperobject(24,50,21,"‹‹Meratayimfş ülkesine,Pekotta yaşayanlara saldır.Onları öldür, tümüyle yok et›› diyor RAB,‹‹Sana ne buyurduysam hepsini yap. gelir."));
trsuperobject.biblemap.put("b24_50_22", new trsuperobject(24,50,22,"Ülkede savaş, büyük yıkımGürültüsü duyuluyor."));
trsuperobject.biblemap.put("b24_50_23", new trsuperobject(24,50,23,"Dünyanın balyozuNasıl da kırılıp paramparça oldu!Babil uluslar arasında nasıl dehşet oldu!"));
trsuperobject.biblemap.put("b24_50_24", new trsuperobject(24,50,24,"Senin için tuzak kurdum, ey Babil,Bilmeden tuzağıma düştün.Bulunup yakalandın,Çünkü RABbe karşı çıktın."));
trsuperobject.biblemap.put("b24_50_25", new trsuperobject(24,50,25,"RAB silahhanesini açtı,Öfkesinin silahlarını çıkardı.Her Şeye Egemen Egemen RABbinKildan ülkesinde yapacağı iş var."));
trsuperobject.biblemap.put("b24_50_26", new trsuperobject(24,50,26,"Uzaktan ona saldırın.Ambarlarını açın,Mallarını tahıl gibi küme küme yığın.Tamamen yok edin onu,Geriye hiçbir şey kalmasın."));
trsuperobject.biblemap.put("b24_50_27", new trsuperobject(24,50,27,"Genç boğalarını öldürün,Kesime gitsinler!Vay başlarına!Çünkü onların günü,Cezalandırılma zamanı geldi."));
trsuperobject.biblemap.put("b24_50_28", new trsuperobject(24,50,28,"Dinleyin! Tanrımız RABbin öç aldığını,Tapınağının öcünü aldığınıBabilden kaçıp kurtulanlarSiyonda duyuruyorlar."));
trsuperobject.biblemap.put("b24_50_29", new trsuperobject(24,50,29,"‹‹Okçuları, yay gerenlerin hepsini çağırın Babile karşı,Çevresini kuşatın, kaçıp kurtulan olmasın.Yaptıklarına göre karşılık verin ona,Yaptıklarının aynısını yapın.Çünkü RABbe, İsrailin KutsalınaKüstahlık etti."));
trsuperobject.biblemap.put("b24_50_30", new trsuperobject(24,50,30,"Bu yüzden gençleri meydanlarda düşecek,Bütün savaşçıları susturulacak o gün›› diyor RAB."));
trsuperobject.biblemap.put("b24_50_31", new trsuperobject(24,50,31,"‹‹İşte, sana karşıyım, ey küstah!››Diyor Her Şeye Egemen Egemen RAB.‹‹Çünkü senin günün,Seni cezalandıracağım zaman geldi."));
trsuperobject.biblemap.put("b24_50_32", new trsuperobject(24,50,32,"Küstah tökezleyip düşecek,Onu kaldıran olmayacak.Kentlerini ateşe vereceğim,Bütün çevresini yakıp yok edecek.››"));
trsuperobject.biblemap.put("b24_50_33", new trsuperobject(24,50,33,"Her Şeye Egemen RAB şöyle diyor:‹‹İsrail halkı da Yahuda halkı daEziyet çekiyor.Onları tutsak edenler sıkı tutmuş,Salıvermek istemiyorlar."));
trsuperobject.biblemap.put("b24_50_34", new trsuperobject(24,50,34,"Ama onların Kurtarıcısı güçlüdür,Onun adı Her Şeye Egemen RABdir.Onların ülkesine huzur,Babilde yaşayanlaraysa kargaşalık getirmek içinDavalarını hararetle savunacak."));
trsuperobject.biblemap.put("b24_50_35", new trsuperobject(24,50,35,"‹‹Kildanilere karşı kılıç!›› diyor RAB,‹‹Babilde yaşayanlara, Babil önderlerine,Bilgelerine karşı kılıç!"));
trsuperobject.biblemap.put("b24_50_36", new trsuperobject(24,50,36,"Sahte peygamberlere karşı kılıç!Aptallıkları ortaya çıkacak.Yiğitlerine karşı kılıç!Şaşkına dönecek onlar."));
trsuperobject.biblemap.put("b24_50_37", new trsuperobject(24,50,37,"Atlarına, savaş arabalarınaAralarındaki yabancılara karşı kılıç!Hepsi kadın gibi ürkek olacak.Hazinelerine karşı kılıç!Yağma edilecek onlar."));
trsuperobject.biblemap.put("b24_50_38", new trsuperobject(24,50,38,"Sularına kuraklık!Kuruyacak sular.Çünkü Babil putlar ülkesidir,Korkunç putlar yüzünden halkı çıldırmış."));
trsuperobject.biblemap.put("b24_50_39", new trsuperobject(24,50,39,"‹‹Bu yüzden yabanıl hayvanlar, çakallar,Baykuşlar yaşayacak orada,Artık insan yaşamayacak,Kuşaklar boyu kimse oturmayacak."));
trsuperobject.biblemap.put("b24_50_40", new trsuperobject(24,50,40,"Sodomla Gomorayı ve çevredeki köyleriNasıl yerle bir ettimse›› diyor RAB,‹‹Orada da kimse oturmayacak,İnsan oraya yerleşmeyecek."));
trsuperobject.biblemap.put("b24_50_41", new trsuperobject(24,50,41,"İşte kuzeyden bir ordu geliyor.Dünyanın uçlarındanBüyük bir ulusVe birçok kral harekete geçiyor."));
trsuperobject.biblemap.put("b24_50_42", new trsuperobject(24,50,42,"Yay, pala kuşanmışlar,Gaddar ve acımasızlar.Atlara binmiş gelirken,Kükreyen denizi andırıyor sesleri.Savaşa hazır savaşçılarKarşına dizilecekler, ey Babil kızı!"));
trsuperobject.biblemap.put("b24_50_43", new trsuperobject(24,50,43,"Babil Kralı onların haberini aldı,Ellerinde derman kalmadı.Doğuran kadın gibiÜzüntü, sancı sardı onu."));
trsuperobject.biblemap.put("b24_50_44", new trsuperobject(24,50,44,"Şeria çalılıklarındanSulak otlağa çıkan aslan gibiKildanileri bir anda yurdundan kovacağım.Seçeceğim kişiyi oraya yönetici atayacağım.Var mı benim gibisi?Var mı bana dava açacak biri,Bana karşı duracak çoban?››"));
trsuperobject.biblemap.put("b24_50_45", new trsuperobject(24,50,45,"Bu yüzden RABbin Babile karşı ne tasarladığını,Kildan ülkesine karşı ne amaçladığını işitin:‹‹Sürünün küçükleri bile sürülecek,Halkı yüzünden otlakları çöle dönüştürülecek."));
trsuperobject.biblemap.put("b24_50_46", new trsuperobject(24,50,46,"‹Babil düştü› sesiyle yeryüzü titreyecek,Çığlığı uluslar arasında duyulacak.››"));
trsuperobject.biblemap.put("b24_51_2", new trsuperobject(24,51,2,"Tahıl savuranları göndereceğim Babile;Onu savurup ayıklasınlar,Ülkesini boşaltsınlar diye.Yıkım günü her yandan saldıracaklar ona."));
trsuperobject.biblemap.put("b24_51_3", new trsuperobject(24,51,3,"Okçu yayını germesin,Zırhını kuşanmasın.Onun gençlerini esirgemeyin!Ordusunu tümüyle yok edin."));
trsuperobject.biblemap.put("b24_51_4", new trsuperobject(24,51,4,"Kildan ülkesinde ölüler,Babil sokaklarında yaralılar serilecek yere."));
trsuperobject.biblemap.put("b24_51_5", new trsuperobject(24,51,5,"İsrailin Kutsalına karşıÜlkeleri suçla dolu olmasına karşın,Tanrıları Her Şeye Egemen RABİsrail ve Yahuda halklarını bırakmadı."));
trsuperobject.biblemap.put("b24_51_6", new trsuperobject(24,51,6,"Babilden kaçın!Herkes canını kurtarsın!Babilin suçu yüzünden yok olmayın!Çünkü RABbin öç alma zamanıdır,Ona hakkettiğini verecek."));
trsuperobject.biblemap.put("b24_51_7", new trsuperobject(24,51,7,"Babil RABbin elinde bir altın kâseydi,Bütün dünyayı sarhoş etti.Uluslar şarabını içtiler,Bu yüzden çıldırdılar."));
trsuperobject.biblemap.put("b24_51_8", new trsuperobject(24,51,8,"Ansızın düşüp paramparça olacak Babil,Yas tutun onun için!Yarasına merhem sürün, belki iyileşir."));
trsuperobject.biblemap.put("b24_51_9", new trsuperobject(24,51,9,"‹Babili iyileştirmek istedik, ama iyileşmedi.Bırakalım onu,Hepimiz kendi ülkemize dönelim.Çünkü onun yargısı göklere erişiyor,Bulutlara kadar yükseliyor."));
trsuperobject.biblemap.put("b24_51_10", new trsuperobject(24,51,10,"‹‹RAB haklı olduğumuzu gösterdi,Gelin, Tanrımız RABbin neler yaptığınıSiyonda anlatalım.›"));
trsuperobject.biblemap.put("b24_51_11", new trsuperobject(24,51,11,"‹‹Okları bileyin,Ok kılıflarını doldurun!RAB Med krallarını harekete geçirdi,Amacı Babili yok etmek.RAB öcünü, tapınağının öcünü alacak."));
trsuperobject.biblemap.put("b24_51_12", new trsuperobject(24,51,12,"Babil surlarına karşı sancak kaldırın!Muhafızları pekiştirin,Nöbetçileri yerleştirin,Pusu kurun!Çünkü RAB Babil halkı için söyledikleriniHem tasarladı hem de yerine getirdi."));
trsuperobject.biblemap.put("b24_51_13", new trsuperobject(24,51,13,"Ey sizler, akarsuların kıyısında yaşayan,Hazinesi bol olanlar,Sonunuz geldi, zamanınız doldu."));
trsuperobject.biblemap.put("b24_51_14", new trsuperobject(24,51,14,"Her Şeye Egemen RAB varlığı hakkı için ant içti:Seni çekirge sürüsüyle doldurur gibiAskerlerle dolduracağım.Sana karşı zafer çığlıkları atacaklar.››"));
trsuperobject.biblemap.put("b24_51_15", new trsuperobject(24,51,15,"‹‹Gücüyle yeryüzünü yaratan,Bilgeliğiyle dünyayı kuran,Aklıyla gökleri yayan RABdir."));
trsuperobject.biblemap.put("b24_51_16", new trsuperobject(24,51,16,"O gürleyince gökteki sular çağıldar,Yeryüzünün dört bucağından bulutlar yükseltir,Yağmur için şimşek çaktırır,Ambarlarından rüzgar estirir."));
trsuperobject.biblemap.put("b24_51_17", new trsuperobject(24,51,17,"Hepsi budala, bilgisiz.Her kuyumcu yaptığı puttan utanacak.O putlar yapmacıktır,Soluk yoktur onlarda."));
trsuperobject.biblemap.put("b24_51_18", new trsuperobject(24,51,18,"Yararsız, alay edilesi nesnelerdir,Cezalandırılınca yok olacaklar."));
trsuperobject.biblemap.put("b24_51_19", new trsuperobject(24,51,19,"Yakupun Payı onlara benzemez.Mirası olan oymak dahilHer şeye biçim veren Odur,Her Şeye Egemen RABdir adı."));
trsuperobject.biblemap.put("b24_51_20", new trsuperobject(24,51,20,"‹‹Sen benim savaş çomağım,Savaş silahımsın.Ulusları parçalayacak,Krallıkları yok edeceğim seninle."));
trsuperobject.biblemap.put("b24_51_21", new trsuperobject(24,51,21,"Seninle atlarla binicilerini,Savaş arabalarıyla sürücülerini kırıp ezeceğim."));
trsuperobject.biblemap.put("b24_51_22", new trsuperobject(24,51,22,"Erkeklerle kadınları,Gençlerle yaşlıları,Delikanlılarla genç kızları,"));
trsuperobject.biblemap.put("b24_51_23", new trsuperobject(24,51,23,"Çobanla sürüsünü,Çiftçiyle öküzlerini,Valilerle yardımcılarını darmadağın edeceğim."));
trsuperobject.biblemap.put("b24_51_24", new trsuperobject(24,51,24,"‹‹Babilde ve Kildan ülkesinde yaşayanlaraSiyonda yaptıkları bütün kötülüğün karşılığınıGözlerinizin önünde ödeteceğim›› diyor RAB."));
trsuperobject.biblemap.put("b24_51_25", new trsuperobject(24,51,25,"‹‹Ey yıkıcı dağ, sana karşıyım,Ey bütün dünyayı yıkan›› diyor RAB,‹‹Elimi sana karşı kaldırıpSeni uçuruma yuvarlayacak,Yanık bir dağa çevireceğim."));
trsuperobject.biblemap.put("b24_51_26", new trsuperobject(24,51,26,"Senden köşe taşı, temel taşı olmayacak,Çünkü sonsuza dek viran kalacaksın›› diyor RAB."));
trsuperobject.biblemap.put("b24_51_27", new trsuperobject(24,51,27,"‹‹Ülkeye sancak dikin!Uluslar arasında boru çalın!Ulusları Babille savaşmaya hazırlayın.Ararat, Minni, Aşkenaz krallıklarınıOna karşı toplayın.Ona karşı bir komutan atayın,Çekirge sürüsü kadar at gönderin üzerine."));
trsuperobject.biblemap.put("b24_51_28", new trsuperobject(24,51,28,"Ulusları -Med krallarını, valilerini,Bütün yardımcılarını,Yönetimi altındaki bütün ülkeleri-Onunla savaşmaya hazırlayın."));
trsuperobject.biblemap.put("b24_51_29", new trsuperobject(24,51,29,"Ülke titreyip kıvranıyor!Çünkü RABbin Babil diyarınıIssız bir viraneye çevirme amacıYerine gelmeli."));
trsuperobject.biblemap.put("b24_51_30", new trsuperobject(24,51,30,"Babil yiğitleri savaştan vazgeçti,Kalelerinde oturuyorlar.Güçleri tükendi,Ürkek kadınlara döndüler.Oturdukları yerler ateşe verildi,Kapı sürgüleri kırıldı."));
trsuperobject.biblemap.put("b24_51_31-32", new trsuperobject(24,51,31-32,"Babil Kralına ulak üstüne ulak,Haberci üstüne haberci geldi.‹Kent bütünüyle düştü,Irmak geçitleri tutuldu,Bataklıklar ateşe verildi,Askerler dehşete kapıldı› diye haber verdiler.››"));
trsuperobject.biblemap.put("b24_51_33", new trsuperobject(24,51,33,"İsrailin Tanrısı, Her Şeye Egemen RAB diyor ki,‹‹Zamanı gelince harman yeri nasıl çiğnenirse,Babil kızı da öyle olacak.Kısa süre sonra onun daBiçim zamanı gelecek.››"));
trsuperobject.biblemap.put("b24_51_34-35", new trsuperobject(24,51,34-35,"Siyon halkı, ‹‹Babil Kralı Nebukadnessar yuttu bizi, ezdi,Boş bir kaba çevirdi›› diyecek,‹‹Canavar gibi yuttu bizi,Güzel yemeklerimizle karnını doyurdu,Sonra bizi kustu.Bize ve yurttaşlarımıza yapılan zorbalıkBabilin başına gelsin.››Yeruşalim, ‹‹Dökülen kanımızın hesabıKildanilerden sorulsun›› diyecek."));
trsuperobject.biblemap.put("b24_51_36", new trsuperobject(24,51,36,"Bunun için RAB diyor ki,‹‹İşte davanızı ben savunacağım,Öcünüzü ben alacağım;Onun ırmağını kurutacak,Kaynağını keseceğim."));
trsuperobject.biblemap.put("b24_51_37", new trsuperobject(24,51,37,"Babil taş yığınına, çakal yuvasına dönecek,Dehşet ve alay konusu olacak.Kimse yaşamayacak orada."));
trsuperobject.biblemap.put("b24_51_38", new trsuperobject(24,51,38,"Halkı genç aslanlar gibi kükreyecek,Aslan yavruları gibi homurdanacak."));
trsuperobject.biblemap.put("b24_51_39", new trsuperobject(24,51,39,"Ama kızıştıklarında onlara şölen veripHepsini sarhoş edeceğim;Keyiflensinler,Uyanmayacakları sonsuz bir uykuyaDalsınlar diye›› diyor RAB."));
trsuperobject.biblemap.put("b24_51_40", new trsuperobject(24,51,40,"‹‹Onları kuzu gibi, koç ve teke gibiBoğazlanmaya götüreceğim.››"));
trsuperobject.biblemap.put("b24_51_41", new trsuperobject(24,51,41,"‹‹Şeşak nasıl alındı!Bütün dünyanın övünç kaynağı nasıl ele geçirildi!Uluslar arasında Babil nasıl dehşet oldu!"));
trsuperobject.biblemap.put("b24_51_42", new trsuperobject(24,51,42,"Deniz basacak Babili,Kabaran dalgalar örtecek."));
trsuperobject.biblemap.put("b24_51_43", new trsuperobject(24,51,43,"Kentleri viran olacak,Toprakları kimsenin yaşamadığı, geçmediğiKurak bir çöle dönecek."));
trsuperobject.biblemap.put("b24_51_44", new trsuperobject(24,51,44,"Babil ilahı Beli orada cezalandıracak,Yuttuğunu ona kusturacağım.Artık akın akın uluslar gelmeyecek ona.Babil surları yıkılacak."));
trsuperobject.biblemap.put("b24_51_45", new trsuperobject(24,51,45,"‹‹Oradan çık, ey halkım!Hepiniz canınızı kurtarın!Kaçın RABbin kızgın öfkesinden!"));
trsuperobject.biblemap.put("b24_51_46", new trsuperobject(24,51,46,"Ülkede duyacağınız söylentiler yüzündenCesaretinizi yitirmeyin, korkmayın.Bir yıl bir söylenti duyulur, ertesi yıl bir başkası;Ülkedeki zorbalıkla,Önderin öndere karşı çıktığıylaİlgili söylentiler yayılır."));
trsuperobject.biblemap.put("b24_51_47", new trsuperobject(24,51,47,"İşte bu yüzden Babilin putlarınıCezalandıracağım günler geliyor.Bütün ülke utandırılacak,Öldürülenler ülkenin ortasında yere serilecek."));
trsuperobject.biblemap.put("b24_51_48", new trsuperobject(24,51,48,"O zaman yer, gök ve onlardaki her şeyBabilin başına gelenlere sevinecek.Çünkü kuzeyden gelen yok edicilerSaldıracaklar ona›› diyor RAB."));
trsuperobject.biblemap.put("b24_51_49", new trsuperobject(24,51,49,"Yeremya şöyle diyor:‹‹İsrailin öldürülenleri yüzünden düşmelidir Babil.Yeryüzünde öldürülen herkes Babil yüzünden düştü."));
trsuperobject.biblemap.put("b24_51_50", new trsuperobject(24,51,50,"Ey sizler, kılıçtan kurtulanlar,Kaçın, oyalanmayın!RABbi anın uzaktan,Yeruşalimi düşünün!››"));
trsuperobject.biblemap.put("b24_51_51", new trsuperobject(24,51,51,"‹‹Rezil olduk, çünkü aşağılandık,Yüzümüz utanç içinde.Çünkü yabancılar RABbin TapınağınınKutsal yerlerine girmişler.››"));
trsuperobject.biblemap.put("b24_51_52", new trsuperobject(24,51,52,"‹‹Bu yüzden›› diyor RAB,‹‹Putlarını cezalandıracağım günler geliyor,Yaralılar inleyecek bütün ülkede."));
trsuperobject.biblemap.put("b24_51_53", new trsuperobject(24,51,53,"Babil göklere çıksa,Yüksekteki kalesini pekiştirse de,Yok edicileri göndereceğim üzerine›› diyor RAB."));
trsuperobject.biblemap.put("b24_51_54", new trsuperobject(24,51,54,"‹‹Babilden çığlık,Kildan ülkesinden büyük yıkım sesi duyuluyor."));
trsuperobject.biblemap.put("b24_51_55", new trsuperobject(24,51,55,"Çünkü RAB Babili yıkıma uğratıyor;Şamatasını susturuyor.Düşman engin sular gibi kükrüyor,Seslerinin gürültüsü yankılanıyor."));
trsuperobject.biblemap.put("b24_51_56", new trsuperobject(24,51,56,"Çünkü Babile karşı bir yok edici çıkacak;Yiğitleri tutsak olacak,Yayları paramparça edilecek.Çünkü RAB karşılık veren bir Tanrıdır,Her şeyin tam karşılığını verir."));
trsuperobject.biblemap.put("b24_51_57", new trsuperobject(24,51,57,"Babil önderlerini, bilgelerini, valilerini,Yardımcılarını, yiğitlerini öyle sarhoş edeceğim ki,Sonsuz bir uykuya dalacak, hiç uyanmayacaklar››Diyor adı Her Şeye Egemen RAB olan Kral."));
trsuperobject.biblemap.put("b24_51_58", new trsuperobject(24,51,58,"Her Şeye Egemen RAB diyor ki,‹‹Babilin kalın surları yerle bir edilecek,Yüksek kapıları ateşe verilecek.Halkların çektiği emek boşuna,Ulusların didinmesi ateşe yarayacak.››"));
trsuperobject.biblemap.put("b24_51_59", new trsuperobject(24,51,59,"Yahuda Kralı Sidkiyanın krallığının dördüncü yılında, baş görevli Mahseya oğlu Neriya oğlu Seraya Sidkiyayla birlikte Babile gittiğinde Peygamber Yeremya ona şu buyruğu verdi."));
trsuperobject.biblemap.put("b24_51_60", new trsuperobject(24,51,60,"Yeremya Babilin başına gelecek bütün felaketleri, Babile ilişkin bütün bu sözleri bir tomara yazmıştı."));
trsuperobject.biblemap.put("b24_51_61", new trsuperobject(24,51,61,"Yeremya Serayaya şöyle dedi: ‹‹Babile varır varmaz bütün bu sözleri okumayı unutma."));
trsuperobject.biblemap.put("b24_51_62", new trsuperobject(24,51,62,"De ki, ‹Ya RAB, burayı yıkacağını, içinde insan da hayvan da yaşamayacağını, ülkenin sonsuza dek viran kalacağını söyledin.›"));
trsuperobject.biblemap.put("b24_51_63", new trsuperobject(24,51,63,"Okumayı bitirince tomarı bir taşa bağlayıp Fırata fırlat."));
trsuperobject.biblemap.put("b24_51_64", new trsuperobject(24,51,64,"Sonra de ki, ‹Babil başına getireceğim felaket yüzünden batacak, bir daha kalkamayacak. Bitkin düşecekler.›› Yeremya'nın sözleri burada son buluyor."));
trsuperobject.biblemap.put("b24_52_1", new trsuperobject(24,52,1,"Sidkiya yirmi bir yaşında kral oldu ve Yeruşalimde on bir yıl krallık yaptı. Annesi Livnalı Yeremyanın kızı Hamutaldı."));
trsuperobject.biblemap.put("b24_52_2", new trsuperobject(24,52,2,"Yehoyakim gibi Sidkiya da RABbin gözünde kötü olanı yaptı."));
trsuperobject.biblemap.put("b24_52_4", new trsuperobject(24,52,4,"Sidkiyanın krallığının dokuzuncu yılında, onuncu ayın onuncu günü, Babil Kralı Nebukadnessar bütün ordusuyla Yeruşalim önlerine gelip ordugah kurdu. Kentin çevresine rampa yaptılar."));
trsuperobject.biblemap.put("b24_52_5", new trsuperobject(24,52,5,"Kral Sidkiyanın krallığının on birinci yılına kadar kent kuşatma altında kaldı."));
trsuperobject.biblemap.put("b24_52_6", new trsuperobject(24,52,6,"Dördüncü ayın dokuzuncu günü kentte kıtlık öyle şiddetlendi ki, halk bir lokma ekmek bulamaz oldu."));
trsuperobject.biblemap.put("b24_52_7", new trsuperobject(24,52,7,"Sonunda kentin surlarında bir gedik açıldı. Kildaniler kenti çepeçevre kuşatmış olmasına karşın, bütün askerler gece kral bahçesinin yolundan iki duvarın arasındaki kapıdan kaçarak Arava yoluna çıktılar."));
trsuperobject.biblemap.put("b24_52_8", new trsuperobject(24,52,8,"Ama Kildani ordusu Kral Sidkiyanın ardına düşerek Eriha ovalarında ona yetişti. Sidkiyanın bütün ordusu dağıldı."));
trsuperobject.biblemap.put("b24_52_9", new trsuperobject(24,52,9,"Kral Sidkiya yakalanıp Hama topraklarında, Rivlada Babil Kralının huzuruna çıkarıldı. Babil Kralı onun hakkında karar verdi."));
trsuperobject.biblemap.put("b24_52_10", new trsuperobject(24,52,10,"Sidkiyanın gözü önünde oğullarını, sonra da bütün Yahuda önderlerini öldürttü."));
trsuperobject.biblemap.put("b24_52_11", new trsuperobject(24,52,11,"Sidkiyanın gözlerini oydu, zincire vurup Babile götürdü. Sidkiya öldüğü güne dek cezaevinde tutuldu."));
trsuperobject.biblemap.put("b24_52_12", new trsuperobject(24,52,12,"Babil Kralı Nebukadnessarın krallığının on dokuzuncu yılında, beşinci ayın onuncu günü muhafız birliği komutanı, Babil Kralının görevlisi Nebuzaradan Yeruşalime girdi."));
trsuperobject.biblemap.put("b24_52_13", new trsuperobject(24,52,13,"RABbin Tapınağını, sarayı ve Yeruşalimdeki bütün evleri ateşe verip önemli yapıları yaktı."));
trsuperobject.biblemap.put("b24_52_14", new trsuperobject(24,52,14,"Muhafız birliği komutanı önderliğindeki Kildani ordusu Yeruşalimi çevreleyen bütün surları yıktı."));
trsuperobject.biblemap.put("b24_52_15", new trsuperobject(24,52,15,"Komutan Nebuzaradan yoksullardan bazılarını, kentte sağ kalanları, Babil Kralının safına geçen kaçakları ve zanaatçıları sürgün etti."));
trsuperobject.biblemap.put("b24_52_16", new trsuperobject(24,52,16,"Ancak bağcılık, çiftçilik yapsınlar diye bazı yoksulları orada bıraktı."));
trsuperobject.biblemap.put("b24_52_17", new trsuperobject(24,52,17,"Kildaniler RABbin Tapınağındaki tunç sütunları, ayaklıkları, tunç havuzu parçalayıp tunçları Babile götürdüler."));
trsuperobject.biblemap.put("b24_52_18", new trsuperobject(24,52,18,"Tapınak törenlerinde kullanılan kovaları, kürekleri, fitil maşalarını, çanakları, tabakları, bütün tunç eşyaları aldılar."));
trsuperobject.biblemap.put("b24_52_19", new trsuperobject(24,52,19,"Muhafız birliği komutanı saf altın ve gümüş tasları, buhurdanları, çanakları, kovaları, kandillikleri, tabakları, dökmelik sunu taslarını alıp götürdü."));
trsuperobject.biblemap.put("b24_52_20", new trsuperobject(24,52,20,"RABbin Tapınağı için Kral Süleymanın yaptırmış olduğu iki sütun, havuz ve altındaki on iki tunç boğa heykeliyle ayaklıklar için hesapsız tunç harcanmıştı."));
trsuperobject.biblemap.put("b24_52_21", new trsuperobject(24,52,21,"Her sütun on sekiz arşınfü yüksekliğindeydi, çevresi on iki arşındı. Her birinin kalınlığı dört parmaktı, içi boştu."));
trsuperobject.biblemap.put("b24_52_22", new trsuperobject(24,52,22,"Üzerinde tunç bir başlık vardı. Başlığın yüksekliği beş arşındı, çevresi tunçtan ağ ve nar motifleriyle bezenmişti. Öbür sütun da nar motifleriyle süslenmişti ve ötekine benziyordu."));
trsuperobject.biblemap.put("b24_52_23", new trsuperobject(24,52,23,"Yanlarda doksan altı nar motifi vardı. Başlığı çevreleyen ağ motifinin üzerinde toplam yüz nar motifi bulunuyordu."));
trsuperobject.biblemap.put("b24_52_24", new trsuperobject(24,52,24,"Muhafız birliği komutanı Nebuzaradan Başkâhin Serayayı, Başkâhin Yardımcısı Sefanyayı ve üç kapı nöbetçisini tutsak aldı."));
trsuperobject.biblemap.put("b24_52_25", new trsuperobject(24,52,25,"Kentte kalan askerlerin komutanını, kralın yedi danışmanını, ayrıca ülke halkını askere yazan ordu komutanının yazmanını ve ülke halkından kentte bulunan altmış kişiyi tutsak etti."));
trsuperobject.biblemap.put("b24_52_26", new trsuperobject(24,52,26,"Hepsini Rivlaya, Babil Kralının yanına götürdü."));
trsuperobject.biblemap.put("b24_52_27", new trsuperobject(24,52,27,"Babil Kralı Hama ülkesinde, Rivlada onları idam etti. Böylece Yahuda halkı ülkesinden sürülmüş oldu."));
trsuperobject.biblemap.put("b24_52_28", new trsuperobject(24,52,28,"Nebukadnessarın sürgüne götürdüğü halkın sayısı şudur:  Yedinci yıl 3 023 Yahudi;"));
trsuperobject.biblemap.put("b24_52_29", new trsuperobject(24,52,29,"Nebukadnessarın on sekizinci yılında Yeruşalimden 832 kişi;"));
trsuperobject.biblemap.put("b24_52_30", new trsuperobject(24,52,30,"yirmi üçüncü yılında, muhafız birliği komutanı Nebuzaradanın sürdüğü 745 Yahudi. Hepsi 4 600 kişiydi."));
trsuperobject.biblemap.put("b24_52_31", new trsuperobject(24,52,31,"Yahuda Kralı Yehoyakinin sürgündeki otuz yedinci yılı Evil-Merodak Babil Kralı oldu. Evil-Merodak o yılın on ikinci ayının yirmi beşinci günü, Yahuda Kralı Yehoyakine lütfederek onu cezaevinden çıkardı."));
trsuperobject.biblemap.put("b24_52_32", new trsuperobject(24,52,32,"Kendisiyle tatlı tatlı konuştu ve ona Babildeki öteki sürgün krallardan daha üstün bir yer verdi."));
trsuperobject.biblemap.put("b24_52_33", new trsuperobject(24,52,33,"Yehoyakin cezaevi giysilerini üstünden çıkardı. Yaşadığı sürece Babil Kralının sofrasında yer aldı."));
trsuperobject.biblemap.put("b24_52_34", new trsuperobject(24,52,34,"Yaşamı boyunca Babil Kralı tarafından günlük yiyeceği sürekli karşılandı."));

