trsuperobject.biblemap.put("b25_1_1", new trsuperobject(25,1,1,"O kent ki, insan doluydu,Nasıl da tek başına kaldı şimdi!Büyüktü uluslar arasında,Dul kadına döndü!Soyluydu iller arasında,Angarya altına düştü!"));
trsuperobject.biblemap.put("b25_1_2", new trsuperobject(25,1,2,"Geceleyin acı acı ağlıyor,Yanaklarında gözyaşı;Avutan tek kişi bile yokBunca oynaşı arasında.Dostları ona hainlik etti,Düşman oldu."));
trsuperobject.biblemap.put("b25_1_3", new trsuperobject(25,1,3,"Yahuda acı çekip ağır kölelik ettikten sonraSürgün edildi,Ulusların arasında oturuyor,Ama rahat bulamıyor.O sıkıntıdayken ardına düşenler ona yetişti."));
trsuperobject.biblemap.put("b25_1_4", new trsuperobject(25,1,4,"Siyona giden yollar yas tutuyor,Çünkü bayramlara gelen yok.Bütün kapıları ıssız, kâhinleri inliyor,Erden kızları sıkıntıda, kendisi de acı çekiyor."));
trsuperobject.biblemap.put("b25_1_5", new trsuperobject(25,1,5,"Hasımları başa geçti, düşmanları rahat içinde.Çok isyan ettiği için RAB ona acı çektiriyor,Yavruları hasımlarının gözü önünde sürgüne gitti."));
trsuperobject.biblemap.put("b25_1_6", new trsuperobject(25,1,6,"Siyon kızının bütün güzelliği uçtu,Önderleri otlak bulamayan geyiklere döndü,Dermanları kesildiKendilerini kovalayanların önünde."));
trsuperobject.biblemap.put("b25_1_7", new trsuperobject(25,1,7,"Yeruşalim sıkıntı içinde başıboş dolaşırkenEski günlerdeki varlığını anımsıyor.Halkı hasmının eline düşüp deYardımına koşan çıkmayınca,Hasımları haline bakıpYıkılışına güldüler."));
trsuperobject.biblemap.put("b25_1_8", new trsuperobject(25,1,8,"Yeruşalim büyük günah işledi,Bu yüzden kirlendi.Ona saygı duyanların hepsiŞimdi onu hor görüyor,Çünkü onu çıplak gördüler.O da inleyip öbür yana dönüyor."));
trsuperobject.biblemap.put("b25_1_9", new trsuperobject(25,1,9,"Kirliliği eteklerindeydi,Sonunu düşünmedi;Bu yüzden düşüşü korkunç oldu,Avutanı yok.‹‹Ya RAB, düşkün halimi gör,Çünkü düşmanım kazandı!››"));
trsuperobject.biblemap.put("b25_1_10", new trsuperobject(25,1,10,"Değerli her şeyine düşman el uzattı.Tapınağına başka ulusların girdiğini gördü,Topluluğuna girmesini yasakladığın uluslar."));
trsuperobject.biblemap.put("b25_1_11", new trsuperobject(25,1,11,"Halkı inleyip ekmek arıyor,Yeniden güçlerine kavuşmak içinDeğerli neleri varsa ekmekle değiştiler;‹‹Bak da gör, ya RAB, ne kadar sefil oldum.››"));
trsuperobject.biblemap.put("b25_1_12", new trsuperobject(25,1,12,"‹‹Ey sizler, yoldan geçenler,Sizin için önemi yok mu bunun?Bakın da görün, başıma gelen dert gibisi var mı?Öyle bir dert ki, RAB öfkesinin alevlendiği günBaşıma yağdırdı onu. başınıza da gelmesin.››"));
trsuperobject.biblemap.put("b25_1_13", new trsuperobject(25,1,13,"Ateş saldı yukarıdan,Kemiklerimin içine işledi ateş;Ağ serdi ayaklarıma,Geri çevirdi beni;Mahvetti, baygın kaldım bütün gün."));
trsuperobject.biblemap.put("b25_1_14", new trsuperobject(25,1,14,"İsyanlarım boyunduruğa döndü,RABbin eliyle birbirine tutturulupBoynuma geçirildi, gücüm tükendi.Rab karşı duramadığımİnsanların eline verdi beni."));
trsuperobject.biblemap.put("b25_1_15", new trsuperobject(25,1,15,"Hiçe saydı beni savunan yiğitleri,Gençlerimi kırıp geçirmek için çağrı yaptı ordulara,Rab erden Yahuda kızınıÜzüm sıkma çukurunda çiğnedi adeta."));
trsuperobject.biblemap.put("b25_1_16", new trsuperobject(25,1,16,"‹‹Ağlıyorum bunlara,Gözlerimden yaşlar boşanıyor;Çünkü beni avutan,Canımı tazeleyen benden uzak.Çocuklarım şaşkına döndü,Çünkü düşmanım üstün çıktı.››"));
trsuperobject.biblemap.put("b25_1_17", new trsuperobject(25,1,17,"Siyon ellerini açmış,Ama onu avutan yok.RAB Yakup soyuna karşı buyruk verdi,Komşuları ona hasım olsun, dedi.Yeruşalim aralarında paçavraya döndü."));
trsuperobject.biblemap.put("b25_1_18", new trsuperobject(25,1,18,"‹‹RAB haklıdır, çünkü buyruğuna karşı geldim.Şimdi dinleyin, ey halklar, çektiğim acıyı görün;Erden kızlarım, gençlerim sürgüne gitti."));
trsuperobject.biblemap.put("b25_1_19", new trsuperobject(25,1,19,"Oynaşlarımı çağırdım,Ama aldattılar beni.Yeniden güçlerine kavuşmak için yiyecek ararkenKâhinlerimle önderlerim kentte can verdi."));
trsuperobject.biblemap.put("b25_1_20", new trsuperobject(25,1,20,"Gör, ya RAB, ne sıkıntılar çektiğimi,İçim kanıyor, yüreğim buruk,Çünkü çok asilik ettim;Dışarıda kılıç beni çocuklarımdan ayırmakta,İçerdeyse ölüm kol gezmekte."));
trsuperobject.biblemap.put("b25_1_21", new trsuperobject(25,1,21,"İnlediğimi duydular,Beni avutan olmadı.Bütün düşmanlarım başıma gelen felaketi duydu,Sen yaptın diye sevinçten coştular.İlan ettiğin günü getir,Onlar da benim gibi olsunlar."));
trsuperobject.biblemap.put("b25_1_22", new trsuperobject(25,1,22,"Yaptıkları her kötülüğü anımsa,İsyanlarımdan ötürü bana ne yaptınsa onlara da yap;Çünkü sürekli inliyor, baygınlık geçiriyorum.››"));
trsuperobject.biblemap.put("b25_2_1", new trsuperobject(25,2,1,"Rab öfkelenince Siyon kızını nasıl bulutla kapladı!İsrailin görkemini gökten yere fırlattı,Öfkelendiği gün ayağının taburesini anımsamadı."));
trsuperobject.biblemap.put("b25_2_2", new trsuperobject(25,2,2,"Yakup soyunun yaşadığı her yeri acımadan yuttu,Yahuda kızının surlu kentlerini gazabıyla yıktı,Yerle bir etti onları,Krallığını ve önderlerini alçalttı."));
trsuperobject.biblemap.put("b25_2_3", new trsuperobject(25,2,3,"Kızgın öfkesiyle İsrailin gücünü kökünden kesti,Düşmanın önünde sağ elini onların üstünden çekti,Çevresini yiyip bitiren alevli ateş gibi Yakup soyunu yaktı."));
trsuperobject.biblemap.put("b25_2_4", new trsuperobject(25,2,4,"Düşman gibi yayını gerdi,Hasım gibi sağ elini kaldırdı,Göz zevkini okşayan herkesi öldürdü,Gazabını Siyon kızının çadırı üstüne ateş gibi döktü."));
trsuperobject.biblemap.put("b25_2_5", new trsuperobject(25,2,5,"Rab adeta bir düşman olup İsraili yuttu,Bütün saraylarını yutup surlu kentlerini yıktı,Yahuda kızının feryadını, figanını arşa çıkardı."));
trsuperobject.biblemap.put("b25_2_6", new trsuperobject(25,2,6,"Bahçe çardağını söker gibi kendi çardağını söküp attı,Buluşma yerini yok etti,RAB Siyonda bayram ve Şabat günlerini unutturdu,Şiddetli öfkesi yüzünden kralı da kâhini de reddetti."));
trsuperobject.biblemap.put("b25_2_7", new trsuperobject(25,2,7,"Rab sunağını attı,Tapınağını terk etti;Siyon saraylarını çeviren surları düşman eline bıraktı.Bayram gününde olduğu gibi,Düşman RABbin Tapınağında sevinç çığlıkları attı."));
trsuperobject.biblemap.put("b25_2_9", new trsuperobject(25,2,9,"Siyonun kapıları yere battı,RAB kapı sürgülerini kırıp yok etti,Kralıyla önderleri başka ulusların arasında kaldı,Kutsal Yasa uygulanmaz oldu,Peygamberlerine RABden görüm gelmiyor artık."));
trsuperobject.biblemap.put("b25_2_10", new trsuperobject(25,2,10,"Siyon kızının ileri gelenleri suskun, yere oturmuş,Başlarına toprak saçıp çul kuşanmışlar,Yeruşalimin erden kızları yere eğmiş başlarını."));
trsuperobject.biblemap.put("b25_2_11", new trsuperobject(25,2,11,"Gözlerim tükenmekte ağlamaktan,İçim kanıyor;Halkımın yıkımındanYüreğim sızlıyor,Çünkü kent meydanlarında çocuklarla bebekler bayılmakta."));
trsuperobject.biblemap.put("b25_2_12", new trsuperobject(25,2,12,"Kent meydanlarında yaralılar gibi bayılıpCan çekişirken annelerinin bağrında,‹‹Ekmekle şarap nerede?›› diye soruyorlar annelerine."));
trsuperobject.biblemap.put("b25_2_13", new trsuperobject(25,2,13,"Senin için ne diyeyim?Ey Yeruşalim kızı, seni neye benzeteyim?Ey Siyonun erden kızı, sana neyi örnek göstereyim deSeni avutayım?Sendeki gedik deniz kadar büyük,Kim sana şifa verebilir?"));
trsuperobject.biblemap.put("b25_2_14", new trsuperobject(25,2,14,"Peygamberlerin senin için boş ve anlamsız görümler gördüler.Suçunu ortaya çıkarsalardı, eski gönencine kavuşabilirdin;Oysa seni ayartacak boş görümler gördüler."));
trsuperobject.biblemap.put("b25_2_15", new trsuperobject(25,2,15,"Yoldan geçen herkes el çırparak seninle alay ediyor,Yeruşalim kızına baş sallayıp ıslık çalarak,‹‹Bütün dünyanın sevinci, güzellik simgesi dedikleri kent bu mu?›› diyorlar."));
trsuperobject.biblemap.put("b25_2_16", new trsuperobject(25,2,16,"Düşmanlarının hepsi seninle alay etti,Islık çalıp diş gıcırdatarak,‹‹Onu yuttuk›› diyorlar,‹‹İşte beklediğimiz gün, sonunda gördük onu.››"));
trsuperobject.biblemap.put("b25_2_17", new trsuperobject(25,2,17,"RAB düşündüğünü yaptı,Geçmişte söylediği sözü yerine getirdi,Yıktı, acımadı,Düşmanı senin haline sevindirdi,Hasımlarını güçlü kıldı. boynuzunu yükseltti››."));
trsuperobject.biblemap.put("b25_2_18", new trsuperobject(25,2,18,"Halk Rabbe yürekten feryat ediyor.Ey Siyon kızının surları,Gece gündüz gözyaşın sel gibi aksın!Dinlenme, gözüne uyku girmesin!"));
trsuperobject.biblemap.put("b25_2_19", new trsuperobject(25,2,19,"Kalk, gece her nöbet başında haykır,Rabbin huzurunda yüreğini su gibi dök!Her sokak başında açlıktan bayılan çocuklarının başı için Ona ellerini aç."));
trsuperobject.biblemap.put("b25_2_20", new trsuperobject(25,2,20,"‹‹Bak, ya RAB, gör! Kime böyle yaptın?Kadınlar çocuklarını, sevgili yavrularını mı yesin?Kâhinle peygamber Rabbin Tapınağında mı öldürülsün?"));
trsuperobject.biblemap.put("b25_2_21", new trsuperobject(25,2,21,"Gençler, yaşlılar sokaklarda, yerlerde yatıyor,Kılıçtan geçirildi erden kızlarımla gençlerim,Öfkelendiğin gün öldürdün onları, acımadan boğazladın."));
trsuperobject.biblemap.put("b25_2_22", new trsuperobject(25,2,22,"Bir bayram günü davet eder gibiBeni dehşete düşürenleri davet ettin her yandan.RAB'bin öfkelendiği gün kaçıp kurtulan,Sağ kalan olmadı.Sevgiyle büyüttüğüm çocuklarımıDüşmanım yok etti.››"));
trsuperobject.biblemap.put("b25_3_2", new trsuperobject(25,3,2,"Beni güttü,Işıkta değil karanlıkta yürüttü."));
trsuperobject.biblemap.put("b25_3_3", new trsuperobject(25,3,3,"Evet, dönüp dönüp bütün gün bana elini kaldırıyor."));
trsuperobject.biblemap.put("b25_3_4", new trsuperobject(25,3,4,"Etimi, derimi yıprattı, kemiklerimi kırdı."));
trsuperobject.biblemap.put("b25_3_5", new trsuperobject(25,3,5,"Beni kuşattı,Acı ve zahmetle sardı çevremi."));
trsuperobject.biblemap.put("b25_3_6", new trsuperobject(25,3,6,"Çoktan ölmüş ölüler gibiBeni karanlıkta yaşattı."));
trsuperobject.biblemap.put("b25_3_7", new trsuperobject(25,3,7,"Çevreme duvar çekti, dışarı çıkamıyorum,Zincirimi ağırlaştırdı."));
trsuperobject.biblemap.put("b25_3_8", new trsuperobject(25,3,8,"Feryat edip yardım isteyince deDuama set çekiyor."));
trsuperobject.biblemap.put("b25_3_9", new trsuperobject(25,3,9,"Yontma taşlarla yollarımı kesti,Dolaştırdı yollarımı."));
trsuperobject.biblemap.put("b25_3_10", new trsuperobject(25,3,10,"Benim için O pusuya yatmış bir ayı,Gizlenmiş bir aslandır."));
trsuperobject.biblemap.put("b25_3_11", new trsuperobject(25,3,11,"Yollarımı saptırdı, paraladı,Mahvetti beni."));
trsuperobject.biblemap.put("b25_3_12", new trsuperobject(25,3,12,"Yayını gerdi, okunu savurmak içinBeni nişangah olarak dikti."));
trsuperobject.biblemap.put("b25_3_13", new trsuperobject(25,3,13,"Oklarını böbreklerime sapladı."));
trsuperobject.biblemap.put("b25_3_14", new trsuperobject(25,3,14,"Halkımın önünde gülünç düştüm,Gün boyu alay konusu oldum türkülerine."));
trsuperobject.biblemap.put("b25_3_15", new trsuperobject(25,3,15,"Beni acıya doyurdu,Bana doyasıya pelinsuyu içirdi."));
trsuperobject.biblemap.put("b25_3_16", new trsuperobject(25,3,16,"Dişlerimi çakıl taşlarıyla kırdı,Kül içinde diz çöktürdü bana."));
trsuperobject.biblemap.put("b25_3_17", new trsuperobject(25,3,17,"Esenlik yüzü görmedi canım,Mutluluğu unuttum."));
