trsuperobject.biblemap.put("b25_3_23", new trsuperobject(25,3,23,"Her sabah tazelenir onlar,Sadakatin büyüktür."));
trsuperobject.biblemap.put("b25_3_24", new trsuperobject(25,3,24,"‹‹Benim payıma düşen RABdir›› diyor canım,‹‹Bu yüzden Ona umut bağlıyorum.››"));
trsuperobject.biblemap.put("b25_3_25", new trsuperobject(25,3,25,"RAB kendisini bekleyenler,Onu arayan canlar için iyidir."));
trsuperobject.biblemap.put("b25_3_26", new trsuperobject(25,3,26,"RABbin kurtarışını sessizce beklemek iyidir."));
