trsuperobject.biblemap.put("b28_1_6", new trsuperobject(28,1,6,"Gomer yine hamile kaldı ve bir kız doğurdu. RAB Hoşeaya, ‹‹Adını Lo-Ruhama koy›› dedi, ‹‹Çünkü artık İsrail soyuna acımayacağım, onları bağışlamayacağım."));
trsuperobject.biblemap.put("b28_1_7", new trsuperobject(28,1,7,"Ancak Yahuda soyuna merhamet edeceğim. Ben kurtaracağım onları, ama yay, kılıç, savaş, at ve atlılar aracılığıyla değil, kendi aracılığımla.››"));
