trsuperobject.biblemap.put("b30_8_11", new trsuperobject(30,8,11,"‹‹İşte günler geliyor,Ülkeye kıtlık göndereceğim››Diyor Egemen RAB,‹‹Ekmek ya da su kıtlığı değil,RABbin sözlerine susamışlık göndereceğim."));
trsuperobject.biblemap.put("b30_8_12", new trsuperobject(30,8,12,"RABbin sözünü bulmak içinİnsanlar denizden denize,Kuzeyden doğuya dek dolaşacak,Oraya buraya koşacak, ama bulamayacaklar."));
