trsuperobject.biblemap.put("b30_10_5", new trsuperobject(30,10,5,"Rab, Her Şeye Egemen RABYere dokununca yer erir,Üzerinde yaşayan herkes yasa bürünür,Bütün yeryüzü Nil gibi kabarır,Mısırın ırmağı gibi yine alçalır."));
trsuperobject.biblemap.put("b30_10_6", new trsuperobject(30,10,6,"Yukarı odalarını gökyüzünde yapan,Kubbesini yeryüzünde kuran,Denizin sularını çağırıp yeryüzüne döken Odur;Onun adı RABdir."));
trsuperobject.biblemap.put("b30_10_7", new trsuperobject(30,10,7,"‹‹Ey İsrailliler,Benim için Kûşlulardan ne farkınız var?››Diyor RAB.‹‹İsraillileri Mısırdan,Filistlileri Kaftordan,Aramlıları Kîrden çıkaran ben değil miyim?"));
trsuperobject.biblemap.put("b30_10_8", new trsuperobject(30,10,8,"‹‹İşte, Egemen RABbin gözleriBu günahlı krallığın üzerindedir.Onu yeryüzünden söküp atacağım,Ancak Yakup soyunu büsbütün yok etmeyeceğim››Diyor RAB."));
trsuperobject.biblemap.put("b30_10_9", new trsuperobject(30,10,9,"‹‹İşte buyruk vereceğim,Bütün uluslar arasındanİsraili kalburla eler gibi eleyeceğim,Bir çakıl bile yere düşmeyecek."));
trsuperobject.biblemap.put("b30_10_10", new trsuperobject(30,10,10,"Halkımın arasındaki bütün günahlılar,‹Kötülük bizi bulmaz, bize erişmez› diyenlerKılıçtan geçirilecek.››"));
trsuperobject.biblemap.put("b30_10_11", new trsuperobject(30,10,11,"‹‹O gün Davutun yıkık çardağını yeniden kuracağım,Gediklerini kapayacak,Yıkık yerlerini onaracağım,Onu eskisi gibi yapacağım,"));
