trsuperobject.biblemap.put("b31_1_4", new trsuperobject(31,1,4,"Kartal gibi yükselsen de, Yuvanı yıldızlar arasında kursan da, Oradan indireceğim seni›› diyor RAB."));
trsuperobject.biblemap.put("b31_1_5", new trsuperobject(31,1,5,"Hırsızlar ya da haydutlar gece sana gelselerdi, Yalnızca gereksindiklerini çalmazlar mıydı? Üzüm toplayanlar bağına girseydi, Birkaç salkım bırakmazlar mıydı? Ama seni ne felaketler bekliyor;"));
trsuperobject.biblemap.put("b31_1_6", new trsuperobject(31,1,6,"Esav`ın her şeyi yağmalanacak, Gizli hazineleri ortaya çıkarılacak."));
trsuperobject.biblemap.put("b31_1_7", new trsuperobject(31,1,7,"Seninle antlaşma yapanların hepsi Seni toprağından sürecek. Güvendiğin insanlar Seni aldatıp yenilgiye uğratacak, Ekmeğini yiyenler sana tuzak kuracak Ve sen farkına varmayacaksın bile.››"));
trsuperobject.biblemap.put("b31_1_9", new trsuperobject(31,1,9,"Ey Teman, yiğitlerin öyle bir korkuya kapılacak ki, Esav`ın dağlarında bulunanların hepsi Kıyıma uğrayıp yok olacak."));
trsuperobject.biblemap.put("b31_1_10", new trsuperobject(31,1,10,"Yakup soyundan gelen kardeşlerine Yaptığın zorbalıktan ötürü utanca boğulacak Ve sonsuza dek yok olacaksın."));
trsuperobject.biblemap.put("b31_1_11", new trsuperobject(31,1,11,"Çünkü yabancılar onların malını mülkünü yağmaladıkları gün Uzakta durup seyrettin. Öteki uluslar kapılarından içeri girip Yeruşalim için kura çektiklerinde Sen de onlardan biri gibi davrandın."));
trsuperobject.biblemap.put("b31_1_12", new trsuperobject(31,1,12,"Yahudalı kardeşlerinin o kötü gününden Zevk almamalıydın. Başlarına gelen yıkıma sevinmemeli, Sıkıntılı günlerinde onlarla alay etmemeliydin."));
trsuperobject.biblemap.put("b31_1_13", new trsuperobject(31,1,13,"Halkım felakete uğradığı gün Kente girmemeliydin, O gün halkımın uğradığı kötülükten zevk almamalı, Malını mülkünü ele geçirmeye kalkmamalıydın."));
trsuperobject.biblemap.put("b31_1_14", new trsuperobject(31,1,14,"Kaçmaya çalışanları öldürmek için Yol ağzında durmamalı, O sıkıntılı günde kurtulanları Düşmana teslim etmemeliydin.››"));
trsuperobject.biblemap.put("b31_1_15", new trsuperobject(31,1,15,"RAB`bin bütün ulusları yargılayacağı gün yaklaştı. Ey Edom, ne yaptıysan sana da aynısı yapılacak. Yaptıkların kendi başına gelecek."));
trsuperobject.biblemap.put("b31_1_16", new trsuperobject(31,1,16,"Ey Yahudalılar, kutsal dağımda nasıl içtiyseniz, Bütün uluslar da öyle içecekler. İçip içip yok olacaklar, Hiç var olmamış gibi.›› İsrail`in Zaferi"));
trsuperobject.biblemap.put("b31_1_17", new trsuperobject(31,1,17,"Ama kurtulanlar Siyon Dağı`nda toplanacak Ve orası kutsal olacak. Yakup soyu da mirasına kavuşacak."));
trsuperobject.biblemap.put("b31_1_18", new trsuperobject(31,1,18,"Yakup soyu ateş, Yusuf soyu alev, Esav soyu anız olacak. Onları yakıp yok edecekler. Esav soyundan kurtulan olmayacak.›› RAB böyle diyor."));
trsuperobject.biblemap.put("b31_1_19", new trsuperobject(31,1,19,"Yahudalılar`dan Negev halkı Esav`ın dağlarını; Şefela halkı Filist bölgesini; Yahudalılar`ın tümü Efrayim ve Samiriye topraklarını; Benyaminliler Gilat`ı mülk edinecekler."));
trsuperobject.biblemap.put("b31_1_20", new trsuperobject(31,1,20,"Kenan`dan sürülmüş olan İsrailli savaşçılar Sarefat`a kadar uzanan toprakları, Sefarat`taki Yeruşalimli sürgünler de Negev`deki kentleri mülk edinecekler."));
