trsuperobject.biblemap.put("b32_1_1-2", new trsuperobject(32,1,1-2,"RAB bir gün Amittay oğlu Yunusa, ‹‹Kalk, Ninovaya, o büyük kente git ve halkı uyar›› diye seslendi, ‹‹Çünkü kötülükleri önüme kadar yükseldi.››"));
trsuperobject.biblemap.put("b32_1_3", new trsuperobject(32,1,3,"Ne var ki, Yunus RABbin huzurundan Tarşişe kaçmaya kalkıştı. Yafaya inip Tarşişe giden bir gemi buldu. Ücretini ödeyip gemiye bindi, RABden uzaklaşmak için Tarşişe doğru yola çıktı."));
trsuperobject.biblemap.put("b32_1_4", new trsuperobject(32,1,4,"Yolda RAB şiddetli bir rüzgar gönderdi denize. Öyle bir fırtına koptu ki, gemi neredeyse parçalanacaktı."));
trsuperobject.biblemap.put("b32_1_5", new trsuperobject(32,1,5,"Gemiciler korkuya kapıldı, her biri kendi ilahına yalvarmaya başladı. Gemiyi hafifletmek için yükleri denize attılar. Yunus ise teknenin ambarına inmiş, yatıp derin bir uykuya dalmıştı."));
trsuperobject.biblemap.put("b32_1_6", new trsuperobject(32,1,6,"Gemi kaptanı Yunusun yanına gidip, ‹‹Hey! Nasıl uyursun sen?›› dedi, ‹‹Kalk, tanrına yalvar, belki halimizi görür de yok olmayız.››"));
trsuperobject.biblemap.put("b32_1_7", new trsuperobject(32,1,7,"Sonra denizciler birbirlerine, ‹‹Gelin, kura çekelim›› dediler, ‹‹Bakalım, bu bela kimin yüzünden başımıza geldi.›› Kura çektiler, kura Yunusa düştü."));
trsuperobject.biblemap.put("b32_1_8", new trsuperobject(32,1,8,"Bunun üzerine Yunusa, ‹‹Söyle bize!›› dediler, ‹‹Bu bela kimin yüzünden başımıza geldi? Ne iş yapıyorsun sen, nereden geliyorsun, nerelisin, hangi halka mensupsun?››"));
trsuperobject.biblemap.put("b32_1_9", new trsuperobject(32,1,9,"Yunus, ‹‹İbraniyim›› diye karşılık verdi, ‹‹Denizi ve karayı yaratan Göklerin Tanrısı RABbe taparım.››"));
trsuperobject.biblemap.put("b32_1_10", new trsuperobject(32,1,10,"Denizciler bu yanıt karşısında dehşete düştüler. ‹‹Neden yaptın bunu?›› diye sordular. Yunusun RABden uzaklaşmak için kaçtığını biliyorlardı. Daha önce onlara anlatmıştı."));
trsuperobject.biblemap.put("b32_1_11", new trsuperobject(32,1,11,"Deniz gittikçe kuduruyordu. Yunusa, ‹‹Denizin dinmesi için sana ne yapalım?›› diye sordular."));
trsuperobject.biblemap.put("b32_1_12", new trsuperobject(32,1,12,"Yunus, ‹‹Beni kaldırıp denize atın›› diye yanıtladı, ‹‹O zaman sular durulur. Çünkü biliyorum, bu şiddetli fırtınaya benim yüzümden yakalandınız.››"));
trsuperobject.biblemap.put("b32_1_13", new trsuperobject(32,1,13,"Denizciler karaya dönmek için küreklere asıldılar, ama başaramadılar. Çünkü deniz gittikçe kuduruyordu."));
trsuperobject.biblemap.put("b32_1_14", new trsuperobject(32,1,14,"RABbe seslenerek, ‹‹Ya RAB, yalvarıyoruz›› dediler, ‹‹Bu adamın canı yüzünden yok olmayalım. Suçsuz bir adamın ölümünden bizi sorumlu tutma. Çünkü sen kendi istediğini yaptın, ya RAB.››"));
trsuperobject.biblemap.put("b32_1_15", new trsuperobject(32,1,15,"Sonra Yunusu kaldırıp denize attılar, kuduran deniz sakinleşti."));
